$t[53] = 'och n&auml;r det l&auml;ses upp f&ouml;r dem, s&auml;ger de: &quot;Detta tror vi p&aring; - det &auml;r sanningen fr&aring;n v&aring;r Herre; redan innan [vi h&ouml;rde det] hade vi underkastat oss [Hans vilja].';
$t[54] = 'De skall f&aring; dubbel bel&ouml;ning, d&auml;rf&ouml;r att de &auml;r t&aring;lmodiga och f&ouml;rdriver ont med gott och ger &aring;t andra av det som Vi har sk&auml;nkt dem f&ouml;r deras f&ouml;rs&ouml;rjning.';
$t[55] = 'Och n&auml;r de h&ouml;r l&auml;ttsinnigt tal, drar de sig undan och s&auml;ger: &quot;Vi f&aring;r svara f&ouml;r v&aring;ra handlingar och ni f&aring;r svara f&ouml;r era handlingar; fred &ouml;ver er! Vi s&ouml;ker inte s&auml;llskap med dem som inte k&auml;nner [skillnaden mellan r&auml;tt och or&auml;tt].&quot;';
$t[56] = 'DU KAN inte leda dem du &auml;lskar [Muhammad] p&aring; r&auml;tt v&auml;g, men Gud leder den Han vill. Och Han vet b&auml;st vilka de &auml;r som &auml;r villiga att l&aring;ta sig v&auml;gledas.';
$t[57] = 'Men [n&aring;gra] s&auml;ger: &quot;Om vi f&ouml;ljde den v&auml;g som du uppmanar oss [att f&ouml;lja], skulle v&aring;r jord tas ifr&aring;n oss.&quot; Har Vi inte gett dem en trygg tillflykt och s&ouml;rjt f&ouml;r deras behov genom de frukter [och nyttigheter av skilda slag] som f&ouml;rs dit fr&aring;n alla h&aring;ll? Men de flesta av dem har inte [detta] klart f&ouml;r sig.';
$t[58] = 'Och hur m&aring;nga st&auml;der, vars inv&aring;nare i [l&auml;ttsinnigt] &ouml;vermod skr&ouml;t med sitt &ouml;verd&aring;diga liv, har Vi inte l&aring;tit g&aring; under. Och dessa boningar som var deras &auml;r nu &ouml;vergivna - &auml;ven om n&aring;gon [f&ouml;rbipasserande] ibland [tar dem i anspr&aring;k]. - Det &auml;r Vi som f&ouml;rblir n&auml;r allt f&ouml;rg&aring;r!';
$t[59] = 'Men din Herre f&ouml;rg&ouml;r inte st&auml;der som inget s&auml;ndebud &auml;nnu kommit till f&ouml;r att framf&ouml;ra V&aring;ra budskap. Och Vi l&aring;ter inte st&auml;der g&aring; under d&auml;r inv&aring;narna inte beg&aring;r [djupt] or&auml;ttf&auml;rdiga handlingar.';
$t[60] = 'Vad det &auml;n &auml;r som ni [h&auml;r] f&aring;r till sk&auml;nks tj&auml;nar det [bara] till att f&ouml;rljuva och f&ouml;rsk&ouml;na detta liv, medan det [som v&auml;ntar] hos Gud &auml;r b&auml;ttre och mer best&aring;ende. Vill ni inte anv&auml;nda ert f&ouml;rst&aring;nd';
$t[61] = 'Kan den som Vi har lovat det [h&ouml;gsta] goda, ett l&ouml;fte som han f&aring;r se uppfyllt [vid uppst&aring;ndelsen], j&auml;mf&ouml;ras med den som Vi har sk&auml;nkt jordisk gl&auml;dje men som p&aring; Uppst&aring;ndelsens dag skall h&ouml;ra till dem som radas upp [f&ouml;r att straffas]?';
$t[62] = 'Den Dagen skall Han kalla fram dem och fr&aring;ga: &quot;Var &auml;r nu dessa medhj&auml;lpare [som ni satte] vid Min sida?&quot;';
$t[63] = 'Och de &ouml;ver vilka domen obevekligen har fallit skall s&auml;ga: &quot;Herre! Det var dem vi ledde vilse men vi ledde dem vilse liksom vi [sj&auml;lva] gick vilse och inf&ouml;r Dig sv&auml;r vi oss fria fr&aring;n allt ansvar [f&ouml;r dem] - det var inte oss de tillbad!&quot;';
$t[64] = 'Och det skall s&auml;gas [till dem]: &quot;Kalla nu [till er hj&auml;lp] dem som ni satte i samband [med Gud] som medhj&auml;lpare!&quot; och de skall kalla p&aring; dem, men de kommer inte att svara. [Och] n&auml;r de ser straffet kommer de att &ouml;nska att de hade l&aring;tit sig ledas p&aring; r&auml;tt v&auml;g!';
$t[65] = 'Och den Dag d&aring; Han kallar fram dem och fr&aring;gar: &quot;Vilket svar gav ni budb&auml;rarna?&quot;';
$t[66] = 'skall alla tankar och argument vara utpl&aring;nade ur deras medvetande och de kan inte heller be varandra [om r&aring;d].';
$t[67] = 'Men f&ouml;r den som k&auml;nner &aring;nger och antar tron och [d&auml;refter] lever ett r&auml;ttskaffens liv, finns hoppet att allt [till sist] skall g&aring; honom v&auml;l i h&auml;nder.';
$t[68] = 'DIN Herre skapar vad Han vill och Han v&auml;ljer f&ouml;r [m&auml;nniskorna] det b&auml;sta. Stor &auml;r Gud i Sin h&auml;rlighet, h&ouml;jd h&ouml;gt &ouml;ver allt vad de s&auml;tter vid Hans sida!';
$t[69] = 'Och din Herre vet vad deras hj&auml;rtan g&ouml;mmer likav&auml;l som det som de &ouml;ppet tillk&auml;nnager.';
$t[70] = 'Han &auml;r Gud, det finns ingen gud utom Han. Honom tillkommer allt lov och pris i denna v&auml;rld och i evigheten. Domen &auml;r Hans och till Honom skall ni f&ouml;ras &aring;ter.';
$t[71] = 'S&auml;g: &quot;Vad tror ni? Om Gud l&auml;t natten r&aring;da &ouml;ver er utan avbrott till Uppst&aring;ndelsens dag, vem &auml;r den gud - utom Gud - som kan sk&auml;nka er ljus? [Varf&ouml;r] lyssnar ni inte [och f&ouml;rs&ouml;ker f&ouml;rst&aring;]?&quot;';
$t[72] = 'S&auml;g: &quot;Vad tror ni? Om Gud l&auml;t dagen r&aring;da &ouml;ver er utan avbrott till Uppst&aring;ndelsens dag, vem &auml;r den gud - utom Gud - som kan ge er m&ouml;rker, s&aring; att ni f&aring;r vila? [Varf&ouml;r f&ouml;rs&ouml;ker] ni inte se klart?&quot;';
$t[73] = 'I Sin n&aring;d skapade Han f&ouml;r er natten och dagen, s&aring; att ni skall ha en tid f&ouml;r vila och en tid d&aring; ni skall kunna s&ouml;ka det som Han i Sin godhet [beviljar er]; kanske k&auml;nner ni tacksamhet.';
$t[74] = 'OCH DEN Dagen skall Han kalla fram dem och fr&aring;ga: &quot;Var &auml;r nu de medhj&auml;lpare som ni satte [vid Min sida]?&quot;';
$t[75] = '[N&auml;r de tiger] skall Vi l&aring;ta ett vittne ur varje samfund stiga fram och Vi skall s&auml;ga [till de f&ouml;rut n&auml;mnda]: &quot;L&auml;gg fram beviset [f&ouml;r ert p&aring;st&aring;ende]!&quot; D&aring; skall de se att sanningen tillh&ouml;r Gud. Och allt som de hade diktat upp skall &ouml;verge dem.';
$t[76] = 'Qaarun h&ouml;rde till Moses folk men han upptr&auml;dde &ouml;vermodigt mot dem [alla]. Vi hade n&auml;mligen sk&auml;nkt honom s&aring;dana rikedomar att hans skattkistor skulle ha k&auml;nts tunga till och med f&ouml;r tio starka m&auml;n eller &auml;nnu fler. Och hans landsm&auml;n sade till honom: &quot;Skryt inte [med dina rikedomar]; Gud &auml;lskar inte skr&auml;vlare!';
$t[77] = 'Str&auml;va med det som Gud har gett dig efter det eviga livets goda utan att f&ouml;rsumma din del av det v&auml;rldsliga! Och g&ouml;r gott mot andra s&aring;som Gud har gjort gott mot dig, och st&ouml;r inte ordningen p&aring; jorden och sprid inte sedligt f&ouml;rd&auml;rv; Gud &auml;r inte v&auml;n av dem som st&ouml;r ordningen p&aring; jorden och f&ouml;rd&auml;rvar sederna.&quot;';
$t[78] = 'Han svarade: &quot;Det [jag &auml;ger] har jag f&aring;tt tack vare mina kunskaper [och min klokhet].&quot; Visste han inte att Gud har l&aring;tit tidigare sl&auml;kten g&aring; under, som &auml;gde mera makt och samlat st&ouml;rre [rikedomar] &auml;n han? Men de obotf&auml;rdiga, trotsiga syndarna beh&ouml;ver inte tillfr&aring;gas om sina synder.';
$t[79] = 'Och [n&auml;r] han gick ut bland folket i hela sin st&aring;t, sade de som [bara] hade det jordiska livet f&ouml;r &ouml;gonen: &quot;T&auml;nk om vi fick [&auml;ga] vad Qarun har f&aring;tt [&auml;ga]; han har verkligen gynnats av turen!&quot;';
$t[80] = 'Men de som hade f&aring;tt del av kunskap sade: &quot;Arma stackare! [Ni inser inte att] f&ouml;r den som tror och lever r&auml;ttskaffens &auml;r Guds bel&ouml;ning vida b&auml;ttre [&auml;n det som ni kan vinna i detta liv]; men denna [bel&ouml;ning] vinner ingen utom den som visar t&aring;lamod och uth&aring;llighet.&quot;';
$t[81] = 'Och s&aring; l&auml;t Vi jorden uppsluka honom och hans hus, och ingen anh&auml;ngarskara fanns till hands, som kunde r&auml;dda honom undan Guds [straff] och han kunde inte hj&auml;lpa sig sj&auml;lv.';
$t[82] = 'Och n&auml;sta morgon sade de som dagen innan hade &ouml;nskat vara i hans st&auml;lle: &quot;Hur kunde vi [f&ouml;rbise] att Gud ger den Han vill av Sina tj&auml;nare riklig och [den Han vill] knappare utkomst! Utan Guds n&aring;diga beskydd, skulle vi [ocks&aring;] ha uppslukats av jorden. Hur kunde vi [tvivla p&aring;] att det skall g&aring; illa f&ouml;r dem som f&ouml;rnekar sanningen!&quot;';
$t[83] = 'De eviga boningarna skall Vi sk&auml;nka dem som inte str&auml;var efter makt och &auml;ra p&aring; jorden eller f&ouml;rs&ouml;ker st&ouml;ra ordningen och f&ouml;rd&auml;rva sederna d&auml;r - den slutliga segern tillh&ouml;r dem som fruktar Gud.';
$t[84] = 'Den som stiger fram [inf&ouml;r Honom] med en god handling skall f&aring; en st&ouml;rre [bel&ouml;ning &auml;n handlingen f&ouml;rtj&auml;nar], och den som stiger fram [inf&ouml;r Honom] med en d&aring;lig handling [skall veta att] de som beg&aring;r d&aring;liga handlingar inte skall straffas f&ouml;r annat &auml;n vad de har gjort.';
$t[85] = 'HAN SOM har uppenbarat denna Koran f&ouml;r dig och gjort dess klara och tydliga f&ouml;reskrifter till bindande regler [f&ouml;r ditt liv] skall helt visst l&aring;ta dig n&aring; ditt slutliga m&aring;l. S&auml;g [till f&ouml;rnekarna av sanningen]: &quot;Min Herre vet b&auml;st vem som &auml;r r&auml;tt v&auml;gledd och vem som har g&aring;tt helt vilse.&quot;';
$t[86] = 'Du v&auml;ntade dig inte att f&aring; ta emot uppenbarelsen av denna Skrift, men s&aring; har skett genom din Herres n&aring;d. Ge d&auml;rf&ouml;r inte f&ouml;rnekarna under n&aring;gra omst&auml;ndigheter ditt st&ouml;d';
$t[87] = 'och l&aring;t dem aldrig hindra dig att [i allt] f&ouml;lja din Herres budskap sedan de uppenbarats f&ouml;r dig. Uppmana [m&auml;nniskorna] att dyrka din Herre [och att bek&auml;nna Hans enhet]. Och n&auml;rma dig inte dem som s&auml;tter n&aring;gon eller n&aring;got vid Hans sida';
$t[88] = 'och anropa aldrig en annan gud j&auml;mte Gud. Det finns ingen gud utom Han. Allt skall f&ouml;rg&aring; utom Han [som f&ouml;rblir i evighet]; domen &auml;r Hans och till Honom skall ni f&ouml;ras &aring;ter.';
?>