$t[24] = 'Hem m&uuml;srikler dediler ki: &quot;Hayat, ancak bu d&uuml;nya hayatimizdan ibarettir. &Ouml;l&uuml;r&uuml;z ve yasariz. Bizi ancak ge&ccedil;en zaman yokluga s&uuml;r&uuml;kler. Halbuki onlarin bu hususta hi&ccedil;bir bilgileri yoktur. Onlar, sadece b&ouml;yle zannederler.';
$t[25] = 'Kendilerine &acirc;yetlerimiz a&ccedil;ik&ccedil;a okundugu zaman; &quot;Eger s&ouml;z&uuml;n&uuml;zde dogru iseniz atalarimizi diriltip getirin.&quot; demekten baska s&ouml;ylenecek hi&ccedil;bir delil yoktur.';
$t[26] = '(Ey Muhammed!) De ki: &quot;Allah sizi diriltir. Sonra sizi o &ouml;ld&uuml;r&uuml;r, sonra da geleceginde s&uuml;phe olmayan kiyamet g&uuml;n&uuml;nde (diriltip) bir araya toplar. Fakat insanlarin &ccedil;ogu bilmezler.';
$t[27] = 'G&ouml;klerin ve yerin m&uuml;lk&uuml; sadece Allah\'indir. Kiy&acirc;metin kapacagi g&uuml;n varya, iste o g&uuml;n batila sapanlar hep h&uuml;srana d&uuml;secekler.';
$t[28] = 'O g&uuml;n her &uuml;mmeti, diz &ccedil;&ouml;km&uuml;s g&ouml;r&uuml;rs&uuml;n. Her &uuml;mmet, kendi kitabina &ccedil;agirilir, onlara: &quot;Bug&uuml;n yaptiginiz amellerin cezasi verilecektir.';
$t[29] = 'Iste kitabiniz, y&uuml;z&uuml;n&uuml;ze karsi hakki s&ouml;yl&uuml;yor, &ccedil;&uuml;nk&uuml; biz sizin yaptiklarnizi hep kaydediyorduk.&quot; (denir).';
$t[30] = 'Iman edip iyi isler yapanlara gelince; Rableri onlari rahmeti i&ccedil;ine koyacaktir. Iste apa&ccedil;ik kurtulus budur.';
$t[31] = 'Ama k&acirc;firlere gelince; onlara da denilir ki; &quot;Size &acirc;yetlerim okunmadi mi? Siz b&uuml;y&uuml;kl&uuml;k tasladiniz ve g&uuml;nah isleyen bir kavim oldunuz degil mi?';
$t[32] = 'Allah\'in vaadi ger&ccedil;ektir. &quot;O kiy&acirc;metin geleceginde s&uuml;phe yoktur.&quot; denildiginde &quot;Kiyamet nedir bilmiyoruz.&quot; Yalniz bir zandan ib&acirc;rettir saniyoruz. Fakat bu hususta kesin bir bilgimiz yok.&quot; derdiniz.';
$t[33] = 'Derken yaptiklari amellerin k&ouml;t&uuml;l&uuml;g&uuml; g&ouml;zlerinin &ouml;n&uuml;ne serildi, alay edip durduklari sey onlari kusativerdi.';
$t[34] = 'O g&uuml;n k&acirc;firlere s&ouml;yle denilir; &quot;Siz, d&uuml;nyada bug&uuml;ne kavusmayi nasil unuttuysaniz, biz de bug&uuml;n sizi &ouml;ylece unutacagiz. Yeriniz atestir ve sizin i&ccedil;in yardimcilardan bir kimse de yoktur.&quot;';
$t[35] = 'Bunun sebebi sudur; Siz Allah\'in &acirc;yetlerini alaya aldiniz, d&uuml;nya hayati sizi aldatti. Artik bug&uuml;n onlar, atesten &ccedil;ikarilmayacaklar ve kendilerinden &ouml;z&uuml;r dilemeleri de kabul edilmeyecektir.';
$t[36] = 'Hamd, g&ouml;klerin Rabbi, yerin Rabbi ve &acirc;lemlerin Rabbi olan Allah\'a mahsustur.';
$t[37] = 'G&ouml;klerde ve yerde b&uuml;y&uuml;kl&uuml;k ve h&acirc;kimiyet O\'nundur. O, Aziz\'dir (herseye galiptir); Hak&icirc;m\'dir (h&uuml;k&uuml;m ve hikmet sahibidir).';
