$t[68] = 'بىز (ئۇنىڭغا) ئېيتتۇقكى، &laquo;قورقمىغىن سەن چوقۇم ئۈستۈنلۈك قازىنىسەن،';
$t[69] = 'قولۇڭدىكىنى تاشلىغىن، ئۇ ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋېتىدۇ، ئۇلارنىڭ كۆرسەتكەنلىكىنى سېھىرگەرلەرنىڭ ھىيلىسىدۇر، سېھىرگەرلەر قەيەرگە بارسا مۇۋەپپەقىيەت قازىنالمايدۇ&raquo;';
$t[70] = '(مۇسا ھاسىسىنى تاشلىۋىدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋەتتى) شۇنىڭ بىلەن سېھىرگەرلەر (سەجدە قىلغان ھالدا يىقىلىپ): &laquo;ھارۇن بىلەن مۇسانىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق&raquo; دېيىشتى';
$t[71] = 'پىرئەۋن ئېيتتى: &laquo;سىلەر مەن رۇخسەت بېرىشتىن بۇرۇن ئۇنىڭغا ئىمان ئېيتتىڭلار، شۈبھىسىزكى، ئۇ چوقۇم سىلەرگە سېھىرنى ئۆگەتكەن چوڭۇڭلار ئىكەن، مەن چوقۇم سىلەرنىڭ قوللىرىڭلارنى، پۇتلىرىڭلارنى ئوڭ - چەپ قىلىپ (يەنى ئوڭ قول بىلەن سول پۇتنى، ئوڭ پۇت بىلەن سول قولنى) كېسىپ تاشلايمەن، سىلەرنى چوقۇم خورما دەرەخلىرىگە ئاسىمەن، سىلەر قايسىمىزنىڭ (يەنى مېنىڭ ياكى مۇسانىڭ پەرۋەردىگارىنىڭ) ئازابىنىڭ ئەڭ قاتتىق ۋە ئەڭ ئۇزۇن ئىكەنلىكىنى چوقۇم بىلىسىلەر&raquo;';
$t[72] = 'ئۇلار ئېيتتى: &laquo;سېنى (يەنى ساڭا ئەگىشىشنى) ئىختىيار قىلىپ، بىزگە كەلگەن (مۇسانىڭ راستلىقىنى كۆرسىتىدىغان) روشەن دەلىللەرنى ۋە بىزنى خەلق ئەتكەن اﷲ نى ھەرگىز تاشلىمايمىز. (بىزنىڭ ھەققىمىزدە) نېمىنى ھۆكۈم قىلساڭ، شۇنى ھۆكۈم قىلغىن، سېنىڭ ھۆكمىڭ پەقەت بۇ دۇنيادىلا ئۆتىدۇ';
$t[73] = 'بىزنىڭ خاتالىقىمىزنى، سېنىڭ مەجبۇرلىشىڭ ئارقىسىدا بىزنىڭ سېھىر كۆرسەتكەنلىكىمىزدىن ئىبارەت گۇناھىمىزنى مەغپىرەت قىلسۇن دەپ بىز ھەقىقەتەن پەرۋەردىگارىمىزغا ئىمان ئېيتتۇق، اﷲ نىڭ (ساۋابىنى سېنىڭ مۇكاپاتىڭدىن) ئارتۇقتۇر؛ اﷲ نىڭ (ئازابى سېنىڭ جازايىڭدىن) دائىمىيدۇر&raquo;';
$t[74] = 'كىمكى گۇناھكار ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ دوزاخقا كىرىدۇ، دوزاختا ئۇ ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ';
$t[75] = 'كىمكى مۆمىن بولۇپ، ياخشى ئەمەللەرنى قىلغان ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ (اﷲ نىڭ دەرگاھىدا) يۇقىرى دەرىجىگە ئېرىشىدۇ';
$t[76] = 'ئۇ (دەرىجىلەر) دائىمىي تۇرالغۇ بولغان جەننەتلەر بولۇپ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇ يەرلەردە ئۇ مەڭگۈ قالىدۇ، ئەنە شۇ (كۇفرىدىن ۋە گۇناھلاردىن) پاك بولغان ئادەمگە بېرىلىدىغان مۇكاپاتتۇر';
$t[77] = 'بىز شەك - شۈبھىسىز مۇساغا ۋەھيى قىلدۇقكى، &laquo;سەن مېنىڭ بەندىلىرىمنى كېچىدە (مىسىردىن) ئېلىپ چىقىپ كەت، سەن (ھاساڭنى) دېڭىزغا ئۇرۇپ ئۇلارغا بىر قۇرۇق يول ئېچىپ بەرگىن، (پىرئەۋننىڭ ئارقاڭدىن قوغلاپ) يېتىۋېلىشىدىن قورقمىغىن، (دېڭىزدا غەرق بولۇپ كېتىشتىنمۇ) قورقمىغىن&raquo;';
$t[78] = 'پىرئەۋن لەشكەرلىرى بىلەن قوغلىدى، ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ لەشكەرلىرىنى) دېڭىزدا دەھشەتلىك ھالەت ئورىۋالدى (يەنى ئۇلار دېڭىزدا غەرق بولدى)';
$t[79] = 'پىر ئەۋن ئۆز قەۋمىنى ئازدۇردى، (ئۇلارنى) توغرا يولغا باشلىمىدى';
$t[80] = 'ئى ئىسرائىل ئەۋلادى! سىلەرنى ھەقىقەتەن دۈشمىنىڭلاردىن قۇتقۇزدۇق، (مۇناجات ئۈچۈن ۋە مۇساغا تەۋراتنى نازىل قىلىش ئۈچۈن) سىلەرگە (تۇرسىنا) نىڭ ئوڭ تەرىپىنى ۋەدە قىلدۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق';
$t[81] = '(ئېيتتۇقكى) &laquo;بىز رىزىق قىلىپ بەرگەن پاك - ھالال نەرسىلەردىن يەڭلار، بۇ ھەقتە چەكتىن ئاشماڭلار، (ئۇنداق قىلساڭلار) سىلەرگە غەزىپىم نازىل بولىدۇ، كىمگە مېنىڭ غەزىپىم نازىل بولىدىكەن، ئۇ چوقۇم ھالاك بولىدۇ';
$t[82] = 'تەۋبە قىلغان، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ئاندىن توغرا يولدا ماڭغان ئادەمنى مەن ئەلۋەتتە ناھايىتى مەغپىرەت قىلغۇچىمەن&raquo;';
$t[83] = '&laquo;ئى مۇسا! ئالدىراپ (تۇرسىناغا) قەۋمىڭدىن ئىلگىرى كېلىشىڭنىڭ سەۋەبى نېمە؟&raquo; دېدۇق';
$t[84] = 'مۇسا ئېيتتى: &laquo;ئۇلار ئىزىمنى قوغلىشىپ كېلىۋاتىدۇ، پەرۋەردىگارىم! سېنى رازى بولسۇن دەپ بۇ يەرگە ئالدىراپ كەلدىم&raquo;';
$t[85] = 'اﷲ ئېيتتى: &laquo;سەندىن (يەنى سەن كەتكەندىن) كېيىن قەۋمىڭنى ھەقىقەتەن سىنىدۇق، ئۇلارنى سامىرى ئازدۇردى&raquo;';
$t[86] = 'مۇسا قەۋمىگە غەزەپلەنگەن ھالدا خاپا قايتتى، ئۇ ئېيتتى: &laquo;سىلەرگە پەرۋەردىگارىڭلار چىرايلىق ۋەدە قىلمىغانمىدى؟ (سىلەر ئەھدىنى ئۇنتۇغۇدەك) ۋاقىت شۇنچە ئۇزۇن بولۇپ كەتتىمۇ؟ ياكى سىلەر ئۆزۈڭلار پەرۋەردىگارىڭلارنىڭ غەزىپى چۈشۈشىنى ئىرادە قىلدىڭلارمۇ؟ شۇنىڭ ئۈچۈن سىلەرنىڭ ماڭا بەرگەن ۋەدەڭلارغا خىلاپلىق قىلدىڭلارمۇ؟&raquo;';
$t[87] = 'ئۇلار ئېيتتى: &laquo;ساڭا بەرگەن ۋەدىگە ئۆزلۈكىمىزدىن خىلاپلىق قىلغىنىمىز يوق، لېكىن بىز (پىرئەۋن) قەۋمىنىڭ زىننەتلىرىدىن نۇرغۇن ئېلىپ چىققان ئىدۇق، (سامىرىنىڭ بۇيرۇقى بويىچە) ئۇلارنى (ئوتقا) تاشلىدۇق، سامىرىمۇ (ئۆزى ئېلىپ چىققان زىبۇزىننەتلىرىنى) تاشلىدى&raquo;';
$t[88] = 'سامىرى (ئېرىتىلگەن زىبۇزىننەتلەردىن) ئۇلار ئۈچۈن بىر موزاي ھەيكەل ياسىدى، ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقاتتى، ئۇلار (يەنى سامىرى ۋە ئۇنىڭ تەۋەلىرى) ئېيتتى: &laquo;بۇ سىلەرنىڭ ئىلاھىڭلاردۇر ۋە مۇسانىڭ ئىلاھىدۇر، مۇسا (ئىلاھنى بۇ يەردە) ئۇنتۇپ قېلىپ (ئۇنى ئىزدەپ تۇرسىناغا كەتتى)&raquo;';
$t[89] = 'ئۇ موزاينىڭ ئۇلارنىڭ سۆزىگە جاۋاب بېرەلمەيدىغانلىقىنى، ئۇلارغا پايدا - زىيان يەتكۈزەلمەيدىغانلىقىنى ئۇلار ئۇقمامدۇ؟';
$t[90] = 'شەك - شۈبھىسىزكى، ھارۇن ئۇلارغا (مۇسا قايتىپ كېلىشتىن) بۇرۇن: &laquo;ئى قەۋمىم! سىلەر موزاي بىلەن قايمۇقتۇرۇلدۇڭلار، سىلەرنىڭ پەرۋەردىگارىڭلار مەرھەمەتلىك اﷲ دۇر، ماڭا بوي سۇنۇڭلار، (موزايغا چوقۇنۇشنى تەرك ئېتىش بىلەن) بۇيرىقىمغا ئىتائەت قىلىڭلار!&raquo; دېدى';
$t[91] = 'ئۇلار: &laquo;مۇسا قايتىپ كەلگەنگە قەدەر ئۇنىڭغا چوقۇنىۋېرىمىز&raquo; دېيىشتى';
$t[92] = 'مۇسا ئېيتتى: &laquo;ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟ مېنىڭ بۇيرىقىمغا خىلاپلىق قىلدىڭمۇ؟&raquo;';
$t[93] = 'مۇسا ئېيتتى: &laquo;ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟ مېنىڭ بۇيرىقىمغا خىلاپلىق قىلدىڭمۇ؟&raquo;';
$t[94] = 'ھارۇن ئېيتتى: &laquo;ئى قېرىندىشىم! مېنىڭ ساقىلىمنى ۋە چېچىمنى تارتمىغىن، مەن ھەقىقەتەن سېنىڭ، ئىسرائىل ئەۋلادىنى بۆلۈۋېتىپسەن، سۆزۈمگە دىققەت قىلماپسەن، دېيىشىڭدىن قورقتۇم&raquo;';
$t[95] = 'مۇسا ئېيتتى: &laquo;ئى سامىرى! سەن نېمە بولدۇڭ؟ (يەنى مۇنداق يامان ئىشنى قىلىشىڭدىن غەرىزىڭ نېمە؟)&raquo;';
$t[96] = 'سامىرى ئېيتتى: &laquo;مەن ئۇلار كۆرمىگەننى كۆردۈم، ئەلچى (يەنى جىبرىئىل) نىڭ ئىزىدىن بىر چاڭگال توپىنى ئالدىم - دە، ئۇنى (موزايغا) چاچتىم، شۇنىڭ بىلەن ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقتى)، شۇنىڭدەك (بۇنى) ماڭا نەپسىم چىرايلىق كۆرسەتتى&raquo;';
$t[97] = 'مۇسا (سامىرىغا) ئېيتتى: &laquo;سەن بارغىن! سەن ئۆمۈرۋايەت (كۆرگەنلا كىشىگە) ماڭا يېقىنلاشماڭلار! دېگەيسەن (يەنى سېنىڭ كىشىگە يېقىنلاشماسلىقىڭ، كىشىنىڭ ساڭا يېقىنلاشماسلىقى بۇ دۇنيادا ساڭا بېرىلگەن جازادۇر)، شۈبھىسىزكى (ئاخىرەتتە جازالىنىدىغان) مۇئەييەن بىر ۋاقتىڭ بار، اﷲ ساڭا قىلغان بۇ ۋەدىسىگە خىلاپلىق قىلمايدۇ، سەن ھامان چوقۇنۇپ كېلىۋاتقان ئىلاھىڭغا قارىغىن، بىز ئۇنى چوقۇم (ئوتتا) كۆيدۈرىمىز، ئاندىن ئۇنىڭ (كۈلىنى) دېڭىزغا چېچىۋېتىمىز&raquo;';
$t[98] = 'سىلەرنىڭ ئىلاھىڭلار بىر اﷲ دۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەر نەرسىنى تولۇق بىلگۈچىدۇر';
$t[99] = '(ئى مۇھەممەد!) شۇنىڭدەك، ساڭا ئۆتكەنكىلەرنىڭ بەزى خەۋەرلىرىنى بايان قىلىمىز، ساڭا ئۆز تەرىپىمىزدىن قۇرئاننى ئاتا قىلدۇق';
$t[100] = 'كىمكى قۇرئاندىن يۈز ئۆرۈيدىكەن، ئۇنىڭغا قىيامەت كۈنى چوڭ گۇناھتىن ئېغىر يۈك يۈكلىنىدۇ';
$t[101] = 'ئۇ گۇناھى ئۈچۈن بېرىلگەن ئازابنى مەڭگۈ تارتىدۇ) قىيامەت كۈنى ئۇلارغا يۈكلەنگەن بۇ يۈك نېمىدېگەن يامان!';
$t[102] = 'بۇ سۇر چىلىنىدىغان كۈندۇر، بۇ كۈندە (شەكلى ئۆزگەرگەن) كۆك كۆزلۈك (قارا يۈزلۈك) گۇناھكارلارنى يىغىمىز';
$t[103] = '(قىيامەتنىڭ دەھشىتىنى كۆرۈپ دۇنيادا تۇرغان ۋاقتى قىسقا بىلىنگەنلىكتىن) (ئۇلار) &laquo;(دۇنيادا) پەقەت ئون كۈنلا تۇردۇڭلار&raquo; دېيىشىپ پىچىرلىشىدۇ';
$t[104] = 'ئۇلارنىڭ دەيدىغانلىرىنى بىز ئوبدان بىلىمىز، ئەينى زاماندا ئۇلارنىڭ ئەڭ توغرا سۆزلەيدىغىنى: &laquo;سىلەر پەقەت بىر كۈن تۇردۇڭلار&raquo; دەيدۇ';
$t[105] = 'ئۇلار سەندىن تاغلار توغرۇلۇق (يەنى تاغنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقى توغرۇلۇق) سورايدۇ، ئېيتقىنكى، &laquo;پەرۋەردىگارىم ئۇلارنى كۈكۈم تالقان قىلىپ (ئاندىن شامالنى ئەۋەتىپ ئۇنى سورۇيدۇ)';
$t[106] = 'ئۇلارنى تۈزلەڭلىككە ئايلاندۇرىدۇ';
$t[107] = 'ئۇ يەرلەردە نە چۇڭقۇرلۇقنى، نە ئېگىزلىكنى كۆرمەيسەن&raquo;';
$t[108] = 'بۇ كۈندە (كىشىلەر اﷲ نىڭ مەھشەرگاھقا) دەۋەت قىلغۇچىسىغا ئەگىشىدۇ، قىڭغىر كەتمەيدۇ، مەرھەمەتلىك اﷲ نىڭ ئۇلۇغلۇقى ۋە ھەيۋىتىدىن قورقۇپ، بارلىق ئاۋازلار بېسىقىدۇ، پىچىرلاشقاندىن باشقىنى ئاڭلىمايسەن';
$t[109] = 'بۇ كۈندە مەرھەمەتلىك اﷲ ئىزىن بەرگەن ۋە سۆزىدىن اﷲ رازى بولغان ئادەمنىڭ شاپائىتىدىن باشقا ھېچقانداق شاپائەت پايدا بەرمەيدۇ';
$t[110] = 'اﷲ ئۇلارنىڭ ئالدىدىكىنى ۋە كەينىدىكىنى بىلىدۇ، ئۇلار بولسا ئۇنى بىلمەيدۇ';
$t[111] = 'گۇناھكارلار مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچى (مەڭگۈ) ھايات اﷲ قا ئېگىلىدۇ، اﷲ قا شېرىك كەلتۈرگەنلەر شەكسىز زىيان تارتىدۇ';
$t[112] = 'مۆمىن تۇرۇپ ياخشى ئەمەللەرنى قىلغانلار يامانلىقلىرىنىڭ ئېشىپ كېتىشىدىن ۋە ياخشىلىقلىرىنىڭ كېمىيىپ كېتىشىدىن قورقمايدۇ';
$t[113] = 'ئۇلارنىڭ تەقۋادار بولۇشلىرى ئۈچۈن ياكى ئۇلارنى ۋەز - نەسىھەتكە ئىگە قىلىش ئۈچۈن، ساڭا ئەرەبچە قۇرئاننى نازىل قىلدۇق ۋە ئۇنىڭدا تۈرلۈك ئاگاھلاندۇرۇشلارنى بايان قىلدۇق';
$t[114] = 'اﷲ (زاتى، سۈپىتى جەھەتتە پەتەن مەخلۇقاتتىن) ئۈستۈندۇر، ھەق پادىشاھتۇر، (ئى مۇھەممەد!) ساڭا قۇرئاننىڭ ۋەھيىسى تاماملىنىشتىن بۇرۇن ئوقۇشقا ئالدىراپ كەتمىگىن، (يەنى جىبرىئىل ساڭا قۇرئاننى ئوقۇپ بېرىۋاتقاندا، بىللە ئوقۇشقا ئالدىراپ كەتمەي، ئۇ تىلاۋىتىدىن فارىغ بولغۇچە تىڭشاپ تۇرغىن). &laquo;ئى پەرۋەردىگارىم! ئىلمىمنى زىيادە قىلغىن!&raquo; دېگىن';
$t[115] = 'بىز ئىلگىرى ئادەمگە (دەرەخنىڭ مېۋىسىنى يېمەسلىكنى) تەۋسىيە قىلدۇق، ئۇ (بۇ تەۋسىيەنى) يادىدىن چىقاردى، ئۇنىڭدا ھېچقانداق چىڭ ئىرادە كۆرمىدۇق';
$t[116] = 'ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: &laquo;ئادەمگە (يەنى ئادەم ئەلەيھىسسالامغا) سەجدە قىلىڭلار&raquo; دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى، ئۇ (سەجدە قىلىشتىن) باش تارتتى';
$t[117] = 'بىز دېدۇق: &laquo;ئى ئادەم! بۇ ھەقىقەتەن سېنىڭ دۈشمىنىڭدۇر، خوتۇنۇڭنىڭ دۈشمىنىدۇر ئۇ سىلەر ئىككىڭلارنىڭ جەننەتتىن چىقىپ كېتىشىڭلارغا سەۋەبچى بولۇپ قالمىسۇن، (ئۇ چاغدا) سىلەر جاپا مۇشەققەتتە قالىسىلەر';
$t[118] = 'جەننەتتە سەن ھەقىقەتەن ئاچ قالمايسەن ۋە يالىڭاچ قالمايسەن';
$t[119] = 'ھەمدە ئۇنىڭدا ھەقىقەتەن ئۇسساپ كەتمەيسەن، ئىسسىپ كەتمەيسەن&raquo;';
$t[120] = 'شەيتان ئۇنىڭغا ۋەسۋەسە قىلىپ: &laquo;ئى ئادەم! ساڭا (مەن يېگەن ئادەم) ئۆلمەيدىغان دەرەخنى ۋە زاۋال تاپماس پادىشاھلىقنى كۆرسىتىپ قويايمۇ؟&raquo; دېدى';
$t[121] = 'ئۇلار ئۇ دەرەخنىڭ مېۋىسىنى يېدى، شۇنىڭ بىلەن ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەت (دەرەخلىرىنىڭ) يوپۇرماقلىرى بىلەن سەترى ئەۋرەت قىلىشقا كىرىشتى، ئادەم پەرۋەردىگارىنىڭ (ئەمرىگە) خىلاپلىق قىلدى، شۇنىڭ بىلەن ئۇ ئازدى';
$t[122] = 'ئاندىن پەرۋەردىگارى ئۇنى تاللىدى، ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى ۋە ئۇنى ھىدايەت قىلدى';
$t[123] = 'اﷲ (ئادەم بىلەن ھەۋۋاغا) ئېيتتى: &laquo;سىلەر ھەممىڭلار جەننەتتىن بەزىڭلار بەزىڭلارغا دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەگەر سىلەرگە مېنىڭ تەرىپىمدىن ھىدايەت كەلسە، كىمكى مېنىڭ ھىدايىتىمگە ئەگەشسە، ئۇ ئازمايدۇ ۋە ئاخىرەتتە شەقىي بولمايدۇ';
$t[124] = 'كىمكى مېنىڭ زىكرىمدىن يۈز ئۆرۈيدىكەن، ئۇنىڭ ھاياتى تار (يەنى خاتىرجەمسىز) بولىدۇ، قىيامەت كۈنى ئۇنى بىز كور قوپۇرىمىز&raquo;';
$t[125] = 'ئۇ: &laquo;ئى پەرۋەردىگارىم! نېمىشقا مېنى كور قوپۇردۇڭ، ۋاھالەنكى، (دۇنيادىكى چاغدا) مېنىڭ كۆزۈم كۆرەتتىغۇ؟&raquo; دەيدۇ';
$t[126] = 'اﷲ ئېيتىدۇ: &laquo;شۇنىڭدەك ساڭا (روشەن) ئايەتلىرىمىز كەلدى، سەن ئۇلارنى تەرك ئەتتىڭ، سەن بۈگۈن شۇنىڭغا ئوخشاش تەرك ئېتىلىسەن&raquo;';
$t[127] = 'ھەددىدىن ئاشقان ۋە پەرۋەردىگارىنى ئايەتلىرىگە ئىمان ئېيتمىغانلارغا شۇنىڭغا ئوخشاش جازا بېرىمىز، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر، تېخىمۇ باقىيدۇر';
$t[128] = 'ئۇلاردىن ئىلگىرى ئۆتكەن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ ئۇلار شۇ ئۈممەتلەرنىڭ ماكانلىرىدا مېڭىپ يۈرىدۇ، ئەقىل ئىگىلىرى ئۈچۈن ئۇنىڭدا ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار';
$t[129] = 'ئەگەر پەرۋەردىگارىڭنىڭ (ئازابنى ئۇلاردىن كېچىكتۈرۈشكە) قىلىۋەتكەن ھۆكمى ۋە ئالدىنئالا بەلگىلەپ قويغان ۋاقتى بولمىسا ئىدى، ئۇ چاغدا (ئۇلارنىڭ ھالاك بولۇشى) چوقۇم ئىدى';
$t[130] = 'ئۇلارنىڭ سۆزلىرىگە سەۋرى قىلغىن، مەمنۇن بولۇشۇڭ ئۈچۈن، كۈن چىقىشتىن ئىلگىرى، كۈن پېتىشتىن بۇرۇن، كېچە ۋاقىتلىرىدا ۋە كۈندۈزنىڭ دەسلەپكى ۋە ئاخىرقى ۋاقىتلىرىدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن (يەنى ناماز ئوقۇغىن) ۋە ھەمدە ئېيتقىن';
$t[131] = 'بىز (كۇففارلاردىن) تۈرلۈك جامائەنى بەھرىمەن قىلغان دۇنيانىڭ نېمەتلىرى ۋە زىبۇزىننەتلىرىگە كۆز سالمىغىن، بۇنىڭ بىلەن ئۇلارنى سىنايمىز، پەرۋەردىگارىڭنىڭ رىزقى (يەنى ساۋابى بۇ پانىي نېمەتتىن) ياخشىدۇر ۋە ئەڭ باقىيدۇر';
$t[132] = '(ئى مۇھەممەد!) ئائىلەڭدىكىلەرنى (ۋە ئۈممىتىڭنى) نامازغا بۇيرۇغىن، ئۆزۈڭمۇ ئۇنى ئادا قىلىشقا چىداملىق بولغىن، سەندىن بىز رىزىق تەلەپ قىلمايمىز. ساڭا بىز رىزىق بېرىمىز، ياخشى ئاقىۋەت پەقەت تەقۋادارلارغا خاستۇر';
$t[133] = 'ئۇلار (يەنى مۇشرىكلار): &laquo;(مۇھەممەد) پەرۋەردىگارى تەرىپىدىن بىزگە بىرەر مۆجىزە كەلتۈرسىچۇ!&raquo; دەيدۇ، ئۇلارغا بۇرۇنقى ئۈممەتلەرنىڭ خەۋەرلىرىنى ئۆز ئىچىگە ئالغان قۇرئان كەلمىدىمۇ؟';
$t[134] = 'ئەگەر بىز ئۇلارنى ئۇنىڭدىن (يەنى مۇھەممەد ئەلەيھىسسالامنى ئەۋەتىشتىن) ئىلگىرى ھالاك قىلغان بولساق، ئۇلار چوقۇم: &laquo;نېمىشقا بىزگە بىرەر پەيغەمبەر ئەۋەتمىدىڭ؟ بىز ۋە رەسۋا بولۇشتىن ئىلگىرى سېنىڭ ئايەتلىرىڭگە ئەگەشتۇق&raquo; دەيتتى';
$t[135] = 'ئېيتقىنكى، ھەممە (سىلەرنىڭ ۋە بىزنىڭ ئىشلىرىمىزنى ئاقىۋىتىنى) كۈتمەكتە. سىلەرمۇ كۈتۈڭلار، كىملەرنىڭ توغرا يول ئىگىلىرى ئىكەنلىكىنى ۋە كىملەرنىڭ ھىدايەت تاپقانلىقىنى (ئۇزاققا قالماي) بىلىسىلەر&raquo;';
?>