$t[32] = 'ئىش مانا شۇنداق، كىمكى دىنىي ئىشلارنى (جۈملىدىن ھەجنىڭ ئەمەللىرىنى قۇربانلىقلارنى) ئۇلۇغلايدىكەن، بۇ، دىللارنىڭ تەقۋادارلىقىدىندۇر';
$t[33] = 'مۇئەييەن مۇددەتكىچە (يەنى بوغۇزلايدىغان ۋاقىتقىچە قۇربانلىق ماللىرىدىن (سېغىپ، نەسىللەندۈرۈپ، مىنىپ) پايدىلىنىسىلەر، ئاندىن ئۇنى بوغۇزلاشقا تېگىشلىك جاي بەيتۇللاھنىڭ يېنىدۇر (يەنى (يەنى ھەرەمدۇر)';
$t[34] = 'ھەر ئۈممەت. اﷲ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋىلارنى (شۈكۈر قىلىش يۈزىسىدىن قۇربانلىق قىلغانلىرىدا) اﷲ نىڭ ئىسمىنى ئېيتسۇن دەپ، ئۇلارغا قۇربانلىقنى بەلگىلىدۇق، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭغا بويسۇنۇڭلار، ئىتائەتمەنلەرگە خۇش خەۋەر بەرگىن';
$t[35] = 'ئۇلار شۇنداق كىشىلەركى اﷲ ياد ئېتىلسە، ئۇلارنىڭ دىللىرى قورقۇپ (تىترەيدۇ)، ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلىدۇ، ناماز ئۆتەيدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) بېرىدۇ';
$t[36] = '(كەبىگە ئېلىپ بېرىلىدىغان) تۆگىنى اﷲ نىڭ (دىننىڭ) ئالامەتلىرىدىن (يەنى ھەجنىڭ ئەھكاملىرىدىن) قىلدۇق، ئۇلاردا سىلەرگە نۇرغۇن پايدا بار، ئۇلار قاتار قىلىپ تۇرغۇزۇپ (يەنى ئۇلارنىڭ ئالدى سول پۇتىنى باغلاپ، ئۈچ پۇت بىلەن تۇرغۇزۇپ بوغۇزلىغىنىڭلاردا) اﷲ نىڭ ئىسمىنى تىلغا ئېلىڭلار (يەنى بىسمىللا دەڭلار)، ئۇلار بوغۇزلىنىپ جېنى چىققاندا، ئۇلارنى يەڭلار، قانائەتچان موھتاجلارغا ۋە سائىللارغا بېرىڭلار، سىلەرنى شۈكۈر قىلسۇن دەپ ئۇ تۆگىلەرنى سىلەرگە بويسۇندۇرۇپ بەردۇق';
$t[37] = 'اﷲ قا ئۇلارنىڭ گۆشلىرى ۋە قانلىرى يېتىپ بارمايدۇ، اﷲ قا يېتىدىغىنى پەقەت سىلەرنىڭ تەقۋادارلىقىڭلاردۇر، اﷲ نىڭ سىلەرنىڭ ئۆزىنىڭ ئەھكاملىرىغا يېتەكلىگەنلىكىنى ئۇلۇغلىشىڭلار ئۈچۈن، اﷲ ئۇلارنى سىلەرگە ئاشۇنداق بويسۇندۇرۇپ بەردى. ياخشى ئىش قىلغۇچىلارغا (ئاخىرەتتە بەختكە ئېرىشىدىغانلىقى بىلەن) خۇش خەۋەر بەرگىن';
$t[38] = 'اﷲ مۆمىنلەرنى چوقۇم قوغدايدۇ، اﷲ ھەر قانداق خىيانەتچى، ناشۈكۈر بەندىنى دوست تۇتمايدۇ';
$t[39] = 'ھۇجۇم قىلىنغۇچىلارغا، زۇلۇمغا ئۇچرىغانلىقلىرى ئۈچۈن، (قارشىلىق كۆرسىتىشكە) رۇخسەت قىلىنىدى، اﷲ ئۇلارغا ياردەم بېرىشكە ئەلۋەتتە قادىر';
$t[40] = '(ئۇلار) پەقەت پەرۋەردىگارىمىز اﷲ دېگەنلىكلىرى ئۈچۈنلا ئۆز يۇرتلىرىدىن ناھەق ھەيدەپ چىقىرىلدى، ئەگەر اﷲ ئىنسانلارنى بىر - بىرىگە قارشىلىق كۆرسەتكۈزمىگەن بولسا، راھىبلارنىڭ ئىبادەتخانىلىرى، چېركاۋلار، يەھۇدىيلارنىڭ ئىبادەتخانىلىرى ۋە اﷲ نىڭ نامى كۆپ يادلىنىدىغان مەسجىدلەر ئەلۋەتتە ۋەيران قىلىناتتى، كىمكى اﷲ نىڭ دىنىغا ياردەم بېرىدىكەن، ئەلۋەتتە اﷲ ئۇنىڭغا ياردەم بېرىدۇ، اﷲ ئەلۋەتتە كۈچلۈكتۇر، غالىبتۇر';
$t[41] = 'اﷲ نىڭ ياردىمىگە ئېرىشىشكە ھەقلىق بولغانلار) شۇنداق كىشىلەردۇركى، ئەگەر ئۇلارنى يەر يۈزىدە ئۈستۈنلۈككە ئىگە قىلساق، نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، ئىشلارنىڭ ئاقىۋىتى اﷲ قا مەنسۇپتۇر (يەنى ھەممە ئىشلار ئاخىرەتتە اﷲ قا قايتىدۇ)';
$t[42] = 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن';
$t[43] = 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن';
$t[44] = 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن';
$t[45] = 'نۇرغۇن شەھەر ئاھالىلىرى زالىم بولغانلىقلىرى ئۈچۈن (شەھەرنىڭ ئۆيلىرىنىڭ) ئۆگزىلىرى ئۆرۈلۈپ، ئۈستىگە تاملىرى يىقىلغان نۇرغۇن تاشلاندۇق قۇدۇقلار ۋە ئېگىز ئىمارەتلەر (ئاھالىسىز) قالغان';
$t[46] = 'ئۇلار زېمىندا سەير قىلمىدىمۇ؟ شۇنىڭ بىلەن ئۇلار (يەنى سەير قىلىپ) چۈشىنىدىغان دىللارغا، ياكى ئاڭلايدىغان قۇلاقلارغا ئىگە بولمىدىمۇ؟ ھەقىقەتەن كۆزلەرلا كور بولمايدۇ، لېكىن كۆكرەكلەردىكى قەلبلەر كور بولىدۇ (يەنى ھەقىقىي كورلۇق كۆزنىڭ كورلۇقى ئەمەس، دىلنىڭ كورلۇقىدۇر، كور ئادەم ئىبرەت ئالمايدۇ ۋە چۈشەنمەيدۇ)';
$t[47] = '(ئى مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) (مەسخىرە قىلىش يۈزىسىدىن سەندىن ئازابنىڭ تېز نازىل بولۇشنى تەلەپ قىلىدۇ (ئازاب چوقۇم نازىل بولىدۇ، لېكىن ئۇنىڭ نازىل بولۇشنىڭ مۇئەييەن ۋاقتى - سائىتى بولىدۇ)، اﷲ ۋەدىسىگە ھەرگىز خىلاپلىق قىلمايدۇ اﷲ بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ھەقىقەتەن پەرۋەردىگارىنىڭ دەرگاھىدىكى بىر كۈن سىلەر سانايدىغان مىڭ يىلچىلىكتۇر';
$t[48] = 'نۇرغۇن شەھەر ئاھالىلىرى زالىم تۇرۇقلۇق، ئۇلارغا مەن مۆھلەت بەردىم (ئۇلار بۇ مۆھلەتكە مەغرۇر بولۇپ كەتتى)، ئاندىن (يەنى ئۇلارغا ئۇزاق مۇددەت مۆھلەت بەرگىنىمدىن كېيىن) ئۇلارنى جازالىدىم، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر (يەنى ئاخىر ھەممىسى ماڭا كېلىدۇ';
$t[49] = '(ئى مۇھەممەد! اﷲ نىڭ ئازابىنىڭ تېز نازىل بولۇشىنى تەلەپ قىلغۇچىلارغا) ئېيتقىنكى، &laquo;ئى ئىنسانلار! سىلەرگە مەن ھەقىقەتەن ئاشكارا ئاگاھلاندۇرغۇچىمەن&raquo;';
$t[50] = 'ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتكە) ئېرىشىدۇ';
$t[51] = 'بىزنى ئاجىز بىلىپ ئايەتلىرىمىزنى يوققا چىقىرىش ئۈچۈن تىرىشقۇچىلار - ئەنە شۇلار ئەھلى دوزاختۇر';
$t[52] = 'بىز سەندىن ئەۋەتكەن قايسىبىر رەسۇل، قايسىبىر پەيغەمبەر بولمىسۇن، ئۇ (اﷲ نازىل قىلغان ئايەتلەرنى) ئوقۇغان چاغدا، شەيتان ھامان ئۇنىڭ قىرائىتىگە (شۈبھە) سالىدۇ. اﷲ شەيتاننىڭ سالغان شۈبھىسىنى بەربات قىلغاندىن كېيىن ئۆزىنىڭ ئايەتلىرىنى مۇستەھكەم قىلىدۇ. اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[53] = '(اﷲ تائالانىڭ مۇنداق قىلىشى) دىللىرىدا (مۇناپىقلىق) ئىللىتى بارلارنى، دىللىرى قەساۋەتلىشىپ كەتكەنلەرنى (يەنى ئەبۇجەھل، نەزر، ئۆتبەگە ئوخشاش اﷲ نىڭ زىكرىگە دىلى ئېرىمەس كۇففارلارنى) شەيتاننىڭ سالغان شۈبھىسى بىلەن سىناش ئۈچۈندۇر، زالىملار (يەنى يۇقىرىدىكى مۇناپىقلار، مۇشرىكلار) ھەقىقەتەن (اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە) قاتتىق ئاداۋەتتىدۇر';
$t[54] = '(اﷲ تائالانىڭ مۇنداق قىلىشى) ئىلىم ئەھلىلىرىنىڭ قۇرئاننىڭ پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى بىلىشلىرى، شۇنىڭ ئۈچۈندۇر. اﷲ مۆمىنلەرنى ھەقىقەتەن توغرا يولغا باشلىغۇچىدۇر';
$t[55] = 'كاپىرلار تاكى ئۇلارغا قىيامەت ئۇشتۇمتۇت كەلگۈچە ياكى ئۇلارغا قىيامەت كۈنىنىڭ ئازابى يەتكۈچە قۇرئاندىن ھامان شەكلىنىدۇ';
$t[56] = 'بۇ كۈندە پادىشاھلىق (يالغۇز) اﷲ قا خاستۇر، اﷲ بەندىلەر ئارىسىدا (ئادىل) ھۆكۈم چىقىرىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار (تۈرلۈك) نازۇنېمەتلەر بار جەننەتلەردە بولىدۇ';
$t[57] = 'كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار (دوزاختا) خار قىلغۇچى ئازابقا دۇچار بولىدۇ';
$t[58] = 'اﷲ نىڭ يولىدا ھىجرەت قىلغانلارغا، ئاندىن (جىھاد قىلىپ) ئۆلتۈرۈلگەنلەرگە ياكى (كېسەل بىلەن) ئۆلگەنلەرگە اﷲ چوقۇم ياخشى رىزىق (يەنى جەننەتنىڭ مەڭگۈلۈك نېمەتلىرىنى) ئاتا قىلىدۇ. رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسى ھەقىقەتەن يالغۇز اﷲ دۇر';
$t[59] = 'اﷲ ئۇلارنى ئەلۋەتتە ئۇلار مەمنۇن بولىدىغان جايغا (يەنى جەننەتكە) كىرگۈزىدۇ، اﷲ ھەقىقەتەن (ئۇلارنى مەمنۇن قىلىدىغان نەرسىلەرنى) بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئۇلارنىڭ دۈشمەنلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)';
$t[60] = 'ئىش مانا شۇنداق، كىمكى قانچىلىك زۇلۇمغا ئۇچراپ شۇنچىلىك ئىنتىقامىنى ئالغان بولسا، ئاندىن يەنە زۇلۇمغا ئۇچرىسا، اﷲ ئۇنىڭغا چوقۇم ياردەم بېرىدۇ، اﷲ ھەقىقەتەن ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر';
$t[61] = '(شۇ نەرسە اﷲ نىڭ قۇدرىتىنىڭ ئالامەتلىرىدىندۇركى) اﷲ كېچىنى كۈندۈزگە، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە - كۈندۈزنىڭ ئۇزۇن - قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر';
$t[62] = 'بۇ (بۇ) (قۇدرەت) شۇنىڭ بۇنىڭ ئۈچۈندۇركى، اﷲ ھەقتۇر، (مۇشرىكلارنىڭ) اﷲ نى قويۇپ ئىبادەت قىلىدىغان نەرسىلىرى (يەنى بۇتلىرى) باتىلدۇر، اﷲ (ھەممە نەرسىدىن) ئۈستۈندۇر، بۈيۈكتۇر';
$t[63] = 'اﷲ نىڭ بۇلۇتتىن يامغۇر ياغدۇرۇپ بېرىدىغانلىقىنى، شۇنىڭ بىلەن زېمىننى يېشىلزارلىققا ئايلاندۇرىدىغانلىقىنى كۆرمىدىڭمۇ؟ اﷲ ھەقىقەتەن ناھايىتى مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر';
$t[64] = 'ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ ھەقىقەتەن ھەممىدىن بىھاجەتتۇر، ھەمدۇساناغا لايىقتۇر';
$t[65] = 'اﷲ نىڭ يەر يۈزىدىكى نەرسىلەرنى، ئۆزىنىڭ ئەمرى بويىچە دېڭىزدا يۈرۈۋاتقان كېمىلەرنى سىلەرگە تويۇندۇرۇپ بەرگەنلىكىنى كۆرمىدىڭمۇ؟ (ئاسماننىڭ زېمىنغا چۈشۈپ كەتمەسلىكى ئۈچۈن، اﷲ ئاسماننى توختىتىپ تۇرىدۇ، پەقەت اﷲ نىڭ ئىزنى بىلەن (ئاسماننىڭ قىيامەت بولغان چاغدا) چۈشۈپ كېتىشى بۇنىڭدىن مۇستەسنا. اﷲ ئىنسانلارغا ھەقىقەتەن ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر';
$t[66] = 'اﷲ سىلەرگە ھاياتلىق بېرىدۇ، ئاندىن سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، ئىنسان ھەقىقەتەن (اﷲ نىڭ نېمەتلىرىگە) ناشۈكۈرلۈك قىلغۇچىدۇر';
$t[67] = 'ھەر ئۈممەت ئۈچۈن بىر شەرىئەت بېكىتتۇق، ئۇلار شۇ شەرىئەتكە ئەمەل قىلىدۇ، (مۇشرىكلار) شەرىئەت ئىشىدا سەن بىلەن جېدەللەشمىسۇن، كىشىلەرنى پەرۋەردىگارىڭنىڭ (ئىبادىتىگە) دەۋەت قىلغىن، سەن ھەقىقەتەن شەكسىز توغرا يولدىدۇرسەن';
$t[68] = 'ئەگەر ئۇلار سەن بىلەن جېدەللەشسە، ئېيتقىنكى، ئېيتقىنكى &laquo;اﷲ سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ';
$t[69] = 'اﷲ قىيامەت كۈنى سىلەر ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىدۇ&raquo;';
$t[70] = 'بىلمەمسەنكى، اﷲ ئاسمان، زېمىندىكى شەيئىلەرنى بىلىپ تۇرىدۇ، بۇ ھەقىقەتەن لەۋھۇلمەھپۇزدا (يېزىلغان)، بۇ (يەنى بىلىش) ھەقىقەتەن اﷲ قا ئاسان';
$t[71] = 'ئۇلار (يەنى قۇرەيش كۇففارلىرى) اﷲ نى قويۇپ، (ئىبادەت قىلىشنىڭ دۇرۇسلۇقىغا) اﷲ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرگە ۋە ئۆزلىرى بىلمەيدىغان نەرسىلەرگە (يەنى بوتلارغا) چوقۇنىدۇ، زالىملارغا ھېچقانداق مەدەتكار يوق';
$t[72] = 'كاپىرلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلسە، ئۇلارنىڭ چېھرىلىرىدە (قوشۇمىسى تۈرۈلگەنلىكتىن) ئىنكار قىلغانلىقىنى كۆرۈسەن، ئۇلارنىڭ بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بەرگۈچىلەرگە ھۇجۇم قىلغىلى تاس قىلغانلىقىنى كۆرۈسەن، ئېيتقىنكى، &laquo;سىلەرگە مەن بۇنىڭدىنمۇ يامانراقىنى ئېيتىپ بېرەيمۇ؟ ئۇ دوزاختۇر. اﷲ ئۇنىڭ كاپىرلارغا ۋەدە قىلدى، ئۇلار قايتىپ بارىدىغان جاي نېمىدېگەن يامان!&raquo;';
$t[73] = 'ئى ئىنسانلار! (يەنى مۇشرىكلار جامائەسى!) بىر مىسال كەلتۈرۈلىدۇ، ئۇنىڭغا قۇلاق سېلىڭلار، شۈبھىسىزكى، سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلارنىڭ ھەممىسى يىغىلغان تەقدىردىمۇ بىر چىۋىننى يارىتالمايدۇ (ئەقلى بار ئادەم قانداقمۇ مۇشۇنداق بۇتلارنى مەبۇد قىلىۋېلىپ ئۇلارغا چوقۇنىدۇ!)، چىۋىن ئۇلارنىڭ بەدىنىدىن بىر نەرسىنى (يەنى بۇتقا سۈركەپ قويۇلغان خۇش پۇراق بىر نەرسىنى) ئېلىپ قاچسا، ئۇنى چىۋىندىن تارتىپ ئالالمايدۇ، بۇتمۇ ۋە (ئۇنىڭغا) چوقۇنغۇچىمۇ ئاجىزدۇر';
$t[74] = 'ئۇلار (ئەرزىمەس بۇتلارنى اﷲ قا شېرىك قىلىۋېلىپ) اﷲ نى تېگىشلىك رەۋىشتە ئۇلۇغلاشمىدى، شۈبھىسىزكى، اﷲ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر';
$t[75] = 'اﷲ پەرىشتىلەردىنمۇ (پەيغەمبەرلىرىگە ۋەھيى يەتكۈزۈش ئۈچۈن)، ئەلچىلەرنى تاللايدۇ، اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر';
$t[76] = 'اﷲ ئۇلارنىڭ ئالدىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ئەھۋالىنى ۋە كەلگۈسىدىكى ئىشلىرىنى) بىلىدۇ. (بەندىلەرنىڭ) ئىشلىرىنىڭ ھەممىسى اﷲ قا قايتۇرۇلىدۇ (اﷲ ئۇلارغا يا مۇكاپات، يا جازا بېرىدۇ)';
$t[77] = 'ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن رۇكۇ قىلىڭلار، سەجدە قىلىڭلار (يەنى تەزەررۇ بىلەن ناماز ئوقۇڭلار)، (يالغۇز) پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار، ياخشى ئىشلارنى (يەنى خىش - ئەقرىبالارغا سىلەر - رەھىم قىلىش، يېتىم - يېسىرلەرنىڭ بېشىنى سىلاش، كېچىسى تەھەججۇد نامىزى ئۆتەش قاتارلىق ئىشلارنى) قىلىڭلار';
$t[78] = 'اﷲ نىڭ يولىدا (پۇل - مېلىڭلار بىلەن، جېنىڭلار بىلەن) تاقىتىڭلارنىڭ يېتىشىچە جىھاد قىلىڭلار، اﷲ (دىنغا پەيغەمبەرنى سىلەرگە خاس قىلدى). سىلەرگە دىندا ھېچقانداق مۈشكۈللۈكنى قىلمىدى (سىلەرنى سىلەر تاقەت قىلالايدىغان ئىشلارنى قىلىشقا تەكلىپ قىلمىدى). (بۇ) ئاتاڭلار ئىبراھىمنىڭ دىنىدۇر (بۇ توغرا دىن بولغانلىقى ئۈچۈن، ئۇنى مەھكەم ئۇچلاڭلار)، اﷲ سىلەرنى ئىلگىرى (يەنى قۇرئاندىن ئىلگىرىكى كىتابلاردا) مۇسۇلمان دەپ ئاتىدۇ. قۇرئاندىمۇ شۇنداق ئاتىدۇ. (اﷲ) پەيغەمبەرنى سىلەرگە گۇۋاھ بولۇشقا ۋە سىلەرنى كىشىلەرگە گۇۋاھ بولۇشقا (تاللىدى)، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا يېپىشىڭلار، اﷲ سىلەرنىڭ ئىگەڭلاردۇر، اﷲ نېمىدېگەن ياخشى ئىگە! نېمىدېگەن ياخشى مەدەتكار!';
?>