$t[32] = 'چوڭچىلىق قىلغانلار بوزەك قىلىنغانلارغا: &laquo;سىلەرگە ھىدايەت كەلگەندىن كېيىن بىز سىلەرنى ئۇنىڭدىن توستۇقمۇ؟ ھەرگىز توسمىدۇق، سىلەر ئۆزۈڭلار گۇناھ قىلغۇچى بولدۇڭلار&raquo; دەيدۇ';
$t[33] = 'بوزەك قىلىنغانلار چوڭچىلىق قىلغانلارغا: &laquo;ھەرگىز ئۇنداق ئەمەس، (سىلەرنىڭ بىزگە) كېچە - كۈندۈز قىلغان ھىيلە - مىكرىڭلار (بىزنى ئىماندىن توستى)، ئۆز ۋاقتىدا سىلەر بىزنى اﷲ نى ئىنكار قىلىشقا ۋە ئۇنىڭغا شېرىك كەلتۈرۈشكە بۇيرۇيتتۇڭلار&raquo; دەيدۇ. ئازابنى كۆرگەن چاغدا، ئۇلار (ھەر ئىككى گۇرۇھ ئىمان ئېيتمىغانلىقلىرىغا) ئىچىدە پۇشايمان قىلىدۇ، كاپىرلارنىڭ بويۇنلىرىغا تاقاقلارنى سالىمىز، ئۇلارغا پەقەت قىلمىشلىرىنىڭ جازاسى بېرىلىدۇ';
$t[34] = 'قايسى شەھەرگە بولمىسۇن، بىرەر ئاگاھلاندۇرغۇچى ئەۋەتسەكلا ئۇنىڭ دۆلەتمەن ئادەملىرى: &laquo;شۈبھىسىزكى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ئىنكار قىلىمىز&raquo; دېدى';
$t[35] = 'ئۇلار يەنە: &laquo;بىزنىڭ ماللىرىمىز ۋە بالىلىرىمىز ئەڭ كۆپ، بىز ئازابلانمايمىز&raquo; دېدى';
$t[36] = 'ئېيتقىنكى، &laquo;ھەقىقەتەن، پەرۋەردىگارىم كىمنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (كىمنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) ئۇقمايدۇ&raquo;';
$t[37] = 'ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى بىزگە يېقىنلاشتۇرالمايدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا قىلغان ئەمەللىرى ئۈچۈن ھەسسىلەپ ساۋاب بېرىلىدۇ، ئۇلار (جەننەتنىڭ) ئېسىل ئۆيلىرىدە (ھەر قانداق كۆڭۈلسىزلىكتىن) ئەمىن بولغان ھالدا تۇرىدۇ';
$t[38] = 'بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار ئازابقا دۇچار قىلىنغۇچىلاردۇر';
$t[39] = 'ئېيتقىنكى، &laquo;ھەقىقەتەن، پەرۋەردىگارىم قايسى بەندىسىنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، (اﷲ نىڭ يولىدا) بەرگەن نەرسەڭلارنىڭ ئورنىنى اﷲ تولدۇرۇپ بېرىدۇ، ئۇ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر&raquo;';
$t[40] = 'ئۇ كۈندە اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، ئاندىن پەرىشتىلەرگە: &laquo;بۇ كىشىلەر سىلەرگە چوقۇنغانمىدى؟&raquo; دەيدۇ';
$t[41] = 'ئۇلار ئېيتىدۇ: &laquo;سەن پاكتۇرسەن، بىزنىڭ دوستىمىز ئۇلار ئەمەس، سەن. (ئۇلار بىزگە چوقۇنمايتتى) بەلكى جىنغا چوقۇناتتى. ئۇلارنىڭ تولىسى جىنغا ئىمان كەلتۈرەتتى&raquo;';
$t[42] = 'بۈگۈن سىلەر بىر - بىرىڭلارغا پايدا - زىيان يەتكۈزەلمەيسىلەر، بىز (اﷲ تىن غەيرىيگە چوقۇنغۇچى) زالىملارغا: &laquo;سىلەر چىنپۈتمىگەن دوزاخ ئازابىنى تېتىڭلار&raquo; دەيمىز';
$t[43] = 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلگەندە، ئۇلار: &laquo;بۇ پەقەت سىلەرنى ئاتا - بوۋاڭلار چوقۇنغان بۇتلارغا چوقۇنۇشتىن توسماقچى بولغان ئادەمدۇر&raquo; دەيدۇ ھەمدە ئۇلار: &laquo;بۇ پەقەت توقۇلغان يالغان سۆزدۇر&raquo; دەيدۇ. كاپىرلارغا ھەقىقەت يېتىپ كەلگەندە، ئۇلار: &laquo;بۇ پەقەت ئوپئوچۇق سېھىردۇر&raquo; دېدى';
$t[44] = 'ئۇلارغا ئوقۇيدىغان ھېچقانداق كىتابلارنى ئاتا قىلغىنىمىز يوق. سەندىن ئىلگىرى ئۇلارغا ھېچقانداق پەيغەمبەرمۇ ئەۋەتكىنىمىز يوق';
$t[45] = 'ئۇلاردىن ئىلگىرى ئۆتكەنلەر (ھەقنى) ئىنكار قىلغان، ئۇلار (يەنى مەككە كۇففارلىرى) ئۇلار (يەنى ئىلگىرىكىلەر) ئېرىشكەن كۈچ - قۇۋۋەتنىڭ ئوندىن بىرىگىمۇ ئېرىشمىگەن، ئۇلار مېنىڭ پەيغەمبەرلىرىمنى ئىنكار قىلدى. ئۇلارغا مېنىڭ جازايىم قانداق بولدى؟';
$t[46] = 'ئېيتقىنكى، &laquo;مەن سىلەرگە بىر ئىشنى تەۋسىيە قىلىمەن: سىلەر خۇدالىق ئۈچۈن ئىككىدىن، يا بىردىن تۇرۇڭلار (يەنى اﷲ نىڭ رازىلىقى ئۈچۈن يىغىلىپ ياكى يەككە ھالدا ھەقنى تىلەڭلار)، ئاندىن پىكىر يۈرگۈزۈڭلار&raquo;. ھەمراھىڭلاردا (يەنى مۇھەممەدتە) ھېچ مەجنۇنلۇق يوق، پەقەت ئۇ قاتتىق ئازاب نازىل بولۇشتىن ئىلگىرى سىلەرگە ئەۋەتىلگەن بىر ئاگاھلاندۇرغۇچىدۇر';
$t[47] = 'ئېيتقىنكى، &laquo;سىلەردىن ھېچقانداق ھەق تەلەپ قىلغىنىم يوق، ئۇ سىلەرگە بولسۇن؛ ئەجرىمنى پەقەت اﷲ تىن تىلەيمەن. اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر&raquo;';
$t[48] = 'ئېيتقىنكى، &laquo;پەرۋەردىگارىم، ھەقىقەتەن ھەق ئارقىلىق (باتىلنى) ئاتىدۇ (يەنى پاكىتنى روشەن بايان قىلىدۇ)، اﷲ غەيبنى تامامەن بىلگۈچىدۇر&raquo;';
$t[49] = 'ئېيتقىنكى، &laquo;ھەق (يەنى ئىسلام) كەلدى (باتىل يوقالدى)، باتىل (بىرەر مەخلۇقنى) پەيدا قىلالمايدۇ، (يوقالغان بىرەر مەخلۇقنى) ئەسلىگە كەلتۈرەلمەيدۇ&raquo;';
$t[50] = 'ئېيتقىنكى، &laquo;ئەگەر ئازسام ئازغانلىقىمنىڭ زىيىنى ئۆزۈمگىدۇر، ئەگەر ھىدايەت تاپسام (بۇ) پەرۋەردىگارىمنىڭ ماڭا ۋەھيى قىلغان قۇرئانىنىڭ سەۋەبىدىندۇر، اﷲ ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، يېقىندۇر&raquo;';
$t[51] = 'ئەگەر سەن ئەينى ۋاقىتتا ئۇلارنىڭ قورقۇپ قاچىدىغان يەر تاپالمايۋاتقانلىقىنى، دوزاخقا (ئېلىپ بېرىلىش ئۈچۈن مەھشەرگاھقا) يېقىن جايدا تۇتۇلۇۋاتقانلىقىنى كۆرسەڭ (ئەلۋەتتە قورقۇنچلۇق ھالىنى كۆرەتتىڭ)';
$t[52] = 'ئۇلار (ئازابنى كۆرگەندە): &laquo;ئىمان ئېيتتۇق&raquo; دەيدۇ، ئۇلار قانداقمۇ يىراق جايدىن ئىماننى قولغا كەلتۈرەلەيدۇ';
$t[53] = 'ئۇلار ئىلگىرى ئۇنى (يەنى پەيغەمبەرنى) ئىنكار قىلغان ئىدى. ئۇلار غەيب (ئىشلىرى) ئۈستىدە يىراق جايدىن قارىسىغا سۆزلەيدۇ';
$t[54] = 'ئۇلار بىلەن ئۇلارنىڭ ئارزۇ قىلغان نەرسىلىرى (يەنى ئىمان ئېيتىش ۋە جەننەتكە كىرىش) ئارىسىدا توسالغۇ پەيدا قىلىندى. ئۇلارغا ئىلگىرى ئۇلارغا ئوخشاشلارغا قىلىنغاندەك قىلىندى. چۈنكى ئۇلار ھازىر ئىمان كەلتۈرگەن نەرسىلىرى ئۈستىدە (دۇنيادىكى چاغلىرىدا) چوڭقۇر شەكتە ئىدى';
?>