$t[35] = '(ئى مۇھەممەد!) ئۇلارنىڭ (ئىسلامدىن) يۈز ئۆرۈگەنلىكى ساڭا ئېغىر كەلگەن بولسا، ئۇنداقتا يەر ئاستىغا كىرىشكە يول ياكى ئاسمانغا چىقىشقا شوتا تېپىپ بىر مۆجىزە كەلتۈرەلىسەڭ (كەلتۈرگىن)، ئەگەر اﷲ خالىسا، ئەلۋەتتە ئۇلارنى ھىدايەتكە بىرلەشتۈرەتتى، سەن ھەرگىز (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدىغانلاردىن بولمىغىن';
$t[36] = '(سۆزۈڭگە) ھەقىقىي قۇلاق سالىدىغانلار (سېنىڭ دەۋىتىڭنى) قوبۇل قىلىدۇ (سۆزۈڭگە قۇلاق سالمىغانلار ئۆلۈكلەرنىڭ ئورنىدا)، ئۆلۈكلەرنى اﷲ تىرىلدۈرىدۇ، ئاندىن ئۇلار اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ';
$t[37] = 'ئۇلار (يەنى مەككە كۇففارلىرى): &laquo;نېمىشقا مۇھەممەدكە پەرۋەردىگارىدىن بىر مۆجىزە چۈشۈرۈلمىدى؟&raquo; دەيدۇ، ئېيتقىنكى، &laquo;اﷲ مۆجىزە چۈشۈرۈشكە ھەقىقەتەن قادىر&raquo;. لېكىن ئۇلارنىڭ تولىسى (مۆجىزە نازىل بولۇپ ئىمان ئېيتمىسا بالاغا دۇچار بولىدىغانلىقىنى) بىلمەيدۇ';
$t[38] = 'مەيلى يەر يۈزىدە ماڭىدىغان ھايۋان بولسۇن، مەيلى ئىككى قانىتى بىلەن ئۇچىدىغان ئۇچار قۇش بولسۇن، ھەممىسى سىلەرگە ئوخشاش ئۈممەتلەردۇر (يەنى اﷲ تەرىپىدىن يارىتىلغان مەخلۇقلاردۇر). لەۋھۇلمەھپۇزدا ھېچ نەرسىنى چۈشۈرۈپ قويمىدۇق (يەنى ھەممىنى تولۇق پۈتتۇق)، كېيىن ئۇلار پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىدۇ (اﷲ ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىدۇ)';
$t[39] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار گاسلاردۇر (يەنى قوبۇل قىلىش يۈزىسىدىن ئاڭلىمايدۇ)، گاچىلاردۇر (يەنى ھەقنى سۆزلىمەيدۇ). ئۇلار (كۇفرىنىڭ) قاراڭغۇلۇقى ئىچىدە قالغانلاردۇر، اﷲ خالىغان ئادەمنى ئازدۇرىدۇ، خالىغان ئادەمنى توغرا يولغا سالىدۇ';
$t[40] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;ئەگەر سىلەر راستچىل بولساڭلار ئېيتىپ بېقىڭلار، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ياكى قىيامەت كەلسە، اﷲ تىن غەيرىيگە ئىلتىجا قىلامسىلەر؟';
$t[41] = 'ئۇنداق ئەمەس، سىلەر پەقەت اﷲ قا ئىلتىجا قىلىسىلەر، ئەگەر اﷲ خالىسا، سىلەر اﷲ نىڭ كۆتۈرۈۋېتىشىنى ئىلتىجا قىلغان بالانى اﷲ كۆتۈرۈۋېتىدۇ، سىلەر اﷲ نىڭ شېرىكلىرى قىلىۋالغان بۇتلارنى ئۇنتۇيسىلەر (يەنى بۇتلارغا ئىلتىجا قىلمايسىلەر)&raquo;';
$t[42] = 'شەك - شۈبھىسىزكى، سەندىن ئىلگىرىكى ئۈممەتلەرگە (نۇرغۇن پەيغەمبەرلەرنى) ئەۋەتتۇق (ئۇلار پەيغەمبەرلەرنى ئىنكار قىلدى). ئۇلارنى (اﷲ قا تەۋبە قىلىپ) يېلىنسۇن دەپ نامراتلىق ۋە ئاغرىق - سىلاق بىلەن جازالىدۇق';
$t[43] = 'ئۇلارغا بىزنىڭ ئازابىمىز چۈشكەن چاغدا نېمىشقا يېلىنمىدى؟ لېكىن ئۇلارنىڭ دىللىرى قەساۋەتلەشتى (ئىمانغا يۇمشىمىدى)، شەيتان ئۇلارغا قىلمىشلىرىنى چىرايلىق كۆرسەتتى';
$t[44] = 'ئۇلار ئۆزلىرىگە قىلىنغان نەسىھەتنى ئۇنتۇغان چاغدا، ئۇلارغا (سىناش ئۈچۈن) پاراۋانلىقنىڭ ھەممە ئىشىكلىرىنى ئېچىۋەتتۇق، ئۇلار تاكى ئۆزلىرىگە بېرىلگەن نېمەتلەردىن خۇشال - خۇرام تۇرغاندا (ئۇلارنى ئۇشتۇمتۇت جازالىدۇق)، ئۇلار ھەسرەتتە قالدى';
$t[45] = 'زۇلۇم قىلغان قەۋمنىڭ يىلتىزى قۇرۇتۇلدى (يەنى ئۇلار پۈتۈنلەي ھالاك قىلىندى). جىمى ھەمدۇسانا (پەيغەمبەرلىرىگە ياردەم بېرىپ، كۇففارلارنى ھالاك قىلغانلىقى ئۈچۈن) ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!';
$t[46] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;ئېيتىپ بېقىڭلارچۇ، ئەگەر اﷲ سىلەرنىڭ قۇلىقىڭلارنى گاس، كۆزۈڭلارنى كور قىلىپ قويسا، دىللىرىڭلارنى پېچەتلىۋەتسە (يەنى سىلەرنى ھېچ نەرسىنى چۈشەنمەيدىغان قىلىپ قويسا)، ئۇلارنى اﷲ تىن باشقا يەنە قايسى ئىلاھ ئەسلىگە كەلتۈرەلەيدۇ؟&raquo; قارىغىنكى، بىز (قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىمىزنى قانداق بايان قىلغانمىز، ئەمما ئۇلار (شۇ ئايەتلەردىن) يۈز ئۆرۈيدۇ (يەنى ئىمان ئېيتمايدۇ)';
$t[47] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;ئېيتىپ بېقىڭلارچۇ، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ئۇشتۇمتۇت ياكى ئاشكارا كەلسە، زالىم قەۋمدىن باشقا يەنە كىملەر ھالاك قىلىنىدۇ؟&raquo;';
$t[48] = 'بىز پەيغەمبەرلەرنى پەقەت (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز، ئىمان ئېيتقان ۋە (ئەمەللىرىنى) تۈزىگەن كىشىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر';
$t[49] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، (اﷲ قا) ئىتائەتسىزلىكلىرى تۈپەيلىدىن، ئازابقا دۇچار بولىدۇ';
$t[50] = '(ئى مۇھەممەد! سەندىن ئادەتتىن تاشقىرى ئىشلارنى تەلەپ قىلغۇچى كۇففارلارغا) ئېيتقىنكى، &laquo;مەن سىلەرگە، مەندە اﷲ نىڭ خەزىنىلىرى بار، دېمەيمەن، مەن غەيبنىمۇ بىلمەيمەن، سىلەرگە، مەن بولسام ھەقىقەتەن بىر پەرىشتە، دەپمۇ ئېيتمايمەن، مەن پەقەت ماڭا قىلىنغان ۋەھيىگىلا ئەمەل قىلىمەن&raquo;. ئېيتقىنكى، &laquo;كور ئادەم بىلەن كۆزى ساق ئادەم باراۋەرمۇ؟ (يەنى كاپىر بىلەن مۆمىن باراۋەرمۇ؟) ئويلاپ باقمامسىلەر؟&raquo;';
$t[51] = 'پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىشتىن قورقىدىغانلارنىڭ (كۇفرىدىن، گۇناھتىن) ساقلىنىشلىرى ئۈچۈن، ئۇلارنى قۇرئان بىلەن ئاگاھلاندۇرغىن، ئۇلارغا اﷲ تىن غەيرىي دوست ۋە شاپائەت قىلغۇچى يوقتۇر';
$t[52] = 'اﷲ نىڭ رازىلىقىنى دەپ ئەتىگەندە، ئاخشامدا پەرۋەردىگارىغا ئىبادەت قىلىدىغانلارنى (يېنىڭدىن) قوغلىۋەتمىگىن، ئۇلارنىڭ ھېسابىدىن ساڭا ھېچ نەرسە يۈكلەنمەيدۇ (يەنى ئۇلارنىڭ قىلمىشلىرى ئۈچۈن سەن جاۋابكارلىققا تارتىلمايسەن). سېنىڭ ھېسابىڭدىنمۇ ئۇلارغا ھېچ نەرسە يۈكلەنمەيدۇ، ئۇلارنى قوغلىۋەتسەڭ، زالىملاردىن بولۇپ قالىسەن';
$t[53] = 'ئۇلارنىڭ: &laquo;اﷲ ئارىمىزدىن (ھىدايەت قىلىش بىلەن) ئىنئام قىلغان كىشىلەر مۇشۇلارمۇ؟&raquo; دېيىشلىرى ئۈچۈن، ئۇلارنى بىر - بىرى بىلەن مۇشۇنداق سىنىدۇق (يەنى پېقىر، ئاجىز كىشىلەرنى ئىمان ئېيتقۇزۇش بىلەن، باي، چوڭ كىشىلەرنى سىنىدۇق). اﷲ شۈكۈر قىلغۇچىلارنى ئوبدان بىلىدۇ ئەمەسمۇ؟ (يەنى شۈكۈر قىلغۇچىلارنى اﷲ ھىدايەت قىلىدۇ)';
$t[54] = 'بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقانلار يېنىڭغا كەلگەن چاغدا: &laquo;سىلەرگە ئامانلىق بولسۇن، پەرۋەردىگارىڭلار (بەندىلىرىگە) رەھمەت قىلىشنى (ئېھسان قىلىش يۈزىسىدىن) ئۆز ئۈستىگە ئالدى، سىلەردىن كىمكى بىلمەستىن بىرەر يامانلىق قىلىپ قويۇپ، ئاندىن كېيىن تەۋبە قىلسا ۋە (ئەمەلىنى) تۈزىسە، اﷲ ئەلۋەتتە ئۇنىڭغا مەغپىرەت قىلىدۇ ۋە رەھىم قىلىدۇ&raquo; دېگىن';
$t[55] = '(بۇ سۈرىدە مۇشرىكلارنىڭ گۇمراھلىقىنى كۆرسىتىدىغان دەلىللەرنى بايان قىلغىنىمىزدەك) گۇناھكارلارنىڭ يولى ئاشكارا بولسۇن ئۈچۈن، ئايەتلىرىمىزنى روشەن بايان قىلىمىز';
$t[56] = '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، &laquo;سىلەر اﷲ نى قايرىپ قويۇپ ئىبادەت قىلغان بۇتلارغا ئىبادەت قىلىشتىن مەن ھەقىقەتەن مەنئى قىلىندىم&raquo;. ئېيتقىنكى، &laquo;مەن سىلەرنىڭ نەپسى خاھىشىڭلارغا ئەگەشمەيمەن، بولمىسا مەن ئازغان بولىمەن ۋە ھىدايەت تاپقۇچىلاردىن بولالماي قالىمەن&raquo;';
$t[57] = 'ئېيتقىنكى، &laquo;ھەقىقەتەن مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان روشەن دەلىلگە (يەنى قۇرئانغا ۋە ۋەھىگە) ئاساسلىنىمەن، ھالبۇكى، سىلەر ئۇنى ئىنكار قىلدىڭلار، سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ ئىگىدارچىلىقىدا ئەمەس، بارچە ھۆكۈم اﷲ قا خاس، اﷲ ھەق ھۆكۈم قىلىدۇ، اﷲ ھۆكۈم قىلغۇچىلارنىڭ ياخشىسىدۇر&raquo;';
$t[58] = 'ئېيتقىنكى، &laquo;سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ قولۇمدىن كەلسە ئىدى، ئۇ چاغدا مەن بىلەن سىلەرنىڭ ئاراڭلاردىكى ئىش چوقۇم ھۆكۈم قىلىنىپ بولغان بولاتتى (يەنى سىلەردىن ئارام تېپىش ئۈچۈن، سىلەرنى ھالاك قىلغان بولاتتىم)&raquo;. اﷲ زالىملارنى ئوبدان بىلىدۇ';
$t[59] = 'غەيبنىڭ خەزىنىلىرى اﷲ نىڭ دەرگاھىدىدۇر، ئۇنى پەقەتلا اﷲ بىلىدۇ، قۇرۇقلۇقتىكى، دېڭىزدىكى نەرسىلەرنىڭ ھەممىسىنى اﷲ بىلىدۇ، (دەرەختىن) تۆكۈلگەن ياپراقتىن اﷲ بىلمەيدىغان بىرەرسىمۇ يوق، مەيلى قاراڭغۇ يەر ئاستىدىكى بىرەر دانە ئۇرۇق بولسۇن، مەيلى ھۆل ياكى قۇرۇق نەرسىلەر بولسۇن، ھەممىسى (اﷲ قا مەلۇم بولۇپ) لەۋھۇلمەھپۇزدا يېزىقلىقتۇر';
$t[60] = 'اﷲ سىلەرنى كېچىسى ئۇخلىتىدۇ، كۈندۈزدىكى قىلغان ئىشلىرىڭلارنى بىلىپ تۇرىدۇ، ئاندىن بەلگىلەنگەن مۇددەتكىچە ياشىشىڭلار ئۈچۈن، كۈندۈزدە سىلەرنى ئويغىتىدۇ، ئاندىن اﷲ نىڭ دەرگاھىغا قايتىسىلەر، ئاندىن قىلغان ئەمەللىرىڭلارنى اﷲ سىلەرگە ئېيتىپ بېرىدۇ';
$t[61] = 'اﷲ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى اﷲ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ سىلەرگە ساقلىغۇچى پەرىشتىلەرنى (يەنى قىلغان - ئەتكەننى خاتىرىلەپ تۇرىدىغان پەرىشتىلەرنى) ئەۋەتىدۇ، بېرىڭلارغا ئۆلۈم كەلسە، ئۇنى بىزنىڭ پەرىشتىلىرىمىز قەبزى روھ قىلىدۇ. ئۇلار (اﷲ نىڭ ئەمرىنى ئورۇنلاشتا) بىپەرۋالىق قىلمايدۇ';
$t[62] = 'ئاندىن ئۇلار (يەنى بەندىلەر) ھەق ھۆكۈم قىلغۇچى، ئىگىسى اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ (قىيامەت كۈنى) ھۆكۈم قىلىش يالغۇز اﷲ غىلا خاستۇر، اﷲ ئەڭ تېز ھېساب ئالغۇچىدۇر';
$t[63] = '(ئى مۇھەممەد! بۇ كۇففارلارغا) ئېيتقىنكى، &laquo;قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرى (يەنى بالايى - ئاپەتلىرى) دىن سىلەرنى كىم قۇتقۇزىدۇ؟ (بۇ قاراڭغۇلۇقلارنى كۆرگەن چېغىڭلاردا) ئەگەر اﷲ بىزنى بۇنىڭدىن قۇتقۇزسا چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز، دەپ اﷲ قا ئاشكارا ۋە يوشۇرۇن ئىلتىجا قىلىسىلەر&raquo;';
$t[64] = 'ئېيتقىنكى، &laquo;(يالغۇز) اﷲ سىلەرنى ئۇلاردىن ۋە بارلىق غەمدىن قۇتقۇزىدۇ، ئاندىن سىلەر (بۇنى) تونۇغۇنۇڭلاردىن كېيىن، ئىمان ئېيتماي اﷲ قا شېرىك كەلتۈرىسىلەر&raquo;';
$t[65] = '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;اﷲ سىلەرنىڭ ئۈستۈڭلاردىن (نۇھ ۋە لۇت ئەلەيھىسسالاملارنىڭ قەۋمىگە، ئەسھابۇلپىلگە ئەۋەتىلگەن ئازابقا ئوخشاش) ياكى ئايىغىڭلار ئاستىدىن (پىرئەۋن ۋە قارۇنلارغا ئەۋەتىلگەن ئازابقا ئوخشاش) ئازاب ئەۋەتىشكە، ياكى سىلەرنى پىرقىلەر قىلىپ، ئارىلاشتۇرۇپ (يەنى ئۇرۇشقا سېلىپ) بېرىڭلارغا بېرىڭلارنىڭ ئازابىنى تېتىتىشقا قادىردۇر&raquo; ئۇلارنى چۈشەنسۇن دەپ، ئايەتلىرىمىزنى تۈرلۈك شەكىللەردە قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن';
$t[66] = 'قۇرئان ھەق تۇرسا، ئۇنى قەۋمىڭ ئىنكار قىلدى. &laquo;مەن سىلەرگە ھامىي ئەمەس&raquo; دېگىن';
$t[67] = '(اﷲ تەرىپىدىن) ئالدىنئالا بېرىلگەن ھەر بىر خەۋەرنىڭ مەيدانغا كېلىدىغان ۋاقتى بار، ئۇزاققا قالماي بىلىسىلەر';
$t[68] = 'بىزنىڭ ئايەتلىرىمىز توغرىسىدا (تەنە ۋە مەسخىرە قىلىپ) پاراڭ سېلىشىۋاتقان كىشىلەرنى كۆرگىنىڭدە، ئۇلار بۇنىڭدىن باشقا پاراڭغا چۈشمىگۈچە ئۇلار بىلەن بىللە ئولتۇرمىغىن، ئۇلار بىلەن بىللە ئولتۇرماسلىقنى شەيتان ساڭا ئۇنتۇلدۇرغان بولسا، ئېسىڭگە كەلگەندىن كېيىن، زالىم قەۋم بىلەن بىللە ئولتۇرمىغىن';
$t[69] = 'تەقۋادارلارغا (قۇرئاننى مەسخىرە قىلغۇچى) كاپىرلارنىڭ ھېسابىدىن ھېچ نەرسە يۈكلەنمەيدۇ، لېكىن تەقۋادارلار ئۇلارغا ۋەز - نەسىھەت قىلىشلىرى كېرەك. (شۇنداق قىلغاندا) ئۇلار (قۇرئاننى مەسخىرە قىلىشتىن) ساقلىنىشلىرى مۇمكىن';
$t[70] = 'دىننى ئويۇن - كۈلكە قىلىۋالغان، دۇنيا تىرىكچىلىكىگە ئالدانغانلارنى تەرك ئەت. ھەر قانداق ئادەمنىڭ ئۆز قىلمىشى تۈپەيلى ھالاك بولۇشتىن ساقلىنىشى ئۈچۈن، (كىشىلەرگە) قۇرئان بىلەن نەسىھەت قىلغىن، ئۇنىڭغا اﷲ تىن باشقا دوست ۋە شاپائەت قىلغۇچى بولمايدۇ، ئۇ ھەر قانداق فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ. ئەنە شۇلار قىلمىشلىرى تۈپەيلىدىن ھالاك بولىدىغانلاردۇر، ئۇلار كۇفرى تۈپەيلىدىن (ئۈچەيلىرىنى ئۆرتىۋېتىدىغان) قايناقسۇدىن بولغان شارابقا ۋە قاتتىق ئازابقا دۇچار بولىدۇ';
$t[71] = '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، بىز اﷲ نى قويۇپ، بىزگە پايدا - زىيان يەتكۈزەلمەيدىغان بۇتلارغا ئىبادەت قىلامدۇق؟ بىزنى اﷲ ھىدايەت قىلغاندىن كېيىن، ئارقىمىزغا چېكىنەمدۇق؟ (يەنى مۇرتەد بولامدۇق؟) (مۇبادا بىز شۇنداق قىلىدىغان بولساق، مۇنداق) بىر ئادەمگە: شەيتانلار ئۇنى بىر چۆلگە باشلاپ بارغان، (چۆلدە ئۇ) تېڭىرقاپ قالغان، دوستلىرى ئۇنى بېرى كەل دەپ توغرا يولغا چاقىرىۋاتقان (ئۇ چاقىرىقنى قوبۇل قىلماي ئاخىر ھالاك بولغان ئادەمگە) ئوخشاپ قالىمىز. (بۇ كۇففارلارغا) ئېيتقىنكى، اﷲ نىڭ ھىدايىتى ھەقىقەتەن ھەقىقىي ھىدايەتتۇر، بىز ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇق';
$t[72] = 'ناماز ئوقۇشقا ۋە اﷲ تىن قورقۇشقا بۇيرۇلدۇق، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا (ھېساب ئۈچۈن) يىغىلىسىلەر';
$t[73] = 'اﷲ ئاسمانلارنى، زېمىننى ھېكمەت بىلەن ياراتتى، اﷲ (بىرەر شەيئىگە) &laquo;ۋۇجۇدقا كەل&raquo; دېسە (دەرھال) ۋۇجۇدقا كېلىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) ئازابتىن قورقۇڭلار. اﷲ نىڭ سۆزى ھەقتۇر، سۇر چېلىنىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) پادىشاھلىق اﷲ قا خاستۇر، اﷲ يوشۇرۇن ۋە ئاشكارا نەرسىلەرنى بىلگۈچىدۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، (بەندىلىرىنىڭ ئەھۋالىدىن) خەۋەرداردۇر';
$t[74] = 'ئۆز ۋاقتىدا، ئىبراھىم ئۆز ئاتىسى ئازەرگە: &laquo;(سەن ئۆزۈڭنى ياراتقان اﷲ نى قويۇپ) بۇتلارنى مەبۇد قىلىۋالامسەن؟ مەن سېنى ۋە قەۋمىڭنى ھەقىقەتەن ئېنىق گۇمراھلىقتا كۆرۈمەن&raquo; دېدى';
$t[75] = 'ئىبراھىمنىڭ (تەۋھىد بارىسىدا) قەتئىي ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، (ئۇنىڭغا ئاتىسى بىلەن قەۋمنىڭ گۇمراھلىقىنى كۆرسەتكىنىمىزدەك) بىز ئىبراھىمغا ئاسمانلارنىڭ، زېمىننىڭ ئاجايىپلىرىنى كۆرسەتتۇق';
$t[76] = 'ئىبراھىم كېچىنىڭ قاراڭغۇلۇقى باسقاندا بىر يۇلتۇزنى كۆرۈپ (قەۋمىنىڭ سۆزىنى دەلىل كەلتۈرۈپ، ئۇلارنى مات قىلىش ئۈچۈن): &laquo;بۇ مېنىڭ پەرۋەردىگارىمدۇر&raquo; دېدى. يۇلتۇز پېتىپ كېتىۋىدى، &laquo;مەن پېتىپ كەتكۈچىلەرنى (يەنى يۇلتۇزلارغا ئىبادەت قىلىشنى) ياقتۇرمايمەن&raquo; دېدى';
$t[77] = 'ئۇ ئاينىڭ تۇغقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): &laquo;بۇ مېنىڭ پەرۋەردىگارىمدۇر&raquo; دېدى. ئاي پېتىپ كېتىۋىدى، &laquo;ئەگەر پەرۋەردىگارىم مېنى ھىدايەت قىلمىغان بولسا، مەن چوقۇم ئازغۇچى قەۋمنىڭ قاتارىدا بولىدىكەنمەن&raquo; دېدى';
$t[78] = 'ئۇ قۇياشنىڭ چىقىۋاتقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): &laquo;بۇ مېنىڭ پەرۋەردىگارىمدۇر، بۇ ھەممىدىن (يەنى يۇلتۇز بىلەن ئايدىن) چوڭدۇر&raquo; دېدى. قۇياش پېتىپ كېتىۋىدى، ئۇ ئېيتتىكى: &laquo;ئى قەۋمىم! مەن ھەقىقەتەن سىلەر شېرىك كەلتۈرگەن مەبۇدلاردىن ئادا - جۇدامەن';
$t[79] = 'مەن ھەقىقەتەن، باتىل دىنلاردىن ھەق دىنغا بۇرۇلۇپ، ئاسمانلارنى، زېمىننى ياراتقان زاتقا يۈزلەندىم، مەن مۇشرىكلاردىن ئەمەسمەن&raquo;';
$t[80] = 'ئىبراھىمنىڭ قەۋمى ئۇنىڭ بىلەن مۇنازىرىلەشتى، ئىبراھىم ئېيتتىكى، &laquo;مېنى اﷲ ھىدايەت قىلغان تۇرسا، مەن بىلەن اﷲ (نىڭ مەۋجۇتلۇقى ۋە بىرلىكى) توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ مەن سىلەرنىڭ اﷲ قا شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن قورقمايمەن، پەقەت پەرۋەردىگارىمنىڭلا خالىغان نەرسىسى بولىدۇ، پەرۋەردىگارىمنىڭ بىلىمى ھەممە نەرسىنى ئۆز ئىچىگە ئالغان، (مەبۇدلىرىڭلارنىڭ ماڭا ھېچبىر زىيان يەتكۈزەلمەيدىغانلىقىنى) ئويلىمامسىلەر!';
$t[81] = 'اﷲ نىڭ شېرىكى بولۇش ھەققىدە اﷲ سىلەرگە ھېچقانداق دەلىل چۈشۈرمىگەن بۇتلارنى اﷲ قا شېرىك كەلتۈرۈشتىن قورقمايۋاتساڭلار، سىلەرنىڭ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن مەن قانداقمۇ قورقاتتىم! ئەگەر سىلەر بىلسەڭلار (ئېيتىڭلارچۇ)، بىز بۇ ئىككى گۇرۇھ ئادەمدىن زادى قايسىمىز ئەڭ قورقماسلىققا تېگىشلىك&raquo;';
$t[82] = 'ئازابتىن ئەمىن بولۇش ئىمان ئېيتقان، ئىمانغا مۇشرىكلىكنى ئارىلاشتۇرمىغان ئادەملەرگە مەنسۇپتۇر، ئۇلار ھىدايەت تاپقۇچىلاردۇر';
$t[83] = 'ئۆز قەۋمىگە رەددىيە بېرىش ئۈچۈن ئىبراھىمغا بەرگەن دەلىلىمىز ئەنە شۇدۇر، خالىغان كىشىمىزنى بىر قانچە دەرىجە كۆتۈرىمىز، پەرۋەردىگارىڭ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر';
$t[84] = 'بىز ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق. ئۇلارنىڭ ھەر بىرىنى ھىدايەت قىلدۇق. (ئىبراھىمدىن) ئىلگىرى نۇھنى ھىدايەت قىلدۇق، ئىبراھىمنىڭ ئەۋلادىدىن داۋۇد، سۇلايمان، ئەييۇب، يۇسۇف، مۇسا، ھارۇنلارنى ھىدايەت قىلدۇق، ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز';
$t[85] = 'زەكەرىيا، يەھيا، ئىسا ۋە ئىلياسلارنى ھىدايەت قىلدۇق، ئۇلارنىڭ ھەممىسى ياخشىلاردىندۇر';
$t[86] = 'ئىسمائىل، ئىليەسە، يۇنۇس ۋە لۇتلارنى ھىدايەت قىلدۇق، ئۇلارنى (ئۆز زامانىدىكى) تامامى جاھان ئەھلىدىن ئۈستۈن قىلدۇق';
$t[87] = 'ئۇلارنىڭ ئاتا - بوۋىلىرىدىن، ئەۋلادلىرىدىن، قېرىنداشلىرىدىن بەزىلىرىنى ھىدايەت قىلدۇق، ئۇلارنى تاللىدۇق، ئۇلارنى توغرا يولغا يېتەكلىدۇق';
$t[88] = 'اﷲ نىڭ يولى ئەنە شۇدۇر، اﷲ بەندىلىرىدىن خالىغان كىشىلەرنى شۇ يولغا باشلايدۇ. ئەگەر ئۇلار (يەنى شۇ پەيغەمبەرلەر) شېرىك كەلتۈرسە ئىدى، ئۇلارنىڭ قىلغان ياخشى ئەمەللىرى، ئەلۋەتتە، بىكار بولۇپ كېتەتتى';
$t[89] = 'ئۇلارغا كىتابنى، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئەگەر بۇ مۇشرىكلار ئۇلارنى (يەنى كىتاب، ھېكمەت ۋە پەيغەمبەرلىكنى) ئىنكار قىلسا، ئۇلارنى ئىنكار قىلمايدىغان باشقا بىر قەۋمگە تاپشۇرىمىز';
$t[90] = 'ئەنە شۇلار (يەنى مەزكۇر پەيغەمبەرلەر) اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئۇلارنىڭ يولىغا ئەگەشكىن، (ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، &laquo;قۇرئاننى تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، قۇرئان پەقەت پۈتۈن جاھان ئەھلى ئۈچۈن ۋەز - نەسىھەتتۇر&raquo;';
$t[91] = 'ئۇلار اﷲ نى ھەقىقىي رەۋىشتە تونۇمىدى. ئۇلار (يەنى يەھۇدىيلار) ئۆز ۋاقتىدا: &laquo;اﷲ ھېچ كىشىگە ھېچ نەرسە نازىل قىلمىدى&raquo; دېدى (يەنى ۋەھيىنى ۋە پەيغەمبەرلەرنى ئىنكار قىلدى). (ئى مۇھەممەد!) ئېيتقىنكى، &laquo;مۇسا ئېلىپ كەلگەن ۋە كىشىلەرگە نۇر، ھىدايەت بولغان كىتابنى كىم نازىل قىلدى؟ ئۇ كىتابنى پارچە - پارچە قەغەزلەرگە كۆچۈرۈپ، بىر قىسىمىنى ئاشكارىلاپ، كۆپ قىسىمىنى يوشۇرىسىلەر. (ئى يەھۇدىيلار!) سىلەرگە ئۆزۈڭلار ۋە ئاتا - بوۋاڭلار بىلمىگەن نەرسىلەر بىلدۈرۈلدى، (بۇ قۇرئاننى) اﷲ (نازىل قىلدى)&raquo;. ئاندىن، ئۇلارنى تەرك ئەتكىنكى، ئۇلار باتىللىرىدا (يەنى قالايمىقان سۆزلىرىدە) ئويناپ يۈرسۇن';
$t[92] = 'بۇ (قۇرئان) مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن بىز نازىل قىلغان، ئۇنىڭدىن ئىلگىرى نازىل قىلىنغان (تەۋرات ۋە ئىنجىلغا ئوخشاش) كىتابلارنى ئېتىراپ قىلغۇچى مۇبارەك كىتابتۇر، ئاخىرەتكە ئىمان ئېيتىدىغانلار ئۇنىڭغا ئىمان ئېيتىدۇ، ئۇلار نامازلىرىنى ئۆز ۋاقتىدا تولۇق ئادا قىلىدۇ';
$t[93] = 'اﷲ قا يالغان چاپلىغان ياكى ئۆزىگە ھېچ نەرسە ۋەھىيى قىلىنمىغان تۇرۇقلۇق، ماڭا ۋەھىيى قىلىندى دېگەن، ياكى اﷲ نازىل قىلغاندەك مەنمۇ كىتاب نازىل قىلالايمەن دېگەن كىشىلەردىنمۇ زالىم كىشىلەر بارمۇ؟ (ئى مۇھەممەد!) زالىملارنى سەكراتقا چۈشكەن ۋاقىتلىرىدا كۆرسەڭ (ئەلۋەتتە، قورقۇنچلۇق ھالىنى كۆرۈسەن)، پەرىشتىلەر (يەنى ئازاب پەرىشتىلىرى) قوللىرىنى سوزۇپ: &laquo;سىلەر جانلىرىڭلارنى چىقىرىڭلار! سىلەر اﷲ توغرىسىدا ھەقسىز سۆزلەرنى قىلغانلىقىڭلار (يەنى ئىختىرا قىلغانلىقىڭلار، شېرىك كەلتۈرگەنلىكىڭلار، بالا نىسبەتەن بەرگەنلىكىڭلار) ۋە اﷲ نىڭ ئايەتلىرىنى مەنسىتمىگەنلىكىڭلار ئۈچۈن، بۈگۈن خورلىغۇچى ئازاب بىلەن جازالىنىسىلەر&raquo; دەيدۇ';
$t[94] = 'بىز دەسلەپتە سىلەرنى قانداق خەلق ئەتكەن بولساق دەرگاھىمىزغا شۇنداق تەنھا كەلدىڭلار، سىلەرگە بىز ئاتا قىلغان نەرسىلەرنى ئارقاڭلاردا (يەنى دۇنيادا) قويۇپ كەلدىڭلار، (ئىبادەت قىلىنىشقا تېگىشلىك بولۇشتا) اﷲ نىڭ شېرىكلىرى دەپ گۇمان قىلغان شاپائەتچىڭلارنى سىلەر بىلەن بىللە كۆرمەيمىزمۇ؟ ئۇلار بىلەن سىلەرنىڭ ئاراڭلاردىكى مۇناسىۋەت ھەقىقەتەن ئۈزۈلدى، سىلەر (شاپائەت قىلىدۇ) دەپ گۇمان قىلغان بۇتلار بەربات بولدى';
$t[95] = 'دانلارنى ۋە ئۇرۇقچىلارنى بىخ ئۇرغۇزغۇچى ھەقىقەتەن اﷲ دۇر، تىرىكنى ئۆلۈكتىن ۋە ئۆلۈكنى تىرىكتىن چىقارغۇچى ئەنە شۇ اﷲ دۇر، (اﷲ نىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟';
$t[96] = 'اﷲ سۈبھىنى (قاراڭغۇدىن) يېرىپ چىقارغۇچىدۇر (يەنى تاڭنى يورۇتقۇچىدۇر)، اﷲ كېچىنى (سىلەر ئۈچۈن) ئىستىراھەت قىلىپ ياراتتى، (سىلەرنىڭ) ۋاقىت ھېسابلىشىڭلار ئۈچۈن قۇياش بىلەن ئاينى ياراتتى، بۇ، غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر';
$t[97] = 'قۇرۇقلۇق ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا (يەنە كېچىلىك يول يۈرگىنىڭلاردا) يول تېپىشىڭلار ئۈچۈن اﷲ سىلەرگە يۇلتۇزلارنى يارىتىپ بەردى. (اﷲ نىڭ ئۇلۇغلۇقىنى) بىلىدىغان قەۋم ئۈچۈن، (اﷲ نىڭ قۇدرىتىنىڭ) دەلىللىرىنى ھەقىقەتەن ئوچۇق بايان قىلدۇق';
$t[98] = 'اﷲ سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، سىلەرنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ بەچچىدانىدا قارارلاشتۇردى، چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى ھەقىقەتەن ئوچۇق بايان قىلدۇق';
$t[99] = 'اﷲ بۇلۇتتىن يامغۇر سۈيىنى چۈشۈردى، شۇنىڭ بىلەن بارلىق ئۆسۈملۈكلەرنى ئۈندۈردى، ئۆسۈملۈكلەردىن يېشىل ياپراقلارنى چىقاردى، (بوغداي ۋە ئارپا باشاقلىرىغا ئوخشاش) بىر - بىرىگە مىنگىشىپ كەتكەن دانلارنى يېتىشتۈردى، خورما دەرىخىنىڭ چېچەكلىرىدىن يەرگە يېقىن ساڭگىلاپ تۇرىدىغان خورما ساپاقلىرىنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) ئۈزۈملۈك باغلارنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) شەكىلدە، ھەجىمدە، تەمدە بىر - بىرىگە ئوخشايدىغان ۋە ئوخشىمايدىغان زەيتۇن، ئانار دەرەخلىرىنى يېتىشتۈردى، (ئۇلارنىڭ ھەر بىرىنىڭ) يېڭى تۇتقان مېۋىسىگە ۋە پىشقان مېۋىسىگە قاراڭلار، ئۇلاردا اﷲ نىڭ بارلىقىغا ئىشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئوچۇق دەلىللەر بار';
$t[100] = 'جىنلارنى اﷲ ياراتقان تۇرسا، (مۇشرىكلار) جىنلارنى اﷲ قا (مۇشرىكلار ئىختىرا قىلغان نەرسىلەردىن) پاكتۇر، ئۇلارنىڭ سۈپەتلىگەنلىرىدىن ئۈستۈندۇر';
$t[101] = 'اﷲ ئاسمانلارنى ۋە يەرنى ئۆرنەكسىز ياراتقۇچىدۇر، اﷲ نىڭ خوتۇنى يوق تۇرسا، قانداقمۇ بالىسى بولسۇن؟ ھەممە نەرسىنى اﷲ ياراتقان، ھەممە نەرسىنى اﷲ ئوبدان بىلگۈچىدۇر';
$t[102] = 'ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، اﷲ تىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ئۇ ھەر نەرسىگە ھامىيدۇر';
$t[103] = 'كۆزلەر اﷲ نى كۆرمەيدۇ، اﷲ كۆزلەرنى كۆرۈپ تۇرىدۇ، اﷲ (بەندىلىرىگە) مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر';
$t[104] = '(ئى پەيغەمبەر ئېيتقىنكى) سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (ھىدايەتنى ئازغۇنلۇقتىن ئايرىپ كۆرسىتىدىغان) دەلىللەر كەلدى، كىمكى ھەقنى كۆرۈپ ئىمان ئېيتسا ئۆزىگە پايدا، كىمكى ئۇنىڭدىن كۆز يۇمىسا (يەنى باش تارتسا) ئۆزىگە زىيان. مەن سىلەرگە (ئەمەلىڭلارنى كۆزىتىپ تۇرىدىغان) كۆزەتچى ئەمەسمەن (پەقەت سىلەرنى ئاگاھلاندۇرغۇچى پەيغەمبەرمەن)';
$t[105] = 'ئۇلار (يەنى مۇشرىكلار): (ئى مۇھەممەد!) سەن (قۇرئاننى باشقىلاردىن) ئۆگەندىڭ دەيدۇ دەپ، ھەقنى بىلىدىغان قەۋمگە بايان قىلىمىز دەپ، ئايەتلەرنى مۇشۇنداق شەكىللەردە بايان قىلدۇق';
$t[106] = '(ئى مۇھەممەد!) ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھيى قىلىنغان قۇرئانغا ئەمەل قىلغىن؛ اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مۇشرىكلار بىلەن تەڭ بولمىغىن';
$t[107] = 'ئەگەر اﷲ (ئۇلارنىڭ ھىدايەت تېپىشنى) خالىسا، ئۇلار شېرىك كەلتۈرمەيتتى، سېنى ئۇلارغا كۆزەتچى قىلغىنىمىز يوق، سەن ئۇلارغا ھامىيمۇ ئەمەسسەن';
$t[108] = 'مۇشرىكلارنىڭ خۇدانى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىنى تىللىماڭلار، (ئۇنداق قىلساڭلار) ئۇلار بىلمىگەنلىكلىرىدىن ھەددىدىن ئېشىپ اﷲ نى تىللايدۇ. شۇنىڭدەك بىز ھەر ئۈممەتكە ئۆزلىرىنىڭ ئەمەلىنى چىرايلىق كۆرسەتتۇق، ئاندىن ئۇلارنىڭ قايتىپ بارىدىغان جايى اﷲ نىڭ دەرگاھىدۇر، اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ (يەنى ياخشىغا مۇكاپات بېرىدۇ، ياماننى جازالايدۇ)';
$t[109] = 'ئەگەر ئۇلارغا بىرەر مۆجىزە كەلسە، ئۇنىڭغا چوقۇم ئىشىنىدىغانلىقلىرىنى (بىلدۈرۈپ) اﷲ نىڭ نامى بىلەن قاتتىق قەسەم ئىچىشىدۇ. (ئى مۇھەممەد!) مۆجىزىلەر (مېنىڭ ئەمەس)، اﷲ نىڭ دەرگاھىدىكى ئىش، دېگىن. (ئى مۆمىنلەر!) سىلەر نېمىنى بىلىسىلەر؟ مۆجىزىلەر كەلسە، ئۇلار، ئېھتىمال، ئۇنىڭغا ئىشەنمەسلىكلىرى مۇمكىن';
$t[110] = 'ئۇلارنىڭ دىللىرىنى، كۆزلىرىنى ھەق (نى چۈشىنىش ۋە كۆرۈش) تىن بۇرىۋېتىمىز، ئۇلار دەسلەپتە مۆجىزىلەرگە ئىشەنمىگىنىدەك (يەنى ئىشەنمەيدۇ)، ئۇلارنى گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشكە قويۇۋېتىمىز';
$t[111] = 'بىز ئۇلارغا (يەنى كۇففارلارغا) پەرىشتىلەرنى چۈشۈرگەن، ئۆلۈكلەرنى ئۇلار بىلەن سۆزلەشتۈرگەن، بىز ھەممە نەرسىنى ئۇلارنىڭ ئالدىغا يىغىپ بەرگەن (يەنى ئۇلارنىڭ يۇقىرىقى تەلەپلىرىنى ئورۇنلىغان) تەقدىردىمۇ، اﷲ خالىمىغۇچە ئۇلار ئىمان ئېيتمايدۇ، لېكىن ئۇلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ';
$t[112] = 'شۇنىڭدەك (يەنى بۇ مۇشرىكلارنى ساڭا دۈشمەن قىلغاندەك) ئىنسانلار ۋە جىنلارنىڭ شەيتانلىرىنى ھەر بىر پەيغەمبەرگە دۈشمەن قىلدۇق، ئۇلار ئالداش ئۈچۈن شېرىن سۆزلەر بىلەن بىر - بىرىگە ۋەسۋەسە قىلىدۇ، ئەگەر پەرۋەردىگارىڭ خالىسا، ئۇلار بۇ ئىشنى (يەنى پەيغەمبەرلىرىگە دۈشمەنلىكنى) قىلمايتتى، ئۇلارنى ئۆزلىرى تۈزگەن ھىيلە - مىكىرلىرى بىلەن قويۇپ بەرگىن (اﷲ ساڭا ئۇلارغا قارشى ياردەم بېرىدۇ)';
$t[113] = 'ئاخىرەتكە ئىمان ئېيتمايدىغانلارنىڭ دىللىرى ئۇلارنىڭ شېرىن سۆزلىرىگە مايىل بولسۇن، ئۇنىڭدىن خۇرسەن بولسۇن ۋە ئۇلار قىلىۋاتقان گۇناھلارنى بۇلارمۇ قىلسۇن، دەپ ۋەسۋەسە قىلىدۇ';
$t[114] = '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;اﷲ سىلەرگە روشەن كىتاب نازىل قىلغان تۇرسا، (ئارىمىزدىكى ئىختىلاپنى ھەل قىلىش ئۈچۈن) اﷲ تىن غەيرىي ھۆكۈم قىلغۇچىنى تەلەپ قىلامدىم؟&raquo; بىز كىتاب ئاتا قىلغانلار (يەنى يەھۇدىيلار ۋە ناسارالارنىڭ ئۆلىمالىرى) ئۇنىڭ (يەنى قۇرئاننىڭ) پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەق كىتاب ئىكەنلىكىنى بىلىدۇ، سەن ھەرگىزمۇ شەك قىلغۇچىلاردىن بولمىغىن';
$t[115] = 'پەرۋەردىگارىڭنىڭ سۆزى ناھايىتى راستتۇر، ناھايىتى توغرىدۇر، ئۇنىڭ سۆزىنى ئۆزگەرتەلەيدىغان ھېچ كىشى يوقتۇر. اﷲ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر';
$t[116] = 'ئەگەر سەن يەر يۈزىدىكى كۆپچىلىك كىشىلەر (يەنى كۇففارلار) گە ئىتائەت قىلساڭ، ئۇلار سېنى اﷲ نىڭ يولىدىن ئازدۇرىدۇ، ئۇلار پەقەت گۇمانغىلا ئەگىشىدۇ، ئۇلار پەقەت يالغاننىلا سۆزلەيدۇ';
$t[117] = '(ئى مۇھەممەد!) سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ھەقىقەتەن ئوبدان بىلىدۇ، توغرا يول تاپقانلارنىمۇ ئوبدان بىلىدۇ';
$t[118] = 'اﷲ نىڭ ئايەتلىرىگە ئىمان كەلتۈرگۈچى بولساڭلار، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى يەڭلار';
$t[119] = 'زۆرۈرىيەت بولمىغۇچە يېيىش ھارام قىلىنغان نەرسىلەرنى اﷲ سىلەرگە ئوچۇق بايان قىلغان تۇرسا، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى نېمىشقا يېمەيسىلەر؟ نۇرغۇن كىشىلەر نەپسى خاھىشىغا ئەگىشىپ، بىلمەستىن، (باشقىلارنى) ھەقىقەتەن ئازدۇرىدۇ، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ (شەرئى دەلىلسىز ھالال، ھارام دەپ) ھەددىدىن ئاشقۇچىلارنى ئوبدان بىلىدۇ';
$t[120] = 'ئاشكارا ۋە يوشۇرۇن گۇناھلارنى تەرك ئېتىڭلار، شۈبھىسىزكى، گۇناھ قىلغۇچىلار ئۆز قىلمىشلىرى تۈپەيلىدىن (ئاخىرەتتە) جازاغا تارتىلىدۇ';
$t[121] = '(ئى مۆمىنلەر!) اﷲ نىڭ ئىسمى ئېيتىلماي بوغۇزلانغان (يەنى بىسمىللاە دەپ زەبھى قىلىنمىغان) ھايۋانلارنى يېمەڭلار، ئۇنى يېيىش ئەلۋەتتە گۇناھتۇر. شۈبھىسىزكى، شەيتانلار ئۆز دوستلىرىنى (يەنى مۇشرىكلارنى) سىلەر بىلەن دەتالاش قىلىشقا ۋەسۋەسە قىلىدۇ، ئەگەر ئۇلارغا ئىتائەت قىلساڭلار، سىلەر چوقۇم مۇشرىك بولۇپ قالىسىلەر';
$t[122] = 'ئەسلىدە ئۆلۈك (يەنى كاپىر) بولۇپ، بىز ئۇنى (يەنى ئۇنىڭ قەلبىنى ئىمان بىلەن) تىرىلدۈرگەن، بىز ئۇنىڭغا كىشىلەر ئارىسىدا ھىدايەت تېپىشقا نۇر بەرگەن بىر ئادەم زۇلمەتتە (يەنى كۇفرىنىڭ قاراڭغۇلۇقىدا) قالغان ۋە ئۇنىڭدىن قۇتۇلالمىغان بىر ئادەم بىلەن ئوخشاشمۇ؟ كاپىرلارغا ئۇلارنىڭ قىلمىشلىرى ئەنە شۇنداق چىرايلىق كۆرسىتىلدى';
$t[123] = 'ھىيلە - مىكىر ئىشلىتىشلىرى ئۈچۈن (مەككىدە قۇرەيشنىڭ چوڭلىرىنى باش گۇناھكار قىلغىنىمىزدەك)، ھەر بىر شەھەرنىڭ چوڭلىرىنى باش گۇناھكار قىلدۇق، ئۇلارنىڭ ئىشلەتكەن ھىيلە - مىكىرلىرى ئۆزلىرىگە يانىدۇ (يەنى ئۇلار شۇ ھىيلە - مىكىرلەرنىڭ ۋابالىغا قالىدۇ)، ئۇلار (بۇنى) سەزمەيدۇ';
$t[124] = 'ئۇلارغا (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەرلىكىنى كۆرسىتىدىغان) بىرەر مۆجىزە كەلگەن چاغدا، (ئۇلار) بىزگە پەيغەمبەرلەرگە بېرىلگەن مۆجىزە بېرىلمىگۈچە (ئۇنىڭغا) ھەرگىز ئىشەنمەيمىز، دەيدۇ. اﷲ پەيغەمبەرلىك ۋەزىپىسىنى قەيەرگە ئورنىتىشنى (يەنى كىمنىڭ ئۇنىڭ ئەھلى بولىدىغانلىقىنى) ئوبدان بىلىدۇ، (بۇ) گۇناھكارلار ئۆزلىرى ئىشلەتكەن ھىيلە - مىكىرلەر تۈپەيلىدىن اﷲ نىڭ دەرگاھىدا خورلۇققا ۋە قاتتىق ئازابقا دۇچار بولىدۇ';
$t[125] = 'اﷲ كىمنى ھىدايەت قىلماقچى بولسا، ئۇنىڭ كۆكسىنى ئىسلام ئۈچۈن ئاچىدۇ (يەنى ئۇنىڭ قەلبىنى ئىمانغا كېڭەيتىپ بېرىدۇ)، اﷲ كىمنى ئازدۇرماقچى بولسا، ئۇنىڭ كۆكسىنى شۇنچە تارايتىۋېتىدۇكى، (ئىمان ئېيتىش) ئۇنىڭغا گويا ئاسمانغا چىقىشتىنمۇ قىيىن تۇيۇلىدۇ، ئىمان ئېيتمىغانلارنى اﷲ مۇشۇنداق ئازابلايدۇ';
$t[126] = 'بۇ (يەنى ئىسلام دىنى) پەرۋەردىگارىڭنىڭ توغرا يولىدۇر، ئىبرەت ئالىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلدۇق';
$t[127] = 'پەرۋەردىگارىنىڭ دەرگاھىدا ئۇلارغا دارى سالام (يەنى جەننەت) بار، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرى ئۈچۈن اﷲ ئۇلارغا مەدەتكاردۇر';
$t[128] = 'اﷲ (ئىنسانلارنى، جىنلارنى ھېساب ئۈچۈن) تولۇق يىغىدىغان كۈننى ئەسلىگىن، (بۇ كۈندە اﷲ ئېيتىدۇكى) &laquo;ئى جىنلار جامائەسى! سىلەر نۇرغۇن ئادەملەرنى ئازدۇردۇڭلار&raquo;. ئۇلارنىڭ ئىنسانلاردىن بولغان دوستلىرى: &laquo;ئى پەرۋەردىگارىمىز! بىز بىر - بىرىمىزدىن پايدىلاندۇق، بىز ئەمدى سەن بەلگىلىگەن ۋاقىتقا (يەنى قىيامەت كۈنىگە) يەتتۇق&raquo; دەيدۇ، اﷲ: &laquo;دوزاخ سىلەرنىڭ جايىڭلاردۇر، سىلەر اﷲ نىڭ خاھىشىغا باغلىق ھالدا ئۇ يەردە مەڭگۈ قالىسىلەر&raquo; دەيدۇ. شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر';
$t[129] = 'شۇنىڭغا (يەنى ئىنسانلار بىلەن جىنلارنى ئۆزئارا پايدىلاندۇرغىنىمىزغا) ئوخشاش، قىلمىشلىرى تۈپەيلىدىن زالىملارنىڭ بەزىسىنى بەزىسىگە مۇسەللەت قىلىمىز';
$t[130] = '&laquo;ئى جىنلار ۋە ئىنسانلار جامائەسى! سىلەرگە ئۆز ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى بايان قىلىپ بېرىدىغان، بۇ كۈنۈڭلاردىكى ئۇچرىشىشتىن سىلەرنى ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىدىمۇ؟&raquo; ئۇلار: &laquo;گۇناھلىرىمىزنى ئېتىراپ قىلدۇق&raquo; دەيدۇ. ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىغان، ئۇلار ئۆزلىرىنىڭ ھەقىقەتەن كاپىر بولغانلىقلىرىنى ئېتىراپ قىلىدۇ';
$t[131] = 'بۇ شۇنىڭ ئۈچۈنكى، پەرۋەردىگارىڭ شەھەرلەرنىڭ ئاھالىسىنى (ئۇلارغا پەيغەمبەرلەر ئەۋەتىپ ئاگاھلاندۇرماستىن) غەپلەتتىكى ۋاقىتلىرىدا زۇلۇم قىلغانلىقلىرى تۈپەيلىدىن ھالاك قىلغىنى يوق';
$t[132] = '(ئاخىرەتتە) ھەر ئادەمنىڭ قىلغان ئەمەلىگە يارىشا دەرىجىسى بولىدۇ، پەرۋەردىگارىڭ ئۇلارنىڭ قىلغان ئىشلىرىدىن غاپىل ئەمەس';
$t[133] = 'پەرۋەردىگارىڭ (خالايىقتىن ۋە ئۇلارنىڭ ئىبادەتلىرىدىن) بىھاجەتتۇر، رەھمەت ئىگىسىدۇر. ئەگەر ئۇ خالىسا، سىلەرنى ھالاك قىلىپ، سىلەرنى باشقا بىر قەۋمنىڭ ئەۋلادىدىن پەيدا قىلغاندەك، سىلەرنىڭ ئورنۇڭلارغا ئۆزى خالىغان (ئىتائەتمەن) باشقا بىر قەۋمنى قويىدۇ';
$t[134] = 'سىلەرگە ۋەدە قىلىنغان (قىيامەت، ئۆلگەندىن كېيىن تىرىلىش قاتارلىق) ئىشلار چوقۇم مەيدانغا كېلىدۇ، سىلەر قېچىپ قۇتۇلالمايسىلەر';
$t[135] = '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;ئى قەۋمىم! سىلەر كۈچۈڭلارنىڭ يېتىشىچە ئىشلەڭلار (يەنى كۇفرىڭلاردا چىڭ تۇرۇڭلار)، مەنمۇ چوقۇم كۈچۈمنىڭ يېتىشىچە ئىشلەيمەن (يەنى مەنمۇ ئۆز دەۋىتىمدە چىڭ تۇرىمەن). ئاخىرەتتە كىمنىڭ ئاقىۋېتى ياخشى بولىدىغانلىقىنى ئۇزۇنغا قالماي بىلىسىلەر، زالىملار ئەلۋەتتە مەقسىتىگە ئېرىشەلمەيدۇ&raquo;';
$t[136] = 'مۇشرىكلار اﷲ ياراتقان ئېكىنلەردىن ۋە چاھار پايلاردىن (يەنى تۆگە، كالا، قويلاردىن) اﷲ ئۈچۈن بىر ھەسسە (بۇتلىرى ئۈچۈن بىر ھەسسە) ئايرىپ قويۇپ، ئۆز گۇمانلىرىچە (ھېچقانداق دەلىلسىزلا): &laquo;بۇ اﷲ ئۈچۈندۇر، بۇ مەبۇدلىرىمىز ئۈچۈندۇر&raquo; دېدى. بۇتلىرىنىڭ نېسىۋېسىدىن اﷲ نىڭكىگە قوشۇشقا بولمايتتى، اﷲ نىڭ نېسىۋېسىدىن بۇتلىرىنىڭكىگە قوشۇشقا بولاتتى (مۇشرىكلار اﷲ قا ئاتىغان ھەسسىسىدىن بۇتلارغا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسى ئىچىدە قالدۇراتتى ۋە اﷲ باي، بۇنىڭغا موھتاج ئەمەس، دەيتتى. بۇتلىرىغا ئاتىغان ھەسسىسىدىن اﷲ قا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسىگە قايتۇرۇۋېتەتتى). ئۇلارنىڭ قارارى نېمىدېگەن قەبىھ!';
$t[137] = 'مۇشرىكلارنىڭ شېرىكلىرى (يەنى شەيتانلار) مۇشرىكلارنى ھالاك قىلىش ۋە ئۇلارنىڭ دىنىنى قالايمىقانلاشتۇرۇش ئۈچۈن، ئۇلارنىڭ نۇرغۇنلىرىغا بالىلىرىنى ئۆلتۈرۈشنى، شۇنىڭدەك (يەنى اﷲ قا ۋە بۇتلارغا ھەسسە ئايرىپ قويۇشتەك تەقسىماتنى شەيتانلارغا ۋە مۇشرىكلارغا چىرايلىق كۆرسەتكەندەك) چىرايلىق كۆرسەتتى. ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلار بۇ (قەبىھ) ئىشنى قىلمايتتى، ئۇلارنى اﷲ قا چاپلىغان يالغانلىرى بىلەن بىللە تەرك ئەت';
$t[138] = 'ئۇلار ئۆزلىرىنىڭ گۇمانىچە: &laquo;بۇ ھايۋانلار ۋە ئېكىنلەر (مەبۇدلىرىمىزغا خاس بولۇپ، باشقىلارغا) ھارامدۇر، ئۇلارنى پەقەت بىز خالىغان ئادەملەرلا يەيدۇ، بۇ ھايۋانلارنى مىنىش ھارامدۇر، بۇ ھايۋانلارنى بوغۇزلىغاندا اﷲ نىڭ ئىسمىنى ئېيتىشقا بولمايدۇ&raquo; دېدى. ئۇلار اﷲ قا يالغان چاپلىدى، اﷲ ئۇلارنى يالغان چاپلىغانلىقلىرى ئۈچۈن جازالايدۇ';
$t[139] = 'ئۇلار: &laquo;بۇ ھايۋانلارنىڭ قورساقلىرىدىكى بالىلىرى بىزنىڭ ئەر كىشىلىرىمىزگە خاستۇر، بىزنىڭ خوتۇن كىشىلىرىمىزگە ھارامدۇر&raquo; دېدى. ئەگەر تۇغۇلغان ھايۋان ئۆلۈك بولسا، بۇ ئەر كىشىلەر بىلەن خوتۇن كىشىلەر ئارىسىدا ئورتاقتۇر (يەنى ئۇنى ھەممەيلەن تەڭ يەيدۇ) دەپ (ھالال - ھارامنى تەيىنلەشتى). اﷲ قا يالغان چاپلىغانلىقلىرى ئۈچۈن اﷲ ئۇلارنى جازالايدۇ. شۈبھىسىزكى، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر';
$t[140] = 'ھاماقەتلىك ۋە نادانلىقلىرىدىن بالىلىرىنى ئۆلتۈرگەن ۋە اﷲ نامىدىن يالغان ئېيتىپ، اﷲ رىزىق قىلىپ بەرگەن نەرسىلەرنى ھارام بىلگەن كىشىلەر ھەقىقەتەن زىيان تارتتى، ئۇلار ھەقىقەتەن (توغرا يولدىن) ئاداشتى، ھىدايەت تاپمىدى';
$t[141] = 'اﷲ بىدىشلىك ۋە بىدىشسىز باغلارنى بەرپا قىلدى، مېۋىلىرى، دانلىرى (رەڭگى، تەمى، ھەجمى ۋە پۇرىقى) بىر - بىرىگە ئوخشىمايدىغان خورما دەرەخلىرىنى، زىرائەتلەرنى، (رەڭگى ۋە شەكلى) بىر - بىرىگە ئوخشايدىغان، (تەمى) ئوخشىمايدىغان زەيتۇن ۋە ئانارنى ياراتتى، مېۋىسى پىشقاندا، ئۇلاردىن يەڭلار، مېۋە (نىڭ ھوسۇلى) نى يىغقان كۈندە (يەنى يىغقان ۋاقىتتا)، ئۇنىڭ ئۆشرىسىنى ئادا قىلىڭلار، ئىسراپ قىلماڭلار، اﷲ ھەقىقەتەن ئىسراپ قىلغۇچىلارنى ياقتۇرمايدۇ';
$t[142] = 'اﷲ چاھارپايلارنى (يەنى تۆگە، كالا، قويلارنى)، يۈك كۆتۈرىدىغانلارنى، بوغۇزلىنىدىغانلارنى ياراتتى. اﷲ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن يەڭلار، شەيتاننىڭ ئارقىسىغا كىرمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئوچۇق دۈشمەندۇر';
$t[143] = '(اﷲ سىلەرگە يېيىش ھالال قىلىنغان) سەككىز خىل چاھارپاينى ياراتتى: قويدىن ئىككى (قوچقار ۋە ساغلىق)، ئۆچكىدىن ئىككى (تېكە ۋە ئۇرغاچى). (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، &laquo;اﷲ (قوي بىلەن ئۆچكىدىن) ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى قوزا بىلەن ئوغلاقنىمۇ؟ ئەگەر (سىلەر اﷲ بۇلارنى بىزگە ھارام قىلدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، قېنى ماڭا بىلىپ ئېيتىپ بېرىڭلار!&raquo;';
$t[144] = '(اﷲ سىلەرگە) تۆگىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى)، كالىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى) ياراتتى، (مۇشرىكلارغا) ئېيتقىنكى، &laquo;اﷲ ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى بوتىلاق بىلەن موزايلىنىمۇ؟ ياكى اﷲ بۇنى سىلەرگە تەۋسىيە قىلغان چاغدا سىلەر ھازىرمىدىڭلار؟ كىشىلەرنى ئازدۇرۇش ئۈچۈن، بىلمەي تۇرۇپ اﷲ نامىدىن يالغان ئېيتقان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ&raquo;';
$t[145] = '(ئى مۇھەممەد! مەككە كۇففارلىرىغا) ئېيتقىنكى، ساڭا ۋەھيى قىلىنغان ئەھكاملار ئىچىدە، ئۆزى ئۆلۈپ قالغان نەرسە ياكى ئېقىپ چىققان قان ۋە ياكى چوشقا گۆشى - چوشقا نىجىس يېمەكلىكلەرگە ئادەتلەنگەنلىكى ئۈچۈن چوشقا گۆشى پاسكىنىدۇر - ۋە اﷲ تىن غەيرىينىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان گۇناھ مالدىن غەيرىينى ھەر قانداق ئادەم ئۈچۈن ھارام كۆرمەيمەن. كىمكى ئۇلاردىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) نائىلاج ۋە ئىختىيارسىز ھالدا ھاياتىنى ساقلاپ قالغۇدەك يېسە (ھېچ گۇناھ بولمايدۇ)، چۈنكى پەرۋەردىگارىڭ مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر';
$t[146] = 'يەھۇدىيلارغا توم تۇياقلىق (يەنى تۆگە، تۆگىقۇشقا ئوخشاش ئاچا تۇياقلىق بولماي توم تۇياقلىق بولغان) ھايۋانلارنىڭ ھەممىسىنى ھارام قىلدۇق، ئۇلارغا كالا، قويلارنىڭ دۈمبىسىدىكى ياكى ئۈچەيلىرىدىكى ياكى سۆڭىكىگە چاپلىشىقلىق ياغلىرىدىن باشقا ياغلىرىنى ھارام قىلدۇق، بۇ ئۇلارنىڭ (پەيغەمبەرلەرنى ئۆلتۈرۈش، جازانە قىلىش، كىشىلەرنىڭ مال - مۈلكىنى ناھەق يەۋېلىش قاتارلىق) زۇلۇملىرى تۈپەيلىدىن، ئۇلارغا بەرگەن جازايىمىزدۇر، بىز ئەلۋەتتە (بەرگەن خەۋەرلىرىمىزدە) راستچىلمىز';
$t[147] = '(ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى يەھۇدىيلار) سېنى ئىنكار قىلسا، سەن: &laquo;پەرۋەردىگارىڭلار (قىلمىشىڭلار ئۈچۈن، سىلەرنى ئالدىراپ جازالىمىغانلىقتىن) كەڭ رەھمەت ئىگىسىدۇر، (اﷲ نىڭ رەھمىتىگە مەغرۇر بولماڭلاركى) اﷲ نىڭ ئازابى گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ&raquo; دېگىن';
$t[148] = 'مۇشرىكلار: &laquo;ئەگەر اﷲ خالىسا ئىدى، بىز ۋە ئاتا - بوۋىلىرىمىز شېرىك كەلتۈرمەيتتۇق ۋە ھېچ نەرسىنى ھارام قىلمايتتۇق&raquo; دەيدۇ. ئۇلاردىن بۇرۇنقى كىشىلەر تاكى (بىزنىڭ ئازابىمىز نازىل بولۇپ) ئازابىمىزنى تېتىغانغا قەدەر (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇنداق (يەنى مۇشرىكلار سېنى ئىنكار قىلغاندەك) ئىنكار قىلغان ئىدى. (ئۇلارغا) ئېيتقىنكى، &laquo;سۆزۈڭلارنىڭ راستلىقىغا پاكىتىڭلار بولسا، بىزگە چىقىرىپ كۆرسىتىڭلار، سىلەر پەقەت گۇمانغىلا ئاساسلىنىسىلەر، اﷲ قا پەقەت يالغاننى چاپلايسىلەر&raquo;';
$t[149] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;(سىلەرنىڭ پاكىتىڭلار بولمىسا) اﷲ نىڭ ئېنىق پاكىتى بار، ئەگەر ئۇ خالىسا ھەممىڭلارنى، ئەلۋەتتە، ھىدايەت قىلاتتى&raquo;';
$t[150] = '(ئى مۇھەممەد! ئۇلارغا) &laquo;اﷲ بۇنى ھارام قىلدى، دەپ گۇۋاھلىق بېرىدىغان گۇۋاھچىلىرىڭلارنى چاقىرىپ كېلىڭلار&raquo; دېگىن، ئەگەر ئۇلار (ھازىر بولۇپ يالغاندىن) گۇۋاھلىق بەرسە، ئۇلارغا قوشۇلۇپ گۇۋاھلىق بەرمىگىن (چۈنكى ئۇ پۈتۈنلەي يالغاندۇر). بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلار پەرۋەردىگارىڭغا شېرىك كەلتۈرىدۇ';
$t[151] = 'ئېيتقىنكى، &laquo;سىلەر كېلىپ (ئاڭلاڭلار)، اﷲ ھارام قىلغان نەرسىلەرنى سىلەرگە ئوقۇپ بېرەي: سىلەر اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا - ئاناڭلارغا ياخشىلىق قىلىڭلار، نامراتلىقتىن قورقۇپ بالاڭلارنى ئۆلتۈرمەڭلار، بىز ئۇلارغا ۋە سىلەرگە رىزىق بېرىمىز، ئاشكارا ۋە يوشۇرۇن يامان ئىشلارغا يېقىن كەلمەڭلار، الله نىڭ ئەمرىگە خىلاپلىق قىلىپ (ناھەق) ئادەم ئۆلتۈرمەڭلار، ھەقلىق رەۋىشتە ئۆلتۈرۈش بۇنىڭدىن مۇستەسنا. سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ';
$t[152] = 'يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر ئۇنىڭغا ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئۆلچەمنى ۋە تارازىنى توغرا قىلىڭلار، ھېچقانداق ئادەمنى كۈچى يەتمەيدىغان ئىشقا تەكلىپ قىلمايمىز. سۆز قىلغان (ھۆكۈم چىقارغان ياكى گۇۋاھلىق بەرگەن) چېغىڭلاردا ئادىل بولۇڭلار، (ھۆكۈم چىقىرىلغان ياكى گۇۋاھلىق بېرىلگەن ئادەم) تۇققىنىڭلار بولغان تەقدىردىمۇ، اﷲ قا بېرىلگەن ئەھدىگە ۋاپا قىلىڭلار، ئىبرەت ئېلىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ&raquo;';
$t[153] = 'بۇ مېنىڭ توغرا يولۇمدۇر، شۇ يولدا مېڭىڭلار، ناتوغرا يوللاردا ماڭماڭلار، ئۇلار سىلەرنى اﷲ نىڭ يولىدىن ئايرىۋېتىدۇ، (اﷲ نىڭ ئەمرىنى تۇتۇپ دوزاختىن) ساقلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ';
$t[154] = 'ئاندىن ئۇلار (يەنى ئىسرائىل ئەۋلادى) ئۆز پەرۋەردىگارىغا مۇلاقات بولىدىغانلىقىغا ئىشەنسۇن دەپ، ياخشىلىق قىلغان ئادەمگە بېرىدىغان نېمىتىمىزنى مۇكەممەللەشتۈرۈش، (ئىسرائىل ئەۋلادىنىڭ دىنىي ئىشلىرىدا ئېھتىياجلىق بولغان) ھەر بىر نەرسىنى تەپسىلىي بايان قىلىش، (ئىسرائىل ئەۋلادىنى) ھىدايەت قىلىش ۋە (ئۇلارغا) رەھمەت قىلىش يۈزىسىدىن، مۇساغا كىتاب (يەنى تەۋرات) ئاتا قىلدۇق';
$t[155] = 'بۇ بىز نازىل قىلغان (قۇرئان) مۇبارەك كىتابتۇر. اﷲ نىڭ رەھمىتىنى ئۈمىد قىلغۇچىلاردىن بولۇش ئۈچۈن، ئۇنىڭغا ئەگىشىڭلار، (ئۇنىڭغا مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار';
$t[156] = '(مېنىڭ قۇرئان نازىل قىلىشىم) سىلەرنىڭ، پەقەت بىزدىن ئىلگىرىكى تائىپىگىلا (يەنى يەھۇدىيلار ۋە ناسارالارغىلا) كىتاب نازىل قىلىنغان، ئۇلارنىڭ ئوقۇغان كىتابلىرىدىن بىزنىڭ زادىلا خەۋىرىمىز يوق، دېمەسلىكىڭلار ئۈچۈندۇر';
$t[157] = 'ياكى سىلەرنىڭ، ئەگەر بىزگە كىتاب نازىل قىلىنغان بولسا، بىز ئەلۋەتتە ئۇلاردىن ئارتۇق ھىدايەت تاپقان بولاتتۇق، دېمەسلىكىڭلار ئۈچۈندۇر. سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەقىقەتەن پاكىت، ھىدايەت ۋە رەھمەت نازىل بولدى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە ئۇلاردىن يۈز ئۆرۈگەن كىشىدىنمۇ زالىم ئادەم بارمۇ؟ بىزنىڭ ئايەتلىرىمىزدىن يۈز ئۆرۈگەنلەرنى ئۇلارنىڭ يۈز ئۆرۈگەنلىكلىرى ئۈچۈن قاتتىق ئازاب بىلەن جازالايمىز';
$t[158] = 'ئۇلار پەقەت (ئۆلۈم) پەرىشتىلىرىنىڭ ياكى پەرۋەردىگارىڭنىڭ ياكى پەرۋەردىگارىڭنىڭ (قۇياشنىڭ مەغرىبتىن چىقىشىدىن ئىبارەت) بەزى ئالامەتلىرىنىڭ كېلىشىنىلا كۈتىدۇ. پەرۋەردىگارىڭنىڭ بەزى ئالامەتلىرى كەلگەن كۈندە ئىلگىرى ئىمان ئېيتمىغانلارنىڭ ياكى ئىمان ئېيتىپ ياخشى ئىش قىلمىغانلارنىڭ ئېيتقان ئىمانى پايدىسىز بولىدۇ. &laquo;(سىلەر ئۆزۈڭلارغا كېلىدىغان ئىشلارنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە كۈتىمىز&raquo; دېگىن';
$t[159] = 'دىنىدا بۆلگۈنچىلىك قىلىپ تۈرلۈك پىرقىلەرگە ئايرىلغانلاردىن سەن ئادا - جۇداسەن؛ ئۇلارنىڭ ئىشى پەقەت اﷲ غىلا خاستۇر (يەنى اﷲ نىڭ باشقۇرۇشىدىدۇر)، كېيىن (يەنى ئاخىرەتتە) اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۆزىگە ئېيتىپ بېرىدۇ';
$t[160] = 'بىرەر ياخشىلىق قىلغان ئادەم ئون ھەسسە ئارتۇق ساۋابقا ئىگە بولىدۇ، بىرەر يامانلىق قىلغان ئادەمگە پەقەت ئۇنىڭ يامانلىقىغا باراۋەرلا جازا بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ';
$t[161] = '(ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، &laquo;پەرۋەردىگارىم مېنى توغرا يولغا يەنى توغرا دىنغا، باتىل دىنلاردىن توغرا دىنغا بۇرالغان ئىبراھىمنىڭ دىنىغا باشلىدى، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى&raquo;';
$t[162] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;مېنىڭ نامىزىم، قۇربانلىقىم، ھاياتىم ۋە ماماتىم (دۇنيادا قىلغان ياخشىلىقلىرىم ۋە تائەت - ئىبادەتلىرىم) ئالەملەرنىڭ پەرۋەردىگارى اﷲ ئۈچۈندۇر';
$t[163] = 'اﷲ نىڭ شېرىكى يوقتۇر، مەن مۇشۇنىڭغا (يەنى يالغۇز اﷲ غىلا خالىس ئىبادەت قىلىشقا) بۇيرۇلدۇم، مەن مۇسۇلمانلارنىڭ ئەۋۋىلىمەن&raquo;';
$t[164] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;اﷲ ھەممە نەرسىنىڭ پەرۋەردىگارى تۇرسا، ئۇنىڭدىن باشقا پەرۋەردىگار تەلەپ قىلامدىمەن؟&raquo; ھەر ئادەم ئۆزى قىلغان گۇناھقا ئۆزى جاۋابكار، بىر گۇناھكار ئادەم باشقا ئادەمنىڭ گۇناھىغا جاۋابكار ئەمەس (يەنى بىر ئادەمنىڭ گۇناھى بىلەن باشقا بىر ئادەم جازاغا تارتىلمايدۇ). كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، سىلەر دەتالاش قىلىشقان ھەق بىلەن ناھەقنى ئۇ سىلەرگە ئېيتىپ بېرىدۇ';
$t[165] = 'اﷲ سىلەرنى (ئۆتكەنكى ئۈممەتلەرنىڭ) ئورۇنباسارلىرى قىلدى، اﷲ سىلەرنى بەرگەن نېمىتىگە شۈكۈر قىلسۇن دەپ، بەزىڭلارنى بەزىڭلاردىن بىر قانچە دەرىجە يۇقىرى قىلدى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلاردىن) ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر، (ئىتائەت قىلغۇچىلارنى) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر';
?>