$t[36] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، ئۇنىڭغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلار دوزاخ ئەھلىدۇر، ئۇلار ھەمىشە دوزاختا قالغۇسىدۇ';
$t[37] = 'اﷲ قا يالغان چاپلىغان ياكى ئۇنىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم كىشى بارمۇ؟ ئۇلار لەۋھۇلمەھپۇزدا يېزىلغان (رىزىقتىن ۋە ئەجەلدىن) ئۆز نېسىۋىسىنى كۆرىدۇ، ھەتتا بىزنىڭ پەرىشتىلىرىمىز ئۇلارنىڭ جانلىرىنى ئېلىش ئۈچۈن كەلگەندىمۇ، ئۇلارغا: &lsaquo;&lsaquo;اﷲ نى قويۇپ ئىبادەت قىلغان مەبۇدلىرىڭلار قەيەردە؟ &rsaquo;&rsaquo; دەيدۇ، ئۇلار: &lsaquo;&lsaquo;ئۇلار بىزدىن غايىب بولۇپ كەتتى&rsaquo;&rsaquo; دەيدۇ. ئۇلار كاپىر بولغانلىقلىرىغا ئۆزلىرىنڭ زىيىنىغا گۇۋاھلىق بېرىدۇ (يەنى كاپىر بولغانلىقلىرىغا ئىقرار قىلىدۇ)';
$t[38] = 'اﷲ (ئايەتلىرىنى ئىنكار قىلغانلارغا) ئېيتىدۇ: &lsaquo;&lsaquo;سىلەردىن ئىلگىرى ئۆتكەن، جىنلاردىن ۋە ئىنسانلاردىن بولغان (كاپىر) ئۈممەتلەر بىلەن بىرلىكتە دوزاخقا كىرىڭلار&rsaquo;&rsaquo;. ھەرقاچان بىر ئۈممەت دوزاخقا كىرىدىكەن، ئۇ ئۆزىدىن ئىلگىرىكى ئۈممەتكە (ئۇنىڭغا ئەگىشىپ گۇمراھ بولغانلىقى ئۈچۈن) لەنەت ئوقۇيدۇ. ئۇلارنىڭ ھەممىسى دوزاخقا كىرىپ بولۇپ جەملەشكەندە، ئۇلارنىڭ ئىچىدىكى كېيىن كىرگەنلەر (يەنى ئەگەشكۈچىلەر) ئىلگىرى كىرگەنلەرگە (يەنى باشلىقلىرىغا) قارىتىپ: &lsaquo;&lsaquo;ئى پەرۋەردىگارىمىز! بۇلار بىزنى ئازدۇرغان. ئۇلارغا ئىككى ھەسسە دوزاخ ئازابى بەرگىن&rsaquo;&rsaquo; دەيدۇ اﷲ: &lsaquo;&lsaquo;ھەممىگە ئىككى ھەسسە ئازاب بېرىلىدۇ، لېكىن سىلەر (ئازابنىڭ دەھشىتىنى) بىلمەيسىلەر&rsaquo;&rsaquo; دەيدۇ';
$t[39] = 'ئۇلارنىڭ ئارىسىدىكى ئىلگىرىكىلەر كېيىنكىلەرگە: &lsaquo;&lsaquo;سىلەرنىڭ بىزدىن ھېچقانداق ئارتۇقچىلىقىڭلار يوق (يەنى گۇمراھلىقتا ۋەئازابقا تېگىشلىك بولۇشتا بىر - بىرىمىزگە ئوخشاشمىز)، قىلمىشلىرىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار&rsaquo;&rsaquo; دەيدۇ';
$t[40] = 'شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئۇلارغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلارغا ئاسماننىڭ دەرۋازىسى ئېچىلمايدۇ (يەنى ئۇلارنىڭ ياخشى ئەمەلى ۋە دۇئاسى قوبۇل بولمايدۇ)، تاكى تۆگە يىڭنىنىڭ تۆشۈكىدىن ئۆتكۈچە ئۇلار جەننەتكە كىرمەيدۇ (يەنى ئۇلار ھەرگىزمۇ جەننەتكە كىرمەيدۇ)، گۇناھكارلارغا مۇشۇنداق جازا بېرىمىز';
$t[41] = 'دوزاختا ئۇلارنىڭ ئاستىغا سالىدىغان كۆرپىسىمۇ ئوتتىن، ئۈستىگە يېپىنىدىغان يوتقىنىمۇ ئوتتىن بولىدۇ، زالىملارغا مۇشۇنداق جازا بېرىمىز';
$t[42] = 'ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار - كىشىنى پەقەت كۈچى يېتىدىغان ئىشقىلا تەكلىپ قىلىمىز ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە ھەمىشە قالىدۇ';
$t[43] = 'ئۇلارنىڭ كۆڭۈللىرىدىكى ئۆچمەنلىكلىرىنى ئېلىپ تاشلىدۇق (يەنى جەننەتكە كىرگەنلەر ئارىسىدا ئۆزئارا ئاغرىق - ئاداۋەت بولمايدۇ). ئۇلارنىڭ (سارايلىرى) ئاستىدىن (جەننەتنىڭ) ئۆستەڭلىرى ئېقىپ تۇرىدۇ، ئۇلار: &laquo;جىمى ھەمدۇسانا بىزنى بۇ (نېمەتلەرنى ھاسىل قىلىشقا) يېتەكلىگەن اﷲ قا بولسۇنكى، اﷲ بىزنى يېتەكلىمىگەن بولسا، توغرا يول تاپمىغان بولاتتۇق، پەرۋەردىگارىمىزنىڭ پەيغەمبەرلىرى بىزگە ھەقنى ئېلىپ كەلدى&rsaquo;&rsaquo; دەيدۇ، ئۇلارغا: &laquo;قىلغان ئەمەلىڭلار ئۈچۈن سىلەرگە بېرىلگەن جەننەت مانا مۇشۇ&rsaquo;&rsaquo; دەپ نىدا قىلىنىدۇ (يەنى پەرىشتىلەر شۇنداق دەپ نىدا قىلىدۇ)';
$t[44] = 'ئەھلى دوزاخقا: &lsaquo;&lsaquo;بىز پەرۋەردىگارىمىز بىزگە ۋەدە قىلغان نەرسىنى ھەق تاپتۇق، سىلەرمۇ پەرۋەردىگارىڭلار ۋەدە قىلغان نەرسىنى ھەق تاپتىڭلارمۇ؟ &rsaquo;&rsaquo; دەپ توۋلايدۇ. ئۇلار: &lsaquo;&lsaquo;ھەئە&rsaquo;&rsaquo; دەيدۇ، شۇنىڭ بىلەن بىر جاكارچى ئۇلارنىڭ ئارىسىدا (مۇنداق دەپ) جاكارلايدۇ: &lsaquo;&lsaquo;زالىملارغا اﷲ نىڭ لەنىتى بولسۇن';
$t[45] = 'زالىملار كىشىلەرنى اﷲ نىڭ داغدام يولىدىن توسىدۇ، ئۇنىڭ ئەگرى بولۇشىنى تىلەيدۇ ھەمدە ئۇلار ئاخىرەتكە ئىشەنمەيدۇ';
$t[46] = 'ئۇلارنىڭ ئارىسىدا بىر توسما بولىدۇ. توسمىنىڭ ئۈستىدە (ئەئرافتا) ئەھلى جەننەت ۋە ئەھلى دوزاخنى ئۇلارنىڭ سىماسىدىن تونۇيدىغان ئادەملەر (يەنى ئەھلى ئەئراف) بولىدۇ، ئۇلار ئەھلى جەننەتكە: &laquo;سىلەرگە ئامانلىق تىلەيمىز&rsaquo;&rsaquo; دەپ توۋلايدۇ. ئۇلار جەننەتكە كىرمىگەن، (ئەمما) كىرىش ئۈمىدىدە تۇرغانلاردۇر';
$t[47] = 'ئۇلارنىڭ كۆزلىرى ئەھلى دوزاخ تەرەپكە بۇرۇلغاندا: &lsaquo;&lsaquo;ئى پەرۋەردىگارىمىز! بىزنى زالىم قەۋم بىلەن بىللە قىلمىغىن&rsaquo;&rsaquo; دەيدۇ';
$t[48] = 'ئەئرافتىكىلەر (يەنى ياخشىلىقلىرى بىلەن يامانلىقلىرى تەڭ بولۇپ قالغان، جەننەتتىمۇ ئەمەس، دوزاختىمۇ ئەمەس كىشىلەر) سىماسىدىن تونۇيدىغان (دوزىخى) ئادەملەرگە: &lsaquo;&lsaquo;توپلىغان پۇل - ماللىرىڭلار ۋە تەكەببۇرلۇقۇڭلار سىلەرگە نېمىگە ئەسقاتتى؟ &rsaquo;&rsaquo; دەپ توۋلايدۇ';
$t[49] = '(ئەئرافتىكىلەر دوزىخىلار مۆمىنلەرنىڭ پېقىرلىرىنى كۆرسىتىپ تۇرۇپ) سىلەر اﷲ نىڭ رەھمىتىگە ئېرىشەلمەيدۇ، دەپ قەسەم قىلغان كىشىلەر مۇشۇلارمۇ؟ (مانا بۇلارغا) جەننەتكە كىرىڭلار، سىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر (دېيىلدى دەيدۇ)';
$t[50] = 'ئەھلى دوزاخ ئەھلى جەننەتكە: &lsaquo;&lsaquo;سۇدىن ياكى اﷲ سىلەرگە بەرگەن ئىچىدىغان نەرسىلەردىن بىزگە بىر ئاز قۇيۇپ بەرسەڭلار! &rsaquo;&rsaquo; دەپ توۋلايدۇ، ئەھلى جەننەت ئۇلارغا (جاۋابەن): &lsaquo;&lsaquo;اﷲ ئۇلارنى كاپىرلارغا ھارام قىلغان&rsaquo;&rsaquo; دەيدۇ';
$t[51] = 'كاپىرلار دىنلىرىنى مەسخىرە ۋە ئويۇنچۇق قىلىۋالدى، ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىدى، ئۇلارنىڭ بۈگۈنكى كۈنگە مۇلاقات بولۇشنى ئۇنتۇغانلىقلىرى ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرىغا ئوخشاش، بىز بۈگۈن ئۇلارنى ئۇنتۇيمىز (يەنى ئۇلارن دوزاختا قالدۇرىمىز)';
$t[52] = 'ئۇلارغا كىتاب (يەنى قۇرئان) بەردۇق، ئۇنى (يەنى قۇرئاننىڭ مەنىللىرىنى) بىلش ئاساسىدا بايان ۋە رەھمەت قىلدۇق';
$t[53] = 'ئۇلار (يەنى كۇففارلار) پەقەت (ئۆزلىرىگە ۋەدە قىلىنغان ئازابنىڭ) نەتىجىسىنىلا كۈتىدۇ، ئۇنىڭ نەتىجىسى كەلگەن كۈنى (يەنى قىيامەت كۈنى)، ئلگىرى كىتابقا ئەمەل قىلمىغانلار: &lsaquo;&lsaquo;پەرۋەردىگارىمىزنىڭ ئەلچىللىرى ھەقىقەتنى ئېلىپ كەلگەن ئىدى (بىز ئۇلارغا ئىمان ئېيتمىدۇق، بۈگۈن) بىزگە شاپائەت قىلىدىغان شاپائەتچىلەر بارمىدۇ؟ ياكى گۇناھ قىلىشتىن قول ئۈزۈپ، ياخشى ئەمەللەرنى قىلىش ئۈچۈن دۇنياغا قايتۇرۇلۇشىمىزغا بولامدىغاندۇ&rsaquo;&rsaquo; دەيدۇ. ئۇلار ئۆزلىرىگە زىيان سالدى، ئۇلارنىڭ اﷲ نىڭ شېرىكلىرى دەپ ئىپتىرا قىلغان مەبۇدلىرى ئۇلارنىڭ كۆزىدىن غايىب بولدى';
$t[54] = 'شۈبھىسىزكى، پەرۋەردىگارىڭلار اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە (يەنى ئالتە دەۋردە) ياراتتى، ئاندىن (ئۆزىنىڭ ئۇلۇغلۇقىغا لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى، اﷲ كېچە بىلەن (كېچىنىڭ قاراڭغۇلۇقى بىلەن) كۈندۈزنى ياپىدۇ، كېچە بىلەن كۈندۈز بىر - بىرىنى قوغلىشىدۇ (يەنى داۋاملىق ئورۇن ئالمىشىپ تۇرىدۇ): اﷲ قۇياش، ئاي ۋە يۇلتۇزلارنى اﷲ نىڭ ئەمرىگە بويسۇندۇرۇلغۇچى قىلىپ ياراتتى، يارىتىش ۋە ئەمر قىلىش (يەنى كائىناتنى تەسەررۇپ قىلىش) راستىنلا اﷲ نىڭ ئىلكىدىدۇر. ئالەملەرنىڭ پەرۋەردىگارى اﷲ نىڭ دەرىجىسى كاتتىدۇر';
$t[55] = 'پەرۋەردىگارىڭلارغا تۆۋەنچىلىك بىلەن يوشۇرۇن دۇئا قىلىڭلار، (دۇئا قىلغاندا كەلسە - كەلمەس سۆزلەپ، توۋلاپ) ھەددىدىن ئاشۇرۋەتكەنلەرنى اﷲ ياقتۇرمايدۇ';
$t[56] = 'اﷲ (پەيغەمبەرلەرنى ئەۋەتىپ) يەر يۈزىنى تۈزىگەندىن كېيىن، يەر يۈزىدە بۇزۇقچىلىق قىلماڭلار (اﷲ نىڭ ئازابىدىن) قورققان ۋە (رەھمىتىنى) ئۇمىد قىلغان ھالدا دۇئا قىلىڭلار، شۈبھىسىزكى اﷲ نىڭ رەھمىتى ياخشىلىق قىلغۇچىلارغا يېقىندۇر';
$t[57] = 'اﷲ نىڭ رەھمىتى (يەنى يامغۇر) مەيدانغا كېلىش ئالدىدا، اﷲ بېشارەتچى قىلىپ شاماللارنى ئەۋەتىدۇ، شاماللار ئېغىر بۇلۇتلارنى كۆتۈرگەن چاغدا، ئۇلارنى ئۆلۈك (يەنى قاغجىراپ كەتكەن گىياھسىز) جايلارغا سۈرىمىز، ئۇنىڭدىن يامغۇر ياغدۇرۇپ تۈرلۈك - تۈرلۈك مېۋىلەرنى چىقىرىمىز، ئىبرەت ئېلىشىڭلار ئۈچۈن (زېمىندىن مېۋىلەرنى چىقارغاندەك، قىيامەتتە) ئۆلۈكلەرنى قەبرىلىرىدىن چىقىرىمىز';
$t[58] = 'مۇنبەت يەرنىڭ گىياھسى اﷲ نىڭ ئىزنى بىلەن تولۇق ئۈنىدۇ (بۇ ۋەز - نەسىھەتكە قۇلاق سېلىپ، ئۇنىڭدىن پايدىلىنىدىغان مۆمىننىڭ مىسالىدۇر)، ناچار يەرنىڭ گىياھسى ئاز ئۆسىدۇ (بۇ ۋەز - نەسىھەتتىن پايدىلانمايدىغان كاپىرنىڭ مىسالىدۇر)، شۈكۈر قىلىدىغان قەۋم ئۈچۈن ئايەتلىرىمىزنى مۇشۇنداق شەكىللەردە بايان قىلىمىز';
$t[59] = 'شەك شۈبھىسىزكى، نۇھنى ئۆز قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: &lsaquo;&lsaquo;ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىغا قېلىشىڭلاردىن قورقىمەن&rsaquo;&rsaquo; دېدى';
$t[60] = 'قەۋمىنىڭ چوڭلىرى: &lsaquo;&lsaquo;بىز سېنى ھەقىقەتەن روشەن گۇمراھلىقتا كۆرۈمىز&rsaquo;&rsaquo; دېدى';
$t[61] = 'نۇھ ئېيتتى: &lsaquo;&lsaquo;ئى قەۋمىم! مەن قىلچىمۇ گۇمراھ ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن';
$t[62] = 'سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، سىلەرگە نەسىھەت قىلىمەن، مەن اﷲ نىڭ ۋەھيى قىلىشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن';
$t[63] = 'سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن، (كۇفرىدىن) ساقلىنىشىڭلار ۋە اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، سىلەرگە ئاراڭلاردىكى بىر كىشى ئارقىلىق پەرۋەردىگارىڭلارنىڭ ۋەھيىسى كېلىشىدىن ئەجەبلەنمەمسىلەر؟ &rsaquo;&rsaquo;';
$t[64] = 'ئۇلار نۇھنى ئىنكار قىلدى، نۇھنى ئۇنىڭ بىلەن كېمىدە بىللە بولغانلارنى قۇتقۇزدۇق، ئايەتلىرىمىزنى ئىنكار قىلغانلارنى پۈتۈنلەي (سۇغا) غەرق قىلدۇق. شۈبھىسىزكى، ئۇلار (ھەقنى كۆرۈشتىن دىللىرى) كور قەۋم ئىدى';
$t[65] = 'ئاد (قەۋمى) گە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد: &lsaquo;&lsaquo;ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر. (ئۇنىڭ ئازابىدىن) قورقمامسىلەر؟ &rsaquo;&rsaquo; دېدى';
$t[66] = 'ئۇنىڭ قەۋمىنىڭ ئىمان ئېيتمىغان چوڭلىرى: &lsaquo;&lsaquo;بىز سېنى ھەقىقەتەن ئەخمەق ھېسابلايمىز، بىز سېنى، ئەلۋەتتە، (پەيغەمبەرلىك دەۋايىڭدا) يالغانچىلاردىن دەپ گۇمان قىلىمىز&rsaquo;&rsaquo; دېدى';
$t[67] = 'ھۇد ئېيتتى: &lsaquo;&lsaquo;ئى قەۋمىم! مەن ئەخمەق ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن';
$t[68] = 'سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، مەن سىلەرگە ئىشەنچىلىك نەسىھەتچىمەن';
$t[69] = 'پەرۋەردىگارىڭلارنىڭ ۋەھيىسى سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ئۆز ئىچىڭلاردىكى بىر كىشى ئارقىلىق كەلگەنلىكىدىن ئەجەبلىنەمسىلەر؟ &rsaquo;&rsaquo; ئۆز ۋاقتىدا نۇھنىڭ قەۋمى ھالاك بولغاندىن كېيىن، اﷲ نىڭ سىلەرنى ئۇلارنىڭ ئورۇنباسارلىرى قىلغانلىقىنى ۋۇجۇدۇڭلارنى، قامەتلىك، بەستلىك قىلغانلىقىنى ياد ئېتىڭلار، سىلەر بەختكە ئېرىشىشىڭلار ئۈچۈن اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار';
$t[70] = 'ئۇلار: &lsaquo;&lsaquo; (ئى ھۇد) ئاتا - بوۋىللىرىمىز ئىبادەت قىلىپ كېلىۋاتقان مەبۇدلارنى تاشلاپ، يالغۇز بىر اﷲ قا ئىبادەت قىلىشىمىزغا دەۋەت قىلغىلى كەلدىڭمۇ؟ ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرگىن&rsaquo;&rsaquo; دېدى';
$t[71] = 'ھۇد: &lsaquo;&lsaquo;سىلەرگە چوقۇم پەرۋەردىگارىڭلارنىڭ ئازابى ۋە غەزىپى نازىل بولىدۇ، سىلەر ئۆزۈڭلار ۋە ئاتا - بوۋاڭلار ئات قويۇۋالغان، بۇ ھەقتە (ئۇلارغا ئىبادەت قىلىشقا) اﷲ ھېچقانداق دەلىل نازىل قىلمىغان (بۇتلىرىڭلارنىڭ) ئىسىملىرى ئۈستىدە مەن بىلەن مۇنازىرىلىشەمسىلەر؟ (اﷲ نىڭ ئازابىنى) كۈتۈڭلار، مەنمۇ ھەقىقەتەن سىلەر بىلەن بىرلىكتە (سىلەرگە نازىل بولىدىغان ئازابنى) كۈتىمەن&rsaquo;&rsaquo; دېدى';
$t[72] = 'بىز ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغۇچىلارنى (يەنى مۆمىنلەرنى) رەھمىتىمىز بىلەن قۇتقۇزدۇق، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تەلتۆكۈس ھالاك قىلدۇق، ئۇلار ئىمان ئېيتمىغان ئىدى';
$t[73] = 'سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ ئەۋەتتۇق). ئۇ: &laquo;ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلار تەرىپىدىن (مېنىڭ پەيغەمبەرلىكىمنى ئىسپاتلايدىغان) روشەن مۆجىزە كەلدى، مانا بۇ - اﷲ (بىۋاسىتە) ياراتقان چېشى تۆگىمۇ سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى اﷲ نىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارىي ئوتلىسۇن، ئۇنىڭغا زىيان - زەخمەت يەتكۈزمەڭلار، بولمىسا، سىلەرنى قاتتىق ئازاب ھالاك قىلىدۇ';
$t[74] = 'ياد ئېتىڭلاركى، ئۆز ۋاقتىدا اﷲ سىلەرنى ئاد قەۋمىدىن كېيىن ئۇلارنىڭ ئورۇنباسارلىرى قىلدى، سىلەرنى (ھىجر) زېمىنىغا ئورۇنلاشتۇردى، ئۇنىڭ تۈزلەڭلىرىگە ئىمارەتلەر سالدىڭلار، تاغلارنى تېشىپ ئۆيلەر ياسىدىڭلار. اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار، زېمىندا بۇزغۇنچىلىق قىلماڭلار&raquo; دېدى';
$t[75] = 'ئۇنىڭ قەۋمىنىڭ تەكەببۇر چوڭلىرى بوزەك قىلىنغانلارغا يەنى ئۇلارنىڭ ئىچىدىكى مۆمىنلەرگە: &laquo;سىلەر سالىھنى ئۇنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەر دەپ بىلەمسىلەر؟&raquo; دېدى. ئۇلار: &laquo;بىز ئۇنىڭ پەيغەمبەرلىكىگە ئەلۋەتتە ئىشىنىمىز&raquo; دېدى';
$t[76] = 'ھېلىقى تەكەببۇرلار: &laquo;سىلەر ئىشەنگەنگە بىز ھەرگىز ئىشەنمەيمىز&raquo; دېدى';
$t[77] = 'ئۇلار چىشى تۆگىنى بوغۇزلىدى، پەرۋەردىگارىنىڭ ئەمرىنى تۇتۇشتىن باش تارتتى ۋە ئى سالىھ: &laquo;راستتىنلا پەيغەمبەرلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرۈپ باق&raquo; دېدى';
$t[78] = 'ئۇلارغا قاتتىق ئازاب يۈزلەندى - دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى';
$t[79] = 'ئۇ (ئۇلار ھالاك بولغاندىن كېيىن) ئۇلارغا ئارقىسىنى قىلدى ۋە: &laquo;ئى قەۋمىم! سىلەرگە ھەقىقەتەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم، سىلەرگە نەسىھەت قىلدىم، لېكىن سىلەر نەسىھەت قىلغۇچىلارنى ياقتۇرمىدىڭلار&raquo; دېدى';
$t[80] = 'لۇتنى (ئەھلى سەدومغا پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ ئۆز قەۋمىگە (ئۇلارنى ئەيىبلەش يۈزىسىدىن) مۇنداق دېگەن ئىدى: &laquo;سىلەر شۇنداق قەبىھ ئىشنى قىلامسىلەر؟ مۇنداق قەبىھ ئىشنى سىلەردىن ئىلگىرى جاھان ئەھلىدىن ھېچقانداق ئادەم قىلمىغان ئىدى';
$t[81] = 'سىلەر ھەقىقەتەن ئاياللارنى تاشلاپ قويۇپ، ئەرلەر بىلەن جىنسىي تەلىپىڭلارنى قاندۇرىسىلەر، سىلەر ھەقىقەتەن ھەددىدىن ئاشقۇچى قەۋمسىلەر&raquo;';
$t[82] = 'لۇتنىڭ قەۋمىنىڭ بىردىنبىر جاۋابى: &laquo;لۇتنى ئۇنىڭ ئەگەشكۈچىلىرى بىلەن قوشۇپ شەھرىڭلاردىن چىقىرىۋېتىڭلار، چۈنكى ئۇلار پاك كىشىلەردۇر&raquo; دېيىشتىن ئىبارەت بولدى';
$t[83] = 'لۇتنى ئۇنىڭ خوتۇنىدىن باشقا، تەۋە كىشىلىرى بىلەن بىللە (ئۇنىڭ قەۋمىگە نازىل بولغان ئازابتىن) قۇتقۇزدۇق، پەقەت خوتۇنىلا (شەھەردە) قىلىپ ھالاك بولغۇچىلاردىن بولدى';
$t[84] = 'ئۇلارغا بىر تۈرلۈك يامغۇر (يەنى تاش) ياغدۇردۇق، گۇناھلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن';
$t[85] = 'مەديەن (خەلقىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۇ (ئۆز قەۋمىگە) ئېيتتى: &laquo;ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە پەرۋەردىگارىڭلاردىن ھەقىقەتەن (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) مۆجىزە كەلدى، ئۆلچەمنى ۋە تارازىنى توغرىلاڭلار، كىشىلەرنىڭ نەرسىلىرىنى كەم بەرمەڭلار. (اﷲ پەيغەمبەرلەرنى ئەۋەتىپ) زېمىننى تۈزىگەندىن كېيىن، زېمىندا (گۇناھلار قىلىپ) بۇزغۇنچىلىق قىلماڭلار، ئەگەر مۆمىن بولساڭلار، ئەنە شۇ سىلەر ئۈچۈن ياخشىدۇر';
$t[86] = 'سىلەر اﷲ قا ئىمان ئېيتقانلارنى قورقۇتقان، اﷲ نىڭ يولىدىن توسقان ۋە ئۇ يولنىڭ ئەگرىلىكىنى تىلىگەن ھالدا يوللاردا ئولتۇرماڭلار، ئەسلىدە سىلەر ئاز ئىدىڭلار، اﷲ نىڭ سىلەرنىڭ سانىڭلارنى كۆپەيتكەنلىكىنى ياد ئېتىڭلار، بۇزۇقچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قاراڭلار';
$t[87] = 'ئەگەر سىلەردىن بىر گۇرۇھ ئادەم مېنىڭ پەيغەمبەرلىكىمگە ئىشەنگەن، يەنە بىر گۇرۇھ ئادەم ئىشەنمىگەن بولسا، اﷲ بىزنىڭ ئارىمىزدا ھۆكۈم چىقارغۇچە سەۋر قىلىپ تۇرۇڭلار. اﷲ ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇر&raquo;';
$t[88] = 'شۇئەيب قەۋمىنىڭ (ئىماندىن باش تارتقان) تەكەببۇر چوڭلىرى: &laquo;ئى شۇئەيب! سېنى چوقۇم ئەگەشكۈچىلىرىڭ (يەنى ساڭا ئىمان ئېيتقانلار) بىلەن قوشۇپ شەھرىمىزدىن ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك&raquo; دېدى. شۇئەيب: &laquo;بىز (بۇنىڭ ھەر ئىككىلىسىنى يامان كۆرۈدىغان تۇرساق (بىزنى يەنىلا شۇنىڭغا مەجبۇر قىلامسىلەر؟)&raquo; دېدى';
$t[89] = '(شۇئەيب يەنى ئېيتتى) &laquo;اﷲ بىزنى سىلەرنىڭ دىنىڭلاردىن قۇتۇلدۇرغاندىن كېيىن ئۇنىڭغا قايتساق، ئەلۋەتتە، اﷲ قا يالغان چاپلىغان بولىمىز. پەرۋەردىگارىمىز اﷲ خالىسىلا، بىز سىلەرنىڭ دىنىڭلارغا قايتمايمىز. پەرۋەردىگارىمىزنىڭ ئىلمى ھەممىنى ئۆز ئىچىگە ئالىدۇ، اﷲ قا تەۋۋەككۈل قىلدۇق (يەنى ھەممە ئىشىمىزنى اﷲ قا تاپشۇرىمىز). پەرۋەردىگارىمىز! بىز بىلەن قەۋمىمىز ئارىسىدا ھەق ھۆكۈم چىقارغىن، سەن ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇرسەن&raquo;';
$t[90] = 'ئۇنىڭ قەۋمىنىڭ ئىمانسىز چوڭلىرى: &laquo;ئەگەر سىلەر شۇئەيبكە ئەگەشسەڭلار، شۈبھىسىزكى، چوقۇم زىيان تارتىسىلەر&raquo; دېدى';
$t[91] = 'ئۇلارغا قاتتىق زىلزىلە يۈزلەندى، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى';
$t[92] = 'شۇئەيبنى ئىنكار قىلغانلار (اﷲ نىڭ ھالاك قىلىشى بىلەن) گويا بۇ يەردە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى. شۇئەيبنى ئىنكار قىلغانلار زىيان تارتقۇچىلاردىن بولدى';
$t[93] = 'شۇئەيب ئۇلارغا ئارقىسىنى قىلىپ: &laquo;ئى قەۋمىم! مەن سىلەرگە ھەقىقەتەن پەرۋەدىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم ۋە نەسىھەت قىلدىم، مەن قانداقمۇ كاپىر قەۋمگە قايغۇراي!&raquo; دېدى';
$t[94] = 'بىز مەيلى قايسى شەھەرگە بولسۇن، بىرەر پەيغەمبەر ئەۋەتكەن بولساق (شەھەر ئەھلى ئۇنى ئىنكار قىلغان بولسا)، ئۇلارنىڭ اﷲ قا يالۋۇرۇشلىرى (تەۋبە قىلىشلىرى) ئۈچۈن، ئۇلارنى نامراتلىققا، كېسەللىككە گىرىپتار قىلدۇق';
$t[95] = 'ئاندىن (ئۇلارنىڭ بېشىغا كەلگەن) كۈلپەتنى نېمەتكە ئايلاندۇردۇق، شۇنىڭ بىلەن ئۇلار كۆپەيدى، ئۇلار: &laquo;بىزنىڭ ئاتا - بوۋىلىرىمىزنىڭ بېشىغا ھەقىقەتەن كۈلپەتلەر، نېمەتلەر كەلگەن ئىدى (بۇ - زاماننىڭ ئادىتى؛ اﷲ تەرىپىدىن كەلگەن ئوقۇبەت ئەمەس)&raquo; دېدى. ئۇلار خەۋەرسىز تۇرغاندا، ئۇلارنى ئۇشتۇمتۇت ھالاك قىلدۇق';
$t[96] = '(پەيغەمبەرلىرىنى ئىنكار قىلغانلىقتىن ھالاك قىلىنغان) شەھەرلەرنىڭ ئاھالىسى ئىمان ئېيتقان ۋە (كۇفرىدىن، گۇناھلاردىن) ساقلانغان بولسا ئىدى، ئەلۋەتتە، ئۇلارنى ئاسمان - زېمىننىڭ پاراۋانلىقلىرىغا مۇيەسسەر قىلاتتۇق، لېكىن ئۇلار (پەيغەمبەرلەرنى) ئىنكار قىلدى، شۇ ئۇلارنى ئۆز قىلمىشلىرى تۈپەيلىدىن ھالاك قىلدۇق';
$t[97] = '(پەيغەمبەرلەرنى ئىنكار قىلىدىغان قىلىدىغان) شەھەرلەرنىڭ ئاھالىسى بىزنىڭ ئازابىمىزنىڭ ئۆزلىرىگە كېچىسى (غەپلەتتە) ئۇخلاۋاتقانلىرىدا كېلىشىدىن قورقمامدۇ';
$t[98] = 'شەھەرلەر ئاھالىسى بىزنىڭ ئازابىمىزنىڭ چۈش ۋاقتىدا - ئۇلار ئويناپ تۇرغانلىرىدا كېلىشىدىن قورقمامدۇ';
$t[99] = 'ئۇلار اﷲ نىڭ مەكرىدىن (يەنى ئۇلار تۇيۇقسىز تۇرغاندا اﷲ نىڭ ئازاب نازىل قىلىشىدىن) قورقمامدۇ؟ پەقەت زىيان تارتقۇچى قەۋملا اﷲ نىڭ مەكرىدىن قورقمايدۇ';
$t[100] = 'زېمىننىڭ ئىلگىرىكى ئىگىلىرى (ھالاك بولغان) دىن كېيىن (زېمىنغا) ۋارىس بولغۇچىلارنى، خالىساق، گۇناھى تۈپەيلىدىن ھالاك قىلىدىغانلىقىمىز، دىللىرىنى پىچەتلىۋېتىدىغانلىقىمىز ئۇلارغا (يەنى زېمىنغا ۋارىسلىق قىلغۇچىلارغا) ئايان بولمىدىمۇ؟ ئۇلار (ھەق سۆزنى) ئاڭلىمايدۇ';
$t[101] = 'ئاشۇ شەھەرلەر (ئاھالىسى) نىڭ بەزى خەۋەرلىرىنى (ئى مۇھەممەد!) ساڭا ھېكايە قىلىپ بېرىمىز، شەك - شۈبھىسىزكى، ئۇلارنىڭ پەيغەمبەرلىرى ئۇلارغا مۆجىزىلەر ئېلىپ كەلگەن ئىدى، ئۇلار ئىلگىرى (ھەقنى) ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇ مۆجىزىلەرگە) ئىمان ئېيتمىدى. اﷲ كاپىرلارنىڭ دىللىرىنى ئەنە شۇنداق پېچەتلەيدۇ';
$t[102] = 'ئۇلارنىڭ تولىسىنىڭ (روزىمىساقتا) بەرگەن ۋەدىسىگە ۋاپا قىلىدىغانلىقىنى بايقىمىدۇق، ئۇلارنىڭ تولىسىنىڭ شەكسىز پاسىقلار (يەنى اﷲ نىڭ ئەمر - پەرمانىدىن باش تارتقۇچىلار) ئىكەنلىكىنى بايقىدۇق';
$t[103] = 'مەزكۇز پەيغەمبەرلەردىن كېيىن، مۇسانى مۆجىزىلىرىمىز بىلەن، (مىسىرنىڭ مۇسا زامانىدىكى پادىشاھى) پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار بۇ مۆجىزىلەرنى ئىنكار قىلدى، بۇزغۇنچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن';
$t[104] = 'مۇسا ئېيتتى: &laquo;ئى پىرئەۋن! مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئەۋەتكەن پەيغەمبەرمەن';
$t[105] = 'اﷲ نامىدىن پەقەت ھەقىقەتتىن باشقىنى ئېيتماسلىققا لايىقمەن، راستتىنلا پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) بىر مۆجىزە ئېلىپ كەلدىم، شۇنىڭ ئۈچۈن، ئىسرائىل ئەۋلادىنى مەن بىلەن قويۇپ بەرگىن (مەن بىلەن بىللە ئاتا - بوۋىلىرىنىڭ ۋەتىنى مۇقەددەس زېمىنغا بارسۇن)&raquo;';
$t[106] = 'پىرئەۋن (مۇساغا): &laquo;ئەگەر سەن پەرۋەردىگارىڭ تەرىپىدىن) بىر مۆجىزە ئېلىپ كەلگەن بولساڭ، (بۇ دەۋايىڭدا) راستچىللاردىن بولساڭ، ئۇنى بىزگە كۆرسەتكىن&raquo; دېدى';
$t[107] = 'مۇسا ھاسىسىنى تاشلىۋېدى، ئۇ ناگاھان ئاشكارا بىر ئەجدىھاغا ئايلاندى';
$t[108] = 'قولىنى (ياقىسىدىن) چىقىرىۋىدى، ئۇ ناگاھان قارىغۇچىلارغا (نۇر چاقنىتىپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى';
$t[109] = 'پىرئەۋن قەۋمنىڭ چوڭلىرى ئېيتتى: &laquo;بۇ ھەقىقەتەن ناھايىتى ئۇستا سېھىرگەر ئىكەن';
$t[110] = 'ئۇ سىلەرنى زېمىنىڭلار (يەنى مىسىر زېمىنى) دىن ھەيدەپ چىقىرىۋەتمەكچى. نېمە مەسلىھەت بېرىسىلەر؟&raquo;';
$t[111] = 'ئۇلار ئېيتتى: &laquo;(ئۇلارنىڭ ئىشىنى ئۆزۈڭ بىر پىكىرگە كەلگۈچە) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىغقۇچى كىشىلەرنى ئەۋەتكىن';
$t[112] = 'ئۇلار بارلىق ئۇستا سېھىرگەرلەرنى سېنىڭ ھۇزۇرۇڭغا ئېلىپ كەلسۇن&raquo;';
$t[113] = '(پىرئەۋن سېھىرگەرلىرىنى يىغىشقا ئادەملىرىنى ئەۋەتتى) سېھىرگەرلەر پىرئەۋننىڭ قېشىغا كەلدى، ئۇلار: &laquo;ئەگەر بىز غەلىبە قىلساق، بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟&raquo; دېدى';
$t[114] = 'پىرئەۋن: &laquo;ھەئە، (مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن كىشىلىرىم بولۇپ قالىسىلەر&raquo; دېدى';
$t[115] = 'ئۇلار (يەنى سېھىرگەرلەر): &laquo;ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن، ياكى بىز (ئۆزىمىزنىڭكىنى) ئاۋۋال تاشلامدۇق؟&raquo; دېدى';
$t[116] = 'مۇسا: &laquo;سىلەر تاشلاڭلار!&raquo; دېدى. ئۇلار ھاسا، ئارغامچىلارنى تاشلاپ كىشىلەرنىڭ كۆزلىرىنى باغلىدى، ئۇلارنى (يەنى كىشىلەرنى) قاتتىق چۆچۈتۈۋەتتى، ئۇلار (كىشىلەرنىڭ كۆزلىرىگە) چوڭ (كۆرۈنىدىغان) سېھىرنى كۆرسەتتى';
$t[117] = 'مۇساغا: &laquo;ھاساڭنى تاشلىغىن&raquo; دەپ ۋەھىي قىلدۇق، (مۇسا ھاسىسىنى تاشلىۋىدى، ئۇ ئەجدىھاغا ئايلىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى دەرھال يۇتۇۋەتتى';
$t[118] = 'ھەقىقەت ئاشكارا بولدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرى بەربات بولدى';
$t[119] = 'بۇ يەردە ئۇلار (يەنى پىرئەۋن بىلەن قەۋمى) مەغلۇپ بولدى، خار بولغان ھالدا (شەھەرگە قايتتى)';
$t[120] = 'سېھىرگەرلەر سەجدىگە باردى';
$t[121] = 'ئۇلار دېدى: &laquo;ئالەملەرنىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق';
$t[122] = 'مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا (ئىمان ئېيتتۇق)&raquo;';
$t[123] = 'پىرئەۋن ئېيتتى: &laquo;سىلەر مەن رۇخسەت قىلماي تۇرۇپ مۇساغا ئىمان ئېيتتىڭلار، بۇ چوقۇم سىلەرنىڭ شەھەر (يەنى مىسىر) دىكى چېغىڭلاردا ئاھالىنى شەھەردىن ھەيدەپ چىقىرىش ئۈچۈن (مۇسا بىلەن بىرلىشىپ) ئالدىن پىلانلىغان ھىيلەڭلاردۇر (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزاققا قالماي بىلىسىلەر';
$t[124] = 'سىلەرنىڭ قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ - چەپ قىلىپ (ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول پۇتۇڭلار بىلەن ئوڭ قولۇڭلارنى) چوقۇم كېسىمەن، ئاندىن ھەممىڭلارنى چوقۇم دارغا ئېسىپ ئۆلتۈرىمەن&raquo;';
$t[125] = 'سېھىرگەرلەر ئېيتتى: &laquo;بىز، ئەلۋەتتە، پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز';
$t[126] = 'سەن بىزنى پەقەت پەرۋەردىگارىمىزنىڭ بىزگە نازىل قىلغان ئايەتلىرىگە ئىمان ئېيتقانلىقىمىز ئۈچۈنلا ئەيىبلەۋاتىسەن، پەرۋەردىگارىمىز! بىزگە سەۋر ئاتا قىلغىن، بىزنى مۇسۇلمان پېتىمىزچە قەبزى روھ قىلغىن&raquo;';
$t[127] = 'پىرئەۋن قەۋمىنىڭ چوڭلىرى (پىرئەۋنگە): &laquo;مۇسا بىلەن ئۇنىڭ قەۋمىنى زېمنىدا پىتنە - پاسات تۇغدۇرۇشقا، سېنى ۋە سېنىڭ ئىلاھلىرىڭنى تاشلاشقا قويۇۋېتەمسەن؟&raquo; دېدى. پىرئەۋن: &laquo;ئۇلارنىڭ باللىلىرىنى ئۆلتۈرىمىز، ئاياللىرىنى بولسا (خىزمەتكە سېلىشقا) قالدۇرۇپ قويىمىز، بىز ئەلۋەتتە ئۇلارنىڭ ئۈستىدىن ھۆكۈمرانلىق قىلىمىز&raquo; دېدى';
$t[128] = 'مۇسا ئۆز قەۋمىگە: &laquo;اﷲ تىن مەدەت تىلەڭلار، سەۋر قىلىڭلار، يەر يۈزى ھەقىقەتەن اﷲ نىڭ مۈلكىدۇر، (اﷲ) ئۇنىڭغا بەندىلىرىدىن ئۆزى خالىغان كىشىلەرنى ۋارىس قىلىدۇ؛ ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپ&raquo; دېدى';
$t[129] = 'ئۇلار: &laquo;سەن بىزگە كېلىشتىن بۇرۇنمۇ، كەلگەندىن كېيىنمۇ ھامان خارلىنىپ كەلدۇق&raquo; دېدى. مۇسا: &laquo;پەرۋاردىگارىڭلارنىڭ دۈشمىنىڭلارنى ھالاك قېلىشى، (مىسىر) زېمىنىدا ئۇلارنىڭ ئورنىغا سىلەرنى دەسسىتىشى، سىلەرنىڭ قانداق ئىش قىلىدىغانلىقىڭلارغا قارىشىمۇ ھەقىقەقتۇر&raquo; دېدى';
$t[130] = 'اﷲ قا قەسەمكى، بىز ھەقىقەتەن پىرئەۋن ۋە ئۇنىڭ تەۋەلىرىنىڭ ئىبرەت ئېلىشلىرى ئۈچۈن، ئۇلارنى قەھەتچىلىك بىلەن، مېۋىلىرىنىڭ ھوسۇلىنى كېمەيتىۋېتىش بىلەن سىنىدۇق';
$t[131] = 'ئۇلار ئەگەر (پاراۋانلىق - مولچىلىقتەك) بىرەر ياخشىلىققا ئېرىشسە، بىز بۇنىڭغا ھەقلىقمىز، دەيدۇ. ئەگەر ئۇلارغا بىرەر يامانلىق كەلسە، ئۇلار: بۇ مۇسا ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنىڭ شۇملۇقىدىن كەلدى، دەيدۇ. ئۇلارغا يەتكەن ياخشىلىق بىلەن يامانلىق (مۇسانىڭ شۇملۇقىدىن ئەمەس) اﷲ نىڭ تەقدىرىدۇر، لېكىن ئۇلارنىڭ تولىسى بۇنى (يەنى ئۇلارغا كەلگەن بالا - قازانىڭ ئۆز گۇناھلىرى تۈپەيلى اﷲ تەرىپىدىن كەلگەنلىكىنى ئۇقمايدۇ';
$t[132] = 'ئۇلار (يەنى پىرئەۋن قەۋمى): &laquo;كۆزىمىزنى باغلاش ئۈچۈن ھەرقانداق مۆجىزە كەلتۈرسەڭمۇ بىز ساڭا ھەرگىز ئىشەنمەيمىز&raquo; دېدى';
$t[133] = 'ئۇلارغا سۇ ئاپىتى، چېكەتكە، پىت، پاقا، قان (بالالىرىنى) روشەن مۆجىزىلەر قىلىپ ئەۋەتتۇق، (اﷲ قا ئىمان ئېيتىشقا) ئۇلارنىڭ گەدەنكەشلىكى يول قويمىدى، ئۇلار گۇناھكار قەۋم بولدى';
$t[134] = 'ئۇلارغا بالا نازىل بولغاندا، ئۇلار: &laquo;ئى مۇسا! پەرۋەردىگارىڭغا ئۆزىنىڭ ساڭا ئاتا قىلغان ئەھدى بىلەن (يەنى ساڭا بەرگەن پەيغەمنبەرلىكنىڭ ھەقىقىي ھۆرمىتى بىلەن بىزدىن بالانى كۆتۈرۈۋېتىشكە) دۇئا قىلساڭ، اﷲ قا قەسەمكى، ئەگەر بىزدىن بالانى كۆتۈرۈۋەتسەڭ، ساڭا چوقۇم ئىمان ئېيتىمىز، ئىسرائىل ئەۋلادىنى چوقۇم سەن بىلەن بىللە قويۇپ بېرىمىز&raquo; دېگەن ئىدى';
$t[135] = 'ئۇلاردىن بالانى ۋاقىتلىق كۆتۈرۈۋەتكەن چېغىمىزدا، ئۇلار توساتتىن ئەھدىلىرىنى بۇزدى';
$t[136] = 'ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە ئايەتلىرىمىزدىن غاپىل بولغانلىقلىرى ئۈچۈن، ئۇلارنى دېڭىزغا غەرق قىلىپ جازالىدۇق';
$t[137] = 'بوزەك قىلىنغان قەۋمنى (يەنى بەنى ئىسرائىلنى) بىز بەرىكەتلىك قىلغان (شام) زېمىننىڭ شەرق تەرەپلىرىگە ۋە غەرب تەرەپلىرىگە (يەنى ھەممە تەرەپكە) ۋارىس قىلدۇق. ئىسرائىل ئەۋلادى سەۋر قىلغانلىقلىرى ئۈچۈن، پەرۋەردىگارىڭنىڭ ئۇلارغا قىلغان چىرايلىق ۋەدىسى تولۇق ئىشقا ئاشتى؛ پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ سالغانلىرى (يەنى ئىمارەتلىرى) نى ۋە ياسىغانلىرى (يەنى باغلىرى ۋە ئېكىنزارلىقلىرى) نى ۋەيران قىلدۇق';
$t[138] = 'ئىسرائىل ئەۋلادىنى بىز (قۇلزۇم) دېڭىزىدىن ئۆتكۈزۈۋەتتۇق، ئۇلار بۇتلىرىغا چوقۇنۇۋاتقان بىر قەۋمنىڭ يېنىدىن ئۆتكەندە: &laquo;ئى مۇسا! بىزگىمۇ ئۇلارنىڭ بۇتلىرىغا ئوخشاش بۇت ئورنىتىپ بەرگىن&raquo; دېدى. مۇسا: &laquo;سىلەر ھەقىقەتەن نادان قەۋم ئىكەنسىلەر&raquo; دېدى';
$t[139] = 'شۈبھىسىزكى، ئاشۇ كىشىلەرنىڭ باتىل دىنلىرى گۇمران بولغۇسىدۇر، ئۇلارنىڭ قىلغان ئەمەللىرى بىكاردۇر';
$t[140] = 'مۇسا: &laquo;اﷲ سىلەرنى (زامانىڭلاردىكى) جاھان ئەھلىدىن ئارتۇق قىلغان تۇرسا، سىلەرگە ئۇنىڭدىن باشقا ئىلاھ ئىزدەمدىم؟&raquo; دېدى';
$t[141] = 'ئۆز ۋاقتىدا سىلەرنى پىرئەۋننىڭ قەۋمىدىن قۇتقۇزدۇق، ئۇلار سىلەرگە قاتتىق ئازابنى تېتىتاتتى، ئوغۇللىرىڭلارنى ئۆلتۈرەتتى، ئاياللىرىڭلارنى (خورلاپ ئىشلىتىشكە) ئېلىپ قالاتتى، بۇنىڭدا پەرۋەردىگارىڭلاردىن سىلەرگە زور سىناق بار ئىدى';
$t[142] = 'مۇسا (بىزگە مۇناجات قىلىشقا) ئوتتۇز كېچىنى ۋەدە قىلدۇق. ئۇنىڭغا يەنە ئون كېچىنى قوشتۇق، شۇنىڭ بىلەن، پەرۋەردىگارىڭنىڭ ۋەدە قىلغان ۋاقتى 40 كېچە بولدى. مۇسا قېرىندىشى ھارۇنغا: &laquo;(مەن قايتىپ كەلگەنگە قەدەر) قەۋمىمگە مېنىڭ ئورۇنباسارىم بولۇپ تۇرغىن، (ئۇلارنىڭ ئىشىنى) تۈزىگىن، بۇزۇقچىلىق قىلغۇچىلارنىڭ يولىدا ماڭمىغىن&raquo; دېدى';
$t[143] = 'مۇسا بىز ۋەدە قىلغان ۋاقىتتا كەلگەن ۋە پەرۋەردىگارى ئۇنىڭغا (بىۋاسىتە) سۆ قىلغان چاغدا: &laquo;پەرۋەردىگارىم! ماڭا ئۆزۈڭنى كۆرسەتكىن، سېنى بىر كۆرۈپ ئالاي&raquo; دېدى. اﷲ: &laquo;مېنى (بۇ دۇنيادا) ھەرگىز كۆرەلمەيسەن (چۈنكى ئىنساننىڭ بۇ ئاجىز تېنى بۇنىڭغا تاقەت قىلالمايدۇ). لېكىن تاغقا قارىغىن، ئەگەر تاغ ئورنىدا مەھكەم تۇرالىسا، مېنى كۆرەلەيسەن&raquo; دېدى. پەرۋەردىگارى تاغقا تەجەللى قىلىش بىلەن، تاغنى تۈپتۈز قىلىۋەتتى، مۇسا بىھوش بولۇپ يىقىلدى. ئۇ ھوشىغا كېلىپ: &laquo;(پەرۋەرىگارىم!) سەن پاكتۇرسەن، ساڭا تەۋبە قىلدىم، مەن (سېنىڭ ئۇلۇغلۇقۇڭغا) ئىشەنگۈچىلەرنىڭ ئەۋۋىلىمەن&raquo; دېدى';
$t[144] = 'اﷲ ئېيتتى: &laquo;ئى مۇسا! مەن ھەقىقەتەن سېنى (زامانىڭدىكى) كىشىلەر ئارىسىدىن پەيغەمبەرلىككە ۋە مەن بىلەن (بىۋاسىتە) سۆزلىشىشكە تاللىدىم، ساڭا مەن ئاتا قىلغن پەيغەمبەرلىكنى قوبۇل قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن&raquo;';
$t[145] = 'بىز ئۇنىڭ ئۈچۈن (تەۋرات) تاختىلىرىغا (ئىسرائىل ئەۋلادى دىنىدا موھتاج بولغان) ۋەز - نەسىھەت، (دىنىي) ئەھكاملارنىڭ ھەممىسىنى تەپسىلىي يازدۇق. (ئى مۇسا) ئۇنى (يەنى تەۋراتنى) مەھكەم تۇتقىن ھەمدە قەۋمىڭنى ئۇنىڭدىكى ئەڭ گۈزەل ئەھكاملارغا ئەمەل قىلىشقا بۇيرۇغىن، سىلەرگە پاسىقلار (يەنى پىرئەۋن ۋە ئۇنىڭ قەۋمى) نىڭ دىيارىنى كۆرسىتىمەن';
$t[146] = 'يەر يۈزىدە ناھەق تەكەببۇرلۇق قىلىدىغانلارغا مېنىڭ ئايەتلىرىمنى پەھىم قىلدۇرمايمەن، ئۇلار بارلىق مۆجىزىلەرنى كۆگەندىمۇ ئۇنىڭغا ئىشەنمەيدۇ، ئۇلار ئەگەر توغرا يولنى كۆرسە، ئۇنىڭدا ماڭمايدۇ، گۇمراھلىق يولىنى كۆرسە ئۇنىڭدا ماڭىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى ۋە ئۇنىڭدىن غاپىل بولدى';
$t[147] = 'بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتە اﷲ قا مۇلاقات بولۇشنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى (ئىمانى بولمىغانلىقى ئۈچۈن) بىكار بولۇپ كېتىدۇ، ئۇلارغا پەقەت قىلغان ئەمەللىرىگە يارىشا جازا بېرىلىدۇ';
$t[148] = 'مۇسانىڭ قەۋمى مۇسادىن كېيىن (يەنى مۇسا پەرۋەردىگارىغا مۇناجات قىلىش ئۈچۈن تۇر تېغىغا كەتكەندىن كېيىن)، زىننەت بۇيۇملرىدىن موزايدەك ئاۋاز چىقىرىدىغان بىر جانسىز موزاي ياسىدى. ئۇلار موزاينىڭ ئۆزلىرىگە سۆزلىيەلمەيدىغانلىقىنى، يول كۆرسىتەلمەيدىغانلىقىنى كۆرمىدىمۇ؟ ئۇلار ئۇنى مەبۇد قىلىۋالدى، ئۇلار (ئۆزلىرىگە) زۇلۇم قىلغۇچى بولدى';
$t[149] = 'ئۇلار (موزايغا چوقۇنغانلىقلىرىغا) پۇشايمان قىلغان ۋە راست ئازغانلىقلىرىنى چۈشەنگەن چاغدا: &laquo;ئەگەر پەرۋەردىگارىمىز بىزگە رەھىم قىلمىسا ۋە مەغپىرەت قىلمىسا، بىز ئەلۋەتتە زىيان تارتقۇچىلاردىن بولىمىز&raquo; دېدى';
$t[150] = 'مۇسا غەزەپلەنگەن، غەمكىن ھالدا قايتىپ كېلىپ، قەۋمىگە: &laquo;مەن يوق چاغدا (موزايغا چوقۇنۇپ) نەقەدەر يامان ئىش قىلدىڭلار - ھە! (كۈتۈپ تۇرماي) پەرۋەردىگارىڭلار نىڭ ئەمرىگە ئالدىراپ كەتتىڭلارمۇ؟&raquo; دېدى ۋە (غەزەپلەنگەنلىكتىن تەۋرات) تاختىلىرىنى (يەرگە) تاشلىدى، قېرىندىشىنىڭ چېچىدىن تۇتۇپ ئۆز تەرىپىگە تارتتى. (ھارۇن) ئېيتتى: &laquo;ئى قېرىندىشىم! (بۇ) قەۋم مېنى بوزەك تاپتى، مېنى ئۆلتۈرۈۋەتكىلى تاس قالدى. مېنى دۈشمەنلەرگە تەپەررۇج قىلىپ بەرمىگىن؟ مېنى زالىم قەۋم (يەنى موزايغا چوقۇنغانلار) قاتارىدا سانىمىغىن&raquo;';
$t[151] = 'مۇسا ئېيتتى: &laquo;پەرۋەردىگارىم! ماڭا ۋە مېنىڭ قېرىندىشىمغا مەغپىرەت قىلغىن، بىزنى رەھمىتىڭ دائىرىسىگە كىرگۈزگىن، سەن ئەڭ رەھىم قىلغۇچى زاتتۇرسەن&raquo;';
$t[152] = 'شۈبھىسىزكى موزاينى مەبۇد قىلىۋالغانلار پەرۋەردىگارىنىڭ غەزىپىگە ئۇچرايدۇ، بۇ دۇنيادا خارلىققا قالىدۇ، (اﷲ قا) بوھتان قىلغۇچىلارغا بىز مۇشۇنداق جازا بېرىمىز';
$t[153] = 'يامان ئىشلارنى قىلىپ قويۇپ، كېيىن تەۋبە قىلغان ۋە ئىمانىدا سەمىمىي بولغانلارنى تەۋبىسىدىن كېيىن پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلىدۇ، (ئۇلارغا) رەھىم قىلىدۇ';
$t[154] = 'مۇسا غەزىپى بېسىلغاندىن كېيىن (يەردە ياتقان تەۋرات) تاختىلىرىنى ئالدى. ئۇنىڭدا پەرۋەردىگارىدىن قورققۇچىلارغا ھىدايەت قىلىنىدۇ ۋە رەھمەت قىلىنىدۇ دەپ يېزىلغان ئىدى';
$t[155] = 'مۇسا، بىز بەلگىلەپ بەرگەن ۋاقىتتا ئېلىپ كېلىش ئۈچۈن، ئۆز قەۋمىدىن 70 كىشىنى تاللىدى؛ ئۇلارغا زىلزىلە يۈزلەنگەندە، مۇسا: &laquo;پەرۋەردىگارىم! خالىغان بولساڭ ئۇلارنى ۋە مېنى بۇرۇنلا ھالاك قىلغان بولاتتىڭ. ئارىمىزدىكى ئەخمەقلەرنىڭ قىلمىشى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟ بۇ پەقەت سېنىڭ سىنىقىڭدۇر، شۇ ئارقىلىق خالىغان بەندەڭنى ئازدۇرىسەن، خالىغان بەندەڭنى ھىدايەت قىلىسەن، سەن بىزنىڭ ئىگىمىزسەن، بىزگە مەغپىرەت قىلغىن، رەھىم قىلغىن. سەن ئەڭ ياخشى مەغپىرەت قىلغۇچىسەن';
$t[156] = 'بىزگە بۇ دۇنيادا ۋە ئاخىرەتتە تېگىشلىك ياخشىلىق قىلغىن، بىز ھەقىقەتەن ساڭا تەۋبە قىلدۇق&raquo; دېدى. اﷲ ئېيتتى: &laquo;ئازابىم بىلەن (بەندىلىرىمدىن) خالىغان كىشىنى ئازابلايمەن، مېنىڭ رەھمىتىم مەخلۇقاتنىڭ ھەممىسىگە ئورتاقتۇر. رەھمىتىمنى (كۇفرىدىن ۋە گۇناھتىن) ساقلانغۇچىلارغا، (ماللىرىنىڭ) زاكىتىنى بېرىدىغانلارغا ۋە بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىدىغانلارغا تېگىشلىك قىلىمەن&raquo;';
$t[157] = 'ئۇلار ئەلچىگە - ئۈممى پەيغەمبەرگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئەگىشىدۇ، ئۇلار ئۆز ئىلكىدىكى تەۋرات، ئىنجىللاردا ئۇنىڭ (سۈپىتىنىڭ) يېزىلغانلىقىنى كۆرىدۇ. ئۇ ئۇلارنى ياخشى ئىش قىلىشقا بۇيرۇيدۇ، يامان ئىش قىلىشتىن توسىدۇ، ئۇلارغا پاك نەرسىلەرنى ھالال قىلىدۇ، ناپاك نەرسىلەرنى ھارام قىلىدۇ، ئۇلارنىڭ ئېغىر يۈكىنى يېنىكلىتىدۇ، ئۇلارنى سېلىنغان تاقاق، كويزا - كىشەنلەردىن بوشىتىدۇ (يەنى ئۇلارغا يۈكلەنگەن ئېغىر ۋەزىپىلەرنى ئېلىپ تاشلايدۇ)، ئۇنىڭغا ئىمان ئېيتقانلار، ئۇنى ھۆرمەتلىگەنلەر، ئۇنىڭغا ياردەم بەرگەنلەر، ئۇنىڭغا نازىل قىلىنغان نۇر (يەنى قۇرئان) غا ئەگەشكۈچىلەر بەختكە ئېرىشكۈچىلەردۇر';
$t[158] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;ئى ئىنسانلار! مەن ھەقىقەتەن سىلەرنىڭ ھەممىڭلارغا اﷲ تەرىپىدىن ئەۋەتىلگەن ئەلچىمەن، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ، اﷲ قا ۋە اﷲ نىڭ سۆزلىرىگە ئىمان كەلتۈرىدىغان ئەلچىسى ئۈممى پەيغەمبەرگە ئىمان كەلتۈرۈڭلار، ھىدايەت تېپىشىڭلار ئۈچۈن ئۇنىڭغا ئەگىشىڭلار&raquo;';
$t[159] = 'مۇسانىڭ قەۋمىنىڭ (يەنى بەنى ئىسرائىلنىڭ) ئىچىدە (كىشىلەرنى) ھەق بىلەن توغرا يولغا باشلايدىغان، ھەق بىلەن توغرا ھۆكۈم قىلىدىغان بىر جامائە بار';
$t[160] = 'ئۇلار (يەنى ئىسرائىل) نى 12 تارماققا (يەنى قەبىلىگە) جامائەلەر قىلىپ بۆلۈۋەتتۇق. مۇسانىڭ قەۋمى (باياۋاندا) ئۇنىڭدىن سۇ تەلەپ قىلغاندا، مۇساغا: &laquo;ھاساڭ بىلەن تاشنى ئۇرغىن&raquo; دەپ ۋەھىي قىلدۇق. (مۇسا ھاسىسى بىلەن تاشنى ئۇرۇۋىدى) ئۇنىڭدىن (قەبىلىلەرنىڭ سانى بويىچە) 12 بۇلاق ئېتىلىپ چىقتى، ھەر قەبىلە ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى. ئۇلارغا بۇلۇتنى سايىۋەن قىلىپ بەردۇق، ئۇلارغا تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (ئۇلارغا ئېيتتۇقكى): &laquo;سىلەر بىز رىزىقلاندۇرغان لەززەتلىك نەرسىلەردىن يەڭلار&raquo;. (ئۇلار بۇ ئۇلۇغ نېمەتلەرگە نانكورلۇق قىلىش بىلەن) بىزگە زىيان سالغىنى يوق، (ئۆزلىرىنى اﷲ نىڭ ئازابىغا دۇچار قىلىشلىرى بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم سالدى';
$t[161] = 'ئۆز ۋاقتىدا (بىز ئۇلارغا) (يەنى بەنى ئىسرائىلنىڭ ئاتا - بوۋىلىرىغا): &laquo;سىلەر بۇ شەھەر (يەنى بەيتۇلمۇقەددەس) دە تۇرۇڭلار، ئۇ يەردىكى يېمەكلىكلەردىن خالىغىنىڭلارچە يەڭلار، شەھەر دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار، (كىرىۋاتقان چېغىڭلاردا) ئى اﷲ! گۇناھلىرىمىزنى كەچۈرگىن، دەڭلار، جىمى گۇناھلىرىڭلارنى مەغپىرەت قىلىمىز، ياخشىلىق قىلغۇچىلارغا (ساۋابىنى) زىيادە بېرىمىز&raquo; دېدۇق';
$t[162] = 'ئۇلارنىڭ ئىچىدىكى زالىملار ئۇلارغا ئېيتىلغان سۆز (اﷲ نىڭ ئەمرىنى) باشقا سۆزلەرگە ئۆزگەرتىۋەتتى، ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب ئەۋەتتۇق';
$t[163] = 'ئۇلار (يەنى يەھۇدىيلار) دىن دېڭىز (يەنى قۇلزۇم دېڭىزى) بويىدىكى (ئەيلە) شەھرىنىڭ ئەھۋالىنى سورىغىن. (ئۇنىڭ ئاھالىسى بېلىق تۇتۇش چەكلەنگەن كۈن) شەنبىدە اﷲ نىڭ چەكلىمىسىدىن چىقاتتى. چۈنكى شەنبە كۈنى بېلىقلار سۇ ئۈستىدىن ئۇلارنىڭ ئالدىغا كېلەتتى، شەنبىدىن غەيرىي كۈنلەردە ئۇلارنىڭ ئالدىغا كەلمەيتتى، پاسىق بولغانلىقلىرى ئۈچۈن. ئۇلارنى مۇشۇنداق سىنايمىز';
$t[164] = 'ئۆز ۋاقتىدا ئۇلارنىڭ ئىچىدىكى بىر جامائە ئادەملەر: اﷲ ھالاك قىلىدىغان ياكى قاتتىق ئازابلايدىغان قەۋمگە نېمە ئۈچۈن ۋەز - نەسىھەت قىلىسىلەر؟&raquo; دېدى. ئۇلار (يەنى ۋەز - نەسىھەت قىلغۇچىلار): &laquo;پەرۋەردىگارىڭلارنىڭ ئالدىدا (گۇناھقا سۈكۈت قىلىپ تۇردۇق دەپ) ئۆزرە ئېيتماسلىقىمىز ۋە ئۇلارنىڭ اﷲ تىن قورقۇپ (گۇناھتىن چەكلىنىشلىرىنى ئۈمىد قىلغانلىقىمىز ئۈچۈن شۇنداق قىلدۇق)&raquo; دېدى';
$t[165] = 'ئۇلار ئۆزلىرىگە قىلىنغان ۋەز - نەسىھەتنى قوبۇل قىلمىغان چاغدا، يامان ئىشتىن مەنئى قىلغۇچىلارنى قۇتقۇزدۇق، اﷲ نىڭ ئەمرىدىن چىققانلىقلىرى ئۈچۈن، زالىملارنى قاتتىق ئازابقا دۇچار قىلدۇق';
$t[166] = 'ئۇلار نەھيى قىلىنغان نەرسىنى تەرك ئېتىشتىن باش تارتقانلىقلىرى ئۈچۈن، ئۇلارغا: &laquo;خار مايمۇن بولۇپ كېتىڭلار&raquo; دېدۇق';
$t[167] = '(ئى مۇھەممەد!) ئۆز ۋاقتىدا، پەرۋەردىگارىڭ ئۇلارغا (يەنى يەھۇدىيلارغا) قىيامەتكىچە چوقۇم قاتتىق ئازابلايدىغان كىشىلەرنى ئەۋەتىپ تۇرىدىغانلىقىنى خەۋەر قىلدى. شۈبھىسىزكى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلارنى) ئەلۋەتتە تېز جازالىغۇچىدۇر، (ئىتائەت قىلغۇچىلارغا) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر';
$t[168] = 'ئۇلارنى (يەنى يەھۇدىيلارنى) يەر يۈزىدە تۈرلۈك پىرقىلەرگە بۆلۈۋەتتۇق، ئۇلارنىڭ ئىچىدە ياخشىلىرىمۇ بار، ياخشىلىرىدىن تۆۋەنلىرىمۇ بار، ئۇلارنىڭ (كۇفرىدىن ۋە گۇناھتىن) قايتىشلىرى ئۈچۈن، ئۇلارنى قاتتىقچىلىق ۋە كەڭچىلىك بىلەن سىنىدۇق';
$t[169] = 'ئۇلاردىن كېيىن، (يامان) ئەۋلادلار تەۋراتقا ۋارىسلىق قىلدى، ئۇلار بۇ دۇنيانىڭ ئەرزىمەس پۇل - مېلىنى (ھالال بىلەن ھارامنى ئايرىماي، قارىقويۇق) ئالىدۇ، ۋە بىزنى اﷲ ئەپۇ قىلىدۇ، دەيدۇ. شۇنداق پۇل - مال ئۇلارنىڭ قولىغا كەلسە، ئۇلار يەنە ئالىدۇ. كىتابتا (يەنى تەۋراتتا) اﷲ قا (يالغان چاپلىماي) پەقەت ھەقنىلا ئېيتىشلىرى توغرۇلۇق ئۇلاردىن چىڭ ۋەدە ئېلىنمىدىمۇ؟ ھالبۇكى، ئۇلار تەۋراتتىكى نەرسىلەرنى ئوقۇغان ئىدى. ھارامنى تەرك ئېتىپ، اﷲ تىن قورقىدىغانلارغا ئاخىرەت ياخشىدۇر، بۇنى چۈشەنمەمسىلەر؟';
$t[170] = 'كىتابنى مەھكەم تۇتىدىغانلار، ناماز ئوقۇيدىغانلار (ساۋابقا ئېرىشىدۇ)، بىز ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى ھەقىقەتەن زايا قىلىۋەتمەيمىز';
$t[171] = 'ئۆز ۋاقتىدا (تۇر) تاغنى قومۇرۇپ ئۇلارنىڭ ئۈستىگە سايىۋەندەك تىكلىدۇق، ئۇلار تاغنى ئۈستىلىرىگە چۈشۈپ كېتىدۇ دەپ ئويلىدى. (ئۇلارغا ئېيتتۇقكى) &laquo;تەقۋادارلار قاتارىدا بولۇشۇڭلار ئۈچۈن ئۆزۈڭلارغا بېرىلگەن كىتابنى (يەنى تەۋراتنى) مەھكەم تۇتۇڭلار، ئۇنىڭدىكى ئەھكاملارغا ئەمەل قىلىڭلار&raquo;';
$t[172] = 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم باللىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: &laquo;مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟&raquo; دېدى (يەنى اﷲ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: &laquo;ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق&raquo; دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: &laquo;بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز&raquo; دېمەسلىكلىرى، ياكى &laquo;ئاتا - بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟&raquo; دېمەسلىكلىرى ئۈچۈندۇر';
$t[173] = 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم باللىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: &laquo;مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟&raquo; دېدى (يەنى اﷲ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: &laquo;ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق&raquo; دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: &laquo;بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز&raquo; دېمەسلىكلىرى، ياكى &laquo;ئاتا - بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟&raquo; دېمەسلىكلىرى ئۈچۈندۇر';
$t[174] = 'ئۇلارنىڭ (گۇمراھلىقتىن، ئاتا - بوۋىلىرىغا ئەگىشىشتىن) قايتىشلىرى ئۈچۈن، ئايەتلىرىمىزنى مۇشۇنداق تەپسىلىي بايان قىلىمىز';
$t[175] = '(ئى مۇھەممەد!) ئۇلار (يەنى يەھۇدىيلار) غا شۇنداق بىر ئادەمنىڭ قىسسىسىنى ئوقۇپ بەرگىنكى، ئۇنىڭغا ئايەتلىرىمىزنى بەردۇق (يەنى كىتابۇللاھنىڭ بەزى ئىلىملىرىنى ئۆگەتتۇق). ئۇ ئايەتلىرىمىزدىن ئۆزىنى تارتتى، ئۇنىڭغا شەيتان ئاپىرىدە بولۇشى بىلەنلا گۇمراھلاردىن بولۇپ كەتتى';
$t[176] = 'ئەگەر بىز خالىساق، ئۇنى (تەقۋادار ئۆلىمالار دەرىجىسىگە) كۆتۈرەتتۇق، لېكىن ئۇ دۇنياغا بېرىلىپ كەتتى، نەپسى خاھىشىغا بويسۇندى، ئۇ گويا بىر ئىتقا ئوخشايدۇكى، ئۇنى قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، بۇ بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ئەنە شۇ قەۋمنىڭ مىسالىدۇر، ئويلاپ بېقىشلىرى ئۈچۈن، بۇ قىسسىنى (قەۋمىڭگە) سۆزلەپ بەرگىن';
$t[177] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلىپ ئۆزلىرىگە زىيان سالغان قەۋمنىڭ مىسالى نېمىدېگەن يامان!';
$t[178] = 'اﷲ ھىدايەت قىلغان ئادەم ھىدايەت تاپقۇچىدۇر، اﷲ گۇمران قىلغان ئادەم زىيان تارتقۇچىدۇر';
$t[179] = 'شەك - شۈبھىسىزكى، جىن ۋە ئىنسانلاردىن نۇرغۇنلىرىنى دوزاخقا (يېقىلغۇ بولۇش ئۈچۈن) ياراتتۇق، ئۇلار دىللىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق ھەقنى چۈشەنمەيدۇ. ئۇلار كۆزلىرى بولغان بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىنى) كۆرمەيدۇ. ئۇلار قۇلاقلىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ ئايەتلىرىنى ئىبرەت ئىلىپ) تىڭشىمايدۇ. ئۇلار گويا ھايۋانغا ئوخشايدۇ، ھايۋاندىنمۇ بەتتەر گۇمراھتۇر، ئەنە شۇلار غاپىلدۇر';
$t[180] = 'اﷲ نىڭ گۈزەل ئىسىملىرى بار، اﷲ نى شۇ (گۈزەل ئىسىملىرى) بىلەن ئاتاڭلار، اﷲ نىڭ ئىسىملىرىنى كەلسە - كەلمەس قوللىنىدىغانلارنى تەرك ئېتىڭلار، ئۇلار (ئاخىرەتتە) قىلمىشلىرىنىڭ جازاسىنى تارتىدۇ';
$t[181] = 'بىز ياراتقان ئۈممەتلەر ئىچىدە ھەق يولغا دەۋەت قىلىدىغان ۋە ھەقتە چىڭ تۇرىدىغان بىر ئۈممەت بار';
$t[182] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تۇيدۇرماستىن، ئاستا - ئاستا ھالاكەتكە يېقىنلاشتۇرىمىز';
$t[183] = 'ئۇلارغا مۆھلەت بېرىمەن مېنىڭ ئازابىم ھەقىقەتەن قاتتىقتۇر';
$t[184] = 'ئۇلار ئۆزىنىڭ ھەمراھى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ مەجنۇن ئەمەسلىكىنى ئويلىمامدۇ؟ ئۇ پەقەت ئاشكارا ئاگاھلاندۇرغۇچىدۇر';
$t[185] = 'ئۇلار ئاسمانلارنىڭ ۋە زېمىننىڭ سەلتەنىتى ئۈستىدە، اﷲ نىڭ ياراتقان مەخلۇقاتلىرى، ئۆزلىرىنىڭ ئەجىلى يېقىنلىشىدىغانلىقىنىڭ مۇمكىنلىكى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئۇلار (كالامۇللاھ ئىكەنلىكى شۇنچە روشەن بولغان) قۇرئاندىن باشقا يەنە قايسى سۆزگە ئىشىنىدۇ؟';
$t[186] = 'اﷲ ئازدۇرغان كىشىنى ھىدايەت قىلغۇچى بولمايدۇ، اﷲ ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقاپ يۈرىدۇ';
$t[187] = '(ئى مۇھەممەد!) ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ، ئېيتقىنكى، &laquo;ئۇنىڭ قاچان بولىدىغانلىقىنى پەقەت پەرۋەردىگارىم (اﷲ) بىلىدۇ، ئۇنى پەقەت ئۆزى بەلگىلىگەن ۋاقىتتا مەيدانغا چىقىرىدۇ، قىيامەت ۋە زېمىننىڭ (ئەھلىگە) ئېغىر (ۋەقەدۇر). ئۇ سىلەرگە تۇيۇقسىز كېلىدۇ&raquo;. گويا سەن قىيامەت بىلەن ناھايىتى تونۇشتەك، ئۇنى سەندىن سورىشىدۇ. ئېيتقىنكى، &laquo;ئۇنىڭ ۋاقتىنى پەقەت اﷲ بىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى ئۇنىڭ ۋاقتىنىڭ مەخپىي بولۇشىدىكى سەۋەبنى) بىلمەيدۇ';
$t[188] = 'اﷲ خالىمىغان ئىكەن، ئۆزۈمگە پايدا يەتكۈزۈش، زىياننى ئۆزۈمدىن دەپئى قىلىش قولۇمدىن كەلمەيدۇ، (قىيامەتنىڭ قاچان بولۇشىنى قانداقمۇ بىلەي؟) ئەگەر مەن غەيبنى بىلىدىغان بولسام (دۇنيا مەنپەئەتلىرىدىن) نۇرغۇن مەنپەئەت ھاسىل قىلغان بولاتتىم، زىيان - زەخمەتكىمۇ ئۇچرىمىغان بولاتتىم (لېكىن مەن غەيبنى بىلمەيمەن، شۇڭا ماڭا تەقدىر قىلىنغان ياخشى - يامانلىق يېتىپ تۇرىدۇ). مەن پەقەت ئىمان ئېيتىدىغان قەۋم ئۈچۈن ئاگاھلاندۇرغۇچى ۋە خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىلگەن پەيغەمبەرمەن&raquo;';
$t[189] = 'ئۇ سىلەرنى بىر جاندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، (ئادەم ئەلەيھىسسالامنىڭ) ئۇنسى - ئۈلپەت ئېلىشى ئۈچۈن، خوتۇنىنى (يەنى ھەۋۋانى) ئۇنىڭ ئۆز جىنسىدىن ياراتتى، خوتۇنى بىلەن يېقىنچىلىق قىلغاندىن كېيىن، خوتۇنى يېنىككىنە قورساق كۆتۈرۈپ ئېغىر بوي يۈردى، ئۇ (بالىنىڭ قورساقتا ئۆسۈپ چوڭىيىشى بىلەن) ئېغىرلاشقاندىن كېيىن، (ئەر - خوتۇن) ئىككىيلەن پەرۋەردىگارى اﷲ قا: &laquo;ئەگەر بىزگە (ئەزالىرى) بېجىرىم پەرزەنت ئاتا قىلساڭ، نېمىتىڭگە ئەلۋەتتە شۈكۈر قىلغۇچىلاردىن بولىمىز&raquo; دەپ دۇئا قىلدى';
$t[190] = 'اﷲ ئۇلارغا بېجىرىم ئوغۇل پەرزەنت ئاتا قىلغان ئىدى، ئۇلارنىڭ ئەۋلادى اﷲ قا شېرىك كەلتۈرۈپ (بۇتلارغا چوقۇندى)، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن مەبۇدلىرىدىن پاكتۇر';
$t[191] = 'ئۆزلىرى يارىتىلغان، ھېچ نەرسىنى يارىتىلمايدىغان نەرسىلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك كەلتۈرەمدۇ!';
$t[192] = 'ئۇ نەرسىلەر چوقۇنغۇچىلىرىغا ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ';
$t[193] = 'ئۇلارنى توغرا يولغا چاقىرساڭلار، ئۇلار سىلەرگە ئەگەشمەيدۇ. مەيلى ئۇلارنى دەۋەت قىلىڭلار، ياكى جىم تۇرۇڭلار، بۇ ئۇلار ئۈچۈن بەرىبىر ئوخشاش (يەنى سىلەر قايسى ھالەتتە بولماڭلار، ئۇلار سىلەرنىڭ دەۋىتىڭلارغا جاۋاب قايتۇرۇشقا قادىر ئەمەستۇر)';
$t[194] = 'سىلەر اﷲ نى قويۇپ ئىبادەت قىلغان بۇتلار سىلەرگە ئوخشاش مەخلۇقلاردۇر، (ئۇلارنى ئىلاھ دېگەن دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارغا دۇئا قىلىپ بېقىڭلار، دۇئايىڭلارنى ئىجابەت قىلىپ باقسۇن';
$t[195] = 'ئۇلار (يەنى بۇتلار) نىڭ ماڭىدىغان پۇتلىرى بارمۇ؟ ياكى تۇتىدىغان قوللىرى بارمۇ؟ ياكى كۆرىدىغان كۆزلىرى بارمۇ ۋە ئاڭلايدىغان قۇلاقلىرى بارمۇ؟ (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;شېرىكلىرىڭلارنى (يەنى بۇتلىرىڭلارنى ماڭا قارشى) چاقىرىڭلار، ئاندىن (قولۇڭلاردىن كېلىشىچە) ماڭا زىيانكەشلىك قىلىڭلار، ئازراقمۇ مۆھلەت بەرمەڭلار (مەن اﷲ قا يۆلەنگەنلىكىم ئۈچۈن سىلەرگە پەرۋا قىلمايمەن)';
$t[196] = 'ھەقىقەتەن كىتابنى (يەنى قۇرئاننى) نازىل قىلغان اﷲ مېنىڭ ئىگەمدۇر، ئۇ ياخشىلارغا ئىگىدارچىلىق قىلىدۇ';
$t[197] = 'سىلەرنىڭ اﷲ نى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىڭلار سىلەرگە ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ';
$t[198] = 'ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭلار، ئۇلار (ھىدايەت تاپماق تۈگۈل دەۋىتىڭنى) ئاڭلىمايدۇ، ئۇلار ساڭا قاراپ تۇرغاندەك كۆرۈنسىمۇ، لېكىن ئۇلار كۆرمەيدۇ';
$t[199] = 'ئەپۇنى دوست تۇتقىن، ياخشىلىققا (يەنى ياخشى سۆز قىلىشقا، ياخشى ئىش قىلىشقا) بۇيرۇغىن، نادانلار بىلەن تەڭ بولمىغىن (يەنى نادانلارنىڭ قىلغىنىنى قىلماي، ئۇلارغا مۇلايىم بولغىن)';
$t[200] = 'ئەگەر شەيتان ساڭا ۋەسۋەسە قىلسا، (شەيتاننىڭ ۋەسۋەسىسىدىن) اﷲ قا سېغىنىپ پاناھ تىلىگىن. اﷲ ھەقىقەتەن (سۆزۈڭنى) ئاڭلاپ تۇرغۇچىدۇر (ئىشىڭنى بىلىپ تۇرغۇچىدۇ';
$t[201] = 'تەقۋادار كىشىلەر شەيتاننىڭ ۋەسۋەسىسىگە ئۇچرىسا، اﷲ نى ئەسلەيدۇ - دە، ھەقىقەتنى كۆرۈۋېلىپ، (شەيتاننىڭ ۋەسۋەسىسىدىن) خالاس تاپىدۇ';
$t[202] = 'شەيتانلار (كۇففار) بۇرادەرلىرىنى بولۇشىچە ئازدۇرىدۇ، ئۇلارنى ئازدۇرۇشتىن بوشىشىپ قالمايدۇ،';
$t[203] = '(ئۇلار تەلەپ) قىلغان مۆجىزىلەردىن) بىرەر مۆجىزىنى كەلتۈرمىسەڭ، ئۇنى نېمىشقا (ئۆزۈڭ) ئىجاد قىلمىدىڭ؟ دېيىشىدۇ، (ئى مۇھەممەد!) ئۇلارغا ئېيتقىنكى، &laquo;(ئىش مېنىڭ ئىلكىمدە ئەمەس) مەن پەقەت پەرۋەردىگارىم تەرىپىدىن ماڭا ۋەھيى قىلىنغانغىلا ئەمەل قىلىمەن (ئۆزلىكىمدىن مۆجىزە يارىتالمايمەن)، بۇ (قۇرئاندا سىلەرگە) پەرۋەردىگارىڭلار تەرىپىدىن كەلتۈرۈلگەن دەلىللەردۇر (قۇرئان باشقا مۆجىزىلەرگە ئېھتىياج قالدۇرمايدۇ)، ئىمان ئېيتقان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر&raquo;';
$t[204] = 'اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، قۇرئان ئوقۇلغان چاغدا، ئۇنى دىققەت بىلەن ئاڭلاڭلار ۋە جىم تۇرۇڭلار (يەنى سۆز قىلماڭلار)';
$t[205] = 'پەرۋەردىگارىڭنى يېلىنغان ۋە ئۇنىڭدىن قورققان ھالدا ئىچىڭدە ياد ئەتكىن، ئەتىگەندە - ئاخشامدا ئۇنى پەس ئاۋازدا زىكرى قىلغىن، غاپىللاردىن بولمىغىن';
$t[206] = 'پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) اﷲ قا ئىبادەت قىلىشتىن باش تارتمايدۇ، اﷲ قا تەسبىھ ئېيتىدۇ ۋە ئۇنىڭغا سەجدە قىلىدۇ';
?>