$t[28] = 'ئى مۆمىنلەر! مۇشرىكلار (اﷲ نى ئىنكار قىلغانلىقلىرى ئۈچۈن) پەقەت نىجىستۇر، مۇشۇ يىلدىن (يەنى ھىجرىيىنىڭ 9 - يىلىدىن) كېيىن ئۇلار مەسجىدى ھەرەمغا (ھەرەمگە) يېقىنلاشمىسۇن (يەنى ھەج قىلمىسۇن، ئۆمرە قىلمىسۇن). ئەگەر سىلەر (ئى مۆمىنلەر! مۇشرىكلارنىڭ ھەرەمگە كىرىشتىن مەنئى قىلىنىشى بىلەن) پېقىرلىقتىن قورقساڭلار، اﷲ خالىسا ئۆز پەزلى بىلەن سىلەرنى باي قىلىدۇ، شۈبھىسىزكى، اﷲ (سىلەرگە پايدىلىق ئىشلارنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[29] = 'سىلەر ئەھلى كىتابتىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار (يەھۇدىيلار ئۇزەيرىنى اﷲ نىڭ ئوغلى، ناسارالار ئىسانى اﷲ نىڭ ئوغلى دەپ ئېتىقاد قىلغانلىقلىرى ئۈچۈن، ئۇلار اﷲ قا ۋە ئاخىرەت كۈنىگە ئېتىقاد قىلىمىز دېگەن بىلەنمۇ ئېتىقاد قىلمىغاننىڭ ئورنىدىدۇر) بىلەن، اﷲ (ئۆزىنىڭ كىتابىدا) ۋە ئۇنىڭ پەيغەمبىرى (سۈننىتىدە) ھارام قىلغان نەرسىلەرنى ھارام بىلمەيدىغانلار ۋە ھەق دىن (يەنى ئىسلام دىنى) غا ئېتىقاد قىلمايدىغانلار بىلەن، تاكى ئۇلار سىلەرگە بويسۇنۇپ خار ھالدا جىزىيە تۆلىگەنگە قەدەر، ئۇرۇش قىلىڭلار';
$t[30] = 'يەھۇدىيلار: &laquo;ئۈزەير اﷲ نىڭ ئوغلىدۇر&raquo; دېدى، ناسارالار: &laquo;مەسىھ (يەنى ئىسا) اﷲ نىڭ ئوغلىدۇر&raquo; دېدى، بۇ، ئۇلارنىڭ ئاغزىدىكى (دەلىلسىز) سۆزىدۇر، (ئۇلارنىڭ سۆزلىرى) ئىلگىرىكى كاپىرلارنىڭ (يەنى مۇشرىكلارنىڭ: &laquo;پەرىشتىلەر اﷲ نىڭ قىزلىرىدۇر&raquo; دېگەن) سۆزلىرىگە ئوخشايدۇ. اﷲ ئۇلارغا لەنەت قىلسۇنكى، (اﷲ نىڭ بالىسى يوقلۇقىغا روشەن دەلىل تۇرسا، ھەقىقەتتىن باتىلغا بۇرۇلۇپ) ئۇلار قانداقمۇ (اﷲ نىڭ بالىسى بار دەپ) اﷲ قا يالغان چاپلايدۇ';
$t[31] = 'ئۇلار ئۆزلىرىنىڭ ھىبرىلىرىنى، راھىبلىرىنى (يەھۇدىيلار بىلەن ناسارالارنىڭ دىنىي باشلىقلىرىنى، ئۆلىمالىرىنى) ۋە مەريەم ئوغلى مەسىھنى مەبۇد قىلىۋالدى. (ھالبۇكى) ئۇلار (پەيغەمبەرلەر ئارقىلىق) پەقەت بىر ئىلاھقا ئىبادەت قىلىشقا بۇيرۇلغان ئىدى، ئۇنىڭدىن باشقا (يەنى ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن باشقا) ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەنلىكلىرىدىن پاكتۇر';
$t[32] = 'ئۇلار (يەنى مۇشرىكلار، يەھۇدىيلار ۋە ناسارالار) اﷲ نىڭ نۇرىنى (يەنى ئىسلام دىنىنى) ئېغىزلىرى (يەنى يالغان - ياۋىداق بوھتانلىرى) بىلەن ئۆچۈرمەكچى بولىدۇ، اﷲ ئۆز نۇرىنى ئۈستۈن قىلماي قالمايدۇ، كاپىرلار (بۇنى) يامان كۆرگەن تەقدىردىمۇ';
$t[33] = 'اﷲ ھەق دىن (ئىسلام) نى بارلىق دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، ئۆزىنىڭ پەيغەمبىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى تولۇق) ھىدايەت ۋە ھەق دىن بىلەن ئەۋەتتى، مۇشرىكلار ئۇنىڭ (ئۈستۈن بولۇشىنى) يامان كۆرگەن تەقدىردىمۇ';
$t[34] = 'ئى مۆمىنلەر! ھىبرىلەر ۋە راھىبلەردىن (يەھۇدىي ۋە ناسارا ئۆلىمالىرىدىن) نۇرغۇنلىرى كىشىلەرنىڭ پۇل - ماللىرىنى ھەقىقەتەن ھارام يەيدۇ، (كىشىلەرنى) اﷲ نىڭ دىنىغا كىرىشتىن توسىدۇ، ئالتۇن - كۈمۈش يىغىپ، ئۇنى اﷲ نىڭ يولىدا سەرپ قىلمايدىغانلارغا (دوزاختا بولىدىغان) قاتتىق ئازاب بىلەن بېشارەت بەرگىن';
$t[35] = 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇ ئالتۇن - كۈمۈشلەر جەھەننەمنىڭ ئوتىدا قىزىتىلىپ، ئۇنىڭ بىلەن ئۇلارنىڭ پېشانىلىرى، يانلىرى ۋە دۈمبىلىرى داغلىنىدۇ. ئۇلارغا: &laquo;بۇ سىلەرنىڭ ئۆزۈڭلار ئۈچۈن يىغقان ئالتۇن - كۈمۈشۈڭلار (سىلەر بۇ دۇنيايىڭلاردىكى اﷲ نىڭ ھەققىنى ئادا قىلمىدىڭلار). يىغقان ئالتۇن - كۈمۈشۈڭلارنىڭ ۋابالىنى تېتىڭلار&raquo; دېيىلىدۇ';
$t[36] = 'شۈبھىسىزكى، ئاسمان ۋە زېمىن يارىتىلغاندىن تارتىپ (قەمەرى) ئايلارنىڭ سانى اﷲ نىڭ دەرگاھىدا (يەنى لەۋھۇلمەھپۇزدا) 12 دۇر، ئۇلاردىن تۆتى (يەنى زۇلقەئدە، زۇلھەججە، مۇھەررەم، رەجەپ) ئۇرۇش قىلىش ھارام قىلىنغان ئايلاردۇر، بۇ توغرا دىندۇر (يەنى مەزكۇر تۆت ئايدا ئۇرۇشنىڭ ھارام قىلىنىشى اﷲ نىڭ توغرا دىنىدۇر)، بۇ ئايلاردا (ئۇلارنىڭ ھۆرمىتىنى ساقلىماسلىق بىلەن، اﷲ چەكلىگەن گۇناھ ئىشلارنى قىلىش بىلەن) ئۆزۈڭلارغا زىيان سالماڭلار، مۇشرىكلار سىلەرگە بىرلىكتە ھۇجۇم قىلغاندەك، سىلەرمۇ ئۇلارغا قارشى بىرلىكتە ئۇرۇش قىلىڭلار. بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ نىڭ ئەمرىنى بەجا كەلتۈرۈش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن اﷲ تىن قورققۇچىلارغا اﷲ نىڭ ياردەم بېرىدىغانلىقىغا ئىشەنچىدە بولۇڭلار)';
$t[37] = 'ئاينى ئارقىغا سۈرۈش (يەنى ئۇرۇش ھارام قىلىنغان ئاينى باشقا بىر ئايغا ئۆزگەرتىپ، يىلنىڭ 12 ئېيىنى 13 ئايغا كۆپەيتىش) كۇفرىنى زىيادە قىلىشتۇر، بۇنىڭ بىلەن كاپىرلار ئازدۇرۇلىدۇ؛ ئۇلار اﷲ ھارام قىلغان ئايلارنىڭ سانىغا مۇۋاپىقلىشىش ئۈچۈن، ئۇنى بىر يىل ھالال قىلسا، ئىككىنچى يىل ھارام قىلىدۇ، شۇنىڭ بىلەن ئۇلار اﷲ ھارام قىلغان ئاينى ھالال قىلىدۇ، ئۇلارغا ئۆزلىرىنىڭ يامان ئەمەللىرى چىرايلىق كۆرسىتىلدى. اﷲ كاپىر قەۋمنى ھىدايەت قىلمايدۇ';
$t[38] = 'ئى مۆمىنلەر! سىلەرگە اﷲ نىڭ يولىدا جىھاد قىلىشقا چىقىڭلار دېيىلسە، نېمىشقا يۇرتۇڭلاردىن ئايرىلغىڭلار كەلمەيدۇ؟ دۇنيا ھاياتىنى ئاخىرەتتىكى (بەخت) بىلەن تېگىشىشكە رازى بولدۇڭلارمۇ؟ دۇنيا ھاياتىدىن بەھرىمەن بولۇش ئاخىرەتنىڭ نېمەتلىرى بىلەن سېلىشتۇرغاندا، ئەرزىمەس نەرسىدۇر';
$t[39] = 'ئەگەر سىلەر جىھادقا چىقمىساڭلار، اﷲ سىلەرگە قاتتىق ئازاب قىلىدۇ. (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا سىلەردىن باشقا قەۋمنى كەلتۈرىدۇ، سىلەر اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر، اﷲ ھەر نەرسىگە قادىردۇر';
$t[40] = 'ئەگەر سىلەر اﷲ نىڭ پەيغەمبىرىگە ياردەم قىلمىساڭلار (اﷲ ياردەم قىلىدۇ)، اﷲ ھەقىقەتەن ئۇنىڭغا ياردەم قىلغان ئىدى. ئۆز ۋاقتىدا كاپىرلار ئۇنى (مەككىدىن) ھەيدەپ چىقارغان ئىدى. ئۇنىڭغا پەقەت بىر كىشى (يەنى ئەبۇبەكرى سىددىق) ھەمراھ ئىدى. ئەينى زاماندا ئۇ ئىككىسى غاردا ئىدى. (رەسۇلۇللاھ) ھەمراھىغا: &laquo;زەم قىلمىغىن، اﷲ ھەقىقەتەن بىز بىلەن بىللە&raquo; دەيتتى، اﷲ ئۇنىڭغا (يەنى پەيغەمبىرىگە) خاتىرجەملىك بېغىشلىدى، ئۇنىڭغا (پەرىشتىلەردىن بولغان) قوشۇنلار بىلەن مەدەت بەردى، ئۇلارنى سىلەر كۆرمىدىڭلار، كاپىرلارنىڭ سۆزىنى (يەنى شېرىك كەلىمىسىنى) پەس قىلدى، اﷲ نىڭ سۆزى (يەنى كەلىمە تەۋھىد) ئۈستۈندۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[41] = '(ئى مۆمىنلەر جامائەسى!) سىلەر يىنىك بولغان ياكى ئېغىر بولغان ھالەتتە (يەنى مەيلى ياش - قېرى، پىيادە، ئۇلاغلىق بولۇڭلار، ئوڭۇشلۇق ۋە قىيىن شارائىتتا بولۇڭلار، ئىختىيارىي ۋە ئىختىيارسىز بولۇڭلار، ھەممە ئەھۋالدا) جىھادقا چىقىڭلار، اﷲ نىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلىڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر';
$t[42] = 'ئەگەر (ئۇلارنىڭ دەۋەت قىلىنغىنى) ئاسان قولغا كېلىدىغان غەنىيمەت ۋە (يىراق ئەمەس) ئوتتۇراھال سەپەر بولىدىغان بولسا، ئۇلار چوقۇم ساڭا ئەگىشىپ (غەنىيمەت ئېلىش مەقسىتىدە) چىقاتتى. لېكىن، بۇ ئارىلىق ئۇلارغا يىراق بىلىندى. ئۇلار بىلەن قەسەم ئىچىپ: &laquo;ئەگەر چىقىشقا قادىر بولالىغان بولساق، ئەلۋەتتە سىلەر بىلەن بىللە چىقاتتۇق&raquo; دەيدۇ، ئۇلار (يالغان قەسەملىرى تۈپەيلىدىن) ئۆزلىرىنى ئۆزلىرى ھالاك قىلىدۇ، اﷲ بىلىدۇكى، ئۇلار شەك - شۈبھىسىز يالغانچىلاردۇر';
$t[43] = '(ئى مۇھەممەد!) اﷲ سېنى كەچۈردى، (ئۆزرىسىدە) راستچىللار بىلەن يالغانچىلار (ساڭا ئېنىق بولمىغۇچە نېمىشقا ئۇلارغا چىقماسلىققا) رۇخسەت بەردىڭ';
$t[44] = '(ئى مۇھەممەد!) اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغانلار سەندىن ماللىرى بىلەن ۋە جانلىرى بىلەن جىھاد قىلىشقا رۇخسەت سورىمايدۇ. اﷲ تەقۋادارلارنى ئوبدان بىلىدۇ';
$t[45] = '(ئى مۇھەممەد) سەندىن پەقەت اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار، دىللىرىدا (اﷲ نىڭ بىرلىكىگە ۋە سېنىڭ پەيغەمبەرلىكىڭگە) گۇمانى بارلار رۇخسەت سورايدۇ، ئۇلار ئۆز گۇمانىدا تېڭىرقاپ يۈرىدۇ';
$t[46] = 'ئەگەر ئۇلارنىڭ جىھادقا چىقىش نىيىتى بولسا، ئەلۋەتتە ئۇنىڭغا تەييارلىق قىلاتتى، لېكىن اﷲ ئۇلارنىڭ چىقىشىنى ياقتۇرمىدى، (ئۇلارنىڭ دىللىرىغا ھۇرۇنلۇقنى سېلىپ) ئۇلارنىڭ ئىرادىسىنى سۇندۇردى. (ئۇلارغا) &laquo;ئۆيلىرىدە قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار، ئاجىزلار) بىلەن قېلىڭلار&raquo; دېيىلدى';
$t[47] = 'ئەگەر ئۇلار سىلەر بىلەن بىرلىكتە چىققان بولسا، ئاراڭلاردا پەقەت پىتنە - پاساتنى كۆپەيتەتتى، ئاراڭلارغا بۆلگۈنچىلىك سېلىش ئۈچۈن چوقۇم سۇخەنچىلىك بىلەن شۇغۇللىناتتى،. ئاراڭلاردا ئۇلار ئۈچۈن تىڭ - تىڭلايدىغانلار بار، اﷲ زالىملارنى (يەنى مۇناپىقلارنىڭ ئىچى - تېشىنى) ئوبدان بىلگۈچىدۇر';
$t[48] = 'ئىلگىرى ئۇلار تاكى اﷲ نىڭ ياردىمى كەلگەنگە، اﷲ نىڭ دىنى ئۈستۈنلۈك قازانغانغا قەدەر، ھەقىقەتەن، بۆلگۈنچىلىك سېلىشقا ئورۇنغان، ساڭا ھىيلە - مىكىرلەرنى ئىشلەتكەن ئىدى، ھالبۇكى، ئۇلار بۇنداق بولۇشىنى (يەنى دىننىڭ ئۈستۈنلۈك قازىنىشىنى) يامان كۆرگۈچىلەردۇر';
$t[49] = 'ئۇلارنىڭ ئارىسىدا: &laquo;ماڭا (يەنى مېنىڭ قېلىشىمغا) رۇخسەت قىلغىن، مېنى بالاغا گىرىپتار قىلمىغىن (يەنى رۇمنىڭ ئاياللىرى بىلەن گۇناھ قىلىپ سېلىشىمغا سەۋەبچى بولمىغىن)&raquo; دېگۈچىلەرمۇ بار. ئەمەلدە ئۇلار (مۇناپىقلىقتىن ئىبارەت چوڭ) بالاغا گىرىپتار بولدى. شۈبھىسىزكى، جەھەننەم كاپىرلارنى ئوراپ تۇرغۇچىدۇر';
$t[50] = 'ئەگەر سەن بىرەر ياخشىلىققا ئېرىشسەڭ، ئۇلارنىڭ كۆڭلى يېرىم بولىدۇ؛ ئەگەر ساڭا بىرەر مۇسىبەت كەلسە: &laquo;ئىشنىڭ ئالدىنى ئالغان ئىكەنمىز&raquo; دېيىشىپ خۇشال قايتىشىدۇ';
$t[51] = 'ئېيتقىنكى، &laquo;بىزگە پەقەت اﷲ نىڭ تەقدىر قىلغان نەرسىسى يېتىدۇ، ئۇ بىزنىڭ مەدەتكارىمىزدۇر. مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن (يەنى مۆمىنلەر ھەممە ئىشنى اﷲ قا تاپشۇرسۇن، ئۇندىن باشقا ھېچ ئەھەدىگە يۆلەنمىسۇن)&raquo;';
$t[52] = 'ئېيتقىنكى، &laquo;سىلەر پەقەت بىزنىڭ (غەلىبە قىلىش ياكى شېھىت بولۇشتىن ئىبارەت) ئىككى خىل ياخشى ئاقىۋەتنىڭ بىرىگە ئېرىشىشىمىزنى كۈتۈۋاتىسىلەر، بىزمۇ اﷲ نىڭ سىلەرگە ئۆز دەرگاھىدىن ئازاب چۈشۈرۈشنى ياكى بىزنىڭ قولىمىز ئارقىلىق سىلەرنى جازالىشىنى كۈتۈۋاتىمىز، سىلەرمۇ (ئۆزۈڭلارغا كەلگەننى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (ئۆزىمىزگە كەلگەننى) كۈتەيلى&raquo;';
$t[53] = 'ئېيتقىنكى، &laquo;(ئى مۇناپىقلار جامائەسى!) سىلەر ئىختىيارىي ياكى مەجبۇرىي يوسۇندا (قانچىلىك پۇل - مال سەرپ قىلساڭلارمۇ) ھەرگىز قوبۇل قىلىنمايدۇ، چۈنكى سىلەر اﷲ نىڭ ئىتائىتىدىن چىققان قەۋم بولدۇڭلار&raquo;';
$t[54] = 'ئۇلارنىڭ بەرگەن نەپىقىلىرى شۇنىڭ ئۈچۈن قوبۇل بولمايدۇكى، ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلىدۇ، نامازنى خۇش ياقماسلىق بىلەن ئوقۇيدۇ، (پۇل - مېلىنى) رازىلىق بىلەن بەرمەيدۇ';
$t[55] = 'ئۇلارنىڭ مال - مۈلكى ۋە ئەۋلادى سېنى ئەجەبلەندۈرمىسۇن، اﷲ ئۇلارنى بۇ دۇنيادا شۇ نەرسىلەر بىلەن ئازابلاشنى ۋە ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ';
$t[56] = 'ئۇلار: &laquo;بىز چوقۇم سىلەر (يەنى سىلەرگە ئوخشاش مۇسۇلمانلار) دىن&raquo; دەپ، اﷲ بىلەن قەسەم ئىچىدۇ. ئۇلار سىلەردىن ئەمەس، لېكىن ئۇلار (زىيان - زەخمەتكە ئۇچراشتىن قورقۇپ، ئاغزىدا مۇسۇلمان بولغان) قورقۇنچاق قەۋمدۇر';
$t[57] = 'ئەگەر ئۇلار بىرەر قورغان ياكى غار ۋە ياكى گەمىنى تاپسا ئىدى، ئۇلار شۇ تەرەپكە قاراپ ئەلۋەتتە يۈگۈرۈشكەن بولاتتى';
$t[58] = '(ئى مۇھەممەد!) ئۇلارنىڭ بەزىسى سېنىڭ سەدىقە (ۋە غەنىيمەت) لەرنى تەقسىم قىلىشىڭنى ئەيىبلەيدۇ، ئۇلارغا ئۇنىڭدىن بېرىلسە خۇش بولىدۇ، بېرىلمىسە خاپا بولۇشىدۇ';
$t[59] = 'ئەگەر ئۇلار اﷲ نىڭ ۋە اﷲ نىڭ پەيغەمبىرىنىڭ ئۇلارغا بەرگىنىگە رازى بولسا ۋە: &laquo;اﷲ بىزگە. كۆرىدۇ قىلىدۇ، اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ئۆز پەزلىدىن ئاتا قىلىدۇ، بىز ھەقىقەتەن اﷲ تىن (اﷲ نىڭ پەزلىنى، ئېھسانىنى) ئۆتۈنۈپ سورىغۇچىلارمىز&raquo; دېسە، ئەلۋەتتە، ئۇلار ئۈچۈن ياخشى بولاتتى';
$t[60] = 'زاكات پەقەت پېقىرلارغا، مىسكىنلەرگە، زاكات خادىملىرىغا، دىللىرىنى ئىسلامغا مايىل قىلىش كۆزدە تۇتۇلغانلارغا، قۇللارنى ئازاد قىلىشقا، قەرزدارلارغا، اﷲ نىڭ يولىغا، ئىبن سەبىللەرگە بېرىلىدۇ، بۇ اﷲ نىڭ بەلگىلىمىسىدۇر، اﷲ (بەندىلىرىنىڭ مەنپەئەتنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[61] = 'ئۇلارنىڭ بەزىلىرى پەيغەمبەرگە (سۆزى ۋە ھەرىكىتى ئارقىلىق) ئەزىيەت يەتكۈزىدۇ ۋە (پەيغەمبەرنى) &laquo;ئاڭلىغان نەرسىسىنىڭ ھەممىسىگە ئىشىنىدۇ&raquo; دەيدۇ. ئېيتقىنكى، &laquo;(دۇرۇس، ئۇ) سىلەرگە پايدىلىق سۆزلەرنى ئاڭلاپ ئىشىنىدۇ، اﷲ نىڭ سۆزلىرىگە ئىشىنىدۇ، مۆمىنلەرنىڭ سۆزىگە (ئۇلارنىڭ ئىخلاسىنى بىلگەنلىكتىن) ئىشىنىدۇ، ئۇ سىلەردىن ئىمان ئېيتقانلار ئۈچۈن رەھمەتتۇر&raquo; اﷲ نىڭ پەيغەمبىرىگە ئەزىيەت يەتكۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ';
$t[62] = 'ئۇلار سىلەرنى رازى قىلىش ئۈچۈن (پەيغەمبەرگە تېگىدىغان گەپنى قىلمىدۇق دەپ) اﷲ بىلەن قەسەم قىلىدۇ، ئەگەر ئۇلار راستلا مۆمىن بولىدىغان بولسا، اﷲ نى ۋە ئۇنىڭ پەيغەمبىرىنى رازى قىلىشلىرى كېرەك ئىدى';
$t[63] = '(بۇ مۇناپىقلار) اﷲ قا ۋە ئۇنى پەيغەمبىرىگە كىمكى قارشىلىق كۆرسىتىدىكەن، ئۇنىڭ دوزاخقا كىرىدىغانلىقىنى، دوزاختا مەڭگۈ قالىدىغانلىقىنى بىلمەمدۇ؟ ئەنە شۇ چوڭ رەسۋالىقتۇر';
$t[64] = 'مۇناپىقلار ئۆزلىرى توغرىسىدا بىرەر سۈرە نازىل بولۇپ، ئۇلارنىڭ دىللىرىدىكىنى (يەنى نىفاقنى) مۆمىنلەرگە خەۋەر بېرىشتىن قورقىدۇ. ئېيتقىنكى، &laquo;سىلەر (اﷲ نىڭ دىنىنى خالىغىنىڭلارچە) مەسخىرە قىلىڭلار، سىلەر (پاش بولۇشتىن) قورقىدىغان نەرسىنى (يەنى مۇناپىقلىقنى) اﷲ چوقۇم ئاشكارا قىلىدۇ&raquo;';
$t[65] = '(ئى مۇھەممەد!) ئەگەر سەن ئۇلارنىڭ (مەسخىرە قىلغانلىقىنى) سورىساڭ، ئۇلار: &laquo;بىز (راست ئەمەس)، پەقەت ئىچ پۇشۇقى قىلىپ ئوينىشىپ دەپ قويدۇق&raquo; دەيدۇ. (بۇ مۇناپىقلارغا) &laquo;سىلەر اﷲ نىڭ دىنىنى، اﷲ نىڭ ئايەتلىرىنى (يەنى كىتابىنى) ۋە اﷲ نىڭ پەيغەمبىرىنى مەسخىرە قىلدىڭلارمۇ؟&raquo; دېگىن';
$t[66] = 'سىلەر (يالغان قەسەم ئىچىپ) ئۆزرە ئېيتماڭلار، سىلەر ئىمان ئېيتقىنىڭلاردىن كېيىن، (پەيغەمبەرنى مەسخىرە قىلىش بىلەن) كاپىر بولدۇڭلار، سىلەردىن بىر گۇرۇھنى (راست تەۋبە قىلغانلىقلىرى ئۈچۈن) ئەپۇ قىلساق، يەنە بىر گۇرۇھنى گۇناھكار بولغانلىقلىرى (يەنى مۇناپىقلىق بىلەن گۇناھنى داۋاملاشتۇرغانلىقلىرى) ئۈچۈن جازالايمىز';
$t[67] = 'مۇناپىق ئەرلەر بىلەن مۇناپىق ئاياللار (مۇناپىقلىقتا ۋە ئىماندىن يىراق بولۇشتا) بىر - بىرىگە ئوخشايدۇ، ئۇلار يامانلىققا بۇيرۇيدۇ، ياخشىلىقتىن توسىدۇ، ئۇلار قوللىرىنى يۇمۇۋالىدۇ (يەنى بېخىللىق قىلىپ سەدىقە بەرمەيدۇ). ئۇلار اﷲ نى ئۇنتۇدى (يەنى اﷲ نىڭ تائىتىنى تەرك ئەتتى)، اﷲ مۇ ئۇلارنى ئۇنتۇدى (يەنى اﷲ ئۇلارنى ئۆزىنىڭ پەزلىدىن ۋە رەھمىتىدىن مەھرۇم قىلدى). شۈبھىسىزكى، مۇناپىقلار پاسىقلاردۇر';
$t[68] = 'اﷲ مۇناپىق ئەرلەرگە، مۇناپىق ئاياللار ۋە كۇففارلارغا دوزاخنى ۋەدە قىلدى، ئۇلار دوزاختا مەڭگۈ قالىدۇ. دوزاخ ئۇلارنى (ئازابلاشقا) يېتەرلىكتۇر، اﷲ ئۇلارغا لەنەت قىلدى، ئۇلار مەڭگۈلۈك ئازابقا دۇچار بولىدۇ';
$t[69] = '(ئى مۇناپىقلار، سىلەرنىڭ ھالىڭلار) سىلەردىن بۇرۇن ئۆتكەن (ھالاك بولغان ئۈممەتلەرنىڭ ھالىغا) ئوخشايدۇ، ئۇلار كۈچ - قۇۋۋەتتە سىلەردىن كۈچلۈك، مال - مۈلكى ۋە ئەۋلادى سىلەرنىڭكىگە قارىغاندا كۆپ ئىدى. سىلەردىن بۇرۇن ئۆتكەنلەر (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋىسىدىن بەھرىمەن بولغاندەك، سىلەرمۇ (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋەڭلاردىن بەھرىمەن بولدۇڭلار؛ ئۇلار باتىلغا چۆمگەندەك، سىلەرمۇ باتىلغا چۆمدۈڭلار؛ ئەنە شۇلارنىڭ ياخشى ئەمەللىرى دۇنيا ۋە ئاخىرەتتە ئېتىبارسىزدۇر، ئەنە شۇلار زىيان تارتقۇچىلاردۇر';
$t[70] = 'ئۇلارغا ئۇلاردىن بۇرۇن ئۆتكەنلەرنىڭ، نۇھ، ئاد ۋە سەمۇد قەۋملىرىنىڭ، ئىبراھىم قەۋمىنىڭ، مەديەن ئاھالىسىنىڭ، كۆمتۈرۈۋېتىلگەن قىشلاقلارنىڭ (يەنى لۇت ئەلەيھىسسالام قەۋمى قىشلاقلىرىنىڭ) خەۋەرلىرى كەلمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر ئېلىپ كەلدى (بۇ مۆجىزىلەرنى ئۇلار ئىنكار قىلدى). اﷲ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار (كاپىر بولۇش ۋە گۇناھ قىلىش بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى';
$t[71] = 'مۆمىن ئەرلەر، مۆمىن ئاياللار بىر - بىرى بىلەن دوستتۇر، ئۇلار (كىشىلەرنى) ياخشى ئىشلارنى قىلىشقا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، نامازنى (تولۇق) ئادا قىلىدۇ، زاكات بېرىدۇ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىدۇ، ئەنە شۇلارغا اﷲ رەھىم قىلىدۇ، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[72] = 'اﷲ مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى ۋەدە قىلدى. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، ھەمىشە تۇرىدىغان جەننەتلەردە گۈزەل جايلارنى ۋەدە قىلدى. (ئۇلار ئېرىشىدىغان) اﷲ نىڭ رازىلىقى (جەننەت نېمەتلىرىنىڭ ھەممىسىدىن) كاتتىدۇر، بۇ چوڭ بەختتۇر';
$t[73] = 'ئى پەيغەمبەر! كاپىرلارغا ۋە مۇناپىقلارغا قارشى جىھاد قىلغىن، ئۇلارغا (قارشى غازات قىلىش ۋە قورقۇنچ سېلىش بىلەن) قاتتىق مۇئامىلە قىلغىن، ئۇلارنىڭ جايى جەھەننەمدۇر، ئۇ نېمىدېگەن يامان جاي!';
$t[74] = 'ئۇلار (يەنى مۇناپىقلار) ھېچنېمە دېمىگەنلىكى توغرىسىدا اﷲ بىلەن قەسەم قىلىدۇ، ئۇلار ھەقىقەتەن كۇفرى سۆزىنى ئېيتتى، ئىسلام دىنىغا كىرگەنلىكىنى ئىپادىلىگەندىن كېيىن كاپىر بولدى، ئۇلار ئىشقا ئاشمايدىغان ئىشنى ئىشقا ئاشۇرۇشقا ئورۇندى (يەنى پەيغەمبەر ئەلەيھىسسالامنى ئۆلتۈرۈشنى قەستلىدى)، ئۇلارنىڭ (پەيغەمبەر ئەلەيھىسسالامنى) يامان كۆرۈشنى پەقەت اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارنى ئۆز پەزلىدىن باي قىلغانلىقى ئۈچۈندۇر، ئەگەر ئۇلار (مۇناپىقلىقتىن) تەۋبە قىلسا، ئۆزلىرىگە ياخشىدۇر، ئەگەر ئۇلار (ئىماندىن داۋاملىق) يۈز ئۆرۈسە، اﷲ ئۇلارنى دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب بىلەن جازالايدۇ، يەر يۈزىدە ئۇلارغا (ئۇلارنى ئازابتىن قۇتۇلدۇرىدىغان) بىرەر دوست ۋە بىرەر مەدەتكار تېپىلمايدۇ';
$t[75] = 'ئۇلارنىڭ ئىچىدىكى بەزىلەر: &laquo;ئەگەر اﷲ بىزگە ئۆز پەزلىدىن ئاتا قىلسا (يەنى رىزقىمىزنى كەڭ قىلسا) ئەلۋەتتە سەدىقە بېرەتتۇق، ئەلۋەتتە ياخشىلاردىن بولاتتۇق&raquo; دەپ اﷲ قا ئەھدە قىلدى';
$t[76] = 'اﷲ ئۇلارغا ئۆز پەزلىدىن ئاتا قىلغان (يەنى باي قىلغان) چاغدا، بېخللىق قىلىشتى (يەنى ئەھدىنى بۇزۇپ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىشتىن يۈز ئۆرۈشتى)';
$t[77] = 'ئۇلار (سەدىقە قىلىمىز، ياخشىلاردىن بولىمىز دەپ) اﷲ قا بەرگەن ئەھدىسىگە خىلاپلىق قىلغانلىقلىرى، (ئىمانىنى، ئېھسانىنى، دەۋا قىلىشتىكى) يالغانچىلىقلىرى ئۈچۈن، اﷲ قا مۇلاقات بولىدىغان كۈن (يەنى قىيامەت كۈنى) گە قەدەر مۇناپىقلىقنىڭ دىللىرىدا (يىلتىز تارتىپ) تۇرۇشىنى اﷲ ئۇلارنىڭ (ئىشىنىڭ) ئاقىۋىتى قىلدى';
$t[78] = 'اﷲ نىڭ ئۇلارنىڭ ئىچكى سىرلىرىنى، (ئۆزئارا قىلىشقان) يوشۇرۇن سۆزلىرىنى بىلىپ تۇرىدىغانلىقىنى، پۈتۈن غەيبلەرنى چوڭقۇر بىلىدىغانلىقىنى ئۇلار ئۇقمامدۇ؟';
$t[79] = '(مۇناپىقلار) مۆمىنلەرنىڭ ئىچىدىكى مەردلىك بىلەن (كۆپ) سەدىقە قىلغۇچىلارنى، تاقىتىنىڭ يېتىشىچە (يەنى كەمبەغەل بولغانلىقتىن ئاز) سەدىقە قىلغۇچىلارنى ئەيىبلەيدۇ، ئۇلارنى مەسخىرە قىلىدۇ، اﷲ ئۇلارنى مەسخىرە قىلغانلىقلىرى ئۈچۈن جازالايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ';
$t[80] = '(ئى مۇھەممەد!) ئۇلار (يەنى مۇناپىقلار) ئۈچۈن مەيلى مەغپىرەت تەلەپ قىل، مەيلى مەغپىرەت تەلەپ قىلما (بەرىبىر ئوخشاش)، ئۇلار ئۈچۈن 70 قېتىم مەغپىرەت قىلمايدۇ. چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى، اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ';
$t[81] = 'رەسۇلۇللاھقا خىلاپلىق قىلىپ (تەبۈك غازىتىغا چىقماي) قېلىپ قالغانلار (يەنى مۇناپىقلار) ئۆيلىرىدە بەخىرامان ئولتۇرغانلىقلىرى بىلەن خۇشال بولۇشتى، ئۇلار اﷲ نىڭ يولىدا ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلىشنى ياقتۇرمىدى، ئۇلار (بىر - بىرىگە): &laquo;ئىسسىقتا چىقماڭلار&raquo; دېيىشتى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;جەھەننەمنىڭ ئوتى تېخىمۇ قىزىقتۇر. ئەگەر ئۇلار چۈشىنىدىغان بولسا، (ئەلۋەتتە ئىسسىقتا رەسۇلۇللاھ بىلەن بىرگە چىقاتتى)&raquo;';
$t[82] = 'ئۇلار قىلمىش (گۇناھ) لىرىنىڭ جازاسى ئۈچۈن، ئاز كۈلسۇن، كۆپ يىغلىسۇن';
$t[83] = 'ئەگەر اﷲ سېنى (تەبۈك غازىتىدىن) قايتۇرۇپ (ئۆزرىسىز قېلىپ قالغان مۇناپىقلاردىن) بىر تۈركۈمنىڭ يېنىغا ئېلىپ بارسا، ئۇلار (ئىككىنچى بىر غازاتقا) چىقىشقا سەندىن ئىزنى سورىسا، (ئۇلارغا) ئېيتقىنكى، &laquo;سىلەر مەن بىلەن بىللە مەڭگۈ (غازاتقا) چىقماڭلار، ھەرگىز مەن بىلەن بىللە چىقىپ ئۇرۇش قىلماڭلار، شۈبھىسىزكى، سىلەر دەسلەپكى قېتىمدا (چىقماي، ئۆيۈڭلاردا بەخىرامان) ئولتۇرۇشقا رازى بولدۇڭلار، ئەمدى (غازاتقا چىقماي) قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار) بىلەن ئولتۇرۇڭلار&raquo;';
$t[84] = '(ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىن ئۆلگەن ھېچبىر ئادەمنىڭ نامىزىنى چۈشۈرمىگىن، (دەپنە قىلىش، زىيارەت ياكى دۇئا ئۈچۈن) ئۇنىڭ قەبرىسى ئۈستىدىمۇ تۇرمىغىن، چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى. ئۇلار پاسىق ھالىتى بىلەن ئۆلدى';
$t[85] = 'ئۇلارنىڭ ماللىرى ۋە ئەۋلادلىرى سېنى ئەجەبلەندۈرمىسۇن. اﷲ دۇنيادا بۇلار ئارقىلىق ئۇلارنى جازالاشنى، ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ';
$t[86] = 'بىرەر سۈرە نازىل قىلىنىپ: &laquo;اﷲ قا ئىمان ئېيتىڭلار ۋە اﷲ نىڭ پەيغەمبىرى بىلەن بىللە جىھاد قىلىڭلار&raquo; دېيىلسە، ئۇلارنىڭ ئارىسىدىكى مالدارلار سەندىن (غازاتتىن قېلىپ قېلىشقا) رۇخسەت سورايدۇ، ئۇلار: &laquo;بىزنى قوي، (غازاتقا چىقماي، ئۆيلىرىدە بەخىرامان) ئولتۇرغۇچىلار بىلەن بىللە بولايلى&raquo; دەيدۇ';
$t[87] = 'ئۇلار ئۆيلىرىدە قالغۇچىلار (يەنى ئاياللار، كېسەللەر ۋە ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، ئۇلارنىڭ دىللىرى پېچەتلەندى. شۇنىڭ ئۈچۈن، ئۇلار (جىھادتىكى پەيغەمبەرگە ئىتائەت قىلىشتىن كېلىدىغان بەختنى) چۈشەنمەيدۇ';
$t[88] = 'لېكىن، پەيغەمبەر ۋە پەيغەمبەر بىلەن بىللە ئىمان ئېيتقانلار ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلدى، ئەنە شۇلار (ئىككىلا دۇنيانىڭ) ياخشىلىقلىرىغا نائىل بولىدۇ، ئەنە شۇلار مەقسەتكە يەتكۈچىلەردۇر';
$t[89] = 'اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى. ئۇلار ئۇنىڭدا مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر';
$t[90] = 'ئەئرابىلاردىن ئۆزرە بايان قىلغۇچىلار (جىھادقا چىقماسلىققا) رۇخسەت سوراپ كەلدى. (ئىماننى دەۋا قىلىپ) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە يالغانچىلىق قىلغانلار جىھادقا چىقمىدى (يەنى ئەئرابىلاردىن يەنە بىر تۈركۈمى جىھادقىمۇ چىقمىدى، ئۆزرە بايان قىلىپمۇ كەلمىدى). ئۇلاردىن كاپىر بولغانلار قاتتىق ئازابقا دۇچار بولىدۇ';
$t[91] = 'ئاجىزلار (يەنى ياشانغان بوۋايلار)، كېسەللەر، (جىھادقا چىقىشقا) خىراجەت تاپالمىغانلار اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە سادىق بولسىلا، جىھادقد چىقمىسا گۇناھ بولمايدۇ، ياخشى ئىش قىلغۇچىلارنى ئەيىبلەشكە يول يوقتۇر، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[92] = 'يەنە مۇنداق كىشىلەرنىمۇ ئەيىبلەشكە يول يوقتۇركى، ئۇلار (جىھادقا چىقىش ئۈچۈن) سەندىن ئۇلاغ سوراپ كەلگەندە، (ئۇلارغا سەن) &laquo;مېنىڭ سىلەرنى تەمىنلەيدىغان ئۇلاغلىرىم يوق&raquo; دېدىڭ؟ ئۇلار (جىھادقا چىقىشقا) سەرپ قىلىدىغان نەرسە تاپالمىغانلىقلىرى ئۈچۈن غەمكىن، كۆزلىرىدىن ياش تۆككەن ھالدا قايتىشتى';
$t[93] = 'ئۆزلىرى باي تۇرۇپ، (جىھادقا چىقماسلىققا) رۇخسەت سورىغانلار ئەيىبلىنىشى كېرەك، ئۇلار (جىھادقا چىقمىغان ئاياللار، كېسەللەر، ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، اﷲ ئۇلارنىڭ دىللىرىنى پېچەتلىۋەتتى، (شۇنىڭ ئۈچۈن) ئۇلار (اﷲ نىڭ ئەمرىنى) بىلمەيدۇ';
$t[94] = 'ئۇلارنىڭ يېنىغا (يەنى مەدىنىگە تەبۈك غازىتىدىن) قايتىپ كەلگەن چېغىڭلاردا، ئۇلار سىلەرگە (غازاتتىن قېلىپ قالغانلىقلىرى توغرىسىدا) ئۆزرە بايان قىلىدۇ، (ئى مۇھەممەد!) ئۇلارغا: &laquo;ئۆزرە بايان قىلماڭلار، سىلەرنىڭ (سۆزۈڭلارغا) ئىشەنمەيمىز، اﷲ سىلەرنىڭ ئەھۋالىڭلارنى (ۋە دىلىڭلاردىكى مۇناپىقلىقنى) بىزگە خەۋەر قىلدى، اﷲ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنىڭ (بۇنىڭدىن كېيىنكى) ئەمەلىڭلارنى كۆرۈپ تۇرىدۇ (مۇناپىقلىقتىن تەۋبە قىلامسىلەر؟ ياكى ئۇنىڭدا چىڭ تۇرامسىلەر؟)، ئاندىن كېيىن (يەنى ئۆلگىنىڭلاردىن كېيىن)، يوشۇرۇن ۋە ئاشكارا ئىشلارنى بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ سىلەرگە پۈتۈن قىلمىشىڭلارنى ئېيتىپ بېرىدۇ&raquo; دېگىن';
$t[95] = 'ئۇلار (يەنى مۇناپىقلار) نىڭ يېنىغا قايتىپ بارغىنىڭلاردا، ئۇلار سىلەرنىڭ ئۇلارنى ئەيىبلىمەسلىكىڭلار ئۈچۈن (يالغان ئۆزرىلەر بايان قىلىپ)، اﷲ بىلەن قەسەم قىلىدۇ، ئۇلاردىن يۈز ئۆرۈڭلار (يەنى ئۇلار بىلەن سۆزلەشمەڭلار ۋە سالاملاشماڭلار)، ئۇلار ھەقىقەتەن نىجىستۇر، قىلمىشلىرىنىڭ جازاسى ئۈچۈن ئۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر';
$t[96] = 'سىلەرنىڭ رازىلىقىڭلارغا ئېرىشىش ئۈچۈن، ئۇلار سىلەرگە قەسەم ئىچىپ بېرىدۇ، سىلەر ئۇلاردىن رازى بولساڭلار (بۇ ئۇلارغا پايدا بەرمەيدۇ)، چۈنكى اﷲ پاسىق قەۋمدىن رازى بولمايدۇ';
$t[97] = 'ئەئرابىلارنىڭ كۇفرى ۋە مۇناپىقلىقى تېخىمۇ زىيادىدۇر، اﷲ پەيغەمبىرىگە نازىل قىلغان شەرىئەت ئەھكاملىرىنى بىلمەسلىككە ئۇلار ئەڭ لايىقتۇر، اﷲ (مەخلۇقاتنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[98] = 'ئەئرابىلاردىن بەزىلىرى (اﷲ يولىدا) بەرگەن نەرسىلىرىنى جەرىمانە ھېسابلايدۇ، سىلەرنىڭ ھادىسىگە ئۇچرىشىڭلارنى كۈتىدۇ، ئۇلارنىڭ ئۆزلىرى ھالاكەتكە ئۇچرىسۇن، اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچى، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر';
$t[99] = 'ئەئرابىلارنىڭ بەزىلىرى اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدۇ، (اﷲ يولىدا) بەرگەن نەرسىلىرىنى اﷲ قا يېقىن بولۇشنىڭ ۋە پەيغەمبەرنىڭ دۇئاسىغا ئېرىشىشنىڭ ۋاسىتىسى قىلىدۇ، ئۇلار بۇ ئارقىلىق راستلا اﷲ قا بولغان يېقىنچىلىققا ئېرىشىدۇ، اﷲ قا ئۇلارنى ئۆزنىڭ رەھمىتى دائىرىسىگە كىرگۈزىدۇ، اﷲ ھەقىقەتەن (مۆمىن بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) تولىمۇ مېھرىباندۇر';
$t[100] = 'ھەممىدىن ئىلگىرى ئىمان ئېيتقان مۇھاجىرلار ۋە ئەنسارلاردىن، ياخشىلىق قىلىش بىلەن ئۇلارغا ئەگەشكەنلەر (يەنى تابىئىنلار ۋە قىيامەتكىچە ئۇلارنىڭ يولىدا ماڭغانلار) دىن اﷲ رازى بولدى، ئۇلارمۇ اﷲ تىن مەمنۇن بولدى. اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر';
$t[101] = 'ئەتراپىڭلاردا ئەئرابىلاردىن مۇناپىقلار بار، مەدىنە ئاھالىسىدىنمۇ مۇناپىقلىقتا چىڭ تۇرغانلار بار، ئۇلارنى سەن بىلمەيسەن، بىز بىلىمىز، ئۇلارنى ئىككى قېتىم (يەنى دۇنيادا قەتل قىلىش ۋە ئەسىر ئېلىش بىلەن، ئۆلگەندە گۆر ئازابى بىلەن) جازالايمىز. ئاندىن ئۇلار (ئاخىرەتتە) چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) قايتۇرۇلىدۇ';
$t[102] = 'يەنە بىر قىسىم كىشىلەر گۇناھلىرىنى ئېتىراپ قىلدى (يەنى باشقىلارغا ئوخشاش جىھادقا چىقمىغانلىقىغا يالغان ئۆزرە ئېيتمىدى). ئۇلار ياخشى ئىشقا يامان ئىشنى ئارىلاشتۇرۇپ قويدى، اﷲ نىڭ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلىشى ئۈمىدلىكتۇر، اﷲ ھەقىقەتەن (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[103] = '(ئى مۇھەممەد!) ئۇلارنىڭ ماللىرىنىڭ بىر قىسىمىنى سەدىقە ھېسابىدا ئالغىنكى، ئۇنىڭ بىلەن ئۇلارنى گۇناھلىرىدىن پاكلىغايسەن ۋە (ياخشىلىقلىرىنى) كۆپەيتكەيسەن، ئۇلارغا دۇئا قىلغىن، شۈبھىسىزكى، سېنىڭ دۇئايىڭ ئۇلارغا خاتىرجەملىك ئېلىپ كېلىدۇ، اﷲ ئۇلارنىڭ (سۆزىنى) ئاڭلاپ تۇرغۇچى، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر';
$t[104] = 'ئۇلار (يەنى تەۋبە قىلغۇچىلار) اﷲ نىڭ ئۆز بەندىلىرىنىڭ تەۋبىسىنى قوبۇل قىلىدىغانلىقىنى، (خالىس نىيەتلىك كىشىلەرنىڭ) سەدىقىسىنى قوبۇل قىلىدىغانلىقىنى ۋە اﷲ نىڭ تەۋبىنى بەكمۇ قوبۇل قىلىدىغانلىقىنى، ناھايىتى مېھرىبان ئىكەنلىكىنى بىلمەمدۇ؟';
$t[105] = '(ئۇلارغا) ئېيتقىنكى، &laquo;(خالىغان) ئەمەللەرنى قىلىڭلار، قىلغان ئەمەلىڭلارنى اﷲ، اﷲ نىڭ پەيغەمبىرى ۋە مۆمىنلەر كۆرۈپ تۇرىدۇ، يوشۇرۇن ۋە ئاشكارا (نەرسىلەرنى) بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ قىلمىشىڭلارنى سىلەرگە خەۋەر قىلىدۇ&raquo;';
$t[106] = 'يەنە بىر قىسىم (جىھادقا چىقمىغان) كىشىلەر (نىڭ ئىشى) اﷲ نىڭ ئەمرىگە قالدۇرۇلدى، اﷲ يا ئۇلارنى (تەۋبە قىلمىسا) جازالايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ (ۋە مەغپىرەت قىلىدۇ). اﷲ ئۇلارنى (يەنى ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[107] = 'بەزى كىشىلەر مۆمىنلەرگە زىيان يەتكۈزۈش، كۇفرىنى كۈچەيتىش، مۆمىنلەرنىڭ ئارىسىنى پارچىلاش، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشى تۇرغان ئادەم (يەنى ئەبۇئامر راھىب) نىڭ كېلىشىنى كۈتۈش مەقسىتىدە مەسجىد بىنا قىلدى. ئۇلار: &laquo;بىز پەقەت ياخشىلىقنىلا ئىرادە قىلدۇق&raquo; دەپ چوقۇم قەسەم قىلىدۇ، اﷲ گۇۋاھلىق بېرىدۇكى، ئۇلار شەك - شۈبھىسىز يالغانچىلاردۇر';
$t[108] = 'ئۇ مەسجىدتە مەڭگۈ ناماز ئوقۇمىغىن، بىرىنچى كۈنىدىن تارتىپلا تەقۋا ئاساسىغا قۇرۇلغان مەسجىد ھەقىقەتەن سېنىڭ ناماز ئوقۇشۇڭغا ئەڭ لايىقتۇر، ئۇنىڭدا (گۇناھلاردىن) پاك بولۇشنى سۆيىدىغان كىشىلەر بار، اﷲ (ئىچكى ۋە تاشقى جەھەتتىن) پاك بولغۇچىلارنى دوست تۇتىدۇ';
$t[109] = '(دىننىڭ) بىناسىنى اﷲ نىڭ تەقۋادارلىقى ۋە رازىلىقى ئاساسىغا قۇرغان ئادەم ياخشىمۇ ياكى يىقىلاي دەپ قالغان يارنىڭ گىرۋىكىگە قۇرۇپ، ئۆزىمۇ ئۇنىڭ بىلەن بىللە دوزاخ ئوتىغا ئۆرۈلۈپ چۈشكەن ئادەم ياخشىمۇ؟ اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ';
$t[110] = 'تاكى ئۇلارنىڭ يۈرەكلىرى پارە - پارە بولۇپ ئۆلمىگىچە، ئۇلارنىڭ سالغان مەسجىدى (يەنى مەسجىدى زىرار) دىللىرىدىكى گۇمان (مۇناپىقلىق) نىڭ يىلتىزى بولۇپ قىلىۋېرىدۇ، اﷲ (مۇناپىقلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ يامان نىيىتىگە ئاساسەن، ئۇلارنى جازالاشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[111] = 'شۈبھىسىزكى، اﷲ مۆمىنلەردىن ئۇلارنىڭ جانلىرىنى، ماللىرىنى ئۇلارغا جەننەتنى بېرىپ سېتىۋالدى. ئۇلار اﷲ نىڭ يولىدا ئۇرۇش قىلىپ (دۈشمەنلەرنى) ئۆلتۈرىدۇ ۋە ئۆلتۈرۈلىدۇ (يەنى دۈشمەنلەر بىلەن جىھاد قىلىپ شېھىت بولىدۇ)، (جىھاد قىلغۇچىلارغا جەننەتنى ۋەدە قىلىش) تەۋراتتا، ئىنجىلدا ۋە قۇرئاندا زىكرى قىلىنغان (اﷲ نىڭ) راست ۋەدىسىدۇر، ۋەدىسىگە اﷲ تىنمۇ بەك ۋاپا قىلغۇچى كىم بار؟ (يەنى اﷲ تىنمۇ ۋاپادار ئەھەدى يوق) قىلغان بۇ سوداڭلاردىن خۇشال بولۇڭلار، بۇ زور مۇۋەپپەقىيەتتۇر';
$t[112] = '(گۇناھلىرىدىن) تەۋبە قىلغۇچىلار، (ئىخلاس بىلەن) ئىبادەت قىلغۇچىلار، (اﷲ قا) ھەمدۇسانا ئېيتقۇچىلار، روزا تۇتقۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار، ياخشى ئىشلارغا دەۋەت قىلىپ، يامان ئىشلاردىن توسقۇچىلار، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلغۇچىلار (يەنى اﷲ نىڭ بېكىتكەن پەرزلىرىنى ئادا قىلىپ، نەھيى قىلغان ئىشلىرىدىن يانغۇچىلار ھەم ئەھلى جەننەتتۇر)؛ مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن';
$t[113] = 'پەيغەمبەرگە ۋە مۆمىنلەرگە مۇشرىكلارنىڭ ئەھلى دوزاخ ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، مۇشرىكلار ئۇلارنىڭ تۇغقىنى بولغان تەقدىردىمۇ، ئۇلارغا مەغپىرەت تەلەپ قىلىشى دۇرۇس ئەمەس';
$t[114] = 'ئىبراھىمنىڭ ئاتىسىغا مەغپىرەت تەلەپ قىلىشى ئۇنىڭغا ئىلگىرى قىلغان ۋەدىسىنى ئورۇنلاش ئۈچۈن ئىدى. ئۇنىڭغا ئاتىسىنىڭ اﷲ نىڭ دۈشمىنى ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، ئۇ ئاتىسى بىلەن ئادا - جۇدا بولدى. ئىبراھىم بولسا ھەقىقەتەن كۆپ ئاھ چەككۈچى (يەنى كۆڭلى يۇمشاق)، (يەتكەن ئەزىيەتلەرگە) چىداملىق ئىدى';
$t[115] = 'اﷲ بىر قەۋمنى ھىدايەت قىلغاندىن كېيىن، ئۇلار ساقلىنىشقا تېگىشلىك ئىشلارنى بايان قىلماي تۇرۇپ، ئۇلارنى گۇمراھ قىلمايدۇ (يەنى گۇمراھلىققا ھۆكۈم قىلمايدۇ)، اﷲ ھەقىقەتەن ھەر نەرسىنى بىلىپ تۇرغۇچىدۇر';
$t[116] = 'شۈبھىسىزكى، ئاسماننىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەرگە اﷲ تىن باشقا ھېچ دوست ۋە مەدەتكار يوق';
$t[117] = 'اﷲ ھەقىقەتەن پەيغەمبەرنىڭ ۋە ئۇنىڭغا قىيىنچىلىق پەيتىدە ئەگەشكەن مۇھاجىرلار ۋە ئەنسارلارنىڭ تەۋبىسىنى قوبۇل قىلدى. (ئەينى زاماندا) ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەرنىڭ دىللىرى ھەقتىن بۇرۇلۇپ كېتىشىگە تاس قالغاندىن كېيىن، اﷲ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى. اﷲ ئۇلارغا تولىمۇ مەرھەمەتلىك، ناھايىتى مېھرىباندۇر';
$t[118] = 'جىھادقا چىقمىغان ئۈچ كىشىنىڭ تەۋبىسىنىمۇ اﷲ قوبۇل قىلدى. شۇنچە كەڭ زېمىن ئۇلارغا تار تۇيۇلغان، ئۇلارنىڭ جانلىرى سىقىلغان، ئۇلار اﷲ قا تەۋبە قىلماي تۇرۇپ اﷲ نىڭ غەزىپىدىن قۇتۇلۇش مۇمكىن ئەمەسلىكىگە ئىشەنگەن ئىدى. ئاندىن ئۇلارنىڭ تەۋبە قىلغۇچىلاردىن بولۇشى ئۈچۈن، اﷲ ئۇلارنى تەۋبىگە مۇۋەپپەق قىلدى. اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (بەندىلىرىگە) ناھايىتى مېھرىباندۇر';
$t[119] = 'ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، راستچىللار بىلەن بىللە بۇلۇڭلار';
$t[120] = 'مەدىنە ئاھالىسى ۋە ئۇلارنىڭ ئەتراپىدىكى ئەئرابىلارنىڭ رەسۇلۇللا بىلەن جىھادقا بىللە چىقماي قېلىپ قېلىشى، ئۆزلىرىنىڭ ئارامى بىلەنلا بولۇپ، جاپادا رەسۇلۇللاھ بىلەن بىللە بولماسلىقى توغرا ئەمەس ئىدى. بۇ (يەنى ئۇلارنى جىھادقا چىقماي قېلىپ قېلىشتىن مەنئى قىلىش) شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ يولىدا ئۇچرىغان ئۇسسۇزلۇق، تارتقان جاپا - مۇشەققەت ۋە ئاچلىق، ئۇلارنىڭ كاپىرلارنى خاپىلىققا سالىدىغان ھەر بىر قەدىمى، دۈشمەنلەر ئۈستىدىن ئېرىشكەن (ئۇلارنى ئۆلتۈرۈش، ئەسىر ئېلىش، مەغلۇپ قىلىش قاتارلىق) ھەر بىر نەرسىسى ئۈچۈن (اﷲ نىڭ دەرگاھىدا) ئۇلارغا ياخشىلىق (يەنى ساۋاب) يېزىلىدۇ، اﷲ ئەلۋەتتە ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ';
$t[121] = 'ئۇلارنىڭ (اﷲ نىڭ يولىدا) مەيلى كىچىك بولسۇن ياكى چوڭ بولسۇن، سەرپ قىلغان نەرسىسى ۋە جىھاد ئۈچۈن باسقان ھەر بىر مۇساپىسى ئۇلارنىڭ قىلغان ئەمەللىرىگە ئەڭ ياخشى مۇكاپات بېرىش يۈزىسىدىن ئۇلارغا يېزىلىدۇ';
$t[122] = 'مۆمىنلەرنىڭ ھەممىسىنىڭ جىھادقا چىقىشى لايىق ئەمەس (ئۇلارنىڭ بىر تۈركۈمى جىھادقا چىقتى)، ئۇلارنىڭ ئىچىدىكى ھەر بىر جامائەدىن يەنە بىر تۈركۈمى دىنىي ئالىم بولۇپ، ئۇلار قەۋمىنىڭ قېشىغا قايتقاندىن كېيىن، قەۋمىنىڭ اﷲ تىن قورقۇشى ئۈچۈن، ئۇلارنى ئاگاھلاندۇرۇش مەقسىتىدە نېمىشقا (ئىلىم تەلەپ قىلىشقا) چىقمىدى؟';
$t[123] = 'ئى مۆمىنلەر! سىلەر ئۆزۈڭلارغا يېقىن جايدىكى كاپىرلار بىلەن ئۇرۇش قىلىڭلار (يەنى سىلەرگە خەتەر بولماسلىق ئۈچۈن، ئالدى بىلەن ئەتراپىڭلاردىكى كۇففارلارنى ئۇجۇقتۇرۇڭلار)، سىلەرنى ئۇلار ھېس قىلسۇن (يەنى ئۇرۇش ئۈستىدە ئۇلارغا قاتتىق قول بۇلۇڭلار، ئۇلارغا ئىچىڭلار ئاغرىمىسۇن)، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ تەقۋادارلارغا مەدەتكاردۇر)';
$t[124] = '(قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار (يەنى مۇناپىقلار) نىڭ ئارىسىدا (مەسخىرە قىلىش يۈزىسىدىن): &laquo;بۇ (يەنى سۈرە) قايسىڭلارنىڭ ئىمانىنى كۈچەيتتى&raquo; دەيدىغانلار بار، مۆمىنلەرگە كېلىدىغان بولساق، (ھەر سۈرىدە يېڭى دەلىل - ئىسپاتلار بولغانلىقى ئۈچۈن) بۇ (يەنى سۈرە) ئۇلارنىڭ ئىمانىنى كۈچەيتتى، ئۇلار (سۈرىنىڭ نازىل بولۇشىدىن) خۇشال بولىدۇ';
$t[125] = 'دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) غا كېلىدىغان بولساق، بۇ (سۈرە) ئۇلارنىڭ كۇفرىغا كۇفرى قوشتى (يەنى ئۇلار مۇناپىقلىقتا تېخىمۇ ئەزۋەيلىدى، تېخىمۇ گۇمراھلاشتى)، ئۇلار كاپىر پېتى ئۆلدى';
$t[126] = 'ئۇلار ئۆزلىرىنىڭ ھەر يىلى بىر قېتىم ياكى ئىككى قېتىم سىنىلىدىغانلىقىنى (يەنى ئۇلار توغرىسىدا ۋەھيى نازىل بولۇپ، سىرلىرىنىڭ پاش قىلىنىدىغانلىقىنى) بىلمەمدۇ؟ ئاندىن ئۇلار تەۋبە قىلمايدۇ (يەنى نىفاقتىن قايتمايدۇ). ئىبرەتمۇ ئالمايدۇ';
$t[127] = '(ئۇلار پەيغەمبەر ئەلەيھىسسالامنىڭ ھۇزۇرىدىكى چاغدا، ئۇلارنىڭ ئەيىبىنى ئاچىدىغان قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار بىر - بىرىگە قارىشىپ: &laquo;سىلەرنى بىرەرسى كۆرۈپ قالارمۇ&raquo; دېيىشىپ تىكىۋېتىدۇ، ئۇلار (ھەقىقەتنى) چۈشەنمەس قەۋم بولغانلىقى ئۈچۈن، اﷲ قا ئۇلارنىڭ دىللىرىنى ئۆزگەرتىۋەتتى';
$t[128] = '(ئى ئىنسانلار!) شۈبھىسىزكى، سىلەرگە ئۆزئارا ئاراڭلاردىن پەيغەمبەر كەلدى. سىلەرنىڭ كۈلپەت چېكىشىڭلار ئۇنىڭغا ئېغىر تۇيۇلىدۇ؛ ئۇ سىلەرنىڭ (ھىدايەت تېپىشىڭلارغا) ھېرىستۇر، مۆمىنلەرگە ئامراقتۇر، ناھايىتى كۆيۈمچاندۇر';
$t[129] = '(ئى پەيغەمبەر!) ئەگەر ئۇلار (ساڭا ئىمان ئېيتىشتىن) يۈز ئۆرۈسە، سەن: &laquo;اﷲ ماڭا. كۆرىدۇ قىلىدۇ، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم (يەنى اﷲ قا يۆلەندىم، اﷲ تىن باشقا ھېچ ئەھەدىدىن ئۈمىد كۈتمەيمەن ۋە ھېچ ئەھەدىدىنمۇ قورقمايمەن)، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر&raquo; دېگىن';
?>