$t[51] = 'ان پر ان کے اعمال کے وبال پڑ گئے۔ اور جو لوگ ان میں سے ظلم کرتے رہے ہیں ان پر ان کے عملوں کے وبال عنقریب پڑیں گے۔ اور وہ (خدا کو) عاجز نہیں کرسکتے';
$t[52] = 'کیا ان کو معلوم نہیں کہ خدا ہی جس کے لئے چاہتا ہے رزق کو فراخ کردیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کر دیتا ہے۔ جو لوگ ایمان لاتے ہیں ان کے لئے اس میں (بہت سی) نشانیاں ہیں';
$t[53] = '(اے پیغمبر میری طرف سے لوگوں کو) کہہ دو کہ اے میرے بندو جنہوں نے اپنی جانوں پر زیادتی کی ہے خدا کی رحمت سے ناامید نہ ہونا۔ خدا تو سب گناہوں کو بخش دیتا ہے۔ (اور) وہ تو بخشنے والا مہربان ہے';
$t[54] = 'اور اس سے پہلے کہ تم پر عذاب آ واقع ہو، اپنے پروردگار کی طرف رجوع کرو اور اس کے فرمانبردار ہوجاؤ پھر تم کو مدد نہیں ملے گی';
$t[55] = 'اور اس سے پہلے کہ تم پر ناگہاں عذاب آجائے اور تم کو خبر بھی نہ ہو اس نہایت اچھی (کتاب) کی جو تمہارے پروردگار کی طرف سے تم پر نازل ہوئی ہے پیروی کرو';
$t[56] = 'کہ (مبادا اس وقت) کوئی متنفس کہنے لگے کہ (ہائے ہائے) اس تقصیر پر افسوس ہے جو میں نے خدا کے حق میں کی اور میں تو ہنسی ہی کرتا رہا';
$t[57] = 'یا یہ کہنے لگے کہ اگر خدا مجھ کو ہدایت دیتا تو میں بھی پرہیزگاروں میں ہوتا';
$t[58] = 'یا جب عذاب دیکھ لے تو کہنے لگے کہ اگر مجھے پھر ایک دفعہ دنیا میں جانا ہو تو میں نیکوکاروں میں ہو جاؤں';
$t[59] = '(خدا فرمائے گا) کیوں نہیں میری آیتیں تیرے پاس پہنچ گئی ہیں مگر تو نے ان کو جھٹلایا اور شیخی میں آگیا اور تو کافر بن گیا';
$t[60] = 'اور جن لوگوں نے خدا پر جھوٹ بولا تم قیامت کے دن دیکھو گے کہ ان کے منہ کالے ہو رہے ہوں گے۔ کیا غرور کرنے والوں کو ٹھکانا دوزخ میں نہیں ہے';
$t[61] = 'اور جو پرہیزگار ہیں ان کی (سعادت اور) کامیابی کے سبب خدا ان کو نجات دے گا نہ تو ان کو کوئی سختی پہنچے گی اور نہ غمناک ہوں گے';
$t[62] = 'خدا ہی ہر چیز کا پیدا کرنے والا ہے۔ اور وہی ہر چیز کا نگراں ہے';
$t[63] = 'اسی کے پاس آسمانوں اور زمین کی کنجیاں ہیں۔ اور جنہوں نے خدا کی آیتوں سے کفر کیا وہی نقصان اُٹھانے والے ہیں';
$t[64] = 'کہہ دو کہ اے نادانو! تم مجھ سے یہ کہتے ہو کہ میں غیر خدا کی پرستش کرنے لگوں';
$t[65] = 'اور (اے محمدﷺ) تمہاری طرف اور ان (پیغمبروں) کی طرف جو تم سے پہلے ہوچکے ہیں یہی وحی بھیجی گئی ہے۔ کہ اگر تم نے شرک کیا تو تمہارے عمل برباد ہوجائیں گے اور تم زیاں کاروں میں ہوجاؤ گے';
$t[66] = 'بلکہ خدا ہی کی عبادت کرو اور شکرگزاروں میں ہو';
$t[67] = 'اور انہوں نے خدا کی قدر شناسی جیسی کرنی چاہیئے تھی نہیں کی۔ اور قیامت کے دن تمام زمین اس کی مٹھی میں ہوگی اور آسمان اس کے داہنے ہاتھ میں لپٹے ہوں گے۔ (اور) وہ ان لوگوں کے شرک سے پاک اور عالی شان ہے';
$t[68] = 'اور جب صور پھونکا جائے گا تو جو لوگ آسمان میں ہیں اور جو زمین میں ہیں سب بےہوش ہو کر گر پڑیں گے مگر وہ جس کو خدا چاہے۔ پھر دوسری دفعہ پھونکا جائے گا تو فوراً سب کھڑے ہو کر دیکھنے لگیں گے';
$t[69] = 'اور زمین اپنے پروردگار کے نور سے جگمگا اُٹھے گی اور (اعمال کی) کتاب (کھول کر) رکھ دی جائے گی اور پیغمبر اور (اور) گواہ حاضر کئے جائیں گے اور ان میں انصاف کے ساتھ فیصلہ کیا جائے گا اور بےانصافی نہیں کی جائے گی';
$t[70] = 'اور جس شخص نے جو عمل کیا ہوگا اس کو اس کا پورا پورا بدلہ مل جائے گا اور جو کچھ یہ کرتے ہیں اس کو سب کی خبر ہے';
$t[71] = 'اور کافروں کو گروہ گروہ بنا کر جہنم کی طرف لے جائیں گے۔ یہاں تک کہ جب وہ اس کے پاس پہنچ جائیں گے تو اس کے دروازے کھول دیئے جائیں گے تو اس کے داروغہ ان سے کہیں گے کہ کیا تمہارے پاس تم ہی میں سے پیغمبر نہیں آئے تھے جو تم کو تمہارے پروردگار کی آیتیں پڑھ پڑھ کر سناتے اور اس دن کے پیش آنے سے ڈراتے تھے کہیں گے کیوں نہیں لیکن کافروں کے حق میں عذاب کا حکم متحقق ہوچکا تھا';
$t[72] = 'کہا جائے گا کہ دوزخ کے دروازوں میں داخل ہوجاؤ ہمیشہ اس میں رہو گے۔ تکبر کرنے والوں کا برا ٹھکانا ہے';
$t[73] = 'اور جو لوگ اپنے پروردگار سے ڈرتے ہیں ان کو گروہ گروہ بنا کر بہشت کی طرف لے جائیں گے یہاں تک کہ جب اس کے پاس پہنچ جائیں گے اور اس کے دروازے کھول دیئے جائیں گے تو اس کے داروغہ ان سے کہیں کہ تم پر سلام تم بہت اچھے رہے۔ اب اس میں ہمیشہ کے لئے داخل ہوجاؤ';
$t[74] = 'وہ کہیں گے کہ خدا کا شکر ہے جس نے اپنے وعدہ کو ہم سے سچا کردیا اور ہم کو اس زمین کا وارث بنا دیا ہم بہشت میں جس مکان میں چاہیں رہیں تو (اچھے) عمل کرنے والوں کا بدلہ بھی کیسا خوب ہے';
$t[75] = 'تم فرشتوں کو دیکھو گے کہ عرش کے گرد گھیرا باندھے ہوئے ہیں (اور) اپنے پروردگار کی تعریف کے ساتھ تسبیح کر رہے ہیں۔ اور ان میں انصاف کے ساتھ فیصلہ کیا جائے گا اور کہا جائے گا کہ ہر طرح کی تعریف خدا ہی کو سزاوار ہے جو سارے جہان کا مالک ہے';
?>