$t[58] = 'اور اندھا اور آنکھ والا برابر نہیں۔ اور نہ ایمان لانے والے نیکوکار اور نہ بدکار (برابر ہیں) (حقیقت یہ ہے کہ) تم بہت کم غور کرتے ہو';
$t[59] = 'قیامت آنے والی ہے اس میں کچھ شک نہیں۔ لیکن اکثر لوگ ایمان نہیں رکھتے';
$t[60] = 'اور تمہارے پروردگار نے کہا ہے کہ تم مجھ سے دعا کرو میں تمہاری (دعا) قبول کروں گا۔ جو لوگ میری عبادت سے ازراہ تکبر کنیاتے ہیں۔ عنقریب جہنم میں ذلیل ہو کر داخل ہوں گے';
$t[61] = 'خدا ہی تو ہے جس نے تمہارے لئے رات بنائی کہ اس میں آرام کرو اور دن کو روشن بنایا (کہ اس میں کام کرو) بےشک خدا لوگوں پر فضل کرنے والا ہے۔ لیکن اکثر لوگ شکر نہیں کرتے';
$t[62] = 'یہی خدا تمہارا پروردگار ہے جو ہر چیز کا پیدا کرنے والا ہے۔ اس کے سوا کوئی معبود نہیں پھر تم کہاں بھٹک رہے ہو؟';
$t[63] = 'اسی طرح وہ لوگ بھٹک رہے تھے جو خدا کی آیتوں سے انکار کرتے تھے';
$t[64] = 'خدا ہی تو ہے جس نے زمین کو تمہارے لئے ٹھیرنے کی جگہ اور آسمان کو چھت بنایا اور تمہاری صورتیں بنائیں اور صورتیں بھی خوب بنائیں اور تمہیں پاکیزہ چیزیں کھانے کو دیں۔ یہی خدا تمہارا پروردگار ہے۔ پس خدائے پروردگار عالم بہت ہی بابرکت ہے';
$t[65] = 'وہ زندہ ہے (جسے موت نہیں) اس کے سوا کوئی عبادت کے لائق نہیں تو اس کی عبادت کو خالص کر کر اسی کو پکارو۔ ہر طرح کی تعریف خدا ہی کو (سزاوار) ہے جو تمام جہان کا پروردگار ہے';
$t[66] = '(اے محمدﷺ ان سے) کہہ دو کہ مجھے اس بات کی ممانعت کی گئی ہے کہ جن کو تم خدا کے سوا پکارتے ہو ان کی پرستش کروں (اور میں ان کی کیونکر پرستش کروں) جب کہ میرے پاس میرے پروردگار (کی طرف) سے کھلی دلیلیں آچکی ہیں اور مجھ کو یہ حکم ہوا ہے کہ پروردگار عالم ہی کا تابع فرمان ہوں';
$t[67] = 'وہی تو ہے جس نے تم کو (پہلے) مٹی سے پیدا کیا۔ ہھر نطفہ بنا کر پھر لوتھڑا بنا کر پھر تم کو نکالتا ہے (کہ تم) بچّے (ہوتے ہو) پھر تم اپنی جوانی کو پہنچتے ہو۔ پھر بوڑھے ہوجاتے ہو۔ اور کوئی تم میں سے پہلے ہی مرجاتا ہے اور تم (موت کے) وقت مقرر تک پہنچ جاتے ہو اور تاکہ تم سمجھو';
$t[68] = 'وہی تو ہے جو جلاتا ہے اور مارتا ہے۔ پھر جب وہ کوئی کام کرنا (اور کسی کو پیدا کرنا) چاہتا ہے تو اس سے کہہ دیتا ہے کہ ہوجا تو وہ جاتا ہے';
$t[69] = 'کیا تم نے ان لوگوں کو نہیں دیکھا جو خدا کی آیتوں میں جھگڑتے ہیں۔ یہ کہاں بھٹک رہے ہیں؟';
$t[70] = 'جن لوگوں نے کتاب (خدا) کو اور جو کچھ ہم نے پیغمبروں کو دے کر بھیجا اس کو جھٹلایا۔ وہ عنقریب معلوم کرلیں گے';
$t[71] = 'جب کہ ان کی گردنوں میں طوق اور زنجیریں ہوں گی (اور) گھسیٹے جائیں گے';
$t[72] = '(یعنی) کھولتے ہوئے پانی میں۔ پھر آگ میں جھونک دیئے جائیں گے';
$t[73] = 'پھر ان سے کہا جائے گا کہ وہ کہاں ہیں جن کو تم (خدا کے) شریک بناتے تھے';
$t[74] = '(یعنی غیر خدا) کہیں گے وہ تو ہم سے جاتے رہے بلکہ ہم تو پہلے کسی چیز کو پکارتے ہی نہیں تھے۔ اسی طرح خدا کافروں کو گمراہ کرتا ہے';
$t[75] = 'یہ اس کا بدلہ ہے کہ تم زمین میں حق کے بغیر (یعنی اس کے خلاف) خوش ہوا کرتے تھے اور اس کی (سزا ہے) کہ اترایا کرتے تھے';
$t[76] = '(اب) جہنم کے دروازوں میں داخل ہوجاؤ۔ ہمیشہ اسی میں رہو گے۔ متکبروں کا کیا برا ٹھکانا ہے';
$t[77] = 'تو (اے پیغمبر) صبر کرو خدا کا وعدہ سچا ہے۔ اگر ہم تم کو کچھ اس میں سے دکھادیں جس کا ہم تم سے وعدہ کرتے ہیں۔ (یعنی کافروں پر عذاب نازل کریں) یا تمہاری مدت حیات پوری کردیں تو ان کو ہماری ہی طرف لوٹ کر آنا ہے';
$t[78] = 'اور ہم نے تم سے پہلے (بہت سے) پیغمبر بھیجے۔ ان میں کچھ تو ایسے ہیں جن کے حالات تم سے بیان کر دیئے ہیں اور کچھ ایسے ہیں جن کے حالات بیان نہیں کئے۔ اور کسی پیغمبر کا مقدور نہ تھا کہ خدا کے حکم کے سوا کوئی نشانی لائے۔ پھر جب خدا کا حکم آپہنچا تو انصاف کے ساتھ فیصلہ کردیا گیا اور اہل باطل نقصان میں پڑ گئے';
$t[79] = 'خدا ہی تو ہے جس نے تمہارے لئے چارپائے بنائے تاکہ ان میں سے بعض پر سوار ہو اور بعض کو تم کھاتے ہو';
$t[80] = 'اور تمہارے لئے ان میں (اور بھی) فائدے ہیں اور اس لئے بھی کہ (کہیں جانے کی) تمہارے دلوں میں جو حاجت ہو ان پر (چڑھ کر وہاں) پہنچ جاؤ۔ اور ان پر اور کشتیوں پر تم سوار ہوتے ہو';
$t[81] = 'اور وہ تمہیں اپنی نشانیاں دکھاتا ہے تو تم خدا کی کن کن نشانیوں کو نہ مانو گے';
$t[82] = 'کیا ان لوگوں نے زمین میں سیر نہیں کی تاکہ دیکھتے جو لوگ ان سے پہلے تھے ان کا انجام کیسا ہوا۔ (حالانکہ) وہ ان سے کہیں زیادہ طاقتور اور زمین میں نشانات (بنانے) کے اعتبار سے بہت بڑھ کر تھے۔ تو جو کچھ وہ کرتے تھے وہ ان کے کچھ کام نہ آیا';
$t[83] = 'اور جب ان کے پیغمبر ان کے پاس کھلی نشانیاں لے کر آئے تو جو علم (اپنے خیال میں) ان کے پاس تھا اس پر اترانے لگے اور جس چیز سے تمسخر کیا کرتے تھے اس نے ان کو آ گھیرا';
$t[84] = 'پھر جب انہوں نے ہمارا عذاب دیکھ لیا تو کہنے لگے کہ ہم خدائے واحد پر ایمان لائے اور جس چیز کو اس کے ساتھ شریک بناتے تھے اس سے نامعتقد ہوئے';
$t[85] = 'لیکن جب وہ ہمارا عذاب دیکھ چکے (اس وقت) ان کے ایمان نے ان کو کچھ بھی فائدہ نہ دیا۔ (یہ) خدا کی عادت (ہے) جو اس کے بندوں (کے بارے) میں چلی آتی ہے۔ اور وہاں کافر گھاٹے میں پڑ گئے';
?>