$t[19] = '(Ey m&uuml;şriklər!) Bir deyin g&ouml;rək (sizin ibadət etdiyiniz) Lat və Uzza nəyə qadirdilər?!';
$t[20] = 'Digər &uuml;&ccedil;&uuml;nc&uuml; (b&uuml;t) olan Mənat da həm&ccedil;inin?!';
$t[21] = 'Məgər oğlanlar sizin, (bəyənmədiyiniz) qızlar Onundurmu?! (Allahındırmı)?!';
$t[22] = 'Elə isə bu ədalətsiz b&ouml;lg&uuml;d&uuml;r!';
$t[23] = 'Onlar (b&uuml;tlər) sizin atalarınızın (&ouml;zlərindən uydurub) qoyduqları adlardan başqa he&ccedil; bir şey deyildir! (Onların yalnız quru adı var, he&ccedil; bir şeyə qadir deyillər). Allah onlara (ibadət edilməsinə) dair he&ccedil; bir dəlil endirməmişdir. Onlar (m&uuml;şriklər) yalnız zənnə və nəfslərindən gələn istəklərə uyarlar. Halbuki Rəbbindən onlara haqq yolu g&ouml;stərən rəhbər (Qur&rsquo;an və Peyğəmbər) gəlmişdir.';
$t[24] = 'Yoxsa insan istədiyi hər şeyə nail ola bilər?! (Xeyr, bu m&uuml;mk&uuml;n deyildir. M&uuml;şriklərin &ccedil;ox istəməsinə baxmayaraq, b&uuml;tlər onlar &uuml;&ccedil;&uuml;n Allah dərgahında he&ccedil; bir şəfaət edə bilməzlər).';
$t[25] = 'Axirətdə, d&uuml;nya da ancaq Allahındır!';
$t[26] = 'G&ouml;ylərdə ne&ccedil;ə-ne&ccedil;ə mələklər vardır ki, onların şəfaəti he&ccedil; bir fayda verməz. Ancaq Allah &Ouml;z istədiyi və razı olduğu kimsəyə (mələyə, yaxud m&ouml;&rsquo;minə) izin verdikdən sonra (onun şəfaəti fayda verər).';
$t[27] = 'Həqiqətən, axirətə inanmayanlar mələklərə (onların Allahın qızları olduğunu iddia edərək) qadın adları verirlər.';
$t[28] = 'Onlar buna dair he&ccedil; bir şey bilmir, yalnız zənnə qapılırlar. Zənn isə əsla həqiqət ola bilməz!';
$t[29] = 'Artıq (ya Peyğəmbər!) Bizim Qur&rsquo;anımızdan &uuml;z &ccedil;evirib d&uuml;nya həyatından başqa bir şey istəməyənlərdən &uuml;z d&ouml;ndər!';
$t[30] = 'Onların bildiyi elə bu qədərdir. Ş&uuml;bhəsiz ki, sənin Rəbbin haqq yoldan azanı da, doğru yolda olanı da ən g&ouml;zəl tanıyandır!';
$t[31] = 'G&ouml;ylərdə və yerdə nə varsa, (hamısı) Allahındır. O, pislik edənlərə əməllərinin cəzasını verəcək, yaxşılıq edənləri isə ən g&ouml;zəl m&uuml;kafatla m&uuml;kafatlandıracaqdır!';
$t[32] = '(O yaxşılıq edənlər ki) ki&ccedil;ik g&uuml;nahlar (xətalar) istisna olmaqla, b&ouml;y&uuml;k g&uuml;nahlardan və rəzil işlərdən (zinadan) &ccedil;əkinərlər. Həqiqətən, sənin Rəbbin &ccedil;ox bağışlayandır. Sizi torpaqdan yaradan da, siz analarınızın bətnlərində r&uuml;şeym halında olanda da sizi ən g&ouml;zəl tanıyan Odur. &Ouml;z&uuml;n&uuml;z&uuml; təmizə &ccedil;ıxartmayın. O, Allahdan qorxub pis əməllərdən &ccedil;əkinənin kim olduğunu ən g&ouml;zəl biləndir!';
$t[33] = '(Ya Peyğəmbər!) İndi g&ouml;rd&uuml;nm&uuml; (imandan) d&ouml;nəni,';
$t[34] = 'Bir az (pul, mal) verib (qalanına) xəsislik g&ouml;stərəni?';
$t[35] = 'Məgər o, qeybi bilirmi ki, (&ouml;z dostunun əzabına y&uuml;klənə biləcəyini) g&ouml;rs&uuml;n?! (M&uuml;şriklərdən Vəlid ibn M&uuml;ğirə islama meyl g&ouml;stərmişdi. Bunu eşidən digər bir m&uuml;şrik onu babalarının dinindən &uuml;z d&ouml;ndərməkdə ittiham edərək m&ouml;hkəm danlamış və demişdi ki, əgər M&uuml;ğirə ona m&uuml;əyyən məbləğ pul, mal versə və islamı atıb &ouml;z dininə qayıtsa, o, axirətdə M&uuml;ğirənin əzabını &ouml;z &uuml;zərinə g&ouml;t&uuml;rəcəkdir. M&uuml;ğirə islamdan &uuml;z d&ouml;ndərmiş, lakin xəsislik g&ouml;stərib həmin şəxsə və&rsquo;d etdiyi məbləğin hamısını deyil, bir qismini vermişdi).';
$t[36] = 'Yoxsa ona Musanın səhifələrindən olanlar xəbər verilmədi?!';
$t[37] = 'Və (əhdə) &ccedil;ox vəfalı olan (Allahın qarşısındakı borcunu layiqincə &ouml;dəyən) İbrahimin (s&uuml;h&uuml;f&uuml;ndəkilər bildirilmədi?!)';
$t[38] = '(T&ouml;vratda və İbrahimin səhifələrində deyilənlər bunlardır:) &ldquo;He&ccedil; bir g&uuml;nahkar başqasının g&uuml;nahına y&uuml;klənməz!';
$t[39] = 'İnsana ancaq &ouml;z zəhməti (sə&rsquo;yi, &ccedil;alışması, əməli) qalar!';
$t[40] = 'Ş&uuml;bhəsiz ki, (qiyamət g&uuml;n&uuml;) onun zəhməti (sə&rsquo;yi, əməli) g&ouml;r&uuml;nəcəkdir!';
$t[41] = 'Sonra da ona (zəhmətinin, əməlinin) tam əvəzi veriləcəkdir!&rdquo;';
$t[42] = 'Həqiqətən, axır d&ouml;n&uuml;ş sənin Rəbbinədir!';
