$t[99] = 'I Mi ćemo tada učiniti da se jedni od njih kao talasi sudaraju s drugima. I puhnuće se u rog, pa ćemo ih sve sakupiti,';
$t[100] = 'i toga dana ćemo nevjernicima Džehennem jasno pokazati,';
$t[101] = 'onima čije su oči bile koprenom zastrte, da o dokazima Mojim razmisle, onima koji nisu htjeli ni&scaron;ta čuti.';
$t[102] = 'Zar nevjernici misle da pored Mene mogu za bogove uzimati robove Moje? Mi smo, doista, za prebivali&scaron;te nevjernicima pripremili Džehennem.';
$t[103] = 'Reci: &quot;Hoćete li da vam kažemo čija djela neće nikako priznata biti,';
$t[104] = 'čiji će trud u životu na ovome svijetu uzaludan biti, a koji će misliti da je dobro ono &scaron;to rade?';
$t[105] = 'To su oni koji u dokaze Gospodara svoga ne budu vjerovali i koji budu poricali da će pred Njega izići; zbog toga će trud njihov uzaludan biti i na Sudnjem danu im nikakva značaja nećemo dati.';
$t[106] = 'Njima će kazna Džehennem biti, zato &scaron;to su nevjernici bili i &scaron;to su se dokazima Mojim i poslanicima Mojim rugali.&quot;';
$t[107] = 'Onima koji budu vjerovali i dobra djela činili &ndash; džennetske ba&scaron;če će prebivali&scaron;te biti,';
$t[108] = 'vječno će u njima boraviti i neće poželjeti da ih nečim drugim zamijene.';
$t[109] = 'Reci: &quot;Kad bi more bilo mastilo da se ispi&scaron;u riječi Gospodara moga, more bi presahlo, ali ne i riječi Gospodara moga, pa i kad bismo se pomogli jo&scaron; jednim sličnim.&quot;';
$t[110] = 'Reci: &quot;Ja sam čovjek kao i vi, meni se objavljuje da je va&scaron; Bog &ndash; jedan Bog. Ko žudi da od Gospodara svoga bude lijepo primljen, neka čini dobra djela i neka, klanjajući se Gospodaru svome, Njemu nikoga ne pridružuje!&quot;';
