$t[77] = 'a pomohli jsme mu proti lidu, jenž prohla&scaron;oval znamen&iacute; Na&scaron;e za vylhan&aacute;. A byl to lid &scaron;patn&yacute; a utopili jsme je v&scaron;echny.';
$t[78] = 'A zmiň se o Davidovi a &Scaron;alomounovi; hle, oni vynesli rozsudek o poli, jež po&scaron;kodilo st&aacute;do ovc&iacute; lid&iacute; jedněch, a byli jsme svědky rozsudku jejich.';
$t[79] = 'A vnukli jsme &Scaron;alomounovi pochopen&iacute; věci t&eacute; a oběma jsme darovali osv&iacute;cen&iacute; a věděn&iacute;. A donutili jsme hory a pt&aacute;ky, aby spolu s Davidem Na&scaron;i pěli chv&aacute;lu. A tak jsme učinili.';
$t[80] = 'A naučili jsme jej vyr&aacute;bět brněn&iacute; pro v&aacute;s, abyste se jimi chr&aacute;nili před &uacute;točnost&iacute; vz&aacute;jemnou - budete za to vděčni?';
$t[81] = 'A &Scaron;alomounovi jsme podrobili v&iacute;tr, jenž jako vichřice na rozkaz jeho duje k zemi, již jsme požehnali. A o v&scaron;ech těch věcech My dobře jsme věděli.';
$t[82] = 'A tak&eacute; někter&eacute; satany jsme mu podrobili, aby se do moře pot&aacute;pěli či pr&aacute;ce jin&eacute; proň vykon&aacute;vali, a My jsme je při tom střežili.';
$t[83] = 'A zmiň se o J&oacute;bovi, jenž volal k P&aacute;nu sv&eacute;mu: &quot;Postihlo mne ne&scaron;těst&iacute;, v&scaron;ak tys nejslitovněj&scaron;&iacute; ze slitovn&iacute;ků!&quot;';
$t[84] = 'A vysly&scaron;eli jsme jej a odvr&aacute;tili od něho ne&scaron;těst&iacute;, jež nesl, a vr&aacute;tili jsme mu jeho rodinu a přidali je&scaron;tě jednou tolik s nimi jako milosrdenstv&iacute; sv&eacute; a jako připomenut&iacute; těm, kdož N&aacute;s uct&iacute;vaj&iacute;.';
$t[85] = 'A zmiň se o Ismaelovi, Idr&iacute;sovi a Dhu l-Kiflovi - a ti v&scaron;ichni byli trpěliv&iacute;,';
$t[86] = 'a uvedli jsme je do milosrdenstv&iacute; Sv&eacute;ho a jsou mezi bez&uacute;honn&yacute;mi.';
$t[87] = 'A zmiň se o Muži s rybou; když ode&scaron;el rozzloben a domn&iacute;val se, že proti němu nic nezmůžeme. A volal n&aacute;s v temnot&aacute;ch ř&iacute;kaje: &quot;Nen&iacute; božstva kromě Tebe, sl&aacute;va budiž Tobě! Věru jsem patřil k nespravedliv&yacute;m!&quot;';
$t[88] = 'I vysly&scaron;eli jsme jej a zachr&aacute;nili jsme jej z &uacute;trap jeho. A takto My poskytujeme z&aacute;chranu věř&iacute;c&iacute;m.';
$t[89] = 'A zmiň se o Zachari&aacute;&scaron;ovi, když volal k P&aacute;nu sv&eacute;mu: &quot;Pane můj, neponech&aacute;vej mne samotn&eacute;ho, Ty, jenž z dědiců jsi nejlep&scaron;&iacute;!&quot;';
$t[90] = 'Vysly&scaron;eli jsme jej a darovali jsme mu Jana a učinili jsme manželku jeho plodnou. A v&scaron;ichni ti se předstihovali v dobročinn&eacute;m kon&aacute;n&iacute; a modlili se k N&aacute;m z touhy a &uacute;cty a byli vůči N&aacute;m pokorn&iacute;.';
$t[91] = 'A zmiň se o t&eacute;, jež panenstv&iacute; sv&eacute; střežila. A vdechli jsme do n&iacute; č&aacute;st ducha Sv&eacute;ho a učinili jsme z n&iacute; i ze syna jej&iacute;ho pro ve&scaron;ker&eacute; lidstvo znamen&iacute;.';
$t[92] = 'Hle, tato va&scaron;e obec je obc&iacute; jedinou a J&aacute; P&aacute;nem jsem va&scaron;&iacute;m, uct&iacute;vejte Mne tedy!';
$t[93] = 'Av&scaron;ak oni se ve věci sv&eacute; mezi sebou rozpoltili, v&scaron;ak přesto se v&scaron;ichni k N&aacute;m navr&aacute;t&iacute;.';
$t[94] = 'Kdokoliv dobr&eacute; skutky kon&aacute; a je při tom věř&iacute;c&iacute;, tomu &uacute;sil&iacute; jeho věru nebude upřeno a zajist&eacute; mu je k dobru přip&iacute;&scaron;eme.';
$t[95] = 'A je zak&aacute;z&aacute;no městu každ&eacute;mu, jež jsme zahubili, aby se obyvatel&eacute; jeho v ně navr&aacute;tili,';
$t[96] = 'pokud nebudou vypu&scaron;těni Gog a Magog; a tehdy se lid&eacute; z každ&eacute;ho hrobu vynoř&iacute;!';
$t[97] = 'Pravdiv&yacute; slib se přibližuje, a hle, strnul&eacute; jsou zraky těch, kdož neuvěřili. &quot;Běda n&aacute;m, že lhostejn&iacute; jsme byli k němu, ba naopak, byli jsme nespravedliv&iacute;.&quot;';
