<?php
$t = array();
$t[1] = 'پاک و منزّه است خدایی که بنده&zwnj;اش را در یک شب، از مسجد الحرام به مسجد الاقصی -که گرداگردش را پربرکت ساخته&zwnj;ایم- برد، تا برخی از آیات خود را به او نشان دهیم؛ چرا که او شنوا و بیناست.';
$t[2] = 'ما به موسی کتاب آسمانی دادیم؛ و آن را وسیله هدایت بنی اسرائیل ساختیم؛ (و گفتیم:) غیر ما را تکیه&zwnj;گاه خود قرار ندهید!';
$t[3] = 'ای فرزندان کسانی که با نوح (بر کشتی) سوار کردیم! او بنده شکرگزاری بود. (شما هم مانند او باشید، تا نجات یابید!)';
$t[4] = 'ما به بنی اسرائیل در کتاب (تورات) اعلام کردیم که دوبار در زمین فساد خواهید کرد، و برتری&zwnj;جویی بزرگی خواهید نمود.';
$t[5] = 'هنگامی که نخستین وعده فرا رسد، گروهی از بندگان پیکارجوی خود را بر ضدّ شما میانگیزیم (تا شما را سخت در هم کوبند؛ حتی برای به دست آوردن مجرمان)، خانه&zwnj;ها را جستجو می&zwnj;کنند؛ و این وعده&zwnj;ای است قطعی!';
$t[6] = 'سپس شما را بر آنها چیره می&zwnj;کنیم؛ و شما را به وسیله داراییها و فرزندانی کمک خواهیم کرد؛ و نفرات شما را بیشتر (از دشمن) قرارمی&zwnj;دهیم.';
$t[7] = 'اگر نیکی کنید، به خودتان نیکی می&zwnj;کنید؛ و اگر بدی کنید باز هم به خود می&zwnj;کنید. و هنگامی که وعده دوم فرا رسد، (آنچنان دشمن بر شما سخت خواهد گرفت که) آثار غم و اندوه در صورتهایتان ظاهر می&zwnj;شود؛ و داخل مسجد (الاقصی) می&zwnj;شوند همان گونه که بار اول وارد شدند؛ و آنچه را زیر سلطه خود می&zwnj;گیرند، در هم می&zwnj;کوبند.';
$t[8] = 'امید است پروردگارتان به شما رحم کند! هرگاه برگردید، ما هم بازمی&zwnj;گردیم؛ و جهنّم را برای کافران، زندان سختی قرار دادیم.';
$t[9] = 'این قرآن، به راهی که استوارترین راه&zwnj;هاست، هدایت می&zwnj;کند؛ و به مؤمنانی که اعمال صالح انجام می&zwnj;دهند، بشارت می&zwnj;دهد که برای آنها پاداش بزرگی است.';
$t[10] = 'و اینکه آنها که به قیامت ایمان نمی&zwnj;آورند، عذاب دردناکی برای آنان آماده ساخته&zwnj;ایم.';
$t[11] = 'انسان (بر اثر شتابزدگی)، بدیها را طلب می&zwnj;کند آن گونه که نیکیها را می&zwnj;طلبد؛ و انسان، همیشه عجول بوده است!';
$t[12] = 'ما شب و روز را دو نشانه توحید و عظمت خود قرار دادیم؛ سپس نشانه شب را محو کرده، و نشانه روز را روشنی&zwnj;بخش ساختیم تا (در پرتو آن،) فضل پروردگارتان را بطلبید (و به تلاش زندگی برخیزید)، و عدد سالها و حساب را بدانید؛ و هر چیزی را بطور مشخّص و آشکار، بیان کردیم.';
$t[13] = 'و هر انسانی، اعمالش را بر گردنش آویخته&zwnj;ایم؛ و روز قیامت، کتابی برای او بیرون می&zwnj;آوریم که آن را در برابر خود، گشوده می&zwnj;بیند! (این همان نامه اعمال اوست!)';
$t[14] = '(و به او می&zwnj;گوییم:) کتابت را بخوان، کافی است که امروز، خود حسابگر خویش باشی!';
$t[15] = 'هر کس هدایت شود، برای خود هدایت یافته؛ و آن کس که گمراه گردد، به زیان خود گمراه شده است؛ و هیچ کس بار گناه دیگری را به دوش نمی&zwnj;کشد؛ و ما هرگز (قومی را) مجازات نخواهیم کرد، مگر آنکه پیامبری مبعوث کرده باشیم (تا وظایفشان را بیان کند.)';
$t[16] = 'و هنگامی که بخواهیم شهر و دیاری را هلاک کنیم، نخست اوامر خود را برای &laquo;مترفین&raquo; (و ثروتمندان مست شهوت) آنجا، بیان می&zwnj;داریم، سپس هنگامی که به مخالفت برخاستند و استحقاق مجازات یافتند، آنها را به شدّت درهم می&zwnj;کوبیم.';
$t[17] = 'چه بسیار مردمی که در قرون بعد از نوح، زندگی می&zwnj;کردند؛ (و طبق همین سنت،) آنها را هلاک کردیم! و کافی است که پروردگارت از گناهان بندگانش آگاه، و نسبت به آن بیناست.';
$t[18] = 'آن کس که (تنها) زندگی زودگذر (دنیا) را می&zwnj;طلبد، آن مقدار از آن را که بخواهیم -و به هر کس اراده کنیم- می&zwnj;دهیم؛ سپس دوزخ را برای او قرار خواهیم داد، که در آتش سوزانش می&zwnj;سوزد در حالی که نکوهیده و رانده (درگاه خدا) است.';
$t[19] = 'و آن کس که سرای آخرت را بطلبد، و برای آن سعی و کوشش کند -در حالی که ایمان داشته باشد- سعی و تلاش او، (از سوی خدا) پاداش داده خواهد شد.';
$t[20] = 'هر یک از این دو گروه را از عطای پروردگارت، بهره و کمک می&zwnj;دهیم؛ و عطای پروردگارت هرگز (از کسی) منع نشده است.';
$t[21] = 'ببین چگونه بعضی را (در دنیا بخاطر تلاششان) بر بعضی دیگر برتری بخشیده&zwnj;ایم؛ درجات آخرت و برتریهایش، از این هم بیشتر است!';
$t[22] = 'هرگز معبود دیگری را با خدا قرار مده، که نکوهیده و بی&zwnj;یار و یاور خواهی نشست!';
$t[23] = 'و پروردگارت فرمان داده: جز او را نپرستید! و به پدر و مادر نیکی کنید! هرگاه یکی از آن دو، یا هر دوی آنها، نزد تو به سن پیری رسند، کمترین اهانتی به آنها روا مدار! و بر آنها فریاد مزن! و گفتار لطیف و سنجیده و بزرگوارانه به آنها بگو!';
$t[24] = 'و بالهای تواضع خویش را از محبّت و لطف، در برابر آنان فرود آر! و بگو: &laquo;پروردگارا! همان&zwnj;گونه که آنها مرا در کوچکی تربیت کردند، مشمول رحمتشان قرار ده!&raquo;';
$t[25] = 'پروردگار شما از درون دلهایتان آگاهتر است؛ (اگر لغزشی در این زمینه داشتید) هر گاه صالح باشید (و جبران کنید) او بازگشت&zwnj;کنندگان را می&zwnj;بخشد.';
$t[26] = 'و حقّ نزدیکان را بپرداز، و (همچنین حق) مستمند و وامانده در راه را! و هرگز اسراف و تبذیر مکن،';
$t[27] = 'چرا که تبذیرکنندگان، برادران شیاطینند؛ و شیطان در برابر پروردگارش، بسیار ناسپاس بود!';
$t[28] = 'و هرگاه از آنان [= مستمندان&zwnj;] روی برتابی، و انتظار رحمت پروردگارت را داشته باشی (تا گشایشی در کارت پدید آید و به آنها کمک کنی)، با گفتار نرم و آمیخته با لطف با آنها سخن بگو!';
$t[29] = 'هرگز دستت را بر گردنت زنجیر مکن، (و ترک انفاق و بخشش منما) و بیش از حدّ (نیز) دست خود را مگشای، تا مورد سرزنش قرار گیری و از کار فرومانی!';
$t[30] = 'به یقین، پروردگارت روزی را برای هر کس بخواهد، گشاده یا تنگ می&zwnj;دارد؛ او نسبت به بندگانش، آگاه و بیناست.';
$t[31] = 'و فرزندانتان را از ترس فقر، نکشید! ما آنها و شما را روزی می&zwnj;دهیم؛ مسلماً کشتن آنها گناه بزرگی است!';
$t[32] = 'و نزدیک زنا نشوید، که کار بسیار زشت، و بد راهی است!';
$t[33] = 'و کسی را که خداوند خونش را حرام شمرده، نکشید، جز بحق! و آن کس که مظلوم کشته شده، برای ولیش سلطه (و حق قصاص) قرار دادیم؛ اما در قتل اسراف نکند، چرا که او مورد حمایت است!';
$t[34] = 'و به مال یتیم، جز به بهترین راه نزدیک نشوید، تا به سر حد بلوغ رسد! و به عهد (خود) وفا کنید، که از عهد سؤال می&zwnj;شود!';
$t[35] = 'و هنگامی که پیمانه می&zwnj;کنید، حق پیمانه را ادا نمایید، و با ترازوی درست وزن کنید! این برای شما بهتر، و عاقبتش نیکوتر است.';
$t[36] = 'از آنچه به آن آگاهی نداری، پیروی مکن، چرا که گوش و چشم و دل، همه مسؤولند.';
$t[37] = 'و روی زمین، با تکبر راه مرو! تو نمی&zwnj;توانی زمین را بشکافی، و طول قامتت هرگز به کوه&zwnj;ها نمی&zwnj;رسد!';
$t[38] = 'همه اینها گناهش نزد پروردگار تو ناپسند است.';
$t[39] = 'این (احکام)، از حکمتهایی است که پروردگارت به تو وحی فرستاده؛ و هرگز معبودی با خدا قرار مده، که در جهنم افکنده می&zwnj;شوی، در حالی که سرزنش شده، و رانده (درگاه خدا) خواهی بود!';
$t[40] = 'آیا پروردگارتان فرزندان پسر را مخصوص شما ساخته، و خودش دخترانی از فرشتگان برگزیده است؟! شما سخن بزرگ (و بسیار زشتی) می&zwnj;گویید!';
$t[41] = 'ما در این قرآن، انواع بیانات مؤثر را آوردیم تا متذکر شوند! ولی (گروهی از کوردلان،) جز بر نفرتشان نمی&zwnj;افزاید.';
$t[42] = 'بگو: &laquo;اگر آنچنان که آنها می&zwnj;گویند با او خدایانی بود، در این صورت، (خدایان) سعی می&zwnj;کردند راهی به سوی (خداوند) صاحب عرش پیدا کنند.&raquo;';
$t[43] = 'او پاک و برتر است از آنچه آنها می&zwnj;گویند، بسیار برتر و منزه&zwnj;تر!';
$t[44] = 'آسمانهای هفتگانه و زمین و کسانی که در آنها هستند، همه تسبیح او می&zwnj;گویند؛ و هر موجودی، تسبیح و حمد او می&zwnj;گوید؛ ولی شما تسبیح آنها را نمی&zwnj;فهمید؛ او بردبار و آمرزنده است.';
$t[45] = 'و هنگامی که قرآن می&zwnj;خوانی، میان تو و آنها که به آخرت ایمان نمی&zwnj;آورند، حجاب ناپیدایی قرارمی&zwnj;دهیم؛';
$t[46] = 'و بر دلهایشان پوششهایی، تا آن را نفهمند؛ و در گوشهایشان سنگینی؛ و هنگامی که پروردگارت را در قرآن به یگانگی یاد می&zwnj;کنی، آنها پشت می&zwnj;کنند و از تو روی بر می&zwnj;گردانند.';
$t[47] = 'هنگامی که به سخنان تو گوش فرامی&zwnj;دهند، ما بهتر می&zwnj;دانیم برای چه گوش فرا می&zwnj;دهند؛ (و همچنین) در آن هنگام که با هم نجوا می&zwnj;کنند؛ آنگاه که ستمگران می&zwnj;گویند: &laquo;شما جز از انسانی که افسون شده، پیروی نمی&zwnj;کنید!&raquo;';
$t[48] = 'ببین چگونه برای تو مثلها زدند! در نتیجه گمراه شدند، و نمی&zwnj;توانند راه حق را پیدا کنند.';
$t[49] = 'و گفتند: &laquo;آیا هنگامی که ما، استخوانهای پوسیده و پراکنده&zwnj;ای شدیم، دگر بار آفرینش تازه&zwnj;ای خواهیم یافت؟!&raquo;';
$t[50] = 'بگو: &laquo;شما سنگ باشید یا آهن،';
$t[51] = 'یا هر مخلوقی که در نظر شما، از آن هم سخت&zwnj;تر است (و از حیات و زندگی دورتر می&zwnj;باشد، باز خدا قادر است شما را به زندگی مجدد بازگرداند). آنها بزودی می گویند: &laquo;چه کسی ما را بازمی&zwnj;گرداند؟!&raquo; بگو: &laquo;همان کسی که نخستین بار شما را آفرید.&raquo; آنان سر خود را (از روی تعجب و انکار،) به سوی تو خم می&zwnj;کنند و می&zwnj;گویند: &laquo;در چه زمانی خواهد بود؟!&raquo; بگو: &laquo;شاید نزدیک باشد!';
$t[52] = 'همان روز که شما را (از قبرهایتان) فرامی&zwnj;خواند؛ شما هم اجابت می&zwnj;کنید در حالی که حمد او را می&zwnj;گویید؛ می&zwnj;پندارید تنها مدت کوتاهی (در جهان برزخ) درنگ کرده&zwnj;اید!&raquo;';
$t[53] = 'به بندگانم بگو: &laquo;سخنی بگویند که بهترین باشد! چرا که (شیطان بوسیله سخنان ناموزون)، میان آنها فتنه و فساد می&zwnj;کند؛ همیشه شیطان دشمن آشکاری برای انسان بوده است!';
$t[54] = 'پروردگار شما، از (نیات و اعمال) شما آگاهتر است؛ اگر بخواهد (و شایسته بداند)، شما را مشمول رحمت خود می&zwnj;سازد؛ و اگر بخواهد، مجازات می&zwnj;کند؛ و ما تو را بعنوان مأمور بر آنان نفرستاده&zwnj;ایم (که آنان را مجبور به ایمان کنی!)';
$t[55] = 'پروردگار تو، از حال همه کسانی که در آسمانها و زمین هستند، آگاهتر است؛ و (اگر تو را بر دیگران برتری دادیم، بخاطر شایستگی توست،) ما بعضی از پیامبران را بر بعضی دیگر برتری دادیم؛ و به داوود، زبور بخشیدیم.';
$t[56] = 'بگو: &laquo;کسانی را که غیر از خدا (معبود خود) می&zwnj;پندارید، بخوانید! آنها نه می&zwnj;توانند مشکلی را از شما برطرف سازند، و نه تغییری در آن ایجاد کنند.&raquo;';
$t[57] = 'کسانی را که آنان می&zwnj;خوانند، خودشان وسیله&zwnj;ای (برای تقرب) به پروردگارشان می&zwnj;جویند، وسیله&zwnj;ای هر چه نزدیکتر؛ و به رحمت او امیدوارند؛ و از عذاب او می&zwnj;ترسند؛ چرا که عذاب پروردگارت، همواره در خور پرهیز و وحشت است!';
$t[58] = 'هیچ شهر و آبادی نیست مگر اینکه آن را پیش از روز قیامت هلاک می&zwnj;کنیم؛ یا (اگر گناهکارند،) به عذاب شدیدی گرفتارشان خواهیم ساخت؛ این، در کتاب الهی [= لوح محفوظ] ثبت است.';
$t[59] = 'هیچ چیز مانع ما نبود که این معجزات (درخواستی بهانه&zwnj;جویان) را بفرستیم جز اینکه پیشینیان (که همین درخواستها را داشتند، و با ایشان هماهنگ بودند)، آن را تکذیب کردند؛ (از جمله،) ما به (قوم) ثمود، ناقه دادیم؛ (معجزه&zwnj;ای) که روشنگر بود؛ اما بر آن ستم کردند (و ناقه را کشتند). ما معجزات را فقط برای بیم دادن (و اتمام حجت) می&zwnj;فرستیم.';
$t[60] = '(به یاد آور) زمانی را که به تو گفتیم: &laquo;پروردگارت احاطه کامل به مردم دارد؛ (و از وضعشان کاملاً آگاه است.) و ما آن رؤیایی را که به تو نشان دادیم، فقط برای آزمایش مردم بود؛ همچنین شجره ملعونه [= درخت نفرین شده&zwnj;] را که در قرآن ذکر کرده&zwnj;ایم. ما آنها را بیم داده (و انذار) می&zwnj;کنیم؛ اما جز طغیان عظیم، چیزی بر آنها نمی&zwnj;افزاید!&raquo;';
$t[61] = '(به یاد آورید) زمانی را که به فرشتگان گفتیم: &laquo;برای آدم سجده کنید!&raquo; آنها همگی سجده کردند، جز ابلیس که گفت: &laquo;آیا برای کسی سجده کنم که او را از خاک آفریده&zwnj;ای؟!&raquo;';
$t[62] = '(سپس) گفت: &laquo;به من بگو، این کسی را که بر من برتری داده&zwnj;ای (به چه دلیل بوده است؟) اگر مرا تا روز قیامت زنده بگذاری، همه فرزندانش را، جز عده کمی، گمراه و ریشه&zwnj;کن خواهم ساخت!&raquo;';
$t[63] = 'فرمود: &laquo;برو! هر کس از آنان از تو تبعیت کند، جهنم کیفر شماست، کیفری است فراوان!';
$t[64] = 'هر کدام از آنها را می&zwnj;توانی با صدایت تحریک کن! و لشکر سواره و پیاده&zwnj;ات را بر آنها گسیل دار! و در ثروت و فرزندانشان شرکت جوی! و آنان را با وعده&zwnj;ها سرگرم کن! -ولی شیطان، جز فریب و دروغ، وعده&zwnj;ای به آنها نمی&zwnj;دهد-';
$t[65] = '(اما بدان) تو هرگز سلطه&zwnj;ای بر بندگان من، نخواهی یافت (و آنها هیچ&zwnj;گاه به دام تو گرفتار نمی&zwnj;شوند)! همین قدر کافی است که پروردگارت حافظ آنها باشد.&raquo;';
$t[66] = 'پروردگارتان کسی است که کشتی را در دریا برای شما به حرکت درمی&zwnj;آورد، تا از نعمت او بهره&zwnj;مند شوید؛ او نسبت به شما مهربان است.';
$t[67] = 'و هنگامی که در دریا ناراحتی به شما برسد، جز او، تمام کسانی را که (برای حل مشکلات خود) می&zwnj;خوانید، فراموش می&zwnj;کنید؛ اما هنگامی که شما را به خشکی نجات دهد، روی می&zwnj;گردانید؛ و انسان، بسیار ناسپاس است!';
$t[68] = 'آیا از این ایمن هستید که در خشکی (با یک زلزله شدید) شما را در زمین فرو ببرد، یا طوفانی از سنگریزه بر شما بفرستد (و در آن مدفونتان کند)، سپس حافظ (و یاوری) برای خود نیابید؟!';
$t[69] = 'یا اینکه ایمن هستید که بار دیگر شما را به دریا بازگرداند، و تندباد کوبنده&zwnj;ای بر شما بفرستد، و شما را بخاطر کفرتان غرق کند، سپس دادخواه و خونخواهی در برابر ما پیدا نکنید؟!';
$t[70] = 'ما آدمیزادگان را گرامی داشتیم؛ و آنها را در خشکی و دریا، (بر مرکبهای راهوار) حمل کردیم؛ و از انواع روزیهای پاکیزه به آنان روزی دادیم؛ و آنها را بر بسیاری از موجوداتی که خلق کرده&zwnj;ایم، برتری بخشیدیم.';
$t[71] = '(به یاد آورید) روزی را که هر گروهی را با پیشوایشان می&zwnj;خوانیم! کسانی که نامه عملشان به دست راستشان داده شود، آن را (با شادی و سرور) می&zwnj;خوانند؛ و بقدر رشته شکاف هسته خرمایی به آنان ستم نمی&zwnj;شود!';
$t[72] = 'اما کسی که در این جهان (از دیدن چهره حق) نابینا بوده است، در آخرت نیز نابینا و گمراهتر است!';
$t[73] = 'نزدیک بود آنها تو را (با وسوسه&zwnj;های خود) از آنچه بر تو وحی کرده&zwnj;ایم بفریبند، تا غیر آن را به ما نسبت دهی؛ و در آن صورت، تو را به دوستی خود برمیگزینند!';
$t[74] = 'و اگر ما تو را ثابت قدم نمی&zwnj;ساختیم (و در پرتو مقام عصمت، مصون از انحراف نبودی)، نزدیک بود به آنان تمایل کنی.';
$t[75] = 'اگر چنین می&zwnj;کردی، ما دو برابر مجازات (مشرکان) در زندگی دنیا، و دو برابر (مجازات) آنها را بعد از مرگ، به تو می&zwnj;چشاندیم؛ سپس در برابر ما، یاوری برای خود نمی&zwnj;یافتی!';
$t[76] = 'و نزدیک بود (با نیرنگ و توطئه) تو را از این سرزمین بلغزانند، تا از آن بیرونت کنند! و هرگاه چنین می&zwnj;کردند، (گرفتار مجازات سخت الهی شده،) و پس از تو، جز مدت کمی باقی نمی&zwnj;ماندند!';
$t[77] = 'این سنت (ما در مورد) پیامبرانی است که پیش از تو فرستادیم؛ و هرگز برای سنت ما تغییر و دگرگونی نخواهی یافت!';
$t[78] = 'نماز را از زوال خورشید (هنگام ظهر) تا نهایت تاریکی شب [= نیمه شب&zwnj;] برپا دار؛ و همچنین قرآن فجر [= نماز صبح&zwnj;] را؛ چرا که قرآن فجر، مشهود (فرشتگان شب و روز) است!';
$t[79] = 'و پاسی از شب را (از خواب برخیز، و) قرآن (و نماز) بخوان! این یک وظیفه اضافی برای توست؛ امید است پروردگارت تو را به مقامی در خور ستایش برانگیزد!';
$t[80] = 'و بگو: &laquo;پروردگارا! مرا (در هر کار،) با صداقت وارد کن، و با صداقت خارج ساز! و از سوی خود، حجتی یاری کننده برایم قرار ده!&raquo;';
$t[81] = 'و بگو: &laquo;حق آمد، و باطل نابود شد؛ یقیناً باطل نابود شدنی است!&raquo;';
$t[82] = 'و از قرآن، آنچه شفا و رحمت است برای مؤمنان، نازل می&zwnj;کنیم؛ و ستمگران را جز خسران (و زیان) نمی&zwnj;افزاید.';
$t[83] = 'هنگامی که به انسان نعمت می&zwnj;بخشیم، (از حق) روی می&zwnj;گرداند و متکبرانه دور می&zwnj;شود؛ و هنگامی که (کمترین) بدی به او می&zwnj;رسد، (از همه چیز) مایوس می&zwnj;گردد!';
$t[84] = 'بگو: &laquo;هر کس طبق روش (و خلق و خوی) خود عمل می&zwnj;کند؛ و پروردگارتان کسانی را که راهشان نیکوتر است، بهتر می&zwnj;شناسد.&raquo;';
$t[85] = 'و از تو درباره &laquo;روح&raquo; سؤال می&zwnj;کنند، بگو: &laquo;روح از فرمان پروردگار من است؛ و جز اندکی از دانش، به شما داده نشده است!&raquo;';
$t[86] = 'و اگر بخواهیم، آنچه را بر تو وحی فرستاده&zwnj;ایم، از تو می&zwnj;گیریم؛ سپس کسی را نمی&zwnj;یابی که در برابر ما، از تو دفاع کند...';
$t[87] = 'مگر رحمت پروردگارت (شامل حالت گردد،) که فضل پروردگارت بر تو بزرگ بوده است!';
$t[88] = 'بگو: &laquo;اگر انسانها و پریان (جن و انس) اتفاق کنند که همانند این قرآن را بیاورند، همانند آن را نخواهند آورد؛ هر چند یکدیگر را (در این کار) کمک کنند.';
$t[89] = 'ما در این قرآن، برای مردم از هر چیز نمونه&zwnj;ای آوردیم (و همه معارف در آن جمع است)؛ اما بیشتر مردم (در برابر آن، از هر کاری) جز انکار، ابا داشتند!';
$t[90] = 'و گفتند: &laquo;ما هرگز به تو ایمان نمی&zwnj;آوریم تا اینکه چشمه&zwnj;جوشانی از این سرزمین (خشک و سوزان) برای ما خارج سازی...';
$t[91] = 'یا باغی از نخل و انگور از آن تو باشد؛ و نهرها در لابه&zwnj;لای آن جاری کنی...';
$t[92] = 'یا قطعات (سنگهای) آسمان را -آنچنان که می&zwnj;پنداری- بر سر ما فرود آری؛ یا خداوند و فرشتگان را در برابر ما بیاوری...';
$t[93] = 'یا برای تو خانه&zwnj;ای پر نقش و نگار از طلا باشد؛ یا به آسمان بالا روی؛ حتی اگر به آسمان روی، ایمان نمی&zwnj;آوریم مگر آنکه نامه&zwnj;ای بر ما فرود آوری که آن را بخوانیم! &laquo;بگو:&raquo; منزه است پروردگارم (از این سخنان بی&zwnj;معنی)! مگر من جز انسانی فرستاده خدا هستم؟!';
$t[94] = 'تنها چیزی که بعد از آمدن هدایت مانع شد مردم ایمان بیاورند، این بود (که از روی نادانی و بی&zwnj;خبری) گفتند: &laquo;آیا خداوند بشری را بعنوان رسول فرستاده است؟!&raquo;';
$t[95] = 'بگو: &laquo;(حتی) اگر در روی زمین فرشتگانی (زندگی می&zwnj;کردند، و) با آرامش گام برمی&zwnj;داشتند، ما فرشته&zwnj;ای را به عنوان رسول، بر آنها می&zwnj;فرستادیم!&raquo; (چرا که رهنمای هر گروهی باید از جنس خودشان باشد).';
$t[96] = 'بگو: &laquo;همین کافی است که خداوند، میان من و شما گواه باشد؛ چرا که او نسبت به بندگانش آگاه و بیناست!';
$t[97] = 'هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را (بخاطر اعمالش) گمراه سازد، هادیان و سرپرستانی غیر خدا برای او نخواهی یافت؛ و روز قیامت، آنها را بر صورتهایشان محشور می&zwnj;کنیم، در حالی که نابینا و گنگ و کرند؛ جایگاهشان دوزخ است؛ هر زمان آتش آن فرونشیند، شعله تازه&zwnj;ای بر آنان می&zwnj;افزاییم!';
$t[98] = 'این کیفر آنهاست، بخاطر اینکه نسبت به آیات ما کافر شدند و گفتند: &laquo;آیا هنگامی که ما استخوانهای پوسیده و خاکهای پراکنده&zwnj;ای شدیم، بار دیگر آفرینش تازه&zwnj;ای خواهیم یافت؟!&raquo;';
$t[99] = 'آیا نمی&zwnj;دانند خدایی که آسمانها و زمین را آفریده، قادر است مثل آنان را بیافریند (و به زندگی جدید بازشان گرداند)؟! و برای آنان سرآمدی قطعی -که شکّی در آن نیست- قرار داده؛ امّا ظالمان، جز کفر و انکار را پذیرا نیستند!';
$t[100] = 'بگو: &laquo;اگر شما مالک خزائن رحمت پروردگار من بودید. در آن صورت، (بخاطر تنگ نظری) امساک می&zwnj;کردید، مبادا انفاق، مایه تنگدستی شما شود&raquo; و انسان تنگ نظر است!';
$t[101] = 'ما به موسی نه معجزه روشن دادیم؛ پس از بنی اسرائیل سؤال کن آن زمان که این (معجزات نه گانه) به سراغ آنها آمد (چگونه بودند)؟! فرعون به او گفت: &laquo;ای موسی! گمان می&zwnj;کنم تو دیوانه (یا ساحری)!&raquo;';
$t[102] = '(موسی) گفت: &laquo;تو می&zwnj;دانی این آیات را جز پروردگار آسمانها و زمین -برای روشنی دلها- نفرستاده؛ و من گمان می&zwnj;کنم ای فرعون، تو (بزودی) هلاک خواهی شد!&raquo;';
$t[103] = 'پس (فرعون) تصمیم گرفت آنان را از آن سرزمین ریشه کن سازد؛ ولی ما، او و تمام کسانی را که با او بودند، غرق کردیم.';
$t[104] = 'و بعد از آن به بنی اسرائیل گفتیم: &laquo;در این سرزمین [= مصر و شام&zwnj;] ساکن شوید! امّا هنگامی که وعده آخرت فرا رسد، همه شما را دسته جمعی (به آن دادگاه عدل) می&zwnj;آوریم&raquo;';
$t[105] = 'و ما قرآن را بحق نازل کردیم؛ و بحق نازل شد؛ و تو را، جز بعنوان بشارت&zwnj;دهنده و بیم&zwnj;دهنده، نفرستادیم!';
$t[106] = 'و قرآنی که آیاتش را از هم جدا کردیم، تا آن را با درنگ بر مردم بخوانی؛ و آن را بتدریج نازل کردیم.';
$t[107] = 'بگو: &laquo;خواه به آن ایمان بیاورید، و خواه ایمان نیاورید، کسانی که پیش از آن به آنها دانش داده شده، هنگامی که (این آیات) بر آنان خوانده می&zwnj;شود، سجده&zwnj;کنان به خاک می&zwnj;افتند...';
$t[108] = 'و می&zwnj;گویند: &laquo;منزّه است پروردگار ما، که وعده&zwnj;هایش به یقین انجام&zwnj;شدنی است!&raquo;';
$t[109] = 'آنها (بی&zwnj;اختیار) به زمین می&zwnj;افتند و گریه می&zwnj;کنند؛ و (تلاوت این آیات، همواره) بر خشوعشان می&zwnj;افزاید.';
$t[110] = 'بگو: &laquo;&laquo;اللّه&raquo; را بخوانید یا &laquo;رحمان&raquo; را، هر کدام را بخوانید، (ذات پاکش یکی است؛ و) برای او بهترین نامهاست!&raquo; و نمازت را زیاد بلند، یا خیلی آهسته نخوان؛ و در میان آن دو، راهی (معتدل) انتخاب کن!';
$t[111] = 'و بگو: &laquo;ستایش برای خداوندی است که نه فرزندی برای خود انتخاب کرده، و نه شریکی در حکومت دارد، و نه بخاطر ضعف و ذلّت، (حامی و) سرپرستی برای اوست!&raquo; و او را بسیار بزرگ بشمار!';
?>