$t[5] = 'نه آنها (هرگز) به این سخن یقین دارند، و نه پدرانشان! سخن بزرگی از دهانشان خارج می&zwnj;شود! آنها فقط دروغ می&zwnj;گویند!';
$t[6] = '] گویی می&zwnj;خواهی بخاطر اعمال آنان، خود را از غم و اندوه هلاک کنی اگر به این گفتار ایمان نیاورند!';
$t[7] = 'ما آنچه را روی زمین است زینت آن قرار دادیم، تا آنها را بیازماییم که کدامینشان بهتر عمل می&zwnj;کنند!';
$t[8] = '(ولی) این زرق و برقها پایدار نیست، و ما (سرانجام) قشر روی زمین را خاک بی گیاهی قرار می&zwnj;دهیم!';
$t[9] = 'آیا گمان کردی اصحاب کهف و رقیم از آیات عجیب ما بودند؟!';
$t[10] = 'زمانی را به خاطر بیاور که آن جوانان به غار پناه بردند، و گفتند: &laquo;پروردگارا! ما را از سوی خودت رحمتی عطا کن، و راه نجاتی برای ما فراهم ساز!&raquo;';
$t[11] = 'ما (پرده خواب را) در غار بر گوششان زدیم، و سالها در خواب فرو رفتند.';
$t[12] = 'سپس آنان را برانگیختیم تا بدانیم (و این امر آشکار گردد که) کدام یک از آن دو گروه، مدّت خواب خود را بهتر حساب کرده&zwnj;اند.';
$t[13] = 'ما داستان آنان را بحق برای تو بازگو می&zwnj;کنیم؛ آنها جوانانی بودند که به پروردگارشان ایمان آوردند، و ما بر هدایتشان افزودیم.';
$t[14] = 'و دلهایشان را محکم ساختیم در آن موقع که قیام کردند و گفتند: &laquo;پروردگار ما، پروردگار آسمانها و زمین است؛ هرگز غیر او معبودی را نمی&zwnj;خوانیم؛ که اگر چنین کنیم، سخنی بگزاف گفته&zwnj;ایم.';
$t[15] = 'این قوم ما هستند که معبودهایی جز خدا انتخاب کرده&zwnj;اند؛ چرا دلیل آشکاری (بر این کار) نمی&zwnj;آورند؟! و چه کسی ظالمتر است از آن کس که بر خدا دروغ ببندد؟!&raquo;';
$t[16] = 'و (به آنها گفتیم:) هنگامی که از آنان و آنچه جز خدا می&zwnj;پرستند کناره&zwnj;گیری کردید، به غار پناه برید؛ که پروردگارتان (سایه) رحمتش را بر شما می&zwnj;گستراند؛ و در این امر، آرامشی برای شما فراهم می&zwnj;سازد!';
$t[17] = 'و (اگر در آنجا بودی) خورشید را می&zwnj;دیدی که به هنگام طلوع، به سمت راست غارشان متمایل می&zwnj;گردد؛ و به هنگام غروب، به سمت چپ؛ و آنها در محل وسیعی از آن (غار) قرار داشتند؛ این از آیات خداست! هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را گمراه نماید، هرگز ولیّ و راهنمایی برای او نخواهی یافت!';
$t[18] = 'و (اگر به آنها نگاه می&zwnj;کردی) می&zwnj;پنداشتی بیدارند؛ در حالی که در خواب فرو رفته بودند! و ما آنها را به سمت راست و چپ میگرداندیم (تا بدنشان سالم بماند). و سگ آنها دستهای خود را بر دهانه غار گشوده بود (و نگهبانی می&zwnj;کرد). اگر نگاهشان می&zwnj;کردی، از آنان می&zwnj;گریختی؛ و سر تا پای تو از ترس و وحشت پر می&zwnj;شد!';
$t[19] = 'این&zwnj;گونه آنها را (از خواب) برانگیختیم تا از یکدیگر سؤال کنند؛ یکی از آنها گفت: &laquo;چه مدّت خوابیدید؟!&raquo; گفتند: &laquo;یک روز، یا بخشی از یک روز!&raquo; (و چون نتوانستند مدّت خوابشان را دقیقاً بدانند) گفتند: &laquo;پروردگارتان از مدّت خوابتان آگاهتر است! اکنون یک نفر از خودتان را با این سکّه&zwnj;ای که دارید به شهر بفرستید، تا بنگرد کدام یک از آنها غذای پاکیزه&zwnj;تری دارند، و مقداری از آن برای روزی شما بیاورد. امّا باید دقّت کند، و هیچ کس را از وضع شما آگاه نسازد...';
$t[20] = 'چرا که اگر آنان از وضع شما آگاه شوند، سنگسارتان می&zwnj;کنند؛ یا شما را به آیین خویش بازمی&zwnj;گردانند؛ و در آن صورت، هرگز روی رستگاری را نخواهید دید!&raquo;';
$t[21] = 'و اینچنین مردم را متوّجه حال آنها کردیم، تا بدانند که وعده خداوند (در مورد رستاخیز) حقّ است؛ و در پایان جهان و قیام قیامت شکّی نیست! در آن هنگام که میان خود درباره کار خویش نزاع داشتند، گروهی می&zwnj;گفتند: &laquo;بنایی بر آنان بسازید (تا برای همیشه از نظر پنهان شوند! و از آنها سخن نگویید که) پروردگارشان از وضع آنها آگاهتر است!&raquo; ولی آنها که از رازشان آگاهی یافتند (و آن را دلیلی بر رستاخیز دیدند) گفتند: &laquo;ما مسجدی در کنار (مدفن) آنها می&zwnj;سازیم (تا خاطره آنان فراموش نشود.)&raquo;';
$t[22] = 'گروهی خواهند گفت: &laquo;آنها سه نفر بودند، که چهارمین آنها سگشان بود!&raquo; و گروهی می&zwnj;گویند: &laquo;پنچ نفر بودند، که ششمین آنها سگشان بود.&raquo; -همه اینها سخنانی بی&zwnj;دلیل است- و گروهی می&zwnj;گویند: &laquo;آنها هفت نفر بودند، و هشتمین آنها سگشان بود.&raquo; بگو: &laquo;پروردگار من از تعدادشان آگاهتر است!&raquo; جز گروه کمی، تعداد آنها را نمی&zwnj;دانند. پس درباره آنان جز با دلیل سخن مگو؛ و از هیچ کس درباره آنها سؤال مکن!';
$t[23] = 'و هرگز در مورد کاری نگو: &laquo;من فردا آن را انجام می&zwnj;دهم&raquo;...';
$t[24] = 'مگر اینکه خدا بخواهد! و هرگاه فراموش کردی، (جبران کن) و پروردگارت را به خاطر بیاور؛ و بگو: &laquo;امیدوارم که پروردگارم مرا به راهی روشنتر از این هدایت کند!&raquo;';
$t[25] = 'آنها در غارشان سیصد سال درنگ کردند، و نه سال (نیز) بر آن افزودند.';
$t[26] = 'بگو: &laquo;خداوند از مدّت توقفشان آگاهتر است؛ غیب آسمانها و زمین از آن اوست! راستی چه بینا و شنواست! آنها هیچ ولیّ و سرپرستی جز او ندارند! و او هیچ کس را در حکم خود شرکت نمی&zwnj;دهد!&raquo;';
$t[27] = 'آنچه را از کتاب پروردگارت به تو وحی شده تلاوت کن! هیچ چیز سخنان او را دگرگون نمی&zwnj;سازد؛ و هرگز پناهگاهی جز او نمی&zwnj;یابی!';
$t[28] = 'با کسانی باش که پروردگار خود را صبح و عصر می&zwnj;خوانند، و تنها رضای او را می&zwnj;طلبند! و هرگز بخاطر زیورهای دنیا، چشمان خود را از آنها برمگیر! و از کسانی که قلبشان را از یاد خود غافل ساختیم اطاعت مکن! همانها که از هوای نفس پیروی کردند، و کارهایشان افراطی است.';
$t[29] = 'بگو: &laquo;این حقّ است از سوی پروردگارتان! هر کس می&zwnj;خواهد ایمان بیاورد (و این حقیقت را پذیرا شود)، و هر کس میخواهد کافر گردد!&raquo; ما برای ستمگران آتشی آماده کردیم که سراپرده&zwnj;اش آنان را از هر سو احاطه کرده است! و اگر تقاضای آب کنند، آبی برای آنان میاورند که همچون فلز گداخته صورتها را بریان می&zwnj;کند! چه بد نوشیدنی، و چه بد محل اجتماعی است!';
$t[30] = 'مسلّماً کسانی که ایمان آوردند و کارهای شایسته انجام دادند، ما پاداش نیکوکاران را ضایع نخواهیم کرد!';
$t[31] = 'آنها کسانی هستند که بهشت جاودان برای آنان است؛ باغهایی از بهشت که نهرها از زیر درختان و قصرهایش جاری است؛ در آنجا با دستبندهایی از طلا آراسته می&zwnj;شوند؛ و لباسهایی (فاخر) به رنگ سبز، از حریر نازک و ضخیم، دربر می&zwnj;کنند؛ در حالی که بر تختها تکیه کرده&zwnj;اند. چه پاداش خوبی، و چه جمع نیکویی!';
$t[32] = '(ای پیامبر!) برای آنان مثالی بزن: آن دو مرد، که برای یکی از آنها دو باغ از انواع انگورها قرار دادیم؛ و گرداگرد آن دو (باغ) را با درختان نخل پوشاندیم؛ و در میانشان زراعت پر برکتی قراردادیم.';
$t[33] = 'هر دو باغ، میوه آورده بود، (میوه&zwnj;های فراوان،) و چیزی فروگذار نکرده بود؛ و میان آن دو، نهر بزرگی جاری ساخته بودیم.';
$t[34] = 'صاحب این باغ، درآمد فراوانی داشت؛ به همین جهت، به دوستش -در حالی که با او گفتگو می&zwnj;کرد- چنین گفت: &laquo;من از نظر ثروت از تو برتر، و از نظر نفرات نیرومندترم!&raquo;';
$t[35] = 'و در حالی که نسبت به خود ستمکار بود، در باغ خویش گام نهاد، و گفت: &laquo;من گمان نمی&zwnj;کنم هرگز این باغ نابود شود!';
$t[36] = 'و باور نمی&zwnj;کنم قیامت برپا گردد! و اگر به سوی پروردگارم بازگردانده شوم (و قیامتی در کار باشد)، جایگاهی بهتر از این جا خواهم یافت!&raquo;';
$t[37] = 'دوست (با ایمان) وی -در حالی که با او گفتگو می&zwnj;کرد- گفت: &laquo;آیا به خدایی که تو را از خاک، و سپس از نطفه آفرید، و پس از آن تو را مرد کاملی قرار داد، کافر شدی؟!';
$t[38] = 'ولی من کسی هستم که &laquo;اللّه&raquo; پروردگار من است؛ و هیچ کس را شریک پروردگارم قرارنمی&zwnj;دهم!';
$t[39] = 'چرا هنگامی که وارد باغت شدی، نگفتی این نعمتی است که خدا خواسته است؟! قوّت (و نیرویی) جز از ناحیه خدا نیست! و اگر می&zwnj;بینی من از نظر مال و فرزند از تو کمترم (مطلب مهمّی نیست)!';
$t[40] = 'شاید پروردگارم بهتر از باغ تو به من بدهد؛ و مجازات حساب شده&zwnj;ای از آسمان بر باغ تو فروفرستد، بگونه&zwnj;ای که آن را به زمین بی&zwnj;گیاه لغزنده&zwnj;ای مبدّل کند!';
$t[41] = 'و یا آب آن در اعمال زمین فرو رود، آن گونه که هرگز نتوانی آن را به دست آوری!&raquo;';
$t[42] = '(به هر حال عذاب الهی فرا رسید،) و تمام میوه&zwnj;های آن نابود شد؛ و او بخاطر هزینه هایی که در آن صرف کرده بود، پیوسته دستهای خود را به هم می&zwnj;مالید -در حالی که تمام باغ بر داربستهایش فرو ریخته بود- و می&zwnj;گفت: &laquo;ای کاش کسی را همتای پروردگارم قرار نداده بودم!&raquo;';
$t[43] = 'و گروهی نداشت که او را در برابر (عذاب) خداوند یاری دهند؛ و از خودش (نیز) نمی&zwnj;توانست یاری گیرد.';
$t[44] = 'در آنجا ثابت شد که ولایت (و قدرت) از آن خداوند بر حق است! اوست که برترین ثواب، و بهترین عاقبت را (برای مطیعان) دارد!';
$t[45] = '(ای پیامبر!) زندگی دنیا را برای آنان به آبی تشبیه کن که از آسمان فرو می&zwnj;فرستیم؛ و بوسیله آن، گیاهان زمین (سرسبز می&zwnj;شود و) در هم فرومیرود. امّا بعد از مدتی می&zwnj;خشکد؛ و بادها آن را به هر سو پراکنده می&zwnj;کند؛ و خداوند بر همه چیز تواناست!';
$t[46] = 'مال و فرزند، زینت زندگی دنیاست؛ و باقیات صالحات [= ارزشهای پایدار و شایسته&zwnj;] ثوابش نزد پروردگارت بهتر و امیدبخش&zwnj;تر است!';
$t[47] = 'و روزی را به خاطر بیاور که کوه&zwnj;ها را به حرکت درآوریم؛ و زمین را آشکار (و مسطح) می&zwnj;بینی؛ و همه آنان [= انسانها] را برمی&zwnj;انگیزیم، و احدی از ایشان را فروگذار نخواهیم کرد!';
$t[48] = 'آنها همه در یک صف به (پیشگاه) پروردگارت عرضه می&zwnj;شوند؛ (و به آنان گفته می&zwnj;شود:) همگی نزد ما آمدید، همان گونه که نخستین بار شما را آفریدیم! اما شما گمان می&zwnj;کردید ما هرگز موعدی برایتان قرار نخواهیم داد!';
$t[49] = 'و کتاب [= کتابی که نامه اعمال همه انسانهاست&zwnj;] در آن جا گذارده می&zwnj;شود، پس گنهکاران را می&zwnj;بینی که از آنچه در آن است، ترسان و هراسانند؛ و می&zwnj;گویند: &laquo;ای وای بر ما! این چه کتابی است که هیچ عمل کوچک و بزرگی را فرونگذاشته مگر اینکه آن را به شمار آورده است؟! و (این در حالی است که) همه اعمال خود را حاضر می&zwnj;بینند؛ و پروردگارت به هیچ کس ستم نمی&zwnj;کند.';
$t[50] = 'به یاد آرید زمانی را که به فرشتگان گفتیم: &laquo;برای آدم سجده کنید!&raquo; آنها همگی سجده کردند جز ابلیس -که از جن بود- و از فرمان پروردگارش بیرون شد آیا (با این حال،) او و فرزندانش را به جای من اولیای خود انتخاب می&zwnj;کنید، در حالی که آنها دشمن شما هستند؟! (فرمانبرداری از شیطان و فرزندانش به جای اطاعت خدا،) چه جایگزینی بدی است برای ستمکاران!';
$t[51] = 'من هرگز آنها [= ابلیس و فرزندانش&zwnj;] را به هنگام آفرینش آسمانها و زمین، و نه به هنگام آفرینش خودشان، حاضر نساختم! و من هیچ گاه گمراه&zwnj;کنندگان را دستیار خود قرار نمی&zwnj;دهم!';
$t[52] = 'به خاطر بیاورید روزی را که (خداوند) می&zwnj;گوید: &laquo;همتایانی را که برای من می&zwnj;پنداشتید، بخوانید (تا به کمک شما بشتابند!)&raquo; ولی هر چه آنها را می&zwnj;خوانند، جوابشان نمی&zwnj;دهند؛ و در میان این دو گروه، کانون هلاکتی قرارداده&zwnj;ایم!';
$t[53] = 'و گنهکاران، آتش (دوزخ) را می&zwnj;بینند؛ و یقین می&zwnj;کنند که با آن درمی&zwnj;آمیزند، و هیچ گونه راه گریزی از آن نخواهند یافت.';
$t[54] = 'و در این قرآن، از هر گونه مثلی برای مردم بیان کرده&zwnj;ایم؛ ولی انسان بیش از هر چیز، به مجادله می&zwnj;پردازد!';
$t[55] = 'و چیزی مردم را بازنداشت از اینکه -وقتی هدایت به سراغشان آمد- ایمان بیاورند و از پروردگارشان طلب آمرزش کنند، جز اینکه (خیره&zwnj;سری کردند؛ گویی می&zwnj;خواستند) سرنوشت پیشینیان برای آنان بیاید، یا عذاب (الهی) در برابرشان قرار گیرد!';
$t[56] = 'ما پیامبران را، جز بعنوان بشارت دهنده و انذار کننده، نمی&zwnj;فرستیم؛ اما کافران همواره مجادله به باطل می&zwnj;کنند، تا (به گمان خود،) حق را بوسیله آن از میان بردارند! و آیات ما، و مجازاتهایی را که به آنان وعده داده شده است، به باد مسخره گرفتند!';
$t[57] = 'چه کسی ستمکارتر است از آن کس که آیات پروردگارش به او تذکّر داده شده، و از آن روی گرداند، و آنچه را با دستهای خود پیش فرستاد فراموش کرد؟! ما بر دلهای اینها پرده&zwnj;هایی افکنده&zwnj;ایم تا نفهمند؛ و در گوشهایشان سنگینی قرار داده&zwnj;ایم (تا صدای حق را نشنوند)! و از این رو اگر آنها را به سوی هدایت بخوانی، هرگز هدایت نمی&zwnj;شوند!';
$t[58] = 'و پروردگارت، آمرزنده و صاحب رحمت است؛ اگر می&zwnj;خواست آنان را به خاطر اعمالشان مجازات کند، عذاب را هر چه زودتر برای آنها می&zwnj;فرستاد؛ ولی برای آنان موعدی است که هرگز از آن راه فراری نخواهند داشت!';
$t[59] = 'این شهرها و آبادیهایی است که ما آنها را هنگامی که ستم کردند هلاک نمودیم؛ و برای هلاکتشان موعدی قرار دادیم! (آنها ویرانه&zwnj;هایش را با چشم می&zwnj;بینند، و عبرت نمی&zwnj;گیرند!)';
$t[60] = 'به خاطر بیاور هنگامی را که موسی به دوست خود گفت: دست از جستجو برنمی&zwnj;دارم تا به محل تلاقی دو دریا برسم؛ هر چند مدت طولانی به راه خود ادامه دهم!';
$t[61] = '(ولی) هنگامی که به محل تلاقی آن دو دریا رسیدند، ماهی خود را (که برای تغذیه همراه داشتند) فراموش کردند؛ و ماهی راه خود را در دریا پیش گرفت (و روان شد).';
$t[62] = 'هنگامی که از آن جا گذشتند، (موسی) به یار همسفرش گفت: &laquo;غذای ما را بیاور، که سخت از این سفر خسته شده&zwnj;ایم!&raquo;';
$t[63] = 'گفت: &laquo;به خاطر داری هنگامی که ما (برای استراحت) به کنار آن صخره پناه بردیم، من (در آن جا) فراموش کردم جریان ماهی را بازگو کنم -و فقط شیطان بود که آن را از خاطر من برد- و ماهی بطرز شگفت&zwnj;آوری راه خود را در دریا پیش گرفت!&raquo;';
$t[64] = '(موسی) گفت: &laquo;آن همان بود که ما می&zwnj;خواستیم!&raquo; سپس از همان راه بازگشتند، در حالی که پی&zwnj;جویی می&zwnj;کردند.';
$t[65] = '(در آن جا) بنده&zwnj;ای از بندگان ما را یافتند که رحمت (و موهبت عظیمی) از سوی خود به او داده، و علم فراوانی از نزد خود به او آموخته بودیم.';
$t[66] = 'موسی به او گفت: &laquo;آیا از تو پیروی کنم تا از آنچه به تو تعلیم داده شده و مایه رشد و صلاح است، به من بیاموزی؟&raquo;';
$t[67] = 'گفت: &laquo;تو هرگز نمی&zwnj;توانی با من شکیبایی کنی!';
$t[68] = 'و چگونه می&zwnj;توانی در برابر چیزی که از رموزش آگاه نیستی شکیبا باشی؟!';
$t[69] = '(موسی) گفت: &laquo;به خواست خدا مرا شکیبا خواهی یافت؛ و در هیچ کاری مخالفت فرمان تو نخواهم کرد!&raquo;';
$t[70] = '(خضر) گفت: &laquo;پس اگر می&zwnj;خواهی بدنبال من بیایی، از هیچ چیز مپرس تا خودم (به موقع) آن را برای تو بازگو کنم.&raquo;';
$t[71] = 'آن دو به راه افتادند؛ تا آن که سوار کشتی شدند، (خضر) کشتی را سوراخ کرد. (موسی) گفت: &laquo;آیا آن را سوراخ کردی که اهلش را غرق کنی؟! راستی که چه کار بدی انجام دادی!&raquo;';
$t[72] = 'گفت: &laquo;آیا نگفتم تو هرگز نمی&zwnj;توانی با من شکیبایی کنی؟!&raquo;';
$t[73] = '(موسی) گفت: &laquo;مرا بخاطر این فراموشکاریم مؤاخذه مکن و از این کارم بر من سخت مگیر!&raquo;';
$t[74] = 'باز به راه خود ادامه دادند، تا اینکه نوجوانی را دیدند؛ و او آن نوجوان را کشت. (موسی) گفت: &laquo;آیا انسان پاکی را، بی آنکه قتلی کرده باشد، کشتی؟! براستی کار زشتی انجام دادی!&raquo;';
$t[75] = '(باز آن مرد عالم) گفت: &laquo;آیا به تو نگفتم که تو هرگز نمی&zwnj;توانی با من صبر کنی؟!&raquo;';
$t[76] = '(موسی) گفت: &laquo;بعد از این اگر درباره چیزی از تو سؤال کردم، دیگر با من همراهی نکن؛ (زیرا) از سوی من معذور خواهی بود!&raquo;';
$t[77] = 'باز به راه خود ادامه دادند تا به مردم قریه&zwnj;ای رسیدند؛ از آنان خواستند که به ایشان غذا دهند؛ ولی آنان از مهمان کردنشان خودداری نمودند؛ (با این حال) در آن جا دیواری یافتند که می&zwnj;خواست فروریزد؛ و (آن مرد عالم) آن را برپا داشت. (موسی) گفت: &laquo;(لااقل) می&zwnj;خواستی در مقابل این کار مزدی بگیری!&raquo;';
$t[78] = 'او گفت: &laquo;اینک زمان جدایی من و تو فرا رسیده؛ اما بزودی راز آنچه را که نتوانستی در برابر آن صبر کنی، به تو خبر می&zwnj;دهم.';
$t[79] = 'اما آن کشتی مال گروهی از مستمندان بود که با آن در دریا کار می&zwnj;کردند؛ و من خواستم آن را معیوب کنم؛ (چرا که) پشت سرشان پادشاهی (ستمگر) بود که هر کشتی (سالمی) را بزور میگرفت!';
$t[80] = 'و اما آن نوجوان، پدر و مادرش با ایمان بودند؛ و بیم داشتیم که آنان را به طغیان و کفر وادارد!';
$t[81] = 'از این رو، خواستیم که پروردگارشان به جای او، فرزندی پاکتر و با محبت&zwnj;تر به آن دو بدهد!';
$t[82] = 'و اما آن دیوار، از آن دو نوجوان یتیم در آن شهر بود؛ و زیر آن، گنجی متعلق به آن دو وجود داشت؛ و پدرشان مرد صالحی بود؛ و پروردگار تو می&zwnj;خواست آنها به حد بلوغ برسند و گنجشان را استخراج کنند؛ این رحمتی از پروردگارت بود؛ و من آن (کارها) را خودسرانه انجام ندادم؛ این بود راز کارهایی که نتوانستی در برابر آنها شکیبایی به خرج دهی!&raquo;';
$t[83] = 'و از تو درباره &laquo;ذو القرنین&raquo; می&zwnj;پرسند؛ بگو: &laquo;بزودی بخشی از سرگذشت او را برای شما بازگو خواهم کرد.&raquo;';
$t[84] = 'ما به او در روی زمین، قدرت و حکومت دادیم؛ و اسباب هر چیز را در اختیارش گذاشتیم.';
$t[85] = 'او از این اسباب، (پیروی و استفاده) کرد...';
$t[86] = 'تا به غروبگاه آفتاب رسید؛ (در آن جا) احساس کرد (و در نظرش مجسّم شد) که خورشید در چشمه تیره و گل&zwnj;آلودی فرو می&zwnj;رود؛ و در آن جا قومی را یافت؛ گفتیم: &laquo;ای ذو القرنین! آیا می&zwnj;خواهی (آنان) را مجازات کنی، و یا روش نیکویی در مورد آنها انتخاب نمایی؟&raquo;';
$t[87] = 'گفت: &laquo;اما کسی را که ستم کرده است، مجازات خواهیم کرد؛ سپس به سوی پروردگارش بازمی&zwnj;گردد، و خدا او را مجازات شدیدی خواهد کرد!';
$t[88] = 'و اما کسی که ایمان آورد و عمل صالح انجام دهد، پاداشی نیکوتر خواهد داشت؛ و ما دستور آسانی به او خواهیم داد.&raquo;';
$t[89] = 'سپس (بار دیگر) از اسبابی (که در اختیار داشت) بهره گرفت...';
$t[90] = 'تا به خاستگاه خورشید رسید؛ (در آن جا) دید خورشید بر جمعیّتی طلوع می&zwnj;کند که در برابر (تابش) آفتاب، پوششی برای آنها قرار نداده بودیم (و هیچ گونه سایبانی نداشتند).';
$t[91] = '(آری) اینچنین بود (کار ذو القرنین)! و ما بخوبی از امکاناتی که نزد او بود آگاه بودیم!';
$t[92] = '(باز) از اسباب مهمّی (که در اختیار داشت) استفاده کرد...';
$t[93] = '(و همچنان به راه خود ادامه داد) تا به میان دو کوه رسید؛ و در کنار آن دو (کوه) قومی را یافت که هیچ سخنی را نمی&zwnj;فهمیدند (و زبانشان مخصوص خودشان بود)!';
$t[94] = '(آن گروه به او) گفتند: &laquo;ای ذو القرنین یأجوج و مأجوج در این سرزمین فساد می&zwnj;کنند؛ آیا ممکن است ما هزینه&zwnj;ای برای تو قرار دهیم، که میان ما و آنها سدّی ایجاد کنی؟!&raquo;';
$t[95] = '(ذو القرنین) گفت: &laquo;آنچه پروردگارم در اختیار من گذارده، بهتر است (از آنچه شما پیشنهاد می&zwnj;کنید)! مرا با نیرویی یاری دهید، تا میان شما و آنها سدّ محکمی قرار دهم!';
$t[96] = 'قطعات بزرگ آهن برایم بیاورید (و آنها را روی هم بچینید)!&raquo; تا وقتی که کاملاً میان دو کوه را پوشانید، گفت: &laquo;(در اطراف آن آتش بیفروزید، و) در آن بدمید!&raquo; (آنها دمیدند) تا قطعات آهن را سرخ و گداخته کرد، و گفت: &laquo;(اکنون) مس مذاب برایم بیاورید تا بر روی آن بریزم!&raquo;';
$t[97] = '(سرانجام چنان سدّ نیرومندی ساخت) که آنها [= طایفه یأجوج و مأجوج&zwnj;] قادر نبودند از آن بالا روند؛ و نمی&zwnj;توانستند نقبی در آن ایجاد کنند.';
$t[98] = '(آنگاه) گفت: &laquo;این از رحمت پروردگار من است! امّا هنگامی که وعده پروردگارم فرا رسد، آن را در هم می&zwnj;کوبد؛ و وعده پروردگارم حق است!&raquo;';
$t[99] = 'و در آن روز (که جهان پایان می&zwnj;گیرد)، ما آنان را چنان رها می&zwnj;کنیم که درهم موج می&zwnj;زنند؛ و در صور [= شیپور] دمیده می&zwnj;شود؛ و ما همه را جمع می&zwnj;کنیم!';
$t[100] = 'در آن روز، جهنم را بر کافران عرضه می&zwnj;داریم!';
$t[101] = 'همانها که پرده&zwnj;ای چشمانشان را از یاد من پوشانده بود، و قدرت شنوایی نداشتند!';
$t[102] = 'آیا کافران پنداشتند می&zwnj;توانند بندگانم را به جای من اولیای خود انتخاب کنند؟! ما جهنم را برای پذیرایی کافران آماده کرده&zwnj;ایم!';
$t[103] = 'بگو: &laquo;آیا به شما خبر دهیم که زیانکارترین (مردم) در کارها، چه کسانی هستند؟';
$t[104] = 'آنها که تلاشهایشان در زندگی دنیا گم (و نابود) شده؛ با این حال، می&zwnj;پندارند کار نیک انجام می&zwnj;دهند!&raquo;';
$t[105] = 'آنها کسانی هستند که به آیات پروردگارشان و لقای او کافر شدند؛ به همین جهت، اعمالشان حبط و نابود شد! از این رو روز قیامت، میزانی برای آنها برپا نخواهیم کرد!';
$t[106] = '(آری،) این گونه است! کیفرشان دوزخ است، بخاطر آنکه کافر شدند، و آیات من و پیامبرانم را به سخریه گرفتند!';
$t[107] = 'امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، باغهای بهشت برین محل پذیرایی آنان خواهد بود.';
$t[108] = 'آنها جاودانه در آن خواهند ماند؛ و هرگز تقاضای نقل مکان از آن جا نمی&zwnj;کنند!';
$t[109] = 'بگو: &laquo;اگر دریاها برای (نوشتن) کلمات پروردگارم مرکّب شود، دریاها پایان می&zwnj;گیرد. پیش از آنکه کلمات پروردگارم پایان یابد؛ هر چند همانند آن (دریاها) را کمک آن قرار دهیم!&raquo;';
$t[110] = 'بگو: &laquo;من فقط بشری هستم مثل شما؛ (امتیازم این است که) به من وحی می&zwnj;شود که تنها معبودتان معبود یگانه است؛ پس هر که به لقای پروردگارش امید دارد، باید کاری شایسته انجام دهد، و هیچ کس را در عبادت پروردگارش شریک نکند!';
?>