$t[93] = 'از من پیروی نکردی؟! آیا فرمان مرا عصیان نمودی؟!&raquo;';
$t[94] = '(هارون) گفت: &laquo;ای فرزند مادرم! [= ای برادر!] ریش و سر مرا مگیر! من ترسیدم بگویی تو میان بنی اسرائیل تفرقه انداختی، و سفارش مرا به کار نبستی!&raquo;';
$t[95] = '(موسی رو به سامری کرد و) گفت: &laquo;تو چرا این کار را کردی، ای سامری؟!&raquo;';
$t[96] = 'گفت: &laquo;من چیزی دیدم که آنها ندیدند؛ من قسمتی از آثار رسول (و فرستاده خدا) را گرفتم، سپس آن را افکندم، و اینچنین (هوای) نفس من این کار را در نظرم جلوه داد!&raquo;';
$t[97] = '(موسی) گفت: &laquo;برو، که بهره تو در زندگی دنیا این است که (هر کس با تو نزدیک شود) بگوئی &laquo;با من تماس نگیر!&raquo; و تو میعادی (از عذاب خدا) داری، که هرگز تخلّف نخواهد شد! (اکنون) بنگر به این معبودت که پیوسته آن را پرستش می&zwnj;کردی! و ببین ما آن را نخست می&zwnj;سوزانیم؛ سپس ذرّات آن را به دریا می&zwnj;پاشیم!';
$t[98] = 'معبود شما تنها خداوندی است که جز او معبودی نیست؛ و علم او همه چیز را فرا گرفته است!&raquo;';
$t[99] = 'این گونه بخشی از اخبار پیشین را برای تو بازگو می&zwnj;کنیم؛ و ما از نزد خود، ذکر (و قرآنی) به تو دادیم!';
$t[100] = 'هر کس از آن روی گردان شود، روز قیامت بار سنگینی (از گناه و مسؤولیّت) بر دوش خواهد داشت!';
$t[101] = 'در حالی که جاودانه در آن خواهند ماند؛ و بد باری است برای آنها در روز قیامت!';
$t[102] = 'همان روزی که در &laquo;صور&raquo; دمیده می&zwnj;شود؛ و مجرمان را با بدنهای کبود، در آن روز جمع می&zwnj;کنیم!';
$t[103] = 'آنها آهسته با هم گفتگو می&zwnj;کنند؛ (بعضی می&zwnj;گویند:) شما فقط ده (شبانه روز در عالم برزخ) توقّف کردید! (و نمی&zwnj;دانند چقدر طولانی بوده است!)';
$t[104] = 'ما به آنچه آنها می&zwnj;گویند آگاهتریم، هنگامی که نیکوروش&zwnj;ترین آنها می&zwnj;گوید: &laquo;شما تنها یک روز درنگ کردید!&raquo;';
$t[105] = 'و از تو درباره کوه&zwnj;ها سؤال می&zwnj;کنند؛ بگو: &laquo;پروردگارم آنها را (متلاشی کرده) برباد می&zwnj;دهد!';
$t[106] = 'سپس زمین را صاف و هموار و بی&zwnj;آب و گیاه رها می&zwnj;سازد...';
$t[107] = 'به گونه&zwnj;ای که در آن، هیچ پستی و بلندی نمی&zwnj;بینی!&raquo;';
$t[108] = 'در آن روز، همه از دعوت کننده الهی پیروی نموده، و قدرت بر مخالفت او نخواهند داشت (و همگی از قبرها برمی&zwnj;خیزند)؛ و همه صداها در برابر (عظمت) خداوند رحمان، خاضع می&zwnj;شود؛ و جز صدای آهسته چیزی نمی&zwnj;شنوی!';
$t[109] = 'در آن روز، شفاعت هیچ کس سودی نمی&zwnj;بخشد، جز کسی که خداوند رحمان به او اجازه داده، و به گفتار او راضی است.';
$t[110] = 'آنچه را پیش رو دارند، و آنچه را (در دنیا) پشت سرگذاشته&zwnj;اند می&zwnj;داند؛ ولی آنها به (علم) او احاطه ندارند!';
$t[111] = 'و (در آن روز) همه چهره&zwnj;ها در برابر خداوند حیّ قیوم، خاضع می&zwnj;شود؛ و مأیوس (و زیانکار) است آن که بار ستمی بر دوش دارد!';
$t[112] = '(امّا) آن کس که کارهای شایسته انجام دهد، در حالی که مؤمن باشد، نه از ظلمی می&zwnj;ترسد، و نه از نقصان حقش.';
$t[113] = 'و این گونه آن را قرآنی عربی [= فصیح و گویا] نازل کردیم، و انواع وعیدها (و انذارها) را در آن بازگو نمودیم، شاید تقوا پیشه کنند؛ یا برای آنان تذکّری پدید آورد!';
$t[114] = 'پس بلندمرتبه است خداوندی که سلطان حقّ است! پس نسبت به (تلاوت) قرآن عجله مکن، پیش از آنکه وحی آن بر تو تمام شود؛ و بگو: &laquo;پروردگارا! علم مرا افزون کن!&raquo;';
$t[115] = 'پیش از این، از آدم پیمان گرفته بودیم؛ امّا او فراموش کرد؛ و عزم استواری برای او نیافتیم!';
$t[116] = 'و به یاد آور هنگامی را که به فرشتگان گفتیم: &laquo;برای آدم سجده کنید!&raquo; همگی سجده کردند؛ جز ابلیس که سرباز زد (و سجده نکرد)!';
$t[117] = 'پس گفتیم: &laquo;ای آدم! این (ابلیس) دشمن تو و (دشمن) همسر توست! مبادا شما را از بهشت بیرون کند؛ که به زحمت و رنج خواهی افتاد!';
$t[118] = '(امّا تو در بهشت راحت هستی! و مزیّتش) برای تو این است که در آن گرسنه و برهنه نخواهی شد؛';
$t[119] = 'و در آن تشنه نمی&zwnj;شوی، و حرارت آفتاب آزارت نمی&zwnj;دهد!&raquo;';
$t[120] = 'ولی شیطان او را وسوسه کرد و گفت: &laquo;ای آدم! آیا می&zwnj;خواهی تو را به درخت زندگی جاوید، و ملکی بی&zwnj;زوال راهنمایی کنم؟!&raquo;';
$t[121] = 'سرانجام هر دو از آن خوردند، (و لباس بهشتیشان فرو ریخت،) و عورتشان آشکار گشت و برای پوشاندن خود، از برگهای (درختان) بهشتی جامه دوختند! (آری) آدم پروردگارش را نافرمانی کرد، و از پاداش او محروم شد!';
$t[122] = 'سپس پروردگارش او را برگزید، و توبه&zwnj;اش را پذیرفت، و هدایتش نمود.';
$t[123] = '(خداوند) فرمود: &laquo;هر دو از آن (بهشت) فرود آیید، در حالی که دشمن یکدیگر خواهید بود! ولی هرگاه هدایت من به سراغ شما آید، هر کس از هدایت من پیروی کند، نه گمراه می&zwnj;شود، و نه در رنج خواهد بود!';
$t[124] = 'و هر کس از یاد من روی گردان شود، زندگی (سخت و) تنگی خواهد داشت؛ و روز قیامت، او را نابینا محشور می&zwnj;کنیم!&raquo;';
$t[125] = 'می&zwnj;گوید: &laquo;پروردگارا! چرا نابینا محشورم کردی؟! من که بینا بودم!&raquo;';
$t[126] = 'می&zwnj;فرماید: &laquo;آن گونه که آیات من برای تو آمد، و تو آنها را فراموش کردی؛ امروز نیز تو فراموش خواهی شد!&raquo;';
$t[127] = 'و این گونه جزا می&zwnj;دهیم کسی را که اسراف کند، و به آیات پروردگارش ایمان نیاورد! و عذاب آخرت، شدیدتر و پایدارتر است!';
$t[128] = 'آیا برای هدایت آنان کافی نیست که بسیاری از نسلهای پیشین را (که طغیان و فساد کردند) هلاک نمودیم، و اینها در مسکنهای (ویران شده) آنان راه می&zwnj;روند! مسلّماً در این امر، نشانه&zwnj;های روشنی برای خردمندان است.';
$t[129] = 'و اگر سنّت و تقدیر پروردگارت و ملاحظه زمان مقرّر نبود، عذاب الهی بزودی دامان آنان را می&zwnj;گرفت!';
$t[130] = 'پس در برابر آنچه می&zwnj;گویند، صبر کن! و پیش از طلوع آفتاب، و قبل از غروب آن؛ تسبیح و حمد پروردگارت را بجا آور؛ و همچنین (برخی) از ساعات شب و اطراف روز (پروردگارت را) تسبیح گوی؛ باشد که (از الطاف الهی) خشنود شوی!';
$t[131] = 'و هرگز چشمان خود را به نعمتهای مادّی، که به گروه&zwnj;هایی از آنان داده&zwnj;ایم، میفکن! اینها شکوفه&zwnj;های زندگی دنیاست؛ تا آنان را در آن بیازماییم؛ و روزی پروردگارت بهتر و پایدارتر است!';
$t[132] = 'خانواده خود را به نماز فرمان ده؛ و بر انجام آن شکیبا باش! از تو روزی نمی&zwnj;خواهیم؛ (بلکه) ما به تو روزی می&zwnj;دهیم؛ و عاقبت نیک برای تقواست!';
$t[133] = 'گفتند: &laquo;چرا (پیامبر) معجزه و نشانه&zwnj;ای از سوی پروردگارش برای ما نمی&zwnj;آورد؟! (بگو:) آیا خبرهای روشنی که در کتابهای (آسمانی) نخستین بوده، برای آنها نیامد؟!';
$t[134] = 'اگر ما آنان را پیش از آن (که قرآن نازل شود) با عذابی هلاک می&zwnj;کردیم، (در قیامت) می&zwnj;گفتند: &laquo;پروردگارا! چرا پیامبری برای ما نفرستادی تا از آیات تو پیروی کنیم، پیش از آنکه ذلیل و رسوا شویم!&raquo;';
$t[135] = 'بگو: &laquo;همه (ما و شما) در انتظاریم! (ما در انتظار وعده پیروزی، و شما در انتظار شکست ما!) حال که چنین است، انتظار بکشید! امّا بزودی می&zwnj;دانید چه کسی از اصحاب صراط مستقیم، و چه کسی هدایت یافته است!';
?>