$t[21] = 'آیا آنها خدایانی از زمین برگزیدند که (خلق می&zwnj;کنند و) منتشر می&zwnj;سازند؟!';
$t[22] = 'اگر در آسمان و زمین، جز &laquo;اللّه&raquo; خدایان دیگری بود، فاسد می&zwnj;شدند (و نظام جهان به هم می&zwnj;خورد)! منزه است خداوند پروردگار عرش، از توصیفی که آنها می&zwnj;کنند!';
$t[23] = 'هیچ کس نمی&zwnj;تواند بر کار او خرده بگیرد؛ ولی در کارهای آنها، جای سؤال و ایراد است!';
$t[24] = 'آیا آنها معبودانی جز خدا برگزیدند؟! بگو: &laquo;دلیلتان را بیاورید! این سخن کسانی است که با من هستند، و سخن کسانی [= پیامبرانی&zwnj;] است که پیش از من بودند!&raquo; امّا بیشتر آنها حق را نمی&zwnj;دانند؛ و به همین دلیل (از آن) روی گردانند.';
$t[25] = 'ما پیش از تو هیچ پیامبری را نفرستادیم مگر اینکه به او وحی کردیم که: &laquo;معبودی جز من نیست؛ پس تنها مرا پرستش کنید.&raquo;';
$t[26] = 'آنها گفتند: &laquo;خداوند رحمان فرزندی برای خود انتخاب کرده است&raquo;! او منزه است (از این عیب و نقص)؛ آنها [= فرشتگان&zwnj;] بندگان شایسته اویند.';
$t[27] = 'هرگز در سخن بر او پیشی نمی&zwnj;گیرند؛ و (پیوسته) به فرمان او عمل می&zwnj;کنند.';
$t[28] = 'او اعمال امروز و آینده و اعمال گذشته آنها را می&zwnj;داند؛ و آنها جز برای کسی که خدا راضی (به شفاعت برای او) است شفاعت نمی&zwnj;کنند؛ و از ترس او بیمناکند.';
$t[29] = 'و هر کس از آنها بگوید: &laquo;من جز خدا، معبودی دیگرم&raquo;، کیفر او را جهنم می&zwnj;دهیم! و ستمگران را این گونه کیفر خواهیم داد.';
$t[30] = 'آیا کافران ندیدند که آسمانها و زمین به هم پیوسته بودند، و ما آنها را از یکدیگر باز کردیم؛ و هر چیز زنده&zwnj;ای را از آب قرار دادیم؟! آیا ایمان نمی&zwnj;آورند؟!';
$t[31] = 'و در زمین، کوه&zwnj;های ثابت و پابرجایی قرار دادیم، مبادا آنها را بلرزاند! و در آن، درّه&zwnj;ها و راه&zwnj;هایی قرار دادیم تا هدایت شوند!';
$t[32] = 'و آسمان را سقف محفوظی قرار دادیم؛ ولی آنها از آیات آن روی&zwnj;گردانند.';
$t[33] = 'او کسی است که شب و روز و خورشید و ماه را آفرید؛ هر یک در مداری در حرکتند!';
$t[34] = 'پیش از تو (نیز) برای هیچ انسانی جاودانگی قرار ندادیم؛ (وانگهی آنها که انتظار مرگ تو را می&zwnj;کشند،) آیا اگر تو بمیری، آنان جاوید خواهند بود؟!';
$t[35] = 'هر انسانی طعم مرگ را می&zwnj;چشد! و شما را با بدیها و خوبیها آزمایش می&zwnj;کنیم؛ و سرانجام بسوی ما بازگردانده می&zwnj;شوید!';
$t[36] = 'هنگامی که کافران تو را می&zwnj;بینند، کاری جز استهزا کردن تو ندارند؛ (و می&zwnj;گویند:) آیا این همان کسی است که سخن از خدایان شما می&zwnj;گوید؟! در حالی که خودشان ذکر خداوند رحمان را انکار می&zwnj;کنند.';
$t[37] = '(آری،) انسان از عجله آفریده شده؛ ولی عجله نکنید؛ بزودی آیاتم را به شما نشان خواهم داد!';
$t[38] = 'آنها می&zwnj;گویند: &laquo;اگر راست میگویید، این وعده (قیامت) کی فرا می&zwnj;رسد؟!&raquo;';
$t[39] = 'ولی اگر کافران می&zwnj;دانستند زمانی که (فرا می&zwnj;رسد) نمی&zwnj;توانند شعله&zwnj;های آتش را از صورت و از پشتهای خود دور کنند، و هیچ کس آنان را یاری نمی&zwnj;کند (این قدر درباره قیامت شتاب نمی&zwnj;کردند)!';
$t[40] = '(آری، این مجازات الهی) بطور ناگهانی به سراغشان می&zwnj;آید و مبهوتشان می&zwnj;کند؛ آنچنان که توانایی دفع آن را ندارند، و به آنها مهلت داده نمی&zwnj;شود!';
$t[41] = '(اگر تو را استهزا کنند نگران نباش،) پیامبران پیش از تو را (نیز) استهزا کردند؛ امّا سرانجام، آنچه را استهزا می&zwnj;کردند دامان مسخره&zwnj;کنندگان را گرفت (و مجازات الهی آنها را در هم کوبید)!';
$t[42] = 'بگو: &laquo;چه کسی شما را در شب و روز از (مجازات) خداوند بخشنده نگاه می&zwnj;دارد؟!&raquo; ولی آنان از یاد پروردگارشان روی&zwnj;گردانند!';
$t[43] = 'آیا آنها خدایانی دارند که می&zwnj;توانند در برابر ما از آنان دفاع کنند؟! (این خدایان ساختگی، حتّی) نمی&zwnj;توانند خودشان را یاری دهند (تا چه رسد به دیگران)؛ و نه از ناحیه ما با نیرویی یاری می&zwnj;شوند!';
$t[44] = 'ما آنها و پدرانشان را (از نعمتها) بهره&zwnj;مند ساختیم، تا آنجا که عمر طولانی پیدا کردند (و مایه غرور و طغیانشان شد)؛ آیا نمی&zwnj;بینند که ما پیوسته به سراغ زمین آمده، و از آن (و اهلش) می&zwnj;کاهیم؟! آیا آنها غالبند (یا ما)؟!';
$t[45] = 'بگو: &laquo;من تنها بوسیله وحی شما را انذار می&zwnj;کنم!&raquo; ولی آنها که گوشهایشان کر است، هنگامی که انذار می&zwnj;شوند، سخنان را نمی&zwnj;شنوند!';
$t[46] = 'اگر کمترین عذاب پروردگارت به آنان برسد، فریادشان بلند می&zwnj;شود که: &laquo;ای وای بر ما! ما همگی ستمگر بودیم!&raquo;';
$t[47] = 'ما ترازوهای عدل را در روز قیامت برپا می&zwnj;کنیم؛ پس به هیچ کس کمترین ستمی نمی&zwnj;شود؛ و اگر بمقدار سنگینی یک دانه خردل (کار نیک و بدی) باشد، ما آن را حاضر می&zwnj;کنیم؛ و کافی است که ما حساب&zwnj;کننده باشیم!';
$t[48] = 'ما به موسی و هارون، &laquo;فرقان&raquo; [= وسیله جدا کردن حق از باطل&zwnj;] و نور، و آنچه مایه یادآوری برای پرهیزگاران است، دادیم.';
$t[49] = 'همانان که از پروردگارشان در نهان می&zwnj;ترسند، و از قیامت بیم دارند!';
$t[50] = 'و این (قرآن) ذکر مبارکی است که (بر شما) نازل کردیم؛ آیا شما آن را انکار می&zwnj;کنید؟!';
$t[51] = 'ما وسیله رشد ابراهیم را از قبل به او دادیم؛ و از (شایستگی) او آگاه بودیم...';
$t[52] = 'آن هنگام که به پدرش (آزر) و قوم او گفت: &laquo;این مجسمه&zwnj;های بی&zwnj;روح چیست که شما همواره آنها را پرستش می&zwnj;کنید؟!&raquo;';
$t[53] = 'گفتند: &laquo;ما پدران خود را دیدیم که آنها را عبادت می&zwnj;کنند.&raquo;';
$t[54] = 'گفت: &laquo;مسلّماً هم شما و هم پدرانتان، در گمراهی آشکاری بوده&zwnj;اید!&raquo;';
$t[55] = 'گفتند: &laquo;آیا مطلب حقّی برای ما آورده&zwnj;ای، یا شوخی می&zwnj;کنی؟!&raquo;';
$t[56] = 'گفت: &laquo;(کاملاً حقّ آورده&zwnj;ام) پروردگار شما همان پروردگار آسمانها و زمین است که آنها را ایجاد کرده؛ و من بر این امر، از گواهانم!';
$t[57] = 'و به خدا سوگند، در غیاب شما، نقشه&zwnj;ای برای نابودی بتهایتان می&zwnj;کشم!&raquo;';
$t[58] = 'سرانجام (با استفاده از یک فرصت مناسب)، همه آنها -جز بت بزرگشان- را قطعه قطعه کرد؛ شاید سراغ او بیایند (و او حقایق را بازگو کند)!';
$t[59] = '(هنگامی که منظره بتها را دیدند،) گفتند: &laquo;هر کس با خدایان ما چنین کرده، قطعاً از ستمگران است (و باید کیفر سخت ببیند)!&raquo;';
$t[60] = '(گروهی) گفتند: &laquo;شنیدیم نوجوانی از (مخالفت با) بتها سخن می&zwnj;گفت که او را ابراهیم می&zwnj;گویند.&raquo;';
$t[61] = '(جمعیّت) گفتند: &laquo;او را در برابر دیدگان مردم بیاورید، تا گواهی دهند!&raquo;';
$t[62] = '(هنگامی که ابراهیم را حاضر کردند،) گفتند: &laquo;تو این کار را با خدایان ما کرده&zwnj;ای، ای ابراهیم؟!&raquo;';
$t[63] = 'گفت: &laquo;بلکه این کار را بزرگشان کرده است! از آنها بپرسید اگر سخن می&zwnj;گویند!&raquo;';
$t[64] = 'آنها به وجدان خویش بازگشتند؛ و (به خود) گفتند: &laquo;حقّا که شما ستمگرید!&raquo;';
$t[65] = 'سپس بر سرهایشان واژگونه شدند؛ (و حکم وجدان را بکلّی فراموش کردند و گفتند:) تو می&zwnj;دانی که اینها سخن نمی&zwnj;گویند!';
$t[66] = '(ابراهیم) گفت: &laquo;آیا جز خدا چیزی را می&zwnj;پرستید که نه کمترین سودی برای شما دارد، و نه زیانی به شما می&zwnj;رساند! (نه امیدی به سودشان دارید، و نه ترسی از زیانشان!)';
$t[67] = 'اف بر شما و بر آنچه جز خدا می&zwnj;پرستید! آیا اندیشه نمی&zwnj;کنید (و عقل ندارید)؟!';
$t[68] = 'گفتند: &laquo;او را بسوزانید و خدایان خود را یاری کنید، اگر کاری از شما ساخته است!&raquo;';
$t[69] = '(سرانجام او را به آتش افکندند؛ ولی ما) گفتیم: &laquo;ای آتش! بر ابراهیم سرد و سالم باش!&raquo;';
$t[70] = 'آنها می&zwnj;خواستند ابراهیم را با این نقشه نابود کنند؛ ولی ما آنها را زیانکارترین مردم قرار دادیم!';
$t[71] = 'و او و لوط را به سرزمین (شام) -که آن را برای همه جهانیان پربرکت ساختیم- نجات دادیم!';
$t[72] = 'و اسحاق، و علاوه بر او، یعقوب را به وی بخشیدیم؛ و همه آنان را مردانی صالح قرار دادیم!';
$t[73] = 'و آنان را پیشوایانی قرار دادیم که به فرمان ما، (مردم را) هدایت می&zwnj;کردند؛ و انجام کارهای نیک و برپاداشتن نماز و ادای زکات را به آنها وحی کردیم؛ و تنها ما را عبادت می&zwnj;کردند.';
$t[74] = 'و لوط را (به یاد آور) که به او حکومت و علم دادیم؛ و از شهری که اعمال زشت و کثیف انجام می&zwnj;دادند، رهایی بخشیدیم؛ چرا که آنها مردم بد و فاسقی بودند!';
$t[75] = 'و او را در رحمت خود داخل کردیم؛ و او از صالحان بود.';
$t[76] = 'و نوح را (به یاد آور) هنگامی که پیش از آن (زمان، پروردگار خود را) خواند! ما دعای او را مستجاب کردیم؛ و او و خاندانش را از اندوه بزرگ نجات دادیم؛';
$t[77] = 'و او را در برابر جمعیّتی که آیات ما را تکذیب کرده بودند یاری دادیم؛ چرا که قوم بدی بودند؛ از این رو همه آنها را غرق کردیم!';
$t[78] = 'و داوود و سلیمان را (به خاطر بیاور) هنگامی که درباره کشتزاری که گوسفندان بی شبان قوم، شبانگاه در آن چریده (و آن را تباه کرده) بودند، داوری می&zwnj;کردند؛ و ما بر حکم آنان شاهد بودیم.';
$t[79] = 'ما (حکم واقعی) آن را به سلیمان فهماندیم؛ و به هر یک از آنان (شایستگی) داوری، و علم فراوانی دادیم؛ و کوه&zwnj;ها و پرندگان را با داوود مسخّر ساختیم، که (همراه او) تسبیح (خدا) می&zwnj;گفتند؛ و ما این کار را انجام دادیم!';
$t[80] = 'و ساختن زره را بخاطر شما به او تعلیم دادیم، تا شما را در جنگهایتان حفظ کند؛ آیا شکرگزار (این نعمتهای خدا) هستید؟';
$t[81] = 'و تندباد را مسخّر سلیمان ساختیم، که بفرمان او بسوی سرزمینی که آن را پربرکت کرده بودیم جریان می&zwnj;یافت؛ و ما از همه چیز آگاه بوده&zwnj;ایم.';
$t[82] = 'و گروهی از شیاطین (را نیز مسخّر او قرار دادیم، که در دریا) برایش غوّاصی می&zwnj;کردند؛ و کارهایی غیر از این (نیز) برای او انجام می&zwnj;دادند؛ و ما آنها را (از سرکشی) حفظ می&zwnj;کردیم!';
$t[83] = 'و ایّوب را (به یاد آور) هنگامی که پروردگارش را خواند (و عرضه داشت): &laquo;بدحالی و مشکلات به من روی آورده؛ و تو مهربانترین مهربانانی!&raquo;';
$t[84] = 'ما دعای او را مستجاب کردیم؛ و ناراحتیهایی را که داشت برطرف ساختیم؛ و خاندانش را به او بازگرداندیم؛ و همانندشان را بر آنها افزودیم؛ تا رحمتی از سوی ما و تذکّری برای عبادت&zwnj;کنندگان باشد.';
$t[85] = 'و اسماعیل و ادریس و ذاالکفل را (به یاد آور) که همه از صابران بودند.';
$t[86] = 'و ما آنان را در رحمت خود وارد ساختیم؛ چرا که آنها از صالحان بودند.';
$t[87] = 'و ذاالنون [= یونس&zwnj;] را (به یاد آور) در آن هنگام که خشمگین (از میان قوم خود) رفت؛ و چنین می&zwnj;پنداشت که ما بر او تنگ نخواهیم گرفت؛ (امّا موقعی که در کام نهنگ فرو رفت،) در آن ظلمتها (ی متراکم) صدا زد: &laquo;(خداوندا!) جز تو معبودی نیست! منزّهی تو! من از ستمکاران بودم!&raquo;';
$t[88] = 'ما دعای او را به اجابت رساندیم؛ و از آن اندوه نجاتش بخشیدیم؛ و این گونه مؤمنان را نجات می&zwnj;دهیم!';
$t[89] = 'و زکریا را (به یاد آور) در آن هنگام که پروردگارش را خواند (و عرض کرد): &laquo;پروردگار من! مرا تنها مگذار (و فرزند برومندی به من عطا کن)؛ و تو بهترین وارثانی!&raquo;';
$t[90] = 'ما هم دعای او را پذیرفتیم، و یحیی را به او بخشیدیم؛ و همسرش را (که نازا بود) برایش آماده (بارداری) کردیم؛ چرا که آنان (خاندانی بودند که) همواره در کارهای خیر بسرعت اقدام می&zwnj;کردند؛ و در حال بیم و امید ما را می&zwnj;خواندند؛ و پیوسته برای ما (خاضع و) خاشع بودند.';
$t[91] = 'و به یاد آور زنی را که دامان خود را پاک نگه داشت؛ و ما از روح خود در او دمیدیم؛ و او و فرزندش [= مسیح&zwnj;] را نشانه بزرگی برای جهانیان قرار دادیم!';
$t[92] = 'این (پیامبران بزرگ و پیروانشان) همه امّت واحدی بودند (و پیرو یک هدف)؛ و من پروردگار شما هستم؛ پس مرا پرستش کنید!';
$t[93] = '(گروهی از پیروان ناآگاه آنها) کار خود را به تفرقه در میان خود کشاندند؛ (ولی سرانجام) همگی بسوی ما بازمی&zwnj;گردند!';
$t[94] = 'و هر کس چیزی از اعمال شایسته بجا آورد، در حالی که ایمان داشته باشد، کوشش او ناسپاسی نخواهد شد؛ و ما تمام اعمال او را (برای پاداش) می&zwnj;نویسیم.';
$t[95] = 'و حرام است بر شهرها و آبادیهایی که (بر اثر گناه) نابودشان کردیم (که به دنیا بازگردند؛) آنها هرگز باز نخواهند گشت!';
$t[96] = 'تا آن زمان که &laquo;یأجوج&raquo; و &laquo;مأجوج&raquo; گشوده شوند؛ و آنها از هر محلّ مرتفعی بسرعت عبور می&zwnj;کنند.';
$t[97] = 'و وعده حقّ [= قیامت&zwnj;] نزدیک می&zwnj;شود؛ در آن هنگام چشمهای کافران از وحشت از حرکت بازمی&zwnj;ماند؛ (می&zwnj;گویند:) ای وای بر ما که از این (جریان) در غفلت بودیم؛ بلکه ما ستمکار بودیم!';
$t[98] = 'شما و آنچه غیر خدا می&zwnj;پرستید، هیزم جهنّم خواهید بود؛ و همگی در آن وارد می&zwnj;شوید.';
$t[99] = 'اگر اینها خدایانی بودند، هرگز وارد آن نمی&zwnj;شدند! در حالی که همگی در آن جاودانه خواهند بود.';
$t[100] = 'برای آنان در آن [= دوزخ&zwnj;] ناله&zwnj;های دردناکی است و چیزی نمی&zwnj;شنوند.';
$t[101] = '(امّا) کسانی که از قبل، وعده نیک از سوی ما به آنها داده شده [= مؤمنان صالح&zwnj;] از آن دور نگاهداشته می&zwnj;شوند.';
$t[102] = 'آنها صدای آتش دوزخ را نمی&zwnj;شوند؛ و در آنچه دلشان بخواهد، جاودانه متنعّم هستند.';
$t[103] = 'وحشت بزرگ، آنها را اندوهگین نمی&zwnj;کند؛ و فرشتگان به استقبالشان می&zwnj;آیند، (و می&zwnj;گویند:) این همان روزی است که به شما وعده داده می&zwnj;شد!';
$t[104] = 'در آن روز که آسمان را چون طوماری در هم می&zwnj;پیچیم، (سپس) همان گونه که آفرینش را آغاز کردیم، آن را بازمی&zwnj;گردانیم؛ این وعده&zwnj;ای است بر ما، و قطعاً آن را انجام خواهیم داد.';
$t[105] = 'در &laquo;زبور&raquo; بعد از ذکر (تورات) نوشتیم: &laquo;بندگان شایسته&zwnj;ام وارث (حکومت) زمین خواهند شد!&raquo;';
$t[106] = 'در این، ابلاغ روشنی است برای جمعیّت عبادت&zwnj;کنندگان!';
$t[107] = 'ما تو را جز برای رحمت جهانیان نفرستادیم.';
$t[108] = 'بگو: &laquo;تنها چیزی که به من وحی می&zwnj;شود این است که معبود شما خدای یگانه است؛ آیا (با این حال) تسلیم (حقّ) می&zwnj;شوید؟ (و بتها را کنار می&zwnj;گذارید؟)&raquo;';
$t[109] = 'اگر باز (روی گردان شوند، بگو: &laquo;من به همه شما یکسان اعلام خطر می&zwnj;کنم؛ و نمی&zwnj;دانم آیا وعده (عذاب خدا) که به شما داده می&zwnj;شود نزدیک است یا دور!';
$t[110] = 'او سخنان آشکار را می&zwnj;داند، و آنچه را کتمان می&zwnj;کنید (نیز) می&zwnj;داند (و چیزی بر او پوشیده نیست)!';
$t[111] = 'و من نمی&zwnj;دانم شاید این آزمایشی برای شماست؛ و مایه بهره&zwnj;گیری تا مدّتی (معیّن)!';
$t[112] = '(و پیامبر) گفت: &laquo;پروردگارا! بحق داوری فرما (و این طغیانگران را کیفر ده)! و پروردگار ما (خداوند) رحمان است که در برابر نسبتهای ناروای شما، از او استمداد می&zwnj;طلبم!&raquo;';
?>