$t[87] = 'بزودی خواهند گفت: &laquo;همه اینها از آن خداست!&raquo; بگو: &laquo;آیا تقوا پیشه نمی&zwnj;کنید (و از خدا نمی&zwnj;ترسید و دست از شرک برنمی&zwnj;دارید)؟!&raquo;';
$t[88] = 'بگو: &laquo;اگر می&zwnj;دانید، چه کسی حکومت همه موجودات را در دست دارد، و به بی پناهان پناه می&zwnj;دهد، و نیاز به پناه&zwnj;دادن ندارد؟!&raquo;';
$t[89] = 'خواهند گفت: &laquo;(همه اینها) از آن خداست&raquo; بگو: &laquo;با این حال چگونه می&zwnj;گویید سحر شده&zwnj;اید (و این سخنان سحر و افسون است)؟!&raquo;';
$t[90] = 'نه، (واقع این است که) ما حقّ را برای آنها آوردیم؛ و آنان دروغ می&zwnj;گویند!';
$t[91] = 'خدا هرگز فرزندی برای خود انتخاب نکرده؛ و معبود دیگری با او نیست؛ که اگر چنین می&zwnj;شد، هر یک از خدایان مخلوقات خود را تدبیر و اداره می&zwnj;کردند و بعضی بر بعضی دیگر برتری می&zwnj;جستند (و جهان هستی به تباهی کشیده می&zwnj;شد)؛ منزّه است خدا از آنچه آنان توصیف می&zwnj;کنند!';
$t[92] = 'او دانای نهان و آشکار است؛ پس برتر است از آنچه برای او همتا قرار می&zwnj;دهند!';
$t[93] = 'بگو: &laquo;پروردگار من! اگر عذابهایی را که به آنان وعده داده می&zwnj;شود به من نشان دهی (و در زندگیم آن را ببینم)...';
$t[94] = 'پروردگار من! مرا (در این عذابها) با گروه ستمگران قرار مده!&raquo;';
$t[95] = 'و ما تواناییم که آنچه را به آنها وعده می&zwnj;دهیم به تو نشان دهیم!';
$t[96] = 'بدی را به بهترین راه و روش دفع کن (و پاسخ بدی را به نیکی ده)! ما به آنچه توصیف می&zwnj;کنند آگاهتریم!';
$t[97] = 'و بگو: &laquo;پروردگارا! از وسوسه&zwnj;های شیاطین به تو پناه می&zwnj;برم!';
$t[98] = 'و از اینکه آنان نزد من حاضر شوند (نیز) -ای پروردگار من- به تو پناه می&zwnj;برم!&raquo;';
$t[99] = '(آنها همچنان به راه غلط خود ادامه می&zwnj;دهند) تا زمانی که مرگ یکی از آنان فرارسد، می&zwnj;گوید: &laquo;پروردگار من! مرا بازگردانید!';
$t[100] = 'شاید در آنچه ترک کردم (و کوتاهی نمودم) عمل صالحی انجام دهم!&raquo; (ولی به او می&zwnj;گویند:) چنین نیست! این سخنی است که او به زبان می&zwnj;گوید (و اگر بازگردد، کارش همچون گذشته است)! و پشت سر آنان برزخی است تا روزی که برانگیخته شوند!';
$t[101] = 'هنگامی که در &laquo;صور&raquo; دمیده شود، هیچ یک از پیوندهای خویشاوندی میان آنها در آن روز نخواهد بود؛ و از یکدیگر تقاضای کمک نمی&zwnj;کنند (چون کاری از کسی ساخته نیست)!';
$t[102] = 'و کسانی که وزنه اعمالشان سنگین است، همان رستگارانند!';
$t[103] = 'و آنان که وزنه اعمالشان سبک باشد، کسانی هستند که سرمایه وجود خود را از دست داده، در جهنم جاودانه خواهند ماند!';
$t[104] = 'شعله&zwnj;های سوزان آتش همچون شمشیر به صورتهایشان نواخته می&zwnj;شود؛ و در دوزخ چهره&zwnj;ای عبوس دارند.';
$t[105] = '(به آنها گفته می&zwnj;شود:) آیا آیات من بر شما خوانده نمی&zwnj;شد، پس آن را تکذیب می&zwnj;کردید؟!';
$t[106] = 'می&zwnj;گویند: &laquo;پروردگارا! بدبختی ما بر ما چیره شد، و ما قوم گمراهی بودیم!';
$t[107] = 'پروردگارا! ما را از این (دوزخ) بیرون آر، اگر بار دیگر تکرار کردیم قطعاً ستمگریم (و مستحق عذاب)!&raquo;';
$t[108] = '(خداوند) می&zwnj;گوید: &laquo;دور شوید در دوزخ، و با من سخن مگویید!';
$t[109] = '(فراموش کرده&zwnj;اید) گروهی از بندگانم می&zwnj;گفتند: پروردگارا! ما ایمان آوردیم؛ ما را ببخش و بر ما رحم کن؛ و تو بهترین رحم کنندگانی!';
$t[110] = 'امّا شما آنها را به باد مسخره گرفتید تا شما را از یاد من غافل کردند؛ و شما به آنان می&zwnj;خندیدید!';
$t[111] = 'ولی من امروز آنها را بخاطر صبر و استقامتشان پاداش دادم؛ آنها پیروز و رستگارند!&raquo;';
$t[112] = '(خداوند) می&zwnj;گوید: &laquo;چند سال در روی زمین توقّف کردید؟&raquo;';
$t[113] = '(در پاسخ) می&zwnj;گویند: &laquo;تنها به اندازه یک روز، یا قسمتی از یک روز! از آنها که می&zwnj;توانند بشمارند بپرس!&raquo;';
$t[114] = 'می&zwnj;گوید: &laquo;(آری،) شما مقدار کمی توقّف نمودید اگر می&zwnj;دانستید!&raquo;';
$t[115] = 'آیا گمان کردید شما را بیهوده آفریده&zwnj;ایم، و بسوی ما باز نمی&zwnj;گردید؟';
$t[116] = 'پس برتر است خداوندی که فرمانروای حقّ است (از اینکه شما را بی&zwnj;هدف آفریده باشد)! معبودی جز او نیست؛ و او پروردگار عرش کریم است!';
$t[117] = 'و هر کس معبود دیگری را با خدا بخواند -و مسلّماً هیچ دلیلی بر آن نخواهد داشت- حساب او نزد پروردگارش خواهد بود؛ یقیناً کافران رستگار نخواهند شد!';
$t[118] = 'و بگو: &laquo;پروردگارا! مرا ببخش و رحمت کن؛ و تو بهترین رحم کنندگانی!';
?>