<?php
$t = array();
$t[1] = '(این) سوره&zwnj;ای است که آن را فرو فرستادیم، و (عمل به آن را) واجب نمودیم، و در آن آیات روشنی نازل کردیم، شاید شما متذکّر شوید!';
$t[2] = 'هر یک از زن و مرد زناکار را صد تازیانه بزنید؛ و نباید رأفت (و محبّت کاذب) نسبت به آن دو شما را از اجرای حکم الهی مانع شود، اگر به خدا و روز جزا ایمان دارید! و باید گروهی از مؤمنان مجازاتشان را مشاهده کنند!';
$t[3] = 'مرد زناکار جز با زن زناکار یا مشرک ازدواج نمی&zwnj;کند؛ و زن زناکار را، جز مرد زناکار یا مشرک، به ازدواج خود درنمی&zwnj;آورد؛ و این کار بر مؤمنان حرام شده است!';
$t[4] = 'و کسانی که زنان پاکدامن را متهمّ می&zwnj;کنند، سپس چهار شاهد (بر مدّعای خود) نمی&zwnj;آورند، آنها را هشتاد تازیانه بزنید و شهادتشان را هرگز نپذیرید؛ و آنها همان فاسقانند!';
$t[5] = 'مگر کسانی که بعد از آن توبه کنند و جبران نمایند (که خداوند آنها را می&zwnj;بخشد) زیرا خداوند آمرزنده و مهربان است.';
$t[6] = 'و کسانی که همسران خود را (به عمل منافی عفّت) متهمّ می&zwnj;کنند، و گواهانی جز خودشان ندارند، هر یک از آنها باید چهار مرتبه به نام خدا شهادت دهد که از راستگویان است؛';
$t[7] = 'و در پنجمین بار بگوید که لعنت خدا بر او باد اگر از دروغگویان باشد.';
$t[8] = 'آن زن نیز می&zwnj;تواند کیفر (زنا) را از خود دور کند، به این طریق که چهار بار خدا را به شهادت طلبد که آن مرد (در این نسبتی که به او می&zwnj;دهد) از دروغگویان است.';
$t[9] = 'و بار پنجم بگوید که غضب خدا بر او باد اگر آن مرد از راستگویان باشد.';
$t[10] = 'و اگر فضل و رحمت خدا شامل حال شما نبود و اینکه او توبه&zwnj;پذیر و حکیم است (بسیاری از شما گرفتار مجازات سخت الهی می&zwnj;شدید)!';
$t[11] = 'مسلّماً کسانی که آن تهمت عظیم را عنوان کردند گروهی (متشکّل و توطئه&zwnj;گر) از شما بودند؛ امّا گمان نکنید این ماجرا برای شما بد است، بلکه خیر شما در آن است؛ آنها هر کدام سهم خود را از این گناهی که مرتکب شدند دارند؛ و از آنان کسی که بخش مهمّ آن را بر عهده داشت عذاب عظیمی برای اوست!';
$t[12] = 'چرا هنگامی که این (تهمت) را شنیدید، مردان و زنان با ایمان نسبت به خود (و کسی که همچون خود آنها بود) گمان خیر نبردند و نگفتند این دروغی بزرگ و آشکار است؟!';
$t[13] = 'چرا چهار شاهد برای آن نیاوردند؟! اکنون که این گواهان را نیاوردند، آنان در پیشگاه خدا دروغگویانند!';
$t[14] = 'و اگر فضل و رحمت الهی در دنیا و آخرت شامل شما نمی&zwnj;شد، بخاطر این گناهی که کردید عذاب سختی به شما می&zwnj;رسید!';
$t[15] = 'به خاطر بیاورید زمانی را که این شایعه را از زبان یکدیگر می&zwnj;گرفتید، و با دهان خود سخنی می&zwnj;گفتید که به آن یقین نداشتید؛ و آن را کوچک می&zwnj;پنداشتید در حالی که نزد خدا بزرگ است!';
$t[16] = 'چرا هنگامی که آن را شنیدید نگفتید: &laquo;ما حق نداریم که به این سخن تکلّم کنیم؛ خداوندا منزّهی تو، این بهتان بزرگی است&raquo;؟!';
$t[17] = 'خداوند شما را اندرز می&zwnj;دهد که هرگز چنین کاری را تکرار نکنید اگر ایمان دارید!';
$t[18] = 'و خداوند آیات را برای شما بیان می&zwnj;کند، و خدا دانا و حکیم است.';
$t[19] = 'کسانی که دوست دارند زشتیها در میان مردم با ایمان شیوع یابد، عذاب دردناکی برای آنان در دنیا و آخرت است؛ و خداوند می&zwnj;داند و شما نمی&zwnj;دانید!';
$t[20] = 'و اگر فضل و رحمت الهی شامل حال شما نبود و اینکه خدا مهربان و رحیم است (مجازات سختی دامانتان را می&zwnj;گرفت)!';
$t[21] = 'ای کسانی که ایمان آورده&zwnj;اید! از گامهای شیطان پیروی نکنید! هر کس پیرو شیطان شود (گمراهش می&zwnj;سازد، زیرا) او به فحشا و منکر فرمان می&zwnj;دهد! و اگر فضل و رحمت الهی بر شما نبود، هرگز احدی از شما پاک نمی&zwnj;شد؛ ولی خداوند هر که را بخواهد تزکیه می&zwnj;کند، و خدا شنوا و داناست!';
$t[22] = 'آنها که از میان شما دارای برتری (مالی) و وسعت زندگی هستند نباید سوگند یاد کنند که از انفاق نسبت به نزدیکان و مستمندان و مهاجران در راه خدا دریغ نمایند؛ آنها باید عفو کنند و چشم بپوشند؛ آیا دوست نمی&zwnj;دارید خداوند شما را ببخشد؟! و خداوند آمرزنده و مهربان است!';
$t[23] = 'کسانی که زنان پاکدامن و بی&zwnj;خبر (از هرگونه آلودگی) و مؤمن را متهم می&zwnj;سازند، در دنیا و آخرت از رحمت الهی بدورند و عذاب بزرگی برای آنهاست.';
$t[24] = 'در آن روز زبانها و دستها و پاهایشان بر ضدّ آنها به اعمالی که مرتکب می&zwnj;شدند گواهی می&zwnj;دهد!';
$t[25] = 'آن روز، خداوند جزای واقعی آنان را بی&zwnj;کم و کاست می&zwnj;دهد؛ و می&zwnj;دانند که خداوند حق آشکار است!';
$t[26] = 'زنان ناپاک از آن مردان ناپاکند، و مردان ناپاک نیز به زنان ناپاک تعلّق دارند؛ و زنان پاک از آن مردان پاک، و مردان پاک از آن زنان پاکند! اینان از نسبتهای ناروایی که (ناپاکان) به آنان می&zwnj;دهند مبرّا هستند؛ و برای آنان آمرزش (الهی) و روزی پرارزشی است!';
$t[27] = 'ای کسانی که ایمان آورده&zwnj;اید! در خانه&zwnj;هایی غیر از خانه خود وارد نشوید تا اجازه بگیرید و بر اهل آن خانه سلام کنید؛ این برای شما بهتر است؛ شاید متذکّر شوید!';
$t[28] = 'و اگر کسی را در آن نیافتید، وارد نشوید تا به شما اجازه داده شود؛ و اگر گفته شد: &laquo;بازگردید!&raquo; بازگردید؛ این برای شما پاکیزه&zwnj;تر است؛ و خداوند به آنچه انجام می&zwnj;دهید آگاه است!';
$t[29] = '(ولی) گناهی بر شما نیست که وارد خانه&zwnj;های غیر مسکونی بشوید که در آن متاعی متعلّق به شما وجود دارد؛ و خدا آنچه را آشکار می&zwnj;کنید و آنچه را پنهان می&zwnj;دارید، می&zwnj;داند!';
$t[30] = 'به مؤمنان بگو چشمهای خود را (از نگاه به نامحرمان) فروگیرند، و عفاف خود را حفظ کنند؛ این برای آنان پاکیزه&zwnj;تر است؛ خداوند از آنچه انجام می&zwnj;دهید آگاه است!';
$t[31] = 'و به زنان با ایمان بگو چشمهای خود را (از نگاه هوس&zwnj;آلود) فروگیرند، و دامان خویش را حفظ کنند و زینت خود را -جز آن مقدار که نمایان است- آشکار ننمایند و (اطراف) روسری&zwnj;های خود را بر سینه خود افکنند (تا گردن و سینه با آن پوشانده شود)، و زینت خود را آشکار نسازند مگر برای شوهرانشان، یا پدرانشان، یا پدر شوهرانشان، یا پسرانشان، یا پسران همسرانشان، یا برادرانشان، یا پسران برادرانشان، یا پسران خواهرانشان، یا زنان هم&zwnj;کیششان، یا بردگانشان [=کنیزانشان&zwnj;]، یا افراد سفیه که تمایلی به زن ندارند، یا کودکانی که از امور جنسی مربوط به زنان آگاه نیستند؛ و هنگام راه رفتن پاهای خود را به زمین نزنند تا زینت پنهانیشان دانسته شود (و صدای خلخال که برپا دارند به گوش رسد). و همگی بسوی خدا بازگردید ای مؤمنان، تا رستگار شوید!';
$t[32] = 'مردان و زنان بی&zwnj;همسر خود را همسر دهید، همچنین غلامان و کنیزان صالح و درستکارتان را؛ اگر فقیر و تنگدست باشند، خداوند از فضل خود آنان را بی&zwnj;نیاز می&zwnj;سازد؛ خداوند گشایش&zwnj;دهنده و آگاه است!';
$t[33] = 'و کسانی که امکانی برای ازدواج نمی&zwnj;یابند، باید پاکدامنی پیشه کنند تا خداوند از فضل خود آنان را بی&zwnj;نیاز گرداند! و آن بردگانتان که خواستار مکاتبه [=قرار داد مخصوص برای آزاد شدن&zwnj;] هستند، با آنان قرار داد ببندید اگر رشد و صلاح در آنان احساس می&zwnj;کنید (که بعد از آزادی، توانایی زندگی مستقل را دارند)؛ و چیزی از مال خدا را که به شما داده است به آنان بدهید! و کنیزان خود را برای دستیابی متاع ناپایدار زندگی دنیا مجبور به خود فروشی نکنید اگر خودشان می&zwnj;خواهند پاک بمانند! و هر کس آنها را (بر این کار) اجبار کند، (سپس پشیمان گردد،) خداوند بعد از این اجبار آنها غفور و رحیم است! (توبه کنید و بازگردید، تا خدا شما را ببخشد!)';
$t[34] = 'ما بر شما آیاتی فرستادیم که حقایق بسیاری را تبیین می&zwnj;کند، و اخباری از کسانی که پیش از شما بودند، و موعظه و اندرزی برای پرهیزگاران!';
$t[35] = 'خداوند نور آسمانها و زمین است؛ مثل نور خداوند همانند چراغدانی است که در آن چراغی (پر فروغ) باشد، آن چراغ در حبابی قرار گیرد، حبابی شفاف و درخشنده همچون یک ستاره فروزان، این چراغ با روغنی افروخته می&zwnj;شود که از درخت پربرکت زیتونی گرفته شده که نه شرقی است و نه غربی؛ (روغنش آنچنان صاف و خالص است که) نزدیک است بدون تماس با آتش شعله&zwnj;ور شود؛ نوری است بر فراز نوری؛ و خدا هر کس را بخواهد به نور خود هدایت می&zwnj;کند، و خداوند به هر چیزی داناست.';
$t[36] = '(این چراغ پرفروغ) در خانه&zwnj;هایی قرار دارد که خداوند اذن فرموده دیوارهای آن را بالا برند (تا از دستبرد شیاطین و هوسبازان در امان باشد)؛ خانه&zwnj;هایی که نام خدا در آنها برده می&zwnj;شود، و صبح و شام در آنها تسبیح او می&zwnj;گویند...';
$t[37] = 'مردانی که نه تجارت و نه معامله&zwnj;ای آنان را از یاد خدا و برپاداشتن نماز و ادای زکات غافل نمی&zwnj;کند؛ آنها از روزی می&zwnj;ترسند که در آن، دلها و چشمها زیر و رو می&zwnj;شود.';
$t[38] = '(آنها به سراغ این کارها می&zwnj;روند) تا خداوند آنان را به بهترین اعمالی که انجام داده&zwnj;اند پاداش دهد، و از فضل خود بر پاداششان بیفزاید؛ و خداوند به هر کس بخواهد بی حساب روزی می&zwnj;دهد (و از مواهب بی&zwnj;انتهای خویش بهره&zwnj;مند می&zwnj;سازد).';
$t[39] = 'کسانی که کافر شدند، اعمالشان همچون سرابی است در یک کویر که انسان تشنه از دور آن را آب می&zwnj;پندارد؛ امّا هنگامی که به سراغ آن می&zwnj;آید چیزی نمی&zwnj;یابد، و خدا را نزد آن می&zwnj;یابد که حساب او را بطور کامل می&zwnj;دهد؛ و خداوند سریع الحساب است!';
$t[40] = 'یا همچون ظلماتی در یک دریای عمیق و پهناور که موج آن را پوشانده، و بر فراز آن موج دیگری، و بر فراز آن ابری تاریک است؛ ظلمتهایی است یکی بر فراز دیگری، آن گونه که هر گاه دست خود را خارج کند ممکن نیست آن را ببیند! و کسی که خدا نوری برای او قرار نداده، نوری برای او نیست!';
$t[41] = 'آیا ندیدی تمام آنان که در آسمانها و زمینند برای خدا تسبیح می&zwnj;کنند، و همچنین پرندگان به هنگامی که بر فراز آسمان بال گسترده&zwnj;اند؟! هر یک از آنها نماز و تسبیح خود را می&zwnj;داند؛ و خداوند به آنچه انجام میدهند داناست!';
$t[42] = 'و از برای خداست حکومت و مالکیّت آسمانها و زمین؛ و بازگشت (تمامی موجودات) بسوی اوست!';
$t[43] = 'آیا ندیدی که خداوند ابرهایی را به آرامی می&zwnj;راند، سپس میان آنها پیوند می&zwnj;دهد، و بعد آن را متراکم می&zwnj;سازد؟! در این حال، دانه&zwnj;های باران را می&zwnj;بینی که از لابه&zwnj;لای آن خارج می&zwnj;شود؛ و از آسمان -از کوه&zwnj;هایی که در آن است [=ابرهایی که همچون کوه&zwnj;ها انباشته شده&zwnj;اند]- دانه&zwnj;های تگرگ نازل می&zwnj;کند، و هر کس را بخواهد بوسیله آن زیان می&zwnj;رساند، و از هر کس بخواهد این زیان را برطرف می&zwnj;کند؛ نزدیک است درخشندگی برق آن (ابرها) چشمها را ببرد!';
$t[44] = 'خداوند شب و روز را دگرگون می&zwnj;سازد؛ در این عبرتی است برای صاحبان بصیرت!';
$t[45] = 'و خداوند هر جنبنده&zwnj;ای را از آبی آفرید؛ گروهی از آنها بر شکم خود راه می&zwnj;روند، و گروهی بر دو پای خود، و گروهی بر چهار پا راه می&zwnj;روند؛ خداوند هر چه را بخواهد می&zwnj;آفریند، زیرا خدا بر همه چیز تواناست!';
$t[46] = 'ما آیات روشنگری نازل کردیم؛ و خدا هر که را بخواهد به صراط مستقیم هدایت می&zwnj;کند!';
$t[47] = 'آنها می&zwnj;گویند: &laquo;به خدا و پیامبر ایمان داریم و اطاعت می&zwnj;کنیم!&raquo; ولی بعد از این ادّعا، گروهی از آنان رویگردان می&zwnj;شوند؛ آنها (در حقیقت) مؤمن نیستند!';
$t[48] = 'و هنگامی که از آنان دعوت شود که بسوی خدا و پیامبرش بیایند تا در میانشان داوری کند، ناگهان گروهی از آنان رویگردان می&zwnj;شوند!';
$t[49] = 'ولی اگر حق داشته باشند (و داوری به نفع آنان شود) با سرعت و تسلیم بسوی او می&zwnj;آیند!';
$t[50] = 'آیا در دلهای آنان بیماری است، یا شکّ و تردید دارند، یا می&zwnj;ترسند خدا و رسولش بر آنان ستم کنند؟! نه، بلکه آنها خودشان ستمگرند!';
$t[51] = 'سخن مؤمنان، هنگامی که بسوی خدا و رسولش دعوت شوند تا میان آنان داوری کند، تنها این است که می&zwnj;گویند: &laquo;شنیدیم و اطاعت کردیم!&raquo; و اینها همان رستگاران واقعی هستند.';
$t[52] = 'و هر کس خدا و پیامبرش را اطاعت کند، و از خدا بترسد و از مخالفت فرمانش بپرهیزد، چنین کسانی همان پیروزمندان واقعی هستند!';
$t[53] = 'آنها با نهایت تأکید سوگند یاد کردند که اگر به آنان فرمان دهی، (از خانه و اموال خود) بیرون می&zwnj;روند (و جان را در طبق اخلاص گذارده تقدیم می&zwnj;کنند)؛ بگو: &laquo;سوگند یاد نکنید؛ شما طاعت خالصانه نشان دهید که خداوند به آنچه انجام می&zwnj;دهید آگاه است!&raquo;';
$t[54] = 'بگو: &laquo;خدا را اطاعت کنید، و از پیامبرش فرمان برید! و اگر سرپیچی نمایید، پیامبر مسؤول اعمال خویش است و شما مسؤول اعمال خود! امّا اگر از او اطاعت کنید، هدایت خواهید شد؛ و بر پیامبر چیزی جز رساندن آشکار نیست!&raquo;';
$t[55] = 'خداوند به کسانی از شما که ایمان آورده و کارهای شایسته انجام داده&zwnj;اند وعده می&zwnj;دهد که قطعاً آنان را حکمران روی زمین خواهد کرد، همان گونه که به پیشینیان آنها خلافت روی زمین را بخشید؛ و دین و آیینی را که برای آنان پسندیده، پابرجا و ریشه&zwnj;دار خواهد ساخت؛ و ترسشان را به امنیّت و آرامش مبدّل می&zwnj;کند، آنچنان که تنها مرا می پرستند و چیزی را شریک من نخواهند ساخت. و کسانی که پس از آن کافر شوند، آنها فاسقانند.';
$t[56] = 'و نماز را برپا دارید، و زکات را بدهید، و رسول (خدا) را اطاعت کنید تا مشمول رحمت (او) شوید.';
$t[57] = 'گمان مبر کافران می&zwnj;توانند از چنگال مجازات الهی در زمین فرار کنند! جایگاه آنان آتش است، و چه بد جایگاهی است!';
$t[58] = 'ای کسانی که ایمان آورده&zwnj;اید! بردگان شما، و همچنین کودکانتان که به حدّ بلوغ نرسیده&zwnj;اند، در سه وقت باید از شما اجازه بگیرند: پیش از نماز صبح، و نیمروز هنگامی که لباسهای (معمولی) خود را بیرون می&zwnj;آورید، و بعد از نماز عشا؛ این سه وقت خصوصی برای شماست؛ امّا بعد از این سه وقت، گناهی بر شما و بر آنان نیست (که بدون اذن وارد شوند) و بر گرد یکدیگر بگردید (و با صفا و صمیمیّت به یکدیگر خدمت نمایید). این گونه خداوند آیات را برای شما بیان می&zwnj;کند، و خداوند دانا و حکیم است!';
$t[59] = 'و هنگامی که اطفال شما به سنّ بلوغ رسند باید اجازه بگیرند، همان گونه که اشخاصی که پیش از آنان بودند اجازه می&zwnj;گرفتند؛ اینچنین خداوند آیاتش را برای شما بیان می&zwnj;کند، و خدا دانا و حکیم است!';
$t[60] = 'و زنان از کارافتاده&zwnj;ای که امید به ازدواج ندارند، گناهی بر آنان نیست که لباسهای (رویین) خود را بر زمین بگذارند، بشرط اینکه در برابر مردم خودآرایی نکنند؛ و اگر خود را بپوشانند برای آنان بهتر است؛ و خداوند شنوا و داناست.';
$t[61] = 'بر نابینا و افراد لنگ و بیمار گناهی نیست (که با شما هم غذا شوند)، و بر شما نیز گناهی نیست که از خانه&zwnj;های خودتان [=خانه&zwnj;های فرزندان یا همسرانتان که خانه خود شما محسوب می&zwnj;شود بدون اجازه خاصّی&zwnj;] غذا بخورید؛ و همچنین خانه&zwnj;های پدرانتان، یا خانه&zwnj;های مادرانتان، یا خانه&zwnj;های برادرانتان، یا خانه&zwnj;های خواهرانتان، یا خانه&zwnj;های عموهایتان، یا خانه&zwnj;های عمّه&zwnj;هایتان، یا خانه&zwnj;های داییهایتان، یا خانه&zwnj;های خاله&zwnj;هایتان، یا خانه&zwnj;ای که کلیدش در اختیار شماست، یا خانه&zwnj;های دوستانتان، بر شما گناهی نیست که بطور دسته&zwnj;جمعی یا جداگانه غذا بخورید؛ و هنگامی که داخل خانه&zwnj;ای شدید، بر خویشتن سلام کنید، سلام و تحیّتی از سوی خداوند، سلامی پربرکت و پاکیزه! این گونه خداوند آیات را برای شما روشن می&zwnj;کند، باشد که بیندیشید!';
$t[62] = 'مؤمنان واقعی کسانی هستند که به خدا و رسولش ایمان آورده&zwnj;اند و هنگامی که در کار مهمّی با او باشند، بی&zwnj;اجازه او جایی نمی&zwnj;روند؛ کسانی که از تو اجازه می&zwnj;گیرند، براستی به خدا و پیامبرش ایمان آورده&zwnj;اند! در این صورت، هر گاه برای بعضی کارهای مهمّ خود از تو اجازه بخواهند، به هر یک از آنان که می&zwnj;خواهی (و صلاح می&zwnj;بینی) اجازه ده، و برایشان از خدا آمرزش بخواه که خداوند آمرزنده و مهربان است!';
$t[63] = 'صدا کردن پیامبر را در میان خود، مانند صدا کردن یکدیگر قرار ندهی؛ خداوند کسانی از شما را که پشت سر دیگران پنهان می&zwnj;شوند، و یکی پس از دیگری فرار می&zwnj;کنند می&zwnj;داند! پس آنان که فرمان او را مخالفت می&zwnj;کنند، باید بترسند از اینکه فتنه&zwnj;ای دامنشان را بگیرد، یا عذابی دردناک به آنها برسد!';
$t[64] = 'آگاه باشید که برای خداست آنچه در آسمانها و زمین است؛ او می&zwnj;داند آنچه را که شما بر آن هستید، و (می&zwnj;داند) روزی را که بسوی او بازمی&zwnj;گردند؛ و (در آن روز) آنها را از اعمالی که انجام دادند آگاه می&zwnj;سازد؛ و خداوند به هر چیزی داناست!';
?>