<?php
$t = array();
$t[1] = 'زوال ناپذیر و پر برکت است کسی که قرآن را بر بنده&zwnj;اش نازل کرد تا بیم&zwnj;دهنده جهانیان باشد.';
$t[2] = 'خداوندی که حکومت آسمانها و زمین از آن اوست، و فرزندی برای خود انتخاب نکرد، و همتایی در حکومت و مالکیّت ندارد، و همه چیز را آفرید، و به دقّت اندازه&zwnj;گیری نمود!';
$t[3] = 'آنان غیر از خداوند معبودانی برای خود برگزیدند؛ معبودانی که چیزی را نمی&zwnj;آفرینند، بلکه خودشان مخلوقند، و مالک زیان و سود خویش نیستند، و نه مالک مرگ و حیات و رستاخیز خویشند.';
$t[4] = 'و کافران گفتند: &laquo;این فقط دروغی است که او ساخته، و گروهی دیگر او را بر این کار یاری داده&zwnj;اند.&raquo; آنها (با این سخن،) ظلم و دروغ بزرگی را مرتکب شدند.';
$t[5] = 'و گفتند: &laquo;این همان افسانه&zwnj;های پیشینیان است که وی آن را رونویس کرده، و هر صبح و شام بر او املا می&zwnj;شود.&raquo;';
$t[6] = 'بگو: &laquo;کسی آن را نازل کرده که اسرار آسمانها و زمین را می&zwnj;داند؛ او (همیشه) آمرزنده و مهربان بوده است!&raquo;';
$t[7] = 'و گفتند: &laquo;چرا این پیامبر غذا می&zwnj;خورد و در بازارها راه می&zwnj;رود؟! (نه سنّت فرشتگان را دارد و نه روش شاهان را!) چرا فرشته&zwnj;ای بر او نازل نشده که همراه وی مردم را انذار کند (و گواه صدق دعوی او باشد)؟!';
$t[8] = 'یا گنجی (از آسمان) برای او فرستاده شود، یا باغی داشته باشد که از (میوه) آن بخورد (و امرار معاش کند)؟!&raquo; و ستمگران گفتند: &laquo;شما تنها از مردی مجنون پیروی می&zwnj;کنید!&raquo;';
$t[9] = 'ببین چگونه برای تو مثلها زدند و گمراه شدند، آن گونه که قدرت پیدا کردن راه را ندارند!';
$t[10] = 'زوال ناپذیر و بزرگ است خدایی که اگر بخواهد برای تو بهتر از این قرار می&zwnj;دهد: باغهایی که نهرها از زیر درختانش جاری است، و (اگر بخواهد) برای تو کاخهایی مجلّل قرارمی&zwnj;دهد.';
$t[11] = '(اینها همه بهانه است) بلکه آنان قیامت را تکذیب کرده&zwnj;اند؛ و ما برای کسی که قیامت را تکذیب کند، آتشی شعله&zwnj;ور و سوزان فراهم کرده&zwnj;ایم!';
$t[12] = 'هنگامی که این آتش آنان را از مکانی دور ببیند، صدای وحشتناک و خشم آلودش را که با نفس&zwnj;زدن شدید همراه است می&zwnj;شنوند.';
$t[13] = 'و هنگامی که در جای تنگ و محدودی از آن افکنده شوند در حالی که در غل و زنجیرند، فریاد واویلای آنان بلند می&zwnj;شود!';
$t[14] = '(به آنان گفته می&zwnj;شود:) امروز یک بار واویلا نگویید، بلکه بسیار واویلا بگویید!';
$t[15] = '(ای پیامبر!) بگو: &laquo;آیا این بهتر است یا بهشت جاویدانی که به پرهیزگاران وعده داده شده؟! بهشتی که پاداش اعمال آنها، و قرارگاهشان است.&raquo;';
$t[16] = 'هر چه بخواهند در آنجا برایشان فراهم است؛ جاودانه در آن خواهند ماند؛ این وعده&zwnj;ای است مسلّم که پروردگارت بر عهده گرفته است!';
$t[17] = '(به خاطر بیاور) روزی را که همه آنان و معبودهایی را که غیر از خدا می&zwnj;پرستند جمع می&zwnj;کند، آنگاه به آنها می&zwnj;گوید: &laquo;آیا شما این بندگان مرا گمراه کردید یا خود آنان راه را گم کردند؟!&raquo;';
$t[18] = '(در پاسخ) می&zwnj;گویند: &laquo;منزّهی تو! برای ما شایسته نبود که غیر از تو اولیایی برگزینیم، ولی آنان و پدرانشان را از نعمتها برخوردار نمودی تا اینکه (به جای شکر نعمت) یاد تو را فراموش کردند و تباه و هلاک شدند.&raquo;';
$t[19] = '(خداوند به آنان می&zwnj;گوید: ببینید) این معبودان، شما را در آنچه می&zwnj;گویید تکذیب کردند! اکنون نمی&zwnj;توانید عذاب الهی را برطرف بسازید، یا از کسی یاری بطلبید! و هر کس از شما ستم کند، عذاب شدیدی به او می&zwnj;چشانیم!';
$t[20] = 'ما هیچ یک از رسولان را پیش از تو نفرستادیم مگر اینکه غذا می&zwnj;خوردند و در بازارها راه می&zwnj;رفتند؛ و بعضی از شما را وسیله امتحان بعضی دیگر قراردادیم، آیا صبر و شکیبایی می&zwnj;کنید (و از عهده امتحان برمی&zwnj;آیید)؟! و پروردگار تو همواره بصیر و بینا بوده است.';
$t[21] = 'و کسانی که امیدی به دیدار ما ندارند (و رستاخیز را انکار می&zwnj;کنند) گفتند: &laquo;چرا فرشتگان بر ما نازل نشدند و یا پروردگارمان را با چشم خود نمی&zwnj;بینیم؟!&raquo; آنها درباره خود تکبّر ورزیدند و طغیان بزرگی کردند!';
$t[22] = '(آنها به آرزوی خود می&zwnj;رسند،) امّا روزی که فرشتگان را می&zwnj;بینند، روز بشارت برای مجرمان نخواهد بود (بلکه روز مجازات و کیفر آنان است)! و می&zwnj;گویند: &laquo;ما را امان دهید، ما را معاف دارید!&raquo; (امّا سودی ندارد!)';
$t[23] = 'و ما به سراغ اعمالی که انجام داده&zwnj;اند می&zwnj;رویم، و همه را همچون ذرّات غبار پراکنده در هوا قرار می&zwnj;دهیم!';
$t[24] = 'بهشتیان در آن روز قرارگاهشان از همه بهتر، و استراحتگاهشان نیکوتر است!';
$t[25] = 'و (بخاطر آور) روزی را که آسمان با ابرها شکافته می&zwnj;شود، و فرشتگان نازل می&zwnj;گردند.';
$t[26] = 'حکومت در آن روز از آن خداوند رحمان است؛ و آن روز، روز سختی برای کافران خواهد بود!';
$t[27] = 'و (به خاطر آور) روزی را که ستمکار دست خود را (از شدّت حسرت) به دندان می&zwnj;گزد و می&zwnj;گوید: &laquo;ای کاش با رسول (خدا) راهی برگزیده بودم!';
$t[28] = 'ای وای بر من، کاش فلان (شخص گمراه) را دوست خود انتخاب نکرده بودم!';
$t[29] = 'او مرا از یادآوری (حق) گمراه ساخت بعد از آنکه (یاد حق) به سراغ من آمده بود!&raquo; و شیطان همیشه خوارکنند* انسان بوده است!';
$t[30] = 'و پیامبر عرضه داشت: &laquo;پروردگارا! قوم من قرآن را رها کردند&raquo;.';
$t[31] = '(آری،) این گونه برای هر پیامبری دشمنی از مجرمان قرار دادیم؛ امّا (برای تو) همین بس که پروردگارت هادی و یاور (تو) باشد!';
$t[32] = 'و کافران گفتند: &laquo;چرا قرآن یکجا بر او نازل نمی&zwnj;شود؟!&raquo; این بخاطر آن است که قلب تو را بوسیله آن محکم داریم، و (از این رو) آن را به تدریج بر تو خواندیم.';
$t[33] = 'آنان هیچ مثلی برای تو نمی&zwnj;آورند مگر اینکه ما حق را برای تو می&zwnj;آوریم، و تفسیری بهتر (و پاسخی دندان شکن که در برابر آن ناتوان شوند)!';
$t[34] = '(تو گمراه نیستی،) آنان که بر صورتهایشان بسوی جهنم محشور می&zwnj;شوند، بدترین محل را دارند و گمراه&zwnj;ترین افرادند!';
$t[35] = 'و ما به موسی کتاب (آسمانی) دادیم؛ و برادرش هارون را یاور او قرار دادیم؛';
$t[36] = 'و گفتیم: &laquo;به سوی این قوم که آیات ما را تکذیب کردند بروید!&raquo; (امّا آن مردم به مخالفت برخاستند) و ما به شدّت آنان را درهم کوبیدیم!';
$t[37] = 'و قوم نوح را هنگامی که رسولان (ما) را تکذیب کردند غرق نمودیم، و آنان را درس عبرتی برای مردم قرار دادیم؛ و برای ستمگران عذاب دردناکی فراهم ساخته&zwnj;ایم!';
$t[38] = '(همچنین) قوم عاد و ثمود و اصحاب الرّس [= گروهی که درختان صنوبر را می&zwnj;پرستیدند] و اقوام بسیار دیگری را که در این میان بودند، هلاک کردیم!';
$t[39] = 'و برای هر یک از آنها مثلها زدیم؛ و (چون سودی نداد،) همگی را نابود کردیم!';
$t[40] = 'آنها [= مشرکان مکّه&zwnj;] از کنار شهری که باران شرّ [= بارانی از سنگهای آسمانی&zwnj;] بر آن باریده بود [= دیار قوم لوط] گذشتند؛ آیا آن را نمی&zwnj;دیدند؟! (آری، می&zwnj;دیدند) ولی به رستاخیز ایمان نداشتند!';
$t[41] = 'و هنگامی که تو را می&zwnj;بینند، تنها به باد استهزایت می&zwnj;گیرند (و می&zwnj;گویند:) آیا این همان کسی است که خدا او را بعنوان پیامبر برانگیخته است؟!';
$t[42] = 'اگر ما بر پرستش خدایانمان استقامت نمی&zwnj;کردیم، بیم آن می&zwnj;رفت که ما را گمراه سازد! امّا هنگامی که عذاب الهی را ببینند، بزودی می&zwnj;فهمند چه کسی گمراهتر بوده است!';
$t[43] = 'آیا دیدی کسی را که هوای نفسش را معبود خود برگزیده است؟! آیا تو می&zwnj;توانی او را هدایت کنی (یا به دفاع از او برخیزی)؟!';
$t[44] = 'آیا گمان می&zwnj;بری بیشتر آنان می&zwnj;شنوند یا می&zwnj;فهمند؟! آنان فقط همچون چهارپایانند، بلکه گمراهترند!';
$t[45] = 'آیا ندیدی چگونه پروردگارت سایه را گسترده ساخت؟! و اگر می&zwnj;خواست آن را ساکن قرار می&zwnj;داد؛ سپس خورشید را بر وجود آن دلیل قرار دادیم!';
$t[46] = 'سپس آن را آهسته جمع می&zwnj;کنیم (و نظام سایه و آفتاب را حاکم می&zwnj;سازیم)!';
$t[47] = 'او کسی است که شب را برای شما لباس قرار داد، و خواب را مایه استراحت، و روز را وسیله حرکت و حیات!';
$t[48] = 'او کسی است که بادها را بشارتگرانی پیش از رحمتش فرستاد، و از آسمان آبی پاک&zwnj;کننده نازل کردیم...';
$t[49] = 'تا بوسیله آن، سرزمین مرده&zwnj;ای را زنده کنیم؛ و آن را به مخلوقاتی که آفریده&zwnj;ایم -چهارپایان و انسانهای بسیار- می&zwnj;نوشانیم.';
$t[50] = 'ما این آیات را بصورتهای گوناگون برای آنان بیان کردیم تا متذکّر شوند، ولی بیشتر مردم از هر کاری جز انکار و کفر ابا دارند.';
$t[51] = 'و اگر می&zwnj;خواستیم، در هر شهر و دیاری بیم&zwnj;دهنده&zwnj;ای برمی&zwnj;انگیختیم (ولی این کار لزومی نداشت).';
$t[52] = 'بنابر این از کافران اطاعت مکن، و بوسیله آن [= قرآن&zwnj;] با آنان جهاد بزرگی بنما!';
$t[53] = 'او کسی است که دو دریا را در کنار هم قرار داد؛ یکی گوارا و شیرین، و دیگر شور و تلخ؛ و در میان آنها برزخی قرار داد تا با هم مخلوط نشوند (گویی هر یک به دیگری می&zwnj;گوید:) دور باش و نزدیک نیا!';
$t[54] = 'او کسی است که از آب، انسانی را آفرید؛ سپس او را نسب و سبب قرار داد (و نسل او را از این دو طریق گسترش داد)؛ و پروردگار تو همواره توانا بوده است.';
$t[55] = 'آنان جز خدا چیزهایی را می&zwnj;پرستند که نه به آنان سودی می&zwnj;رساند و نه زیانی؛ و کافران همیشه در برابر پروردگارشان (در طریق کفر) پشتیبان یکدیگرند.';
$t[56] = '(ای پیامبر!) ما تو را جز بعنوان بشارت دهنده و انذار کننده نفرستادیم!';
$t[57] = 'بگو: &laquo;من در برابر آن (ابلاغ آیین خدا) هیچ گونه پاداشی از شما نمی&zwnj;طلبم؛ مگر کسی که بخواهد راهی بسوی پروردگارش برگزیند (این پاداش من است.)&raquo;';
$t[58] = 'و توکّل کن بر آن زنده&zwnj;ای که هرگز نمی&zwnj;میرد؛ و تسبیح و حمد او را بجا آور؛ و همین بس که او از گناهان بندگانش آگاه است!';
$t[59] = 'همان (خدایی) که آسمانها و زمین و آنچه را میان این دو وجود دارد، در شش روز [= شش دوران&zwnj;] آفرید؛ سپس بر عرش (قدرت) قرار گرفت (و به تدبیر جهان پرداخت، او خداوند) رحمان است؛ از او بخواه که از همه چیز آگاه است!';
$t[60] = 'و هنگامی که به آنان گفته شود: &laquo;برای خداوند رحمان سجده کنید!&raquo; می&zwnj;گویند: &laquo;رحمان چیست؟! (ما اصلاً رحمان را نمی&zwnj;شناسیم!) آیا برای چیزی سجده کنیم که تو به ما دستور می&zwnj;دهی؟!&raquo; (این سخن را می&zwnj;گویند) و بر نفرتشان افزوده می&zwnj;شود!';
$t[61] = 'جاودان و پربرکت است آن (خدایی) که در آسمان منزلگاه&zwnj;هائی برای ستارگان قرار داد؛ و در میان آن، چراغ روشن و ماه تابانی آفرید!';
$t[62] = 'و او همان کسی است که شب و روز را جانشین یکدیگر قرار داد برای کسی که بخواهد متذکّر شود یا شکرگزاری کند (و آنچه را در روز کوتاهی کرده در شب انجام دهد و به عکس).';
$t[63] = 'بندگان (خاص خداوند) رحمان، کسانی هستند که با آرامش و بی&zwnj;تکبّر بر زمین راه می&zwnj;روند؛ و هنگامی که جاهلان آنها را مخاطب سازند (و سخنان نابخردانه گویند)، به آنها سلام می&zwnj;گویند (و با بی&zwnj;اعتنایی و بزرگواری می&zwnj;گذرند)؛';
$t[64] = 'کسانی که شبانگاه برای پروردگارشان سجده و قیام می&zwnj;کنند؛';
$t[65] = 'و کسانی که می&zwnj;گویند: &laquo;پروردگارا! عذاب جهنم را از ما برطرف گردان، که عذابش سخت و پر دوام است!';
$t[66] = 'مسلّماً آن (جهنم)، بد جایگاه و بد محلّ اقامتی است!&raquo;';
$t[67] = 'و کسانی که هرگاه انفاق کنند، نه اسراف می&zwnj;نمایند و نه سخت&zwnj;گیری؛ بلکه در میان این دو، حدّ اعتدالی دارند.';
$t[68] = 'و کسانی که معبود دیگری را با خداوند نمی&zwnj;خوانند؛ و انسانی را که خداوند خونش را حرام شمرده، جز بحق نمی&zwnj;کشند؛ و زنا نمی&zwnj;کنند؛ و هر کس چنین کند، مجازات سختی خواهد دید!';
$t[69] = 'عذاب او در قیامت مضاعف می&zwnj;گردد، و همیشه با خواری در آن خواهد ماند!';
$t[70] = 'مگر کسانی که توبه کنند و ایمان آورند و عمل صالح انجام دهند، که خداوند گناهان آنان را به حسنات مبدّل می&zwnj;کند؛ و خداوند همواره آمرزنده و مهربان بوده است!';
$t[71] = 'و کسی که توبه کند و عمل صالح انجام دهد، بسوی خدا بازگشت می&zwnj;کند (و پاداش خود را از او می&zwnj;گیرد.)';
$t[72] = 'و کسانی که شهادت به باطل نمی&zwnj;دهند (و در مجالس باطل شرکت نمی&zwnj;کنند)؛ و هنگامی که با لغو و بیهودگی برخورد کنند، بزرگوارانه از آن می&zwnj;گذرند.';
$t[73] = 'و کسانی که هرگاه آیات پروردگارشان به آنان گوشزد شود، کر و کور روی آن نمی افتند.';
$t[74] = 'و کسانی که می&zwnj;گویند: &laquo;پروردگارا! از همسران و فرزندانمان مایه روشنی چشم ما قرارده، و ما را برای پرهیزگاران پیشوا گردان!&raquo;';
$t[75] = '(آری،) آنها هستند که درجات عالی بهشت در برابر شکیباییشان به آنان پاداش داده می&zwnj;شود؛ و در آن، با تحیّت و سلام روبه&zwnj;رو می&zwnj;شوند.';
$t[76] = 'در حالی که جاودانه در آن خواهند ماند؛ چه قرارگاه و محلّ اقامت خوبی!';
$t[77] = 'بگو: &laquo;پروردگارم برای شما ارجی قائل نیست اگر دعای شما نباشد؛ شما (آیات خدا و پیامبران را) تکذیب کردید، و (این عمل) دامان شما را خواهد گرفت و از شما جدا نخواهد شد!&raquo; ظ';
?>