$t[25] = 'و هنگامی که حقّ را از سوی ما برای آنها آورد، گفتند: &laquo;پسران کسانی را که با موسی ایمان آورده&zwnj;اند بکشید و زنانشان را (برای اسارت و خدمت) زنده بگذارید!&raquo; امّا نقشه کافران جز در گمراهی نیست (و نقش بر آب می&zwnj;شود).';
$t[26] = 'و فرعون گفت: &laquo;بگذارید موسی را بکشم، و او پروردگارش را بخواند (تا نجاتش دهد)! زیرا من می&zwnj;ترسم که آیین شما را دگرگون سازد، و یا در این سرزمین فساد بر پا کند!&raquo;';
$t[27] = 'موسی گفت: &laquo;من به پروردگارم و پروردگار شما پناه می&zwnj;برم از هر متکبّری که به روز حساب ایمان نمی&zwnj;آورد!&raquo;';
$t[28] = 'و مرد مؤمنی از آل فرعون که ایمان خود را پنهان می&zwnj;داشت گفت: &laquo;آیا می&zwnj;خواهید مردی را بکشید بخاطر اینکه می&zwnj;گوید: پروردگار من &laquo;اللّه&raquo; است، در حالی که دلایل روشنی از سوی پروردگارتان برای شما آورده است؟! اگر دروغگو باشد، دروغش دامن خودش را خواهد گرفت؛ و اگر راستگو باشد، (لااقل) بعضی از عذابهایی را که وعده می&zwnj;دهد به شما خواهد رسید؛ خداوند کسی را که اسرافکار و بسیار دروغگوست هدایت نمی&zwnj;کند.';
$t[29] = 'ای قوم من! امروز حکومت از آن شماست و در این سرزمین پیروزید؛ اگر عذاب خدا به سراغ ما آید، چه کسی ما را یاری خواهد کرد؟!&raquo; فرعون گفت: &laquo;من جز آنچه را معتقدم به شما ارائه نمی&zwnj;دهم، و شما را جز به راه صحیح راهنمایی نمی&zwnj;کنم! (دستور، همان قتل موسی است!)&raquo;';
$t[30] = 'آن مرد باایمان گفت: &laquo;ای قوم من! من بر شما از روزی همانند روز (عذاب) اقوام پیشین بیمناکم!';
$t[31] = 'و از عادتی همچون عادت قوم نوح و عاد و ثمود و کسانی که بعد از آنان بودند (از شرک و کفر و طغیان) می&zwnj;ترسم؛ و خداوند ظلم و ستمی بر بندگانش نمی&zwnj;خواهد.';
$t[32] = 'ای قوم من! من بر شما از روزی که مردم یکدیگر را صدا می&zwnj;زنند (و از هم یاری می&zwnj;طلبند و صدایشان به جایی نمی&zwnj;رسد) بیمناکم!';
$t[33] = 'همان روزی که روی می&zwnj;گردانید و فرار می&zwnj;کنید؛ امّا هیچ پناهگاهی در برابر عذاب خداوند برای شما نیست؛ و هر کس را خداوند (بخاطر اعمالش) گمراه سازد، هدایت&zwnj;کننده&zwnj;ای برای او نیست!';
$t[34] = 'پیش از این یوسف دلایل روشن برای شما آورد، ولی شما همچنان در آنچه او برای شما آورده بود تردید داشتید؛ تا زمانی که از دنیا رفت، گفتید: هرگز خداوند بعد از او پیامبری مبعوث نخواهد کرد! این گونه خداوند هر اسرافکار تردیدکننده&zwnj;ای را گمراه می&zwnj;سازد!';
$t[35] = 'همانها که در آیات خدا بی&zwnj;آنکه دلیلی برایشان آمده باشد به مجادله برمی&zwnj;خیزند؛ (این کارشان) خشم عظیمی نزد خداوند و نزد آنان که ایمان آورده&zwnj;اند به بار می&zwnj;آورد؛ این گونه خداوند بر دل هر متکبّر جبّاری مُهر می&zwnj;نهد!&raquo;';
$t[36] = 'فرعون گفت: &laquo;ای هامان! برای من بنای مرتفعی بساز، شاید به وسایلی دست یابم،';
$t[37] = 'وسایل (صعود به) آسمانها تا از خدای موسی آگاه شوم؛ هر چند گمان می&zwnj;کنم او دروغگو باشد!&raquo; اینچنین اعمال بد فرعون در نظرش آراسته جلوه کرد و از راه حق باز داشته شد؛ و توطئه فرعون (و همفکران او) جز به نابودی نمی&zwnj;انجامد!';
$t[38] = 'کسی که (از قوم فرعون) ایمان آورده بود گفت: &laquo;ای قوم من! از من پیروی کنید تا شما را به راه درست هدایت کنم.';
$t[39] = 'ای قوم من! این زندگی دنیا، تنها متاع زودگذری است؛ و آخرت سرای همیشگی است!';
$t[40] = 'هر کس بدی کند، جز بمانند آن کیفر داده نمی&zwnj;شود؛ ولی هر کس کار شایسته&zwnj;ای انجام دهد -خواه مرد یا زن- در حالی که مؤمن باشد آنها وارد بهشت می&zwnj;شوند و در آن روزی بی&zwnj;حسابی به آنها داده خواهد شد.';
$t[41] = 'ای قوم من! چرا من شما را به سوی نجات دعوت می&zwnj;کنم، امّا شما مرا بسوی آتش فرا می&zwnj;خوانید؟!';
$t[42] = 'مرا دعوت می&zwnj;کنید که به خداوند یگانه کافر شوم و همتایی که به آن علم ندارم برای او قرار دهم، در حالی که من شما را بسوی خداوند عزیز غفّار دعوت می&zwnj;کنم!';
$t[43] = 'قطعاً آنچه مرا بسوی آن می&zwnj;خوانید، نه دعوت (و حاکمیّتی) در دنیا دارد و نه در آخرت؛ و تنها بازگشت ما در قیامت بسوی خداست؛ و مسرفان اهل آتشند!';
$t[44] = 'و بزودی آنچه را به شما می&zwnj;گویم به خاطر خواهید آورد! من کار خود را به خدا واگذارم که خداوند نسبت به بندگانش بیناست!&raquo;';
$t[45] = 'خداوند او را از نقشه&zwnj;های سوء آنها نگه داشت، و عذاب شدید بر آل فرعون وارد شد!';
$t[46] = 'عذاب آنها آتش است که هر صبح و شام بر آن عرضه می&zwnj;شوند؛ و روزی که قیامت برپا شود (می&zwnj;فرماید:) &laquo;آل فرعون را در سخت&zwnj;ترین عذابها وارد کنید!&raquo;';
$t[47] = 'به خاطر بیاور هنگامی را که در آتش دوزخ با هم محاجّه می&zwnj;کنند؛ ضعیفان به مستکبران می&zwnj;گویند: &laquo;ما پیرو شما بودیم، آیا شما (امروز) سهمی از آتش را بجای ما پذیرا می&zwnj;شوید؟!&raquo;';
$t[48] = 'مستکبران می&zwnj;گویند: &laquo;ما همگی در آن هستیم، زیرا خداوند در میان بندگانش (بعدالت) حکم کرده است!&raquo;';
$t[49] = 'و آنها که در آتشند به مأموران دوزخ می&zwnj;گویند: &laquo;از پروردگارتان بخواهید یک روز عذاب را از ما بردارد!&raquo;';
$t[50] = 'آنها می&zwnj;گویند: &laquo;آیا پیامبران شما دلایل روشن برایتان نیاوردند؟!&raquo; می&zwnj;گویند: &laquo;آری!&raquo; آنها می&zwnj;گویند: &laquo;پس هر چه می&zwnj;خواهید (خدا را) بخوانید؛ ولی دعای کافران (به جایی نمی&zwnj;رسد و) جز در ضلالت نیست!&raquo;';
$t[51] = 'ما به یقین پیامبران خود و کسانی را که ایمان آورده&zwnj;اند، در زندگی دنیا و (در آخرت) روزی که گواهان به پا می&zwnj;خیزند یاری می&zwnj;دهیم!';
$t[52] = 'روزی که عذرخواهی ظالمان سودی به حالشان نمی&zwnj;بخشد؛ و لعنت خدا برای آنها، و خانه (و جایگاه) بد نیز برای آنان است.';
$t[53] = 'و ما به موسی هدایت بخشیدیم، و بنی اسرائیل را وارثان کتاب (تورات) قرار دادیم.';
