$t[55] = 'پس (ای پیامبر!) صبر و شکیبایی پیشه کن که وعده خدا حقّ است، و برای گناهت استغفار کن، و هر صبح و شام تسبیح و حمد پروردگارت را بجا آور!';
$t[56] = 'کسانی که در آیات خداوند بدون دلیلی که برای آنها آمده باشد ستیزه&zwnj;جویی می&zwnj;کنند، در سینه&zwnj;هایشان فقط تکبّر (و غرور) است، و هرگز به خواسته خود نخواهند رسید، پس به خدا پناه بر که او شنوا و بیناست!';
$t[57] = 'آفرینش آسمانها و زمین از آفرینش انسانها مهمتر است، ولی بیشتر مردم نمی&zwnj;دانند.';
$t[58] = 'هرگز نابینا و بینا یکسان نیستند؛ همچنین کسانی که ایمان آورده، و اعمال صالح انجام داده&zwnj;اند با بدکاران یکسان نخواهند بود؛ امّا کمتر متذکّر می&zwnj;شوید!';
$t[59] = 'روز قیامت به یقین آمدنی است، و شکّی در آن نیست؛ ولی اکثر مردم ایمان نمی آورند!';
$t[60] = 'پروردگار شما گفته است: &laquo;مرا بخوانید تا (دعای) شما را بپذیرم! کسانی که از عبادت من تکبّر می&zwnj;ورزند به زودی با ذلّت وارد دوزخ می&zwnj;شوند!&raquo;';
$t[61] = 'خداوند کسی است که شب را برای شما آفرید تا در آن بیاسایید، و روز را روشنی&zwnj;بخش قرار داد؛ خداوند نسبت به مردم صاحب فضل و کرم است؛ ولی بیشتر مردم شکرگزاری نمی&zwnj;کنند!';
$t[62] = 'این است خداوند، پروردگار شما که آفریننده همه چیز است؛ هیچ معبودی جز او نیست؛ با این حال چگونه از راه حق منحرف می&zwnj;شوید؟!';
$t[63] = 'اینچنین کسانی که آیات خدا را انکار می&zwnj;کردند (از راه راست) منحرف می&zwnj;شوند!';
$t[64] = 'خداوند کسی است که زمین را برای شما جایگاه امن و آرامش قرار داد و آسمان را همچون سقفی (بالای سرتان)؛ و شما را صورتگری کرد، و صورتتان را نیکو آفرید؛ و از چیزهایی پاکیزه به شما روزی داد؛ این است خداوند پروردگار شما! جاوید و پربرکت است خداوندی که پروردگار عالمیان است!';
$t[65] = 'زنده (واقعی) اوست؛ معبودی جز او نیست؛ پس او را بخوانید در حالی که دین خود را برای او خالص کرده&zwnj;اید! ستایش مخصوص خداوندی است که پروردگار جهانیان است!';
$t[66] = 'بگو: &laquo;من نهی شده&zwnj;ام از اینکه معبودهایی را که شما غیر از خدا می&zwnj;خوانید بپرستم، چون دلایل روشن از جانب پروردگارم برای من آمده است؛ و مأمورم که تنها در برابر پروردگار عالمیان تسلیم باشم!&raquo;';
$t[67] = 'او کسی است که شما را از خاک آفرید، سپس از نطفه، سپس از علقه (خون بسته شده)، سپس شما را بصورت طفلی (از شکم مادر) بیرون می&zwnj;فرستد، بعد به مرحله کمال قوّت خود می&zwnj;رسید، و بعد از آن پیر می&zwnj;شوید و (در این میان) گروهی از شما پیش از رسیدن به این مرحله می&zwnj;میرند و در نهایت به سرآمد عمر خود می&zwnj;رسید؛ و شاید تعقّل کنید!';
$t[68] = 'او کسی است که زنده می&zwnj;کند و می&zwnj;میراند؛ و هنگامی که کاری را مقرّر کند، تنها به آن می&zwnj;گوید: &laquo;موجود باش!&raquo; بی&zwnj;درنگ موجود می&zwnj;شود!';
$t[69] = 'آیا ندیدی کسانی را که در آیات خدا مجادله می&zwnj;کنند، چگونه از راه حقّ منحرف می شوند؟!';
$t[70] = 'همان کسانی که کتاب (آسمانی) و آنچه رسولان خود را بدان فرستاده&zwnj;ایم تکذیب کردند؛ امّا بزودی (نتیجه کار خود را) می&zwnj;دانند!';
$t[71] = 'در آن هنگام که غل و زنجیرها بر گردن آنان قرار گرفته و آنها را می&zwnj;کشند...';
$t[72] = 'و در آب جوشان وارد می&zwnj;کنند؛ سپس در آتش دوزخ افروخته می&zwnj;شوند!';
$t[73] = 'سپس به آنها گفته می&zwnj;شود: &laquo;کجایند آنچه را همتای خدا قرار می&zwnj;دادید،';
$t[74] = 'همان معبودهایی را که جز خدا پرستش می&zwnj;کردید؟!&raquo; آنها می&zwnj;گویند: &laquo;همه از نظر ما پنهان و گم شدند؛ بلکه ما اصلاً پیش از این چیزی را پرستش نمی&zwnj;کردیم&raquo;! این گونه خداوند کافران را گمراه می&zwnj;سازد!';
$t[75] = 'این (عذاب) بخاطر آن است که بناحقّ در زمین شادی می&zwnj;کردید و از روی غرور و مستی به خوشحالی می&zwnj;پرداختید!';
$t[76] = 'از درهای جهنّم وارد شوید و جاودانه در آن بمانید؛ و چه بد است جایگاه متکبّران!';
$t[77] = 'پس (ای پیامبر) صبر کن که وعده خدا حقّ است؛ و هرگاه قسمتی از مجازاتهایی را که به آنها وعده داده&zwnj;ایم در حال حیاتت به تو ارائه دهیم، یا تو را (پیش از آن) از دنیا ببریم (مهمّ نیست)؛ چرا که همه آنان را تنها بسوی ما باز می&zwnj;گردانند!';
$t[78] = 'ما پیش از تو رسولانی فرستادیم؛ سرگذشت گروهی از آنان را برای تو بازگفته، و گروهی را برای تو بازگو نکرده&zwnj;ایم؛ و هیچ پیامبری حق نداشت معجزه&zwnj;ای جز بفرمان خدا بیاورد و هنگامی که فرمان خداوند (برای مجازات آنها) صادر شود، بحق داوری خواهد شد؛ و آنجا اهل باطل زیان خواهند کرد!';
$t[79] = 'خداوند کسی است که چهارپایان را برای شما آفرید تا بعضی را سوار شوید و از بعضی تغذیه کنید.';
$t[80] = 'و برای شما در آنها منافع بسیاری (جز اینها) است، تا بوسیله آنها به مقصدی که در دل دارید برسید؛ و بر آنها و بر کشتیها سوار می&zwnj;شوید.';
$t[81] = 'او آیاتش را همواره به شما نشان می&zwnj;دهد؛ پس کدام یک از آیات او را انکار می&zwnj;کنید؟!';
$t[82] = 'آیا روی زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنها بودند چه شد؟! همانها که نفراتشان از اینها بیشتر، و نیرو و آثارشان در زمین فزونتر بود؛ امّا هرگز آنچه را به دست می&zwnj;آوردند نتوانست آنها را بی&zwnj;نیاز سازد (و عذاب الهی را از آنان دور کند)!';
$t[83] = 'هنگامی که رسولانشان دلایل روشنی برای آنان آوردند، به دانشی که خود داشتند خوشحال بودند (و غیر آن را هیچ می&zwnj;شمردند)؛ ولی آنچه را (از عذاب) به تمسخر می&zwnj;گرفتند آنان را فراگرفت!';
$t[84] = 'هنگامی که عذاب (شدید) ما را دیدند گفتند: &laquo;هم اکنون به خداوند یگانه ایمان آوردیم و به معبودهایی که همتای او می&zwnj;شمردیم کافر شدیم!&raquo;';
$t[85] = 'امّا هنگامی که عذاب ما را مشاهده کردند، ایمانشان برای آنها سودی نداشت! این سنّت خداوند است که همواره در میان بندگانش اجرا شده، و آنجا کافران زیانکار شدند!';
?>