$t[9] = 'و اگر او را فرشته&zwnj;ای قرارمی&zwnj;دادیم، حتماً وی را بصورت انسانی درمی&zwnj;آوردیم؛ (باز به پندار آنان،) کار را بر آنها مشتبه می&zwnj;ساختیم؛ همان&zwnj;طور که آنها کار را بر دیگران مشتبه می&zwnj;سازند!';
$t[10] = '(با این حال، نگران نباش!) جمعی از پیامبران پیش از تو را استهزا کردند؛ امّا سرانجام، آنچه را مسخره می&zwnj;کردند، دامانشان را می&zwnj;گرفت؛ (و عذاب الهی بر آنها فرود آمد).';
$t[11] = 'بگو: &laquo;روی زمین گردش کنید! سپس بنگرید سرانجام تکذیب&zwnj;کنندگان آیات الهی چه شد؟!&raquo;';
$t[12] = 'بگو: &laquo;آنچه در آسمانها و زمین است، از آن کیست؟&raquo; بگو: &laquo;از آن خداست؛ رحمت (و بخشش) را بر خود، حتم کرده؛ (و به همین دلیل،) بطور قطع همه شما را در روز قیامت، که در آن شکّ و تردیدی نیست، گرد خواهد آورد. (آری،) فقط کسانی که سرمایه&zwnj;های وجود خویش را از دست داده و گرفتار خسران شدند، ایمان نمی&zwnj;آورند.';
$t[13] = 'و برای اوست آنچه در شب و روز قرار دارد؛ و او، شنوا و داناست.';
$t[14] = 'بگو: &laquo;آیا غیر خدا را ولیّ خود انتخاب کنم؟! (خدایی) که آفریننده آسمانها و زمین است؛ اوست که روزی می&zwnj;دهد، و از کسی روزی نمی&zwnj;گیرد.&raquo; بگو: &laquo;من مأمورم که نخستین مسلمان باشم؛ و (خداوند به من دستور داده که) از مشرکان نباش!&raquo;';
$t[15] = 'بگو: &laquo;من (نیز) اگر نافرمانی پروردگارم کنم، از عذاب روزی بزرگ [= روز رستاخیز] می&zwnj;ترسم!';
$t[16] = 'آن کس که در آن روز، مجازات الهی به او نرسد، خداوند او را مشمول رحمت خویش ساخته؛ و این همان پیروزی آشکار است.&raquo;';
$t[17] = 'اگر خداوند زیانی به تو برساند، هیچ کس جز او نمی&zwnj;تواند آن را برطرف سازد! و اگر خیری به تو رساند، او بر همه چیز تواناست؛ (و از قدرت او، هرگونه نیکی ساخته است.)';
$t[18] = 'اوست که بر بندگان خود، قاهر و مسلّط است؛ و اوست حکیم آگاه!';
$t[19] = 'بگو: &laquo;بالاترین گواهی، گواهی کیست؟&raquo; (و خودت پاسخ بده و) بگو: &laquo;خداوند، گواه میان من و شماست؛ و (بهترین دلیل آن این است که) این قرآن بر من وحی شده، تا شما و تمام کسانی را که این قرآن به آنها می&zwnj;رسد، بیم دهم (و از مخالفت فرمان خدا بترسانم). آیا براستی شما گواهی می&zwnj;دهید که معبودان دیگری با خداست؟!&raquo; بگو: &laquo;من هرگز چنین گواهی نمی&zwnj;دهم&raquo;. بگو: &laquo;اوست تنها معبود یگانه؛ و من از آنچه برای او شریک قرارمی&zwnj;دهید، بیزارم!&raquo;';
$t[20] = 'آنان که کتاب آسمانی به ایشان داده&zwnj;ایم، بخوبی او [= پیامبر] را می&zwnj;شناسند، همان&zwnj;گونه که فرزندان خود را می&zwnj;شناسند؛ فقط کسانی که سرمایه وجود خود را از دست داده&zwnj;اند، ایمان نمی&zwnj;آورند.';
$t[21] = 'چه کسی ستمکارتر است از آن کس که بر خدا دروغ بسته [= همتایی برای او قائل شده&zwnj;]، و یا آیات او را تکذیب کرده است؟! مسلماً ظالمان، رستگار نخواهند شد!';
$t[22] = 'آن روز که همه آنها را محشور می&zwnj;کنیم؛ سپس به مشرکان می&zwnj;گوییم: &laquo;معبودهایتان، که همتای خدا می&zwnj;پنداشتید، کجایند؟&raquo; (چرا به یاری شما نمی&zwnj;شتابند؟!)';
$t[23] = 'سپس پاسخ و عذر آنها، چیزی جز این نیست که می&zwnj;گویند: &laquo;به خداوندی که پروردگار ماست سوگند که ما مشرک نبودیم!&raquo;';
$t[24] = 'ببین چگونه به خودشان (نیز) دروغ می&zwnj;گویند، و آنچه را بدروغ همتای خدا می&zwnj;پنداشتند، از دست می&zwnj;دهند!';
$t[25] = 'پاره&zwnj;ای از آنها به (سخنان) تو، گوش فرامی&zwnj;دهند؛ ولی بر دلهای آنان پرده&zwnj;ها افکنده&zwnj;ایم تا آن را نفهمند؛ و در گوش آنها، سنگینی قرار داده&zwnj;ایم. و (آنها بقدری لجوجند که) اگر تمام نشانه&zwnj;های حق را ببینند، ایمان نمی&zwnj;آورند؛ تا آنجا که وقتی به سراغ تو می&zwnj;آیند که با تو پرخاشگری کنند، کافران می&zwnj;گویند: &laquo;اینها فقط افسانه&zwnj;های پیشینیان است!&raquo;';
$t[26] = 'آنها دیگران را از آن بازمی&zwnj;دارند؛ و خود نیز از آن دوری می&zwnj;کنند؛ آنها جز خود را هلاک نمی&zwnj;کنند، ولی نمی&zwnj;فهمند!';
$t[27] = 'کاش (حال آنها را) هنگامی که در برابر آتش (دوزخ) ایستاده&zwnj;اند، ببینی! می&zwnj;گویند: ای کاش (بار دیگر، به دنیا) بازگردانده می&zwnj;شدیم، و آیات پروردگارمان را تکذیب نمی&zwnj;کردیم، و از مؤمنان می&zwnj;بودیم!';
$t[28] = '(آنها در واقع پشیمان نیستند،) بلکه اعمال و نیّاتی را که قبلاً پنهان می&zwnj;کردند، در برابر آنها آشکار شده (و به وحشت افتاده&zwnj;اند). و اگر بازگردند، به همان اعمالی که از آن نهی شده بودند بازمی&zwnj;گردند؛ آنها دروغگویانند.';
$t[29] = 'آنها گفتند: &laquo;چیزی جز این زندگی دنیای ما نیست؛ و ما هرگز برانگیخته نخواهیم شد!&raquo;';
$t[30] = 'اگر آنها را به هنگامی که در پیشگاه پروردگارشان ایستاده&zwnj;اند، ببینی! (به آنها) می&zwnj;گوید: &laquo;آیا این حق نیست؟&raquo; می&zwnj;گویند: &laquo;آری، قسم به پروردگارمان (حق است!)&raquo; می&zwnj;گوید: &laquo;پس مجازات را بچشید به سزای آنچه انکار می&zwnj;کردید!&raquo;';
$t[31] = 'آنها که لقای پروردگار را تکذیب کردند، مسلماً زیان دیدند؛ (و این تکذیب، ادامه می یابد) تا هنگامی که ناگهان قیامت به سراغشان بیاید؛ می&zwnj;گویند: &laquo;ای افسوس بر ما که درباره آن، کوتاهی کردیم!&raquo; و آنها (بار سنگین) گناهانشان را بر دوش می&zwnj;کشند؛ چه بد باری بر دوش خواهند داشت!';
$t[32] = 'زندگی دنیا، چیزی جز بازی و سرگرمی نیست! و سرای آخرت، برای آنها که پرهیزگارند، بهتر است! آیا نمی&zwnj;اندیشید؟!';
$t[33] = 'ما می&zwnj;دانیم که گفتار آنها، تو را غمگین می&zwnj;کند؛ ولی (غم مخور! و بدان که) آنها تو را تکذیب نمی&zwnj;کنند؛ بلکه ظالمان، آیات خدا را انکار می&zwnj;نمایند.';
$t[34] = 'پیش از تو نیز پیامبرانی تکذیب شدند؛ و در برابر تکذیبها، صبر و استقامت کردند؛ و (در این راه،) آزار دیدند، تا هنگامی که یاری ما به آنها رسید. (تو نیز چنین باش! و این، یکی از سنتهای الهی است؛) و هیچ چیز نمی&zwnj;تواند سنن خدا را تغییر دهد؛ و اخبار پیامبران به تو رسیده است.';
$t[35] = 'و اگر اعراض آنها بر تو سنگین است، چنانچه بتوانی نقبی در زمین بزنی، یا نردبانی به آسمان بگذاری (و اعماق زمین و آسمانها را جستجو کنی، چنین کن) تا آیه (و نشانه دیگری) برای آنها بیاوری! (ولی بدان که این لجوجان، ایمان نمی&zwnj;آورند!) امّا اگر خدا بخواهد، آنها را (به اجبار) بر هدایت جمع خواهد کرد. (ولی هدایت اجباری، چه سودی دارد؟) پس هرگز از جاهلان مباش!';
$t[36] = 'تنها کسانی (دعوت تو را) می&zwnj;پذیرند که گوش شنوا دارند؛ امّا مردگان (و آنها که روح انسانی را از دست داده&zwnj;اند، ایمان نمی&zwnj;آورند؛ و) خدا آنها را (در قیامت) برمی&zwnj;انگیزد؛ سپس به سوی او، بازمی&zwnj;گردند.';
$t[37] = 'و گفتند: &laquo;چرا نشانه (و معجزه&zwnj;ای) از طرف پروردگارش بر او نازل نمی&zwnj;شود؟!&raquo; بگو: &laquo;خداوند، قادر است که نشانه&zwnj;ای نازل کند؛ ولی بیشتر آنها نمی&zwnj;دانند!&raquo;';
$t[38] = 'هیچ جنبنده&zwnj;ای در زمین، و هیچ پرنده&zwnj;ای که با دو بال خود پرواز می&zwnj;کند، نیست مگر اینکه امتهایی همانند شما هستند. ما هیچ چیز را در این کتاب، فرو گذار نکردیم؛ سپس همگی به سوی پروردگارشان محشور می&zwnj;گردند.';
$t[39] = 'آنها که آیات ما را تکذیب کردند، کرها و لالهایی هستند که در تاریکیها قرار دارند. هر کس را خدا بخواهد (و مستحق باشد،) گمراه می&zwnj;کند؛ و هر کس را بخواهد (و شایسته بداند،) بر راه راست قرار خواهد داد.';
$t[40] = 'بگو: &laquo;به من خبر دهید اگر عذاب پروردگار به سراغ شما آید، یا رستاخیز برپا شود، آیا (برای حل مشکلات خود،) غیر خدا را می&zwnj;خوانید اگر راست می&zwnj;گویید؟!&raquo;';
$t[41] = '(نه،) بلکه تنها او را می&zwnj;خوانید! و او اگر بخواهد، مشکلی را که بخاطر آن او را خوانده&zwnj;اید، برطرف می&zwnj;سازد؛ و آنچه را (امروز) همتای خدا قرارمی&zwnj;دهید، (در آن روز) فراموش خواهید کرد.';
$t[42] = 'ما به سوی امتهایی که پیش از تو بودند، (پیامبرانی) فرستادیم؛ (و هنگامی که با این پیامبران به مخالفت برخاستند،) آنها را با شدّت و رنج و ناراحتی مواجه ساختیم؛ شاید (بیدار شوند و در برابر حق،) خضوع کنند و تسلیم گردند!';
$t[43] = 'چرا هنگامی که مجازات ما به آنان رسید، (خضوع نکردند و) تسلیم نشدند؟! بلکه دلهای آنها قساوت پیدا کرد؛ و شیطان، هر کاری را که می&zwnj;کردند، در نظرشان زینت داد!';
$t[44] = '(آری،) هنگامی که (اندرزها سودی نبخشید، و) آنچه را به آنها یادآوری شده بود فراموش کردند، درهای همه چیز (از نعمتها) را به روی آنها گشودیم؛ تا (کاملا) ً خوشحال شدند (و دل به آن بستند)؛ ناگهان آنها را گرفتیم (و سخت مجازات کردیم)؛ در این هنگام، همگی مأیوس شدند؛ (و درهای امید به روی آنها بسته شد).';
$t[45] = 'و (به این ترتیب،) دنباله (زندگی) جمعیّتی که ستم کرده بودند، قطع شد. و ستایش مخصوص خداوند، پروردگار جهانیان است.';
$t[46] = 'بگو: &laquo;به من خبر دهید اگر خداوند، گوش و چشمهایتان را بگیرد، و بر دلهای شما مهر نهد (که چیزی را نفهمید)، چه کسی جز خداست که آنها را به شما بدهد؟!&raquo; ببین چگونه آیات را به گونه&zwnj;های مختلف برای آنها شرح می&zwnj;دهیم، سپس آنها روی می&zwnj;گردانند!';
$t[47] = 'بگو: &laquo;به من خبر دهید اگر عذاب خدا بطور ناگهانی (و پنهانی) یا آشکارا به سراغ شما بیاید، آیا جز جمعیّت ستمکار هلاک می&zwnj;شوند؟!&raquo;';
$t[48] = 'ما پیامبران را، جز (به عنوان) بشارت دهنده و بیم دهنده، نمی&zwnj;فرستیم؛ آنها که ایمان بیاورند و (خویشتن را) اصلاح کنند، نه ترسی بر آنهاست و نه غمگین می&zwnj;شوند.';
$t[49] = 'و آنها که آیات ما را تکذیب کردند، عذاب (پروردگار) بخاطر نافرمانیها به آنان می&zwnj;رسد.';
$t[50] = 'بگو: &laquo;من نمی&zwnj;گویم خزاین خدا نزد من است؛ و من، (جز آنچه خدا به من بیاموزد،) از غیب آگاه نیستم! و به شما نمی&zwnj;گویم من فرشته&zwnj;ام؛ تنها از آنچه به من وحی می&zwnj;شود پیروی می&zwnj;کنم.&raquo; بگو: &laquo;آیا نابینا و بینا مساویند؟! پس چرا نمی&zwnj;اندیشید؟!&raquo;';
$t[51] = 'و به وسیله آن (قرآن)، کسانی را که از روز حشر و رستاخیز می&zwnj;ترسند، بیم ده! (روزی که در آن،) یاور و سرپرست و شفاعت&zwnj;کننده&zwnj;ای جز او [= خدا] ندارند؛ شاید پرهیزگاری پیشه کنند!';
$t[52] = 'و کسانی را که صبح و شام خدا را می&zwnj;خوانند، و جز ذات پاک او نظری ندارند، از خود دور مکن! نه چیزی از حساب آنها بر توست، و نه چیزی از حساب تو بر آنها! اگر آنها را طرد کنی، از ستمگران خواهی بود!';
$t[53] = 'و این چنین بعضی از آنها را با بعض دیگر آزمودیم (توانگران را بوسیله فقیران)؛ تا بگویند: &laquo;آیا اینها هستند که خداوند از میان ما (برگزیده، و) بر آنها منّت گذارده (و نعمت ایمان بخشیده است؟!&raquo; آیا خداوند، شاکران را بهتر نمی&zwnj;شناسد؟!';
$t[54] = 'هرگاه کسانی که به آیات ما ایمان دارند نزد تو آیند، به آنها بگو: &laquo;سلام بر شما پروردگارتان، رحمت را بر خود فرض کرده؛ هر کس از شما کار بدی از روی نادانی کند، سپس توبه و اصلاح (و جبران) نماید، (مشمول رحمت خدا می&zwnj;شود چرا که) او آمرزنده مهربان است.&raquo;';
$t[55] = 'و این چنین آیات را برمیشمریم، (تا حقیقت بر شما روشن شود،) و راه گناهکاران آشکار گردد.';
$t[56] = 'بگو: &laquo;من از پرستش کسانی که غیر از خدا می&zwnj;خوانید، نهی شده&zwnj;ام!&raquo; بگو: &laquo;من از هوی و هوسهای شما، پیروی نمی&zwnj;کنم؛ اگر چنین کنم، گمراه شده&zwnj;ام؛ و از هدایت&zwnj;یافتگان نخواهم بود!&raquo;';
$t[57] = 'بگو: &laquo;من دلیل روشنی از پروردگارم دارم؛ و شما آن را تکذیب کرده&zwnj;اید! آنچه شما در باره آن (از نزول عذاب الهی) عجله دارید، به دستِ من نیست! حکم و فرمان، تنها از آنِ خداست! حق را از باطل جدا می&zwnj;کند، و او بهترین جداکننده (حق از باطل) است.&raquo;';
$t[58] = 'بگو: &laquo;اگر آنچه درباره آن عجله دارید نزد من بود، (و به درخواست شما ترتیب&zwnj;اثر می&zwnj;دادم، عذاب الهی بر شما نازل می&zwnj;گشت؛) و کار میان من و شما پایان گرفته بود؛ ولی خداوند ظالمان را بهتر می&zwnj;شناسد (و بموقع مجازات می&zwnj;کند.)&raquo;';
$t[59] = 'کلیدهای غیب، تنها نزد اوست؛ و جز او، کسی آنها را نمی&zwnj;داند. او آنچه را در خشکی و دریاست می&zwnj;داند؛ هیچ برگی (از درختی) نمی&zwnj;افتد، مگر اینکه از آن آگاه است؛ و نه هیچ دانه&zwnj;ای در تاریکیهای زمین، و نه هیچ تر و خشکی وجود دارد، جز اینکه در کتابی آشکار [= در کتاب علم خدا] ثبت است.';
$t[60] = 'او کسی است که (روح) شما را در شب (به هنگام خواب) می&zwnj;گیرد؛ و از آنچه در روز کرده&zwnj;اید، با خبر است؛ سپس در روز شما را (از خواب) برمی&zwnj;انگیزد؛ و (این وضع همچنان ادامه می&zwnj;یابد) تا سرآمد معینی فرا رسد؛ سپس بازگشت شما به سوی اوست؛ و سپس شما را از آنچه عمل می&zwnj;کردید، با خبر می&zwnj;سازد.';
$t[61] = 'او بر بندگان خود تسلّط کامل دارد؛ و مراقبانی بر شما می&zwnj;گمارد؛ تا زمانی که یکی از شما را مرگ فرا رسد؛ (در این موقع،) فرستادگان ما جان او را می&zwnj;گیرند؛ و آنها (در نگاهداری حساب عمر و اعمال بندگان،) کوتاهی نمی&zwnj;کنند.';
$t[62] = 'سپس (تمام بندگان) به سوی خدا، که مولای حقیقی آنهاست، بازمی&zwnj;گردند. بدانید که حکم و داوری، مخصوص اوست؛ و او، سریعترین حسابگران است!';
$t[63] = 'بگو: &laquo;چه کسی شما را از تاریکیهای خشکی و دریا رهایی می&zwnj;بخشد؟ در حالی که او را با حالت تضرع (و آشکارا) و در پنهانی می&zwnj;خوانید؛ (و می&zwnj;گویید:) اگر از این (خطرات و ظلمتها) ما را رهایی می&zwnj;بخشد، از شکرگزاران خواهیم بود.&raquo;';
$t[64] = 'بگو: &laquo;خداوند شما را از اینها، و از هر مشکل و ناراحتی، نجات می&zwnj;دهد؛ باز هم شما برای او شریک قرار می&zwnj;دهید! (و راه کفر می&zwnj;پویید.)&raquo;';
$t[65] = 'بگو: &laquo;او قادر است که از بالا یا از زیر پای شما، عذابی بر شما بفرستد؛ یا بصورت دسته&zwnj;های پراکنده شما را با هم بیامیزد؛ و طعم جنگ (و اختلاف) را به هر یک از شما بوسیله دیگری بچشاند.&raquo; ببین چگونه آیات گوناگون را (برای آنها) بازگو می&zwnj;کنیم! شاید بفهمند (و بازگردند)!';
$t[66] = 'قوم و جمعیّت تو، آن (آیات الهی) را تکذیب و انکار کردند، در حالی که حق است! (به آنها) بگو: &laquo;من مسؤول (ایمان&zwnj;آوردن) شما نیستم! (وظیفه من، تنها ابلاغ رسالت است، نه اجبار شما بر ایمان.)&raquo;';
$t[67] = 'هر خیری (که خداوند به شما داده،) سرانجام قرارگاهی دارد، (و در موعد خود انجام میگیرد.) و بزودی خواهید دانست!';
$t[68] = 'هرگاه کسانی را دیدی که آیات ما را استهزا می&zwnj;کنند، از آنها روی بگردان تا به سخن دیگری بپردازند! و اگر شیطان از یاد تو ببرد، هرگز پس از یادآمدن با این جمعیّت ستمگر منشین!';
$t[69] = 'و (اگر) افراد با تقوا (برای ارشاد و اندرز با آنها بنشینند)، چیزی از حساب (و گناه) آنها بر ایشان نیست؛ ولی (این کار، باید تنها) برای یادآوری آنها باشد، شاید (بشنوند و) تقوی پیشه کنند!';
$t[70] = 'و رها کن کسانی را که آیین (فطری) خود را به بازی و سرگرمی گرفتند، و زندگی دنیا، آنها را مغرور ساخته، و با این (قرآن)، به آنها یادآوری نما، تا گرفتار (عواقب شوم) اعمال خود نشوند! (و در قیامت) جز خدا، نه یاوری دارند، و نه شفاعت&zwnj;کننده&zwnj;ای! و (چنین کسی) هر گونه عوضی بپردازد، از او پذیرفته نخواهد شد؛ آنها کسانی هستند که گرفتار اعمالی شده&zwnj;اند که خود انجام داده&zwnj;اند؛ نوشابه&zwnj;ای از آب سوزان برای آنهاست؛ و عذاب دردناکی بخاطر اینکه کفر می&zwnj;ورزیدند (و آیات الهی را انکار) می&zwnj;کردند.';
$t[71] = 'بگو: &laquo;آیا غیر از خدا، چیزی را بخوانیم (و عبادت کنیم) که نه سودی به حال ما دارد، نه زیانی؛ و (به این ترتیب،) به عقب برگردیم بعد از آنکه خداوند ما را هدایت کرده است؟! همانند کسی که بر اثر وسوسه&zwnj;های شیطان، در روی زمین راه را گم کرده، و سرگردان مانده است؛ در حالی که یارانی هم دارد که او را به هدایت دعوت می&zwnj;کنند (و می&zwnj;گویند:) به سوی ما بیا!&raquo; بگو: &laquo;تنها هدایت خداوند، هدایت است؛ و ما دستور داریم که تسلیم پروردگار عالمیان باشیم.';
$t[72] = 'و (نیز به ما فرمان داده شده به) اینکه: نماز را برپا دارید! و از او بپرهیزید! و تنها اوست که به سویش محشور خواهید شد.&raquo;';
$t[73] = 'اوست که آسمانها و زمین را بحق آفرید؛ و آن روز که (به هر چیز) می&zwnj;گوید: &laquo;موجود باش!&raquo; موجود می&zwnj;شود؛ سخن او، حق است؛ و در آن روز که در &laquo;صور&raquo; دمیده می&zwnj;شود، حکومت مخصوص اوست، از پنهان و آشکار با خبر است، و اوست حکیم و آگاه.';
$t[74] = '(به خاطر بیاورید) هنگامی را که ابراهیم به پدرش [= عمویش&zwnj;] &laquo;آزر&raquo; گفت: &laquo;آیا بتهائی را معبودان خود انتخاب می&zwnj;کنی؟! من، تو و قوم تو را در گمراهی آشکاری می&zwnj;بینم.&raquo;';
$t[75] = 'و این چنین، ملکوت آسمانها و زمین (و حکومت مطلقه خداوند بر آنها) را به ابراهیم نشان دادیم؛ (تا به آن استدلال کند،) و اهل یقین گردد.';
$t[76] = 'هنگامی که (تاریکی) شب او را پوشانید، ستاره&zwnj;ای مشاهده کرد، گفت: &laquo;این خدای من است؟&raquo; امّا هنگامی که غروب کرد، گفت: &laquo;غروب&zwnj;کنندگان را دوست ندارم!&raquo;';
$t[77] = 'و هنگامی که ماه را دید که (سینه افق را) می&zwnj;شکافد، گفت: &laquo;این خدای من است؟&raquo; امّا هنگامی که (آن هم) غروب کرد، گفت: &laquo;اگر پروردگارم مرا راهنمایی نکند، مسلّماً از گروه گمراهان خواهم بود.&raquo;';
$t[78] = 'و هنگامی که خورشید را دید که (سینه افق را) می&zwnj;شکافت، گفت: &laquo;این خدای من است؟ این (که از همه) بزرگتر است!&raquo; امّا هنگامی که غروب کرد، گفت: &laquo;ای قوم من از شریکهایی که شما (برای خدا) می&zwnj;سازید، بیزارم!';
$t[79] = 'من روی خود را به سوی کسی کردم که آسمانها و زمین را آفریده؛ من در ایمان خود خالصم؛ و از مشرکان نیستم!';
$t[80] = 'ولی قوم او [= ابراهیم&zwnj;]، با وی به گفتگو و ستیز پرداختند؛ گفت: &laquo;آیا درباره خدا با من گفتگو و ستیز می&zwnj;کنید؟! در حالی که خداوند، مرا با دلایل روشن هدایت کرده؛ و من از آنچه شما همتای (خدا) قرار می&zwnj;دهید، نمی&zwnj;ترسم (و به من زیانی نمی رسانند)! مگر پروردگارم چیزی را بخواهد! وسعت آگاهی پروردگارم همه چیز را در برمی&zwnj;گیرد؛ آیا متذکّر (و بیدار) نمی&zwnj;شوید؟!';
$t[81] = 'چگونه من از بتهای شما بترسم؟! در حالی که شما از این نمی&zwnj;ترسید که برای خدا، همتایی قرار داده&zwnj;اید که هیچ گونه دلیلی درباره آن، بر شما نازل نکرده است! (راست بگویید) کدام یک از این دو دسته (بت&zwnj;پرستان و خداپرستان)، شایسته&zwnj;تر به ایمنی (از مجازات) هستند اگر می&zwnj;دانید؟!';
$t[82] = '(آری،) آنها که ایمان آوردند، و ایمان خود را با شرک و ستم نیالودند، ایمنی تنها از آن آنهاست؛ و آنها هدایت&zwnj;یافتگانند!&raquo;';
$t[83] = 'اینها دلایل ما بود که به ابراهیم در برابر قومش دادیم! درجات هر کس را بخواهیم (و شایسته بدانیم،) بالا می&zwnj;بریم؛ پروردگار تو، حکیم و داناست.';
$t[84] = 'و اسحاق و یعقوب را به او [= ابراهیم&zwnj;] بخشیدیم؛ و هر دو را هدایت کردیم؛ و نوح را (نیز) پیش از آن هدایت نمودیم؛ و از فرزندان او، داوود و سلیمان و ایّوب و یوسف و موسی و هارون را (هدایت کردیم)؛ این&zwnj;گونه نیکوکاران را پاداش می&zwnj;دهیم!';
$t[85] = 'و (همچنین) زکریّا و یحیی و عیسی و الیاس را؛ همه از صالحان بودند.';
$t[86] = 'و اسماعیل و الیسع و یونس و لوط را؛ و همه را بر جهانیان برتری دادیم.';
$t[87] = 'و از پدران و فرزندان و برادران آنها (افرادی را برتری دادیم) و برگزیدیم و به راه راست، هدایت نمودیم.';
$t[88] = 'این، هدایت خداست؛ که هر کس از بندگان خود را بخواهد با آن راهنمایی می&zwnj;کند! و اگر آنها مشرک شوند، اعمال (نیکی) که انجام داده&zwnj;اند، نابود می&zwnj;گردد (و نتیجه&zwnj;ای از آن نمی&zwnj;گیرند).';
$t[89] = 'آنها کسانی هستند که کتاب و حکم و نبوّت به آنان دادیم؛ و اگر (بفرض) نسبت به آن کفر ورزند، (آیین حقّ زمین نمی&zwnj;ماند؛ زیرا) کسان دیگری را نگاهبان آن می&zwnj;سازیم که نسبت به آن، کافر نیستند.';
$t[90] = 'آنها کسانی هستند که خداوند هدایتشان کرده؛ پس به هدایت آنان اقتدا کن! (و) بگو: &laquo;در برابر این (رسالت و تبلیغ)، پاداشی از شما نمی&zwnj;طلبم! این (رسالت)، چیزی جز یک یادآوری برای جهانیان نیست! (این وظیفه من است)&raquo;';
$t[91] = 'آنها خدا را درست نشناختند که گفتند: &laquo;خدا، هیچ چیز بر هیچ انسانی، نفرستاده است!&raquo; بگو: &laquo;چه کسی کتابی را که موسی آورد، نازل کرد؟! کتابی که برای مردم، نور و هدایت بود؛ 0امّا شما) آن را بصورت پراکنده قرارمی&zwnj;دهید؛ قسمتی را آشکار، و قسمت زیادی را پنهان می&zwnj;دارید؛ و مطالبی به شما تعلیم داده شده که نه شما و نه پدرانتان، از آن با خبر نبودید!&raquo; بگو: &laquo;خدا!&raquo; سپس آنها را در گفتگوهای لجاجت&zwnj;آمیزشان رها کن، تا بازی کنند!';
$t[92] = 'و این کتابی است که ما آن را نازل کردیم؛ کتابی است پربرکت، که آنچه را پیش از آن آمده، تصدیق می&zwnj;کند؛ (آن را فرستادیم تا مردم را به پاداشهای الهی، بشارت دهی،) و تا (اهل) امّ&zwnj;القری [= مکّه&zwnj;] و کسانی را که گرد آن هستند، بترسانی! (یقین بدان) آنها که به آخرت ایمان دارند، به آن ایمان می&zwnj;آورند؛ و بر نمازهای خویش، مراقبت می کنند!';
$t[93] = 'چه کسی ستمکارتر است از کسی که دروغی به خدا ببندد، یا بگوید: &laquo;بر من، وحی فرستاده شده&raquo;، در حالی که به او وحی نشده است، و کسی که بگوید: &laquo;من نیز همانند آنچه خدا نازل کرده است، نازل می&zwnj;کنم&raquo;؟! و اگر ببینی هنگامی که (این) ظالمان در شداید مرگ فرو رفته&zwnj;اند، و فرشتگان دستها را گشوده، به آنان می&zwnj;گویند: &laquo;جان خود را خارج سازید! امروز در برابر دروغهایی که به خدا بستید و نسبت به آیات او تکبّر ورزیدید، مجازات خوارکننده&zwnj;ای خواهید دید&raquo;! (به حال آنها تأسف خواهی خورد)';
$t[94] = 'و (روز قیامت به آنها گفته می&zwnj;شود:) همه شما تنها به سوی ما بازگشت نمودید، همان&zwnj;گونه که روز اوّل شما را آفریدیم! و آنچه را به شما بخشیده بودیم، پشت سر گذاردید! و شفیعانی را که شریک در شفاعت خود می&zwnj;پنداشتید، با شما نمی&zwnj;بینیم! پیوندهای شما بریده شده است؛ و تمام آنچه را تکیه&zwnj;گاه خود تصوّر می&zwnj;کردید، از شما دور و گم شده&zwnj;اند!';
$t[95] = 'خداوند، شکافنده دانه و هسته است؛ زنده را از مرده خارج می&zwnj;سازد، و مرده را از زنده بیرون می&zwnj;آورد؛ این است خدای شما! پس چگونه از حقّ منحرف می&zwnj;شوید؟!';
$t[96] = 'او شکافنده صبح است؛ و شب را مایه آرامش، و خورشید و ماه را وسیله حساب قرار داده است؛ این، اندازه&zwnj;گیری خداوند توانای داناست!';
$t[97] = 'او کسی است که ستارگان را برای شما قرار داد، تا در تاریکیهای خشکی و دریا، به وسیله آنها راه یابید! ما نشانه&zwnj;ها (ی خود) را برای کسانی که می&zwnj;دانند، (و اهل فکر و اندیشه&zwnj;اند) بیان داشتیم!';
$t[98] = 'او کسی است که شما را از یک نفس آفرید! و شما دو گروه هستید: بعضی پایدار (از نظر ایمان یا خلقت کامل)، و بعضی ناپایدار؛ ما آیات خود را برای کسانی که می&zwnj;فهمند، تشریح نمودیم!';
$t[99] = 'او کسی است که از آسمان، آبی نازل کرد، و به وسیله آن، گیاهان گوناگون رویاندیم؛ و از آن، ساقه&zwnj;ها و شاخه&zwnj;های سبز، خارج ساختیم؛ و از آنها دانه&zwnj;های متراکم، و از شکوفه نخل، شکوفه&zwnj;هایی با رشته&zwnj;های باریک بیرون فرستادیم؛ و باغهایی از انواع انگور و زیتون و انار، (گاه) شبیه به یکدیگر، و (گاه) بی&zwnj;شباهت! هنگامی که میوه می دهد، به میوه آن و طرز رسیدنش بنگرید که در آن، نشانه&zwnj;هایی (از عظمت خدا) برای افراد باایمان است!';
$t[100] = 'آنان برای خدا همتایانی از جنّ قرار دادند، در حالی که خداوند همه آنها را آفریده است؛ و برای خدا، به دروغ و از روی جهل، پسران و دخترانی ساختند؛ منزّه است خدا، و برتر است از آنچه توصیف می&zwnj;کنند!';
$t[101] = 'او پدید آورنده آسمانها و زمین است؛ چگونه ممکن است فرزندی داشته باشد؟! حال آنکه همسری نداشته، و همه چیز را آفریده؛ و او به همه چیز داناست.';
$t[102] = '(آری، این است پروردگار شما! هیچ معبودی جز او نیست؛ آفریدگار همه چیز است؛ او را بپرستید و او نگهبان و مدّبر همه موجودات است.';
$t[103] = 'چشمها او را نمی&zwnj;بینند؛ ولی او همه چشمها را می&zwnj;بیند؛ و او بخشنده (انواع نعمتها، و با خبر از دقایق موجودات،) و آگاه (از همه) چیز است.';
$t[104] = 'دلایل روشن از طرف پروردگارتان برای شما آمد؛ کسی که (به وسیله آن، حقّ را) ببیند، به سود خود اوست؛ و کسی که از دیدن آن چشم بپوشد، به زیان خودش می&zwnj;باشد؛ و من نگاهبان شما نیستم (و شما را بر قبول ایمان مجبور نمی&zwnj;کنم)';
$t[105] = 'و اینچنین آیات (خود) را تشریح می&zwnj;کنیم؛ بگذار آنها بگویند: &laquo;تو درس خوانده&zwnj;ای (و آنها را از دیگری آموخته&zwnj;ای)&raquo;! می&zwnj;خواهیم آن را برای کسانی که آماده درک حقایقند، روشن سازیم.';
$t[106] = 'از آنچه که از سوی پروردگارت بر تو وحی شده، پیروی کن! هیچ معبودی جز او نیست! و از مشرکان، روی بگردان!';
$t[107] = 'اگر خدا می&zwnj;خواست، (همه به اجبار ایمان می&zwnj;آوردند،) و هیچ یک مشرک نمی&zwnj;شدند؛ و ما تو را مسؤول (اعمال) آنها قرار نداده&zwnj;ایم؛ و وظیفه نداری آنها را (به ایمان) مجبور سازی!';
$t[108] = '(به معبود) کسانی که غیر خدا را می&zwnj;خوانند دشنام ندهید، مبادا آنها (نیز) از روی (ظلم و) جهل، خدا را دشنام دهند! اینچنین برای هر امّتی عملشان را زینت دادیم سپس بازگشت همه آنان به سوی پروردگارشان است؛ و آنها را از آنچه عمل می&zwnj;کردند، آگاه می&zwnj;سازد (و پاداش و کیفر می&zwnj;دهد).';
$t[109] = 'با نهایت اصرار، به خدا سوگند یاد کردند که اگر نشانه&zwnj;ای [= معجزه&zwnj;ای&zwnj;] برای آنان بیاید، حتماً به آن ایمان می&zwnj;آورند؛ بگو: &laquo;معجزات فقط از سوی خداست (و در اختیار من نیست که به میل شما معجزه&zwnj;ای بیاورم)؛ و شما از کجا می&zwnj;دانید که هرگاه معجزه&zwnj;ای بیاید (ایمان می&zwnj;آورند؟ خیر،) ایمان نمی&zwnj;آورند!&raquo;';
$t[110] = 'و ما دلها و چشمهای آنها را واژگونه می&zwnj;سازیم؛ (آری آنها ایمان نمی&zwnj;آورند) همان&zwnj;گونه که در آغاز، به آن ایمان نیاوردند! و آنان را در حال طغیان و سرکشی، به خود وامی&zwnj;گذاریم تا سرگردان شوند!';
$t[111] = '(و حتّی) اگر فرشتگان را بر آنها نازل می&zwnj;کردیم، و مردگان با آنان سخن می&zwnj;گفتند، و همه چیز را در برابر آنها جمع می&zwnj;نمودیم، هرگز ایمان نمی&zwnj;آوردند؛ مگر آنکه خدا بخواهد! ولی بیشتر آنها نمی&zwnj;دانند!';
$t[112] = 'اینچنین در برابر هر پیامبری، دشمنی از شیاطین انس و جنّ قرار دادیم؛ آنها بطور سری (و درگوشی) سخنان فریبنده و بی&zwnj;اساس (برای اغفال مردم) به یکدیگر می&zwnj;گفتند؛ و اگر پروردگارت می&zwnj;خواست، چنین نمی&zwnj;کردند؛ (و می&zwnj;توانست جلو آنها را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار!';
$t[113] = 'نتیجه (وسوسه&zwnj;های شیطان و تبلیغات شیطان&zwnj;صفتان) این خواهد شد که دلهای منکران قیامت، به آنها متمایل گردد؛ و به آن راضی شوند؛ و هر گناهی که بخواهند، انجام دهند!';
$t[114] = '(با این حال،) آیا غیر خدا را به داوری طلبم؟! در حالی که اوست که این کتاب آسمانی را، که همه چیز در آن آمده، به سوی شما فرستاده است؛ و کسانی که به آنها کتاب آسمانی داده&zwnj;ایم می&zwnj;دانند این کتاب، بحق از طرف پروردگارت نازل شده؛ بنابر این از تردیدکنندگان مباش!';
$t[115] = 'و کلام پروردگار تو، با صدق و عدل، به حدّ تمام رسید؛ هیچ کس نمی&zwnj;تواند کلمات او را دگرگون سازد؛ و او شنونده داناست.';
$t[116] = 'اگر از بیشتر کسانی که در روی زمین هستند اطاعت کنی، تو را از راه خدا گمراه می کنند؛ (زیرا) آنها تنها از گمان پیروی می&zwnj;نمایند، و تخمین و حدس (واهی) می&zwnj;زنند.';
$t[117] = 'پروردگارت به کسانی که از راه او گمراه گشته&zwnj;اند، آگاهتر است؛ و همچنین از کسانی که هدایت یافته&zwnj;اند.';
$t[118] = 'از (گوشت) آنچه نام خدا (هنگام سر بریدن) بر آن گفته شده، بخورید (و غیر از آن نخورید) اگر به آیات او ایمان دارید!';
$t[119] = 'چرا از چیزها [= گوشتها] ئی که نام خدا بر آنها برده شده نمی&zwnj;خورید؟! در حالی که (خداوند) آنچه را بر شما حرام بوده، بیان کرده است! مگر اینکه ناچار باشید؛ (که در این صورت، خوردن از گوشت آن حیوانات جایز است.) و بسیاری از مردم، به خاطر هوی و هوس و بی&zwnj;دانشی، (دیگران را) گمراه می&zwnj;سازند؛ و پروردگارت، تجاوزکاران را بهتر می&zwnj;شناسد.';
$t[120] = 'گناهان آشکار و پنهان را رها کنید! زیرا کسانی که گناه می&zwnj;کنند، بزودی در برابر آنچه مرتکب می&zwnj;شدند، مجازات خواهند شد.';
$t[121] = 'و از آنچه نام خدا بر آن برده نشده، نخورید! این کار گناه است؛ و شیاطین به دوستان خود مطالبی مخفیانه القا می&zwnj;کنند، تا با شما به مجادله برخیزند؛ اگر از آنها اطاعت کنید، شما هم مشرک خواهید بود!';
$t[122] = 'آیا کسی که مرده بود، سپس او را زنده کردیم، و نوری برایش قرار دادیم که با آن در میان مردم راه برود، همانند کسی است که در ظلمتها باشد و از آن خارج نگردد؟! این گونه برای کافران، اعمال (زشتی) که انجام می&zwnj;دادند، تزیین شده (و زیبا جلوه کرده) است.';
$t[123] = 'و (نیز) این گونه در هر شهر و آبادی، بزرگان گنهکاری قرار دادیم؛ (افرادی که همه گونه قدرت در اختیارشان گذاردیم؛ اما آنها سوء استفاده کرده، و راه خطا پیش گرفتند؛) و سرانجام کارشان این شد که به مکر (و فریب مردم) پرداختند؛ ولی تنها خودشان را فریب می&zwnj;دهند و نمی&zwnj;فهمند!';
$t[124] = 'و هنگامی که آیه&zwnj;ای برای آنها بیاید، می&zwnj;گویند: &laquo;ما هرگز ایمان نمی&zwnj;آوریم، مگر اینکه همانند چیزی که به پیامبران خدا داده شده، به ما هم داده شود!&raquo; خداوند آگاهتر است که رسالت خویش را کجا قرار دهد! بزودی کسانی که مرتکب گناه شدند، (و مردم را از راه حق منحرف ساختند،) در مقابل مکر (و فریب و نیرنگی) که می&zwnj;کردند، گرفتار حقارت در پیشگاه خدا، و عذاب شدید خواهند شد.';
$t[125] = 'آن کس را که خدا بخواهد هدایت کند، سینه&zwnj;اش را برای (پذیرش) اسلام، گشاده می سازد؛ و آن کس را که بخاطر اعمال خلافش بخواهد گمراه سازد، سینه&zwnj;اش را آنچنان تنگ می&zwnj;کند که گویا می&zwnj;خواهد به آسمان بالا برود؛ این گونه خداوند پلیدی را بر افرادی که ایمان نمی&zwnj;آورند قرار می&zwnj;دهد!';
$t[126] = 'و این راه مستقیم (و سنت جاویدان) پروردگار توست؛ ما آیات خود را برای کسانی که پند می&zwnj;گیرند، بیان کردیم!';
$t[127] = 'برای آنها (در بهشت) خانه امن و امان نزد پروردگارشان خواهد بود؛ و او، ولیّ و یاور آنهاست بخاطر اعمال (نیکی) که انجام می&zwnj;دادند.';
$t[128] = 'در آن روز که (خدا) همه آنها را جمع و محشور میسازد، (می&zwnj;گوید:) ای جمعیّت شیاطین و جن! ّ شما افراد زیادی از انسانها را گمراه ساختید! دوستان و پیروان آنها از میان انسانها می&zwnj;گویند: &laquo;پروردگارا! هر یک از ما دو گروه [= پیشوایان و پیروان&zwnj;گمراه&zwnj;] از دیگری استفاده کردیم؛ (ما به لذّات هوس آلود و زودگذر رسیدیم؛ و آنها بر ما حکومت کردند؛) و به اجلی که برای ما مقرّر داشته بودی رسیدیم.&raquo; (خداوند) می&zwnj;گوید: &laquo;آتش جایگاه شماست؛ جاودانه در آن خواهید ماند، مگر آنچه خدا بخواهد&raquo; پروردگار تو حکیم و داناست.';
$t[129] = 'ما این&zwnj;گونه بعضی از ستمگران را به بعضی دیگر وامی&zwnj;گذاریم به سبب اعمالی که انجام می&zwnj;دادند.';
$t[130] = '(در آن روز به آنها می&zwnj;گوید:) ای گروه جنّ و انس! آیا رسولانی از شما به سوی شما نیامدند که آیات مرا برایتان بازگو می&zwnj;کردند، و شما را از ملاقات چنین روزی بیم می دادند؟! آنها می&zwnj;گویند: &laquo;بر ضدّ خودمان گواهی می&zwnj;دهیم؛ (آری، ما بد کردیم.)&raquo; و زندگی (پر زرق و برق) دنیا آنها را فریب داد؛ و به زیان خود گواهی می&zwnj;دهند که کافر بودند!';
$t[131] = 'این بخاطر آن است که پروردگارت هیچ&zwnj;گاه (مردم) شهرها و آبادیها را بخاطر ستمهایشان در حال غفلت و بی&zwnj;خبری هلاک نمی&zwnj;کند. (بلکه قبلاً رسولانی برای آنها می&zwnj;فرستد.)';
$t[132] = 'و برای هر یک (از این دو دسته)، درجات (و مراتبی) است از آنچه عمل کردند؛ و پروردگارت از اعمالی که انجام می&zwnj;دهند، غافل نیست.';
$t[133] = 'پروردگارت بی&zwnj;نیاز و مهربان است؛ (پس به کسی ستم نمی&zwnj;کند؛ بلکه همه، نتیجه اعمال خود را می&zwnj;گیرند؛) اگر بخواهد، همه شما را می&zwnj;برد؛ سپس هر کس را بخواهد جانشین شما می&zwnj;سازد؛ همان&zwnj;طور که شما را از نسل اقوام دیگری به وجود آورد.';
$t[134] = 'آنچه به شما وعده داده می&zwnj;شود، یقیناً می&zwnj;آید؛ و شما نمی&zwnj;توانید (خدا را) ناتوان سازید (و از عدالت و کیفر او فرار کنید)!';
$t[135] = 'بگو: &laquo;ای قوم من! هر کار در قدرت دارید بکنید! من (هم به وظیفه خود) عمل می&zwnj;کنم؛ امّا بزودی خواهید دانست چه کسی سرانجام نیک خواهد داشت (و پیروزی با چه کسی است! امّا) به یقین، ظالمان رستگار نخواهند شد!&raquo;';
$t[136] = 'آنها [= مشرکان&zwnj;] سهمی از آنچه خداوند از زراعت و چهارپایان آفریده، برای او قرار دادند؛ (و سهمی برای بتها!) و بگمان خود گفتند: &laquo;این مال خداست! و این هم مال شرکای ما [= یعنی بتها] است!&raquo; آنچه مال شرکای آنها بود، به خدا نمی&zwnj;رسید؛ ولی آنچه مال خدا بود، به شرکایشان می&zwnj;رسید! (آری، اگر سهم بتها با کمبودی مواجه می&zwnj;شد، مال خدا را به بتها می&zwnj;دادند؛ امّا عکس آن را مجاز نمی&zwnj;دانستند!) چه بد حکم می&zwnj;کنند (که علاوه بر شرک، حتّی خدا را کمتر از بتها می&zwnj;دانند)!';
$t[137] = 'همین&zwnj;گونه شرکای آنها [= بتها]، قتل فرزندانشان را در نظرشان جلوه دادند؛ (کودکان خود را قربانی بتها می&zwnj;کردند، و افتخار می&zwnj;نمودند!) سرانجام آنها را به هلاکت افکندند؛ و آیینشان را بر آنان مشتبه ساختند. و اگر خدا می&zwnj;خواست، چنین نمی&zwnj;کردند؛ (زیرا می&zwnj;توانست جلو آنان را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار (و به آنها اعتنا مکن)!';
$t[138] = 'و گفتند: &laquo;این قسمت از چهارپایان و زراعت (که مخصوص بتهاست، برای همه) ممنوع است؛ و جز کسانی که ما بخواهیم -به گمان آنها- نباید از آن بخورند! و (اینها) چهارپایانی است که سوارشدن بر آنها (بر ما) حرام شده است!&raquo; و (نیز) چهارپایانی (بود) که (هنگام ذبح،) نام خدا را بر آن نمی&zwnj;بردند، و به خدا دروغ می&zwnj;بستند؛ (و می&zwnj;گفتند: &laquo;این احکام، همه از ناحیه اوست.&raquo;) بزودی (خدا) کیفر افتراهای آنها را می&zwnj;دهد!';
$t[139] = 'و گفتند: &laquo;آنچه (از بچّه) در شکم این حیوانات است، مخصوص مردان ماست؛ و بر همسران ما حرام است! امّا اگر مرده باشد [= مرده متولّد شود]، همگی در آن شریکند.&raquo; بزودی (خدا) کیفر این توصیف (و احکام دروغین) آنها را می&zwnj;دهد؛ او حکیم و داناست.';
$t[140] = 'به یقین آنها که فرزندان خود را از روی جهل و نادانی کشتند، گرفتار خسران شدند؛ (زیرا) آنچه را خدا به آنها روزی داده بود، بر خود تحریم کردند؛ و بر خدا افترا بستند. آنها گمراه شدند؛ و (هرگز) هدایت نیافته بودند.';
$t[141] = 'اوست که باغهای معروش [= باغهایی که درختانش روی داربست&zwnj;ها قرار دارد]، و باغهای غیرمعروش [= باغهایی که نیاز به داربست ندارد] را آفرید؛ همچنین نخل و انواع زراعت را، که از نظر میوه و طعم با هم متفاوتند؛ و (نیز) درخت زیتون و انار را، که از جهتی با هم شبیه، و از جهتی تفاوت دارند؛ (برگ و ساختمان ظاهریشان شبیه یکدیگر است، در حالی که طعم میوه آنها متفاوت می&zwnj;باشد.) از میوه آن، به هنگامی که به ثمر می&zwnj;نشیند، بخورید! و حقّ آن را به هنگام درو، بپردازید! و اسراف نکنید، که خداوند مسرفان را دوست ندارد!';
$t[142] = '(او کسی است که) از چهارپایان، برای شما حیوانات باربر، و حیوانات کوچک (برای منافع دیگر) آفرید؛ از آنچه به شما روزی داده است، بخورید! و از گامهای شیطان پیروی ننمایید، که او دشمن آشکار شماست!';
$t[143] = 'هشت جفت از چهارپایان (برای شما) آفرید؛ از میش دو جفت، و از بز دو جفت؛ بگو: &laquo;آیا خداوند نرهای آنها را حرام کرده، یا ماده&zwnj;ها را؟ یا آنچه شکم ماده&zwnj;ها در برگرفته؟ اگر راست می&zwnj;گویید (و بر تحریم اینها دلیلی دارید)، به من خبر دهید!&raquo;';
$t[144] = 'و از شتر یک جفت، و از گاو هم یک جفت (برای شما آفرید)؛ بگو: &laquo;کدامیک از اینها را خدا حرام کرده است؟ نرها یا ماده&zwnj;ها را؟ یا آنچه را شکم ماده&zwnj;ها دربرگرفته؟ یا هنگامی که خدا شما را به این موضوع توصیه کرد، شما گواه (بر این تحریم) بودید؟! پس چه کسی ستمکارتر است از آن کس که بر خدا دروغ می&zwnj;بندد، تا مردم را از روی جهل گمراه سازد؟! خداوند هیچ گاه ستمگران را هدایت نمی&zwnj;کند&raquo;';
$t[145] = 'بگو: &laquo;در آنچه بر من وحی شده، هیچ غذای حرامی نمی&zwnj;یابم؛ بجز اینکه مردار باشد، یا خونی که (از بدن حیوان) بیرون ریخته، یا گوشت خوک -که اینها همه پلیدند- یا حیوانی که به گناه، هنگام سر بریدن، نام غیر خدا [= نام بتها] بر آن برده شده است.&raquo; اما کسی که مضطر (به خوردن این محرمات) شود، بی آنکه خواهان لذت باشد و یا زیاده روی کند (گناهی بر او نیست)؛ زیرا پروردگارت، آمرزنده مهربان است.';
$t[146] = 'و بر یهودیان، هر حیوان ناخن&zwnj;دار [= حیواناتی که سم یکپارچه دارند] را حرام کردیم؛ و از گاو و گوسفند، پیه و چربیشان را بر آنان تحریم نمودیم؛ مگر چربیهایی که بر پشت آنها قرار دارد، و یا در دو طرف پهلوها، و یا آنها که با استخوان آمیخته است؛ این را بخاطر ستمی که می&zwnj;کردند به آنها کیفر دادیم؛ و ما راست می&zwnj;گوییم.';
$t[147] = 'اگر تو را تکذیب کنند (و این حقایق را نپذیرند)، به آنها بگو: &laquo;پروردگار شما، رحمت گسترده&zwnj;ای دارد؛ اما مجازات او هم از مجرمان دفع شدنی نیست! (و اگر ادامه دهید کیفر شما حتمی است)';
$t[148] = 'بزودی مشرکان (برای تبرئه خویش) می&zwnj;گویند: &laquo;اگر خدا می&zwnj;خواست، نه ما مشرک می&zwnj;شدیم و نه پدران ما؛ و نه چیزی را تحریم می&zwnj;کردیم!&raquo; کسانی که پیش از آنها بودند نیز، همین گونه دروغ می&zwnj;گفتند؛ و سرانجام (طعم) کیفر ما را چشیدند. بگو: &laquo;آیا دلیل روشنی (بر این موضوع) دارید؟ پس آن را به ما نشان دهید؟ شما فقط از پندارهای بی&zwnj;اساس پیروی می&zwnj;کنید، و تخمینهای نابجا می&zwnj;زنید.&raquo;';
$t[149] = 'بگو: &laquo;دلیل رسا (و قاطع) برای خداست (دلیلی که برای هیچ کس بهانه&zwnj;ای باقی نمی&zwnj;گذارد). و اگر او بخواهد، همه شما را (به اجبار) هدایت می&zwnj;کند. (ولی چون هدایت اجباری بی&zwnj;ثمر است، این کار را نمی&zwnj;کند.)&raquo;';
$t[150] = 'بگو: &laquo;گواهان خود را، که گواهی می&zwnj;دهند خداوند اینها را حرام کرده است، بیاورید!&raquo; اگر آنها (بدروغ) گواهی دهند، تو با آنان (همصدا نشو! و) گواهی نده! و از هوی و هوس کسانی که آیات ما را تکذیب کردند، و کسانی که به آخرت ایمان ندارند و برای خدا شریک قائلند، پیروی مکن!';
$t[151] = 'بگو: &laquo;بیایید آنچه را پروردگارتان بر شما حرام کرده است برایتان بخوانم: اینکه چیزی را شریک خدا قرار ندهید! و به پدر و مادر نیکی کنید! و فرزندانتان را از (ترس) فقر، نکشید! ما شما و آنها را روزی می&zwnj;دهیم؛ و نزدیک کارهای زشت نروید، چه آشکار باشد چه پنهان! و انسانی را که خداوند محترم شمرده، به قتل نرسانید! مگر بحق (و از روی استحقاق)؛ این چیزی است که خداوند شما را به آن سفارش کرده، شاید درک کنید!';
$t[152] = 'و به مال یتیم، جز به بهترین صورت (و برای اصلاح)، نزدیک نشوید، تا به حد رشد خود برسد! و حق پیمانه و وزن را بعدالت ادا کنید! -هیچ کس را، جز بمقدار تواناییش، تکلیف نمی&zwnj;کنیم- و هنگامی که سخنی می&zwnj;گویید، عدالت را رعایت نمایید، حتی اگر در مورد نزدیکان (شما) بوده باشد و به پیمان خدا وفا کنید، این چیزی است که خداوند شما را به آن سفارش می&zwnj;کند، تا متذکّر شوید!';
$t[153] = 'این راه مستقیم من است، از آن پیروی کنید! و از راه&zwnj;های پراکنده (و انحرافی) پیروی نکنید، که شما را از طریق حق، دور می&zwnj;سازد! این چیزی است که خداوند شما را به آن سفارش می&zwnj;کند، شاید پرهیزگاری پیشه کنید!&raquo;';
$t[154] = 'سپس به موسی کتاب (آسمانی) دادیم؛ (و نعمت خود را) بر آنها که نیکوکار بودند، کامل کردیم؛ و همه چیز را (که مورد نیاز آنها بود، در آن) روشن ساختیم؛ کتابی که مایه هدایت و رحمت بود؛ شاید به لقای پروردگارشان (و روز رستاخیز)، ایمان بیاورند!';
$t[155] = 'و این کتابی است پر برکت، که ما (بر تو) نازل کردیم؛ از آن پیروی کنید، و پرهیزگاری پیشه نمائید، باشد که مورد رحمت (خدا) قرار گیرید!';
$t[156] = '(ما این کتاب را با این امتیازات نازل کردیم) تا نگویید: &laquo;کتاب آسمانی تنها بر دو طایفه پیش از ما [= یهود و نصاری&zwnj;] نازل شده بود؛ و ما از بحث و بررسی آنها بی خبر بودیم&raquo;!';
$t[157] = 'یا نگویید: &laquo;اگر کتاب آسمانی بر ما نازل می&zwnj;شد، از آنها هدایت یافته&zwnj;تر بودیم&raquo;! اینک آیات و دلایل روشن از جانب پروردگارتان، و هدایت و رحمت برای شما آمد! پس، چه کسی ستمکارتر است از کسی که آیات خدا را تکذیب کرده، و از آن روی گردانده است؟! اما بزودی کسانی را که از آیات ما روی می&zwnj;گردانند، بخاطر همین اعراض بی&zwnj;دلیلشان، مجازات شدید خواهیم کرد!';
$t[158] = 'آیا جز این انتظار دارند که فرشتگان (مرگ) به سراغشان آیند، یا خداوند (خودش) به سوی آنها بیاید، یا بعضی از آیات پروردگارت (و نشانه&zwnj;های رستاخیز)؟! اما آن روز که بعضی از آیات پروردگارت تحقّق پذیرد، ایمان&zwnj;آوردن افرادی که قبلاً ایمان نیاورده&zwnj;اند، یا در ایمانشان عمل نیکی انجام نداده&zwnj;اند، سودی به حالشان نخواهد داشت! بگو: &laquo;(اکنون که شما چنین انتظارات نادرستی دارید،) انتظار بکشید ما هم انتظار (کیفر شما را) می&zwnj;کشیم!&raquo;';
$t[159] = 'کسانی که آیین خود را پراکنده ساختند، و به دسته&zwnj;های گوناگون (و مذاهب مختلف) تقسیم شدند، تو هیچ گونه رابطه&zwnj;ای با آنها نداری! سر و کار آنها تنها با خداست؛ سپس خدا آنها را از آنچه انجام می&zwnj;دادند، با خبر می&zwnj;کند.';
$t[160] = 'هر کس کار نیکی بجا آورد، ده برابر آن پاداش دارد، و هر کس کار بدی انجام دهد، جز بمانند آن، کیفر نخواهد دید؛ و ستمی بر آنها نخواهد شد.';
$t[161] = 'بگو: &laquo;پروردگارم مرا به راه راست هدایت کرده؛ آیینی پابرجا (و ضامن سعادت دین و دنیا)؛ آیین ابراهیم؛ که از آیینهای خرافی روی برگرداند؛ و از مشرکان نبود.&raquo;';
$t[162] = 'بگو: &laquo;نماز و تمام عبادات من، و زندگی و مرگ من، همه برای خداوند پروردگار جهانیان است.';
$t[163] = 'همتایی برای او نیست؛ و به همین مأمور شده&zwnj;ام؛ و من نخستین مسلمانم!&raquo;';
$t[164] = 'بگو: &laquo;آیا غیر خدا، پروردگاری را بطلبم، در حالی که او پروردگار همه چیز است؟! هیچ کس، عمل (بدی) جز به زیان خودش، انجام نمی&zwnj;دهد؛ و هیچ گنهکاری گناه دیگری را متحمّل نمی&zwnj;شود؛ سپس بازگشت همه شما به سوی پروردگارتان است؛ و شما را از آنچه در آن اختلاف داشتید، خبر خواهد داد.';
$t[165] = 'و او کسی است که شما را جانشینان (و نمایندگان) خود در زمین ساخت، و درجات بعضی از شما را بالاتر از بعضی دیگر قرار داد، تا شما را به وسیله آنچه در اختیارتان قرار داده بیازماید؛ به یقین پروردگار تو سریع العقاب و آمرزنده مهربان است. (کیفر کسانی را که از بوته امتحان نادرست درآیند، زود می&zwnj;دهد؛ و نسبت به حق پویان مهربان است.)';
?>