$t[19] = 'Ya ce: &quot;Abin sani kawai, ni Manzon Ubangijinki ne d&otilde;min in b&atilde;yar da wani y&atilde;ro tsarkakke gare ki.&quot;';
$t[20] = 'Ta ce: &quot;A in&atilde; y&atilde;ro zai kasance a gare ni alh&atilde;li kuwa wani mutum bai sh&atilde;fe ni ba, kuma ban kasance k&atilde;ruwa ba?&quot;';
$t[21] = 'Ya ce: &quot;Kamar wancan Ubangijinki Ya ce. Shĩ, a gare Ni mai sauƙi ne. Kuma d&otilde;min Mu sanya shi wata al&atilde;ma ga mut&atilde;ne, kuma wata rahama ce daga gare Mu.\' Kuma abin y&atilde; kasance wani al\'amari hukuntacce.&quot;';
$t[22] = 'Sai ta yi cikinsa, sai ta tsallake da shi ga wani wuri mai nĩsa.';
$t[23] = 'Sai n&atilde;ƙuda ta kai ta zuwa ga wani kututturen dabĩniya, ta ce &quot;Kaitona, d&atilde; dai na mutu a gab&atilde;nin wannan kuma na kasance wani abu wulakantacce wanda aka manta!&quot;';
$t[24] = 'Sai (y&atilde;ron da ta haifa) ya kira ta daga ƙarƙashinta, &quot;Kada ki yi baƙin ciki! Haƙĩƙa Ubangijinki Ya sanya wani marmaro a ƙarƙashinki.';
$t[25] = '&quot;Kuma ki girgiza zuwa gare ki game da kututturen dabĩnon ya zuba a kanki yan&atilde; \'ya\'yan dabĩno, ruɗabi nunannu.&quot;';
$t[26] = '&quot;Sai ki ci kuma ki sha kuma ki ji sanyi ga id&atilde;nunki. To, idan kin ga wani aya daga mut&atilde;ne, sai ki ce, \'Lalle nĩ, na yi alw&atilde;shin azumi d&otilde;min Mai rahama sab&otilde;da haka b&atilde; zan yi wa wani mutum magana ba.&quot;';
$t[27] = 'Sai ta je wa mut&atilde;nenta tan&atilde; auke da shi. Suka ce: &quot;Y&atilde; Maryamu! Lalle ne, haƙĩƙa kin zo da wani abu mai girma!';
$t[28] = '&quot;Y&atilde; \'yar\'uwar H&atilde;rũna! Ubanki bai kasance mutumin alf&atilde;sha ba, kuma uwarki ba ta kasance k&atilde;ruwa ba.&quot;';
$t[29] = 'Sai ta yi ish&atilde;ra zuwa gare shi, suka ce: &quot;Y&atilde;ya z&atilde; mu yi magana da wanda ya kasance a cikin shimfiar tsumma yan&atilde; j&atilde;rĩri?&quot;';
$t[30] = 'Ya ce: &quot;Lalle ne, nĩ b&atilde;wan Allah ne Allah Y&atilde; b&atilde; ni Litt&atilde;fi kuma Ya sanya ni Annabi.&quot;';
$t[31] = '&quot;Kuma Y&atilde; sanya ni mai albarka a inda duk na kasance kuma Ya umurce ni da yin salla da zakka matuƙar in&atilde; da rai.&quot;';
$t[32] = '&quot;Kuma mai biyayya ga uw&atilde;ta, kuma bai sanya ni mai kaushin zũciya ba marashin alhẽri.&quot;';
$t[33] = '&quot;Kuma aminci ya tabbata a gare ni a r&atilde;nar da aka haife ni da r&atilde;nar da nake mutũwa da r&atilde;nar da ake t&atilde;yar da ni in&atilde; mai rai.&quot;';
$t[34] = 'Wancan ne Ĩs&atilde; ɗan Maryamu, maganar gaskiya wadda suke shakka a cikinta.';
$t[35] = 'B&atilde; ya kasancẽwa ga Allah Ya riƙi wani ɗ&atilde;. Tsarki ya tabbata a gare shi! Idan Y&atilde; hukunta wani al\'amari sai kawai Ya ce masa, &quot;Kasance.&quot; Sai ya dinga kasan cẽwa.';
$t[36] = '&quot;Kuma lalle Allah ne Ubangijina kuma Ubangijinku, sai ku bauta Masa. Wannan shi ne tafarki madaidaici.&quot;';
$t[37] = 'Sai ƙungiy&otilde;yin suka s&atilde;ɓ&atilde; wa jũna a tsak&atilde;ninsu. To, b&otilde;ne ya tabbata ga waɗanda suka k&atilde;firta daga halartar yini mai girma.';
$t[38] = 'Mẽne ne ya yi jinsu, kuma mẽne ne ya yi ganinsu a r&atilde;nar da suke zo Mana! Amma azz&atilde;lumai sun&atilde; a cikin ɓata bayyananna.';
$t[39] = 'Kuma ka yi musu gargaɗi da r&atilde;nar nad&atilde;ma a l&otilde;kacin da aka hukunta al\'amari alh&atilde;li kuwa sun&atilde; a cikin ɓ&atilde;ta, kuma sũ b&atilde; su yin ĩm&atilde;ni.';
$t[40] = 'Lalle ne Mũ, Mũ ne ke g&atilde;don ƙasa da wanda yake a kanta, kuma zuwa gare Mu ake mayar da su.';
$t[41] = 'Kuma ambaci Ibr&atilde;hĩm a cikin Littafi. Lalle shi ya kasance mai yawan gaskat&atilde;wa, Annabi.';
$t[42] = 'A l&otilde;kacin da ya ce wa ubansa, &quot;Y&atilde; b&atilde;ba! Don me kake bauta wa abin da b&atilde; ya ji, kuma b&atilde; ya ga ni, kuma ba ya wad&atilde;tar da k&otilde;me daga barinka?&quot;';
$t[43] = '&quot;Y&atilde; b&atilde;ba! Lalle ni, haƙĩƙa abin da bai je maka ba na ilmi ya zo mini, sab&otilde;da haka ka bĩ ni in shiryar da kai wani tafarki madaidaici.&quot;';
$t[44] = '&quot;Y&atilde; b&atilde;ba! Kada ka bauta wa Shaiɗan. Lalle Shaiɗan ya kasance mai saɓ&atilde;wa ga Mai rahama.&quot;';
$t[45] = '&quot;Y&atilde; b&atilde;ba! Lalle ne ni in&atilde; ts&otilde;ron wata az&atilde;ba daga Mai rahama ta sh&atilde;fe ka, har ka zama mas&otilde;yi ga Shaiɗan.&quot;';
$t[46] = 'Ya ce: &quot;Ashe, mai gudu ne kai daga gum&atilde;k&atilde;na? Y&atilde; Ibr&atilde;ĩm! Lalle ne, idan ba ka hanu ba, haƙĩ ƙa, zan jẽfe ka. Kuma ka ƙaurace mini tun kan&atilde; mai mutunci.&quot;';
$t[47] = 'Ya ce: &quot;Aminci ya tabbata a gare ka! zan nẽmi Ubangijina Ya g&atilde;farta maka. Lalle Shi Ya kasance Mai girmam&atilde;wa gare ni.&quot;';
$t[48] = '&quot;Kuma in&atilde; nĩsantar ku da abin da kuke kira, baicin Allah kuma in&atilde; kiran Ubangijina, tsamm&atilde;nin kada in zama marashin arziki game da kiran Ubangijina.&quot;';
$t[49] = 'To, sa\'ad da ya nĩsance su da abin da suke baut&atilde;wa baicin Allah, Muka b&atilde; shi Is\'h&atilde;ƙa da Ya\'aƙuba alh&atilde;li kuwa k&otilde;wanensu Mun mayar da shi Annabi.';
$t[50] = 'Kuma Muka yi musu kyauta daga RahamarMu, kuma Muka sanya musu harshen gaskiya maɗaukaki.';
$t[51] = 'Kuma ka ambaci Mũs&atilde; a cikin Litt&atilde;fi. Lalle ne shi, y&atilde; kasance z&atilde;ɓaɓɓe, kuma y&atilde; kasance Manzo, Annabi.';
$t[52] = 'Kuma Muka kira shi daga gẽfen dũtse na d&atilde;ma kuma Muka kusanta shi yan&atilde; ab&otilde;kin g&atilde;n&atilde;wa.';
$t[53] = 'Kuma Muka yi masa kyauta daga RahamarMu da ɗan\'uwansa H&atilde;rũna, ya zama Annabi.';
$t[54] = 'Kuma ka ambaci Ism&atilde;\'ila a cikin Litt&atilde;fi. Lalle shi, y&atilde; kasance mai gaskiyar alkawari, kuma y&atilde; kasance Manzo, Annabi.';
$t[55] = 'Kuma y&atilde; kasance yan&atilde; umurnin mut&atilde;nens da salla da zakka. Kuma y&atilde; kasance yardajje a wurin Ubangijinsa.';
$t[56] = 'Kuma ka ambaci Idrĩsa a cikin Litt&atilde;fi. Lalle shi, ya kasance mai yawan gaskat&atilde;wa, Annabi.';
$t[57] = 'Muka ɗaukaka shi a wuri maɗaukaki.';
$t[58] = 'Waɗancan sũ ne waɗanda Allah Ya yi wa ni\'ima daga Annab&atilde;wa daga zurriyar &Atilde;damu, kuma daga waɗanda muka ɗauka t&atilde;re da Nũhu, kuma daga zurriyar Ibr&atilde;hĩm da Isr&atilde;\'ila, kuma daga waɗanda Muka shiryar kuma Muka z&atilde;ɓe su. Idan an&atilde; kar&atilde;tun &atilde;y&otilde;yin Mai rahama a kansu, sai su f&atilde;ɗi sun&atilde; m&atilde;su sujada kuma m&atilde;su kũka.';
$t[59] = 'Sai waɗansu \'yan b&atilde;ya suka maye a b&atilde;yansu suka t&otilde;zarta salla, kuma suka bi sha\'aw&otilde;winsu. To, da sannu z&atilde; su hau da wani sharri.';
$t[60] = 'F&atilde;ce wanda ya tũba, kuma ya yi ĩm&atilde;ni, kuma ya aikata aiki na ƙwarai. To, waɗannan sun&atilde; shiga Aljanna, kuma b&atilde; a z&atilde;luntar su da k&otilde;me.';
$t[61] = 'Gid&atilde;jen Aljannar zama wadda Mai rahama ya yi alkawarin b&atilde;yarwa ga b&atilde;yinSa (m&atilde;su aikin ĩm&atilde;ni) a fake. Lalle ne shĩ, alkawarin ya kasance abin riskuwa.';
$t[62] = 'B&atilde; su jin yasassar magana a cikinta, f&atilde;ce sallama. Kuma sun&atilde; da abinci, a cikinta, s&atilde;fe da maraice.';
$t[63] = 'Wancan Aljannar ce wadda Muke g&atilde;dar da wanda ya kasance mai aiki da taƙawa daga b&atilde;yiNa.';
$t[64] = '(Sun&atilde; m&atilde;su cẽwa) &quot;Kuma b&atilde; mu sauka F&atilde;ce da umuruin Ubangijinka (Muhammad). Shĩ ne da mulkin abin da ke a gaba gare mu da abin da ke a b&atilde;yanmu da abin da ke a tsak&atilde;nin wannan.&quot; Kuma (Ubangijinka bai kasance wanda ake mant&atilde;wa ba.';
$t[65] = 'Shi ne Ubangijin sammai da ƙasa da abin da yake a tsak&atilde;ninsu. sai ka bauta Masa, kuma ka yi haƙuri ga bautarsa. Shin k&atilde; san wani takwara a gare shi?';
$t[66] = 'Kuma mutum yana cẽwa, &quot;Shin idan na mutu lalle ne haƙĩ ƙa da sannu z&atilde; a fitar da ni in&atilde; mai rai?&quot;';
$t[67] = 'Shin, kuma mutum b&atilde; zai tuna ba cẽwa lalle ne Mun halitta shi a gab&atilde;ni, alh&atilde;li kuwa bai kasance k&otilde;me ba?';
$t[68] = 'To, Mun&atilde; rantsuwa da Ubangijinka, lalle ne, Muna t&atilde;yar da su da kuma shaianun sa\'an nan, kuma lalle Muna halatar da su da kuma Shaiɗanun sa\'an nan, kuma, lalle Muna halatar da su a gẽfen Jahannama sun&atilde; gurf&atilde;ne.';
$t[69] = 'Sa\'an nan kuma lalle Mun&atilde; fizge wanda yake mafi tsananin girman kai ga Mai rahama daga k&otilde;wace ƙungiya.';
$t[70] = 'Sa\'an nan kuma lalle Mũ ne Mafi sani ga waɗanda suke sũ ne mafiya cancantar ƙ&otilde;nuwa da ita.';
$t[71] = 'Kuma b&atilde;bu k&otilde;wa daga gare ku sai mai tuzga mata. Y&atilde; kasance wajibi ga Ubangijinka, hukuntacce.';
$t[72] = 'Sa\'an nan kuma Mu tsẽrar da waɗanda suka yi aiki da taƙawa, kuma Mu bar azz&atilde;lumai a cikinta gurf&atilde;ne.';
$t[73] = 'Kuma idan an&atilde; karatun &atilde;y&otilde;yinMu, bayyanannu a kansu, sai waɗanda suka k&atilde;firta su ce wa waɗanda suka yi ĩm&atilde;ni, &quot;Wanne daga ƙungiy&otilde;yin biyu ya fi zama mafi alhẽri ga matsayi, kuma mafi kyaun majalisa?&quot;';
$t[74] = 'Kuma da yawa daga mut&atilde;nen ƙarni Muka halakar a gab&atilde;ninsu sũ (waɗandaMuka halakar) in ne mafi kyaun k&atilde;yan &atilde;ki da mag&atilde;n&atilde;.';
$t[75] = 'Ka ce: &quot;Wanda ya kasance a cikin ɓata sai Mai rahama Ya yalwata masa yalwat&atilde;wa, har idan sun ga abin da ake yi musu wa\'adi, imma az&atilde;ba k&otilde; s&atilde;\'a, to z&atilde; su sani, wane ne yake shĩ ne, mafi sharri ga wuri, kuma mafi rauni ga runduna!&quot;';
$t[76] = 'Kuma Allah na ƙ&atilde;ra wa wa ɗanda suka nẽmi shiryuwa da shiriya. Kuma ayyuka m&atilde;su wanzuwa na ƙwarai ne mafi alhẽri awurin Ubangijinka ga l&atilde;da, kuma mafi alhẽri ga mak&otilde;ma.';
$t[77] = 'Shin, ka ga wanda ya k&atilde;firta da ay&otilde;yinMu, kuma ya ce: &quot;Lalle ne z&atilde; a b&atilde; ni dũkiya da ɗiya?&quot;';
$t[78] = 'Shin, y&atilde; tsink&atilde;yi gaibi ne, k&otilde; kuwa y&atilde; ɗauki wani alkawari daga Mai rahama ne?';
$t[79] = '&Atilde;\'aha! z&atilde; mu rubũta abin da yake faɗa, kuma Mu yalwata masa, daga az&atilde;ba, yalwat&atilde;wa.';
$t[80] = 'Kuma Mu g&atilde;de shi ga abin da yake faɗa, kuma ya zo Mana yan&atilde; shi kɗai.';
$t[81] = 'Kuma suka riƙi gum&atilde;ka, baicin Allah, d&otilde;min su k&atilde;sance mataimaka a gare su.';
$t[82] = '&Atilde;\'aha! Z&atilde; su k&atilde;firta da ib&atilde;darsu, kuma su kasance maƙiya a kansu.';
$t[83] = 'Shin, ba ka gani ba cẽwa Mun saki shaiɗanu a kan k&atilde;firai sun&atilde; shũshũta su ga zunubi shũshũt&atilde;wa?';
$t[84] = 'Sab&otilde;da haka kada ka yi gaggawa a kansu, Mun&atilde; yi musu ƙid&atilde;yar ajali ne kawai, ƙid&atilde;y&atilde;wa.';
$t[85] = 'A r&atilde;nar da Muka t&atilde;ra m&atilde;su taƙawa zuwa ga Mai rahama sun&atilde; b&atilde;ƙin girma.';
$t[86] = 'Kuma Mun&atilde; k&otilde;ra m&atilde;su laifi zuwa Jahannama, garga&atilde;wa.';
$t[87] = 'Ba su mal1akar cẽto f&atilde;ce wanda ya riƙi alkawari a wurin Mai rahama.';
$t[88] = 'Kuma suka ce: &quot;Mai rahama Y&atilde; riƙi &atilde;!&quot;';
$t[89] = 'Lalle ne haƙĩƙa kun zo da wani abu mai girman mũni.';
$t[90] = 'Sammai sun&atilde; kusa su tsattsage sab&otilde;da shi, kuma ƙasa ke kẽce kuma duw&atilde;tsu su faɗi sun&atilde; karyayyu.';
$t[91] = 'D&otilde;min sun yi da\'awar ɗ&atilde; ga Mai rahama.';
$t[92] = 'Alh&atilde;li b&atilde; ya kamata ga Mai rahama ya riƙi wani ɗ&atilde;.';
$t[93] = 'Dukkan wanda yake a cikin sammai da ƙasa bai zama ba f&atilde;ce mai jẽ wa Mai rahama ne yan&atilde; b&atilde;w&atilde;.';
$t[94] = 'Lalle ne haƙĩƙa Y&atilde; lissafe su, kuma ya ƙid&atilde;ye su, ƙid&atilde;ya.';
$t[95] = 'Kuma dukan k&otilde;wanensu mai jẽ Masa ne a R&atilde;nar &iexcl;iy&atilde;ma yan&atilde; shi kaɗai.';
$t[96] = 'Lalle ne waɗanda suka yi ĩm&atilde;ni, kuma suka aikata ayyuka na ƙwarai, Mai rahama zai sanya musu so.';
$t[97] = 'Sa\'an nan Ibin sani kawai Mun sauƙaƙar da shi (Alƙur\'&atilde;ni) a harshenka, d&otilde;min ka yi bush&atilde;ra da shi ga m&atilde;su aiki da taƙawa kuma ka yi gargaɗi da shi ga mut&atilde;ne m&atilde;su tsananin husũma.';
$t[98] = 'Kuma da yawa Muka halakar da mut&atilde;nen ƙarn&otilde;ni a gab&atilde;ninsu. Shin kan&atilde; jin m&otilde;tsin wani guda daga gare su, k&otilde; kuwa kan&atilde; jin wata ɗuriya t&atilde;su?';
?>