$t[153] = 'Suka ce: &quot;Kai daga m&atilde;su sihiri kurum kake.&quot;';
$t[154] = '&quot;B&atilde; k&otilde;wa kake ba, f&atilde;ce mutum kamarmu. To, k&atilde; zo da wata &atilde;y&atilde; idan k&atilde; kasance daga m&atilde;su gaskiya.&quot;';
$t[155] = 'Ya ce: &quot;Wannan r&atilde;kuma ce tan&atilde; da shan yini, kuma kun&atilde; da shan yini sasanne.&quot;';
$t[156] = '&quot;Kada ku sh&atilde;fe ta da cũta har az&atilde;bar yini mai girm&atilde; ta sh&atilde;fe ku.&quot;';
$t[157] = 'Sai suka s&otilde;ke ta sa\'an nan suka w&atilde;yi gari sun&atilde; m&atilde;su nad&atilde;ma.';
$t[158] = 'Sab&otilde;da haka az&atilde;ba ta k&atilde;ma su. Lalle ne a cikin wannan akwai &atilde;y&atilde;, kuma mafi yawansu, ba su kasance m&atilde;su ĩm&atilde;ni ba.';
$t[159] = 'Lalle ne Ubangijinka, haƙĩ ƙa, Shĩ ne Mabuwayi, Mai jin ƙai.';
$t[160] = 'Mut&atilde;nen Lũɗu sun ƙaryata Manzanni.';
$t[161] = 'A l&otilde;kacin da ɗan\'uwansu, Lũɗu ya ce musu, &quot;B&atilde; z&atilde; ku yi taƙawa ba?&quot;';
$t[162] = '&quot;Lalle ne nĩ, zuwa gare ku, Manz&otilde; ne, amintacce.&quot;';
$t[163] = '&quot;Sab&otilde;da haka ku bi Allah da taƙawa, kuma ku yi mini ɗ&atilde;\'&atilde;.&quot;';
$t[164] = '&quot;Kuma b&atilde; ni tambayar ku wata ij&atilde;ra, Ij&atilde;r&atilde;ta ba ta zama ba f&atilde;ce daga Ubangijin halittu.&quot;';
$t[165] = '&quot;Shin kun&atilde; jẽ wa maza daga cikin talikai?&quot;';
$t[166] = '&quot;Kuma kun&atilde; barin abin da Ubangijinku Ya halitta muku daga m&atilde;tanku? &Atilde;\'a, ku mut&atilde;ne ne m&atilde;su ƙẽtarẽwa!&quot;';
$t[167] = 'Suka ce: &quot;Lalle ne haƙĩƙa idan ba ka hanu ba, y&atilde; Lũɗu! Tĩlas ne kan&atilde; kasancẽwa daga waɗanda ake fitarwa (daga gari).&quot;';
$t[168] = 'Ya ce: &quot;Lalle ne ga aikinku, haƙĩƙa, in&atilde; daga m&atilde;su ƙinsa.&quot;';
$t[169] = '&quot;Ya Ubangijina! Ka tsĩrar da ni da iy&atilde;lĩna daga abin da suke aikat&atilde;wa.&quot;';
$t[170] = 'Sab&otilde;da haka Muka tsĩrar da shi, Shi da mut&atilde;nensa gab&atilde; ɗaya.';
$t[171] = 'F&atilde;ce wata ts&otilde;huwa a cikin m&atilde;su wanzuwa.';
$t[172] = 'Sa\'an nan kuma Muka dark&atilde;ke wasu.';
$t[173] = 'Kuma Muka yi ruwan sama a kansu ruwa, sai dai ruwan waɗanda ake yi wa gargaɗi ya mũnana.';
$t[174] = 'Lalle ne ga wannan, akwai &atilde;y&atilde;, kuma mafi yawansu b&atilde; su kasance m&atilde;su ĩm&atilde;ni ba.';
$t[175] = 'Kuma lalle ne Ubangijinka, haƙĩƙa, Shĩ ne Mabuw&atilde;yi, Mai jin ƙai.';
$t[176] = 'Ma\'abũta ƙunci sun ƙaryata Manzanni.';
$t[177] = 'A l&otilde;kacin da Shu\'aibu ya ce musu, &quot;B&atilde; z&atilde; ku yi taƙawa ba?&quot;';
$t[178] = '&quot;Lalle ne ni, zuwa gare ku, Manzo ne, amintacce.&quot;';
$t[179] = '&quot;Sab&otilde;da haka ku bi Allah da taƙawa kuma ku yi mini ɗ&atilde;\'&atilde;.&quot;';
$t[180] = '&quot;Kuma ba ni tambayar ku wata ij&atilde;ra a kansa. Ij&atilde;rata ba ta zama ba f&atilde;ce daga Ubangijin halittu.&quot;';
$t[181] = '&quot;Ku cika mũdu, kuma kada ku kasance daga m&atilde;su sany&atilde;war has&atilde;ra (ga mut&atilde;ne).&quot;';
$t[182] = '&quot;Kuma ku yi awo da sikẽli daidaitacce.&quot;';
$t[183] = '&quot;Kuma kada ku nakasa wa mut&atilde;ne abũbuwansu kuma kada ku yi fas&atilde;di a cikin ƙasa kuna m&atilde;su ɓarna.&quot;';
$t[184] = '&quot;Kuma ku ji ts&otilde;ron Allah wanda Ya halitta ku, kũ da jama\'ar farko.&quot;';
$t[185] = 'Suka ce: &quot;Kai dai daga waɗanda suke sihirtattu kawai ne.&quot;';
$t[186] = '&quot;Kuma b&atilde; k&otilde;wa kake ba, f&atilde;ce mutum kamarmu kuma lalle ne mun&atilde; zaton ka, haƙĩƙa, daga maƙaryata.&quot;';
$t[187] = '&quot;To ka jẽfo wani ɓaɓɓake daga sama a kanmu, idan ka kasance daga m&atilde;su gaskiya.&quot;';
$t[188] = 'Ya ce: &quot;Ubangijĩna ne Mafi sani ga abin da kuke aikat&atilde;wa.&quot;';
$t[189] = 'Sai suka ƙaiyat&atilde; shi, sab&otilde;da haka, az&atilde;bar r&atilde;nar girgije ta k&atilde;ma su. Lalle ne ita, t&atilde; kasance az&atilde;bar yini mai girma.';
$t[190] = 'Lalle a cikin wancan haƙĩka akwai &atilde;ya, amma mafi yawansu ba su kasance m&atilde;su ĩm&atilde;ni ba.';
$t[191] = 'Kuma lalle Ubangijĩnka, haƙĩƙa, Shĩ ne Mabuw&atilde;yi, Mai jin ƙai.';
$t[192] = 'Kuma lalle shi (Alƙur\'&atilde;ni), haƙĩƙa, saukarwar Ubangijinhalittu ne.';
$t[193] = 'Rũhi amintacce ne ya sauka da shi.';
$t[194] = 'A kan zũciyarka, d&otilde;min ka kasance daga m&atilde;su gargaɗi.';
$t[195] = 'Da harshe na Larabci mai bay&atilde;ni.';
$t[196] = 'Kuma lalle shin, haƙĩƙa, yan&atilde; a cikin litattafan (Manzannin) farko.';
$t[197] = 'Ashe, bai kasance &atilde;y&atilde; ba a gare su, ya zama M&atilde;laman Banĩ Isr&atilde;\'ĩla sun san shi?';
$t[198] = 'Kuma d&atilde; mun saukar da shi a kan s&atilde;shen Ajam&atilde;wa,';
$t[199] = 'Ya karanta shi a kansu, ba su kasance sab&otilde;da shi m&atilde;su ĩm&atilde;ni ba.';
$t[200] = 'Kamar wancan ne! Muka shigar da shi a cikin zuk&atilde;tan m&atilde;su laifi.';
$t[201] = 'B&atilde; z&atilde; su yi ĩm&atilde;ni da shĩ ba sai sun ga az&atilde;bar nan mai raɗadi.';
$t[202] = 'Sai ta taho musu kwatsam, alh&atilde;li kuwa sũ, ba su sansance ba.';
$t[203] = 'Sai su ce: &quot;Shin, mu waɗanda ake yi wa jinkiri ne?&quot;';
$t[204] = 'Ashe, to, da az&atilde;barMu suke nẽman gagg&atilde;wa?';
$t[205] = 'Ashe, to, k&atilde; gani, idan Muka jĩshe su daɗi a shẽkaru,';
$t[206] = 'Sa\'an nan abin da suka kasance an&atilde; yi musu wa\'adi (da shi) ya jẽ musu,';
$t[207] = 'Abin da suka kasance an&atilde; jĩshe su d&atilde;ɗin b&atilde; zai tunkuɗe az&atilde;ba ba daga gare su.';
$t[208] = 'Kuma b&atilde; Mu halakar da wata alƙarya ba f&atilde;ce tan&atilde; da m&atilde;su gargaɗi.';
$t[209] = 'D&otilde;min tun&atilde;tarwa, kuma ba Mu kasance M&atilde;su z&atilde;lunci ba.';
$t[210] = 'Kuma (Alƙur\'&atilde;ni) Shaiɗ&atilde;nu ba su ɗ&otilde;ra sauka da shĩba.';
$t[211] = 'Kuma b&atilde; ya kam&atilde;ta a gare su (su Shaiɗ&atilde;nu su sauka da shi), kuma ba su iy&atilde;wa.';
$t[212] = 'Lalle ne sũ, daga saur&atilde;re, haƙĩƙa, waɗanda aka nĩsantar ne.';
$t[213] = 'Sab&otilde;da haka, kada ka kira wani abin baut&atilde;wa na dabam t&atilde;re da Allah, sai ka kasance daga waɗanda ake yi wa az&atilde;ba.';
$t[214] = 'Kuma ka yi gargaɗi ga danginka mafiya kusanci.';
$t[215] = 'Kuma ka sassauta fik&atilde;fikanka ga wanda ya bĩ ka daga mũminai.';
$t[216] = 'Sa\'an nan idan suka s&atilde;ɓ&atilde; maka to ka ce: &quot;Lalle nĩ, barrantacce ne daga abin da kuko aikat&atilde;wa.&quot;';
$t[217] = 'Kuma ka d&otilde;gara ga Mabuw&atilde;yi, Mai jin ƙai.';
$t[218] = 'Wanda Yake ganin ka a l&otilde;kacin da kake t&atilde;shi tsaye.';
$t[219] = 'Da jujjuy&atilde;warka a cikin m&atilde;su yin sujada.';
$t[220] = 'Lalle Shi, Shi ne Mai ji, Masani.';
$t[221] = 'Shin, (kun&atilde; so) in gaya muku a kan wanda Shaiɗ&atilde;nnu kan sauka?';
$t[222] = 'Sukan sauka a kan dukkan maƙaryaci, mai zunubi.';
$t[223] = 'Sun&atilde; jẽfa (abin da suka) ji, alh&atilde;li kuwa mafi yawansu maƙaryata ne.';
$t[224] = 'Kuma maw&atilde;ƙa halakakku ne ke bin su.';
$t[225] = 'Shin, ba ku cẽwa, lalle ne sũ, cikin k&otilde;wane rango sun&atilde; yin ɗimuwa (su ƙẽtare haddi) ba?';
$t[226] = 'Kuma lalle ne, sũ, sun&atilde; faɗin abin da b&atilde; su aikat&atilde;wa?';
$t[227] = 'F&atilde;ce waɗanda suka yi ĩm&atilde;ni suka aikata ayyukan ƙwarai, kuma suka ambaci Allah da yawa, kuma suka r&atilde;ma z&atilde;lunci, daga b&atilde;yan an z&atilde;lunce su. Kuma waɗanda suka yi z&atilde;lunci, z&atilde; su sani a wace majũya suke jũy&atilde;wa.';
?>