<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقرة) بده&zwnj;، ئه&zwnj;و ئایه&zwnj;تانه&zwnj;ی له&zwnj;م سووره&zwnj;ته&zwnj;دا هاتووه چه&zwnj;ند ئایه&zwnj;تێکی ئه&zwnj;و قورئانه ڕوون و ئاشکرایه&zwnj;یه&zwnj;.';
$t[2] = 'ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون (له ئاینده&zwnj;یه&zwnj;کی نزیکدا) ئاواته&zwnj;خواز ده&zwnj;بن که خۆزگه موسڵمان بوونایه&zwnj;، خۆزگه ئیماندار بوونایه&zwnj;.';
$t[3] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) جارێ وازیان لێبهێنه با بخۆن و ڕابوێرن و هیواو ئاواتیان سه&zwnj;ر گه&zwnj;رم و بێ ئاگایان بکات، سه&zwnj;ره&zwnj;نجام خۆیان ده&zwnj;بیننه&zwnj;وه (چیان به&zwnj;سه&zwnj;ر دێت).';
$t[4] = 'هیچ شارو شارۆچکه&zwnj;یه&zwnj;کمان وێران نه&zwnj;کردووه که له&zwnj;و کاته&zwnj;دا نامه&zwnj;و کتێبێکی دیاری کراوی نه&zwnj;بووبێت.';
$t[5] = 'هیچ میلله&zwnj;ت و گه&zwnj;لێك پێش کاتی دیاری کراوی له&zwnj;ناو بردنی خۆی ناکه&zwnj;وێت و (هه&zwnj;رگیز) دواش ناکه&zwnj;وێت.';
$t[6] = '(خوا نه&zwnj;ناسان به گاڵته&zwnj;وه به پێغه&zwnj;مبه&zwnj;ریان صلی الله علیه وسلم) ده&zwnj;وت: ئه&zwnj;ی ئه&zwnj;و که&zwnj;سه&zwnj;ی که قورئانت دابه&zwnj;زێنراوه&zwnj;ته سه&zwnj;ر بێگومان تۆ شێتی!!';
$t[7] = 'ئه&zwnj;وه بۆچی فریشته&zwnj;ت بۆ نه&zwnj;هێناین ئه&zwnj;گه&zwnj;ر تۆ له ده&zwnj;سته&zwnj;ی ڕاستگۆیانیت (تا شایه&zwnj;تی له&zwnj;سه&zwnj;ر ڕاستی تۆ بده&zwnj;ن)!';
$t[8] = '(خوای گه&zwnj;وره&zwnj;ش بۆ په&zwnj;رچدانه&zwnj;وه&zwnj;یان ده&zwnj;فه&zwnj;رموێت) ئێمه فریشته دانابه&zwnj;زێنین ته&zwnj;نها بۆ چه&zwnj;سپاندنی حه&zwnj;ق و پشتگیری ڕاستی نه&zwnj;بێت، ئه&zwnj;و کاته&zwnj;ش (که دابه&zwnj;زین، خوانه&zwnj;ناسان) مۆڵه&zwnj;ت نادرێن و (له&zwnj;ناو ده&zwnj;برێن).';
$t[9] = 'به&zwnj;ڕاستی هه&zwnj;ر ئێمه قورئانمان دابه&zwnj;زاندووه&zwnj;و بێگومان هه&zwnj;ر ئێمه&zwnj;ش پارێزگاری ده&zwnj;که&zwnj;ین و (نایه&zwnj;ڵین ده&zwnj;ستی خیانه&zwnj;تکار هیچ جۆره ده&zwnj;ستێکی تێ بخات و هه&zwnj;موو هه&zwnj;وڵێك بۆ ده&zwnj;ستکاری کردنی نه&zwnj;زۆکه&zwnj;).';
$t[10] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه پێغه&zwnj;مبه&zwnj;رانمان پێش تۆ ڕه&zwnj;وانه کردووه&zwnj;، بۆ سه&zwnj;ر گه&zwnj;ل و هۆزو میلله&zwnj;تانی پێشوو.';
$t[11] = 'خوا نه&zwnj;ناسان هیچ پێغه&zwnj;مبه&zwnj;رێکیان بۆ نه&zwnj;هاتووه که گاڵته&zwnj;یان پێ نه&zwnj;کردبێت.';
$t[12] = 'ئا به&zwnj;و شێوه&zwnj;یه ئێمه قورئان ده&zwnj;ده&zwnj;ین به گوێ و دڵی تاوانباران و تاوانکاراندا (تا به&zwnj;ڵگه بێت له&zwnj;سه&zwnj;ریان).';
$t[13] = 'له کاتێکدا باوه&zwnj;ڕی پێ ناهێنن و هه&zwnj;ر له&zwnj;سه&zwnj;ر به&zwnj;رنامه&zwnj;ی پێشینان ده&zwnj;ڕۆن.';
$t[14] = '(ئه&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;) ئه&zwnj;گه&zwnj;ر ده&zwnj;روازه&zwnj;یه&zwnj;کمان له ئاسمانه&zwnj;وه بۆ بکردنایه&zwnj;ته&zwnj;وه تا پیادا سه&zwnj;ربکه&zwnj;ون و تێپه&zwnj;ر ببن و (نهێنی دروستکراوانی ئێمه ببینن، هه&zwnj;ر باوه&zwnj;ڕ ناهێنن)...';
$t[15] = 'ئه&zwnj;وه ده&zwnj;یانوت: بێگومان چاوبه&zwnj;ستمان لێکراوه&zwnj;، یاخود ئێمه که&zwnj;سانێکی جادوو لێکراوین!!';
$t[16] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه له ئاسماندا چه&zwnj;ند خولگه&zwnj;یه&zwnj;کی گه&zwnj;وره&zwnj;مان به&zwnj;دی هێناوه بۆ ئه&zwnj;ستێره&zwnj;کان و ڕازاندوومانه&zwnj;ته&zwnj;وه بۆ ئه&zwnj;وانه&zwnj;ی که به وردی ته&zwnj;ماشای ده&zwnj;که&zwnj;ن (تا گه&zwnj;وره&zwnj;یی ده&zwnj;سه&zwnj;ڵاتی په&zwnj;روه&zwnj;ردگاریان بۆ ده&zwnj;ربکه&zwnj;وێت).';
$t[17] = 'هه&zwnj;روه&zwnj;ها پاڕاستوومانه له (پیلان و ده&zwnj;ست درێژی) هه&zwnj;موو شه&zwnj;یتانێکی ڕه&zwnj;جم کراوو نه&zwnj;فرین لێکراو.';
$t[18] = 'مه&zwnj;گه&zwnj;ر شه&zwnj;یتانێك به&zwnj;رز ببێته&zwnj;وه&zwnj;و به هه&zwnj;واڵێك بزانێت و بیدزێت، ئه&zwnj;وسا خێرا پارچه ئاگرێکی ئاشکرای بۆ ده&zwnj;کشێت و ته&zwnj;فرو تونای ده&zwnj;کات.';
$t[19] = 'زه&zwnj;ویشمان ڕاخستووه&zwnj;، چیاو کێوه&zwnj;کانمان له&zwnj;سه&zwnj;ر داناوه&zwnj;، له هه&zwnj;موو جۆره ڕووه&zwnj;ك و به&zwnj;رو بوومێکمان تیایدا ڕواندووه به&zwnj;شێوه&zwnj;یه&zwnj;کی ڕێك و پێك و به&zwnj;ئه&zwnj;ندازه&zwnj;ی پێویست و دیاری کراو.';
$t[20] = 'له&zwnj;سه&zwnj;ر زه&zwnj;ویشدا بژێوی ڕۆژانه&zwnj;مان بۆ ئێوه دابین کردووه&zwnj;، هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;وانه&zwnj;ش که ئێوه ناتوانن ڕۆزیان بۆ دابین بکه&zwnj;ن.';
$t[21] = 'هیچ شتێك نی یه (له به&zwnj;هره&zwnj;و نازو نیعمه&zwnj;ته&zwnj;کان) که گه&zwnj;نجینه&zwnj;کانی لای ئێمه نه&zwnj;بێت و ئێمه نایبه&zwnj;خشین به&zwnj;ئه&zwnj;ندازه&zwnj;ی دیاریکراو له کاتی له&zwnj;باردا نه&zwnj;بێت.';
$t[22] = '(یه&zwnj;کێك له به&zwnj;خششه&zwnj;کانی خوا ئه&zwnj;وه&zwnj;یه&zwnj;) ئێمه شنه&zwnj;بامان ناردووه که پیتێنه&zwnj;ری ڕووه&zwnj;ك و دارو دره&zwnj;خته&zwnj;، هه&zwnj;روه&zwnj;ها بارانمان له ئاسمانه&zwnj;وه باراندووه&zwnj;و ئێوه&zwnj;ی پێ تێراو ده&zwnj;که&zwnj;ین (سه&zwnj;ره&zwnj;ڕای کشتوکاڵ و زیندوه&zwnj;ران) بێ ئه&zwnj;وه&zwnj;ی ئێوه ئه&zwnj;و ئاوانه&zwnj;تان زه&zwnj;خیره کردبێت (ئێمه له&zwnj;ژێر زه&zwnj;ویدا بۆمان دابین کردوون، به&zwnj;هۆی کانی، یاخود بیره&zwnj;وه ده&zwnj;ستتان ده&zwnj;که&zwnj;وێت).';
$t[23] = 'به&zwnj;ڕاستی هه&zwnj;ر ئێمه ژیان ده&zwnj;به&zwnj;خشین و مردنیش پێش ده&zwnj;هێنین، سه&zwnj;رئه&zwnj;نجام هه&zwnj;ر ئێمه&zwnj;ش خاوه&zwnj;نی هه&zwnj;موو جیهانین.';
$t[24] = 'سوێند به خوا بێگومان ئێمه ئاگادارین له پێشینانی ئێوه&zwnj;، هه&zwnj;روه&zwnj;ها ئاگادارین له نه&zwnj;وه&zwnj;کانی داهاتوویش.';
$t[25] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ هه&zwnj;ر خۆی هه&zwnj;مووان کۆ ده&zwnj;کاته&zwnj;وه&zwnj;و بێگومان هه&zwnj;ر ئه&zwnj;و زاته داناو زانایه&zwnj;.';
$t[26] = 'سوێند به&zwnj;خوا ئێمه سه&zwnj;ره&zwnj;تا ئینسانمان له قوڕێکی وشك دروست کرد که پێشتر قوڕێکی ڕه&zwnj;شباوو ترشاو بوو.';
$t[27] = 'په&zwnj;ریشمان پێشتر له ئاگرێکی به&zwnj;تین دروست کرد که به وردیله کونه&zwnj;کاندا تێده&zwnj;په&zwnj;ڕێت.';
$t[28] = 'یادی ئه&zwnj;وه بکه&zwnj;ره&zwnj;وه کاتێك په&zwnj;روه&zwnj;ردگارت به فریشته&zwnj;کانی وت: به&zwnj;ڕاستی من دروستکاری به&zwnj;شه&zwnj;رێکم له قوڕێکی وشك که پێشتر قوڕێکی ڕه&zwnj;شباوو ترشاو بوو.';
$t[29] = 'جا کاتێك ڕێك و پێك و ته&zwnj;واوم کرد، له&zwnj;و ڕۆحه تایبه&zwnj;تیه&zwnj;ی که من (به&zwnj;دیهێنه&zwnj;ری ئه&zwnj;وم)، پێمبه&zwnj;خشی، ئه&zwnj;وه هه&zwnj;مووتان کڕنووشی بۆ به&zwnj;رن (کڕنووشی ڕێزو به فه&zwnj;رمانی خوا).';
$t[30] = 'ئینجا فریشته&zwnj;کان (فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاریان به&zwnj;جێ هێناو) هه&zwnj;موویان به&zwnj;گشتی کڕنووشی ڕێزیان برد.';
$t[31] = 'جگه له ئیبلیس (شه&zwnj;یتانی گه&zwnj;وره&zwnj;) سه&zwnj;رپێچی کرد که له&zwnj;گه&zwnj;ڵ کڕنووش به&zwnj;راندا بێت.';
$t[32] = 'ئه&zwnj;وسا خوا فه&zwnj;رمووی: ئه&zwnj;ی ئیبلیس ئه&zwnj;وه بۆ له&zwnj;گه&zwnj;ڵ کڕنووش به&zwnj;راندا نیت؟!';
$t[33] = 'شه&zwnj;یتان له وه&zwnj;ڵامدا وتی: من ئه&zwnj;وه&zwnj;نده (نزم و بچوك) نه&zwnj;بووم کڕنووش به&zwnj;رم بۆ به&zwnj;شه&zwnj;رێك که تۆ پێشتر قوڕێکی ڕه&zwnj;شباوو ترشاو بووه&zwnj;؟!';
$t[34] = 'خوا فه&zwnj;رمووی: (چونکه له فه&zwnj;رمانی من سه&zwnj;رپێچیت کرد، ده&zwnj;بێت له&zwnj;م شوێنه نه&zwnj;مێنیت، که به&zwnj;هه&zwnj;شته یان ئاسمان) که&zwnj;واته ده&zwnj;رچۆ لێره چونکه به&zwnj;ڕاستی تۆ نه&zwnj;فرین لێکراویت.';
$t[35] = 'به&zwnj;ڕاستی نه&zwnj;فرینی خوات به&zwnj;رده&zwnj;وام له&zwnj;سه&zwnj;ره هه&zwnj;تا ڕۆژی دوایی.';
$t[36] = 'شه&zwnj;یتان وتی: په&zwnj;روه&zwnj;ردگارا، مۆڵه&zwnj;تم بده هه&zwnj;تا ئه&zwnj;و ڕۆژه&zwnj;ی هه&zwnj;مووان زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[37] = 'خوایش فه&zwnj;رمووی: ده&zwnj;تۆ له مۆڵه&zwnj;تدراوانیت.';
$t[38] = 'تا ڕۆژی کاته دیاریکراوو زانراوه&zwnj;که (لای خوا).';
$t[39] = 'ئیبلیس (به بوغرو کینه&zwnj;و حه&zwnj;ساده&zwnj;ته&zwnj;وه&zwnj;) وتی: په&zwnj;روه&zwnj;ردگارم، ماده&zwnj;م به&zwnj;هۆی ئه&zwnj;م ئینسانه&zwnj;وه منت سه&zwnj;ر لێشێواو کرد، شه&zwnj;رت بێت له زه&zwnj;ویدا هه&zwnj;موو گوناهو لادان و تاوانێکیان لا&zwnj;جوان و شیرین بکه&zwnj;م و هه&zwnj;ر هه&zwnj;موویان سه&zwnj;ر لێشێواوو سه&zwnj;رگه&zwnj;ردان بکه&zwnj;م.';
$t[40] = 'بێجگه له به&zwnj;نده پوخته و دڵسۆزه&zwnj;کانی خۆت نه&zwnj;بێت (چونکه دڵیان به باوه&zwnj;ڕ به تۆو یادی تۆ ئاوه&zwnj;دانه&zwnj;، هه&zwnj;وڵ و کۆششی من بۆ وێڵ کردنیان ناکامه&zwnj;).';
$t[41] = 'خوای گه&zwnj;وره فه&zwnj;رمووی: ئا ئه&zwnj;وه ڕێگه&zwnj;و ڕێبازێکی ڕاست و دروسته هه&zwnj;ر بۆ لای من دێت و هه&zwnj;ر من چاودێری ده&zwnj;که&zwnj;م.';
$t[42] = 'به&zwnj;ڕاستی به&zwnj;نده چاك و پاکه&zwnj;کانی من به تۆ فریو نادرێن و تۆ هیچ ده&zwnj;سه&zwnj;ڵاتێکت نیه به&zwnj;سه&zwnj;ریاندا، جگه له&zwnj;وانه&zwnj;ی که شوێنت که&zwnj;وتوون له سه&zwnj;رکه&zwnj;ش و یاخی و سه&zwnj;رگه&zwnj;ردانه&zwnj;کان.';
$t[43] = 'بێگومان دۆزه&zwnj;خیش به&zwnj;ڵێنگای هه&zwnj;ر هه&zwnj;موو ئه&zwnj;وانه به&zwnj;گشتی.';
$t[44] = 'حه&zwnj;وت ده&zwnj;روازه&zwnj;ی گه&zwnj;وره&zwnj;ی هه&zwnj;یه&zwnj;، هه&zwnj;ر ده&zwnj;روازه&zwnj;یه&zwnj;کیشی چه&zwnj;ند ده&zwnj;رگاو چه&zwnj;ند به&zwnj;شێکه که ده&zwnj;سته&zwnj;ی دۆزه&zwnj;خیان، به&zwnj;گوێره&zwnj;ی تاوان و گوناهیان ڕاپێچ ده&zwnj;کرێن بۆ ناوی.';
$t[45] = '(له&zwnj;و لاشه&zwnj;وه&zwnj;) به&zwnj;ڕاستی خواناس و پارێزکاران له&zwnj;ناو باخه&zwnj;کانی به&zwnj;هه&zwnj;شت و کانیاوه سازگاره&zwnj;کاندان.';
$t[46] = 'پێیان ده&zwnj;وترێت: فه&zwnj;رموون بچنه ناوی به سه&zwnj;لامه&zwnj;تی و سه&zwnj;لامتان له&zwnj;سه&zwnj;ر بێت، ژیانی پر له ئاسووده&zwnj;یی تیادا به&zwnj;رنه سه&zwnj;ر.';
$t[47] = 'له کاتێکدا هه&zwnj;رچی کینه&zwnj;و بوغزه له دڵ و ده&zwnj;روونیاندا ده&zwnj;رمان کێشاو ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه برای یه&zwnj;کترن و له&zwnj;سه&zwnj;ر کورسی و قه&zwnj;نه&zwnj;فه ڕازاوه&zwnj;کان له به&zwnj;رامبه&zwnj;ر یه&zwnj;که&zwnj;وه دانیشتون (له به&zwnj;یه&zwnj;کگه&zwnj;یشتنه&zwnj;وه&zwnj;یه&zwnj;کی پڕ سۆزو خۆشه&zwnj;ویستیدا، ته&zwnj;ماشای یه&zwnj;کتر ده&zwnj;که&zwnj;ن و سه&zwnj;لام له یه&zwnj;کتر ده&zwnj;که&zwnj;ن و سوپاس و ستایشی په&zwnj;روه&zwnj;ردگار ده&zwnj;که&zwnj;ن).';
$t[48] = 'هیچ جۆره بێ تاقه&zwnj;تی و بێزاری و ناخۆشی و په&zwnj;ستیه&zwnj;ك دووچاریان نابێت و هیچ کات ئه&zwnj;وان له&zwnj;و به&zwnj;هه&zwnj;شته ده&zwnj;رناکرێن.';
$t[49] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم هه&zwnj;واڵ بده به به&zwnj;نده&zwnj;کانم که هه&zwnj;ر من به ڕاستی لێبوورده&zwnj;ی میهره&zwnj;بانم (بۆ باوه&zwnj;ڕ داران).';
$t[50] = 'بێگومان هه&zwnj;ر سزاو تۆڵه&zwnj;ی منیش، سزاو ئازارێکی زۆر به ئێشه (ئاماده&zwnj;م کردووه بۆ بێ باوه&zwnj;ڕو سه&zwnj;رکه&zwnj;ش و یاخیه&zwnj;کان).';
$t[51] = 'هه&zwnj;روه&zwnj;ها هه&zwnj;واڵیان بده&zwnj;رێ ده&zwnj;رباره&zwnj;ی میوانه&zwnj;کانی ئیبراهیم علیه السلام';
$t[52] = 'کاتێك کتوپر خۆیان کرد به ماڵداو وتیان: سڵاو (ئیبراهیم دوای وه&zwnj;ڵامدانه&zwnj;وه&zwnj;ی سڵاوه&zwnj;که&zwnj;یان) وتی: (ئێوه کێن؟) به&zwnj;ڕاستی ئێمه لێتان ده&zwnj;ترسین!!.';
$t[53] = 'فریشته&zwnj;کان وتیان: مه&zwnj;ترسه (ئێمه له لایه&zwnj;ن خواوه ڕه&zwnj;وانه کراوین) و بێگومان ئێمه مژده&zwnj;ی (له دایك بوونی) کوڕێکی زۆر زانات پێ ده&zwnj;ده&zwnj;ین.';
$t[54] = 'ئیبراهیم وتی: ئایا مژده&zwnj;م ده&zwnj;ده&zwnj;نێ له کاتێکدا که ئاوا پیری یه&zwnj;خه&zwnj;ی پێگرتووم!.. جا به&zwnj;چی مژده&zwnj;م ده&zwnj;ده&zwnj;نێ؟!';
$t[55] = 'فریشته&zwnj;کان وتیان: به&zwnj;ڕاستی مژده&zwnj;ت ده&zwnj;ده&zwnj;ینێ، که&zwnj;واته نه&zwnj;که&zwnj;یت له&zwnj;و که&zwnj;سانه بیت که نائومێدن (له به&zwnj;خششه&zwnj;کانی خوا).';
$t[56] = 'ئیبراهیم وتی: ئایا کێ نائومێد ده&zwnj;بێت له ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری، مه&zwnj;گه&zwnj;ر که&zwnj;سانێك که گومڕاو سه&zwnj;رگه&zwnj;ردانن.';
$t[57] = '(ئینجا دوای ئه&zwnj;وه&zwnj;ی دڵنیابوو لێیان) وتی: ئه&zwnj;ی فریشته نێرراوه&zwnj;کان چ کارێکی گرنگتان به ده&zwnj;سته&zwnj;وه&zwnj;یه&zwnj;؟! (جگه له&zwnj;م مژده&zwnj;یه&zwnj;)';
$t[58] = 'فریشته&zwnj;کان وتیان: به&zwnj;ڕاستی ئێمه نیردراوین بۆ سه&zwnj;ر قه&zwnj;وم و هۆزێکی زۆر تاوانبار.';
$t[59] = 'به&zwnj;ڵام خانه&zwnj;واده&zwnj;ی لوط هه&zwnj;موویان ڕزگار ده&zwnj;که&zwnj;ین.';
$t[60] = 'جگه هاوسه&zwnj;ره&zwnj;که&zwnj;ی، به&zwnj;ڕاستی بڕیارمانداوه ئه&zwnj;ویش له ڕیزی تیاچووه&zwnj;کاندا بمێنێته&zwnj;وه چونکه بێ باوه&zwnj;ڕه&zwnj;.';
$t[61] = 'ئینجا (دوای ئه&zwnj;وه&zwnj;ی ئیبراهیمیان به&zwnj;جێ هێشت، فریشته&zwnj;کان به&zwnj;ره&zwnj;و شاره&zwnj;که&zwnj;ی لوط ڕۆیشتن) کاتێك ئه&zwnj;و فرستادانه هاتنه ناو خانه&zwnj;واده&zwnj;ی لوط...';
$t[62] = 'پێی وتن: (داوای لێبوردن ده&zwnj;که&zwnj;ین، جه&zwnj;نابتان کێن؟) به&zwnj;ڕاستی ئێوه که&zwnj;سانێکی نه&zwnj;ناسراون...';
$t[63] = 'دوای ئه&zwnj;وه&zwnj;ی فریشته&zwnj;کان خۆیان پێناساند وتیان: دڵت هیچ نه&zwnj;کات به&zwnj;ڵکو ئێمه هاتووین بۆ لای تۆ بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;و هه&zwnj;ڕه&zwnj;شانه&zwnj;ی تۆ ده&zwnj;تکردو ئه&zwnj;و بێ ڕه&zwnj;وشتانه گومانیان لێده&zwnj;کرد، بیهێنینه دی...';
$t[64] = 'ئه&zwnj;مڕۆ ئیتر بۆ به&zwnj;رپاکردنی ئه&zwnj;و ڕاستیه هاتووین و بێگومان ڕاستیش ده&zwnj;ڵێین...';
$t[65] = 'به&zwnj;شێکی شه&zwnj;و بمێنێت خۆت و خانه&zwnj;واده&zwnj;ت ده&zwnj;رچن و تۆ دوایان بکه&zwnj;وه&zwnj;، با هیچ کامتان ئاوڕ نه&zwnj;داته&zwnj;وه&zwnj;، بڕۆن به&zwnj;ره&zwnj;و ئه&zwnj;و شوێنه&zwnj;ی که فه&zwnj;رمانتان پێ ده&zwnj;درێت.';
$t[66] = 'ئێمه نیگامان نارد بۆ لای _لوط_ و بڕیارماندا که به&zwnj;ڕاستی له کاتی به&zwnj;ره&zwnj;به&zwnj;یاندا دوای ئه&zwnj;وانه بڕاوه&zwnj;و ده&zwnj;یاریان نه&zwnj;ماوه&zwnj;.';
$t[67] = 'ئه&zwnj;وسا خه&zwnj;ڵکی ناپوخت و بێ ڕه&zwnj;وشتی ئه&zwnj;و شاره (زانیان به میوانه به&zwnj;ڕێزه&zwnj;کان) هاتن (به&zwnj;گرمه&zwnj;و گاڵته&zwnj;وه&zwnj;و) مژده&zwnj;ی ناپوختیان به&zwnj;یه&zwnj;کتری ده&zwnj;دا!';
$t[68] = 'لوط وتی: به&zwnj;ڕاستی ئه&zwnj;وانه میوانی منن و حه&zwnj;یای من مه&zwnj;به&zwnj;ن، شه&zwnj;رمه&zwnj;زارم مه&zwnj;که&zwnj;ن و ئابڕووم مه&zwnj;به&zwnj;ن.';
$t[69] = 'له خوا بترسن و ڕیسوام مه&zwnj;که&zwnj;ن (له شه&zwnj;ڕواڵ پیسی کۆڵ بده&zwnj;ن).';
$t[70] = '(شه&zwnj;ڕواڵ پیس و ناپوخته&zwnj;کان) وتیان: ئایا ڕێگری ئه&zwnj;وه&zwnj;مان لێ نه&zwnj;کردیت که حه&zwnj;قی که&zwnj;ست نه&zwnj;بێت و به&zwnj;رگری له که&zwnj;س نه&zwnj;که&zwnj;یت!';
$t[71] = 'لوط وتی: ئا ئه&zwnj;وانه (ئافره&zwnj;تانی شار) هه&zwnj;موو وه&zwnj;ك کچی منن( ئه&zwnj;گه&zwnj;ر ده&zwnj;تانه&zwnj;وێت ئه&zwnj;وانه&zwnj;تان لێ ماره ده&zwnj;که&zwnj;م، تا ئاره&zwnj;زووی خۆتان له ڕێگه&zwnj;ی دروستی خۆیه&zwnj;وه ئه&zwnj;نجام بده&zwnj;ن) ئه&zwnj;گه&zwnj;ر ئاماده بن.';
$t[72] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) سوێند به گیانی تۆ ئه&zwnj;وانه له سه&zwnj;رخۆشی خۆیاندا بۆ گوناه له&zwnj;سه&zwnj;ر گه&zwnj;ردانیدا ده&zwnj;تلێنه&zwnj;وه&zwnj;.';
$t[73] = 'جا هه&zwnj;ر له به&zwnj;ره&zwnj;به&zwnj;یاندا ده&zwnj;نگه سامناکه&zwnj;که&zwnj;و بوومه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;ك یه&zwnj;خه&zwnj;ی پێگرتن.';
$t[74] = 'ئینجا شاره&zwnj;که&zwnj;مان ژێره&zwnj;و ژوور کردو به سه&zwnj;نگگڵ به&zwnj;رد بارانمان کردن.';
$t[75] = 'بێگومان ئا له&zwnj;و به&zwnj;سه&zwnj;رهاته&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ که&zwnj;سانێك که په&zwnj;ندو ئامۆژگاری وه&zwnj;رگرن.';
$t[76] = 'بێگومان ئه&zwnj;و شاره وێرانه ده&zwnj;که&zwnj;وێته سه&zwnj;ره ڕێیه&zwnj;کی به&zwnj;رده&zwnj;وامی کاروانچیانه&zwnj;وه&zwnj;( نیشانه&zwnj;ی خه&zwnj;شم و قینی خوایه له بێ ڕه&zwnj;وشتان، تا خه&zwnj;ڵکی تێفکرن).';
$t[77] = 'به&zwnj;ڕاستی له&zwnj;و به&zwnj;سه&zwnj;رهاته&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;یه&zwnj;کی ڕوون و ئاشکرا ده&zwnj;رده&zwnj;که&zwnj;وێت بۆ ئیمانداران (که هێزو ده&zwnj;سه&zwnj;ڵاتی په&zwnj;روه&zwnj;ردگاریان بێ سنووره و له کاتی له&zwnj;باردا ده&zwnj;یخاته کارو شارو دێهاتی خوانه&zwnj;ناسان و بێ ڕه&zwnj;وشتان و کاول ده&zwnj;کات).';
$t[78] = 'دانیشتوانی باخچه&zwnj;و باخ و باخاته&zwnj;کانیش (وه&zwnj;کو قه&zwnj;ومی لوط) سته&zwnj;مکار بوون.';
$t[79] = 'ئێمه تۆڵه&zwnj;مان له&zwnj;وانیش سه&zwnj;ند، هه&zwnj;ردوو شوێنه&zwnj;واره&zwnj;که به ئاشکرا دیارن(چیمان به&zwnj;سه&zwnj;ر هێناون).';
$t[80] = 'سوێند به&zwnj;خوا دانیشتوانی (حجر) یش بڕوایان به به&zwnj;رنامه&zwnj;ی پێغه&zwnj;مبه&zwnj;ران نه&zwnj;کرد(حجر: شوێنێکه له نێوان ته&zwnj;بووك و مه&zwnj;دینه&zwnj;دا، قه&zwnj;ومی ثه&zwnj;مود، تیادا ژیاوه&zwnj;).';
$t[81] = 'ئێمه نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی خۆمانمان پێبه&zwnj;خشین که&zwnj;چی ئه&zwnj;وان گوێیان پێنه&zwnj;داو پشتیان تێکرد.';
$t[82] = 'ئه&zwnj;وانه له چیاکاندا خانووبه&zwnj;ره&zwnj;یان ده&zwnj;تاشی و، به ئاسووده&zwnj;ییه&zwnj;وه ژیانیان تێدا ده&zwnj;برده سه&zwnj;ر.';
$t[83] = 'ئینجا ئه&zwnj;وانیش ده&zwnj;نگێکی سامناك و بوومه&zwnj;له&zwnj;رزه&zwnj;یه&zwnj;کی سه&zwnj;خت یه&zwnj;خه&zwnj;ی پێگرتن له به&zwnj;ره&zwnj;به&zwnj;یاندا.';
$t[84] = 'ئه&zwnj;و کارو کرده&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد فریایان نه&zwnj;که&zwnj;وت و (له تۆڵه&zwnj;ی خوایی نه&zwnj;یپاڕاستن).';
$t[85] = 'ئێمه ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;رچی له نێوانیاندایه دروستمان نه&zwnj;کردووه&zwnj;، بۆ مه&zwnj;به&zwnj;ستی چه&zwnj;سپاندنی حه&zwnj;ق و ڕاستی نه&zwnj;بێت (هیچ شتێك له دروست کراوان بێ مه&zwnj;به&zwnj;ست نیه&zwnj;، هیچ شتێك بێ ئامانج نیه&zwnj;..)، به&zwnj;ڕاستی ڕۆژی قیامه&zwnj;تیش به&zwnj;ڕێوه&zwnj;یه&zwnj;و هه&zwnj;ر دێت و تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر، ئه&zwnj;ی ئیماندار) به&zwnj;شێوازێکی جوان چاوپۆشی بکه&zwnj;و لێبورده به (له ڕه&zwnj;فتاری نادروستی نه&zwnj;فامان).';
$t[86] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ هه&zwnj;ر خۆی به&zwnj;دیهێنه&zwnj;رێکی شاره&zwnj;زاو زاناو لێزانه&zwnj;.';
$t[87] = 'سوێند بێت ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ئێمه حه&zwnj;وت ئایه&zwnj;تمان پێداویت (که بریتی یه له سووره&zwnj;تی فاتحه&zwnj;) هه&zwnj;روه&zwnj;ها قورئانی گه&zwnj;وره&zwnj;و مه&zwnj;زنیشمان پێ به&zwnj;خشیوویت.';
$t[88] = 'جا تۆ نه&zwnj;که&zwnj;یت چاوه&zwnj;کانت ببڕیته ئه&zwnj;و که&zwnj;سانه&zwnj;ی که له هه&zwnj;ندێك نازو نیعمه&zwnj;تی دنیا به&zwnj;هره&zwnj;وه&zwnj;رمان کردوون، خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ لێیان (کاتێك ده&zwnj;بینیت شوێنی حه&zwnj;ق ناکه&zwnj;ون)، باڵی به&zwnj;زه&zwnj;یی نه&zwnj;وی بکه بۆ ئیمانداران (واته خۆشت بوێن و به&zwnj;سۆز به بۆیان).';
$t[89] = 'بڵێ: به&zwnj;ڕاستی من بێدارخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکراو دیارم (ئه&zwnj;گه&zwnj;ر سه&zwnj;رکه&zwnj;شی بکه&zwnj;ن سه&zwnj;رئه&zwnj;نجامی سامناك چاوه&zwnj;ڕێتانه&zwnj;).';
$t[90] = 'هه&zwnj;روه&zwnj;ك کتێبی ئاسمانیمان دابه&zwnj;زانده سه&zwnj;ر به&zwnj;ش به&zwnj;شکه&zwnj;ران (که مه&zwnj;به&zwnj;ست جووله&zwnj;که&zwnj;و گاوڕه&zwnj;، باوه&zwnj;ڕ به هه&zwnj;ندێکی ده&zwnj;که&zwnj;ن و په&zwnj;یڕه&zwnj;وی هه&zwnj;ندێکی ناکه&zwnj;ن، یاخود به&zwnj;شێکی زۆریان ده&zwnj;ستکاری کردووه&zwnj;).';
$t[91] = 'ئه&zwnj;وانه&zwnj;ی ڕایان ده&zwnj;رباره&zwnj;ی قورئان جۆراو جۆره&zwnj;، به هه&zwnj;ندێکی باوه&zwnj;ڕیان هه&zwnj;یه&zwnj;و، هه&zwnj;ندێکی ناچێته مێشکیانه&zwnj;وه&zwnj;، یاخود هه&zwnj;ندێکی په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن و هه&zwnj;ندێکی په&zwnj;یڕه&zwnj;وی ناکه&zwnj;ن.';
$t[92] = 'سوێند به په&zwnj;روه&zwnj;ردگارت پرسیار له هه&zwnj;موویان ده&zwnj;که&zwnj;ین به&zwnj;گشتی ( له بیرو باوه&zwnj;ڕی هه&zwnj;موویان ده&zwnj;کۆڵینه&zwnj;وه&zwnj;).';
$t[93] = 'له&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یانکرد.';
$t[94] = 'هه&zwnj;ر فه&zwnj;رمانێکت پێدراوه (ئه&zwnj;ی محمد صلی الله علیه وسلم به ڕێك و پێکی) ئه&zwnj;نجامی بده&zwnj;و گوێ مه&zwnj;ده به بوختان و ڕه&zwnj;فتاری موشریك و هاوه&zwnj;ڵگه&zwnj;ران.';
$t[95] = 'ئێمه تۆ ده&zwnj;پارێزین (له شه&zwnj;ڕو پیلانی) گاڵته&zwnj;چی و گاڵته&zwnj;جاڕان.';
$t[96] = 'ئه&zwnj;وانه&zwnj;ی له&zwnj;گه&zwnj;ڵ خوای گه&zwnj;وره&zwnj;دا خوایه&zwnj;کی تر بڕیار ده&zwnj;ده&zwnj;ن، جا له ئاینده&zwnj;دا هه&zwnj;موو ڕاستیه&zwnj;کان بۆ ڕوون ده&zwnj;بێته&zwnj;وه&zwnj;و ده&zwnj;یزانن.';
$t[97] = 'سوێند به خوا ئێمه چاك ده&zwnj;زانین که به&zwnj;ڕاستی دڵی تۆ ته&zwnj;نگ ده&zwnj;بێت به&zwnj;و گوفتاره نابه&zwnj;جێیه&zwnj;ی که خوانه&zwnj;ناسان ده&zwnj;یڵێن.';
$t[98] = '(جا له هه&zwnj;موو کاتێکدا، به تایبه&zwnj;ت له کاتی دڵ ته&zwnj;نگیدا) ته&zwnj;سبیحات و سوپاس و ستایشی په&zwnj;روه&zwnj;ردگارت بکه&zwnj;و له ڕیزی سوژده به&zwnj;راندابه&zwnj;.';
$t[99] = 'به&zwnj;رده&zwnj;وامیش په&zwnj;روه&zwnj;ردگارت بپه&zwnj;رسته هه&zwnj;تا مردن یه&zwnj;خه&zwnj;ت پێده&zwnj;گرێت ( ئه&zwnj;و کاته ئیتر به&zwnj;یه&zwnj;ك جاریی ڕزگارت ده&zwnj;بێت له هه&zwnj;موو ناخۆشیه&zwnj;کان).';
?>