<?php
$t = array();
$t[1] = 'فه&zwnj;رمانی خوا به&zwnj;ڕێوه&zwnj;یه و واگه&zwnj;یشت، په&zwnj;له&zwnj;ی لێمه&zwnj;که&zwnj;ن و په&zwnj;له&zwnj;تان نه&zwnj;بێت، پاکی و بڵندی بۆ ئه&zwnj;و زاته&zwnj;یه له&zwnj;وه&zwnj;ی که هاوه&zwnj;ڵگه&zwnj;ران ده&zwnj;یکه&zwnj;نه هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ خوا.';
$t[2] = 'په&zwnj;روه&zwnj;ردگار، خۆی فریشته&zwnj;کان به ڕه&zwnj;حمه&zwnj;ت و ژیانه&zwnj;وه (که وه&zwnj;حی ئاسمانیه&zwnj;) ده&zwnj;نێرێت بۆ هه&zwnj;رکه&zwnj;س بیه&zwnj;وێت له به&zwnj;نده&zwnj;کانی (که پێغه&zwnj;مبه&zwnj;رانن)، فه&zwnj;رمانیان پێده&zwnj;دات که&zwnj;: خه&zwnj;ڵکی بێدار بکه&zwnj;نه&zwnj;وه له&zwnj;وه&zwnj;ی که به&zwnj;ڕاستی جگه له من خوایه&zwnj;کی تر نیه&zwnj;، له&zwnj;به&zwnj;ر ئه&zwnj;وه له خه&zwnj;شم و قینم خۆتان بپارێزن (چونکه له&zwnj;و که&zwnj;سانه خۆش نابم که غه&zwnj;یری من ده&zwnj;په&zwnj;رستن).';
$t[3] = '(ئه&zwnj;و زاته&zwnj;) ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی دروست کردووه&zwnj;، به&zwnj;رزو بڵنده له&zwnj;وه&zwnj;ی (نه&zwnj;فامان) شه&zwnj;ریك و هاوبه&zwnj;شی بۆ بڕیار بده&zwnj;ن.';
$t[4] = 'ئاده&zwnj;میزادیشی له تاکه نوتفه&zwnj;یه&zwnj;ك به&zwnj;دیهێناوه&zwnj;، که&zwnj;چی ئه&zwnj;و، دوای ماوه&zwnj;یه&zwnj;ك که خۆی ناسیووه بووه&zwnj;ته دوژمنێکی ئاشکرا (بێ ئه&zwnj;وه&zwnj;ی هیچ خراپه&zwnj;یه&zwnj;کمان بۆی بووبێت جگه له چاکه&zwnj;).';
$t[5] = 'ماڵاتیشی دروستکردووه بۆ خزمه&zwnj;تی ئێوه&zwnj;، (له خوری و موو پێستیان جۆره&zwnj;ها پۆشاك و ڕایه&zwnj;خ دروست ده&zwnj;که&zwnj;ن) که گه&zwnj;رمیتان پێ ده&zwnj;به&zwnj;خشێت (له سه&zwnj;رماو سۆڵه&zwnj;دا)، هه&zwnj;روه&zwnj;ها جۆره&zwnj;ها سوودی تری لێ وه&zwnj;رده&zwnj;گرن له شیرو گۆشته&zwnj;که&zwnj;شی ده&zwnj;خۆن...';
$t[6] = 'کاتێك ماڵاته&zwnj;کان ده&zwnj;هێننه&zwnj;وه له ئێواراندا (به تێری و شیره&zwnj;وه&zwnj;)، له به&zwnj;ره&zwnj;به&zwnj;یانه&zwnj;کانیشدا که له ماڵ ده&zwnj;ریان ده&zwnj;که&zwnj;ن، دیمه&zwnj;نێکی جوانتان بۆ ده&zwnj;نوێنن (ده&zwnj;روونی ئیماندار ده&zwnj;خرۆشێنن، به&zwnj;جیاوازی شێوازو قه&zwnj;باره&zwnj;و ده&zwnj;نگ و ڕه&zwnj;نگ و یاری و گه&zwnj;مه&zwnj;یان).';
$t[7] = 'هه&zwnj;ندێك له&zwnj;و ماڵاتانه بارو که&zwnj;ل و په&zwnj;لی سه&zwnj;نگینتان هه&zwnj;ڵده&zwnj;گرن بۆ وڵات و شوێنێك که ئێوه دوای زه&zwnj;حمه&zwnj;ت و ماندوو بوونێکی زۆر نه&zwnj;بێت پێی نه&zwnj;ده&zwnj;گه&zwnj;یشتن، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارتان زاتێکی زۆر به سۆزو میهره&zwnj;بانه (بۆ نه&zwnj;وه&zwnj;کانی ئاده&zwnj;م).';
$t[8] = 'هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها جۆری ئه&zwnj;سپ و ماین و هێسترو گوێدرێژی بۆ به&zwnj;دیهێناون تا بۆ سواری و گواستنه&zwnj;وه به&zwnj;کاری بهێنن، سه&zwnj;ره&zwnj;ڕای دیمه&zwnj;نی قه&zwnj;شه&zwnj;نگیان (به&zwnj;تایبه&zwnj;ت ئه&zwnj;سپ و ماین، که له سه&zwnj;رده&zwnj;می زانستی و ته&zwnj;کنه&zwnj;لۆژیادا هه&zwnj;ر به&zwnj;کار دێن)و شتی تریش دروست ده&zwnj;کات که ئێسته ئێوه نایزانن (ئه&zwnj;م ئاماژه قورئانیه&zwnj;، هه&zwnj;موو هۆیه&zwnj;کانی گواستنه&zwnj;وه&zwnj;ی ئاسمانی و ده&zwnj;ریایی و زه&zwnj;مینی ده&zwnj;گرێته&zwnj;وه که ئاده&zwnj;میزاد په&zwnj;ی پێ بردووه&zwnj;و په&zwnj;ی پێ ده&zwnj;بات، چونکه ئاده&zwnj;میزاد خۆی و زانیاری و زانسته&zwnj;که&zwnj;ی و که&zwnj;ره&zwnj;سته خاوه&zwnj;کان، هه&zwnj;ر هه&zwnj;مووی به&zwnj;خششی خوای به&zwnj;دیهێنه&zwnj;ری تواناو زانایه&zwnj;).';
$t[9] = 'ڕوونکردنه&zwnj;وه&zwnj;ی ڕێبازی ڕاست و به&zwnj;رنامه&zwnj;ی دروست له&zwnj;سه&zwnj;ر خوایه (ده&zwnj;بێت وریا بن) هه&zwnj;ندێك ڕێبازی لارو چه&zwnj;وت و ناڕێکیش هه&zwnj;یه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر بیویستایه هیدایه&zwnj;تی هه&zwnj;ر هه&zwnj;مووتانی ده&zwnj;دا به&zwnj;گشتی (سه&zwnj;رپشکی نه ده&zwnj;کردن، به&zwnj;ڵام ڕێزی لێناون).';
$t[10] = '(په&zwnj;روه&zwnj;ردگار) ئه&zwnj;و زاته&zwnj;یه که له ئاسمانه&zwnj;وه بارانتان بۆ ده&zwnj;بارێنێت که لێی ده&zwnj;خۆنه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها تێکه&zwnj;ڵ به جۆره&zwnj;ها شه&zwnj;ربه&zwnj;ت و شله&zwnj;مه&zwnj;نیش ده&zwnj;یخۆنه&zwnj;وه و ده&zwnj;بێته هۆی ڕوان و گه&zwnj;شه&zwnj;کردنی دره&zwnj;خت و گژوگیاو ماڵاتی تیادا ده&zwnj;له&zwnj;وه&zwnj;ڕێنن.';
$t[11] = 'هه&zwnj;ر به&zwnj;و بارانه خوای گه&zwnj;وره جۆره&zwnj;ها کشتوکاڵ ده&zwnj;هێنێته به&zwnj;رهه&zwnj;م بۆتان، هاوڕێ له&zwnj;گه&zwnj;ڵ زه&zwnj;یتون و خورماو جۆره&zwnj;ها ترێ، له هه&zwnj;موو جۆره به&zwnj;روبوومێکیش، به&zwnj;ڕاستی ئا له&zwnj;وه&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆ که&zwnj;سانێك که بیربکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[12] = 'هه&zwnj;روه&zwnj;ها شه&zwnj;وو ڕۆژو خۆرو مانگی بۆ ئێوه ڕام هێناوه&zwnj;، ئه&zwnj;ستێره&zwnj;کانیش به فه&zwnj;رمانی ئه&zwnj;و زاته ڕام هێنراون، به ڕاستی ئا له&zwnj;و دیارده&zwnj;و دروست کراوانه&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك که عه&zwnj;قڵ و ژیریان بخه&zwnj;نه&zwnj;کار.';
$t[13] = 'هه&zwnj;روه&zwnj;ها شتی زۆرو جۆراو جۆرو هه&zwnj;مه&zwnj;ڕه&zwnj;نگی له زه&zwnj;ویدا بۆ به&zwnj;دیهێناون، که به&zwnj;ڕاستی ئا له&zwnj;وه&zwnj;شدا به&zwnj;ڵگه هه&zwnj;یه بۆ که&zwnj;سانێك که یاداوه&zwnj;ری وه&zwnj;رده&zwnj;گرن.';
$t[14] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که ده&zwnj;ریای بۆ بار هێناون تا گۆشتی ته&zwnj;ڕو تازه&zwnj;ی لێ بخۆن، تا له ئاویدا جۆره&zwnj;ها گه&zwnj;وهه&zwnj;رو مرواری ده&zwnj;ربهێنن و له ده&zwnj;ست و ملی بکه&zwnj;ن، هه&zwnj;روه&zwnj;ها که&zwnj;شتیه&zwnj;کان ده&zwnj;بینیت ده&zwnj;ریا له&zwnj;ت ده&zwnj;که&zwnj;ن، تا فه&zwnj;زڵ و به&zwnj;خششی تری په&zwnj;روه&zwnj;ردگار به&zwnj;ده&zwnj;ست بهێنن، بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاریش بکه&zwnj;ن.';
$t[15] = 'هه&zwnj;ر خوا له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی چیاکانی چه&zwnj;سپاندووه تا له&zwnj;نگه&zwnj;ری بگرێت و ئێوه ئه&zwnj;له&zwnj;رزێنێت و لارتان ئه&zwnj;کاته&zwnj;وه&zwnj;و ژێره&zwnj;و ژوور نه&zwnj;بن، هه&zwnj;روه&zwnj;ها چه&zwnj;م و ڕووبارو ڕێگه&zwnj;وبانی تیادا به&zwnj;دیهێناوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ڕێ ده&zwnj;رکه ن و به مه&zwnj;رامی خۆتان بگه&zwnj;ن.';
$t[16] = 'هه&zwnj;روه&zwnj;ها نیشانه&zwnj;ی زۆرو زه&zwnj;به&zwnj;ندی له زه&zwnj;ویدا دابین کردووه (وه&zwnj;کو که&zwnj;ژو کێوه&zwnj;کان و، چه&zwnj;م و ڕووباره&zwnj;کان و به&zwnj;رزی و نشێوه&zwnj;کان، تا ون نه&zwnj;بن، له شه&zwnj;وگاریشدا)و به&zwnj;هۆی ئه&zwnj;ستێره&zwnj;کانه&zwnj;وه خه&zwnj;ڵکی ڕێی خۆیان ده&zwnj;ربکه&zwnj;ن.';
$t[17] = '(باشه ئه&zwnj;ی هۆشمه&zwnj;نده&zwnj;کان) ئایا ئه&zwnj;و زاته&zwnj;ی ئه&zwnj;م هه&zwnj;موو دروستکراوه سه&zwnj;ر سوڕهێنه&zwnj;رانه&zwnj;ی به&zwnj;دیهێناوه&zwnj;، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه وایه که ناتوانێت هیچ به&zwnj;دی بهێنێت، ئایا ئه&zwnj;وه بۆ لێکی نادانه&zwnj;وه&zwnj;و یاداوه&zwnj;ری وه&zwnj;رناگرن؟';
$t[18] = 'خۆ ئه&zwnj;گه&zwnj;ر بێن و بتانه&zwnj;وێت نازو نیعمه&zwnj;ته&zwnj;کانی خوا بژمێرن، بۆتان ناژمێردرێت (له ئه&zwnj;ندامه&zwnj;کانی ده&zwnj;ره&zwnj;وه و ناوه&zwnj;وه&zwnj;ی خۆتان و ئه&zwnj;و ڕزق و ڕۆزیانه&zwnj;ی که ده&zwnj;یخۆن و ئه&zwnj;و پۆشاك و ڕایه&zwnj;خ و ناوماڵه&zwnj;ی به&zwnj;کاری ده&zwnj;هێنن... هتد، سه&zwnj;ره&zwnj;ڕای هه&zwnj;زاران هه&zwnj;زار دروستکراوانی تر&zwnj;ی دیارو نادیار له جیهانی مادده و ڕووه&zwnj;ك و گیاندارو... هتد)، سه&zwnj;ره&zwnj;ڕای که&zwnj;م سوپاسیتان له&zwnj;و هه&zwnj;موو نازو نیعمه&zwnj;ته بێ شوماره&zwnj;) به&zwnj;ڕاستی خوای په&zwnj;روه&zwnj;ردگار هه&zwnj;ر لێخۆش بوو میهره&zwnj;بانه&zwnj;.';
$t[19] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;و خوایه ده&zwnj;زانێت چی ده&zwnj;شارنه&zwnj;وه&zwnj;و چیش ده&zwnj;رده&zwnj;خه&zwnj;ن و ئاشکرای ده&zwnj;که&zwnj;ن له (گفتوگۆو نیه&zwnj;ت و کارو کرده&zwnj;وه&zwnj;تان).';
$t[20] = 'ئه&zwnj;و بت و که&zwnj;س و شتانه&zwnj;ی که هاواریان لێ ده&zwnj;که&zwnj;ن و ده&zwnj;یانپه&zwnj;رستن جگه له خوا، هیچیان پێ دروست ناکرێت، له کاتێکدا که خۆیان دروست ده&zwnj;کرێن.';
$t[21] = 'ئه&zwnj;وانه مردوون و زیندوو نین، هه&zwnj;ستیش ناکه&zwnj;ن که&zwnj;ی خۆیان و خه&zwnj;ڵکی زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[22] = 'ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;ی ئێوه (که حه&zwnj;ق وایه هه&zwnj;ر ئه&zwnj;و بپه&zwnj;رستن) خوایه&zwnj;کی تاك و ته&zwnj;نهایه&zwnj;، جا ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان به قیامه&zwnj;ت نیه دڵه&zwnj;کانیان هه&zwnj;میشه ئینکاری هه&zwnj;موو ڕاستیه&zwnj;ك ده&zwnj;کات، له کاتێکدا ئه&zwnj;وانه خۆیان به گه&zwnj;وره ده&zwnj;زانن و فیز ده&zwnj;که&zwnj;ن.';
$t[23] = 'شتێکی ڕاسته&zwnj;و گومانی تێدانیه که خوای گه&zwnj;وره ئاگایه به&zwnj;وه&zwnj;ی په&zwnj;نهانی ده&zwnj;که&zwnj;ن و ده&zwnj;یشارنه&zwnj;وه&zwnj;، به&zwnj;وه&zwnj;ش که ده&zwnj;ری ده&zwnj;خه&zwnj;ن و ئاشکرای ده&zwnj;که&zwnj;ن، به&zwnj;ڕاستی ئه&zwnj;و زاته ئه&zwnj;و که&zwnj;سانه&zwnj;ی خۆش ناوێت که لووت به&zwnj;رزو خۆ به گه&zwnj;وره ده&zwnj;نوێنن.';
$t[24] = 'خۆ ئه&zwnj;گه&zwnj;ر به (بێ باوه&zwnj;ڕان) بوترێت: ئه&zwnj;وه په&zwnj;روه&zwnj;ردگارتان چی دابه&zwnj;زاندووه&zwnj;؟! ئه&zwnj;وه (بێ شه&zwnj;رمانه&zwnj;) ده&zwnj;ڵێن: چیرۆك و داستانی کۆن و خه&zwnj;یاڵیی پێشینانه&zwnj;!!';
$t[25] = 'ئه&zwnj;وانه ده&zwnj;بێت له ڕۆژی قیامه&zwnj;تدا کۆڵی گوناهو تاوانی خۆیان به ته&zwnj;واوی هه&zwnj;ڵگرن، هه&zwnj;روه&zwnj;ها له گوناهی ئه&zwnj;وانه&zwnj;ش هه&zwnj;ڵده&zwnj;گرن که گومڕایان کردوون بێ زانستی و زانیاری (بێ ئه&zwnj;وه&zwnj;ی له کۆڵ و باری ئه&zwnj;وان که&zwnj;م بێته&zwnj;وه&zwnj;)، ئاگاداربن! چه&zwnj;ند خراپ و ناشرینه ئه&zwnj;وباره گرانه&zwnj;ی که هه&zwnj;ڵی ده&zwnj;گرن (چ گیرێکیان خواردوه&zwnj;).';
$t[26] = 'بێگومان پێش ئه&zwnj;مانیش (خوا نه&zwnj;ناسانی) پێشوو پیلانیان گێڕا (دژ به ئایینی خوا)، ئینجا خوای به&zwnj;ده&zwnj;سه&zwnj;ڵاتیش کۆشك و ته&zwnj;لارو نه&zwnj;خشه&zwnj;یانی له بن و بێخ ده&zwnj;رهێنا، سه&zwnj;قفه&zwnj;که&zwnj;شی له سه&zwnj;ره&zwnj;وه داڕمان به&zwnj;سه&zwnj;ریانداو له شوێنێکه&zwnj;وه به&zwnj;ڵاو سزا یه&zwnj;خه&zwnj;ی پێگرتن که هه&zwnj;ستیان پێ نه&zwnj;ده&zwnj;کردو به&zwnj;ته&zwnj;مای نه&zwnj;بوون (ئه&zwnj;ڵبه&zwnj;ته ئه&zwnj;م هه&zwnj;ڕه&zwnj;شه&zwnj;یه پیلانگێڕانی هه&zwnj;موو سه&zwnj;رده&zwnj;مێك ده&zwnj;گرێته&zwnj;وه&zwnj;).';
$t[27] = 'له&zwnj;وه&zwnj;و دواش له ڕۆژی قیامه&zwnj;تدا خوا ئه&zwnj;و پیلانگێڕانه خه&zwnj;جاڵه&zwnj;ت و ڕیسوا ده&zwnj;کات و پێیان ده&zwnj;ڵێت: کوان و له کوێن هاوه&zwnj;ڵه&zwnj;کانم؟ ئه&zwnj;وانه&zwnj;ی که دژایه&zwnj;تی سه&zwnj;رسه&zwnj;ختتان ده&zwnj;کرد له باره&zwnj;یانه&zwnj;وه&zwnj;، (ئیمانداران) ئه&zwnj;وانه&zwnj;ی که زانیاری و زانستی خوا ناسیان پێدراوه ده&zwnj;ڵێن: به&zwnj;ڕاستی ئه&zwnj;مڕۆ سه&zwnj;رشۆڕی و خه&zwnj;جاڵه&zwnj;تی و نه&zwnj;هامه&zwnj;تی و سزا له&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕانه&zwnj;.';
$t[28] = 'ئه&zwnj;وانه&zwnj;ی که فریشته&zwnj;کان له سه&zwnj;ره&zwnj;مه&zwnj;رگدا گیانیان ده&zwnj;کێشن، له کاتێکدا که به&zwnj;رده&zwnj;وامن له&zwnj;سه&zwnj;ر سته&zwnj;م له خۆیان (به&zwnj;هۆی بێ باوه&zwnj;ڕی و یاخی بونیانه&zwnj;وه&zwnj;) ئینجا به ناچاری ته&zwnj;سلیمن و ده&zwnj;ڵێن: ئێمه هیچ گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;کمان نه&zwnj;کردووه&zwnj;، فریشته&zwnj;کان ده&zwnj;ڵێن: نه&zwnj;خێر وا نی یه (ئه&zwnj;وه ئێوه له بیرتان کردووه&zwnj;) ئه&zwnj;گینا به&zwnj;ڕاستی خوا ئاگادارو زانایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;تانکرد و ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[29] = 'که&zwnj;واته له ده&zwnj;روازه&zwnj;کانی دۆزه&zwnj;خه&zwnj;وه بچنه ژووره&zwnj;وه و بۆ هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه&zwnj;ش تیادا بن، ئای که جێگه&zwnj;و ڕێگه&zwnj;ی خۆ به&zwnj;زل زانه&zwnj;کان ناخۆش و نا له&zwnj;بارو ناسۆره&zwnj;.';
$t[30] = 'له&zwnj;و لاشه&zwnj;وه به&zwnj;و که&zwnj;سانه ده&zwnj;وترێت که خواناسی و پارێزکاریان کردووه&zwnj;: په&zwnj;روه&zwnj;ردگارتان چی ڕه&zwnj;وانه کردووه&zwnj;؟ چ به&zwnj;رنامه&zwnj;و کتێبێکی دابه&zwnj;زاندووه&zwnj;؟، هه&zwnj;مووان ده&zwnj;ڵێن: خێرو به&zwnj;ره&zwnj;که&zwnj;ت، (بۆ هه&zwnj;ردوو جیهان)، بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که له&zwnj;م جیهانه&zwnj;دا چاکه&zwnj;یان کردووه&zwnj;، چاکه دێته&zwnj;وه ڕێیان، بێگومان جێگه&zwnj;و ڕێگه&zwnj;ی ئه&zwnj;و جیهان خۆشترو چاکترو سازگارترو پڕ له نازو نیعمه&zwnj;تتره (به&zwnj;راورد ناکرێت له&zwnj;گه&zwnj;ڵ دنیادا) ئای که&zwnj;چه&zwnj;نده جێگه&zwnj;و ڕێگه&zwnj;و کۆشك و ته&zwnj;لاری خواناس و پارێزکاران سازگارو خۆش و جوان و ڕازاوه&zwnj;یه&zwnj;...';
$t[31] = '(ئه&zwnj;و جێگه&zwnj;و ڕێگه&zwnj;یه&zwnj;) باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی_عه&zwnj;دنه&zwnj;_ به&zwnj;خته&zwnj;وه&zwnj;ران ده&zwnj;چنه ناوی، چه&zwnj;نده&zwnj;ها ڕووبار به به&zwnj;رده&zwnj;م کۆشکه&zwnj;کان و به&zwnj;ژێر دره&zwnj;خته&zwnj;کاندا جاری و ڕه&zwnj;وانه&zwnj;، هه&zwnj;رچیه&zwnj;ك بخوازن و هه&zwnj;رچی ئاره&zwnj;زوو بکه&zwnj;ن و هه&zwnj;رچی داوا بکه&zwnj;ن، بۆیان ئاماده&zwnj;یه تیایدا، ئا به&zwnj;و شێوه&zwnj;یه خوای میهره&zwnj;بان پاداشتی خواناس و پارێزکاران ده&zwnj;داته&zwnj;وه&zwnj;...';
$t[32] = 'ئه&zwnj;وانه&zwnj;ی که له سه&zwnj;ره&zwnj;مه&zwnj;رگدا فریشته&zwnj;کان دێن بۆ لایان له کاتێکدا گیانی پاك و پیرۆزیان ده&zwnj;کێشن (که دووره له بێ باوه&zwnj;ڕی)و پێیان ده&zwnj;ڵێن: سڵاوتان لێ بێت (ئیتر ڕزگارتان بوو، ئیتر ناخۆشی نه&zwnj;ما) ده بچنه ناو ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، به هۆی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;وه که کاتی خۆی ده&zwnj;تانکرد.';
$t[33] = 'ئایا خوا نه&zwnj;ناسان چاوه&zwnj;ڕێی چی ده&zwnj;که&zwnj;ن؟ مه&zwnj;گه&zwnj;ر هه&zwnj;ر چاوه&zwnj;ڕێی ئه&zwnj;وه بن کتوپڕ فریشته&zwnj;ی گیانکێشان یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت، یان فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارت ده&zwnj;ربچێت بۆ تۆڵه سه&zwnj;ندن، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانیش هه&zwnj;ر ڕه&zwnj;فتاری وا ناله&zwnj;باریان کردووه&zwnj;، جا وه&zwnj;نه&zwnj;بێت خوا سته&zwnj;می لێکردبن به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;م له خۆیان ده&zwnj;کرد.';
$t[34] = 'سه&zwnj;ره&zwnj;نجام هه&zwnj;رچی گوناهو خراپه&zwnj;یه&zwnj;کیان ئه&zwnj;نجام دابوو هاته&zwnj;وه ڕێیان، ئه&zwnj;و هه&zwnj;ڕه&zwnj;شه&zwnj;و دۆزه&zwnj;خه&zwnj;ی که گاڵته&zwnj;یان پێده&zwnj;کرد یه&zwnj;خه&zwnj;ی پێگرتن و ده&zwnj;وری دان.';
$t[35] = 'ئه&zwnj;وانه&zwnj;ی هاوه&zwnj;ڵیان داناوه بۆ خوا ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بوایه هیچ شتێکمان له جیاتی ئه&zwnj;و نه&zwnj;ده&zwnj;په&zwnj;رست، نه ئێمه&zwnj;و نه باوو باپیرانمان، هیچ شتێکیشمان له خۆمان حه&zwnj;رام نه&zwnj;ده&zwnj;کرد به بێ ئه&zwnj;و، خوا نه&zwnj;ناسانی پێش ئه&zwnj;مانیش هه&zwnj;روایان ده&zwnj;کردو هه&zwnj;ر وایان ده&zwnj;وت، مه&zwnj;گه&zwnj;ر ئه&zwnj;رکی سه&zwnj;رشانی پێغه&zwnj;مبه&zwnj;ران هه&zwnj;ر ئه&zwnj;وه نیه که ئایینی خوا به ڕوونی ڕابگه&zwnj;یه&zwnj;نن؟';
$t[36] = 'سوێند به خوا بێگومان ئێمه له&zwnj;ناو هه&zwnj;ر گه&zwnj;ل و نه&zwnj;ته&zwnj;وه&zwnj;یه&zwnj;کدا پێغه&zwnj;مبه&zwnj;رێکمان ڕه&zwnj;وانه&zwnj;کردووه&zwnj;و پێمان ڕاگه&zwnj;یاندووه که پێیان بڵێت: ته&zwnj;نها خوا بپه&zwnj;رستن، خۆتان دووره په&zwnj;رێز بگرن له هه&zwnj;موو په&zwnj;رستراوێکی تر، جا هه&zwnj;بوو له&zwnj;و خه&zwnj;ڵکه (که بیرو هۆش و ژیری خۆی خسته&zwnj;کارو به&zwnj;شوێن ڕاستیدا ده&zwnj;گه&zwnj;ڕا) خوایش هیدایه&zwnj;تی ده&zwnj;دا، هه&zwnj;شیان بوو خۆی شایسته&zwnj;ی گومڕایی ده&zwnj;کردو به&zwnj;سه&zwnj;ریدا ده&zwnj;چه&zwnj;سپا، که&zwnj;وابوو بگه&zwnj;ڕێن به&zwnj;سه&zwnj;ر زه&zwnj;ویداو سه&zwnj;رنج بده&zwnj;ن، بزانن چۆن بوو سه&zwnj;ره&zwnj;نجامی ئه&zwnj;و که&zwnj;سانه&zwnj;ی بڕوایان به به&zwnj;رنامه&zwnj;ی ئێمه نه&zwnj;بوو.';
$t[37] = 'خۆ ئه&zwnj;گه&zwnj;ر تۆ زۆر سوور بیت له&zwnj;سه&zwnj;ر هیدایه&zwnj;تدانی ئه&zwnj;وان، ئه&zwnj;وه تۆ ناتوانیت، چونکه به&zwnj;ڕاستی خوا ڕێنموونی که&zwnj;سێك ناکات که خه&zwnj;ڵك گومڕا بکات، ئه&zwnj;وانه (له قیامه&zwnj;تدا) هیچ جۆره پشتیوانێکیان نیه&zwnj;.';
$t[38] = 'بێ باوه&zwnj;ڕان سوێندیان ده&zwnj;خواردو جه&zwnj;ختیان له&zwnj;سه&zwnj;ر سوێنده&zwnj;که&zwnj;یان ده&zwnj;کردو ده&zwnj;یانوت: ئه&zwnj;وه&zwnj;ی بمرێت خوا زیندووی ناکاته&zwnj;وه&zwnj;! نه&zwnj;خێر، زیندووی ده&zwnj;کاته&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڵێنێکی ڕاست و دروستی خوایه و لێی پاشگه&zwnj;ز نابێته&zwnj;وه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;م ڕاستیه نازانن و بڕوای پێناکه&zwnj;ن.';
$t[39] = 'بێگومان خوا هه&zwnj;مووان زیندوو ده&zwnj;کاته&zwnj;وه تا بۆیان ڕوون بکاته&zwnj;وه و بۆیان ده&zwnj;ربخات، ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان کێشه&zwnj;یان له&zwnj;سه&zwnj;ری هه&zwnj;بوو، بڕوایان پێی نه&zwnj;بوو هاته دی، تا ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون بێگومان ئه&zwnj;وان درۆزن بوون.';
$t[40] = 'به&zwnj;ڕاستی کاتێك زیندوو کردنه&zwnj;وه&zwnj;و به&zwnj;دیهێنانی هه&zwnj;ر شتێکمان بوێت، ته&zwnj;نها ئه&zwnj;وه&zwnj;نده&zwnj;ی ده&zwnj;وێت پێی بڵێین ببه! ده&zwnj;ستبه&zwnj;جێ ئه&zwnj;ویش ده&zwnj;بێت.';
$t[41] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ی له&zwnj;به&zwnj;ر خواو بۆ خوا کۆچیان کرد (بۆ پاڕاستنی ئیمانه&zwnj;که&zwnj;یان) دوای ئه&zwnj;وه&zwnj;ی که سته&zwnj;میان لێکرابوو، ئه&zwnj;وه هه&zwnj;ر له دنیادا به&zwnj;چاکی و ڕێزه&zwnj;وه نیشته&zwnj;جێیان ده&zwnj;که&zwnj;ین، بێگومان پاداشتی ئه&zwnj;و جیهان گه&zwnj;وره&zwnj;ترو فراوانتره بۆیان، ئه&zwnj;گه&zwnj;ر ده&zwnj;یانزانی.';
$t[42] = 'ئه&zwnj;وانه&zwnj;ش ئه&zwnj;وانه&zwnj;ن که به ئارام و خۆگر بوون و پشت به په&zwnj;روه&zwnj;ردگاریان ده&zwnj;به&zwnj;ستن.';
$t[43] = 'ئێمه&zwnj;ش پێش تۆ جگه له پیاوانی (ڕاستگۆو چاك و پاك نه&zwnj;بێت) که&zwnj;سی ترمان ڕه&zwnj;وانه نه&zwnj;کردووه و وه&zwnj;حیمان بۆ نه&zwnj;ناردووه&zwnj;، ئه&zwnj;گه&zwnj;ر هه&zwnj;ر بڕوا ناکه&zwnj;ن و نایزانن ده ئێوه له شاره&zwnj;زایان بپرسن؟ (که ڕه&zwnj;وانه کراوانی ئێمه پیاوانێکی ناسراو بوون له&zwnj;ناو قه&zwnj;ومه&zwnj;کانیاندا).';
$t[44] = 'ئێمه ئه&zwnj;وانمان ڕه&zwnj;وانه کردووه هاوڕێ له&zwnj;گه&zwnj;ڵ به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;و (موعجیزه&zwnj;) ی زۆرو کتێبی ئاسمانیداو ئێمه قورئانمان بۆ تۆ دابه&zwnj;زاند، تا بۆ خه&zwnj;ڵکی ئه&zwnj;وه ڕوون بکه&zwnj;یته&zwnj;وه که بۆیان هێنراوه&zwnj;ته خواره&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی بیرێك بکه&zwnj;نه&zwnj;وه&zwnj;و تێفکرن.';
$t[45] = 'جا ئایا ئه&zwnj;وانه&zwnj;ی که چه&zwnj;نده&zwnj;ها پیلان و نه&zwnj;خشه&zwnj;ی خراپیان کێشاوه دڵنیان له&zwnj;وه&zwnj;ی که خوا نایانبات به ناخی زه&zwnj;ویدا، یاخود له لایه&zwnj;که&zwnj;وه سزاو تۆڵه&zwnj;یان بۆ بێت که هه&zwnj;ر هه&zwnj;ستی پێنه&zwnj;که&zwnj;ن؟!...';
$t[46] = 'یاخود له کاتی هاتوچۆو سه&zwnj;رگه&zwnj;رم بوونیان به کاروباره&zwnj;وه له ناویان ببات، ئه&zwnj;وانیش ده&zwnj;سته&zwnj;وسانکه&zwnj;ری خوانین؟!...';
$t[47] = 'یاخود (په&zwnj;روه&zwnj;ردگار به&zwnj;توانایه&zwnj;) له کاتی ترس و ناخۆشی و بیمدا.. بیانگرێت و له ناویان به&zwnj;رێت، به&zwnj;ڵام (مۆڵه&zwnj;ت ده&zwnj;دات تا به&zwnj;ڵکو ڕووبکه&zwnj;نه ئیمان) چونکه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارتان به&zwnj;سۆزو میهره&zwnj;بانه&zwnj;.';
$t[48] = 'ئایا ئه&zwnj;وه نه&zwnj;یانڕوانیوه&zwnj;ته ئه&zwnj;و شته بێ شومارانه&zwnj;ی که خوا دروستی کردووه&zwnj;؟ که سێبه&zwnj;ره&zwnj;که&zwnj;ی به&zwnj;لای ڕاست و به&zwnj;لای چه&zwnj;پیانه&zwnj;وه ده&zwnj;سوڕێته&zwnj;وه&zwnj;، له کاتێکدا هه&zwnj;موویان ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردان بۆ په&zwnj;روه&zwnj;ردگار، له حاڵێکیشدا خاوه&zwnj;ن سێبه&zwnj;ره&zwnj;کان ملکه&zwnj;چ و زه&zwnj;لیلی ویستی خواین.';
$t[49] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه سوژده ده&zwnj;به&zwnj;ن بۆ خوای به&zwnj;دیهێنه&zwnj;ریان، هه&zwnj;روه&zwnj;ها هه&zwnj;موو زینده&zwnj;وه&zwnj;ران، فریشته&zwnj;کانیش به تایبه&zwnj;ت سوژده ده&zwnj;به&zwnj;ن بۆ خواو خۆ به&zwnj;گه&zwnj;وره نازانن له هیچ فه&zwnj;رمانێکی خوا لاناده&zwnj;ن.';
$t[50] = '(جگه له&zwnj;وه&zwnj;ش) ئه&zwnj;و فریشتانه له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن که فه&zwnj;رمانڕه&zwnj;وایه به&zwnj;سه&zwnj;ریانه&zwnj;وه&zwnj;، هه&zwnj;رچی فه&zwnj;رمانێکمان پێ بدرێت به&zwnj;چاکی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[51] = 'خوا فه&zwnj;رموویه&zwnj;تی: هه&zwnj;رگیز نه&zwnj;که&zwnj;ن دوو دانه خوا بۆ خۆتان بڕیار بده&zwnj;ن، چونکه ئه&zwnj;و خوایه&zwnj;ی که پێویسته ئێوه بیپه&zwnj;رستن بێگومان خوایه&zwnj;کی تاك و ته&zwnj;نهایه&zwnj;، که&zwnj;وابوو بیم و ترستان با هه&zwnj;ر له من بێت.';
$t[52] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر ئه&zwnj;و زاته خاوه&zwnj;نیانه&zwnj;، هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که شایسته&zwnj;ی ئه&zwnj;وه بێت ئاین و به&zwnj;رنامه&zwnj;و فه&zwnj;رمانڕه&zwnj;وایی له&zwnj;و وه&zwnj;ربگیرێت و ملکه&zwnj;چی هه&zwnj;ر بۆ ئه&zwnj;و بکرێت، ئایا ڕه&zwnj;وایه بێجگه له خوا له که&zwnj;سی تر بپه&zwnj;رستن و حسابی بۆ بکه&zwnj;ن؟';
$t[53] = 'هه&zwnj;رچی نازو نیعمه&zwnj;تێکتان هه&zwnj;یه هه&zwnj;مووی به&zwnj;هره&zwnj;و به&zwnj;خششی خوایه&zwnj;، پاشان کاتێك ناخۆشی و به&zwnj;ڵایه&zwnj;کتان بۆ پێشهات، ئه&zwnj;وه هاناو هاوارو په&zwnj;نا هه&zwnj;ر بۆ لای ئه&zwnj;و ده&zwnj;به&zwnj;ن و هه&zwnj;ر له&zwnj;و زاته ده&zwnj;پاڕێنه&zwnj;وه&zwnj;.';
$t[54] = 'له&zwnj;وه&zwnj;ودوا کاتێك به&zwnj;ڵاو ناخۆشی له&zwnj;سه&zwnj;ر لابردن، ده&zwnj;ستبه&zwnj;جێ ده&zwnj;سته&zwnj;یه&zwnj;ك له ئێوه هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ په&zwnj;روه&zwnj;ردگاریان بڕیار ده&zwnj;ده&zwnj;ن!!';
$t[55] = '(وازیان لێ بهێنه&zwnj;) با ناشوکرو سوپاس ناپه&zwnj;رێز بن له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و هه&zwnj;موو به&zwnj;خشش و نازو نیعمه&zwnj;تانه&zwnj;وه که پێمان به&zwnj;خشیوون، با جارێ ڕابوێرن ئینجا له ئاینده&zwnj;دا ده&zwnj;زانن چیتان به&zwnj;سه&zwnj;ر دێت!!';
$t[56] = 'خوا نه&zwnj;ناسان، هه&zwnj;ندێك له&zwnj;و شتانه&zwnj;ی که ئێمه پێمان به&zwnj;خشیوون، داده&zwnj;نێن و بڕیار ده&zwnj;ده&zwnj;ن بۆ ئه&zwnj;وانه&zwnj;ی که نه&zwnj;زان و نه&zwnj;فام و بێ ده&zwnj;سه&zwnj;ڵاتن (که بته&zwnj;کانن)، سوێند به خوا بێگومان پرسیارتان لێده&zwnj;کرێت ده&zwnj;رباره&zwnj;ی هه&zwnj;موو ئه&zwnj;و درۆو پڕو پاگه&zwnj;نده&zwnj;یه&zwnj;ی که هه&zwnj;ڵتان ده&zwnj;به&zwnj;ست.';
$t[57] = 'ئه&zwnj;و نه&zwnj;فامانه کچان ده&zwnj;ده&zwnj;نه پاڵ خوا (گوایه فریشته&zwnj;کان کچی خوان)، پاکی و بێگه&zwnj;ردی شایسته&zwnj;ی ئه&zwnj;و زاته&zwnj;یه هیچ پێویستیه&zwnj;کی به کچ و کوڕ نیه له کاتێکدا بۆ خۆیان حه&zwnj;ز به&zwnj;چی ده&zwnj;که&zwnj;ن ئه&zwnj;وه بۆ خۆیان داده&zwnj;نێن!!';
$t[58] = 'خۆ ئه&zwnj;گه&zwnj;ر مژده&zwnj;ی بوونی کچ بدرێت به یه&zwnj;کێك له&zwnj;و نه&zwnj;فامانه&zwnj;، ڕووی گرژو تاڵ ده&zwnj;بێت و ڕه&zwnj;ش داده&zwnj;گیرسێت و سینه&zwnj;ی پڕ ده&zwnj;بێت له خه&zwnj;م و په&zwnj;ژاره&zwnj;...';
$t[59] = 'له داخ و خه&zwnj;فه&zwnj;تی ئه&zwnj;و مژده ناخۆشه&zwnj;، خۆی ده&zwnj;شارێته&zwnj;وه له خه&zwnj;ڵکی و به&zwnj;ته&zwnj;نها (بیر ده&zwnj;کاته&zwnj;وه&zwnj;): ئایا بیهێڵێته&zwnj;وه به زه&zwnj;لیلی و سووکی و بێ نرخی، یاخود زینده به&zwnj;چاڵی بکات و بیخاته توێی خاکه&zwnj;وه&zwnj;، ئاگاداربن؟! ئای که چه&zwnj;نده خراپه ئه&zwnj;و بڕیاره&zwnj;ی که ده&zwnj;یده&zwnj;ن!! (وه&zwnj;چه&zwnj;و نه&zwnj;وه&zwnj;ی خۆیان زینده به&zwnj;چاڵ ده&zwnj;که&zwnj;ن).';
$t[60] = 'سیفه&zwnj;ت و ناونیشانی ناشرین و نزم و خراپ شایسته&zwnj;ی ئه&zwnj;وانه&zwnj;یه که باوه&zwnj;ڕ به قیامه&zwnj;ت ناکه&zwnj;ن، خوای په&zwnj;روه&zwnj;ردگاریش خاوه&zwnj;نی هه&zwnj;موو سیفاتی به&zwnj;رزو بڵندو پیرۆزه&zwnj;و ئه&zwnj;و زاته هه&zwnj;ر خۆیشی باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[61] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا خه&zwnj;ڵکی له&zwnj;ناو ببات و بیانپێچێته&zwnj;وه به&zwnj;هۆی سته&zwnj;میانه&zwnj;وه (ئه&zwnj;وه&zwnj;نده گوناهو تاوان زۆره&zwnj;) زینده&zwnj;وه&zwnj;ر له&zwnj;سه&zwnj;ر زه&zwnj;ویدا نایه&zwnj;ڵێت (یه&zwnj;عنی هه&zwnj;ر زه&zwnj;وی خاپوور ده&zwnj;کات به&zwnj;هه&zwnj;موو گیانله&zwnj;به&zwnj;رانه&zwnj;وه&zwnj;، به&zwnj;ڵام ئه&zwnj;و خوایه ئارامی و خۆگریی بێ سنووره&zwnj;) له&zwnj;به&zwnj;ر ئه&zwnj;وه تا کاتێکی دیاری کراو مۆڵه&zwnj;تی ئه&zwnj;و خه&zwnj;ڵکه ده&zwnj;دات و دوایان ده&zwnj;خات، جا کاتێك کاته دیاری کراوه&zwnj;که&zwnj;یان هات، نه&zwnj;تاوێك دوا ده&zwnj;که&zwnj;ون، نه&zwnj;تاوێك پێشیش ده&zwnj;که&zwnj;ون (به&zwnj;ڵکو له چرکه&zwnj;ی دیاری کراوی خۆیدا له&zwnj;ناو ده&zwnj;برێن).';
$t[62] = 'خوا نه&zwnj;ناسان ئه&zwnj;وه&zwnj;ی بۆ خۆیان پێیان خۆش نیه&zwnj;و نایانه&zwnj;وێت بۆ خوای بڕیار ده&zwnj;ده&zwnj;ن، (گوایه فریشته&zwnj;کان کچن و کچی خوایشن) و له کاتێکدا زمانیان باس و خواسی درۆ ده&zwnj;کات که بێگومان به&zwnj;نیازن له&zwnj;و دنیاش به&zwnj;هه&zwnj;شت و خۆشی بۆ ئه&zwnj;وان بێت، شتێکی ڕاسته&zwnj;و گومانی تێدانیه ئاگری دۆزه&zwnj;خ بۆ ئه&zwnj;وانه و به&zwnj;ڕاستی به&zwnj;په&zwnj;له فڕێ ده&zwnj;درێنه ناوی و فه&zwnj;رامۆش ده&zwnj;کرێن.';
$t[63] = 'سوێند به خوا پێش تۆ بۆ گه&zwnj;ل و قه&zwnj;ومه&zwnj;کانی تریش پێغه&zwnj;مبه&zwnj;رانمان ڕه&zwnj;وانه کردووه&zwnj;، به&zwnj;ڵام شه&zwnj;یتان کارو کرده&zwnj;وه&zwnj;ی (نادروست و هه&zwnj;ڵه&zwnj;یانی) بۆ ڕازاندوونه&zwnj;ته&zwnj;وه&zwnj;، ئه&zwnj;مڕۆ له دنیادا هه&zwnj;ر ئه&zwnj;و پشتیوانیانه و سزای به&zwnj;ئێش بۆ ئه&zwnj;و جۆره قه&zwnj;وم و که&zwnj;سه نا له&zwnj;بارو ناحاڵیانه ئاماده کراوه&zwnj;.';
$t[64] = 'ئێمه ئه&zwnj;م قورئانه&zwnj;مانه&zwnj;مان دانه&zwnj;به&zwnj;زاندووه&zwnj;ته سه&zwnj;ر تۆ ته&zwnj;نها بۆ ئه&zwnj;وه نه&zwnj;بێت که هه&zwnj;رچی ئه&zwnj;و خه&zwnj;ڵکه کێشه&zwnj;ی له&zwnj;سه&zwnj;ر هه&zwnj;یه (له بابه&zwnj;ته&zwnj;کانی دنیاو دین) بۆیان ڕوون بکه&zwnj;ینه&zwnj;وه و بیکه&zwnj;ینه هۆی هیدایه&zwnj;ت و ڕه&zwnj;حمه&zwnj;تیش بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که ئیمان و باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[65] = 'هه&zwnj;ر خوا بارانی له ئاسمانه&zwnj;وه باراندووه&zwnj;و زه&zwnj;وی پێ زیندوو کردۆته&zwnj;وه دوای ئه&zwnj;وه&zwnj;ی که مردبوو، به&zwnj;ڕاستی ئا له&zwnj;و دیارده&zwnj;یه&zwnj;دا نیشانه و به&zwnj;ڵگه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆ که&zwnj;سانێك که گوێ بۆ ڕاستی ده&zwnj;گرن.';
$t[66] = 'به&zwnj;ڕاستی له بوونی ماڵات و (زینده&zwnj;وه&zwnj;رانی جۆراو جۆردا) په&zwnj;ندو ئامۆژگاری هه&zwnj;یه بۆتان و جێگه&zwnj;ی سه&zwnj;رنج و تێڕامانه&zwnj;، له نێوان ئه&zwnj;و شتانه&zwnj;ی که له سکیاندا هه&zwnj;یه&zwnj;، له گژو گیای هه&zwnj;رس کراوو خوێندا، شیرێکی پاك و بێگه&zwnj;ردو خۆش و به&zwnj;تام و گه&zwnj;وارا به&zwnj;رهه&zwnj;م ده&zwnj;هێنین بۆ ئه&zwnj;وانه&zwnj;ی ده&zwnj;یخۆنه&zwnj;وه&zwnj;.';
$t[67] = 'له به&zwnj;روبوومه&zwnj;کانی دار خورما و ڕه&zwnj;زی ترێکانیش (هه&zwnj;ندێ جار) شتی سه&zwnj;رخۆشکه&zwnj;ر دروست ده&zwnj;که&zwnj;ن، (گه&zwnj;لێ جاریش) به&zwnj;رهه&zwnj;می چاك و به&zwnj;سوود (وه&zwnj;کو دۆشاوو مێوژو شه&zwnj;ربه&zwnj;ت،.. هتد)، به&zwnj;ڕاستی ئاله&zwnj;وه&zwnj;شدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکرا هه&zwnj;یه بۆ که&zwnj;سانێك عه&zwnj;قڵ و ژیری خۆیان ده&zwnj;خه&zwnj;نه&zwnj;کار، (تا بیر بکه&zwnj;نه&zwnj;وه چۆن له&zwnj;م دارو دره&zwnj;خته ڕه&zwnj;ق و ته&zwnj;قانه ئه&zwnj;م به&zwnj;رهه&zwnj;مانه دروست ده&zwnj;بێت، چ کارخانه&zwnj;یه&zwnj;ك له ناوکی خورماو ترێ و میوه جۆراو جۆره&zwnj;کاندا حه&zwnj;شاردراوه تا له بارودۆخی خۆیدا بکه&zwnj;وێته کارو خزمه&zwnj;ت به ئاده&zwnj;میزاد بکات... هتد).';
$t[68] = 'په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی ئینسان) نیگاو ئیلهامی کردووه بۆ هه&zwnj;نگ که جێگه بۆ خۆی ساز بکات له کون و که&zwnj;لێنی چیاکاندا، هه&zwnj;روه&zwnj;ها له که&zwnj;له&zwnj;به&zwnj;ری دره&zwnj;خته&zwnj;کان و له&zwnj;و شوێنانه&zwnj;ش که ئاده&zwnj;میزاد بۆی ئاماده ده&zwnj;کات.';
$t[69] = 'له&zwnj;وه&zwnj;ودوا (فێرمان کردووه که&zwnj;) له هه&zwnj;موو به&zwnj;رو بووم و (شیله&zwnj;ی گوڵان و شتی به&zwnj;سوود) بخوات و پێمان ڕاگه&zwnj;یاندووه&zwnj;: ئه&zwnj;و نه&zwnj;خشانه&zwnj;ی که په&zwnj;روه&zwnj;ردگارت بۆی کێشاویت به ملکه&zwnj;چیه&zwnj;وه ئه&zwnj;نجامی بده&zwnj;، (له دروست کردنی شانه&zwnj;و مژینی شیله&zwnj;و دروستکردنی هه&zwnj;نگوین و پووره&zwnj;دان به&zwnj;ده&zwnj;وری شازداو به&zwnj;خێوکردنی گه&zwnj;راکان... هتد)، جا له&zwnj;ناو سکی ئه&zwnj;و هه&zwnj;نگانه&zwnj;وه شله&zwnj;یه&zwnj;کی ڕه&zwnj;نگ جیاواز دێته ده&zwnj;ره&zwnj;وه که شیفای تیادایه بۆ (زۆر نه&zwnj;خۆشی) خه&zwnj;ڵکی (سه&zwnj;ره&zwnj;ڕای تامی شیرین و خۆشی)، به&zwnj;ڕاستی ئاله&zwnj;مه&zwnj;شدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی به&zwnj;هێز هه&zwnj;یه بۆ که&zwnj;سانێك بیر بکه&zwnj;نه&zwnj;وه (سه&zwnj;رنج بده&zwnj;ن له هه&zwnj;نگ و شانه&zwnj;کانی له فڕین و هاتووچۆی، له هه&zwnj;نگوین و تام و بۆن و ڕه&zwnj;نگی، له&zwnj;..، له&zwnj;.. هتد).';
$t[70] = '(خه&zwnj;ڵکینه&zwnj;) به&zwnj;ڕاستی هه&zwnj;ر خوا دروستی کردوون و پاشان هه&zwnj;ر ئه&zwnj;ویش ده&zwnj;تانمرێنێت، هه&zwnj;تا ته&zwnj;مه&zwnj;نی درێژ ده&zwnj;بێت و ده&zwnj;برێته&zwnj;وه بۆ لاوازترین ته&zwnj;مه&zwnj;ن (که&zwnj;نه&zwnj;فه&zwnj;تی و که&zwnj;فته&zwnj;کاری) تاوه&zwnj;کو ده&zwnj;گاته ڕاده&zwnj;یه&zwnj;ك که دوای ئه&zwnj;و هه&zwnj;موو زانستی و زانیاری و تێگه&zwnj;یشتنه&zwnj;ی که هه&zwnj;ی بوو، هه&zwnj;موو شتێکی له&zwnj;بیر ده&zwnj;چێته&zwnj;وه و هیچ نازانێت، به&zwnj;ڕاستی هه&zwnj;ر خوا زاناو به&zwnj;ده&zwnj;سه&zwnj;ڵاته&zwnj;.';
$t[71] = 'خوا له&zwnj;به&zwnj;خشینی ڕزق و ڕۆزیدا به&zwnj;شی هه&zwnj;ندێکتانی زۆرترداوه له هه&zwnj;ندێکی ترتان (بۆ تاقیکردنه&zwnj;وه&zwnj;تان)، جا ئه&zwnj;وانه&zwnj;ی که ڕزق و ڕۆزی زیاتریان پێدراوه ئاماده&zwnj;نین که به&zwnj;نده&zwnj;کانیان و به&zwnj;رده&zwnj;سته&zwnj;کانیان بهێننه ڕیزی خۆیان و بیانکه&zwnj;نه شه&zwnj;ریك (له خانوو به&zwnj;ره&zwnj;و ماڵ و سامانیاندا)، ئینجا ئه&zwnj;وانیش یه&zwnj;کسان بن تیایدا (ئێوه له ژیانی دنیای خۆتاندا کاری وا ناکه&zwnj;ن و به&zwnj;ڕه&zwnj;وای نازانن، ئیتر چۆن بۆ خوای گه&zwnj;وره&zwnj;، به&zwnj;ڕه&zwnj;وای ده&zwnj;زانن که به&zwnj;نده&zwnj;کانی ببنه شه&zwnj;ریکی و یاسادانه&zwnj;ر) جا ئایا ئه&zwnj;وانه ئینکاری نازو نیعمه&zwnj;ته&zwnj;کانی خوا ده&zwnj;که&zwnj;ن؟!';
$t[72] = 'خوا هه&zwnj;ر له خۆتان هاوسه&zwnj;رانی بۆ فه&zwnj;راهه&zwnj;م هێناون، هه&zwnj;ر له ڕێگه&zwnj;ی هاوسه&zwnj;رانتانه&zwnj;وه کوڕو کوڕه&zwnj;زای پێبه&zwnj;خشیوون و له هه&zwnj;موو ڕزق و ڕۆزیه&zwnj;کی چاك و پاك و به&zwnj;سوود به&zwnj;هره&zwnj;وه&zwnj;ری کردوون، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;و ڕاستیانه&zwnj;دا) ئایا ئه&zwnj;وانه هه&zwnj;ر بڕوا به&zwnj;شتی پووچ و ناحه&zwnj;قی ده&zwnj;که&zwnj;ن و له به&zwnj;رامبه&zwnj;ر نازو نیعمه&zwnj;ته&zwnj;کانی خواوه ئه&zwnj;وانه هه&zwnj;ر ناشوکرو سوپاس ناپه&zwnj;زێری و بێ باوه&zwnj;ڕ ده&zwnj;بن؟';
$t[73] = 'ئه&zwnj;و (خه&zwnj;ڵکه سه&zwnj;رگه&zwnj;ردانه&zwnj;) شتێك ده&zwnj;په&zwnj;رستن له جیاتی خوا، که هیچ جۆره ڕزق و ڕۆزیه&zwnj;کی ئه&zwnj;وانی به&zwnj;ده&zwnj;ست نیه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، ناشتوانن هیچ به&zwnj;ده&zwnj;ست بن!!';
$t[74] = 'که&zwnj;وابوو ئێوه ئه&zwnj;ی خوا نه&zwnj;ناسان نموونه مه&zwnj;هێننه&zwnj;وه بۆ خواو به&zwnj;راوردی هه&zwnj;ڵه مه&zwnj;که&zwnj;ن (ده&zwnj;یانوت پیاوانی پاشا خزمه&zwnj;تکاریان هه&zwnj;یه&zwnj;، پیاوانی پاشاش خزمه&zwnj;تکاری پاشان!!)، چونکه خوا خۆی هه&zwnj;موو شتێك چاك ده&zwnj;زانێت، به&zwnj;ڵام ئێوه نه&zwnj;زان و نه&zwnj;فامن.';
$t[75] = 'خوا نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به که&zwnj;سێکی به&zwnj;نده که بێ ده&zwnj;سه&zwnj;ڵاته&zwnj;و هیچ شتێکی نیه&zwnj;، له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;دا که له ڕزق و ڕۆزی چاك و زۆر به&zwnj;هره&zwnj;وه&zwnj;رمان کردووه&zwnj;، به ئاره&zwnj;زووی خۆی لێی ده&zwnj;به&zwnj;خشێت به&zwnj;نهێنی و ئاشکرا، ئایا ئه&zwnj;و دوو که&zwnj;سه وه&zwnj;ك یه&zwnj;کن؟! سوپاس و ستایش بۆ خوا (که په&zwnj;روه&zwnj;ردگارێکی کاربه&zwnj;جێ یه&zwnj;) به&zwnj;ڵام زۆربه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه نازانن و له&zwnj;م ڕاستیانه سه&zwnj;ریان ده&zwnj;رناچێت (چونکه ته&zwnj;نها دنیا ده&zwnj;بینن).';
$t[76] = 'هه&zwnj;روه&zwnj;ها خوا نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به دوو پیاو که یه&zwnj;کێکیان ڵاڵ بێت و هیچی بۆ نه&zwnj;وترێت و هیچی بۆ نه&zwnj;کرێت و بووبێته بار به&zwnj;سه&zwnj;ر هاوه&zwnj;ڵ و که&zwnj;س و کاریه&zwnj;وه&zwnj;، بۆ هه&zwnj;رچی و بۆ هه&zwnj;ر کوێ ڕه&zwnj;وانه بکرێت خێر ناداته&zwnj;وه&zwnj;، ئایا ئه&zwnj;و که&zwnj;سه&zwnj;، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه وایه که (به&zwnj;وپه&zwnj;ڕی ئازادیه&zwnj;وه&zwnj;)، فه&zwnj;رمان به دادو دادپه&zwnj;روه&zwnj;ری بدات و ڕێبازی ڕاست و دروست ده&zwnj;گرێته به&zwnj;ر؟!';
$t[77] = 'هه&zwnj;رچی نهێنی و شاراوه هه&zwnj;یه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;ر بۆ خوایه&zwnj;و به&zwnj;رپا بوونی قیامه&zwnj;ت پێش نایه&zwnj;ت وه&zwnj;ك چاو تر&zwnj;وکاندنێك نه&zwnj;بێت یان که&zwnj;متر له&zwnj;وه&zwnj;ش، به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[78] = 'خوا ئێوه&zwnj;ی له سکی دایکتان ده&zwnj;رهێناوه بێ ئه&zwnj;وه&zwnj;ی هیچ شتێك بزانن، ئینجا ده&zwnj;زگای بیستن و بینین و تێگه&zwnj;یشتنی بۆ دابین کردوون، بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزاری بکه&zwnj;ن.';
$t[79] = 'ئایا باڵنده&zwnj;کانیان نه&zwnj;بینیوه&zwnj;و سه&zwnj;رنجیان نه&zwnj;داون چۆن له هه&zwnj;وای ئاسماندا بارهێنراون بۆ فڕین؟، کێ ده&zwnj;توانێت ڕایانبگرێت به&zwnj;ئاسمانه&zwnj;وه جگه له خوای گه&zwnj;وره (هه&zwnj;وا، چرکه&zwnj;یه&zwnj;کی زۆر که&zwnj;مه&zwnj;، که&zwnj;چی ئه&zwnj;م هه&zwnj;موو جۆره باڵه فڕه تیایدا به سه&zwnj;ربه&zwnj;ستی ده&zwnj;فڕێت، بێگومان ئه&zwnj;م ڕه&zwnj;حمه&zwnj;ته&zwnj;ی خوا هه&zwnj;موو فڕۆکه&zwnj;و باڵه فڕێکی ده&zwnj;ست کردی ئاده&zwnj;میزادیشی گرتۆته&zwnj;وه&zwnj;، که له بنه&zwnj;ڕه&zwnj;تدا ده&zwnj;ستکاری خوای باڵاده&zwnj;ستن)، به&zwnj;ڕاستی ئا له&zwnj;وه&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[80] = 'هه&zwnj;ر خوایه (که هه&zwnj;موو شتێکی بۆ ڕه&zwnj;خساندوون تا ماڵ و خانووبه&zwnj;ره بۆ خۆتان دروست بکه&zwnj;ن) ماڵه&zwnj;کانتانی بۆ کردوون به&zwnj;هۆی ئارامگرتن و حه&zwnj;وانه&zwnj;وه&zwnj;و له پێست و مووی ماڵاتیش ده&zwnj;توانن شوێن و جێگه بۆ خۆتان بسازێنن، پاشان به&zwnj;ئاسانی بیگوێزنه&zwnj;وه له&zwnj;کاتی کۆچتاندا، به&zwnj;ئاسانی هه&zwnj;ڵی بده&zwnj;ن له&zwnj;کاتی نیشته&zwnj;جێ بوونتاندا و له خوری و کوڵك و مووه&zwnj;که&zwnj;ی، ده&zwnj;توانن که&zwnj;ل و په&zwnj;ل و شتومه&zwnj;ك بۆ خۆتان دروست بکه&zwnj;ن و تاماوه&zwnj;یه&zwnj;ك سوودیان لێ وه&zwnj;ربگرن.';
$t[81] = 'خوا له&zwnj;و شتانه&zwnj;ی که دروستی کردووه سێبه&zwnj;ری بۆ ئێوه ڕه&zwnj;خساندووه&zwnj;، له کێوه&zwnj;کانیشدا کون و که&zwnj;لێن و ئه&zwnj;شکه&zwnj;وتی سازاندووه&zwnj;و پۆشاکی بۆتان به&zwnj;دیهێناوه تا له گه&zwnj;رماو (سه&zwnj;رما) بتانپارێزێت، هه&zwnj;روه&zwnj;ها جۆره&zwnj;ها پۆشاکی تر بۆ به&zwnj;دیهێناون بۆ پاڕاستنی گیانتان له&zwnj;کاتی جه&zwnj;نگ و شه&zwnj;ڕو شۆڕدا (وه&zwnj;کو زرێ و شتومه&zwnj;کی تر)، ئا به&zwnj;و شێوه&zwnj;یه په&zwnj;روه&zwnj;ردگارتان نازو نیعمه&zwnj;تی خۆیتان به&zwnj;ته&zwnj;واوی به&zwnj;سه&zwnj;ردا ده&zwnj;ڕژێنێت بۆ ئه&zwnj;وه&zwnj;ی ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار بن.';
$t[82] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو به&zwnj;ڵگه&zwnj;و نازو نیعمه&zwnj;تانه&zwnj;دا) ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه هه&zwnj;ر پشتیان هه&zwnj;ڵبکه&zwnj;ن و ڕوو وه&zwnj;ڕبگێڕن (ئه&zwnj;وه خه&zwnj;مت نه&zwnj;بێت هه&zwnj;ر خۆیان زه&zwnj;ره&zwnj;ر ده&zwnj;که&zwnj;ن) چونکه به&zwnj;ڕاستی ئه&zwnj;رکی سه&zwnj;رشانی تۆ ئه&zwnj;وه&zwnj;یه که به جوانی و تێرو ته&zwnj;واوی بانگه&zwnj;وازه&zwnj;که بگه&zwnj;یه&zwnj;نیت.';
$t[83] = '(بێ سه&zwnj;لیقه&zwnj;و نه&zwnj;فامان) نازو نیعمه&zwnj;ته&zwnj;کانی خوا ده&zwnj;ناسن و به&zwnj;هره&zwnj;ی لێ ده&zwnj;به&zwnj;ن، پاشان ئینکاری ده&zwnj;که&zwnj;ن و قه&zwnj;دری نازانن (به&zwnj;دنمه&zwnj;ك و سپڵه&zwnj;ن)، زۆربه&zwnj;یان بێ دین و بێ باوه&zwnj;ڕن.';
$t[84] = 'ڕۆژێك دێت له&zwnj;هه&zwnj;ر گه&zwnj;ل و نه&zwnj;ته&zwnj;وه&zwnj;یه&zwnj;کدا پێغه&zwnj;مبه&zwnj;رێك بۆ شایه&zwnj;تی ده&zwnj;هێنینه مه&zwnj;یدان، له&zwnj;وه&zwnj;ودوا به&zwnj;هیچ جۆرێك مۆڵه&zwnj;ت به&zwnj;وانه نادرێت که بێ باوه&zwnj;ڕ بوون و پاکانه&zwnj;یان لێ وه&zwnj;رناگیرێت.';
$t[85] = 'کاتێك سته&zwnj;مکاران سزای سه&zwnj;ختی دۆزه&zwnj;خ ده&zwnj;بینن (داوای سووك کردنی ده&zwnj;که&zwnj;ن) ئه&zwnj;وه سووك ناکرێت له&zwnj;سه&zwnj;ریان، هیچ جۆره مۆڵه&zwnj;تێکیشیان نادرێت.';
$t[86] = '(له&zwnj;ناو دۆزه&zwnj;خدا) کاتێکیش ئه&zwnj;وانه&zwnj;ی که هاوه&zwnj;ڵیان داناوه&zwnj;، بته&zwnj;کان و ئه&zwnj;و که&zwnj;س و شتانه ده&zwnj;بینن که جاران ده&zwnj;یانپه&zwnj;رست، ئه&zwnj;وه ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئا ئه&zwnj;وانه&zwnj;، ئه&zwnj;و شتانه&zwnj;ن که له جیاتی تۆ هاناو هاوارمان بۆ ده&zwnj;بردن له&zwnj;جیاتی تۆ، ئه&zwnj;وانیش قسه&zwnj;که ده&zwnj;ده&zwnj;نه&zwnj;وه به&zwnj;سه&zwnj;ریانداو ده&zwnj;ڵێن: به&zwnj;ڕاستی ئێوه درۆ ده&zwnj;که&zwnj;ن و ڕاست ناکه&zwnj;ن.';
$t[87] = 'ئه&zwnj;و ڕۆژه هه&zwnj;موویان خۆیان ته&zwnj;سلیمی خوا کرد به ملکه&zwnj;چی و زه&zwnj;لیلی، هه&zwnj;رچیش هه&zwnj;ڵیانده&zwnj;به&zwnj;ست و ئومێدیان پێی بوو لێیان بزر بوو (ئیتر بۆ هه&zwnj;میشه هه&zwnj;ناسه ساردو ڕیسوان).';
$t[88] = 'ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون و به&zwnj;ربه&zwnj;ستیان ده&zwnj;خسته سه&zwnj;ر ڕێبازی خواو کۆسپیان داده&zwnj;نا له به&zwnj;رده&zwnj;م ئاینی خوادا، سزامان له&zwnj;سه&zwnj;ریه&zwnj;ك بۆ که&zwnj;ڵه&zwnj;که کردن، به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه که زۆر هه&zwnj;وڵ و کۆششیان ده&zwnj;دا بۆ بڵاوکردنه&zwnj;وه&zwnj;ی فه&zwnj;سادو تاوان و خراپه (له&zwnj;ناو خه&zwnj;ڵکیدا).';
$t[89] = 'ڕۆژێك دێت له هه&zwnj;موو ئوممه&zwnj;تێکدا شایه&zwnj;تێك ده&zwnj;نێرین له خۆیان بۆ سه&zwnj;ریان، تۆشمان هێناوه (ئه&zwnj;ی محمد صلی الله علیه وسلم) که شایه&zwnj;ت بیت له&zwnj;سه&zwnj;ر ئه&zwnj;و خه&zwnj;ڵکه&zwnj;، قورئانیشمان بۆ تۆ دابه&zwnj;زاندووه&zwnj;، ڕوونکه&zwnj;ره&zwnj;وه&zwnj;ی هه&zwnj;موو شتێکه&zwnj;، هیدایه&zwnj;ت به&zwnj;خش و ڕه&zwnj;حمه&zwnj;ته&zwnj;و مزگێنیشه بۆ موسوڵمانان.';
$t[90] = 'به&zwnj;ڕاستی خوا فه&zwnj;رمان ده&zwnj;دات به دادپه&zwnj;روه&zwnj;ری و چاکه&zwnj;کاری و یارمه&zwnj;تیدان و به&zwnj;خشنده&zwnj;یی به خزمان و قه&zwnj;ده&zwnj;غه له گوناهو تاوان و ده&zwnj;ستدرێژی ده&zwnj;کات (بۆ سه&zwnj;ر ماڵ و نه&zwnj;فس و نامووس) و ئامۆژگاریتان ده&zwnj;کات بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن و تێفکرن و (هه&zwnj;ر چاکه بکه&zwnj;ن).';
$t[91] = 'به&zwnj;وه&zwnj;فاو به ئه&zwnj;مه&zwnj;ك بن بۆ ئه&zwnj;نجامدانی په&zwnj;یمانی خوا کاتێك بڕیاری ده&zwnj;ده&zwnj;ن و سوێند و په&zwnj;یمان و به&zwnj;ڵێنتان هه&zwnj;ڵمه&zwnj;وه&zwnj;شێننه&zwnj;وه دوای ئه&zwnj;وه&zwnj;ی که دووپات کرابۆوه و ته&zwnj;ئکیدی له&zwnj;سه&zwnj;ر کرابوو، له کاتێکدا که ئێوه خواتان کردۆته که&zwnj;فیل و زامن به&zwnj;سه&zwnj;ر خۆتانه&zwnj;وه&zwnj;، بێگومان خوا ده&zwnj;زانێت به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[92] = '(کاتێك به&zwnj;ڵێن و په&zwnj;یمانێك ده&zwnj;ده&zwnj;ن ئه&zwnj;مه&zwnj;ك و وه&zwnj;فاتان هه&zwnj;بێت بۆ ئه&zwnj;نجامدانی) وه&zwnj;کو ئه&zwnj;و ئافره&zwnj;ته مه&zwnj;بن که ڕیسه&zwnj;که&zwnj;ی ده&zwnj;کرده&zwnj;وه به خوری، دوای ڕستن و تۆکمه&zwnj;کردنی هه&zwnj;ڵی ده&zwnj;وه&zwnj;شانده&zwnj;وه&zwnj;، له کاتێکدا ئێوه&zwnj;ش به&zwnj;ڵێن و سوێندو په&zwnj;یمانتان بکه&zwnj;نه هۆی فێڵ و ته&zwnj;ڵه&zwnj;که له نێوانتاندا، نه&zwnj;وه&zwnj;کو ده&zwnj;سته&zwnj;و کۆمه&zwnj;ڵێکی ژماره زۆرو ده&zwnj;سه&zwnj;ڵاتدار په&zwnj;لاماری ده&zwnj;سته&zwnj;یه&zwnj;کی تری که&zwnj;م ده&zwnj;ست و لاواز بدات، بێگومان خوا به&zwnj;و جۆره شتانه تاقیتان ده&zwnj;کاته&zwnj;وه&zwnj;، سوێند به خوا هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;تان بۆ ڕوون ده&zwnj;کاته&zwnj;وه که جیاوازی و کێشه&zwnj;تان تێدا هه&zwnj;بوو.';
$t[93] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا بیویستایه ده&zwnj;یکردن به&zwnj;یه&zwnj;ك ئوممه&zwnj;ت (به&zwnj;ڵکو ڕێزی بۆ داناون و مۆڵه&zwnj;تی داون و سه&zwnj;ربه&zwnj;ستی کردوون)، به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی خوا بیه&zwnj;وێت گومڕای ده&zwnj;کات و ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت ڕێنموویی ده&zwnj;کات (ئه&zwnj;ڵبه&zwnj;ته ویستی خوا دژایه&zwnj;تی ویستی ئاده&zwnj;میزاد ناکات، ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت ڕێبازی هیدایه&zwnj;ت بگرێت ڕێنموویی ده&zwnj;کات، ئه&zwnj;وه&zwnj;ش ڕێبازی گومڕایی بگرێته به&zwnj;ر مۆڵه&zwnj;تی ده&zwnj;دات و زۆری لێ ناکات)، بێگومان پرسیارتان لێ ده&zwnj;کرێت ده&zwnj;رباره&zwnj;ی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامتان ده&zwnj;دا.';
$t[94] = 'نه&zwnj;که&zwnj;ن په&zwnj;یمان و سوێنده&zwnj;کانتان بکه&zwnj;نه هۆی پیلان گێڕان و فێڵ و ته&zwnj;ڵه&zwnj;که له نێوانتاندا، (ئه&zwnj;و بڕیارو په&zwnj;یمانانه هه&zwnj;ڵنه&zwnj;وه&zwnj;شێت) تا پێ هه&zwnj;ڵنه&zwnj;خلیسکێت دوای ئه&zwnj;وه&zwnj;ی قایم وه&zwnj;ستابوو، تاڵاوی ناخۆش نه&zwnj;چێژن کاتێك به&zwnj;هۆی ئه&zwnj;و کاره&zwnj;تانه&zwnj;وه به&zwnj;ربه&zwnj;ست ده&zwnj;خه&zwnj;نه به&zwnj;رده&zwnj;م ڕێبازی خوا، (ئه&zwnj;گه&zwnj;ر وابکه&zwnj;ن) ئه&zwnj;وکاته سزایه&zwnj;کی گه&zwnj;وره&zwnj;تان بۆ هه&zwnj;یه&zwnj;.';
$t[95] = '(په&zwnj;یمانی خوا مه&zwnj;شکێنن) و به هه&zwnj;ڵوه&zwnj;شاندنه&zwnj;وه&zwnj;ی به&zwnj;نرخێکی که&zwnj;م مه&zwnj;یگۆڕن، (ئه&zwnj;و نرخه هه&zwnj;رچه&zwnj;ند بێ هه&zwnj;ر که&zwnj;مه&zwnj;) چونکه بێگومان ئه&zwnj;و به&zwnj;شه&zwnj;ی لای خوایه له به&zwnj;هه&zwnj;شتدا هه&zwnj;ر ئه&zwnj;وه چاکتره بۆتان ئه&zwnj;گه&zwnj;ر بزانن و تێبگه&zwnj;ن.';
$t[96] = 'ئه&zwnj;وه&zwnj;ی لای ئێوه&zwnj;یه و به ئێوه به&zwnj;خشراوه&zwnj;، ته&zwnj;واو ده&zwnj;بێت، به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی لای خوایه&zwnj;و (له پێناوی ئه&zwnj;ودا ده&zwnj;یبه&zwnj;خشن) هه&zwnj;ر ده&zwnj;مێنێته&zwnj;وه&zwnj;، (که پاداشتی به&zwnj;هه&zwnj;شتی به&zwnj;رین و نه&zwnj;بڕاوه&zwnj;یه&zwnj;) سوێند به خوا بێگومان پاداشتی ئه&zwnj;وانه&zwnj;ی که خۆگرو ئارامگر بوون به&zwnj;جوانتر ده&zwnj;ده&zwnj;ینه&zwnj;وه له&zwnj;وه&zwnj;ی که ئه&zwnj;وان ده&zwnj;یانکرد.';
$t[97] = 'ئه&zwnj;وه&zwnj;ی کارو کرده&zwnj;وه&zwnj;ی چاکه&zwnj;ی کردبێت، پیاو بێت یان ئافره&zwnj;ت له کاتێکدا بڕوادار بێت، سوێند به خوا به&zwnj;ژیانێکی کامه&zwnj;ران و ئاسووده له دنیادا ده&zwnj;یژێنین، له قیامه&zwnj;تیشدا پاداشتیان به&zwnj;جوانتر ده&zwnj;ده&zwnj;ینه&zwnj;وه له&zwnj;وه&zwnj;ی که ئه&zwnj;وان ده&zwnj;یانکرد.';
$t[98] = 'ئینجا کاتێك قورئانت خوێندو ده&zwnj;ورت کرده&zwnj;وه&zwnj;، ئه&zwnj;وه په&zwnj;نا بگره به خوا له شه&zwnj;یتانی نه&zwnj;فرین لێکراو.';
$t[99] = 'بێگومان شه&zwnj;یتان ده&zwnj;سه&zwnj;ڵاتی نیه به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;دا که باوه&zwnj;ڕیان هێناوه و پشت به په&zwnj;روه&zwnj;ردگاریان ده&zwnj;به&zwnj;ستن...';
$t[100] = 'به&zwnj;ڕاستی ته&zwnj;نها ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;دا هه&zwnj;یه که پشتیان پێ به&zwnj;ستووه&zwnj;و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;و ده&zwnj;بن، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش که هاوه&zwnj;ڵگه&zwnj;رن بۆ په&zwnj;روه&zwnj;ردگار.';
$t[101] = 'کاتێکیش ئایه&zwnj;تێك ده&zwnj;خه&zwnj;ینه جێی ئایه&zwnj;تێکی تر (بۆ ئه&zwnj;وه&zwnj;ی پله به پله شتێك حه&zwnj;رام بکه&zwnj;ین، یان دیارده&zwnj;یه&zwnj;کی نه&zwnj;فامی لابه&zwnj;رین) خوایش خۆی ده&zwnj;زانێت چی داده&zwnj;به&zwnj;زێنێت و چ فه&zwnj;رمانێك ده&zwnj;دات، ئه&zwnj;وه نه&zwnj;فامان ده&zwnj;ڵێن: (ئه&zwnj;ی محمد صلی الله علیه وسلم) بێگومان تۆ هه&zwnj;ر له خۆته&zwnj;وه شت هه&zwnj;ڵده&zwnj;به&zwnj;ستیت، نه&zwnj;خێر وا نیه&zwnj;، (ئه&zwnj;و له خۆیه&zwnj;وه شت ناڵێت) به&zwnj;ڵکو زۆربه&zwnj;ی ئه&zwnj;و نه&zwnj;فامانه تێناگه&zwnj;ن و نازانن.';
$t[102] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: ڕوح القدس، (جوبره&zwnj;ئیل، ئه&zwnj;م قورئانه&zwnj;ی) له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه به حه&zwnj;ق و ڕاستی دابه&zwnj;زاندووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه دامه&zwnj;زراو پایه&zwnj;دار بکات و ڕێنموویی و مژده&zwnj;شه بۆ موسوڵمانان.';
$t[103] = 'سوێند به خوا ئێمه به&zwnj;ڕاستی ده&zwnj;زانین بێ باوه&zwnj;ڕان ده&zwnj;ڵێن: ئه&zwnj;م قورئانه ته&zwnj;نها که&zwnj;سێك فێری ده&zwnj;کات، (به&zwnj;مه&zwnj;ر جێك) زمانی ئه&zwnj;و که&zwnj;سه&zwnj;ی ئه&zwnj;وان ده&zwnj;ڵێن قورئان فێری محمد ده&zwnj;کات، عه&zwnj;جه&zwnj;میه&zwnj;و عه&zwnj;ره&zwnj;بی نیه&zwnj;و ڕه&zwnj;وان نیه&zwnj;، له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;دا ئه&zwnj;م قورئانه به زمانی عه&zwnj;ره&zwnj;بی و پاراوو ڕوون و ئاشکرا داڕێژراوه&zwnj;.';
$t[104] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که بڕوایان به ئایه&zwnj;ته&zwnj;کانی خوا نیه&zwnj;، خوای گه&zwnj;وره هیدایه&zwnj;ت و ڕێنموویان ناکات، به&zwnj;ڵکو سزای به ئێشی بۆ ئاماده کردوون.';
$t[105] = 'بێگومان هه&zwnj;ر ئه&zwnj;وانه درۆ هه&zwnj;ڵده&zwnj;به&zwnj;ستن که باوه&zwnj;ڕیان به ئایه&zwnj;ته&zwnj;کانی خوا نیه&zwnj;، هه&zwnj;ر ئه&zwnj;وانیش درۆزنن و درۆیان کردۆته پیشه&zwnj;.';
$t[106] = 'ئه&zwnj;وه&zwnj;ی پاشگه&zwnj;ز بێته&zwnj;وه له خوا ناسی، دوای باوه&zwnj;ڕهێنانی، جگه له&zwnj;وانه&zwnj;ی که ناچار ده&zwnj;کرێن وشه&zwnj;ی کوفر بێت به&zwnj;سه&zwnj;ر زاریاندا له کاتێکدا دڵیان پڕه له ئیمان، به&zwnj;ڵام ئه&zwnj;و که&zwnj;سه&zwnj;ی دڵی به کوفرو بێ دینی خۆش کردبێت، ئه&zwnj;وانه خه&zwnj;شم و قینی خوایان له&zwnj;سه&zwnj;ره&zwnj;و سزای گه&zwnj;وره&zwnj;و سه&zwnj;خت و دژواریان بۆ هه&zwnj;یه&zwnj;.';
$t[107] = 'ئه&zwnj;و خه&zwnj;شم و قینه&zwnj;ش له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه چونکه به&zwnj;ڕاستی ئه&zwnj;وانه ژیانی دنیایان خۆش ویستووه تا ژیانی دواڕۆژ، بێگومان خوا هیدایه&zwnj;تی قه&zwnj;وم و که&zwnj;سانی بێ باوه&zwnj;ڕو خوا نه&zwnj;ناس نادات.';
$t[108] = 'ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;، ئه&zwnj;وانه&zwnj;ن که خوا مۆری ناوه به&zwnj;سه&zwnj;ر دڵ و ده&zwnj;روون و ده&zwnj;زگای بیستن و بینینیانداو ئه&zwnj;وانه غافڵ و بێ ئاگان (له حه&zwnj;قیقه&zwnj;ت و ڕاستیه&zwnj;کان).';
$t[109] = 'شتێکی ڕاسته&zwnj;و گومانی تێدانیه ئه&zwnj;وان له ڕۆژی قیامه&zwnj;تدا خه&zwnj;ساره&zwnj;&zwnj;تمه&zwnj;ندو ڕیسواو زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[110] = 'پاشان بێگومان (ئه&zwnj;ی محمد صلی الله علیه وسلم) په&zwnj;روه&zwnj;ردگاری تۆ به&zwnj;نیسبه&zwnj;تی ئه&zwnj;وانه&zwnj;وه که کۆچیان کرد، دوای ئه&zwnj;وه&zwnj;ی ئازارو ئه&zwnj;شکه&zwnj;نجه&zwnj;دران، له&zwnj;وه&zwnj;ودوا جیهادو کۆشش خۆگرییان به&zwnj;رده&zwnj;وام بوو، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت دوای ئه&zwnj;و پێشهاتانه زۆر لێخۆش بوو میهره&zwnj;بانه (به&zwnj;رامبه&zwnj;ر ئه&zwnj;و ئیماندارانه&zwnj;).';
$t[111] = 'ڕۆژێك دێت که هه&zwnj;موو که&zwnj;س خه&zwnj;می خۆیه&zwnj;تی، به&zwnj;رگری له خۆی ده&zwnj;کات، پاکانه بۆ خۆی ده&zwnj;کات، (گوێ به&zwnj;که&zwnj;سی تر نادات، ئه&zwnj;و ڕۆژه&zwnj;) هه&zwnj;رکه&zwnj;س پاداشتی کرده&zwnj;وه&zwnj;کانی ده&zwnj;درێته&zwnj;وه و ئه&zwnj;و خه&zwnj;ڵکه سته&zwnj;میان لێ ناکرێت.';
$t[112] = 'خوا نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه (به دانیشتوانی) شارێك که ژیانیان به هێمنی و ئاسووده&zwnj;یی ده&zwnj;برده سه&zwnj;ر، له هه&zwnj;موو لایه&zwnj;که&zwnj;وه ڕزق و ڕۆزی جۆراو جۆریان بۆ ده&zwnj;هات (به&zwnj;ڵام له جیاتی سوپاسگوزاریی) ناشوکرو قه&zwnj;درنه&zwnj;زانی نازو نیعمه&zwnj;ته&zwnj;کانی خوا بوون، ئینجا خوای گه&zwnj;وره پۆشاکی برسێتی و ترسی کرده کاڵای باڵایان و تاڵاوی نه&zwnj;داریی و ترسی پێ چه&zwnj;شتن له سه&zwnj;ر ئه&zwnj;نجامی ئه&zwnj;و ئیش و کاره (ناپوخت و ناڕێکانه&zwnj;دا) که ده&zwnj;یانکرده پیشه&zwnj;ی خۆیان.';
$t[113] = 'سوێند به خوا به&zwnj;ڕاستی ئه&zwnj;وانه پێغه&zwnj;مبه&zwnj;رێکیان هه&zwnj;ر له خۆیان بۆ هات، که&zwnj;چی به درۆیان زانی و بڕوایان پێنه&zwnj;کرد، ئه&zwnj;وسا سزای سه&zwnj;ختی خوایی گرتنی له&zwnj;ناوی بردن له کاتێکدا ئه&zwnj;وان سته&zwnj;مکار بوون.';
$t[114] = 'جا ئێوه ئه&zwnj;ی ئیمانداران بخۆن له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی حه&zwnj;ڵاڵ و پاکه&zwnj;ی که خوا پێی به&zwnj;خشیوون، هه&zwnj;میشه شوکرو سوپاسی نازو نیعمه&zwnj;تی خوا بکه&zwnj;ن، ئه&zwnj;گه&zwnj;ر ئێوه ته&zwnj;نها ئه&zwnj;و زاته ده&zwnj;په&zwnj;رستن.';
$t[115] = '(خوای په&zwnj;روه&zwnj;ردگار) ته&zwnj;نها مرداره&zwnj;وه بوو و، خوێن و، گۆشتی به&zwnj;رازو، ئه&zwnj;و ماڵاتانه&zwnj;ی که ناوی غه&zwnj;یری خوایان له کاتی سه&zwnj;ربڕیندا له&zwnj;سه&zwnj;ر هێنراوه&zwnj;، حه&zwnj;رامی کردووه لێتان، جا ئه&zwnj;گه&zwnj;ر که&zwnj;سێك پێویستی ناچاری کرد به خواردنیان بێ زیاده&zwnj;ڕه&zwnj;وی و له سنوور ده&zwnj;رچوون، ئه&zwnj;وه به&zwnj;ڕاستی خوا زۆر لێخۆشبوو به به&zwnj;زه&zwnj;یی و دلۆڤانه&zwnj;.';
$t[116] = 'نه&zwnj;که&zwnj;ن به&zwnj;قاڵه&zwnj;ی ده&zwnj;م و زمانتان بڵێن ئه&zwnj;مه حه&zwnj;ڵاڵه&zwnj;و ئه&zwnj;مه حه&zwnj;رامه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی درۆ به&zwnj;ناوی خواو ئاینی خواوه هه&zwnj;ڵبه&zwnj;ستن، (دیاری کردنی حه&zwnj;ڵاڵ و حه&zwnj;رام به&zwnj;ده&zwnj;ست ئێوه نیه&zwnj;، چونکه&zwnj;) به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی درۆ بۆ خواو ئاینی خوا هه&zwnj;ڵده&zwnj;به&zwnj;ستن سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتوو نابن.';
$t[117] = '(ئه&zwnj;وانه له دنیادا) که&zwnj;مێك ڕاده&zwnj;بوێرن، به&zwnj;ڵام له قیامه&zwnj;تدا سزای به ئێش و ئازاریان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[118] = 'له&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;ش که بوونه&zwnj;ته جوو، هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;مان له&zwnj;سه&zwnj;ر حه&zwnj;رام کردوون که پێشتر بۆ تۆمان باس کردووه (له ئایه&zwnj;تی ١٤٦ سووره&zwnj;تی الانعام دا) جا وه&zwnj;نه&zwnj;بێت ئێمه سته&zwnj;ممان لێکردبن به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;میان له خۆیان ده&zwnj;کرد.';
$t[119] = 'پاشان، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ بۆ که&zwnj;سانێك که گوناهو تاوانیان ئه&zwnj;نجامداوه به نه&zwnj;فامی، له&zwnj;وه&zwnj;ودوا په&zwnj;شیمان بونه&zwnj;ته&zwnj;وه و ته&zwnj;وبه&zwnj;یان کردووه&zwnj;و چاکسازیان ئه&zwnj;نجامداوه&zwnj;، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ دوای ئه&zwnj;و ته&zwnj;وبه&zwnj;و چاکه&zwnj;کاریه زۆر لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[120] = 'به&zwnj;ڕاستی ئیبراهیم علیه السلام هه&zwnj;ر خۆی ئومه&zwnj;تێك بوو، (هه&zwnj;ر خۆی پێشه&zwnj;وایه&zwnj;ك بوو) ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری خوا بوو و له هه&zwnj;موو بیرو باوه&zwnj;ڕێکی چه&zwnj;وت به&zwnj;دوور بوو، له موشریك و هاوه&zwnj;ڵگه&zwnj;رانیش نه&zwnj;بوو...';
$t[121] = 'هه&zwnj;روه&zwnj;ها زۆر سوپاسگوزاری خوا بوو له به&zwnj;رامبه&zwnj;ر نازو نیعمه&zwnj;تی بێ شوماری په&zwnj;روه&zwnj;ردگاریه&zwnj;وه&zwnj;، ئه&zwnj;ویش هه&zwnj;ڵی بژاردو ڕێنموویی کرد بۆ ڕێبازی ڕاست و دروست.';
$t[122] = 'ئێمه هه&zwnj;ر له دنیادا چاکه&zwnj;مان پێبه&zwnj;خشی و پاداشتی چاکه&zwnj;مان دایه&zwnj;وه (به به&zwnj;خشینی هاوسه&zwnj;ری پاك و نه&zwnj;وه&zwnj;ی خواناس) به&zwnj;ڕاستی ئه&zwnj;و له جیهانی داهاتوودا له ڕیزی چاکاندایه (که ڕێزدارو به&zwnj;خته&zwnj;وه&zwnj;رو کامه&zwnj;رانن).';
$t[123] = 'له&zwnj;وه&zwnj;ودوا (دوای چه&zwnj;نده&zwnj;ها سه&zwnj;ده&zwnj;) وه&zwnj;حی و نیگامان بۆ تۆ (ئه&zwnj;ی محمد صلی الله علیه وسلم) نارد که شوێنی ئاین و به&zwnj;رنامه&zwnj;ی ئیبراهیم بکه&zwnj;ویت که به دوور بووه له هه&zwnj;موو بیرو باوه&zwnj;ڕێکی چه&zwnj;وت، ئه&zwnj;و له ده&zwnj;سته&zwnj;ی موشریك و هاوه&zwnj;ڵگه&zwnj;ران نه&zwnj;بووه&zwnj;.';
$t[124] = 'به&zwnj;ڕاستی قه&zwnj;ده&zwnj;غه&zwnj;کردنی کاروباری دنیایی له ڕۆژانی شه&zwnj;موواندا ته&zwnj;نها له&zwnj;سه&zwnj;ر ئه&zwnj;وانه بڕیار درا که کێشه&zwnj;یان هه&zwnj;بوو تیایدا، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ فه&zwnj;رمانڕه&zwnj;وایی ده&zwnj;کات له نێوانیاندا له ڕۆژی قیامه&zwnj;تدا له&zwnj;سه&zwnj;ر ئه&zwnj;و شتانه&zwnj;ی که ئه&zwnj;وان کێشه&zwnj;و جیاوازیان تێدا ده&zwnj;کرد.';
$t[125] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) بانگه&zwnj;واز بکه بۆ لای به&zwnj;رنامه&zwnj;و ڕێبازی په&zwnj;روه&zwnj;ردگارت به&zwnj;حیکمه&zwnj;ت و دانایی (بۆ هه&zwnj;ندێك) و ئامۆژگاری جوان و به&zwnj;جێ، (بۆ هه&zwnj;ندێکی تر)و گفتو گۆو موجاده&zwnj;له به&zwnj;جوانترین شێوه (له&zwnj;گه&zwnj;ڵ هه&zwnj;ندێکی تردا به تایبه&zwnj;تی خاوه&zwnj;ن کتێبه&zwnj;کان)، چونکه به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت هه&zwnj;ر خۆی زانایه&zwnj;، که کێ گومڕایه&zwnj;، هه&zwnj;روه&zwnj;ها هه&zwnj;ر خۆیشی زانایه به هیدایه&zwnj;ت و وه&zwnj;رگران.';
$t[126] = 'ئه&zwnj;گه&zwnj;ر ویستتان تۆڵه بکه&zwnj;نه&zwnj;وه&zwnj;، ده&zwnj;بێت هه&zwnj;ر به&zwnj;قه&zwnj;ده&zwnj;ر ئه&zwnj;و سته&zwnj;مه&zwnj;ی که لێتان کراوه تۆڵه بکه&zwnj;نه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ئارام گرو خۆگر بن، ئه&zwnj;وه چاکتره بۆ ئارامگرو خۆگران.';
$t[127] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) خۆگرو ئارامگربه&zwnj;و خۆگری و ئارامگریه&zwnj;که&zwnj;شت ته&zwnj;نها به&zwnj;کۆمه&zwnj;کی و یارمه&zwnj;تی خوا ده&zwnj;بێت به&zwnj;که&zwnj;سی تر ناکرێت، خه&zwnj;فه&zwnj;تیش مه&zwnj;خۆ له خوانه&zwnj;ناسان که باوه&zwnj;ڕ ناهێنن و دڵته&zwnj;نگ مه&zwnj;به له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و پیلان و نه&zwnj;خشانه&zwnj;ی که دوژمنان ده&zwnj;یگێڕن.';
$t[128] = 'چونکه به&zwnj;ڕاستی خوا له&zwnj;گه&zwnj;ڵ ئه&zwnj;و که&zwnj;سانه&zwnj;دایه که خۆیان پاڕاستووه له گوناهو هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش که چاکه&zwnj;کارو چاکه&zwnj;خوازن.';
?>