<?php
$t = array();
$t[1] = 'پاکی و بێگه&zwnj;ردی و ستایش بۆ ئه&zwnj;و زاته&zwnj;ی که شه&zwnj;وڕه&zwnj;وی کرد له به&zwnj;شێکی که&zwnj;می شه&zwnj;ودا به&zwnj;به&zwnj;نده&zwnj;ی خۆی (محمد صلی الله علیه وسلم) له مزگه&zwnj;وتی (حرام، که&zwnj;عبه&zwnj;ی پیرۆزه&zwnj;وه&zwnj;) بۆ مزگه&zwnj;وتی (الاقصی) له (بیت المقدس) که ده&zwnj;وروبه&zwnj;ریمان پیرۆزو پڕ به&zwnj;ره&zwnj;که&zwnj;ت کردووه&zwnj;، تا هه&zwnj;ندێك نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;و ده&zwnj;سه&zwnj;ڵاتداری و توانایی خۆمانی نیشان بده&zwnj;ین، به&zwnj;ڕاستی ئه&zwnj;و زاته بیسه&zwnj;رو بینایه&zwnj;.';
$t[2] = 'ئێمه کتێبی (ته&zwnj;وراتمان) به&zwnj;خشی به موساو کردمانه هۆی هیدایه&zwnj;ت و ڕێنموویی بۆ نه&zwnj;وه&zwnj;ی ئیسرائیل، بۆ ئه&zwnj;وه&zwnj;ی که&zwnj;سی تر جگه له من نه&zwnj;که&zwnj;نه پشتیوان و یاریده&zwnj;ده&zwnj;ر بۆ خۆیان.';
$t[3] = '(ئه&zwnj;مانه&zwnj;و خه&zwnj;ڵکی تریش) نه&zwnj;وه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ن که (له که&zwnj;شتیه&zwnj;که&zwnj;دا) له&zwnj;گه&zwnj;ڵ نوح دا هه&zwnj;ڵمان گرتن (تا سوپاسگوزار بن و قه&zwnj;دری نازو نیعمه&zwnj;ت و به&zwnj;خششه&zwnj;کان بزانن)، چونکه به&zwnj;ڕاستی (نوح) به&zwnj;نده&zwnj;یه&zwnj;کی زۆر سوپاسگوزار بوو.';
$t[4] = 'له کتێبی ته&zwnj;وراتدا یان له لوح المحفوظ دا بڕیارمان داوه&zwnj;و پێمان ڕاگه&zwnj;یاندوون که سوێند به خوا ئێوه (له به&zwnj;رنامه&zwnj;ی ئێمه لاده&zwnj;ده&zwnj;ن و یاخی ده&zwnj;بن) و فه&zwnj;سادو خراپه دووجار له زه&zwnj;ویدا به&zwnj;رپا ده&zwnj;که&zwnj;ن، (له غه&zwnj;فڵه&zwnj;تی خه&zwnj;ڵکیدا) زۆر گه&zwnj;شه ده&zwnj;که&zwnj;ن و ده&zwnj;سه&zwnj;ڵاتێکی زۆر گه&zwnj;وره به&zwnj;ده&zwnj;ست ده&zwnj;هێنن (له&zwnj;م سه&zwnj;رده&zwnj;مه&zwnj;دا دووباره ئه&zwnj;م هه&zwnj;واڵه قورئانیه هاتۆته&zwnj;وه دی).';
$t[5] = 'جا کاتێك (ئه&zwnj;ی جووه&zwnj;کان) به&zwnj;ڵێنی یه&zwnj;که&zwnj;میان دێت (که تۆڵه&zwnj;یان لێ بکه&zwnj;ینه&zwnj;وه&zwnj;) به&zwnj;نده تایبه&zwnj;تیه&zwnj;کانی خۆمان ده&zwnj;نێرینه سه&zwnj;رتان که خاوه&zwnj;نی هێزو توانای زۆرن، ئیتر ئه&zwnj;وان به هه&zwnj;موو کون و که&zwnj;لێنێکی قه&zwnj;ڵاو شارۆچکه&zwnj;کاندا ده&zwnj;سوڕێنه&zwnj;وه&zwnj;و ده&zwnj;گه&zwnj;ڕێن به شوێنیانداو ئه&zwnj;م هه&zwnj;ڕه&zwnj;شه&zwnj;ی تۆڵه&zwnj;یه به&zwnj;ڵێنی خوایه&zwnj;و ده&zwnj;بێت بێته دی (زانایانی سه&zwnj;رده&zwnj;م ده&zwnj;ڵێن_عبادا لنا_ مه&zwnj;به&zwnj;ست هاوه&zwnj;ڵانی پێغه&zwnj;مبه&zwnj;ره که قورئان وه&zwnj;سفی کردوون به توندو تیژی دژ به خوا نه&zwnj;ناسان له کاتی جه&zwnj;نگدا).';
$t[6] = 'له دوای ئه&zwnj;و لاوازیه&zwnj;تان (ئه&zwnj;ی جووه&zwnj;کان، له غه&zwnj;فڵه&zwnj;ت و دواکه&zwnj;وتنی جیهانی ئیسلامدا) نۆره&zwnj;تان ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;و ده&zwnj;سه&zwnj;ڵاتمان به&zwnj;سه&zwnj;ر وڵاتی ئیامانداراندا پێده&zwnj;ده&zwnj;ین و ماڵ و سامان و کوڕی زۆرتان پێده&zwnj;به&zwnj;خشین و واتان لێده&zwnj;کردن که زۆر ئاماده بن بۆ هێرش بردن و په&zwnj;لاماردان و پڕو پاگه&zwnj;نده کردن.';
$t[7] = 'ئه&zwnj;گه&zwnj;ر (له کاتی ده&zwnj;سه&zwnj;ڵاتدا) چاکه بکه&zwnj;ن، ئه&zwnj;وه هه&zwnj;ر بۆ خۆتان چاك و به&zwnj;سووده&zwnj;، ئه&zwnj;گه&zwnj;ر خراپه&zwnj;و تاوانیش بکه&zwnj;ن ئه&zwnj;وه هه&zwnj;ر یه&zwnj;خه&zwnj;ی خۆتان ده&zwnj;گرێته&zwnj;وه&zwnj;، جا کاتێك که نۆره&zwnj;ی ئه&zwnj;نجامدانی به&zwnj;ڵێنی دواجار دێت، ئیمانداران کارێکتان پێ ده&zwnj;که&zwnj;ن که له ڕوخسارتاندا ڕه&zwnj;نگ بداته&zwnj;وه&zwnj;، (چونکه ئه&zwnj;وان دڵنیان که هه&zwnj;موو ئه&zwnj;و به&zwnj;ڵاو ناخۆشیانه&zwnj;ی به&zwnj;سه&zwnj;ر گه&zwnj;لانی موسوڵماندا به گشتی، هاتووه به&zwnj;شێوه&zwnj;یه&zwnj;کی ڕاسته&zwnj;وخۆ یان ناڕاسته&zwnj;وخۆ ده&zwnj;ستی ئه&zwnj;وانی تیادایه&zwnj;)، ئه&zwnj;وسا ده&zwnj;با ئیماندار بچنه مزگه&zwnj;وته&zwnj;وه وه&zwnj;کو یه&zwnj;که&zwnj;مجار چوونه ناوی، ده&zwnj;با ئیتر ئیمانداران تۆڵه&zwnj;ی خۆیان بکه&zwnj;نه&zwnj;وه&zwnj;و ده&zwnj;ستیان به&zwnj;سه&zwnj;ر هه&zwnj;ر چیه&zwnj;کدا ده&zwnj;ڕوات درێغی نه&zwnj;که&zwnj;ن (چونکه ئه&zwnj;وان له سته&zwnj;م و زۆرداری و خوێن ڕێژی درێغیان نه&zwnj;کرد).';
$t[8] = 'ئینجا دوور نی یه&zwnj;، په&zwnj;روه&zwnj;ردگارتان به&zwnj;زه&zwnj;یی پیاتاندا بێته&zwnj;وه&zwnj;و ڕه&zwnj;حمتان پێ بکات ئه&zwnj;گه&zwnj;ر به&zwnj;خۆتاندا بێنه&zwnj;وه&zwnj;و ته&zwnj;وبه بکه&zwnj;ن، خۆ ئه&zwnj;گه&zwnj;ر بگه&zwnj;ڕێنه&zwnj;وه بۆ پیلانگێڕان و خوێنڕێژی ئێمه&zwnj;ش ده&zwnj;ست ده&zwnj;که&zwnj;ینه&zwnj;وه به تۆڵه سه&zwnj;ندن و ته&zwnj;مێکردنتان، جگه له&zwnj;وه دۆزه&zwnj;خیش ده&zwnj;که&zwnj;ین به زیندان بۆ کافران که تیایدا گیر بخۆن.';
$t[9] = 'به&zwnj;ڕاستی ئه&zwnj;م قورئانه هیدایه&zwnj;ت به&zwnj;خشه بۆ چاکترین ڕێگه&zwnj;و ڕێبازو به&zwnj;رنامه&zwnj;، مژده&zwnj;یش ده&zwnj;دات به&zwnj;و ئیماندارانه&zwnj;ی که کارو کرده&zwnj;وه چاکه&zwnj;کان ده&zwnj;که&zwnj;ن که بێگومان پاداشتی زۆرو بێ سنوور چاوه&zwnj;ڕێیانه&zwnj;.';
$t[10] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕ به ڕۆژی دوایی ناهێنن، سزایه&zwnj;کی به ئێش و ئازارمان بۆ ئاماده کردوون.';
$t[11] = 'ئینسان (کاتێك ته&zwnj;نگه&zwnj;تاو ده&zwnj;بێت) دوعاو نزای شه&zwnj;ڕ ده&zwnj;کات، هه&zwnj;روه&zwnj;ك چۆن له کاتی ئاسایدا دوعای خێر ده&zwnj;کات، ئینسان هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام به په&zwnj;له&zwnj;و هه&zwnj;ڵه&zwnj;شه&zwnj;یه&zwnj;.';
$t[12] = 'ئێمه دیارده&zwnj;ی شه&zwnj;وو ڕۆژمان کردووه به دوو به&zwnj;ڵگه&zwnj;و نیشانه (له&zwnj;سه&zwnj;ر ده&zwnj;سه&zwnj;ڵاتی خۆمان و بۆ حه&zwnj;سانه&zwnj;وه و ئه&zwnj;نجامدانی کاروبارتان و ڕاگرتنی پله&zwnj;یه&zwnj;کی گه&zwnj;رمای له&zwnj;بار بۆ ژیانتان)، ئینجا ئێمه تاریکیمان به&zwnj;سه&zwnj;ر دیارده&zwnj;ی شه&zwnj;ودا هێناوه&zwnj;و دیارده&zwnj;ی ڕۆژیشمان به ئاشکراو دیارو بینراو سازاندووه&zwnj;، تا له نازو نیعمه&zwnj;تی په&zwnj;روه&zwnj;ردگارتان به&zwnj;هره&zwnj;وه&zwnj;ر ببن، تا ژماره&zwnj;ی ساڵه&zwnj;کان بزانن و حساب ڕاگرن (که زۆر پێویسته بۆ ڕێکخستنی کاروباری ژیان) ئێمه هه&zwnj;موو شتێکی پێویستی ئێوه&zwnj;مان به فراوانی و به&zwnj;ر بڵاوی ڕوون کردۆته&zwnj;وه&zwnj;.';
$t[13] = 'نامه&zwnj;ی کرده&zwnj;وه&zwnj;ی هه&zwnj;ر که&zwnj;سێکیشمان هه&zwnj;ڵواسیووه به گه&zwnj;ردنیداو له ڕۆژی دوایدا کارنامه&zwnj;ی کرده&zwnj;وه&zwnj;کانی بۆ ده&zwnj;رده&zwnj;هێنین و به کراوه&zwnj;یی ده&zwnj;خه&zwnj;ینه به&zwnj;رده&zwnj;ستی (ئاشکرایه که ئه&zwnj;و ده&zwnj;زگایانه&zwnj;ی کارو کرده&zwnj;وه&zwnj;ی ئێمه&zwnj;ی له&zwnj;سه&zwnj;ر تۆمار ده&zwnj;کرێت زۆر پێشکه&zwnj;وتوو تره له&zwnj;وه&zwnj;ی که ئاده&zwnj;میزاد تا ئێستا په&zwnj;ی پێ بردووه&zwnj;).';
$t[14] = 'ئه&zwnj;وسا پێی ده&zwnj;وترێت: ده&zwnj;ی نامه&zwnj;ی کرده&zwnj;وه&zwnj;کانت بخوێنه&zwnj;ره&zwnj;وه و ته&zwnj;ماشای بکه&zwnj;، ئه&zwnj;مڕۆ هه&zwnj;ر خۆت بڕیار بده&zwnj;و حساب بۆ خۆت بکه (شایسته&zwnj;ی چ جۆره پاداشتێکیت؟!)';
$t[15] = 'ئه&zwnj;وه&zwnj;ی هه&zwnj;وڵی دابێت ڕێبازی ڕاست و دروست بگرێت، ئه&zwnj;وه به&zwnj;ڕاستی به&zwnj;قازانجی خۆی ته&zwnj;واو ده&zwnj;بێت و هه&zwnj;ر خۆی به&zwnj;هره له هیدایه&zwnj;ته&zwnj;که&zwnj;ی وه&zwnj;رده&zwnj;گرێت، ئه&zwnj;وه&zwnj;ش گومڕا بووه&zwnj;و ڕێبازی سه&zwnj;رلێشێواوی گرتۆته&zwnj;به&zwnj;ر، ئه&zwnj;وه بێگومان هه&zwnj;ر خۆی زه&zwnj;ره&zwnj;ر ده&zwnj;کات و گومڕاییه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ری ده&zwnj;که&zwnj;وێت، که&zwnj;س گوناهی که&zwnj;س هه&zwnj;ڵناگرێت، که&zwnj;س به&zwnj;رپرسیار نیه له هه&zwnj;ڵه&zwnj;ی که&zwnj;سانی تر، ئێمه&zwnj;ش سزای که&zwnj;سمان نه&zwnj;داوه تا پیغه&zwnj;مبه&zwnj;رێکی بۆ ده&zwnj;نێرین.';
$t[16] = 'کاتێك بمانه&zwnj;وێت دانیشتوانی شارو شارۆچکه&zwnj;یه&zwnj;ك له&zwnj;ناو ببه&zwnj;ین، ئه&zwnj;وه سه&zwnj;ره&zwnj;تا فه&zwnj;رمانی گوێڕایه&zwnj;ڵی ده&zwnj;ده&zwnj;ین به ده&zwnj;وڵه&zwnj;مه&zwnj;نده&zwnj;کان (که گوێ به&zwnj;حه&zwnj;رام و حه&zwnj;ڵاڵ ناده&zwnj;ن، که چاکه&zwnj;و چاکه&zwnj;کاری بکه&zwnj;ن) که&zwnj;چی ئه&zwnj;وان یاخی ده&zwnj;بن و خراپه&zwnj;کاری ده&zwnj;که&zwnj;ن له شاره&zwnj;که&zwnj;دا، ئه&zwnj;وسا ئیتر شایسته&zwnj;ی ئه&zwnj;وه ده&zwnj;بن که بڕیاری ئێمه به&zwnj;سه&zwnj;ریاندا بسه&zwnj;پێنرێت، ئینجا ئێمه&zwnj;ش کاولی ده&zwnj;که&zwnj;ین به&zwnj;سه&zwnj;ر یه&zwnj;کداو داروبه&zwnj;ردی به&zwnj;سه&zwnj;ر یه&zwnj;که&zwnj;وه ناهێڵین.';
$t[17] = 'چه&zwnj;نده&zwnj;ها نه&zwnj;وه&zwnj;مان له دوای نوح له&zwnj;ناو بردووه&zwnj;، ئه&zwnj;وه&zwnj;نده&zwnj;ش به&zwnj;سه بۆ په&zwnj;روه&zwnj;ردگاری تۆ که ئاگادارو بینایه به گوناهو تاوانی به&zwnj;نده&zwnj;کانی، هیچ شتێکی لێ شاراوه نیه&zwnj;.';
$t[18] = 'ئه&zwnj;وه&zwnj;ی مه&zwnj;به&zwnj;ستی ته&zwnj;نها دنیابێت که به&zwnj;په&zwnj;له&zwnj;و به&zwnj;خێرایی تێده&zwnj;په&zwnj;ڕێت، ئه&zwnj;وه ئێمه (به ئاره&zwnj;زووی که&zwnj;س ناکه&zwnj;ین به&zwnj;ڵکو) هه&zwnj;رچیمان بوێت به زوویی، به&zwnj;ویستی خۆمان و به&zwnj;هه&zwnj;رکه&zwnj;س که بمانه&zwnj;وێت ده&zwnj;یبه&zwnj;خشین، پاشان (له&zwnj;و جیهان) ئه&zwnj;و جۆره که&zwnj;سانه (واته دنیا په&zwnj;رستان) ده&zwnj;خه&zwnj;ینه ناو دۆزه&zwnj;خه&zwnj;وه&zwnj;، به سه&zwnj;رزه&zwnj;نشت کراوی و ڕاونراویه&zwnj;وه (که به&zwnj;رده&zwnj;وام فریشته&zwnj;کان ئێش ده&zwnj;که&zwnj;ن به&zwnj;دڵیانداو نایه&zwnj;ڵن پشوو بده&zwnj;ن، هه&zwnj;ر ڕاویان ده&zwnj;نێن به&zwnj;ناو تونێل و شوێنه سامناکه&zwnj;کاندا).';
$t[19] = '(به&zwnj;ڵام) ئه&zwnj;وه&zwnj;ش مه&zwnj;به&zwnj;ستی به&zwnj;شی ئه&zwnj;و جیهان بێت و هه&zwnj;وڵ و کۆششی چاکی بۆ بدات، له هه&zwnj;مان کاتیشدا ئیماندار بێت، هه&zwnj;وڵ و کۆششی ئه&zwnj;و جۆره که&zwnj;سانه جێگه&zwnj;ی ڕێزو سوپاسه و سوپاسکراون.';
$t[20] = 'ئێمه هه&zwnj;ردوولا (دنیا په&zwnj;رستان و خوا په&zwnj;رستان) له به&zwnj;خششی په&zwnj;روه&zwnj;ردگارت به&zwnj;هره&zwnj;وه&zwnj;ر ده&zwnj;که&zwnj;ین، بێگومان به&zwnj;خششی په&zwnj;روه&zwnj;ردگارت سنووردارو قه&zwnj;ده&zwnj;غه کراو نیه له دنیادا.';
$t[21] = 'ته&zwnj;ماشا بکه&zwnj;و سه&zwnj;رنج بده چۆن ڕێزو پله&zwnj;و پایه&zwnj;ی هه&zwnj;ندێکیانمان داوه به&zwnj;سه&zwnj;ر هه&zwnj;ندێکی تریاندا، بێگومان پله&zwnj;و جیاوازیه&zwnj;کانی ئه&zwnj;و جیهان زۆر گه&zwnj;وره&zwnj;ترو زۆر زیاترن.';
$t[22] = '(ئه&zwnj;ی ئینسان) هه&zwnj;رگیز نه&zwnj;که&zwnj;یت خوایه&zwnj;کی تر بکه&zwnj;یته هاوتای خواو هاوه&zwnj;ڵی بۆ بڕیار بده&zwnj;یت چونکه به&zwnj;و کاره&zwnj;ت زه&zwnj;بوون و خه&zwnj;جاڵه&zwnj;ت ده&zwnj;بیت.';
$t[23] = 'په&zwnj;روه&zwnj;ردگاری تۆ بڕیاریداوه که جگه له&zwnj;و که&zwnj;سی تر نه&zwnj;په&zwnj;رستن، هه&zwnj;روه&zwnj;ها بڕیاریداوه چاکه&zwnj;کاریش بن له&zwnj;گه&zwnj;ڵ دایك و باوکتاندا، هه&zwnj;ر کاتێك یه&zwnj;کێکیان یان هه&zwnj;ردووکیان به پیری که&zwnj;وتنه لای تۆ (ته&zwnj;نانه&zwnj;ت) ئۆفیان له ده&zwnj;ست مه&zwnj;که&zwnj;و (هه&zwnj;رگیز) پیایاندا هه&zwnj;ڵمه&zwnj;شاخێ و هاواریان به&zwnj;سه&zwnj;ردا مه&zwnj;که&zwnj;، به&zwnj;رده&zwnj;وامیش قسه&zwnj;و گفتو گۆت له&zwnj;گه&zwnj;ڵیاندا با چاك و جوان و به&zwnj;جێ بێت.';
$t[24] = 'باڵی میهره&zwnj;بانی خۆتیان بۆ ڕاخه&zwnj;و ملکه&zwnj;چ به بۆیان، نزا بکه&zwnj;و بڵێ: په&zwnj;روه&zwnj;ردگارا ڕه&zwnj;حمیان پێ بکه&zwnj;و میهره&zwnj;بان به بۆیان، هه&zwnj;روه&zwnj;کو چۆن ئه&zwnj;وان ڕه&zwnj;حمیان به من کردوو و منیان په&zwnj;روه&zwnj;رده کردووه به بچووکی و منداڵی.';
$t[25] = 'په&zwnj;روه&zwnj;ردگارتان زۆر زاناتره له خۆتان به&zwnj;وه&zwnj;ی که له&zwnj;دڵ و ده&zwnj;روونتاندا هه&zwnj;یه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر چاك و پاك و ڕێکو پێك بن، ئه&zwnj;وه به&zwnj;ڕاستی ئیتر ئه&zwnj;و زاته بۆ ته&zwnj;وبه&zwnj;کاران و په&zwnj;شیمانان زۆر لێخۆشبوو به&zwnj;خشنده&zwnj;یه&zwnj;.';
$t[26] = 'مافی خزم و خوێشان به&zwnj;جێ بهێنه و په&zwnj;یوه&zwnj;ندیه&zwnj;کان (له چوار چێوه&zwnj;ی شه&zwnj;رعدا چاودێری بکه&zwnj;) هه&zwnj;روه&zwnj;ها مافی هه&zwnj;ژارانیش (به یارمه&zwnj;تیدانیان به گوێره&zwnj;ی توانا) ئه&zwnj;نجام بده هه&zwnj;روه&zwnj;ها مافی ڕێبواریش (به&zwnj;ده&zwnj;ستگیرۆیی و ڕێ نیشاندان) جێبه&zwnj;جێ بکه&zwnj;، ده&zwnj;ست بڵاویش مه&zwnj;به&zwnj;و ماڵ و سامانت به&zwnj;فیڕۆ مه&zwnj;ده&zwnj;.';
$t[27] = 'چونکه به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی ماڵ و سامانیان به&zwnj;فیڕۆ ده&zwnj;ده&zwnj;ن (له ڕێگه&zwnj;ی خراپدا سه&zwnj;رفی ده&zwnj;که&zwnj;ن)، ئه&zwnj;وانه هه&zwnj;میشه وه&zwnj;ك برای شه&zwnj;یتانه&zwnj;کان وان، شه&zwnj;یتانیش له به&zwnj;رامبه&zwnj;ر (نازو نیعمه&zwnj;ته&zwnj;کانی) په&zwnj;روه&zwnj;ردگاریه&zwnj;وه زۆر ناشوکرو به&zwnj;د نمه&zwnj;که&zwnj;.';
$t[28] = 'خۆ ئه&zwnj;گه&zwnj;ر نه&zwnj;تتوانی یارمه&zwnj;تی ناوبراوان بده&zwnj;یت و به&zwnj;ئومێدی ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگارو به&zwnj;خششی ئه&zwnj;و بوویت ئه&zwnj;وه ده&zwnj;کرێت به گوفتاری جوان و قسه&zwnj;ی خۆش ڕازییان بکه&zwnj;یت.';
$t[29] = 'نه&zwnj;که&zwnj;یت ئه&zwnj;وه&zwnj;نده ده&zwnj;ست نوقاو بیت وه&zwnj;ك ئه&zwnj;وه&zwnj;ی که ده&zwnj;ستی به&zwnj;کۆت و زنجیر به&zwnj;گه&zwnj;ردنیدا هه&zwnj;ڵواسرابێت، هه&zwnj;روه&zwnj;ها ئه&zwnj;وه&zwnj;نده&zwnj;ش ده&zwnj;ست بڵاو مه&zwnj;به (هیچیت به&zwnj;ده&zwnj;سته&zwnj;وه نه&zwnj;مێنێت) له&zwnj;وه&zwnj;ودوا بکه&zwnj;ویته به&zwnj;رده&zwnj;م لۆمه&zwnj;و سه&zwnj;زه&zwnj;نشتی ئه&zwnj;مو ئه&zwnj;وو هیچت به ده&zwnj;سته&zwnj;وه نه&zwnj;مێنێت و به هه&zwnj;ناسه ساردی و نائومێدی بۆی دانیشیت.';
$t[30] = 'به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارت به هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت ڕزق و ڕۆزی فراوان ده&zwnj;به&zwnj;خشێت، یاخود که&zwnj;م و سنوورداری ده&zwnj;کات، بێگومان ئه&zwnj;و زاته ئاگادارو بینایه به&zwnj;به&zwnj;نده&zwnj;کانی، (به&zwnj;خشین و نه&zwnj;به&zwnj;خشینی هه&zwnj;ڕه&zwnj;مه&zwnj;کی نیه&zwnj;).';
$t[31] = 'نه&zwnj;که&zwnj;ن منداڵه&zwnj;کانتان له ترسی گرانی و برسێتی بکوژن (چونکه په&zwnj;روه&zwnj;ردگارتان ڕۆزی به&zwnj;خش و میهره&zwnj;بانه&zwnj;)، ئێمه ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشین به&zwnj;وانیش و به ئێوه&zwnj;ش، چونکه به&zwnj;ڕاستی کوشتنی ئه&zwnj;وان گوناهو تاوانێکی زۆر گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[32] = 'هه&zwnj;رگیز نه&zwnj;که&zwnj;ن توخنی زینا بکه&zwnj;ون و لێی نزیك ببنه&zwnj;وه&zwnj;، چونکه ئه&zwnj;وه به&zwnj;ڕاستی گوناهو تاوان و هه&zwnj;ڵه و ڕێچکه&zwnj;یه&zwnj;کی زۆر ناقۆڵایه&zwnj;.';
$t[33] = 'که&zwnj;سێك نه&zwnj;کوژن که خوا کوشتنه&zwnj;که&zwnj;ی حه&zwnj;رام کردبێت مه&zwnj;گه&zwnj;ر به&zwnj;هۆی تاوانێکه&zwnj;وه شایسته بێت و کوشتنی ڕه&zwnj;وا بێت، ئه&zwnj;وه&zwnj;ش که مه&zwnj;زڵومانه&zwnj;و بێ تاوان ده&zwnj;کوژێت، ئه&zwnj;وه ئێمه به&zwnj;ڕاستی ماف و ده&zwnj;سه&zwnj;ڵاتمان داوه به&zwnj;نزیکترین که&zwnj;سی که تۆڵه&zwnj;ی بۆ بسێنێت، ئه&zwnj;ویش نابێت زیاده&zwnj;ڕه&zwnj;ویی بکات له کوشتن و تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;دا، (خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;خواترس و فه&zwnj;رمانبه&zwnj;ردار بوو له&zwnj;و کاره&zwnj;یدا) ئه&zwnj;وه بێگومان سه&zwnj;رکه&zwnj;وتوو و پشتیوانی لێکراوه له لایه&zwnj;ن ده&zwnj;سه&zwnj;ڵاتی ڕه&zwnj;واوه&zwnj;.';
$t[34] = 'نزیك ماڵی هه&zwnj;تیوو مه&zwnj;که&zwnj;ون مه&zwnj;گه&zwnj;ر به&zwnj;جوانترین شێوه نه&zwnj;بێت (وه&zwnj;ك ئه&zwnj;وه&zwnj;ی بیخه&zwnj;نه بازرگانیه&zwnj;که&zwnj;وه تا گه&zwnj;شه بکات) هه&zwnj;تا ئه&zwnj;گاته هه&zwnj;ڕه&zwnj;تی لاوی، وه&zwnj;فا بکه&zwnj;ن به به&zwnj;ڵێن و په&zwnj;یمان، چونکه به&zwnj;ڕاستی به&zwnj;رپرسیارن له به&zwnj;رامبه&zwnj;ر به&zwnj;ڵێن و په&zwnj;یمانه&zwnj;وه&zwnj;.';
$t[35] = 'هه&zwnj;ر کاتێك کێشان و پێوانتان ئه&zwnj;نجامدا به&zwnj;ڕێك و پێكی و تێرو ته&zwnj;واوی ئه&zwnj;نجامی بده&zwnj;ن، ئه&zwnj;و شێوه&zwnj;یه چاکتره&zwnj;و جوانترین ئه&zwnj;نجامی هه&zwnj;یه (له دنیادا ئاسووده ده&zwnj;بن و پاداشتی قیامه&zwnj;تیش به&zwnj;ده&zwnj;ست ده&zwnj;هێنن).';
$t[36] = '(ئه&zwnj;ی ئینسان) له شتێك مه&zwnj;دوێ و شوێنی شتێك مه&zwnj;که&zwnj;وه که زانست و زانیاریت ده&zwnj;رباره&zwnj;ی نیه&zwnj;، چونکه به&zwnj;ڕاستی ده&zwnj;زگاکانی بیستن و بینین و تێگه&zwnj;یشتن هه&zwnj;ر هه&zwnj;مووی، به&zwnj;ر پرسیاره له به&zwnj;رامبه&zwnj;ریه&zwnj;وه&zwnj;.';
$t[37] = 'هه&zwnj;روه&zwnj;ها به&zwnj;سه&zwnj;ر زه&zwnj;ویدا به&zwnj;که&zwnj;ش و فش و له خۆبایی بوونه&zwnj;وه مه&zwnj;ڕۆ، چونکه تۆ هه&zwnj;رگیز ناتوانیت زه&zwnj;وی کوناوده&zwnj;ر بکه&zwnj;یت، ناشگه&zwnj;یته به&zwnj;رزی چیاکان (ئیتر ئه&zwnj;م که&zwnj;شخه&zwnj;و شانازیه&zwnj;ت له&zwnj;چی یه&zwnj;؟).';
$t[38] = 'هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که له ئایه&zwnj;ته&zwnj;کانی پێشوودا باسمان کرد، خراپه&zwnj;کانی لای په&zwnj;روه&zwnj;ردگارت ناپه&zwnj;سه&zwnj;ندو نابه&zwnj;جێیه (هه&zwnj;وڵ بده هه&zwnj;میشه دووره په&zwnj;رێزبیت لێیان).';
$t[39] = 'ئه&zwnj;و ڕێنموییانه&zwnj;ی که په&zwnj;روه&zwnj;ردگارت به وه&zwnj;حی بۆی ڕه&zwnj;وانه کردیت به&zwnj;شێکن له حیکمه&zwnj;ت و دانایی، نه&zwnj;که&zwnj;یت خوایه&zwnj;کی تر به&zwnj;هاوتای خوای باڵاده&zwnj;ست بزانیت، چونکه ئه&zwnj;گه&zwnj;ر وابیت، فڕێ ده&zwnj;درێیته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه به سه&zwnj;رزه&zwnj;نشت کراوی و ڕیسوایی و شه&zwnj;رمه&zwnj;زاری و دوور خراوه&zwnj;ی له به&zwnj;زه&zwnj;یی خوایی.';
$t[40] = 'ئینجا ڕوو به&zwnj;هاوه&zwnj;ڵگه&zwnj;ران ده&zwnj;فه&zwnj;رموێت: ئایا ڕه&zwnj;وایه خوا ئێوه&zwnj;ی هه&zwnj;ڵبژاردبێت تا کوڕتان پێ ببه&zwnj;خشێت و خۆی فریشته&zwnj;کانی هه&zwnj;ڵبژاردبێت تا بیانکات به&zwnj;کچی خۆی؟ (ئه&zwnj;م بیرو باوه&zwnj;ڕه پووچ و بێ سه&zwnj;روبنانه که&zwnj;ی ڕه&zwnj;وایه&zwnj;، مه&zwnj;گه&zwnj;ر کوڕو کچ هه&zwnj;ر خوا نایانبه&zwnj;خشێت؟)، به&zwnj;ڕاستی ئێوه قسه&zwnj;و گوفتارێکی نابه&zwnj;جێ و گه&zwnj;وره و ناقۆڵا ده&zwnj;که&zwnj;ن.';
$t[41] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه له&zwnj;م قورئانه&zwnj;دا به چه&zwnj;نده&zwnj;ها شێوه له هه&zwnj;موو مه&zwnj;به&zwnj;سته&zwnj;کان دواوین تا بیربکه&zwnj;نه&zwnj;وه&zwnj;و یاداوه&zwnj;ری وه&zwnj;ربگرن، که&zwnj;چی قورئان بۆ خوانه&zwnj;ناسان ته&zwnj;نها دووره په&zwnj;رێزی نه&zwnj;بێت هیچی تریان بۆ زیاد ناکات.';
$t[42] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به&zwnj;و موشریکانه&zwnj;) بڵێ: ئه&zwnj;گه&zwnj;ر خوای تریش هه&zwnj;بوایه له&zwnj;گه&zwnj;ڵ خوای په&zwnj;روه&zwnj;ردگاردا هه&zwnj;روه&zwnj;کو ئه&zwnj;وان ده&zwnj;ڵێن.. ئه&zwnj;و کاته هه&zwnj;موان هه&zwnj;وڵیانده&zwnj;دا بۆ ئه&zwnj;وه&zwnj;ی ده&zwnj;سه&zwnj;ڵات له خاوه&zwnj;نی عه&zwnj;رش و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی بسه&zwnj;نن و لێی زه&zwnj;وت بکه&zwnj;ن.';
$t[43] = 'ستایش و پاکی و بڵندی بۆ ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;یه (که ئه&zwnj;و نه&zwnj;فامانه ده&zwnj;رباره&zwnj;ی ئه&zwnj;و هه&zwnj;موو ناڕه&zwnj;واییه ده&zwnj;ڵێن) و ئه&zwnj;و زاته زۆر به&zwnj;رزو بڵندو گه&zwnj;وره&zwnj;تره (له بۆچوونی ئه&zwnj;وان).';
$t[44] = 'هه&zwnj;ر حه&zwnj;وت ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی که تیایاندا هه&zwnj;ن، ته&zwnj;سبیحات و ستایشی ئه&zwnj;و زاته ده&zwnj;که&zwnj;ن، هیچ شتێك نی یه له&zwnj;م بوونه&zwnj;وه&zwnj;ره&zwnj;دا که ته&zwnj;سبیحات و ستایش و سوپاسی ئه&zwnj;و زاته نه&zwnj;کات، به&zwnj;ڵام ئێوه له ته&zwnj;سبیحات و ستایشی ئه&zwnj;و شتانه تێناگه&zwnj;ن، به&zwnj;ڕاستی ئه&zwnj;و زاته هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خۆگرو به حه&zwnj;وسه&zwnj;ڵه&zwnj;یه (له به&zwnj;رامبه&zwnj;ر لادانی خه&zwnj;ڵکیه&zwnj;وه&zwnj;) لێخۆشبووه&zwnj;و چاوپۆشی ده&zwnj;کات (ئه&zwnj;گه&zwnj;ر ڕووی تێبکه&zwnj;ن و باوه&zwnj;ڕی پێ بهێنن).';
$t[45] = 'کاتێك که قورئان ده&zwnj;خوێنیت ئێمه به&zwnj;ربه&zwnj;ستێکی نادیارو شاراوه&zwnj;، ده&zwnj;خه&zwnj;ینه نێوان تۆو ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ به ڕۆژی دوایی ناهێنن.. (چونکه سوودی لێ وه&zwnj;رناگرن)';
$t[46] = '(چونکه مه&zwnj;به&zwnj;ستیان نیه تێبگه&zwnj;ن) ئێمه&zwnj;ش په&zwnj;رده ده&zwnj;هێنین به&zwnj;سه&zwnj;ر دڵیاندا تا هه&zwnj;رتێی نه&zwnj;گه&zwnj;ن، گوێشیان سه&zwnj;نگین ده&zwnj;که&zwnj;ین تا نه&zwnj;یبیستن، جا کاتێك له قورئاندا ته&zwnj;نها ناوی په&zwnj;روه&zwnj;ردگارت ده&zwnj;به&zwnj;یت (باسی بت و به&zwnj;رنامه&zwnj;یان ناکه&zwnj;یت)، ئه&zwnj;وه پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و ڕوو وه&zwnj;رده&zwnj;گێڕن و بێزاری ده&zwnj;رده&zwnj;بڕن و ده&zwnj;ڕۆن.';
$t[47] = 'ئێمه له هه&zwnj;موو که&zwnj;س چاکتر ده&zwnj;زانین که خوانه&zwnj;ناسان بۆ گوێت بۆ ده&zwnj;گرن، ئه&zwnj;وانه کاتێك گوێت لێ ده&zwnj;گرن، چپه چپ و سرته سرت ده&zwnj;که&zwnj;ن، ئه&zwnj;وسا سته&zwnj;مکاران به&zwnj;یه&zwnj;کتر ده&zwnj;ڵێن: ئێوه شوێن که&zwnj;س ناکه&zwnj;ون ته&zwnj;نها پیاوێکی جادوو لێکراو نه&zwnj;بێت!!';
$t[48] = 'سه&zwnj;رنج بده چۆن تۆ ده&zwnj;که&zwnj;ن به نموونه (تاوێك ده&zwnj;ڵێن: جادووگه&zwnj;ر، ده&zwnj;مێك ده&zwnj;ڵێن: شاعیر.... هتد) سه&zwnj;رئه&zwnj;نجام خۆیان گومڕا کردووه&zwnj;و ناتوانن ڕێگه&zwnj;و ڕێبازێکی چاك و دروست بگرنه&zwnj;به&zwnj;ر.';
$t[49] = 'ئه&zwnj;وانه به سه&zwnj;رسامیه&zwnj;وه ده&zwnj;ڵێن: باشه ئه&zwnj;گه&zwnj;ر بووینه ئێسکی ڕزیوو پرتوکاو، ئه&zwnj;وه سه&zwnj;ر له نوێ زیندوو ده&zwnj;کرێینه&zwnj;&zwnj;وه&zwnj;؟!';
$t[50] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: ببنه به&zwnj;رد یان ئاسن...';
$t[51] = 'یان هه&zwnj;ر شتێك و هه&zwnj;ر دروستکراوێك که زۆر سه&zwnj;خت و گرنگ و گه&zwnj;وره&zwnj;یه له بیرو هۆشتاندا، ئه&zwnj;وسا ئه&zwnj;وانه (به سه&zwnj;رسامیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: ده&zwnj;ی باشه&zwnj;، کێ زیندوومان ده&zwnj;کاته&zwnj;وه&zwnj;؟ بڵێ: ئه&zwnj;و زاته&zwnj;ی که یه&zwnj;که&zwnj;مجار به&zwnj;دیهێناون (زۆر لای ئاسانه زیندووتان بکاته&zwnj;وه&zwnj;)، ئه&zwnj;وسا سه&zwnj;ریان شۆڕده&zwnj;که&zwnj;نه&zwnj;وه بۆ لات و سه&zwnj;ر باده&zwnj;ده&zwnj;ن و ده&zwnj;ڵێن: ئه&zwnj;وه که&zwnj;ی زیندوو ده&zwnj;کرێینه&zwnj;وه&zwnj;؟ که&zwnj;ی قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت؟ پێیان بڵێ: دوور نیه&zwnj;و له&zwnj;وانه&zwnj;یه به&zwnj;م نزیکانه پێش بێت.';
$t[52] = 'ڕۆژێك دێت په&zwnj;روه&zwnj;ردگارتان بانگتان لێ ده&zwnj;کات، به&zwnj;ده&zwnj;م سوپاس و ستایشیه&zwnj;وه (زیندوو ده&zwnj;بنه&zwnj;وه&zwnj;و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارن) واگومان ده&zwnj;به&zwnj;ن که ماوه&zwnj;یه&zwnj;کی که&zwnj;م نه&zwnj;بێت له دنیادا نه&zwnj;ژیاون (یاخود دوای مردنتان ماوه&zwnj;یه&zwnj;کی زۆر که&zwnj;م له گۆڕدا بوون).';
$t[53] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به&zwnj;به&zwnj;نده&zwnj;کانم بڵێ: چاکترین گوفتار بڵێن (جوانترین گوفتار بنووسن) چونکه شه&zwnj;یتان به&zwnj;ڕاستی ده&zwnj;یه&zwnj;وێت نێوانیان تێك بدات (هه&zwnj;میشه هانیان ده&zwnj;دات بۆ وتنی گوفتاری هه&zwnj;ڵه&zwnj;و خراپ)، به&zwnj;ڕاستی شه&zwnj;یتان دوژمنی ئاشکرای ئینسانه&zwnj;و (ده&zwnj;بێت وریابێت ئه&zwnj;و دوژمنه تووشی هه&zwnj;ڵه&zwnj;ی نه&zwnj;کات).';
$t[54] = 'په&zwnj;روه&zwnj;ردگارتان چاکتان ده&zwnj;ناسێت خۆ ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت ڕه&zwnj;حمتان پێ ده&zwnj;کات (کاتێك ڕێبازی چاکه ده&zwnj;گرنه به&zwnj;ر)، یان ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت سزاتان ده&zwnj;دات (کاتێك ڕێبازی خراپه ده&zwnj;گرنه به&zwnj;ر)و ئێمه تۆمان بۆ ئه&zwnj;وه ڕه&zwnj;وانه نه&zwnj;کردووه که چاودێرو پارێزه&zwnj;ری ئه&zwnj;وان بیت (تا ئه&zwnj;وان به ناچاریی باوه&zwnj;ڕ بهێنن).';
$t[55] = 'په&zwnj;روه&zwnj;ردگارت چاك ده&zwnj;زانێت به&zwnj;وه&zwnj;ی له ئاسمانه&zwnj;کان و زه&zwnj;ویدایه (هه&zwnj;ریه&zwnj;که&zwnj;یان سه&zwnj;رگه&zwnj;رمی چین)، بێگومان ئێمه ڕێزی هه&zwnj;ندێ له پێغه&zwnj;مبه&zwnj;رانمان له&zwnj;لا زیاتره له هه&zwnj;ندێکی تریان و (کتێبی) زه&zwnj;بووریشمان به&zwnj;_داوود_به&zwnj;خشیووه&zwnj;.';
$t[56] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به هاوه&zwnj;ڵگه&zwnj;ران بڵێ: ئاده&zwnj;ی هاناو هاوار بکه&zwnj;ن له&zwnj;و بت و شتانه&zwnj;ی که له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;رستن (ئایا هیچیان له ده&zwnj;ست دێت؟ دڵنیابن که ئه&zwnj;وانه&zwnj;) نه ده&zwnj;توانن به&zwnj;ڵاو ناخۆشیه&zwnj;کتان له&zwnj;سه&zwnj;ر لابه&zwnj;رن. نه&zwnj;ده&zwnj;توانن بارودۆخ بگۆڕن.';
$t[57] = 'ئا ئه&zwnj;وانه&zwnj;ی که ئه&zwnj;وان هاناو هاواریان لێ ده&zwnj;که&zwnj;ن، کامیان له خوا نزیکتره&zwnj;؟! له کاتێکدا که ئه&zwnj;وان خۆیان هاوار له خوا ده&zwnj;که&zwnj;ن و داوای نزیك بوونه&zwnj;وه له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;خوازن و چاوه&zwnj;ڕوانی ڕه&zwnj;حمه&zwnj;تی ده&zwnj;که&zwnj;ن، له سزاو تۆڵه&zwnj;شی ده&zwnj;ترسن، به&zwnj;ڕاستی سزای په&zwnj;روه&zwnj;ردگارت مه&zwnj;ترسی لێکراوه&zwnj;.';
$t[58] = 'هیچ شارو شارۆچکه&zwnj;یه&zwnj;ك نی یه که ئێمه بڕیارمان دابێت پێش به&zwnj;رپابوونی ڕۆژی قیامه&zwnj;ت له ناوی به&zwnj;رین، یان سزایه&zwnj;کی سه&zwnj;ختی بده&zwnj;ین، له کتێب و دۆسیه&zwnj;ی (تایبه&zwnj;تدا، له لوح المحفوظ دا) تۆمار نه&zwnj;کرا بێت.';
$t[59] = 'هیچ شتێك نه&zwnj;بووه&zwnj;، به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که ئێمه موعجیزه به&zwnj;رپا بکه&zwnj;ین و نیشانی بده&zwnj;ین له&zwnj;وه&zwnj;ی که ئه&zwnj;وان داوایان کردووه&zwnj;، ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت که (جۆره&zwnj;ها موعجیزه&zwnj;مان نیشانی پێشینه&zwnj;کان دا)، به&zwnj;ڵام ئه&zwnj;وان هه&zwnj;ر بڕوایان پێ نه&zwnj;کرد؛ ئێمه&zwnj;ش له ناومان بردن، حوشتره تایبه&zwnj;تیه&zwnj;که&zwnj;شمان به ئاشکراو ڕوون به قه&zwnj;ومی (ثمود) به&zwnj;خشی، که&zwnj;چی سته&zwnj;میان لێکردو (سه&zwnj;ریان بڕی)، به&zwnj;ڕاستی ئێمه ته&zwnj;نها بۆ ترس و داچڵه&zwnj;کاندن نه&zwnj;بێت، هیچ جۆره موعجیزه&zwnj;یه&zwnj;ك نانێرین.';
$t[60] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم یادی ئه&zwnj;وه بکه کاتێك پێمان وتی: که به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ ده&zwnj;وری خه&zwnj;ڵکی داوه&zwnj;و ئاگاداره به&zwnj;هه&zwnj;موو شتێکیان و ئه&zwnj;و شتانه&zwnj;ش که له خه&zwnj;وندا نیشانمان دایت ته&zwnj;نها تاقیکردنه&zwnj;وه بوو بۆ خه&zwnj;ڵکی، هه&zwnj;روه&zwnj;ها به&zwnj;باسی دره&zwnj;خته نه&zwnj;فرین لێ کراوه&zwnj;که&zwnj;ش که له&zwnj;قورئاندا ناو بڕاوه تاقیانده&zwnj;که&zwnj;ینه&zwnj;وه (کافره&zwnj;کان کردیانه گاڵته&zwnj;جاڕو وتیان: چۆن دره&zwnj;خت له&zwnj;ناو ئاگردا ده&zwnj;ڕوێت، عه&zwnj;قڵیان کول بوو له&zwnj;ئاستی توانای بێ سنووری خواییدا)، به&zwnj;مه&zwnj;رجێك ئێمه ده&zwnj;مانه&zwnj;وێت به&zwnj;م باسانه بیانترسێنین، دایانبچه&zwnj;ڵه&zwnj;کێنین، که&zwnj;چی ئه&zwnj;و باسانه هیچیان بۆ زیاد ناکات ته&zwnj;نها لادان و یاخی بوونێکی گه&zwnj;وره و بێ سنوور نه&zwnj;بێت.';
$t[61] = '(بیریان بهێنه&zwnj;ره&zwnj;وه&zwnj;) کاتێك به فریشته&zwnj;کانمان وت: ئاده&zwnj;ی سوژده به&zwnj;رن بۆ ئاده&zwnj;م، هه&zwnj;موو سوژده&zwnj;یان برد جگه له (ئیبلیس)، که وتی: ئایا چۆن سوژده ده&zwnj;به&zwnj;م بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که له قوڕ دروستت کردووه&zwnj;؟!';
$t[62] = '(پاشان بێ شه&zwnj;رمانه&zwnj;) وتی: پێم بڵێ؛ ئا ئه&zwnj;مه&zwnj;یه که ڕێزت داوه به&zwnj;سه&zwnj;ر مندا؟! سوێند به خوا ئه&zwnj;گه&zwnj;ر دوام بخه&zwnj;یت تا ڕۆژی قیامه&zwnj;ت ئه&zwnj;وه بێگومان هه&zwnj;موو نه&zwnj;وه&zwnj;کانی گومڕاو سه&zwnj;رگه&zwnj;ردان ده&zwnj;که&zwnj;م جگه له که&zwnj;مێکیان (که ده&zwnj;سه&zwnj;ڵاتم به&zwnj;سه&zwnj;ریاندا نیه&zwnj;).';
$t[63] = 'خوای په&zwnj;روه&zwnj;ردگار فه&zwnj;رمووی: بڕۆ ئه&zwnj;وه&zwnj;ی شوێنی تۆ بکه&zwnj;وێت له&zwnj;وان، ئه&zwnj;وه بێگومان دۆزه&zwnj;خ پاداشتی هه&zwnj;موانتانه&zwnj;، که پاداشتێکی مسۆگه&zwnj;ره&zwnj;و جێگه&zwnj;ی هه&zwnj;مووتانی تێدا ده&zwnj;بێته&zwnj;وه&zwnj;...';
$t[64] = 'بڕۆ چیت له ده&zwnj;ست دێت درێغی مه&zwnj;که&zwnj;، هه&zwnj;رکه&zwnj;س که توانیت (له&zwnj;و به&zwnj;دبه&zwnj;ختانه&zwnj;) به بانگ کردن و هاندانیان بۆ تاوانکاری، دایانبڕه&zwnj;، به&zwnj;هۆی له&zwnj;شکری سواره&zwnj;و پیاده&zwnj;ی خۆته&zwnj;وه هێرشیان بۆ به&zwnj;ره&zwnj;و هاوبه&zwnj;شییان بکه له ماڵ و سامان و منداڵ و نه&zwnj;وه&zwnj;یاندا (به&zwnj;سامانی حه&zwnj;رام و منداڵی زۆڵ،.. هتد)، ئه&zwnj;وه&zwnj;نده&zwnj;ش به&zwnj;ڵێن و ئومێدیان پێ بده&zwnj;، سه&zwnj;ریان لێ بشێوێنه&zwnj;، هه&zwnj;رچه&zwnj;نده به&zwnj;ڵێن و هاندانی شه&zwnj;یتان جگه له فریودان و فێڵکردن و ساخته&zwnj;، شتێکی تر نیه&zwnj;.';
$t[65] = 'به&zwnj;ڕاستی به&zwnj;نده&zwnj;کانی من (ئه&zwnj;ی شه&zwnj;یتانی نه&zwnj;فرین لێکراو) هیچ ده&zwnj;سه&zwnj;ڵاتی تۆیان له&zwnj;سه&zwnj;ر نابێت و هه&zwnj;وڵ و کۆششی تۆ کاریان تێ ناکات و په&zwnj;روه&zwnj;ردگاری تۆ (ئه&zwnj;ی ئیماندار) به&zwnj;سه بۆ پارێزگاری و پاڕاستنت.';
$t[66] = 'په&zwnj;روه&zwnj;ردگارتان ئه&zwnj;و زاته&zwnj;یه که که&zwnj;شتی به ده&zwnj;ریادا هاتوچۆ پێ ده&zwnj;کات بۆتان، تا له فه&zwnj;زڵ و به&zwnj;خششی (بێشوماری) به&zwnj;هره&zwnj;وه&zwnj;ر ببن، به&zwnj;ڕاستی ئه&zwnj;و زاته هه&zwnj;میشه و به&zwnj;رده&zwnj;وام به&zwnj;ڕه&zwnj;حم و میهره&zwnj;بانه بۆتان.';
$t[67] = '(خه&zwnj;ڵکینه ئێوه&zwnj;) کاتێك له ده&zwnj;ریادا ناخۆشی و ته&zwnj;نگانه تووشتان ده&zwnj;بێت، ئه&zwnj;وه هه&zwnj;رچی په&zwnj;رستراو هه&zwnj;یه له بیرو هۆش و باوه&zwnj;ڕتاندا نامێنێت و ون ده&zwnj;بێت جگه له&zwnj;و زاته (هاناو هاوار هه&zwnj;ر بۆ زاتی خوا ده&zwnj;به&zwnj;ن تا ڕزگارتان بکات) به&zwnj;ڵام کاتێك (فریاتان ده&zwnj;که&zwnj;وێت) و ڕزگارتان ده&zwnj;کات و ده&zwnj;تانخاته وشکانیه&zwnj;وه&zwnj;، ئێوه پشت له به&zwnj;رنامه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن و ڕوو وه&zwnj;رده&zwnj;گێڕن و نایناسن، به&zwnj;ڵکو زۆربه&zwnj;ی ئینسانه&zwnj;کان ناشوکرو سپڵه&zwnj;ن.';
$t[68] = 'خه&zwnj;ڵکینه ئایا ئێوه له خۆتان ئه&zwnj;مینن خوا نه&zwnj;تانبات به&zwnj;ناخی زه&zwnj;ویداو لایه&zwnj;کی وشکانی نه&zwnj;ڕوخێنێت به&zwnj;سه&zwnj;رتاندا، یاخود به&zwnj;رد بارانتان نه&zwnj;کات؟! له&zwnj;وه&zwnj;ودوا هیچ که&zwnj;ستان ده&zwnj;ست ناکه&zwnj;وێت فریاتان بکه&zwnj;وێت و په&zwnj;ناتان بدات!!؟';
$t[69] = 'ئایا دڵنیان و ناترسن له&zwnj;وه&zwnj;ی که جارێکی تر له که&zwnj;شتیدا ڕێتان بکه&zwnj;وێته ناو ده&zwnj;ریاوه&zwnj;، ئه&zwnj;وسا بایه&zwnj;کی به&zwnj;هێزی سه&zwnj;خت هه&zwnj;ڵکاته سه&zwnj;رتان، ئینجا به هۆی کافر بوون و یاخی بوونتانه&zwnj;وه نوقمی ده&zwnj;ریاتان بکات؟! له&zwnj;وه&zwnj;ودوا که&zwnj;سیشتان ده&zwnj;ست نه&zwnj;که&zwnj;وێت بتوانێت داکۆکیتان لێ بکات لای ئێمه&zwnj;!!';
$t[70] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه ڕێزمان له نه&zwnj;وه&zwnj;ی ئاده&zwnj;م گرتووه&zwnj;و به&zwnj;سه&zwnj;ر وشکانی و ده&zwnj;ریادا (به جۆره&zwnj;ها هۆکاری گواستنه&zwnj;وه&zwnj;) هه&zwnj;ڵمان گرتوون و له ڕزق و ڕۆزی پوخت و چاکی جۆراو جۆر به&zwnj;هره&zwnj;وه&zwnj;رمان کردوون (له خۆراك و پۆشاك و ماڵ و ناوماڵ و.. هتد) به&zwnj;ڕاستی ئێمه ڕێزی زیاده&zwnj;ی ئه&zwnj;وانمان داوه به&zwnj;سه&zwnj;ر زۆربه&zwnj;ی ئه&zwnj;و به&zwnj;دی هێنراوانه&zwnj;دا که دروستمان کردوون (له هه&zwnj;موو ڕوویه&zwnj;که&zwnj;وه&zwnj;، تا قه&zwnj;درو ڕێزی خۆیان بزانن).';
$t[71] = 'یادی ئه&zwnj;و ڕۆژه بکه که هه&zwnj;موو کۆمه&zwnj;ڵه خه&zwnj;ڵکێك به&zwnj;ناوی پێشه&zwnj;واکه&zwnj;یانه&zwnj;وه بانگ ده&zwnj;که&zwnj;ین، جا ئه&zwnj;وه&zwnj;ی نامه&zwnj;و دۆسیه&zwnj;ی کرده&zwnj;وه&zwnj;کانی بدرێته ده&zwnj;ستی ڕاستی، ئه&zwnj;وانه (به&zwnj;خۆشیه&zwnj;وه&zwnj;) سه&zwnj;یری نامه&zwnj;ی کرده&zwnj;وه&zwnj;کانی خۆیان ده&zwnj;که&zwnj;ن و ده&zwnj;یخوێننه&zwnj;وه&zwnj;و به&zwnj;قه&zwnj;ده&zwnj;ر تاڵه باریکه&zwnj;که&zwnj;ی ناوکی خورما سته&zwnj;میان لێ ناکرێت (که شتێکی بێ نرخه&zwnj;).';
$t[72] = 'جا ئه&zwnj;وه&zwnj;ی له&zwnj;م دنیایه&zwnj;دا (دڵ و ده&zwnj;روون و بیرو هۆشی) کوێر بێت، ئه&zwnj;وه له قیامه&zwnj;تدا چاوی کوێره&zwnj;و زۆر سه&zwnj;رگه&zwnj;ردانترو سه&zwnj;رلێشێواوتره&zwnj;، چونکه چاوی سه&zwnj;ری بۆ بینایی و تێفکران به&zwnj;کار نه&zwnj;هێناوه له دنیادا.';
$t[73] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم موشریك و هاوه&zwnj;ڵگه&zwnj;ران خه&zwnj;ریك بوو تۆ لابده&zwnj;ن له&zwnj;وه&zwnj;ی که به&zwnj;وه&zwnj;حی و نیگا بۆمان ڕه&zwnj;وانه کردوویت، تاشتێکی ترمان بۆ هه&zwnj;ڵببه&zwnj;ستیت و شێوازی کارو بانگه&zwnj;وازت بگۆڕیت، ئیتر ئه&zwnj;و کاته ئه&zwnj;گه&zwnj;ر به&zwnj;قسه&zwnj;ی ئه&zwnj;وان بکه&zwnj;یت ئه&zwnj;وان تۆ ده&zwnj;که&zwnj;نه هاوه&zwnj;ڵ و خۆشه&zwnj;ویستی خۆیان (چونکه به&zwnj;دڵی ئه&zwnj;وانت کردووه&zwnj;)!!.';
$t[74] = 'خۆ ئه&zwnj;گه&zwnj;ر ئێمه تۆمان پایه&zwnj;دارو دامه&zwnj;زراو نه&zwnj;کردایه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی نزیك بوویت (به&zwnj;مه&zwnj;زه&zwnj;نده&zwnj;ی خۆت، بۆ به&zwnj;رژه&zwnj;وه&zwnj;ندیی بانگه&zwnj;وازه&zwnj;که&zwnj;ت) که&zwnj;مێك به لایاندا مه&zwnj;یل بکه&zwnj;یت و به&zwnj;دڵی ئه&zwnj;وان بکه&zwnj;یت!!';
$t[75] = 'ئه&zwnj;و کاته ئیتر دوو به&zwnj;رامبه&zwnj;ری (هاوه&zwnj;ڵگه&zwnj;ران) له دنیاداو به دوو به&zwnj;رامبه&zwnj;ری ئه&zwnj;وان له دوای مردن سزاو ئازارمان پێ ده&zwnj;چه&zwnj;شتیت، پاشان که&zwnj;سیشت ده&zwnj;ست نه&zwnj;ده&zwnj;که&zwnj;وت فریات بکه&zwnj;وێت و له&zwnj;ده&zwnj;ستی ئێمه ڕزگارت بکات.';
$t[76] = 'ئه&zwnj;و موشریکانه خه&zwnj;ریکبوو زۆرت بۆ بهێنن و ئازارو ناخۆشیت بۆ پێش بهێنن تا له&zwnj;سه&zwnj;ر زه&zwnj;وی (مه&zwnj;ککه&zwnj;) ده&zwnj;رت په&zwnj;ڕێنن و ده&zwnj;ربه&zwnj;ده&zwnj;رت بکه&zwnj;ن، ئه&zwnj;و کاته ئیتر نه&zwnj;ده&zwnj;مانه&zwnj;وه دوای تۆ ته&zwnj;نها ماوه&zwnj;یه&zwnj;کی که&zwnj;م نه&zwnj;بێت (زوو به زوو به به&zwnj;ڵایه&zwnj;کی سه&zwnj;خت له&zwnj;ناومان ده&zwnj;بردن).';
$t[77] = '(له&zwnj;ناوبردن) بڕیارو یاسای ئێمه بووه پێش تۆ (بۆ قه&zwnj;ومه خوانه&zwnj;ناسه&zwnj;کان) کاتێك پێغه&zwnj;مبه&zwnj;رانیان ده&zwnj;رکردبێت، بێگومان نابینیت سوننه&zwnj;ت و به&zwnj;رنامه&zwnj;ی ئێمه گۆڕانی به&zwnj;سه&zwnj;ردا بێت).';
$t[78] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) نوێژ ئه&zwnj;نجام بده کاتێك خۆر نیوه&zwnj;ی ئاسمان به&zwnj;جێ ده&zwnj;هێڵێت (ئاماژه&zwnj;یه بۆ نوێژی نیوه&zwnj;ڕۆو عه&zwnj;سر) له تاریکی شه&zwnj;ویشدا نوێژی (مه&zwnj;غریب و عیشا) ئه&zwnj;نجام بده&zwnj;، نوێژی به&zwnj;یانیانیش (که زۆر قورئانی تێدا ده&zwnj;خوێنرێت فه&zwnj;رامۆشی مه&zwnj;که&zwnj;) به&zwnj;ڕاستی قورئان خوێندنی نوێژی به&zwnj;یانی ده&zwnj;بینرێت (له لایه&zwnj;ن فریشته&zwnj;کانیشه&zwnj;وه&zwnj;).';
$t[79] = 'له به&zwnj;شێکی شه&zwnj;وگاردا خۆت ماندوو بکه به ده&zwnj;ورکردنه&zwnj;وه&zwnj;ی قورئانه&zwnj;وه له شه&zwnj;ونوێژدا که زیاده ئه&zwnj;رکێکه له&zwnj;سه&zwnj;ر تۆ، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، بۆ ئه&zwnj;وه&zwnj;ی په&zwnj;روه&zwnj;ردگارت بتخاته شوێن و جێگه&zwnj;ی به&zwnj;ڕێزه&zwnj;وه که تیایدا سوپاسکراو بیت.';
$t[80] = 'بڵێ: په&zwnj;روه&zwnj;ردگارا یارمه&zwnj;تیم بده تا هه&zwnj;رکاتێك ده&zwnj;ستبه&zwnj;کاری (هه&zwnj;ر خێرێك بووم) به&zwnj;چاکی و ڕاستی و دروستی ئه&zwnj;نجامی بده&zwnj;م و له هه&zwnj;رکارێك بوومه&zwnj;وه&zwnj;و ئه&zwnj;نجاممدا سه&zwnj;ره&zwnj;نجامێکی چاکی هه&zwnj;بێت و به&zwnj;جوانی لێی ده&zwnj;رچم و هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام له&zwnj;لایه&zwnj;ن خۆته&zwnj;وه ده&zwnj;سه&zwnj;ڵاتێکم پێ ببه&zwnj;خشه که ببێته هۆی سه&zwnj;رکه&zwnj;وتنی به&zwnj;رده&zwnj;وامم.';
$t[81] = 'هه&zwnj;روه&zwnj;ها بڵێ: تازه حه&zwnj;ق و ڕاستی هاتووه&zwnj;و به&zwnj;رپا بووه&zwnj;و به&zwnj;تاڵ و ناحه&zwnj;قی، پووچ و ته&zwnj;فرو توونا بووه&zwnj;، به&zwnj;ڕاستی ناحه&zwnj;ق و به&zwnj;تاڵ هه&zwnj;رتیاچووه&zwnj;و جێگیر نه&zwnj;بووه&zwnj;و به&zwnj;زیووه&zwnj;.';
$t[82] = 'ئێمه له قورئان ئایه&zwnj;ت و سووره&zwnj;تانێك داده&zwnj;به&zwnj;زێنین، که ببێته هۆی شیفاو چاره&zwnj;سه&zwnj;ر بۆ نه&zwnj;خۆشیه ده&zwnj;روونی و جه&zwnj;سته&zwnj;ییه&zwnj;کان هه&zwnj;روه&zwnj;ها ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیش، بۆ ئیمانداران، بۆ سته&zwnj;مکارو خوانه&zwnj;ناسانیش ته&zwnj;نها زیادکردنی زه&zwnj;ره&zwnj;رو زیان نه&zwnj;بێت هیچی تر نی یه&zwnj;، هیچ به&zwnj;هره&zwnj;و سوودێکی لێ نابه&zwnj;ن.';
$t[83] = 'کاتێکیش نازو نیعمه&zwnj;ت ببه&zwnj;خشین به ئینسانی (خوانه&zwnj;ناس) ڕوو وه&zwnj;رده&zwnj;گێڕێت و فیز ده&zwnj;کات و هه&zwnj;ر خه&zwnj;مخۆری خۆیه&zwnj;تی، کاتێکیش ناخۆشی و ته&zwnj;نگانه&zwnj;ی تووش دێت، ئه&zwnj;وه نائومێدو په&zwnj;ست و غه&zwnj;مگین ده&zwnj;بێت.';
$t[84] = 'بڵێ: هه&zwnj;موو که&zwnj;س له&zwnj;سه&zwnj;ر سروشت و فیتره&zwnj;تی خۆی کارده&zwnj;کات، جا په&zwnj;روه&zwnj;ردگارتان چاك ده&zwnj;زانێت کێ ڕێگه&zwnj;و ڕێبازو به&zwnj;رنامه&zwnj;ی ڕاست و دروستی گرتۆته به&zwnj;ر.';
$t[85] = 'پرسیارت لێ ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ڕۆح (که چیه&zwnj;و چۆنه&zwnj;؟) بڵێ: ڕۆح به&zwnj;شێکه له&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارم و به&zwnj;ده&zwnj;ست ئه&zwnj;وه (من نازانم چیه&zwnj;و چۆنه&zwnj;)و هیچتان پێنه&zwnj;به&zwnj;خشراوه له عیلم و زانست ته&zwnj;نها شتێکی که&zwnj;م نه&zwnj;بێت.';
$t[86] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر بمانه&zwnj;وێت هه&zwnj;موو ئه&zwnj;و قورئانه&zwnj;ی که به وه&zwnj;حی بۆمان ڕه&zwnj;وانه کردوویت، ئه&zwnj;وه له دڵ و ده&zwnj;روون و لاپه&zwnj;ڕه&zwnj;کاندا نایهێڵین، پاشان که&zwnj;سێکت ده&zwnj;ست ناکه&zwnj;وێت بتپارێزێت و به&zwnj;رگریت لێ بکات و هه&zwnj;وڵێکت بۆ بدات تا له&zwnj;و به&zwnj;هره&zwnj;یه بێ به&zwnj;هره نه&zwnj;بیت.';
$t[87] = ')به&zwnj;ڵام ئێمه ئه&zwnj;و کاره ناکه&zwnj;ین) چونکه ئه&zwnj;مه ڕه&zwnj;حمه&zwnj;تێکه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;، به&zwnj;ڕاستی فه&zwnj;زڵ و به&zwnj;خشنده&zwnj;ی ئه&zwnj;و زاته (بۆ تۆو بۆ هه&zwnj;موو ئیماندارێك) زۆر گه&zwnj;وره&zwnj;و فراوانه&zwnj;.';
$t[88] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: سوێند به خوا ئه&zwnj;گه&zwnj;ر هه&zwnj;موو ئاده&zwnj;میزادو په&zwnj;ریه&zwnj;کان کۆ ببنه&zwnj;وه&zwnj;و (هه&zwnj;وڵی به&zwnj;رده&zwnj;وام بده&zwnj;ن تا) قورئانێکی وه&zwnj;کو ئه&zwnj;مه دانێن و بیهێننه (مه&zwnj;یدان) ناتوانن کاری وابکه&zwnj;ن و ناتوانن له وێنه&zwnj;ی بهێنن، ئه&zwnj;گه&zwnj;ر چی هه&zwnj;موو لایه&zwnj;کیان ببنه پشتگیرو یارمه&zwnj;تیده&zwnj;ری یه&zwnj;کتر.';
$t[89] = 'سوێند به خوا بێگومان ئێمه له&zwnj;م قورئانه&zwnj;دا له هه&zwnj;موو جۆره نموونه&zwnj;یه&zwnj;کمان بۆ خه&zwnj;ڵکی هێناوه&zwnj;ته&zwnj;وه (تا تێفکرن و بیر بکه&zwnj;نه&zwnj;وه&zwnj;) که&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی ته&zwnj;نها ڕێبازی کوفرو خوانه&zwnj;ناسی نه&zwnj;بێت هیچی تر وه&zwnj;رناگرن...';
$t[90] = '(هه&zwnj;ندێکیان بیانوویان گرت و) وتیان: هه&zwnj;رگیز ئێمه باوه&zwnj;ڕ به تۆ ناکه&zwnj;ین تا کانی و سه&zwnj;رچاوه&zwnj;یه&zwnj;کمان له زه&zwnj;ویه&zwnj;وه بۆ هه&zwnj;ڵنه&zwnj;قوڵێنیت.';
$t[91] = 'یاخود ده&zwnj;بێت خاوه&zwnj;نی باخێك بیت که پڕ بێت له دارخورماو ڕه&zwnj;زو چه&zwnj;نده&zwnj;ها ڕووبار له&zwnj;ناویدا هه&zwnj;ڵبقوڵێنیت و به&zwnj;خوڕو ڕه&zwnj;وان به&zwnj;ژێریاندا بڕوات.';
$t[92] = 'یاخود ئاسمان پارچه پارچه به&zwnj;سه&zwnj;رماندا، هه&zwnj;ر وه&zwnj;کو خۆت پڕوپاگه&zwnj;نده ده&zwnj;که&zwnj;ی دابارێنه&zwnj;، یاخود خوای گه&zwnj;وره&zwnj;و فریشته&zwnj;کانی بهێنه&zwnj;و ڕوو به&zwnj;ڕوومانی بکه&zwnj;!!';
$t[93] = 'یان کۆشك و ته&zwnj;لارو ماڵێکت هه&zwnj;بێت که به (ئاڵتون و زیوو شتی جوان) نه&zwnj;خشێنرابێت و زه&zwnj;خره&zwnj;فه کرابێت، یان (ببینین)که به&zwnj;ره&zwnj;و ئاسمان بڵندو به&zwnj;رز ده&zwnj;بیته&zwnj;وه&zwnj;، ئێمه هه&zwnj;رگیز به&zwnj;وه&zwnj;ش باوه&zwnj;ڕ ناکه&zwnj;ین هه&zwnj;تا کتێبێك دانه&zwnj;به&zwnj;زێنیت و بیخوێنینه&zwnj;وه&zwnj;، تۆش پێیان بڵێ: پاکی و بێگه&zwnj;ردی بۆ په&zwnj;روه&zwnj;ردگارم.. (باشه&zwnj;، که&zwnj;ی ئه&zwnj;وانه له ده&zwnj;ست مندایه&zwnj;) من ته&zwnj;نها پێغه&zwnj;مبه&zwnj;رێکم له&zwnj;گرۆی ئاده&zwnj;میزادو هیچی تر نیم (ئێوه&zwnj;ش له&zwnj;و ئاسته&zwnj;دا نین ئه&zwnj;و بیانوانه&zwnj;تان جێبه&zwnj;جێ بکه&zwnj;ین).';
$t[94] = 'هیچ شتێك نه&zwnj;بووه&zwnj;ته کۆسپ له ڕێگه&zwnj;ی ئه&zwnj;وه&zwnj;دا خه&zwnj;ڵکی باوه&zwnj;ڕ بهێنن_ کاتێك هیدایه&zwnj;تیان بۆ هات_ ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت که وتیان: ئایا خوا مرۆڤێکی به فرستاده ناردووه&zwnj;؟! (له&zwnj;باتی ئه&zwnj;وه&zwnj;ی زوو به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواوه بچن و ئه&zwnj;وه به&zwnj;ڕێز بۆ خۆیان بزانن، که نوێنه&zwnj;ریان لێ هه&zwnj;ڵده&zwnj;بژێرێت و ئیمانی پێ بهێنن، زۆربه&zwnj;ی خه&zwnj;ڵکی له سه&zwnj;رده&zwnj;مه&zwnj;کانی عه&zwnj;قڵیان کول بووه له ئاستی ئه&zwnj;و ڕێزه تایبه&zwnj;تیه&zwnj;دا).';
$t[95] = 'پێیان بڵێ: ئه&zwnj;گه&zwnj;ر له&zwnj;سه&zwnj;ر زه&zwnj;ویدا فریشته هه&zwnj;بوونایه&zwnj;و به&zwnj;دڵنیاییه&zwnj;وه هاتوچۆیان بکردایه&zwnj;و ژیانیان ببردایه&zwnj;ته سه&zwnj;ر، ئه&zwnj;وه ئێمه هه&zwnj;ر له ئاسمانه&zwnj;وه فریشته&zwnj;یه&zwnj;کمان داده&zwnj;به&zwnj;زانده سه&zwnj;ریان که پێغه&zwnj;مبه&zwnj;رو فرستاده بێت بۆیان (چونکه فرستاده ده&zwnj;بێت له جۆری خۆیان بێت).';
$t[96] = 'بڵێ: هه&zwnj;ر خوا به&zwnj;سه که شایه&zwnj;ت بێت له نێوان من و ئێوه&zwnj;دا که چۆن به ڕێکوپێکی په&zwnj;یامی په&zwnj;روه&zwnj;ردگارم پێ ڕاگه&zwnj;یاندوون، به&zwnj;ڕاستی ئه&zwnj;و زاته هه&zwnj;میشه به به&zwnj;نده&zwnj;کانی ئاگادارو بینایه&zwnj;.';
$t[97] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ش خوا ڕێنموویی بکات و (شایسته بێت) ئه&zwnj;وه ئیتر هیدایه&zwnj;ت دراوه&zwnj;و ڕێنموویی وه&zwnj;رگرتووه&zwnj;، ئه&zwnj;وه&zwnj;ش که سه&zwnj;رگه&zwnj;ردانی بکات، ئه&zwnj;وه هه&zwnj;رگیز که&zwnj;سێکت ده&zwnj;ست ناکه&zwnj;وێت جگه له&zwnj;و زاته که پشتیوانی بکات و ڕزگاری بکات و فریای بکه&zwnj;وێت، له ڕۆژی قیامه&zwnj;تیشدا (ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;) له&zwnj;سه&zwnj;ر ڕوو کۆده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;و له&zwnj;کاتێکدا کوێرو لاڵ و که&zwnj;ڕن، ئه&zwnj;وانه شوێنیان ناو ئاگری دۆزه&zwnj;خه&zwnj;، هه&zwnj;ر کاتێك بڵێسه&zwnj;ی کوژایه&zwnj;وه&zwnj;، بڵێسه&zwnj;ی زیاد ده&zwnj;که&zwnj;ینه&zwnj;وه بۆیان.';
$t[98] = 'ئه&zwnj;و دۆزه&zwnj;خه پاداشتیانه چونکه به&zwnj;ڕاستی ئه&zwnj;وانه بڕوایان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;بوو (له بوونه&zwnj;وه&zwnj;رو له قورئاندا) ده&zwnj;شیانوت: باشه&zwnj;، ئایا ئه&zwnj;گه&zwnj;ر ئێمه بووینه ئێسك و پڕتووکاین، ئایا ئێمه به&zwnj;ڕاستی جارێکی تر سه&zwnj;رله&zwnj;نوێ زیندوو ده&zwnj;کرێینه&zwnj;وه&zwnj;؟!';
$t[99] = 'ئایا ئه&zwnj;وانه نه&zwnj;یانبینیووه به&zwnj;ڕاستی ئه&zwnj;و خوایه&zwnj;ی که ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه به&zwnj;توانایه که وه&zwnj;ك ئه&zwnj;وان دروست بکات و (وه&zwnj;ك خۆیان به&zwnj;دیان بهێنێته&zwnj;وه&zwnj;)، دیاره که ڕۆژو کاتێکی تایبه&zwnj;تی دیاری کردووه (بۆ مراندنیان و زیندوو کردنه&zwnj;وه&zwnj;یان) که هیچ گومانی تێدا نیه (له&zwnj;گه&zwnj;ڵ ئه&zwnj;و به&zwnj;ڵگه به&zwnj;هێزانه&zwnj;دا) که&zwnj;چی سته&zwnj;مکاران هه&zwnj;ر بڕیاری یاخی بوون و سه&zwnj;رکه&zwnj;شی و کوفریانداوه&zwnj;و هیچی تر.';
$t[100] = 'پێیان بڵێ: ئه&zwnj;گه&zwnj;ر ئێوه خاوه&zwnj;نی گه&zwnj;نجینه&zwnj;کانی به&zwnj;خشین و ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگارم بن، ئه&zwnj;وکاته ده&zwnj;ستان ده&zwnj;گرته&zwnj;وه له ترسی به&zwnj;خشین، ئاده&zwnj;میزادیش ده&zwnj;ست نوقاوو ڕه&zwnj;زیله (ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;رده&zwnj;ی خوایی نه&zwnj;بێت).';
$t[101] = 'سوێند به خوا بێگومان ئێمه نۆ موعجیزه&zwnj;و به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ئاشکرامان به موسا به&zwnj;خشی، (جا ئه ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم له نه&zwnj;وه&zwnj;ی ئیسرائیلی (زه&zwnj;مانی خۆت) بپرسه کاتێك موسایان بۆ هات (چیان وت؟) جا فیرعه&zwnj;ون پێی وت: ئه&zwnj;ی موسا به&zwnj;ڕاستی من واگومان ده&zwnj;به&zwnj;م که تۆ جادووت لێکراوه&zwnj;.';
$t[102] = 'موسا وتی: سوێند به خوا (ئه&zwnj;ی فیرعه&zwnj;ون) تۆ چاك زانیوته که ئه&zwnj;و (معجزانه&zwnj;) که&zwnj;س ئاوا پێشی ناهێنێت جگه له په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;وی، تا به&zwnj;رچاو ڕوونکه&zwnj;ره&zwnj;وه بێت و (خه&zwnj;ڵکی به&zwnj;ڕۆشنی حه&zwnj;ق و ناحه&zwnj;ق ببینن و له یه&zwnj;کی جیا بکه&zwnj;نه&zwnj;وه&zwnj;)و من دڵنیام به&zwnj;ڕاستی ئه&zwnj;ی فیرعه&zwnj;ون که تۆ تیا ده&zwnj;چیت و له&zwnj;ناو ده&zwnj;برێیت (ئه&zwnj;گه&zwnj;ر هه&zwnj;ر ئاوا بیر بکه&zwnj;یته&zwnj;وه&zwnj;).';
$t[103] = 'جا فیرعه&zwnj;ون ویستی له&zwnj;سه&zwnj;ر زه&zwnj;وی میسر به&zwnj;یه&zwnj;کجاری و به&zwnj;سوکایه&zwnj;تی کردنه&zwnj;وه ده&zwnj;ربه&zwnj;ده&zwnj;ریان بکات، ئێمه&zwnj;ش نوقمی ده&zwnj;ریامان کرد، خۆی و هه&zwnj;رچی ئه&zwnj;وانه&zwnj;ی که له گه&zwnj;ڵیدا بوون.';
$t[104] = 'له&zwnj;دوای له&zwnj;ناوبردنیشی فه&zwnj;رمانماندا به&zwnj;نه&zwnj;وه&zwnj;ی ئیسرائیل و وتمان: که له&zwnj;سه&zwnj;ر زه&zwnj;وی (فه&zwnj;ڵه&zwnj;ستین) نیشته&zwnj;جێ ببن (کاتێك به&zwnj;ته&zwnj;واوی ڕێبازی خوایان گرته&zwnj;به&zwnj;رو، ئیمانداری چاك و پاکی زۆریان تێدا هه&zwnj;ڵکه&zwnj;وت، هه&zwnj;ندێ جار پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تیان ده&zwnj;کردن، وه&zwnj;ك له سه&zwnj;رده&zwnj;می حه&zwnj;زره&zwnj;تی داودو سڵێماندا بووه&zwnj;، به&zwnj;ڵام به&zwnj;داخه&zwnj;وه جووله&zwnj;که&zwnj;ی ئه&zwnj;م سه&zwnj;رده&zwnj;مه ڕێبازی دوژمنایه&zwnj;تی هه&zwnj;موو خه&zwnj;ڵکیان گرتۆته به&zwnj;ر، به تایبه&zwnj;تی ئوممه&zwnj;تی ئیسلام، به&zwnj;ڵام به پشتیوانی خوای گه&zwnj;وره کۆتایی هێنان به ده&zwnj;سه&zwnj;ڵاتیان نزیکه&zwnj;)، کاتێك به&zwnj;ڵێنی دوایی هات ده&zwnj;سته ده&zwnj;سته&zwnj;و پۆل پۆل (له&zwnj;سه&zwnj;ر زه&zwnj;وی فه&zwnj;ڵه&zwnj;ستین) کۆتان ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;.';
$t[105] = 'ئێمه قورئانمان له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی دابه&zwnj;زاندووه&zwnj;، بۆ چه&zwnj;سپاندنی حه&zwnj;ق و ڕاستیش دابه&zwnj;زیوه&zwnj;، ئێمه تۆمان ڕه&zwnj;وانه نه&zwnj;کردووه ته&zwnj;نها بۆ ئه&zwnj;وه نه&zwnj;بێت که مژده به&zwnj;خش بیت (به ئیمانداران) و ترسێنه&zwnj;ر بیت (بۆ خوانه&zwnj;ناسان).';
$t[106] = 'ئێمه قورئانیشمان به&zwnj;ش به&zwnj;ش کردووه تا به شێنه&zwnj;یی و به ئارامی بیخوێنیته&zwnj;وه به&zwnj;سه&zwnj;ر خه&zwnj;ڵکیدا، هه&zwnj;ر ئێمه&zwnj;ش به&zwnj;و شێوه&zwnj;یه دامانبه&zwnj;زاندووه (که تۆ ڕایده&zwnj;گه&zwnj;یه&zwnj;نیت).';
$t[107] = 'پێیان بڵێ: (ئه&zwnj;ی خوانه&zwnj;ناسان) باوه&zwnj;ڕ به قورئان بهێنن یان باوه&zwnj;ڕی پێ نه&zwnj;هێنن (به&zwnj;ده&zwnj;ست خۆتانه و ئه&zwnj;مڕۆ سه&zwnj;ربه&zwnj;ستن، به&zwnj;ڵام بزانن) به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی پێشتر زانیاریان پێدراوه&zwnj;، (ده&zwnj;رباره&zwnj;ی ته&zwnj;ورات و ئینجیلی ده&zwnj;ستکاری نه&zwnj;کراو، چاك ده&zwnj;زانن که ئه&zwnj;م قورئانه له هه&zwnj;مان سه&zwnj;رچاوه&zwnj;وه&zwnj;یه&zwnj;، هه&zwnj;ر بۆیه&zwnj;) کاتێك به&zwnj;سه&zwnj;ریاندا ده&zwnj;خوێندرێته&zwnj;وه (خۆیان پێناگیرێت) خێرا سه&zwnj;ری ڕێزو نه&zwnj;وازشی بۆ داده&zwnj;نه&zwnj;وێنن و سوژده (بۆ نێره&zwnj;ری قورئان ده&zwnj;به&zwnj;ن).';
$t[108] = 'ده&zwnj;شڵێن: پاکی و بێگه&zwnj;ردی بۆ په&zwnj;روه&zwnj;ردگارمانه&zwnj;، به&zwnj;ڕاستی به&zwnj;ڵێنی په&zwnj;روه&zwnj;ردگارمان هه&zwnj;ر دێته دی (به&zwnj;سه&zwnj;ر خستنی ئیمانداران له دنیاداو سه&zwnj;رفرازیان له قیامه&zwnj;تدا).';
$t[109] = 'ئه&zwnj;وانه سوژده و کڕنووش ده&zwnj;به&zwnj;ن و ده&zwnj;گرین و ئه&zwnj;سرینی خۆشه&zwnj;ویستی په&zwnj;روه&zwnj;ردگار له چاوانیان ده&zwnj;بارێت، فرمێسکی ئیمان و شادی هه&zwnj;ڵده&zwnj;وه&zwnj;رێنن و تا دێت زیاتر ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردار دڵ پڕ له ئیمان ده&zwnj;بن.';
$t[110] = 'پێیان بڵێ: ئێوه له نزاو دوعاتاندا هاوار له (الله&zwnj;) بکه&zwnj;ن، یان به&zwnj;سیفه&zwnj;تی پیرۆزی (الرحمن) هانای بۆ به&zwnj;رن، به&zwnj;هه&zwnj;ر ناوو سیفه&zwnj;تێکی جوان و به&zwnj;رز هاواری لێبکه&zwnj;ن و نزا بکه&zwnj;ن دروسته&zwnj;، چونکه ئه&zwnj;و خاوه&zwnj;نی هه&zwnj;موو سیفات و ناوه پیرۆزو جوانه&zwnj;کانه&zwnj;، به&zwnj;هه&zwnj;ر سیفه&zwnj;تێك که له&zwnj;گه&zwnj;ڵ داخوازیه&zwnj;کانتاندا گونجا، داوای لێ بکه&zwnj;ن، له&zwnj;کاتی داواکردنی ڕزق و ڕۆزیدا به (یا ڕزاق، یا وهاب) له&zwnj;کاتی داوای سه&zwnj;رکه&zwnj;وتندا به (یا قوی یا متین یا عزیز یا.... هتد، ئه&zwnj;ی ئیماندار) له&zwnj;کاتی خوێندنی قورئان له نوێژه&zwnj;کاندا ده&zwnj;نگ به&zwnj;رز مه&zwnj;که&zwnj;و زۆریش نزمی مه&zwnj;که&zwnj;، له&zwnj;و نێوانه&zwnj;دا حاڵه&zwnj;تێکی له&zwnj;بارو مامناوه&zwnj;ندی ئه&zwnj;نجام بده&zwnj;.';
$t[111] = 'بشڵێ: سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که که&zwnj;سی نه&zwnj;کردووه به&zwnj;ڕۆڵه&zwnj;ی خۆی و ڕۆڵه&zwnj;شی هه&zwnj;ر نی یه&zwnj;، هه&zwnj;روه&zwnj;ها له موڵك و مه&zwnj;مله&zwnj;که&zwnj;تیدا که&zwnj;سی نه&zwnj;کردووه به&zwnj;شه&zwnj;ریك و هاوتای خۆی، هه&zwnj;رگیز به&zwnj;هۆی بێ ده&zwnj;سه&zwnj;ڵاتی و که&zwnj;م توانینه&zwnj;وه که&zwnj;سی نه&zwnj;کردووه به یاوه&zwnj;رو یاریده&zwnj;ده&zwnj;ری خۆی، (له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;) هه&zwnj;میشه (الله اکبر) بڵێ و ئه&zwnj;و زاته به&zwnj;گه&zwnj;وره و مه&zwnj;زن بزانه&zwnj;.';
?>