<?php
$t = array();
$t[1] = 'سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی ئه&zwnj;م کتێبه که قورئانه بۆ سه&zwnj;ر به&zwnj;نده&zwnj;ی خۆی (محمد صلی الله علیه وسلم ) دابه&zwnj;زاندووه&zwnj;، هیچ که&zwnj;م و کووڕی و ناته&zwnj;واویه&zwnj;کیشی بۆ ڕه&zwnj;وا نه&zwnj;داوه&zwnj;.';
$t[2] = 'کردوویه&zwnj;تی به&zwnj;چاودێری هه&zwnj;رچی به&zwnj;رژه&zwnj;وه&zwnj;ندیه&zwnj;کانی ئاده&zwnj;میزاده&zwnj;و ڕێك و ڕاسته له هه&zwnj;موو شتێکدا، بۆ ئه&zwnj;وه&zwnj;ی یاخیه&zwnj;کان له سزایه&zwnj;کی سه&zwnj;خت بێدار بکاته&zwnj;وه که له&zwnj;لایه&zwnj;ن خواوه ( له دنیاو قیامه&zwnj;تدا) یه&zwnj;خه&zwnj;یان پێ ده&zwnj;گرێت و مژده&zwnj;ش بدات به&zwnj;و ئیماندارانه&zwnj;ی که کارو کرده&zwnj;وه چاکه&zwnj;کان ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن به&zwnj;وه&zwnj;ی که&zwnj;به&zwnj;ڕاستی پاداشتی زۆر چاکیان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[3] = 'به&zwnj;رده&zwnj;وام و بۆ هه&zwnj;میشه له&zwnj;و به&zwnj;هه&zwnj;شته سازگاره&zwnj;دا ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[4] = 'هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانه بترسێنێت و بێداریان ده&zwnj;کاته&zwnj;وه&zwnj;، که ده&zwnj;یانوت: خوا منداڵی بۆ خۆی بڕیارداوه&zwnj;!!';
$t[5] = 'جا ئه&zwnj;وانه نه خۆیان و نه باوو باپیرانیان له ڕووی زانست و زانیاری و تێگه&zwnj;یشتنه&zwnj;وه ئه&zwnj;و گوفتاره ناقۆڵایه ناڵێن، ئه&zwnj;و بوختانه&zwnj;یان قسه&zwnj;یه&zwnj;کی زۆر گه&zwnj;وره&zwnj;و ناڕێکه که له ده&zwnj;میان ده&zwnj;رده&zwnj;چێت، ئه&zwnj;وانه جگه له درۆو گوفتاری نابه&zwnj;جێ هیچی تر ناڵێن.';
$t[6] = 'جا تۆ له&zwnj;وانه&zwnj;یه له خه&zwnj;م و په&zwnj;ژاره&zwnj;دا له سه&zwnj;ره&zwnj;نجامی ڕه&zwnj;شی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕ به&zwnj;م قورئان و فه&zwnj;رمووده&zwnj;یه ناهێنن خۆت له&zwnj;ناو به&zwnj;ریت و له داخ و خه&zwnj;فه&zwnj;تدا بمریت.';
$t[7] = 'بێگومان ئێمه هه&zwnj;رچی له&zwnj;سه&zwnj;ر ڕووی زه&zwnj;ویدا هه&zwnj;یه کردوومانه به&zwnj;هۆی ڕازاندنه&zwnj;وه&zwnj;ی، تا تاقیان بکه&zwnj;ینه&zwnj;وه کامیان کارو کرده&zwnj;وه&zwnj;ی چاکترو ڕێك و پێك تر ئه&zwnj;نجام ده&zwnj;دات (نازو نیعمه&zwnj;ته&zwnj;کان بۆ چاکه به&zwnj;کار ده&zwnj;هێنێت).';
$t[8] = 'بێگومان ئێمه سه&zwnj;رئه&zwnj;نجام هه&zwnj;رچی له&zwnj;سه&zwnj;ر ئه&zwnj;م زه&zwnj;ویه&zwnj;دا هه&zwnj;یه ته&zwnj;ختی ده&zwnj;که&zwnj;ین و ده&zwnj;یکه&zwnj;ینه گۆڕه&zwnj;پانێکی وشك و برنگ و ساف.';
$t[9] = '(به&zwnj;ڵگه&zwnj;یه&zwnj;کی تر له&zwnj;سه&zwnj;ر زیندوو بوونه&zwnj;وه ئه&zwnj;وه&zwnj;یه که خوای گه&zwnj;وره ده&zwnj;پرسێت) ئایا وا دانانێییت که به&zwnj;ڕاستی یارانی ئه&zwnj;شکه&zwnj;وت و تابلۆ (که ناویانی له&zwnj;سه&zwnj;ر نووسراوه&zwnj;) له نیشانه&zwnj;و به&zwnj;ڵگه سه&zwnj;رسوڕهێنه&zwnj;ره&zwnj;کانی ئێمه&zwnj;ن؟! (له سه&zwnj;رده&zwnj;می پاشایه&zwnj;کی زۆرداری خوانه&zwnj;ناسدا چه&zwnj;ند لاوێکی ئیماندار هه&zwnj;ست ده&zwnj;که&zwnj;ن که ئیمان و ژیانیان له مه&zwnj;ترسیدایه بۆیه ڕوو ده&zwnj;که&zwnj;نه ئه&zwnj;شکه&zwnj;وتێك، له&zwnj;وێدا ماوه&zwnj;یه&zwnj;کی دوورو درێژ خوای گه&zwnj;وره خه&zwnj;ویان لێ ده&zwnj;خات)...';
$t[10] = 'کاتێك لاوه&zwnj;کان په&zwnj;نایان برده به&zwnj;ر ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;و نزایان کرد، جا وتیان: په&zwnj;روه&zwnj;ردگارا له&zwnj;لایه&zwnj;ن خۆته&zwnj;وه ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی تایبه&zwnj;تیمان پێ ببه&zwnj;خشه و له سۆزی خۆت به&zwnj;هره&zwnj;وه&zwnj;رمان بکه&zwnj;و ڕێگه&zwnj;ی ڕزگاری و سه&zwnj;رفرازیمان بۆ بسازێنه له&zwnj;م کاره&zwnj;ماندا.';
$t[11] = 'ئینجا (ئێمه&zwnj;ش نزامان گیراکردن) په&zwnj;رده&zwnj;مان هێنا به&zwnj;سه&zwnj;ر گوێیاندا، (تا به&zwnj;ئارامی) چه&zwnj;نده&zwnj;ها ساڵ له ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;دا بۆ خۆیان (به خه&zwnj;وتوویی) بمێننه&zwnj;وه&zwnj;.';
$t[12] = 'پاشان (دوای ماوه&zwnj;یه&zwnj;کی دوورو درێژ) له خه&zwnj;و خه&zwnj;به&zwnj;رمان کردنه&zwnj;وه&zwnj;و له نوستن هه&zwnj;ستان، تا بزانین کام ده&zwnj;سته&zwnj;یه&zwnj;یان ده&zwnj;زانن که چه&zwnj;نده خه&zwnj;ویان لێکه&zwnj;وتووه (ئه&zwnj;وانه&zwnj;یان که ده&zwnj;ڵێن ڕۆژێك یان که&zwnj;متر، یان ئه&zwnj;وانه&zwnj;یان که ده&zwnj;ڵێن مه&zwnj;گه&zwnj;ر هه&zwnj;ر خوا بزانێت چه&zwnj;نده خه&zwnj;وتووین).';
$t[13] = 'ئێمه به&zwnj;ڕاستی و دروستی هه&zwnj;واڵی به&zwnj;سه&zwnj;رهاتی ئه&zwnj;و لاوانه&zwnj;ت بۆ ده&zwnj;گێڕینه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وانه لاوانێك بوون باوه&zwnj;ڕی دامه&zwnj;زراویان هێنابوو به په&zwnj;روه&zwnj;ردگاریان، ئێمه&zwnj;ش زیاتر له نووری ئیمان و هیدایه&zwnj;ت به&zwnj;هره&zwnj;وه&zwnj;رمان کردن.';
$t[14] = 'ئێمه&zwnj;ش دڵه&zwnj;کانیانمان دامه&zwnj;زراو کردو ترسمان تێدا نه&zwnj;هێشت (بڕیاریاندا کۆمه&zwnj;ڵگه&zwnj;ی نه&zwnj;فامی به&zwnj;جێبهێڵن) کاتێك هه&zwnj;ستان و وتیان: په&zwnj;روه&zwnj;ردگاری ئێمه په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;، هه&zwnj;رگیز هاناو هاوار بۆ هیچ خوایه&zwnj;کی تر نابه&zwnj;ین جگه له&zwnj;و، ئه&zwnj;گه&zwnj;ر له&zwnj;و ڕاستی یه لابده&zwnj;ین، سوێند به خوا ئه&zwnj;وه بێگومان ئێمه له&zwnj;و کاته&zwnj;دا قسه&zwnj;ی نابه&zwnj;جێ و نادروستمان کردووه&zwnj;.';
$t[15] = 'ئا ئه&zwnj;وانه قه&zwnj;ومه&zwnj;که&zwnj;ی خۆمانن (له ڕێبازی ڕاست لایانداوه له&zwnj;جیاتی خواناسی) شتی تریان کردووه به خوای خۆیان، ده&zwnj;با به&zwnj;ڵگه&zwnj;یه&zwnj;کی ئاشکراش بهێنن له&zwnj;سه&zwnj;ریان، جا کێ سته&zwnj;مکارتره له&zwnj;وه&zwnj;ی که درۆ بۆ خوا هه&zwnj;ڵده&zwnj;به&zwnj;ستێت؟!';
$t[16] = '(هه&zwnj;ندێکیان وتیان) جا ئێسته که ئێوه بڕیارتانداوه خۆتان دووره په&zwnj;رێزبگرن له&zwnj;م خه&zwnj;ڵکه&zwnj;و له&zwnj;وشتانه که له جیاتی خوا ده&zwnj;یانپه&zwnj;رستن، ته&zwnj;نها خواپه&zwnj;رستی بکه&zwnj;ن، ده په&zwnj;نا به&zwnj;رنه ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;، ئه&zwnj;و کاته په&zwnj;روه&zwnj;ردگارتان ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆیتان به&zwnj;سه&zwnj;ردا په&zwnj;خش ده&zwnj;کاته&zwnj;وه&zwnj;و هۆکاری حه&zwnj;وانه&zwnj;وه&zwnj;تان بۆ دابین ده&zwnj;کات.';
$t[17] = 'کاتێکیش که خۆر هه&zwnj;ڵدێت، تیشکه&zwnj;که&zwnj;ی ده&zwnj;بینیت له لای ڕاستی ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;وه ده&zwnj;دات و تێپه&zwnj;ڕ ده&zwnj;بێت، کاتێکیش که ئاوا ده&zwnj;بێت له لای چه&zwnj;پی ده&zwnj;دات و تیشکه&zwnj;که&zwnj;یان به&zwnj;سه&zwnj;ردا تێپه&zwnj;ڕ ده&zwnj;بێت، له&zwnj;و کاته&zwnj;دا ئه&zwnj;و لاوانه له&zwnj;ناو بۆشایی فراوانی ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;دا ڕاکشاون، ئه&zwnj;وه هه&zwnj;مووی به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;یه&zwnj;که له به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کانی خوا، ئه&zwnj;وه&zwnj;ی خوا هیدایه&zwnj;تی بدات هه&zwnj;ر ئه&zwnj;و هیدایه&zwnj;ت دراوه&zwnj;و ڕێنموویی وه&zwnj;رگرتووه&zwnj;، ئه&zwnj;وه&zwnj;ش خوا گومڕای بکات، ئه&zwnj;وه هه&zwnj;رگیز که&zwnj;سێکت ده&zwnj;ست ناکه&zwnj;وێت که پشتیوانی بکات و ڕێنموویی بکات.';
$t[18] = '(پاشان دیمه&zwnj;نیان ده&zwnj;هێنێته به&zwnj;رچاوو ده&zwnj;فه&zwnj;رموێت): واده&zwnj;زانیت به&zwnj;خه&zwnj;به&zwnj;رن له&zwnj;کاتێکدا به&zwnj;خه&zwnj;وتویی ڕاکشاون، ئێمه ئه&zwnj;م دیوو ئه&zwnj;و دیویان پێده&zwnj;که&zwnj;ین و به&zwnj;لای ڕاست و چه&zwnj;پیاندا هه&zwnj;ڵیان ده&zwnj;گێڕینه&zwnj;وه (بۆ ئه&zwnj;وه&zwnj;ی زه&zwnj;وی جه&zwnj;سته&zwnj;یان نه&zwnj;خوات و هه&zwnj;وای پاك لێیان بدات)، سه&zwnj;گه&zwnj;که&zwnj;شیان چه&zwnj;مۆڵه&zwnj;ی داداوه له&zwnj;به&zwnj;رده&zwnj;م که&zwnj;له&zwnj;به&zwnj;ری ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;دا، ئه&zwnj;گه&zwnj;ر تۆ (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) سه&zwnj;رێکت بکێشایه بۆ سه&zwnj;ر دیمه&zwnj;نیان، ئه&zwnj;وه به په&zwnj;له هه&zwnj;ڵده&zwnj;هاتیت و ترسێکی زۆر له دیمه&zwnj;نیان ده&zwnj;چووه دڵته&zwnj;وه&zwnj;.';
$t[19] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;ش که به&zwnj;ده&zwnj;سه&zwnj;ڵاتی خۆمان خه&zwnj;ومان لێخستن، خه&zwnj;به&zwnj;رمان کردنه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی (له ماوه&zwnj;ی خه&zwnj;وتنه&zwnj;که&zwnj;یان) له&zwnj;یه&zwnj;کتر بپرسن، یه&zwnj;کێکیان وتی: باشه ئه&zwnj;وه ئێوه واده&zwnj;زانن چه&zwnj;نده خه&zwnj;وتوون، هه&zwnj;ندێکیان وتیان: ڕۆژێك یان که&zwnj;متر له ڕۆژێك خه&zwnj;وتووین، هه&zwnj;ندێکی تریان وتیان: مه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگارتان خۆی بزانێت چه&zwnj;نده خه&zwnj;وتوون، دیاره هه&zwnj;ستیان به&zwnj;برسێتی کردووه&zwnj;، بۆیه لاوێکیان پێشنیاری کردو وتی: ده یه&zwnj;کێکتان بنێرن با ئه&zwnj;م پاره زیوه&zwnj;تان ببات بۆ شار، جا سه&zwnj;رنج بدات، بزانێت کێ خۆراکی خاوێن تری هه&zwnj;یه&zwnj;، با له&zwnj;و خۆراکه&zwnj;تان بۆ بکڕێت و بۆتان بهێنێت، با وریاو نه&zwnj;رم و نیان و له&zwnj;سه&zwnj;ر خۆش بێت، با که&zwnj;سیش ئاگادار نه&zwnj;کات به کارو بارتان.';
$t[20] = 'چونکه به&zwnj;ڕاستی ئه&zwnj;و بت په&zwnj;رستانه ئه&zwnj;گه&zwnj;ر پێتان بزانن، ئه&zwnj;وه به&zwnj;رد بارانتان ده&zwnj;که&zwnj;ن، یان ده&zwnj;تانگێڕنه&zwnj;وه بۆ ناو ئایینی خۆیان، ئه&zwnj;و کاته ئیتر هه&zwnj;رگیز سه&zwnj;رفراز نابن.';
$t[21] = '(وادیاره که یه&zwnj;کێکیان ناردووه بۆ کڕینی خوارده&zwnj;مه&zwnj;نی و پاره زیوه&zwnj;که&zwnj;ی ده&zwnj;رهێناوه&zwnj;، کابرای دوکاندار خه&zwnj;ڵکی له ده&zwnj;ور کۆ ده&zwnj;کاته&zwnj;وه&zwnj;، چونکه مێژووی سکه&zwnj;ی پاره&zwnj;که زۆر کۆن بوو، ئه&zwnj;وسا بۆیان ده&zwnj;رکه&zwnj;وت، ئه&zwnj;م لاوه یه&zwnj;کێکه له&zwnj;وانه&zwnj;ی که چه&zwnj;ند سه&zwnj;ده&zwnj;یه&zwnj;ك له&zwnj;مه&zwnj;وبه&zwnj;ر له ده&zwnj;ست پاشای بت په&zwnj;رست و سته&zwnj;مکار هه&zwnj;ڵاتوون، خه&zwnj;ڵکه&zwnj;که له&zwnj;گه&zwnj;ڵ لاوه&zwnj;که&zwnj;دا به&zwnj;ره&zwnj;و ئه&zwnj;شکه&zwnj;وته&zwnj;که بۆ سه&zwnj;ردانی ئه&zwnj;وانی تر چون)، تا به&zwnj;و شێوه&zwnj;یه&zwnj;ش خه&zwnj;ڵکیمان ڕێنموویی کرد تا بیاندۆزنه&zwnj;وه و بزانن و دڵنیابن که به&zwnj;ڕاستی به&zwnj;ڵێنی خوا له زیندوو کردنه&zwnj;وه&zwnj;ی مردووه&zwnj;کاندا حه&zwnj;ق و ڕاسته&zwnj;و بێگومان قیامه&zwnj;تیش هه&zwnj;ر دێت و هیچ گومانی تێدا نیه (له&zwnj;وێ که به&zwnj;دیداریان شادبوون)، لاوه&zwnj;کان خه&zwnj;وی یه&zwnj;کجاریان لێکه&zwnj;وت) له&zwnj;کاتێکدا ئه&zwnj;و خه&zwnj;ڵکه کێشه&zwnj;یان بوو له&zwnj;سه&zwnj;ر مه&zwnj;سه&zwnj;له&zwnj;ی زیندوو بوونه&zwnj;وه&zwnj;، ئینجا هه&zwnj;ندێکیان وتیان: با چه&zwnj;ند خانوویه&zwnj;کیان له&zwnj;سه&zwnj;ر دروست بکه&zwnj;ن، په&zwnj;روه&zwnj;ردگاریان زانایه پێیان، به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی که ده&zwnj;سه&zwnj;ڵاتیان هه&zwnj;بوو به&zwnj;سه&zwnj;ر کارو باریاندا، وتیان: بێگومان مزگه&zwnj;وتێکیان به&zwnj;سه&zwnj;ره&zwnj;وه دروست ده&zwnj;که&zwnj;ین (که ئه&zwnj;مه&zwnj;ش شتێکی داهێنراو بوو له ئایینی گاوڕیدا که له&zwnj;سه&zwnj;ر گۆڕی پیاو چاکان په&zwnj;رستگایان دروست ده&zwnj;کرد).';
$t[22] = 'جا (ده&zwnj;رباره&zwnj;ی ژماره&zwnj;ی یارانی ئه&zwnj;شکه&zwnj;وت) له ئایینده&zwnj;دا ده&zwnj;ڵێن: سیانن و چوارهه&zwnj;میان سه&zwnj;گه&zwnj;که&zwnj;یانه&zwnj;، هه&zwnj;ندێکی تر ده&zwnj;ڵێن: پێنجن و شه&zwnj;ش هه&zwnj;میان سه&zwnj;گه&zwnj;که&zwnj;یانه&zwnj;، که هه&zwnj;موو ئه&zwnj;مانه قسه&zwnj;یه&zwnj;کی بێ به&zwnj;ڵگه&zwnj;ن و چێو له تاریکی کوتانه&zwnj;، هه&zwnj;ندێکی تر ده&zwnj;ڵێن حه&zwnj;وتن و هه&zwnj;شته&zwnj;میان سه&zwnj;گه&zwnj;که&zwnj;یانه&zwnj;، ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: هه&zwnj;ر په&zwnj;روه&zwnj;ردگارم خۆی ده&zwnj;زانێت ژماره&zwnj;یان چه&zwnj;نده&zwnj;، ته&zwnj;نها که&zwnj;مێك نه&zwnj;بێت که&zwnj;س ژماره&zwnj;یان نازانێت، له&zwnj;به&zwnj;ر ئه&zwnj;وه موجاده&zwnj;له مه&zwnj;که له&zwnj;باره&zwnj;ی ئه&zwnj;وانه&zwnj;وه جگه له موجاده&zwnj;له&zwnj;یه&zwnj;کی ئاشکراو ئاسان نه&zwnj;بێت، ده&zwnj;رباره&zwnj;شیان له&zwnj;هیچ که&zwnj;سێك له خاوه&zwnj;نانی کتێب مه&zwnj;پرسه&zwnj;، (چونکه ئه&zwnj;وه&zwnj;ی به&zwnj;وه&zwnj;حی به&zwnj;تۆ گه&zwnj;ییووه به&zwnj;سه له&zwnj;و باره&zwnj;یه&zwnj;وه&zwnj;).';
$t[23] = 'هه&zwnj;رگیز ده&zwnj;رباره&zwnj;ی هیچ شتێك و هیچ کارێك مه&zwnj;ڵێ، من به&zwnj;ڕاستی سبه&zwnj;ینێ ئه&zwnj;نجامده&zwnj;ری ئه&zwnj;و کاره&zwnj;م...';
$t[24] = 'مه&zwnj;گه&zwnj;ر بڵێیت: ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت و ویستی په&zwnj;روه&zwnj;ردگاری له&zwnj;سه&zwnj;ر بێت، یادی په&zwnj;روه&zwnj;ردگاریشت بکه کاتێك که له بیرت چوو بڵێیت (ان شاء الله&zwnj;) له ئه&zwnj;نجامدانی هه&zwnj;ر کارێکدا بڵێ: ئومێده&zwnj;وارم په&zwnj;روه&zwnj;ردگارم ڕێنموویم بکات بۆ به&zwnj;ڵگه&zwnj;یه&zwnj;کی نزیکتر له&zwnj;مه تا به&zwnj;و شێوه&zwnj;یه&zwnj;ی که پێی چاکه&zwnj;و لای په&zwnj;سه&zwnj;نده جێبه&zwnj;جێی بکه&zwnj;م.';
$t[25] = 'ئه&zwnj;وسا ئیتر یارانی ئه&zwnj;شکه&zwnj;وت سێ سه&zwnj;د ساڵی ته&zwnj;واوی (هه&zwnj;تاوی) له ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;یاندا مانه&zwnj;وه (ئه&zwnj;گه&zwnj;ر به ساڵی مانگی حسابی بکه&zwnj;یت) نۆ دانه ساڵی تریشیان خسته سه&zwnj;ر (واته ده&zwnj;کاته 309 ساڵ خه&zwnj;ویان لێکه&zwnj;وت).';
$t[26] = 'پێیان بڵێ: خوا خۆی ده&zwnj;زانێت به&zwnj;ماوه&zwnj;ی مانه&zwnj;وه&zwnj;یان له ئه&zwnj;شکه&zwnj;وته&zwnj;که&zwnj;دا، هه&zwnj;ر بۆ خوایه زانینی نهێنی ئاسمانه&zwnj;کان و زه&zwnj;وی، ئای که چه&zwnj;نده بینه&zwnj;رو بیسه&zwnj;ره خوای گه&zwnj;وره&zwnj;! هیچ که&zwnj;س نیه جگه له&zwnj;و زاته پشت و په&zwnj;نای خه&zwnj;ڵکی بێت، بێگومان ئه&zwnj;و خوایه له کارو فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تیه&zwnj;که&zwnj;یدا هیچ که&zwnj;س ناکات به&zwnj;هاوتاو هاوبه&zwnj;شی خۆی.';
$t[27] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) ئه&zwnj;وه&zwnj;ی به&zwnj;وه&zwnj;حی ڕه&zwnj;وانه کراوه بۆ تۆ، له قورئانی په&zwnj;روه&zwnj;ردگارت ده&zwnj;وری بکه&zwnj;ره&zwnj;وه&zwnj;، هیچ که&zwnj;س نی یه فه&zwnj;رمووده&zwnj;و گوفتاری ئه&zwnj;و زاته بگۆڕێت، دڵنیاش به که هه&zwnj;رگیز په&zwnj;ناگاو یاریده&zwnj;ده&zwnj;رێکی وه&zwnj;کو ئه&zwnj;و زاته&zwnj;ت ده&zwnj;ست ناکه&zwnj;وێت.';
$t[28] = 'ئارام بگره&zwnj;و خۆت ڕابگره له&zwnj;گه&zwnj;ڵ ئه&zwnj;و که&zwnj;سانه&zwnj;دا که خواپه&zwnj;رستی ده&zwnj;که&zwnj;ن و یادی په&zwnj;روه&zwnj;ردگاریان ویردی سه&zwnj;رزاریانه له به&zwnj;ره&zwnj;به&zwnj;یان و ده&zwnj;مه&zwnj;و ئێواراندا، مه&zwnj;به&zwnj;ستیانه&zwnj;و ده&zwnj;یانه&zwnj;وێت که ئه&zwnj;و زاته له خۆیان ڕازی بکه&zwnj;ن، ڕوو له&zwnj;وان وه&zwnj;رمه&zwnj;گێڕه&zwnj;و (ڕوو مه&zwnj;که دنیاپه&zwnj;رستان و ده&zwnj;وڵه&zwnj;مه&zwnj;ندان) له&zwnj;کاتێکدا تۆ زینه&zwnj;ت و ڕازاوه&zwnj;یی ژیانی دنیات بوێت و به&zwnj;گوێی ئه&zwnj;وجۆره که&zwnj;سه مه&zwnj;که که دڵیمان غافڵ کردووه له یادی خۆمان (به&zwnj;هۆی دنیاپه&zwnj;رستیه&zwnj;وه&zwnj;) شوێنی ئاره&zwnj;زووی خۆی که&zwnj;وتووه&zwnj;و هه&zwnj;رچی کاروباری هه&zwnj;یه بێ سه&zwnj;رو بنه&zwnj;و له سنوور ده&zwnj;رچووه&zwnj;.';
$t[29] = 'پێیان بڵێ: حه&zwnj;ق هه&zwnj;ر ئه&zwnj;وه&zwnj;یه که له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆتان ڕه&zwnj;وانه&zwnj;کراوه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ده&zwnj;یه&zwnj;وێت با باوه&zwnj;ڕ بهێنێت و ئه&zwnj;وه&zwnj;ی ده&zwnj;یه&zwnj;وێت با کافرو بێ باوه&zwnj;ڕ بێت، بێگومان ئێمه دۆزه&zwnj;خێکمان ئاماده کردووه بۆ سته&zwnj;مکاران که له هه&zwnj;موولایه&zwnj;که&zwnj;وه ده&zwnj;وره&zwnj;یان ده&zwnj;دات و دیواری بڵێسه&zwnj;ی ئابڵوقه&zwnj;ی داون، خۆ ئه&zwnj;گه&zwnj;ر هاوار بکه&zwnj;ن له ئازارو تینویه&zwnj;تیدا، ئه&zwnj;وه ئاو ده&zwnj;درێن به ئاوێکی گه&zwnj;رمی پیس و بۆن ناخۆش که وه&zwnj;کو خڵته&zwnj;ی کانزایی تواوه وایه&zwnj;، ئه&zwnj;وه&zwnj;نده&zwnj;ش گه&zwnj;رمه ده&zwnj;م و چاویان هه&zwnj;ڵده&zwnj;کوڕوزێنێت و ده&zwnj;یبرژێنێت، ئای که چه&zwnj;ند خواردنه&zwnj;وه&zwnj;یه&zwnj;کی تاڵ و ناخۆشه&zwnj;، چه&zwnj;نده جێگه&zwnj;و ڕێگه&zwnj;یه&zwnj;کی پڕ له ئازاره&zwnj;.';
$t[30] = 'له&zwnj;ولاشه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وانه&zwnj;ی که به&zwnj;ڕاستی ئیمان و باوه&zwnj;ڕیان هێناوه&zwnj;و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، با دڵنیابن ئێمه پاداشتی ئه&zwnj;و که&zwnj;سه به&zwnj;زایه ناده&zwnj;ین که کردارو ڕه&zwnj;فتاری جوان و په&zwnj;سه&zwnj;نده&zwnj;...';
$t[31] = 'ئا ئه&zwnj;وانه باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی_عه&zwnj;دن_بۆیان ئاماده&zwnj;یه&zwnj;، که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;به&zwnj;رده&zwnj;میاندا جاری و ڕه&zwnj;وانه&zwnj;، بازن و ده&zwnj;سته&zwnj;به&zwnj;ندو (سه&zwnj;عاتی) ئاڵتونیان له ده&zwnj;ست ده&zwnj;کرێت، پۆشاکی سه&zwnj;وزی جوان ده&zwnj;پۆشن که دووتوێیه&zwnj;، چینی ژێره&zwnj;وه ئاوریشمی ئه&zwnj;ستووره&zwnj;، چینی سه&zwnj;ره&zwnj;وه ئاوریشمی ته&zwnj;نکه&zwnj;، (جا به&zwnj;که&zwnj;ماڵی شادیه&zwnj;وه&zwnj;) له&zwnj;سه&zwnj;ر کورسی و قه&zwnj;نه&zwnj;فه ڕازاوه&zwnj;کان شانیان داداوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه چاکترین و جوانترین و خۆشترین پاداشتیان ده&zwnj;ستکه&zwnj;وتووه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ خۆشترین و سازگارترین جێگه&zwnj;و ڕێگه&zwnj;دا.';
$t[32] = '(ده&zwnj;رباره&zwnj;ی دۆزه&zwnj;خیان و به&zwnj;هه&zwnj;شتیان) ئه&zwnj;ی محمد صلی الله علیه وسلم، نموونه&zwnj;یان بۆ بهێنه&zwnj;ره&zwnj;وه به&zwnj;دوو پیاو که یه&zwnj;کێکیانمان کردووه به&zwnj;خاوه&zwnj;نی دوو باخی ڕه&zwnj;زو، ده&zwnj;وری هه&zwnj;ردوو باخه&zwnj;که&zwnj;مان به&zwnj;دار خورما ته&zwnj;نیوه&zwnj;و له نێوانیاندا باخ و بێستانمان فه&zwnj;راهه&zwnj;م هێناوه&zwnj;...';
$t[33] = 'هه&zwnj;ردوو باخه&zwnj;که&zwnj;ش میوه&zwnj;ی چاکیان به&zwnj;رهه&zwnj;م هێناوه&zwnj;و هیچیان لێ خه&zwnj;سار نه&zwnj;بووه&zwnj;و له نێوان هه&zwnj;ردوو باخه&zwnj;که&zwnj;شدا ڕووبارێکمان هه&zwnj;ڵقوڵاندووه&zwnj;...';
$t[34] = 'جا ئه&zwnj;و خاوه&zwnj;ن باخه&zwnj;، زۆر سه&zwnj;روه&zwnj;ت و سامانی تری هه&zwnj;بوو، (کابرای خاوه&zwnj;ن باخ غه&zwnj;ڕابوو به&zwnj;سامانه&zwnj;که&zwnj;ی) بۆیه به هاوه&zwnj;ڵه هه&zwnj;ژاره ئیمانداره&zwnj;که&zwnj;ی وت کاتێك گفتوگۆی له&zwnj;گه&zwnj;ڵدا ده&zwnj;کرد: من سامانی زۆر زیاترم هه&zwnj;یه له تۆ، ماڵ و منداڵیشم به&zwnj;هێز تره له&zwnj;وانه&zwnj;ی تۆ.';
$t[35] = 'ڕۆژێك چوو بۆ سه&zwnj;ردانی یه&zwnj;کێك له باخه&zwnj;کانی (له&zwnj;گه&zwnj;ڵ هاوه&zwnj;ڵه&zwnj;که&zwnj;یدا) له&zwnj;کاتێکدا که سته&zwnj;مکار بوو له خۆی (به&zwnj;هۆی له خۆبایی بوونیه&zwnj;وه&zwnj;)، بۆیه له&zwnj;به&zwnj;رخۆیه&zwnj;وه وتی: گومان نابه&zwnj;م که ئه&zwnj;م باخ و باخاته هه&zwnj;رگیز له&zwnj;ناو بچێت...';
$t[36] = 'واشنازانم قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت، سوێند به خوا ئه&zwnj;گه&zwnj;ر بمگێڕنه&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگارم دوای مردنم، ئه&zwnj;وه دڵنیام که&zwnj;لای ئه&zwnj;ویش جێگه&zwnj;و ڕێگه&zwnj;و چه&zwnj;نده&zwnj;ها باخی ڕازاوه&zwnj;ی له&zwnj;مه باشترم بۆ ئاماده کراوه&zwnj;.';
$t[37] = 'هاوه&zwnj;ڵه ئیمانداره&zwnj;که&zwnj;ی له&zwnj;کاتێکدا که گفتوگۆی له&zwnj;گه&zwnj;ڵدا ده&zwnj;کرد پێی وت: ئایا باوه&zwnj;ڕت نه&zwnj;ماوه به&zwnj;و زاته&zwnj;ی که سه&zwnj;ره&zwnj;تا تۆی له خاك دروست کردووه&zwnj;؟! له&zwnj;وه&zwnj;ودوا نوفته&zwnj;یه&zwnj;ك بوویت، پاشان کردیتی به پیاو؟!';
$t[38] = 'به&zwnj;ڵام من ده&zwnj;ڵێم که&zwnj;: _الله&zwnj;_ (به&zwnj;دیهێنه&zwnj;ری هه&zwnj;موو بوونه&zwnj;وه&zwnj;ر)، هه&zwnj;ر ئه&zwnj;و په&zwnj;روه&zwnj;ردگارمه&zwnj;، هیچ جۆره هاوه&zwnj;ڵ و شه&zwnj;ریکێك بۆ ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;م بڕیار ناده&zwnj;م.';
$t[39] = 'ده&zwnj;بوا کاتێك چوویته ناو باخه&zwnj;که&zwnj;ته&zwnj;وه&zwnj;، بتوتایه&zwnj;: ما شاء الله&zwnj;، هه&zwnj;رچی خوا ویستی له&zwnj;سه&zwnj;ر بێت هه&zwnj;ر ئه&zwnj;وه پێش دێت ئه&zwnj;م هه&zwnj;موو نازو نیعمه&zwnj;ته به&zwnj;خششی خوایه&zwnj;و به&zwnj;هه&zwnj;رکه&zwnj;س بیه&zwnj;وێت ده&zwnj;یبه&zwnj;خشێت، هیچ هێزو توانایه&zwnj;ك بۆ هیچ که&zwnj;س نی یه ته&zwnj;نها به&zwnj;هۆی خواوه نه&zwnj;بێت، ئه&zwnj;گه&zwnj;ر تۆ من ده&zwnj;بینیت ماڵ و سامان و نه&zwnj;وه&zwnj;م له تۆ که&zwnj;متره&zwnj;...';
$t[40] = 'ئه&zwnj;وه نزیکه و ئومێده&zwnj;وارم په&zwnj;روه&zwnj;ردگارم له&zwnj;و باخه&zwnj;ی تۆ چاکترم پێ ببه&zwnj;خشێت (له دنیادا یان له قیامه&zwnj;تدا) به&zwnj;ڵایه&zwnj;کیش له ئاسمانه&zwnj;وه بۆ باخه&zwnj;که&zwnj;ت بنێرێت و، بیکاته زه&zwnj;ویه&zwnj;کی ڕووت و خلیسك و وشك...';
$t[41] = 'یاخود (دوور نیه&zwnj;) ئاوی باخه&zwnj;که ڕۆبچێت و هه&zwnj;رگیز نه&zwnj;توانیت به&zwnj;ده&zwnj;ستی بهێنیت.';
$t[42] = '(سه&zwnj;رئه&zwnj;نجام پێشبینی هاوه&zwnj;ڵی خواناسی هاته&zwnj;دی) سه&zwnj;روه&zwnj;ت و سامانی به&zwnj;با دراو له&zwnj;ناو برا، ئینجا (به&zwnj;ده&zwnj;م ئاخ و داخ و هه&zwnj;ناسه&zwnj;ی سارده&zwnj;وه&zwnj;)، ده&zwnj;ستی ده&zwnj;دا به&zwnj;یه&zwnj;کداو له&zwnj;سه&zwnj;ر ئه&zwnj;و مه&zwnj;سره&zwnj;فه&zwnj;ی که&zwnj;تێدا کردبوو، له&zwnj;کاتێکدا باخه&zwnj;که به&zwnj;سه&zwnj;ریه&zwnj;کدا ته&zwnj;پی بوو، له جێی خۆیدا کاول بوو، ئه&zwnj;وسا ده&zwnj;یوت: خۆزگه من هیچ جۆره هاوه&zwnj;ڵێکم بۆ خوا بڕیار نه&zwnj;دایه (باوه&zwnj;ڕی دامه&zwnj;زراوم ته&zwnj;نها به&zwnj;و هه&zwnj;بوایه&zwnj;).';
$t[43] = 'ئیتر هیچ ده&zwnj;سته&zwnj;و تاقمێك نه&zwnj;بوو جگه له خوا فریای بکه&zwnj;وێت و له&zwnj;خه&zwnj;شمی خوا ده&zwnj;ربازی بکات، سه&zwnj;رکه&zwnj;وتووش نه&zwnj;بوو.';
$t[44] = 'ئا له&zwnj;و کاتانه&zwnj;داو له&zwnj;و جۆره حاڵانه&zwnj;دا یارمه&zwnj;تی و کۆمه&zwnj;ك ته&zwnj;نها له خوا، په&zwnj;روه&zwnj;ردگاری حه&zwnj;ق و ڕاست و دروست وه&zwnj;رده&zwnj;گیرێت، هه&zwnj;ر خۆی چاکترین پاداشتی ئاماده کردووه&zwnj;، چاکترین سه&zwnj;رئه&zwnj;نجامیش چاوه&zwnj;ڕێی به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;.';
$t[45] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) نموونه&zwnj;یان بۆ بهێنه&zwnj;ره&zwnj;وه که ژیانی دنیا وه&zwnj;ك ئه&zwnj;و ئاوی بارانه وایه که له ئاسمانه&zwnj;وه ده&zwnj;یبارێنین و ڕووه&zwnj;کی زه&zwnj;وی به&zwnj;هۆیه&zwnj;وه به&zwnj;یه&zwnj;کدا دێن، له دوای ماوه&zwnj;یه&zwnj;کی که&zwnj;م (هه&zwnj;موو ئه&zwnj;و ڕووه&zwnj;ك و گژو گیایه&zwnj;) ده&zwnj;بێته پووش و په&zwnj;ڵاشێکی وردو خاش، ڕه&zwnj;شه&zwnj;با به&zwnj;م لاو لادا بڵاوی ده&zwnj;کاته&zwnj;وه&zwnj;، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[46] = 'ماڵ و سامان و وه&zwnj;چه&zwnj;، زینه&zwnj;ت و جوانین بۆ ژیانی دنیا، به&zwnj;ڵام دڵنیابن کرده&zwnj;وه چاکه&zwnj;کان که به&zwnj;رهه&zwnj;میان ده&zwnj;مێنێته&zwnj;وه پاداشتیان چاکتره لای په&zwnj;روه&zwnj;ردگارت و ئومێدو هیوای چاکتریشی لێ ده&zwnj;کرێت بۆ خاوه&zwnj;نه&zwnj;کانیان.';
$t[47] = 'ڕۆژێك دێت که کێوه&zwnj;کان له جێگه&zwnj;ی خۆیان هه&zwnj;ڵده&zwnj;که&zwnj;نین و گۆشه&zwnj;و که&zwnj;ناری زه&zwnj;وی به&zwnj;زه&zwnj;قی ده&zwnj;بینیت، له&zwnj;کاتێکدا هه&zwnj;موو خه&zwnj;ڵکیشمان کۆ کردۆته&zwnj;وه&zwnj;و که&zwnj;سیشمان لێ به&zwnj;جێ نه&zwnj;هێشتوون.';
$t[48] = 'ئه&zwnj;و ڕۆژه سه&zwnj;رجه&zwnj;م نه&zwnj;وه&zwnj;ی ئاده&zwnj;م، ڕیز کراون له&zwnj;به&zwnj;رده&zwnj;م په&zwnj;روه&zwnj;ردگارا، خوای گه&zwnj;وره ڕوو به&zwnj;کافران ده&zwnj;فه&zwnj;رموێت: سوێند بێت واهاتنه&zwnj;وه بۆ لامان هه&zwnj;ر وه&zwnj;ك له سه&zwnj;ره&zwnj;تادا دروستمان کردوون (به&zwnj;ڕووتی بێ ماڵ و سامان و... هتد) له&zwnj;حاڵێکدا که ئێوه گومانتان وابوو، واشتان ده&zwnj;وت که هه&zwnj;رگیز کاتێکمان دیاری نه&zwnj;کردووه بۆ لێپرسینه&zwnj;وه&zwnj;تان.';
$t[49] = 'نامه&zwnj;ی کرده&zwnj;وه&zwnj;ی هه&zwnj;ر که&zwnj;س داده&zwnj;نرێت، (ئیمانداران دڵخۆشن) به&zwnj;ڵام تاوانباران ده&zwnj;بینیت دڵه له&zwnj;رزێ و دڵه ڕاوکێیانه له کارو کرده&zwnj;وه&zwnj;ی ناپه&zwnj;سه&zwnj;ندی تۆمار کراویان، بۆیه ده&zwnj;ڵێن: هاوار له ئێمه ئه&zwnj;م نامه&zwnj;و دۆسیه&zwnj;یه چیه&zwnj;؟! هیچ گوناهێکی وردو درشت به&zwnj;جێ ناهێڵێت به&zwnj;ڵکو هه&zwnj;مووی تۆمار کردوه&zwnj;، ئه&zwnj;و خه&zwnj;ڵکه هه&zwnj;رچیان کردووه (به&zwnj;ده&zwnj;نگ و ڕه&zwnj;نگه&zwnj;وه&zwnj;) ئاماده&zwnj;یه&zwnj;، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ سته&zwnj;م له هیچ که&zwnj;س ناکات.';
$t[50] = 'ئێمه کاتێك به فریشته&zwnj;کانمان وت سوژده&zwnj;به&zwnj;رن بۆ ئاده&zwnj;م، هه&zwnj;ر هه&zwnj;موو سوژده&zwnj;یان برد جگه له ئیبلیس که له ده&zwnj;سته&zwnj;ی په&zwnj;ریه&zwnj;کان بوو به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه له فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاری یاخی بوو، ئایا ڕه&zwnj;وایه که ئه&zwnj;وو نه&zwnj;وه&zwnj;که&zwnj;ی بکه&zwnj;نه پاڵپشت و یارو یاوه&zwnj;ری خۆتان له جیاتی من، له&zwnj;حاڵێکدا ئه&zwnj;وان دوژمنی هه&zwnj;میشه&zwnj;یی ئێوه&zwnj;ن، ئای که چه&zwnj;نده کارێکی خراپ و ناڕه&zwnj;وایه بۆ سته&zwnj;مکاران که له جیاتی خوا دوژمنێکی وه&zwnj;ك شه&zwnj;یتانیان داناوه&zwnj;و گوێڕایه&zwnj;ڵی ده&zwnj;که&zwnj;ن.';
$t[51] = 'وه&zwnj;نه&zwnj;بێت من له&zwnj;کاتی دروستکردنی ئاسمانه&zwnj;کان و زه&zwnj;ویدا ئه&zwnj;وانم ئاماده کردبێت، بگره هه&zwnj;تا له دروست بوونی خۆشیان ئاگادارنین، به&zwnj;ڕاستی من هه&zwnj;رگیز ئه&zwnj;وانه&zwnj;ی خه&zwnj;ڵك گومڕا ده&zwnj;که&zwnj;ن نه&zwnj;مکردووه به&zwnj;یارمه&zwnj;تیده&zwnj;ری خۆم و پشتم پێ نه&zwnj;به&zwnj;ستوون.';
$t[52] = 'ڕۆژێك دێت که خوا له هاوه&zwnj;ڵگه&zwnj;ران ده&zwnj;پرسێت و پێیان ده&zwnj;ڵێت: کوا ئه&zwnj;و شه&zwnj;ریك و هاوه&zwnj;ڵانه&zwnj;ی که بۆ منتان بڕیار ده&zwnj;دا، جا بانگیان لێ ده&zwnj;که&zwnj;ن و هاواریان لێده&zwnj;که&zwnj;ن، (به&zwnj;ڵام بێ سووده&zwnj;) هه&zwnj;ر وه&zwnj;ڵامیشیان ناده&zwnj;نه&zwnj;وه&zwnj;، ئیتر بۆ هه&zwnj;میشه سزای سه&zwnj;خت ده&zwnj;خه&zwnj;ینه نێوانیانه&zwnj;وه&zwnj;.';
$t[53] = '(ئا له&zwnj;و گیر خواردنه&zwnj;دا) تاوانباران دۆزه&zwnj;خ ده&zwnj;بینن و دڵنیان بێگومان ده&zwnj;چنه ناوی و تێکه&zwnj;ڵی ده&zwnj;بن، هیچ په&zwnj;ناگاو که&zwnj;لێنێکیش نیه که لێی ده&zwnj;ربازبن.';
$t[54] = 'سوێند به خوا بێگومان ئێمه له&zwnj;م قورئانه&zwnj;دا له هه&zwnj;موو جۆره نموونه&zwnj;یه&zwnj;کمان هێناوه&zwnj;ته&zwnj;وه بۆ خه&zwnj;ڵکی (تا په&zwnj;ندو ئامۆژگاری لێ وه&zwnj;ربگرن) له کاتێکدا ئینسان له هه&zwnj;موو شتێك زیاتر موجاده&zwnj;له ده&zwnj;کات و له شت ده&zwnj;کۆڵێته&zwnj;وه&zwnj;.';
$t[55] = 'خه&zwnj;ڵکی خوانه&zwnj;ناس هیچ شتێك نه&zwnj;بووه&zwnj;ته کۆسپ له ڕێیاندا تا باوه&zwnj;ڕ بهێنن کاتێك که هیدایه&zwnj;تیان بۆ ڕه&zwnj;وانه کراوه&zwnj;و داوای لێخۆشبوون بکه&zwnj;ن له په&zwnj;روه&zwnj;ردگاریان، مه&zwnj;گه&zwnj;ر ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت که یاسای پێشینان به&zwnj;سه&zwnj;ریاندا بسه&zwnj;پێت (به له&zwnj;ناو بردنیان) یاخود سزا ڕوو به&zwnj;ڕوویان بێته&zwnj;وه (له قیامه&zwnj;تدا).';
$t[56] = 'ئێمه پێغه&zwnj;مبه&zwnj;ران ته&zwnj;نها به&zwnj;مژده&zwnj;ده&zwnj;رو ترسێنه&zwnj;ر نه&zwnj;بێت نانێرین که&zwnj;چی ئه&zwnj;وانه&zwnj;ی کافرو بێ باوه&zwnj;ڕ بوون هه&zwnj;ر موجاده&zwnj;له&zwnj;و ده&zwnj;مبازی ده&zwnj;که&zwnj;ن به به&zwnj;ڵگه&zwnj;ی پڕوپووچ، بۆ ئه&zwnj;وه&zwnj;ی حه&zwnj;ق و ڕاستی به&zwnj;تاڵ بکه&zwnj;نه&zwnj;وه&zwnj;و بیترازێنن، هه&zwnj;روه&zwnj;ها نایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی من و ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانی پێ هۆشیار کراوه&zwnj;ته&zwnj;وه کردویانه به&zwnj;گاڵته&zwnj;.';
$t[57] = 'کێ له&zwnj;وه سته&zwnj;مکارتره که یاداوه&zwnj;ری کرابێت به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاری، که&zwnj;چی ڕووی لێ وه&zwnj;رگێڕاون و لووتی به&zwnj;رز گرتووه&zwnj;و ئه&zwnj;وه&zwnj;شی له یاد کردووه که چ ده&zwnj;ستپێشکه&zwnj;ریه&zwnj;کی کردووه له گوناهو تاوان، ئێمه به&zwnj;ڕاستی په&zwnj;رده&zwnj;مان هێناوه به&zwnj;سه&zwnj;ر دڵیاندا تا له ئایه&zwnj;ته&zwnj;کانی ئێمه تێنه&zwnj;گه&zwnj;ن و قورسی و که&zwnj;ڕیشمان کردۆته گوێیانه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر بانگیان بکه&zwnj;یت بۆ ڕێبازی هیدایه&zwnj;ت و دینداری، ئه&zwnj;وه هه&zwnj;رگیز ئه&zwnj;وانه ئه&zwnj;و هیدایه&zwnj;ته وه&zwnj;رناگرن.';
$t[58] = 'په&zwnj;روه&zwnj;ردگاری تۆ لێخۆشبووه&zwnj;و خاوه&zwnj;نی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیه&zwnj;، ئه&zwnj;گه&zwnj;ر به&zwnj;کرده&zwnj;وه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه بکات هه&zwnj;موویان به خێرایی ده&zwnj;پێچێته&zwnj;وه&zwnj;و به&zwnj;سزای سه&zwnj;خت دایانده&zwnj;گرێت، به&zwnj;ڵکو کاتی دیاریکراویان بۆ هه&zwnj;یه (هه&zwnj;ر کاتێك یه&zwnj;خه&zwnj;ی پێگرتن) هه&zwnj;رگیز په&zwnj;نایه&zwnj;کیان ده&zwnj;ست ناکه&zwnj;وێت بێجگه له په&zwnj;نای خوا.';
$t[59] = 'ئه&zwnj;وه&zwnj;ته ئه&zwnj;و شارو شارۆچکانه (که باسیانتان بیستووه&zwnj;، یاخود به&zwnj;چاوی خۆتان ده&zwnj;یبینن) له&zwnj;ناومان بردوون و کاولمان کردوون کاتێك که (دانیشتوانی) سته&zwnj;میان کردووه&zwnj;و بۆ له&zwnj;ناوبردنیان کاتی دیاریکراومان بڕیارداوه&zwnj;.';
$t[60] = '(یادیان بێنه&zwnj;) کاتێك موسا به&zwnj;هاوه&zwnj;ڵانی لاوی خۆی وت: من کۆڵ ناده&zwnj;م و ده&zwnj;ڕۆم، ئه&zwnj;گه&zwnj;ر چه&zwnj;نده&zwnj;ها ڕۆژم پێ بچێت تا ده&zwnj;گه&zwnj;مه ئه&zwnj;و شوێنه&zwnj;ی که دوو ده&zwnj;ریاکه یه&zwnj;کده&zwnj;گرن یان ده&zwnj;گه&zwnj;مه ئه&zwnj;نجام، یاخود ماوه&zwnj;یه&zwnj;کی زۆر ده&zwnj;ڕۆم و ده&zwnj;گه&zwnj;ڕێم (خوای گه&zwnj;وره ویستوویه&zwnj;تی موسا پێغه&zwnj;مبه&zwnj;ر علیه السلام تێبگه&zwnj;یه&zwnj;نێت که به&zwnj;نده&zwnj;ی هه&zwnj;یه له بواری تردا شاره&zwnj;زان، له&zwnj;به&zwnj;ر ئه&zwnj;وه ڕێنموویی کردووه تا بگه&zwnj;ڕێت به&zwnj;شوێن ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;دا و له&zwnj;وانیش سوود وه&zwnj;ربگرێت).';
$t[61] = 'کاتێك گه&zwnj;یشتنه ئه&zwnj;و شوێنه&zwnj;ی که دوو ده&zwnj;ریاکه به&zwnj;یه&zwnj;ك ده&zwnj;گه&zwnj;ن، ماسیه&zwnj;کان که له&zwnj;گه&zwnj;ڵ خۆیاندا هه&zwnj;ڵیان گرتبوو بۆ توێشووی سه&zwnj;فه&zwnj;ر، فه&zwnj;رامۆشیان کردبوو، ئه&zwnj;ویش هه&zwnj;ڵبه&zwnj;زی بۆوه&zwnj;و ڕێگه&zwnj;ی ناو ده&zwnj;ریای بۆ خۆی گرته&zwnj;به&zwnj;ر.';
$t[62] = 'جا کاتێك تێپه&zwnj;ڕین (له&zwnj;و شوێنه&zwnj;و، له جێیه&zwnj;کی تر لایاندا) موسا به&zwnj;هاوه&zwnj;ڵه&zwnj;که&zwnj;ی وت: ئاده&zwnj;ی خواردنه&zwnj;که&zwnj;مان بهێنه&zwnj;، چونکه به&zwnj;ڕاستی له&zwnj;م سه&zwnj;فه&zwnj;ره&zwnj;ماندا زۆر هیلاك و ماندوو بووین.';
$t[63] = 'لاوه&zwnj;که وتی: ئه&zwnj;گه&zwnj;ر له بیرت بێت ئه&zwnj;و کاته&zwnj;ی که چووینه سه&zwnj;ر به&zwnj;رده گه&zwnj;وره&zwnj;که (بۆ حه&zwnj;وانه&zwnj;وه&zwnj;) من بیرم چوو باسی ماسیه&zwnj;که&zwnj;ت بۆ بکه&zwnj;م، که&zwnj;سیش له بیری نه&zwnj;بردمه&zwnj;وه شه&zwnj;یتان نه&zwnj;بێت، که بۆتی باس بکه&zwnj;م، له&zwnj;ڕاستیدا ئه&zwnj;و ماسیه (زیندوو بۆوه&zwnj;و) به شێوه&zwnj;یه&zwnj;کی زۆر سه&zwnj;یر خۆی کرد به&zwnj;ناو ده&zwnj;ریادا.';
$t[64] = 'موسا علیه السلام وتی: هه&zwnj;ر ئه&zwnj;وه&zwnj;بوو که ئێمه ده&zwnj;مانویست (واته&zwnj;: ده&zwnj;بوو ئا له&zwnj;و شوێنه&zwnj;دا چاوه&zwnj;ڕێ بوینایه&zwnj;) ئیتر به ناچاری گه&zwnj;ڕانه&zwnj;وه بۆ ئه&zwnj;و شوێنه&zwnj;ی که به جێیان هێشتبوو.';
$t[65] = 'له&zwnj;وێ به به&zwnj;نده&zwnj;یه&zwnj;ك له به&zwnj;نده&zwnj;کانی ئێمه ئاشنا بوو که له ڕه&zwnj;حمه&zwnj;تی تایبه&zwnj;تی خۆمان به&zwnj;هره&zwnj;وه&zwnj;رمان کردبووو زانست و زانیاری تایبه&zwnj;تی خۆمانمان پێ به&zwnj;خشیبوو، موسا علیه السلام پێی وت: ئایا مۆڵه&zwnj;تم ده&zwnj;ده&zwnj;یت که شوێنت بکه&zwnj;وم تا له&zwnj;و زانست و زانیاریه&zwnj;ی پێت به&zwnj;خشراوه منیش به&zwnj;هره&zwnj;دار بکه&zwnj;یت؟';
$t[66] = 'موسا (علیه وسلام) پێی گوت: ئاخۆ مۆڵتم ده&zwnj;ده&zwnj;یت شوێنت بکه&zwnj;وم؟ تا له&zwnj;و زانسته&zwnj;ی -له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;- فێرکراویت، منیش فێر بکه&zwnj;یت و نیشانمی بده&zwnj;ی، به&zwnj;ڵکو که&zwnj;ڵکی لێ وه&zwnj;رگرم له کاروبارمدا.';
$t[67] = 'زاناکه وتی: به&zwnj;ڕاستی تۆ هه&zwnj;رگیز ناتوانیت له&zwnj;گه&zwnj;ڵ مندا خۆڕاگر بیت (بێده&zwnj;نگ بیت به&zwnj;رامبه&zwnj;ر ڕه&zwnj;فیتارو کردارم، چونکه من شتی سه&zwnj;رسوڕهێنه&zwnj;ر ده&zwnj;که&zwnj;م و تۆ له نهێنیه&zwnj;که&zwnj;ی تێناگه&zwnj;یت).';
$t[68] = 'جا چۆن خۆده&zwnj;گریت له به&zwnj;رامبه&zwnj;ر شتێکه&zwnj;وه که هه&zwnj;واڵت لێی نیه و ئه&zwnj;م سه&zwnj;رو ئه&zwnj;و سه&zwnj;ری نازانیت.';
$t[69] = 'موسا وتی: ده&zwnj;مبینیت ئه&zwnj;گه&zwnj;ر خوا ویستی له&zwnj;سه&zwnj;ر بێت خۆڕاگر ده&zwnj;بم و له هیچ فه&zwnj;رمانێکت ده&zwnj;رناچم.';
$t[70] = 'زاناکه وتی: جا ئه&zwnj;گه&zwnj;ر شوێنم که&zwnj;وتیت ئه&zwnj;وه پرسیارم له&zwnj;هیچ شتێك لێ مه&zwnj;که&zwnj;، هه&zwnj;تا خۆم له نهێنیه&zwnj;که&zwnj;ی ئاگادارت نه&zwnj;که&zwnj;م.';
$t[71] = 'هه&zwnj;تا کاتێك پێکه&zwnj;وه ده&zwnj;رچوون و سواری که&zwnj;شتیه&zwnj;که بوو&zwnj;ن، زاناکه ده&zwnj;ستی کرد به کون کردن و عه&zwnj;یبدار کردنی، موسا (خۆی پێنه&zwnj;گیراو) وتی: باشه&zwnj;، ئاخر، ئه&zwnj;وه تۆ ئه&zwnj;م که&zwnj;شتیه کون ده&zwnj;که&zwnj;یت، ده&zwnj;ته&zwnj;وێت هه&zwnj;رچی سه&zwnj;رنشینه&zwnj;کانی هه&zwnj;یه نوقمی بکه&zwnj;یت، سوێند به&zwnj;خوا به&zwnj;ڕاستی کارێکی ناشایسته&zwnj;ی گه&zwnj;وره&zwnj;ت کرد!!';
$t[72] = 'زاناکه وتی: نه&zwnj;م وت: تۆ هه&zwnj;رگیز ناتوانیت له&zwnj;گه&zwnj;ڵ مندا خۆڕاگر بیت!!.';
$t[73] = 'موسا وتی: لێم مه&zwnj;گره چونکه مه&zwnj;رجه&zwnj;که&zwnj;م فه&zwnj;رامۆش کردو فێربوونی ئه&zwnj;م زانیاریه&zwnj;ی که ده&zwnj;مه&zwnj;وێت له تۆوه فێربم له&zwnj;سه&zwnj;رم قورس و گران مه&zwnj;که&zwnj;.';
$t[74] = '(دوایی دابه&zwnj;زینه سه&zwnj;ر وشکانی) که&zwnj;وتنه ڕێ، هه&zwnj;تا گه&zwnj;یشتنه منداڵێك، زاناکه گرتی و کوشتی، موسا وتی: سه&zwnj;یره&zwnj;، ئه&zwnj;وه بۆچی که&zwnj;سێکی بێ گوناهت کوشت بێ ئه&zwnj;وه&zwnj;ی ئه&zwnj;و تاوانێکی وای کردبێت و (شایسته&zwnj;ی کوشتن بێت) سوێند به&zwnj;خوا به&zwnj;ڕاستی کارێکی ناپه&zwnj;سه&zwnj;ندو ناڕه&zwnj;وات ئه&zwnj;نجامدا!!';
$t[75] = 'زاناکه وتی: پێم نه&zwnj;وتیت که به&zwnj;ڕاستی تۆ هه&zwnj;رگیز ناتوانیت له&zwnj;گه&zwnj;ڵ مندا ئارام بگریت و دان به&zwnj;خۆتدا بگریت.';
$t[76] = 'موسا وتی: ئه&zwnj;گه&zwnj;ر دوای ئه&zwnj;مه پرسیاری هه&zwnj;ر شتێکی ترم لێ کردیت، ئه&zwnj;وه هاوڕێیه&zwnj;تیم مه&zwnj;که&zwnj;، به&zwnj;ڕاستی به&zwnj;ڕای من مافی خۆته و له&zwnj;ڵایه&zwnj;ن منه&zwnj;وه لێ بوردراویت (چونکه من به&zwnj;ئاسانی له هه&zwnj;ڵسوکه&zwnj;وتی تۆ تێناگه&zwnj;م).';
$t[77] = 'پاشان ده&zwnj;رچوون و ڕۆشتن هه&zwnj;تا گه&zwnj;یشتنه ناو خه&zwnj;ڵکی شارۆچکه&zwnj;یه&zwnj;ک (برسیان بوو) داوای خۆراکیان له خاڵکه&zwnj;که کرد، که&zwnj;چی ده&zwnj;ستیان نا به ڕوویانه&zwnj;وه و میوانداریان لێ نه&zwnj;کردن، ئه&zwnj;وسا له&zwnj;ناو شاره&zwnj;که&zwnj;دا دیوارێکیان به&zwnj;دی کرد خه&zwnj;ریکبوو ده&zwnj;ڕوخا، به&zwnj;ڵام زاناکه هه&zwnj;ڵیچنیه&zwnj;وه&zwnj;و چاکی کرده&zwnj;وه&zwnj;و به&zwnj;رزی کرده&zwnj;وه&zwnj;، موسا وتی: ئه&zwnj;گه&zwnj;ر بتویستایه&zwnj;، ئه&zwnj;وه کرێیه&zwnj;کت به&zwnj;رامبه&zwnj;ر ئه&zwnj;م کاره وه&zwnj;رده&zwnj;گرت (ده&zwnj;ماندا به&zwnj;خۆراک پیی تێر ده&zwnj;بووین).';
$t[78] = 'زاناکه وتی: ئیتر ئالێره به دواوه کاتی جیابوونه&zwnj;وه&zwnj;ی نێوانی من و تۆیه&zwnj;، من تاوێکی تر تۆ ئاگادار ده&zwnj;که&zwnj;م له حه&zwnj;قیقه&zwnj;ت و سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و شتانه&zwnj;ی که نه&zwnj;تتوانی ئارام بگریت له به&zwnj;رامبه&zwnj;ریانه&zwnj;وه&zwnj;.';
$t[79] = 'ئه&zwnj;و که&zwnj;شتیه&zwnj;ی (عه&zwnj;یبدارم کرد چه&zwnj;ند هه&zwnj;ژارێک خاوه&zwnj;نی بوون له ده&zwnj;ریادا کاریان ده&zwnj;کرد، جا منیش ویستم عه&zwnj;یبداری بکه&zwnj;م، له&zwnj;کاتێکدا له&zwnj;و ناوه&zwnj;دا پاشایه&zwnj;ک هه&zwnj;بوو که هه&zwnj;موو که&zwnj;شتیه&zwnj;کی (چاک و ڕێکوپێکی) داگیر ده&zwnj;کرد.';
$t[80] = 'منداڵه&zwnj;که&zwnj;ش (که کوشتم) دایک و باوکێکی ئیمانداری هه&zwnj;بوو، ترساین له&zwnj;وه&zwnj;ی که به&zwnj;سه&zwnj;رکه&zwnj;شی و بێ دینی و خوانه&zwnj;ناسی هیلاک و ماندویان بکات (چونکه هه&zwnj;ر له ئێستاوه سه&zwnj;رکه&zwnj;شی و لاساریی پێوه دیار بوو).';
$t[81] = '(به&zwnj;و کاره&zwnj;مان) ویستمان که په&zwnj;روه&zwnj;ردگاریان (له&zwnj;جیاتی ئه&zwnj;و) پاکترو چاکتریان پێ ببه&zwnj;خشێت و به&zwnj;منداڵێکی خاوه&zwnj;ن به&zwnj;زه&zwnj;یی و میهرابانتر به&zwnj;هره&zwnj;وه&zwnj;ریان بکات (منداڵه&zwnj;که&zwnj;ش به&zwnj;هه&zwnj;شتی بێت).';
$t[82] = 'دیواره&zwnj;که&zwnj;ش که چاکم کرده&zwnj;وه&zwnj;، خاوه&zwnj;نه&zwnj;که&zwnj;ی دوو منداڵی هه&zwnj;تیووی شارۆچکه&zwnj;که بوون، له&zwnj;ژێریدا گه&zwnj;نجینه&zwnj;یه&zwnj;کی (زێڕو زیویان) هه&zwnj;بوو (که باوکیان بۆی شاردبوونه&zwnj;وه&zwnj;، ئه&zwnj;گه&zwnj;ر گوێمان پێ نه&zwnj;دایه&zwnj;، باران و لافاو ده&zwnj;ری ده&zwnj;خست و ئه&zwnj;و خه ڵکه ڕه&zwnj;زیله&zwnj;ی که میوانداریان لێ نه&zwnj;کردین&zwnj;، ده&zwnj;ستیان ده&zwnj;که&zwnj;وت)، باوکی ئه دوو منداڵه&zwnj;ش خواناس و چاک بوو، جا په&zwnj;روه&zwnj;ردگارت ویستی کاتێک گه&zwnj;نجینه&zwnj;که&zwnj;یان ده&zwnj;ست بکه&zwnj;وێت که گه&zwnj;وره و به&zwnj;هێزبن، ئه&zwnj;وسا ده&zwnj;ری بهێنن، ئه&zwnj;مه&zwnj;ش وه&zwnj;کو ڕه&zwnj;حمه&zwnj;تێکی تایبه&zwnj;تی له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه (له&zwnj;به&zwnj;ر خاتری باوکیان)، دڵنیاشبه که هه&zwnj;موو ئه&zwnj;و کارانه&zwnj;م له خۆوه نه&zwnj;کردووه&zwnj;، ئیتر ئه&zwnj;وه بوو ڕاز و نهێنی ئه&zwnj;و شتانه&zwnj;ی که خۆت بۆ نه&zwnj;ده&zwnj;گیرا کاتێک ئانجامم ده&zwnj;دان.';
$t[83] = 'ده&zwnj;رباره&zwnj;ی (ذو القرنین) یش پرسیارت لێ ده&zwnj;که&zwnj;ن، پێیان بڵێ: گۆشه&zwnj;یه&zwnj;ک له به&zwnj;سه&zwnj;رهاتی ئه&zwnj;وتان بۆ باس ده&zwnj;که&zwnj;م.';
$t[84] = 'ئێمه توانا و ده&zwnj;سه&zwnj;ڵاتمان له زه&zwnj;ویدا پێ به&zwnj;خشی بوو، بۆ هه&zwnj;رشتێکی پێویستی ئه&zwnj;و سه&zwnj;رده&zwnj;مه هۆکاره&zwnj;که&zwnj;یمان بۆ ڕه&zwnj;خساندبوو.';
$t[85] = 'ئه&zwnj;ویش که&zwnj;ڵکی ته&zwnj;واوی له هۆکاره&zwnj;کان وه&zwnj;رده&zwnj;گرت.';
$t[86] = '(خۆی و له&zwnj;شکره&zwnj;که&zwnj;ی) که&zwnj;وته ڕێ هه&zwnj;تا کاتێک گه&zwnj;یشته ناوچه&zwnj;کانی خۆرئاوا، (که&zwnj;وا ده&zwnj;هاته به&zwnj;رچاو) له گۆمێکی قوڕاودا، ڕۆژ، ئاوا ده&zwnj;بێت، له&zwnj;و ناوه&zwnj;دا ڕێی که&zwnj;وت له قه&zwnj;ومێکی (خوانه&zwnj;ناس و بێ دین)، ئێمه&zwnj;ش پێمان وت: ئه&zwnj;ی (ذو القرنین) یان ئه&zwnj;وه&zwnj;یه به سزا له&zwnj;ناویان ده&zwnj;به&zwnj;یت (ئه&zwnj;گه&zwnj;ر له&zwnj;سه&zwnj;ر کوفر و تاوانیان به&zwnj;رده&zwnj;وام بوون) یان چاکه&zwnj;یان له&zwnj;گه&zwnj;ڵدا ده&zwnj;که&zwnj;یت (ئه&zwnj;گه&zwnj;ر شوێنی ئیمان و چاکه نه&zwnj;که&zwnj;وتن).';
$t[87] = '(ذو القرنین) وتی: بێگومان ئه&zwnj;وه&zwnj;ی سته&zwnj;مکار بێت ئێمه سزای ده&zwnj;ده&zwnj;ین، له&zwnj;وه&zwnj;ودوایش که گه&zwnj;ڕایه&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگاری سزایه&zwnj;کی زۆر سه&zwnj;خت و گرانی ده&zwnj;دات.';
$t[88] = 'به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی ئیمان و باوه&zwnj;ڕ بهێنێت و کارو کرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام بدات، ئه&zwnj;وه پاداشتی چاک بۆ ئه&zwnj;وه&zwnj;، له&zwnj;مه&zwnj;ودوا قسه&zwnj;ی خۆش و فه&zwnj;رمانی ئاسان و کاری سووکی ده&zwnj;ده&zwnj;ین به&zwnj;سه&zwnj;ردا.';
$t[89] = 'له&zwnj;وه&zwnj;ودوا که&zwnj;ڵکی ته&zwnj;واوی له هۆکاره&zwnj;کان وه&zwnj;رگرت و به&zwnj;شوێنیاندا گه&zwnj;ڕا.';
$t[90] = 'هه&zwnj;تا کاتێک گه&zwnj;یشته ناوچه&zwnj;ی خۆرهه&zwnj;ڵاتی (دوور)، بینی خۆر هه&zwnj;ڵدێت له&zwnj;سه&zwnj;ر قه&zwnj;ومێک که په&zwnj;ناگایه&zwnj;کیان نیه تا له&zwnj;تینی خۆیان بپارێزن.';
$t[91] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ڕه&zwnj;فتاری کرد، که له&zwnj;گه&zwnj;ڵ خه&zwnj;ڵکی خۆرئاوادا کردی، بێگومان ئێمه به&zwnj;ته&zwnj;واویی ئاگاداربووین له&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;دا.';
$t[92] = 'له&zwnj;وه&zwnj;ودوا هه&zwnj;ر ده&zwnj;ستبه&zwnj;رداری هۆکاره&zwnj;کان نه&zwnj;بوو، به&zwnj;شوێنیاندا گه&zwnj;ڕا.';
$t[93] = 'هه&zwnj;تا کاتێک گه&zwnj;یشته نێوان هه&zwnj;ردوو به&zwnj;ربه&zwnj;سته&zwnj;که&zwnj;، که له نێوانیاندا قه&zwnj;ومێک هه&zwnj;بوو که له هیچ قسه&zwnj;یه&zwnj;ک تێ نه&zwnj;ده&zwnj;گه&zwnj;یشتن (مه&zwnj;گه&zwnj;ر زۆر به&zwnj;ده&zwnj;گمه&zwnj;ن).';
$t[94] = 'کاتێک ده&zwnj;سه&zwnj;ڵاتی (ذو القرنین) یان بینی وتیان: ئه&zwnj;ی (ذو القرنین) به&zwnj;ڕاستی یه&zwnj;ئجوج و مه&zwnj;ئجوج تۆوی خراپه و تاوان ده&zwnj;چێنن له زه&zwnj;ویدا، تا به&zwnj;ربه&zwnj;ستێک له نێوان ئێمه و ئه&zwnj;واندا دروست بکه&zwnj;یت؟';
$t[95] = '(ذو القرنین) وتی: ئه&zwnj;و ده&zwnj;سه&zwnj;ڵات و زانیاری و شتانه&zwnj;ی په&zwnj;روه&zwnj;ردگارم پێی به&zwnj;خشیووم چاکتره بۆ من (له پاره و سامان) جا ئێوه به&zwnj;هێزی بازوو یارمه&zwnj;تیم بده&zwnj;ن تا به&zwnj;ربه&zwnj;ستێکی به&zwnj;هێز له نێوان ئێوه و ئه&zwnj;واندا دروست بکه&zwnj;م.';
$t[96] = 'پارچه ئاسنم بۆ بهێنن و (که&zwnj;ڵه&zwnj;که&zwnj;ی بکه&zwnj;ن) هه&zwnj;تا ئه&zwnj;مبه&zwnj;رو ئه&zwnj;وبه&zwnj;ری هه&zwnj;ردوو کێوه&zwnj;که&zwnj;ی پڕ کرد، ئه&zwnj;وسا فه&zwnj;رمانیدا، تا به (موشه&zwnj;ده&zwnj;مه&zwnj;) فوو بکه&zwnj;ن له&zwnj;و ئاگره&zwnj;ی که له ژێریدا کرابۆوه&zwnj;، هه&zwnj;تا کاتێک هه&zwnj;مووی کرد به ئاگر، ئینجا (ذو القرنین) وتی: ئاده&zwnj;ی مسی تواوه&zwnj;م بۆ بهێنن تا بیکه&zwnj;م به&zwnj;سه&zwnj;ریدا (مس له&zwnj;گه&zwnj;ڵ ئاسندا تێکه&zwnj;ڵ بکرێت پێکهاته&zwnj;یه&zwnj;کی به&zwnj;هێزی لێ دروست ده&zwnj;بێت).';
$t[97] = 'ئه&zwnj;و&zwnj;سا ئیتر (یه&zwnj;ئجوج و مه&zwnj;ئجوج) نه توانیان سه&zwnj;ربکه&zwnj;ون، نه توانیان کوناو ده&zwnj;ری بکه&zwnj;ن.';
$t[98] = '(ئه&zwnj;وسا ذو القرنین) وتی: دروستکردنی ئه&zwnj;م به&zwnj;ربه&zwnj;سته ڕه&zwnj;حمه&zwnj;تێک بوو له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه&zwnj;، ئینجا کاتێک په&zwnj;روه&zwnj;ردگارم بڕیاری تێکدانی بدات، ورد و خاشی ده&zwnj;کات، بێگومان به&zwnj;ڵێنی په&zwnj;روه&zwnj;ردگارم حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;.';
$t[99] = 'له&zwnj;و ڕۆژه به&zwnj;دواوه وازمان لێهێنان تا وه&zwnj;ک شه&zwnj;پۆل به&zwnj;ناو یه&zwnj;کدا بێن، یاخود وازمان لێهێنان بۆ ئه&zwnj;و ڕۆژه&zwnj;ی (که به&zwnj;ربه&zwnj;سته&zwnj;که ده&zwnj;شکێت و به په&zwnj;له&zwnj;) وه&zwnj;کو لێشاودێنه ده&zwnj;رێ و شه&zwnj;پۆل ده&zwnj;ده&zwnj;ن به&zwnj;ناو یه&zwnj;کدا (که یه&zwnj;کێکه له نیشانه&zwnj;کانی کۆتایی هاتنی دنیا)، ئه&zwnj;وسا فوو ده&zwnj;کرێت به - صور - دا، به&zwnj;و هۆیه&zwnj;وه هه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی به ته&zwnj;واوی کۆ ده&zwnj;که&zwnj;ینه&zwnj;وه (بۆ موحاسه&zwnj;به و لێپرسینه&zwnj;وه&zwnj;).';
$t[100] = 'ئیتر ئه&zwnj;و ڕۆژه دۆزه&zwnj;خ نیشانی کافران ده&zwnj;ده&zwnj;ین و به&zwnj;ته&zwnj;واوی ده&zwnj;ری ده&zwnj;خه&zwnj;ین (هه&zwnj;مووشیانی تیادا کۆ ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;).';
$t[101] = 'ئه&zwnj;وانه&zwnj;ی که په&zwnj;رده به&zwnj;سه&zwnj;ر چاویاندا هاتبوو (نه&zwnj;یانده&zwnj;ویست نیشانه&zwnj;کانی ده&zwnj;سه&zwnj;ڵاتداری ئێمه ببینن) و یادی ئێمه بکه&zwnj;ن و توانای بیستنی (قورئانی ئێمه&zwnj;یان نه&zwnj;بوو، حه&zwnj;زیان لێ نه&zwnj;ده&zwnj;کرد).';
$t[102] = 'ئایا ئه&zwnj;وانه&zwnj;ی کافرو بێ باوه&zwnj;ڕ بوون وا ده&zwnj;زانن که هه&zwnj;روا بۆیان ده&zwnj;چێته سه&zwnj;رو چاوپۆشییان لێ ده&zwnj;که&zwnj;ین له کاتێکدا که ئه&zwnj;وان به&zwnj;نده&zwnj;کانی ئێمه ده&zwnj;که&zwnj;نه یارو یاوه&zwnj;ری خۆیان و پشت به&zwnj;وان ده&zwnj;به&zwnj;ستن و ئێمه فه&zwnj;رامۆش ده&zwnj;که&zwnj;ن، ئه&zwnj;وانه بابزانن که به&zwnj;ڕاستی ئێمه دۆزه&zwnj;خمان ئاماده&zwnj;کردووه بۆ بێ باوه&zwnj;ڕان که&zwnj;له&zwnj;وێ داده&zwnj;به&zwnj;زن و نیشته&zwnj;جێ ده&zwnj;بن.';
$t[103] = 'ئه&zwnj;ی محمد (صلی الله علیه و سلم) پێیان بڵێ: ئایا هه&zwnj;واڵی ئه&zwnj;و که&zwnj;سانه&zwnj;تان بده&zwnj;مێ که له کارو کرده&zwnj;وه&zwnj;یاندا زۆر زه&zwnj;ره&zwnj;رمه&zwnj;ند و خه&zwnj;ساره&zwnj;تمه&zwnj;ندن؟!';
$t[104] = '(ئه&zwnj;وانه&zwnj;) ئه&zwnj;و که&zwnj;سانه&zwnj;ن که هه&zwnj;وڵ و کۆششیان له&zwnj;ژیانی دنیادا، به&zwnj;زایه و به&zwnj;هه&zwnj;ده&zwnj;ر چووه به&zwnj;مه&zwnj;رجێک وایان ده&zwnj;زانی که به&zwnj;ڕاستی کاری چاک ده&zwnj;که&zwnj;ن و کرده&zwnj;وه&zwnj;ی ڕه&zwnj;وا ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن.';
$t[105] = 'ئه&zwnj;وانه که&zwnj;سانێکن که باوه&zwnj;ڕیان به به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان نه&zwnj;بووه&zwnj;و به&zwnj;نیازی ئاماده&zwnj;بوونی به&zwnj;رده&zwnj;م باره&zwnj;گای خوایی نه&zwnj;بوون، ئیتر هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;کانیان بێ نرخ و پووچ بووه&zwnj;وه&zwnj;، له ڕۆژی قیامه&zwnj;تیشدا هیچ جۆره بایه&zwnj;خ و نرخێکیان بۆ دانانێین.';
$t[106] = 'ئه&zwnj;وانه پاداشتیان ئاگری دۆزه&zwnj;خه به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که باوه&zwnj;ڕیان نه&zwnj;بوو و گاڵته&zwnj;جاڕیان به&zwnj;به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کان و پێغه&zwnj;مبه&zwnj;رانی ئێمه&zwnj;کرد.';
$t[107] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه&zwnj;و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، ئێمه هه&zwnj;ر له زووه&zwnj;وه به به&zwnj;هه&zwnj;شتی فیرده&zwnj;وسمان بۆ ئاماده کردوون، تا له&zwnj;وێ دابه&zwnj;زن و ژیانی کامه&zwnj;رانی تیایدا به&zwnj;رنه&zwnj;سه&zwnj;ر.';
$t[108] = 'ژیانیان تیایدا به&zwnj;رده&zwnj;وام و نه&zwnj;بڕاوه&zwnj;یه&zwnj;، نایانه&zwnj;وێت بگوێزرێنه&zwnj;وه بۆ شوێنێکی تر (له&zwnj;به&zwnj;ر چاویان ناکه&zwnj;وێت).';
$t[109] = 'پێیان بڵێ: ئه&zwnj;گه&zwnj;ر ده&zwnj;ریا هه&zwnj;مووی مره&zwnj;که&zwnj;ب بێت و زانست و زانیاری په&zwnj;روه&zwnj;ردگارمی پێ بنووسرایه&zwnj;، ده&zwnj;ریا ته&zwnj;واو ده&zwnj;بوو پێش ئه&zwnj;وه&zwnj;ی زانست و زانیاری په&zwnj;روه&zwnj;ردگارم ته&zwnj;واو ببێت، ئه&zwnj;گه&zwnj;ر چه&zwnj;نده&zwnj;ها ئه&zwnj;وه&zwnj;نده&zwnj;ی تر ده&zwnj;ریاش بهێنن و بیکه&zwnj;نه مره&zwnj;که&zwnj;ب، چونکه زانست و زانیاری په&zwnj;روه&zwnj;ردگار کۆتایی نایه و بێ سنووره&zwnj;.';
$t[110] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم) پێیان) بڵێ: به&zwnj;ڕاستی من ته&zwnj;نها ئاده&zwnj;میزادێکم وه&zwnj;ک ئێوه&zwnj;، به&zwnj;ڵام ئه&zwnj;وه&zwnj;نده هه&zwnj;یه وه&zwnj;حی و نیگام بۆ دێت (ئه&zwnj;م ڕاستیه&zwnj;تان تێبگه&zwnj;یه&zwnj;نم) که&zwnj;: بێگومان خوای ئێوه خوایه&zwnj;کی تاک و ته&zwnj;نهایه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ده&zwnj;یه&zwnj;وێت به&zwnj;دیداری په&zwnj;روه&zwnj;ردگاری شادببێت، به&zwnj;خۆ&zwnj;شنودیه&zwnj;وه با کارو کرده&zwnj;وه&zwnj;ی چاک و دروست ئه&zwnj;نجام بدات و به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک له په&zwnj;رستندا هیچ که&zwnj;سێک نه&zwnj;کاته هاوه&zwnj;ڵ و شه&zwnj;ریک بۆی.';
?>