$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم)، ئه&zwnj;م باسه&zwnj;) باسی ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگارته&zwnj;، ده&zwnj;رباره&zwnj;ی به&zwnj;نده&zwnj;ی خۆی زه&zwnj;که&zwnj;ریا.';
$t[3] = 'کاتێک له (په&zwnj;رستگای خۆیدا) هانا و هاواری برد بۆ په&zwnj;روه&zwnj;ردگاری به&zwnj;په&zwnj;نهانی.';
$t[4] = 'وتی: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی من ئێسکه&zwnj;کانم لاواز بوون و قژی سه&zwnj;رم هه&zwnj;ر هه&zwnj;مووی سپێتی دایگرتووه&zwnj;، به&zwnj;ڵام من هه&zwnj;رگیز له دوعاو پاڕانه&zwnj;وه&zwnj;م له زاتی تۆ نائومێد نه&zwnj;بووم و (ده&zwnj;ستت نه&zwnj;ناوه به&zwnj;ڕوومه&zwnj;وه&zwnj;).';
$t[5] = 'به&zwnj;ڕاستی من له&zwnj;وه ترساوه&zwnj;م که خزم و خوێشان عه&zwnj;شره&zwnj;ت دوای مردنم سه&zwnj;گه&zwnj;ردان بن، (چونکه که&zwnj;سێکی وایان تیادا به&zwnj;دی ناکه&zwnj;م که شایسته&zwnj;ی گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;ت بێت، خۆت ده&zwnj;زانیت) خێزانکه&zwnj;شم منداڵی نابێت، بۆیه داواکارم له له&zwnj;به&zwnj;خششی خۆت به&zwnj;هره&zwnj;وه&zwnj;رم بکه و منداڵێکم پێ ببه&zwnj;خشیت که بتوانێت جێگه&zwnj;ی من بگرێته&zwnj;وه&zwnj;.';
$t[6] = 'تا میراتی پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی من و نه&zwnj;وه&zwnj;ی یه&zwnj;عقوب وه&zwnj;رربگرێت، هه&zwnj;روه&zwnj;ها په&zwnj;روه&zwnj;ردگارا بیکه به&zwnj;که&zwnj;سێکی وا، که&zwnj;تۆش و به&zwnj;نده چاکه&zwnj;کانیشت لێی ڕازی بن (ئه&zwnj;وه&zwnj;نده چاکه&zwnj;کارو چاکه&zwnj;خوازو دیندار بێت).';
$t[7] = 'خوای گه&zwnj;وره نزاکه&zwnj;ی گیراکردو فه&zwnj;رمووی: ئه&zwnj;ی زه&zwnj;که&zwnj;ریا به&zwnj;ڕاستی ئێمه مژده&zwnj;ت ده&zwnj;ده&zwnj;ینێ به&zwnj;وه&zwnj;ی که کوڕێکت پێ ده&zwnj;به&zwnj;خشین و ناویشی - یه&zwnj;حیا - یه&zwnj;، پێش ئه&zwnj;و که&zwnj;سمان هاوناوی ئه&zwnj;و نه&zwnj;کردووه&zwnj;.';
$t[8] = 'زه&zwnj;که&zwnj;ریا وتی: په&zwnj;روه&zwnj;ردگارا به چ شێوه&zwnj;یه&zwnj;ک کوڕم پێ ده&zwnj;به&zwnj;خشیت، له&zwnj;کاتێکدا هاوسه&zwnj;ره&zwnj;که&zwnj;شم منداڵی نابێت و منیش گه&zwnj;یشتوومه&zwnj;ته ئه&zwnj;وپه&zwnj;ڕی پیری و په&zwnj;ککه&zwnj;وته&zwnj;یی (به&zwnj;ڵام هه&zwnj;موو شت لای تۆ ئاسانه&zwnj;).';
$t[9] = 'وتی: په&zwnj;روه&zwnj;ردگاری تۆ هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه بڕیاری داوه و فه&zwnj;رمووشیه&zwnj;تی ئه&zwnj;وکاره لای من زۆر ئاسانه&zwnj;، به&zwnj;ڕاستی پێش تریش تۆم دروستکردووه، وه&zwnj;نه&zwnj;بێت شتێکت بوو بیت (نه ناوت هه&zwnj;بووه نه بوون)...';
$t[10] = 'زه&zwnj;که&zwnj;ریا وتی: په&zwnj;روه&zwnj;ردگارا نیشانه&zwnj;یه&zwnj;کم پێ ببه&zwnj;خشه تا&zwnj;دڵنیابم، که ئه&zwnj;و مژده&zwnj;یه هاتۆته&zwnj;دی، خوای گه&zwnj;وره فه&zwnj;رمووی: نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;ت ئه&zwnj;وه&zwnj;یه (له&zwnj;سه&zwnj;ر بوونی ئه&zwnj;و کوڕه&zwnj;) که تۆ ناتوانیت سێ شه&zwnj;وی ته&zwnj;واو (به ڕۆژه&zwnj;کانیه&zwnj;وه&zwnj;) قسه له&zwnj;گه&zwnj;ڵ خه&zwnj;ڵکیدا بکه&zwnj;یت!';
$t[11] = '(ئه&zwnj;وسا دوای دڵنیابوون) له په&zwnj;رستگاکه&zwnj;ی هاته ده&zwnj;ره&zwnj;وه بۆ ناو قه&zwnj;ومه&zwnj;که&zwnj;ی و ئاماژه&zwnj;ی بۆ کردن (که له به&zwnj;رامبه&zwnj;ر نازو نیعمه&zwnj;ت و به&zwnj;خششه&zwnj;کانی خواوه&zwnj;) به&zwnj;یانیان و ئێواران ته&zwnj;سبیحات و ستایش بکه&zwnj;ن.';
$t[12] = '(پاشان که یه&zwnj;حیا گه&zwnj;وره بوو فه&zwnj;رمانمان پێدا) ئه&zwnj;ی یه&zwnj;حیا کتێبی (ته&zwnj;ورات) به&zwnj;توندی بگره&zwnj;و پابه&zwnj;ندی به&zwnj;و هه&zwnj;ر له ته&zwnj;مه&zwnj;نی نه&zwnj;وجه&zwnj;وانیدا، توانای جیاکردنه&zwnj;وه&zwnj;ی ڕاست و ناڕاستمان پێبه&zwnj;خشی، به دادوه&zwnj;رێکی ڕێک و پێک کێشه&zwnj;ی خه&zwnj;ڵکه&zwnj;که&zwnj;ی چاره&zwnj;سه&zwnj;ر ده&zwnj;کرد.';
$t[13] = 'یه&zwnj;حیا دڵ و ده&zwnj;روونێکی پڕ له سۆزو خۆشه&zwnj;ویستی و پاک و خاوێنی هه&zwnj;بوو که له&zwnj;لایه&zwnj;ن خۆمانه&zwnj;وه پێمان به&zwnj;خشیبوو و ئه&zwnj;و زۆر خواناس و پارێزگار بوو.';
$t[14] = 'هه&zwnj;روه&zwnj;ها چاک ڕه&zwnj;فتارو جوان ڕه&zwnj;فتار بوو له&zwnj;گه&zwnj;ڵ باوک و دایکیدا، وه&zwnj;نه&zwnj;بێت ئه&zwnj;و که&zwnj;سێکی دڵره&zwnj;ق و یاخی و ناله&zwnj;بار بوو بێت.';
$t[15] = 'درودو سه&zwnj;لامی خوای له&zwnj;سه&zwnj;ر بیت له ڕۆژه&zwnj;دا که له دایک بوو و له&zwnj;و ڕۆژه&zwnj;دا که وه&zwnj;فات ده&zwnj;کات، له&zwnj;و ڕۆژه&zwnj;شدا که هه&zwnj;ڵده&zwnj;ستێت و زیندوو ده&zwnj;بێته&zwnj;وه&zwnj;.';
$t[16] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) له قورئاندا باسی به&zwnj;سه&zwnj;رهاتی مه&zwnj;ریه&zwnj;م یاد بکه&zwnj;، کاتێک که&zwnj;نارگیری کرد له که&zwnj;سوکاری و شوێنێکی له ڕۆژهه&zwnj;ڵاتی (بیت المقدس) هه&zwnj;ڵبژارد (بۆ خواپه&zwnj;رستی).';
$t[17] = 'ئینجا له&zwnj;نێوان خۆی و ئه&zwnj;واندا په&zwnj;رده&zwnj;یه&zwnj;کی ڕایه&zwnj;ڵ کرد (تادوور له&zwnj;خه&zwnj;ڵکی به&zwnj;سۆزه&zwnj;وه خواپه&zwnj;رستی بکات) ئێمه&zwnj;ش جو&zwnj;بره&zwnj;ئیلی فریشته&zwnj;مان نارد بۆ لای، وه&zwnj;کو به&zwnj;شه&zwnj;رێکی ته&zwnj;واو (خۆی خسته به&zwnj;رچاوی).';
$t[18] = 'مه&zwnj;ریه&zwnj;م وتی: به&zwnj;ڕاستی من په&zwnj;نا ده&zwnj;گرم به خوای میهره&zwnj;بان له تۆ ئه&zwnj;گه&zwnj;ر که&zwnj;سێکی به ته&zwnj;قواو له&zwnj;خواترس بیت.';
$t[19] = 'جوبره&zwnj;ئیل وتی: (مه&zwnj;ترسه&zwnj;) دڵنیابه من ته&zwnj;نها نێردراوی په&zwnj;روه&zwnj;ردگاری تۆم، تا منداڵێکی خاوێنت پێ ببه&zwnj;خشم.';
$t[20] = 'مه&zwnj;ریه&zwnj;م وتی: جا چۆن من منداڵم ده&zwnj;بێت، له&zwnj;کاتێکدا هیچ به&zwnj;شه&zwnj;رێک توخنم نه&zwnj;که&zwnj;وتووه و داوێن پیسیش نه&zwnj;بووم.';
$t[21] = 'جوبره&zwnj;ئیل وتی: ئه&zwnj;و قسانه&zwnj;ی تۆ ڕاست و دروستن، به&zwnj;ڵام ئه&zwnj;وه بڕیاری په&zwnj;روه&zwnj;ردگاره و ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;وکاره لای من ئاسانه (که به&zwnj;بێ باوک ئه&zwnj;و منداڵه بهێنمه کایه&zwnj;وه&zwnj;)، بۆ ئه&zwnj;وه&zwnj;ی بیکه&zwnj;مه نیشانه&zwnj;و موعجیزه&zwnj;یه&zwnj;ک بۆ خه&zwnj;ڵکی، هه&zwnj;روه&zwnj;ها ڕه&zwnj;حمه&zwnj;تێکی تایبه&zwnj;تیش بێت له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، ئیتر ئه&zwnj;و کاره&zwnj;ش بڕیاری له&zwnj;سه&zwnj;ر دراوه و بڕاوه&zwnj;ته&zwnj;وه&zwnj;.';
$t[22] = 'ده&zwnj;ستبه&zwnj;جێ مه&zwnj;ریه&zwnj;م سکی پڕ بوو، (به&zwnj;غه&zwnj;م و په&zwnj;ژاره&zwnj;وه&zwnj;) به&zwnj;ره&zwnj;و شوێنێکی دوور ڕۆیشت.';
$t[23] = 'ئینجا ژانی منداڵبوون گرتی و بردی بۆ لای قه&zwnj;دی دارخورمایه&zwnj;ک، (به&zwnj;ده&zwnj;م ئاهو ناڵه&zwnj;وه&zwnj;) ده&zwnj;یوت: پێش ئه&zwnj;م حاڵته بمردمایه&zwnj;و وه&zwnj;ک شتێکی بێ نرخ فه&zwnj;رامۆش بکرامایه&zwnj;.';
$t[24] = '(ئه&zwnj;و منداڵه&zwnj;ی که له داوێنی به&zwnj;ربۆوه&zwnj;) له ژێریه&zwnj;وه (بانگی کردو وتی: دایه گیان) خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ، ئه&zwnj;وه&zwnj;ته په&zwnj;روه&zwnj;ردگارت به به&zwnj;رده&zwnj;متدا جۆگه&zwnj;له&zwnj;یه&zwnj;کی جاری کردووه&zwnj;.';
$t[25] = 'ئه&zwnj;و دارخورمایه&zwnj;ش (که پاڵت پێوه&zwnj;داوه&zwnj;) که&zwnj;مێک ڕایوه&zwnj;شێنه خورمای گه&zwnj;ییوت به&zwnj;سه&zwnj;ردا ده&zwnj;بارێنێت.';
$t[26] = 'ئینجا له&zwnj;و خورمایه بخۆ، له&zwnj;و ئاوه&zwnj;یش بخۆره&zwnj;وه&zwnj;، خه&zwnj;میشت نه&zwnj;بێت، ئه&zwnj;گه&zwnj;ر که&zwnj;سێکت له خه&zwnj;ڵکی بینی و (پرسیاری لێ کردی)، ئه&zwnj;وه به ئیشاره&zwnj;ت تێی بگه&zwnj;یه&zwnj;نه پێی بڵێ: به&zwnj;ڕاستی من بڕیارمداوه و نه&zwnj;زرم کردووه به ڕۆژوو بم بۆ خوای میهره&zwnj;بانم، له&zwnj;به&zwnj;ر ئه&zwnj;وه ئه&zwnj;مڕۆ من هه&zwnj;رگیز قسه له&zwnj;گه&zwnj;ڵ که&zwnj;سدا ناکه&zwnj;م.';
$t[27] = 'ئه&zwnj;وسا مه&zwnj;ریه&zwnj;م منداڵی نازداری خۆی له باوه&zwnj;ش گرت و بردی بۆ لای خزم و که&zwnj;س و کاری (هه&zwnj;موو به&zwnj;سه&zwnj;رسامیه&zwnj;وه&zwnj;) وتیان: ئه&zwnj;ی مه&zwnj;ریه&zwnj;م به&zwnj;ڕاستی کارێکی ناله&zwnj;بارو ئابڕووبه&zwnj;رت ئه&zwnj;نجامداوه&zwnj;.';
$t[28] = 'ئه&zwnj;ی خوشکی هاروون (له&zwnj;به&zwnj;ر ته&zwnj;قواو خواناسی ئه&zwnj;و ناز ناوه&zwnj;یان پێدابوو) تۆ نه باوکت که&zwnj;سێکی خراپ و (شه&zwnj;رواڵ پیس بووه&zwnj;) نه دایکیشت ئافره&zwnj;تێکی داوێن پیس!!';
$t[29] = '(مه&zwnj;ریه&zwnj;میش هیچی نه&zwnj;گوت) به&zwnj;ڵکو ئاماژه&zwnj;ی بۆ ڕۆڵه&zwnj;ی نازداری کرد (تا له&zwnj;و بپرسن)، هه&zwnj;موو (به سه&zwnj;رسامیه&zwnj;وه&zwnj;) وتیان: چۆن قسه له&zwnj;گه&zwnj;ڵ منداڵی ناو بێشکه&zwnj;دا ده&zwnj;کرێت.';
$t[30] = '(ئینجا منداڵی ساوا هاته گفت و به&zwnj;ده&zwnj;نگه ناسکه&zwnj;که&zwnj;ی) وتی: به&zwnj;ڕاستی من به&zwnj;نده&zwnj;ی خوام، کتێبی ئینجیلی پێ به&zwnj;خشیووم و کردوومی به پێغه&zwnj;مبه&zwnj;ری خۆی.';
$t[31] = 'منی موباره&zwnj;ک و پیرۆزیش کردووه له هه&zwnj;ر کوێدا بم و ئامۆژگاری کردووم که نوێژه&zwnj;کانم ئه&zwnj;نجام بده&zwnj;م و له زه&zwnj;کاتدانیش درێغی نه&zwnj;که&zwnj;م هه&zwnj;تا له ژیاندا هه&zwnj;م.';
$t[32] = '(هه&zwnj;روه&zwnj;ها فه&zwnj;رمانی پێداوم که&zwnj;) چاک ڕه&zwnj;فتار بم له&zwnj;گه&zwnj;ڵ دایکم دا و نه&zwnj;یکردووم به&zwnj;که&zwnj;سێکی زۆرداو به&zwnj;دره&zwnj;فتار..';
$t[33] = 'سڵاوی په&zwnj;روه&zwnj;ردگارم له&zwnj;سه&zwnj;ره ئه&zwnj;و ڕۆژه&zwnj;ی که له&zwnj;دایک بووم و ئه ڕۆژه&zwnj;ش که ده&zwnj;مرم، ئه&zwnj;و ڕۆژه&zwnj;ش که زیندوو ده&zwnj;کرێمه&zwnj;وه&zwnj;.';
$t[34] = 'ئا ئه&zwnj;وه&zwnj;یه عیسای کوڕی مه&zwnj;ریه&zwnj;م، هه&zwnj;ر ئه&zwnj;وه&zwnj;شه قسه&zwnj;ی ڕاست و دروست ده&zwnj;رباره&zwnj;ی، که ئه&zwnj;وانه (واته جووه&zwnj;کان) گومان به&zwnj;ده&zwnj;وریدا دروست ده&zwnj;که&zwnj;ن (گاوران، به&zwnj;کوڕی خوا له قه&zwnj;ڵه&zwnj;می ده&zwnj;ده&zwnj;ن).';
$t[35] = 'بێگومان ناگونجێت بۆ خوای گه&zwnj;وره&zwnj;، که&zwnj;س بکاته ڕۆڵه&zwnj;ی خۆی (چونکه پێوستی پێی نیه&zwnj;) پاکی و بێگه&zwnj;ردی شایسته&zwnj;ی ئه&zwnj;وه&zwnj;، کاتێک هه&zwnj;رشتێک بڕیار بدات، ئه&zwnj;وه به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;وه&zwnj;نده&zwnj;ی ده&zwnj;وێت که فه&zwnj;رمان بدات و بڵێت: ببه&zwnj;، ده&zwnj;ستبه&zwnj;جێ ده&zwnj;بێت.';
$t[36] = 'به&zwnj;ڕاستی هه&zwnj;ر خوا په&zwnj;روه&zwnj;ردگاری منیش و په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;شه&zwnj;، که&zwnj;وابوو بیپه&zwnj;رستن، ئه&zwnj;مه&zwnj;یه ڕێبازی ڕاست و دروست (ڕێبازی دینداری و سه&zwnj;رفرازی هه&zwnj;ردوو جیهان).';
$t[37] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو ڕاستیانه&zwnj;دا) ئینجا یه&zwnj;کسه&zwnj;ر گرۆو ده&zwnj;سته&zwnj;کان له نێو خۆیاندا ڕاو بۆچوونی جیاوازیان بۆ په&zwnj;یدا بوو، هاوارو ئاهو ناڵه بۆ ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن و (بڕوا به&zwnj;و ڕاستیانه ناکه&zwnj;ن) کاتێک قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت و دیمه&zwnj;نی سامناک و به&zwnj;سه&zwnj;رهاتی گه&zwnj;وره ده&zwnj;بینن (که ئێسته به&zwnj;دووری ده&zwnj;زانن).';
$t[38] = 'ئه&zwnj;و ڕۆژه&zwnj;ی (که ئه&zwnj;و خوانه&zwnj;ناسانه&zwnj;) ئاماده ده&zwnj;کرێن و دێن بۆ لامان، (زۆر سه&zwnj;یره&zwnj;!) ئای که چه&zwnj;نده ده&zwnj;زگای بیستن و بینینیان به&zwnj;هێزه&zwnj;، به&zwnj;ڵام سته&zwnj;مکاران ئه&zwnj;مڕۆ له گومڕاییه&zwnj;کی ئاشکرادا ده&zwnj;ژین.';
$t[39] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، سته&zwnj;مکاران ئاگادار بکه له ڕۆژی ئاهو ناڵه و په&zwnj;شیمانی کاتێک کار له کار ترازاوه&zwnj;، له&zwnj;کاتێکدا ئه&zwnj;وانه له غه&zwnj;فڵه&zwnj;تدان و باواڕ ناهێنن.';
$t[40] = 'به&zwnj;ڕاستی سه&zwnj;ره&zwnj;نجام هه&zwnj;رئێمه ده&zwnj;بینه&zwnj;وه خاوه&zwnj;ن و میراتگری سه&zwnj;ر زه&zwnj;وی و هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی که له سه&zwnj;ریه&zwnj;تی و بۆ لای ئێمه&zwnj;ش ده&zwnj;گه&zwnj;رێنه&zwnj;وه&zwnj;.';
$t[41] = 'هه&zwnj;روه&zwnj;ها له قورئاندا باسی (گۆشه&zwnj;یه&zwnj;ک له به&zwnj;سه&zwnj;رهاتی) ئیبراهیم بۆ ئه&zwnj;و خه&zwnj;ڵکه بگێڕه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و که&zwnj;سایه&zwnj;تیه&zwnj;کی ڕاستگۆ بوو، له پێغه&zwnj;مبه&zwnj;رانیش بوو.';
$t[42] = 'کا&zwnj;تێک به&zwnj;باوکی وت: باوکی به&zwnj;ڕێزم! ئه&zwnj;وه تۆ بۆچی شتێک ده&zwnj;په&zwnj;رستیت که نه&zwnj;گوێ بیسته&zwnj;، نه&zwnj;که&zwnj;س به&zwnj;رچاو ڕوون و ڕۆشنبیر ده&zwnj;کات، نه هیچ سوودێکت پێ ده&zwnj;گه&zwnj;یه&zwnj;نێت.';
$t[43] = 'ئه&zwnj;ی باوکی به&zwnj;نرخم! به&zwnj;ڕاستی ئه&zwnj;و زانستی و زانیاریه&zwnj;ی که بۆ من هاتووه&zwnj;، (ده&zwnj;رباره&zwnj;ی ده&zwnj;سه&zwnj;ڵات و توانای به&zwnj;دیهێنه&zwnj;ری ئه&zwnj;م جیهانه&zwnj;) بێگومان بۆ تۆ نه&zwnj;هاتووه&zwnj;، که&zwnj;واته په&zwnj;یڕه&zwnj;وی من بکه ڕێنمووییت ده&zwnj;که&zwnj;م بۆ ڕێگه و ڕێبازێکی ڕاست و دروست.';
$t[44] = 'ئه&zwnj;ی باوکی ئازیزم! شه&zwnj;یتان مه&zwnj;په&zwnj;رسته&zwnj;، چونکه به&zwnj;ڕاستی شه&zwnj;یتان یاخی یه له خوای میهره&zwnj;بان و سه&zwnj;رکه&zwnj;شیی کردووه&zwnj;.';
$t[45] = 'ئه&zwnj;ی باوکی به&zwnj;رێزم! به&zwnj;ڕاستی من ده&zwnj;ترسم سزایه&zwnj;ک له&zwnj;لایه&zwnj;ن خوای میهره&zwnj;بانه&zwnj;وه یه&zwnj;خه&zwnj;ت پێ بگرێت&zwnj;&zwnj;، ئه&zwnj;وکاته ئیتر ده&zwnj;بیته یارو یاوه&zwnj;رو پشتتگیری شه&zwnj;یتان.';
$t[46] = '(باوکی ئیبراهیم به&zwnj;سه&zwnj;غڵه&zwnj;تیه&zwnj;وه&zwnj;) وتی: بۆچی ئه&zwnj;ی ئیبراهیم تۆ (ئه&zwnj;وه&zwnj;نده&zwnj;) له خواکانی من بێزاری، به&zwnj;ڕاستی ئه&zwnj;گه&zwnj;ر کۆڵ نه&zwnj;ده&zwnj;یت و واز نه&zwnj;هێنیت (چاوپۆشیت لێ ناکه&zwnj;ن و) بێگومان به&zwnj;رد بارانت ده&zwnj;که&zwnj;م، بڕۆ، ون به له&zwnj;به&zwnj;رچاوم هه&zwnj;ر نه&zwnj;تبینمه&zwnj;وه&zwnj;.';
$t[47] = 'ئیبراهیم وتی: سڵاوت لێ بێت (وا من ده&zwnj;ڕۆم) له&zwnj;مه&zwnj;ولا داوای لێخۆشبوون و (هیدایه&zwnj;تت) له په&zwnj;روه&zwnj;ردگارم بۆ ده&zwnj;خوازم، چونکه ئه&zwnj;و زاته زۆر منی خۆش ده&zwnj;وێت و ڕێزی بۆ داناوم.';
$t[48] = 'وا من دوور ده&zwnj;که&zwnj;ومه&zwnj;وه و که&zwnj;نارگیری ده&zwnj;که&zwnj;م، له ئێوه و له&zwnj;و شتانه&zwnj;ی که له&zwnj;جیاتی خوا ده&zwnj;یپه&zwnj;رستن، من ته&zwnj;نها هاناو هاوار بۆ په&zwnj;روه&zwnj;ردگارم ده&zwnj;به&zwnj;م و هه&zwnj;ر ئه&zwnj;و ده&zwnj;په&zwnj;رستم، ئومێده&zwnj;وارم که په&zwnj;روه&zwnj;ردگارم نائومێد و ڕه&zwnj;نجه&zwnj;ڕۆم نه&zwnj;کات و دوعاو نزاو په&zwnj;رستنم لێ وه&zwnj;ربگرێت.';
$t[49] = 'جا کاتێک ئیبراهیم ڕۆیشت و دوور که&zwnj;وته&zwnj;وه له&zwnj;و خه&zwnj;ڵکه گومڕایه و له&zwnj;و شتانه&zwnj;ی که له جیاتی خوا ده&zwnj;یانپه&zwnj;رست، ئێمه ئیسحاقمان پێبه&zwnj;خشی و به ئیسحاقیش، یه&zwnj;عقوبمان به&zwnj;خشی، هه&zwnj;ریه&zwnj;ک له&zwnj;وانیشمان کرده پێغه&zwnj;مبه&zwnj;ر.';
$t[50] = 'ئێمه له ڕه&zwnj;حم و به&zwnj;خشنده&zwnj;یی خۆشمان هه&zwnj;موویانمان به&zwnj;هره&zwnj;وه&zwnj;ر کردو ئێمه زمانی خه&zwnj;ڵکی دیندارمان هێناوه&zwnj;ته گۆ که (هه&zwnj;تا دنیا دنیایه&zwnj;) باسی ئه&zwnj;وانه به&zwnj;چاکی و پاکی و بڵندی بکه&zwnj;ن.';
$t[51] = 'یادی موساش باس بکه له قورئاندا، به&zwnj;ڕاستی ئه&zwnj;ویش که&zwnj;سێکی هه&zwnj;ڵبژارده و دڵسۆزو پێغه&zwnj;مبه&zwnj;ر فرستاده بوو.';
$t[52] = 'له&zwnj;لای ڕاستی کێوی - طور - ه&zwnj;وه بانگمان لێ کرد، له&zwnj;کاتی گفتوگۆی نهێنیدا نزیکمان خسته&zwnj;وه&zwnj;.';
$t[53] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ره&zwnj;حمه&zwnj;ت و به&zwnj;خشنده&zwnj;یی خۆمان هاروونی برایمان کرده پێغه&zwnj;مبه&zwnj;ر و (هاوکار) بۆی.';
$t[54] = 'ئیسماعیل یش باس بکه له قورئاندا، به ڕاستی ئه&zwnj;و له به&zwnj;ڵێن و په&zwnj;یمانه&zwnj;کانیدا ڕاستگۆ بوو، ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رو ڕه&zwnj;وانه کراوی خوا بوو.';
$t[55] = '(ئیسماعیل) هه&zwnj;میسشه&zwnj;و به&zwnj;رده&zwnj;وام، فه&zwnj;رمانی ده&zwnj;دا به خێزانی و خزمانی، که نوێژه&zwnj;کانیان (به ڕێک و پێکی و له کاتی خۆیدا) ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کاتیش فه&zwnj;رامۆش نه&zwnj;که&zwnj;ن و ئه&zwnj;و لای په&zwnj;روه&zwnj;ردگاری شیایسته&zwnj;ی ڕێزو ڕه&zwnj;زامه&zwnj;ندی بوو.';
$t[56] = 'هه&zwnj;روه&zwnj;ها له قورئاندا باسی ئیدریس بکه&zwnj;، چونکه ئه&zwnj;ویش پێغه&zwnj;مبه&zwnj;رێکی زۆر ڕاستکارو ڕاستگۆ بوو..';
$t[57] = 'بۆیه ڕێزی زۆرمان لێگرت و پایه&zwnj;دارو به&zwnj;رزو بڵندمان کرد له جێگه و ڕێگه&zwnj;ی تایبه&zwnj;تدا.';
$t[58] = 'ئا ئه&zwnj;وانه که&zwnj;سانێکن که خوای گه&zwnj;وره نازو نیعمه&zwnj;تی به&zwnj;سه&zwnj;ردا ڕشتوون له پێغه&zwnj;مبه&zwnj;ران، له نه&zwnj;وه&zwnj;ی ئاده&zwnj;م و ئه&zwnj;وانه&zwnj;ش (که له که&zwnj;شتیه&zwnj;که&zwnj;دا) له&zwnj;گه&zwnj;ڵ نوحدا سوارمان کردن و له نه&zwnj;وه&zwnj;ی ئیبراهیم و یه&zwnj;عقوب و له&zwnj;وانه&zwnj;ی که هیدایه&zwnj;تمان داون و هه&zwnj;ڵمان بژاردوون، ئه&zwnj;وانه&zwnj;، هه&zwnj;رکاتێک ئایه&zwnj;ته&zwnj;کانی خوای میهره&zwnj;بانیان به&zwnj;سه&zwnj;ردا بخوێنرێته&zwnj;وه&zwnj;، به په&zwnj;له سوژده ده&zwnj;به&zwnj;ن و ئه&zwnj;سرین له دیده&zwnj;یان ده&zwnj;بارێت، (له سۆزو خۆشه&zwnj;ویستی په&zwnj;روه&zwnj;ردگاریان دڵانی پڕ له نوورو ئیمانیان ده&zwnj;کوڵێت) و ده&zwnj;گرین.';
$t[59] = 'له دوای ئه&zwnj;وان که&zwnj;سانێک له نه&zwnj;وه&zwnj;کانیان بوونه جێنشین، که نوێژه&zwnj;کانیان به&zwnj;زایه داو شوێنی ئاره&zwnj;زووه&zwnj;کانی نه&zwnj;فسیان که&zwnj;وتن، ئه&zwnj;وانه تووشی گومڕای و سه&zwnj;رگه&zwnj;ردانی و سزا ده&zwnj;بن (له دنیاو قیامه&zwnj;تدا).';
$t[60] = 'جگه له&zwnj;وانه&zwnj;ی که ته&zwnj;وبه ده&zwnj;که&zwnj;ن و باوه&zwnj;ڕی دامه&zwnj;زراو ده&zwnj;هێنن و کارو کرده&zwnj;وه&zwnj;ی چاک ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، ئا ئه&zwnj;وانه ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه و هیچ جۆره سته&zwnj;مێکیان لێ ناکرێت.';
$t[61] = 'ئه&zwnj;و به&zwnj;هه&zwnj;شته پڕ له باخه چڕانه&zwnj;ی که خوای میهره&zwnj;بان به&zwnj;ڵێنی به به&zwnj;نده&zwnj;کانی داوه له جیهانی شاراوه&zwnj;دا، به&zwnj;ڕاستی ئه&zwnj;و به&zwnj;ڵێنه&zwnj;ی خوا، به&zwnj;ڕێوه&zwnj;یه و هه&zwnj;ر دێته دی.';
$t[62] = 'ئه&zwnj;وسا له&zwnj;وێ هیچ گوفتارێکی نابه&zwnj;جێ نابیسن، جگه له سڵاو کردن و (گوفتاری خۆش و به&zwnj;جێ) نه&zwnj;بێت، له به&zwnj;یانیان و ئێواراندا (له هه&zwnj;موو کاتێکدا) ڕزق و ڕۆزی (خواردن و خواردنه&zwnj;وه&zwnj;ی خۆش و به&zwnj;تام) بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[63] = 'ئه&zwnj;و به&zwnj;هه&zwnj;شت و شوێنه خۆشه&zwnj;، به&zwnj;و به&zwnj;ندانه&zwnj;مان ده&zwnj;به&zwnj;خشین و ده&zwnj;یانکه&zwnj;ینه خاوه&zwnj;نی، که خواناس و پارێزکارن.';
$t[64] = '(ماوه&zwnj;یه&zwnj;ک وه&zwnj;حی وه&zwnj;ستا، پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) له فریشته جوبره&zwnj;ئیلی پرسی، خوای گه&zwnj;وره&zwnj;ش له قورئاندا وه&zwnj;ڵامی دایه&zwnj;وه&zwnj;): ئێمه ته&zwnj;نها به&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارت نه&zwnj;بێت دانابه&zwnj;زین، هه&zwnj;رچی داهاتوو ڕابوردوومان هه&zwnj;یه&zwnj;و ئه&zwnj;وه&zwnj;ش له نێوانیاندا هه&zwnj;یه&zwnj;، بۆ ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;یه&zwnj;، وه&zwnj;نه&zwnj;بێت په&zwnj;روه&zwnj;ردگاری تۆ هیچ شتێک فه&zwnj;رامۆش بکات.';
$t[65] = 'ئه&zwnj;و زاته په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;موو دروستکراوانی نێوانیانه&zwnj;، که&zwnj;واته&zwnj;، هه&zwnj;ر ئه&zwnj;و بپه&zwnj;رسته و زۆر به&zwnj;رده&zwnj;وام و خۆگربه له په&zwnj;رستنیدا، ئایا که&zwnj;س شک ده&zwnj;به&zwnj;یت هاوناوی ئه&zwnj;و و هاوتای ئه&zwnj;و بێت؟ (خاوه&zwnj;نی سیفه&zwnj;تی به&zwnj;رزو بڵندو پیرۆز بێت وه&zwnj;ک ئه&zwnj;و؟!).';
$t[66] = 'ئاده&zwnj;میزادی (بێ باوه&zwnj;ڕ) ده&zwnj;ڵێت: ئایا کاتێک مردم، جارێکی تر له ئاینده&zwnj;دا زیندوو ده&zwnj;کرێمه&zwnj;وه&zwnj;؟!.';
$t[67] = 'باشه ئاخر ئاده&zwnj;میزاد بۆ ئه&zwnj;وه یاد ناکات به&zwnj;ڕاستی ئێمه پێشتر ڕدوستمان کرد، هه&zwnj;ر هیچ نه&zwnj;بوو؟';
$t[68] = 'سوێند به په&zwnj;روه&zwnj;ردگارت خوانه&zwnj;ناسان و شه&zwnj;یتانه&zwnj;کان کۆ ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، پاشان هه&zwnj;موویان به ده&zwnj;وری دۆزه&zwnj;خدا به&zwnj;چۆکدا ده&zwnj;هێنین و (ده&zwnj;یانخه&zwnj;ینه ناوی).';
$t[69] = 'له&zwnj;وه&zwnj;ودوا له هه&zwnj;ر گرۆ و ده&zwnj;سته&zwnj;یه&zwnj;ک، ئه&zwnj;وانه ده&zwnj;رده&zwnj;کێشین که زۆر یاخی بوون له خوای میهره&zwnj;بان (دژایه&zwnj;تی دین و به&zwnj;رنامه&zwnj;که&zwnj;یان ده&zwnj;کرد).';
$t[70] = 'پاشان زۆرچاک ئاگادارین به&zwnj;وه&zwnj;ی که&zwnj;کێ له&zwnj;وانه له&zwnj;پێشترو شایسته&zwnj;تره به&zwnj;وه&zwnj;ی که زوو بخرێته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه (تا ببرژێت تیایدا).';
$t[71] = 'ئێوه (خه&zwnj;ڵکینه&zwnj;) که&zwnj;ستان نی یه تێپه&zwnj;ڕ نه&zwnj;کات به&zwnj;سه&zwnj;ر دۆزه&zwnj;خدا، (ئیمانداران بۆ ئه&zwnj;وه&zwnj;ی زیاتر قه&zwnj;دری به&zwnj;هه&zwnj;شت و ڕه&zwnj;حمه&zwnj;تی خوا بزانن، کافران بۆ ئه&zwnj;وه&zwnj;ی بکه&zwnj;ونه ناوی) ئه&zwnj;م پێشهاته بڕیاردراوه هه&zwnj;ر جێبه&zwnj;جێ ده&zwnj;کرێت.';
$t[72] = 'له&zwnj;وه&zwnj;ودوا ئه&zwnj;وانه ڕزگار ده&zwnj;که&zwnj;ین که پارێزکارو خواناس بوون، سته&zwnj;مکارانیش له&zwnj;ناو دۆزه&zwnj;خدا به&zwnj;چۆکدا ده&zwnj;هێنین.';
$t[73] = 'کاتێک ئایه&zwnj;ته&zwnj;کانی ئێمه به&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕاندا به ڕوون و ئاشکرا بخوێنرێته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون به&zwnj;وانه ده&zwnj;ڵێن که باوه&zwnj;ڕیان هێناوه&zwnj;: باشه کام له&zwnj;و دوو ده&zwnj;سته&zwnj;یه، جێگه&zwnj;و ڕێگه&zwnj;ی خۆش و ژیانی پڕ عه&zwnj;یش و نۆشی بۆ لواوه&zwnj;!! (نه&zwnj;فامان پێوه&zwnj;ریان ژیانی دنیای ته&zwnj;مه&zwnj;ن کۆتایه&zwnj;).';
$t[74] = 'چه&zwnj;نده&zwnj;ها قه&zwnj;وم و خه&zwnj;ڵکی ناله&zwnj;بارمان پێش ئه&zwnj;مان له&zwnj;ناوداوه&zwnj;، که&zwnj;لوپه&zwnj;ل و ناوماڵیان له&zwnj;مان چاکتر بووه&zwnj;، سیماو قیافه&zwnj;شیان له&zwnj;مان ڕێکو پێکتر بووه (کوان؟... له کوێن؟... بۆ دیار نین؟...).';
$t[75] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) تۆ) بڵێ: ئه&zwnj;وه&zwnj;ی له گومڕایی دایه باهه&zwnj;رخوای میهره&zwnj;بان ته&zwnj;مه&zwnj;نی درێژو ماڵ و سامان و دنیای پێ ببه&zwnj;خشێت (به&zwnj;چی ده&zwnj;چێت، بۆ کوێی ده&zwnj;بات، ئه&zwnj;په&zwnj;ڕی ئه&zwnj;وه&zwnj;یه&zwnj;) هه&zwnj;تا کاتێک ئه&zwnj;و هه&zwnj;ڕه&zwnj;شانه ده&zwnj;بینن که&zwnj;لێیان ده&zwnj;کرێت، یان سزاو ئازاری دنیایه&zwnj;، یان به&zwnj;رپابوونی قیامه&zwnj;ته&zwnj;، جا خوانه&zwnj;ناسان له&zwnj;ئاینده&zwnj;دا ده&zwnj;زانن کێ جێگه و ڕێگه&zwnj;ی ناخۆشه&zwnj;و کێ له&zwnj;شکرو سه&zwnj;ربازانی لاوازه&zwnj;.';
$t[76] = '(له&zwnj;ولاشه&zwnj;وه&zwnj;) ئه&zwnj;وانه&zwnj;ی ڕێبازی هیدایه&zwnj;تیان گرتۆته&zwnj;به&zwnj;ر خوا زیاتر هیداتیان ده&zwnj;دات، به&zwnj;ڵام دڵنیابن کرده&zwnj;وه چاکه&zwnj;کان که به&zwnj;رهه&zwnj;میان ده&zwnj;مێنێته&zwnj;وه پاداشتیان چاکتره لای په&zwnj;روه&zwnj;ردگارت باشترین سه&zwnj;ره&zwnj;نجامیشه&zwnj;.';
$t[77] = 'ئایا ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ت بینیووه&zwnj;، که باوه&zwnj;ڕی به&zwnj;ئایات و فه&zwnj;رمانه&zwnj;کانی ئێمه نیه&zwnj;و ده&zwnj;ڵێت: سوێند به خوا من سامان و کوڕو نه&zwnj;وه&zwnj;م پێ ده&zwnj;درێت!';
$t[78] = 'باشه&zwnj;، ئاخۆ (ئه&zwnj;و به&zwnj;نده یاخیه&zwnj;) به&zwnj;نهێنی و شاراوه&zwnj;کانی زانیووه&zwnj;، یاخود به&zwnj;ڵێنی وه&zwnj;رگرتووه له خوای میهره&zwnj;بان (که دنیا به دڵی ئه&zwnj;و بێت؟!).';
$t[79] = 'نه&zwnj;خێر، وانی یه (به&zwnj;دڵی ئه&zwnj;و نابێت) ئه&zwnj;وه&zwnj;ی که ده&zwnj;یڵێت له ئایینده&zwnj;دا تۆماری ده&zwnj;که&zwnj;ین له&zwnj;سه&zwnj;ری و سزای دوور و درێژی بۆ ساز ده&zwnj;که&zwnj;ین.';
$t[80] = 'بێگومان ئه&zwnj;وه&zwnj;ی ئه&zwnj;و ده&zwnj;یڵێت: به&zwnj;میراتی لێی ده&zwnj;سێنینه&zwnj;وه&zwnj;، به&zwnj;ته&zwnj;نهاش دێته لامان (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[81] = 'ئه&zwnj;و بێ باوه&zwnj;ڕانه له&zwnj;جیاتی خوا جۆره&zwnj;ها شتی تریان کردۆته خوای خۆیان تا پایه&zwnj;داری و ده&zwnj;سه&zwnj;ڵاتیان (به&zwnj;خه&zwnj;یاڵی خۆیان) ده&zwnj;ست بکه&zwnj;وێت.';
$t[82] = 'نه&zwnj;خێر، وانی یه (خه&zwnj;یاڵیان خاوه&zwnj;)، ئه&zwnj;و شتانه&zwnj;ی ئه&zwnj;وان ده&zwnj;یپه&zwnj;رستن، (له ڕۆژی قیامه&zwnj;تدا) باواڕیان به&zwnj;په&zwnj;رستنی ئه&zwnj;وان نیه و ناڕه&zwnj;زایی ده&zwnj;رده&zwnj;بڕن، (به&zwnj;ڵکو به سه&zwnj;ختی) دژایه&zwnj;تیان ده&zwnj;که&zwnj;ن و به&zwnj;رپه&zwnj;رچیان ده&zwnj;ده&zwnj;نه&zwnj;وه&zwnj;.';
$t[83] = 'ئایا نابینیت به&zwnj;ڕاستی ئێمه چۆن شه&zwnj;یتانه&zwnj;کان ده&zwnj;نێرینه سه&zwnj;ر خوانه&zwnj;ناسان، به&zwnj;گه&zwnj;رمی هانیان ده&zwnj;ده&zwnj;ن بۆ تاوان و گوناهو خراپه&zwnj;.';
$t[84] = '(ئه ی پێغه&zwnj;مبه&zwnj;ر &lrm;صلی الله علیه وسلم) په&zwnj;له&zwnj;یان لێ مه&zwnj;که&zwnj;، چونکه بێگومان ئێمه به&zwnj;وردی هه&zwnj;ر هه&zwnj;موو کارو کرده&zwnj;وه&zwnj;یان به&zwnj;ژماره تۆمار ده&zwnj;که&zwnj;ین.';
$t[85] = 'ڕۆژێک دێت ئێمه ده&zwnj;سته ده&zwnj;سته خواناس و پارێزکاران بۆ لای خوای میهره&zwnj;بان به ڕێ ده&zwnj;که&zwnj;ین (تا به&zwnj;دیداری شادببن و بیانخاته به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;).';
$t[86] = 'تاوانبارو گوناهکارانیش به&zwnj;په&zwnj;له&zwnj;و به&zwnj;تینویه&zwnj;تی ڕابێچ ده&zwnj;که&zwnj;ین بۆ ناو دۆزه&zwnj;خ.';
$t[87] = 'ئه&zwnj;وانه خاوه&zwnj;نی تکا نین و که&zwnj;س تکایان بۆ ناکات، مه&zwnj;گه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی که له&zwnj;کاتی خۆیدا خوای میهره&zwnj;بان به&zwnj;ڵێنی پێداوه که تکا بکات یان تکای بۆ بکرێت.';
$t[88] = '(جووله&zwnj;که و گاورو بت په&zwnj;رستان) ده&zwnj;یانوت: خوای میهره&zwnj;بان ڕۆڵه و نه&zwnj;وه&zwnj;ی بۆ خۆی داناوه&zwnj;!!';
$t[89] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی بوختانێکی ناقۆڵاو ده&zwnj;که&zwnj;ن و گوفتارێکی ناشایسه و سه&zwnj;رسوڕهێنه&zwnj;ر ده&zwnj;ڵێن.';
$t[90] = 'خه&zwnj;ریکه ئاسمانه&zwnj;کان له ناقۆڵایی ئه&zwnj;و گوفتاره نابه&zwnj;جێیه&zwnj;، له&zwnj;ت و په&zwnj;ت ببێت، زه&zwnj;وی خه&zwnj;ریکه شه&zwnj;ق به&zwnj;رێت و ڕؤبچێت، کێوه&zwnj;کانیش خه&zwnj;ریکه بکه&zwnj;ون و ورد و خاش ببن.';
$t[91] = 'کاتێک که نه&zwnj;فامان بوونی ڕۆڵه و نه&zwnj;وه&zwnj;یان دایه پاڵ خوای میهره&zwnj;بان.';
$t[92] = 'له&zwnj;کاتیکدا بۆ خوای میهره&zwnj;بان نه&zwnj;هاتووه و ڕه&zwnj;وا نیه که نه&zwnj;وه بۆ خۆی بڕیار بدات.';
$t[93] = 'هیچ که&zwnj;سێک نی یه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، که به&zwnj;نده&zwnj;ی ملکه&zwnj;چی خوای میهره&zwnj;بان نه&zwnj;بێت.';
$t[94] = 'سوێند به&zwnj;خوا بێگومان هه&zwnj;ر هه&zwnj;موویانی سه&zwnj;رژمێر کردووه و یه&zwnj;ک یه&zwnj;ک ژماره&zwnj;ی کردوون (که&zwnj;سی لێ ون نابێت)، هه&zwnj;ر هه&zwnj;موویان ڕۆژی قیامه&zwnj;ت دێنه پێشه&zwnj;وه&zwnj;، بۆ لای به تاک و ته&zwnj;نها (بێ پاسه&zwnj;وان و بێ که&zwnj;س).';
$t[95] = 'یه&zwnj;که یه&zwnj;که&zwnj;ی ئه&zwnj;وخه&zwnj;ڵکه به تاک و ته&zwnj;نهای دێت ئاماده ده&zwnj;کرێت له&zwnj;به&zwnj;رده&zwnj;م دادگای پڕ له دادوه&zwnj;ری خوادا.';
$t[96] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، له ئاینده&zwnj;دا خوای میهره&zwnj;بان سۆزو خۆشه&zwnj;ویستی و ڕێزی (تایبه&zwnj;تیان) بۆ ئاماده ده&zwnj;کات، (هه&zwnj;روه&zwnj;ها له لایه&zwnj;ن فریشته&zwnj;کان و ئیمانداران و نیشته&zwnj;جێکانی به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;) خۆشه&zwnj;ویست و ڕێزدارن.';
$t[97] = 'جا بێگومان ئێمه ئه&zwnj;م قورئانه&zwnj;مان ئاسان کردووه و هه&zwnj;ر به&zwnj;زمانی تۆ ڕه&zwnj;وانه&zwnj;مان کردووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی مژده&zwnj;ی پێ بده&zwnj;یت به&zwnj;پارێزکارو خواناسه&zwnj;کان، هه&zwnj;روه&zwnj;ها که&zwnj;سانی سه&zwnj;رسه&zwnj;خت و که&zwnj;لله&zwnj;ڕه&zwnj;قی پێ بترسێنێت.';
$t[98] = 'ئێمه چه&zwnj;نده&zwnj;ها قه&zwnj;وم و خه&zwnj;ڵکی ناله&zwnj;بارمان پێش ئه&zwnj;مان له&zwnj;ناوبردووه&zwnj;، ئایا هه&zwnj;ست ده&zwnj;که&zwnj;یت که یه&zwnj;کێ له&zwnj;وانه مابێت، یان سرته&zwnj;یه&zwnj;ک، چرپه&zwnj;یه&zwnj;ک، مشه&zwnj;یه&zwnj;کیان، لێوه بێت؟!';
?>