<?php
$t = array();
$t[1] = 'موحاسه&zwnj;به&zwnj;و لێ پرسینه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی نزیک بۆته&zwnj;وه&zwnj;، له کاتێکدا (زۆربه&zwnj;ی) ئه&zwnj;وان بێ ئاگا وغافڵن وپشتیان له ئاینی خوا کردووه و ڕوو وه&zwnj;ر&zwnj;گێرن (له&zwnj;به&zwnj;رنامه&zwnj;که&zwnj;ی).';
$t[2] = 'هیچ یاداوه&zwnj;ریه&zwnj;کی تازه&zwnj;یان له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;نه&zwnj;وه بۆ نایه&zwnj;ت که ئه&zwnj;وان گوێ بیستی نه&zwnj;بوو بن و له&zwnj;وکاته&zwnj;دا یاری و گه&zwnj;مه&zwnj;ی پێنه&zwnj;که&zwnj;ن...';
$t[3] = 'دڵه&zwnj;کانیان بێ ئاگاو سه&zwnj;رگه&zwnj;ردانه&zwnj;، ئه&zwnj;وانه&zwnj;ش که سته&zwnj;میان کرد، قسه&zwnj;کانیان شارده&zwnj;وه (به&zwnj;نهێنی ده&zwnj;یانوت) ئایا ئه&zwnj;مه تانها به&zwnj;شه&zwnj;رێک نیه وه&zwnj;ک ئێوه&zwnj;؟! ئایا ئێوه به&zwnj;ده&zwnj;م جادووگه&zwnj;ریه&zwnj;وه ده&zwnj;چن؟! له&zwnj;کاتێکدا ئێوه چاوتان هه&zwnj;یه و ده&zwnj;یبینن!!';
$t[4] = '(خوای گه&zwnj;وره پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی له باس و خواسیان ئاگادارکرد بۆیه&zwnj;) وتی: په&zwnj;روه&zwnj;ردگارم ئاگاداری هه&zwnj;موو قسه و گوفتارێکه له ئاسمان و زه&zwnj;ویدا و ئه&zwnj;و زاته بیسه&zwnj;رو زانایه.';
$t[5] = 'به&zwnj;ڵکو (شتی تریشیان) ده&zwnj;وت: (وه&zwnj;ک ئه&zwnj;وه&zwnj;ی که گوایه ئه&zwnj;م قورئانه&zwnj;): خه&zwnj;وو خه&zwnj;یاڵی بێ سه&zwnj;ر و بنه&zwnj;، یاخود خۆی هه&zwnj;ڵی به&zwnj;ستووه&zwnj;، به&zwnj;ڵکو ئه&zwnj;و که&zwnj;سێکی شاعیره&zwnj;، ده&zwnj;با به&zwnj;ڵگه و موعجیزه&zwnj;یه&zwnj;ک بهێنێت وه&zwnj;ک پێغه&zwnj;مبه&zwnj;رانی پێشوو!!';
$t[6] = 'ئه&zwnj;و شارو شارۆچکه و دێهاتانه&zwnj;ی پێش ئه&zwnj;مان (که داوای موعجیزه&zwnj;یان کردووه و دوایی باوه&zwnj;ڕیان نه&zwnj;هێناوه&zwnj;) له ناومان بردوون، ئایا ئه&zwnj;مان باوه&zwnj;ڕ ده&zwnj;هێنن؟! (ئایا ئه&zwnj;وان باوه&zwnj;ڕ ده&zwnj;هێنن)؟';
$t[7] = 'ئێمه پێش تۆ که&zwnj;سانی ترمان ڕه&zwnj;وانه نه&zwnj;کردووه&zwnj;، جگه له پیاوانێک (که هه&zwnj;ڵمانبژاردوون)، وه&zwnj;حی و نیگایان بۆ ڕه&zwnj;وانه ده&zwnj;که&zwnj;ین، ده&zwnj;بپرسن له خاوه&zwnj;نی کتێبه&zwnj;کانی پێشوو ئه&zwnj;گه&zwnj;ر ئێوه ئه&zwnj;م ڕاستیانه نازانن.';
$t[8] = 'ئێمه وه نه&zwnj;بێت ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;مان له په&zwnj;یکه&zwnj;ر و لاشه&zwnj;یه&zwnj;ک دروست کردبێت که خواردن نه&zwnj;خۆن و ژیانی هه&zwnj;میشه&zwnj;یی&zwnj;شمان پێ نه&zwnj;به&zwnj;خشیوون.';
$t[9] = 'له&zwnj;وه&zwnj;ودوا به&zwnj;ڵێنی خۆمانمان بۆ بردوونه&zwnj;ته سه&zwnj;ر، جا ڕزگارمان کردوون له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا که ده&zwnj;مانه&zwnj;وێت و (باوه&zwnj;ڕیان هێناوه&zwnj;) سه&zwnj;رکه&zwnj;ش و له&zwnj;سنور ده&zwnj;رچووه&zwnj;کانیشمان له&zwnj;ناوبردووه&zwnj;.';
$t[10] = 'سوێند به&zwnj;خوا بێگومان ئێمه کتێبێکمان ڕه&zwnj;وانه کردووه بۆتان که یاداوه&zwnj;ریه و سه&zwnj;ربه&zwnj;رزی ئێوه له شوێنکه&zwnj;وتنیایه&zwnj;تی، ئایا ئه&zwnj;وه بیرو هۆشتان ناخه&zwnj;نه&zwnj;کار و ژیر نابن؟';
$t[11] = 'چه&zwnj;نده&zwnj;ها شارو شارۆچکه&zwnj;مان کاول کرد که خه&zwnj;ڵکه&zwnj;که&zwnj;ی سته&zwnj;مکار بوون و به&zwnj;دوایاندا گه&zwnj;لانی ترمان هێناوه&zwnj;ته مه&zwnj;یدانه&zwnj;وه&zwnj;.';
$t[12] = 'جا کاتێک ئه&zwnj;و خوا نه&zwnj;ناسانه هه&zwnj;ستیان کردووه که تۆڵه و سزای ئێمه به&zwnj;ره&zwnj;و ڕوویان هاتووه و نزیکه یه&zwnj;خه&zwnj;یان بگرێت، ئه&zwnj;وه ده&zwnj;ست به&zwnj;جێ ڕایان کردووه لێی و سه&zwnj;ریان لێشێواوه&zwnj;.';
$t[13] = ')ئه&zwnj;وسا به گاڵته پێکردنه&zwnj;وه پێیان وتراوه&zwnj;) ڕامه&zwnj;که&zwnj;ن و بگه&zwnj;ڕێنه&zwnj;وه بۆ ئه&zwnj;و ڕابواردنه و بۆ ئه&zwnj;و ماڵ و حاڵه&zwnj;ی که تیایدا ده&zwnj;ژیان، بۆ ئه&zwnj;وه&zwnj;ی پرس و ڕاتان پێ بکرێت و کارێک یان ئیشێک چاوه&zwnj;ڕێتان بکات!!';
$t[14] = '(ئه&zwnj;وسا به ده&zwnj;م ڕاکردنه&zwnj;وه&zwnj;) ده&zwnj;یانوت: هاوار بۆ ئێمه به&zwnj;ڕاستی ئێمه سته&zwnj;مکار بووین.';
$t[15] = 'جا به&zwnj;رده&zwnj;وام هه&zwnj;ر ئه&zwnj;وه قسه&zwnj;یانه&zwnj;، هه&zwnj;تا هه&zwnj;موویانمان دروێنه و خامۆش کرد.';
$t[16] = 'ئێمه ئاسمان و زه&zwnj;وی، هه&zwnj;رچیش له نێوانیاندایه به گاڵته (بێ مه&zwnj;به&zwnj;ست) دروستمان نه&zwnj;کردووه&zwnj;.';
$t[17] = 'ئه&zwnj;گه&zwnj;ر بمانویستایه یاری و گه&zwnj;مه&zwnj;یه&zwnj;ک بسازێنین، له&zwnj;لایه&zwnj;ن خۆمانه&zwnj;وه ده&zwnj;مانسازاند، ئه&zwnj;گه&zwnj;ر بڕیار بێت کاری وا بکه&zwnj;ین...';
$t[18] = '(جا ئه&zwnj;وه له ئێمه ناوه&zwnj;شێته&zwnj;وه&zwnj;) به&zwnj;ڵکو حه&zwnj;ق ده&zwnj;ماڵین به&zwnj;سه&zwnj;ر به&zwnj;تاڵ و ناحه&zwnj;قیداو ده&zwnj;ست به&zwnj;جێ داغانی ده&zwnj;که&zwnj;ین، هاوارو واوه&zwnj;یلا له ئێوه&zwnj;، بۆ ئه&zwnj;و بوختان و قسه نابه&zwnj;جێیانه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[19] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدایه هه&zwnj;ر خوا خاوه&zwnj;نیانه و له خزمه&zwnj;تی ئه&zwnj;ودان، بێگومان ئه&zwnj;و فریشتانه&zwnj;ی که له خزمه&zwnj;تیدان، خۆیان به&zwnj;گه&zwnj;وره نازانن له په&zwnj;رستنیدا و هه&zwnj;رگیز ماندوو بێ تاقه&zwnj;ت و بێزار نابن له ستایشی ئه&zwnj;و زاته&zwnj;.';
$t[20] = '(به&zwnj;رده&zwnj;وام) به&zwnj;شه&zwnj;و و به ڕۆژ ته&zwnj;سبیحات و ستایشی ده&zwnj;که&zwnj;ن و له&zwnj;کاتێکدا ساردبوونه&zwnj;وه&zwnj;ش ڕوویان تێناکات.';
$t[21] = 'ئایا خه&zwnj;ڵکی ناله&zwnj;بار په&zwnj;رستراوی ساخته&zwnj;یان بڕیارداوه له&zwnj;زه&zwnj;ویدا و ئه&zwnj;و په&zwnj;رستراوانه ده&zwnj;توانن ژیان ببه&zwnj;خشن؟';
$t[22] = 'ئه&zwnj;گه&zwnj;ر له هه&zwnj;ردووکیاندا (له ئاسمان و زه&zwnj;ویدا) چه&zwnj;ند خوایه&zwnj;کی تر هه&zwnj;بوایه غه&zwnj;یری (الله&zwnj;)، ئه&zwnj;وه کاول و وێران ده&zwnj;بوون و تێک ده&zwnj;چوون به&zwnj;سه&zwnj;ر یه&zwnj;کدا، پاکی و بێگه&zwnj;ردی بۆ خوا که په&zwnj;روه&zwnj;ردگاری عه&zwnj;رشه&zwnj;، له&zwnj;و باس و خواسانه&zwnj;دا (که خوانه&zwnj;ناسان) ده&zwnj;یڵێن (هاوه&zwnj;ڵ بۆ ئه&zwnj;و زاته بڕیار ده&zwnj;ده&zwnj;ن).';
$t[23] = 'پرسیار ناکرێت له&zwnj;وه&zwnj;ی که خوا ده&zwnj;یکات، له کاتێکدا ئه&zwnj;وان پرسیاریان لێ ده&zwnj;کرێت و موحاسه&zwnj;به ده&zwnj;کرێن.';
$t[24] = 'ئایا ئه&zwnj;وانه خوایه&zwnj;کی تریان له جیاتی په&zwnj;روه&zwnj;ردگار بۆ خۆیان ڕه&zwnj;خساندووه&zwnj;؟! (یاخود خۆیان ده&zwnj;خه&zwnj;ڵه&zwnj;تێنن و گێلن؟!) ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: به&zwnj;ڵگه&zwnj;تان بهێنن له&zwnj;سه&zwnj;ر ئه&zwnj;و بیروباوه&zwnj;ڕه&zwnj;تان، من دڵنیام که ئه&zwnj;م قورئانه یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;، بۆ ئه&zwnj;وانه&zwnj;ی که هاوه&zwnj;ڵی منن و ڕێبازی منیان گرتۆته&zwnj;به&zwnj;ر، هه&zwnj;روه&zwnj;ها له به&zwnj;سه&zwnj;رهاتی پێغه&zwnj;مبه&zwnj;ران و قه&zwnj;ومه&zwnj;کانی پێش منیش ده&zwnj;دوێت، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی حه&zwnj;ق و حه&zwnj;قیقه&zwnj;ت ناناسن و دژایه&zwnj;تی ده&zwnj;که&zwnj;ن و ڕووی لێ وه&zwnj;رده&zwnj;چه&zwnj;ر خێنن.';
$t[25] = 'ئێمه پێش تۆ هیچ پێغه&zwnj;مبه&zwnj;رێکمان ڕه&zwnj;وانه نه&zwnj;کردووه&zwnj;، که وه&zwnj;حیمان بۆ نه&zwnj;ناردبێت به&zwnj;وه&zwnj;ی که&zwnj;: بێگومان هیچ خوایه&zwnj;ک نیه جگه له من و ده&zwnj;بێت هه&zwnj;ر من بپه&zwnj;رستن.';
$t[26] = 'خوانه&zwnj;ناسان ده&zwnj;یانوت: خوای میهره&zwnj;بان کوڕی بۆ خۆی بڕیارداوه (فریشته&zwnj;کان ڕۆڵه&zwnj;ی ئه&zwnj;ون)، پاکی و بێگه&zwnj;ردی بۆ ئه&zwnj;و زاته&zwnj;یه&zwnj;، به&zwnj;ڵکو ئه&zwnj;وانه به&zwnj;نده&zwnj;ی ڕێزداری ئه&zwnj;ون.';
$t[27] = '(ئه&zwnj;و فریشتانه ئه&zwnj;وه&zwnj;نده ڕه&zwnj;وشت به&zwnj;رزن) هه&zwnj;رگیز (نافه&zwnj;رمانی ناکه&zwnj;ن و) قسه له&zwnj;قسه&zwnj;یدا ناکه&zwnj;ن و له گوفتاردا پێشی ناکه&zwnj;ون، ئه&zwnj;وانه به فه&zwnj;رمانی ئه&zwnj;و ده&zwnj;جوڵێنه&zwnj;وه و فه&zwnj;رمانی ئه جێبه&zwnj;جێ ده&zwnj;که&zwnj;ن.';
$t[28] = 'هه&zwnj;ر خوا ده&zwnj;زانێت ئه خه&zwnj;ڵکه له ڕابوردوودا چیان ئه&zwnj;نجامداوه و له داهاتووشدا چی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن (دنیا و قیامه&zwnj;ت، ڕابوردوو و داهاتوو، بۆ ئه&zwnj;و زاته چوونیه&zwnj;که&zwnj;)، فریشته&zwnj;کانیش تکاکار نابن ته&zwnj;نها بۆ ئه&zwnj;و که&zwnj;سانه نه&zwnj;بێت خوا پێی ڕازییه و ئه&zwnj;و فریشتانه له ترسی ئه&zwnj;و زاته ناوێرن هیچ سه&zwnj;رپێچیه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن.';
$t[29] = 'خۆ ئه&zwnj;گه&zwnj;ر یه&zwnj;کێک له فریشته&zwnj;کان (له خۆی غه&zwnj;ڕڕا بێت) بڵێت: به&zwnj;ڕاستی منیش خوام بێجگه له&zwnj;و، ئه&zwnj;وه پاداشتی ده&zwnj;ده&zwnj;ینه&zwnj;وه به دۆزه&zwnj;خ، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه پاداشتی سته&zwnj;مکاران و یاخیه&zwnj;کان ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;';
$t[30] = 'ئایا ئه&zwnj;وانه&zwnj;ی بێباوه&zwnj;ڕ بوون نه&zwnj;یانبینیووه و بۆیان ڕوون نه&zwnj;بۆته&zwnj;وه به&zwnj;ڕاستی ئاسمانه&zwnj;کان و زه&zwnj;وی یه&zwnj;ک پارچه بوون له&zwnj;وه&zwnj;ودوا لێکمان جیا کردنه&zwnj;وه (که ئه&zwnj;مه ئاماژه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;ی که ئه&zwnj;م ڕاسته&zwnj;قینه له&zwnj;لایه&zwnj;ن ئه&zwnj;وانه&zwnj;وه په&zwnj;ی پێ ده&zwnj;برێت)، له ئاویش هه&zwnj;موو شتێکی زیندوومان فه&zwnj;راهه&zwnj;م هێناوه (ئاو زۆربه&zwnj;ی پێکهاته&zwnj;ی له&zwnj;شی ئاده&zwnj;میزاد و زینده&zwnj;واران و ڕووه&zwnj;ک پێکده&zwnj;هێنێت)، ئایا ئه&zwnj;وانه هه&zwnj;ر باوه&zwnj;ڕ ناهێنن (به&zwnj;و ڕاستیانه و به&zwnj;و ده&zwnj;سه&zwnj;ڵاته بێ سنووره&zwnj;ی ئێمه&zwnj;)؟!';
$t[31] = 'له زه&zwnj;ویشدا که&zwnj;ژو کێوه&zwnj;کانمان چه&zwnj;سپاندووه&zwnj;، تا لاسه&zwnj;نگ نه&zwnj;بێت و لار نه&zwnj;بێته&zwnj;وه و هه&zwnj;ڵیان نه&zwnj;گێرێته&zwnj;وه&zwnj;، له لاپاڵی چیاکان و له دۆڵه&zwnj;کاندا ڕێگاو بانی فراوانمان سازاندووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی ژیان بۆ خۆیان دابین بکه&zwnj;ن و هه&zwnj;روه&zwnj;ها ڕێیان له گه&zwnj;نجینه و ناز و نیعمه&zwnj;ته شاراوه&zwnj;کان بکه&zwnj;وێت.';
$t[32] = 'ئێمه ئاسمانمان کردووه به سه&zwnj;قفێکی پارێزراو، که&zwnj;چی ئه&zwnj;وانه ڕوو وه&zwnj;رده&zwnj;گێڕن له هه&zwnj;موو نیشانه و به&zwnj;ڵگه&zwnj;کانی ئاسمان.';
$t[33] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که شه&zwnj;وو ڕۆژی به&zwnj;دیهێناوه&zwnj;، خۆر مانگیش دروست کردووه&zwnj;، هه&zwnj;ریه&zwnj;که&zwnj;یان له خولگه&zwnj;یه&zwnj;کدا ده&zwnj;سوڕێته&zwnj;وه (زوه&zwnj;ی به ده&zwnj;وری خۆردا و مانگیش به&zwnj;ده&zwnj;وری زه&zwnj;ویدا).';
$t[34] = 'ئێمه پێش تۆ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;مریمان به هیچ که&zwnj;سێک نه&zwnj;به&zwnj;خشیووه&zwnj;، جا ئایا ئه&zwnj;گه&zwnj;ر تۆ بمریت، (دوای مه&zwnj;رگی تۆ) ئه&zwnj;وه ئه&zwnj;و خه&zwnj;ڵکه ژیانی هه&zwnj;میشه&zwnj;یی ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر و نامرن؟!';
$t[35] = 'هه&zwnj;موو که&zwnj;سێک ده&zwnj;بێت مردن بچێژێت و شه&zwnj;رابی مه&zwnj;رگ بنۆشێت، بێگومان ئێمه ئێوه تاقی ده&zwnj;که&zwnj;ینه&zwnj;وه به&zwnj;شه&zwnj;ڕ و ناخۆشی و به&zwnj;ڵاو به&zwnj;خێرو خۆشی و دارایی و توانایی، سه&zwnj;ره&zwnj;نجام هه&zwnj;ر بۆ لای ئێمه ده&zwnj;هێنرێنه&zwnj;وه (تا پاداشتتان بده&zwnj;ینه&zwnj;وه&zwnj;).';
$t[36] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) کاتێک ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون تۆ ده&zwnj;بینن، هیچ کارێکیان له ده&zwnj;ست نایه&zwnj;ت له&zwnj;گه&zwnj;ڵ تۆی بکه&zwnj;ن جگه له گاڵته و گاڵته جاڕیی، (جا به&zwnj;یه&zwnj;کتر ده&zwnj;ڵێن): له&zwnj;کاتێکدا ئه&zwnj;وان هه&zwnj;میشه بێ&zwnj;باوه&zwnj;ڕن به قورئانی خوای میهره&zwnj;بان.';
$t[37] = 'ئه&zwnj;وه&zwnj;نده ئینسان به په&zwnj;له تاڵوکه&zwnj;یه&zwnj;. (هه&zwnj;ر ده&zwnj;ڵێیت) له په&zwnj;له&zwnj;و تاڵوکه دروستکراوه&zwnj;، له ئایینده من نیشانه&zwnj;ی و به&zwnj;ڵگه&zwnj;کانی خۆمتان نیشان ده&zwnj;ده&zwnj;م، جا په&zwnj;له&zwnj;م لێ مه&zwnj;که&zwnj;ن.';
$t[38] = 'خوانه&zwnj;ناسان ده&zwnj;ڵێن: باشه، که&zwnj;ی ئه&zwnj;م به&zwnj;ڵێنه دێته دی ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن؟';
$t[39] = 'ئه&zwnj;گه&zwnj;ر بێ باوه&zwnj;ڕان هه&zwnj;واڵی ئه&zwnj;و ڕۆژه&zwnj;یان بزانیایه (که به&zwnj;ڕێوه&zwnj;یه و نزیکه و ده&zwnj;خرێنه ناو ئاگری دۆزه&zwnj;خه&zwnj;وه&zwnj;)، کاتێک ناتوانن بڵێسه&zwnj;ی ئاگر له ده&zwnj;م و چاو و ڕوخسارو له پشتیان دوور بخه&zwnj;نه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وانه سه&zwnj;رکه&zwnj;وتوو ڕزگار نابن (ئه&zwnj;وانه نه&zwnj;گاڵته&zwnj;یان ده&zwnj;کردو نه بێ باوه&zwnj;ڕ ده&zwnj;بوون و نه&zwnj;په&zwnj;له&zwnj;یان ده&zwnj;کرد).';
$t[40] = 'به&zwnj;ڵکو قیامه&zwnj;ت کتوپڕ یه&zwnj;خه&zwnj;یان ده&zwnj;گرێت و سه&zwnj;رسام و ڕیسوایان ده&zwnj;کات، ئه&zwnj;وسا ناتوانن به&zwnj;ره&zwnj;نگاری بوه&zwnj;ستن و بیگێرنه&zwnj;وه&zwnj;، مۆڵه&zwnj;تیشیان نادرێت.';
$t[41] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) سوێند به&zwnj;خوا بێگومان پێش تۆ گاڵته کراوه به پێغه&zwnj;مبه&zwnj;رانی تریش، ئه&zwnj;وسا ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان گاڵته&zwnj;یان پێ ده&zwnj;کرد، یه&zwnj;خه&zwnj;ی پێگرتن و به&zwnj;هۆی ئه&zwnj;وانه&zwnj;یانه&zwnj;وه گاڵته&zwnj;یان کرد.';
$t[42] = 'پێیان بڵێ: کێ ده&zwnj;توانێت ئێوه بپارێزێت له شه&zwnj;وو ڕۆژدا له سزاو تۆڵه&zwnj;ی خوای میهره&zwnj;بان، که&zwnj;چی له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا ئه&zwnj;وانه هه&zwnj;میشه ڕوو وه&zwnj;رگێڕن له یادی په&zwnj;روه&zwnj;ردگاریان.';
$t[43] = 'یان ئه&zwnj;و بێ&zwnj;باوه&zwnj;ڕانه چه&zwnj;ند خوایه&zwnj;کیان هه&zwnj;یه که ده&zwnj;توانن له سزامان ڕزگاریان بکات جگه له ئێمه؟! (ئه&zwnj;و کۆلکه خوایانه&zwnj;) نه ده&zwnj;توانن خۆیان ڕزگار بکه&zwnj;ن و خۆیان ڕزگار بکه&zwnj;ن و خۆیان سه&zwnj;ربخه&zwnj;ن، نه په&zwnj;نا ده&zwnj;درێت له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه و نه&zwnj;کۆمه&zwnj;کیان ده&zwnj;کرێت.';
$t[44] = 'ئێمه (به&zwnj;په&zwnj;له تۆڵه&zwnj;ناسێنین) به&zwnj;ڵکو ئه&zwnj;وانه خۆیان و باوو باپیرانیان له نازو نیعمه&zwnj;تی دنیا به&zwnj;هره&zwnj;وه&zwnj;ر کردووه&zwnj;، هه&zwnj;تا ته&zwnj;مانیان درێژ بۆته&zwnj;وه&zwnj;، جا ئایا ئه&zwnj;وانه نابینن و نازانن که به&zwnj;ڕاستی ئێمه ورده ورده دێین و ده&zwnj;سه&zwnj;ڵاتیان له&zwnj;ملاو لای وڵاتیاندا که&zwnj;م ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، (کاتێک ئوممه&zwnj;تی ئیسلام شایسته ده&zwnj;بێت، خوای باڵا ده&zwnj;ست ده&zwnj;سه&zwnj;ڵاتی ڕۆژهه&zwnj;ڵات و ڕۆژئاوا که&zwnj;م ده&zwnj;کاته&zwnj;وه&zwnj;) جا ئایا ئه&zwnj;و بێ&zwnj;باوه&zwnj;ڕانه هه&zwnj;میشه سه&zwnj;رکه&zwnj;وتوو و زاڵن؟!';
$t[45] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: به&zwnj;ڕاستی من ته&zwnj;نها له ڕێگه&zwnj;ی وه&zwnj;حی و نیگاوه ئێوه بێدار ده&zwnj;که&zwnj;مه&zwnj;وه&zwnj;، که&zwnj;سانێکیش هه&zwnj;ن که&zwnj;ڕن له ئاستی حه&zwnj;قدا و سه&zwnj;دای په&zwnj;یامی خوا نابیستن کاتێک بێدار ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[46] = 'سوێند به&zwnj;خوا ئه&zwnj;گه&zwnj;ر که&zwnj;مێک له هاڵاوی سزای په&zwnj;روه&zwnj;ردگارت ڕوویان تێ بکات و لێیان بدات، ئه&zwnj;وه خوانه&zwnj;ناسان به کوڵ ده&zwnj;ڵێن: هاوارو ئاهو ناڵه بۆ ئێمه&zwnj;، به ڕاستی ئێمه که&zwnj;سانێکی سته&zwnj;مکار بووین.';
$t[47] = 'ئێمه پێوه&zwnj;ر و ته&zwnj;رازووه&zwnj;کانی دادپه&zwnj;روه&zwnj;ری له ڕۆژی قیامه&zwnj;تدا داده&zwnj;نێین، جا هیچ که&zwnj;س به هیچ شێوه&zwnj;یه&zwnj;ک سته&zwnj;می لێ ناکرێت، ئه&zwnj;گه&zwnj;ر به قه&zwnj;ده&zwnj;ر تۆوه خه&zwnj;رته&zwnj;له&zwnj;یه&zwnj;ک (که زۆر بچووک و ورده&zwnj;، کارێکی چاک یان خراپی ئه&zwnj;نجام دابێت) ده&zwnj;یهێنینه مه&zwnj;یدان، جا ئه&zwnj;وه&zwnj;نده به&zwnj;سه بۆ ئێمه که ئاوا به وردی حساب و لێپرسینه&zwnj;وه ئه&zwnj;نجام ده&zwnj;ده&zwnj;ین.';
$t[48] = 'سوێند به خوا بێگومان ئێمه به موساو هاروون کتێبی ته&zwnj;وراتمان به&zwnj;خشی که جیاکه&zwnj;ره&zwnj;وه&zwnj;ی حه&zwnj;ق و به&zwnj;تاڵه&zwnj;، هه&zwnj;روه&zwnj;ها ڕووناکی و یادخه&zwnj;ره&zwnj;وه&zwnj;شه بۆ پارێزکاران...';
$t[49] = 'ئه&zwnj;وانه&zwnj;ن له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن له کاتێکدا که که&zwnj;س دیار نیه و په&zwnj;روه&zwnj;ردگاریش لێیان پانهانه&zwnj;، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه له قیامه&zwnj;ت و لێپرسینه&zwnj;وه بیمیان هه&zwnj;یه (بێگومان ئه&zwnj;وانه له قیامه&zwnj;تدا دڵنیا و ئاسووده و دڵخۆشن).';
$t[50] = 'جا ئه&zwnj;م قورئانه&zwnj;ش یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی موباره&zwnj;ک و به&zwnj;فه&zwnj;ڕ و پیرۆزه دامان به&zwnj;زاندووه&zwnj;، جا ئایا ئێوه خۆتانی لێ وێڵ ده&zwnj;که&zwnj;ن و نایناسن؟!';
$t[51] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه ژیری و هۆشمه&zwnj;ندیمان پێشتر به ئیبراهیم به&zwnj;خشی، ئێمه چاک ئه&zwnj;ومان ده&zwnj;ناسی.';
$t[52] = 'کاتێک به باوک و قه&zwnj;ومه&zwnj;که&zwnj;ی ده&zwnj;وت: ئه&zwnj;م بتانه چیه&zwnj;؟! ئێوه هه&zwnj;میشه به&zwnj;ده&zwnj;وریدا دێن و ده&zwnj;یانپه&zwnj;رستن!';
$t[53] = 'له&zwnj;وه&zwnj;ڵامدا (ته&zwnj;نها به&zwnj;ڵگه&zwnj;یان ئه&zwnj;وه بوو، که&zwnj;) وتیان: ئێمه باوو باپیرانمان بینیووه ئه&zwnj;مانه&zwnj;یان په&zwnj;رستووه&zwnj;.';
$t[54] = 'ئیبراهیم وتی: به&zwnj;ڕاستی ئێوه و باوو باپیرانی ئێوه له گومڕاییه&zwnj;کی ئاشکرادا بوون.';
$t[55] = 'خه&zwnj;ڵکه&zwnj;که وتیان: به&zwnj;ڕاست، تۆ حه&zwnj;قیقه&zwnj;تت بۆ ئێمه هێناوه یان گاڵته ده&zwnj;که&zwnj;یت؟!';
$t[56] = 'ئیبراهیم وتی: (من گاڵته ناکه&zwnj;م) به&zwnj;ڵکو ده&zwnj;مه&zwnj;وێت تێتان بگه&zwnj;یه&zwnj;نم که په&zwnj;روه&zwnj;ردگارتان په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;ویه و به&zwnj;دی هێناون و من له&zwnj;سه&zwnj;ر ئه&zwnj;و بیروباوه&zwnj;ڕه له&zwnj;شایه&zwnj;ت و ئاگاکانم.';
$t[57] = 'ئینجا ئیبراهیم له&zwnj;به&zwnj;ر خۆیه&zwnj;وه سوێندی خوارد و وتی: به&zwnj;خوا نه&zwnj;خشه&zwnj;یه&zwnj;ک ده&zwnj;کێشم و داوێک ده&zwnj;نێمه&zwnj;وه بۆ بته&zwnj;کانتان دوای ئه&zwnj;وه&zwnj;ی ئێوه به&zwnj;جێتان هێشتن و پشتتان هه&zwnj;ڵکرد.';
$t[58] = 'ئینجا هه&zwnj;موو وردو خاش کرد جگه له گه&zwnj;وره&zwnj;که&zwnj;یان، به&zwnj;ڵکو که گه&zwnj;ڕانه&zwnj;وه (پرسیار بکه&zwnj;ن) و ئیبراهیم ڕاستیان بۆ ڕوون بکاته&zwnj;وه&zwnj;.';
$t[59] = 'بت په&zwnj;رستان (که هاتنه&zwnj;وه بینیان بته&zwnj;کانیان وردوخاش کراوه&zwnj;) وتیان: ئه&zwnj;وه&zwnj;ی ئه&zwnj;مه&zwnj;ی کردووه به خواکانمان، به&zwnj;ڕاستی له سته&zwnj;مکارانه&zwnj;!!';
$t[60] = 'هه&zwnj;ندێکیان وتیان: بیستومانه لاوێک به خراپه باسیان ده&zwnj;کات پێی ده&zwnj;ڵێن ئیبراهیم.';
$t[61] = 'وتیان: که&zwnj;وابوو بیهێنن به&zwnj;به&zwnj;رچاوی خه&zwnj;ڵکه&zwnj;وه (موحاسه&zwnj;به&zwnj;ی بکه&zwnj;ین و لێی بکۆڵینه&zwnj;وه&zwnj;) بائه&zwnj;وانیش ئاگاداربن و بیبینن.';
$t[62] = '(ئیتر چوون ئیبراهیمیان هێناو لێیان پرسی) وتیان باشه&zwnj;، ئه&zwnj;ی ئیبراهیم ئایا تۆ ئه&zwnj;مه&zwnj;ت کردووه به خواکانمان؟!';
$t[63] = 'ئیبراهیم وتی: (بۆچی له من ده&zwnj;پرسن، خۆ بته گه&zwnj;وره&zwnj;که ساغ و سه&zwnj;لیمه&zwnj;) نه&zwnj;خێر؛ هه&zwnj;ر کاری ئه&zwnj;و بته زله&zwnj;یه&zwnj;، ده لێیان بپرسن ئه&zwnj;گه&zwnj;ر قسه ده&zwnj;که&zwnj;ن و تێ ده&zwnj;گه&zwnj;ن!!';
$t[64] = '(که&zwnj;میک داچڵه&zwnj;کان و) له دڵی خۆیاندا لێکیان دایه&zwnj;وه&zwnj;، سه&zwnj;ره&zwnj;نجام وتیان به خۆیان: به&zwnj;ڕاستی ئێوه هه&zwnj;میشه سته&zwnj;مکارن (ئیبراهیم ڕاست ده&zwnj;کات، ئه&zwnj;مانه ناتوانن به&zwnj;رگری له خۆیان بکه&zwnj;ن جۆن ده&zwnj;توانن به&zwnj;رگری له ئێمه بکه&zwnj;ن).';
$t[65] = 'پاشان به&zwnj;سه&zwnj;رشۆڕی پاشگه&zwnj;زکرانه&zwnj;وه بۆ بیرو باوه&zwnj;ڕه کۆنه&zwnj;که&zwnj;یان وتیان: بێگومان تۆ زانیووته که ئه&zwnj;وانه قسه ناکه&zwnj;ن و تێ ناگه&zwnj;ن!! (زۆرزان و کاربه&zwnj;ده&zwnj;ستانیان چه&zwnj;واشه&zwnj;یان کردوونه&zwnj;).';
$t[66] = 'ئیبراهیم وتی: باشه&zwnj;، ئاخر، چۆن شتێک ده&zwnj;په&zwnj;رستن له جیاتی خوا که ناتوانێت هیچ جۆره قازانج یان زیانێکتان پێ بگه&zwnj;یه&zwnj;نێت، (ته&zwnj;نانه&zwnj;ت ئه&zwnj;وه&zwnj;تا نه&zwnj;یانتوانی به&zwnj;رگری له خۆشیان بکه&zwnj;ن).';
$t[67] = 'ئۆن له ده&zwnj;ستان و له&zwnj;و شتانه&zwnj;ش که له&zwnj;جیاتی خوا ده&zwnj;یپه&zwnj;رستن، جا ئایا ئه&zwnj;وه تێناگه&zwnj;ن و بیرو هۆشتان ناخه&zwnj;نه&zwnj;کار؟!';
$t[68] = 'هه&zwnj;ندێکیان وتیان: ئیبراهیم بسوتێنن و پشتیوانی له خواکانتان بکه&zwnj;ن، ئه&zwnj;گه&zwnj;ر ئێوه شتێکتان بۆ ده&zwnj;کرێت.';
$t[69] = '(خوای گه&zwnj;وره ده&zwnj;فه&zwnj;رموێت، ئێمه&zwnj;ش فه&zwnj;رمانمان ده&zwnj;رکرد و) وتمان: ئه&zwnj;ی ئاگر ساردو سه&zwnj;لامه&zwnj;ت له&zwnj;سه&zwnj;ر ئیبراهیم (هه&zwnj;رچه&zwnj;نده ئاگر سوتێنه&zwnj;ره باڵام به فه&zwnj;رمانی خوا توانای سووتاندنی نه&zwnj;ماو ئه&zwnj;گه&zwnj;ر په&zwnj;روه&zwnj;ردگار فه&zwnj;رمانی به&zwnj;سه&zwnj;لامه&zwnj;تی نه&zwnj;دایه حه&zwnj;زره&zwnj;تی ئیبراهیم له&zwnj;ناو ئاگره&zwnj;که&zwnj;دا سه&zwnj;رمای ده&zwnj;بوو).';
$t[70] = 'ئه&zwnj;وانه ویستیان پیلان بگێڕن دژی ئیبراهیم ئینجا ئێمه&zwnj;ش ئه&zwnj;وانمان به ڕه&zwnj;نجه&zwnj;ڕۆترین که&zwnj;س گێڕا.';
$t[71] = 'ئێمه ئه&zwnj;ویش و - لوط - ی برازایشمان ڕزگار کرد و ناردمانن بۆ (وڵاتی شام) ئه&zwnj;و سه&zwnj;رزه&zwnj;مینه&zwnj;ی که به&zwnj;ره&zwnj;که&zwnj;تمان به&zwnj;سه&zwnj;ردا ڕژاندووه بۆ هه&zwnj;موو خه&zwnj;ڵکی.';
$t[72] = 'هه&zwnj;روه&zwnj;ها ئێمه ئیسحاقمان به&zwnj;خشی به ئیبراهیم و یه&zwnj;عقوبیش به&zwnj;نه&zwnj;وه&zwnj;ی (وه&zwnj;ک زیاده ڕێزێک بۆ ئیبراهیم)، هه&zwnj;ر یه&zwnj;ک له&zwnj;وانمان کرد به پیاوچاک و خواناس.';
$t[73] = 'هه&zwnj;موو ئه&zwnj;وانیشمان کرده پێشه&zwnj;واو به فه&zwnj;رمانی ئێمه ڕێنموویی خه&zwnj;ڵکیان ده&zwnj;کردو نیگامان بۆ کردن به ئه&zwnj;نجامدانی کارو کرده&zwnj;وه چاکه&zwnj;کان و ڕاگرتنی نوێژ و زه&zwnj;کات دان، (به&zwnj;ڕاستی) ئه&zwnj;وان هه&zwnj;ر ئێمه&zwnj;یان ده&zwnj;په&zwnj;رست و هه&zwnj;ر به&zwnj;ندایه&zwnj;تی ئێمه&zwnj;یان ده&zwnj;کرد.';
$t[74] = 'به لوط پێغه&zwnj;مبه&zwnj;ریش حیکمه&zwnj;ت و دانایی و زانیاریمان به&zwnj;خشی و ڕزگارمان کرد له&zwnj;و شاره&zwnj;ی کرده&zwnj;وه پیسه&zwnj;کانیان تێدا ئه&zwnj;نجام ده&zwnj;دا، بێگومان ئه&zwnj;وانه قه&zwnj;ومێکی ناله&zwnj;بار و خراپ و به&zwnj;دخوو تاوانکار بوون...';
$t[75] = 'ئێمه ئه&zwnj;ومان خسته ژێر سایه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆمانه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و له ڕیزی به&zwnj;نده چاک و باشه&zwnj;کاندایه&zwnj;.';
$t[76] = 'یادی نوح پێغه&zwnj;مبه&zwnj;ریش بکه&zwnj;ره&zwnj;وه کاتێک هاناو هاواری بۆ هێناین له&zwnj;مه&zwnj;و پێش، جا ئێمه&zwnj;ش نزامان گیرا کردو خۆی ه خێزانه&zwnj;که&zwnj;یمان له ته&zwnj;نگانه گه&zwnj;وره&zwnj;که ڕزگار کرد...';
$t[77] = 'سه&zwnj;رکه&zwnj;وتنیشمان پێبه&zwnj;خشی به&zwnj;سه&zwnj;ر ئه&zwnj;و قه&zwnj;وم و هۆزه&zwnj;ی به&zwnj;ڵگه و نیشانه&zwnj;کانی ئێمه&zwnj;یان به درۆ ده&zwnj;زانی و بڕوایان پێی نه&zwnj;بوو، چونکه به&zwnj;ڕاستی ئه&zwnj;وانیش قه&zwnj;ومێکی ناله&zwnj;بارو خراپ بوون، به گشتی نوقمی ئاوی زریانه&zwnj;که&zwnj;مان کردن.';
$t[78] = 'یادی داودو سله&zwnj;یمانیش بکه&zwnj;ره&zwnj;وه کاتێک بڕیاردرا ده&zwnj;رباره&zwnj;ی ئه&zwnj;و کێلگه کشتوکاڵیه&zwnj;، کاتێک که مه&zwnj;ڕو ماڵاتی خه&zwnj;ڵکی تیایدا بڵاو بوونه&zwnj;وه به&zwnj;شه&zwnj;و و زیانیان به کێلگه&zwnj;که گه&zwnj;یاند، ئێمه&zwnj;یش شایه&zwnj;ت بووین له&zwnj;سه&zwnj;ر ئه&zwnj;و داوه&zwnj;ری و بڕیاره&zwnj;ی ئه&zwnj;وان. (له&zwnj;سه&zwnj;رده&zwnj;می حوکمی حه&zwnj;زره&zwnj;تی داوددا ڕانه&zwnj;مه&zwnj;ڕێک زه&zwnj;وی و زاری کابرایه&zwnj;کی کاولکرد، کابراش چوو سکاڵای خۆی گه&zwnj;یانده داود، ئه&zwnj;ویش بڕیاریدا که هه&zwnj;موو ڕانه مه&zwnj;ڕه&zwnj;که بدرێت به خاوه&zwnj;ن زه&zwnj;ویه&zwnj;که&zwnj;، به&zwnj;ڵام حه&zwnj;زره&zwnj;تی سوله&zwnj;یمانی کوڕی ڕایه&zwnj;کی په&zwnj;سه&zwnj;ندتری هه&zwnj;بوو وتی: با به&zwnj;روبومی ماڵاته&zwnj;که بدرێت به خاوه&zwnj;ن زه&zwnj;ویه&zwnj;که تا زیانه&zwnj;که&zwnj;ی پڕ ده&zwnj;کاته&zwnj;وه&zwnj;).';
$t[79] = 'جا ئێمه چاره&zwnj;سه&zwnj;ری کێشه&zwnj;که&zwnj;مان له سوله&zwnj;یمان گه&zwnj;یاند، به&zwnj;ڵام هه&zwnj;ریه&zwnj;که (له&zwnj;م باوک و کوڕه&zwnj;) به&zwnj;هره&zwnj;وه&zwnj;رمان کردبوو له حیکمه&zwnj;ت و دانایی وعیلم و زانستی، هه&zwnj;روه&zwnj;ها باڵنده&zwnj;و چیاکانیشمان له&zwnj;گه&zwnj;ڵ داودا ملکه&zwnj;چ وفه&zwnj;رمانبه&zwnj;ردار کردبوو، هه&zwnj;مووان پێکه&zwnj;وه ته&zwnj;سبیحات و ستایشی ئێمه&zwnj;یان ده&zwnj;کرد، ئێمه&zwnj;ش ئه&zwnj;وه&zwnj;مان کرد.';
$t[80] = 'هه&zwnj;روه&zwnj;ها ئێمه داودمان فێری دروستکردنی زرێ و قه&zwnj;ڵغان کرد، تا له توندو تیژی کاتی شه&zwnj;ڕ بتانپارێزێت، ئایا (له به&zwnj;رامبه&zwnj;ر ئه&zwnj;م هه&zwnj;موو نازو نیعمه&zwnj;تانه&zwnj;وه&zwnj;) ئێوه سوپاسگوزار ده&zwnj;بن؟!';
$t[81] = 'بۆ سوله&zwnj;یمانیش بایه&zwnj;کی به&zwnj;هێزو تیژمان ڕه&zwnj;خساند که به&zwnj;رمانی ئه&zwnj;و، جاروبار هاتووچۆی ده&zwnj;کرد بۆ ئه&zwnj;و زه&zwnj;ویه&zwnj;ی که به&zwnj;ره&zwnj;که&zwnj;تمان به&zwnj;سه&zwnj;ردا ڕژاندبوو و ئێمه به&zwnj;هه&zwnj;موو شتێک زاناو شاره&zwnj;زاین.';
$t[82] = 'له په&zwnj;ریه&zwnj;کانیش هه&zwnj;ندێکمان بۆ ڕام هێنابوو که بچنه ناو بنی ده&zwnj;ریاوه (تا گه&zwnj;وهه&zwnj;ر و مرواری و شتی به نرخی بۆ ده&zwnj;ربهێنن) و جگه له&zwnj;وه کاری تریشیان ده&zwnj;کرد (وه&zwnj;کو دروستکردنی کۆشک وته&zwnj;لارو په&zwnj;یکه&zwnj;رو... هتد) و ئێمه&zwnj;ش چاودێریمان ده&zwnj;کردن (تا کاره&zwnj;کانیان به چاکی ئه&zwnj;نجام بده&zwnj;ن و زیان به&zwnj;خه&zwnj;ڵک نه&zwnj;گه&zwnj;یه&zwnj;نن).';
$t[83] = 'یادی - ئه&zwnj;یوب- یش بکه کاتێک هاناو هاواری بۆ په&zwnj;روه&zwnj;ردگاری برد (دوای ئه&zwnj;وه&zwnj;ی ماوه&zwnj;یه&zwnj;کی دوورو درێژ نه&zwnj;خۆش که&zwnj;وتبوو، جگه له&zwnj;وه پێشتریش ماڵ و سامان ومنداڵه&zwnj;کانی تیاچوو بوون) وتی: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی من ئازارو ناخۆشیم تووش بووه&zwnj;، تۆش له هه&zwnj;موو که&zwnj;س میهره&zwnj;بانترو له هه&zwnj;موو که&zwnj;س دلۆڤانتریت.';
$t[84] = 'جا ئێمه نزامان گیراکردو به&zwnj;هاواریه&zwnj;وه چووین وهه&zwnj;موو ناخۆشی و نه&zwnj;خۆشیه&zwnj;کمان له کۆڵ کرده&zwnj;وه&zwnj;، هه&zwnj;موو ماڵ و منداڵیمان پێبه&zwnj;خشیوه و ئه&zwnj;وه&zwnj;نده&zwnj;ی تریش له&zwnj;گه&zwnj;ڵیاندا (ده&zwnj;کرێت خوای گه&zwnj;وره هه&zwnj;ر هه&zwnj;مان ماڵ و منداڵی پێشووی بۆ زیندوو کردبێته&zwnj;وه&zwnj;، یاخود نه&zwnj;وه&zwnj;ی نوێی پێبه&zwnj;خشیبێت)، ئه&zwnj;وه ڕه&zwnj;حمه&zwnj;تێکی تایبه&zwnj;تی بوو له لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، یادا&zwnj;وه&zwnj;ریشه بۆ به&zwnj;نده خواپه&zwnj;رسته&zwnj;کان (که ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانیش تووشی به&zwnj;ڵاو ناخۆشی بوون وه&zwnj;ک ئه&zwnj;م، له&zwnj;وه&zwnj;ودوا نزایان کرد، خوای میهره&zwnj;بان به هاناو هاواری ئه&zwnj;وانیشه&zwnj;وه ده&zwnj;چێت).';
$t[85] = 'یادی (اسماعیل و ادریس و ذوالکفل) یش بکه&zwnj;ره&zwnj;وه&zwnj;، هه&zwnj;ریه&zwnj;که له&zwnj;وان (له تاقیکردنه&zwnj;وه جۆراو جۆره&zwnj;کاندا سه&zwnj;رکه&zwnj;وتووبوون) له خۆگرو ئارامگران بوون.';
$t[86] = 'ئێمه هه&zwnj;موویانمان خسته ژێر سایه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆمانه&zwnj;وه&zwnj;، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه له چاک و پاکانن.';
$t[87] = 'یادی (ذو النون)یش بکه&zwnj;ره&zwnj;وه (که یونس پێغه&zwnj;مبه&zwnj;ره&zwnj;) کاتێک به توڕه&zwnj;یی&zwnj;یه&zwnj;وه ڕۆیشت، گومانی وابوو که هه&zwnj;رگیز دنیای لێ ته&zwnj;نگ ناکه&zwnj;ینه&zwnj;وه (به&zwnj;ڵام به پێچه&zwnj;وانه&zwnj;ی گومانه&zwnj;که&zwnj;یه&zwnj;وه خوا خستیه ناو سکی نه&zwnj;هه&zwnj;نگه&zwnj;وه&zwnj;)، جا له ناو تاریکایه&zwnj;کاندا، (تاریکای شه&zwnj;وو ده&zwnj;ریا و ناوسکی نه&zwnj;هه&zwnj;نگه&zwnj;که&zwnj;دا) هاوارو نزای لێ به&zwnj;رز بۆوه&zwnj;و وتی: په&zwnj;روه&zwnj;ردگارا هیچ خوایه&zwnj;ک نیه جگه له تۆ، پاکی و بێگه&zwnj;ردی هه&zwnj;ر بۆ تۆیه&zwnj;، به&zwnj;ڕاستی من له سته&zwnj;مکاران بووم (کاتێک به بێ فه&zwnj;رمانی تۆ قه&zwnj;ومه&zwnj;که&zwnj;م به&zwnj;جێهێشت).';
$t[88] = 'ئێمه&zwnj;ش به&zwnj;هانایه&zwnj;وه چووین و ڕزگارمان کرد؛ له&zwnj;و غه&zwnj;م و په&zwnj;ژاره&zwnj;و ته&zwnj;نگانه&zwnj;یه&zwnj;ی که تێی که&zwnj;وتبوو، به&zwnj;و شێوه&zwnj;یه&zwnj;ش ئیمانداران ڕزگار ده&zwnj;که&zwnj;ین (کاتێک که ده&zwnj;که&zwnj;ونه ته&zwnj;نگانه&zwnj;وه&zwnj;و هاناو هاوار بۆ ئێمه ده&zwnj;هیێنن).';
$t[89] = 'یادی (زکریا) ش بکه&zwnj;ره&zwnj;وه کاتێک هاناو هاواری بۆ په&zwnj;روه&zwnj;ردگاری بردو وتی: په&zwnj;روه&zwnj;ردگارا؛ من به ته&zwnj;نهای مه&zwnj;هێڵه&zwnj;ره&zwnj;وه (نه&zwnj;وه&zwnj;یه&zwnj;کی چاک و پاکم پێ ببه&zwnj;خشه&zwnj;) تۆیش چاکترین زاتێکی له پاش من و هه&zwnj;موو مردوویه&zwnj;ک ده&zwnj;مێنیته&zwnj;وه&zwnj;.';
$t[90] = 'ئینجا ئێمه نزاکه&zwnj;یمان گیرا کردو (یحیی)مان پێبه&zwnj;خشی، هاوسه&zwnj;ره&zwnj;که&zwnj;شیمان بۆ چاککرد که منداڵی ببێ، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه چالاک و گورجو گۆڵ بوون له ئه&zwnj;نجامدانی هه&zwnj;موو خێرو چاکه&zwnj;یه&zwnj;کدا و نزای به&zwnj;کوڵیان ده&zwnj;کرد، به ئومێدی ڕه&zwnj;حمه&zwnj;ت و به&zwnj;هه&zwnj;شتی ئێمه و له دۆزه&zwnj;خ و خه&zwnj;شمی ئێمه ده&zwnj;ترسانو ئه&zwnj;وانه هه&zwnj;میشه گه&zwnj;ردن که&zwnj;چ بوون بۆ ئێمه&zwnj;.';
$t[91] = 'یادی (مریم) یش بکه&zwnj;ره&zwnj;وه&zwnj;، ئه&zwnj;و ئافره&zwnj;ته&zwnj;ی داوێنی خۆی به خاوێنی ڕاگرت، ئێمه&zwnj;ش له&zwnj;و ڕۆحه&zwnj;ی خاوه&zwnj;نی بووین پێمان به&zwnj;خشی و خۆی و کوڕه&zwnj;که&zwnj;یمان کرد به (موعجیزه&zwnj;) و به&zwnj;ڵگه بۆ هه&zwnj;موو خه&zwnj;ڵکی.';
$t[92] = 'خه&zwnj;ڵکینه! به&zwnj;ڕاستی ئه&zwnj;م ئایینه&zwnj;ی که ئه&zwnj;م پێغه&zwnj;مبه&zwnj;رانه هێناویانه ئایینه&zwnj;که&zwnj;ی ئێوه&zwnj;یه&zwnj;، که یه&zwnj;ک ئایینه و منیش په&zwnj;روه&zwnj;ردگارتانم، ته&zwnj;نها من بپه&zwnj;رستن';
$t[93] = '(له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا، له ڕێبازی یه&zwnj;کتاناسی و ئاینی ئیسلام لایانداو) به&zwnj;ش به&zwnj;شیان کرد له نێوانی خۆیاندا. جۆره&zwnj;ها ده&zwnj;سته&zwnj;و گرۆ دروست بوو، به مه&zwnj;رجێک هه&zwnj;ر هه&zwnj;مووشیان بۆ لای ئێمه ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه (له&zwnj;ده&zwnj;ستمان ده&zwnj;رناچن).';
$t[94] = 'جا ئه&zwnj;وه&zwnj;ی کارو کرده&zwnj;وه چاکه&zwnj;کان ئه&zwnj;نجام بدات له کاتێکدا که ئه&zwnj;و که&zwnj;سه باوه&zwnj;ڕداره&zwnj;، جا ئه&zwnj;وه هه&zwnj;وڵ و کۆششه&zwnj;که&zwnj;ی پشتگوێ ناخرێت و به&zwnj;زایه ناچێت به&zwnj;ڕاستی ئێمه بۆی یاداشت و تۆمار ده&zwnj;که&zwnj;ین.';
$t[95] = 'جارێکی تر حه&zwnj;رامه و ئه&zwnj;سته&zwnj;مه له دانیشتوانی هه&zwnj;ر شارو شارۆچکه&zwnj;یه&zwnj;ک که کاولمان کردووه&zwnj;، بێگومان ناگه&zwnj;ڕێنه&zwnj;وه&zwnj;...';
$t[96] = 'هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی به&zwnj;ربه&zwnj;سته&zwnj;که&zwnj;ی (ذو القرنین) له&zwnj;سه&zwnj;ر دوو تیره&zwnj;ی یاجوج و ماجوج ده&zwnj;کرێته&zwnj;وه (که ئه&zwnj;مه&zwnj;ش یه&zwnj;کێکه له نیشانه&zwnj;کانی نزیک بوونه&zwnj;وه&zwnj;ی کۆتای دنیا) و ئه&zwnj;وانه ئه&zwnj;و کاته له هه&zwnj;موو کون و که&zwnj;لێنێکه&zwnj;وه به لێشاو په&zwnj;یدا ده&zwnj;بن و زۆر ده&zwnj;بن.';
$t[97] = 'ئه&zwnj;وسا ئیتر به&zwnj;ڵێنی ڕاستی نزیک ده&zwnj;بێته&zwnj;وه و ده&zwnj;ست پێ ده&zwnj;کات، ئینجا ده&zwnj;بینیت چاوی ئه&zwnj;وانه&zwnj;ی بێ&zwnj;باوه&zwnj;ڕن ئه&zwnj;بڵه&zwnj;ق و زه&zwnj;ق ده&zwnj;بێت، (بێ په&zwnj;روا ده&zwnj;ڵێن) هاوار بۆ ئێمه&zwnj;، چاک غافڵ بووین له&zwnj;م ڕۆژه&zwnj;، له&zwnj;م سه&zwnj;ره&zwnj;نجامه&zwnj;، نه&zwnj;ک هه&zwnj;ر ئه&zwnj;وه&zwnj;! به&zwnj;ڵکو ئێمه هه&zwnj;ر سته&zwnj;مکاربووین.';
$t[98] = '(پاشان پێیان ده&zwnj;وترێت) بێگومان ئێوه و ئه&zwnj;و شتانه&zwnj;ی له جیاتی خوا ده&zwnj;تانپه&zwnj;رست سووته&zwnj;مه&zwnj;نی دۆزه&zwnj;خن و سه&zwnj;لکه بزوتی ئاگرن، ده&zwnj;بێت هه&zwnj;ر بچنه ناوی و هه&zwnj;میشه تێیدان.';
$t[99] = '(هه&zwnj;روه&zwnj;ها به&zwnj;هاوه&zwnj;ڵگه&zwnj;ڕان ده&zwnj;وترێت) ئه&zwnj;وانه&zwnj;ی ئێوه ده&zwnj;تانپه&zwnj;رست ئه&zwnj;گه&zwnj;ر خوا بوونایه ئاوا نه&zwnj;ده&zwnj;خرانه ناو دۆزه&zwnj;خه&zwnj;وه&zwnj;، بێگومان هه&zwnj;رهه&zwnj;موویان (هاوه&zwnj;ڵگه&zwnj;ران و په&zwnj;رستراوه&zwnj;کانیان) له دۆزه&zwnj;خدا بۆ هه&zwnj;میشه ده&zwnj;مێننه&zwnj;وه&zwnj;.';
$t[100] = 'ئه&zwnj;وانه&zwnj;؛ به&zwnj;هه&zwnj;ناسه&zwnj;ی قورس و لرخه&zwnj;لرخ ئاهو ناڵه&zwnj;وه (ژیانی سه&zwnj;ختی تیایدا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر) و مژده و هیواو قسه&zwnj;یه&zwnj;کی خۆش نابیستن.';
$t[101] = 'به&zwnj;ڕاستی ئه&zwnj;وانه که هه&zwnj;ر زوو بڕیارمانداوه چاکه بهێنینه&zwnj;وه ڕێیان (به&zwnj;هۆی ئیمان و باوه&zwnj;ڕو کرده&zwnj;وه&zwnj;ی چاکیانه&zwnj;وه&zwnj;) ئه&zwnj;وانه دووره په&zwnj;رێز کراون له دۆزه&zwnj;خه&zwnj;وه&zwnj;.';
$t[102] = 'قرچه قرچ و ورشه و ده&zwnj;نگی بڵێسه&zwnj;ی نابیستن، (به&zwnj;ڵکو ئه&zwnj;وان له نازو نیعمه&zwnj;تدا) که حه&zwnj;زیان لێی بوو و ئاره&zwnj;زوویان ده&zwnj;کرد بۆ هه&zwnj;تا هه&zwnj;تایی ده&zwnj;مێنن';
$t[103] = '(ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;) لێقه&zwnj;ومانی گه&zwnj;وره و ته&zwnj;نگانه&zwnj;ی بێ سنوور (قیامه&zwnj;ت) تووشی غه&zwnj;مباری و په&zwnj;ژاره&zwnj;یان ناکات، به&zwnj;ڵکو فریشه&zwnj;کان (به ڕووی خۆشه&zwnj;وه پێشوازیان لی ده&zwnj;که&zwnj;ن و) ده&zwnj;ڵێن ئه&zwnj;مه ئه&zwnj;و ڕۆژه&zwnj;یه که کاتی خۆی به&zwnj;ڵێنیان پێده&zwnj;درا (ئێوه ئیتر ڕزگارتان بوو له ته&zwnj;نگانه و ناخۆشی دنیا، بۆ هه&zwnj;میشه که&zwnj;ونه&zwnj;ته خۆشیه&zwnj;وه&zwnj;).';
$t[104] = 'ڕۆژێک دێت ئێمه ئاسمان ده&zwnj;پێچینه&zwnj;وه&zwnj;، وه&zwnj;کو چۆن کتێبی کراوه داده&zwnj;خرێت و ده&zwnj;پێچرێته&zwnj;وه هه&zwnj;روه&zwnj;ک چۆن له سه&zwnj;ره&zwnj;تاوه دروستمان کرد، وه&zwnj;ک ئه&zwnj;وسای خۆی لێ ده&zwnj;که&zwnj;ینه&zwnj;وه (بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;ر له&zwnj;نوێ به شێوه&zwnj;یه&zwnj;کی تر دروستی بکه&zwnj;ینه&zwnj;وه&zwnj;) جا ئه&zwnj;وه په&zwnj;یمان و به&zwnj;ڵێنی ئێمه&zwnj;یه&zwnj;، بێگومان ئێمه ئه&zwnj;و کاره ئه&zwnj;نجام ده&zwnj;ده&zwnj;ین.';
$t[105] = 'سوێند به خوا به ڕاستی ئێمه له کتێبی زه&zwnj;بووردا دوای ته&zwnj;ورات بڕیارمان داوه که&zwnj;: بێگومان به&zwnj;نده شیاو و چاکه&zwnj;کانم ده&zwnj;بنه خاوه&zwnj;نی زه&zwnj;وی.';
$t[106] = 'به&zwnj;ڕاستی ئا له&zwnj;م باسه&zwnj;دا مژده&zwnj;و ڕاگه&zwnj;یاندن بۆ که&zwnj;سانی خواپه&zwnj;رست و به&zwnj;نده ملکه&zwnj;چه&zwnj;کان.';
$t[107] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) ئێمه تۆمان ڕه&zwnj;وانه نه&zwnj;کردووه ته&zwnj;نها بۆ ئه&zwnj;وه نه&zwnj;بێت که ببیته ڕه&zwnj;حمه&zwnj;ت و به&zwnj;ره&zwnj;که&zwnj;ت بۆ هه&zwnj;موو جیهانه&zwnj;کان (جیهانی ئاده&zwnj;میزاد و گیاندار و ڕووه&zwnj;ک و په&zwnj;ری و... هتد)';
$t[108] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تۆ) بڵێ: به&zwnj;ڕاستی من ته&zwnj;نها ئاگادار ده&zwnj;کرێم که بێگومان خوای ئێوه هه&zwnj;ر خوایه&zwnj;کی تاک و ته&zwnj;نهایه&zwnj;، ئایا ئێوه ته&zwnj;سلیمی ئه&zwnj;م بڕیاره ده&zwnj;بن و مسوڵمان ده&zwnj;بن؟!';
$t[109] = 'خۆ ئه&zwnj;گه&zwnj;ر پشتیان هه&zwnj;ڵکردو گوێیان نه&zwnj;گرت (بۆ گوفتارو به&zwnj;رنامه&zwnj;ی ڕوون و ئاشکرای تۆ، ئه&zwnj;وه پێیان) بڵێ: من په&zwnj;یامی خوام به یه&zwnj;کسانی به هه&zwnj;مووتان گه&zwnj;یاندووه (هیچ شتێکم له که&zwnj;س نه&zwnj;شاردۆته&zwnj;وه&zwnj;) من ئیتر نازانم ئه&zwnj;و هه&zwnj;ڕه&zwnj;شه&zwnj;یه&zwnj;ی که له ئێوه ده&zwnj;کرێت نزیکه یان دووره (ده&zwnj;رباره&zwnj;ی شکستی ئێوه&zwnj;، یان به&zwnj;رپا بوونی قیامه&zwnj;ت).';
$t[110] = '(دڵنیا بن) به ڕاستی هه&zwnj;ر ئه&zwnj;و زاته خۆی ده&zwnj;زانێت و ئاگایه به گوفتاری ئاشکراو هه&zwnj;ر ئه&zwnj;ویش ده&zwnj;زانێت و ئاگاداره له&zwnj;وه&zwnj;ی که ده&zwnj;یشارنه&zwnj;وه (له کارو کرده&zwnj;وه&zwnj;تان).';
$t[111] = 'من نازانم له&zwnj;وانه&zwnj;یه ئه&zwnj;م دواخستنی تۆڵه و به&zwnj;ڵێنی په&zwnj;روه&zwnj;ردگاره بۆ تاقیکردنه&zwnj;وه و سزاتان بێت و ڕابواردنێکیش بێت تا کاتێکی دیاریکراو (له دنیای کۆتا ته&zwnj;مه&zwnj;ندا، یاخود به&zwnj;ڵکو به خۆدا بچنه&zwnj;وه و ڕێبازی دینداری بگرنه به&zwnj;ر).';
$t[112] = '(دوای گه&zwnj;یاندنی په&zwnj;یامی خوا و گوێ پێنه&zwnj;دانی خوانه&zwnj;ناسان) پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)ووتی: په&zwnj;روه&zwnj;ردگارا دادگه&zwnj;رانه له نێوان من و ئه&zwnj;مانه&zwnj;دا داوه&zwnj;ری بفه&zwnj;رموو له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی، په&zwnj;روه&zwnj;ردگارمان به&zwnj;خشنده و میهره&zwnj;بانه&zwnj;وه داوای یارمه&zwnj;تی لێکراوه بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رمان بخات به&zwnj;سه&zwnj;ر ئه&zwnj;و پێناسه&zwnj;یه&zwnj;ی که ئێوه ده&zwnj;یکه&zwnj;ن ده&zwnj;رباره&zwnj;ی خواو پێغه&zwnj;مبه&zwnj;ر و په&zwnj;یامه&zwnj;که&zwnj;ی';
?>