$t = array();
$t[1] = 'ئه&zwnj;ی خه&zwnj;ڵکینه له په&zwnj;روه&zwnj;ردگارتان بترسن و خۆتان له خه&zwnj;شمی بپارێزن چونکه به&zwnj;ڕاستی زه&zwnj;وی له&zwnj;رزه&zwnj;ی قیامه&zwnj;ت و کاول بوونی بوونه&zwnj;وه&zwnj;ر کاره&zwnj;ساتێکی زۆر گه&zwnj;وره&zwnj;یه و ڕووداوێکی سامناکه&zwnj;.';
$t[2] = 'له&zwnj;و ڕۆژه&zwnj;دا که کاره&zwnj;ساته&zwnj;که ده&zwnj;بینن، هه&zwnj;موو شیرده&zwnj;رێك له شیر خۆره&zwnj;که&zwnj;ی بێئاگا ده&zwnj;بێت و فه&zwnj;رامۆشی ده&zwnj;کات، هه&zwnj;موو سك پڕو دوو گیانێکش کۆرپه&zwnj;له&zwnj;که&zwnj;ی ده&zwnj;دانێت، خه&zwnj;ڵکی ده&zwnj;بینیت، وه&zwnj;کو سه&zwnj;رخۆشن، له ڕاستیدا سه&zwnj;رخۆشیش نین، به&zwnj;ڵکو سزای خوایی زۆر سه&zwnj;خت و سامناکه و سه&zwnj;ری لێ شێواندوون.';
$t[3] = 'هه&zwnj;ندێ له خه&zwnj;ڵکی ده&zwnj;بینیت به&zwnj;بێ هیچ زانستی و زانیاریه&zwnj;ك ده&zwnj;رباره&zwnj;ی (گه&zwnj;وره&zwnj;یی و ده&zwnj;سه&zwnj;ڵاتی) خوای په&zwnj;روه&zwnj;ردگار موجاده&zwnj;له ده&zwnj;که&zwnj;ن و باس و خواس ده&zwnj;که&zwnj;ن و شوێن هه&zwnj;موو شه&zwnj;یتانێکی یاخی و سه&zwnj;رکه&zwnj;ش ده&zwnj;که&zwnj;ون.';
$t[4] = 'له&zwnj;سه&zwnj;ر شه&zwnj;یتان بڕیاردراوه&zwnj;، بێگومان ئه&zwnj;وه&zwnj;ی شوێنی بکه&zwnj;وێت و به&zwnj;گوێی بکات، ئه&zwnj;وه به&zwnj;ڕاستی گومڕای ده&zwnj;کات و ڕێگه&zwnj;ی بۆ خۆش ده&zwnj;کات تا به&zwnj;ره&zwnj;و سزای دۆزه&zwnj;خ بیبات.';
$t[5] = 'ئه&zwnj;ی خه&zwnj;ڵکینه ئه&zwnj;گه&zwnj;ر ئێوه گومانتان له زیندوو بوونه&zwnj;وه هه&zwnj;یه&zwnj;، ئه&zwnj;وه چاك بزانن که ئێمه سه&zwnj;ره&zwnj;تا ئێوه&zwnj;مان له خاك و خۆڵ دروستکردووه&zwnj;، له&zwnj;وه&zwnj;ودوا (نه&zwnj;وه&zwnj;کان) له نوتفه&zwnj;یه&zwnj;ك (په&zwnj;یدا بوون)، پاشان نوتفه&zwnj;ش ده&zwnj;بێته خۆهه&zwnj;ڵواسه&zwnj;رێك له&zwnj;ناو منداڵداندا، له&zwnj;وه&zwnj;ودوا ده&zwnj;بێته گۆشتپاره&zwnj;یه&zwnj;کی ته&zwnj;واوو سه&zwnj;رو سیمادار، یان ناته&zwnj;واو، (هه&zwnj;موو ئه&zwnj;م شتانه ده&zwnj;هێنینه&zwnj;وه یادتان) تا بۆتان ڕوون بکه&zwnj;ینه&zwnj;وه که ئێمه ئاوا ئێوه&zwnj;مان دروستکدووه و دووباره دروستکردنه&zwnj;وه&zwnj;شتان لامان ئاسانه&zwnj;، ئه&zwnj;وه&zwnj;ش بمانه&zwnj;وێت له منداڵدانه&zwnj;کاندا جێگیری ده&zwnj;که&zwnj;ین، تا کاتێکی دیاریکراو، له&zwnj;وه&zwnj;ودوا به&zwnj;ساوایی و به&zwnj;بچووکی ده&zwnj;رتانده&zwnj;هێنین (له سکی دایکتان) دوایی بۆ ئه&zwnj;وه&zwnj;ی گه&zwnj;وره ببن و بگه&zwnj;نه ئه&zwnj;وپه&zwnj;ڕی هێزو تواناتان، هه&zwnj;تانه ده&zwnj;مرێنرێت به&zwnj;گه&zwnj;نجی، هه&zwnj;شتانه ده&zwnj;گه&zwnj;ڕێنرێته&zwnj;وه بۆ په&zwnj;رپووتترنی ته&zwnj;مه&zwnj;نه&zwnj;کان، بۆ ئه&zwnj;وه&zwnj;ی هیچ شتێك نه&zwnj;زانێت دوای ئه&zwnj;وه&zwnj;ی جاران ده&zwnj;یزانی، (به&zwnj;ڵگه&zwnj;یه&zwnj;کی تریش له&zwnj;سه&zwnj;ر زیندووبوونه&zwnj;وه ئه&zwnj;وه&zwnj;یه که ئه&zwnj;ی ئینسان) تۆ زه&zwnj;ویش ده&zwnj;بینیت وشك و برنگه&zwnj;، جا کاتێك ئاوی بارانمان به&zwnj;سه&zwnj;ردا باراند، داده&zwnj;چڵه&zwnj;کێت و ده&zwnj;له&zwnj;رزێت و تۆوه&zwnj;کان چه&zwnj;که&zwnj;ره ده&zwnj;که&zwnj;ن و ڕه&zwnj;گ و چڵ ده&zwnj;رده&zwnj;که&zwnj;ن و ده&zwnj;ڕوێن، ئه&zwnj;وسا له هه&zwnj;موو جۆره گژوگیاو گوڵ و گوڵزارێکی جوان جووت ده&zwnj;ڕوێنێت.';
$t[6] = '(ئه&zwnj;م شتانه&zwnj;ی که باسکران) بۆ ئه&zwnj;وه&zwnj;یه که دڵنیابن هه&zwnj;ر خوا زاتێکی حه&zwnj;ق و ڕاسته&zwnj;، بێگومان هه&zwnj;ر ئه&zwnj;ویش به&zwnj;ئاسانی مردووان زیندوو ده&zwnj;کاته&zwnj;وه و به&zwnj;ڕاستی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[7] = 'بێگومان قیامه&zwnj;تیش هه&zwnj;ر به&zwnj;رپا ده&zwnj;بێت و پێش دێت و هیچ گومانی تێدا نیه و به&zwnj;ڕاستی ئه&zwnj;و خوایه هه&zwnj;ر هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی که له&zwnj;ناو گۆڕدان زیندوویان ده&zwnj;کاته&zwnj;وه و ده&zwnj;یانهێنێته ده&zwnj;ره&zwnj;وه&zwnj;.';
$t[8] = 'هه&zwnj;ندێك خه&zwnj;ڵکی هه&zwnj;ن موجاده&zwnj;له ده&zwnj;رباره&zwnj;ی زاتی خواو سیفاته پیرۆژه&zwnj;کانی ده&zwnj;که&zwnj;ن دوور له هه&zwnj;موو زانست و زانیاری بۆچوون و کتێب و سه&zwnj;رچاوه&zwnj;یه&zwnj;کی ڕوون و ئاشکرا.';
$t[9] = '(هه&zwnj;ندێك له&zwnj;و جۆره که&zwnj;سانه&zwnj;) ڕوو وه&zwnj;رده&zwnj;گێرێت و شان هه&zwnj;ڵده&zwnj;ته&zwnj;کێنێت و فیز ده&zwnj;کات تا خۆی و خه&zwnj;ڵکی له ڕێبازی خوا وه&zwnj;ربگێرێت، ئه&zwnj;و جۆره که&zwnj;سانه هه&zwnj;ر له دنیادا ڕیسوایی و خه&zwnj;جاڵه&zwnj;تی و سه&zwnj;رشۆڕی ده&zwnj;یانگرێته&zwnj;وه&zwnj;، له ڕۆژی قیامه&zwnj;تیشدا سزای سوتێنه&zwnj;ری پێ ده&zwnj;چێژین.';
$t[10] = '(ئه&zwnj;وسا پێی ده&zwnj;وترێت) ئه&zwnj;م سه&zwnj;رئه&zwnj;نجامه ده&zwnj;ستپێشکه&zwnj;ری خۆته تووشت هاتۆته&zwnj;وه&zwnj;، بێگومانیش خوا به&zwnj;هیچ جۆره سته&zwnj;م له به&zwnj;نده&zwnj;کانی ناکات.';
$t[11] = 'هه&zwnj;ندێك له خه&zwnj;ڵکی هه&zwnj;یه ته&zwnj;نها له یه&zwnj;ك حاڵه&zwnj;تدا خواپه&zwnj;رستی ده&zwnj;کات (ئه&zwnj;ویش ئه&zwnj;و حاڵه&zwnj;ته&zwnj;یه که دنیا به&zwnj;دڵی ئه&zwnj;و بێت)، ئه&zwnj;گه&zwnj;ر تووشی خێرو خۆشی بوو دڵی پێی خۆشه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر تووشی تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;ك هات، سواری سه&zwnj;ری خۆی ده&zwnj;بێت و هه&zwnj;ڵده&zwnj;گه&zwnj;رێته&zwnj;وه&zwnj;، ئا به&zwnj;و حاڵه&zwnj;ته&zwnj;ی دنیا و قیامه&zwnj;ت له ده&zwnj;ست خۆی ده&zwnj;دات و زه&zwnj;ره&zwnj;رمه&zwnj;ندی هه&zwnj;ردوو لا&zwnj;یه و هه&zwnj;ر ئه&zwnj;وه&zwnj;شه خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندی ڕوون و ئاشکرا.';
$t[12] = '(ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;) هاناو هاوار له&zwnj;که&zwnj;سێك ده&zwnj;که&zwnj;ن له&zwnj;جیاتی خوا، یاخود په&zwnj;نا بۆ که&zwnj;سێك ده&zwnj;به&zwnj;ن نه زه&zwnj;ره&zwnj;رو نه قازانجی پێده&zwnj;گه&zwnj;یه&zwnj;نێت، ئه&zwnj;و حاڵه&zwnj;ته گومڕایی و سه&zwnj;رلێشێواویه&zwnj;کی زۆر نابه&zwnj;جێ و دووره&zwnj;.';
$t[13] = '(نه&zwnj;فامان هاناو هاوار) له&zwnj;که&zwnj;سێك ده&zwnj;که&zwnj;ن که زه&zwnj;ره&zwnj;ری زیاتر و نزیکتره له قازانجی، ئای که چه&zwnj;نده یارو یاوه&zwnj;ر و هاوده&zwnj;مێکی ناڕه&zwnj;سه&zwnj;ن و خراپیان هه&zwnj;یه&zwnj;.';
$t[14] = 'به&zwnj;ڕاستی خوا ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه و کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه ده&zwnj;خاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، بێگومان خوای گه&zwnj;وره هه&zwnj;رشتێکی بوێت ئه&zwnj;نجامی ده&zwnj;دات.';
$t[15] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی واده&zwnj;زانێت و واگومان ده&zwnj;بات که خوا هه&zwnj;رگیز سه&zwnj;ری ناخات و پشتیوانی لێناکات، نه له&zwnj;دنیادا نه&zwnj;له قیامه&zwnj;تدا، ده&zwnj;با په&zwnj;تێك به به&zwnj;رزیه&zwnj;وه هه&zwnj;ڵبواسێت (ملی خۆی پیادا بکات) ئینجا بیبڕێت و خۆی بخنکێنێت (یاخود که&zwnj;سێك واگومان ده&zwnj;بات که هه&zwnj;رگیز خوا پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) و په&zwnj;یامه&zwnj;که&zwnj;ی سه&zwnj;رناخات له دنیاو قیامه&zwnj;تدا، با بڕوا بهێنێت به&zwnj;ئایینێکی ئاسمانی پاشان پاشگه&zwnj;ز بێته&zwnj;وه&zwnj;) با ته&zwnj;ماشا بکات و بزانێت ئه&zwnj;و پیلان و فێڵ و نه&zwnj;خشه&zwnj;یه&zwnj;ی، ڕق و کینه و بوغزی لاده&zwnj;بات؟!';
$t[16] = 'ئا به&zwnj;و شێوه&zwnj;یه قورئانمان دابه&zwnj;زاندووه&zwnj;، که ئایه&zwnj;ته&zwnj;کانی هه&zwnj;مووی ڕوون و ئاشکران، به&zwnj;ڕاستی خوا هه&zwnj;ر که&zwnj;سێکی بوێت (شایسته بێت، به&zwnj;م قورئانه) هیدایه&zwnj;تی ده&zwnj;دات.';
$t[17] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه و ئه&zwnj;وانه&zwnj;ش که بوونه&zwnj;ته جوو، له&zwnj;گه&zwnj;ڵ صابئه&zwnj;کاندا که فریشته په&zwnj;رست؛ یان ئه&zwnj;ستێره په&zwnj;رستن و گاوره&zwnj;کان و ئاگرپه&zwnj;رسته&zwnj;کان و ئه&zwnj;وانه&zwnj;ی که هه&zwnj;وه&zwnj;ڵایان (بۆ خوا) بڕیارداوه&zwnj;، بێگومان خوای گه&zwnj;وره له ڕۆژی قیامه&zwnj;تدا جیایان ده&zwnj;کاته&zwnj;وه له&zwnj;یه&zwnj;کترو بڕیاری خۆی ده&zwnj;دات له&zwnj;سه&zwnj;ریان، به&zwnj;ڕاستی خوا شایه&zwnj;ته به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکه&zwnj;وه&zwnj;.';
$t[18] = 'ئایا نه&zwnj;تبینیوه و نه&zwnj;تبیستووه که به&zwnj;ڕاستی هه&zwnj;رکه&zwnj;س و هه&zwnj;رچی هه&zwnj;یه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا سوژده بۆ خوا ده&zwnj;به&zwnj;ن، هه&zwnj;روه&zwnj;ها خۆرو مانگ و ئه&zwnj;ستێره و چیاکان و دره&zwnj;خت و ماڵات و زۆرێك له&zwnj;خه&zwnj;ڵکی، زۆرێکیش خۆی شایسته&zwnj;ی سزاو تۆڵه کردووه&zwnj;، جا ئه&zwnj;وه&zwnj;ی خوا قه&zwnj;درو ڕێزی بشکێنێت و ڕیسوای بکات ئه&zwnj;وه هیچ ڕێز لێنه&zwnj;رێکی نیه&zwnj;، به&zwnj;ڕاستی خوا ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت ده&zwnj;یکات.';
$t[19] = 'ئه&zwnj;مانه دووده&zwnj;سته&zwnj;ی دژبه&zwnj;یه&zwnj;کن (ئیمانداران و بێ باوه&zwnj;ڕان) کردیانه کێشه له&zwnj;باره&zwnj;ی په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه (له زات و سیفات و قه&zwnj;زاو قه&zwnj;ده&zwnj;ری و... هتد): جا ئه&zwnj;وانه&zwnj;یان که بێ باوه&zwnj;ڕ بوون پارچه پۆشاك له ئاگر بڕڕاوه به&zwnj;باڵایان، ئاوی زۆر له&zwnj;کوڵ له&zwnj;سه&zwnj;رویانه&zwnj;وه ده&zwnj;کرێت به&zwnj;سه&zwnj;ریاندا (له ده&zwnj;رئه&zwnj;نجامی سه&zwnj;ر سه&zwnj;ختی و سه&zwnj;رکه&zwnj;شییاندا).';
$t[20] = 'ئه&zwnj;و ئاوه له&zwnj;کوڵه هه&zwnj;رچی له&zwnj;ناو سکیاندا هه&zwnj;یه به&zwnj;پێسته&zwnj;کانه&zwnj;وه ده&zwnj;یتوێنێته&zwnj;وه&zwnj;.';
$t[21] = 'گورزیش له ئاسن (بۆ پیاماڵین و لێدانیان) ئاماده&zwnj;کراوه&zwnj;.';
$t[22] = 'هه&zwnj;ر که هه&zwnj;وڵ بده&zwnj;ن و بیانه&zwnj;وێت خۆیان ڕزگار بکه&zwnj;ن و له&zwnj;و سزایه هه&zwnj;ڵبێن (به&zwnj;قامچی و تێهه&zwnj;ڵدان) ده&zwnj;گێڕدرێنه&zwnj;وه ناوی و پێیان ده&zwnj;وترێت: بچێژن سزای سووتان و ئاگر.';
$t[23] = 'به&zwnj;ڕاستی خوا ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه ده&zwnj;خاته ناو باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا و به&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا جاری و ڕه&zwnj;وانه&zwnj;، ده&zwnj;ستیان ده&zwnj;رازێنرێته&zwnj;وه به&zwnj;بازنی ئاڵتون و مرواری (له&zwnj;شێوه&zwnj;ی سه&zwnj;عاتی ده&zwnj;ست و هه&zwnj;ر جۆرێکدا که حه&zwnj;زی لێ بکه&zwnj;ن)، پۆشاکیشیان له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا ئاوریشمی جوان و نایاب و هه&zwnj;مه&zwnj;ڕه&zwnj;نگه&zwnj;.';
$t[24] = 'ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه ڕێنموویی کراون بۆ گوفتاری جوان و په&zwnj;سه&zwnj;ند، هه&zwnj;روه&zwnj;ها پێشتر ڕێنموویی کرابوون بۆ ڕێبازی ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;ی که شایسته&zwnj;ی شوکرو سوپاسه&zwnj;.';
$t[25] = 'بێگومان ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون و دژایه&zwnj;تی ڕێبازی خوا به&zwnj;جۆره&zwnj;ها شێوه ده&zwnj;که&zwnj;ن و نایه&zwnj;ڵن خه&zwnj;ڵکی ڕووبکاته مزگه&zwnj;وتی حه&zwnj;رام که بڕیارمانداوه بۆ هه&zwnj;موو خه&zwnj;ڵکی بێت چوونیه&zwnj;که بۆ ئه&zwnj;وانی که له&zwnj;وێ ده&zwnj;ژین، یان ئه&zwnj;وانه&zwnj;ی له شوێنانی تره&zwnj;وه بۆی ده&zwnj;چن، جا ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت به&zwnj;هۆی یاخی بوون و لادانه&zwnj;وه سته&zwnj;م بکات، ئه&zwnj;وه سزایه&zwnj;کی به ئازاری پێ ده&zwnj;چێژین.';
$t[26] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) به&zwnj;یاد بێنه&zwnj;) کاتێك شوێنی که&zwnj;عبه&zwnj;مان دیاری کرد بۆ ئیبراهیم (دوای ته&zwnj;واوبوو&zwnj;نی، فه&zwnj;رمانمان پێدا) که هیچ جۆره هاوه&zwnj;ڵێکم بۆ بڕیار نه&zwnj;ده&zwnj;یت و (بیکه به بنکه&zwnj;ی یه&zwnj;کخواناسی) و ماڵی من پاك بکه&zwnj;ره&zwnj;وه بۆ ئه&zwnj;وانه&zwnj;ی به&zwnj;ده&zwnj;وریدا ده&zwnj;سوڕێنه&zwnj;وه&zwnj;، یاخود به پێوه&zwnj;ن یان له کڕنووش و سوژده بردندان.';
$t[27] = 'جا بانگی خه&zwnj;ڵکی بکه و جاڕ بده له ناویاندا با بێن بۆ حه&zwnj;ج، ئه&zwnj;وانیش به&zwnj;پیاده دێن به&zwnj;ده&zwnj;م بانگه&zwnj;وازه&zwnj;که&zwnj;ته&zwnj;وه&zwnj;، یان به&zwnj;سواری (ئه&zwnj;و وشترانه&zwnj;ی که دووری ماوه&zwnj;که&zwnj;) لاوازی کردوون و له هه&zwnj;موو ڕێگه&zwnj;یه&zwnj;کی دووره&zwnj;وه هه&zwnj;ر دێن...';
$t[28] = 'بۆ ئه&zwnj;وه&zwnj;ی سوود و که&zwnj;ڵکی ماددی و مه&zwnj;عنه&zwnj;وی به&zwnj;ده&zwnj;ست بهێنن و ببینن و یادی خواش بکه&zwnj;ن و ناوی پیرۆزی به&zwnj;رده&zwnj;وام له&zwnj;سه&zwnj;ر زاریان بێت له ڕۆژانێکی دیاریکراودا (که ڕۆژانی عه&zwnj;ره&zwnj;فه و جه&zwnj;ژنی قوربانه&zwnj;، سروودی (لبیك أللهم لبیك، لبیك لا شریك لك لبیك، إن الحمد و النعمة لك والملك، لا شریك لك)، له حه&zwnj;جدا (ألله أکبر، ألله أکبر، ألله أکبر، لا إله إلاألله و ألله أکبر ولله الحمد) له ڕۆژانی جه&zwnj;ژندا، فه&zwnj;رامۆش نه&zwnj;که&zwnj;ن، هه&zwnj;روه&zwnj;ها ناوی خوا (بسم الله&zwnj;) بکه&zwnj;ن له&zwnj;کاتی سه&zwnj;ربڕینی ئه&zwnj;و ماڵاتانه&zwnj;دا که پێمان به&zwnj;خشیوون، ئه&zwnj;وسا له گۆشته&zwnj;که&zwnj;ی بخۆن و به&zwnj;شی هه&zwnj;ژارو نه&zwnj;دارانیشی لێ بده&zwnj;ن.';
$t[29] = 'له&zwnj;وه&zwnj;ودوا با چڵك و ئاره&zwnj;ق و موو و نینۆك له خۆیان دووربخه&zwnj;نه&zwnj;وه (ئیحرام بشکێنن)، هه&zwnj;روه&zwnj;ها وه&zwnj;فا بکه&zwnj;ن به نه&zwnj;زره&zwnj;کانیان و جێبه&zwnj;جێی بکه&zwnj;ن و به&zwnj;ده&zwnj;وری ماڵی دێرین و به&zwnj;نرخی خوا (که&zwnj;عبه&zwnj;)دا، با (طواف) بکه&zwnj;ن.';
$t[30] = '(جه&zwnj;ج ئه&zwnj;وه&zwnj;بوو که باسکرا) جا ئه&zwnj;وه&zwnj;ی که سنووره&zwnj;کانی خوا بپارێزێت و به&zwnj;گه&zwnj;وره دایانبنێت و ڕێزی لێ بگرێت، بێگومان ئه&zwnj;وه چاکتره لای په&zwnj;روه&zwnj;ردگاری (بێ پاداشت نیه&zwnj;)... کۆشتی مه&zwnj;ڕو بزن و گاو و وشتر بۆتان حه&zwnj;ڵاله جگه له&zwnj;وانه&zwnj;ی که به&zwnj;سه&zwnj;رتاندا ده&zwnj;خوێنرێته&zwnj;وه و قه&zwnj;ده&zwnj;غه&zwnj;کراوه (که له سووره&zwnj;تی ألمائدة دا باسکرا)، جا له&zwnj;به&zwnj;رئه&zwnj;وه خۆتان له په&zwnj;رستنی بته ناپوخت و بێ نرخه&zwnj;کان بپارێزن، هه&zwnj;روه&zwnj;ها له&zwnj;گوفتاری درۆو قسه&zwnj;ی هه&zwnj;ڵبه&zwnj;ستراو خۆتان دووربگرن.';
$t[31] = 'هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;میشه شوێن ڕاستی بکه&zwnj;ون و ملکه&zwnj;چ بن بۆ حه&zwnj;ق له&zwnj;به&zwnj;ر خاتری زاتی خوا، نه&zwnj;که&zwnj;ن هیچ شه&zwnj;ریك و هاوه&zwnj;ڵێکی بۆ بڕیار بده&zwnj;ن، جا ئه&zwnj;وه&zwnj;ی هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ خوا بڕیار بدات، وه&zwnj;کو ئه&zwnj;وه وایه له ئاسمانه&zwnj;وه به&zwnj;ر ببێته&zwnj;وه و باڵنده په&zwnj;لاماری بدات و (پارچه پارچه&zwnj;ی بکات) یاخود ڕه&zwnj;شه&zwnj;بایه&zwnj;کی به&zwnj;هێز بیفڕێنێت و بیبات بۆشوێنێکی دوور و قوڵ.';
$t[32] = 'به&zwnj;و شێوه&zwnj;یه که حه&zwnj;ج باسکرا پێویسته ئه&zwnj;نجام بدرێت جا ئه&zwnj;وه&zwnj;ی ڕێز داده&zwnj;نێت بۆدروشم و به&zwnj;رنامه&zwnj;کانی خوا، ئه&zwnj;وه نیسانه&zwnj;ی پارێزکاری و له خواترسانی دڵه&zwnj;کانه&zwnj;.';
$t[33] = 'بۆتان هه&zwnj;یه چه&zwnj;نده&zwnj;ها سوود وه&zwnj;رگرن له&zwnj;و ماڵاته&zwnj;ی دانراوه بۆ قوربانی، تا کاتی دیاریکرا (که ڕۆژانی جه&zwnj;ژنی قوربانه&zwnj;) له&zwnj;وه&zwnj;ودوا شوێنی سه&zwnj;ربڕینیان به&zwnj;ره&zwnj;و ماڵه دێرینه&zwnj;که&zwnj;ی خوای گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[34] = 'بۆ هه&zwnj;ر ئوممه&zwnj;تێك (له ئوممه&zwnj;تانی پێشوو) ڕێ و شوێن و به&zwnj;رنامه&zwnj;یه&zwnj;کمان بۆ داناون (بۆ خواپه&zwnj;رستی و قوربانی) تا ناوی خوا به&zwnj;رن له&zwnj;کاتی سه&zwnj;ربڕینی مه&zwnj;ڵاته&zwnj;کاندا، خوای ئێوه خوایه&zwnj;کی تاك و ته&zwnj;نهایه&zwnj;، هه&zwnj;مووتان ته&zwnj;سلیم و ملکه&zwnj;چی ئه&zwnj;ون، مژده&zwnj;ش بده به&zwnj;به&zwnj;نده ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;ردارو دڵ ئاوه&zwnj;دانه&zwnj;کان...';
$t[35] = 'ئه&zwnj;وانه&zwnj;ی کاتێك ناوی خوا برا (یا باسی ده&zwnj;سه&zwnj;ڵات و ڕه&zwnj;حمه&zwnj;ت و زات و سیفاتی کرا) دڵکانیان داده&zwnj;چڵه&zwnj;کێت و ته&zwnj;زووی پیادا دێت، ئه&zwnj;وانه&zwnj;ی خۆگرو ئارامگرن له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و ناخۆشیانه&zwnj;وه که تووشیان ده&zwnj;بێت، ئه&zwnj;وانه&zwnj;ی که نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی که پێمان به&zwnj;خشیوون ده&zwnj;به&zwnj;خشن.';
$t[36] = 'ئێمه سه&zwnj;ربڕینی قوربانییه دابه&zwnj;سته&zwnj;کانمان بڕیارداوه که هه&zwnj;ندێك بن له دروشمه&zwnj;کانی خوا (که له&zwnj;کاتی حه&zwnj;خ و جه&zwnj;ژنی قورباندا ئه&zwnj;نجام ده&zwnj;درێت) بێگومان خێرو بێری زۆری تێدایه بۆتان (بازاڕی کڕین و فرۆشتن ده&zwnj;جولێنێت و هه&zwnj;ژاران تێر ده&zwnj;بن له گۆشت، پاداشتی زۆری قیامه&zwnj;تیش مسۆگه&zwnj;ر ده&zwnj;کات... هتد) ده ناوی خوا ببه&zwnj;ن له&zwnj;کاتی سه&zwnj;ربڕینیدا (بڵێن بسم الله&zwnj;... الله أکبر) کاتێك ئاماده کراون و ڕیزکراون بۆ سه&zwnj;ربڕین، جاکاتێك که سه&zwnj;ربڕان و گیانیان تیادا نه&zwnj;ماو پاك کران و پارچه پارچه کران لێیان بخۆن و به&zwnj;شی هه&zwnj;ژاران و سواڵکه&zwnj;رانی لێ بده&zwnj;ن، ئا به&zwnj;و شێوه&zwnj;یه ئێمه ماڵاتمان بۆ ئێوه ڕامهێناوه بۆ ئه&zwnj;وه&zwnj;ی سوپاسی خوا بکه&zwnj;ن.';
$t[37] = 'دڵنیابن هه&zwnj;رگیز گۆشته&zwnj;که&zwnj;ی و خوێنه&zwnj;که&zwnj;ی به&zwnj;خوا ناگات، به&zwnj;ڵکو ته&zwnj;قواو پارێزکاری ئێوه به&zwnj;و ده&zwnj;گات و (حسابی بۆ ده&zwnj;کات)، ئا به&zwnj;و شێوه&zwnj;یه ده&zwnj;بینن به&zwnj;چاوی خۆتان ماڵاتی بۆ ئێوه ڕام هێناوه&zwnj;، تا له&zwnj;کاتی سه&zwnj;ر بڕینیدا، ألله أکبر بڵێن و قه&zwnj;دری په&zwnj;روه&zwnj;ردگاری گه&zwnj;وره بزانن که هیدایه&zwnj;تی داون و مژده&zwnj;یش بده به چاکه&zwnj;کاران و خێرخوازان.';
$t[38] = 'بێگومان خوا پشتگیری ده&zwnj;کات له&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هێناوه چونکه به&zwnj;ڕاستی خوا هه&zwnj;موو خیانه&zwnj;تکارو کافرو بێ باوه&zwnj;ڕێکی خۆش ناوێت.';
$t[39] = 'مۆڵه&zwnj;ت دراوه به&zwnj;و ئیماندارانه&zwnj;ی شه&zwnj;ڕیان پێده&zwnj;گێرن، که بجه&zwnj;نگن و به&zwnj;رهه&zwnj;ڵستی بکه&zwnj;ن چونکه به&zwnj;ڕاستی سته&zwnj;م لێکراون، بێگومان خوا ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;یه که سه&zwnj;ریان بخات و سه&zwnj;رکه&zwnj;وتنیان پێببه&zwnj;خشێت...';
$t[40] = 'ئه&zwnj;وانه&zwnj;ی که به&zwnj;ناحه&zwnj;ق له وڵاتیان ده&zwnj;ربه&zwnj;ده&zwnj;ر کران (ته&zwnj;نها تاوانیان ئه&zwnj;وه&zwnj;بوو) که ده&zwnj;یانوت: په&zwnj;روه&zwnj;ردگارمان (ألله&zwnj;) یه، خۆ ئه&zwnj;گه&zwnj;ر خوا خه&zwnj;ڵکی نه&zwnj;دا به&zwnj;یه&zwnj;کداو حه&zwnj;ق دژی به&zwnj;تاڵ نه&zwnj;وه&zwnj;ستێت، هه&zwnj;رچی شوێنی خواپه&zwnj;رستی جووله&zwnj;که و گاور هه&zwnj;یه له خه&zwnj;ڵوه&zwnj;تگه و کڵێساو که&zwnj;نیسه&zwnj;، هه&zwnj;روه&zwnj;ها مزگه&zwnj;وتانیش که&zwnj;ناوی خوای زۆر تێدا ده&zwnj;برێت، هه&zwnj;مووی کاول ده&zwnj;کرێت، سوێند بێت بێگومان خوا ئه&zwnj;و که&zwnj;سه سه&zwnj;رده&zwnj;خات که پشتگیری ئاین و به&zwnj;رنامه&zwnj;که&zwnj;ی ده&zwnj;کات و هه&zwnj;واڵی سه&zwnj;رخستنی ده&zwnj;دات، به&zwnj;ڕاستی خوا زۆر به&zwnj;هێز و باڵاده&zwnj;سته&zwnj;.';
$t[41] = 'ئه&zwnj;وانه&zwnj;ی که ئه&zwnj;گه&zwnj;ر پایه&zwnj;دارمان کردن له زه&zwnj;ویدا نوێژ و دروشمه&zwnj;کانی به&zwnj;چاکی ڕاده&zwnj;گرن، زه&zwnj;کاتیش به&zwnj;ته&zwnj;واوی ده&zwnj;ده&zwnj;ن، فه&zwnj;رمان به&zwnj;چاکه ده&zwnj;که&zwnj;ن، قه&zwnj;ده&zwnj;غه له خراپه ده&zwnj;که&zwnj;ن، سه&zwnj;رئه&zwnj;نجامی هه&zwnj;موو کارێکیش هه&zwnj;ر بۆ لای خوا ده&zwnj;گه&zwnj;رێته&zwnj;وه&zwnj;.';
$t[42] = 'ئه&zwnj;گه&zwnj;ر خوانه&zwnj;ناسان بڕوا به تۆ ناکه&zwnj;ن و به&zwnj;درۆت ده&zwnj;زانن، ئه&zwnj;وه دڵنیابه که پێش ئه&zwnj;وان هۆزی نوح و عاد و ثه&zwnj;موود پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی خۆیان به&zwnj;درۆ زانیوه&zwnj;و بڕوایان پێ نه&zwnj;کردوون.';
$t[43] = 'هۆزی ئیبراهیم و هۆزی لوطیش (بڕوایان به پێغه&zwnj;مبه&zwnj;رانی خۆیان نه&zwnj;کردووه&zwnj;).';
$t[44] = 'هه&zwnj;روه&zwnj;ها دانیشتوانی مه&zwnj;دیه&zwnj;ن (بڕوایان به&zwnj;شوعه&zwnj;یب نه&zwnj;کرد)، موساش بڕوای پێنه&zwnj;کراو به&zwnj;درۆ زانرا، منیش مۆڵه&zwnj;تی کافرانمدا (تا ماوه&zwnj;یه&zwnj;ك) له&zwnj;وه&zwnj;ودوا هه&zwnj;مویانم گرت، ئیتر ڕوون و ئاشکرایه که تۆڵه&zwnj;و خه&zwnj;شم و قینم چۆن بوو.';
$t[45] = 'چه&zwnj;نده&zwnj;ها شارو شاره&zwnj;چکه&zwnj;مان له&zwnj;ناوبرد له&zwnj;کاتێکدا دانیشتوانی سته&zwnj;مکار بوون، (ئه&zwnj;وه&zwnj;ته ده&zwnj;بینیت) خانووبه&zwnj;ره&zwnj;کانیان به&zwnj;سه&zwnj;ریه&zwnj;کدا ته&zwnj;پیووه&zwnj;، بیری ئاوی له&zwnj;کار خراو، کۆشك و ته&zwnj;لاری به&zwnj;رزو بڵندی زۆریشمان به&zwnj;چۆڵی هێشه&zwnj;ته&zwnj;وه و بێ خاوه&zwnj;ن ماوه&zwnj;ته&zwnj;وه (که به&zwnj;ڵگه&zwnj;یه له&zwnj;سه&zwnj;ر خه&zwnj;شم و قینی خوا لێیان).';
$t[46] = 'ئایا ئه&zwnj;وانه به&zwnj;سه&zwnj;ر زه&zwnj;ویدا نه&zwnj;ڕۆشتوون به&zwnj;دڵ و ده&zwnj;روونێکی هۆشیاره&zwnj;وه تا بیرێك بکه&zwnj;نه&zwnj;وه&zwnj;... (کوان: خه&zwnj;ڵکانی ڕابوردوو؟ ئه&zwnj;و شارانه بۆ کاولکران؟ کێ ئێمه&zwnj;ی دروست کردووه&zwnj;؟ ئامانج له ژیان چیه&zwnj;؟؟) یان خاوه&zwnj;نی گوێ و ده&zwnj;زگای بیستن نه&zwnj;بوون به&zwnj;سه&zwnj;رهاته&zwnj;کانیان ببستن و لێکی بده&zwnj;نه&zwnj;وه&zwnj;، جا وه&zwnj;نه&zwnj;بێت چاوان کوێر بووبێت، به&zwnj;ڵکو ئه&zwnj;و دڵانه کوێر ده&zwnj;بن که وان له سینه&zwnj;کاندا (چونکه هه&zwnj;ر ڕواڵه&zwnj;ت ده&zwnj;بینن و ناوه&zwnj;ڕۆك لێك ناده&zwnj;نه&zwnj;وه&zwnj;).';
$t[47] = 'خوانه&zwnj;ناسان په&zwnj;له&zwnj;ت لێده&zwnj;که&zwnj;ن که ئه&zwnj;و سزایه زوو یه&zwnj;خه&zwnj;یان پێ بگرێت، بێگومان هه&zwnj;رگیز خوا له به&zwnj;ڵێنی خۆی لانادات (له&zwnj;سه&zwnj;ر داخوازی نه&zwnj;فامان پاش و پێشی ناخات، چونکه کات و زه&zwnj;مان بایه&zwnj;خی نیه لای خوا) به&zwnj;ڕاستی ڕۆژێك له&zwnj;لای ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;ت وه&zwnj;كو هه&zwnj;زار ساڵ وایه له&zwnj;وه&zwnj;ی که ده&zwnj;یژمێرن.';
$t[48] = 'چه&zwnj;نده&zwnj;ها شارو شارۆچکه هه&zwnj;بوون که من مۆڵه&zwnj;تمداون له&zwnj;کاتێکدا ده&zwnj;نیشتوانی سته&zwnj;مکاریش بوون، دوای ماوه&zwnj;یه&zwnj;ك له&zwnj;ناوم بردوون، سه&zwnj;رئه&zwnj;نجام و گه&zwnj;ڕانه&zwnj;وه&zwnj;ش هه&zwnj;ر بۆ لای منه&zwnj;.';
$t[49] = 'پێیان بڵێ: ئه&zwnj;ی خه&zwnj;ڵکینه من ته&zwnj;نها ترسێنه&zwnj;رو بێدارخه&zwnj;ره&zwnj;وه&zwnj;یکی ئاشکرام بۆتان.';
$t[50] = 'جا ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه لێخۆش بوون و ڕۆزی به&zwnj;نرخ و فراوان بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[51] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ش هه&zwnj;وڵ ده&zwnj;ده&zwnj;ن به&zwnj;ربه&zwnj;ست دابنێن له&zwnj;به&zwnj;رده&zwnj;م ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;دا له کاتێکدا ده&zwnj;سته&zwnj;وسانن، ئه&zwnj;وانه نیشته&zwnj;جێی ناو دۆزه&zwnj;خن.';
$t[52] = '(ئه&zwnj;ی محمد (صلی الله علیه وسلم) پێش تۆ هیچ پێغه&zwnj;مبه&zwnj;رێکی په&zwnj;یامدار و بێ په&zwnj;یاممان ڕه&zwnj;وانه نه&zwnj;کردووه ئاواتی نه&zwnj;خواستبێت به&zwnj;باوه&zwnj;ڕ هێنانی قه&zwnj;ومه&zwnj;که&zwnj;ی، به&zwnj;ڵام کاتێك ئاواتی خواستووه شه&zwnj;یتان ئه&zwnj;وه&zwnj;ی به فرسه&zwnj;ت زانیووه&zwnj;، شتی نابه&zwnj;جێی تێکه&zwnj;ڵ به&zwnj;ئاواته&zwnj;که&zwnj;ی کردووه&zwnj;، ئینجا خوایش فریای که&zwnj;وتووه و ئه&zwnj;وه&zwnj;ی شه&zwnj;یتان فڕێی داوه&zwnj;ته نا ئاواته&zwnj;که&zwnj;یه&zwnj;وه&zwnj;، سڕیویه&zwnj;تیوه&zwnj;، له&zwnj;وه&zwnj;ودوا خوای گه&zwnj;وره ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;که&zwnj;ی خۆی چه&zwnj;سپاو دامه&zwnj;زراو کردووه&zwnj;، خوا خۆی زاناو دانایه&zwnj;.';
$t[53] = '(خوای گه&zwnj;وره بۆیه ئه&zwnj;و ده&zwnj;سه&zwnj;ڵاته&zwnj;ی داوه به&zwnj;شه&zwnj;یتان) تا ئه&zwnj;و (خه&zwnj;ته&zwnj;ره و خه&zwnj;یاڵ و وه&zwnj;سوه&zwnj;سه&zwnj;یه&zwnj;&zwnj;ی) شه&zwnj;یتان فڕێ ده&zwnj;داته نێو ئاواتی پێغه&zwnj;مبه&zwnj;رانه&zwnj;وه ببێته هۆی سزاو تاقیکردنه&zwnj;وه بۆ ئه&zwnj;وانه&zwnj;ی له دڵ و ده&zwnj;روونیاندا نه&zwnj;خۆشی دووڕوویی و ناپاکی هه&zwnj;یه و بۆ ئه&zwnj;وانه&zwnj;ش که به&zwnj;رامبه&zwnj;ر ئایینی خوا دڵ ڕه&zwnj;قن، به&zwnj;ڕاستی سته&zwnj;مکاران له دژایه&zwnj;تی و شه&zwnj;ڕه&zwnj;نگێزیه&zwnj;کی قووڵ و بێ بندا ڕۆچوون.';
$t[54] = 'بۆ ئه&zwnj;وه&zwnj;ش ئه&zwnj;وانه&zwnj;ی زانست و زانیاریان پێ به&zwnj;خشراوه دڵنیابن و بزانن که بێگومان ئه&zwnj;م قورئانه حه&zwnj;قیقه&zwnj;ت و ڕاسته و له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری تۆوه (ئه&zwnj;ی محمد (صلی الله علیه وسلم) ڕه&zwnj;وانه کراوه&zwnj;، ئینجا بڕوای پته&zwnj;وی پێ ده&zwnj;هێنن و دڵه&zwnj;کانیشیان ملکه&zwnj;چ ده&zwnj;بێت بۆ خوا، بێگومان خوا هیدایه&zwnj;تده&zwnj;ری ئه&zwnj;وانه&zwnj;یه که باوه&zwnj;ڕیان هێناوه بۆ ڕێگه و ڕێبازی ڕاست و دروست.';
$t[55] = 'به&zwnj;رده&zwnj;وام ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن ده&zwnj;رباره&zwnj;ی قورئان له شك و گوماندان، هه&zwnj;تا کوتوپڕ قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت، یاخود تا ئه&zwnj;و کاته&zwnj;ی که سزاو ئازار له ڕۆژێکی بێ خێردا یه&zwnj;خه&zwnj;یان پێده&zwnj;گرێت.';
$t[56] = 'ئه&zwnj;و ڕۆژه پاشایه&zwnj;تی و ده&zwnj;سه&zwnj;ڵات ته&zwnj;نها به ده&zwnj;ست خوایه و فه&zwnj;رمانڕه&zwnj;وای له نێوانیاندا ده&zwnj;کات، جا ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه له چه&zwnj;نده&zwnj;ها باخی پڕ له نازو نیعمه&zwnj;ته&zwnj;کاندان.';
$t[57] = 'ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون و ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ زانیووه&zwnj;، ئا ئه&zwnj;وانه به&zwnj;هۆی بیروباوه&zwnj;ڕی ناپه&zwnj;سه&zwnj;ندیانه&zwnj;وه سزای پڕ له خه&zwnj;جاڵه&zwnj;تی و ڕیسواکه&zwnj;ریان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[58] = 'ئه&zwnj;وانه&zwnj;ش که له&zwnj;به&zwnj;ر ڕه&zwnj;زامه&zwnj;ندی خواو له پێناوی سه&zwnj;رخستنی ڕێبازه&zwnj;که&zwnj;ی ئه&zwnj;ودا کۆچیان کرد له&zwnj;وه&zwnj;ودوا کوژران و شه&zwnj;هیدکران یاخود مردن، سوێند بێت بێگومان خوا له ڕزق و ڕۆزی چاك به&zwnj;هره&zwnj;وه&zwnj;ریان ده&zwnj;کات، به&zwnj;ڕاستی خوا هه&zwnj;ر خۆی چاکترین ڕزق و ڕۆزی به&zwnj;خشه&zwnj;.';
$t[59] = 'سوێند بێت خوای گه&zwnj;وره ئه&zwnj;و جۆره که&zwnj;سانه ده&zwnj;خاته شوێنێکه&zwnj;وه که زۆر پێی ڕازی و خۆشنوود ده&zwnj;بن، به&zwnj;ڕاستی خوا زانایه (به&zwnj;هه&zwnj;وڵ و کۆشش و نیه&zwnj;ت و چاکه&zwnj;یان) و خۆگره (زۆر په&zwnj;له ناکات له تۆڵه&zwnj;سه&zwnj;ندندا).';
$t[60] = 'ئه&zwnj;وه بڕیاداده&zwnj;ی خوابوو، هه&zwnj;رکه&zwnj;سێك تۆڵه بسێنێت له سته&zwnj;مکار به&zwnj;ئه&zwnj;ندازه&zwnj;ی ئه&zwnj;وه&zwnj;ی که سته&zwnj;می لێکراوه&zwnj;، له&zwnj;وه&zwnj;ودوا ده&zwnj;ستدرێژیی بکرێته&zwnj;وه سه&zwnj;رو سته&zwnj;می لێ بکرێت، ئه&zwnj;وه بێگومان خوا سه&zwnj;ری ده&zwnj;خات به&zwnj;سه&zwnj;ر ئه&zwnj;و سته&zwnj;مکاره&zwnj;دا، به&zwnj;ڕاستی خوا زۆر چاوپۆشی ده&zwnj;کات و لێخۆش بووه (له&zwnj;و که&zwnj;سانه&zwnj;ی واز له سته&zwnj;م ده&zwnj;هێنن، یاخود توانای تۆڵه&zwnj;سه&zwnj;ندیان هه&zwnj;یه و چاوپۆشی ده&zwnj;که&zwnj;ن).';
$t[61] = 'ئه&zwnj;و سه&zwnj;رکه&zwnj;وتنه به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;یه که به&zwnj;ڕاستی خوای گه&zwnj;وره زاتێکه (ئه&zwnj;وه&zwnj;نده به&zwnj;توانایه&zwnj;) شه&zwnj;و تێهه&zwnj;ڵکێشی ڕۆژ ده&zwnj;کات و ڕۆژ تێهه&zwnj;ڵکێشی شه&zwnj;و ده&zwnj;کات، به&zwnj;ڕاستی خوا بیسه&zwnj;رو بینایه (به&zwnj;هه&zwnj;ڵسوکه&zwnj;وت و قسه&zwnj;و گوفتاری به&zwnj;نده&zwnj;کانی).';
$t[62] = 'ئه&zwnj;و بیستن و ببینینه&zwnj;ش به هۆی ئه&zwnj;وه&zwnj;یه چونکه به&zwnj;ڕاستی خوا خۆی حه&zwnj;ق و ڕاسته&zwnj;، ئه&zwnj;وانه&zwnj;ش که ئه&zwnj;وان هاناو هاواری بۆ ده&zwnj;به&zwnj;ن له جیاتی ئه&zwnj;و هه&zwnj;ر ئه&zwnj;وه خۆی باتاڵ و ناحه&zwnj;قیه&zwnj;. بێگومان ئه&zwnj;و خوایه بڵندو به&zwnj;رزو مه&zwnj;زن و گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[63] = 'ئایا نه&zwnj;تبینیووه که به&zwnj;ڕاستی خوا بارانی له ئاسمانه&zwnj;وه باراندووه و زه&zwnj;وی زۆر پێ سه&zwnj;وز ده&zwnj;بێت، به&zwnj;ڕاستی خوا وردبین و به&zwnj;سۆزو دلۆڤان و ئاگایه&zwnj;.';
$t[64] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کاندایه و هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;مووی ئه&zwnj;و زاته خاوه&zwnj;نیانه&zwnj;، به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی ده&zwnj;وڵه&zwnj;مه&zwnj;ند و بێ نیازه و هه&zwnj;ر خۆی شایسته&zwnj;ی سوپاس و ستایشه&zwnj;.';
$t[65] = 'ئایا به&zwnj;چاوی خۆت نه&zwnj;تبینیووه چۆن خوای گه&zwnj;وره هه&zwnj;رچی له زه&zwnj;ویدا هه&zwnj;یه بۆ ئێوه&zwnj;ی ڕام هێناوه&zwnj;، هه&zwnj;روه&zwnj;&zwnj;ها که&zwnj;شتیه&zwnj;کانیش که له ده&zwnj;ریاکاندا ده&zwnj;ڕوات به&zwnj;فه&zwnj;رمانی ئه&zwnj;و زاته&zwnj;، ئاسمانیش ڕاگیر ده&zwnj;کات له&zwnj;وه&zwnj;ی که بکه&zwnj;وێت به&zwnj;سه&zwnj;ر زه&zwnj;ویدا مه&zwnj;گه&zwnj;ر به&zwnj;ویست و فه&zwnj;رمانی خۆی، به&zwnj;ڕاستی خوا بۆ خه&zwnj;ڵکی زۆر به&zwnj;سۆزو میهره&zwnj;بان و لێخۆشبوو به&zwnj;خشنده&zwnj;یه&zwnj;.';
$t[66] = 'هه&zwnj;ر ئه&zwnj;و زاته ژیانی پێبه&zwnj;خشیوون، له&zwnj;وه&zwnj;ودوا ئه&zwnj;و ده&zwnj;تانمرێنێت، پاشان زیندووتان ده&zwnj;کاته&zwnj;وه&zwnj;، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو شتانه&zwnj;دا) زۆربه&zwnj;ی خه&zwnj;ڵکی سپڵه&zwnj;و به&zwnj;دنمه&zwnj;که&zwnj;.';
$t[67] = 'بۆ هه&zwnj;ر میلله&zwnj;تێك به&zwnj;رنامه و پرۆگرامێکمان دیاری کردووه په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن و له&zwnj;سه&zwnj;ری ده&zwnj;ڕۆن (به&zwnj;گوێره&zwnj;ی کات و شوێن و بارودۆخیان، ئه&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;شمان ناردووه بۆ هه&zwnj;تا هه&zwnj;تایی) که&zwnj;واته با خاوه&zwnj;ن ئایینه&zwnj;کانی تر نه&zwnj;که&zwnj;ینه کێشه و ئاژاوه له&zwnj;گه&zwnj;ڵتدا له&zwnj;باره&zwnj;ی ئه&zwnj;م ئایینه&zwnj;وه&zwnj;، ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;رده&zwnj;وام به&zwnj;نگه&zwnj;وازی خه&zwnj;ڵکی بکه بۆ لای په&zwnj;روه&zwnj;ردگارت چونکه به&zwnj;ڕاستی تۆ له&zwnj;سه&zwnj;ر ڕێگه و ڕێباز و هیدایه&zwnj;تێکی ڕاست و دروستیت.';
$t[68] = 'خۆ ئه&zwnj;گه&zwnj;ر موجاده&zwnj;له&zwnj;ت له&zwnj;گه&zwnj;ڵدا بکه&zwnj;ن (ئه&zwnj;وه تۆ موجاده&zwnj;له مه&zwnj;که به&zwnj;ڵکو) بڵێ: خوا خۆی چاك زانایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[69] = 'هه&zwnj;ر خوا خۆی له ڕۆژی قیامه&zwnj;تدا داوه&zwnj;ری ده&zwnj;کات له نێوانتاندا له&zwnj;سه&zwnj;ر ئه&zwnj;و شتانه&zwnj;ی که ئێوه کێشه و جیاوازیتان له&zwnj;سه&zwnj;ر ده&zwnj;کرد (له&zwnj;وێ ڕاستیه&zwnj;کان به&zwnj;ته&zwnj;واوی ده&zwnj;رده&zwnj;که&zwnj;وێت).';
$t[70] = '(ئه&zwnj;ی هۆشمه&zwnj;ند) مه&zwnj;گه&zwnj;ر نه&zwnj;تزانیووه که به&zwnj;ڕاستی خوا ئاگاو زانایه به&zwnj;هه&zwnj;رچی له ئاسمان و زه&zwnj;ویدا هه&zwnj;یه و هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی (که ده&zwnj;یکه&zwnj;ن) له کتێب و دۆسیه&zwnj;ی (لوح المحفوظ) دا تۆمارکراوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وکاره بۆ خوا زۆر ئاسانه&zwnj;.';
$t[71] = '(موشریك و هاوه&zwnj;ڵگه&zwnj;ران) له جیاتی خواپه&zwnj;رستی شتێك ده&zwnj;په&zwnj;رستن که خوا هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کی ڕه&zwnj;وانه نه&zwnj;کردووه له&zwnj;سه&zwnj;ری (که ڕه&zwnj;وایه بیپه&zwnj;رستن) و خۆیشیان هیچ زانیاریه&zwnj;کیان ده&zwnj;رباره&zwnj;ی نیه&zwnj;، سته&zwnj;مکارانیش هیچ جۆره پشتیوانێكیان نیه (تا له قیامه&zwnj;تدا فریایان بکه&zwnj;وێت).';
$t[72] = 'کاتێك که ئایه&zwnj;ته&zwnj;کانی ئێمه به&zwnj;روونی و ئاشکرایی به&zwnj;سه&zwnj;ریاندا ده&zwnj;خوێنرێته&zwnj;وه&zwnj;، له ڕووخساری ئه&zwnj;وانه&zwnj;دا که بێ باوه&zwnj;ڕن بێزاری و تاڵی و گرژی به&zwnj;دی ده&zwnj;که&zwnj;یت، به&zwnj;جۆرێك خه&zwnj;ریکه هێرش به&zwnj;رنه سه&zwnj;ر ئه&zwnj;وانه&zwnj;ی که ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;یان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێننه&zwnj;وه&zwnj;، پێیان بڵێ: ئایا هه&zwnj;واڵتان بده&zwnj;مێ به خراپتر له&zwnj;و کینه&zwnj;و بوغزه&zwnj;تان؟! ئه&zwnj;ویش ئاگری دۆزه&zwnj;خه&zwnj;!! که خوای گه&zwnj;وره به&zwnj;ڵێنی داوه به&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ پێی، ئای که سه&zwnj;ره&zwnj;نجام و چاره&zwnj;نووسێکی خراپه&zwnj;.';
$t[73] = 'ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;: نموونه&zwnj;یه&zwnj;ك هێنراوه&zwnj;ته&zwnj;وه چاك گوێی بۆ بگرن؛ به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که هاناو هاواریان لێده&zwnj;که&zwnj;ن له&zwnj;جیاتی خوا، هه&zwnj;رگیز ناتوانن تاکه مێشێك دروست بکه&zwnj;ن، هه&zwnj;رچه&zwnj;نده بۆ ئه&zwnj;و کاره کۆببنه&zwnj;وه و (یارمه&zwnj;تی یه&zwnj;کتریش بده&zwnj;ن) و ئه&zwnj;گه&zwnj;ر مێشه&zwnj;که شتێکیان لێ بفڕێنێت، ناتوانن لێی بسه&zwnj;ننه&zwnj;وه&zwnj;، داواکارو داوالێکراو هه&zwnj;ردووکیان لاواز و بێ ده&zwnj;سه&zwnj;ڵاتن.';
$t[74] = 'ئه&zwnj;وانه (وه&zwnj;کو پێویست) قه&zwnj;دری خوایان به&zwnj;چاکی نه&zwnj;زانی، ڕێزی په&zwnj;روه&zwnj;ردگاریان به&zwnj;ته&zwnj;واوی نه&zwnj;گرت، له&zwnj;کاتێکدا به&zwnj;ڕاستی خوا زۆر به&zwnj;هێزو به توانا و باڵاده&zwnj;سته&zwnj;.';
$t[75] = 'هه&zwnj;ر خوا به ویستی خۆی له فریشته&zwnj;کان و له&zwnj;خه&zwnj;ڵکیش فرستاده هه&zwnj;ڵده&zwnj;بژێرێت، به&zwnj;ڕاستی خوا بیسه&zwnj;رو بینایه (چاك ده&zwnj;زانێت کێ هه&zwnj;ڵده&zwnj;بژێرێت بۆ گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;ی).';
$t[76] = 'خوا ئاگایه به&zwnj;وه&zwnj;ی که له پێشیانه&zwnj;وه ڕووی داوه به&zwnj;وه&zwnj;ش که له ئاینده و داهاتوودا ڕوو ده&zwnj;دات و هه&zwnj;موو کارو هه&zwnj;موو شتێك بۆ لای خوا ده&zwnj;برێته&zwnj;وه&zwnj;.';
$t[77] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێنه&zwnj;وه كڕنووش و سو&zwnj;ژده به&zwnj;رن و (نوێژه&zwnj;کانتان به&zwnj;جوانی بکه&zwnj;ن) و په&zwnj;روه&zwnj;ردگارتان بپه&zwnj;رستن له هه&zwnj;موو بارودۆخێکداو تا ده&zwnj;توانن چاکه و چاکه&zwnj;کاری ئه&zwnj;نجام بده&zwnj;ن بۆ ئه&zwnj;وه&zwnj;ی سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتووبن...';
$t[78] = 'هه&zwnj;روه&zwnj;ها به&zwnj;رده&zwnj;وام بن له خه&zwnj;بات و جیهاد و هه&zwnj;وڵ و کۆشش له پێناوی خوادا به&zwnj;و شێوه&zwnj;یه&zwnj;ی که شایسته&zwnj;یه&zwnj;تی، له&zwnj;به&zwnj;ر خاتری ئه&zwnj;و بێت، ئه&zwnj;و زاته بۆ ئه&zwnj;وه هه&zwnj;ڵی بژاردوون و له ئاین و به&zwnj;رنامه&zwnj;که&zwnj;یدا هیچ جۆره شتێكی ناڕه&zwnj;واو قورس و گرانی له&zwnj;سه&zwnj;ر دانه&zwnj;ناون (که نه&zwnj;توانن ئه&zwnj;نجامی بده&zwnj;ن، جا ئه&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;) ڕێ و شوێن و به&zwnj;رنامه&zwnj;ی ئیبراهیمی باواتانه که پێشتر هه&zwnj;ر خوای گه&zwnj;وره ناوی ناون موسڵمان و له&zwnj;م قورئانه&zwnj;شدا به&zwnj;موسڵمان ناوی بردوون، تا پێغه&zwnj;مبه&zwnj;ر ببێته شایه&zwnj;ت به&zwnj;سه&zwnj;رتانه&zwnj;وه (که په&zwnj;یامی خوای پێ ڕاگه&zwnj;یاندوون) و ئێوه&zwnj;ش (به گه&zwnj;یاندنی ئه&zwnj;و په&zwnj;یامه&zwnj;) ببنه شایه&zwnj;ت به&zwnj;سه&zwnj;ر خه&zwnj;ڵکیوه&zwnj;، که&zwnj;واته نوێژه&zwnj;کانتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کاتیش (له ماڵ و سامان و زانستی و هێزتان) ده&zwnj;ربکه&zwnj;ن و پشت به&zwnj;خوا ببه&zwnj;ستن، چونکه هه&zwnj;ر ئه&zwnj;و زاته یارو یاوه&zwnj;رتانه&zwnj;، جا دڵنیابن که خوا چه&zwnj;نده یارمه&zwnj;تیده&zwnj;رێکی چاکه و چه&zwnj;نده پشت و په&zwnj;نایه&zwnj;کی به&zwnj;هێزو به نرخه&zwnj;.';
?>