<?php
$t = array();
$t[1] = 'به&zwnj;رزو پیرۆزو پاک و بڵنده ئه&zwnj;و زاته&zwnj;ی که قورئانی دابه&zwnj;زاندووه تا ببێته جیاکه&zwnj;ره&zwnj;وه&zwnj;ی ڕاست و ناڕاست، بۆ سه&zwnj;ر به&zwnj;نده&zwnj;ی خۆی (محمد صلی الله علیه وسلم) تا ببێته هۆشیار که&zwnj;ره&zwnj;وه&zwnj;و بێدارکه&zwnj;ره&zwnj;وه&zwnj;ی دانیشتوانی جیهان له هه&zwnj;موو سه&zwnj;رده&zwnj;مێکدا.';
$t[2] = 'ئه&zwnj;و زاته&zwnj;ی که هه&zwnj;موو ئاسمانه&zwnj;کان و زه&zwnj;وی موڵکی ئه&zwnj;وه&zwnj;و هه&zwnj;ر ئه&zwnj;و خاوه&zwnj;نیانه، که&zwnj;سیشی نه&zwnj;کردووه به ڕۆڵه&zwnj;ی خۆی و که&zwnj;سی نه&zwnj;کردووه به شه&zwnj;ریک و هاوبه&zwnj;ش له مه&zwnj;ودای موڵک و ده&zwnj;سه&zwnj;ڵاتیدا، هه&zwnj;موو شتێکی دروستکردووه به ئه&zwnj;ندازه&zwnj;و قه&zwnj;باره و شێوه و تام و بۆن و ڕه&zwnj;نگ و جیاوازی هه&zwnj;مه جۆر... هتد، نه&zwnj;خشه&zwnj;ی ڕێک و پێکی بۆ هه&zwnj;موو شتێک داناوه (به شێوه&zwnj;یه&zwnj;ک که پێویستیه&zwnj;کانی ئاده&zwnj;میزاد دابین بکه&zwnj;ن و له خزمه&zwnj;تیدا بن).';
$t[3] = 'که&zwnj;چی هاوه&zwnj;ڵگه&zwnj;ران له جیاتی ئه&zwnj;و زاته شتانێکی تریان کردووه به خوای خۆیان که هیچ دروست ناکه&zwnj;ن و هیچیان پی به&zwnj;دی ناهێنرێت، به&zwnj;ڵکو خۆیان دروست ده&zwnj;کرێن، (ته&zwnj;نانه&zwnj;ت) ناتوانن سودێک یان زه&zwnj;ره&zwnj;رێک به خۆیان بگه&zwnj;یه&zwnj;نن، هه&zwnj;روه&zwnj;ها نه مردنیان به ده&zwnj;سته، نه ژیان، نه زیندوو کردنه&zwnj;وه.';
$t[4] = 'ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون وتویانه: ئه&zwnj;م قورئانه درۆیه&zwnj;ک نه&zwnj;بێت که (محمد صلی الله علیه وسلم) خۆی هه&zwnj;ڵیبه&zwnj;ستووه، شتێکی تر نی&zwnj;یه، که&zwnj;سانی تریش یارمه&zwnj;تیانداوه، به ڕاستی ئه&zwnj;وانه (به&zwnj;و گوفتاره&zwnj;یان) سته&zwnj;م و بوختانێکی گه&zwnj;وره ده&zwnj;که&zwnj;ن و قسه&zwnj;یه&zwnj;کی ناڕه&zwnj;وایان کردووه.';
$t[5] = 'هه&zwnj;روه&zwnj;ها وتویانه: ئه&zwnj;م قورئانه داستانی پێشینه&zwnj;کانه&zwnj;و داوای نوسینه&zwnj;وه&zwnj;یانی کردووه، به&zwnj;یانیان و ئێواران به&zwnj;سه&zwnj;ریدا ده&zwnj;یخویننه&zwnj;وه!!';
$t[6] = 'تۆ ئه&zwnj;ی (محمد صلی الله علیه وسلم) بڵێ: ئه&zwnj;و زاته&zwnj;ی ئه&zwnj;م قورئانه&zwnj;ی دابه&zwnj;زاندووه به هه&zwnj;موو نهێنیه&zwnj;ک ده&zwnj;زانێت له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، هه&zwnj;ر ئه&zwnj;ویش هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام لێخۆشبوو میهره&zwnj;بانه.';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕن ده&zwnj;ڵێن: ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ره چییه&zwnj;تی، خواردن ده&zwnj;خوات و به&zwnj;بازاڕدا ده&zwnj;ڕوات و ده&zwnj;گه&zwnj;ڕێت، ده&zwnj;بوایه فریشته&zwnj;یه&zwnj;ک بۆ لای دابه&zwnj;زییایه&zwnj;!! تائه&zwnj;ویش له&zwnj;گه&zwnj;ڵیدا خه&zwnj;ڵکی بێداربکاته&zwnj;وه&zwnj;و (له&zwnj;سه&zwnj;ره&zwnj;نجامی سه&zwnj;رپێچی) بیانترسێنێت!!.';
$t[8] = 'یاخود گه&zwnj;نجینه&zwnj;یه&zwnj;کی له ئاسمانه&zwnj;وه بۆ فڕێ بدرایه، یاخود باخ و باخات و بێستانێکی تایبهتی ببوایه و لێی بخواردایه، به&zwnj;ڵکو سته&zwnj;مکاران ده&zwnj;ڵێن به ئیمانداران: ئێوه ته&zwnj;نها شوێن پیاوێکی جادوو لێکراو نه&zwnj;بێت، شوێن که&zwnj;سی تر نه&zwnj;که&zwnj;وتوون؟!';
$t[9] = 'ته&zwnj;ماشاکه و سه&zwnj;رنج بده (له&zwnj;و نه&zwnj;فامانه&zwnj;) چۆن نموونه و بیانووت بۆ هێنایته&zwnj;وه، ئه&zwnj;وانه گومڕا بوون و ڕێگه&zwnj;یه&zwnj;ک نادۆزنه&zwnj;وه بۆ سه&zwnj;رزه&zwnj;نشت کردنت، یاخود بۆ ڕزگاری و ئاسووده&zwnj;یی دنیاو قیامه&zwnj;ت.';
$t[10] = '(له&zwnj;حاڵه&zwnj;تێکدا) ئه&zwnj;و زاته مه&zwnj;زن وپیرۆزه، زاتێکه که ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت زۆر له&zwnj;وه&zwnj;ی که خوانه&zwnj;ناسان باسی ده&zwnj;که&zwnj;ن چاکترت پێ ده&zwnj;به&zwnj;خشێت.. باخ وباخاتی (جوان و ڕازاوه&zwnj;ت هه&zwnj;رله دنیادا بۆ ده&zwnj;ڕه&zwnj;خسێنێت) که چه&zwnj;نده&zwnj;ها ڕووبار به&zwnj;ژێر دره&zwnj;خته&zwnj;کانیدا ده&zwnj;ڕوات، هه&zwnj;روه&zwnj;ها کۆشک و ته&zwnj;لاری به&zwnj;رزو بڵندت پێ ده&zwnj;به&zwnj;خشێت.';
$t[11] = 'نه&zwnj;خێر، (ڕه&zwnj;خنه&zwnj;کانیان به&zwnj;جێ نی&zwnj;یه&zwnj;)، ئه&zwnj;گینا ئه&zwnj;وان بڕوایان هه&zwnj;ر به&zwnj;قیامه&zwnj;ت نیه&zwnj;، ئێمه&zwnj;ش ئاگری بڵێسه&zwnj;دارمان بۆ ئه&zwnj;و که&zwnj;سانه ئاماده&zwnj;کردووه که بڕوایان به قیامه&zwnj;ت نیه&zwnj;.';
$t[12] = 'کاتێک هه&zwnj;ر له دووره&zwnj;وه ئه&zwnj;و ئاگره بڵێسه&zwnj;داره خوانه&zwnj;ناسان ده&zwnj;بینێت، ده&zwnj;نگی خه&zwnj;شم و قین و هه&zwnj;ناسه&zwnj;ی داخ له دڵی ده&zwnj;بیستن.';
$t[13] = 'هه&zwnj;رکاتێک ئه&zwnj;وانه به کۆت و زنجیره&zwnj;وه فڕێ ده&zwnj;درێنه ناو شوێنێکی ته&zwnj;نگه&zwnj;به&zwnj;ره&zwnj;وه له دۆزه&zwnj;خدا، داوای مه&zwnj;رگ و تیاچوون ده&zwnj;که&zwnj;ن و مردن به ئاوات ده&zwnj;خوازن له&zwnj;وێ.';
$t[14] = '(ئه&zwnj;وسا پێییان ده&zwnj;وترێت) ئه&zwnj;مڕۆ داوای ته&zwnj;نها مردنێک مه&zwnj;که&zwnj;ن، به&zwnj;ڵکو داوای مردنی زۆر بێ شومار بکه&zwnj;ن و ئاواتی بۆ بخوازن!!';
$t[15] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: باشه، ئه&zwnj;و حاڵه چاکتره&zwnj;، یان به&zwnj;هه&zwnj;شتی هه&zwnj;میشه و به&zwnj;رده&zwnj;وام که به&zwnj;ڵێن دراوه به خواناسان وپارێزکاران، هه&zwnj;ر ده&zwnj;مێکیشه ئاماده کراوه بۆیان، که پاداشته&zwnj;و سه&zwnj;&zwnj;رئه&zwnj;نجام.';
$t[16] = 'له&zwnj;وبه&zwnj;هه&zwnj;شته&zwnj;دا هه&zwnj;رچی داوای بکه&zwnj;ن بۆیان ئاماده&zwnj;یه هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه&zwnj;دا، ئه&zwnj;وه&zwnj;ش به&zwnj;ڵێنێکه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه و خۆی به&zwnj;ر پرسیار کردووه لێی.';
$t[17] = 'ڕۆژێک دێت خوا بێ باوه&zwnj;ڕان کۆده&zwnj;کاته&zwnj;وه و هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ش که له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;رست، ئه&zwnj;وسا (خوا ڕوو به&zwnj;په&zwnj;رستراوه&zwnj;کان) ده&zwnj;فه&zwnj;رموێت: ئایا ئێوه ئه&zwnj;و به&zwnj;ندانه&zwnj;ی منتان گومڕا کرد؟ یاخود خۆیان ڕێبازی ڕاستیان وێڵ کرد و گومڕا بوون؟.';
$t[18] = 'له وه&zwnj;ڵامدا ده&zwnj;ڵێن: خوایه، پاکی و بێگه&zwnj;ردی بۆ تۆیه، هه&zwnj;رگیز ڕه&zwnj;وا نیه ئێمه جگه له تۆ که&zwnj;سێک، یان شتێکی تر بپه&zwnj;رستین یان بیکه&zwnj;ینه پشتیوان و یاوه&zwnj;ری خۆمان، به&zwnj;ڵام تۆ نازو نیعمه&zwnj;تت به&zwnj;و خه&zwnj;ڵکه گومڕاییه و باوو باپیرانیان به&zwnj;خشی بوو، ئیتر هه&zwnj;موو یاداوه&zwnj;ری و یاد خستنه&zwnj;وه&zwnj;کانیان فه&zwnj;رامۆش کرد و بوونه قه&zwnj;ومێکی بێ خێر و تیاچوو.';
$t[19] = '(ئه&zwnj;وسا به&zwnj;سه&zwnj;ر گه&zwnj;ردانه&zwnj;کان ده&zwnj;وترێت) ئه&zwnj;وه&zwnj;ته په&zwnj;رستراوه&zwnj;کان گوفتاری ئێوه&zwnj;یان به درۆ خسته&zwnj;وه، ئیتر نه ده&zwnj;توانن له سزای خوایی خۆتان ده&zwnj;رباز بکه&zwnj;ن، نه ده&zwnj;توانن پشتیوانێک به ده&zwnj;ست بهێنن، جا هه&zwnj;ر که&zwnj;ستان بیه&zwnj;وێت سته&zwnj;مێک بکات، ئه&zwnj;وه ئێمه سزایه&zwnj;کی سه&zwnj;خت و گه&zwnj;وره وسامناکی پێ ده&zwnj;چێژین.';
$t[20] = 'ئێمه پێش تۆ (ئه&zwnj;ی محمد صلی الله علیه وسلم) هیچ که&zwnj;سێکمان له پێغه&zwnj;مبه&zwnj;ران ڕه&zwnj;وانه نه&zwnj;کردووه که خواردنیان نه&zwnj;خواردبێت، یان به بازاڕدا نه&zwnj;گه&zwnj;ڕابن، ئێمه هه&zwnj;ندێک له ئێومان کردۆته هۆی تاقیکردنه&zwnj;وه&zwnj;ی هه&zwnj;ندێکی ترتان.. ئایا خۆده&zwnj;گرن و خۆڕاگر ده&zwnj;بن، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ هه&zwnj;میشه و به&zwnj;رده&zwnj;وام بینایه (به هه&zwnj;ڵسوکه&zwnj;وتی به&zwnj;نده&zwnj;کانی).';
$t[21] = 'ئه&zwnj;وانه&zwnj;ی به ئومێدی دیداری ئێمه نین وتیان: ئه&zwnj;وه بۆ فریشه دانه&zwnj;به&zwnj;زێنرا بۆ لامان (تا دڵنیا بین له حه&zwnj;قیقه&zwnj;تی قورئان و پێغه&zwnj;مبه&zwnj;ر) یاخود په&zwnj;روه&zwnj;ردگارمان به چاوی خۆمان ببینین، سوێند به خوا بێگومان ئه&zwnj;وانه له ده&zwnj;روونی خۆیاندا خۆیان به&zwnj;گه&zwnj;وره ده&zwnj;زانن، له سنوور ترازاون و سه&zwnj;رکه&zwnj;شیان بێ ئه&zwnj;ندازه&zwnj;یه.';
$t[22] = 'ئه&zwnj;و ڕۆژه&zwnj;ی که فریشته کان ده&zwnj;بینن هیچ جۆره مژده&zwnj;یه&zwnj;ک بۆ تاوانباران نیه (بۆیه به کزی و خه&zwnj;جاڵه&zwnj;تی و که&zwnj;ساسیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: خۆزگه بۆ په&zwnj;نایه&zwnj;ک، یان شوێنێکی داڵده&zwnj;دارو پارێزراو!!';
$t[23] = 'کاتێک هاتین و ته&zwnj;ماشای ئه&zwnj;وه&zwnj;مان کرد که کردوویانه (هه&zwnj;مووی تاوان بوو) به&zwnj;ربامانداو کردمانه ته&zwnj;پوتۆزێکی په&zwnj;رش و بڵاو.';
$t[24] = 'به&zwnj;هه&zwnj;شتیه&zwnj;کان له&zwnj;و ڕۆژه&zwnj;دا جێگه&zwnj;و ڕێگه&zwnj;یان زۆر خۆش و ڕازاوه&zwnj;یه و شوێنی حه&zwnj;وانه&zwnj;وه&zwnj;یان چاک و له&zwnj;باره.';
$t[25] = 'ڕۆژێک دێت ئاسمان له&zwnj;ت وپه&zwnj;ت ده&zwnj;بێت به هۆی هه&zwnj;ورێکی تایبه&zwnj;تی&zwnj;یه&zwnj;وه&zwnj;، به&zwnj;ڕاستی فریشته&zwnj;کانیش داده&zwnj;به&zwnj;زێنرێن ده&zwnj;سته ده&zwnj;سته و گرۆ گرۆ به شێوه&zwnj;یه&zwnj;کی تایبه&zwnj;ت.';
$t[26] = 'ئه&zwnj;و ڕۆژه ئیتر موڵک و ده&zwnj;سه&zwnj;ڵاتداری ڕاسته&zwnj;قینه به ده&zwnj;ست خوای میهره&zwnj;بانه، بێگومان ئه&zwnj;و ڕۆژه ڕۆژێکی زۆر سه&zwnj;خت و دژوار ده&zwnj;بێت له&zwnj;سه&zwnj;ر بی باوه&zwnj;ڕ و خوانه&zwnj;ناسان (زۆر په&zwnj;شیمان ده&zwnj;بن).';
$t[27] = 'ڕۆژێک دێت سته&zwnj;مکار قه&zwnj;پ ده&zwnj;کات به&zwnj;هه&zwnj;ردوو ده&zwnj;ستی خۆیدا وگاز له هه&zwnj;ردوو ده&zwnj;ستی خۆی ده&zwnj;گرێت (له په&zwnj;شیمانیدا و) ده&zwnj;ڵێت: خۆزگه له&zwnj;گه&zwnj;ڵ پێغه&zwnj;مبه&zwnj;ردا ڕێبازی ئیمانم بگرتایه.';
$t[28] = 'مه&zwnj;رگ بۆ خۆم، خۆزگه فڵان که&zwnj;سم نه&zwnj;کردایه&zwnj;ته هاوه&zwnj;ڵ و دۆستی خۆم (چونکه سه&zwnj;ری لێشێواندم).';
$t[29] = 'به&zwnj;ڕاستی گومڕاو وێلی کردم له به&zwnj;رنامه&zwnj;ی خوا دوای ئه&zwnj;وه&zwnj;ی پێم ڕاگه&zwnj;یه&zwnj;نرا بوو، شه&zwnj;یتان هه&zwnj;میشه و به&zwnj;رده&zwnj;وام هانی مرۆڤ ده&zwnj;دات بۆ گوناهو تاوان، له&zwnj;کاتی ته&zwnj;نگانه&zwnj;شدا پشتی به&zwnj;رده&zwnj;دات.';
$t[30] = '(دوای دوور که&zwnj;وتنه&zwnj;وه&zwnj;ی ئوممه&zwnj;ت له قورئان) پیغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم سکاڵا ده&zwnj;کات و ده&zwnj;فه&zwnj;رموێت: په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی قه&zwnj;ومه&zwnj;که&zwnj;م وازیان له&zwnj;م قورئانه هێناوه و پشت گوێیان خستووه و گرنگی پێ ناده&zwnj;ن.';
$t[31] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه&zwnj;ی (که تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم دژایه&zwnj;تی ده&zwnj;کرێیت) بۆ هه&zwnj;ر پێغه&zwnj;مبه&zwnj;رێک دوژمن و مڵۆزممان له تاوانباران فه&zwnj;راهه&zwnj;م هێناوه، (به&zwnj;ڵام دلنیابه که)په&zwnj;روه&zwnj;ردگات به&zwnj;سه که ڕێنموویت بکات و پشتیوانیت لێ بکات.';
$t[32] = 'کافره&zwnj;کان (بیانوویه&zwnj;کی تریان گرت و) وتیان: ئه&zwnj;وه بۆ قورئان به یه&zwnj;ک که&zwnj;ڕه&zwnj;ت بۆی ڕه&zwnj;وانه ناکرێت، بێگومان ئێمه هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ڕه&zwnj;وانه&zwnj;ی ده&zwnj;که&zwnj;ین، تا دڵی تۆ زیاتر دڵنیا بکه&zwnj;ین، به&zwnj;رده&zwnj;وام به&zwnj;جوانی دایده&zwnj;ڕێژین و ده&zwnj;یگه&zwnj;یه&zwnj;نین.';
$t[33] = 'خوانه&zwnj;ناسان هیچ ڕه&zwnj;خنه&zwnj;یه&zwnj;کت لێ ناگرن، له به&zwnj;رامبه&zwnj;ریه&zwnj;وه ئێمه حه&zwnj;قیقه&zwnj;ت و ڕاستیمان بۆ تۆ نه&zwnj;هێنا بێت و به&zwnj;جوانی هه&zwnj;موو شتێکت بۆ ڕوون نه&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;.';
$t[34] = 'ئه&zwnj;و تاوانبارانه&zwnj;ی (دژایه&zwnj;تی به&zwnj;رنامه&zwnj;ی خوا ده&zwnj;که&zwnj;ن)، له&zwnj;سه&zwnj;ر ڕووخساریان کێش ده&zwnj;کرێن بۆ دۆزه&zwnj;خ و تیایدا کۆده&zwnj;کرێنه&zwnj;وه، ئا ئه&zwnj;وانه ناخۆشترین جێگه&zwnj;یان هه&zwnj;یه&zwnj;، چونکه گومڕاترین ڕێبازیان کاتی خۆی گرتبووه به&zwnj;ر.';
$t[35] = 'سوێند به خوا بێگومان ئێمه کاتی خۆی کتێبی ته&zwnj;وراتمان به&zwnj;خشی به موسا و هاروونی برایشیمان کرده وه&zwnj;زیرو یاریده&zwnj;ده&zwnj;ری.';
$t[36] = 'ئه&zwnj;وسا پیمان وتن: هه&zwnj;ردووکتان بچن بۆ سه&zwnj;ر ئه&zwnj;و قه&zwnj;ومه&zwnj;ی که ئایه&zwnj;ته&zwnj;کانی ئیمه به&zwnj;درۆ ده&zwnj;زانن و بڕوای پێ ناکه&zwnj;ن (ئه&zwnj;وسا دوای ساڵه&zwnj;ها سه&zwnj;رکه&zwnj;شی و یاخی بوون) به توندو تیژی تێکمان شکاندن به&zwnj;سه&zwnj;ر یه&zwnj;کداو له&zwnj;ناومان بردن.';
$t[37] = 'قه&zwnj;ومی_ نوح_یش کاتێک به&zwnj;رنامه&zwnj;ی پێغه&zwnj;مبه&zwnj;رانیان به&zwnj;درۆ زانی (چونکه بڕوا نه بوون به پێغه&zwnj;مبه&zwnj;رێک بڕوا نه&zwnj;بوونه به هه&zwnj;موویان) ئه&zwnj;وسا ئێمه نوقمی زریانه&zwnj;که&zwnj;مان کردن و کردمانن به په&zwnj;ندو عیبره&zwnj;ت بۆ خه&zwnj;ڵکی و سزایه&zwnj;کی به ئێشمان بۆ سته&zwnj;مکاران ئاماده کردووه.';
$t[38] = 'هه&zwnj;روه&zwnj;ها هۆزی عاد و ثمود خاوه&zwnj;ن چاڵ و بیره&zwnj;کان، چه&zwnj;نده&zwnj;ها قه&zwnj;ومی زۆری تریش له نێوانیاندا (که خوانه&zwnj;ناس بوون له&zwnj;ناومان بردن، سته&zwnj;مکارانی هه&zwnj;موو سه&zwnj;رده&zwnj;مێک له ده&zwnj;ستی به توانای ئێمه ده&zwnj;رناچن).';
$t[39] = 'بۆ هه&zwnj;مووانیان نمونه&zwnj;مان هێنایه&zwnj;وه (ساڵه&zwnj;های دوورو درێژ پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان له&zwnj;ناویاندا بوو، به&zwnj;ڵام که هه&zwnj;ر ڕێبازی بێ&zwnj;باوه&zwnj;ڕیان به&zwnj;رنه&zwnj;دا) هه&zwnj;مووانیانمان به سه&zwnj;ختی له ناو برد و وردو خاشمان کردن.';
$t[40] = 'خۆ (قوره&zwnj;یشیه&zwnj;کان و خه&zwnj;ڵکێکی زۆر) سه&zwnj;ردانی ئه&zwnj;و شارۆچکه&zwnj;یان کردووه که&zwnj;کاتی خۆی به&zwnj;رد بارانی سه&zwnj;خت و دژواری به&zwnj;سه&zwnj;ردا باری، ئایا ئاسه&zwnj;واری ئه&zwnj;و کاول کاریه&zwnj;یان نه&zwnj;بینیووه&zwnj;؟ نه&zwnj;خێر هۆی نه بینین نی&zwnj;یه، به&zwnj;ڵکو ئه&zwnj;وانه به&zwnj;ته&zwnj;مای لێپرسینه&zwnj;وه و زیندوو بوونه&zwnj;وه نین؟';
$t[41] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم کاتێک خوانه&zwnj;ناسان تۆ ده&zwnj;بینن بێجگه له گاڵته پێکردنت نه&zwnj;بێت هیچ ڕێزيکت بۆ دانانێن ده&zwnj;ڵێن: ئا ئه&zwnj;مه&zwnj;یه که خوا کردوویه&zwnj;تی به پێغه&zwnj;مبه&zwnj;رو ڕه&zwnj;وانه&zwnj;ی کردووه؟!';
$t[42] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;ڵێن: خه&zwnj;ریک بوو له په&zwnj;رستنی خواکانمان وێڵمان بکات، ئه&zwnj;گه&zwnj;ر ئێمه خۆڕاگر نه&zwnj;بووینایه له سه&zwnj;ر په&zwnj;رستنیان، جا له داهاتوودا چاک بۆیان ده&zwnj;رده&zwnj;که&zwnj;وێت، کاتێک که سزاو ئازار ده&zwnj;بینن کێ زۆر گومڕاو سه&zwnj;رلێشێواوه؟!';
$t[43] = 'ئایا نابینیت ئه&zwnj;و که&zwnj;سه&zwnj;ی که ئاره&zwnj;زووی خۆی کردۆته خوای خۆی (شوێن نه&zwnj;فسی به&zwnj;دکردار که&zwnj;وتووه) ئایا تۆ ده&zwnj;توانیت ئه&zwnj;وانه بپارێزیت له لادان و سه&zwnj;رکه&zwnj;شی؟! (یاخود سنوورێکیان بۆ دابنێیت؟ خه&zwnj;مت نه&zwnj;بێ بۆ کوێ ده&zwnj;چن؟).';
$t[44] = 'ئایا واده&zwnj;زانیت زۆربه&zwnj;ی ئه&zwnj;وانه ڕاستیه&zwnj;کان ده&zwnj;بیستن، یان بیرو هۆشیان ده&zwnj;خه&zwnj;نه کار؟ ئه&zwnj;وانه ماڵات نه بێت، هیچی تر نین، به&zwnj;ڵکو گومڕاتریشن له ماڵات.';
$t[45] = 'ئایا نابینیت چۆن په&zwnj;روه&zwnj;ردگارت سێبه&zwnj;ر درێژ ده&zwnj;کاته&zwnj;وه، ئه&zwnj;گه&zwnj;ر بیویستایه جێگیری ده&zwnj;کرد، پاشان خۆریشمان کردووه به&zwnj;به&zwnj;ڵگه و نیشانه له&zwnj;سه&zwnj;ر سێبه&zwnj;ر (به هۆیه&zwnj;وه دیمه&zwnj;نی جوانی جۆراو جۆر به دروست کراوان ده&zwnj;به&zwnj;خشن).';
$t[46] = 'له&zwnj;وه&zwnj;ودوا به شێنه&zwnj;یی ئه&zwnj;و سێبه&zwnj;ره بۆ لای خۆمان ده&zwnj;گێڕینه&zwnj;وه و ده&zwnj;یگرینه&zwnj;وه (دیاره که خوا ده&zwnj;یه&zwnj;وێت له ڕێگه&zwnj;ی ئه&zwnj;و دیارده&zwnj;یه&zwnj;وه زاتی مه&zwnj;زنی فه&zwnj;رامۆش نه&zwnj;که&zwnj;ین).';
$t[47] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که شه&zwnj;وی کردووه به پۆشاک بۆتان و خه&zwnj;ویشی کردووه به هۆی ماتیی و بێ ده&zwnj;نگیی و ئارامیی و ڕۆژیشی کردووه به هۆی بڵاو بوونه&zwnj;وه و کار و کۆشش.';
$t[48] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که بای شه&zwnj;ماڵ ده&zwnj;نێرێت که مژده&zwnj;یه&zwnj;که له&zwnj;لایه&zwnj;ن خوای میهره&zwnj;بانه&zwnj;وه، (ئینجا ده&zwnj;فه&zwnj;رمووێت): ئێمه&zwnj;ش له ئاسمانه&zwnj;وه ئاوێکی پاک و خاوێنمان باراندووه (به شێوه&zwnj;ی باران و به&zwnj;فرو ته&zwnj;رزه&zwnj;)...';
$t[49] = 'تا به&zwnj;هۆیه&zwnj;وه سه&zwnj;رزه&zwnj;وی نیشتمانی مردوو زیندوو بکه&zwnj;ینه&zwnj;وه، هه&zwnj;تا ئاوی خواردنه&zwnj;وه&zwnj;ش ببه&zwnj;خشین به ماڵات و خه&zwnj;ڵکانێکی زۆر که دروستمان کردوون.';
$t[50] = 'سوێند به خوا ئێمه قورئانمان ڕاگه&zwnj;یاندوه و به چه&zwnj;نده&zwnj;ها شێوه خستومانه&zwnj;ته به&zwnj;رده&zwnj;میان تا یاداوه&zwnj;ری وه&zwnj;ربگرن، که&zwnj;چی زۆربه&zwnj;ی خه&zwnj;ڵکی هه&zwnj;ر ڕێبازی بێ باوه&zwnj;ڕی ده&zwnj;گرنه به&zwnj;ر، هه&zwnj;ر شوێنی بێ&zwnj;باوه&zwnj;ڕی ده&zwnj;که&zwnj;ون و هیچی تر.';
$t[51] = 'خۆ ئه&zwnj;گه&zwnj;ر بمانویستایه بۆ هه&zwnj;ر شارو شارۆچکه&zwnj;یه&zwnj;ک پێغه&zwnj;مبه&zwnj;رێک و یادخه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کمان ڕه&zwnj;وانه ده&zwnj;کرد.';
$t[52] = '(تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر به&zwnj;رده&zwnj;وام به له&zwnj;سه&zwnj;ر گه&zwnj;یاندنی په&zwnj;یامه&zwnj;که&zwnj;ت) و به گوێی بێ باوه&zwnj;ڕان مه&zwnj;که و ملکه&zwnj;چیان مه&zwnj;به و به&zwnj;م قورئانه هه&zwnj;وڵ و کۆشش و خه&zwnj;باتێکی گه&zwnj;وره&zwnj;یان له&zwnj;گه&zwnj;ڵدا بکه&zwnj;.';
$t[53] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که ئاوی دوو ده&zwnj;ریا یان دوو ڕووبار له پاڵ یه&zwnj;کدا (وه&zwnj;ک خۆیان ده&zwnj;هێلێته&zwnj;وه و تێکه&zwnj;ڵ نابن، چونکه چڕیان جیاوازه)، ئه&zwnj;مه&zwnj;یان شیرین و خۆش و سازگار، ئه&zwnj;وی تریان تفت و تاڵه، له نێوانیاندا به&zwnj;ربه&zwnj;ست و کۆسپی فه&zwnj;راهه&zwnj;م هێناوه ناهێڵێت تیکه&zwnj;ڵ ببن.';
$t[54] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;یه که ئاوی (مه&zwnj;نی) به&zwnj;شه&zwnj;ر دروست ده&zwnj;کات (له&zwnj;وه&zwnj;ودواش زۆربه&zwnj;ی پێکهاته&zwnj;ی له&zwnj;شی هه&zwnj;ر ئاوه) و کردوویه&zwnj;تی به هۆی خزمایه&zwnj;تی (له ڕێگه&zwnj;ی پیاوه&zwnj;وه کۆمه&zwnj;ڵێک خزم دروست ده&zwnj;بێت هه&zwnj;روه&zwnj;ها له ڕێگه&zwnj;ی ئافره&zwnj;ته&zwnj;وه کۆمه&zwnj;ڵێک)، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ به&zwnj;رده&zwnj;وام ده&zwnj;سه&zwnj;ڵاتی بێ سنووره (له هه&zwnj;موو بوارێکدا).';
$t[55] = 'خوانه&zwnj;ناسان له جیاتی خوا شتیک ده&zwnj;په&zwnj;رستن که نه سوودیان پێده&zwnj;گه&zwnj;یه&zwnj;نێت نه زیان و هه&zwnj;میشه و به&zwnj;رده&zwnj;وام بێ&zwnj;باوه&zwnj;ڕ دژ به به&zwnj;رنامه&zwnj;ی په&zwnj;روه&zwnj;ردگاری ده&zwnj;وه&zwnj;ستێت.';
$t[56] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم تۆمان ڕه&zwnj;وانه کردووه&zwnj;، مه&zwnj;گه&zwnj;ر بۆ ئه&zwnj;وه&zwnj;ی مژده&zwnj;ده&zwnj;ر بیت به (ئیمانداران) ترسێنه&zwnj;ر بیت (بۆ بێ&zwnj;باوه&zwnj;ڕان).';
$t[57] = 'به&zwnj;و خه&zwnj;لکه بڵێ: من هیچ کرێ و پاداشتێکم له ئێوه ناوێت جگه جگه له&zwnj;وه که ڕی نیشانده&zwnj;رم بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی ده&zwnj;یه&zwnj;وێت ڕێگه&zwnj;ی به&zwnj;ره&zwnj;و په&zwnj;روه&zwnj;ردگاری بگرێته&zwnj;به&zwnj;رو مه&zwnj;به&zwnj;ستی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;و بێت.';
$t[58] = 'تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم، پشت ببه&zwnj;سته به&zwnj;و زاته&zwnj;ی که هه&zwnj;میشه زیندووه و هه&zwnj;رگیز نامرێت، به&zwnj;رده&zwnj;وام ته&zwnj;سبیحات و سوپاس و ستایشی بکه&zwnj;، ئه&zwnj;وه&zwnj;نده به&zwnj;سه بۆ ئه&zwnj;و زاته که به هه&zwnj;موو گوناهو تاوانی به&zwnj;نده&zwnj;کانی ئاگایه و (هیچی لێ په&zwnj;نهان نیه&zwnj;).';
$t[59] = 'ئه&zwnj;و زاته&zwnj;ی که ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;رچی وا له نێوانیاندایه له ماوه&zwnj;ی شه&zwnj;ش ڕۆژدا دروستی کردووه، له&zwnj;وه&zwnj;ودوا له&zwnj;سه&zwnj;ر عه&zwnj;رش وته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی وه&zwnj;ستاوه (سه&zwnj;رنجی ئایه&zwnj;تی_2_سوره&zwnj;تی- الرعد- بده&zwnj;)، خوا په&zwnj;روه&zwnj;ردگارێکی میهره&zwnj;بانه، (جا بۆ تێگه&zwnj;یشتن له ورده&zwnj;کاری و گرنگی به&zwnj;دیهێنراوه&zwnj;کانی له جیهانی ئه&zwnj;ستێره&zwnj;کان و کۆمه&zwnj;ڵه&zwnj;ی خۆر و ئاده&zwnj;میزاد و گیانله&zwnj;به&zwnj;ران و ڕووه&zwnj;ک و... هتد) له که&zwnj;سێکی ئاگاو زانا بپرسه&zwnj;!!';
$t[60] = 'کاتێک به خوانه&zwnj;ناسان بوترێت سوژده ببه&zwnj;ن بۆ خوای میهره&zwnj;بان (ئه&zwnj;و نه&zwnj;فامانه به&zwnj;سه&zwnj;رکه&zwnj;شیه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: ڕه&zwnj;حمان کێیه؟ خوای میهره&zwnj;بان کامه&zwnj;یه؟ ئایا سوژده به&zwnj;رین بۆ که&zwnj;سێک که تۆ فه&zwnj;رمانمان پێ ده&zwnj;ده&zwnj;یت؟! تا بێت فه&zwnj;رمانی خوا زیاتر ئه&zwnj;وانه ته&zwnj;ره&zwnj;و وێڵ ده&zwnj;کات.';
$t[61] = 'زۆر گه&zwnj;وره&zwnj;و مه&zwnj;زنه ئه&zwnj;و زاته&zwnj;ی له ئاسماندا چه&zwnj;نده&zwnj;ها خولگه&zwnj;ی گه&zwnj;وره&zwnj;ی (بۆ هه&zwnj;ساره&zwnj;و ئه&zwnj;ستێره&zwnj;کان) به&zwnj;دیهێناوه&zwnj;، هه&zwnj;روه&zwnj;ها چرایه&zwnj;کی پڕشنگدار، که خۆره و مانگێکی ڕوناکیشی بۆ زه&zwnj;وی دابین کردووه.';
$t[62] = 'خوا؛ ئه&zwnj;و زاته&zwnj;یه که شه&zwnj;و و ڕۆژی به شوێن یه&zwnj;کدا ڕێکخستووه که فرسه&zwnj;تێکه بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که ده&zwnj;یه&zwnj;وێت یاداوه&zwnj;ری وه&zwnj;ربگرێت و ده&zwnj;یه&zwnj;وێت سوپاسگوزاری ئه&zwnj;نجام بدات.';
$t[63] = 'به&zwnj;نده&zwnj;کانی خوای میهره&zwnj;بان ئه&zwnj;و که&zwnj;سانه&zwnj;ن که زۆر به هێمنی و سه&zwnj;لاری به&zwnj;سه&zwnj;ر زه&zwnj;ویدا ده&zwnj;ڕۆن، کاتێکیش نه&zwnj;فامان به قسه و قسه&zwnj;ڵۆکی ناخۆش به&zwnj;ره&zwnj;نگاریان ده&zwnj;بن، له وه&zwnj;ڵامیاندا ده&zwnj;ڵێن: سڵاو (ڕه&zwnj;وشت به&zwnj;رزی خۆیان ده&zwnj;نوێنن).';
$t[64] = 'ئه&zwnj;وانه&zwnj;ی که شه&zwnj;وگار بۆ په&zwnj;روه&zwnj;ردگاریان به سوژده و وه&zwnj;ستاون و نوێژ ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[65] = 'ئه&zwnj;وانه&zwnj;ی که ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا، له&zwnj;سزای ئاگری دۆزه&zwnj;خ به&zwnj;دوورمان بکه و په&zwnj;نامان بده&zwnj;، چونکه سزاو ئازاری له کۆڵ نابێته&zwnj;وه و به&zwnj;رده&zwnj;وامه (بۆ ئه&zwnj;وکه&zwnj;سه&zwnj;ی خوانه&zwnj;ناس و یاخیه).';
$t[66] = 'به&zwnj;ڕاستی ئه&zwnj;و دۆزه&zwnj;خه شوێن و نیشته&zwnj;جێیه&zwnj;کی زۆر ناخۆش و ناسازه (ئاهو ناڵه&zwnj;و په&zwnj;شیمانی بێ سنووره تیایدا).';
$t[67] = '(هه&zwnj;روه&zwnj;ها به&zwnj;نده&zwnj;کانی خوای میهره&zwnj;بان) ئه&zwnj;وانه&zwnj;ن که&zwnj;: کاتێک ماڵو سامانیان ده&zwnj;به&zwnj;خشن و خه&zwnj;رجی ده&zwnj;که&zwnj;ن، نه زیاده&zwnj;ڕه&zwnj;وی تێدا ده&zwnj;که&zwnj;ن و بازایه&zwnj;ی ده&zwnj;ده&zwnj;ن، نه ده&zwnj;ست نوقاو ڕه&zwnj;زیلیشن، به&zwnj;ڵکو له&zwnj;و نێوانه&zwnj;دان (مام ناوه&zwnj;ندین و له شتی به&zwnj;سوود به&zwnj;کاری ده&zwnj;هێنن).';
$t[68] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ی هیچ جۆره خوایه&zwnj;کی تر له&zwnj;گه&zwnj;ڵ خوادا ناپه&zwnj;رستن، که&zwnj;سیش ناکوژن که خوا کوشتنی حه&zwnj;رام کردبێت مه&zwnj;گه&zwnj;ر به حه&zwnj;ق و ڕه&zwnj;وا، هه&zwnj;روه&zwnj;ها زینا ناکه&zwnj;ن و نزیکی ناکه&zwnj;ون، جا ئه&zwnj;وه&zwnj;ی ئه&zwnj;و جۆره کاره خراپانه ئه&zwnj;نجام بدات، ئه&zwnj;وه توشی سزا و ئازارو ڕیسوایی ده&zwnj;بێت.';
$t[69] = 'له قیامه&zwnj;تیشدا ئازارو سزای بۆ چه&zwnj;ند به&zwnj;رابه&zwnj;ر ده&zwnj;کرێت وبه زه&zwnj;لیلی و خه&zwnj;جاڵه&zwnj;تی و شه&zwnj;رمه&zwnj;زارییه&zwnj;وه ده&zwnj;بێت ژیانی تێدا به&zwnj;رێته سه&zwnj;ر.';
$t[70] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی ته&zwnj;وبه بکات و باوه&zwnj;ڕ بهێنێت وکار وکرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام بدات، ئا ئه&zwnj;و جۆره که&zwnj;سانه خوا کار و کرده&zwnj;وه خراپه&zwnj;کانیشیان بۆ ده&zwnj;گۆڕێت به چاکه&zwnj;، چونکه هه&zwnj;میشه و به&zwnj;رده&zwnj;وام خوا لێخۆشبوو میهره&zwnj;بانه (به&zwnj;رامبه&zwnj;ر خه&zwnj;ڵکی به گشتی و ئه&zwnj;و جۆره که&zwnj;سانه به تایبه&zwnj;تی).';
$t[71] = 'جا ئه&zwnj;وه&zwnj;ی که ته&zwnj;وبه ده&zwnj;کات و کارو کرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام ده&zwnj;دات و به&zwnj;ته&zwnj;مایه په&zwnj;روه&zwnj;ردگار ته&zwnj;وبه&zwnj;که&zwnj;ی لێوه&zwnj;ربگرێت؛ ئه&zwnj;وه بێگومان ده&zwnj;گه&zwnj;ڕێته&zwnj;وه بۆ لای خوا به ته&zwnj;وبه&zwnj;یه&zwnj;کی ڕاست ودروسته&zwnj;وه.';
$t[72] = '(به&zwnj;نده&zwnj;کانی خوای میهره&zwnj;بان) ئه&zwnj;وانه&zwnj;شن که شایه&zwnj;تی ناحه&zwnj;ق ناده&zwnj;ن، هه&zwnj;رگیز له شوێنێکدا ئاماده نابن که شایه&zwnj;تی ناحه&zwnj;قی تێدا ئه&zwnj;نجام ده&zwnj;درێت و یان درۆی تێدا ده&zwnj;کرێت، کاتێکیش به لای گه&zwnj;مه و گاڵه و چه&zwnj;پۆکانی نه&zwnj;فاماندا تێده&zwnj;په&zwnj;ڕن، به هێمنی و له&zwnj;سه&zwnj;رخۆیی و سه&zwnj;ربه&zwnj;رزیه&zwnj;وه تێده&zwnj;په&zwnj;ڕن.';
$t[73] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش؛ کاتێک به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی په&zwnj;روه&zwnj;ردگاریان بێدار بکرێنه&zwnj;وه&zwnj;، هه&zwnj;روا به&zwnj;که&zwnj;ڕی و کوێری به&zwnj;لایاندا تێناپه&zwnj;ڕن (به&zwnj;ڵکو تێده&zwnj;فکرن و لێکی ده&zwnj;ده&zwnj;نه&zwnj;وه&zwnj;و پاشان په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن).';
$t[74] = 'ئه&zwnj;وانه&zwnj;شن که ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا له هاوسه&zwnj;ران و نه&zwnj;وه&zwnj;کانمان که&zwnj;سانێکمان پێ ببه&zwnj;خشه که ببنه مایه&zwnj;ی ڕووناکی دیده&zwnj;مان و شادمانی دڵمان و بمان که به پێشه&zwnj;وا بۆ پارێزکاران و خواناسان.';
$t[75] = 'ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ی (که باسکران) پاداشتیان ژووره (ڕازاوه و به&zwnj;رزو خۆشه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;) له به&zwnj;رامبه&zwnj;ر خۆگرتن و ئارامگرتنیانه&zwnj;وه، جا له&zwnj;وێ به سڵاو و دروود پێشوازیان لێده&zwnj;کرێت (بۆ هه&zwnj;میشه ده&zwnj;حه&zwnj;وێنه&zwnj;وه تیایدا).';
$t[76] = 'بێگومان له ناو ژووره&zwnj;کانی به&zwnj;هه&zwnj;شتدا ژیانی کامه&zwnj;رانی و به&zwnj;رده&zwnj;وام ده&zwnj;به&zwnj;نه سه&zwnj;ر، به&zwnj;ڕاستی جێگه وڕێگه و نیشته&zwnj;جێییه&zwnj;کی خۆش و سازگار و ڕازاوه&zwnj;یه&zwnj;.';
$t[77] = 'تۆ ئه&zwnj;ی محمد صلی الله علیه وسلم پێیان بڵێ: (خه&zwnj;ڵکینه&zwnj;) په&zwnj;روه&zwnj;ردگارم هیچ گوێتان پێ نادات و نرختان بۆ دانانێت، ئه&zwnj;گه&zwnj;ر دوعاو نزاو خواپه&zwnj;رستیتان نه&zwnj;بێت، که&zwnj;چی زۆربه&zwnj;تان هه&zwnj;ر پێغه&zwnj;مبه&zwnj;ر و قورئانتان به درۆزانی، ئه&zwnj;گه&zwnj;ر به&zwnj;رده&zwnj;وام وابن، ئه&zwnj;وا سزاو تۆڵه&zwnj;ی خوایی له ئاینده&zwnj;دا یه&zwnj;خه&zwnj;تان پێ ده&zwnj;گرێت له کۆڵتان نابێته&zwnj;وه.';
?>