<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (1لبقره&zwnj;) بده&zwnj;، ئه&zwnj;وه ئایه&zwnj;ته&zwnj;کانی ئه&zwnj;م قورئانه&zwnj;ن و فه&zwnj;رمانی کتێبێکی پیرۆزو ئاشکرا و ڕوونه&zwnj;.';
$t[2] = 'ڕێنمووی به&zwnj;خش و مژده به&zwnj;خشه به ئیمانداران (له هه&zwnj;موو سه&zwnj;رده&zwnj;مێکدا).';
$t[3] = 'ئه&zwnj;وانه&zwnj;ی که نوێژه&zwnj;کانیان به چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و زه&zwnj;کات ده&zwnj;به&zwnj;خشن و باوه&zwnj;ڕێکی به&zwnj;تین و دامه&zwnj;زراویان به ڕۆژی قیامه&zwnj;ت هه&zwnj;یه&zwnj;.';
$t[4] = 'بێگومان ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕ به قیامه&zwnj;ت ناهێنن، کارو کرده&zwnj;وه خراپه&zwnj;کانمان له&zwnj;به&zwnj;رچاو جوان کردوون، له&zwnj;به&zwnj;ر ئه&zwnj;وه چاوی دڵیان کوێر بووه خراپی کرداره&zwnj;کانیان نابینن.';
$t[5] = 'ئائه&zwnj;وانه ئه&zwnj;وانه&zwnj;ن ناخۆشترین و ناسۆرترین سزا بۆیان ئامه&zwnj;ده&zwnj;یه و ئه&zwnj;وان له قیامه&zwnj;تدا هه&zwnj;موو که&zwnj;س خه&zwnj;ساره&zwnj;تمه&zwnj;ندترن.';
$t[6] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) به&zwnj;ڕاستی تۆ له&zwnj;لایه&zwnj;ن زاتێکی داناو زاناوه قورئان فێر ده&zwnj;کرێیت و پێت ده&zwnj;ردرێت.';
$t[7] = 'یادیان بهێنه&zwnj;، کاتێک موسا (له&zwnj;ڕێگای گه&zwnj;ڕانه&zwnj;وه&zwnj;یدا بۆ میسر) به هاوسه&zwnj;ره&zwnj;که&zwnj;ی وت: (ئێوه لێره&zwnj;دا بمێننه&zwnj;وه&zwnj;) به&zwnj;ڕاستی من ئاگرێکم به&zwnj;دی کردووه و دڵم پێی خۆشه&zwnj;، له&zwnj;وێوه هه&zwnj;واڵێکتان بۆ ده&zwnj;هێنم، یان سه&zwnj;لکه بزوتێکتان بۆ ده&zwnj;هێنم، بۆ ئه&zwnj;وه&zwnj;ی خۆتانی پێ گه&zwnj;رم بکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[8] = 'جا کاتێک گه&zwnj;یشته لای ئه&zwnj;وه&zwnj;ی به ئاگری زانی (بینی نوورو ڕووناکیه&zwnj;کی بێ وێنه&zwnj;یه&zwnj;) بانگی لێکرا: پیرۆزه ئه&zwnj;و که&zwnj;سه&zwnj;ی که له&zwnj;پاڵ ئاگره&zwnj;که&zwnj;دایه و ئه&zwnj;و فریشتانه&zwnj;ش که له&zwnj;جێی ئاگره تایبه&zwnj;تیه&zwnj;که&zwnj;دا هه&zwnj;ن، پاکی و بێگه&zwnj;ردی بۆ خوایه&zwnj;، که په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیانه&zwnj;.';
$t[9] = 'ئه&zwnj;ی موسا ئه&zwnj;و که&zwnj;سه&zwnj;ی قسه&zwnj;ت بۆ ده&zwnj;کات ئه&zwnj;وه به&zwnj;ڕاستی منم، ئه&zwnj;و خوایه&zwnj;ی که باڵا ده&zwnj;ست و دانایه&zwnj;.';
$t[10] = 'عه&zwnj;ساکه&zwnj;ت فڕێ بده&zwnj;، جاکاتێک فڕێی&zwnj;دا بینی، وه&zwnj;کو توله&zwnj;مار به&zwnj;خێرایی ده&zwnj;جووڵا، موسا هه&zwnj;ڵات و پشتی تێکردو ئاوڕی نه&zwnj;دایه&zwnj;وه&zwnj;، (ئه&zwnj;وسا خوای گه&zwnj;وره فه&zwnj;رمووی): ئه&zwnj;ی موسا مه&zwnj;ترسه&zwnj;، چونکه به&zwnj;ڕاستی پێغه&zwnj;مبه&zwnj;ران له لای من ناترسن و بیمیان نیه&zwnj;...';
$t[11] = 'مه&zwnj;گه&zwnj;ر که&zwnj;سێک سته&zwnj;مێکی کردبێت و پاشان چاکه&zwnj;کاری ئه&zwnj;نجام بدات دوای ئه&zwnj;وه&zwnj;ی پێشتر هه&zwnj;ڵه&zwnj;ی هه&zwnj;بووه&zwnj;، ئه&zwnj;وه من بۆ ئه&zwnj;و جۆره که&zwnj;سانه لێخۆشبوو میهره&zwnj;بانم.';
$t[12] = 'جا ده&zwnj;سته&zwnj;که&zwnj;ت بخه&zwnj;ره گیرفانته&zwnj;وه&zwnj;، هه&zwnj;ر که ده&zwnj;رتهێنا به سپییه&zwnj;کی بێ عه&zwnj;یب دێته ده&zwnj;ره&zwnj;وه&zwnj;، ئه&zwnj;م دوو _ موعجیزه _یه له ڕیزی نۆ _ موعجیزه _ دایه بۆ فیرعه&zwnj;ون و قه&zwnj;ومه&zwnj;که&zwnj;ی، به&zwnj;ڕاستی ئه&zwnj;وانه قه&zwnj;ومێکی له سنوور ده&zwnj;رچوو بوون.';
$t[13] = 'جا کاتێک _ موعجیزه _ کانی ئێمه&zwnj;یان به ڕوونی و ئاشکرا بۆ هات و خرایه به&zwnj;رچاویان وتیان: ئه&zwnj;مه جادوویه&zwnj;کی ئاشکرایه...';
$t[14] = 'جا ئه&zwnj;وانه&zwnj;ی هه&zwnj;ر ڕق ئه&zwnj;ستوورییان کردو باوه&zwnj;ڕیان پێی نه&zwnj;بوو، به زۆرداری و خۆبه&zwnj;زلزانی، له کاتێکدا له ناخی ده&zwnj;روونیانه&zwnj;وه هه&zwnj;ستیان ده&zwnj;کرد حه&zwnj;قیقه&zwnj;ته&zwnj;و ڕاسته&zwnj;، ده ته&zwnj;ماشا بکه و سه&zwnj;رنج بده بزانه سه&zwnj;ره&zwnj;نجامی تاوانباران و گوناهکاران چۆن بوو (چۆن تۆڵه&zwnj;مان لێ سه&zwnj;ندن).';
$t[15] = 'سوێند به&zwnj;خوا بێگومان ئێمه داود و سوله&zwnj;یمان زانیاری و زانستیمان به&zwnj;خشی، هه&zwnj;ردووکیان وتیان: سوپاس وستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که زیاده ڕێزی ئێمه&zwnj;ی داوه به&zwnj;سه&zwnj;ر زۆرێک له به&zwnj;نده ئیمانداره&zwnj;کانیدا.';
$t[16] = 'سوله&zwnj;یمان جێنشینی داودی باوکی بوو، ڕۆژێک وتی: ئه&zwnj;ی خه&zwnj;ڵکینه ئێمه فێری زمانی باڵنده کراوین و له هه&zwnj;موو شتێکمان پێدراوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;مه هه&zwnj;ر فه&zwnj;زڵ و ڕێزێکی ئاشکرایه (له&zwnj;لایه&zwnj;ن خواوه پێمان به&zwnj;خشراوه&zwnj;).';
$t[17] = 'هه&zwnj;رچی سه&zwnj;ربازو له&zwnj;شکری سوله&zwnj;یمان هه&zwnj;بوو له په&zwnj;ری و ئاده&zwnj;میزاد و باڵنده کۆکرانه&zwnj;وه و نمایشیان کرد به ڕیزو ڕێک و پێکی هه&zwnj;موویان به&zwnj;سه&zwnj;ره&zwnj;تاو کۆتاییان یه&zwnj;کیان گرت.';
$t[18] = 'هه&zwnj;تا گه&zwnj;یشته دۆڵی مێرووله&zwnj;، مێرووله مێچکه&zwnj;یه&zwnj;ک (هاواری کرد و) وتی: ئه&zwnj;های مێرووله&zwnj;ینه&zwnj;، بچنه ماڵ و کون و که&zwnj;له&zwnj;به&zwnj;ره&zwnj;کانتانه&zwnj;وه نه&zwnj;وه&zwnj;کو سوله&zwnj;یمان و سه&zwnj;ربازانی پێشێلتان بکه&zwnj;ن، له&zwnj;کاتێکدا هه&zwnj;ستتان پێناکه&zwnj;ن.';
$t[19] = 'سوله&zwnj;یمان (که گوێی له هاواری مێرووله&zwnj;که بوو) زه&zwnj;رده&zwnj;خه&zwnj;نه گرتی و پێکه&zwnj;نی به هاوارو قسه&zwnj;که&zwnj;ی، بۆیه وتی: په&zwnj;روه&zwnj;ردگارا، یارمه&zwnj;تیم بده دڵ و ده&zwnj;روون و ڕۆح وگیانم کۆبکه&zwnj;ره&zwnj;وه&zwnj;، تا سوپاسگوزاری نازو نیعمه&zwnj;ته&zwnj;کانت بم که به&zwnj;سه&zwnj;ر من و دایک وباوکمدا ڕژاندووته&zwnj;، هه&zwnj;روه&zwnj;ها کۆمه&zwnj;کیم بکه تا کاروکرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام بده&zwnj;م که تۆ پێی ڕازیی بیت و به ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆت بمخه&zwnj;ره ڕیزی به&zwnj;نده چاکه&zwnj;کانته&zwnj;وه&zwnj;.';
$t[20] = '(ئینجا سوله&zwnj;یمان) که&zwnj;وته به&zwnj;سه&zwnj;ر کردنه&zwnj;وه و پشکنینی ده&zwnj;سته&zwnj;و تاقمی باڵنده&zwnj;کان و وتی: ئه&zwnj;وه بۆچی من په&zwnj;پوو نابینم؟ یاخود لێره نیه و خۆی دزیوه&zwnj;ته&zwnj;وه و له نادیاره&zwnj;کانه&zwnj;!!';
$t[21] = 'سوێند بێت ئه&zwnj;بێت سزایه&zwnj;کی زۆر سه&zwnj;ختی بده&zwnj;م یان سه&zwnj;ری ببڕم، یان ده&zwnj;بێت به به&zwnj;ڵگه&zwnj;یه&zwnj;کی ئاشکراوه بێت بۆ لام.';
$t[22] = '(سوله&zwnj;یمان) زۆر چاوه&zwnj;ڕێی نه&zwnj;کرد (په&zwnj;پوو په&zwnj;یدابوو، یه&zwnj;کسه&zwnj;ر هات و نیشته&zwnj;وه و) وتی: ئه&zwnj;وه&zwnj;ی من پێم زانیووه تۆ پێت نه&zwnj;زانیووه&zwnj;، من له وڵاتی سه&zwnj;به&zwnj;ئه&zwnj;وه هه&zwnj;واڵێکی ڕاست و دروستم بۆ هێناویت!!';
$t[23] = 'به&zwnj;ڕاستی من بۆم ده&zwnj;رکه&zwnj;وت ئافره&zwnj;تێک فه&zwnj;رمانڕه&zwnj;واییان ده&zwnj;کات و له هه&zwnj;موو جۆره ده&zwnj;سه&zwnj;ڵات و نازو نیعمه&zwnj;تێکی پێبه&zwnj;خشراوه&zwnj;، ته&zwnj;ختێکی پاشایه&zwnj;تی گه&zwnj;وره&zwnj;شی هه&zwnj;یه&zwnj;، بینیم خۆی و قه&zwnj;ومه&zwnj;که&zwnj;ی سوژده بۆ خۆر ده&zwnj;به&zwnj;ن له جیاتی خوا، شه&zwnj;یتان کارو کرده&zwnj;وه&zwnj;ی چه&zwnj;وتیانی بۆ ڕازاندوونه&zwnj;ته&zwnj;وه&zwnj;، به&zwnj;و هۆیه&zwnj;وه ڕێبازی ڕاست و دروستی لێگرتوون، بۆیه ئه&zwnj;وان ڕێنمووی وه&zwnj;رناگرن.';
$t[24] = 'بینیم که خۆی قه&zwnj;ومه&zwnj;که&zwnj;ی سوجه&zwnj;ده&zwnj;یان ده&zwnj;برد بۆ ڕۆژ له جیاتی خوا، وه (شه&zwnj;یتان) کاروکرده&zwnj;وه&zwnj;کانیانی شیرین و جوان کردبوو له به&zwnj;رچاویان هه&zwnj;ر بۆیه به هۆی ئه&zwnj;وه له ڕێگا لایان داوه&zwnj;و هیدایه&zwnj;تیان وه&zwnj;رنه&zwnj;گرتووه&zwnj;.';
$t[25] = '(شه&zwnj;یتان ڕێی لێگرتن) بۆ ئه&zwnj;وه&zwnj;ی سوژده نه&zwnj;به&zwnj;ن بۆ ئه&zwnj;و خوایه&zwnj;ی که نهێنی ده&zwnj;رده&zwnj;کێشێت له ئاسمانه&zwnj;کان و زه&zwnj;ویداو ده&zwnj;زانێت ئێوه چی ده&zwnj;شارنه&zwnj;وه و چیش ئاشکرا ده&zwnj;که&zwnj;ن...';
$t[26] = 'خوا ئه&zwnj;و زاته&zwnj;یه که جگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، خاوه&zwnj;نی ته&zwnj;ختی مه&zwnj;زن وگه&zwnj;وره&zwnj;یه&zwnj;.';
$t[27] = 'سوله&zwnj;یمان وتی به په&zwnj;پوو: ئێمه له&zwnj;مه&zwnj;ودوا له&zwnj;و گوفتاره&zwnj;ی تۆ ده&zwnj;کۆلینه&zwnj;وه&zwnj;، تابزانین ڕاست ده&zwnj;که&zwnj;یت یان له ڕیزی درۆزنانیت.';
$t[28] = '(ئه&zwnj;وسا سوله&zwnj;یمان نامه&zwnj;یه&zwnj;کی نووسی و دای به په&zwnj;پوو فه&zwnj;رمانی پێدا که&zwnj;): بڕۆ ئه&zwnj;م نامه&zwnj;یه ببه و (له شوێنێکی به&zwnj;رچاودا) بۆیان فڕێ بده و پاشان (له په&zwnj;نایه&zwnj;کدا) خۆت حه&zwnj;شار بده و بزانه ده&zwnj;ڵێن چی و ج باس و خواسێک ده&zwnj;که&zwnj;ن.';
$t[29] = '(شاژن هات ونامه&zwnj;ی بینی و خوێندیه&zwnj;وه&zwnj;، پاشان وه&zwnj;زیران و ئه&zwnj;نجومه&zwnj;نه&zwnj;که&zwnj;ی خۆی کۆکرده&zwnj;وه و) وتی: ئه&zwnj;ی ڕێزداران، به&zwnj;ڕاستی من نامه&zwnj;یه&zwnj;کی پیرۆزو به&zwnj;ڕێزم بۆ دانراوه&zwnj;.';
$t[30] = 'نامه&zwnj;که له سوله&zwnj;یمانه&zwnj;وه و به ناوی خوای به&zwnj;خشنده&zwnj;ی میهره&zwnj;بانه&zwnj;وه (ده&zwnj;ست پێ ده&zwnj;کات و ده&zwnj;ڵێت):';
$t[31] = 'خۆتان به&zwnj;گه&zwnj;وره مه&zwnj;زانن و لێم یاخی مه&zwnj;بن و وه&zwnj;رن موسوڵمان ببن (واز له خۆر په&zwnj;رستی بهێنن و به&zwnj;دیهێنه&zwnj;ری خۆر و مانگ و ئه&zwnj;ستێره&zwnj;و زه&zwnj;وی و... هتد بپه&zwnj;رستن).';
$t[32] = 'ئینجا به&zwnj;ڵقیس به ئاماده بووانی وت: ئه&zwnj;ی ڕێزداران ڕاتان چیه&zwnj;، چی بکه&zwnj;م باشه&zwnj;، خۆ من هیچ بڕیارێکم نه&zwnj;داوه هه&zwnj;تا ئێوه لێی ئاگادار نه&zwnj;بوو بن و ڕاتان له&zwnj;سه&zwnj;ری نه&zwnj;دابێت.';
$t[33] = 'ئه&zwnj;نجومه&zwnj;نه&zwnj;که&zwnj;ی وتیان: خۆت ده&zwnj;زانیت که ئێمه خاوه&zwnj;نی هێزو تواناو ده&zwnj;سه&zwnj;ڵاتی توندو تیژین، فه&zwnj;رمانیش فه&zwnj;رمانی تۆیه&zwnj;، بیر بکه&zwnj;ره&zwnj;وه و تێفکره چ فه&zwnj;رمانێک ده&zwnj;ده&zwnj;یت (ئێمه ئاماده&zwnj;ین).';
$t[34] = 'به&zwnj;ڵقیس وتی: به&zwnj;ڕاستی له&zwnj;شکری پاشاکان کاتێک چووبنه شارو دێهاته&zwnj;کانه&zwnj;وه&zwnj;، وێران وکاولیان کردووه و تۆوی خراپه&zwnj;یان چاندووه&zwnj;، که&zwnj;سانی خاوه&zwnj;ن ده&zwnj;سه&zwnj;ڵات و پیاوی ناوداری ئه&zwnj;و شوێنه&zwnj;یان زه&zwnj;لیل و ڕیسوا کردووه و هه&zwnj;میشه ئه&zwnj;وه پیشه&zwnj;یانه و به&zwnj;و جۆره ده&zwnj;که&zwnj;ن و هه&zwnj;رواش ده&zwnj;بن (بێگومان من حه&zwnj;ز له کاره&zwnj;ساتی وا ناکه&zwnj;م)...';
$t[35] = 'بێگومان له&zwnj;به&zwnj;رئه&zwnj;وه من بڕیارمداوه که&zwnj;: دیاریه&zwnj;کیان بۆ بنێرم، جا چاوه&zwnj;ڕێ ده&zwnj;که&zwnj;م بزانم نوێنه&zwnj;ره&zwnj;کان چ هه&zwnj;واڵێکم بۆ ده&zwnj;هێننه&zwnj;وه و (هه&zwnj;ڵوێستی سوله&zwnj;یمان جۆن ده&zwnj;بێت).';
$t[36] = 'جا کاتێک (نوێنه&zwnj;ری) نێردراوه&zwnj;کان گه&zwnj;یشته لای سوله&zwnj;یمان، (هه&zwnj;واڵی دیاریه&zwnj;که&zwnj;ی گه&zwnj;یاند، سوله&zwnj;یمان به تووڕه&zwnj;ییه&zwnj;وه&zwnj;) پێی وت: ئێوه ده&zwnj;تانه&zwnj;وێت به ماڵ و سامان ودیاری کۆمه&zwnj;کیم بکه&zwnj;ن؟! من دڵنیام که ئه&zwnj;و شتانه&zwnj;ی خوا پێی به&zwnj;خشیووم له&zwnj;وه چاکتر و به&zwnj;نرختره که به ئێوه&zwnj;ی به&zwnj;خشیووه&zwnj;، نه&zwnj;خێر من به&zwnj;و دیاریه دڵشاد نابم، به&zwnj;ڵکو ئێوه به دیاریه&zwnj;که&zwnj;تان شاد ده&zwnj;بن.';
$t[37] = '(ئه&zwnj;ی نوێنه&zwnj;ری به&zwnj;ڵقیس) بگه&zwnj;ڕێره&zwnj;وه بۆ لایان به خۆت و دیاریه&zwnj;که&zwnj;ته&zwnj;وه&zwnj;، سوێند به خوا ده&zwnj;بێت به سه&zwnj;ربازانێکه&zwnj;وه بچینه سه&zwnj;ریان که نه&zwnj;یان دیبێت و نه&zwnj;توانن به&zwnj;رگری بکه&zwnj;ن، ده&zwnj;بێت ده&zwnj;ریان بکه&zwnj;ین له وڵاتیان له کاتێکدا ئه&zwnj;وان زه&zwnj;لیل و ڕیسواو شکاون.';
$t[38] = '(سوله&zwnj;یمان زانی هه&zwnj;ڕه&zwnj;شه&zwnj;که&zwnj;ی کاریگه&zwnj;ر ده&zwnj;بێت بۆیه ڕوو به&zwnj;ئه&zwnj;ندامانی حکومه&zwnj;ته&zwnj;که&zwnj;ی) وتی: ئه&zwnj;ی خه&zwnj;ڵکینه کامتان ده&zwnj;توانێت ته&zwnj;ختی شاژنه به&zwnj;ڵقیسم بۆ بهێنێت پێش ئه&zwnj;وه&zwnj;ی بێن بۆ لام و ته&zwnj;سلیم ببن.';
$t[39] = 'دێوێک له په&zwnj;ریه&zwnj;کان وتی: من بۆت ده&zwnj;هێنم پێش ئه&zwnj;وه&zwnj;ی ده&zwnj;وام ته&zwnj;واو ببێت و له جێی خۆت هه&zwnj;ستیت، به&zwnj;ڕاستی من بۆ ئه&zwnj;و کاره خۆم به به&zwnj;هێزو ئه&zwnj;مین ده&zwnj;زانم.';
$t[40] = '(به&zwnj;ڵام یه&zwnj;کێک له پیاو چاکان) که زانستی و زانیاری له (نهێنیه&zwnj;کانی) کتێبی ته&zwnj;ورات هه&zwnj;بوو وتی: من بۆت ده&zwnj;هێنم به چاو تروکاندنێک (ده&zwnj;ستی کرد به نزاو پاڕانه&zwnj;وه و داوای له خوا کرد)، ئه&zwnj;وسا کتو پڕ سوله&zwnj;یمان بینی ته&zwnj;ختی پاشایه&zwnj;تی له&zwnj;لایدا دانراوه وتی: ئه&zwnj;مه فه&zwnj;زڵ و به&zwnj;خششه&zwnj;کانی په&zwnj;روه&zwnj;ردگارمه بۆ ئه&zwnj;وه&zwnj;ی تاقیم بکاته&zwnj;وه&zwnj;.. که&zwnj;: ئایا سوپاسگوزار ده&zwnj;بم یان ناشکورو به&zwnj;دنمه&zwnj;ک، جا ئه&zwnj;وه&zwnj;ی سوپاسگوزار بێت ئه&zwnj;وه به&zwnj;ڕاستی سوپاسگوزاره بۆ خۆی، ئه&zwnj;وه&zwnj;ش که سپڵه&zwnj;یه&zwnj;، ئه&zwnj;وه بێگومان په&zwnj;روه&zwnj;ردگارم ده&zwnj;وڵه&zwnj;مه&zwnj;ند و بێ نیازه و خاوه&zwnj;نی به&zwnj;خششه به هه&zwnj;مووان (ڕایه&zwnj;کیش هه&zwnj;یه له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی که ئه&zwnj;و که&zwnj;سه سوله&zwnj;یمان خۆی بووه&zwnj;).';
$t[41] = '(ئینجا سوله&zwnj;یمان فه&zwnj;رمانیدا و) وتی: ته&zwnj;ختی پاشایه&zwnj;تیه&zwnj;که&zwnj;ی ده&zwnj;ستکاری بکه&zwnj;ن (هه&zwnj;ندێک ڕه&zwnj;نگ و نه&zwnj;خشی بگۆڕن) سه&zwnj;رنج ده&zwnj;ده&zwnj;ین بزانین به&zwnj;ڵقیس ده&zwnj;یناسێته&zwnj;وه&zwnj;، یان له&zwnj;و که&zwnj;سانه ده&zwnj;بێت که نایناسنه&zwnj;وه&zwnj;.';
$t[42] = 'جا کاتێک به&zwnj;ڵقیس گه&zwnj;یشت، پێی وترا: ته&zwnj;ختی پاشایه&zwnj;تی تۆ ئاوایه&zwnj;؟! هه&zwnj;ر ده&zwnj;ڵێی ئه&zwnj;وه&zwnj;و له&zwnj;و ده&zwnj;چێت، ئێمه پێش ئه&zwnj;مه&zwnj;ش زانیاریمان هه&zwnj;بوو (ده&zwnj;رباره&zwnj;ی تواناو ده&zwnj;سه&zwnj;ڵاتی سوله&zwnj;یمان) ئێمه ته&zwnj;سلیم بووین (نیازی جه&zwnj;نگمان نیه&zwnj;).';
$t[43] = 'ئه&zwnj;و شتانه&zwnj;ی که به&zwnj;ڵقیس ده&zwnj;یپه&zwnj;رست له جیاتی خوا بووبوونه به&zwnj;ربه&zwnj;ست له دڵ و ده&zwnj;روونیدا له به&zwnj;رامبه&zwnj;ر ئاینی خوادا به&zwnj;ڕاستی ئه&zwnj;و کاتی خۆی له ڕیزی قه&zwnj;ومێکی بێ باوه&zwnj;ڕ و خوا نه&zwnj;ناساندا بوو.';
$t[44] = 'ئه&zwnj;وسا پێی وترا بچۆره کۆشکه&zwnj;که&zwnj;وه&zwnj;، (کاتێک به&zwnj;ره&zwnj;و کۆشک که&zwnj;وته ڕێ، ئاوێک له&zwnj;به&zwnj;رده&zwnj;م کۆشکه&zwnj;که&zwnj;دا بوو، به شووشه سه&zwnj;ری گیرا بوو) جا که به&zwnj;ڵقیس بینی وای زانی ئاوێکی زۆره و قاچی خۆی هه&zwnj;ڵکرد تا ته&zwnj;ڕ نه&zwnj;بێت، سوله&zwnj;یمان وتی: ئه&zwnj;و پانتاییه به شووشه سه&zwnj;ری گیراوه (به&zwnj;ڵقیس سه&zwnj;رسام بوو له&zwnj;ده&zwnj;سه&zwnj;ڵات و کۆشک و ته&zwnj;لارو نازو نیعمه&zwnj;تی مادی و مه&zwnj;عنه&zwnj;وی که خوای گه&zwnj;وره به سوله&zwnj;یمانی به&zwnj;خشیووه بۆیه&zwnj;) وتی: په&zwnj;روه&zwnj;ردگارا من به&zwnj;ڕاستی تا ئێستا سته&zwnj;مم له خۆم کردووه&zwnj;، ئیتر وا له&zwnj;گه&zwnj;ڵ سوله&zwnj;یماندا ته&zwnj;سلیمی خوای په&zwnj;روه&zwnj;ردگاری جیهانیان بووم.';
$t[45] = 'سوێند به خوا بۆ هۆزی (ثمود) یش صاڵحی برایانمان نارد، فه&zwnj;رمانمان پێدا که پێیان بڵێ: ته&zwnj;نها خوا بپه&zwnj;رستن، که&zwnj;چی ده&zwnj;ستبه&zwnj;جێ خه&zwnj;ڵکه&zwnj;که بوون به دوو ده&zwnj;سته&zwnj;وه و کێشه و ناخۆشی له نێوانیاندا به&zwnj;رپا بوو.';
$t[46] = 'صاڵح پێی وتن: ئه&zwnj;ی قه&zwnj;وم و که&zwnj;سم بۆچی ئێوه په&zwnj;له&zwnj;ی خراپه&zwnj;تانه پێش چاکه و حه&zwnj;ز ده&zwnj;که&zwnj;ن به&zwnj;ڵاو ناخۆشیتان به&zwnj;سه&zwnj;ر بێت، ئه&zwnj;ی بۆچی داوای لێخۆشبوون له خوا ناکه&zwnj;ن بۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;حمتان پێ بکرێت (خوای گه&zwnj;وره ده&zwnj;رووی زیاترتان لێ بکاته&zwnj;وه&zwnj;).';
$t[47] = 'خه&zwnj;ڵکه&zwnj;که وتیان: ئێمه تووشی شووم و نه&zwnj;گبه&zwnj;تی بووین به هۆی تۆو ئه&zwnj;وانه&zwnj;ی له&zwnj;گه&zwnj;ڵ تۆدان، صاڵح وتی: نه&zwnj;خێر وا نی&zwnj;یه، نه&zwnj;گبه&zwnj;تی و شوومتان له&zwnj;لایه&zwnj;ن خواوه بۆ دێت (ئه&zwnj;گه&zwnj;ر به&zwnj;رده&zwnj;وام بن له&zwnj;سه&zwnj;ر یاخی بوونتان)، به&zwnj;ڵکو ئێوه قه&zwnj;ومێکن که تاقی ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[48] = 'له&zwnj;و شاره&zwnj;دا نۆ ده&zwnj;سته هه&zwnj;بوو که گوناهو تاوانیان ئه&zwnj;نجام ده&zwnj;داو تۆوی خراپه&zwnj;یان له وڵاتدا ده&zwnj;چاندو به&zwnj;لای چاکه&zwnj;و چاکه&zwnj;کاریدا نه&zwnj;ده&zwnj;چاوون.';
$t[49] = 'هه&zwnj;موویان سوێندیان به خوا بۆ یه&zwnj;کتر خوارد که له شه&zwnj;ودا بده&zwnj;ن به&zwnj;سه&zwnj;ر صاڵح و ماڵ و منداڵیدا و له&zwnj;ناویان به&zwnj;رن، پاشان (ئه&zwnj;گه&zwnj;ر گومانیان لێکردین) سوێند ده&zwnj;خۆین و به که&zwnj;س و کاری ده&zwnj;ڵێین: ئێمه ئاگامان له تیاچوونی ئه&zwnj;وان نیه و دڵنیا بن ئێمه ڕاستگۆشین.';
$t[50] = '(ئه&zwnj;و تاوانبارانه&zwnj;) پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;یان گێڕا (بۆ له&zwnj;ناو بردنی صاڵح و شوێنکه&zwnj;وتوانی) ئێمه&zwnj;ش نه&zwnj;خشه&zwnj;یه&zwnj;کی ترمان کێشا (بۆ ڕزگار کردنی ئیمانداران و له&zwnj;ناوبردانی خوا نه&zwnj;ناسان) ئه&zwnj;وان ئاگایان لێ نه&zwnj;بوو، هه&zwnj;ستیان پێ نه&zwnj;ده&zwnj;کرد.';
$t[51] = 'جا ئیتر سه&zwnj;رنج بده و ته&zwnj;ماشاکه&zwnj;، سه&zwnj;ره&zwnj;نجامی پیلانه&zwnj;که&zwnj;یان چیان به&zwnj;سه&zwnj;رهات، به&zwnj;ڕاستی ئێمه ئه&zwnj;وانیش و هه&zwnj;موو قه&zwnj;ومه&zwnj;که&zwnj;یانمان له ناوبرد (به&zwnj;هۆی بومه&zwnj;له&zwnj;رزه&zwnj;وه&zwnj;).';
$t[52] = 'ئه&zwnj;وه&zwnj;ته ماڵه&zwnj;کانیان کاول و چۆڵه به هۆی ئه&zwnj;و سته&zwnj;مه&zwnj;ی که کردیان، به&zwnj;ڕاستی ئا له&zwnj;و به&zwnj;سه&zwnj;رهاته&zwnj;دا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆ که&zwnj;سانێک که له&zwnj;شت تێبگه&zwnj;ن و بزانن.';
$t[53] = 'ئه&zwnj;وسا ئێمه ئه&zwnj;و که&zwnj;سا&zwnj;نه&zwnj;مان ڕزگار کرد که باوه&zwnj;ڕیان هێنابوو و ئه&zwnj;وانه پارێزکار بوون و له خوا ده&zwnj;ترسان.';
$t[54] = '(لوط پێغه&zwnj;مبه&zwnj;ریش) کاتی خۆی به قه&zwnj;ومه&zwnj;که&zwnj;ی وت: باشه ئێوه ئه&zwnj;وکاره نابه&zwnj;جێ وتاوان و گوناهه ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن؟!، له&zwnj;کاتێکدا ئێوه چاوتان هه&zwnj;یه و ده&zwnj;زانن (چۆن شه&zwnj;رمه&zwnj;زار و خه&zwnj;جاڵه&zwnj;ت و ڕیسوا نابن).';
$t[55] = 'ئایا ڕه&zwnj;وایه که ئاره&zwnj;زووی خۆتان له&zwnj;گه&zwnj;ڵ پیاواندا ئه&zwnj;نجام بده&zwnj;ن له جیاتی ژنان؟! نه&zwnj;خێر ئێوه ژیر نین، به&zwnj;ڵکو ئێوه گه&zwnj;لێکن سه&zwnj;ره&zwnj;نجامی ئه&zwnj;م ئیشه خراپه نازانن.';
$t[56] = 'که&zwnj;چی وه&zwnj;ڵامی قه&zwnj;ومه&zwnj;که&zwnj;ی هیچ نه&zwnj;بوو ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت که وتیان: (لوط) و شوێنکه&zwnj;وتووانی له شاره&zwnj;که&zwnj;تان ده&zwnj;ربکه&zwnj;ن، چونکه به&zwnj;ڕاستی ئه&zwnj;وانه که&zwnj;سانێکن خۆیان به پاک ڕاده&zwnj;گرن!!';
$t[57] = 'سه&zwnj;ره&zwnj;نجام ئێمه (لوط&lrm;) و خاوو خێزانیمان ڕزگارکرد جگه له ژنه&zwnj;که&zwnj;ی که بڕیارمان دابوو له تیاچووان بێت (چونکه هه&zwnj;ڵوێستی چاک نه&zwnj;بوو، باوه&zwnj;ری به مێردی پێغه&zwnj;مبه&zwnj;ری نه&zwnj;بوو).';
$t[58] = '(له به&zwnj;ره&zwnj;به&zwnj;یاندا) به&zwnj;ردبارانمان کردن، ئای که به&zwnj;ردبارانی بێدارکراوه&zwnj;کان سه&zwnj;خت و دژواره&zwnj;!';
$t[59] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ئه&zwnj;ی ئیماندار) بڵێ: سوپاس و ستایش بۆ خوای میهره&zwnj;بان، سڵاو له&zwnj;سه&zwnj;ر ئه&zwnj;و به&zwnj;ندانه&zwnj;ش که هه&zwnj;ڵی بژاردووه&zwnj;، ئایا ئه&zwnj;و خوا میهره&zwnj;بانه چاکتره (&lrm;که بپه&zwnj;رسترێت و په&zwnj;یڕه&zwnj;وی به&zwnj;رنامه&zwnj;که&zwnj;ی بکرێت) یان ئه&zwnj;وانه&zwnj;ی ئه&zwnj;وان ده&zwnj;یکه&zwnj;نه شه&zwnj;ریک و هاوه&zwnj;ڵ بۆی؟';
$t[60] = '(ئایا شه&zwnj;ریک و هاوه&zwnj;ڵ چاکتره بپه&zwnj;رسترێت) یان ئه&zwnj;و زاته&zwnj;ی که ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه و له ئاسمانه&zwnj;وه بارانی باراندووه&zwnj;، جا باخ و بێستانی جوان و ڕازاوه&zwnj;مان پێ به&zwnj;رهه&zwnj;م هێناوه&zwnj;، ئێوه نه&zwnj;تانده&zwnj;توانی دره&zwnj;خته&zwnj;کانی بڕوێنن، چ جای به&zwnj;رو بوومه&zwnj;که&zwnj;ی، ئایا ڕه&zwnj;وایه&zwnj;، که خوایه&zwnj;کی تر له&zwnj;گه&zwnj;ڵ خوای په&zwnj;روه&zwnj;ردگاردا هه&zwnj;بێت؟! نه&zwnj;خێر، له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه&zwnj;دا زۆربه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه له ڕێبازی ڕاست لاده&zwnj;ده&zwnj;ن و شوێنی چه&zwnj;وتی و ناردوستی ده&zwnj;که&zwnj;ون.';
$t[61] = '(ئایا ته&zwnj;نها ئه&zwnj;و زاته نیه&zwnj;) که زه&zwnj;وی دامه&zwnj;زراندووه و له ناویدا جۆگه و ڕووباری ڕه&zwnj;خساندووه&zwnj;، هه&zwnj;روه&zwnj;ها زنجیره چیای تیادا چه&zwnj;سپاندووه&zwnj;، له نێوان دوو ده&zwnj;ریاکه&zwnj;شدا به&zwnj;ربه&zwnj;ستی به&zwnj;رپاکردووه (که ئاوی شیرین و سوێر تێکه&zwnj;ڵ نابن) ئایا ڕه&zwnj;وایه خوایه&zwnj;کی تر له&zwnj;گه&zwnj;ڵ خوا په&zwnj;روه&zwnj;ردگاردا هه&zwnj;بێت؟! نه&zwnj;خێر وا نییه&zwnj;، له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو به&zwnj;ڵگانه&zwnj;دا زۆربه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه (ئه&zwnj;م ڕاستیانه&zwnj;) نازانن و تێناگه&zwnj;ن.';
$t[62] = 'ئایا کێیه جگه له زاتی په&zwnj;روه&zwnj;ردگار که بێت به هانا و هاواری لێقه&zwnj;وماو و بێ ده&zwnj;ره&zwnj;تان و بێ داڵده&zwnj;وه&zwnj;، کاتێک نزا ده&zwnj;کات و لێی ده&zwnj;پارێته&zwnj;وه&zwnj;، ئه&zwnj;وسا به&zwnj;ڵاو ناخۆشیه&zwnj;کان لاده&zwnj;بات و ده&zwnj;تانکاته نیشته&zwnj;جێ و جێنشین له زه&zwnj;ویدا، ئایا ڕه&zwnj;وایه له&zwnj;گه&zwnj;ڵ ئه&zwnj;و زاته&zwnj;دا خوای تر هه&zwnj;بێت؟! که&zwnj;مێک یاداوه&zwnj;ری وه&zwnj;ربگرن و تێفکرن و بیربکه&zwnj;نه&zwnj;وه&zwnj;.';
$t[63] = 'ئایا کێ له تاریکستانی سه&zwnj;ر وشکانی و ده&zwnj;ریا ڕێنمووییتان ده&zwnj;کات تا ون نه&zwnj;بن؟! کێ بای شه&zwnj;ماڵ (هه&zwnj;ڵگری هه&zwnj;ڵمی ئاو) ده&zwnj;نێرێت وه&zwnj;ک مژده&zwnj;یه&zwnj;ک پێش گه&zwnj;یشتنی بارانی ڕه&zwnj;حمه&zwnj;ت؟، ئایا ڕه&zwnj;وایه له&zwnj;گه&zwnj;ڵ خوادا خوای تر هه&zwnj;بێت، به&zwnj;رزی و بڵندی و مه&zwnj;زنی بۆ خوای گه&zwnj;وره له&zwnj;به&zwnj;رامبه&zwnj;ر ئه&zwnj;و شتانه&zwnj;وه که نه&zwnj;فامان ده&zwnj;یکه&zwnj;نه هاوه&zwnj;ڵ و شه&zwnj;ریک بۆ ئه&zwnj;و زاته&zwnj;.';
$t[64] = 'ئایا کێ له سه&zwnj;رتاوه دروستکراوان به&zwnj;دی ده&zwnj;هێنێت، پاشان دوای تێکدانی سه&zwnj;رله&zwnj;نوێ دروستی ده&zwnj;کاته&zwnj;وه&zwnj;، کێ له&zwnj;و ئاسمانه&zwnj;وه (به&zwnj;هۆی تیشکی خۆر و باران و هه&zwnj;واوه&zwnj;) له زه&zwnj;وییه&zwnj;وه (به هۆی خاکه&zwnj;وه&zwnj;) ڕزق و ڕۆزیتان پی ده&zwnj;به&zwnj;خشێت؟، ئایا ڕه&zwnj;وایه له&zwnj;گه&zwnj;ڵ زاتی خوادا خوای تر هه&zwnj;بێت؟، پێیان بڵێ: ئاده&zwnj;ی به&zwnj;ڵگه&zwnj;تان چییه بیهێننه مه&zwnj;یدان ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن.';
$t[65] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ری (صلی الله علیه وسلم) پێیان) بڵێ: جگه له خوا هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هیچیان ئاگادارینین له په&zwnj;نهان و شاراوه&zwnj;کان، هه&zwnj;ستیش ناکه&zwnj;ن که&zwnj;ی زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;...';
$t[66] = 'نه&zwnj;خێر، ئه&zwnj;وانه زانیاری و زانستیان ده&zwnj;رباره&zwnj;ی قیامه&zwnj;ت کۆتا و که&zwnj;م و کورته&zwnj;، به&zwnj;ڵکو ئه&zwnj;وان له گوماندان ده&zwnj;رباره&zwnj;ی، به&zwnj;ڵکو هه&zwnj;ر کوێرن له ئاستیدا و دڵیان نابینایه&zwnj;.';
$t[67] = 'ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕن بوون ده&zwnj;یانوت: باشه ئه&zwnj;گه&zwnj;ر ئێمه بوونیه خاک له&zwnj;گه&zwnj;ڵ باوو و باپیرانماندا، ئایا ئێمه (بۆ ژیانێکی نوێ) ده&zwnj;ده&zwnj;هێنرێینه&zwnj;وه&zwnj;؟!';
$t[68] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه پێشتر له&zwnj;م به&zwnj;ڵێنانه&zwnj;مان پێدراوه له&zwnj;گه&zwnj;ڵ باوو باپیرانماندا ئه&zwnj;مه هیچ شتێک نییه&zwnj;، ته&zwnj;نها داستان و قسه&zwnj;ی پێشینه&zwnj;کانه&zwnj;!!';
$t[69] = 'پێیان بڵێ: ده بگه&zwnj;ڕێن به&zwnj;سه&zwnj;ر زه&zwnj;ویدا و سه&zwnj;رنج بده&zwnj;ن و بزانن چؤن بوو سه&zwnj;رئه&zwnj;نجامی تاوانبار و تاوانکاران.';
$t[70] = 'تۆش خه&zwnj;فه&zwnj;ت مه&zwnj;خۆ بۆیان و غه&zwnj;مگین مه&zwnj;به له&zwnj;و پیلان و ته&zwnj;ڵه&zwnj;که و فێڵانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[71] = 'خوانه&zwnj;ناسان ده&zwnj;ڵێن: که&zwnj;ی ئه&zwnj;م به&zwnj;ڵێنه پێش دێت ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن؟';
$t[72] = 'پێیان بڵێ: دوور نیه به&zwnj;شێک له&zwnj;و سازایه&zwnj;ی که ئێوه په&zwnj;له&zwnj;ی لێ ده&zwnj;که&zwnj;ن، به&zwnj;م زووانه بۆتان پێش بێت.';
$t[73] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) به ڕاستی په&zwnj;روه&zwnj;ردگاری تۆ خاوه&zwnj;نی فه&zwnj;زڵ و به&zwnj;خشنده&zwnj;ییه به&zwnj;سه&zwnj;ر خه&zwnj;ڵکیه&zwnj;وه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;یان سپڵه&zwnj;ن و سوپاسی خوا ناکه&zwnj;ن.';
$t[74] = 'بێگومان په&zwnj;روه&zwnj;ردگارت چاک ده&zwnj;زانێت چی له سینه&zwnj;یاندا حه&zwnj;شارده&zwnj;ده&zwnj;ن و چیش ئاشکرا ده&zwnj;که&zwnj;ن.';
$t[75] = 'هیچ شتێک له خوا په&zwnj;نهان نیه له ئاسماندا بێت یان له زه&zwnj;ویدا، له دۆسیه&zwnj;ی ئاشکرادا تۆمار نه&zwnj;کرابێت.';
$t[76] = 'به&zwnj;ڕاستی ئه&zwnj;م قورئانه زۆربه&zwnj;ی زۆری ئه&zwnj;و شتانه&zwnj;ی که نه&zwnj;وه&zwnj;ی ئیسرائیل جیاوازی و کێشه&zwnj;یان له&zwnj;سه&zwnj;ری هه&zwnj;یه باسی ده&zwnj;کات و لێی ده&zwnj;دوێت و ڕوونی ده&zwnj;کاته&zwnj;وه&zwnj;.';
$t[77] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڕاستی ئه&zwnj;م قورئانه هۆی هیدایه&zwnj;ت و ڕێنموویی و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیه بۆ ئیمانداران.';
$t[78] = 'به&zwnj;&lrm;ڕاستی په&zwnj;روه&zwnj;ردگارت داوه&zwnj;ریی ده&zwnj;کات له زاتێکی باڵاده&zwnj;ست و زانایه&zwnj;.';
$t[79] = '(ئه&zwnj;ی محمد &lrm; (صلی الله علیه وسلم)، ئه&zwnj;ی ئیماندار) که&zwnj;واته تۆ پشت به&zwnj;خوا ببه&zwnj;سته&zwnj;، به&zwnj;ڕاستی تۆ له&zwnj;سه&zwnj;ر حه&zwnj;ق و ڕاستیه&zwnj;کی ئاشكرایت.';
$t[80] = 'بێگومان تۆ ناتوانیت ئه&zwnj;م بانگه&zwnj;وازه بخه&zwnj;یته ناو دڵی دڵ مردوو و که&zwnj;ڕه&zwnj;واڵه&zwnj;کانه&zwnj;وه&zwnj;، به&zwnj;تایبه&zwnj;ت کاتێک ڕوو وه&zwnj;رده&zwnj;گێڕن و پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن (بڕیاری داخستنی دڵ و گوێیان ده&zwnj;ده&zwnj;ن).';
$t[81] = 'هه&zwnj;وره&zwnj;ها تۆ ناتوانیت هیدایه&zwnj;ت و ڕێنموویی کوێر و نابیناکان بکه&zwnj;یت و له تاریکی ڕزگاریان بکه&zwnj;یت &lrm; (ئه&zwnj;وانه&zwnj;ی دڵیان کوێره&zwnj;و سه&zwnj;رنجی ڕاستیه&zwnj;کان ناده&zwnj;ن)، تۆ ته&zwnj;نها ده&zwnj;توانیت (ئه&zwnj;م ڕاستیانه&zwnj;) بده&zwnj;یت به گوێی ئه&zwnj;و که&zwnj;سانه&zwnj;دا که باوه&zwnj;ڕیان به نایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;ده&zwnj;هێنن (له قورئان و بوونه&zwnj;وه&zwnj;ردا) چونکه ئه&zwnj;وانه هه&zwnj;میشه ملکه&zwnj;چی فه&zwnj;رمانه&zwnj;کانی خوای گه&zwnj;وره&zwnj;ن.';
$t[82] = 'کاتێک بڕیاری به&zwnj;رپابوونی (قیامه&zwnj;ت درا، یاخود نزیک بۆوه&zwnj;)، زینده&zwnj;وه&zwnj;رێک له زه&zwnj;وی ده&zwnj;رده&zwnj;هێنین بۆیان که قسه&zwnj;یان بۆ ده&zwnj;کات که&zwnj;: به&zwnj;ڕاستی زۆربه&zwnj;ی خه&zwnj;ڵکی باوه&zwnj;ڕیان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه نه&zwnj;بووه&zwnj;.';
$t[83] = 'ڕۆژێک دێت له هه&zwnj;ر ئوممه&zwnj;تێک ده&zwnj;سته&zwnj;یه&zwnj;ک کێش ده&zwnj;که&zwnj;ین و کۆیان ده&zwnj;که&zwnj;ینه&zwnj;وه&zwnj;، له&zwnj;وانه&zwnj;ی که ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ ده&zwnj;زانن، ئینجا به&zwnj;رودوا هه&zwnj;موویان کۆده&zwnj;کردێنه&zwnj;وه&zwnj;.';
$t[84] = 'هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی دێنه پێشه&zwnj;وه بۆ لێپرسینه&zwnj;وه&zwnj;، خوا ده&zwnj;فه&zwnj;رموێت: ئایا ئه&zwnj;وه ئێوه بڕواتان به ئایه&zwnj;ته&zwnj;کانی من نه&zwnj;هێنا له کاتێکدا ئێوه هێچ زانستی و زانیاریه&zwnj;کتان ده&zwnj;رباره&zwnj;ی نه&zwnj;بوو؟ یاخود ئێوه خه&zwnj;ریکی چی بوون؟ چیتان ده&zwnj;کرد به ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;؟';
$t[85] = 'سه&zwnj;رئه&zwnj;نجام بڕیارو فه&zwnj;رمانی خوا درچوو دژیان به&zwnj;هۆی ئه&zwnj;وسته&zwnj;مه&zwnj;ی کردیان، جا قسه&zwnj;یان بۆ ناکرێت و ورته&zwnj;یان لێنایه&zwnj;ت.';
$t[86] = 'ئایا ئه&zwnj;و خه&zwnj;ڵکه نه&zwnj;یانبینیوه که به&zwnj;ڕاستی ئێمه شه&zwnj;ومان تاریک کردووه&zwnj;، تا تیایدا بحه&zwnj;سێنه&zwnj;وه&zwnj;، ڕۆژیشمان ڕووناک کردۆته&zwnj;وه&zwnj;، تا هه&zwnj;موو شت ببینن، به&zwnj;ڕاستی ئا له&zwnj;و دیاردانه&zwnj;دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ که&zwnj;سانێک ئیمان و باوه&zwnj;ر بهێنن.';
$t[87] = 'یادی ڕۆژێک بکه فوو ده&zwnj;کرێت به _صور_ دا، هه&zwnj;رکه&zwnj;س له ئاسمانه&zwnj;کان و هه&zwnj;رکه&zwnj;س له زه&zwnj;ویدا هه&zwnj;یه&zwnj;، ترس و بیمێکی زۆر دایده&zwnj;گرێت، جگه له&zwnj;وانه&zwnj;ی که ویستی خوا وایه ترس و بیمیان، هه&zwnj;مووانیش دێن به&zwnj;ملکه&zwnj;چێ و ناچاریی.';
$t[88] = 'تۆ کێوه&zwnj;کان ده&zwnj;بینیت وا ده&zwnj;زانیت که وه&zwnj;ستاون و بێ جووڵه&zwnj;ن، له کاتێكدا که ئه&zwnj;وانیش وه&zwnj;کو هه&zwnj;ور له جووڵه&zwnj;دان (چونکه زه&zwnj;وی به&zwnj;خێرایی هه&zwnj;زار کێلۆمه&zwnj;تر له سه&zwnj;عارتێكدا به ده&zwnj;وری خۆیدا ده&zwnj;سوڕێته&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها له&zwnj;سه&zwnj;ره&zwnj;تای به&zwnj;رپابوونی قیامه&zwnj;تدا کێوه&zwnj;کان وه&zwnj;کو خوری شی کراوه ده&zwnj;برێن به ئاسماندا و چاڵ و چۆڵی زه&zwnj;ویان پێ پڕده&zwnj;کرێته&zwnj;وه&zwnj;)، ئه&zwnj;مه&zwnj;یه ده&zwnj;ستکردی ئه&zwnj;وخوایه&zwnj;ی که هه&zwnj;موو شتێکی به&zwnj;وپه&zwnj;ری ڕێکوپێکی دروستکردووه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;وزاته زۆر ئاگایه به&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[89] = 'که&zwnj;سێک به کارو کرده&zwnj;وه&zwnj;ی چاکه&zwnj;وه بێت، ئه&zwnj;وه پاداشتی چاکتر له&zwnj;وه&zwnj;ی خۆی پێشکه&zwnj;ش ده&zwnj;کرێت و ئه&zwnj;وانه له&zwnj;و ڕۆژه&zwnj;دا له ترس و بیم پارێزراون و ئاسووده و ئارامن.';
$t[90] = 'ئه&zwnj;وه&zwnj;ش به تاوان و گوناهو خراپه&zwnj;وه دێت، به ڕوودا و سه&zwnj;ره&zwnj;و خوار ده&zwnj;خرێته ناو ئاگری دۆزه&zwnj;خه&zwnj;وه و (پێیان ده&zwnj;وترێت): ئایا پاداشتان ده&zwnj;درێته&zwnj;وه جگه له&zwnj;وه&zwnj;ی ده&zwnj;تانکرد؟!';
$t[91] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ): به&zwnj;ڕاستی من ته&zwnj;نها فه&zwnj;رمانی ئه&zwnj;وه&zwnj;م پێدراوه خوای ئه&zwnj;م شاره&zwnj;و ئه&zwnj;م شوینه بپه&zwnj;رستم که حورمه&zwnj;تی بۆ داناوه&zwnj;و هه&zwnj;موو شت ئه&zwnj;و خاوه&zwnj;نیه&zwnj;تی، فه&zwnj;رمانیشم پێدراوه له فه&zwnj;رمانبه&zwnj;ردارو موسڵمانه&zwnj;کان بم.';
$t[92] = '(فه&zwnj;رمانیشـم) پێدراوه که&zwnj;): قورئان ده&zwnj;ور بکه&zwnj;مه&zwnj;وه و به&zwnj;رده&zwnj;وام بیخوێنمه&zwnj;وه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ڕێبازی هیدایه&zwnj;ت وه&zwnj;ربگرێت، ئه&zwnj;وه به&zwnj;ڕاستی قازانجی وه&zwnj;رگرتنی ڕێنمووماییه&zwnj;که&zwnj;ی هه&zwnj;ر بۆ خۆیه&zwnj;تی (له دنیاو قیامه&zwnj;تدا سوودی لێوه&zwnj;رده&zwnj;گرێت)، ئه&zwnj;وه&zwnj;ش که گومڕا ده&zwnj;بێت، تۆ پێی بڵێ: (من زۆرم له سه&zwnj;رتان نیه&zwnj;) بێگومان من ته&zwnj;نها یه&zwnj;کێکم له بێدار که&zwnj;ره&zwnj;وه&zwnj;کان.';
$t[93] = 'بڵێ (الحمد لله&zwnj;) سوپاس و ستایش بۆ خوا، له ئاینده&zwnj;یه&zwnj;کی نزیکدا هه&zwnj;موو به&zwnj;ڵگه و نیشانه&zwnj;کانی خۆیتان نیشان ده&zwnj;دات و ده&zwnj;یانناسنه&zwnj;وه (که هه&zwnj;مووی ده&zwnj;ستکار و به&zwnj;دیهێنراوی خوایه&zwnj;)، بێگومان په&zwnj;روه&zwnj;ردگاری تۆ غافڵ و بێ ئاگا نیه له&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
?>