$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقره&zwnj;) بده&zwnj;.';
$t[2] = 'ئه&zwnj;وه چه&zwnj;ند ئایه&zwnj;تێكه له ئایه&zwnj;ته&zwnj;كانی ئه&zwnj;م قورئانه ڕوون و ئاشكرایه&zwnj;.';
$t[3] = 'ئێمه هه&zwnj;ندێك له هه&zwnj;واڵ و به&zwnj;سه&zwnj;رهاتی گرنگی ڕاست و نه&zwnj;گۆڕی موسا و فیرعه&zwnj;ون ده&zwnj;خوێنینه&zwnj;وه به&zwnj;سه&zwnj;رتدا، بۆ ئه&zwnj;وه&zwnj;ی سوود بگه&zwnj;یه&zwnj;نین به گه&zwnj;لێك كه بڕوا ده&zwnj;هێنن (تا ببێته په&zwnj;ندو ئامۆژگاری).';
$t[4] = 'بێگومان فیرعه&zwnj;ون زاڵ و دڕنده بوو له وڵته&zwnj;كه&zwnj;یدا، خه&zwnj;ڵكه&zwnj;كه&zwnj;ی پارچه پارچه كرد (جیاوازی خسته نێوانیانه&zwnj;وه&zwnj;)، ده&zwnj;سته&zwnj;یه&zwnj;كیانی ده&zwnj;چه&zwnj;وسانده&zwnj;وه و لوازی ده&zwnj;كردن، منداڵه نێرینه&zwnj;كانی سه&zwnj;رده&zwnj;بڕین، كچان و ئافره&zwnj;تانی (به&zwnj;زه&zwnj;لیلی ده&zwnj;هێشته&zwnj;وه&zwnj;)، به&zwnj;ڕاستی ئه&zwnj;و سته&zwnj;مكاره یه&zwnj;كێك بوو له&zwnj;وانه&zwnj;ی تۆوی خراپی ده&zwnj;چاند.';
$t[5] = 'ئێمه&zwnj;ش ده&zwnj;مانه&zwnj;وێت ده&zwnj;روو بكه&zwnj;ینه&zwnj;وه له چه&zwnj;وساوه&zwnj;كان و منه&zwnj;تی خێر بكه&zwnj;ین به&zwnj;سه&zwnj;ر لوازكراوه&zwnj;كاندا له وڵتداو ده&zwnj;مانه&zwnj;وێت بیانكه&zwnj;ینه پێشه&zwnj;واو بیانكه&zwnj;ینه میراتگری سامان و ده&zwnj;ساڵتی سته&zwnj;مكاران.';
$t[6] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;مانه&zwnj;وێت پایه&zwnj;داریان بكه&zwnj;ین و ده&zwnj;سه&zwnj;ڵتیان بچه&zwnj;سپێنین له وڵتداو، ئه&zwnj;وه پێش بهێنین بۆ فیرعه&zwnj;ون و هامان و سه&zwnj;ربازانیان كه&zwnj;لێی ده&zwnj;ترسان (ئه&zwnj;م به&zwnj;ڵێنه خواییه به&zwnj;ویستی په&zwnj;روه&zwnj;ردگاری باڵده&zwnj;ست دووباره ده&zwnj;بێته&zwnj;وه بۆ ئیمانداران له بارودۆخی تایبه&zwnj;تی و گونجاودا).';
$t[7] = 'ئێمه دایكی موسامان ئاگاداركرد، كه شیری بداتێ و (پێمان وت) جا كاتێك مه&zwnj;ترسیت پایدا كرد له&zwnj;وه&zwnj;ی كه پێی بزانرێت فڕێی بده&zwnj;ره ڕووباری (نیله&zwnj;وه&zwnj;) نه بترسه (له&zwnj;وه&zwnj;ی تووشی به&zwnj;ڵ ببێت) نه خه&zwnj;فه&zwnj;تیشی بۆ بخۆ، چونكه ئێمه به&zwnj;ڕاستی ده&zwnj;یگێڕینه&zwnj;وه بۆ ڵت و له&zwnj;ئاینده&zwnj;شدا ده&zwnj;یكه&zwnj;ین به&zwnj;یه&zwnj;كێك له پێغه&zwnj;مبه&zwnj;ره په&zwnj;یامداره&zwnj;كان.';
$t[8] = 'ئه&zwnj;وسا ئیتر دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون هه&zwnj;ڵیان گرته&zwnj;وه&zwnj;، تا سه&zwnj;ره&zwnj;نجام ببهێته دوژمن و خه&zwnj;فه&zwnj;ت بۆیان، به&zwnj;ڕاستی فیرعه&zwnj;ون و هامان و سه&zwnj;ربازه&zwnj;كانیان گوناهكارو تاوانبار و هه&zwnj;ڵه بوون.';
$t[9] = '(كاتێك فیرعه&zwnj;ون موسای ساوای بینی فه&zwnj;رمانی كوشتنی ده&zwnj;ركرد) خێزانه&zwnj;كه&zwnj;ی نه&zwnj;یهێشت و وتی: هیوادارم ببێته مایه&zwnj;ی خۆش&zwnj;حاڵی و چاوروونی بۆ منیش و بۆ تۆش، مه&zwnj;یكوژن، به&zwnj;ڵكو له&zwnj;ئاینده&zwnj;دا سوودمان پێ بگه&zwnj;یه&zwnj;نێت، یان ده&zwnj;یكه&zwnj;ینه كوڕی خۆمان، له كاتێكدا ئه&zwnj;وان هه&zwnj;ر پێی نازانن و هه&zwnj;ستی پێ ناكه&zwnj;ن.';
$t[10] = 'ئه&zwnj;وسا دایكی موسا هه&zwnj;ستی به&zwnj;بێتاقه&zwnj;تی و به بۆشاییه&zwnj;كی زۆر ده&zwnj;كرد له دڵ و ده&zwnj;رونی خۆیدا، به&zwnj;ڕاستی خه&zwnj;ریك بوو ڕازه&zwnj;كه ئاشكرا بكات، ئه&zwnj;گه&zwnj;ر ئێمه دڵیمان دامه&zwnj;زراو نه&zwnj;كردایه تا له&zwnj;و كه&zwnj;سانه بێت كه بڕوایان به به&zwnj;ڵێنی خوا هه&zwnj;یه.';
$t[11] = 'پاشان به&zwnj;خوشكه&zwnj;كه&zwnj;ی موسای وت: بڕۆ به شوێنیدا، (دوای تاوێك) له كه&zwnj;نارێكه&zwnj;وه (موسای به&zwnj;باوه&zwnj;شی ئافره&zwnj;تێكه&zwnj;وه) بینی، ئه&zwnj;وان هه&zwnj;ستیان به&zwnj;وه نه&zwnj;كرد (كه خوشكه&zwnj;كه&zwnj;ی چاودێری ده&zwnj;كات).';
$t[12] = 'ئێمه پێشتر هه&zwnj;رچی ئافره&zwnj;تی شیرده&zwnj;ره له&zwnj;ومان حه&zwnj;رام كرد، خوشكه&zwnj;كه&zwnj;ی (به&zwnj;مه&zwnj;ی زانی حه&zwnj;كیمانه له كۆشك نزیك بۆوه&zwnj;) وتی: ئایا ناونیشانی ماڵێكتان پێ بڵێم كه ئه&zwnj;و مناڵه&zwnj;تان بۆ به&zwnj;خێو بكه&zwnj;ن، دڵسۆزو میهره&zwnj;بانیش بن بۆی.';
$t[13] = 'به&zwnj;و شێوه&zwnj;یه گێرامانه&zwnj;وه بۆ لای دایكی تا دڵنیاو دڵ ئارام بێت و چاوی به&zwnj;دیتنی ئاسوده ببێت ئیتر خه&zwnj;فه&zwnj;ت نه&zwnj;خوات و لای ڕوون بێت كه به&zwnj;ڵینی خوا حه&zwnj;ق و ڕاسته&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی (ئه&zwnj;و خه&zwnj;ڵكه&zwnj;) نازانن و هه&zwnj;ستی په ناكه&zwnj;ن.';
$t[14] = '(دوای چه&zwnj;ند ساڵ) كاتێك ته&zwnj;مه&zwnj;نی گه&zwnj;یشته ئه&zwnj;وپه&zwnj;ری هێزو توانایی، ئێمه&zwnj;ش حیكمه&zwnj;ت و دانایی و زانیاریمان پێبه&zwnj;خشی، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه پاداشتی چاكه&zwnj;كاران ده&zwnj;ده&zwnj;ینه&zwnj;وه.';
$t[15] = '(موسا) له&zwnj;كاتی غه&zwnj;فڵه&zwnj;تی خه&zwnj;ڵكی شاردا خۆی كرد به شه&zwnj;قام و بازاردا، بینی دوو پیاو شه&zwnj;ڕ ده&zwnj;كه&zwnj;ن، ئه&zwnj;مه&zwnj;یان له تایفه&zwnj;ی خۆیه&zwnj;تی و ئه&zwnj;مه&zwnj;شیان له ده&zwnj;سته&zwnj;ی دوژمنه&zwnj;كه&zwnj;یه&zwnj;تی (سه&zwnj;ربازی فیرعه&zwnj;ونه&zwnj;)، ئه&zwnj;وه&zwnj;ی خزمی خۆی بوو موسای بینی هاواری لێكرد كه فریای بكه&zwnj;وێت و زاڵی بكات به سه&zwnj;ر دوژمنه&zwnj;كه&zwnj;یدا، موساش به&zwnj;په&zwnj;له چوو به&zwnj;ده&zwnj;میه&zwnj;وه بۆكزه&zwnj;كی ماڵی به كابرای (قبطی دا)و كوشتی، موسا په&zwnj;شیمان بۆوه له&zwnj;به&zwnj;ر خۆیه&zwnj;وه وتی: ئه&zwnj;م كاره شه&zwnj;یتان پێی كردم (من نه&zwnj;مده&zwnj;ویست بیكوژم) به ڕاستی شه&zwnj;یتان دوژمنێكی گومڕاو ئاشكرایه.';
$t[16] = '(ئینجا ده&zwnj;سته&zwnj;كانی به&zwnj;رز كرده&zwnj;وه و) وتی: په&zwnj;روه&zwnj;ردگاره من به&zwnj;ڕاستی سته&zwnj;مم له خۆم كرد كاتێك له ده&zwnj;ستم ده&zwnj;رچوو ئه&zwnj;و كابرایه&zwnj;م كوشت، لێم ببووره و بمبه&zwnj;خشه&zwnj;، جا خوای میهره&zwnj;بانیش لێی خۆشبوو، بێگومان ئه&zwnj;و زاتێكی لێخۆشبوو میهره&zwnj;بانه.';
$t[17] = '(موسا هه&zwnj;ستی كرد په&zwnj;روه&zwnj;ردگاری نزای گیرا كردوه بۆیه&zwnj;) وتی: په&zwnj;روه&zwnj;ردگارا ماده&zwnj;م ئاوا نازو نیعمه&zwnj;تی لێخۆش بوونت به سه&zwnj;ردا ڕژاندم، من ئیتر هه&zwnj;رگیز نابمه پشتیوان بۆ تاوانكاران.';
$t[18] = 'به&zwnj;هۆی ئه&zwnj;وه&zwnj;و ترس و بیم دایگرت و به&zwnj;شاردا ده&zwnj;سوڕایه&zwnj;وه و چاودێری ده&zwnj;كرد، كوتوپر كابرای كه دوێنێ فریای كه&zwnj;وت، هاواری لێكرد فریام بكه&zwnj;وه! موسا چوو به هاواریه&zwnj;وه&zwnj;و پێی وت: به&zwnj;ڕاستی تۆ كابرایه&zwnj;كی شه&zwnj;ره&zwnj;نگێز و گێچه&zwnj;ڵ گێڕی ئاشكرایت.';
$t[19] = 'كاتێك موسا ویستی هێرش بكاته سه&zwnj;ر ئه&zwnj;وه&zwnj;ی كه دوژمی هه&zwnj;ردووكیانه (واته قبطیه&zwnj;كه&zwnj;، كابرا) وتی: ئه&zwnj;ی موسا ده&zwnj;ته&zwnj;وێت منیش بكوژیت وه&zwnj;ك ئه&zwnj;و كه&zwnj;سه&zwnj;ی دوێنێ كوشتت، دیاره تۆ هه&zwnj;ر ده&zwnj;ته&zwnj;وێت ببیته زۆردار له زه&zwnj;ویدا ناته&zwnj;وێت بچیته ڕێزی ئه&zwnj;و كه&zwnj;سانه&zwnj;وه كه چاكه&zwnj;كارن.';
$t[20] = '(دوای ماوه&zwnj;یه&zwnj;ك) له&zwnj;وپه&zwnj;ڕی شاره&zwnj;وه پیاوێكی (ئیماندار كه له خزمانی فیرعه&zwnj;ون بوو) به په&zwnj;له هات و وتی: ئه&zwnj;ی موسا (فریای خۆت بكه&zwnj;وه&zwnj;) به&zwnj;ڕاستی دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون دانیشتوون و نه&zwnj;خشه ده&zwnj;كێشن تا بتكوژن، تا زووه ده&zwnj;رچۆ و ئێره به&zwnj;جێ بهێڵه&zwnj;، بێگومان من له دڵسۆزانم بۆ تۆ.';
$t[21] = 'موسا له شار ده&zwnj;رچوو به&zwnj;ترس و بیمه&zwnj;وه&zwnj;، هه&zwnj;ر سه&zwnj;یری ئه&zwnj;ملاو ئه&zwnj;ولای خۆی ده&zwnj;كرد، له&zwnj;به&zwnj;ر خۆیه&zwnj;وه ده&zwnj;یوت: په&zwnj;روه&zwnj;ردگارا له ده&zwnj;ست قه&zwnj;ومی سته&zwnj;مكار ڕزگارم بكه&zwnj;.';
$t[22] = 'كاتێك ڕووی كرده شاری مه&zwnj;دیه&zwnj;ن (دووباره ڕووی كرده په&zwnj;روه&zwnj;ردگاری و) وتی: ئومێده&zwnj;وارم كه په&zwnj;روه&zwnj;ردگارم ڕێنموویم بكات بۆ شوێن و جێگه&zwnj;یه&zwnj;كی ڕاست و دروست (دوور له كێشه و ناخۆشی).';
$t[23] = 'كاتێك گه&zwnj;یشته ئاوه&zwnj;كه&zwnj;ی مه&zwnj;دیه&zwnj;ن خه&zwnj;ڵكێكی بینی ماڵاته&zwnj;كانیان ئاوده&zwnj;دا، له&zwnj;ولاشه&zwnj;وه دوو ئافره&zwnj;تی بینی كه به&zwnj;خۆیان و مه&zwnj;ڕه&zwnj;كانیانه&zwnj;وه دووره په&zwnj;رێز وه&zwnj;ستا بوون، بۆیه&zwnj;لێیان چووه پێشه&zwnj;وه و پرسی و وتی: ئه&zwnj;وه ئێوه بۆچی لێره وه&zwnj;ستاون؟ كێشه&zwnj;تان چیه وادووره&zwnj;په&zwnj;رێزن؟ ئافره&zwnj;ته&zwnj;كان وتیان: ئێمه ناتوانین ماڵاته&zwnj;كه&zwnj;مان ئاوبده&zwnj;ین، هه&zwnj;تا شوانه&zwnj;كان نه&zwnj;رۆن و چۆڵی نه&zwnj;كه&zwnj;ن، باوكیشمان پیره&zwnj;مێردێكی به&zwnj;ساڵا چووه&zwnj;.';
$t[24] = 'موسا مه&zwnj;ردانه&zwnj;، شوانه&zwnj;كانی بڕدا و ماڵاته&zwnj;كه&zwnj;یانی ئاودا و به&zwnj;ڕێی كردن، دوایی پشتی تێكردن و چووه ژێر سێبه&zwnj;ره&zwnj;كه و نزای كردو وتی: په&zwnj;روه&zwnj;ردگارا به&zwnj;ڕاستی تۆ خێرو بێری زۆرت به&zwnj;سه&zwnj;ردا ڕژاندووم، به&zwnj;ڵام هێشتا هه&zwnj;ژارم و ئاتاجی به&zwnj;خششی زیاترم.';
$t[25] = 'دوای تاوێك یه&zwnj;كێك له كچه&zwnj;كا&zwnj;ن هات بۆ لای مو&zwnj;سا زۆر به&zwnj;شه&zwnj;رم و حه&zwnj;یاوه ده&zwnj;رۆیشت به ڕێگه&zwnj;دا، كه گه&zwnj;یشته لای پێی وت: باوكم بانگت ده&zwnj;كات و حه&zwnj;ز ده&zwnj;كات پاداشتی ئه&zwnj;و چاكه&zwnj;و ئاودانی ماڵاته كه بۆ ئێمه&zwnj;ت ئه&zwnj;نجامدا، بداته&zwnj;وه&zwnj;...، كاتێك موسا گه&zwnj;یشته لای باوكیان، دوای حه&zwnj;وانه&zwnj;وه هه&zwnj;موو به&zwnj;سه&zwnj;رهاته&zwnj;كه&zwnj;ی خۆی بۆ گێرایه&zwnj;وه باوكی كچه&zwnj;كان وتی: مه&zwnj;ترسه&zwnj;، تۆ ئیتر ڕزگارت بوو له ده&zwnj;ست قه&zwnj;ومی سته&zwnj;مكار.';
$t[26] = 'یه&zwnj;كێك له كچه&zwnj;كان وتی: بابه بۆ به كرێی ناگریت، چونكه به&zwnj;ڕاستی چاكترین كه&zwnj;سێك كه تۆ بیگریت به كرێ، ئه&zwnj;م پیاوه به&zwnj;هێزو ئه&zwnj;مین و ده&zwnj;سپاك و ده&zwnj;روون پاكه&zwnj;یه&zwnj;.';
$t[27] = 'باوكیان پێشنیاره&zwnj;كه&zwnj;ی پێ باش بوو بۆیه وتی: من پێم چاكه بتكه&zwnj;م به كوڕی خۆم، به زاوای خۆم، یه&zwnj;كێك له&zwnj;م دوو كچه&zwnj;ت لێ ماره بكه&zwnj;م به&zwnj;مه&zwnj;رجی ئه&zwnj;وه&zwnj;ی هه&zwnj;شت ساڵ لای من كار بكه&zwnj;یت، خۆ ئه&zwnj;گه&zwnj;ر ده ساڵیشت برده سه&zwnj;ر ئه&zwnj;وه&zwnj;چاكه&zwnj;ی خۆته&zwnj;، منیش نامه&zwnj;وێت بێزارت بكه&zwnj;م و زۆرت بۆ بهێنم، ده&zwnj;مبینیت ئه&zwnj;گه&zwnj;ر خوا بیه&zwnj;وێت له چاكان ده&zwnj;بم؟';
$t[28] = 'موسا پێشنیاره&zwnj;كه&zwnj;ی لا په&zwnj;سه&zwnj;ند بوو بۆیه وتی: ئه&zwnj;م په&zwnj;یمانه له نێوان من و تۆدایه&zwnj;، من پێی ڕازیم، به&zwnj;ڵام هه&zwnj;ركام له&zwnj;و ماوانه&zwnj;م بۆ ته&zwnj;واوكرا با سته&zwnj;مم لێ نه&zwnj;كرێت و ئازادبم، خوای گه&zwnj;وره&zwnj;ش شایه&zwnj;ت و ئاگایه به&zwnj;وه&zwnj;ی كه ده&zwnj;یڵێین و بڕیاری له&zwnj;سه&zwnj;ر ده&zwnj;ده&zwnj;ین.';
$t[29] = 'جا كاتێك موسا ماوه&zwnj;ی دیاریكراوی به&zwnj;سه&zwnj;ر برد و (بڕیاری گه&zwnj;ڕانه&zwnj;وه&zwnj;یدا، له شه&zwnj;وێكی ساردا) له&zwnj;لایه&zwnj;كی كێوی طوردا ئاگرێكی به&zwnj;دی كرد، به&zwnj;خێزانه&zwnj;كه&zwnj;ی وت&zwnj;: ئێوه لێره بن، من به&zwnj;ڕاستی تروسكایی ئاگرێك ده&zwnj;بینم به&zwnj;ڵكو بچم هه&zwnj;واڵێكتان بۆ بهێنمه&zwnj;وه&zwnj;، یان چڵۆسكێك ئاگرتان بۆ بهێنم تا خۆتانی پێ گه&zwnj;رم بكه&zwnj;نه&zwnj;وه&zwnj;.';
$t[30] = 'كاتێك كه موسا له ئاگره&zwnj;كه نزیك بۆوه له كه&zwnj;ناری لای ڕاستی دۆڵه&zwnj;كه&zwnj;وه له شوێنێكی پیرۆزه&zwnj;وه له پاڵ دره&zwnj;خته&zwnj;كه&zwnj;وه بانگی لێكرا ئه&zwnj;ی موسا: (ئه&zwnj;وه&zwnj;ی گفتوگۆت له&zwnj;گه&zwnj;ڵدا ده&zwnj;كا&zwnj;ت) ئه&zwnj;وه به&zwnj;ڕاستی منم (الله&zwnj;) كه په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیانم.';
$t[31] = 'ئینجا فه&zwnj;رمووی: عه&zwnj;ساكه&zwnj;شت فڕێ بده&zwnj;، (كاتێك فرێیدا موسا) بینی زۆر به خێرایی ده&zwnj;جوڵێته&zwnj;وه وه&zwnj;كو تووله مار، هه&zwnj;ڵهات و ئاوڕی نه&zwnj;دایه&zwnj;وه&zwnj;، خوا فه&zwnj;رمووی ئه&zwnj;ی موسا وه&zwnj;ره و مه&zwnj;ترسه به&zwnj;ڕاستی تۆ له&zwnj;و كه&zwnj;سانه&zwnj;یت كه ئه&zwnj;مین ده&zwnj;بیت و له ترس به&zwnj;دووریت.';
$t[32] = 'ئینجا ده&zwnj;ستت بخه&zwnj;ره بن باخه&zwnj;ڵته&zwnj;وه&zwnj;، به&zwnj;شێوه&zwnj;یه&zwnj;كی سپی (وه&zwnj;كو بلوور، ده&zwnj;ستت خۆی ده&zwnj;نوێنێت)بێگه&zwnj;رد و بێ په&zwnj;ڵه و بێ عه&zwnj;یب، هه&zwnj;روه&zwnj;ها بازووه&zwnj;كانت بهێنه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;ك و له&zwnj;سه&zwnj;ر سینه و دڵتی دابنێ، ترس و بیمت نامێنێت، ئه&zwnj;م دوو (معجزه) یه دوو باڵگه&zwnj;ن له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;بۆ سه&zwnj;ر فیرعه&zwnj;ون و دارو ده&zwnj;سته&zwnj;ی، به&zwnj;ڕاستی ئه&zwnj;وانه قه&zwnj;ومێكی یاخی و ناله&zwnj;بارو تاوانبار بوون.';
$t[33] = 'موسا وتی: په&zwnj;روه&zwnj;ردگارا خۆت ده&zwnj;زانیت كه بێگومان من كه&zwnj;سێكم لێ كوشتوون، ده&zwnj;ترسم بمكوژنه&zwnj;وه&zwnj;.';
$t[34] = 'هاروونی براشم زمان پاراوتره له من، ئه&zwnj;ویش له&zwnj;گه&zwnj;ڵ من ڕه&zwnj;وانه بكه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی پشتیوانیم لێ بكات و پشتگیرم بێت و به&zwnj;ڕاستدانه&zwnj;رم بێت، چونكه من به&zwnj;ڕاستی ده&zwnj;ترسم بروام پێنه&zwnj;كه&zwnj;ن.';
$t[35] = 'خوا فه&zwnj;رمووی: ئێمه پشت و بازووی تۆ به&zwnj;هێز ده&zwnj;كه&zwnj;ین به&zwnj;هۆی براكه&zwnj;ته&zwnj;وه و ده&zwnj;سه&zwnj;ڵاتێكتان پێ ده&zwnj;به&zwnj;خشین كه نه&zwnj;توانن بێنه سه&zwnj;رتان، جا به&zwnj;هۆی موعجیزه&zwnj;كانمانه&zwnj;وه هه&zwnj;ردووكتان و هه&zwnj;موو شوێنكه&zwnj;وتوانتان سه&zwnj;ركه&zwnj;وتوو و زاڵن.';
$t[36] = 'كاتێك موسا به موعجیزه ئاشكراكانمانه&zwnj;وه ڕووبه&zwnj;روویان بۆوه&zwnj;، وتیان: ئه&zwnj;مه هه&zwnj;ر جادوویه&zwnj;كی ده&zwnj;ست هه&zwnj;ڵبه&zwnj;سته و شتێكی تر نی یه&zwnj;، ئێمه له باوو باپیرانی دێرینمانه&zwnj;وه هه&zwnj;واڵی یه&zwnj;كتاناسی و پێغه&zwnj;مبه&zwnj;ران و قیامه&zwnj;ت و له&zwnj;و بابه&zwnj;تانه&zwnj;مان نه&zwnj;بیستووه&zwnj;.';
$t[37] = 'موسا وتی: په&zwnj;روه&zwnj;ردگارم خۆی چاك ده&zwnj;زانێت كه كێ له&zwnj;لایه&zwnj;ن ئه&zwnj;وه&zwnj;وه به&zwnj;رنامه&zwnj;ی هیدایه&zwnj;تی هێناوه&zwnj;، سه&zwnj;ره&zwnj;نجامیش به&zwnj;هه&zwnj;شت بۆ كێ ده&zwnj;بێت، به&zwnj;ڕاستی سته&zwnj;مكاران سه&zwnj;رفراز نابن.';
$t[38] = 'فیرعه&zwnj;ون سه&zwnj;ركه&zwnj;شانه وتی: ئه&zwnj;ی خه&zwnj;ڵكینه من كه&zwnj;سی تر شك نابه&zwnj;ن كه شایسته&zwnj;بێت به&zwnj;وه&zwnj;ی خواتان بێت جگه له خۆم، ئه&zwnj;ی هامان تۆ له گڵ خشتی سووره&zwnj;وه كراوم بۆ دروست بكه و قه&zwnj;ڵایه&zwnj;كی به&zwnj;رزو پته&zwnj;وم بۆ بینا بكه باڵكو پیایدا سه&zwnj;ربكه&zwnj;وم تا خوای موسا ببینم هه&zwnj;رچه&zwnj;نده من به&zwnj;ڕاستی بڕوام وایه له ڕیز درۆزنه&zwnj;كاندایه&zwnj;...!!';
$t[39] = 'فیرعه&zwnj;ون و سه&zwnj;ربازان و ده&zwnj;ست و پێوه&zwnj;ندی به ناحه&zwnj;ق له&zwnj;سه&zwnj;ر زه&zwnj;وی (مصر) دا فیزیان كرد و خۆیان به گه&zwnj;وره زانی و گومانیان وابوو كه بۆ لای ئێمه ناهێنرێنه&zwnj;وه&zwnj;.';
$t[40] = 'سه&zwnj;ره&zwnj;نجام ئێمه خۆی و سه&zwnj;ربازان و ده&zwnj;ست و پێوه&zwnj;ندیمان پێچایه&zwnj;وه و فرێمان دانه ناو ده&zwnj;ریاكه&zwnj;وه&zwnj;، سه&zwnj;رنج بده و ته&zwnj;ماشا بكه و بزانه&zwnj;: چۆن بوو سه&zwnj;رئه&zwnj;نجامی سته&zwnj;مكاران.';
$t[41] = 'ئێمه ئه&zwnj;وانمان كرد به&zwnj;چه&zwnj;ند پێشه&zwnj;وایه&zwnj;ك كه بانگی خه&zwnj;ڵكی ده&zwnj;كه&zwnj;ن بۆ ناو ئاگر، له ڕۆژی قیامه&zwnj;تیشدا سه&zwnj;ركه&zwnj;وتوو نابن.';
$t[42] = 'هه&zwnj;ر له&zwnj;م دنیادا نه&zwnj;فرینمان خسته شوێنیان، له ڕۆژی قیامه&zwnj;تیشدا ئه&zwnj;وانه زۆر ئێسك تاڵ و ڕووخسار ناشیرینن.';
$t[43] = 'سوێند به خوا ئێمه ته&zwnj;وراتمان به&zwnj;خشی به موسا، دوای ئه&zwnj;وه&zwnj;ی كه قه&zwnj;ومه پێشووه&zwnj;كانمان له ناوبرد، (چونكه یاخی بوون) تا ببێته هۆی به&zwnj;رچاو ڕوونی و هیدایه&zwnj;ت و ڕێنموویی و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی بۆ خه&zwnj;ڵكی، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن و تێفكرن.';
$t[44] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (ص) تۆ له به&zwnj;شی ڕۆژئاوای كێوی طوردا نه&zwnj;بوویت كاتێك په&zwnj;یامی خۆمان ڕاگه&zwnj;یاند به موساو فه&zwnj;رمانمان پێدا، یه&zwnj;كێكیش نه&zwnj;بوویت له ئاماده&zwnj;بووانی ئه&zwnj;و ڕووداوه&zwnj;.';
$t[45] = 'به&zwnj;ڵام ئێمه چه&zwnj;نده&zwnj;ها نه&zwnj;وه&zwnj;مان هێنایه كایه&zwnj;وه و ته&zwnj;مه&zwnj;نێكی زۆر تێپه&zwnj;ڕی به&zwnj;سه&zwnj;ریاندا، هه&zwnj;روه&zwnj;ها وه&zwnj;نه&zwnj;بێت تۆ له ناو دانیشتووانی شاری مه&zwnj;دیه&zwnj;ندا نیشته&zwnj;جێ بوو بیت و ئایه&zwnj;ته&zwnj;كانی ئێمه به&zwnj;سه&zwnj;ریاندا بخوێنیته&zwnj;وه&zwnj;، به&zwnj;ڵكو هه&zwnj;ر ئێمه&zwnj;ین كه پێغه&zwnj;مبه&zwnj;ران ڕه&zwnj;وانه ده&zwnj;كه&zwnj;ین، (بۆ په&zwnj;ندو ئامۆژگاری بۆ تۆی باس ده&zwnj;كه&zwnj;ین)';
$t[46] = 'هه&zwnj;روه&zwnj;ها له لای كێوی طوریشه&zwnj;وه نه&zwnj;بوویت كاتێك بانگمان له موسا كرد، باڵكو ئه&zwnj;وه ڕه&zwnj;حمه&zwnj;تی تایبه&zwnj;تی بوو له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه كه تۆمان كرد به پێغه&zwnj;مبه&zwnj;ر، تا قه&zwnj;ومێك بێدار بكه&zwnj;یته&zwnj;وه كه پێش تۆ بێداركه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;كیان بۆ ڕه&zwnj;وانه نه&zwnj;كراوه بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری وه&zwnj;ربگرن و تێ بفكرن و له&zwnj;و به&zwnj;سه&zwnj;رهاتانه سوود وه&zwnj;ربگرن.';
$t[47] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;و خاڵكه تووشی به&zwnj;ڵایه&zwnj;ك ببونایه (پێش ڕه&zwnj;وانه&zwnj;كردنی تۆ ئه&zwnj;ی محمد (ص)) به&zwnj;هۆی گوناهو تاوان و ده&zwnj;ست پێشغه&zwnj;ری خراپیانه&zwnj;وه&zwnj;، ئه&zwnj;وه ده&zwnj;یانوت: په&zwnj;روه&zwnj;ردگارا: نه&zwnj;ده&zwnj;بوو پێغه&zwnj;مبه&zwnj;رێكمان بۆ ڕه&zwnj;وانه بكه&zwnj;یت تا شوێنی به&zwnj;رنامه و ئایه&zwnj;ته&zwnj;كانی تۆ بكه&zwnj;وتینایه&zwnj;و له ڕیزی ئیمانداراندا بووینایه&zwnj;؟!';
$t[48] = 'كه&zwnj;چی كاتێك حه&zwnj;ق و ڕاستیان بۆ هات له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، وتیان: نه&zwnj;ده با موعجیزه&zwnj;یه&zwnj;كی پێ بدرایه هه&zwnj;روه&zwnj;كو ئه&zwnj;وه&zwnj;ی كه درابوو به موسا، مه&zwnj;گه&zwnj;ر ئه&zwnj;و جووله&zwnj;كانه كاتی خۆی بێ باوه&zwnj;ڕ نه&zwnj;بوون به&zwnj;وه&zwnj;ی به موسا به&zwnj;خشرابوو؟ وتیان: ئه&zwnj;مانه دوو جادوون په&zwnj;یدا بوون كه پشتگیریی یه&zwnj;كتر ده&zwnj;كه&zwnj;ن و ده&zwnj;یان وت: به&zwnj;ڕاستی ئێمه باوه&zwnj;رمان به هێچ كامیان نیه&zwnj;.';
$t[49] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (ص) پێیان بڵێ: ده&zwnj;باشه ئێوه كتێبێكی تر بهێنن له&zwnj;لایه&zwnj;ن خواوه كه باشترو چاكتر بێت له&zwnj;و دووانه (واته قورئان و ته&zwnj;ورات)، ئه&zwnj;وه منیش شوێنی ده&zwnj;كه&zwnj;وم، ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن.';
$t[50] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ده&zwnj;م بانگه&zwnj;وازی تۆوه نه&zwnj;هاتن (واته نه&zwnj;یانتوانی كتێبی تر بهێنن)، ئه&zwnj;وه چاك بزانه كه ئه&zwnj;وانه هه&zwnj;ر شوێنی ئاره&zwnj;زووی خۆیان ده&zwnj;كه&zwnj;ون، كێیش هه&zwnj;یه له&zwnj;وه گومراتره كه شوێن ئاره&zwnj;زووه&zwnj;كانی خۆی كه&zwnj;وتبێت، دوور له هیدایه&zwnj;ت و ڕێنمویی خوا، بێگومان خوا هیدایه&zwnj;تی قه&zwnj;ومی سته&zwnj;مكار نادات.';
$t[51] = 'سوێند به خوا به&zwnj;ڕاستی ئێمه په&zwnj;یامی خۆمانمان به&zwnj;ش به&zwnj;ش پێگه&zwnj;یاندوون بۆ ئه&zwnj;وه&zwnj;ی په&zwnj;ند و یاداوه&zwnj;ری وه&zwnj;ربگرن.';
$t[52] = 'ئه&zwnj;وانه&zwnj;ی پێشتر كتێبی ئاسمانیمان پێ به&zwnj;خشیوون، ئه&zwnj;وان به&zwnj;و قورئانه باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[53] = 'كاتێكیش قورئان ده&zwnj;خوێنرێته&zwnj;وه به&zwnj;سه&zwnj;ریاندا ده&zwnj;ڵێن: ئێمه باوه&zwnj;ڕمان پێ هێناوه چونكه به&zwnj;ڕاستی ئه&zwnj;مه حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمانه&zwnj;وه پێمان ڕاگه&zwnj;یه&zwnj;نراوه بێگومان ئێمه پێشتر هه&zwnj;رموسڵمان و فه&zwnj;رمانبه&zwnj;ردار بووین.';
$t[54] = 'ئه&zwnj;وانه دووجار پاداشتیان پێده&zwnj;درێت به هۆی خۆگری و ئارام گریانه&zwnj;وه&zwnj;، به چاكه&zwnj;ش به&zwnj;رهه&zwnj;ڵستی خراپه ده&zwnj;كه&zwnj;ن، ئه&zwnj;وانه هه&zwnj;روه&zwnj;ها له&zwnj;و ڕزق و ڕۆزیه&zwnj;ی كه پێمانداون ده&zwnj;به&zwnj;خشن.';
$t[55] = 'ئه&zwnj;وانه كاتێك قسه و گوفتاری ناپه&zwnj;سه&zwnj;ند و نابه&zwnj;جێ ده&zwnj;بیستن پشتی تێ ده&zwnj;كه&zwnj;ن و ده&zwnj;ڵێن به نه&zwnj;فامان: كارو كرده&zwnj;وه&zwnj;ی خۆمان بۆ خۆمان، كارو كرده&zwnj;وه&zwnj;ی ئێوه&zwnj;ش بۆ خۆتان، سڵاوتان لێ بێت ئێمه شه&zwnj;ڕ خواز نین و نامانه&zwnj;وێت هاوڕێیه&zwnj;تی نه&zwnj;فامان بكه&zwnj;ین.';
$t[56] = 'به&zwnj;ڕاستی ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (ص) تۆ ناتوانیت ئیمان و باوه&zwnj;ڕ ببه&zwnj;خشیت به&zwnj;و كه&zwnj;سه&zwnj;ی خۆشت ده&zwnj;وێت، به&zwnj;ڵكو خوا هه&zwnj;ر كه&zwnj;سێكی بوێت ده&zwnj;یخاته سه&zwnj;ر ڕێگه&zwnj;ی ڕاست و ئیمانی پێده&zwnj;به&zwnj;خشێت، خوایش ئاگاداره به&zwnj;وانه&zwnj;ی كه ڕێبازی هیدایه&zwnj;تیان گرتۆته به&zwnj;ر.';
$t[57] = '(قوڕه&zwnj;یش و باوه&zwnj;ڕلاوازان له هه&zwnj;موو سه&zwnj;رده&zwnj;مێكدا) ده&zwnj;یانوت: ئه&zwnj;گه&zwnj;ر ئێمه شوێنی هیدایه&zwnj;ت و به&zwnj;رنامه&zwnj;ی ئیسلام بكه&zwnj;وین له&zwnj;گه&zwnj;ڵ تۆدا له وڵاتی خۆماندا ده&zwnj;فڕێنرێین و تووشی نه&zwnj;هامه&zwnj;تی ده&zwnj;بین، مه&zwnj;گه&zwnj;ر ئێمه شوێنێكی دوور له&zwnj;سته&zwnj;م و پر له ئاسایشمان بۆ فه&zwnj;راهه&zwnj;م نه&zwnj;هێناون كه&zwnj;جۆره&zwnj;ها به&zwnj;روبوومی بۆدێت و له هه&zwnj;موو نازو نیعمه&zwnj;تێك به&zwnj;هره&zwnj;وه&zwnj;ر؟ هه&zwnj;موویشی ڕزق و به&zwnj;خششن له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;یان نازانن و هه&zwnj;ست به&zwnj;ده&zwnj;سه&zwnj;ڵاتی ئێمه ناكه&zwnj;ن.';
$t[58] = 'زۆر شارو شارۆچكه&zwnj;مان له&zwnj;ناوبرد كه دانیشتوانی یاخی و له سنوور ده&zwnj;رچوو بوون له ژیانی دنیایاندا، جا ئه&zwnj;وه&zwnj;ته خانووبه&zwnj;ره و شوێنه&zwnj;كانیان چۆڵه&zwnj;، مه&zwnj;گه&zwnj;ر كه&zwnj;مێك نه&zwnj;بێت، هه&zwnj;ر ئێمه&zwnj;ش خاوه&zwnj;نی موڵك و خانووبه&zwnj;ره و سامانیان بووین.';
$t[59] = 'په&zwnj;روه&zwnj;ردگاری تۆ هیچ وڵات و شارێكی كاول نه&zwnj;كردووه&zwnj;، تا له پایته&zwnj;خته&zwnj;كه&zwnj;یدا پێغه&zwnj;مبه&zwnj;رێكی نه&zwnj;ناردبێت كه ئایه&zwnj;ته&zwnj;كانی ئێمه&zwnj;یان به&zwnj;سه&zwnj;ردا بخوێنێته&zwnj;وه&zwnj;، وه&zwnj;نه&zwnj;بێت ئێمه شار و وڵاتانمان كاول كردبێت به&zwnj;بێ هۆ، مه&zwnj;گه&zwnj;ر كاتێك نه&zwnj;بێت كه دانیشتووانی سته&zwnj;مكار بوون.';
$t[60] = 'هه&zwnj;رچیتان پێبه&zwnj;خشراوه ئه&zwnj;وه نازونیعمه&zwnj;ت و زینه&zwnj;ت و جوانی ژیانی دنیایه&zwnj;، به&zwnj;ڵام دڵنیابن كه ئه&zwnj;و به&zwnj;شه&zwnj;ی لای خوایه چاكترو ده&zwnj;وامدارتره&zwnj;، ئایا ئه&zwnj;وه عه&zwnj;قڵ و ژیریتان ناخه&zwnj;نه كار&zwnj;؟!';
$t[61] = 'ئایا ئه&zwnj;و كه&zwnj;سه&zwnj;ی به&zwnj;ڵێنێكی چاكمان داوه&zwnj;تێ و پێی ده&zwnj;گات، وه&zwnj;كو ئه&zwnj;و كه&zwnj;سه وایه هه&zwnj;ر له ژیانی دنیادا كه&zwnj;مێك ڕابواردنی پێبببه&zwnj;خشین، پاشان له ده&zwnj;رئه&zwnj;نجامی كارو كرده&zwnj;وه&zwnj;ی ناپوختیدا له ئاماده كراوان بێت بۆ لێپر سینه&zwnj;وه&zwnj;و ئاگری دۆزه&zwnj;خ؟';
$t[62] = 'له ڕۆژێكدا بانگیان لێده&zwnj;كرێت (بۆ لێپرسینه&zwnj;وه&zwnj;)، خوا، ڕوو به&zwnj;وان ده&zwnj;فه&zwnj;رموێت: ئایا له كوێن ئه&zwnj;و شه&zwnj;ریك و بتانه&zwnj;ی كه ئێوه به هاوه&zwnj;ڵی منتان داده&zwnj;نا به&zwnj;حسابی خۆتان.';
$t[63] = 'ئه&zwnj;وانه&zwnj;ی بڕیارداده&zwnj;ی خوا دژیان ده&zwnj;رچوو (به&zwnj;خه&zwnj;جاڵه&zwnj;تیه&zwnj;وه باسی ئه&zwnj;و كه&zwnj;سانه ده&zwnj;كه&zwnj;ن كه گومڕایان كردوون) ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا، ئا ئه&zwnj;وانه&zwnj;ی گومڕامان كردن، گومڕامان كردن وه&zwnj;ك خۆمان گومڕا بووین، ئێمه به&zwnj;رین له&zwnj;وانه&zwnj;و له هه&zwnj;ڵه&zwnj;یان له&zwnj;به&zwnj;رده&zwnj;م دادگای تۆدا، (چونكه ده&zwnj;سه&zwnj;ڵاتمان به&zwnj;سه&zwnj;ر دڵیاندا نه&zwnj;بوو) ئه&zwnj;وانه (درۆ ده&zwnj;كه&zwnj;ن) ئێمه&zwnj;یان هه&zwnj;ر نه&zwnj;ده&zwnj;په&zwnj;رست.';
$t[64] = 'ئه&zwnj;وسا پێیان ده&zwnj;وترێت: ئاده&zwnj;ی هاوه&zwnj;ڵانتان و خواكانتان بانگ بكه&zwnj;ن، جا ئه&zwnj;وانیش بانگیان ده&zwnj;كه&zwnj;ن، به&zwnj;ڵام وه&zwnj;ڵام و به&zwnj;ده&zwnj;مه&zwnj;وه چوون نه&zwnj;بوو بۆیان، ئه&zwnj;و كاته سزای سه&zwnj;ختی دۆزه&zwnj;خ ده&zwnj;بینن، ئاوات ده&zwnj;خوازن كه خۆزگه به&zwnj;ڕاستی ئه&zwnj;وان ڕێبازی هیدایه&zwnj;تیان بگرتایه&zwnj;ته به&zwnj;ر.';
$t[65] = 'رۆژێك دێت خوا بانگیان ده&zwnj;كات و پێیان ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;وه وه&zwnj;ڵامی پێغه&zwnj;مبه&zwnj;رانتان چۆ&zwnj;ن دایه&zwnj;وه&zwnj;؟';
$t[66] = 'له سه&zwnj;ختیی ئه&zwnj;و ڕۆژه هه&zwnj;موو هه&zwnj;واڵێك فه&zwnj;رامۆش ده&zwnj;كه&zwnj;ن (هه&zwnj;موو شتێكیان له بیر ده&zwnj;چێت) پرسیار له یه&zwnj;كتریش ناكه&zwnj;ن.';
$t[67] = 'جا ئه&zwnj;وه&zwnj;ی تۆبه&zwnj;ی كردووه و باوه&zwnj;ری دامه&zwnj;زراوی ههێناوه&zwnj;و كارو كرده&zwnj;وه&zwnj;ی چاكی ئه&zwnj;نجام داوه&zwnj;، ئه&zwnj;وه بێگومان ئه&zwnj;وجۆره كه&zwnj;سانه له سه&zwnj;رفرازو به&zwnj;خته&zwnj;وه&zwnj;ران ده&zwnj;بن.';
$t[68] = 'هه&zwnj;ر په&zwnj;روه&zwnj;ردگاری تۆیه هه&zwnj;ر چیه&zwnj;كی بوێت دروستی ده&zwnj;كات و هه&zwnj;ڵی ده&zwnj;بژێرێت خاڵكی ئه&zwnj;و ده&zwnj;سه&zwnj;ڵاته&zwnj;یان نیه&zwnj;وسه&zwnj;رپشك نه&zwnj;كراون، پاكی و بێگه&zwnj;ردی و بڵندی بۆ خوایه له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و شتانه&zwnj;وه نه&zwnj;فامان ده&zwnj;یكه&zwnj;نه شه&zwnj;ریك و هاوه&zwnj;ڵ.';
$t[69] = 'هه&zwnj;روه&zwnj;ها په&zwnj;روه&zwnj;ردگاری تۆ ده&zwnj;زانێت ئه&zwnj;و خه&zwnj;ڵكه چیان له سینه&zwnj;و ده&zwnj;رونیاندا شاردۆته&zwnj;وه وه&zwnj;چی ده&zwnj;رده&zwnj;خه&zwnj;ن و ئاشكرای ده&zwnj;كه&zwnj;ن.';
$t[70] = 'خوا زاتێكه كه جگه له&zwnj;و خوایه&zwnj;كی تر نیه&zwnj;، سوپاس و ستایش له سه&zwnj;ره&zwnj;تا و كۆتاییدا، له دنیا و قیامه&zwnj;تدا، هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه&zwnj;، حوكم و فه&zwnj;رمانره&zwnj;وایش هه&zwnj;ر به&zwnj;ده&zwnj;ست ئه&zwnj;وه&zwnj;، هه&zwnj;ر بۆلای ئه&zwnj;ویش ده&zwnj;برێنه&zwnj;وه.';
$t[71] = 'ئه&zwnj;ی محمد (ص) بڵێ: خه&zwnj;ڵكینه هه&zwnj;واڵم بده&zwnj;نێ ئه&zwnj;گه&zwnj;ر خوا شه&zwnj;وگار بكات به&zwnj;هه&zwnj;میشه&zwnj;یی تا ڕۆژی قیامه&zwnj;ت (هه&zwnj;ر ڕۆژ نه&zwnj;بێته&zwnj;وه&zwnj;) چ خوایه&zwnj;كی تر هه&zwnj;یه جگه له ا لله بتوانێت ڕوناكیتان بۆ فه&zwnj;راهه&zwnj;م بهێنێت، ئایه ئێوه ئه&zwnj;م ڕاستیانه نابیسن؟';
$t[72] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ ئه&zwnj;گه&zwnj;ر خوا ڕۆژگاری كرده هه&zwnj;میشه&zwnj;یی له سه&zwnj;رتان، تا ڕۆژی قیامه&zwnj;ت خه&zwnj;یاندی، كام خوا هه&zwnj;یه جگه له ا لله كه شه&zwnj;وگارتان بۆ پێش بهێنێت تا ئارامی تێدا بگرن، ئایه ئێوه سه&zwnj;رنجی ئه&zwnj;م دیاردانه ناده&zwnj;ن و نایانبینن؟!';
$t[73] = 'جا له ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی ئه&zwnj;و زاته ئه&zwnj;وه&zwnj;یه كه&zwnj;: شه&zwnj;وو ڕۆژی كردووه به هۆی ئارامی و حه&zwnj;وانه&zwnj;وه و كارو كاسپی كردن و به&zwnj;هره بردن له به&zwnj;خششه&zwnj;كانی، بۆ ئه&zwnj;وه&zwnj;ی تا ده&zwnj;توانن شوكرانه بژێر و سوپاسگوزاری بكه&zwnj;ن.';
$t[74] = 'ڕۆژێك دێت خوا بانگیان ده&zwnj;كات و پێیان ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;وه كوان ئه&zwnj;و شتانه&zwnj;ی به گومانی ئێوه هه&zwnj;وه&zwnj;ڵ و شه&zwnj;ریكی من بوون؟!';
$t[75] = '(هه&zwnj;ر له&zwnj;و ڕۆژه&zwnj;دا) له هه&zwnj;ر ئوممه&zwnj;تێك شایه&zwnj;تێك له خۆیان ده&zwnj;هێنینه مه&zwnj;یدان له پێغه&zwnj;مبه&zwnj;ران، ئه&zwnj;وسا به&zwnj;خه&zwnj;ڵكی سه&zwnj;رگه&zwnj;ردان ده&zwnj;ڵێین: ئاده&zwnj;ی ئێوه به&zwnj;ڵگه&zwnj;تان چییه له سه&zwnj;ر لادان و گوناهتان بیهێنن، ئه&zwnj;و ڕۆژه ئیتر زۆر به&zwnj;چاكی ده&zwnj;زانن و دڵنیا ده&zwnj;بن كه حه&zwnj;ق و حه&zwnj;قیقه&zwnj;ت لای خوایه و هه&zwnj;ر ئه&zwnj;و شاییسته&zwnj;ی په&zwnj;رستن بووه&zwnj;، هه&zwnj;رچی ئه&zwnj;و شتانه&zwnj;ش كه بڕوایان پێی هه&zwnj;بوو، هه&zwnj;ڵیان به&zwnj;ستبوو، هه&zwnj;مووی ون و نادیار و بێ نرخ دێته به&zwnj;رچاو.';
$t[76] = 'بێگومان قارون له قه&zwnj;وم و خوێشانی موسا بو كه&zwnj;چی (به&zwnj;هۆی ماڵ و سامانیه&zwnj;وه&zwnj;) خۆی گیڤ ده&zwnj;كرده&zwnj;وه به سه&zwnj;ریاندا و فیزی به&zwnj;سه&zwnj;ردا ده&zwnj;كردن، ئه&zwnj;وه&zwnj;نده ماڵ و سامان و داراییمان پێبه&zwnj;خشیبوو، كه به&zwnj;ڕاستی كلیلی گه&zwnj;نجینه و قاسه&zwnj;كانی با چه&zwnj;ند كه&zwnj;سێكی به&zwnj;هێز به&zwnj;گرانی هه&zwnj;ڵده&zwnj;گیران، (له&zwnj;كاتی لووت به&zwnj;رزیه&zwnj;كه&zwnj;یدا) هه&zwnj;ندێك ئیمانداری خزمی پێیان وت: كه&zwnj;شخه مه&zwnj;كه و له خۆت بایی مابه به&zwnj;م سامانه&zwnj;، چونكه بێگومان خوا ئه&zwnj;و كه&zwnj;سانه&zwnj;ی خۆشناوێت كه كه&zwnj;شخه ده&zwnj;كه&zwnj;ن به&zwnj;سه&zwnj;روه&zwnj;ت و سامانیانه&zwnj;وه و له خۆیان بایی ده&zwnj;بن.';
$t[77] = 'هه&zwnj;وڵبده به&zwnj;و ماڵ و سامانه&zwnj;ی خوا پیی به&zwnj;خشیووی ماڵی قیامه&zwnj;ت و به&zwnj;هه&zwnj;شتی به&zwnj;رین بكڕه&zwnj;و به&zwnj;شی خۆشت له دنیادا به&zwnj;حه&zwnj;ڵاڵی فه&zwnj;رامۆش مه&zwnj;كه&zwnj;، چاكه له&zwnj;گه&zwnj;ڵ به&zwnj;نده&zwnj;كانی خوادا بكه هه&zwnj;روه&zwnj;ك خوا چاكه&zwnj;ی هێناوه&zwnj;ته ڕێت، نه&zwnj;كه&zwnj;یت هه&zwnj;وڵی فه&zwnj;ساد و گوناهو تاوان بده&zwnj;ی له زه&zwnj;ویدا، چونكه به&zwnj;ڕاستی خوا ئه&zwnj;و كه&zwnj;سانه&zwnj;ی خۆشناوێت تۆی خراپه ده&zwnj;چینن.';
$t[78] = 'قاروون وتی: بێگومان ئه&zwnj;م سامان و داراییه&zwnj;م هه&zwnj;ر به هۆی زانست و زانیاری نه&zwnj;خشه&zwnj;ی خۆمه&zwnj;وه پێمدراوه&zwnj;، باشه مه&zwnj;گه&zwnj;ر قاروون نه&zwnj;یده&zwnj;زانی به&zwnj;ڕاستی خوا پێش ئه&zwnj;م چه&zwnj;نده&zwnj;ها قه&zwnj;وم و هۆز و گه&zwnj;لانی له ناو بردووه كه له&zwnj;و زیاتر به&zwnj;ده&zwnj;سه&zwnj;ڵاتر و سه&zwnj;روه&zwnj;تمه&zwnj;ندتر بوون؟ تاوان باران پرسیاریان لێناكرێت ده&zwnj;رباره&zwnj;ی گوناهه&zwnj;كانیان (چونكه تۆماره و لای خوا ئاشكرایه&zwnj;).';
$t[79] = 'ئینجا ڕۆژێك قاروون خۆی سازدا و به ڕازاوه&zwnj;یی چووه ده&zwnj;ره&zwnj;وه بۆ ناو قه&zwnj;ومه&zwnj;كه&zwnj;ی بۆ كه&zwnj;شخه&zwnj;كردن و خۆ نواندن، ئه&zwnj;وانه&zwnj;ی كه ئاواتیان ژیانی دنیایه; وتیان: ئای خۆزگه ئێمه&zwnj;ش وه&zwnj;كو قاروون، سامان و ده&zwnj;سه&zwnj;ڵاتمان ببوایه&zwnj;، به&zwnj;ڕاستی خاوه&zwnj;نی به&zwnj;ختێكی گه&zwnj;وره&zwnj;یه!';
$t[80] = '(به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی كه زانستی و زانیاری ڕاستیان پێبه&zwnj;خشراوه (ده&zwnj;رباره&zwnj;ی حه&zwnj;ڵاڵ و حه&zwnj;رام و نرخی قیامه&zwnj;ت ده&zwnj;زانن) وتیان: هاوار بۆ ئێوه&zwnj;، بۆ تێنافكرن پاداشتی خوایی زۆر چاكتره بۆ ئه&zwnj;و كه&zwnj;سه&zwnj;ی باوه&zwnj;ڕی دامه&zwnj;زراوی هێناوه&zwnj;و كارو كرده&zwnj;وه&zwnj;ی چاكی ئه&zwnj;نجام داوه&zwnj;، ئه&zwnj;و زانست و باوه&zwnj;ڕو كرده&zwnj;وه&zwnj;یه به كه&zwnj;س نادرێت، ته&zwnj;نها به خۆگرو ئارامگران نه&zwnj;بێت.';
$t[81] = 'ئه&zwnj;وسا (كه بینیمان قاروون به&zwnj;ته&zwnj;واوی له سنوور ده&zwnj;رچووه&zwnj;) كوتوپڕ خۆی و ماڵ و سامانیمان برد به&zwnj;ناخی زه&zwnj;ویدا، هیچ ده&zwnj;سته&zwnj;و گرۆیه&zwnj;كی نه&zwnj;بوو فریای بكه&zwnj;وێت و له ده&zwnj;ست خاشم و قینی خوا ده&zwnj;ربازی بكات، بێگومان له&zwnj;و كه&zwnj;سانه&zwnj;ش نه&zwnj;بوو كه بتوانێت خۆی ڕزگار ببێت.';
$t[82] = 'ئینجا ئه&zwnj;وانه&zwnj;ی دوێنێ ئاواتیان به قاروون ده&zwnj;خواست ده&zwnj;یانوت: ئه&zwnj;ی هاوار، به&zwnj;س نه&zwnj;بوو وه&zwnj;كو ئه&zwnj;و تیانه&zwnj;چووین، دیاره كه خوا ڕزق و ڕۆزی زۆر ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر كه&zwnj;س بیه&zwnj;وێت یاخود كه&zwnj;می ده&zwnj;كاته&zwnj;وه (بێ حیكمه&zwnj;ت نیه&zwnj;)، ئه&zwnj;گه&zwnj;ر فه&zwnj;زڵ و به&zwnj;هره&zwnj;ی خوایی نه&zwnj;بوایه له سه&zwnj;رمان، ئێمه&zwnj;ش به&zwnj;ناخی زه&zwnj;ویدا ڕۆچوو ده&zwnj;بووین، ئای كه ڕوون و ئاشكرایه&zwnj;، كافرو خوانه&zwnj;ناسان سه&zwnj;رفراز نابن.';
$t[83] = '(جا با خه&zwnj;ڵكی دڵنیابن) كه&zwnj;: ئه&zwnj;و ماڵی قیامه&zwnj;ته و ئه&zwnj;و به&zwnj;هه&zwnj;شتی به&zwnj;رینه&zwnj;مان ئاماده&zwnj;كردووه بۆ ئه&zwnj;و كه&zwnj;سانه&zwnj;ی كه نه&zwnj;لووت به&zwnj;رزی و گه&zwnj;وره&zwnj;ییان ده&zwnj;وێت له زه&zwnj;ویدا، نه فه&zwnj;سادو گوناهو خراپه&zwnj;، سه&zwnj;ره&zwnj;نجام و سه&zwnj;رفرازیش هه&zwnj;ر بۆ پارێزكاران و خواناسه&zwnj;كانه&zwnj;.';
$t[84] = 'ئه&zwnj;وه&zwnj;ی به كاروكرده&zwnj;وه&zwnj;ی چاكه&zwnj;وه بێت ئه&zwnj;وه چاكتر دێته&zwnj;وه ڕێی، ئه&zwnj;وه&zwnj;ش به&zwnj;گوناهو هه&zwnj;ڵه&zwnj;وه بێت، ئه&zwnj;وه پاداشتی ئه&zwnj;وانه&zwnj;ی كه گوناهو هه&zwnj;ڵه&zwnj;یان كردووه نادرێته&zwnj;وه&zwnj;، ته&zwnj;نها ئه&zwnj;وه نه&zwnj;بێت كه ده&zwnj;یانكرد.';
$t[85] = 'به&zwnj;ڕاستی ئه&zwnj;و زاته&zwnj;ی كه گه&zwnj;یاندنی قورئانی له&zwnj;سه&zwnj;ر تۆ پێویست كردووه&zwnj;، سه&zwnj;ره&zwnj;نجام ده&zwnj;تگێرێته&zwnj;وه بۆ مه&zwnj;ككه&zwnj;، یان قیامه&zwnj;ت و به&zwnj;هه&zwnj;شتی به&zwnj;رین، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (ص) پێیان بڵێ: په&zwnj;روه&zwnj;ردگارم خۆی چاك ده&zwnj;زانێت و له هه&zwnj;موو كه&zwnj;س زاناتره به&zwnj;و كه&zwnj;سه&zwnj;ی كه ڕێنموویی و هیدایه&zwnj;تی هێناوه و به&zwnj;و كه&zwnj;سه&zwnj;ش كه له&zwnj;ناو گومڕاییه&zwnj;كی ئاشكرادایه&zwnj;.';
$t[86] = 'تۆ ئه&zwnj;ی محمد (ص) به&zwnj;ته&zwnj;ما نه&zwnj;بوویت قورئانت بۆ ڕه&zwnj;وانه بكرێت، جگه&zwnj;له میهره&zwnj;بانیه&zwnj;ك نه&zwnj;بێت له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه&zwnj;، كه&zwnj;وابوو خۆت مه&zwnj;كه به پشتیوان و هاوكاری بێ باوه&zwnj;ڕ و خوانه&zwnj;ناسان';
$t[87] = 'با بێ باوه&zwnj;ڕان به&zwnj;رگریت نه&zwnj;كه&zwnj;ن له&zwnj;گه&zwnj;یاندنی ئایه&zwnj;ته&zwnj;كانی خوا، دوای ئه&zwnj;و كاته&zwnj;ی بۆ تۆ دابه&zwnj;زێنراوه&zwnj;، هه&zwnj;میشه بانگه&zwnj;شه بكه بۆ لای په&zwnj;روه&zwnj;ردگارت، هه&zwnj;رگیز نه&zwnj;كه&zwnj;یت خۆت بخه&zwnj;یته ڕیزی هاوه&zwnj;ڵگه&zwnj;رانه&zwnj;وه&zwnj;.';
$t[88] = 'له&zwnj;گه&zwnj;ڵ خوادا ها&zwnj;نا و هاوار له خوایه&zwnj;كی تر مه&zwnj;كه&zwnj;، چونكه جگه له&zwnj;و زاته خوایه&zwnj;كی تر نیه&zwnj;، هه&zwnj;رچی شت هه&zwnj;یه تیاچووه له&zwnj;ناوچووه جگه له&zwnj;و زاته&zwnj;، فه&zwnj;رمانڕه&zwnj;وایی هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه&zwnj;، هه&zwnj;مووانیش هه&zwnj;ر بۆ لای ئه&zwnj;و ده&zwnj;برێنه&zwnj;وه&zwnj;.';
?>