$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = 'ڕۆمه&zwnj;کان تێکشکێنران (به&zwnj;ده&zwnj;ستی فارسه&zwnj;کان).';
$t[3] = 'له نزیکترین و نزمترین وڵاتیاندا، به&zwnj;ڵام له ئاینده&zwnj;دا (نۆره&zwnj;یان دێته&zwnj;وه&zwnj;) ڕۆمه&zwnj;کان دوای شکستیان سه&zwnj;رده&zwnj;که&zwnj;ونه&zwnj;وه&zwnj;.';
$t[4] = 'له چه&zwnj;ند ساڵێکدا (چه&zwnj;ند ساڵێکی داهاتوو نێوان 3-9 ساڵ)، هه&zwnj;موو کارو فرمانێك ته&zwnj;نها به&zwnj;ده&zwnj;ست خوایه&zwnj;، له&zwnj;وه&zwnj;و پێش و له&zwnj;وه&zwnj;و دوایش (له هه&zwnj;موو کات و هه&zwnj;موو شوێنێك)، جا ئه&zwnj;و ڕۆژه ئیمانداران خۆشحاڵ و شاد ده&zwnj;بن...';
$t[5] = 'به سه&zwnj;رکه&zwnj;وتنی خوا، (که به&zwnj;خشی به ئیمانداران و به ڕۆمه&zwnj;کان)، هه&zwnj;ر که&zwnj;سێك یان هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ك ئه&zwnj;و زاته بیه&zwnj;وێت، سه&zwnj;ری ده&zwnj;خات، هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;ش خاوه&zwnj;نی ده&zwnj;سه&zwnj;ڵاتی بێ سنووره و زۆریش دلۆڤان و میهره&zwnj;بانه.';
$t[6] = 'بێگومان ئه&zwnj;وه به&zwnj;ڵێنی خوا خۆیه&zwnj;تی (که ئیمانداران و ڕۆمه&zwnj;کان سه&zwnj;رده&zwnj;خات به&zwnj;سه&zwnj;ر فارسه&zwnj;کاندا)، خوا سه&zwnj;رپێچی ناکات له به&zwnj;ڵێنه&zwnj;که&zwnj;ی، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ته&zwnj;نها ڕواڵه&zwnj;ت له دنیا ده&zwnj;زانن (عه&zwnj;قڵیان له چاویاندایه&zwnj;)، ئه&zwnj;وانه بێ ئاگاو بێ خه&zwnj;به&zwnj;رن له ڕۆژی دوایی و قیامه&zwnj;ت...';
$t[7] = 'ئه&zwnj;وانه هه&zwnj;ر ڕواڵه&zwnj;ت له ژیانی دنیا به&zwnj;دی ده&zwnj;که&zwnj;ن، له به&zwnj;رامبه&zwnj;ر قیامه&zwnj;تیشه&zwnj;وه بێ ئاگان.';
$t[8] = 'ئایا ئه&zwnj;وه له ناخی خۆیاندا بیرێکیان نه&zwnj;کردۆته&zwnj;وه&zwnj;، که خوا ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;رچی له نێوانیاندا هه&zwnj;یه ته&zwnj;نها به حه&zwnj;ق و سنووردار نه&zwnj;بێت دروستی نه&zwnj;کردوون؟ له ڕاستیدا زۆربه&zwnj;ی خه&zwnj;ڵکی بێ باوه&zwnj;ڕن به گه&zwnj;یشتنه خزمه&zwnj;تی په&zwnj;روه&zwnj;ردگاریان.';
$t[9] = 'ئایا به&zwnj;سه&zwnj;ر زه&zwnj;ویدا نه&zwnj;گه&zwnj;ڕاون، تا ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن چیمان به&zwnj;سه&zwnj;ر ئه&zwnj;و نه&zwnj;ته&zwnj;وانه&zwnj;ی پێش ئه&zwnj;ماندا هێناوه&zwnj;، له&zwnj;مان زۆر به&zwnj;هێزترو به ده&zwnj;سه&zwnj;ڵات تر بوون، زه&zwnj;ویان ژێره&zwnj;و ژوور ده&zwnj;کرد (واته ده&zwnj;یان کێڵا بۆ کشتوکاڵ و کانزایان لێ ده&zwnj;رده&zwnj;هێنا)و ئاوه&zwnj;دانیان کردبۆوه زیاتر له&zwnj;مان، پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیشیان کاتی خۆی به&zwnj;ڵگه&zwnj;ی زۆرو ئاشکرایان بۆ هێنان؟ (به&zwnj;ڵام بێ سوود بوو، زۆربه&zwnj;یان بڕوایان نه&zwnj;کرد) جا وه&zwnj;نه&zwnj;بێت خوا سته&zwnj;می لێکردبن، به&zwnj;ڵکو هه&zwnj;ر خۆیان سته&zwnj;میان له خۆیان ده&zwnj;کرد.';
$t[10] = 'پاشان ئه&zwnj;و تیاچوونه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه بوو که تاوان و گوناهیان کرد؛ چونکه ئه&zwnj;وانه بڕوایان به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا نه&zwnj;هێناو گاڵته&zwnj;یان پێ ده&zwnj;کرد.';
$t[11] = 'هه&zwnj;ر خوا خۆی دروستکراوان ده&zwnj;هێنێته مه&zwnj;یدان و دوایی وه&zwnj;ك خۆیان لێده&zwnj;کاته&zwnj;وه&zwnj;، (سه&zwnj;ره&zwnj;تا هه&zwnj;ر ئه&zwnj;و دروستی کردوون و هه&zwnj;ر ئه&zwnj;ویش دوای مردن دووباره زیندوویان ده&zwnj;کاته&zwnj;وه&zwnj;)، سه&zwnj;رئه&zwnj;نجامیش هه&zwnj;ر بۆ لای ئه&zwnj;و زاته ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[12] = 'ئه&zwnj;و ڕۆژه&zwnj;ی قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت، تاوانباران و تاوانکاران نائومێد و بێ هیوا ده&zwnj;بن (چونکه ده&zwnj;زانن چیان چاندووه&zwnj;و چیان کێڵاوه و چیان ده&zwnj;ست پێشکه&zwnj;ری کردوه&zwnj;).';
$t[13] = 'که&zwnj;سیان ده&zwnj;ست نه&zwnj;که&zwnj;وت له&zwnj;وانه&zwnj;ی به شه&zwnj;ریك و هاوه&zwnj;ڵیان ده&zwnj;زانی تا تکایان بۆ بکات و فریایان بکه&zwnj;وێت، ئه&zwnj;وسا ئیتر به&zwnj;ناچاری، بێ باوه&zwnj;ڕ ده&zwnj;بن به شه&zwnj;ریك و هاوه&zwnj;ڵ (که له دنیادا پشتیان پێ ده&zwnj;به&zwnj;ستن و هاناو هاواریان بۆ ده&zwnj;بردن).';
$t[14] = 'ئه&zwnj;و ڕۆژه&zwnj;ی قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت، له&zwnj;و ڕۆژه&zwnj;دا؛ ئه&zwnj;و خه&zwnj;ڵکه&zwnj;؛ له یه&zwnj;ك داده&zwnj;بڕێن و ده&zwnj;بنه دوو ده&zwnj;سته&zwnj;وه&zwnj;.';
$t[15] = 'جا ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕیان هێناوه&zwnj;و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، ئه&zwnj;وانه له باخچه&zwnj;و باخاتی به&zwnj;هه&zwnj;شتدا ژیانی پڕ له کامه&zwnj;رانی و به&zwnj;خته&zwnj;وه&zwnj;ری و ئاسووده&zwnj;یی شاد ده&zwnj;کرێن.';
$t[16] = 'ئه&zwnj;وانه بێ بڕوا بوون، کاتی خۆی به&zwnj;ڵگه و نیشانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆ ده&zwnj;زانی و بروایان به&zwnj;گه&zwnj;یشتنی قیامه&zwnj;ت و زیندوو بوونه&zwnj;وه نه&zwnj;بوو، ئه&zwnj;وانه ئاماده&zwnj;ن له&zwnj;ناو سزایه&zwnj;کی سه&zwnj;خت و دۆزه&zwnj;خێکی پڕ ئه&zwnj;شکه&zwnj;نجه&zwnj;و ئازاردا.';
$t[17] = 'که&zwnj;واته پاکی و بێگه&zwnj;ردی و ستایش بۆ خوا، کاتێك که ئێواره&zwnj;و به&zwnj;یانی ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;.';
$t[18] = 'شوکرو سوپاسگوزاری هه&zwnj;ر شایسته&zwnj;ی زاتی خوایه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، له&zwnj;کاتی عیشاو تاریکیدا، هه&zwnj;روه&zwnj;ها کاتێك که نیوه&zwnj;ڕۆ ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;.';
$t[19] = 'ئه&zwnj;و خوایه زیندوو له مردوو ده&zwnj;رده&zwnj;هێنێت (له مادده بێ گیانه&zwnj;کان زینده&zwnj;واران دروست ده&zwnj;کات)، مردوویش له زیندو ده&zwnj;رده&zwnj;هێنێت (ژیان و مردن به&zwnj;رده&zwnj;وامه و تێهه&zwnj;ڵکێشی یه&zwnj;کن)، زه&zwnj;وی دوای مردنی زیندو ده&zwnj;کاته&zwnj;وه (زه&zwnj;وی له وه&zwnj;رزی پایزو زستاندا جۆره مردنێکی به&zwnj;سه&zwnj;ردا دێت، له وه&zwnj;رزی به&zwnj;هارو هاویندا جۆره زیندوو بوونه&zwnj;وه&zwnj;یه&zwnj;کی تێدا ئاشکرایه&zwnj;)، جا هه&zwnj;ر ئا به&zwnj;و شێوه&zwnj;یه ئێوه&zwnj;ش له گۆڕه&zwnj;کان ده&zwnj;رده&zwnj;هێنرێنه&zwnj;وه&zwnj;. (له&zwnj;وه ده&zwnj;چێت وه&zwnj;کو زۆربه&zwnj;ی زانایان ده&zwnj;فه&zwnj;رموون: زیندوو بوونه&zwnj;وه&zwnj;ی دووه&zwnj;م جار وێنه&zwnj;ی ڕوه&zwnj;ك له&zwnj;م خاکه سه&zwnj;رده&zwnj;ربهێنین).';
$t[20] = 'له نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی خوا ئه&zwnj;وه&zwnj;یه ئێوه&zwnj;ی له خاك دروست کردووه&zwnj;، له&zwnj;وه&zwnj;ودوا دوای بڕینی چه&zwnj;ند قۆناغێك کوتوپڕ بوونه&zwnj;ته مرۆڤێك و بڵاو ده&zwnj;بنه&zwnj;وه به زه&zwnj;ویدا.';
$t[21] = 'یه&zwnj;کێك له نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی تری ئه&zwnj;وه&zwnj;یه&zwnj;: که هه&zwnj;ر له خۆتان هاوسه&zwnj;ری بۆ دروست کردوون بۆ ئه&zwnj;وه&zwnj;ی ئارام بگرن له&zwnj;لایداو له نێوانتاندا خۆشه&zwnj;ویستی و سۆزو میهره&zwnj;بانی فه&zwnj;راهه&zwnj;م هێناوه&zwnj;، به&zwnj;ڕاستی ئا له&zwnj;و دیاردانه&zwnj;دا نیشانه&zwnj;و به&zwnj;ڵگه هه&zwnj;یه بۆ که&zwnj;سانێك بیرده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;و تێده&zwnj;فکرن.';
$t[22] = '(یه&zwnj;کێکی تر) له نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی، دروستکردنی ئاسمانه&zwnj;کان و زه&zwnj;ویه (که زاناکانی ئه&zwnj;ستێره&zwnj;ناسی له ئاستیدا سه&zwnj;ر&zwnj;سام و سه&zwnj;رگه&zwnj;ردانن) و جیاوازی زمان و ڕه&zwnj;نگتانه (ده&zwnj;نگی هیچ که&zwnj;س له که&zwnj;سی تر ناکات، سه&zwnj;ره&zwnj;ڕای سه&zwnj;ده&zwnj;ها جۆر زمان، که جۆری قسه&zwnj;کردن و داڕشتنی هه&zwnj;ر گه&zwnj;لێك تایبه&zwnj;تمه&zwnj;ندی خۆی هه&zwnj;یه&zwnj;، جگه له هه&zwnj;زاران شێواز که له&zwnj;و زمانه سه&zwnj;ره&zwnj;کیانه ده&zwnj;بێته&zwnj;وه&zwnj;)، به&zwnj;ڕاستی ئا له&zwnj;و دیارده&zwnj;یه&zwnj;شدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ زانایان و شاره&zwnj;زایان.';
$t[23] = 'یه&zwnj;کێکی تر له نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی خه&zwnj;وتنتانه له شه&zwnj;وو ڕۆژدا (ئه&zwnj;ڵبه&zwnj;ته خه&zwnj;و دیارده&zwnj;یه&zwnj;کی سه&zwnj;رنج ڕاکێشه&zwnj;و جێگه&zwnj;ی ورد بوونه&zwnj;وه&zwnj;و تێڕامانه&zwnj;)، له کاتی بێداریشدا هه&zwnj;وڵ و کۆششتانه بۆ به&zwnj;هره وه&zwnj;رگرتن له به&zwnj;خششه&zwnj;کانی خوا، جا به&zwnj;ڕاستی ئا له&zwnj;و دیارده&zwnj;یه&zwnj;شدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك که ده&zwnj;بیستن و تێده&zwnj;گه&zwnj;ن.';
$t[24] = 'هه&zwnj;روه&zwnj;ها له به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کانی تری ئه&zwnj;وه&zwnj;یه که هه&zwnj;وره بروسکه&zwnj;تان نیشان ده&zwnj;دات که ترسی لێ ده&zwnj;کرێت و ئومێد به&zwnj;خشیشه (ترس له ده&zwnj;نگ و ڕوناکی به&zwnj;هێزی، ترس له&zwnj;وه&zwnj;ی ببێته مایه&zwnj;ی ته&zwnj;رزه&zwnj;و لافاو، هه&zwnj;روه&zwnj;ها به&zwnj;ئومێدی فه&zwnj;رو به&zwnj;ره&zwnj;که&zwnj;ت) و له ئاسمانه&zwnj;وه باران ده&zwnj;بارێنێت، زه&zwnj;وی پێ زیندوو ده&zwnj;کاته&zwnj;وه دوای ئه&zwnj;وه&zwnj;ی که مردبوو (ڕوه&zwnj;ك و کژو گیاکان وشك بوون) به&zwnj;ڕاستی ئا له&zwnj;و دیارده&zwnj;یه&zwnj;شدا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆر هه&zwnj;ن بۆ که&zwnj;سانێك ژیریان بخه&zwnj;نه کار.';
$t[25] = 'له نیشانه&zwnj;و به&zwnj;ڵگه&zwnj;کانی تری خوا ئه&zwnj;وه&zwnj;یه که ئاسمان و زه&zwnj;وی ڕاوه&zwnj;ستاون به فه&zwnj;رمانی ئه&zwnj;و (خۆرو هه&zwnj;ساره&zwnj;و ئه&zwnj;ستێره&zwnj;کانی له بۆشایدا ڕاگرتووه بێ کۆڵه&zwnj;که&zwnj;و بێ پاڵپشتێکی دیار)، له&zwnj;وه&zwnj;ودوا کاتێك بانگێکی لێکردن، که ئێوه له توێی خاکدان، ده&zwnj;ستبه&zwnj;جێ هه&zwnj;مووتان ده&zwnj;رده&zwnj;چن له زه&zwnj;ویداو زیندوو ده&zwnj;بنه&zwnj;وه&zwnj;.';
$t[26] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;مووی ئه&zwnj;و به&zwnj;دیهێنه&zwnj;ریانه&zwnj;و خاوه&zwnj;نیانه&zwnj;، هه&zwnj;ر هه&zwnj;مووشیان ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئه&zwnj;ون.';
$t[27] = 'هه&zwnj;ر ئه&zwnj;و زاته له سه&zwnj;ره&zwnj;تاوه به&zwnj;دیهێنراوه&zwnj;کان به&zwnj;دی ده&zwnj;هێنێت، پاشان به جۆرێکی تر دروستیان ده&zwnj;کاته&zwnj;وه&zwnj;، ئه&zwnj;و کاره&zwnj;ش بۆ خوا ئاسانه&zwnj;، نموونه&zwnj;ی به&zwnj;رزو وه&zwnj;سفی بڵند هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;و زاته&zwnj;یه له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، هه&zwnj;ر ئه&zwnj;ویش زاتێکی باڵا&zwnj;ده&zwnj;ست و دانایه&zwnj;.';
$t[28] = 'هه&zwnj;ر له خۆتان نموونه&zwnj;ی بۆ هێناونه&zwnj;ته&zwnj;وه (که چه&zwnj;نده بڕیاردانی هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ خوا نادروست و نابه&zwnj;جێیه&zwnj;) ئایا ئێوه ڕازین به&zwnj;نده&zwnj;و به&zwnj;رده&zwnj;سته&zwnj;کانتان ببنه هاوبه&zwnj;شتان و له&zwnj;و ماڵ و سامانه&zwnj;ی پێمانداون شه&zwnj;ریك بن و وه&zwnj;ك یه&zwnj;ك سوودی لێوه&zwnj;ربگرن و ده&zwnj;ستی تێبخه&zwnj;ن و لێیان بترسن و حسابیان بۆ بکه&zwnj;ن، هه&zwnj;روه&zwnj;ك چۆن ئێوه له یه&zwnj;کتر سڵده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;و حساب بۆ یه&zwnj;کتر ده&zwnj;که&zwnj;ن؟! ئا به&zwnj;و شێوه&zwnj;یه به&zwnj;ڵگه&zwnj;و نموونه&zwnj;ی زۆر ڕوون ده&zwnj;که&zwnj;ینه&zwnj;وه بۆ که&zwnj;سانێك که ژیرو هۆشمه&zwnj;ند بن...';
$t[29] = 'نه&zwnj;خێر، ئه&zwnj;وانه ژیر نابن، به&zwnj;ڵکو ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان کردووه شوێنی ئاره&zwnj;زووی خۆیان ده&zwnj;که&zwnj;ون دوور له هه&zwnj;موو بنه&zwnj;مایه&zwnj;کی زانستی و زانیاری، جا ئیتر کێ ده&zwnj;توانێت ڕێنموویی ئه&zwnj;و که&zwnj;سانه بکات خوا گومڕای کردوون (ئه&zwnj;ڵبه&zwnj;ته خۆیان ڕێگه&zwnj;ی گومڕاییان هه&zwnj;ڵبژاردووه&zwnj;، خوای گه&zwnj;وره&zwnj;ش به زۆر هیدایه&zwnj;تی که&zwnj;س نادات)، که&zwnj;سیش پشتیوان و یارمه&zwnj;تیده&zwnj;ریان له سزای سه&zwnj;خت بیانپارێزێت.';
$t[30] = '(ئه&zwnj;ی ئینسان به&zwnj;دڵێکی پاك و بێگه&zwnj;رده&zwnj;وه&zwnj;) ڕوو بکه&zwnj;ره به&zwnj;رنامه&zwnj;و ئاینی خواو ئه&zwnj;ملاولا مه&zwnj;که&zwnj;، خه&zwnj;ڵکینه ئێوه&zwnj;ش پابه&zwnj;ندی ئه&zwnj;و نه&zwnj;خشه&zwnj;یه بن که خوا ئێوه&zwnj;ی له&zwnj;سه&zwnj;ر دروست کردووه (واته قورئان که&zwnj;ته&zwnj;لۆك و نه&zwnj;خشه&zwnj;ی ژیانی ئاده&zwnj;میزاده&zwnj;کانه لێی لامه&zwnj;ده&zwnj;ن)، هیچ جۆره گۆڕانکاریه&zwnj;ك له هیچ نه&zwnj;خشه&zwnj;و دروستکراوێکی خوادا نی یه&zwnj;، ئا ئه&zwnj;وه&zwnj;یه دینی ڕاست و دروست، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;م ڕاستیانه نازانن.';
$t[31] = 'خه&zwnj;ڵکینه&zwnj;: ڕوو بکه&zwnj;نه په&zwnj;روه&zwnj;ردگارتان، به&zwnj;ملکه&zwnj;چی بگه&zwnj;ڕێنه&zwnj;وه بۆلای، له&zwnj;و بترسن و خۆتان له سزای بپارێزن، نوێژه&zwnj;کانیشتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن، مه&zwnj;بن به&zwnj;و که&zwnj;سانه&zwnj;ی که شه&zwnj;ریك و هاوه&zwnj;ڵی بۆ بڕیار ده&zwnj;ده&zwnj;ن...';
$t[32] = 'له&zwnj;وانه&zwnj;ی که ئاینه&zwnj;که&zwnj;یان به&zwnj;ش به&zwnj;ش کردووه&zwnj;و بوونه&zwnj;ته چه&zwnj;نده&zwnj;ها گرۆو ده&zwnj;سته (بڕوایان به&zwnj;هه&zwnj;ندێکی هه&zwnj;یه&zwnj;و بڕوایان به&zwnj;هه&zwnj;ندێکی نی یه&zwnj;و کوێرانه شوێن ڕابه&zwnj;ره&zwnj;که&zwnj;یان ده&zwnj;که&zwnj;ون) هه&zwnj;ر گرۆو پارتێکیش دڵخۆش به به&zwnj;رنامه&zwnj;و نه&zwnj;خشه&zwnj;و پلانی خۆیان (وا ده&zwnj;زانن چاکترین ڕێبازیان گرتۆته&zwnj;به&zwnj;ر).';
$t[33] = 'کاتێك ته&zwnj;نگانه&zwnj;و ناخۆشی تووشی خه&zwnj;ڵکی ده&zwnj;بێت، هاناو هاوار بۆ په&zwnj;روه&zwnj;ردگاریان ده&zwnj;به&zwnj;ن و هه&zwnj;ر له&zwnj;و ده&zwnj;پاڕێنه&zwnj;وه&zwnj;، پاشان که ناخۆشی و ته&zwnj;نگانه&zwnj;که نه&zwnj;ماو خوا ده&zwnj;وری ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆی لێکردنه&zwnj;وه&zwnj;، یه&zwnj;کسه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;کیان هاوه&zwnj;ڵ بۆ په&zwnj;روه&zwnj;ردگاریان بڕیار ده&zwnj;ده&zwnj;ن.';
$t[34] = 'با ئه&zwnj;وانه هه&zwnj;ر به&zwnj;دنمه&zwnj;ك بن و قه&zwnj;دری نازو نیعمه&zwnj;ت و به&zwnj;خششه&zwnj;کانمان نه&zwnj;زانن، با ڕابوێرن، له ئاینده&zwnj;دا ده&zwnj;زانن چ په&zwnj;نێکیان داوه&zwnj;و چ به&zwnj;ڵایه&zwnj;کیان به&zwnj;سه&zwnj;ر خۆیان هێناوه&zwnj;.';
$t[35] = 'ئایا مه&zwnj;گه&zwnj;ر بۆ سه&zwnj;ر ئه&zwnj;و هاوه&zwnj;ڵگه&zwnj;رانه به&zwnj;ڵگه&zwnj;مان ناردووه تا وێڵ بن له خواپه&zwnj;رستی و شوێنی نادروستی بکه&zwnj;ون؟! به&zwnj;ڵگه&zwnj;که&zwnj;ش قسه بکات و شه&zwnj;ریك بۆ خوا دانانه&zwnj;که&zwnj;یان په&zwnj;سه&zwnj;ند بکات؟!';
$t[36] = 'کاتێکیش ڕه&zwnj;حمه&zwnj;تێك به&zwnj;خه&zwnj;ڵکی بچێژین، ئه&zwnj;وا له خۆیان بایی ده&zwnj;بن (زۆربه&zwnj;یان دینداری فه&zwnj;رامۆش ده&zwnj;که&zwnj;ن)، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڵاو ناخۆشیه&zwnj;کیشیان تووش بێت له سه&zwnj;رئه&zwnj;نجامی کارو کرده&zwnj;وه&zwnj;و ده&zwnj;ست پێشخه&zwnj;ری خۆیاندا، ده&zwnj;ست به&zwnj;جێ نائومێد ده&zwnj;بن له ڕه&zwnj;حمه&zwnj;تی خوا.';
$t[37] = 'ئایا ئه&zwnj;وانه سه&zwnj;رنجیان نه&zwnj;داوه که به&zwnj;ڕاستی خوا ڕزق و ڕۆزی و سامان فراوان ده&zwnj;کات بۆ هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت، یاخود ده&zwnj;یگرێته&zwnj;وه&zwnj;و که&zwnj;می ده&zwnj;کاته&zwnj;وه&zwnj;، بێگومان ئا له&zwnj;و حاڵه&zwnj;تانه&zwnj;شدا به&zwnj;ڵگه&zwnj;ی زۆر هه&zwnj;یه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که باوه&zwnj;ڕ ده&zwnj;هێنن.';
$t[38] = 'که&zwnj;واته ئه&zwnj;ی ئیماندار: مافی خزمان بده&zwnj;، له هه&zwnj;ر ڕوویه&zwnj;که&zwnj;وه مافیان ده&zwnj;که&zwnj;وێته سه&zwnj;رت جێبه&zwnj;جێی بکه&zwnj;، هه&zwnj;روه&zwnj;ها مافی هه&zwnj;ژاران و ڕێبوارانیش فه&zwnj;رامۆش مه&zwnj;که&zwnj;، ئه&zwnj;و ڕه&zwnj;فتارانه چاکترو په&zwnj;سه&zwnj;ندتره بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی که مه&zwnj;به&zwnj;ستیان ڕه&zwnj;زامه&zwnj;ندی خوایه&zwnj;، بێگومان هه&zwnj;ر ئه&zwnj;وانه&zwnj;ش سه&zwnj;رفرازو سه&zwnj;رکه&zwnj;وتوون.';
$t[39] = 'هه&zwnj;ر پاره&zwnj;و سامانێکتان به سوو دابێت به خه&zwnj;ڵکی بۆ ئه&zwnj;وه&zwnj;ی زیاد بکات له&zwnj;ناو سامانه&zwnj;که&zwnj;یاندا، ئه&zwnj;وه به&zwnj;ره&zwnj;که&zwnj;ت ناکات و پاداشتی نابێت لای خوا (به&zwnj;ڵکو موحاسه&zwnj;به&zwnj;شی له دوایه&zwnj;)، ئه&zwnj;وه&zwnj;ش به زه&zwnj;کات داوتانه&zwnj;و مه&zwnj;به&zwnj;ستان ڕه&zwnj;زامه&zwnj;ندی خوایه&zwnj;، ئا ئه&zwnj;و جۆره که&zwnj;سانه پاداشتیان چه&zwnj;ند به&zwnj;رامبه&zwnj;ر کردۆته&zwnj;وه.';
$t[40] = 'خوا ئه&zwnj;و زاته&zwnj;یه که دروستی کردوون و پاشان ڕزق و ڕۆزی پێبه&zwnj;خشیون (ئاوو هه&zwnj;واو خۆرو خاك و هۆکاره&zwnj;کانی به&zwnj;ده&zwnj;ست هێنانی سامان به&zwnj;شێکن له به&zwnj;خششه&zwnj;کانی) له&zwnj;دوایدا ده&zwnj;تانمرێنێت، پاشان زیندووتان ده&zwnj;کاته&zwnj;وه&zwnj;، ئایا هیچ کام له&zwnj;وانه&zwnj;ی که کردوتانن به هاوبه&zwnj;شی خوا، شتی وایان له ده&zwnj;ست دێت؟! پاکی و بڵندی بۆ ئه&zwnj;و زاته&zwnj;یه له&zwnj;وه&zwnj;ی ئه&zwnj;وانه ده&zwnj;یکه&zwnj;ن به هاوبه&zwnj;شی.';
$t[41] = 'تاوان و خراپه ده&zwnj;رکه&zwnj;وتووه&zwnj;و هه&zwnj;موو سه&zwnj;ر زه&zwnj;وی و ده&zwnj;ریاکانیشی گرتۆته&zwnj;وه&zwnj;، به&zwnj;هۆی ئه&zwnj;و تاوان و گوناهانه&zwnj;وه که خه&zwnj;ڵکی ده&zwnj;ستیان داوه&zwnj;تێ، سه&zwnj;رئه&zwnj;نجام ده&zwnj;بێت تاڵاوی هه&zwnj;ندێك له کرده&zwnj;وه&zwnj;کانیان بچێژن (که به&zwnj;رپابوونی جه&zwnj;نگ و کاره&zwnj;ساته سروشتیه&zwnj;کانه&zwnj;) بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;ڕێنه&zwnj;وه بۆ ڕێبازی پاکی و خواناسی.';
$t[42] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) پێیان بڵێ: بگه&zwnj;ڕێن به&zwnj;سه&zwnj;ر زه&zwnj;ویدا و ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن و بزانن چۆن بوو سه&zwnj;رئه&zwnj;نجامی پێشینه&zwnj;کان، ئه&zwnj;وانه&zwnj;ی زۆربه&zwnj;یان هاوه&zwnj;ڵگرو بت په&zwnj;رست بوون.';
$t[43] = 'که&zwnj;واته ڕووی خۆت بکه&zwnj;ره ئه&zwnj;م ئاینه پته&zwnj;وو چاك و دامه&zwnj;زراوه&zwnj;، پێش ئه&zwnj;وه&zwnj;ی ڕۆژێك بێت هیچ که&zwnj;س نه&zwnj;توانێت جڵه&zwnj;وی بگرێت و دژایه&zwnj;تی به&zwnj;رنامه&zwnj;ی خوا بکات، ئه&zwnj;و ڕۆژه خه&zwnj;ڵکی هه&zwnj;موو له یه&zwnj;ك جیاده&zwnj;بنه&zwnj;وه (به&zwnj;هه&zwnj;شتیان بۆ به&zwnj;هه&zwnj;شت به ڕێ ده&zwnj;کرێن، دۆزه&zwnj;خیه&zwnj;کانیش بۆ دۆزه&zwnj;خ).';
$t[44] = 'ئه&zwnj;وه&zwnj;ی کافر بێت ئه&zwnj;وه کافر بوونه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ری ده&zwnj;که&zwnj;وێت، ئه&zwnj;وانه&zwnj;ش که کارو کرده&zwnj;وه&zwnj;ی چاك ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، ئه&zwnj;وه ڕێگه بۆ خۆیان خۆش ده&zwnj;که&zwnj;ن (به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت).';
$t[45] = 'تا خوا زۆر به زیاده&zwnj;وه پاداشتی ئه&zwnj;وانه بداته&zwnj;وه که ئیمانیان هێناوه&zwnj;و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و زاته کافرانی خۆش ناوێت.';
$t[46] = 'له به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کانی (گه&zwnj;وره&zwnj;یی په&zwnj;روه&zwnj;ردگار) ئه&zwnj;وه&zwnj;یه چه&zwnj;نده&zwnj;ها جۆر با ده&zwnj;نێرێت وه&zwnj;ك مژده به&zwnj;خشێك (له کاتێکدا که هه&zwnj;ڵگری هه&zwnj;ورو هه&zwnj;ڵمی ئاوه&zwnj;)، تا ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خۆیتان پێ بچێژێت، تا که&zwnj;شتیه&zwnj;کان به ده&zwnj;ریادا به فه&zwnj;رمانی ئه&zwnj;و هاتوچۆ بکه&zwnj;ن و تا له به&zwnj;خششه&zwnj;کانی به&zwnj;هره&zwnj;وه&zwnj;ر ببن، بۆ ئه&zwnj;وه&zwnj;ش سوپاسگوزاری بکه&zwnj;ن.';
$t[47] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی پێش تۆ (ئه&zwnj;ی محمد صلی الله علیه وسلم) پێغه&zwnj;مبه&zwnj;رانێکمان ڕه&zwnj;وانه کردووه&zwnj;، بۆ سه&zwnj;ر هۆزو گه&zwnj;له&zwnj;کانیان، به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی زۆریان بۆ هێنان، به&zwnj;ڵام زۆریان باوه&zwnj;ڕیان نه&zwnj;هێنا، بۆیه ئێمه&zwnj;ش تۆڵه&zwnj;مان سه&zwnj;ند له&zwnj;وانه&zwnj;ی که تاوانیان کرد، ئه&zwnj;وسا ئیتر سه&zwnj;ر خستنی ئیمانداران به ئه&zwnj;رکی سه&zwnj;رشانی خۆمان زانی و سه&zwnj;رکه&zwnj;وتنمان پێ به&zwnj;خشین.';
$t[48] = 'خوا ئه&zwnj;و زاته&zwnj;یه که چه&zwnj;نده&zwnj;ها جۆر - با - ده&zwnj;نێرێت، ئه&zwnj;و - با - یه هه&zwnj;ور هه&zwnj;ڵده&zwnj;گرێت و له&zwnj;گه&zwnj;ڵ خۆیدا ڕاپێچی ده&zwnj;کات، جا خوا په&zwnj;خشی ده&zwnj;کاته&zwnj;وه به ئاسماندا به&zwnj;هه&zwnj;ر شێوازو شێوه&zwnj;یه&zwnj;ك که بیه&zwnj;وێت، هه&zwnj;وره&zwnj;کان پارچه پارچه ده&zwnj;کات، ئینجا ده&zwnj;بینیت باران له نێوانیاندا ده&zwnj;رده&zwnj;چێت و داده&zwnj;به&zwnj;زێته سه&zwnj;ر زه&zwnj;وی، جا کاتێك بارانه&zwnj;که&zwnj;ی باراند بۆ هه&zwnj;ر تاقمێك له به&zwnj;نده&zwnj;کانی که بیه&zwnj;وێت، ئه&zwnj;وانیش یه&zwnj;کسه&zwnj;ر خۆشحاڵ و دڵخۆش ده&zwnj;بن و مژده ده&zwnj;ده&zwnj;ن به یه&zwnj;کتری...';
$t[49] = 'به&zwnj;ڕاستی پێش ئه&zwnj;وه&zwnj;ی دابارێته سه&zwnj;ریان، زۆر نائومێدو بێ هیوا بوون.';
$t[50] = 'جا ئیتر سه&zwnj;رنج بده&zwnj;و ته&zwnj;ماشای چه&zwnj;نده&zwnj;ها ئاسه&zwnj;واری ڕه&zwnj;حمه&zwnj;تی خوا بکه&zwnj;، که چۆن زه&zwnj;وی زیندوو ده&zwnj;کاته&zwnj;وه دوای مردنی، بێگومان ئه&zwnj;و زاته&zwnj;ی که ئه&zwnj;و کارانه ئه&zwnj;نجام ده&zwnj;دات، مردووان زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;و هه&zwnj;ر ئه&zwnj;و زاته ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;، (مردووانیش وه&zwnj;ك ڕوه&zwnj;ك له کاتی خۆیدا له زه&zwnj;وی ده&zwnj;رده&zwnj;هێنێته&zwnj;وه بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[51] = 'سوێند به خوا خۆ ئه&zwnj;گه&zwnj;ر ڕه&zwnj;شه&zwnj;بایه&zwnj;کی زه&zwnj;ره&zwnj;رمه&zwnj;ند بنێرن و ببینن کشتوکاڵ و باخه&zwnj;کانیانی زه&zwnj;رد هه&zwnj;ڵگه&zwnj;ڕان، ئه&zwnj;وه ئه&zwnj;وانه ئیتر ده&zwnj;ست ده&zwnj;که&zwnj;ن به ناشوکری و له دین وه&zwnj;رده&zwnj;گه&zwnj;ڕێن.';
$t[52] = 'جا به&zwnj;ڕاستی تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ناتوانیت ده&zwnj;نگی خۆت، واته&zwnj;: بانگه&zwnj;وازه&zwnj;که&zwnj;ت، به مردووه&zwnj;کان ڕابگه&zwnj;یه&zwnj;نیت، هه&zwnj;روه&zwnj;ها ناتوانیت که&zwnj;ڕو گوێ گرانه&zwnj;کانی لێ تێبگه&zwnj;یه&zwnj;نیت (کافران وه&zwnj;ك مردوو، وه&zwnj;ك که&zwnj;ڕن)، کاتێك ڕوو وه&zwnj;رده&zwnj;گێڕن و پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و بایه&zwnj;خ به بانگه&zwnj;وازه&zwnj;که&zwnj;ت ناده&zwnj;ن.';
$t[53] = 'هه&zwnj;روه&zwnj;ها ناتوانیت ڕێنموویی کوێرو نابینایان بکه&zwnj;یت (ئه&zwnj;وانه&zwnj;ی دڵیان داخستووه&zwnj;و نایانه&zwnj;وێت تێبینی بکه&zwnj;ن) تۆ ناتوانیت له گومڕایی ڕزگاریان بکه&zwnj;یت، به&zwnj;ڵکو تۆ ته&zwnj;نها ده&zwnj;توانیت ئایه&zwnj;ته&zwnj;کانی قورئانی ئێمه بده&zwnj;ی به&zwnj;گوێی ئه&zwnj;و که&zwnj;سانه&zwnj;دا باوه&zwnj;ڕیان پێیه&zwnj;تی و هیچی تر، چونکه ئه&zwnj;وانه هه&zwnj;میشه ملکه&zwnj;چی فه&zwnj;رمانی خوان.';
$t[54] = 'خوا ئه&zwnj;و زاته&zwnj;یه ئێوه&zwnj;ی به لاوازی دروست کردووه (سه&zwnj;ره&zwnj;تا نوتفه&zwnj;یه&zwnj;کی لاواز، دوایی وه&zwnj;ك دیل له سکی دایکدا، پاشان هه&zwnj;ر منداڵی لاوازیت تا ده&zwnj;گه&zwnj;یته ته&zwnj;مه&zwnj;نی لاوی)، ئه&zwnj;وسا خوا هێزو ده&zwnj;سه&zwnj;ڵاتێکت پێ ده&zwnj;به&zwnj;خشێت، دوای هێزو توانایی دووباره نۆره&zwnj;ی لاوازی و پیری دێت، بێگومان ئه&zwnj;و خوایه هه&zwnj;رچی بوێت دروستی ده&zwnj;کات (ئه&zwnj;م هێزو بێ هێزیه به&zwnj;شێکه له ویستی ئه&zwnj;و زاته)، ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره زاناو به توانایه&zwnj;.';
$t[55] = 'ئه&zwnj;و ڕۆژه&zwnj;ی که قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;بێت تاوانباران سوێند ده&zwnj;خۆن (که&zwnj;: له جیهانی به&zwnj;رزه&zwnj;خدا، یان له ژیانی دنیادا) ته&zwnj;نها کاژێرێك ماونه&zwnj;ته&zwnj;وه&zwnj;، ئا ئه&zwnj;وانه کاتی خۆی له ڕاستی لاده&zwnj;دران.';
$t[56] = '(له ڕۆژی قیامه&zwnj;تدا که تاوانباران سه&zwnj;ریان لێشێواوه&zwnj;و سوێندی درۆ ده&zwnj;خۆن) ئه&zwnj;وانه&zwnj;ی زانیاری و ئیمانیان پێبه&zwnj;خشرابوو ده&zwnj;ڵێن: سوێند به&zwnj;خوا بێگومان ئێوه به&zwnj;ڕاده&zwnj;ی ئه&zwnj;وه&zwnj;ی خوا بڕیاری له&zwnj;سه&zwnj;ر بووه ماونه&zwnj;ته&zwnj;وه (له دنیاداو له جیهانی به&zwnj;رزه&zwnj;خیشدا) تا ڕۆژی زیندووبوونه&zwnj;وه&zwnj;، جا ئه&zwnj;مه ڕۆژی زیندووبوونه&zwnj;وه&zwnj;که&zwnj;یه&zwnj;، به&zwnj;ڵام ئێوه به&zwnj;م ڕاستیه&zwnj;تان نه&zwnj;ده&zwnj;زانی و بڕواتان پێی نه&zwnj;بوو.';
$t[57] = 'جا له&zwnj;و ڕۆژه&zwnj;دا تکاو پاڕانه&zwnj;وه&zwnj;و بڕو بیانووی ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان کردووه&zwnj;، هیچ سوودێکی نیه بۆیان، به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ك خوا لێیان ڕازی نابێت و ئه&zwnj;وانه&zwnj;ش داوای لابردنی ڕق و قینی خوایان لێ ناکرێت، به&zwnj;هۆی تۆبه&zwnj;وه خۆیان ڕزگار بکه&zwnj;ن.';
$t[58] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه له&zwnj;م قورئانه&zwnj;دا له هه&zwnj;موو بابه&zwnj;تێك نموونه&zwnj;مان بۆ خه&zwnj;ڵکی هێناوه&zwnj;ته&zwnj;وه&zwnj;، که&zwnj;چی هه&zwnj;ر ئایه&zwnj;تێکیشیان بۆ بهێنیت و به&zwnj;سه&zwnj;ریاندا بخوێنیته&zwnj;وه&zwnj;، ئه&zwnj;وه بێگومان ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن ده&zwnj;ڵێن: ئێوه ته&zwnj;نها پێشێلکه&zwnj;ری حه&zwnj;قن و هیچی تر نین!!';
$t[59] = 'ئا به&zwnj;و شێوه&zwnj;یه خوا مۆر ده&zwnj;نێت به&zwnj;سه&zwnj;ر دڵی ئه&zwnj;وانه&zwnj;دا که نازانن و به&zwnj;شوێن حه&zwnj;ق و ڕاستیدا ناگه&zwnj;ڕێن، (شوێن ڕاستی و حه&zwnj;قیقه&zwnj;ت ناکه&zwnj;ون).';
$t[60] = 'جا (ئه&zwnj;ی محمد صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار) دان به&zwnj;خۆدا بگره&zwnj;و ئارام بگره&zwnj;، چونکه بێگومان وه&zwnj;عدو به&zwnj;ڵێنی خوا حه&zwnj;ق و ڕاسته و هه&zwnj;ر دێته&zwnj;دی، ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان نیه بێ ئارامت نه&zwnj;که&zwnj;ن و ساردت نه&zwnj;که&zwnj;نه&zwnj;وه&zwnj;.';
?>