$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقرة) بده&zwnj;.';
$t[2] = 'ئه&zwnj;وه چه&zwnj;نده&zwnj;ها ئایه&zwnj;ت و فه&zwnj;رمانێکن له قورئانی پڕ له حیکمه&zwnj;ت و دانایی.';
$t[3] = 'ئه&zwnj;م قورئانه هیدایه&zwnj;ت به&zwnj;خش و ڕێنموویی به&zwnj;خش و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانیه بۆ چاکه&zwnj;کاران...';
$t[4] = 'ئه&zwnj;وانه&zwnj;ی که نوێژه&zwnj;کانیان به چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، زه&zwnj;کاتیش (له ماڵ و سامانیان) ده&zwnj;رده&zwnj;که&zwnj;ن، دڵنیاش ده&zwnj;بن به&zwnj;هاتنه دیی ڕۆژی قیامه&zwnj;ت (خۆیانی بۆ ئاماده ده&zwnj;که&zwnj;ن).';
$t[5] = 'ئا ئه&zwnj;وانه له&zwnj;سه&zwnj;ر ڕێنموویی و ڕێبازی په&zwnj;روه&zwnj;ردگاریانن، ئا ئه&zwnj;وانه&zwnj;شن ڕزگار و سه&zwnj;رفراز.';
$t[6] = 'هه&zwnj;ندێک له خه&zwnj;ڵکی هه&zwnj;ن قسه&zwnj;ی بێ خێر و بێ مانا ده&zwnj;کڕن (وه&zwnj;ک گۆرانیه ناپوخته&zwnj;کان، کتێب و شریته بێ خێره&zwnj;کان... هتد) تا خۆی و که&zwnj;سانی تریش له ڕێبازی خوا وێڵ بکات، بێ ئه&zwnj;وه&zwnj;ی هه&zwnj;ست بکات به&zwnj;مه&zwnj;ترسی کاره&zwnj;که&zwnj;ی و بزانێت چ که&zwnj;تنێک ده&zwnj;کات به خۆی و خه&zwnj;ڵکی و بۆیه ئه&zwnj;و شته بێ نرخو خێرانه ده&zwnj;کرێت تا گاڵته به په&zwnj;یام و ڕێبازی خوا بکات، ئا ئه&zwnj;وانه سزا و تۆڵه&zwnj;یه&zwnj;کی ڕیسواکه&zwnj;رو ئابڕووبه&zwnj;ر چاوه&zwnj;ڕێیانه&zwnj;.';
$t[7] = 'هه&zwnj;ر کاتێکیش ئایه&zwnj;ته&zwnj;کانی ئێمه&zwnj;ی به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنرێته&zwnj;وه&zwnj;، فیز ده&zwnj;کات و فوو ده&zwnj;کاته خۆی و پشت هه&zwnj;ڵده&zwnj;کات، هه&zwnj;روه&zwnj;ک نه&zwnj;یبیستبێت، هه&zwnj;روه&zwnj;ک له گوێیدا قورسی و گرانیی هه&zwnj;بێت، که&zwnj;وابوو مژده بده به&zwnj;و جۆره که&zwnj;سانه به&zwnj;وه&zwnj;ی سزای به ئێش چاوه&zwnj;ڕێیانه&zwnj;!!';
$t[8] = 'به&zwnj;ڕاستی ئه&zwnj;وانه ئیمانیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجامداوه&zwnj;، چه&zwnj;نده&zwnj;ها باخچه&zwnj;ی به&zwnj;هه&zwnj;شتی پڕ له نازو نیعمه&zwnj;ت بۆیان ئاماده&zwnj;یه&zwnj;...';
$t[9] = 'هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی نه&zwnj;بڕاوه و نه&zwnj;مریی تیایدا، ئه&zwnj;و به&zwnj;ڵێنه&zwnj;ی خوا پیی داوون، وه&zwnj;عده و به&zwnj;ڵێنێکی حه&zwnj;ق و ڕاسته و هه&zwnj;ر دێته دی، به&zwnj;ڕاستی ئه&zwnj;و زاته به ده&zwnj;سه&zwnj;ڵات و دانایه&zwnj;.';
$t[10] = 'ئاسمانه&zwnj;کانی دروست کردووه به&zwnj;بێ کۆڵه&zwnj;که به&zwnj;چاوی خۆتان ده&zwnj;یانبینن، له زه&zwnj;ویشدا کێوه&zwnj;کانی دامه&zwnj;زراندوه که له&zwnj;نگه&zwnj;ری زه&zwnj;ویان ڕاگرتووه و (چه&zwnj;نده&zwnj;ها سوودی تریان هه&zwnj;یه&zwnj;) ئه&zwnj;وه&zwnj;کو ژێره و ژوورببن و لارببنه&zwnj;وه&zwnj;، جۆره&zwnj;ها زینده&zwnj;وه&zwnj;ر و گیانله&zwnj;به&zwnj;ریشی تیادا بڵاوکردۆته&zwnj;وه&zwnj;، (هه&zwnj;روه&zwnj;ها ده&zwnj;فه&zwnj;رموێت): له ئاسمانیشه&zwnj;وه ئاومان دابه&zwnj;زاند، جۆره&zwnj;ها وه&zwnj;ک و گژوگیای هاو جووتی به&zwnj;سوود و به که&zwnj;ڵک و جوان و ڕازاوه&zwnj;مان له زه&zwnj;ویدا پێ ڕواند.';
$t[11] = 'ئا ئه&zwnj;مه&zwnj;یه دروستکراوانی خوا، وه&zwnj;رن نیشانم بده&zwnj;ن بزانم ئه&zwnj;وانی ترجگه له&zwnj;وزاته&zwnj;، چیان دروست کردووه&zwnj;، نه&zwnj;خێر هیچیان دروست نه&zwnj;کردووه&zwnj;، به&zwnj;ڵکو سته&zwnj;مکاران هه&zwnj;میشه له گومڕاییه&zwnj;کی ئاشکرادان.';
$t[12] = 'سوێند به خوا به ڕاستی ئێمه حیکمه&zwnj;ت و دانایمان به &lrm;_&lrm;&lrm;لوقمان_ به&zwnj;خشی، به&zwnj;م شێوه&zwnj;یه (پێمان وت) شوکرانه بژێرو سوپاسگوزاریه&zwnj;که&zwnj;ی هه&zwnj;ر بۆ خۆی ده&zwnj;گه&zwnj;ڕێته&zwnj;وه، ئه&zwnj;وه&zwnj;ش سوپاسی خوای نه&zwnj;کرد، ئه&zwnj;وه بێگومان خوا بێ نیازه&zwnj;و سوپاسکراوه (پێویستی به&zwnj;سوپاسی که&zwnj;س نیه&zwnj;).';
$t[13] = 'جا لوقمان به کوڕه&zwnj;که&zwnj;ی خۆی وت له کاتێکدا که ئامۆژگاری ده&zwnj;کرد: کوڕه شیرینه&zwnj;که&zwnj;م; نه&zwnj;که&zwnj;یت شتێک یان که&zwnj;سێک به هاوتاو هاوبه&zwnj;شی خوا بڕیار بده&zwnj;یت، چونکه به&zwnj;ڕاستی هاوتاو هاوبه&zwnj;ش بڕیاردان سته&zwnj;مێکی زۆر گه&zwnj;وره و ناڕه&zwnj;وایه&zwnj;.';
$t[14] = '(لێره&zwnj;دا خوا ئامۆژگاریه&zwnj;کان له ده&zwnj;می لوقمان ده&zwnj;فه&zwnj;رموێت): ئێمه فه&zwnj;رمانمانداوه به ئاده&zwnj;میزاد به چاک ڕه&zwnj;فتاری له&zwnj;گه&zwnj;ڵ دایک و باوکیدا، دایکی به ئێش و ئازارو لاوازی و بێتاقه&zwnj;تیه&zwnj;کی زۆره&zwnj;وه&zwnj;، (به پێستی سکی نۆ مانگ) هه&zwnj;ڵی گرتووه&zwnj;، بڕینه&zwnj;وه&zwnj;شی له دوای دوو ساڵه&zwnj;، فه&zwnj;رمانمان پێدا که ئه&zwnj;ی ئینسان سوپاسگوزاری منیش و دایک و باوکیشت به&zwnj;، دڵنیابن که گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای منه&zwnj;.';
$t[15] = 'ئه&zwnj;گه&zwnj;ر دایک و باوکت کۆششیان له&zwnj;گه&zwnj;ڵتا کرد، تا به&zwnj;بێ هیچ به&zwnj;ڵگه و زانیاریه&zwnj;ک هاوه&zwnj;ك بۆ من قسه&zwnj;یان مه&zwnj;که&zwnj;، له دنیاشدا هاوڕێ و هاوه&zwnj;ڵ و خزمه&zwnj;تگوزارێکی چاک به بۆیان له هه&zwnj;موو بارو دۆخێکدا، شوێنی ڕێبازی ئه&zwnj;وانه بکه&zwnj;وه که بۆلای من گه&zwnj;ڕاونه&zwnj;ته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;وداو گه&zwnj;ڕانه&zwnj;وه&zwnj;تان هه&zwnj;ر بۆ لای منه&zwnj;، جا هه&zwnj;واڵتان ده&zwnj;ده&zwnj;مێ به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ده&zwnj;تانکرد.';
$t[16] = 'پاشان له&zwnj;سه&zwnj;ر زاری لوقمان ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;ی کوڕی ئازیزم: به&zwnj;ڕاستی ئه&zwnj;گه&zwnj;ر گوناهێک به قه&zwnj;ده&zwnj;ر تۆوی خه&zwnj;رته&zwnj;له&zwnj;یه&zwnj;ک (که زۆر ورده&zwnj;) هه&zwnj;بێت و له&zwnj;ناو به&zwnj;ردێکدا له ئاسمانه&zwnj;کان یان له&zwnj;و زه&zwnj;ویدا حه&zwnj;شاردرا بێت، (لای خوا شاراوه نیه&zwnj;) و ده&zwnj;یهێنێته مه&zwnj;یدان، چونکه به&zwnj;ڕاستی خوا هیچ شتێکی لێ شاراوه نیه و زۆریش ئاگایه&zwnj;.';
$t[17] = 'کوڕی شیرینم نوێژه&zwnj;کانت به چاکی ئه&zwnj;نجام بده&zwnj;، فه&zwnj;رمان به چاکه بده&zwnj;، قه&zwnj;ده&zwnj;غه له گوناهو خراپه بکه&zwnj;، به ئارامبه له به&zwnj;رامبه&zwnj;ر ئه&zwnj;و ناخۆشیانه&zwnj;ی که تووشت ده&zwnj;بێت، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که باسکران هه&zwnj;ندێکن له&zwnj;وانه&zwnj;ی هه&zwnj;ر ده&zwnj;بێت بکرێن.';
$t[18] = 'هه&zwnj;وره&zwnj;ها ڕووت وه&zwnj;رمه&zwnj;گێڕه له خه&zwnj;ڵكی و (لووت به&zwnj;رز مه&zwnj;به و به&zwnj;چاوی سووک سه&zwnj;یریان مه&zwnj;که&zwnj;)، به&zwnj;سه&zwnj;ر زه&zwnj;ویدا به&zwnj;ناز و فیزه&zwnj;وه مه&zwnj;ڕۆ، به&zwnj;ڕاستی خوا ئه&zwnj;و که&zwnj;سانه&zwnj;ی خۆش ناوێت که خۆهه&zwnj;ڵکێش و فه&zwnj;خر فرۆش و فیزاوین.';
$t[19] = 'هه&zwnj;وڵ بده له ڕۆیشتنتدا مام ناوه&zwnj;ندیانه بڕۆ، به&zwnj;ره&zwnj;و ئامانجێکی چاک هه&zwnj;نگا و بنێ، به&zwnj;ده&zwnj;نگی هێواش و له&zwnj;بار گفتوگۆ بکه و بدوێ، چونکه به&zwnj;ڕاستی بێزارترین و ناخۆشترینی ده&zwnj;نگه&zwnj;کان ده&zwnj;نگی گوێ درێژه&zwnj;کانه&zwnj;.';
$t[20] = 'ئایا سه&zwnj;رنجتان نه&zwnj;داوه که به&zwnj;ڕاستی هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه هه&zwnj;ر هه&zwnj;مووی خوا بۆ خزمه&zwnj;تی ئێوه&zwnj;ی دروست کردووه و ملکه&zwnj;چی کردووه&zwnj;، هه&zwnj;روه&zwnj;ها به&zwnj;فراوانی نازو نیعمه&zwnj;تی ئاشكراو په&zwnj;نهانی به&zwnj;سه&zwnj;ردا ڕژاندون، (له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا) هه&zwnj;ندێک له خه&zwnj;ڵکی موجاده&zwnj;له و گفتوگۆی نابه&zwnj;جێ ده&zwnj;رباره&zwnj;ی خوا وبه&zwnj;رنامه&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن، بێ هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کی زانستی و ڕێنموویه&zwnj;کی پێغه&zwnj;مبه&zwnj;ران و کتێببێکی ڕوونکه&zwnj;ره&zwnj;وه&zwnj;ی خوایی.';
$t[21] = 'کاتێكیش پێیان بوترێت شوێنی ئه&zwnj;و به&zwnj;رنامه&zwnj;یه بکه&zwnj;ون که خوا ناردویه&zwnj;تی، ئه&zwnj;وه هه&zwnj;ر شوێنی ئه&zwnj;و شتانه ده&zwnj;که&zwnj;وین که باو و باپیران و مامۆستایانمان په&zwnj;یڕه&zwnj;وییان لی کردووه&zwnj;، ئایا ئه&zwnj;گه&zwnj;ر شه&zwnj;یتانیش بانگی کردبن بۆ سزای داگیرساوی دۆزه&zwnj;خ، هه&zwnj;ر په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن؟!';
$t[22] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی ڕوو بکاته په&zwnj;روه&zwnj;ردگاری و کاره&zwnj;کانی هه&zwnj;مووی بگێڕێته&zwnj;وه بۆ لای ئه&zwnj;و، له کاتێکدا ئاده&zwnj;میزادێکی چاکه&zwnj;خوازه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی مانای وایه که ده&zwnj;ستی به به هێزترین کارێکیش هه&zwnj;ر بۆ لای خوا ده&zwnj;گه&zwnj;ڕێته&zwnj;وه&zwnj;.';
$t[23] = 'ئه&zwnj;وه&zwnj;ش کافر بووه&zwnj;، ئه&zwnj;وه کافربوونه&zwnj;که&zwnj;ی، خه&zwnj;فه&zwnj;تبارت نه&zwnj;کات، گه&zwnj;ڕانه&zwnj;وه&zwnj;یان هه&zwnj;ر بۆ لای ئێمه&zwnj;یه&zwnj;، جا ئاگاداریان ده&zwnj;که&zwnj;ینه&zwnj;وه به&zwnj;وه&zwnj;ی که کردوویانه&zwnj;، بێگومان خوا ئاگاداره به هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که له سینه&zwnj;و دڵ و ده&zwnj;روونه&zwnj;کاندا حه&zwnj;شاردراون.';
$t[24] = 'که&zwnj;مێک ماوه&zwnj;یان ده&zwnj;ده&zwnj;ین، تا تاوێک ڕابوێرن، پاشان ناچاریان ده&zwnj;کین که بچنه ناو سزای توندو سه&zwnj;خته&zwnj;وه&zwnj;.';
$t[25] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر پرسیار له خوانه&zwnj;ناسان بکه&zwnj;یت: کێ ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;، ئه&zwnj;وه به دڵنیاییه&zwnj;وه ده&zwnj;ڵێن: (الله&zwnj;)، دروستی کردوون، تۆش ئه&zwnj;ی محمد (صلی الله علیه وسلم) بڵێ: سوپاس و ستایش بۆ خوا (له&zwnj;سه&zwnj;ر وتنی ڕاستی)، نه&zwnj;خێر وانییه&zwnj;، ئه&zwnj;و وه&zwnj;ڵامه له قه&zwnj;ناعه&zwnj;ته&zwnj;وه هه&zwnj;ڵنه&zwnj;قوڵاوه&zwnj;، به&zwnj;ڵکو زۆربه&zwnj;یان نازانن (له خۆیانه&zwnj;وه قسه ده&zwnj;که&zwnj;ن).';
$t[26] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، هه&zwnj;ر خوا خاوه&zwnj;نیانه&zwnj;، به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;و خوایه خۆی بێ نیازه و سوپاسگوزاریی هه&zwnj;ر شایسته&zwnj;ی ئه&zwnj;وه (ئه&zwnj;گه&zwnj;ر خه&zwnj;ڵكی به&zwnj;وه&zwnj;فا و به ئه&zwnj;مه&zwnj;ک بن).';
$t[27] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی هه&zwnj;رچی دره&zwnj;خت هه&zwnj;یه له زه&zwnj;ویدا ببنه پێنووس، ده&zwnj;ریاش چه&zwnj;نده&zwnj;یه ببێته حه&zwnj;وت ئه&zwnj;وه&zwnj;نده و (ببێته مره&zwnj;که&zwnj;ف و زانستی ونهێنی دروستکاوه&zwnj;کانی خوا بنووسنه&zwnj;وه&zwnj;)، ئه&zwnj;وه هێشتا زانست و زانیاری و فه&zwnj;رمانه&zwnj;کانی خوا هه&zwnj;ر ته&zwnj;واو نابێت، به&zwnj;ڕاستی خوای گه&zwnj;وره به&zwnj;ده&zwnj;سه&zwnj;ڵاته و دانایه&zwnj;.';
$t[28] = 'دروستکردن و به دیهێنانی هه&zwnj;مووتان و زیندو کردنه&zwnj;وه&zwnj;تان، ته&zwnj;نها وه&zwnj;ک دروستکردن و زیندووکردنه&zwnj;وه&zwnj;ی تاکه&zwnj;که&zwnj;سێک وایه&zwnj;، به&zwnj;ڕاستی خوا بیسه&zwnj;ره و بینایه&zwnj;.';
$t[29] = 'ئایا سه&zwnj;رنجت نه&zwnj;داوه که به&zwnj;ڕاستی خوا شه&zwnj;و تێهه&zwnj;ڵکێشی ڕۆژ ده&zwnj;کات و ڕۆژیش تێهه&zwnj;ڵکێشی شه&zwnj;و ده&zwnj;کات، خۆر و مانگی بۆ ته&zwnj;رخان و ملکه&zwnj;چ کردون. هه&zwnj;ریه&zwnj;که&zwnj;یان تاکاتێکی دیاریکراو له سوڕانه&zwnj;وه&zwnj;یاندا به&zwnj;رده&zwnj;وام ده&zwnj;بن، به&zwnj;ڕاستی خوا به&zwnj;کارو کرده&zwnj;وه&zwnj;تان زۆر ئاگاداره&zwnj;.';
$t[30] = 'ئه&zwnj;و شتانه ده&zwnj;ریده&zwnj;خه&zwnj;ن که&zwnj;: بێگومان هه&zwnj;ر خوا خۆی حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، به&zwnj;ڕاستی هه&zwnj;رچی خه&zwnj;ڵکی له&zwnj;باتی و ناحه&zwnj;قیه&zwnj;، دڵنیا بن خوا هه&zwnj;ر خۆی هه&zwnj;میشه به&zwnj;رز و پایه بڵندو گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[31] = 'ئایا سه&zwnj;رنجت نه&zwnj;داوه که به&zwnj;ڕاستی که&zwnj;شتیه&zwnj;کان به ده&zwnj;ریادا هاتوچۆ دکه&zwnj;ن و ناز و نیعمه&zwnj;تی خوا دهێنن و ده&zwnj;یبه&zwnj;ن و ده&zwnj;سه&zwnj;ڵاتی خوا ده&zwnj;خه&zwnj;نه ڕوو، به&zwnj;ڕاستی ئا له&zwnj;و شتانه دا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;ی زۆر دان به خۆدا ده&zwnj;گرن و زۆر سوپاسگوزارن.';
$t[32] = 'کاتێکیش له ده&zwnj;ریادا شه&zwnj;پۆلێک ئابڵوقه&zwnj;ی هاوه&zwnj;ڵگه&zwnj;رو کاره&zwnj;کان بدات، وه&zwnj;ک کێوه&zwnj;کان گه&zwnj;وره بێت، ئه&zwnj;وه هاناو هاوار هه&zwnj;ر بۆ خوا ده&zwnj;به&zwnj;ن زۆر به دڵسۆزانه ملکه&zwnj;چن بۆ خوا، جا کاتێک فریایان ده&zwnj;که&zwnj;وێت و ڕزگاریان ده&zwnj;کات و ده&zwnj;یانگه&zwnj;یه&zwnj;نێته وشکانی، جا هه&zwnj;یانه به&zwnj;رده&zwnj;وام ده&zwnj;بێت له سه&zwnj;ر ڕێبازی ئیمان و ده&zwnj;مێنێته&zwnj;وه له&zwnj;سه&zwnj;ر یه&zwnj;کتاناسینی خوای میهره&zwnj;بان، (هه&zwnj;شیانه ڕێگه&zwnj;ی نادروستی خۆی ده&zwnj;گرێته&zwnj;وه&zwnj;)، جا که&zwnj;س له ئایات و به&zwnj;ڵگه&zwnj;کانی ئێمه نکول و په&zwnj;یمان شکێنی ناکت جگه له زۆردار و ناپاک و سپڵه&zwnj;.';
$t[33] = 'ئه&zwnj;ی خه&zwnj;ڵکینه له په&zwnj;روه&zwnj;ردگارتان بترسن (خۆتان له خه&zwnj;شم و قینی بپارێزن) و له ڕۆژێک بترسن که باوک فریای ڕۆڵه&zwnj;ی خۆی ناکه&zwnj;وێت، ڕۆڵه&zwnj;یش هیچی له ده&zwnj;ست نایه&zwnj;ت که بۆ باوکی ئه&zwnj;نجامی بدات، به&zwnj;ڕاستی به&zwnj;ڵێنی خوا به به&zwnj;رپابوونی قیامه&zwnj;ت، حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، که&zwnj;وابوو وریا بن ژیانی دنیا له خشته&zwnj;یان نه&zwnj;بات، (نه&zwnj;فسی به&zwnj;دکار و شه&zwnj;یتانی به&zwnj;دره&zwnj;فتار) سه&zwnj;رگه&zwnj;ردانتان نه&zwnj;کات و غه&zwnj;ڕاتان نه&zwnj;کات له ڕێبازی خوا.';
$t[34] = 'به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی زانیاری به&zwnj;رپابوونی ڕۆژی قیامه&zwnj;ت ده&zwnj;زانێت، بارانیش هه&zwnj;ر ئه&zwnj;و ده&zwnj;یبارێنێت، ده&zwnj;زانێت چی له سکی دایکاندا هه&zwnj;یه&zwnj;، هیچ که&zwnj;س نازانێت سبه&zwnj;ی چی به&zwnj;سه&zwnj;ردێت و چی ده&zwnj;ست ده&zwnj;که&zwnj;وێت (له&zwnj;به&zwnj;رهه&zwnj;می مادی دنیایی یان له زه&zwnj;خیره و توێشووی قیامه&zwnj;ت)، هیچ که&zwnj;سیش نازانێت له&zwnj;سه&zwnj;ر چ زه&zwnj;ویه&zwnj;ک ده&zwnj;مرێت و کۆتای به&zwnj;ژیانی دێت (هه&zwnj;روه&zwnj;ها کاتی مردن و چۆنیه&zwnj;تیه&zwnj;که&zwnj;یشی شاراوه&zwnj;یه&zwnj;) به&zwnj;ڕاستی خوا به هه&zwnj;موو شتێک زانایه و ئاگاداره&zwnj;.';
?>