<?php
$t = array();
$t[1] = 'سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه، هه&zwnj;ر هه&zwnj;مووی، ئه&zwnj;و خاوه&zwnj;نێتی، ستایش و سوپاس له قیامه&zwnj;تدا (له به&zwnj;هه&zwnj;شتدا) هه&zwnj;ر پێشکه&zwnj;ش به&zwnj;و ده&zwnj;کرێت، ئه&zwnj;و (خوایه&zwnj;) زۆر داناو ئاگایه&zwnj;.';
$t[2] = 'ده&zwnj;زانی چی ده&zwnj;چێ به&zwnj;ناخی زه&zwnj;ویداو چیشی لێ دێته ده&zwnj;ره&zwnj;وه&zwnj;، چی له ئاسمان دێته خواره&zwnj;وه&zwnj;و چیش پیایدا به&zwnj;رزده&zwnj;بێته&zwnj;وه&zwnj;، ئه&zwnj;و زاته زۆر میهره&zwnj;بان و لێخۆشبووه (به&zwnj;نیسبه&zwnj;ت ئیمانداران و ته&zwnj;وبه&zwnj;کارانه&zwnj;وه&zwnj;).';
$t[3] = 'ئه&zwnj;وهانه&zwnj;ی کافرو حه&zwnj;قپۆشن ده&zwnj;ڵێن: قیامه&zwnj;ت هه&zwnj;ر نایه&zwnj;ت و نامانگاتێ، (ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) پێیان بڵێ: (وانیه&zwnj;) ئه&zwnj;رێ!! سوێند به په&zwnj;روه&zwnj;ردگارم هه&zwnj;ر یه&zwnj;خه&zwnj;تان ده&zwnj;گرێت (چونکه ئه&zwnj;و زاته&zwnj;) زانایه به نهێنی و شاراوه&zwnj;کان و (هه&zwnj;ر خۆی ده&zwnj;زانێ که&zwnj;ی به&zwnj;رپای ده&zwnj;کات)، به&zwnj;قه&zwnj;ده&zwnj;ر ئه&zwnj;تۆمێك له ئاسمانه&zwnj;کان و زه&zwnj;ویدا ناتوانێتله&zwnj;و په&zwnj;نهان بێت، وه له&zwnj;وه&zwnj;ش بچووکتر بێت یان گه&zwnj;وره&zwnj;تر (چونکه هه&zwnj;موو شتێك) له کتێبی تایبه&zwnj;تیدا ڕوون و ئاشکرایه (لوح المحفوظ)دا که وه&zwnj;ك ده&zwnj;سگایه&zwnj;کی کۆمپیوته&zwnj;ری وایه&zwnj;، هه&zwnj;موو شتێك تۆماره&zwnj;).';
$t[4] = 'ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی پاداشتی ئه&zwnj;وانه بداته&zwnj;وه که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه&zwnj;ی چاك ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، هه&zwnj;ر بۆ ئه&zwnj;وانیش لێخۆشبوون و ڕزق و ڕۆزوی ناسك و نازدار (له به&zwnj;هه&zwnj;شتی به&zwnj;ریندا) بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[5] = 'ئه&zwnj;وانه&zwnj;ش که هه&zwnj;وڵ و کۆشش ده&zwnj;ده&zwnj;ن تا به&zwnj;ربه&zwnj;ست دروست بکه&zwnj;ن له ڕێگه&zwnj;ی په&zwnj;یامی ئێمه&zwnj;داو خه&zwnj;ڵکی وێڵ ده&zwnj;که&zwnj;ن له به&zwnj;رنامه&zwnj;ی ئێمه&zwnj;، ئه&zwnj;وانه سزاو تۆڵه&zwnj;یه&zwnj;کی زۆر به&zwnj;ئێش و پڕ ئازاریان بۆ ئاماده&zwnj;یه&zwnj;.';
$t[6] = 'ئه&zwnj;وانه&zwnj;ی که زانستی و زانیاریان هه&zwnj;یه (چ زانستییه ئایینیه&zwnj;کان وه&zwnj;ك گاوڕو جوو یان ئه&zwnj;وانه&zwnj;ی ڕۆده&zwnj;چن له&zwnj;زانستیه مادییه&zwnj;کانا) چاك ده&zwnj;زانن که ئه&zwnj;وه&zwnj;ی له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارته&zwnj;وه بۆت ڕه&zwnj;وانه&zwnj;کراوه حه&zwnj;ق و ڕاسته&zwnj;قینه&zwnj;یه&zwnj;، هیدایه&zwnj;تبه&zwnj;خشه&zwnj;و ڕێنمونی به&zwnj;خشه بۆ ڕێبازی په&zwnj;روه&zwnj;ردگاری خاوه&zwnj;ن ده&zwnj;سه&zwnj;ڵات و شایه&zwnj;سته&zwnj;ی سوپاس و ستایش.';
$t[7] = 'بێ بڕواکان (به&zwnj;گاڵته&zwnj;پێکردنه&zwnj;وه&zwnj;) ده&zwnj;ڵێن: ئایا پیاوێکتان پێبناسێنین که هه&zwnj;واڵتان ده&zwnj;داتێ، که کاتێك پارچه پارچه بوون و هه&zwnj;لا هه&zwnj;لا بوون له توێی خاکدا، ئێوه سه&zwnj;رله&zwnj;نوێ دروستده&zwnj;کرێنه&zwnj;وه&zwnj;!!.';
$t[8] = 'ئایا درۆ به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستی، یا شێت بووه&zwnj;؟! (نه&zwnj;خێر..!!) ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان به ڕۆژی دوایی و قیامه&zwnj;ت نیه، له سزای سه&zwnj;ختا ده&zwnj;بێ ژیان به&zwnj;رنه&zwnj;سه&zwnj;ر (چونکه له&zwnj;دنیادا) زۆر گومڕاو سه&zwnj;رلێشێواوبوون.';
$t[9] = 'ئایا ئه&zwnj;وه نابینن چی ده&zwnj;وری داون له ئاسمانه&zwnj;کان و زه&zwnj;ویدا، سه&zwnj;رنجی به&zwnj;رده&zwnj;میان ناده&zwnj;ن؟! ئاوڕێك لو دواوه ناده&zwnj;نه&zwnj;وه&zwnj;؟! ئه&zwnj;گه&zwnj;ر بمانه&zwnj;وێ له ناخی زه&zwnj;ویدا نوقمیان ده&zwnj;که&zwnj;ین، یان پارچه&zwnj;یه&zwnj;کیان له ئاسمانه&zwnj;وه به&zwnj;سه&zwnj;ردا به&zwnj;رده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;، ئا له&zwnj;و تواناو ده&zwnj;سه&zwnj;ڵاتدارییه&zwnj;دا نیشانه&zwnj;ی ڕوون و ئاشکرا هه&zwnj;یه بۆ هه&zwnj;ر به&zwnj;نده&zwnj;یه&zwnj;ك که دڵی لای خوا بێت و هۆشی لای ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;وبێت.';
$t[10] = '(ئێمه&zwnj;) ڕێزێك و بایه&zwnj;خێکی زۆرمان به&zwnj;خشیوه به داود (فه&zwnj;رمانمان داوه که&zwnj;): ئه&zwnj;ی کێوه&zwnj;کان و باڵنده&zwnj;کان (ته&zwnj;سبیحات و سرودی ستایشی) له&zwnj;گه&zwnj;ڵدا بڵێنه&zwnj;وه&zwnj;، هه&zwnj;روه&zwnj;ها ئاسنیشمان بۆ نه&zwnj;رم کردووه (وه&zwnj;کو (معجزة)یه&zwnj;ك تا پێداویستیه&zwnj;کانی به ئاسانی لێ دروست بکات).';
$t[11] = '(هه&zwnj;روه&zwnj;ها فه&zwnj;رمانمان پێدا) که قه&zwnj;ڵغانی ده&zwnj;ره&zwnj;دامان دروست بکات، ئه&zwnj;ڵقه&zwnj;کانی به&zwnj;ڕێک و پێکی ڕێك بخات و له یه&zwnj;کیان تێهه&zwnj;ڵکێش بکات، (نه زۆر ته&zwnj;نك بێت و به&zwnj;رگه نه&zwnj;گرێت، نه زۆر سه&zwnj;نگین بێت و هه&zwnj;ڵگری بێزار بکات)، &lrm; (هه&zwnj;روه&zwnj;ها فه&zwnj;رمانمان پێ دان&lrm;): کارو کرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام بده&zwnj;ن، دڵنیاشبن که من بینام به کارو کرده&zwnj;وه&zwnj;تان.';
$t[12] = 'بۆ سوله&zwnj;یمانیش بامان خسته ژێر فه&zwnj;رمانیه&zwnj;وه&zwnj;، به&zwnj;یانیان یه&zwnj;ك مانگه ڕێ ده&zwnj;ڕۆیشت و ئێواران یه&zwnj;ك مانگه ڕێ ده&zwnj;هاته&zwnj;وه&zwnj;، کانی مسیشمان بۆ توانده&zwnj;وه&zwnj;، له په&zwnj;رییانیش هه&zwnj;ندێکمان بۆ ڕاهێنا که له&zwnj;به&zwnj;رده&zwnj;ستیدا کار بکه&zwnj;ن به مۆڵه&zwnj;تی په&zwnj;روه&zwnj;ردگاری، جا ئه&zwnj;وه&zwnj;ی له فه&zwnj;رمانی ئێمه ده&zwnj;رچێت سزای دۆزه&zwnj;خی پێ ده&zwnj;چێژێت.';
$t[13] = 'سوله&zwnj;یمان هه&zwnj;رچی بویستایه&zwnj;، په&zwnj;ر&lrm;ی&lrm;یه&zwnj;کان بۆیان دروست ده&zwnj;کرد، له شوێنی خوا په&zwnj;رستی و په&zwnj;یکه&zwnj;رو حه&zwnj;وزو مه&zwnj;نجه&zwnj;ڵی گه&zwnj;وره&zwnj;ی به&zwnj;جۆرێك که له جێگه&zwnj;ی خۆی دامه&zwnj;زراوه&zwnj;و ناجوڵێت. ئه&zwnj;ی نه&zwnj;وه&zwnj;و شوێنکه&zwnj;وتوانی داوود: ئێوه به کردارو گوفتار سوپاسگوزاربن، هه&zwnj;رچه&zwnj;نده که&zwnj;مێك له به&zwnj;نده&zwnj;کانم سوپاسگوزارن.';
$t[14] = 'کاتێ که بریاری مردنی (سوله&zwnj;یمانماندا، په&zwnj;رییه&zwnj;کان نه&zwnj;یانزانی که مردووه&zwnj;)، تا مۆرانه گۆچانه&zwnj;که&zwnj;ی کلۆر کرد، کاتێ که که&zwnj;وت ئه&zwnj;وسا په&zwnj;رییه&zwnj;کان بۆیان ده&zwnj;رکه&zwnj;وت که ئه&zwnj;گه&zwnj;ر غه&zwnj;یب زان و نهێنیزان بوونانیه ئاوا له کارو فرمانی ناخۆشدا به&zwnj;رده&zwnj;وام نه&zwnj;ده&zwnj;بوون، (هه&zwnj;ر که زانیان سوله&zwnj;یمان وه&zwnj;فاتی کردووه ده&zwnj;ستیان له کار هه&zwnj;ڵده&zwnj;گرت).';
$t[15] = 'به&zwnj;ڕاستی بۆ خه&zwnj;ڵکی سه&zwnj;به&zwnj;ء له&zwnj;شوێنه&zwnj;واریاندا به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی (ده&zwnj;سه&zwnj;ڵاتی خوا هه&zwnj;یه&zwnj;)، باخی زۆر گه&zwnj;وره&zwnj;له&zwnj;لای ڕاست و چه&zwnj;پی ناوجه&zwnj;که&zwnj;یاندا خه&zwnj;مڵی بوو، (مۆڵه&zwnj;ت درابوون که&zwnj;): بخۆن له&zwnj;ڕزق و ڕۆزی په&zwnj;روه&zwnj;ردگارتان و سوپاسگوزاری بن، وڵاتێکی خۆش و ئاوه&zwnj;دان و په&zwnj;روه&zwnj;ردگارێکی لێخۆشبوو و لێبورده&zwnj;تان هه&zwnj;یه&zwnj;.';
$t[16] = '(به&zwnj;ڵام ئه&zwnj;مان له&zwnj;باتی شوکرانه&zwnj;بژێری و خواپه&zwnj;ره&zwnj;ستی) پشتیان هه&zwnj;ڵکردو (به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواوه&zwnj;نه&zwnj;چوون)، ئێمه&zwnj;ش لافاوێکی وێرانکه&zwnj;رمان بۆ ناردن، (که&zwnj;باخ و بێستانی کاولکردن) له&zwnj;جیاتی ئه&zwnj;و دوو باخه&zwnj;گه&zwnj;وره&zwnj;یه&zwnj;دوو باخی دڕکاوی به&zwnj;روبووم تاڵ و دره&zwnj;ختی بێبه&zwnj;رو که&zwnj;مێك دره&zwnj;ختی (سدر)یش که&zwnj;به&zwnj;روبوومێکی که&zwnj;می هه&zwnj;یه&zwnj;، پێمان به&zwnj;خشین.';
$t[17] = '(ئه&zwnj;و بارودۆخه&zwnj;ی که به&zwnj;سه&zwnj;ریاندا هات) ده&zwnj;رئه&zwnj;نجامی بێ&lrm;ئیمانی و کافرێتیاندا بوو، مه&zwnj;گه&zwnj;ر ئێمه تۆڵه هه&zwnj;ر له کافران ناسێنین؟!).';
$t[18] = '(له&zwnj;وه&zwnj;ودوا) ئێمه له نێوان ئه&zwnj;وان و ئه&zwnj;و شوێنه&zwnj;دا که به&zwnj;ره&zwnj;که&zwnj;تمان به&zwnj;سه&zwnj;ردا ڕژاندبوون دێهات و ئاوه&zwnj;دانی ئاشکرا هه&zwnj;بوون، که به&zwnj;بێ ماندوبوون ئه&zwnj;و سه&zwnj;فه&zwnj;رانه&zwnj;&zwnj;یان ده&zwnj;کرد له نێوان شام و یه&zwnj;مه&zwnj;ندا) ماوه&zwnj;ی نێوان دێهاته&zwnj;کانیان ده&zwnj;بڕی به ئاسانی و بێ ماندوبوون، وه (پێمان وتبوون، ده سه&zwnj;فه&zwnj;رو بازرگانی بکه&zwnj;ن)، بڕۆن چه&zwnj;نده&zwnj;ها شه&zwnj;و و ڕۆژ بێ ئه&zwnj;وه&zwnj;ی هیچ ترس و بیمتان هه&zwnj;بێت.';
$t[19] = '(که&zwnj;چی له&zwnj;و نازو نیعمه&zwnj;ته&zwnj;ش بێزار بوونو) وتیان: په&zwnj;روه&zwnj;دگارا نێوانی ڕێگه&zwnj;و سه&zwnj;فه&zwnj;ره&zwnj;کانمان له&zwnj;یه&zwnj;ك دوور بخه&zwnj;ره&zwnj;وه، (به&zwnj;م داخوازییه&zwnj;یان) سته&zwnj;میان له خۆیان کرد ئێمه&zwnj;ش کردمانن به داستان و باسی سه&zwnj;رزاری خه&zwnj;ڵکی، وه پاجه پارچه&zwnj;مان کردن، به&zwnj;ڕاستی ئا له&zwnj;و)ڕووداو حاڵه&zwnj;تانه&zwnj;دا) به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ته&zwnj;واو هه&zwnj;یه بۆ که&zwnj;سانێك که خۆگربن له&zwnj;کاتی ناخۆشی و ته&zwnj;نگانه&zwnj;دا، وه سوپاسگوزاربن له کاتی خۆشیدا (وه هه&zwnj;وڵ بده&zwnj;ن وه&zwnj;کو ئه&zwnj;و قه&zwnj;ومه ناله&zwnj;باره بیانووگره نه&zwnj;بن).';
$t[20] = 'له ڕاستیدا گومانی شیتان به&zwnj;ڕاست ده&zwnj;رچوو به&zwnj;رامبه&zwnj;ر ئه&zwnj;و جۆره که&zwnj;سانه (که وتبووی: ده&zwnj;بینی زۆربه&zwnj;ی خه&zwnj;ڵکی سوپاسگوزار نابن) بێ په&zwnj;روا شوێنی که&zwnj;وتن، جگه له ده&zwnj;سته&zwnj;یه&zwnj;ك له بڕواداران.';
$t[21] = 'جا وه&zwnj;نه&zwnj;بێ (ئیبلس) هیچ ده&zwnj;سه&zwnj;ڵاتێکی به&zwnj;سه&zwnj;ریاندا بێت، به&zwnj;ڵکو ئێمه (ویستومانه&zwnj;) که کێ باوه&zwnj;ڕی به&zwnj;قیامه&zwnj;ت و ڕۆژی دوایی هه&zwnj;یه&zwnj;و کێش له&zwnj;شك و گوماندایه ده&zwnj;ربکه&zwnj;وێت، (دڵنیابه ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) که&zwnj;په&zwnj;ره&zwnj;ردگاری تۆ ئاگادارو چاودێره به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکداو هیچ شتێکی لێ بزر نابێت.';
$t[22] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به هاوه&zwnj;ڵپه&zwnj;رستان) بڵێ: با هاناو هاوار ببه&zwnj;ن بۆ ئه&zwnj;و شتانه&zwnj;ی که له&zwnj;جیاتی خوا (ده&zwnj;یانپه&zwnj;رستن)!! (ئه&zwnj;وانه&zwnj;) خاوه&zwnj;نی بچوکتر له ئه&zwnj;تۆمێك نین نه له ئاسمانه&zwnj;کان، نه له زه&zwnj;ویدا، وه&zwnj;هیچ هاوبه&zwnj;شی و هاوکاریه&zwnj;کیشیان نیه تیایاندا، ده&zwnj;سه&zwnj;ڵاتی هیچ پشتیوانی و یارمه&zwnj;&zwnj;تیه&zwnj;کیشیان نیه&zwnj;.';
$t[23] = 'هیچ تکاو پاڕانه&zwnj;وه&zwnj;یه&zwnj;ك سودی نیه لای (خوا) مه&zwnj;گه&zwnj;ر خۆی مۆڵه&zwnj;ت بدات هه&zwnj;تا ئه&zwnj;وکاته&zwnj;ی ئیتر (ئه&zwnj;وانه&zwnj;ی تکاکارن و ئه&zwnj;وانه&zwnj;ش که تکایان بۆ ده&zwnj;کرێت دڵیان دێته&zwnj;وه جێ و هۆشیان پیادێته&zwnj;وه و له&zwnj;یه&zwnj;کتر ده&zwnj;پرسن): په&zwnj;روه&zwnj;ردگارتان فه&zwnj;رمانی به&zwnj;چی داو چی فه&zwnj;رموو: (له وه&zwnj;ڵامیاندا) ده&zwnj;ڵێن: حه&zwnj;ق و ڕاست و دروست، به&zwnj;ڕاستی ئه&zwnj;و (په&zwnj;روه&zwnj;ردگاره&zwnj;) زۆر به&zwnj;رزو بڵندو گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[24] = '(ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به هاوه&zwnj;ڵپه&zwnj;رستان) بڵێ: کێ ڕزق و ڕۆزیتان پێ&lrm;ده&zwnj;به&zwnj;خشێت له ئاسمانه&zwnj;کان و زه&zwnj;ویدا (ئه&zwnj;گه&zwnj;ر به&zwnj;هۆی سپڵه&zwnj;ییانه&zwnj;وه وه&zwnj;ڵامیان نه&zwnj;دایته&zwnj;وه تۆ) بڵێ: (مه&zwnj;گه&zwnj;ر هه&zwnj;ر) زاتی الله (نیه ڕۆزیبه&zwnj;خش)، ئه&zwnj;وسا پێیان بڵێ: بێگومان ئێمه&zwnj;، یا ئێوه له&zwnj;سه&zwnj;ر ڕێگه&zwnj;ی (ڕاست و دروستی) هیدایه&zwnj;تین، یاخود له گومڕاییه&zwnj;کی ئاشکرادا ڕۆچووین!! (خۆتان ببنه دادوه&zwnj;ر).';
$t[25] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: ئێوه پرسیارتان لێناکرێت ده&zwnj;رباره&zwnj;ی گوناگی ئێمه&zwnj;، ئێمه&zwnj;ش پرسیارمان لێناکرێت ده&zwnj;رباره&zwnj;ی ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;تان که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن (هه&zwnj;رکه&zwnj;س به&zwnj;رپرسی کارو کرده&zwnj;وه&zwnj;و بیروباوه&zwnj;ڕی خۆیه&zwnj;تی).';
$t[26] = 'پێیان بڵێ: (ڕۆژێك دێت) په&zwnj;روه&zwnj;ردگارمان هه&zwnj;موو لایه&zwnj;کمان کۆده&zwnj;کاته&zwnj;وه&zwnj;، له&zwnj;وه&zwnj;ودوا له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی جیامان ده&zwnj;کاته&zwnj;وه&zwnj;، (باوه&zwnj;ڕداران بۆ به&zwnj;هه&zwnj;شت و به&zwnj;خته&zwnj;وه&zwnj;ری، کافرو هاوه&zwnj;ڵپه&zwnj;ره&zwnj;ستان بۆ دۆزه&zwnj;خ و ده&zwnj;ردی سه&zwnj;ری)، (ئه&zwnj;و ڕۆژه&zwnj;) ئه&zwnj;و زاته هه&zwnj;ر خۆی دادوه&zwnj;ری دادپه&zwnj;روه&zwnj;ره&zwnj;و زانای بێسنووره&zwnj;.';
$t[27] = '(دووباره پێیان) بڵێ: ئاده&zwnj;ی ئه&zwnj;وه&zwnj;انه&zwnj;م نیشان بده&zwnj;ن که دابووتانه پاڵ خواو به هاوبه&zwnj;شی ئه&zwnj;وتان داده&zwnj;نا، هه&zwnj;رگیز کاری وا ڕه&zwnj;وا نیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;و خوایه&zwnj;، زۆر باڵاده&zwnj;ست و دانایه (به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ك پێویستی به هاوه&zwnj;ڵ نیه&zwnj;).';
$t[28] = 'ئێمه تۆمان ڕه&zwnj;وانه نه&zwnj;کردووه&zwnj;، مه&zwnj;گه&zwnj;ر بۆ ئه&zwnj;وه نه&zwnj;بێ که بۆ هه&zwnj;موو خه&zwnj;ڵکی ببیته مژده&zwnj;ده&zwnj;رو ترسێنه&zwnj;ر.';
$t[29] = '(بێ&lrm;بڕواکان) ده&zwnj;ڵێن: (باشه&zwnj;) که&zwnj;ی ئه&zwnj;م به&zwnj;ڵێنه (قیامه&zwnj;ت و زیندوبونه&zwnj;وه&zwnj;) دێته دی ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن.';
$t[30] = 'پێیان بڵێ: کاتێك دێت که ناتوانن نه تاوێك دوا بکه&zwnj;ون نه تاوێك پێش بکه&zwnj;ون (به&zwnj;ڵکو له&zwnj;کاتی خۆیدا ئاماده ده&zwnj;کرێن).';
$t[31] = 'ئه&zwnj;وانه&zwnj;ی که بێ&lrm;بڕوان ده&zwnj;ڵێن: هه&zwnj;رگیز باوه&zwnj;ڕ به&zwnj;م قورئانه&zwnj;و به&zwnj;و (کتێبانه&zwnj;ش) ناکه&zwnj;ین که پێش ئه&zwnj;م (ڕه&zwnj;وانه کراوه&zwnj;)، خۆ ئه&zwnj;گه&zwnj;ر ده&zwnj;بینی (له ڕۆژی قیامه&zwnj;تدا) چۆن سته&zwnj;مکاران وه&zwnj;ستێنراون له&zwnj;به&zwnj;رده&zwnj;م (دادگای) په&zwnj;روه&zwnj;ردگاریاندا قسه ده&zwnj;ڵێن به یه&zwnj;کترو (یه&zwnj;کتری تاوانبار ده&zwnj;که&zwnj;ن)، لاوازکراوه&zwnj;کان به زۆردارو ده&zwnj;سه&zwnj;ڵاتداره&zwnj;کان ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر ئێوه نه&zwnj;بوونایه ئێمه باوه&zwnj;ڕدار ده&zwnj;بووین.';
$t[32] = 'ده&zwnj;سه&zwnj;ڵاتدارو زۆرداره&zwnj;کانیش به&zwnj;لاوازکراوو ژێرده&zwnj;سته&zwnj;کان ده&zwnj;ڵێن: بۆچی ئێمه ڕێگه&zwnj;ی هیدایه&zwnj;تمان لێ&lrm;به&zwnj;ستبوون و نه&zwnj;مانده&zwnj;هێشت دوای ئه&zwnj;وه&zwnj;ی که بۆتان هاتبوو؟! نا، وا نیه&zwnj;، خۆتان تاوانبارو تاوانکارن.';
$t[33] = '(دووباره&zwnj;) لاوازکراو ژێرده&zwnj;سته&zwnj;کان به زۆردارو ده&zwnj;سه&zwnj;ڵاتداره&zwnj;کان ده&zwnj;ڵێن: نا، وانیه&zwnj;، به&zwnj;ڵکو پیلان و نه&zwnj;خشه&zwnj;ی شه&zwnj;وانه ڕۆژ بوو که فه&zwnj;رمانتان پیاده&zwnj;داین که بڕوامان به خوا نه&zwnj;بێ و (شوێنی ڕێگه&zwnj;و ڕێبازی نه&zwnj;که&zwnj;وین)، هاوه&zwnj;ڵی بۆ بریار بده&zwnj;ین، (پاشان هه&zwnj;موان) په&zwnj;شیمانی له ده&zwnj;رونیاندا ده&zwnj;شارنه&zwnj;وه (به&zwnj;تایبه&zwnj;تی کاتێك که&zwnj;) ئه&zwnj;و سزاو تۆڵه&zwnj;یان بینی که بۆیان ئاماده&zwnj;یه&zwnj;، (ئه&zwnj;وسا ئیتر) کۆت و زنجیرمان کرده گه&zwnj;ردنی ئه&zwnj;وانه&zwnj;ی که بێ&lrm;بڕوا بوون، مه&zwnj;گه&zwnj;ر پاداشتیان له&zwnj;بری کارو کردوه&zwnj;کانیان نیه&zwnj;؟!.';
$t[34] = '(ئێمه&zwnj;) بۆ هه&zwnj;ر شوێنێك (پێغه&zwnj;مبه&zwnj;رێکی) بێدارخه&zwnj;ره&zwnj;وه&zwnj;مان نه&zwnj;ناردووه&zwnj;، خۆشگوزه&zwnj;رانان نه&zwnj;یانووتبێ: ئێمه بڕوامان به&zwnj;م (بیروباوه&zwnj;ڕه&zwnj;) نیه که به&zwnj;ئێوه&zwnj;دا ڕه&zwnj;وانه&zwnj;کراوه&zwnj;!!.';
$t[35] = 'هه&zwnj;روه&zwnj;ها وتوویانه&zwnj;: ئێمه سامان و نه&zwnj;وه&zwnj;ی زۆرترمان هه&zwnj;یه (له&zwnj;ئیمانداران، دیاره که خوا ئێمه&zwnj;ی خۆش ده&zwnj;وێت!!)، ئێمه (له قیامه&zwnj;تدا) سزاو ئه&zwnj;شکه&zwnj;نجه نادرێین!!.';
$t[36] = '(پێیان بڵێ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی&lrm; الله علیه&zwnj;&lrm; وسلم) به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;، په&zwnj;روه&zwnj;ردگاری منه که ڕزق و ڕۆزی زۆر ده&zwnj;به&zwnj;خشێت به هه&zwnj;رکه&zwnj;سێك که بیه&zwnj;وێ، یان که&zwnj;می ده&zwnj;کاته&zwnj;وه&zwnj;و ته&zwnj;نگی ده&zwnj;کاته&zwnj;وه (بێگومان هه&zwnj;ردوو حاڵه&zwnj;ته&zwnj;که بۆ تاقیکردنه&zwnj;وه&zwnj;یه&zwnj;) به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی به (نهێنی کاره&zwnj;کانمان) نازانن و په&zwnj;ی پێ&lrm; نابه&zwnj;ن.';
$t[37] = '(له&zwnj;ڕاستیدا) نه ماڵتان و، نه نه&zwnj;وه&zwnj;تان شتێك نین که ببنه هۆی نزیکبوونه&zwnj;وه&zwnj;تان له ئێمه&zwnj;، (یا ڕازیبوونی ئێمه لێتان)، به&zwnj;ڵکو ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;و کاروکرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجامده&zwnj;ده&zwnj;ن (ئه&zwnj;وانه نزیکن له ئێمه&zwnj;وه&zwnj;و ڕێزیان لای ئێمه هه&zwnj;یه&zwnj;) ئا ئه&zwnj;وانه پاداشتیان چه&zwnj;ند به&zwnj;رابه&zwnj;ره به&zwnj;هۆی کرده&zwnj;وه&zwnj;ی چاکیانه&zwnj;وه&zwnj;، هه&zwnj;ر ئه&zwnj;وانیش له کۆشك و ته&zwnj;لاره&zwnj;کانی (به&zwnj;هه&zwnj;شتدا) به ئاسودیی و ئارامیی و خۆشیی، ژیان ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[38] = 'ئه&zwnj;وانه&zwnj;س که هه&zwnj;وڵ و کۆشش ده&zwnj;ده&zwnj;ن (تا به&zwnj;ربه&zwnj;ست بخه&zwnj;نه ڕێی) ئایه&zwnj;ته&zwnj;کان ئێمه&zwnj;، ئا ئه&zwnj;وانه بۆ ناو سزاو ئازار ڕاپێچ ده&zwnj;کرێن.';
$t[39] = '(ئه&zwnj;ی پێغه&zwnj;&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) پێیان بڵێ: به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشێت به هه&zwnj;رکه&zwnj;س له به&zwnj;نده&zwnj;کانی یا لێی ده&zwnj;گرێته&zwnj;وه&zwnj;، ئێوه&zwnj;ش هه&zwnj;ر شتێك ده&zwnj;به&zwnj;خشن ئه&zwnj;و زاته پێتان ده&zwnj;به&zwnj;خشێته&zwnj;وه (هه&zwnj;ندێك جار له دینادا، پاداشتی قیامه&zwnj;تیش با بوه&zwnj;ستێت)، چونکه ئه&zwnj;و خوایه چاکترین ڕزق و ڕۆزیی به&zwnj;خشه&zwnj;.';
$t[40] = 'ڕۆژێك دێت که هه&zwnj;موان کۆده&zwnj;کرێنه&zwnj;وه (ئه&zwnj;وسا خوا به فریشته&zwnj;کان ده&zwnj;فه&zwnj;رموێت: (به&zwnj;ڕاست)، ئه&zwnj;وانه ئێوه&zwnj;یان ده&zwnj;په&zwnj;رست؟!';
$t[41] = '(له وه&zwnj;ڵامدا) ده&zwnj;ڵێن: پاکیی و ستایش بۆ تۆ (ئه&zwnj;ی په&zwnj;روه&zwnj;ردگار) تۆ یارو یاوه&zwnj;ری ئێمه&zwnj;ی نه&zwnj;ك ئه&zwnj;وان، به&zwnj;ڵکو ئه&zwnj;وانه په&zwnj;رییان ده&zwnj;په&zwnj;رست، زۆربه&zwnj;یان باوه&zwnj;ڕیان پێیان هه&zwnj;بوو (باوه&zwnj;ڕیان به جادووگه&zwnj;رو ده&zwnj;جاله&zwnj;کان هه&zwnj;بوو).';
$t[42] = 'ئه&zwnj;وه ئیتر ئه&zwnj;مڕۆ که&zwnj;ستان ده&zwnj;سه&zwnj;ڵاتی ئه&zwnj;وه&zwnj;ی نیه که قازانجێك یا زه&zwnj;ره&zwnj;رێك به که&zwnj;سی&lrm;ترتان بگه&zwnj;یه&zwnj;نێت، به&zwnj;وستهمکارانیش ده&zwnj;ڵێین: بچێژن سزای ئه&zwnj;و ئاگره&zwnj;ی که ئێوه به درۆتان ده&zwnj;زانی و باوه&zwnj;ڕتان پێی نه&zwnj;بوو.';
$t[43] = 'جا ئه&zwnj;گه&zwnj;ر ئایه&zwnj;ته ئاشکه&zwnj;او ڕوونه&zwnj;کانی ئێمه به&zwnj;سه&zwnj;ریاندا بخوێنرێته&zwnj;وه&zwnj;، (کافران) ده&zwnj;ڵێن: ئه&zwnj;مه کابرایه&zwnj;که&zwnj;و ده&zwnj;یه&zwnj;وێت له ڕێگه&zwnj;و ڕێبازی باوانتان وێڵتان بکات، هه&zwnj;روه&zwnj;ها ده&zwnj;ڵێن: ئه&zwnj;مه شتێکی ده&zwnj;ستهه&zwnj;ڵبه&zwnj;ست و هه&zwnj;ڵبه&zwnj;ستراوه&zwnj;، ئه&zwnj;وانه&zwnj;ش که کافرن ده&zwnj;رباره&zwnj;ی ئه&zwnj;و حه&zwnj;ق و ڕاستییه&zwnj;ی که بۆیان هاتووه ده&zwnj;ڵێن: ئا ئه&zwnj;مه سیحرو جادوویه&zwnj;کی ئاشکرایه&zwnj;!!.';
$t[44] = '(جا خۆ ئێمه&zwnj;) هیچ کتێب و نامه&zwnj;یه&zwnj;کمان بۆ نه&zwnj;ناردوون تا بیخوێنن (تا له&zwnj;ڕووی ئه&zwnj;وه&zwnj;وه تۆ به&zwnj;درۆ بخه&zwnj;نه&zwnj;وه&zwnj;و بڕوات پێ نه&zwnj;که&zwnj;ن)، وه پێش تۆش هیچ پێغه&zwnj;مبه&zwnj;رێکمان بۆ ڕه&zwnj;وانه نه&zwnj;کردوون (تا له سزای بێ&lrm;ئیمانی بیانترسێنێت).';
$t[45] = 'ئه&zwnj;و خه&zwnj;ڵکه&zwnj;ی پێش ئه&zwnj;مانێش (پێغه&zwnj;مبه&zwnj;رانی خوایان) به&zwnj;درۆ خسته&zwnj;وه به مه&zwnj;رجێك به&zwnj;قه&zwnj;ده&zwnj;ر ده&zwnj;یه&zwnj;کی ئه&zwnj;وانه&zwnj;، ئه&zwnj;مانه ده&zwnj;سه&zwnj;ڵاتدارو خاوه&zwnj;ن دارایی نین، که&zwnj;چی پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی منیان به&zwnj;درۆ خسته&zwnj;وه&zwnj;، ئایا نابینن چۆن تۆڵه&zwnj;م لێ سه&zwnj;ندن و چیم به&zwnj;سه&zwnj;رهێنان؟!';
$t[46] = '(پێیان) بڵێ: من ئه&zwnj;ك ئامۆژگاری ئێوه ده&zwnj;که&zwnj;م، که&zwnj;، بۆ خواو له&zwnj;به&zwnj;ر خوا، دوو قۆڵی و به&zwnj;ته&zwnj;نهایی، دانیشن و بیرکه&zwnj;نه&zwnj;وه (له&zwnj;م قورئانه&zwnj;، له&zwnj;م پێغه&zwnj;مبه&zwnj;ره&zwnj;، له&zwnj;م به&zwnj;رنامه&zwnj;یه&zwnj;) ئه&zwnj;م هاوه&zwnj;ڵه&zwnj;ی ئێوه (محمد صلی الله علیه وسلم)، شێت نیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;و ته&zwnj;نها ترسێنه&zwnj;ری ئێوه&zwnj;یه پێش ئه&zwnj;وه&zwnj;ی سزایه&zwnj;کی سه&zwnj;خت ڕووبه&zwnj;ڕووتان ببێت..';
$t[47] = 'پێیان بڵێ: هه&zwnj;رچی پاداشت و کرێیه&zwnj;کم داواکردووه هه&zwnj;ر بۆ خۆتان، من پاداشتم ته&zwnj;نها له&zwnj;سه&zwnj;ر خوایه&zwnj;، ئه خوایه&zwnj;ش شایه&zwnj;ته به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکه&zwnj;وه&zwnj;.';
$t[48] = '(ئه&zwnj;ی محمد صلی الله علیه وسلم) بڵێ: به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارم (سه&zwnj;رئه&zwnj;نجام) حه&zwnj;ق ده&zwnj;ماڵی به&zwnj;سه&zwnj;ر (به&zwnj;تاڵ و ناحه&zwnj;قیدا) به&zwnj;ڕاستی ئه&zwnj;و زاته زۆر شاره&zwnj;زایه به نهێنی و شاراوه&zwnj;کان.';
$t[49] = 'هه&zwnj;روه&zwnj;ها بڵێ: حه&zwnj;ق و ڕاستی هات و به&zwnj;تاڵ و ناحه&zwnj;قی بوی نه&zwnj;ما.. نه ده&zwnj;توانێ ده&zwnj;ست پێ&lrm;بکاته&zwnj;وه&zwnj;، نه ده&zwnj;توانێ بگه&zwnj;ڕێته&zwnj;وه دۆخی جارانی.';
$t[50] = 'بڵێ: ئه&zwnj;گه&zwnj;ر من گومڕابم ئه&zwnj;وه گومڕاییه&zwnj;که&zwnj;م زه&zwnj;ره&zwnj;ری بۆ خۆم هه&zwnj;یه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر ڕێگه&zwnj;ی هیدایه&zwnj;تم گرتبێت ئه&zwnj;وه به&zwnj;هۆی نیگاو وه&zwnj;حیی په&zwnj;روه&zwnj;ردگارمه&zwnj;وه&zwnj;یه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و زاته زۆر بیسه&zwnj;رو نزیکه&zwnj;.';
$t[51] = '(ڕۆژێك دێت کافران) ده&zwnj;بینیت له&zwnj;ترسا خه&zwnj;ریکن زیڕه ده&zwnj;که&zwnj;ن، دڵنیان که له ده&zwnj;ست ده&zwnj;رناچن و ڕزگار بوونیان ئه&zwnj;سته&zwnj;مه&zwnj;، ئیتر هه&zwnj;ر له نزیکه&zwnj;وه ده&zwnj;ستگیر ده&zwnj;کرێن و باڵبه&zwnj;ست ده&zwnj;کرێن.';
$t[52] = 'ئه&zwnj;و کاته (به ناچاریی) ده&zwnj;ڵێن: ئیمانمان (به په&zwnj;روه&zwnj;ردگارمان) هێنا، به&zwnj;ڵام له&zwnj;و شوێنه دووره&zwnj;وه ئیمان چۆن ده&zwnj;ستده&zwnj;که&zwnj;وێت (تازه کار له&zwnj;کار ترازراوه&zwnj;).';
$t[53] = 'ئه&zwnj;وانه کاتی خۆی باوه&zwnj;ڕیان به&zwnj;خوا نه&zwnj;بوو، هه&zwnj;ر له&zwnj;دووره&zwnj;وه باسی نهێنی و شاراوه&zwnj;کانیان ده&zwnj;کردو، ده&zwnj;میان لێده&zwnj;دا.';
$t[54] = '(ئه&zwnj;وسا ئیتر) له&zwnj;نێوان ئه&zwnj;مان و ئاره&zwnj;زوکانیاندا (به&zwnj;ربست دانرا)، هه&zwnj;روه&zwnj;کو پێشتر به هاوشێوه&zwnj;ی ئه&zwnj;مان کرا، به&zwnj;ڕاستی ئه&zwnj;وانه هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام له گومان و دوودڵیدا بوون (به&zwnj;رانبه&zwnj;ر ئیمان و خۆیان بۆ دینداری یه&zwnj;کلایی نه&zwnj;ده&zwnj;کرده&zwnj;وه&zwnj;).';
?>