<?php
$t = array();
$t[1] = 'سوپاس وستایش بۆ خوایه&zwnj;، که به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;، فریشته&zwnj;کانی کردووه به فرستاده&zwnj;ی خۆی که خاوه&zwnj;نی چه&zwnj;نده&zwnj;ها باڵن، هه&zwnj;یانه دوو، یان سێ، یان چوار باڵه&zwnj;، له دروستکردندا هه&zwnj;رچی بوێت زیادی ده&zwnj;کات (له ژماره&zwnj;ی فریشته&zwnj;کان، له ئه&zwnj;ستێره و هه&zwnj;ساره&zwnj;کان، له ئاده&zwnj;میزاد و په&zwnj;ری... هتد) به&zwnj;ڕاستی خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[2] = 'هه&zwnj;ر کاتێک خوا ده&zwnj;روازه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششی خۆی له خه&zwnj;ڵکی بکاته&zwnj;وه&zwnj;، ئه&zwnj;وه هیچ که&zwnj;س و هیچ شتێک ناتوانێت به&zwnj;ری بگرێت، هه&zwnj;ر کاتێکیش ده&zwnj;روازه&zwnj;ی ڕه&zwnj;حمه&zwnj;تی بگرێته&zwnj;وه و دای بخات، که&zwnj;س ناتوانێت له دوای گرتنه&zwnj;وه&zwnj;که&zwnj;ی خوا بێجگه له خۆی بیکاته&zwnj;وه&zwnj;، ئه&zwnj;و زاته هه&zwnj;ر خۆیشێ باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[3] = 'ئه&zwnj;ی خه&zwnj;ڵکینه بیربکه&zwnj;نه&zwnj;وه له چاکه&zwnj;ی خوا به&zwnj;سه&zwnj;رتانه&zwnj;وه، ئایا دروستکارو به دیهێنه&zwnj;رێکی تر هه&zwnj;یه جگه له خوا له ئاسمان و زه&zwnj;ویه&zwnj;وه ڕزق و ڕۆزیاتان پێ ببه&zwnj;خشێت؟ جگه له&zwnj;و زاته خوایه&zwnj;کی تر نیه&zwnj;، جا ئه&zwnj;وه چۆن له&zwnj;و بیروباوه&zwnj;ڕه دروسته لا ده&zwnj;درێن؟ (روو ده&zwnj;که&zwnj;نه گومڕایی).';
$t[4] = '(پاشان دڵخۆشی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ده&zwnj;داته&zwnj;وه و ده&zwnj;فه&zwnj;رموێت): خۆ ئه&zwnj;گه&zwnj;ر خوانه&zwnj;ناسان بڕوات پێنه&zwnj;که&zwnj;ن، ئه&zwnj;وه دڵنیایه که پێغه&zwnj;مبه&zwnj;رانی تریش پێش تۆ بروایان پێ نه&zwnj;کراوه&zwnj;، به&zwnj;ڵام غه&zwnj;مت نه&zwnj;بێت هه&zwnj;ر بۆ لای خوا هه&zwnj;موو کارێک ده&zwnj;برێته&zwnj;وه و ئه&zwnj;وانه له ده&zwnj;ستی ده&zwnj;رناچن.';
$t[5] = 'ئه&zwnj;ی خه&zwnj;ڵکینه&zwnj;: دڵنیابن به&zwnj;ڵێنی خوا ڕاست و دروسته (قیامه&zwnj;ت به&zwnj;رپا ده&zwnj;کات و له هه&zwnj;موو که&zwnj;س ده&zwnj;پرسێته&zwnj;وه&zwnj;) که&zwnj;وابوو ژیانی ئه&zwnj;م دنیایه سه&zwnj;رتان لی نه&zwnj;شێوێنێت و شه&zwnj;تیان نه&zwnj;تانخه&zwnj;ڵتێنێت به ناوی ڕه&zwnj;حم و به&zwnj;زه&zwnj;یی خواوه (که گوایه هه&zwnj;رچی بکه&zwnj;ن خوا به&zwnj;به&zwnj;زه&zwnj;یییه و لێتان خۆش ده&zwnj;بێت).';
$t[6] = 'چونکه به&zwnj;ڕاستی شه&zwnj;یتان دوژمنتانه ئێوه&zwnj;ش به دوژمنی خۆتانی دابنێن، بێگومان بانگی تاقم و داروده&zwnj;سته&zwnj;ی خۆی ده&zwnj;کات بۆ ئه&zwnj;وه&zwnj;ی ببنه نیشته&zwnj;جیی دۆزه&zwnj;خ.';
$t[7] = 'ئه&zwnj;وانه&zwnj;ی بێ بڕوا بوون سزای زۆر پڕ ئێش و ئازار چاوه&zwnj;ڕێیانه&zwnj;، ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;جامداوه لێخۆشبوون و پاداشتی بێ سنوور چاوه&zwnj;ڕێیانه بۆ بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[8] = 'ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که کرده&zwnj;وه ناشیرینه&zwnj;کانی بۆ ڕازێنراوه&zwnj;ته&zwnj;وه و به جوان و چاک ده&zwnj;یبینێت؟! (وه&zwnj;ک ئه&zwnj;و که&zwnj;سه وایه که پێوه&zwnj;ری خوایی پێیه بۆ چاکه و خارپه&zwnj;)؟ به&zwnj;ڕاستی خوا بیه&zwnj;وێت هه&zwnj;ر که&zwnj;س گومڕا بکات ده&zwnj;یکات، هه&zwnj;ر که&zwnj;سێکیش هیدایه&zwnj;ت بدات ده&zwnj;یدات، (ئه&zwnj;ڵبه&zwnj;ته نه&zwnj;ک به&zwnj;شێوه&zwnj;ی هه&zwnj;ڕه&zwnj;مه&zwnj;کی) که&zwnj;واته تۆ به خه&zwnj;فه&zwnj;رتیانه&zwnj;وه خۆت مه&zwnj;فه&zwnj;وتێنه&zwnj;، چونکه به&zwnj;ڕاستی خوا ئاگاو زانایه به&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[9] = 'خوا ئه&zwnj;و زاته&zwnj;یه که بای ڕه&zwnj;حمه&zwnj;ت ده&zwnj;نێرێت و هه&zwnj;ڵمی ده&zwnj;ریاکانی پێ ده&zwnj;گوێزێته&zwnj;وه و ده&zwnj;یکاته هه&zwnj;ور، جا ده&zwnj;ینێرین بۆ وڵاتێکی وشك و مردوو، ده&zwnj;فه&zwnj;رموێت: ئینجا ئه&zwnj;و زه&zwnj;ویه&zwnj;مان پێ زیندوو کردوه دوای ئه&zwnj;وه&zwnj;ی که مردبوو، زیندو کردنه&zwnj;وه&zwnj;ی ئێوه&zwnj;ش هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه ده&zwnj;بێت.';
$t[10] = 'ئه&zwnj;وه&zwnj;ی سه&zwnj;ربه&zwnj;رزی وپایه&zwnj;بڵندی ده&zwnj;وێت، با بزانێت که هه&zwnj;موو سه&zwnj;ربه&zwnj;رزی و پایه بڵندیه&zwnj;ک هه&zwnj;ر بۆ خوایه&zwnj;، گوفتاری چاک و جوان و به&zwnj;جێ بۆ لای ئه&zwnj;و زاته به&zwnj;رز ده&zwnj;بێه&zwnj;وه&zwnj;، کاروکرده&zwnj;وه&zwnj;ی چاکیش گوفتاری شیرین به&zwnj;رز ده&zwnj;کاته&zwnj;وه (هه&zwnj;روه&zwnj;ها پێچه&zwnj;وانه&zwnj;که&zwnj;شی هه&zwnj;روایه&zwnj;)، جا ئه&zwnj;وانه&zwnj;ی که پیلانی خراپ ده&zwnj;گێڕن سزای زۆر توندو تیژ بۆتان ئاماده&zwnj;یه&zwnj;، پیلانی ئه&zwnj;وانه هه&zwnj;ر خۆی پووچ ده&zwnj;بێته&zwnj;وه و بێ به&zwnj;روو و ناکامه&zwnj;.';
$t[11] = 'خوا؛ ئێوه&zwnj;ی له خاک دروستکردووه&zwnj;، له&zwnj;وه&zwnj;ودواش له نوتفه&zwnj;یه&zwnj;ک (که بریتیه له به&zwnj;یه&zwnj;کگه&zwnj;یشتنی سه&zwnj;ره&zwnj;مێکوته&zwnj;یه&zwnj;ک له&zwnj;گه&zwnj;ڵ هیلکۆکه&zwnj;یه&zwnj;کدا) له&zwnj;وه&zwnj;ودوا کردوونی به جووت (به نێرو مێ)، هیچ مێننه&zwnj;یه&zwnj;ک ئاوس نابێت و نازێت و سکه&zwnj;که&zwnj;ی دانانێت ته&zwnj;نها به ئاگاداری ئه&zwnj;و زاته نه&zwnj;بێت، هیچ ته&zwnj;مه&zwnj;ن درێژێک، ته&zwnj;مه&zwnj;نی درێژ نابێت و له ته&zwnj;مه&zwnj;نی که&zwnj;م نابێته&zwnj;وه&zwnj;، بێ ئه&zwnj;وه&zwnj;ی له زانستی خوادا یاداشت نه&zwnj;کرابێت، بێگومان ئه&zwnj;و شته له&zwnj;سه&zwnj;ر خوا ئاسانه&zwnj;.';
$t[12] = 'هه&zwnj;ر دوو خۆره ده&zwnj;ریا وه&zwnj;کو یه&zwnj;ک نین، یه&zwnj;کێکیان ئاوی سازگار و خۆش، ئه&zwnj;وی تریان سوێر و تاڵ، له هه&zwnj;ردووکیان گۆشتی ئه&zwnj;ڕو تازه ده&zwnj;خۆن، جۆره&zwnj;ها شتی گرانبه&zwnj;ها (وه&zwnj;ک گه&zwnj;وهه&zwnj;رو مرواری و یاقووت و... هتد) ه&zwnj;رده&zwnj;هێنن و له&zwnj;به&zwnj;ری ده&zwnj;که&zwnj;ن و خۆتانی پێ ده&zwnj;ڕازێننه&zwnj;وه&zwnj;، ئینجا که&zwnj;شتییه&zwnj;کان ده&zwnj;بینیت ئاوه&zwnj;که له&zwnj;ت ده&zwnj;که&zwnj;ن و (به ئاسانی پیایدا ده&zwnj;ڕۆن) تا له به&zwnj;خششه&zwnj;کانی په&zwnj;روه&zwnj;ردگار به&zwnj;هره&zwnj;وه&zwnj;ر ببن، بۆ ئه&zwnj;وه&zwnj;ی سوپاسگوزریش بن.';
$t[13] = '(هه&zwnj;ر خوایه که&zwnj;): شه&zwnj;و تێهه&zwnj;ڵکێشی ڕۆژ ده&zwnj;کات، ڕۆژیش تێهه&zwnj;ڵکێشی شه&zwnj;و ده&zwnj;کات، خۆرو مانگی ڕامهێناوه&zwnj;، هه&zwnj;ریه&zwnj;که&zwnj;یان تاکاتێکی دیاری کراو له سووڕانه&zwnj;وه&zwnj;دایه&zwnj;، ئائه&zwnj;و خوایه په&zwnj;روه&zwnj;ردگارتانه خاوه&zwnj;نی هه&zwnj;موو موڵک و به&zwnj;دیهاتووه&zwnj;کانه&zwnj;، (به&zwnj;ڵام) ئه&zwnj;وانه&zwnj;ی جگه له&zwnj;و هاناو هاواری بۆ ده&zwnj;به&zwnj;ن ولێی ده&zwnj;پارێنه&zwnj;وه ته&zwnj;نانه&zwnj;ت خاوه&zwnj;نی توێکڵه ته&zwnj;نکه&zwnj;که&zwnj;ی ده&zwnj;وری ناوکی خورمانیش نین.';
$t[14] = 'خۆ ئه&zwnj;گه&zwnj;ر هاناو هاواریان بۆ به&zwnj;رن گۆێیان لێتان نییه و نابیستن، ئه&zwnj;گه&zwnj;ر بشبیستن به ده&zwnj;متانه&zwnj;وه نایه&zwnj;، ڕۆژی قیامه&zwnj;تیش حاشا ده&zwnj;که&zwnj;ن لێتان و له&zwnj;وه&zwnj;ی که به (خواتان) زانیوون، بێگومان که&zwnj;س نییه وه&zwnj;ک (خوا) ئاگاو زانا بێت و هه&zwnj;واڵی ڕاست و دروست بگه&zwnj;یه&zwnj;نیت.';
$t[15] = 'ئه&zwnj;ی خه&zwnj;ڵکینه ئێوه هه&zwnj;ژارو نه&zwnj;دارن و هه&zwnj;میشه ئاتاجی خوان، (له هه&zwnj;موو کات و شوێنێکدا و بۆ هه&zwnj;موو شتێک) هه&zwnj;ر ته&zwnj;نها خوایه که بێ نیازه و شایانی سوپاسه&zwnj;.';
$t[16] = 'ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت ئێوه له&zwnj;ناو ده&zwnj;بات و که&zwnj;سانی تر سه&zwnj;رله&zwnj;نوێ ده&zwnj;هێنێته کایه&zwnj;وه&zwnj;.';
$t[17] = 'ئه&zwnj;مه&zwnj;ش بۆ خوا زۆر کارێکی ئاسانه و (ئه&zwnj;نجامدانی هیچی پێ ناچێت).';
$t[18] = 'هیچ گوناهبارێکیش گوناهی که&zwnj;سانی تر هه&zwnj;ڵناگرێت (له قیامه&zwnj;تیشدا)ئه&zwnj;گه&zwnj;ر بارگرانێک (گو&zwnj;ناهبارێک) هاوار له که&zwnj;سێک بکات تا باری سه&zwnj;رشانی سووک بکات هیچ باری سووک ناکات هه&zwnj;تا ئه&zwnj;گه&zwnj;ر خزمیشی بێت (جا تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم) ته&zwnj;نها ئه&zwnj;وانه بێدار ده&zwnj;که&zwnj;یته&zwnj;وه که له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن له&zwnj;غه&zwnj;یب و په&zwnj;نهانیدا و نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، ئه&zwnj;وسا ئه&zwnj;وه&zwnj;ی دڵ و ده&zwnj;روون و گوفتارو کرده&zwnj;وه&zwnj;ی خۆی پاك بکاته&zwnj;وه&zwnj;، ئه&zwnj;وه به&zwnj;ڕاستی سووده&zwnj;که&zwnj;ی بۆ خزمه&zwnj;تی، گه&zwnj;ڕانه&zwnj;وه و سه&zwnj;رئه&zwnj;نجامیش بۆ لای خوای گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[19] = 'ئاشکرایه که کوێرو چاوساغ وه&zwnj;ك یه&zwnj;ك نین.';
$t[20] = 'تاریکیه&zwnj;کان و ڕوناکیش چون یه&zwnj;ك نین.';
$t[21] = 'سێبه&zwnj;رو خۆره&zwnj;تاویش وه&zwnj;ك یه&zwnj;ك نین.';
$t[22] = 'زیندووان و مردووانیش هاوتای یه&zwnj;ك نین بێگومان خوا ڕاستی ده&zwnj;دات به&zwnj;گوێی هه&zwnj;ر که&zwnj;سێکدا بیه&zwnj;وێت به&zwnj;ڵام تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ناتوانیت په&zwnj;یامه&zwnj;که&zwnj;ت بگه&zwnj;یه&zwnj;نیت به&zwnj;و که&zwnj;سانه&zwnj;ی که له&zwnj;ناو گۆڕدان (مه&zwnj;به&zwnj;ست کافرانه که وه&zwnj;ك مردوو نابیستن و ئه&zwnj;گه&zwnj;ر بشبیستن سوودی لێ وه&zwnj;رناگرن).';
$t[23] = 'به&zwnj;ڵکو تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ته&zwnj;نها بێدارکه&zwnj;ره&zwnj;وه و ترسێنه&zwnj;ریت و به&zwnj;س.';
$t[24] = 'بێگومان ئێمه تۆمان ڕه&zwnj;وانه&zwnj;کردووه به&zwnj;حه&zwnj;ق و ڕاستیه&zwnj;کی ته&zwnj;واوه&zwnj;وه&zwnj;، تۆ مژده&zwnj;ده&zwnj;ری (ئیماندارانیت)، ترسێنه&zwnj;ری (بێ بڕواکانیت) هیچ گه&zwnj;ل و نه&zwnj;ته&zwnj;وه&zwnj;یه&zwnj;کیش نه&zwnj;بووه که فرستاده و بێدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی بۆ ڕه&zwnj;وانه نه&zwnj;کرابێت.';
$t[25] = 'ئه&zwnj;ی محمد صلی الله علیه وسلم خۆ ئه&zwnj;گه&zwnj;ر بڕوایان به تۆ نه&zwnj;کرد ئه&zwnj;وه دڵت له خۆت دانه&zwnj;مێنێت، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانیش هه&zwnj;ر هه&zwnj;ڵویستیان وابووه&zwnj;، بڕوایان به پێغه&zwnj;مبه&zwnj;ران نه&zwnj;بووه&zwnj;، هه&zwnj;رچه&zwnj;نده ئه&zwnj;وان به موعجیزه&zwnj;و به&zwnj;ڵگه&zwnj;ی به&zwnj;هێزه&zwnj;وه&zwnj;، به په&zwnj;یام و کتێبی ڕوون و ئاشکراوه بۆیان ڕه&zwnj;وانه کرابوون.';
$t[26] = 'پاشان ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕبوون خه&zwnj;شم و قینی خۆمم لێگرتن و له&zwnj;ناوم بردن، جا چۆن بوو به&zwnj;ڵاو تۆڵه&zwnj;ی من.';
$t[27] = 'ئایا سه&zwnj;رنجت نه&zwnj;داوه که به&zwnj;ڕاستی خوا له ئاسمانه&zwnj;وه بارانی باراندووه&zwnj;، جا ئێمه به&zwnj;و بارانه جۆره&zwnj;ها به&zwnj;روبوومی جیاوازمان ده&zwnj;رهێنا که (ره&zwnj;نگیان، قه&zwnj;باره&zwnj;یان، تامیان، بۆنیان، شێوه&zwnj;یان، شوێنیان، وه&zwnj;رزیان) جیاوازه&zwnj;... هه&zwnj;روه&zwnj;ها هه&zwnj;ندێ له&zwnj;که&zwnj;ژو کێوه&zwnj;کانمان به&zwnj;زنجیره کێشاوه&zwnj;، هه&zwnj;یانه چین و هێڵ و نه&zwnj;خشی سپی و سووره و ئه&zwnj;وانیش ڕه&zwnj;نگیان جیاوازه&zwnj;، هه&zwnj;شیانه ڕه&zwnj;شێکی تاریك و تۆخه&zwnj;...';
$t[28] = 'هه&zwnj;روه&zwnj;ها به&zwnj;و شێوه&zwnj;یه له خه&zwnj;ڵکیش، له گیانداران و ماڵاتیش، جۆره&zwnj;ها ڕه&zwnj;نگ و شێوه و قه&zwnj;باره&zwnj;ی جیاواز هه&zwnj;یه&zwnj;. به&zwnj;ڕاستی هه&zwnj;ر زاناکان په&zwnj;ی به&zwnj;و نهێنییانه ده&zwnj;به&zwnj;ن و له&zwnj;ناو به&zwnj;نده&zwnj;کانی خوادا هه&zwnj;ر ئه&zwnj;وان له&zwnj;خوا ده&zwnj;ترسن (له سه&zwnj;رده&zwnj;می علم و زانستیدا ئه&zwnj;م ڕاستیه ڕوونه چونکه زانا و پسپۆڕان کاتێك ورده&zwnj;کاری به&zwnj;دیهێنراوان په&zwnj;ی پێ ده&zwnj;به&zwnj;ن، زۆر هه&zwnj;ست به گه&zwnj;وره&zwnj;یی و ده&zwnj;سه&zwnj;ڵاتی خوا ده&zwnj;که&zwnj;ن)، به&zwnj;ڕاستی ئه&zwnj;و خوایه زۆر باڵاده&zwnj;سته و، زۆریش لێخۆشبووه (کاتێك ڕووی تێ ده&zwnj;که&zwnj;ن و باوه&zwnj;ڕی پێ ده&zwnj;هێنن).';
$t[29] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که په&zwnj;یامی خوا، قورئان ده&zwnj;خوێننه&zwnj;وه و نوێژه&zwnj;کانیان به&zwnj;چاکی ئه&zwnj;نجامداوه&zwnj;و له&zwnj;و ڕزق و ڕۆزی و سامانه جۆراو جۆرانه&zwnj;ی پێمان به&zwnj;خشیوون، به&zwnj;خشیوویانه&zwnj;، به&zwnj;نهێنی و به&zwnj;ئاشکرا... (به&zwnj;نهێنی بۆ ئه&zwnj;وه&zwnj;ی ڕیاو ڕووپامایی تێکه&zwnj;ڵ نه&zwnj;بێت، به ئاشکراش بۆ هاندانی خه&zwnj;ڵکی) ئه&zwnj;وانه به&zwnj;ئومێدی بازرگانیه&zwnj;ك ئه&zwnj;و کاره ده&zwnj;که&zwnj;ن که هه&zwnj;رگیز نائومێد و زه&zwnj;ره&zwnj;مه&zwnj;ند نابن.';
$t[30] = 'بۆ ئه&zwnj;وه&zwnj;ی خوای میهره&zwnj;بانیش وه&zwnj;فا بکات به به&zwnj;خشینی پاداشت بۆیان و له&zwnj;خه&zwnj;ڵات و به&zwnj;خششی زیاتریش به&zwnj;هره&zwnj;مه&zwnj;ندیان بکات، به&zwnj;ڕاستی ئه&zwnj;و خوایه لێخۆشبوو و سوپاسگوزاره&zwnj;...';
$t[31] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ئه&zwnj;وه&zwnj;ی که به&zwnj;وه&zwnj;حی و نیگا بۆمان ڕه&zwnj;وانه&zwnj;کردیت له&zwnj;م قورئانه&zwnj;، ئه&zwnj;وه حه&zwnj;ق و ڕاستیه&zwnj;و به&zwnj;ڕاست دانه&zwnj;ری کتێبه پێشه&zwnj;واکانیشه&zwnj;، به&zwnj;ڕاستی خوا ئاگاو بینایه به به&zwnj;نده&zwnj;کانی.';
$t[32] = 'پاشان که&zwnj;سانێکمان کرده خاوه&zwnj;ن و ڕاگه&zwnj;یه&zwnj;نه&zwnj;ری ئه&zwnj;م قورئانه که هه&zwnj;ڵمانبژاردوون له به&zwnj;نده&zwnj;کانمان، جا هه&zwnj;یانه سته&zwnj;می له خۆی کردووه (چاکه&zwnj;ی که&zwnj;مه&zwnj;و گوناهی زۆره&zwnj;)، هه&zwnj;شیانه مام ناوه&zwnj;ندیه&zwnj;، هه&zwnj;شیانه زۆر گه&zwnj;رم و گوڕه له چاکه&zwnj;کاریدا به ویست و یارمه&zwnj;تی خوا، ئا ئه&zwnj;و هه&zwnj;ڵبژاردنه&zwnj;و ئه&zwnj;و گه&zwnj;رم و گوڕیه له چاکه&zwnj;کاریدا، ڕێزو خه&zwnj;ڵاتێکی زۆر گه&zwnj;وره&zwnj;یه..';
$t[33] = 'ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه (له پاداشتی ئه&zwnj;و هه&zwnj;وڵ و کۆششانه&zwnj;یاندا) ده&zwnj;چنه باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی- عدن-ه&zwnj;وه&zwnj;، له&zwnj;وێ ده&zwnj;ستبه&zwnj;ندی ئاڵتون و مرواری ده&zwnj;کرێته ده&zwnj;ستیان، پۆشاکیشیان له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا ئاوریشمه.';
$t[34] = 'ئینجا هه&zwnj;مووان له خۆشیدا ده&zwnj;ڵێن: سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی که هه&zwnj;رچی غه&zwnj;م و په&zwnj;ژاره&zwnj;یه له دڵ و ده&zwnj;روونی ده&zwnj;رکردین، به&zwnj;ڕاستی په&zwnj;روه&zwnj;ردگارمان زۆر لێ خۆشبوو سوپاسگوزاره&zwnj;.';
$t[35] = 'ئه&zwnj;و خوایه&zwnj;ی که جێنشینی کردین له ماڵی هه&zwnj;میشه&zwnj;یدا به به&zwnj;خشش و ڕێزی تایبه&zwnj;تی خۆی ئیتر هیچ نه&zwnj;خۆشی و ناخۆشیه&zwnj;ك توخنمان ناکه&zwnj;وێت، هیچ ئازارو ئێشێکی ده&zwnj;روونی توشمان نابێت و هیچ بێزاری و ماندوو بوونێکیش ڕومان تێناکات.';
$t[36] = 'له&zwnj;و لاشه&zwnj;وه ئه&zwnj;وانه&zwnj;ی که کافرو بێ باوه&zwnj;ڕ بوون، دۆزه&zwnj;خ بۆیان ئاماده&zwnj;یه&zwnj;، بڕیاری له&zwnj;ناو چوونیان نادرێت، تا بمرن و ڕزگاریان ببێت!! ته&zwnj;نانه&zwnj;ت به&zwnj;شێك له سزاکه&zwnj;ش له&zwnj;سه&zwnj;ریان که&zwnj;م ناکرێته&zwnj;وه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه هه&zwnj;موو کافرو بێ باوه&zwnj;ڕه سه&zwnj;رسه&zwnj;خته&zwnj;کان سزا ده&zwnj;درێن و تۆڵه&zwnj;یان لێ ده&zwnj;ستێنین.';
$t[37] = 'ئه&zwnj;وانه گریه&zwnj;و زاری و هاوارو قیژه&zwnj;و ناڵه&zwnj;یان له&zwnj;ناو دۆزه&zwnj;خدا لێ به&zwnj;رز ده&zwnj;بێته&zwnj;وه&zwnj;و به&zwnj;که&zwnj;ساسیه&zwnj;وه ده&zwnj;ڵێن په&zwnj;روه&zwnj;ردگارا: ده&zwnj;رمان بهێنه&zwnj;و ڕزگارمان بکه له&zwnj;م حاڵه&zwnj;، مه&zwnj;رج بێت کارو کرده&zwnj;وه&zwnj;ی چاك ئه&zwnj;نجام بده&zwnj;ین، نه&zwnj;ك ئه&zwnj;وه&zwnj;ی که جاران ده&zwnj;مانکرد، له وه&zwnj;ڵامیاندا خوا ده&zwnj;فه&zwnj;رموێت: ئایا ئه&zwnj;وه&zwnj;نده ته&zwnj;مه&zwnj;نمان پێ نه&zwnj;به&zwnj;خشین؟ ئه&zwnj;وه&zwnj;ی بیه&zwnj;وێت بیرێك بکاته&zwnj;وه تیایدا بیربکاته&zwnj;وه؟! بێدارکه&zwnj;ره&zwnj;وه&zwnj;شتان بۆ هات، (دیاره هه&zwnj;موویان خه&zwnj;جاڵه&zwnj;ت و سه&zwnj;رشۆڕده&zwnj;بن و قسه&zwnj;یان نامێنێت)، بۆیه پێیان ده&zwnj;وترێت: که&zwnj;واته ده بچێژن سزای دۆزه&zwnj;خ، چونکه سته&zwnj;مکاران هیچ پشتیوانێکیان نیه&zwnj;.';
$t[38] = 'به&zwnj;ڕاستی خوا زاناو شاره&zwnj;زای نهێنیه&zwnj;کانی ئاسمانه&zwnj;کان و زه&zwnj;ویه&zwnj;، به&zwnj;ڕاستی ئه&zwnj;و زاته زانایه به&zwnj;وه&zwnj;ی که له سینه&zwnj;و دڵ و ده&zwnj;روونه&zwnj;کاندا حه&zwnj;شاردراوه&zwnj;..';
$t[39] = 'هه&zwnj;ر ئه&zwnj;و زاته ئێوه&zwnj;ی کرده جێنشین له زه&zwnj;ویدا، جا ئه&zwnj;وه&zwnj;ی که کافرو بێ بڕوا بێت، ئه&zwnj;وه کافر بوونه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ری ده&zwnj;که&zwnj;وێت، بێ باوه&zwnj;ڕیی کافران، هیچیان لای په&zwnj;روه&zwnj;ردگاریان بۆ زیاد ناکات، جگه له خه&zwnj;شم و قینی خوا لێیان، خوانه&zwnj;ناسی کافران هیچ ده&zwnj;ستکه&zwnj;وتێکی نیه جگه له خه&zwnj;ساره&zwnj;ت مه&zwnj;ندی و زه&zwnj;ره&zwnj;رمه&zwnj;نی نه&zwnj;بڕاوه&zwnj;.';
$t[40] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ ده&zwnj;رباره&zwnj;ی ئه&zwnj;و شتانه&zwnj;ی کردوتانه به هاوه&zwnj;ڵ بۆ خواو هاناو هاواریان بۆ ده&zwnj;به&zwnj;ن جگه له خوا، پیشانم بده&zwnj;ن کام شوێنی زه&zwnj;ویان دروست کردووه&zwnj;، ئایا به&zwnj;شداریه&zwnj;کیان هه&zwnj;یه له&zwnj;گه&zwnj;ڵ خوادا له دروستکردنی ئاسمانه&zwnj;کاندا!! یاخود کتێبێکمان پێبه&zwnj;خشیوون تا دڵنیابن له هاوکاریی بته&zwnj;کان!! نه&zwnj;خێر وانی یه&zwnj;، به&zwnj;ڵکو ئه&zwnj;و سته&zwnj;مکارانه هیچ به&zwnj;ڵێنێك به یه&zwnj;کتری ناده&zwnj;ن، ته&zwnj;نها خه&zwnj;ڵه&zwnj;تاندن نه&zwnj;بێت.';
$t[41] = 'به&zwnj;ڕاستی هه&zwnj;ر خوایه که ئاسمانه&zwnj;کان و زه&zwnj;وی ڕاده&zwnj;گرێت و نایه&zwnj;ڵێت له&zwnj;ناو بچێت و له&zwnj;یه&zwnj;ك بترازێت، سوێند به خوا ئه&zwnj;گه&zwnj;ر تێك بچن و له&zwnj;یه&zwnj;ك بترازێن، ئه&zwnj;وه هیچ که&zwnj;سێك نی یه بتوانێت دوای ئه&zwnj;و زاته فریایان بکه&zwnj;وێت، به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;و هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام خۆگرو ئارامگرو لێخۆشبووه&zwnj;.';
$t[42] = 'ئه&zwnj;و کافرو هاوه&zwnj;ڵگه&zwnj;رانه&zwnj;ی قوڕه&zwnj;یش زۆر به&zwnj;گه&zwnj;رمی به&zwnj;خوا سوێندیان ده&zwnj;خواردو ده&zwnj;یانوت: ئه&zwnj;گه&zwnj;ر پێغه&zwnj;مبه&zwnj;رێکی بێدارکه&zwnj;ره&zwnj;وه بۆیان بێت، بێگومان له هه&zwnj;موو گه&zwnj;لانی تر باوه&zwnj;ڕدارتر ده&zwnj;بن، که&zwnj;چی کاتێ که پێغه&zwnj;مبه&zwnj;رێکیان بۆ هات له&zwnj;سه&zwnj;ر که&zwnj;شی و دووره په&zwnj;رێزی زیاتر هیچی تریان لێ نه&zwnj;وه&zwnj;شایه&zwnj;وه&zwnj;...';
$t[43] = 'چونکه خۆیان به&zwnj;زل و گه&zwnj;وره ده&zwnj;زانی له زه&zwnj;ویداو پیلانی شه&zwnj;ڕو تاوانیان کێشا، به&zwnj;مه&zwnj;رجێك پیلانی شه&zwnj;ڕو تاوان ته&zwnj;نها به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;دا ده&zwnj;شکێته&zwnj;وه که نه&zwnj;خشه&zwnj;ی بۆ ده&zwnj;کێشن، مه&zwnj;گه&zwnj;ر ئه&zwnj;مانه به&zwnj;دژایه&zwnj;تی ئیسلام ته&zwnj;نها چاوه&zwnj;ڕێی ئه&zwnj;و کاره&zwnj;ساتانه بکه&zwnj;ن که به&zwnj;سه&zwnj;ر گه&zwnj;لانی پێش ئه&zwnj;ماندا هاتووه&zwnj;، (کاتێ بڕوایان به پێغه&zwnj;مبه&zwnj;ران نه&zwnj;هێناوه&zwnj;، له&zwnj;ناوبران) ئیتر تۆ نابینیت هه&zwnj;رگیز له یاساو به&zwnj;رنامه&zwnj;کانی خوادا گۆڕانکاری پێش بێت، هه&zwnj;رگیز نابینیت یاساو به&zwnj;رنامه&zwnj;کانی خوا له نه&zwnj;خشه&zwnj;ی خۆیان لابده&zwnj;ن. (که به&zwnj;شێکی ڕیسواکردنی پیلانگێڕانه&zwnj;).';
$t[44] = 'ئایا ئه&zwnj;وه نه&zwnj;گه&zwnj;ڕاون به زه&zwnj;ویدا، تا سه&zwnj;رنج بده&zwnj;ن و ته&zwnj;ماشا بکه&zwnj;ن، چۆن بوو سه&zwnj;رئه&zwnj;نجامی گه&zwnj;لانی پێس ئه&zwnj;مان و چیمان به&zwnj;سه&zwnj;ر هێنان، ئه&zwnj;وانه هێزو ده&zwnj;سه&zwnj;ڵاتیان له&zwnj;مان زیاتر بوو، وه&zwnj;نه&zwnj;بێت خوا هیچ شتێك ده&zwnj;سته&zwnj;وسانی بکات له ئاسمانه&zwnj;کان، یان له زه&zwnj;ویدا چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام زانایه&zwnj;و به&zwnj;ده&zwnj;سه&zwnj;ڵات و توانایه&zwnj;.';
$t[45] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا تۆڵه&zwnj;ی له خه&zwnj;ڵکی بسه&zwnj;ندایه له سه&zwnj;ره&zwnj;نجامی کارو کرده&zwnj;وه&zwnj;ی نادروستیاندا، هه&zwnj;رچی زینده&zwnj;وه&zwnj;ر هه&zwnj;یه له&zwnj;سه&zwnj;ر ڕووکاری زه&zwnj;وی نه&zwnj;ده&zwnj;ما، به&zwnj;ڵام خوا ئه&zwnj;وه&zwnj;نده به&zwnj;په&zwnj;له نیه له تۆڵه&zwnj;سه&zwnj;ندن، به&zwnj;ڵکو دوایان ده&zwnj;خات بۆ کاتێکی دیاریکراو، جا کاتێك ڕۆژی خۆی هات، بێگومان ئه&zwnj;و کاته خوای گه&zwnj;وره بینایه به به&zwnj;نده&zwnj;کانی (کارو کرده&zwnj;وه&zwnj;یانی هه&zwnj;موو له&zwnj;به&zwnj;رده&zwnj;ست دایه&zwnj;).';
?>