<?php
$t = array();
$t[1] = '(ص) سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سوره&zwnj;تی (البقره&zwnj;) بده&zwnj;، سوێند به&zwnj;م قورئانه که خاوه&zwnj;نی ئامۆژگاری و شکۆمه&zwnj;ندی و قه&zwnj;در و ڕێزه&zwnj;.';
$t[2] = 'که&zwnj;چی ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون (بڕوا به&zwnj;م قورئانه پیرۆزه ناهێنن، ئه&zwnj;وه له&zwnj;به&zwnj;ر ئه&zwnj;وه نیه که&zwnj;م و کووری و ناته&zwnj;واوی له&zwnj;م قورئانه&zwnj;دا هه&zwnj;بیت) به&zwnj;ڵکو له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;یه که خۆیان به&zwnj;زل ده&zwnj;زانن و بڕیاریانداوه لووت به&zwnj;رز بن له ئاستیدا و دژایه&zwnj;تی بکه&zwnj;ن.';
$t[3] = 'چه&zwnj;نده&zwnj;ها گه&zwnj;ل و نه&zwnj;وه&zwnj;مان پێش نه&zwnj;مان له&zwnj;ناو بردووه (له&zwnj;کاتی ده&zwnj;سپێکردنی سزای ئێمه&zwnj;دا)هاواریان ده&zwnj;کرد، شیوه&zwnj;ن و زاریان بوو، به&zwnj;ڵام تازه کار له&zwnj;کار ترازابوو، وه&zwnj;ختی ڕزگاربوون به&zwnj;سه&zwnj;رچوو.';
$t[4] = 'بێ باوه&zwnj;ڕه&zwnj;کان سه&zwnj;رانسوڕما له&zwnj;وه&zwnj;ی که بیدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;ک له خۆیان هاتووه بۆیان و وتیان: ئائه&zwnj;مه جادوگه&zwnj;رێکی زۆر درۆزنه&zwnj;.';
$t[5] = '(هه&zwnj;روه&zwnj;ها ده&zwnj;یانوت: ئایا پێتان سه&zwnj;یر نیه&zwnj;) هه&zwnj;موو خواکانی کردووه به یه&zwnj;ک خوا، به&zwnj;ڕاستی ئه&zwnj;مه شتێکی زۆر سه&zwnj;یرو سه&zwnj;مه&zwnj;ره&zwnj;یه&zwnj;!!';
$t[6] = 'ده&zwnj;سه&zwnj;ڵاتدار و گه&zwnj;وره&zwnj;کانیان ڕۆیشتن بۆ لای یه&zwnj;کتر، داوای پابه&zwnj;ندی و خۆگریان ده&zwnj;کرد، له پێناو بته&zwnj;کانیاندا و ده&zwnj;رباره&zwnj;ی بانگه&zwnj;وازی یه&zwnj;کخواناسی ده&zwnj;یانوت: به&zwnj;ڕاستی ئه&zwnj;مه شتێکه مه&zwnj;به&zwnj;ستێکی تیادایه (که ببێته گه&zwnj;وره&zwnj;)...!!';
$t[7] = 'ئێمه ئه&zwnj;م ده&zwnj;نگوباسه&zwnj;مان له کۆتا ئایندا (که ئاینی مه&zwnj;سیحیه و سێ خوا ده&zwnj;په&zwnj;رستن) نه&zwnj;بیستووه! ئه&zwnj;مه هه&zwnj;ر ته&zwnj;نها شتێکی ده&zwnj;ست هه&zwnj;ڵبه&zwnj;ست و دروستکراوه&zwnj;!!';
$t[8] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;یانوت: ئایا ڕه&zwnj;وایه له نێوان ئێمه&zwnj;ماناندا قورئان بۆ محمد صلی الله علیه وسلم دابه&zwnj;زێت؟! خوا ده&zwnj;فه&zwnj;رموێت: نه&zwnj;خێر، ئه&zwnj;وانه هه&zwnj;ر له گوماندان به&zwnj;رامبه&zwnj;ر قورئانه&zwnj;که&zwnj;ی من، جا ئه&zwnj;گه&zwnj;ر سزاو ئازاری من بچێژن (ئه&zwnj;وسا باوه&zwnj;ڕی پێ ده&zwnj;هێنن)؟!!';
$t[9] = 'مه&zwnj;گه&zwnj;ر ئه&zwnj;مانه گه&zwnj;نجینه&zwnj;ی ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خشنده&zwnj;یی په&zwnj;روه&zwnj;ردگاری تۆیان به&zwnj;ده&zwnj;سته که زاتێکی باڵاده&zwnj;ست و به&zwnj;خشنده&zwnj;یه (تا دیاریکردنی پێغه&zwnj;مبه&zwnj;ریش به&zwnj;ویستی ئه&zwnj;وان بێت)؟!';
$t[10] = 'ئایا مه&zwnj;گه&zwnj;ر ئه&zwnj;وانه خاوه&zwnj;نی ئاسمانه&zwnj;کان و زه&zwnj;وی و نێوانیانن، تا خێرو بێرو به&zwnj;خشینه&zwnj;وه&zwnj;ش به&zwnj;ئاره&zwnj;زووی ئه&zwnj;وان بێت؟ ئه&zwnj;گه&zwnj;ر وایه با به&zwnj;هۆکارێک به&zwnj;رزبنه&zwnj;وه بۆ ئاسمان و ده&zwnj;ست بخه&zwnj;نه کاروباری خوا...!!';
$t[11] = 'سه&zwnj;ربازانێکی زۆر وا له&zwnj;وێن، له ده&zwnj;سته&zwnj;و گرۆ جۆر به جۆره&zwnj;کان، هه&zwnj;میشه تێکده&zwnj;شکێن.';
$t[12] = 'پێش ئه&zwnj;مان قه&zwnj;ومی نوح و عاد و فیرعه&zwnj;ونی خاوه&zwnj;نی هێزو ده&zwnj;سه&zwnj;ڵات (مه&zwnj;به&zwnj;ست ئه&zwnj;هرامه&zwnj;کانه&zwnj;)';
$t[13] = 'هه&zwnj;روه&zwnj;ها ثمود، قه&zwnj;ومی صالح و قه&zwnj;ومی لوط و خاوه&zwnj;ن باخه چڕو پڕه&zwnj;کان، ئائه&zwnj;و ده&zwnj;سته و گرۆیانه (هه&zwnj;ر هه&zwnj;موویان دژایه&zwnj;تی ئاین و به&zwnj;رنامه&zwnj;ی خوایان کرد، خوای گه&zwnj;وره&zwnj;ش دوای ماوه&zwnj;یه&zwnj;کی دیاریکراو هه&zwnj;موویانی ڕیشه&zwnj;که&zwnj;ن کرد).';
$t[14] = 'هه&zwnj;ریه&zwnj;ک له&zwnj;وانه به&zwnj;رنامه&zwnj;ی هه&zwnj;موو پێغه&zwnj;مبه&zwnj;رانیان به&zwnj;درۆزانی و به&zwnj;و کاره&zwnj;یان، خۆیان شایسته&zwnj;ی تۆڵه&zwnj;ی ئیمه کرد.';
$t[15] = 'ئه&zwnj;وانه چاوه&zwnj;ڕانی هیچ شتێک ناکه&zwnj;ن، ته&zwnj;نها یه&zwnj;کده&zwnj;نگی سامناکی گه&zwnj;وره نه&zwnj;بێت، که کتوپڕ ڕووده&zwnj;دات و پێویستی به دووباره بوونه&zwnj;وه نیه.';
$t[16] = 'که&zwnj;چی خه&zwnj;ڵکی نه&zwnj;فام ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا هه&zwnj;ر چیت بۆ بڕیارداوین پێش ڕۆژی لێپرسینه&zwnj;وه&zwnj;، پێمان ببه&zwnj;خشه&zwnj;.';
$t[17] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تۆ خۆگربه له به&zwnj;رامبه&zwnj;ر هه&zwnj;موو ئه&zwnj;و گوفتاره نادروست و هه&zwnj;ڵانه&zwnj;وه که ده&zwnj;یڵێن، به&zwnj;ڵکو یادی به&zwnj;نده&zwnj;مان داود بکه که توانایی و ده&zwnj;سه&zwnj;ڵاتمان پێ به&zwnj;خشیبوو، به&zwnj;ڕاستی ئه&zwnj;و هه&zwnj;میشه دڵی لای ئێمه بوو، به&zwnj;ته&zwnj;نگ ڕه&zwnj;زامه&zwnj;ندی ئێمه&zwnj;وه بوو.';
$t[18] = 'به&zwnj;ڕاستی ئێمه&zwnj;ش که&zwnj;ژ و کێوه&zwnj;کانمان بۆ ڕام هێنابوو، ئه&zwnj;وانیس یاد و ته&zwnj;سبیحاتی خوایان ده&zwnj;کرد و پێکه&zwnj;وه ده&zwnj;یانوته&zwnj;وه له ده&zwnj;مه و ئێواران و به&zwnj;ره&zwnj;به&zwnj;یانه&zwnj;کاندا.';
$t[19] = 'هه&zwnj;روه&zwnj;ها به&zwnj;ڵنده&zwnj;کانیشمان بۆ ڕام هێنابوو به ده&zwnj;وریشیدا کۆ کرابوونه&zwnj;وه&zwnj;، هه&zwnj;موویان پێکه&zwnj;وه ته&zwnj;سبیحاتیان ده&zwnj;کرد، هه&zwnj;موویان گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;رداری خوا بوون.';
$t[20] = 'ئێمه پاشایه&zwnj;تیه&zwnj;که&zwnj;مان به&zwnj;هێزکردو حیکمه&zwnj;ت و دانایی وقسه و فه&zwnj;رمانی به&zwnj;جێمان پێبه&zwnj;خشی.';
$t[21] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم ئایا هه&zwnj;واڵی ئه&zwnj;وانه&zwnj;ت پێنه&zwnj;گهیشتووه که کێشه&zwnj;یه&zwnj;کیان هه&zwnj;بوو، به دیوارا سه&zwnj;رکه&zwnj;وتن و خۆیان گه&zwnj;یانده میحرابه&zwnj;که&zwnj;ی!';
$t[22] = 'کاتێک خۆیانکرد به ژوورداو چوونه لای داود، ئه&zwnj;ویش لێیان ترسا، به&zwnj;ڵام ئه&zwnj;وان وتیان: مه&zwnj;ترسه&zwnj;، هه&zwnj;ردووکمان ناکۆکیه&zwnj;کمان هه&zwnj;یه و لایه&zwnj;کمان سته&zwnj;می له&zwnj;لایه&zwnj;کمان کردووه&zwnj;، داواکارین که به دادپه&zwnj;روه&zwnj;ریی له نێوانماندا بڕیار بده&zwnj;یت و لایه&zwnj;نی هیچ که&zwnj;سێکمان نه&zwnj;گریت، ڕێنموویشمان بکه بۆ ڕێگه&zwnj;ی ڕاست و دروست.';
$t[23] = 'یه&zwnj;کێکیان وتی: به&zwnj;ڕاستی ئه&zwnj;م برایه&zwnj;م نه&zwnj;وه&zwnj;د و نۆ مه&zwnj;ڕی هه&zwnj;یه به&zwnj;ڵام من ته&zwnj;نها یه&zwnj;ک مه&zwnj;ڕم هه&zwnj;یه&zwnj;، که&zwnj;چی پێم ده&zwnj;ڵێت: ئه&zwnj;و مه&zwnj;ڕه&zwnj;شم بده&zwnj;رێ، به&zwnj;قسه&zwnj;ی زل و زۆرزانیی ده&zwnj;یه&zwnj;وێت لێم زه&zwnj;وت بکات.';
$t[24] = 'داود وتی: سوێند به خوا بێگومان ئه&zwnj;و برایه&zwnj;ت سته&zwnj;می لێ کردوویت به&zwnj;وه&zwnj;ی ده&zwnj;یه&zwnj;وێت تاکه مه&zwnj;ڕه&zwnj;که&zwnj;ی تۆ بخاته سه&zwnj;ر مه&zwnj;ڕه&zwnj;کانی خۆی، له&zwnj;ڕاستیدا زۆربه&zwnj;ی ئه&zwnj;وانه&zwnj;ی شه&zwnj;ریک و هاوکارن سته&zwnj;م له یه&zwnj;کتری ده&zwnj;که&zwnj;ن، جگه له&zwnj;وانه&zwnj;ی که ئیمان و باوه&zwnj;ڕیان هه&zwnj;یه و کاروکرده&zwnj;وه&zwnj;ی چاک ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، ئه&zwnj;وانیش زۆر که&zwnj;من، (ئینجا دووکه&zwnj;سه&zwnj;که دیارنه&zwnj;ما) ئه&zwnj;وسا ئیتر داود زانی که ئێمه ته&zwnj;نها تاقیمان کردۆته&zwnj;وه&zwnj;، (له&zwnj;به&zwnj;ر دوو هۆی سه&zwnj;ره&zwnj;کی، یه&zwnj;که&zwnj;م: فێر ببێت به&zwnj;په&zwnj;له حوکم ده&zwnj;رنه&zwnj;کات... دووه&zwnj;هه&zwnj;م: له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی سوێندی خوارد له&zwnj;سه&zwnj;ر شتێک که لێکۆڵینه&zwnj;وه&zwnj;ی ده&zwnj;رباره&zwnj;ی نه&zwnj;کردبوو) ئیتر داوای لێخۆشبوونی له په&zwnj;روه&zwnj;ردگاری کردو به&zwnj;کڕنووش و سوژده&zwnj;دا چوو، داوای لێبووردنی کرد...';
$t[25] = 'ئێمه&zwnj;ش له&zwnj;و هه&zwnj;ڵه&zwnj;یه&zwnj;ی خۆش بووین، بێگومان ئه&zwnj;و له نزیکان و خۆشه&zwnj;ویستانی ئێمه&zwnj;یه و خۆشترین جێگه و ڕێگه&zwnj;مان بۆ ئاماده کردووه&zwnj;.';
$t[26] = 'ئه&zwnj;ی داود: به&zwnj;ڕاستی ئێمه تۆمان کردۆته جێنشین له&zwnj;م وڵاته&zwnj;دا، فه&zwnj;رمانڕه&zwnj;وایی له نێوان خه&zwnj;ڵکدا له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی ئه&zwnj;نجام بده&zwnj;، هه&zwnj;رگیز شوێنی ئاره&zwnj;زوو مه&zwnj;که&zwnj;وه&zwnj;، تا نه&zwnj;بێته هۆی گومڕاکردن و لادانت له ڕێبازی خوا، چونکه بێگومان ئه&zwnj;وانه&zwnj;ی له ڕێبازی خوا لاده&zwnj;ده&zwnj;ن، سزای به ئێش و پڕ ئازار بۆیان ئاماده&zwnj;یه&zwnj;، به&zwnj;هۆی فه&zwnj;رامۆشکردنی ڕۆژی لێپرسینه&zwnj;وه&zwnj;.';
$t[27] = 'ئێمه ئاسمان و زه&zwnj;وی دروستکراوانی نێوانمان بێ هوده و هه&zwnj;ڕه&zwnj;مه&zwnj;که&zwnj;ی دروست نه&zwnj;کردووه&zwnj;، ئه&zwnj;وه بیرو باوه&zwnj;ڕی ئه&zwnj;وانه&zwnj;یه که بێ باوه&zwnj;ڕبوون، هاوار و ناهوناڵه بۆ ئه&zwnj;وانه&zwnj;ی که کافر و بێ بڕوان له سزای ئاگری دۆزه&zwnj;خ.';
$t[28] = 'نه&zwnj;خێر، ئه&zwnj;و باوه&zwnj;ڕه&zwnj;یان هه&zwnj;ڵه&zwnj;یه&zwnj;، به&zwnj;ڵکو ئایا ڕه&zwnj;وایه پاداشتی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه و کاروکرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;، وه&zwnj;ک ئه&zwnj;وانه لێ بکه&zwnj;ین که تۆوی خراپه&zwnj;یان چاندووه له زه&zwnj;ویدا، یاخود ئایا ئه&zwnj;وانه&zwnj;ی پارێزگار و له&zwnj;خواترسن وه&zwnj;ک خراپه&zwnj;کاران سه&zwnj;یریان بکه&zwnj;ین؟!';
$t[29] = 'ئه&zwnj;م قورئانه کتێبێکی پیرۆزه دامانبه&zwnj;زاندووه بۆ لای تۆ، بۆ ئه&zwnj;وه&zwnj;ی ئایه&zwnj;ته&zwnj;کانی لێک بده&zwnj;نه&zwnj;وه و بیری لێ بکه&zwnj;نه&zwnj;وه و بۆ ئه&zwnj;وه&zwnj;ی خاوه&zwnj;ن بیرو هۆشه&zwnj;کان په&zwnj;ند و ئامۆژگاری وه&zwnj;ربگرن و تێفکرن.';
$t[30] = 'ئێمه سوله&zwnj;یمانمان به&zwnj;خشی به داود، ئای چ به&zwnj;نده&zwnj;یه&zwnj;کی چاک بوو، چونکه بێگومان سوله&zwnj;یمان هه&zwnj;میشه به&zwnj;سۆزه&zwnj;وه داوای لێ خۆشبوونی له خوا ده&zwnj;کرد.';
$t[31] = 'کاتێک ده&zwnj;مه&zwnj;و ئێواره ئه&zwnj;و ئه&zwnj;سپه چاکانه&zwnj;ی که ئاماده کرابوو بۆ غه&zwnj;زا هێنایان و نمایش کران له پێش چاویدا.';
$t[32] = 'ئینجا وتی: من هه&zwnj;میسه حه&zwnj;ز له&zwnj;م جۆره کارانه ده&zwnj;که&zwnj;م و گرنگی به&zwnj;م ئه&zwnj;سپانه به&zwnj;خێر ده&zwnj;زانم، له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;شدا نابێت ببنه هۆی ئه&zwnj;وه&zwnj;ی که له&zwnj;یادی په&zwnj;روه&zwnj;ردگارم غه&zwnj;فڵم بکه&zwnj;ن و یادی فه&zwnj;رامۆش بکه&zwnj;م، ئه&zwnj;سپه&zwnj;کان غاریان کرد تا له چاو ون بوون.';
$t[33] = 'له&zwnj;وه&zwnj;ودوا سلێمان فه&zwnj;رمانیدا که بیانگێڕێته&zwnj;وه و که گه&zwnj;ڕانه&zwnj;وه بۆ لای ده&zwnj;ستی ده&zwnj;هێنا به ڕان و ملیاندا (به&zwnj;و شێوه&zwnj;یه خۆشه&zwnj;ویستی خۆی ده&zwnj;رده&zwnj;بڕی بۆیان، چونکه ئه&zwnj;وانه هۆکارێکن بۆ غه&zwnj;زاو گه&zwnj;یاندنی ئایینی خوا).';
$t[34] = 'سوێن به خوا ئێمه سوله&zwnj;یمانمان تاقیکرده&zwnj;وه و ساوایه&zwnj;کی ناته&zwnj;واومان فڕێدایه سه&zwnj;ر ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تیه&zwnj;که&zwnj;ی، پاشان گه&zwnj;ڕایه&zwnj;وه بۆ لای خوا و په&zwnj;نای بۆ ئێمه هێنا... (چونکه به&zwnj;بێ وتنی - ان شاء الله - به ئومێدی چه&zwnj;ند کوڕێک بوو).';
$t[35] = 'وتی: په&zwnj;روه&zwnj;ردگارا لێم خۆش ببه و ده&zwnj;سه&zwnj;ڵاتێکم پێببه&zwnj;خشه که ده&zwnj;ست نه&zwnj;دات بۆ که&zwnj;سی تر له دوای خۆم، چونکه به&zwnj;ڕاستی ته&zwnj;نها تۆ به&zwnj;خشێنه&zwnj;ری هه&zwnj;موو ناز و نیعمه&zwnj;تێکیت.';
$t[36] = 'ئێمه نزامان گیراکرد و - با - مان بۆ ڕام هێنا، که فه&zwnj;رمانی ئه&zwnj;و به&zwnj;نه&zwnj;رمی هاتووچۆ بکات، بۆهه&zwnj;رشوێنێک ویستی.';
$t[37] = 'هه&zwnj;روه&zwnj;ها شه&zwnj;یتانه&zwnj;کانیشمان کرده خزمه&zwnj;تگوزاری، له هه&zwnj;موو جۆره بیناسازێک و مه&zwnj;له&zwnj;وانێکی ژێر ده&zwnj;ریا.';
$t[38] = 'هه&zwnj;ندێکی تریش به&zwnj;کۆت و زنجیر به&zwnj;سترابوونه&zwnj;وه (تا نه&zwnj;توانن خراپه&zwnj;کاری ئه&zwnj;نجام بده&zwnj;ن).';
$t[39] = 'ئه&zwnj;مه به&zwnj;خششی ئێمه&zwnj;یه بۆ تۆ ئه&zwnj;ی سوله&zwnj;یمان تۆش ده&zwnj;به&zwnj;خشیت، یان نابه&zwnj;خشیت لێپرسینه&zwnj;وه&zwnj;ت نییه و خۆت سه&zwnj;ربه&zwnj;ستیت.';
$t[40] = 'بێگومان سوله&zwnj;یمان له لای ئێمه پله&zwnj;ی زۆر نزیکی هه&zwnj;یه و پاشه&zwnj;ڕۆژ و ئاینده&zwnj;یه&zwnj;کی جوانی هه&zwnj;یه لای ئێمه&zwnj;.';
$t[41] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم سه&zwnj;ر گوزه&zwnj;شته&zwnj;ی به&zwnj;نده&zwnj;ی ئێمه - ئه&zwnj;یوب- یان بۆ باس بکه&zwnj;، کاتێك بانگی له په&zwnj;روه&zwnj;ردگاری کردو وتی: بێگومان شه&zwnj;یتان توشی نه&zwnj;خۆشی و ئازاری کرووم.';
$t[42] = 'ئێمه&zwnj;ش دوعا و نزامان گیرا کرد و وتمان: هه&zwnj;سته&zwnj;، به&zwnj;پێی خۆت ڕابکه&zwnj;، ئه&zwnj;وه ئاوێکی سارده بۆ خۆشتن و خواردنه&zwnj;وه&zwnj;.';
$t[43] = 'ئه&zwnj;وسا هه&zwnj;موو که&zwnj;س و کاریمان پێبه&zwnj;خشی و ئه&zwnj;وه&zwnj;نده&zwnj;ی تریش، ئه&zwnj;وه ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی تایبه&zwnj;تی بوو له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، یاداوه&zwnj;ریش بۆ هه&zwnj;موو خاوه&zwnj;ن بیرو هۆشه&zwnj;کان.';
$t[44] = '(ئه&zwnj;یوب صلی الله علیه وسلم جارێک له خێزانه&zwnj;که&zwnj;ی دڵی ئێشا، سوێندی خوارد که ئه&zwnj;گه&zwnj;ر چاك بۆوه چه&zwnj;ند دارێکی لێبدات، خوای میهره&zwnj;بانیش وه&zwnj;ك ڕێزێك بۆ ئه&zwnj;و ئافره&zwnj;ته وه&zwnj;فاداره فه&zwnj;رمانی پێدا): چه&zwnj;پکێ لاسکی ناسك بگرێت به&zwnj;ده&zwnj;سته&zwnj;&zwnj;وه (به&zwnj;قه&zwnj;ده&zwnj;ر ئه&zwnj;و ژماره&zwnj;یه&zwnj;ی که بڕیاری دابوو و بیماڵێت به هاوسه&zwnj;ره&zwnj;که&zwnj;یداو فه&zwnj;رمووی) سوێنده&zwnj;که&zwnj;شت مه&zwnj;شکێنه&zwnj;، ئێمه بینیمان به&zwnj;ڕاستی خۆگر و به&zwnj;ئارامگر بوو، به&zwnj;نده&zwnj;یه&zwnj;کی چاك و ڕێك و پێك بوو، بێگومان ئه&zwnj;و ته&zwnj;وبه&zwnj;کاره و هه&zwnj;میشه دڵی لای په&zwnj;روه&zwnj;ردگاره&zwnj;.';
$t[45] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم باس و یادی به&zwnj;نده به&zwnj;ڕێزه&zwnj;کانمان ئیبراهیم و ئیسحاق و یه&zwnj;عقوب بکه&zwnj;، هه&zwnj;مویان ده&zwnj;ستێکی باڵایان هه&zwnj;بوو له گه&zwnj;یاندنی ئایینی خوادا، ده&zwnj;ستێکی باڵایان هه&zwnj;بوو له چاکه و چاکه&zwnj;کاریدا، هه&zwnj;روه&zwnj;ها به&zwnj;رچاو ڕوونی و شاره&zwnj;زایی ته&zwnj;واویان هه&zwnj;بوو له هه&zwnj;موو ڕوویه&zwnj;که&zwnj;وه&zwnj;.';
$t[46] = 'ئێمه به&zwnj;ڕاستی کردمانن به&zwnj;به&zwnj;نده&zwnj;یه&zwnj;کی تایبه&zwnj;تی خۆمان به&zwnj;هۆی سیفه&zwnj;تی دنیانه&zwnj;ویستی که ئه&zwnj;ویش یاده&zwnj;وه&zwnj;ری ڕۆژی قیامه&zwnj;ته&zwnj;.';
$t[47] = 'به&zwnj;ڕاستی ئه&zwnj;وانه لای ئێمه له&zwnj;و که&zwnj;سانه&zwnj;ن که هه&zwnj;ڵمان بژاردوون و په&zwnj;سه&zwnj;ندمان کردوون.';
$t[48] = 'هه&zwnj;روه&zwnj;ها باس و یادی -اسماعیل و الیسع و ذوالکفل- یشیان بۆ بکه که هه&zwnj;ریه&zwnj;کێك له&zwnj;وانه له هه&zwnj;ڵبژارده و په&zwnj;سه&zwnj;ندکراوه&zwnj;کانن.';
$t[49] = 'ئه&zwnj;مانه هه&zwnj;مووی ناوبانگی ئه&zwnj;و پێغه&zwnj;مبه&zwnj;ره به&zwnj;ڕێزانه&zwnj;یه و بێگومان بۆ پارێزکار و خواناسیش چاکترین جێگه و ڕێگه و شوێنمان ئاماده کردووه&zwnj;.';
$t[50] = 'باخه&zwnj;کانی به&zwnj;هه&zwnj;شتی عه&zwnj;دنه&zwnj;، که هه&zwnj;ر هه&zwnj;مووی ده&zwnj;روازه&zwnj;کانی له&zwnj;سه&zwnj;ر پشته بۆیان.';
$t[51] = 'تیایدا به ئاسووده&zwnj;یی شانیان داداوه&zwnj;، به&zwnj;رده&zwnj;وام داوای میوه&zwnj;ی زۆر و خواردنه&zwnj;وه&zwnj;ی هه&zwnj;مه&zwnj;جۆر ده&zwnj;که&zwnj;ن.';
$t[52] = 'هه&zwnj;روه&zwnj;ها حۆری هاوته&zwnj;مه&zwnj;نی به حه&zwnj;یاو شه&zwnj;رم لایانه&zwnj;، که ته&zwnj;نها عاشقی هاوسه&zwnj;ره&zwnj;کانیانن و چاو بۆ که&zwnj;سی تر هه&zwnj;ڵنابڕن.';
$t[53] = 'جا ئا ئه&zwnj;م به&zwnj;ڵێنی نازو نیعمه&zwnj;ته&zwnj;ی پێتان دراوه له ڕۆژی پاداشتدا بۆتان ئاماده&zwnj;یه&zwnj;.';
$t[54] = 'به&zwnj;ڕاستی ئه&zwnj;مه ڕزق و ڕۆزیمانه بۆ به&zwnj;خته&zwnj;وه&zwnj;ران، هه&zwnj;رگیز ته&zwnj;واو نابێت و کۆتایی نایه&zwnj;ت.';
$t[55] = 'ئه&zwnj;مه&zwnj;ی باسکرا بۆ ئیماندارانه&zwnj;، به&zwnj;ڵام بۆ سته&zwnj;مکار و یاخی بووه&zwnj;کان، به&zwnj;ڕاستی ناخۆشترین جێگه و ڕێگه ئاماده&zwnj;یه&zwnj;.';
$t[56] = 'دۆزه&zwnj;خه&zwnj;، که ده&zwnj;گه&zwnj;یه&zwnj;نرێنه ناوی، که&zwnj;واته ئای چ شوێنێکی ناساز و سه&zwnj;خت و ناسۆره&zwnj;.';
$t[57] = 'ئه&zwnj;مه و، ده&zwnj;با هه&zwnj;ر بچێژن و بخۆن له ئاو و خۆراکی زۆر گه&zwnj;رم و کێم و زووخاوی داچۆڕاو.';
$t[58] = 'شتی تریس له&zwnj;و بابه&zwnj;تانه هه&zwnj;مه&zwnj;جۆر ده&zwnj;رخواردیان ده&zwnj;درێت.';
$t[59] = '(کاتێك سته&zwnj;مکاران به&zwnj;ره&zwnj;و دۆزه&zwnj;خ ڕاپێچ ده&zwnj;کرێن هه&zwnj;ندێ فریشته پێیان ده&zwnj;ڵێن:) ئه&zwnj;وه تاقمێکه له&zwnj;گه&zwnj;ڵ ئێوه&zwnj;دا پاڵه&zwnj;په&zwnj;ستۆیانه&zwnj;، ئه&zwnj;وانیش ده&zwnj;ڵێن: ده&zwnj;ك به&zwnj;خێر نه&zwnj;یه&zwnj;ن، ئه&zwnj;وانیش ده&zwnj;بێت بگه&zwnj;یه&zwnj;نرێنه دۆزه&zwnj;خ و بچنه ناو ئاگره&zwnj;وه&zwnj;.';
$t[60] = 'له وه&zwnj;ڵامیاندا ده&zwnj;ڵێن: نه&zwnj;خێر ئێوه به&zwnj;خێر نه&zwnj;یه&zwnj;نه&zwnj;وه&zwnj;، ئێوه ئه&zwnj;م شوێنه&zwnj;تان بۆ ئێمه مسۆگه&zwnj;ر کرد ئای که جێگه&zwnj;یه&zwnj;کی ناساز و ناخۆشه&zwnj;.';
$t[61] = 'دوایی به&zwnj;ده&zwnj;م ئاهو ناڵه&zwnj;وه ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا ئه&zwnj;وه&zwnj;ی بوو به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که ئا ئه&zwnj;م شوێنه به&zwnj;نسیبی ئێمه ببێت، سزای چه&zwnj;ند به&zwnj;رامبه&zwnj;ری له&zwnj;ناو دۆزه&zwnj;خدا بۆ پێش بهێنه&zwnj;.';
$t[62] = 'هه&zwnj;ره&zwnj;وه&zwnj;ها ده&zwnj;ڵێن: ئه&zwnj;وه چیمانه بۆچی ئێمه که&zwnj;سانێك نابینین که کاتی خۆی به خراپ و دواکه&zwnj;وتوو کۆنه&zwnj;په&zwnj;رست ده&zwnj;ماندانه قه&zwnj;ڵه&zwnj;م.';
$t[63] = 'کاتی خۆی گاڵته&zwnj;مان پێ ده&zwnj;کردن، توانجمان تێده&zwnj;گرتن که&zwnj;چی ئه&zwnj;مڕۆ یان لێره نین، یان به&zwnj;رچاومان لێڵ و تاریکه و نایانبینن!!';
$t[64] = 'به&zwnj;ڕاستی ئه&zwnj;وه به&zwnj;سه&zwnj;رهات و کێشمه&zwnj;کێش و ده&zwnj;مه ده&zwnj;مێی دۆزه&zwnj;خیه&zwnj;کان و حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه و بۆیان پێش دێت.';
$t[65] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ به&zwnj;ڕاستی من ته&zwnj;نها بێدارکه&zwnj;ره&zwnj;وه&zwnj;م، که جگه&zwnj;له خوای تاکو تاکو ته&zwnj;نها و به&zwnj;ده&zwnj;سه&zwnj;ڵات و خه&zwnj;شمگیر له تاوانباران هیچ خوایه&zwnj;کی تر نیه&zwnj;.';
$t[66] = 'ئه&zwnj;و خوایه په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;موو دروستکراوانی نێوانیانه&zwnj;، به&zwnj;ڵاده&zwnj;سته به&zwnj;سه&zwnj;ر کافراندا، لێخۆشبووه و چاوپۆشی ده&zwnj;کات له ئیمانداران.';
$t[67] = 'پێیان بڵێ: ئه&zwnj;م هه&zwnj;واڵ و ده&zwnj;نگ و باسێکی زۆر گه&zwnj;وره و گرنگن.';
$t[68] = 'به&zwnj;ڵام ئێوه پشتی تێده&zwnj;که&zwnj;ن و گوێی بۆ ناگرن.';
$t[69] = 'خۆ من (مه&zwnj;به&zwnj;ست پێغه&zwnj;مبه&zwnj;ره&zwnj;) هیچ ئاگاداری ئه&zwnj;وه نه&zwnj;بووم که&zwnj;چی گوزاره&zwnj;وه له شوێنه بڵنده&zwnj;کاندا کاتێك که فریشته&zwnj;کان کێشه&zwnj;یان هه&zwnj;بوو له جێنشینی ئاده&zwnj;مدا.';
$t[70] = 'وه&zwnj;حی و نیگام بۆ نایه&zwnj;ت ته&zwnj;نها له&zwnj;به&zwnj;ر ئه&zwnj;وه نه&zwnj;بێت که من بێدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکرام.';
$t[71] = 'کێشه&zwnj;ی فریشته&zwnj;کان کاتێك بوو که په&zwnj;روه&zwnj;ردگارت پێی وتن: من ده&zwnj;مه&zwnj;وێت به&zwnj;شه&zwnj;رێک، له قوڕ دروست بکه&zwnj;م.';
$t[72] = 'جا کاتێك دروستم کرد و له&zwnj;و گیان و ڕۆحه&zwnj;ی که من خاوه&zwnj;نی ئه&zwnj;وم، فووم پێداکردو گیان و ژیانم پێبه&zwnj;خشی ئێوه&zwnj;ی فریشته هه&zwnj;مووتان سووژده&zwnj;ی (رێزو ته&zwnj;قدیری به&zwnj;فه&zwnj;رمانی من) بۆ به&zwnj;رن.';
$t[73] = 'هه&zwnj;موو فریشته&zwnj;کان فه&zwnj;رمانی خوایان به&zwnj;جێ هێنا و سووژده&zwnj;یان برد.';
$t[74] = 'جگه له ئیبلیس نه&zwnj;بێت که له&zwnj;و کاته&zwnj;دا له&zwnj;ناو فریشته&zwnj;کاندا بوو فووی کرده خۆی و خۆی به&zwnj;گه&zwnj;وره زانی و خۆی خسته ڕیزی کافرانه&zwnj;وه&zwnj;.';
$t[75] = 'خوا فه&zwnj;رمووی: ئه&zwnj;ی ئیبلیس ئه&zwnj;وه چی نه&zwnj;یهێشت و نایه&zwnj;ڵێت سووژده به&zwnj;ریت بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که به دوو ده&zwnj;ستی قودره&zwnj;تی خۆم دروستم کردووه&zwnj;؟! ئایا خۆتت به&zwnj;گه&zwnj;وره زانی یان هه&zwnj;ر له زووه&zwnj;وه خۆت لا به&zwnj;رزو گه&zwnj;وره&zwnj;یه&zwnj;.';
$t[76] = 'شه&zwnj;یتان وتی: من له&zwnj;و چاکترو به&zwnj;نرختر و پیرۆزترم، چونکه تۆ منت له ئاگر دروست کردووه و ئه&zwnj;وت له قوڕ به&zwnj;دیهێناوه&zwnj;.';
$t[77] = 'خوا فه&zwnj;رمووی: ماده&zwnj;م وایه ده&zwnj;رچۆ له به&zwnj;هه&zwnj;شت و به&zwnj;جێی بهێڵه چونکه تۆ ئیتر ڕه&zwnj;جم کراوو دوور خراوه&zwnj;ته&zwnj;وه له ڕه&zwnj;حمه&zwnj;تی من.';
$t[78] = 'به&zwnj;ڕاستی له&zwnj;عنه&zwnj;ت و نه&zwnj;فرینی منت له&zwnj;سه&zwnj;ره تا ڕۆژی قیامه&zwnj;ت (چونکه تۆ فه&zwnj;رمانی منت جێبه&zwnj;جێ نه&zwnj;کرد).';
$t[79] = 'شه&zwnj;یتان وتی: په&zwnj;روه&zwnj;ردگارا ده&zwnj;ی که&zwnj;واته مۆڵه&zwnj;تم بده تا ئه&zwnj;و ڕۆژه&zwnj;ی که نه&zwnj;وه&zwnj;کانی ئاده&zwnj;م زیندوو ده&zwnj;کرێنه&zwnj;وه&zwnj;.';
$t[80] = 'خوا فه&zwnj;رمووی: باشه تۆ له مۆڵه&zwnj;ت دراوانیت...';
$t[81] = 'تا ڕۆژو کاتی دیاریکراو.';
$t[82] = 'ئینجا شه&zwnj;یتان وتی: سوێند به&zwnj;ده&zwnj;سه&zwnj;ڵاتی بێ سنوورت شه&zwnj;رت بێت هه&zwnj;ر هه&zwnj;موویان گومڕاو سه&zwnj;رگه&zwnj;ردان بکه&zwnj;م.';
$t[83] = 'جگه له به&zwnj;نده و عه&zwnj;بده دڵسۆزه&zwnj;کانت که هه&zwnj;ڵبژاره&zwnj;ده&zwnj;ی خۆتن (ده&zwnj;سه&zwnj;ڵاتی ئه&zwnj;وانم نیه&zwnj;).';
$t[84] = 'په&zwnj;روه&zwnj;ردگار فه&zwnj;رمووی: من خۆم حه&zwnj;ق و ڕاستیم و هه&zwnj;ر حه&zwnj;ق و ڕاستیش ده&zwnj;ڵێم.';
$t[85] = 'سوێند به&zwnj;زاتی خۆم ده&zwnj;بێت دۆزه&zwnj;خ پڕ بکه&zwnj;م له تۆو له هه&zwnj;موو ئه&zwnj;وانه&zwnj;ی که شوێنی تۆ ده&zwnj;که&zwnj;ون.';
$t[86] = 'جا تۆش ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به&zwnj;و خه&zwnj;ڵکه بڵێ: من هیچ پاداشت و خه&zwnj;ڵاتێکم له ئێوه ناوێت، من له&zwnj;و که&zwnj;سانه&zwnj;ش نیم که شت هه&zwnj;ڵبه&zwnj;ستم و زۆر له خۆم بکه&zwnj;م.';
$t[87] = 'ئه&zwnj;م قورئانه ته&zwnj;نها ئامۆژگاریه بۆ هه&zwnj;موو خه&zwnj;ڵکی جیهان له هه&zwnj;موو کات و شوێنێکدا.';
$t[88] = 'به&zwnj;ڕاستی له داهاتوویه&zwnj;کی نزیکدا، هه&zwnj;ر له دنیادا هه&zwnj;واڵی ڕاستی و دروستی ئه&zwnj;م ئایینه ده&zwnj;زانن، له قیامه&zwnj;تیشدا هه&zwnj;موو شتێك به ته&zwnj;واوی ئاشکراو ڕوون ده&zwnj;بێت.';
?>