<?php
$t = array();
$t[1] = 'هاتنه خواره&zwnj;وه&zwnj;ی ئه&zwnj;م قورئانه له لایه&zwnj;ن خوای باڵاده&zwnj;ست و داناوه&zwnj;یه&zwnj;.';
$t[2] = 'بێگومان ئێمه ئه&zwnj;م قورئانه&zwnj;مان ڕه&zwnj;وانه کردووه بۆ تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم تا حه&zwnj;ق و ڕاستی ڕوون بکاته&zwnj;وه&zwnj;، که&zwnj;واته هه&zwnj;ر خوا بپه&zwnj;رسته و هه&zwnj;ر ملکه&zwnj;چ و دڵسۆزبه بۆ ئاین و به&zwnj;رنامه&zwnj;که&zwnj;ی.';
$t[3] = 'ئاگاداربن: دینی پاك و خاوێن ته&zwnj;نها بۆ خوایه&zwnj;، ئه&zwnj;وانه&zwnj;ش جگه له&zwnj;و زاته که&zwnj;سانی ترو شتی تر ده&zwnj;که&zwnj;نه پشتیوانی خۆیان ده&zwnj;ڵێن: ئێمه ئه&zwnj;م بت و شتانه ناپه&zwnj;رستین ته&zwnj;نها له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی له خوا نزیکمان بخه&zwnj;نه&zwnj;وه&zwnj;، له&zwnj;کاتێکدا ئه&zwnj;وان له&zwnj;خواوه نزیکترن، بێگومان خوا داوه&zwnj;ری ده&zwnj;کات له نێوانیاندا، له&zwnj;و شتانه&zwnj;دا که کێشه&zwnj;یان له&zwnj;سه&zwnj;ری هه&zwnj;یه&zwnj;و ڕایان ده&zwnj;رباره&zwnj;ی جۆراو جۆره&zwnj;، به&zwnj;ڕاستی خوا ئه&zwnj;و بیروباوه&zwnj;ڕ چه&zwnj;وته&zwnj;یان لێوه&zwnj;رناگرێت و هیدایه&zwnj;ت و ڕێنموویی ئه&zwnj;و که&zwnj;سانه ناکات که درۆزن و بێ باوه&zwnj;ڕن.';
$t[4] = 'ئه&zwnj;گه&zwnj;ر خوا بیویستایه ڕۆڵه&zwnj;یه&zwnj;ک بۆ خۆی بڕیار بدات، ئه&zwnj;وا له نێوان ئه&zwnj;و شتانه&zwnj;دا که دروستی کردووه شتێکی هه&zwnj;ڵده&zwnj;بژارد که به ویستی خۆی بوایه نه&zwnj;ک به&zwnj;ویستی ئه&zwnj;وان، پاکی و بێگه&zwnj;ردی بۆ ئه&zwnj;و زاته&zwnj;یه که دووره له شتی ناره&zwnj;واو نابه&zwnj;جێ، ئه&zwnj;و زاته خوایه&zwnj;کی تاک و ته&zwnj;نهاو زۆر به&zwnj;ده&zwnj;سه&zwnj;ڵاته (تۆله&zwnj;ده&zwnj;سێنێت له&zwnj;وانه&zwnj;ی دژایه&zwnj;تی ده&zwnj;که&zwnj;ن و نایپه&zwnj;رستن).';
$t[5] = 'ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی دروست کردووه&zwnj;، به&zwnj;شێوه&zwnj;ی که&zwnj;وانه&zwnj;یی شه&zwnj;و به&zwnj;سه&zwnj;ر ڕۆژدا ده&zwnj;هێنێت، به&zwnj;هه&zwnj;مان شیوه&zwnj;ی که&zwnj;وانه&zwnj;یی ڕۆژیش به&zwnj;سه&zwnj;ر شه&zwnj;ودا ده&zwnj;هێنێت (که ئه&zwnj;مه خۆی به&zwnj;ڵگه&zwnj;یه&zwnj;کی قورئانیه له&zwnj;سه&zwnj;ر شێوازی خڕی زه&zwnj;وی)، ڕۆژو مانگیشی ڕام کردووه و ڕای هێناون (له فه&zwnj;رمانی ده&zwnj;رناچن و له&zwnj;خزمه&zwnj;تی ئاده&zwnj;میزادان)، هه&zwnj;ریه&zwnj;ک له&zwnj;وانه له چه&zwnj;رخ و خولدان تا کاتیکی دیاریکراو، ئاگاداربن: هه&zwnj;رئه&zwnj;و خوایه زۆر به&zwnj;ده&zwnj;سه&zwnj;لاته (تۆله له کافران ده&zwnj;سێنێت و له&zwnj;ده&zwnj;ستی ده&zwnj;رناچن) لێخۆشبووه (چاوپۆشی ده&zwnj;کات له هه&zwnj;له&zwnj;و که&zwnj;م و کووڕی ئیمانداران).';
$t[6] = 'خه&zwnj;ڵکینه بیرتان نه&zwnj;چێت خوا ئێوه&zwnj;ی له تاکه نه&zwnj;فسێك هێناوه&zwnj;ته کایه&zwnj;وه له&zwnj;وه&zwnj;ودوا له&zwnj;و تاکه نه&zwnj;فسه هاوسه&zwnj;ره&zwnj;که&zwnj;شی فه&zwnj;راهه&zwnj;م هێناوه&zwnj;، له ماڵاتیشدا هه&zwnj;شت جۆری دروست کردووه (له ئایه&zwnj;تی -143، 144- له&zwnj;سووره&zwnj;تی- الانحام- دا ئاماژه&zwnj;ی بۆ کردووه&zwnj;) له&zwnj;وه&zwnj;ودوا له سکی دایکتاندا ئێوه دروست ده&zwnj;کات به&zwnj;چه&zwnj;ند قۆناغێك، له&zwnj;ناو سێ تاریکیدا (مه&zwnj;به&zwnj;ست سێ پێسته&zwnj;که&zwnj;یه که کۆرپه&zwnj;له&zwnj;ی تێدا حه&zwnj;شاره&zwnj;دراوه&zwnj;)، جا گۆرانکاری به&zwnj;رده&zwnj;وامه و گه&zwnj;وره بوون و جیابوونه&zwnj;وه&zwnj;ی ئه&zwnj;ندامه&zwnj;کان (ڕۆژ له دوای ڕۆژ زیاتر ڕوو ده&zwnj;دات) ئا ئه&zwnj;وه&zwnj;یه خوا، ئا ئه&zwnj;وه&zwnj;یه په&zwnj;روه&zwnj;ردگارتان، خاوه&zwnj;نی هه&zwnj;موو به&zwnj;دیهێنراوه&zwnj;کانه جگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، جا ئیتر بۆ کوێ لا ده&zwnj;درێن له خواپه&zwnj;رستی؟!';
$t[7] = 'گه&zwnj;ر خۆتان بێ باوه&zwnj;ڕ بکه&zwnj;ن، بێگومان خوا بێ نیازه لێتان، هه&zwnj;رچه&zwnj;نده ڕازی نابێت که به&zwnj;نده&zwnj;کانی بێ باوه&zwnj;ڕبن و ڕێبازی خوانه&zwnj;ناسی بگرن&zwnj;، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر ڕێبازی سو&zwnj;پاسگوزاری بگرنه به&zwnj;ر، به&zwnj;و کاره&zwnj;تان ڕازی ده&zwnj;بێت، دڵنیاش بن که هیچ که&zwnj;س کۆڵی گه&zwnj;ناهی که&zwnj;سی تر هه&zwnj;ڵناگرێت، پاشان گه&zwnj;رانه&zwnj;وه&zwnj;تان بۆ لای په&zwnj;روه&zwnj;ردگارتانه&zwnj;، ئه&zwnj;وسا هه&zwnj;واڵی هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;تان پێده&zwnj;دا که ئه&zwnj;نجامتان ده&zwnj;دا، چونکه ئه&zwnj;و زاته زانایه به هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی، له سینه و دڵ و ده&zwnj;روونه&zwnj;کاندا حه&zwnj;شاردراون.';
$t[8] = 'ئاده&zwnj;میزاد کاتێك ناخۆشی و زه&zwnj;ره&zwnj;رێکی تووشهات، هاناو هاوار بۆ په&zwnj;روه&zwnj;ردگاری ده&zwnj;بات و به&zwnj;دڵشکاوی لێی ده&zwnj;لاڵێته&zwnj;وه&zwnj;، پاشان کاتێك خوا ده&zwnj;رووی لێ کرده&zwnj;وه و ناز و نیعمه&zwnj;تی پێبه&zwnj;خشی، دوعا و نزا و هانا و هاواره&zwnj;کانی ئه&zwnj;وسای فه&zwnj;رامۆش ده&zwnj;کات، چه&zwnj;نده&zwnj;ها هاوه&zwnj;ڵ و شه&zwnj;ریك بۆ خوا بڕیار ده&zwnj;دات، ده&zwnj;یه&zwnj;وێت خه&zwnj;ڵکی له ڕێبازی خوا وێڵ بکات، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم به&zwnj;و جۆره که&zwnj;سه بڵێ: که&zwnj;مێك به خوانه&zwnj;ناسی ڕابوێره&zwnj;، به&zwnj;ڵام دڵنیابه که تۆ نیشته&zwnj;جێی ناو ئاگری دۆزه&zwnj;خیت.';
$t[9] = '(ئایا ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ی که باسکران چاکترن) یان ئه&zwnj;و ئیمانداره&zwnj;ی که له دووتوێی شه&zwnj;وگاردا هه&zwnj;ڵده&zwnj;ستێت بۆ خواپه&zwnj;رستی، سوژده ده&zwnj;بات و به&zwnj;پێوه ده&zwnj;وه&zwnj;ستێت، له لێپرسینه&zwnj;وه&zwnj;ی قیامه&zwnj;ت ده&zwnj;ترسێت و ئومێد و هیوای به&zwnj;میهره&zwnj;بانی په&zwnj;روه&zwnj;ردگاری هه&zwnj;یه&zwnj;، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم بڵێ: ئایا ئه&zwnj;و که&zwnj;سانه&zwnj;ی که ده&zwnj;زانن و شاره&zwnj;زان، وه&zwnj;ک ئه&zwnj;وانه&zwnj;ن که هیچ نازانن و شاره&zwnj;زاییان نیه&zwnj;؟، به&zwnj;ڕاستی ته&zwnj;نها خاوه&zwnj;ن بیروهۆشه&zwnj;کان له&zwnj;م جۆره یاداوه&zwnj;ریانه سوود وه&zwnj;رده&zwnj;گرن و تێده&zwnj;گه&zwnj;ن (له حه&zwnj;قیقه&zwnj;تی دنیا و قیامه&zwnj;ت، وه دڵنیان ژیانی دنیا فرسه&zwnj;تێکی به&zwnj;نرخه بۆ به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی خوا به&zwnj;کاری ده&zwnj;هێنن).';
$t[10] = 'به&zwnj;و به&zwnj;ندانه&zwnj;م بڵێ که خاوه&zwnj;ن باوه&zwnj;ڕن: له په&zwnj;روه&zwnj;ردگارتان بترسن و خواناس بن، بێگومان بۆ ئه&zwnj;وانه&zwnj;ی له&zwnj;م دنیایه&zwnj;دا چاکه ده&zwnj;که&zwnj;ن، پاداشتی چاک هه&zwnj;یه&zwnj;، زه&zwnj;وی په&zwnj;روه&zwnj;ردگاریش فراوانه (ئه&zwnj;گه&zwnj;ر له شوێنێك دنیاتان لێ ته&zwnj;نگ کرایه&zwnj;وه کۆچ بکه&zwnj;ن بۆ شوێنێکی تر) به&zwnj;ڕاستی ته&zwnj;نها خۆگران پاداشتیان بێ سنوور و بێ ئه&zwnj;ندازه&zwnj;یه&zwnj;.';
$t[11] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: به&zwnj;ڕاستی من فه&zwnj;رمانم پێدراوه هه&zwnj;ر خوا بپه&zwnj;رستم و له&zwnj;هه&zwnj;موو ژیانمدا ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری دین و ئاین و به&zwnj;رنامه&zwnj;ی ئه&zwnj;و بم.';
$t[12] = 'فه&zwnj;رمانیشم پێدراوه که یه&zwnj;که&zwnj;مین که&zwnj;س بم له خواپه&zwnj;رستی و ئیسلامه&zwnj;تی و دینداریدا.';
$t[13] = 'هه&zwnj;روه&zwnj;ها بڵێ: من زۆر ده&zwnj;ترسم له سزای ڕۆژێکی پڕ مه&zwnj;ترسی و سامناك، به&zwnj;تایبه&zwnj;ت ئه&zwnj;گه&zwnj;ر سه&zwnj;رکه&zwnj;شی بکه&zwnj;م له فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگارم.';
$t[14] = 'بڵێ: من ته&zwnj;نها هه&zwnj;ر خوا ده&zwnj;په&zwnj;رستم و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئاین و به&zwnj;رنامه&zwnj;که&zwnj;ی ئه&zwnj;و ده&zwnj;بم.';
$t[15] = 'جا ئێوه&zwnj;ش ئه&zwnj;ی بێ باوه&zwnj;ڕان چی ده&zwnj;په&zwnj;رستن جگه له&zwnj;و خوایه&zwnj;، بیپه&zwnj;رستن و پێیان بڵێ: بێگومان خه&zwnj;ساره&zwnj;تمه&zwnj;ندان ئه&zwnj;و که&zwnj;سانه&zwnj;ن خۆیان و خاو و خێزانیان له ده&zwnj;ستچووه له ڕۆژی قیامه&zwnj;تدا، ئاگادار بن: که به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;وه&zwnj;یه خه&zwnj;ساره&zwnj;تمه&zwnj;ندی ئاشکراو زیانی دیارو ڕوون.';
$t[16] = 'ئه&zwnj;وانه له سه&zwnj;رویانه&zwnj;وه په&zwnj;ڵه هه&zwnj;وری دووکه&zwnj;ڵ له ئاگری ده&zwnj;وری داون، له ژێریشیانه&zwnj;وه هه&zwnj;مان په&zwnj;ڵه هه&zwnj;ور و دووکه&zwnj;ڵ هه&zwnj;یه&zwnj;، ئابه&zwnj;و شێوه&zwnj;یه خوای گه&zwnj;وره به&zwnj;نده&zwnj;کانی ده&zwnj;ترسێنێت و پێیان ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;ی به&zwnj;نده&zwnj;کانم پارێزکاربن و خۆتان له سزای من بپارێزن.';
$t[17] = 'جا ئه&zwnj;وانه&zwnj;ی که خۆیان دووره په&zwnj;رێز گرتووه له هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی خه&zwnj;ڵکی ده&zwnj;یپه&zwnj;رستن، جگه له خوای په&zwnj;روه&zwnj;ردگار و به&zwnj;ته&zwnj;وبه و په&zwnj;شیمانیه&zwnj;وه گه&zwnj;ڕاونه&zwnj;ته&zwnj;وه بۆ لای خوا، مژده&zwnj;ی سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازی و به&zwnj;خته&zwnj;وه&zwnj;ری و کامه&zwnj;رانی بۆ ئه&zwnj;وانه&zwnj;یه&zwnj;، ده مژده بده به&zwnj;و به&zwnj;ندانه&zwnj;م...';
$t[18] = 'ئه&zwnj;وانه&zwnj;ی گوێ بۆ قسه&zwnj;و گوفتار ده&zwnj;گرن و په&zwnj;یڕه&zwnj;وی چاکترینی ده&zwnj;که&zwnj;ن، ئا ئه&zwnj;وانه که&zwnj;سانێکن که خوا هیدایه&zwnj;ت و ڕێنموویی کردوون، ئه&zwnj;وانه خاوه&zwnj;نی عه&zwnj;قڵ و ژیری و بیروهۆشن.';
$t[19] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که به&zwnj;هۆی خراپی و خوانه&zwnj;ناسیه&zwnj;وه بریاری سزادانی درابێت (تۆ هیچ کاروباری ئه&zwnj;وت به&zwnj;ده&zwnj;سته&zwnj;)... ئایا تۆ ده&zwnj;توانیت ئه&zwnj;و جۆره که&zwnj;سانه ڕزگار بکه&zwnj;یت که له&zwnj;ناو ئاگری دۆزه&zwnj;خدا گیریان خواردووه&zwnj;؟!';
$t[20] = 'به&zwnj;ڵام ئه&zwnj;وانه&zwnj;ی که له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسان و دیندار بوون کۆشک و ته&zwnj;لاری چه&zwnj;ند نهۆم که ژووره&zwnj;کانی له&zwnj;سه&zwnj;ریه&zwnj;کن بۆیان ئاماده&zwnj;یه&zwnj;، چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێر به به&zwnj;رده&zwnj;م ئه&zwnj;و کۆشکانه&zwnj;دا جاریه&zwnj;، ئه&zwnj;وه&zwnj;ش به&zwnj;ڵێنی خوایه&zwnj;، بێگومان خوایش به&zwnj;ڵێنی خۆی ده&zwnj;باته سه&zwnj;ر.';
$t[21] = 'ئایا تۆ نه&zwnj;تبینیوه&zwnj;و سه&zwnj;رنجت نه&zwnj;داوه&zwnj;: به&zwnj;ڕاستی خوا له ئاسمانه&zwnj;وه بارانی باراندووه&zwnj;، له&zwnj;وه&zwnj;ودوا له&zwnj;ناو ناخی زه&zwnj;ویدا کۆی ده&zwnj;کاته&zwnj;وه&zwnj;، پاشان به&zwnj;شێوه&zwnj;ی کارێز وکانی ده&zwnj;ریده&zwnj;هێنێت، ئه&zwnj;وسا هه&zwnj;ر به&zwnj;و ئاوه کشتوکاڵی هه&zwnj;مه&zwnj;جۆر و هه&zwnj;مه&zwnj;ڕه&zwnj;نگ ده&zwnj;روێنێت، ئینجا وشك ده&zwnj;بێت و زه&zwnj;رد هه&zwnj;ڵده&zwnj;گه&zwnj;ڕێت، پاشان ده&zwnj;یخات و وردو خاشی ده&zwnj;کات، جا به&zwnj;ڕاستی ئا له&zwnj;و شتانه&zwnj;دا یاداوه&zwnj;ری و یادخستنه&zwnj;وه هه&zwnj;یه بۆ که&zwnj;سانی ژیرو هۆشمه&zwnj;ند.';
$t[22] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که خوای میهره&zwnj;بان سینه&zwnj;ی گوشاد کردووه بۆ ئیسلام و له&zwnj;سه&zwnj;ر ڕێبازی نوورو ڕوناکی په&zwnj;روه&zwnj;ردگاریه&zwnj;تی، (وه&zwnj;ك ئه&zwnj;و که&zwnj;سه وایه که پێچه&zwnj;وانه&zwnj;یه&zwnj;؟) هاوار و وه&zwnj;یل بۆ ئه&zwnj;وانه&zwnj;ی که دڵیان ڕه&zwnj;قه له ئاستی قورئان و یادی خوادا، ئا ئه&zwnj;وانه له گومڕاییه&zwnj;کی ئاشکرادا گیریان خواردووه&zwnj;.';
$t[23] = 'خوا جل جلا له جوانترین و پیرۆزترین فه&zwnj;رمایشتی ناردۆته خواره&zwnj;وه که ئه&zwnj;م قورئانه&zwnj;یه&zwnj;، (له جوانی داڕشتندا) بابه&zwnj;ته&zwnj;کانی له یه&zwnj;ك ده&zwnj;چن، هه&zwnj;ندێك باسیشی به&zwnj;شێوازی جیاواز تیادا دووباره بۆته&zwnj;وه&zwnj;، جا کارکردی ئه&zwnj;م قورئانه ئه&zwnj;وه&zwnj;یه که&zwnj;: ته&zwnj;زوو ده&zwnj;هێنێت به گیان و پێستی ئه&zwnj;وکه&zwnj;سانه&zwnj;دا که ترسی په&zwnj;روه&zwnj;ردگاریان له دڵ و ده&zwnj;رووندا هه&zwnj;یه&zwnj;، پاشان گیان و پێست و دڵیان نه&zwnj;رم و ئاماده و چالاك ده&zwnj;بێت بۆ یادی خواو خواپه&zwnj;رستی ئا ئه&zwnj;وه هیدایه&zwnj;ت و ڕێنموویی خوایه که هه&zwnj;ر که&zwnj;س شایسته بێت هیدایه&zwnj;ت و ڕێنموویی ده&zwnj;کات بۆ پله&zwnj;و پایه به&zwnj;رزانه&zwnj;، ئه&zwnj;وه&zwnj;ش که ڕێبازی خوا ون بکات که&zwnj;س نیه ڕێنموویی بکات.';
$t[24] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی به ڕوخسارو ده&zwnj;م و چاوی به&zwnj;رگری له سزای سه&zwnj;ختی ڕۆژی قیامه&zwnj;ت بکات، (وه&zwnj;ك ئه&zwnj;وه وایه که نه&zwnj;ترسێت)، ئه&zwnj;وسا به&zwnj;و سته&zwnj;مکارانه ده&zwnj;وترێت: ده&zwnj;ی بچێژن سزای ئه&zwnj;وه&zwnj;ی که ده&zwnj;تانکرد.';
$t[25] = 'ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانیس بڕوایان به پێغه&zwnj;مبه&zwnj;ران نه&zwnj;بوو، زۆری پێ نه&zwnj;چوو تۆڵه و سزا یه&zwnj;خه&zwnj;ی پێگرتن له شوێنێکه&zwnj;وه که به&zwnj;ته&zwnj;مای نه&zwnj;بوون و هه&zwnj;ستیان پێنه&zwnj;ده&zwnj;کرد.';
$t[26] = 'ئه&zwnj;وسا خوا هه&zwnj;ر له ژیانی دنیادا تاڵاوی کرد به گه&zwnj;روویاندا و ڕیسوای کردن، بێگومان سزای قیامه&zwnj;ت سه&zwnj;ختتره بۆیان ئه&zwnj;گه&zwnj;ر بیانزانیایه&zwnj;.';
$t[27] = 'سوێند بێت به&zwnj;خوا به&zwnj;ڕاستی ئێمه له&zwnj;م قورئانه&zwnj;دا هه&zwnj;موو جۆره نموونه&zwnj;یه&zwnj;کمان بۆ خه&zwnj;ڵکی هێناوه&zwnj;ته&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی یاده&zwnj;وه&zwnj;ری وه&zwnj;ربگرن و بیرێك بکه&zwnj;نه&zwnj;وه له ڕاستیه&zwnj;کان و تێ بگه&zwnj;ن.';
$t[28] = 'قورئانێکی پاراو، به&zwnj;زمانی عه&zwnj;ره&zwnj;بی، هیچ که&zwnj;چی ولاریه&zwnj;کی تێدا به&zwnj;دی ناکرێت، بۆ ئه&zwnj;وه&zwnj;ی پارێزکارو خواناس بن.';
$t[29] = 'خوای گه&zwnj;وره نموونه&zwnj;ی هێناوه&zwnj;ته&zwnj;وه به پیاوێك که گیری خوارد بێت به&zwnj;ده&zwnj;ست چه&zwnj;ند به&zwnj;رپرسێکی شه&zwnj;ڕانی و سه&zwnj;رکه&zwnj;شه&zwnj;وه&zwnj;، پیاوێکی تریش ته&zwnj;نها یه&zwnj;ك به&zwnj;رپرسی هه&zwnj;بێت، ئایا ئه&zwnj;و دوو جۆره نموونه&zwnj;یه&zwnj;، وه&zwnj;ك یه&zwnj;کن، سوپاس و ستایش ته&zwnj;نها شایسته&zwnj;ی خوایه&zwnj;، نه&zwnj;خێر یه&zwnj;کسان نی یه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی ئه&zwnj;و خه&zwnj;ڵکه له&zwnj;م ڕاستیه تێناگه&zwnj;ن و نایزانن و لێکی ناده&zwnj;نه&zwnj;وه&zwnj;.';
$t[30] = 'به&zwnj;ڕاستی تۆش ده&zwnj;مریت و ئه&zwnj;وانیش ده&zwnj;مرن و که&zwnj;س نامێنێت.';
$t[31] = 'پاشان بێگومان ئێوه (واته نه&zwnj;گبه&zwnj;ت و یاخیه&zwnj;کان) ڕۆژی قیامه&zwnj;ت، له&zwnj;لای په&zwnj;روه&zwnj;ردگارتان یه&zwnj;خه&zwnj;ی یه&zwnj;ك ده&zwnj;گرن، داواچی ده&zwnj;بن له&zwnj;سه&zwnj;ر یه&zwnj;کتر.';
$t[32] = 'جاکێ له&zwnj;و که&zwnj;سه سته&zwnj;مکارتره&zwnj;، که درۆ به&zwnj;ده&zwnj;م خواوه هه&zwnj;ڵده&zwnj;به&zwnj;ستێت و ئه&zwnj;و ڕاستیه&zwnj;ی که پێی ڕاگه&zwnj;یه&zwnj;نراوه&zwnj;، بروای پێی نی یه&zwnj;. کاتێک بۆی هاتووه&zwnj;، مه&zwnj;گه&zwnj;ر له دۆزه&zwnj;خدا شوێنی مانه&zwnj;وه بۆ کافران نیه؟!';
$t[33] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ش ڕاستی و حه&zwnj;قیقتی هێناوه&zwnj;و باوه&zwnj;ڕیشی پێیه&zwnj;تی، ته&zwnj;نها ئا ئه&zwnj;وان خواناس و دیندارن.';
$t[34] = 'جا ئه&zwnj;و به خته&zwnj;وه&zwnj;رانه هه&zwnj;ر چیان بوێت له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریانه&zwnj;وه بۆیان ئاماده&zwnj;یه و پێشکه&zwnj;شیان ده&zwnj;کرێت، ئا ئه&zwnj;وه&zwnj;یه پاداشتی چاکه&zwnj;کاران و چاکه&zwnj;خوازان.';
$t[35] = 'بۆ ئه&zwnj;وه&zwnj;ی خوا چاو پۆشی بکات له خراپترینی ئه&zwnj;و گوناهانه&zwnj;ی که ئه&zwnj;نجامیانداوه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ش پاداشتیان بداته&zwnj;وه به&zwnj;چاکتر له&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد.';
$t[36] = 'مه&zwnj;گه&zwnj;ر خوا به&zwnj;س نیه که پشتیوان بێت بۆ به&zwnj;نده&zwnj;ی خۆی که پێغه&zwnj;مبه&zwnj;ره (صلی الله علیه وسلم)، که&zwnj;چی ئه&zwnj;وان تۆ ده&zwnj;ترسێنن به شتی تر جگه له خوا، خۆ ئه&zwnj;وه&zwnj;ی خوا گومڕای بکات و سه&zwnj;ری لێبشێوێنێت، ئیتر که&zwnj;س ناتوانێت ده&zwnj;ستی بگرێت و ڕێنمویی بکات.';
$t[37] = 'ئه&zwnj;وه&zwnj;ش که خوا هیدایه&zwnj;تی بدات و ڕێنموویی بکات و بیخاته سه&zwnj;ر ڕێی ڕاست، که&zwnj;س ناتوانێت گومڕای بکات، مه&zwnj;گه&zwnj;ر هه&zwnj;ر خوا باڵاده&zwnj;ست و تۆڵه&zwnj;سێنه&zwnj;ر نیه (له تاوانکارو تاوانباران)؟';
$t[38] = 'ئه&zwnj;گه&zwnj;ر له بێ باوه&zwnj;ران بپرسیت: کێ ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;؛ بێ دوودڵی و به&zwnj;وپه&zwnj;ڕی دڵنیاییه&zwnj;وه ده&zwnj;ڵێن: خوا، باشه&zwnj;؛ پێمان بڵێن: ئه&zwnj;وه&zwnj;ی هاواری لێ ده&zwnj;&zwnj;که&zwnj;ن له جیاتی خوا، ئه&zwnj;گه&zwnj;ر ئه&zwnj;و خوایه بیه&zwnj;وێت تووشی به&zwnj;ڵاو ناخۆشیه&zwnj;کم بکات، ئایا ئه&zwnj;و بتانه ده&zwnj;توانن فریام بکه&zwnj;ون و نه&zwnj;هێڵن؟ یاخود ئه&zwnj;گه&zwnj;ر بیه&zwnj;وێت ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششێکم پێ ببه&zwnj;خشێت، ئایا ئه&zwnj;وانه ده&zwnj;توانن ئه&zwnj;و ڕه&zwnj;حمه&zwnj;ت و به&zwnj;خششه قه&zwnj;ده&zwnj;غه بکه&zwnj;ن و نه&zwnj;یه&zwnj;ڵن و بیگرنه&zwnj;وه؟! تۆ ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (&lrm;صلی الله علیه وسلم) بڵێ: من خوام به&zwnj;سه که له ته&zwnj;نگانه&zwnj;دا ده&zwnj;گاته فریام، ده&zwnj;با ئه&zwnj;وانه&zwnj;ی پشتیوانیان ده&zwnj;وێت هه&zwnj;ر پشت به&zwnj;و ببه&zwnj;ستن.';
$t[39] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: ئه&zwnj;ی قه&zwnj;وم و هۆزه&zwnj;که&zwnj;م ئێوه له&zwnj;سه&zwnj;ر شێوازی خۆتان کاربکه&zwnj;ن منیش به&zwnj;ڕاستی سه&zwnj;رگه&zwnj;رمی کاری خۆمم، جا له ئاینده&zwnj;دا بۆتان ڕوون ده&zwnj;بێته&zwnj;وه&zwnj;...';
$t[40] = 'که&zwnj;کێ تووشی سزایه&zwnj;ک ده&zwnj;بێت که سه&zwnj;رشۆڕو شه&zwnj;رمه&zwnj;زارو سه&zwnj;رگه&zwnj;ردانی ده&zwnj;کات له دنیادا، له قیامه&zwnj;تیشدا سزایه&zwnj;کی به&zwnj;رده&zwnj;وام داوێنگری ده&zwnj;بێت و له&zwnj;کۆڵی نه&zwnj;بێته&zwnj;وه&zwnj;.';
$t[41] = 'بێگومان ئێمه ئه&zwnj;م قورئانه&zwnj;مان بۆ تۆ دابه&zwnj;زاندووه تا هه&zwnj;رچی ڕاستیه&zwnj;کانه بۆ خه&zwnj;ڵکی ڕوون ببێته&zwnj;وه&zwnj;، جا ئه&zwnj;وه&zwnj;ی ڕێبازی هیدایه&zwnj;ت بگرێته به&zwnj;ر، ئه&zwnj;وه خۆی قازانجی کردووه&zwnj;، ئه&zwnj;وش که گومڕابووه&zwnj;، گومڕابوونه&zwnj;که&zwnj;ی له&zwnj;سه&zwnj;ر خۆی ده&zwnj;که&zwnj;وێت، وه&zwnj;نه&zwnj;بێت تۆ چاودێرو پاڵپێوه&zwnj;نه&zwnj;ریان بیت (تا به&zwnj;زۆر بیانخه&zwnj;یته سه&zwnj;ر ڕێبازی هیدایه&zwnj;ت).';
$t[42] = 'هه&zwnj;ر خوایه که گیانه&zwnj;کان ده&zwnj;کێشێت له&zwnj;کاتی مردنیاندا، هه&zwnj;روه&zwnj;ها ئه&zwnj;و گیانه&zwnj;ش که نه&zwnj;مرد له خه&zwnj;وه&zwnj;که&zwnj;یدا هه&zwnj;ر خوا بۆی ده&zwnj;نێرێته&zwnj;وه&zwnj;، جا ئه&zwnj;و که&zwnj;سه&zwnj;ی که خوا بڕیاری مردنی دابێت ڕوحه&zwnj;که&zwnj;ی ده&zwnj;گرێته&zwnj;وه و ناگه&zwnj;ڕێته&zwnj;وه بۆ لاشه&zwnj;ی، ئه&zwnj;و که&zwnj;سه&zwnj;ش که ڕۆژی ته&zwnj;واو نه&zwnj;بووبێت ده&zwnj;ینێرێته&zwnj;وه بۆی تا کاتێکی دیاریکراو، به&zwnj;ڕاستی ئا له&zwnj;و دیاردانه&zwnj;دا چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ که&zwnj;سانێک که تێفکرن و بیر بکه&zwnj;نه&zwnj;وه (له&zwnj;م ساڵانه&zwnj;ی دوایدا ئارسه&zwnj;ر ئه&zwnj;لیسۆن زانای فیزیای ناوکیی له کۆنگره&zwnj;یه&zwnj;کدا له قاهیره بابه&zwnj;تێکی ده&zwnj;رباره&zwnj;ی مردن و خه&zwnj;و پێشکه&zwnj;ش کرد تیایدا وتبووی: دوای نزیکه&zwnj;ی 25 ساڵ لێکۆڵینه&zwnj;وه و وردبوونه&zwnj;وه بۆم ده&zwnj;رکه&zwnj;وتووه که خه&zwnj;و جۆره مردنێکه&zwnj;، ئه&zwnj;و کاته زانایه&zwnj;کی موسڵمان ئاماژه&zwnj;ی بۆ ئه&zwnj;م ئایه&zwnj;ته&zwnj;کرد، ئیتر ئارسه&zwnj;ر سه&zwnj;ری سوڕماو سه&zwnj;ره&zwnj;نجام شایه&zwnj;تمانی هێنا، ئێستا موسڵمانێکی ئینگلیزی تێکۆشه&zwnj;ره&zwnj;).';
$t[43] = 'نه&zwnj;خێر، ئه&zwnj;وانه بیر ناکه&zwnj;نه&zwnj;وه&zwnj;، به&zwnj;ڵکو سه&zwnj;ر لێشێواوه&zwnj;کان له جیاتی خوا شتی تریان کردووه به تکاکاری خۆیان؟! پێیان بڵێ: باشه&zwnj;، ئه&zwnj;گه&zwnj;ر هیچیشیان به&zwnj;ده&zwnj;ست نه&zwnj;بێت و هه&zwnj;ست و شعوریشیان نه&zwnj;بێت، (هه&zwnj;ر ده&zwnj;یانکه&zwnj;نه تکاکار وبه&zwnj;ته&zwnj;مان شتێکتان بۆ بکه&zwnj;ن؟).';
$t[44] = 'ئینجا پێیان بڵێ: تکاکارو تکاوه&zwnj;رگرتن و تکاکردن، هه&zwnj;ر هه&zwnj;مووی به&zwnj;ده&zwnj;ست خوایه&zwnj;، هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدایه&zwnj;، هه&zwnj;ر ئه&zwnj;و خاوه&zwnj;نیانه&zwnj;، له&zwnj;وه&zwnj;ودوایش هه&zwnj;ر بۆلای ئه&zwnj;و ده&zwnj;برێنه&zwnj;وه&zwnj;.';
$t[45] = 'کاتێکیش ته&zwnj;نها ناوی خوا برا، ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان به&zwnj;قیامه&zwnj;ت و ڕۆژی دوایی نیه دڵیان ده&zwnj;گیرێت و بێزاری دایان ده&zwnj;گرێت، (به&zwnj;ڵام) کاتێکیش باسی شتی تر بکرێت جگه له خوا، ده&zwnj;ستبه&zwnj;جێ دڵخۆش و که&zwnj;یف خۆش ده&zwnj;بن و حه&zwnj;زی پێده&zwnj;که&zwnj;ن.';
$t[46] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بڵێ: ئه&zwnj;ی ئه&zwnj;و خوایه&zwnj;ی که به&zwnj;دیهێنه&zwnj;ری ئاسمانه&zwnj;کان و زه&zwnj;ویت، زانای په&zwnj;نهان و ئاشکرایت، هه&zwnj;ر تۆ دادوه&zwnj;ری ده&zwnj;که&zwnj;یت له نێوان به&zwnj;نده&zwnj;کانتدا، له&zwnj;و شتانه&zwnj;دا که کێشه&zwnj;یان تیاده&zwnj;کرد.';
$t[47] = 'خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;سته&zwnj;میان کردووه&zwnj;، خاوه&zwnj;نی هه&zwnj;ر هه&zwnj;موو زه&zwnj;وی بن، ئه&zwnj;وه&zwnj;نده&zwnj;ی تریشی له&zwnj;گه&zwnj;ڵ بێت، ئه&zwnj;وه له ڕۆژی قیامه&zwnj;تدا هه&zwnj;ر هه&zwnj;مووی ده&zwnj;که&zwnj;نه قوربانی خۆیان و هه&zwnj;مووی ده&zwnj;به&zwnj;خشن له پێناوی ڕزگاریی خۆیاندا کاتێک که ده&zwnj;بینن سزاو ئازاری دۆزه&zwnj;خ زۆرسه&zwnj;خته و له&zwnj;لایه&zwnj;ن خواوه سزاو ئازار و ئه&zwnj;شکه&zwnj;نجه&zwnj;ی وایان بۆ ده&zwnj;رده&zwnj;که&zwnj;وێت که هه&zwnj;رگیز به ته&zwnj;مای نه&zwnj;بوون.';
$t[48] = 'ئه&zwnj;وسا ئیتر سه&zwnj;ره&zwnj;نجامی کاروکردوه ناشرینه&zwnj;کانی خۆیانیان بۆ ده&zwnj;رکه&zwnj;وت و ئه&zwnj;وه&zwnj;ی به گاڵته گرتبوویان و بڕوایان پێی نه&zwnj;بوو داوێنگیریان ده&zwnj;بێت.';
$t[49] = 'جا کاتێک ناخۆشی و ئازارێک تووشی ئینسان بێت، هاناو هاوار بۆ ئێمه ده&zwnj;هێنێت و ئه&zwnj;گه&zwnj;ر نازو نیعمه&zwnj;تێکمان پێبه&zwnj;خشی؛ ده&zwnj;ڵێت؛ ئه&zwnj;مه ته&zwnj;نها شاره&zwnj;زایی خۆمه&zwnj;، زیره&zwnj;کی خۆمه&zwnj;، لێهاتوویی خۆمه&zwnj;، نه&zwnj;خێر وا نیه&zwnj;؛ ئه&zwnj;و به&zwnj;خشینه بۆ تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;یان به&zwnj;م ڕاستیه نازانن.';
$t[50] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مانیش شتی وایان ده&zwnj;وت، وه&zwnj;نه&zwnj;بێت ئه&zwnj;و کاروکرده&zwnj;وه&zwnj;ی که ده&zwnj;یانکرد فریایان که&zwnj;وتبێت و سوودی پێگه&zwnj;یاندبن.';
$t[51] = 'ئه&zwnj;وسا تۆڵه&zwnj;ی ئه&zwnj;و کاروکرده&zwnj;وه ناشرین و نادروستانه&zwnj;ی که کردبوویان داوێنگیریان بوو، ئه&zwnj;وانه&zwnj;ش که سته&zwnj;میان کردووه (له هاوه&zwnj;ڵگه&zwnj;رانی مه&zwnj;ککه&zwnj;و ئه&zwnj;وانه&zwnj;ی ده&zwnj;بنه کۆسپ له&zwnj;به&zwnj;رده&zwnj;م ئاینی خوادا)، ئه&zwnj;وانیش تۆڵه&zwnj;ی کرده&zwnj;وه ناشرینه&zwnj;کانیان داوێنگیریان ده&zwnj;بێت له ئایینده&zwnj;دا، وه&zwnj;نه&zwnj;بێت ئه&zwnj;وانه له تۆڵه&zwnj;ی خوایی ده&zwnj;ربازببن.';
$t[52] = 'ئایا نه&zwnj;یانده&zwnj;زانی که به&zwnj;ڕاستی هه&zwnj;ر خوا ڕزق و ڕۆزی ده&zwnj;به&zwnj;خشێت به هه&zwnj;ر که&zwnj;س که بیه&zwnj;وێت، یان ته&zwnj;نگی ده&zwnj;کاته&zwnj;وه&zwnj;؟ بێگومان ئا له&zwnj;و (که&zwnj;م و زۆری ڕزق و ڕۆزیه&zwnj;دا) به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر هه&zwnj;یه بۆ که&zwnj;سانێک که ئیمان ده&zwnj;هێنن و باوه&zwnj;ریان هه&zwnj;یه&zwnj;.';
$t[53] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: خوا ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;ی به&zwnj;نده&zwnj;کانم.. ئه&zwnj;وانه&zwnj;ی که خۆتان گوناهبار کردووه و هه&zwnj;ڵه&zwnj;تان زۆره&zwnj;، نائومێد مه&zwnj;بن له ڕه&zwnj;حمه&zwnj;تی خوا، چونکه به&zwnj;ڕاستی ئه&zwnj;گه&zwnj;ر ئێوه ته&zwnj;وبه&zwnj;ی ڕاست و دروست بکه&zwnj;ن، خوا له&zwnj;هه&zwnj;موو گوناهو هه&zwnj;ڵه&zwnj;کانتان خۆش ده&zwnj;بێت، چونکه بێگومان ئه&zwnj;و خوایه زۆر لێخۆشبوو لێبورده&zwnj;یه و زۆر به سۆزو میهره&zwnj;بانیشه&zwnj;.';
$t[54] = 'هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;میشه و به&zwnj;رده&zwnj;وام دڵتان لای خوابێت و بگه&zwnj;ڕێنه&zwnj;وه بۆ لای په&zwnj;روه&zwnj;ردگارتان و ته&zwnj;سلیمی ئه&zwnj;وبن، پێش ئه&zwnj;وه&zwnj;ی که سزای خوا یه&zwnj;خه&zwnj;تان بگرێت له&zwnj;وه&zwnj;ودواش سه&zwnj;رکه&zwnj;توو نابن که&zwnj;س نابێت به&zwnj;رگریتان لێ بکات.';
$t[55] = 'کۆشش بکه&zwnj;ن؛ به&zwnj;رده&zwnj;وام شوێنی چاکترینی ئه&zwnj;و فه&zwnj;رمانانه بکه&zwnj;ون که له&zwnj;لایه&zwnj;ن خواوه ڕه&zwnj;وانه&zwnj;کراوه بۆتان (بێگومان له هه&zwnj;موو شتێکدا چاک و چاکتر هه&zwnj;یه&zwnj;، نوێژه فه&zwnj;رزه&zwnj;کان ئه&zwnj;نجام بدرێت چاکه&zwnj;، به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر سوننه&zwnj;ته&zwnj;کان و شه&zwnj;ونوێژیش بکرێت چاکتره&zwnj;، زه&zwnj;کات بدرێت چاکه&zwnj;، به&zwnj;ڵام به&zwnj;خشینی زۆرترو زیاتریش له زه&zwnj;کات، چاکتره&zwnj;، تۆڵه&zwnj;سه&zwnj;ندن له که&zwnj;سێک که حه&zwnj;قت به&zwnj;سه&zwnj;ریه&zwnj;وه هه&zwnj;یه ڕێی پێدراوه&zwnj;، به&zwnj;ڵام چاوپۆشی و لێ بووردن چاکتره&zwnj;... هتد) پێش ئه&zwnj;وه&zwnj;ی سزاو ئازاری کتوپڕ یه&zwnj;خه&zwnj;تان پێبگرێت وله&zwnj;کاتێکدا ئێوه هه&zwnj;ست به نزیکی سزاکه نه&zwnj;که&zwnj;ن.';
$t[56] = 'نه&zwnj;وه&zwnj;کو یه&zwnj;کێک بڵێت: ئاخ و داخ و په&zwnj;شیمانی بۆ ئه&zwnj;و هه&zwnj;موو لادان و نادروستیانه&zwnj;ی که کردم، له فه&zwnj;رمان و به&zwnj;رنامه&zwnj;ی خوادا، بێگومان له&zwnj;و که&zwnj;سانه&zwnj;ش بووم که گاڵته&zwnj;یان به&zwnj;دین و به&zwnj;رنامه&zwnj;ی خوا ده&zwnj;کرد و (ئیماندارنم به دواکه&zwnj;وتوو کۆنه&zwnj;په&zwnj;رست ده&zwnj;زانی).';
$t[57] = 'یاخود بڵێت: ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی خوا هیدایه&zwnj;ت و ڕێنموویی بکردمایه&zwnj;، ئه&zwnj;وه من له ڕیزی پارێزکاران و چاکاندا ده&zwnj;بووم!!';
$t[58] = 'یاخود کاتێک که سزاو ئازار ده&zwnj;بینێت بڵێت: خۆ ئه&zwnj;گه&zwnj;ر بێگومان مۆڵه&zwnj;تێک ببێت و جارێکی تر بگه&zwnj;ڕێمه&zwnj;وه بۆ دنیا، ئه&zwnj;وه مه&zwnj;رج بێت له چاکه&zwnj;کاران بم.';
$t[59] = 'نه&zwnj;خێر وانى یه&zwnj;، مۆڵه&zwnj;ت به&zwnj;سه&zwnj;رچوو، چونکه به&zwnj;ڕاستی کاتی خۆی ئایه&zwnj;ت و فه&zwnj;رمانی منت بۆ هات، که&zwnj;چی تۆ ئه&zwnj;و کاته بڕوات پێ نه&zwnj;ده&zwnj;کرد و به&zwnj;درۆت ده&zwnj;زانی، فیزو ده&zwnj;مارگیریت ده&zwnj;کردو، لوطت هه&zwnj;ڵده&zwnj;بڕی له&zwnj;ئاستیدا، تۆ هه&zwnj;ر له ڕیزی بێ بڕواکاندا بوویت (ئیتر ئێستا بۆچی بێ شه&zwnj;رمانه داوای گه&zwnj;ڕانه&zwnj;وه بۆ دنیا ده&zwnj;که&zwnj;یت؟!).';
$t[60] = 'جا ڕۆژی قیامه&zwnj;ت ده&zwnj;بینیت، ئه&zwnj;وانه&zwnj;ی درۆیان به&zwnj;ناوی خواوه ده&zwnj;کرد (شتی ناڕه&zwnj;وایان تێکه&zwnj;ڵی دین ده&zwnj;کرد) ڕووخساریان ڕه&zwnj;ش و تاریکه&zwnj;، مه&zwnj;گه&zwnj;ر شوێن و جێگه بۆ ئه&zwnj;و خۆ به زل زان و لووت به&zwnj;رزانه له ئاگری دۆزه&zwnj;خدا نی یه&zwnj;؟!';
$t[61] = 'له&zwnj;ولاشه&zwnj;وه خوای گه&zwnj;وره ئه&zwnj;وانه ڕزگار ده&zwnj;کات که دینداری و پارێزکارییان ده&zwnj;کرد: به&zwnj;هۆی سه&zwnj;رکه&zwnj;وتن و ده&zwnj;رچوونیان له تاقیگه&zwnj;ی دنیادا، ئیتر هیچ جۆره ناخۆشیه&zwnj;کیان تووش نایه&zwnj;ت و دڵگرانیش نابن.';
$t[62] = 'خوا به&zwnj;دیهێنه&zwnj;ری هه&zwnj;موو شتێکه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش سه&zwnj;رپه&zwnj;رشتیار و پارێزه&zwnj;ری هه&zwnj;موو شتێکه&zwnj;.';
$t[63] = 'کلیک و جڵه&zwnj;وی ئاسمانه&zwnj;کان و زه&zwnj;ویش هه&zwnj;ر به&zwnj;ده&zwnj;ست ئه&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش که بێ باوه&zwnj;ڕ بوون به ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا، ئا ئه&zwnj;وانه خۆیان ڕه&zwnj;نجه&zwnj;ڕۆ و زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[64] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بڵێ: ئه&zwnj;ی نه&zwnj;فامه&zwnj;ن، ده&zwnj;تانه&zwnj;وێت و فه&zwnj;رمانم پێده&zwnj;که&zwnj;ن که&zwnj;جگه له خوا شتی تر بپه&zwnj;رستم؟!';
$t[65] = 'سوێند به&zwnj;خوا بادڵنیابن که وه&zwnj;حی و نیگا نێردراوه بۆ تۆو بۆ ئه&zwnj;وانه&zwnj;ی پێش تۆش: ئه&zwnj;گه&zwnj;ر هاوه&zwnj;ڵگه&zwnj;ر بیت و شه&zwnj;ریک بۆ خوا بڕیار بده&zwnj;یت کاروکرده&zwnj;وه چاکه&zwnj;کانیشت پووچه و ده&zwnj;چیته ڕیزی خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندانه&zwnj;وه&zwnj;.';
$t[66] = 'نه&zwnj;خێر به&zwnj;گوێیان مه&zwnj;که&zwnj;، که&zwnj;واته هه&zwnj;ر ته&zwnj;نها خوا بپه&zwnj;رسته و له ده&zwnj;سته&zwnj;ی سوپاسگوزاران به&zwnj;.';
$t[67] = 'خوانه&zwnj;ناسان؛ قه&zwnj;دری خوایان وه&zwnj;ک پێوست نه&zwnj;گرت، ڕێزیان بۆ دانه&zwnj;نا، به&zwnj;مه&zwnj;رجێک هه&zwnj;ر هه&zwnj;موو گۆی زه&zwnj;وی پڕ مشتێکی قودره&zwnj;تی ئه&zwnj;و زاته&zwnj;یه له ڕۆژی قیامه&zwnj;تدا، ئاسمانه&zwnj;کانیش به&zwnj;ده&zwnj;ستی قودره&zwnj;تی ڕاست و دروستی خوا پێچراوه&zwnj;ن (ئاماژه&zwnj;یه بۆ ده&zwnj;سته&zwnj;ڵاتی بێ سنووری ئه&zwnj;و زاته&zwnj;)، پاکی و بێگه&zwnj;ردی و بڵندی بۆ ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره مه&zwnj;زنه&zwnj;ی شایسته&zwnj;ی ئه&zwnj;وه نی یه نه&zwnj;فامان هاوه&zwnj;ڵی بۆ بڕیار بده&zwnj;ن و قه&zwnj;دری نه&zwnj;زانن.';
$t[68] = 'له&zwnj;و ڕۆژه&zwnj;دا فوو ده&zwnj;کرێت به - صور - دا، (مه&zwnj;گه&zwnj;ر هه&zwnj;ر خوا خۆی بزانێت چ ده&zwnj;زگایه&zwnj;که&zwnj;) هه&zwnj;ر که&zwnj;س له ئاسمانه&zwnj;کان و زه&zwnj;ویدا مابێت خێرا ده&zwnj;مرێت، مه&zwnj;گه&zwnj;ر که&zwnj;سێک خوا ویستی له&zwnj;سه&zwnj;ر مردنی نه بێت، له&zwnj;وه&zwnj;ودوا فوویه&zwnj;کی تری پیادا ده&zwnj;کرێته&zwnj;وه&zwnj;، جا ده&zwnj;ستبه&zwnj;جێ هه&zwnj;رهه&zwnj;موو خه&zwnj;ڵکی هه&zwnj;ستاونه&zwnj;ته سه&zwnj;رپێ و به&zwnj;سه&zwnj;رسامیه&zwnj;وه چاوه&zwnj;ڕوانن.';
$t[69] = '(دیاره که خۆری دنیا نه&zwnj;ماوه&zwnj;، زه&zwnj;وی بۆته گۆڕه&zwnj;پانێکی فراوان) ئه&zwnj;وسا به&zwnj;نوورێک که خوا فه&zwnj;راهه&zwnj;می ده&zwnj;هێنێت زه&zwnj;وی ڕوناک ده&zwnj;بێته&zwnj;وه&zwnj;، کارنامه&zwnj;و و دۆسیه&zwnj;کانیش دانراون، سه&zwnj;ره&zwnj;تا پێغه&zwnj;مبه&zwnj;ران و شه&zwnj;هیدان ده&zwnj;هێنرێن و دادوه&zwnj;ری له نێوانیاندا ئه&zwnj;نجام ده&zwnj;درێت به&zwnj;شێوه&zwnj;یه&zwnj;کی حه&zwnj;ق و ڕاستی، بێگومان ئه&zwnj;وان هیچ جۆره سته&zwnj;مێکیان لێ ناکرێت.';
$t[70] = 'هه&zwnj;ر که&zwnj;سه ئه&zwnj;نجامی کرده&zwnj;وه&zwnj;ی خۆی وه&zwnj;رده&zwnj;گرێت به&zwnj;ته&zwnj;واوی، سه&zwnj;رئه&zwnj;نجامی کۆششی خۆی ده&zwnj;ست ده&zwnj;که&zwnj;وێت، چونکه ئه&zwnj;و زاته زانایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که خه&zwnj;ڵکی ده&zwnj;یکه&zwnj;ن.';
$t[71] = 'ئه&zwnj;وسا ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕ بوون، ڕاپێچ ده&zwnj;کرێن به&zwnj;ره&zwnj;و دۆزه&zwnj;خ ده&zwnj;سته ده&zwnj;سته و تاقم تاقم ده&zwnj;گوێزرێنه&zwnj;وه&zwnj;، تا کاتێک ده&zwnj;گه&zwnj;نه به&zwnj;ر قاپیه&zwnj;کانی، (هه&zwnj;موو مۆڵده&zwnj;خۆن له&zwnj;به&zwnj;رده&zwnj;میدا، دوای ماوه&zwnj;یه&zwnj;ک) بۆیان ده&zwnj;کرێته&zwnj;وه&zwnj;، فریشته&zwnj;ی به&zwnj;رپرس و سه&zwnj;رپه&zwnj;رشتیار سه&zwnj;رزه&zwnj;نشتیان ده&zwnj;کات و پێیان ده&zwnj;ڵێت: باشه&zwnj;، مه&zwnj;گه&zwnj;ر پێغه&zwnj;مبه&zwnj;ران له خۆتان ڕه&zwnj;وانه نه&zwnj;کرابوو بۆتان که ئایه&zwnj;ته&zwnj;کانی په&zwnj;روه&zwnj;ردگاری خۆتان به&zwnj;سه&zwnj;ردا بخوێنێته&zwnj;وه&zwnj;، وه یاداوه&zwnj;ریتان بکات و داتان بچڵه&zwnj;کێنێت له گه&zwnj;یشتنی ئه&zwnj;م ڕۆژه&zwnj;؟! به&zwnj;ده&zwnj;م ئاخوداخ و ناڵه&zwnj;وه ده&zwnj;ڵێن: به&zwnj;ڵێ، به&zwnj;ڵام تازه&zwnj;، به&zwnj;ڵێ، سوودی نیه و بڕیاری سزاو ئازار دراوه به&zwnj;سه&zwnj;ر کافر و بێ بڕواکاندا.';
$t[72] = 'ئه&zwnj;وسا ئیتر پێیان ده&zwnj;وترێت: ده&zwnj;ی ئیتر له قاپیه&zwnj;کانی دۆزه&zwnj;خه&zwnj;وه بۆ ژووره&zwnj;وه&zwnj;، بچنه ناوی ژیانی ناخۆش و سه&zwnj;ختیش بۆتان به&zwnj;رده&zwnj;وامه&zwnj;، مردنیش نیه ئای که چاند ناسازه&zwnj;، جێگه و ڕێگه&zwnj;ی لووت به&zwnj;رزو خۆ به&zwnj;زل زانه&zwnj;کان&zwnj;.';
$t[73] = 'له&zwnj;ولاشه&zwnj;وه ئه&zwnj;وانه&zwnj;ی که پارێزکار و به&zwnj;نده&zwnj;ی ملکه&zwnj;چی په&zwnj;روه&zwnj;ردگاریان بوون، ئه&zwnj;وانه&zwnj;ی حسابیان بۆ ئه&zwnj;و ڕۆژه ده&zwnj;کرد ده&zwnj;سته ده&zwnj;سته و پۆل پۆل به&zwnj;وپه&zwnj;ڕی ڕێزه&zwnj;وه به&zwnj;ڕێ ده&zwnj;کرێن به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت، هه&zwnj;تا کاتێ که ده&zwnj;گه&zwnj;نه ئه&zwnj;وێ ده&zwnj;روازه&zwnj;کانی له ڕوویاندا کراوه&zwnj;یه و له سه&zwnj;ر پشته&zwnj;، فریشته&zwnj;کانی به&zwnj;رپرس و سه&zwnj;رپه&zwnj;رشتیارانی به&zwnj;هه&zwnj;شت زۆر به&zwnj;گه&zwnj;رمی و ڕووخۆشیه&zwnj;وه پێشوازییان لێ ده&zwnj;که&zwnj;ن و ده&zwnj;ڵێن: درود و سڵاوتان لێبێت، ئێوه پاک و خاوێنن، که&zwnj;واته فه&zwnj;رموون بچنه ناو به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه&zwnj;دا.';
$t[74] = 'ئه&zwnj;وسا ئیتر ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه ده&zwnj;ڵێن: سوپاس و ستایش بۆ ئه&zwnj;و خوایه&zwnj;ی، که به&zwnj;ڵێنی خۆی بۆمان برده سه&zwnj;ر و کردینی به&zwnj;خاوه&zwnj;نی سه&zwnj;رزه&zwnj;مینی به&zwnj;هه&zwnj;شت و له هه&zwnj;رشوێنێک و له هه&zwnj;ر جێیه&zwnj;کیدا بمانه&zwnj;وێت سه&zwnj;ربه&zwnj;ستانه ژیان ده&zwnj;به&zwnj;ینه سه&zwnj;ر، ئای چه&zwnj;نده پاداشتی تێکۆشه&zwnj;ران و خه&zwnj;باتکاران چاک و پاک و به&zwnj;نرخه&zwnj;.';
$t[75] = 'ئه&zwnj;وسا ئیتر فریشته&zwnj;کان ده&zwnj;بینیت ده&zwnj;وری ته&zwnj;ختی - شاهی شاهانیان - داوه و سه&zwnj;رگه&zwnj;رمی ته&zwnj;سبیحات و سوپاس و ستایشی په&zwnj;روه&zwnj;ردگاریانن و حه&zwnj;ق و دادوه&zwnj;ری له نێوان هه&zwnj;موواند جێبه&zwnj;جێ کرا، ئینجا به یه&zwnj;کده&zwnj;نگ له هه&zwnj;موو لایه&zwnj;که&zwnj;وه ده&zwnj;وترێت: الحمد لله ڕب العالمین.';
?>