$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (البقرة) بده&zwnj;..';
$t[2] = 'هاتنه خواره&zwnj;وه&zwnj;ی ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن خوای باڵاده&zwnj;ست و شاره&zwnj;زا و زاناوه&zwnj;یه&zwnj;.';
$t[3] = 'هه&zwnj;میشه ئه&zwnj;و زاته لێخۆش بووی گوناهه&zwnj;، وه&zwnj;رگری ته&zwnj;وبه و په&zwnj;شیمانیه له به&zwnj;نده گوناهباره&zwnj;کانی تۆڵه&zwnj;سێنه&zwnj;ره زۆر به&zwnj;سه&zwnj;ختی له یاخی بووان و سته&zwnj;مکاران، ده&zwnj;سته&zwnj;ڵاتداره و خاوه&zwnj;ن و به&zwnj;خشێنه&zwnj;ری ناز و نیعمه&zwnj;ته&zwnj;کانه&zwnj;، هیچ خوایه&zwnj;ک نیه جگه له&zwnj;و، سه&zwnj;ره&zwnj;نجامیش هه&zwnj;ر بۆ لای ئه&zwnj;وه&zwnj;.';
$t[4] = 'هیچ که&zwnj;س له به&zwnj;رامبه&zwnj;ری ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خواوه موجاده&zwnj;له و ده&zwnj;مه&zwnj;ده&zwnj;مێ ناکات، جگه له&zwnj;وانه&zwnj;ی که بێ بڕواو خوانه&zwnj;ناس بوون، ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه و سلم) که&zwnj;واته ده&zwnj;سه&zwnj;ڵات و سه&zwnj;رمایه و هێزیان فریوت نه&zwnj;دات.';
$t[5] = 'پێش ئه&zwnj;مانیش (کافرانی مه&zwnj;ککه و ئه&zwnj;وانه&zwnj;ی له دوایان دێن) قه&zwnj;ومی نوح و گه&zwnj;لێ ده&zwnj;سته و گرۆی تریش باوه&zwnj;ڕیان به پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان نه&zwnj;کرد، هه&zwnj;ر گه&zwnj;لێک په&zwnj;لاماری پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;دا تا ده&zwnj;ستگیری بکات و ئازاری بدات و له به&zwnj;رنامه&zwnj;که&zwnj;ی کای بدات، هه&zwnj;روه&zwnj;ها موجاده&zwnj;له و ده&zwnj;مه&zwnj;ده&zwnj;مێیان ده&zwnj;کرد بۆ ئه&zwnj;وه&zwnj;ی حه&zwnj;ق داپۆشن و نه&zwnj;یهێڵن، جا منیش پێچامنه&zwnj;وه و له&zwnj;ناوم بردن، بڕوانن چۆن تۆڵه&zwnj;یه&zwnj;کم لێسه&zwnj;ندن!';
$t[6] = 'ئا به&zwnj;و شێوه&zwnj;یه بڕیاری په&zwnj;روه&zwnj;ردگاری تۆ ئه&zwnj;ی محمد (صلی الله علیه و سلم) چه&zwnj;سپا به&zwnj;سه&zwnj;ر ئه&zwnj;وانه&zwnj;دا که خوانه&zwnj;ناس و بێ باوه&zwnj;ڕ بوون، به&zwnj;ڕاستی ئه&zwnj;وانه نیشته&zwnj;جێی دۆزه&zwnj;خن.';
$t[7] = 'ئه&zwnj;و فریشتانه&zwnj;ی که عه&zwnj;رش و ته&zwnj;ختی په&zwnj;روه&zwnj;ردگار هه&zwnj;ڵده&zwnj;گرن، ئه&zwnj;وانه&zwnj;ش که به&zwnj;ده&zwnj;وریدا ده&zwnj;سوڕێنه&zwnj;وه&zwnj;، ته&zwnj;سبیحات و ستایش و سوپاسی په&zwnj;روه&zwnj;ردگاریان ده&zwnj;که&zwnj;ن، ئیمان و باوه&zwnj;ڕی زۆر به&zwnj;هێزو دامه&zwnj;زراویان پێیه&zwnj;تی، داوای لێخۆشبوونیش ده&zwnj;که&zwnj;ن بۆ ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه&zwnj;، ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا! ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی تۆ، زانست و زانیاری تۆ، هه&zwnj;موو شتیێکت پێ گرتۆته&zwnj;وه&zwnj;، که&zwnj;وابوو خۆش ببه له&zwnj;وانه&zwnj;ی که ته&zwnj;وبه&zwnj;یان کردووه و له گوناهه&zwnj;کانیان په&zwnj;شیمانن و شوێن به&zwnj;رنامه و ڕێبازی تۆ ده&zwnj;که&zwnj;ون، هه&zwnj;روه&zwnj;ها له ئاگری دۆزه&zwnj;خیش بیانپارێزه&zwnj;.';
$t[8] = 'په&zwnj;روه&zwnj;ردگار بیانخه&zwnj;ره ئه&zwnj;و باخ و باخاته&zwnj;ی به&zwnj;هه&zwnj;شتی عه&zwnj;دنه&zwnj;وه که به&zwnj;ڵێنت پێداون، هاوڕێ له&zwnj;گه&zwnj;ڵ ئه&zwnj;و که&zwnj;سانه&zwnj;دا که چاک و خواناسن، له باوک دایک و هاوسه&zwnj;ران و نه&zwnj;وه&zwnj;کانیان، به&zwnj;ڕاستی هه&zwnj;ر تۆ خوایه&zwnj;کی باڵاده&zwnj;ست و دانایت.';
$t[9] = 'له تۆڵه&zwnj;ی هه&zwnj;موو گوناهه&zwnj;کان بیانپارێزه&zwnj;، خۆ هه&zwnj;رکه&zwnj;سێک له تۆڵه&zwnj;ی گوناهه&zwnj;کان بپارێزیت له ڕۆژی قیامه&zwnj;تدا، ئه&zwnj;وه ئیتر تۆ ڕه&zwnj;حمت پێ کردووه&zwnj;، هه&zwnj;ر ئه&zwnj;وه&zwnj;شه سه&zwnj;رفرازی و سه&zwnj;رکه&zwnj;وتنی گه&zwnj;وره و بێ سنوور.';
$t[10] = 'بێگومان ئه&zwnj;وانه&zwnj;ی که بێ باوه&zwnj;ڕن بوون له قیامه&zwnj;تدا بانگیان لێ ده&zwnj;کرێت که ڕق و خه&zwnj;شم و قینی خوا زۆر زیاتره له ڕق و خه&zwnj;شم و قینیان دژ به&zwnj;یه&zwnj;کتر، چونکه کاتی خۆی بانگ ده&zwnj;کران بۆ ئیمان و باوه&zwnj;ڕ که&zwnj;چی ڕێبازی کوفرتان به&zwnj;رنه&zwnj;دا و له&zwnj;سه&zwnj;ری به&zwnj;رده&zwnj;وام ده&zwnj;بوون.';
$t[11] = 'کافران له وه&zwnj;ڵامدا ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا تۆ ده&zwnj;سته&zwnj;ڵاتت بێ سنووره دووجار ئێمه&zwnj;ت مراندووه و دووجار ئێمه&zwnj;ت زیندوو کردۆته&zwnj;وه (مه&zwnj;به&zwnj;ست نه&zwnj;بوونی پێش هاتنه دنیایه&zwnj;، که جۆره مردنێکه و ئه&zwnj;و مردنه&zwnj;ش که ده&zwnj;یزانن، ژیانی دنیاو زیندوو بوونه&zwnj;وه&zwnj;ی قیامه&zwnj;تیش دوو جۆره ژیانه&zwnj;) ئایا چاره&zwnj;سه&zwnj;رێک، ڕێبازێک نیه&zwnj;، بۆ ڕزگاریمان؟';
$t[12] = 'ئێستاش ئه&zwnj;و بارودۆخه&zwnj;ی که تێی که&zwnj;وتوون به&zwnj;هۆی ئه&zwnj;وه&zwnj;&zwnj;وه&zwnj;یه&zwnj;، به&zwnj;ڕاستی کاتێک بانگه&zwnj;واز بکرێت بۆ په&zwnj;رستنی خوای تاک و ته&zwnj;نها، ئێوه باوه&zwnj;ڕ ناهێنن، خۆ ئه&zwnj;گه&zwnj;ر هاوه&zwnj;ڵی بۆ بڕیار بدرێت و ڕێبازی یه&zwnj;کخواناسین که&zwnj;ناربخرێت، ئێوه باوه&zwnj;ڕ به&zwnj;وه ده&zwnj;هێنن، ئێستاش فه&zwnj;رمان، هه&zwnj;ر فارمانی خوای به&zwnj;رزو بڵند و گه&zwnj;وره&zwnj;یه (بۆ جیاکردنه&zwnj;وه&zwnj;ی ئیمانداران له بێ باوه&zwnj;ڕان).';
$t[13] = 'ئه&zwnj;و خوایه به&zwnj;رده&zwnj;وام به&zwnj;ڵگه و نیشانه&zwnj;ی ده&zwnj;سته&zwnj;ڵاتداری خۆیتان نیشان ده&zwnj;دات، له ئاسمانیشه&zwnj;وه ڕزق و ڕۆزیتان بۆ داده&zwnj;به&zwnj;زێنێت (وه&zwnj;ک: باران، هه&zwnj;وا، جۆره&zwnj;ها تیشک، که ده&zwnj;بنه سه&zwnj;رچاوه&zwnj;ی ڕۆزی) که&zwnj;سیش یاداوه&zwnj;ری وه&zwnj;رناگرێت و بیرناکاته&zwnj;وه جگه له&zwnj;وانه&zwnj;ی که&zwnj;: ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه بۆ لای ئه&zwnj;و زاته&zwnj;.';
$t[14] = 'که&zwnj;واته ئه&zwnj;ی ئیمانداران: ئێوه هه&zwnj;ر هانا و هاوار بۆ خوا به&zwnj;رن و ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری ئاین و به&zwnj;رنامه&zwnj;که&zwnj;ی ئه&zwnj;و بن، ئه&zwnj;گه&zwnj;ر چی کافرو بێ بڕواکان پێی سه&zwnj;غڵه&zwnj;ت بن و پێیان ناخۆش بێت.';
$t[15] = 'ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره پله&zwnj;و پایه&zwnj;ی زۆر بڵنده&zwnj;، خاوه&zwnj;نی ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی تایبه&zwnj;تی خۆیه&zwnj;تی، ئه&zwnj;وزاته وه&zwnj;حی و نیگا به فه&zwnj;رمانی خۆی ده&zwnj;نێرێت بۆ هه&zwnj;رکام له به&zwnj;نده&zwnj;کانی که بیه&zwnj;وێت، (قورئان ڕۆحه&zwnj;، چونکه ژیان به&zwnj;خشه به عه&zwnj;قڵ و دڵ و ده&zwnj;روون)، بۆ ئه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی بترسێنێت له ڕۆژی به&zwnj;یه&zwnj;کگه&zwnj;شتنه&zwnj;وه (گه&zwnj;شتنه&zwnj;وه&zwnj;ی ڕۆح به&zwnj;جه&zwnj;سته&zwnj;، گه&zwnj;شتنه&zwnj;وه&zwnj;ی ئاده&zwnj;میزاد به&zwnj;کرده&zwnj;وه&zwnj;کانی..).';
$t[16] = 'ئه&zwnj;و ڕۆژه هه&zwnj;موویان به&zwnj;ده&zwnj;ره&zwnj;وه&zwnj;ن، (ڕوت و ڕه&zwnj;جاڵ، نهێنی و ئاشکرا وه&zwnj;ک یه&zwnj;که&zwnj;)، هیچ شتێکیان له خوا شاراوه نى یه&zwnj;، ئه&zwnj;وسا په&zwnj;روه&zwnj;ردگاری مه&zwnj;زن، ده&zwnj;فه&zwnj;رموێت: ئه&zwnj;مرۆ پاشایه&zwnj;تی و فه&zwnj;رمانڕه&zwnj;وایی شایسته&zwnj;ی کێیه؟ کێ خاوه&zwnj;نی هه&zwnj;موو ئه&zwnj;م بوونه&zwnj;وه&zwnj;ره&zwnj;یه&zwnj;؟ که&zwnj;س نیه وه&zwnj;ڵام بداته&zwnj;وه&zwnj;، خۆی ده&zwnj;فه&zwnj;رموێت، هه&zwnj;ر هه&zwnj;مووی بۆ خوای تاک و ته&zwnj;نها و خه&zwnj;شمگیره&zwnj;. &zwnj;';
$t[17] = 'ئه&zwnj;مڕۆ هه&zwnj;موو که&zwnj;س پاداشتی کارو کرده&zwnj;وه&zwnj;ی خۆی وه&zwnj;رده&zwnj;گرێت، ئه&zwnj;مڕۆ هیچ جۆره سته&zwnj;مێک له که&zwnj;س ناکرێت، به&zwnj;ڕاستی خوای گه&zwnj;وره به&zwnj;خێرایی لێپرسینه&zwnj;وه&zwnj;کان ئه&zwnj;نجام ده&zwnj;دات (چونکه هه&zwnj;موو شت ڕوونه لای).';
$t[18] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) بیانترسێنه و ئاگایان بکه له ڕۆژه نزیکه&zwnj;که&zwnj;، ڕۆژی قیامه&zwnj;ت، کاتێک که دڵه&zwnj;کان ده&zwnj;گه&zwnj;نه گه&zwnj;روو، غه&zwnj;مگینی و په&zwnj;ژاره و ته&zwnj;نگانه دڵ وده&zwnj;روونی خوانه&zwnj;ناسان به&zwnj;ته&zwnj;واوی داگیر ئه&zwnj;کات، هیچ دڵسۆز و پشتیوانێک بۆ سته&zwnj;مکاران نی یه&zwnj;، که&zwnj;س تکاکار و غه&zwnj;مخۆریان نی یه، تا قسه&zwnj;یه&zwnj;کی ڕه&zwnj;واجی هه&zwnj;بێت و تکای لێ وه&zwnj;ربگیرێت.';
$t[19] = 'ئه&zwnj;و خوایه هه&zwnj;میشه چاوه خیانه&zwnj;تکار و ناپاکه&zwnj;کان ده&zwnj;ناسێت و ده&zwnj;زانێت چی له دڵ و ده&zwnj;روون و سینه&zwnj;کاندا حه&zwnj;شاردراوه&zwnj;.';
$t[20] = 'هه&zwnj;میشه خوا دادوه&zwnj;ریی له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی ئه&zwnj;نجام ده&zwnj;دات، ئه&zwnj;وانه&zwnj;ی جگه له&zwnj;و زاته هاواریان لێ ده&zwnj;که&zwnj;ن، هیچ ده&zwnj;سه&zwnj;ڵات و داوه&zwnj;ریه&zwnj;کیان له ده&zwnj;ست نایه&zwnj;ت، به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی بیسه&zwnj;ر و زانایه&zwnj;.';
$t[21] = 'ئایا ئه&zwnj;وه به&zwnj;سه&zwnj;ر زه&zwnj;ویدانه&zwnj;ڕۆیشتوون، تا ته&zwnj;ماشا بکه&zwnj;ن و سه&zwnj;رنج بده&zwnj;ن، سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مان چۆن بوو؛ چیان به&zwnj;سه&zwnj;رهات؟ ئه&zwnj;وانه له&zwnj;مان زۆر به&zwnj;هێزتر بوون و جێ ده&zwnj;ست و شوێنه&zwnj;واریان زیاتر له زه&zwnj;ویدا ئاشکرابوو، جا ئه&zwnj;وه بوو خوا به&zwnj;هۆی گوناهه&zwnj;کانیانه&zwnj;وه له ناوی بردن، که&zwnj;س نه&zwnj;یتوانی له سزای خوا بیانپارێزێت و په&zwnj;نایان بدات و ڕزگاریان بکات.';
$t[22] = 'ئه&zwnj;و بارودۆخه&zwnj;یان بۆیه به&zwnj;سه&zwnj;ردا هات، چونکه به&zwnj;ڕاستی کاتی خۆی پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان به چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه و نیشانه&zwnj;ی ئاشکراوه ڕاستیه&zwnj;کانیان پێڕاده&zwnj;گه&zwnj;یاندن، که&zwnj;چی بڕوایان نه&zwnj;کرد و باوه&zwnj;ڕیان نه&zwnj;هێنا، ئنجا خوای گه&zwnj;وره له&zwnj;ناوی بردن و پێچانیه&zwnj;وه&zwnj;، چونکه بێگومان ئه&zwnj;و خوایه به&zwnj;هێزه و تۆڵه&zwnj;سه&zwnj;ندنه&zwnj;وه&zwnj;ی زۆر توند و به ئێشه له بێ باوه&zwnj;ڕان.';
$t[23] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه موسامان ڕه&zwnj;وانه کرد هاوڕێ له&zwnj;گه&zwnj;ڵ چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه و نیشانه&zwnj;ی ئاشکرادا...';
$t[24] = 'بۆ سه&zwnj;ر فیرعه&zwnj;ون و هامان و قاروون، به&zwnj;ڵام ئه&zwnj;و سته&zwnj;مکارانه وتیان: جادووگه&zwnj;رێکی درۆزنه&zwnj;!!';
$t[25] = 'جاکاتێک هات بۆ لایان و هه&zwnj;موو ڕاسته&zwnj;کی بۆ سه&zwnj;لماندن به به&zwnj;ڵگه و معجزه&zwnj;ی ئاشکراوه که له لایه&zwnj;ن ئێمه&zwnj;وه پێی به&zwnj;خشرابوو، فیرعه&zwnj;ون و داروده&zwnj;سته&zwnj;که&zwnj;ی فه&zwnj;رمانیاندا و وتیان: ڕۆڵه&zwnj;ی نێرینه&zwnj;ی هه&zwnj;موو ئه&zwnj;وانه بکوژن که باوه&zwnj;ڕیان هێناوه و له گه&zwnj;ڵ موسادان، ئافره&zwnj;ت و کچاکانیان بهێڵنه&zwnj;وه بۆ کاره&zwnj;که&zwnj;ری، به&zwnj;ڵام خه&zwnj;یاڵیان خاوه&zwnj;، له کاتێکدا پیلان و ته&zwnj;ڵه&zwnj;که&zwnj;ی بێ باوه&zwnj;ڕان هیچ نی یه&zwnj;، ته&zwnj;نها سه&zwnj;ری له&zwnj;سه&zwnj;ر گه&zwnj;ردانیه&zwnj;وه ده&zwnj;رده&zwnj;چێت.';
$t[26] = 'ئینجا فیرعه&zwnj;ون وتی: وازم لێ بهێنن با موسا بکوژم، با هه&zwnj;ر هاوار له په&zwnj;روه&zwnj;ردگاری بکات، چونکه به&zwnj;ڕاستی من ده&zwnj;ترسم که دین و ئاینتان بگۆرێت!، یاخود دووبه&zwnj;ره&zwnj;کی و خراپه و شه&zwnj;ڕ و شۆڕ له وڵات و سه&zwnj;رزه&zwnj;ویدا به&zwnj;رپا بکات!!';
$t[27] = 'موساش وتی: بێگومان من په&zwnj;نا ده&zwnj;گرم به په&zwnj;روه&zwnj;ردگاری خۆم که په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;شه له هه&zwnj;موو لووت به&zwnj;رزو خۆبه&zwnj;زل زانێک باوه&zwnj;ڕی به&zwnj;ڕۆژی لێپرسینه نه&zwnj;بێت (ڕاستیه&zwnj;کان ئاوه&zwnj;ژوو بکات).';
$t[28] = '(دیاره سه&zwnj;رئه&zwnj;نجام فیرعه&zwnj;ون بڕیاریداوه موسا شه&zwnj;هید بکات بۆیه&zwnj;): پیاوێکی ئیماندار له خزمانی فیرعه&zwnj;ون که باوه&zwnj;ڕه&zwnj;که&zwnj;ی شاردبۆوه تا ئه&zwnj;و کاته&zwnj;، وتی: ئایا ڕه&zwnj;وایه پیاوێک بکوژن ته&zwnj;نها تاوانی ئه&zwnj;وه&zwnj;یه که ده&zwnj;ڵێت په&zwnj;روه&zwnj;دگارم -الله- یه&zwnj;، له&zwnj;کاتێکدا چه&zwnj;نده&zwnj;ها به&zwnj;ڵگه و نیشانه&zwnj;شی له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه بۆ هێناون و نیشانی داون، خۆ ئه&zwnj;گه&zwnj;ر درۆزن بێت، ئه&zwnj;وه درۆزنیه&zwnj;که&zwnj;ی به زه&zwnj;ره&zwnj;ری خۆی ته&zwnj;واو ده&zwnj;بێت، خۆ ئه&zwnj;گه&zwnj;ر ڕاستگۆبێت هه&zwnj;ندێک له&zwnj;و به&zwnj;ڵایانه&zwnj;تان به&zwnj;سه&zwnj;ردێت که پێی ڕاگه&zwnj;یاندوون، به&zwnj;ڕاستی من دڵنیام که&zwnj;: خوا هیدایه&zwnj;ت و ڕێنموویی که&zwnj;سێک ناکات زیاده&zwnj;ڕه و درۆزن بێت (واته&zwnj;: ئه&zwnj;گه&zwnj;ر مووسا درۆزن بوایه&zwnj;، ئه&zwnj;و هه&zwnj;موو پشتیوانیه&zwnj;ی لێ نه&zwnj;ده&zwnj;کرا له&zwnj;لایه&zwnj;ن خواوه&zwnj;).';
$t[29] = 'هه&zwnj;روه&zwnj;ها وتی: ئه&zwnj;ی قه&zwnj;وم و هۆزم ئه&zwnj;مڕۆ ئێوه ده&zwnj;سه&zwnj;ڵاتتان هه&zwnj;یه به&zwnj;سه&zwnj;ر ئه&zwnj;م ناوچه&zwnj;یه&zwnj;دا و به&zwnj;ئاره&zwnj;زووی خۆتان فه&zwnj;رمانڕه&zwnj;وایی تیادا ده&zwnj;که&zwnj;ن، جا بیر بکه&zwnj;نه&zwnj;وه و تێفکرن کێ ده&zwnj;توانێت فریامان بکه&zwnj;وێت، کێ ده&zwnj;توانێت ڕزگارمان بکات، له تۆڵه و سزای خوا ئه&zwnj;گه&zwnj;ر یه&zwnj;خه&zwnj;ی پێگرتین؟ فیرعه&zwnj;ون قسه&zwnj;که&zwnj;ی پێ بڕی وتی: من ته&zwnj;نها هه&zwnj;ر ئه&zwnj;و ڕاو بۆچوونه به&zwnj;چاک ده&zwnj;زانم و لام په&zwnj;سه&zwnj;نده&zwnj;که پێم ڕاگه&zwnj;یاندن، من ته&zwnj;نها ڕێبازی چاک و دروستم نیشانداون!!';
$t[30] = 'ئه&zwnj;و پیاوه&zwnj;ی که ئیمانی هێنابوو وتی: بێگومان من ده&zwnj;ترسم ڕۆژێکتان به&zwnj;سه&zwnj;ربێت وه&zwnj;کو ئه&zwnj;و ڕۆژه&zwnj;ی به&zwnj;سه&zwnj;ر گرۆ و قه&zwnj;ومه ناله&zwnj;باره&zwnj;کانی پێشوودا هات...';
$t[31] = 'وه&zwnj;کو ئه&zwnj;وه&zwnj;ی که به&zwnj;سه&zwnj;ر قه&zwnj;ومی نوح و عاد و ثمود و ئه&zwnj;وانه&zwnj;ی به&zwnj;دوای ئه&zwnj;واندا هاتن، جا وه&zwnj;نه&zwnj;بێت خوا بیه&zwnj;وێت سته&zwnj;م له به&zwnj;نده&zwnj;کان بکات و حه&zwnj;ز به تیاچونیان بکات.';
$t[32] = 'ئه&zwnj;ی قه&zwnj;وم و هۆزم من به&zwnj;ڕاستی ده&zwnj;ترسم له ڕۆژی هاوارو ئاهوناڵه و بانگکردنتان بۆ لێپرسینه&zwnj;وه (قیامه&zwnj;ت).';
$t[33] = 'ئه&zwnj;و ڕۆژه&zwnj;ی که هه&zwnj;موو له&zwnj;به&zwnj;ری هه&zwnj;ڵدێن و پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و هه&zwnj;موو سه&zwnj;رگه&zwnj;ردانن، نازانن چی بکه&zwnj;ن و ڕوو بکه&zwnj;نه کوێ، که&zwnj;س نیه په&zwnj;ناتان بدات له خه&zwnj;شم و قینی خوا، ئه&zwnj;وه&zwnj;ش خوا گومڕای بکات به&zwnj;هۆی خراپه&zwnj;کاری خۆیه&zwnj;وه، هیچ که&zwnj;سی تر ناتوانێت هیدایه&zwnj;ت و ڕێنموویی بکات.';
$t[34] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی کاتی خۆی یوسف به&zwnj;ڵگه و نیشانه&zwnj;ی زۆری بۆ هێنان تا ڕێبازی خواناسی بگرنه&zwnj;به&zwnj;ر، که&zwnj;چی به&zwnj;رده&zwnj;وام له گوماندا بوون، به&zwnj;رامبه&zwnj;ر ئه&zwnj;و به&zwnj;رنامه&zwnj;و ئاینه&zwnj;ی که بۆی هێنابوون، هه&zwnj;تا ئه&zwnj;و کاته&zwnj;ی وه&zwnj;فاتی کرد، ئه&zwnj;وسا وتتان: ئیتر ته&zwnj;واو هه&zwnj;رگیز خوا دوای ئه&zwnj;م پێغه&zwnj;مبه&zwnj;ری تر نانێرێت، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه خوا که&zwnj;سانی سه&zwnj;رکه&zwnj;ش و گوناهکار و دڵ دانه&zwnj;مه&zwnj;زراو ڕاڕا گومڕا ده&zwnj;کات.';
$t[35] = 'ئه&zwnj;و که&zwnj;سانه&zwnj;ی که موجاده&zwnj;له و گفتوگۆ ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا، بێ ئه&zwnj;وه&zwnj;ی هیچ به&zwnj;ڵگه&zwnj;یه&zwnj;کیان بۆ هاتبێت، ئه&zwnj;م مجاده&zwnj;له و گفتوگۆیه زۆر بێزراو تاوانه لای خوا و لای ئه&zwnj;وانه&zwnj;ش که ئیمانیان هێناوه&zwnj;، هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه خوا مۆر ده&zwnj;نێت به&zwnj;سه&zwnj;ر هه&zwnj;موو خاوه&zwnj;ن دڵێکی خۆبه&zwnj;زل زان و ڕق ئه&zwnj;ستوور و سته&zwnj;مکاردا.';
$t[36] = 'ئا له&zwnj;و گه&zwnj;رمه&zwnj;ی قسه&zwnj;ی ئیمانداره&zwnj;دا فیرعه&zwnj;ون به هامانی وت: ئه&zwnj;ی هامان بورجێکی به&zwnj;رزم بۆ دروست بکه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی هه&zwnj;ندێک هۆکارم ده&zwnj;ست بکه&zwnj;وێت!!...';
$t[37] = 'هۆکاری به&zwnj;رزبوونه&zwnj;وه بۆ ئاسمانه&zwnj;کان، تا له&zwnj;وێوه سه&zwnj;رکه&zwnj;وم و نهێنیه&zwnj;کان شاره&zwnj;زا ببم تا سه&zwnj;ردانێکی خوای موسا بکه&zwnj;م و بیبینم!! هه&zwnj;رچه&zwnj;نده من دڵنیام که درۆزنه&zwnj;!!، ئابه&zwnj;و شێوه&zwnj;یه کاروکرده&zwnj;وه ناشیرینه&zwnj;کان لای فیرعه&zwnj;ون جوانکراو، له ڕێبازی ڕاست و دروست لادرا، بێگومان پیلان و نه&zwnj;خشه&zwnj;ی نادروستی فیرعه&zwnj;ون هه&zwnj;ر له زه&zwnj;ره&zwnj;ردایه&zwnj;.';
$t[38] = 'ئه&zwnj;و که&zwnj;سه&zwnj;ی که ئیمانی هێنابوو دووباره هاته&zwnj;وه قسه وتی: ئه&zwnj;ی قه&zwnj;وم و هۆزه&zwnj;که&zwnj;م شوێنی من بکه&zwnj;ون به&zwnj;قسه&zwnj;ی من بکه&zwnj;ن ڕێنموویتان ده&zwnj;که&zwnj;م بۆ ڕێبازی سه&zwnj;رفه&zwnj;رازی و چاکه و چاکه&zwnj;کاری.';
$t[39] = 'ئه&zwnj;ی قه&zwnj;وم و هۆز&zwnj;م بێگومان ژیانی ئه&zwnj;م دنیایه ته&zwnj;نها بریتیه له ڕابوردنێکی که&zwnj;م، به&zwnj;ڕاستی ژیانی پاشه ڕۆژ و داهاتوو، هه&zwnj;ر ئه&zwnj;و شوێنی ئومێد و ئارام گرتن و حه&zwnj;سانه&zwnj;وه&zwnj;یه بۆ ئیمانداران.';
$t[40] = 'ئه&zwnj;وه&zwnj;ی گوناهو هه&zwnj;ڵه&zwnj;یه&zwnj;کی کردبێت، ته&zwnj;نها به&zwnj;قه&zwnj;ده&zwnj;ر هه&zwnj;ڵه و گوناهه&zwnj;که&zwnj;ی خۆی تۆڵه&zwnj;ی لێده&zwnj;سێنرێت، ئه&zwnj;وه&zwnj;ش که چاکه&zwnj;ی کردووه له پیاوان و ئافره&zwnj;تان، له کوڕان و له کچان له&zwnj;کاتێکتدا که خاوه&zwnj;نی باوه&zwnj;ڕی دامه&zwnj;زراون، ئا ئه&zwnj;وانه ده&zwnj;چنه به&zwnj;هه&zwnj;شته&zwnj;وه و بێ سنوور و بێ حساب ڕزق و ڕۆزی هه&zwnj;مه&zwnj;جۆر و هه&zwnj;مه چه&zwnj;شنیان پێده&zwnj;به&zwnj;خشرێت.';
$t[41] = 'ئه&zwnj;ی قه&zwnj;وم و هۆزم ئه&zwnj;وه چی یه&zwnj;، خۆ من بانگی ئێوه ده&zwnj;که&zwnj;م بۆ ڕزگاریی، بۆ سه&zwnj;رفرازی بۆ به&zwnj;هه&zwnj;شت، که&zwnj;چی ئێوه بانگی من ده&zwnj;که&zwnj;ن، بۆ خوانه&zwnj;ناسی، بۆ سه&zwnj;رگه&zwnj;ردانی بۆ دۆزه&zwnj;خ!';
$t[42] = 'بانگم ده&zwnj;که&zwnj;ن تا باوه&zwnj;ڕم به خوا نه&zwnj;مێنێت و هاوه&zwnj;ڵ و شه&zwnj;ریکی بۆ بڕیار بده&zwnj;م، بێ هیچ به&zwnj;ڵگه&zwnj;و زانستی و زانیاریه&zwnj;ک، له&zwnj;کاتێکدا من بانگتان ده&zwnj;که&zwnj;م بۆلای زاتێک که زۆر باڵاده&zwnj;سته و زۆریش لێخۆشبووه&zwnj;...';
$t[43] = 'شتێکی ڕاسته&zwnj;و گومانی تێدانیه ئه&zwnj;وه&zwnj;ی که ئێوه من بانگ ده&zwnj;که&zwnj;ن بۆ لای خاوه&zwnj;نی هیچ بانگه&zwnj;واز و به&zwnj;رنامه و پرۆگرامێک نیه&zwnj;، نه له دنیادا، نه له قیامه&zwnj;تیشدا، ئه&zwnj;وه&zwnj;ش نابێت له&zwnj;یاد بکه&zwnj;ین، که به&zwnj;ڕاستیش گه&zwnj;ڕانه&zwnj;وه&zwnj;مان بۆ لای خوای گه&zwnj;وره&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ش تاوانی زۆر ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن و سه&zwnj;رکه&zwnj;ش و له سنوور ترازاون، ئه&zwnj;وانه نیشته&zwnj;جێی دۆزه&zwnj;خن.';
$t[44] = 'جا له ئاینده&zwnj;دا ڕۆژێک دێت، ئه&zwnj;م قسانه&zwnj;ی منتان بێته&zwnj;وه یاد، ئێستاکه&zwnj;ش من خۆم وکارو باره&zwnj;کانم ده&zwnj;ده&zwnj;مه ده&zwnj;ست خواو پشت به&zwnj;وده&zwnj;به&zwnj;ستم، چونکه بێگومان خوا بینایه به به&zwnj;نده&zwnj;کانی.';
$t[45] = 'جا خوای گه&zwnj;وره ئه&zwnj;و ئیمانداره ژیره&zwnj;ی پاڕاست له پیلانی خراپ و دڕندانه&zwnj;یان، دارو ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ونیش سزایه&zwnj;کی زۆر سه&zwnj;ختیان تووش بوو...';
$t[46] = 'ئاگری دۆزه&zwnj;خه&zwnj;، به&zwnj;یانیان و ئێواران، نیشانیان ده&zwnj;درێت، یاخود پیایاندا ده&zwnj;چزێنرێت، له (جیهانی به&zwnj;رزه&zwnj;خدا) ڕۆژی قیامه&zwnj;تیش فه&zwnj;رمان ده&zwnj;درێت: ئاده&zwnj;ی تاقم و ده&zwnj;سته&zwnj;ی فیرعه&zwnj;ون بخه&zwnj;نه ناو ئێش و ئازاری زۆر ناخۆشه&zwnj;وه&zwnj;.';
$t[47] = 'ئه&zwnj;و کاته ئیتر شه&zwnj;ڕو کێشه ده&zwnj;ست پێده&zwnj;که&zwnj;ن له&zwnj;ناو ئاگری دۆزه&zwnj;خدا، لاواز و چه&zwnj;وساوه&zwnj;کان به&zwnj;ده&zwnj;سه&zwnj;ڵاتدارو لووت به&zwnj;رزه&zwnj;کان ده&zwnj;ڵێن: خۆ به&zwnj;ڕاستی ئێمه شوێنکه&zwnj;وته&zwnj;ی ئێوه بووین، ئایا ئێوه ناتوانن تۆزێک سزای ئاگرمان له&zwnj;سه&zwnj;ر که&zwnj;م بکه&zwnj;نه&zwnj;وه&zwnj;؟';
$t[48] = 'له وه&zwnj;ڵامیاندا لووت به&zwnj;رزه&zwnj;کان ده&zwnj;ڵێن: ئێمه هه&zwnj;موومان له&zwnj;ناو ئاگردا گیرمان خواردووه&zwnj;، بێگومان خوای گه&zwnj;وره داوه&zwnj;ری کردوه له نێوان به&zwnj;نده&zwnj;کانیدا و بڕیاره&zwnj;کانی بڕاوه&zwnj;ته&zwnj;وه&zwnj;.';
$t[49] = 'ئینجا ئه&zwnj;وانه&zwnj;ی که له&zwnj;ناو ئاگری دۆزه&zwnj;خدان به فریشته&zwnj;کانی کارگێری دۆزه&zwnj;خ ده&zwnj;ڵێن: داوا له په&zwnj;روه&zwnj;ر&zwnj;دگارتان بکه&zwnj;ن؛ ته&zwnj;نها ڕۆژێک سزاو ئازامان له&zwnj;سه&zwnj;ر که&zwnj;م بکاته&zwnj;وه&zwnj;.';
$t[50] = 'ئه&zwnj;وانیش له وه&zwnj;ڵامی داخوازیه&zwnj;که&zwnj;یاندا ده&zwnj;ڵێن: باشه&zwnj;، مه&zwnj;گه&zwnj;ر پێغه&zwnj;مبه&zwnj;ره&zwnj;کا&zwnj;نتان به به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ڕوون و ئاشکراوه&zwnj;، ڕه&zwnj;وانه نه&zwnj;کرابوون بۆتان؟ هه&zwnj;مووان به&zwnj;یه&zwnj;کده&zwnj;نگ ده&zwnj;ڵێن: به&zwnj;ڵێ، ڕاسته&zwnj;، ئینجا فریشته&zwnj;کان ده&zwnj;ڵێن: ده&zwnj;ی که&zwnj;وابوو هه&zwnj;ر داوابکه&zwnj;ن وبپاڕێنه&zwnj;وه&zwnj;، بێگومان داخوازی و پاڕانه&zwnj;وه&zwnj;ی کافران به&zwnj;هیچ ناچێت، تازه هیچ نرخێکی نیه&zwnj;و گیرا نابێت.';
$t[51] = 'به&zwnj;ڕاستی ئێمه پێغه&zwnj;مبه&zwnj;ره&zwnj;کانمان و ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان هێناوه سه&zwnj;ر ده&zwnj;خه&zwnj;ین، له ژیانی دنیادا هه&zwnj;روه&zwnj;ها ئه&zwnj;و ڕۆژه&zwnj;ش که شایه&zwnj;ته&zwnj;کان شایه&zwnj;تی ده&zwnj;ده&zwnj;ن (فریشته&zwnj;کان شایه&zwnj;تی بۆ پێغه&zwnj;مبه&zwnj;ران ده&zwnj;ده&zwnj;ن، پێغه&zwnj;مبه&zwnj;ران شایه&zwnj;تی بۆ بانگخوازان ده&zwnj;ده&zwnj;ن بانگخوازان شایه&zwnj;تن له&zwnj;سه&zwnj;ر خه&zwnj;ڵکی)';
$t[52] = 'له&zwnj;و ڕۆژه&zwnj;دا سته&zwnj;مکاران تکاو پاڕانه&zwnj;وه&zwnj;یان بێ سووده و که&zwnj;ڵکیان پێناگه&zwnj;یه&zwnj;نێت، نه&zwnj;فرین هه&zwnj;ر بۆ ئه&zwnj;وانه و هه&zwnj;ر بۆ ئه&zwnj;وانیشه جێگه&zwnj;ی ناسازو ناخۆش. &zwnj;';
$t[53] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه هیدایه&zwnj;ت و ڕێنموویمان به&zwnj;خشی به موسا و ته&zwnj;وراتیشمان هێشته&zwnj;وه له&zwnj;ناو نه&zwnj;وه&zwnj;ی ئیسرائیلدا تا په&zwnj;یڕه&zwnj;وی بکه&zwnj;ن و پابه&zwnj;ندی بن.';
$t[54] = 'کردوشمانه به&zwnj;هۆی ڕءنموویی و یادخستنه&zwnj;وه بۆ خاوه&zwnj;ن بیر و هۆشه&zwnj;کان.';
$t[55] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) به ئارام و خۆڕاگربه&zwnj;، چونکه به&zwnj;ڕاستی به&zwnj;ڵێن و واده&zwnj;کانی خوا هه&zwnj;ر دێته&zwnj;دی، داوای لێخۆشبوونیش بکه بۆ گوناهه&zwnj;کانت، ته&zwnj;سبیحات و ستایش و سوپاسی په&zwnj;روه&zwnj;ردگاریشت به&zwnj;رده&zwnj;وام ئه&zwnj;نجام بده&zwnj;، به تایبه&zwnj;ت له ده&zwnj;مه و ئێواران و ده&zwnj;مه و به&zwnj;یانیاندا.';
$t[56] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که موجاده&zwnj;له و گفتوگۆی سه&zwnj;رکه&zwnj;شانه ده&zwnj;که&zwnj;ن و، به نه&zwnj;شاره&zwnj;زایی له ئایه&zwnj;ته&zwnj;کانی خوا ده&zwnj;دوێن بێ ئه&zwnj;وه&zwnj;ی به&zwnj;ڵگه&zwnj;یه&zwnj;کی به&zwnj;هێزیان پێ بێت، ئه&zwnj;وانه ته&zwnj;نها له سینه&zwnj;یاندا خۆ به&zwnj;ل زانین هه&zwnj;یه&zwnj;، به&zwnj;مه&zwnj;رجێک هیچ کات ناگه&zwnj;نه ئه&zwnj;و ئامانجه&zwnj;ی که هه&zwnj;یانه&zwnj;، که&zwnj;واته تۆ په&zwnj;نا بگره به خوا له شه&zwnj;ڕی خوانه&zwnj;ناسان چونکه به&zwnj;ڕاستی ئه&zwnj;و زاته بیسه&zwnj;ر و بینایه&zwnj;.';
$t[57] = 'به&zwnj;ڕاستی دروستکردنی ئاسمانه&zwnj;کان و زه&zwnj;وی له دروستکردنی خه&zwnj;ڵکی زۆر گه&zwnj;وره و گرنگتره&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;م ڕاستیانه تێناگه&zwnj;ن و په&zwnj;ی پێ نابه&zwnj;ن و نایزانن.';
$t[58] = 'که&zwnj;سانی کوێر له به&zwnj;رامبه&zwnj;ر چاوساغه&zwnj;وه&zwnj;، وه&zwnj;ک یه&zwnj;ک نین، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه کاروکرده&zwnj;وه&zwnj;ی چاکه ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن، له&zwnj;گه&zwnj;ڵ ئه&zwnj;وانه&zwnj;دا که تاوان و گوناهـ ده&zwnj;که&zwnj;ن (چوونیه&zwnj;ک نین)، که&zwnj;چی زۆر که&zwnj;م خه&zwnj;ڵکی تێده&zwnj;فکرن و بیرده&zwnj;که&zwnj;نه&zwnj;وه و به&zwnj;راورد ده&zwnj;که&zwnj;ن.';
$t[59] = 'به&zwnj;ڕاستی ڕۆژی قیامه&zwnj;ت به&zwnj;ڕێوه&zwnj;یه و هه&zwnj;ر دێت، هیچ گومانیشی تیادانیه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی ئیمان و باوه&zwnj;ڕناهێنن و باوه&zwnj;ڕیان پێی نیه&zwnj;.';
$t[60] = 'ئه&zwnj;ی خه&zwnj;ڵکینه په&zwnj;روه&zwnj;ردگارتان فه&zwnj;رموویه&zwnj;تی: ئێوه هاناو هاوار بۆمن بهێنن؛ ئه&zwnj;وه من دوعاو نزاتان گیرا ده&zwnj;که&zwnj;م، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی خۆیان به&zwnj;گه&zwnj;وره داده&zwnj;نێن ولووت به&zwnj;رزی ده&zwnj;که&zwnj;ن له&zwnj;ئاستی په&zwnj;رستنی مندا، له&zwnj;ئایینده&zwnj;دا به&zwnj;سه&zwnj;رشۆڕی و خه&zwnj;جاڵه&zwnj;تی یه&zwnj;وه ده&zwnj;خرێنه ناو ئاگری دۆزه&zwnj;خه&zwnj;وه&zwnj;.';
$t[61] = 'خوا ئه&zwnj;و په&zwnj;روه&zwnj;ردگاره&zwnj;یه شه&zwnj;وی بۆ سازاندوون، تا تیایدا ئارام بگرن و بحه&zwnj;وێنه&zwnj;وه&zwnj;، ڕۆژگاریشی بۆ ڕۆشن و ڕووناک کردوونه&zwnj;ته&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوای گه&zwnj;وره خاوه&zwnj;نی به&zwnj;خشنده&zwnj;ی و نازو نیعمه&zwnj;تی زۆره به&zwnj;سه&zwnj;ر خه&zwnj;ڵکیه&zwnj;وه&zwnj;، به&zwnj;ڵام زۆربه&zwnj;ی خه&zwnj;ڵکی سوپاسگوزاری ناکه&zwnj;ن.';
$t[62] = 'ئه&zwnj;و زاته خوای ئێوه&zwnj;یه و په&zwnj;روه&zwnj;ردگارتانه&zwnj;، به&zwnj;دیهێنه&zwnj;ری هه&zwnj;موو شتێکه&zwnj;، هیچ خوایه&zwnj;ک نیه جگه له&zwnj;و، ئیتر ئێوه به&zwnj;ره&zwnj;و کوێ ده&zwnj;برێن؟ چۆن لاده&zwnj;درێن له له&zwnj;به&zwnj;رنامه&zwnj;که&zwnj;ی؟';
$t[63] = 'هه&zwnj;ر به&zwnj;و شێوه&zwnj;یه لاداربوون ئه&zwnj;وانه&zwnj;ی که له پێش ئه&zwnj;مانه&zwnj;وه دژایه&zwnj;تی ئایات و فه&zwnj;رمانه&zwnj;کانی خوایان ده&zwnj;کرد و له ڕێبازه&zwnj;که&zwnj;ی لایان ده&zwnj;دا و دژایه&zwnj;تیان ده&zwnj;کرد (به&zwnj;ڵام کوان؟! بۆ کوێ چوون؟! چیان به&zwnj;سه&zwnj;ر هات؟!).';
$t[64] = 'خوا ئه&zwnj;و زاته&zwnj;یه که زه&zwnj;وی بۆ کردوون به شوێنی سره&zwnj;وتن و ژیان و حه&zwnj;وانه&zwnj;وه&zwnj;، ئاسمانیشی به&zwnj;سه&zwnj;رتانه&zwnj;وه ڕاگرتووه&zwnj;، ڕووخسارو شێوازتانی نه&zwnj;خشاندووه&zwnj;، به&zwnj;جوانترین شێوه&zwnj;ش نه&zwnj;خشاندویه&zwnj;تی، له ڕزق و ڕۆزی چاک و پاک و به&zwnj;سوود به&zwnj;هره&zwnj;وه&zwnj;ری کردوون، جا ئا ئه&zwnj;وه&zwnj;یه خوای په&zwnj;روه&zwnj;ردگارتان، پاک و بێ گه&zwnj;رد و بڵنده ئه&zwnj;و خوایه&zwnj;ی که په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیانه.';
$t[65] = 'ئه&zwnj;و زاته هه&zwnj;میشه و به&zwnj;رده&zwnj;وام هه&zwnj;ر زیندووه&zwnj;، جگه له&zwnj;و خوایه&zwnj;کی تر نیه&zwnj;، که&zwnj;وابوو هانا و هاوار و نزاتان ڕوو به&zwnj;و بێت، ملکه&zwnj;چ و فه&zwnj;رمانبه&zwnj;رداری به&zwnj;رنامه و ئاینه&zwnj;که&zwnj;ی ئه&zwnj;و بن، سوپاس و ستایش بۆ خوا، که په&zwnj;روه&zwnj;ر&zwnj;دگاری هه&zwnj;موو جیهانیانه&zwnj;.';
$t[66] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێیان بڵێ: بێگومان من قه&zwnj;ده&zwnj;غه&zwnj;م لێکراوه ئه&zwnj;و شتانه بپه&zwnj;رستم که ئێوه له جیاتی خوا هانا و هاواری بۆ ده&zwnj;به&zwnj;ن، له کاتێکدا به&zwnj;ڵگه و نیشانه&zwnj;ی زۆر ئاشکرام له لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارمه&zwnj;وه پێگه&zwnj;یشتووه و من فه&zwnj;رمانم پێ دراوه به ملکه&zwnj;چی خۆم بده&zwnj;مه ده&zwnj;ست په&zwnj;روه&zwnj;ردگاری هه&zwnj;موو جیهانیان.';
$t[67] = 'ئه&zwnj;و خوایه له خاک ئێوه&zwnj;ی دروست کردووه&zwnj;، له&zwnj;وه&zwnj;ودوا له نوتفه (که بریتی یه له به&zwnj;یه&zwnj;کگه&zwnj;یشتنی سه&zwnj;ره&zwnj;مێکوته&zwnj;یه&zwnj;ک که به&zwnj;چاو نابینرێت به هێلکۆکه&zwnj;یه&zwnj;ک که به&zwnj;قه&zwnj;ده&zwnj;ر خاڵێکی بچووکه&zwnj;) له&zwnj;وه&zwnj;ودوا ده&zwnj;بێته خۆ هه&zwnj;ڵواسه&zwnj;رێک پاشان ده&zwnj;رتان ده&zwnj;هێنێت که منداڵێکی ساوان، به&zwnj;ره&zwnj;به&zwnj;ره&zwnj;گه&zwnj;وره&zwnj;تان ده&zwnj;کات بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;نه ئه&zwnj;وپه&zwnj;ڕی هێزو تواناو ژیریتان، پاشان بۆ ئه&zwnj;وه&zwnj;ی ببنه پیرو په&zwnj;ککه&zwnj;وته&zwnj;، هه&zwnj;شتانه&zwnj;، پێش ئه&zwnj;وه&zwnj;ی بگاته یه&zwnj;کێک له&zwnj;و قۆناغانه&zwnj;، ده&zwnj;مرێت، هه&zwnj;تانه ده&zwnj;مێنێته&zwnj;وه بۆ ئه&zwnj;وه&zwnj;ی بگاته کاتێکی دیاریکراو، (خوای گه&zwnj;وره ئه&zwnj;م هه&zwnj;موو ڕاستیانه&zwnj;تان ده&zwnj;خاته به&zwnj;رچاو) بۆ ئه&zwnj;وه&zwnj;ی عه&zwnj;قڵ و ژیریتان بخه&zwnj;نه کار.';
$t[68] = 'هه&zwnj;ر ئه&zwnj;وزاته&zwnj;، ژیان ده&zwnj;به&zwnj;خشێت وزینده&zwnj;وه&zwnj;ران و گیانله&zwnj;به&zwnj;ران ده&zwnj;مرێنێت، جاکاتێک بڕیاری شتێکی دا هه&zwnj;ر ئه&zwnj;وه&zwnj;نده&zwnj;یه که پێی بفه&zwnj;رموێت: ببه&zwnj;، ده&zwnj;ست به&zwnj;جێ ده&zwnj;بێت.';
$t[69] = 'ئایا نا&zwnj;بینیت ئه&zwnj;وانه&zwnj;ی که موجاده&zwnj;له&zwnj;و گفتوگۆی نابه&zwnj;جێ ده&zwnj;که&zwnj;ن ده&zwnj;رباره&zwnj;ی ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی خوا، ئه&zwnj;و سه&zwnj;رگه&zwnj;ردانانه به&zwnj;ره&zwnj;و کوێ لاده&zwnj;درێن له حه&zwnj;ق و ڕاستی، ڕوو له کوێ ده&zwnj;که&zwnj;ن؟';
$t[70] = 'ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان نی یه به قورئان و به&zwnj;و به&zwnj;رنامه و ڕێبازه&zwnj;ی که به پێغه&zwnj;مبه&zwnj;ره&zwnj;کانماندا ڕه&zwnj;وانه&zwnj;مان کردووه&zwnj;، ئه&zwnj;وانه له ئایینده&zwnj;دا ده&zwnj;زانن چیان به&zwnj;سه&zwnj;ر دێت و خۆیان ده&zwnj;بیننه&zwnj;وه&zwnj;...';
$t[71] = 'کاتێک کۆت و زنجیره&zwnj;کان ده&zwnj;کرێنه گه&zwnj;ردنیان و ڕاده&zwnj;کێشرێن...';
$t[72] = 'بۆ ناو ئاوی زۆر گه&zwnj;رم، پاشان ئاگریان پێ خۆش ده&zwnj;کرێت و ده&zwnj;بنه سووته&zwnj;مه&zwnj;نی بۆی...';
$t[73] = 'له&zwnj;وه&zwnj;ودوا پێیان ده&zwnj;وترێت: کوان ئه&zwnj;و شتانه&zwnj;ی که به هاوه&zwnj;ڵتان داده&zwnj;نا بۆ خوای باڵاده&zwnj;ست؟!...';
$t[74] = 'له جیاتی خوا ده&zwnj;تانپه&zwnj;رست، به&zwnj;که&zwnj;ساسی و خه&zwnj;جاڵه&zwnj;تیه&zwnj;وه ده&zwnj;ڵێن: لێمان ون بوون، دیار نین!!، به&zwnj;ڵکو ئێمه هه&zwnj;ر ناوی که&zwnj;سی ترمان نه&zwnj;ده&zwnj;برد (دیاره له ترسدا، یان هه&zwnj;ر بیروباوه&zwnj;ڕی خۆیان فه&zwnj;رامۆش کردووه&zwnj;)، ئا به&zwnj;و شێوه&zwnj;یه خوا کافران سه&zwnj;رگه&zwnj;ردان و سه&zwnj;رلێشێواو ده&zwnj;کات...';
$t[75] = 'ئا ئه&zwnj;م بارودۆخه&zwnj;ی که ئێستا تیایدا گیرتان خواردووه سه&zwnj;رئه&zwnj;نجامی ئه&zwnj;و که&zwnj;یف وشادی و سوڕ و سه&zwnj;مایه&zwnj;یه که له&zwnj;سه&zwnj;ر زه&zwnj;ویدا به ناحه&zwnj;ق ئه&zwnj;نجامتان ده&zwnj;دا و که&zwnj;شخه&zwnj;تان ده&zwnj;کرد به&zwnj;سه&zwnj;ر خڵکه&zwnj;وه و نازتان ده&zwnj;کرد به&zwnj;سه&zwnj;ریاندا...';
$t[76] = 'ده&zwnj;ی.. له قاپیه&zwnj;کانی دۆزه&zwnj;خه&zwnj;وه بۆ ژووره&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ش بزانن ناشمرن تیایدا و به هه&zwnj;میشه&zwnj;یی تیایدا ده&zwnj;مێننه&zwnj;وه&zwnj;، ئای که شوێن و جێگه&zwnj;ی خۆبه&zwnj;زل زانه&zwnj;کان ناخۆش و ناسازه&zwnj;...';
$t[77] = 'جا ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) ئارامگربه و دڵنیاش به که به&zwnj;ڵێنی خوا ڕاست و دروسته و هه&zwnj;ر دێته&zwnj;دی، ئه&zwnj;وسا یان ئه&zwnj;وه&zwnj;یه که هه&zwnj;ندێ له هه&zwnj;ڕه&zwnj;شه&zwnj;کانمان دژیان به&zwnj;چاوی خۆت ده&zwnj;بینیت، یاخود ئه&zwnj;گه&zwnj;ر گیانمان کێشایت و وه&zwnj;فاتت کرد؛ ئه&zwnj;وانه بۆ کوێ ده&zwnj;چن؟!، به&zwnj;ناچاری هه&zwnj;ر بۆ لای ئێمه ده&zwnj;برێنه&zwnj;وه (ئه&zwnj;وسا به&zwnj;توندی تۆڵه&zwnj;یان لێ ده&zwnj;سێنین).';
$t[78] = 'به&zwnj;ڕاستی ئێمه پێش تۆ پێغه&zwnj;مبه&zwnj;رانی تریشمان ڕه&zwnj;وانه کردووه&zwnj;، هه&zwnj;یانه سه&zwnj;رگوزه&zwnj;شته و به&zwnj;سه&zwnj;رهاتیانمان بۆ گێڕاویته&zwnj;وه&zwnj;، هه&zwnj;شیانه که سه&zwnj;رگوزه&zwnj;شته و به&zwnj;سه&zwnj;رهاتیمان بۆ نه&zwnj;گێڕاویته&zwnj;وه&zwnj;، هیچ پێغه&zwnj;مبه&zwnj;رێکیش بۆی نه&zwnj;بووه موعجزه&zwnj;یه&zwnj;ک پێس بهێنێت به&zwnj;بێ مۆله&zwnj;تی خوای به&zwnj;ده&zwnj;سته&zwnj;ڵات، جاکاتێک فه&zwnj;رمانی خوا هاتبێت، ئه&zwnj;وه جێبه&zwnj;جێ کراوه به&zwnj;ڕاستی، ئه&zwnj;وکاته ئیتر ناحه&zwnj;ق و نادروسته&zwnj;کان خه&zwnj;ساره&zwnj;تمه&zwnj;ند بوون له&zwnj;وێدا.';
$t[79] = 'هه&zwnj;ر خوایه که ماڵاتی بۆ ئێوه ڕام هێناوه&zwnj;، تا هه&zwnj;ندێکی بۆ سواری به&zwnj;کاربهێنن وهه&zwnj;ندێکیشی بۆ خۆراک سوودی لێ وه&zwnj;ربگرن.';
$t[80] = 'هه&zwnj;روه&zwnj;ها سوودی زۆری ترتان ده&zwnj;ست ده&zwnj;که&zwnj;وێت له&zwnj;و ماڵاتانه&zwnj;دا، بۆ ئه&zwnj;وه&zwnj;ی به&zwnj;سواری ئه&zwnj;وانه&zwnj;وه بگه&zwnj;نه ئه&zwnj;و شوێنانه&zwnj;ی نیازتان له دڵدایه بیگه&zwnj;بێ و کاروباری خۆتان ئه&zwnj;نجام بده&zwnj;ن، جا به&zwnj;سواری ئه&zwnj;و وڵاخانه و که&zwnj;شتیش هه&zwnj;ڵده&zwnj;گیرێن (هاتوچۆیان بۆ ئاسان کردووه&zwnj;).';
$t[81] = 'ئه&zwnj;وزاته به&zwnj;ڵگه&zwnj;و ده&zwnj;سته&zwnj;ڵاتی خۆیتان نیشان ده&zwnj;دات، جا به چ به&zwnj;ڵگه&zwnj;و نیشانه و موعجزه&zwnj;یه&zwnj;کی خوایی ئێوه بڕوا ناکه&zwnj;ن و دژی ده&zwnj;وه&zwnj;ستن؟!';
$t[82] = 'ئایا ئه&zwnj;وه به&zwnj;زه&zwnj;ویدا نه&zwnj;گه&zwnj;ڕاون تا ته&zwnj;ماشا بکه&zwnj;ن که چیمان به&zwnj;سه&zwnj;ر قه&zwnj;وم و خه&zwnj;ڵکانی پێش ئه&zwnj;ماندا هێناوه و سه&zwnj;رئه&zwnj;نجامیان چۆن بووه&zwnj;، ئه&zwnj;وانه زۆر له&zwnj;مان زیاتر بوون و هێزیان له&zwnj;مان زۆرتر بوو، شوێنه&zwnj;واریان له&zwnj;زه&zwnj;ویدا ئاشکراتربوو بێگومان ئه&zwnj;و کاروکرده&zwnj;وه نادروسته&zwnj;ی که ده&zwnj;یانکرد هیچ فریایان نه&zwnj;که&zwnj;وت و ڕزگاری نه&zwnj;کردن.';
$t[83] = 'جاکاتێک که پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیان به&zwnj;به&zwnj;ڵگه و نیشانه&zwnj;ی ته&zwnj;واوه&zwnj;وه بۆیان ڕه&zwnj;وانه کرا، له جیاتی ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕیان پێ بهێنن خۆیان دڵخۆش کرد به&zwnj;و ڕاده که&zwnj;مه&zwnj;ی که له زانست و زانیاری هه&zwnj;یانبوو سه&zwnj;رئه&zwnj;نجام هه&zwnj;ڕه&zwnj;شه&zwnj;ی خوا له&zwnj;سه&zwnj;رزاری پێغه&zwnj;مبه&zwnj;راندا یه&zwnj;خه&zwnj;ی پێگرتن و ئه&zwnj;وه&zwnj;ی که ئه&zwnj;وان گاڵته&zwnj;یان پێده&zwnj;کرد ده&zwnj;سته&zwnj;و یه&zwnj;خه&zwnj;یان بوو، فه&zwnj;وتاندنی.';
$t[84] = 'جا کاتێ که تۆڵه و سزای ئێمه&zwnj;یان بینی و دڵنیا بوون که ڕزگاریان نابێت وتیان: ئیتر ئێمه باوه&zwnj;ڕمان به تاک و ته&zwnj;نهایی خوا هێناوه (جگه له&zwnj;و که&zwnj;سی تر ناپه&zwnj;رستین)، بێ باوه&zwnj;ڕیش بووین به&zwnj;و شتانه&zwnj;ی که دامان نابوو به&zwnj;هاوه&zwnj;ڵی خوای په&zwnj;روه&zwnj;ردگار و په&zwnj;شیمانین.';
$t[85] = 'جا بێگومان ئیمان ئاوا سوودی نه&zwnj;بوو بۆیان، کاتێک تۆڵه و خه&zwnj;شم و قینی ئێمه ده&zwnj;بینن و له ترساندا باوه&zwnj;ڕ ده&zwnj;هێنن، چونکه ئه&zwnj;مه به&zwnj;ڕاستی به&zwnj;رنامه&zwnj;ی خوایه بۆ به&zwnj;نده&zwnj;کانی به&zwnj;درێژایی زه&zwnj;مانه ده&zwnj;بێت له ته&zwnj;واوی ئازادیدا، بێ ترس و زۆر باوه&zwnj;ڕی پێبکه&zwnj;ن، باوه&zwnj;ڕی ته&zwnj;نگانه و کاتی تیاچوون په&zwnj;سه&zwnj;ند نیه، ئا له شوێنه&zwnj;دا ئیتر کافرو بێ بڕواکان خه&zwnj;ساره&zwnj;تمه&zwnj;ندی و زه&zwnj;ره&zwnj;ری بێ سنووریان بۆ ماوه&zwnj;یه&zwnj;.';
?>