<?php
$t = array();
$t[1] = 'سه&zwnj;رنجی سه&zwnj;ره&zwnj;تای سووره&zwnj;تی (الشوری) بده&zwnj;';
$t[2] = 'سوێند به&zwnj;م قورئانه ڕوون و ئاشکرایه&zwnj;.';
$t[3] = 'به&zwnj;ڕاستی ئێمه له شه&zwnj;وێکی به فه&zwnj;ڕو پیرۆزدا (که شه&zwnj;وی قه&zwnj;دره&zwnj;) دامانبه&zwnj;زاندووه&zwnj;ته خواره&zwnj;وه&zwnj;، بێگومان ئێمه هه&zwnj;میشه بێدارکه&zwnj;ره&zwnj;ره&zwnj;ی به&zwnj;نده&zwnj;کانمانین.';
$t[4] = 'له&zwnj;و شه&zwnj;وه&zwnj;دا هه&zwnj;موو کارو کرده&zwnj;وه و به&zwnj;رنامه&zwnj;یه&zwnj;کی پڕ له حیکمه&zwnj;ت و دانایی له یه&zwnj;ك جیا ده&zwnj;کرێته&zwnj;وه&zwnj;.';
$t[5] = 'ئه&zwnj;مه&zwnj;ش فه&zwnj;رمان و بڕیارێکه له&zwnj;لایه&zwnj;ن ئێمه&zwnj;وه&zwnj;، بێگومان ئێمه&zwnj;یش پێغه&zwnj;مبه&zwnj;ران ده&zwnj;نێرین.';
$t[6] = 'ڕه&zwnj;وانه کردنی ئه&zwnj;م قورئانه&zwnj;، ڕه&zwnj;حمه&zwnj;تی په&zwnj;روه&zwnj;ردگاری تۆیه بۆ ئاده&zwnj;میزاد، تا له سه&zwnj;رگه&zwnj;ردانی ڕزگاری ببێت، به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;ویشه که بیسه&zwnj;رو زانایه به هه&zwnj;موو شتێك.';
$t[7] = 'ئه&zwnj;و زاته که په&zwnj;روه&zwnj;ردگاری ئاسمانه&zwnj;کان و زه&zwnj;وی و هه&zwnj;موو ئه&zwnj;و شتانه&zwnj;ی که له نێوانیاندایه&zwnj;، ئه&zwnj;گه&zwnj;ر ئێوه دڵنیان و باوه&zwnj;ڕتان هه&zwnj;یه&zwnj;.';
$t[8] = 'هیچ خوایه&zwnj;ك نی یه جگه له&zwnj;و زاته&zwnj;، ده&zwnj;ژێنێ و ده&zwnj;مرێنێت، په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;ش و باوو باپیرانی دێرینیشتانه&zwnj;.';
$t[9] = 'له&zwnj;گه&zwnj;ڵ ئه&zwnj;م هه&zwnj;موو به&zwnj;ڵگه و نیشانه گه&zwnj;وره و گرنگانه&zwnj;دا، خوانه&zwnj;ناسان له گومان و دوودڵیدا ڕۆچوون و سه&zwnj;رگه&zwnj;رمی گه&zwnj;مه&zwnj;و یارین.';
$t[10] = '(ئه&zwnj;وانه هه&zwnj;روا به&zwnj;سه&zwnj;ریانه&zwnj;وه ناچێت) چاوه&zwnj;ڕێی ڕۆژێك بکه که دوکه&zwnj;ڵێکی ئاشکرا به&zwnj;ری ئاسمان ده&zwnj;گرێت.';
$t[11] = 'هه&zwnj;موو خه&zwnj;ڵکی داده&zwnj;گرێته&zwnj;، بۆیه به ترسه&zwnj;وه ده&zwnj;ڵێن: ئه&zwnj;مه سزاو ئازارێکی به ئێشه (وا ڕووی تێ کردووین).';
$t[12] = 'به&zwnj;ناچاری دان به&zwnj;ده&zwnj;سه&zwnj;ڵاتی خوادا ده&zwnj;نێن و ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا تۆ ئه&zwnj;م سزاو ئازاره&zwnj;مان له کۆڵ بکه&zwnj;ره&zwnj;وه&zwnj;، بێگومان ئێمه باوه&zwnj;ڕدارین!!';
$t[13] = '(تازه به&zwnj;سه&zwnj;رچوو!! که&zwnj;ی بێداربوونه&zwnj;وه و ئیمان هێنانی ناچاری سوودی هه&zwnj;یه بۆیان)، چۆن و له&zwnj;کوێ ئامۆژگاری وه&zwnj;رده&zwnj;گرن، له&zwnj;کاتێکدا پێغه&zwnj;مبه&zwnj;رێکی ئاشکرایان بۆ ڕه&zwnj;وانه کرابوو.';
$t[14] = 'پاشان پشتیان تێکرد و وتیان: فێرکراوێکی شێته&zwnj;!!';
$t[15] = 'ئێمه چاك ده&zwnj;تانناسین، هه&zwnj;ر که&zwnj;مێك سزاو ئازارتان لێ دوور بخه&zwnj;نه&zwnj;وه، بێگومان ده&zwnj;چنه&zwnj;وه دۆخی جاران.';
$t[16] = 'ڕۆژێك دێت هه&zwnj;ڵمه&zwnj;تی گه&zwnj;وره&zwnj;ی خۆمان بۆ تاوانباران ده&zwnj;به&zwnj;ین و ئێمه به&zwnj;دڵنیایی یه&zwnj;وه تۆڵه&zwnj;سێنه&zwnj;رین له سته&zwnj;مکاران.';
$t[17] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه پێش ئه&zwnj;مان قه&zwnj;وم و هۆزی فیرعه&zwnj;ونمان تاقیکرده&zwnj;وه&zwnj;، پێغه&zwnj;مبه&zwnj;رێکی به&zwnj;ڕێز و میهره&zwnj;بانیان بۆ هات.';
$t[18] = 'به فیرعه&zwnj;ون و داروده&zwnj;سته&zwnj;که&zwnj;ی وت: که به&zwnj;نده&zwnj;کانی خوا به من بسپێرن، چونکه به&zwnj;ڕاستی من پێغه&zwnj;مبه&zwnj;رێکی ئه&zwnj;مین و دڵسۆزم بۆ هه&zwnj;مووتان.';
$t[19] = 'خۆشتان به&zwnj;گه&zwnj;وره دامه&zwnj;نێن له به&zwnj;رامبه&zwnj;ر خوای به&zwnj;رزو به&zwnj;ده&zwnj;سه&zwnj;ڵاته&zwnj;وه&zwnj;، بێگومان من به&zwnj;ڵگه و نیشانه&zwnj;ی گرنگ و به&zwnj;هێز و ئاشکرام هێناوه بۆتان.';
$t[20] = 'من په&zwnj;نا ده&zwnj;گرم به په&zwnj;روه&zwnj;ردگاری خۆم، که په&zwnj;روه&zwnj;ردگاری ئێوه&zwnj;شه له&zwnj;وه&zwnj;ی که به&zwnj;ردبارانم بکه&zwnj;ن.';
$t[21] = 'خۆ ئه&zwnj;گه&zwnj;ر هه&zwnj;ر باوه&zwnj;ڕم پێ ناکه&zwnj;ن وازم لێ بهێنن و کارتان به&zwnj;منه&zwnj;وه نه&zwnj;بێت.';
$t[22] = 'به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که وازیان لێ نه&zwnj;هێنا، هاناو هاواری بردووه بۆ په&zwnj;روه&zwnj;ردگاری: په&zwnj;روه&zwnj;ردگارا، به&zwnj;ڕاستی ئه&zwnj;مانه کۆمه&zwnj;ڵێکی تاوانبارن.';
$t[23] = 'ئه&zwnj;وسا ئیتر خوا فه&zwnj;رمانی بۆ نارد که شه&zwnj;و ڕه&zwnj;وی بکه به&zwnj;به&zwnj;نده&zwnj;کانم و شار چۆڵ بکه&zwnj;ن، بێگومان ئه&zwnj;وانه به دواتاندا دێن.';
$t[24] = 'جاکه خۆت و ئیمانداران له ده&zwnj;ریا په&zwnj;ڕینه&zwnj;وه ده&zwnj;ریا به&zwnj;جێ بهێڵه به&zwnj;واڵا کراوی چونکه به&zwnj;ڕاستی ئه&zwnj;وانه سوپایه&zwnj;کی خنکێنراون تیایدا...';
$t[25] = 'ئه&zwnj;وانه چه&zwnj;نده&zwnj;ها باخ و باخات و کانیاوی سازگاری زۆریان به&zwnj;جێهێشت...';
$t[26] = 'چه&zwnj;نده&zwnj;ها کێڵگه و بێستان و جێگه و ڕێگه&zwnj;ی خۆشیان له دوا به&zwnj;جێما...';
$t[27] = 'جۆره&zwnj;ها نازو نیعمه&zwnj;تی تریش که ژیانیان به خۆشی تیایدا ده&zwnj;برده سه&zwnj;ر...';
$t[28] = 'ئا به&zwnj;و شێوه&zwnj;یه له&zwnj;وانمان سه&zwnj;نده&zwnj;وه و کردمانه میراتی خه&zwnj;ڵکانێکی تر...';
$t[29] = 'جا نه ئاسمان بۆیان گریا و نه زه&zwnj;ویش، ساته وه&zwnj;ختێکیش مۆڵه&zwnj;تیان نه&zwnj;درا...';
$t[30] = 'پاشان ئێمه نه&zwnj;وه&zwnj;ی ئیسرائیلمان له سزای چه&zwnj;وسێنه&zwnj;ره&zwnj;وه&zwnj;ی فیرعه&zwnj;ون ڕزگارکرد...';
$t[31] = 'به&zwnj;ڵێ، له&zwnj;ده&zwnj;ست فیرعه&zwnj;ون که زۆر خۆی به&zwnj;گه&zwnj;وره ده&zwnj;زانی و به&zwnj;ته&zwnj;واوی له&zwnj;سنووری ده&zwnj;رچوو بوو...';
$t[32] = 'ئێمه ئه&zwnj;و کاته نه&zwnj;وه&zwnj;ی ئیسرائیلمان هه&zwnj;ڵبژارد له ڕووی ئاگایی و زانیاریه&zwnj;وه و ڕێزی ئه&zwnj;وانماندا به&zwnj;سه&zwnj;ر هه&zwnj;موو خه&zwnj;ڵکی ئه&zwnj;و سه&zwnj;رده&zwnj;مه&zwnj;دا...';
$t[33] = 'ئێمه به&zwnj;ڵگه و نیشانه&zwnj;ی ئاشکرای زۆرمان پێبه&zwnj;خشین و به&zwnj;جۆره&zwnj;ها شێوه&zwnj;ی ئاشکراو ڕوون تاقیمانکردنه&zwnj;وه&zwnj;.';
$t[34] = 'به&zwnj;ڕاستی ئه&zwnj;وانه (بت په&zwnj;رستان) ده&zwnj;ڵێن....';
$t[35] = 'ژیانمان ته&zwnj;نها یه&zwnj;ك جاره و زیندوو ناکرێینه&zwnj;وه&zwnj;.';
$t[36] = 'ئه&zwnj;گه&zwnj;ر زیندوووبوونه&zwnj;وه حه&zwnj;قیقه&zwnj;ته&zwnj;، باو و باپیرانمان که مردوون بیانهێننه&zwnj;وه ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن؟!!';
$t[37] = 'ئایا ئه&zwnj;مانه چاکترن یان قه&zwnj;ومی _تبع_ (که قه&zwnj;ومێکی ده&zwnj;سه&zwnj;ڵاتدار بوون له پێش ئیسلامدا، قوڕه&zwnj;یش ده&zwnj;نگوباسی ئازایه&zwnj;تیانی بیستبوو)، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ی پێشیان، ئێمه هه&zwnj;موانیانمان له&zwnj;ناوبرد، به&zwnj;ڕاستی هه&zwnj;رهه&zwnj;موویان تاوانبار بوون.';
$t[38] = 'خۆ ئێمه ئاسمانه&zwnj;کان و زه&zwnj;وی و ئه&zwnj;و شتانه&zwnj;ی نێوانیانمان به&zwnj;گاڵته و بێ هووده دروست نه&zwnj;کردووه&zwnj;.';
$t[39] = '(به&zwnj;ڵکو هه&zwnj;مووی) له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی نه&zwnj;بێت دروستمان نه&zwnj;کردوون، به&zwnj;ڵام زۆربه&zwnj;یان له&zwnj;م ڕاستیه نازانن.';
$t[40] = 'بێگومان دوارۆژ که ڕۆژی جیاکردنه&zwnj;وه&zwnj;یه&zwnj;، واده&zwnj;ی هه&zwnj;مووانیانه سه&zwnj;رجه&zwnj;م.';
$t[41] = 'ڕۆژێکه هیچ گه&zwnj;وره&zwnj;یه&zwnj;ك فریای بچوکی خۆی ناکه&zwnj;وێت و هیچیشیان پشتگیری ناکرێن له&zwnj;لایه&zwnj;ن که&zwnj;سێکه&zwnj;وه&zwnj;.';
$t[42] = 'مه&zwnj;گه&zwnj;ر که&zwnj;سێك به&zwnj;زه&zwnj;یی خوا گرتبێتیه&zwnj;وه به ڕاستی ته&zwnj;نها ئه&zwnj;و خوایه باڵاده&zwnj;ست و میهره&zwnj;بانه.';
$t[43] = 'به&zwnj;ڕاستی دره&zwnj;ختی ژه&zwnj;قنه&zwnj;بووت (دره&zwnj;ختێکه له ناخی دۆزه&zwnj;خدا، له&zwnj;ناو ئاگردا، خوای به&zwnj;ده&zwnj;سه&zwnj;ڵات ڕوواندوویه&zwnj;تی و لق و په&zwnj;لکه&zwnj;کانی به ناویدا بڵاو بۆته&zwnj;وه&zwnj;).';
$t[44] = 'به&zwnj;رو بوومه&zwnj;که&zwnj;ی خۆراکی تاوانبارانه&zwnj;.';
$t[45] = 'هه&zwnj;روه&zwnj;ك کانزای تواوه له&zwnj;ناو ورگ و ڕیخۆڵه&zwnj;کاندا ده&zwnj;کوڵێت.';
$t[46] = 'وه&zwnj;ك کوڵانی ئاوی یه&zwnj;کجار گه&zwnj;رم.';
$t[47] = 'فه&zwnj;رمان ده&zwnj;درێت: ئه&zwnj;و تاوانباره بگرن و به&zwnj;رزی بکه&zwnj;نه&zwnj;وه به&zwnj;سه&zwnj;ر ناوه&zwnj;ڕاستی دۆزه&zwnj;خداو به&zwnj;ری بده&zwnj;نه&zwnj;وه&zwnj;.';
$t[48] = 'له&zwnj;وه&zwnj;ودوا ئاگرو ئاوی زۆر له&zwnj;کوڵ بکه&zwnj;ن به&zwnj;سه&zwnj;ریدا.';
$t[49] = 'ئه&zwnj;وسا پێی ده&zwnj;وترێت: ده&zwnj;بچێژه بێگومان تۆ زۆر خۆتت به&zwnj;شت ده&zwnj;زانی و خۆشت زۆر به&zwnj;ماقووڵ ده&zwnj;خسته به&zwnj;رچاو.';
$t[50] = 'به&zwnj;ڕاستی ئا ئه&zwnj;مه ئه&zwnj;و دۆزه&zwnj;خه&zwnj;یه کاتی خۆی گومانتان لێی هه&zwnj;بوو، مووجاده&zwnj;له&zwnj;تان له&zwnj;سه&zwnj;ری ده&zwnj;کرد.';
$t[51] = '(له&zwnj;ولاشه&zwnj;وه له به&zwnj;هه&zwnj;شدا له سایه&zwnj;ی میهره&zwnj;بانی په&zwnj;روه&zwnj;ردگارا): به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که پارێزگارو دیندارن، له جێگه و شوێنێکی زۆر ئارامدا ژیان ده&zwnj;به&zwnj;نه سه&zwnj;ر.';
$t[52] = 'له باخچه و باخه&zwnj;کانی به&zwnj;هه&zwnj;شتدا، له پاڵ کارێز و کانی و تاڤگه&zwnj;کاندا ده&zwnj;ژین (هه&zwnj;میشه له سه&zwnj;یران و خۆشیدان).';
$t[53] = 'ده&zwnj;پۆشن له ئاوریشمی ته&zwnj;نك و ئه&zwnj;ستوور، له&zwnj;و کاته&zwnj;دا، خۆشه&zwnj;ویستان به&zwnj;رامبه&zwnj;ر یه&zwnj;ك داده&zwnj;نیشن و (به&zwnj;ڕووی خۆشه&zwnj;وه بۆ یه&zwnj;کتر ده&zwnj;ڕوانن و گفتوگۆی خۆش و به&zwnj;تام ده&zwnj;که&zwnj;ن).';
$t[54] = 'هه&zwnj;روه&zwnj;ها حوری چاوگه&zwnj;ش و گوڵئه&zwnj;نداممان کردووه به هاوسه&zwnj;ریان.';
$t[55] = 'بانگ ده&zwnj;که&zwnj;ن تیایدا بۆ هه&zwnj;موو جۆره میوه&zwnj;یه&zwnj;ك، بێ ئه&zwnj;وه&zwnj;ی بترسن له ته&zwnj;واو بوون و، نه&zwnj;خۆش که&zwnj;وتن و له&zwnj;به&zwnj;رچاو که&zwnj;وتن.';
$t[56] = 'له هه&zwnj;مووی خۆشتر ئه&zwnj;وه&zwnj;یه که هه&zwnj;رگیز تاڵی مردن ناچێژن تیایدا جگه له&zwnj;و مردنه&zwnj;ی یه&zwnj;که&zwnj;مجار و په&zwnj;روه&zwnj;ردگاریان له سزای دۆزه&zwnj;خیش به دووری گرتوون و پاڕاستوونی.';
$t[57] = 'جا ئه&zwnj;مه ڕێزو فه&zwnj;زڵێکی تایبه&zwnj;تیه له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری تووه ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، ئه&zwnj;ی ئیماندار، به&zwnj;ڕاستی ته&zwnj;نها ئه&zwnj;وه&zwnj;یه سه&zwnj;رکه&zwnj;وتنی گه&zwnj;وره&zwnj;و بێ سنوور.';
$t[58] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم، که&zwnj;واته بۆیه به&zwnj;زمانی تۆ ئه&zwnj;م قورئانه&zwnj;مان به&zwnj;ڕوونی و ئاسانی ڕه&zwnj;وانه&zwnj;کردووه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی یاداوه&zwnj;ری و ئامۆژگاری وه&zwnj;ربگرن و دابچڵه&zwnj;کێن.';
$t[59] = 'که&zwnj;واته تۆ ئیتر چاوه&zwnj;ڕێی پێش هاتنی به&zwnj;ڵێنه&zwnj;کانی ئێمه بکه له دنیا و قیامه&zwnj;تدا، چونکه بێگومان ئه&zwnj;وانیش چاوه&zwnj;ڕێن!!';
?>