<?php
$t = array();
$t[1] = 'چه&zwnj;ند پیتێک له&zwnj;سه&zwnj;ره&zwnj;تای هه&zwnj;ندێ له&zwnj;سووره&zwnj;ته&zwnj;کانی قورئاندا هاتوون ژماره&zwnj;یان ( 14 ) پیته&zwnj;، نیوه&zwnj;ی کۆی پیته&zwnj;کانی زمانی عه&zwnj;ره&zwnj;بین، بۆ سه&zwnj;لماندنی (اعجاز) و مه&zwnj;زنی و گرنگی قورئان که&zwnj;له&zwnj;توانای هیچ که&zwnj;سدانیه به&zwnj;و پیتانه کتێبێکی ئاوا بێوێنه دابنێت به&zwnj;راده&zwnj;یه&zwnj;ک هه&zwnj;تا هه&zwnj;موو گرۆی ئاده&zwnj;میزاد و په&zwnj;ری کۆ ببنه&zwnj;وه&zwnj;بۆ ئه&zwnj;و مه&zwnj;به&zwnj;سته&zwnj;، بێگومان چه&zwnj;نده&zwnj;ها نهێنی تریشی تیایه&zwnj;، زاناکان له&zwnj;هه&zwnj;ندێکی دواون، هه&zwnj;ندێکیشیان وتویانه: هه&zwnj;ر خوا خۆی زانایه به&zwnj;نهێنی ئه&zwnj;و پیتانه.';
$t[2] = 'ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن خوای باڵا ده&zwnj;ست و داناوه&zwnj;دابه&zwnj;زیوه&zwnj;.';
$t[3] = 'ئێمه ئاسمانه&zwnj;کان و زه&zwnj;وی و ئه&zwnj;م هه&zwnj;موو شتانه&zwnj;ی نێوانمان دروست نه&zwnj;کردووه&zwnj;، ته&zwnj;نها له&zwnj;سه&zwnj;ر بنچینه&zwnj;ی حه&zwnj;ق و ڕاستی نه&zwnj;بێت، بۆ کاتێکی دیاریکراویش، که&zwnj;چی بێ باوه&zwnj;ڕان پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن له&zwnj;و ئاگاداری و بێدارکردنه&zwnj;وه&zwnj;یه&zwnj;';
$t[4] = 'ئه&zwnj;ی محمد ( صلی الله علیه وسلم ) هه&zwnj;واڵم بده&zwnj;نێ: ئایا سه&zwnj;رنجتان داوه&zwnj;له&zwnj;و شتانه&zwnj;ی که&zwnj;له&zwnj;جیاتی خوا ده&zwnj;یانپه&zwnj;رستن و، هاناو هاواریان بۆ ده&zwnj;به&zwnj;ن؟ باشه&zwnj;ئاخر ئه&zwnj;وانه&zwnj;چ شتێکیان له&zwnj;زه&zwnj;ویدا به&zwnj;دیهێناوه&zwnj;، با پێشانم بده&zwnj;ن!... یاخود ئه&zwnj;وانه له&zwnj;هه&zwnj;ڵسوڕانی ئاسمانه&zwnj;کاندا هیچ هاوبه&zwnj;شی وهاوکاریه&zwnj;کیان هه&zwnj;یه&zwnj;؟!.. ( ئه&zwnj;گه&zwnj;ر ده&zwnj;ڵێن: به&zwnj;ڵێ ) ده&zwnj;که&zwnj;واته کتێبێکی ئاسمانیم نیشان بده&zwnj;ن که&zwnj;پێش ئه&zwnj;م قورئانه بۆتان هاتبێت، یان دیارده&zwnj;یه&zwnj;کی زانستی له&zwnj;و ڕووه&zwnj;وه بیسه&zwnj;لمێنێت ئه&zwnj;گه&zwnj;ر ئێوه ڕاست ده&zwnj;که&zwnj;ن!';
$t[5] = 'جا ئیتر کێ له&zwnj;و که&zwnj;سه گومڕاتره که دوعاو نزا و پاڕانه&zwnj;وه&zwnj;، ڕوو به&zwnj;که&zwnj;سێک یان شتێک ده&zwnj;کات، جگه له&zwnj;خوا که ئه&zwnj;گه&zwnj;ر هه&zwnj;تا ڕۆژی قیامه&zwnj;تیش لێی بپاڕێته&zwnj;وه بێ سووده&zwnj;، نه&zwnj;به&zwnj;هانایه&zwnj;وه ده&zwnj;چێت، نه&zwnj;به&zwnj;په&zwnj;رستنه&zwnj;که&zwnj;ش ده&zwnj;زانێت، له&zwnj;کاتێکدا ئه&zwnj;وانه&zwnj;له&zwnj;دۆعاو پارانه&zwnj;وه&zwnj;ی ئه&zwnj;وان بێ ئاگاو غافڵن و هه&zwnj;ستی پێ ناکه&zwnj;ن.';
$t[6] = 'کاتێکیش که&zwnj;خه&zwnj;ڵکی کۆده&zwnj;کرێنه&zwnj;وه بۆ لێ پرسینه&zwnj;وه&zwnj;، بت و په&zwnj;رستراوه&zwnj;کان ده&zwnj;بنه دوژمن بۆ ئه&zwnj;و که&zwnj;سانه&zwnj;که له&zwnj;دونیادا ده&zwnj;یانپه&zwnj;رستن، به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک بڕوایان پێ ناکه&zwnj;ن، به&zwnj;ڵکو به&zwnj;درۆیان ده&zwnj;خه&zwnj;نه&zwnj;وه&zwnj;.';
$t[7] = 'له کاتێکدا ئایه&zwnj;ته ئاشکراو ڕوونه&zwnj;کانی ئێمه به&zwnj;سه&zwnj;ریاندا بخوێنرێته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;ڕ بوون به&zwnj;و حه&zwnj;ق و ڕاستیه&zwnj;ی که&zwnj;بۆیان دێت ده&zwnj;ڵێن: ئه&zwnj;مه&zwnj;جادوویه&zwnj;کی ئاشکرایه.';
$t[8] = 'یاخوا ده&zwnj;ڵێن محمد ( صلی الله علیه وسلم ) خۆی ئه&zwnj;م قورئانه&zwnj;ی هه&zwnj;ڵبه&zwnj;ستووه... پێیان بڵێ: ئه&zwnj;گه&zwnj;ر خۆم هه&zwnj;ڵم به&zwnj;ستبێت، ( ئه&zwnj;وه دڵنیابن خوا تۆڵه&zwnj;م لێده&zwnj;ستێنێت و سزام ده&zwnj;دات )، ئه&zwnj;و کاته ئێوه به&zwnj;هیچ شێوه&zwnj;یه&zwnj;ک ناتوانن له&zwnj;خه&zwnj;شم و قینی خوا ڕزگارم بکه&zwnj;ن، جا هه&zwnj;ر خوا ده&zwnj;زانێت ده&zwnj;رباره&zwnj;ی ئه&zwnj;م قورئانه&zwnj;ئێوه چ دڕۆ و بوختانێک هه&zwnj;ڵده&zwnj;به&zwnj;ستن وتێیدا ڕۆده&zwnj;چن؟ له&zwnj;به&zwnj;ر ئه&zwnj;وه خوام به&zwnj;سه که شایه&zwnj;ت بێت له&zwnj;نێوان من و ئێوه&zwnj;دا، به&zwnj;ڕاستی ئه&zwnj;وزاته لێبوورده&zwnj;و به&zwnj;سۆزو میهره&zwnj;بانه ( بۆ ئه&zwnj;وانه&zwnj;ی په&zwnj;شیمان ده&zwnj;بنه&zwnj;وه له&zwnj;بیروبۆچوونه چه&zwnj;وته&zwnj;کانیان و ڕێگه&zwnj;ی ئیمان ده&zwnj;گرنه&zwnj;به&zwnj;ر ).';
$t[9] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ( صلی الله علیه وسلم ) پێیان بڵێ: که&zwnj;من یه&zwnj;که&zwnj;م پێغه&zwnj;مبه&zwnj;رو تازه&zwnj;په&zwnj;یدابوو نیم، ناشزانم چیم به&zwnj;سه&zwnj;ر دێت، چیش به&zwnj;سه&zwnj;ر ئێوه دێت؟ ( چونکه غه&zwnj;یب زان نیم و له&zwnj;نهێنی و شاراوه&zwnj;کان ئاگادار نیم )، من ته&zwnj;نها شوێنی ئه&zwnj;و وه&zwnj;حی و نیگایه ده&zwnj;که&zwnj;وم که&zwnj;پێم ده&zwnj;گات و من ته&zwnj;نها بێدارکه&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکرام.';
$t[10] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ، باشه&zwnj;، ئه&zwnj;ی ئه&zwnj;وه چۆنه وحاڵتان چۆن ده&zwnj;بێت ئه&zwnj;گه&zwnj;ر ئه&zwnj;م قورئانه له&zwnj;لایه&zwnj;ن خواوه&zwnj;بێت و، ئێوه ئاوا بڕوای پێنه&zwnj;که&zwnj;ن؟! هاوکات له&zwnj;گه&zwnj;ڵ ئه&zwnj;وه&zwnj;دا که&zwnj;شایه&zwnj;تێک له&zwnj;نه&zwnj;وه&zwnj;ی ئیسرائیل شایه&zwnj;تی داوه&zwnj;که&zwnj;چ ته&zwnj;ورات و، چ قورئان له&zwnj;یه&zwnj;ک سه&zwnj;رچاوه&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;باوه&zwnj;ڕی هێنا به&zwnj;محمد ( صلی الله علیه وسلم ) و قورئان که&zwnj;چی ئێوه خۆتان به&zwnj;زل و گه&zwnj;وره&zwnj;زانی و، باوه&zwnj;ڕتان نه&zwnj;هێنا... بێگومان خوا هیدایه&zwnj;ت و ڕێنموویی ده&zwnj;سته&zwnj;ی سته&zwnj;مکاران ناکات.';
$t[11] = 'ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;رن ده&zwnj;رباره&zwnj;ی ئه&zwnj;وانه&zwnj;ی ئیمان و باوه&zwnj;ڕیان هێناوه ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕهێنان به&zwnj;قورئان وبه&zwnj;محمد ( صلى الله عليه وسلم) چاک بوایه&zwnj;، ئه&zwnj;وانه&zwnj;پێش ئێمه نه&zwnj;ده&zwnj;که&zwnj;وتن!! جا چونکه خۆیان هیدایه&zwnj;ت و ڕێنموویی قورئان وه&zwnj;رناگرن ده&zwnj;ڵێن: ئه&zwnj;مه هه&zwnj;ڵبه&zwnj;ستراو و ئه&zwnj;فسانه&zwnj;یه&zwnj;کی کۆنه!!';
$t[12] = 'خۆ پێش ئه&zwnj;م قورئانه&zwnj;، ته&zwnj;وراتی موسا ڕه&zwnj;وانه&zwnj;کراوه&zwnj;تا پێشه&zwnj;واو ڕه&zwnj;حمه&zwnj;ت بێت له&zwnj;کاتی خۆیدا... ئه&zwnj;م قورئانه&zwnj;ش په&zwnj;یامێکه سه&zwnj;لمێنه&zwnj;ری په&zwnj;یام و کتێبه&zwnj;کانی پێش خۆیه&zwnj;تی و، ڕێکه له&zwnj;گه&zwnj;ڵیانداو به&zwnj;زمانێکی عه&zwnj;ره&zwnj;بی پاراوی ڕه&zwnj;وان هاتووه&zwnj;بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;وانه&zwnj;ی سته&zwnj;میان کردووه&zwnj;بێداریان بکاته&zwnj;وه&zwnj;مژده&zwnj;به&zwnj;خشیشه به&zwnj;چاکه&zwnj;کاران';
$t[13] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارمان الله یه&zwnj;و، پاشان پابه&zwnj;ندی ئه&zwnj;و ئیمانه ده&zwnj;بن وڕاست وره&zwnj;وان په&zwnj;یڕه&zwnj;وی ده&zwnj;که&zwnj;ن و له&zwnj;سه&zwnj;ری به&zwnj;رده&zwnj;وامن، ئه&zwnj;وانه&zwnj;هیچ جۆره ترس و بیمێکیان له&zwnj;سه&zwnj;ر نی یه و دڵته&zwnj;نگ نابن...';
$t[14] = 'ئه&zwnj;وانه&zwnj;به&zwnj;هه&zwnj;شتین و ژیانی نه&zwnj;مریی تیایدا ده&zwnj;به&zwnj;نه&zwnj;سه&zwnj;ر، له&zwnj;پاداشتی ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;دا که&zwnj;ئه&zwnj;نجامیان ده&zwnj;دا.';
$t[15] = 'ئێمه فه&zwnj;رمانمان به&zwnj;ئاده&zwnj;میزاد داوه&zwnj;که چاکه&zwnj;کارو چاک ڕه&zwnj;فتار بێت له&zwnj;گه&zwnj;ڵ دایک و باوکیدا، دایکی به&zwnj;ئازاره&zwnj;وه هه&zwnj;ڵی گرتووه&zwnj;و به&zwnj;ده&zwnj;م ئێش و ئازاره&zwnj;وه&zwnj;بوویه&zwnj;تی، سک پربوون و له&zwnj;شیر بڕینه&zwnj;وه&zwnj;ی سی دانه مانگی خایاندوون.. هه&zwnj;تا کاتێک ئینسان ده&zwnj;گاته حاڵه&zwnj;تی توندوتۆڵی و توانایی و ته&zwnj;مه&zwnj;نی چڵ ساڵی ( ئه&zwnj;وسا ئیتر حه&zwnj;ق وایه ئه&zwnj;م دۆعایه بکاته&zwnj;یه&zwnj;کێک له&zwnj;وێرده&zwnj;کانی سه&zwnj;ر زاری و ) بڵێت: په&zwnj;روه&zwnj;ردگار!! کۆمه&zwnj;کیم بکه&zwnj;و یارمه&zwnj;تیم بده&zwnj;تا شوکرانه&zwnj;بژێر و سوپاسگوزاری ئه&zwnj;و نازو نیعمه&zwnj;تانه بم که&zwnj;به&zwnj;من و دایک و باوکمت به&zwnj;خشیوه&zwnj;، هه&zwnj;روه&zwnj;ها یارمه&zwnj;تیم بده&zwnj;له&zwnj;و جۆره کاروکرده&zwnj;وه&zwnj;چاکانه&zwnj;ئه&zwnj;نجام بده&zwnj;م، که&zwnj;تۆ پێی ڕازیت، داواکاریشم چاکه&zwnj;بڕوێنیت له&zwnj;ناو نه&zwnj;وه&zwnj;کانمدا له&zwnj;به&zwnj;ر قازانجی خۆم ( چاکه&zwnj;ی هه&zwnj;رکه&zwnj;س به&zwnj;شی دایک و باوکی تیادایه ) به&zwnj;ڕاستی من گه&zwnj;ڕاومه&zwnj;ته&zwnj;وه بۆلای تۆ، تا له&zwnj;گوناهه&zwnj;کانم خۆش ببیت و من له&zwnj;موسڵمانانی گوێڕایه&zwnj;ڵ و فه&zwnj;رمانبه&zwnj;ردارم.';
$t[16] = 'ئا ئه&zwnj;وانه&zwnj;ئه&zwnj;و که&zwnj;سانه&zwnj;ن که&zwnj;به&zwnj;گوێره&zwnj;ی چاکترینی کرده&zwnj;وه&zwnj;کانیان پاداشتیان ده&zwnj;ده&zwnj;ینه&zwnj;وه&zwnj;، چاوپۆشیش له&zwnj;هه&zwnj;ڵه&zwnj;و گو&zwnj;ناهه&zwnj;کانیان ده&zwnj;که&zwnj;ین له&zwnj;ڕیزی به&zwnj;هه&zwnj;شتی یه&zwnj;کاندا، ئه&zwnj;مه&zwnj;ش به&zwnj;ده&zwnj;ستهێنانی ئه&zwnj;وبه&zwnj;ڵێنه ڕاست و دروسته&zwnj;یه که&zwnj;له&zwnj;کاتی خۆی پێیان درابوو.';
$t[17] = 'به&zwnj;ڵام ئه&zwnj;وه&zwnj;ی که&zwnj;بێزاره له&zwnj;دایک و باوکی باوه&zwnj;ڕداری و پێیان ده&zwnj;ڵێت: ئاخ و ئۆف له&zwnj;ده&zwnj;ست ئێوه&zwnj;، ئایا ئێوه هه&zwnj;ره&zwnj;شه&zwnj;م لێ ده&zwnj;که&zwnj;ن، ئێوه چۆن بڕواتان به&zwnj;وه&zwnj;هه&zwnj;یه من دوای مردن له&zwnj;گۆر ده&zwnj;ربهێنرێم و زیندوو ببمه&zwnj;وه&zwnj;!! له&zwnj;کاتێکدا ئه&zwnj;و هه&zwnj;موو خه&zwnj;ڵکه&zwnj;ش ساڵه&zwnj;هایه و سه&zwnj;ده&zwnj;هایه ده&zwnj;مرن و پێش من مردوون... ( کوان؟! له&zwnj;کوێن بۆ یه&zwnj;کێکیان نه&zwnj;گه&zwnj;ڕایه&zwnj;وه؟! ).. دایک و باوکه&zwnj;که&zwnj;ش زۆر به&zwnj;په&zwnj;رۆشه&zwnj;وه دۆعا و نزا ده&zwnj;که&zwnj;ن و له&zwnj;خوا ده&zwnj;پارێنه&zwnj;وه... کوره&zwnj;هاوار بۆتۆ، داد له&zwnj;ده&zwnj;ست تۆ، ئیمان و باوه&zwnj;ڕبهێنه&zwnj;، بێگومان به&zwnj;ڵێنی خوا حه&zwnj;ق و ڕاسته&zwnj;، که&zwnj;چی ( مێشکی پووچه&zwnj;و په&zwnj;رده&zwnj;ی هێناوه به&zwnj;سه&zwnj;رچاوو دڵیدا )، بێ هیچ بیرکردنه&zwnj;وه&zwnj;یه&zwnj;ک ده&zwnj;ڵێت: ئه&zwnj;مه&zwnj;قسه&zwnj;یه&zwnj;کی کۆنه و داستانی پێشووه&zwnj;کانه!!';
$t[18] = 'ئا ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ئه&zwnj;وانه&zwnj;ن که&zwnj;بڕیاری خوایان له&zwnj;سه&zwnj;ر خۆیان کردووه&zwnj;به&zwnj;ماڵ و چه&zwnj;سپاوه&zwnj;له&zwnj;سه&zwnj;ریان، هاوڕێ له&zwnj;گه&zwnj;ڵ چه&zwnj;نده&zwnj;ها گرۆو ده&zwnj;سته&zwnj;و میلله&zwnj;تانی تری ڕابوردووی بێ باوه&zwnj;ڕی پێش خۆیان، له&zwnj;هه&zwnj;ردوو به&zwnj;ره&zwnj;ی په&zwnj;ری و ئاده&zwnj;میزاد، به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;هه&zwnj;رهه&zwnj;موویان خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;ر مه&zwnj;ند بوون.';
$t[19] = 'هه&zwnj;ر ده&zwnj;سته&zwnj;یه&zwnj;ک، هه&zwnj;ر که&zwnj;سێک له&zwnj;ئیمانداران یان له&zwnj;بێ بڕواکان پله&zwnj;و پایه&zwnj;ی به&zwnj;رز، یان نزمیان هه&zwnj;یه&zwnj;، به&zwnj;گوێره&zwnj;ی کرده&zwnj;وه&zwnj;کانیان که&zwnj;ئه&zwnj;نجامیانداوه&zwnj;، ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;یه خوای په&zwnj;روه&zwnj;ردگار به&zwnj;گوێره&zwnj;ی کرده&zwnj;وه&zwnj;کانیان به&zwnj;ته&zwnj;واوی پاداشتیان بداته&zwnj;وه&zwnj;، بێ ئه&zwnj;وه&zwnj;ی هیچ جۆره سته&zwnj;مێکیان لێ بکرێت.';
$t[20] = 'ڕۆژێک دێت ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ بڕوان به&zwnj;ره&zwnj;و دۆزه&zwnj;خ ده&zwnj;برێن و نیشانیان ده&zwnj;درێت و پێیان ده&zwnj;وترێت: هه&zwnj;رچی خۆشی و شادیه&zwnj;کتان هه&zwnj;بوو له&zwnj;ژیانی دنیاتاندا به&zwnj;ئاره&zwnj;زووی خۆتان ڕاتانبوارد، که&zwnj;وابوو ئه&zwnj;مرۆ ئیتر نۆره&zwnj;ی ئه&zwnj;وه&zwnj;یه که&zwnj;سزای خه&zwnj;جاڵه&zwnj;تی و ڕیسوایی بدرێنه&zwnj;وه&zwnj;، به&zwnj;هۆی ئه&zwnj;وه&zwnj;ی که&zwnj;لووت به&zwnj;رزیتان ده&zwnj;کرد له&zwnj;زه&zwnj;ویدا به&zwnj;ناحه&zwnj;ق و به&zwnj;هۆی ده&zwnj;رچوونتان له&zwnj;سنوور و فه&zwnj;رمانی خوا.';
$t[21] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ( صلى الله عليه وسلم) یادی براکه&zwnj;ی قه&zwnj;ومی عاد ( هود پێغه&zwnj;مبه&zwnj;ر ) بکه&zwnj;، کاتێک قه&zwnj;ومه&zwnj;که&zwnj;ی بێدارکردنه&zwnj;وه له&zwnj;دۆڵ و بیابانه&zwnj;کاندا ( مه&zwnj;به&zwnj;ست ناوچه&zwnj;ی حچر موت ) ه&zwnj;، بێگومان بێدار خه&zwnj;ره&zwnj;وه&zwnj;ی زۆر له&zwnj;پێش ئه&zwnj;و و دوای ئه&zwnj;ویش تێپه&zwnj;ریان کرد، هه&zwnj;ر هه&zwnj;موویان ئه&zwnj;وه&zwnj;یان به&zwnj;خه&zwnj;ڵکی ڕاگه&zwnj;یاندووه که&zwnj;جگه له&zwnj;خوا که&zwnj;سی تر نه&zwnj;په&zwnj;رستن... به&zwnj;ڕاستی من ده&zwnj;ترسم سزایه&zwnj;کی زۆر گه&zwnj;وره&zwnj;و بێ سنوورتان له&zwnj;رۆژێکی سامناکدا بۆ پێش بێت.';
$t[22] = 'خوانه&zwnj;ناسانی قه&zwnj;ومه&zwnj;که&zwnj;ی پێیان وت: ئایا تۆ بۆ ئه&zwnj;وه&zwnj;هاتوویت که ئێمه له&zwnj;به&zwnj;نداینتی خواکانمان لابده&zwnj;یت؟! که&zwnj;واته&zwnj;ئه&zwnj;گه&zwnj;ر له&zwnj;ڕاستگۆیانی ئه&zwnj;و هه&zwnj;ڕه&zwnj;شانه&zwnj;ی که&zwnj;لێمان ده&zwnj;که&zwnj;یت، پێشی بێنه بۆمان!';
$t[23] = 'هود وتی: ڕوودانی ئه&zwnj;و سزایانه و زانینی کاته&zwnj;که&zwnj;ی به&zwnj;ڕاستی ته&zwnj;نها هه&zwnj;ر لای خوا خۆیه&zwnj;تی، من ئه&zwnj;وه&zwnj;م له&zwnj;سه&zwnj;ره که ئه&zwnj;وه&zwnj;ی من بۆی ڕه&zwnj;وانه کراوم پێتان ڕابگه&zwnj;یه&zwnj;نم، به&zwnj;ڵام به&zwnj;داخه&zwnj;وه ئێوه به قه&zwnj;وم و هۆزێکی نه&zwnj;زان و نه&zwnj;فام ده&zwnj;بینم.';
$t[24] = 'جا کاتێک په&zwnj;ڵه هه&zwnj;ورێکیان بینی ڕووی کردۆته دۆڵ و ناوچه&zwnj;که&zwnj;یان، دڵیان خۆشبوو، وتیان: ئه&zwnj;مه&zwnj;بارانی پێیه بۆمان، نه&zwnj;خێر وانیه&zwnj;، به&zwnj;ڵکو ئه&zwnj;وه&zwnj;ئه&zwnj;و کاره&zwnj;ساته&zwnj;یه په&zwnj;له&zwnj;تانه&zwnj;پێش بێت، ڕه&zwnj;شه&zwnj;بایه&zwnj;کی توند، که&zwnj;سزایه&zwnj;کی به&zwnj;ئازارو به&zwnj;ئێشی پێیه.';
$t[25] = 'ئه&zwnj;وه&zwnj;ره&zwnj;شه&zwnj;بایه&zwnj;، هه&zwnj;موو شتێک و هه&zwnj;موو شوێنێک کاول ده&zwnj;کات به&zwnj;فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاری، ئیتر که&zwnj;سیان دیار نه&zwnj;مان جگه&zwnj;ماڵه چۆڵ و هۆڵه&zwnj;کانیان، هه&zwnj;ر ئا به&zwnj;و شێوه&zwnj;یه و به&zwnj;و جۆره سزایانه تۆڵه له&zwnj;قه&zwnj;وم و هۆزو گه&zwnj;لانی تاوانبارو تاوانکار ده&zwnj;سێنین ( له&zwnj;هه&zwnj;موو کات و شووێنێکدا، ئه&zwnj;گه&zwnj;ر یاخی ببن )..';
$t[26] = 'سوێند به&zwnj;خوا به&zwnj;ڕاستی ئێمه ده&zwnj;سه&zwnj;ڵات و تواناییه&zwnj;کی وامان به&zwnj;خشی به&zwnj;قه&zwnj;ومی ( عاد )، که&zwnj;به&zwnj;ئێوه&zwnj;مان نه&zwnj;به&zwnj;خشیووه&zwnj;، هه&zwnj;روه&zwnj;ها هۆکاره&zwnj;ی بیستن و بینین و لێکدانه&zwnj;وه&zwnj;شمان پێدابوون، مه&zwnj;خابن هۆکاره&zwnj;کانی بیستن و بینین و لێکدانه&zwnj;وه سوودی پێنه&zwnj;گه&zwnj;یاندن! ئه&zwnj;مه&zwnj;ش له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;بوو چونکه&zwnj;به&zwnj;به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;کانی خوا بێ باوه&zwnj;ڕ بوون و، به&zwnj;رپه&zwnj;چیان ده&zwnj;دایه&zwnj;وه... ئه&zwnj;و سزایه&zwnj;ی که&zwnj;گاڵته&zwnj;یان پێی ده&zwnj;هات، داوێن گیریان بوو، لێی ده&zwnj;رباز نه&zwnj;بوون.';
$t[27] = 'بێگومانیش ئێمه دانیشتوانی شارو دێهاته&zwnj;کانی ده&zwnj;وروبه&zwnj;رتانمان له&zwnj;ناو برد ( کاتێک یاخی بوون له&zwnj;به&zwnj;رنامه&zwnj;ی ئێمه )، به&zwnj;ڵگه و نیشانه&zwnj;ی جۆراو جۆریشمان خسته&zwnj;به&zwnj;ر چاویان، بۆ ئه&zwnj;وه&zwnj;ی بگه&zwnj;ڕێنه&zwnj;وه بۆ لای ئیمان و ڕوو بکه&zwnj;نه&zwnj;وه&zwnj;باوه&zwnj;ڕ.';
$t[28] = 'جا ئه&zwnj;و بتانه&zwnj;ی که&zwnj;به&zwnj;ته&zwnj;ما بوون له&zwnj;خوا نزیکیان بخه&zwnj;نه&zwnj;وه&zwnj;و له&zwnj;کاتی ته&zwnj;نگانه&zwnj;دا فریایان بکه&zwnj;ون!! ده&zwnj;با فریایان بکه&zwnj;وتنایه&zwnj;؟! به&zwnj;ڵکو هه&zwnj;ر لێیان ون بوون و ئاسه&zwnj;واریان نه&zwnj;ما، ئائه&zwnj;وه&zwnj;بوو درۆکانیان و ئه&zwnj;وه&zwnj;ش بوو که&zwnj;هه&zwnj;ڵیانده&zwnj;به&zwnj;ست ( که&zwnj;گوایه ئه&zwnj;م بتانه&zwnj;بۆیه ده&zwnj;په&zwnj;رستن تا له&zwnj;خوا نزیکیان بکه&zwnj;نه&zwnj;وه&zwnj;، فریایان بکه&zwnj;وه&zwnj;! ).';
$t[29] = 'یادی ئه&zwnj;وه&zwnj;ش بکه&zwnj;کاتێک ئێمه ده&zwnj;سته&zwnj;یه&zwnj;ک په&zwnj;ریمان لادایه لای تۆ، تا گوێ له&zwnj;قورئان بگرن، ئه&zwnj;وسا ئاماده&zwnj;بوون و به&zwnj;یه&zwnj;کتریان وت: گوێ بگرن و بێ ده&zwnj;نگ بن، کاتێک قورئان خوێندنه&zwnj;که ته&zwnj;واو بوو، په&zwnj;ریه&zwnj;کان گه&zwnj;ڕانه&zwnj;وه بۆ ناو هۆزو قه&zwnj;ومه&zwnj;که&zwnj;یان و، ده&zwnj;ستیان کرد به&zwnj;ئاگادارکردنه&zwnj;وه&zwnj;و بانگکردنیان بۆ په&zwnj;یره&zwnj;وی کردنی ئه&zwnj;م ئاینه نوێیه.';
$t[30] = 'وتیان: ئه&zwnj;ی هۆز و گه&zwnj;لی ئێمه! باوه&zwnj;ڕ بکه&zwnj;ن، گوێمان له&zwnj;کتێبێک بوو که&zwnj;له&zwnj;دوای موسا نێردراوه&zwnj;، سه&zwnj;لمێنه&zwnj;ری په&zwnj;یام و کتێبه&zwnj;کانی پێش خۆیه&zwnj;تی و ڕێکه له&zwnj;گه&zwnj;ڵیاندا و، ڕێنموویی و هیدایه&zwnj;ت ده&zwnj;دات بۆ حه&zwnj;ق و ڕاسته&zwnj;شه&zwnj;قامی ڕاست و دروستیش. نباراو گوناهکاران نین که&zwnj;تیاده&zwnj;چن؟!';
$t[31] = 'ئه&zwnj;ی خزمینه!! بچن به&zwnj;ده&zwnj;م بانگه&zwnj;وازی خواوه&zwnj;و ئیمان و باوه&zwnj;ڕی پێبهێنن، ئه&zwnj;و کاته&zwnj;له&zwnj;گوناهه&zwnj;کانیان خۆش ده&zwnj;بێت و له&zwnj;سزای به&zwnj;ئێش و ئازار ده&zwnj;تانپارێزێت';
$t[32] = 'جا ئه&zwnj;وه&zwnj;ی پێشوازی له&zwnj;بانگه&zwnj;وازی خوا ناکات، ئه&zwnj;وه&zwnj;وه&zwnj;نه&zwnj;بێت له&zwnj;ده&zwnj;سه&zwnj;ڵاتی خوا له&zwnj;م سه&zwnj;ر زه&zwnj;ویه&zwnj;دا ده&zwnj;رباز ببێت و خوا ده&zwnj;سه&zwnj;وسان بکات، که&zwnj;سیش ناتوانێت جگه&zwnj;له&zwnj;خوا ڕزگاری بکات و پشتیوانی لێ بکات، ئا ئه&zwnj;وانه&zwnj;له&zwnj;گومڕایی و سه&zwnj;رلێشێواویه&zwnj;کی زۆر ئاشکرادان.';
$t[33] = 'ئایا ئه&zwnj;وه&zwnj;سه&zwnj;رنجیان نه&zwnj;داوه&zwnj;به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;خوایه که&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;وی دروست کردووه&zwnj;و له&zwnj;دروست کردنیشیاندا هیچ ماندوو نه&zwnj;بووه&zwnj;، توانای نی یه مردووه&zwnj;کان زیندوو بکاته&zwnj;وه&zwnj;، به&zwnj;ڵێ، بێگومان ئه&zwnj;و زاته&zwnj;، به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا ده&zwnj;سه&zwnj;ڵاتی هه&zwnj;یه.';
$t[34] = 'ڕۆژێک دێت کافرو خوانه&zwnj;ناسان دۆزه&zwnj;خیان نیشان ده&zwnj;درێت و پێیان ده&zwnj;وترێت: ئێستا ده&zwnj;ڵێن چی؟! ئا ئه&zwnj;مه&zwnj;ئیتر حه&zwnj;ق و حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه نیه؟! ( هه&zwnj;موو به&zwnj;یه&zwnj;ک ده&zwnj;نگ ) ده&zwnj;ڵێن: به&zwnj;ڵێ... سوێند به&zwnj;په&zwnj;روه&zwnj;ردیگارمان ڕاسته&zwnj;و حه&zwnj;قه&zwnj;، ( به&zwnj;ڵام ئه&zwnj;و سوێند خواردن و دان پێدانانه&zwnj;، تازه&zwnj;بێ سووده&zwnj;) بۆیه خوا ده&zwnj;فه&zwnj;رموێت: ماده&zwnj;م وایه سزاو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی ناو ئاگر بچێژن له&zwnj;سه&zwnj;ر ئه&zwnj;نجامی بێ بڕوایی و کافربوونتاندا.';
$t[35] = 'که&zwnj;وابوو ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر ( صلى الله عليه وسلم) دان به&zwnj;خۆدا بگره&zwnj;و خۆڕاگربه&zwnj;، هه&zwnj;روه&zwnj;کوو پێغه&zwnj;مبه&zwnj;رانی خاوه&zwnj;ن ویستی به&zwnj;هێز خۆڕاگر بوون، په&zwnj;له&zwnj;شیان لێ مه&zwnj;که&zwnj;، ئه&zwnj;و ڕۆژه&zwnj;ی که&zwnj;به&zwnj;ڵێنیان دراوه&zwnj;تێ هه&zwnj;ر کاتێک پێشهات وا ده&zwnj;زانن ته&zwnj;نها ساته&zwnj;وه&zwnj;ختێکیان له&zwnj;رۆژێکدا پێچووه&zwnj;، ئه&zwnj;مه&zwnj;به&zwnj;ڵێن و په&zwnj;یام و ڕاگه&zwnj;یاندنێکی ته&zwnj;واوه&zwnj;، جا ئه&zwnj;و کاته&zwnj;مه&zwnj;گه&zwnj;ر ته&zwnj;نها تاوانباراو گوناهکاران نین که&zwnj;تیاده&zwnj;چن؟!';
?>