$t = array();
$t[1] = 'ئه&zwnj;وانه&zwnj;ی بێ بڕوا بوون و به&zwnj;رهه&zwnj;ڵستی ڕێبازی خوایان کرد، ئه&zwnj;وانه&zwnj;خوا کاروکرده&zwnj;وه&zwnj;کانیانی ون و پووچ کردۆته&zwnj;وه&zwnj;، (ته&zwnj;نانه&zwnj;ت چاکه&zwnj;کانیشیان بێ پاداشته&zwnj;، چونکه&zwnj;مه&zwnj;به&zwnj;ستیان خوا نه&zwnj;بووه&zwnj;). ئه&zwnj;وانه&zwnj;ی بێ بڕوا بوون و به&zwnj;رهه&zwnj;ڵستی ڕێبازی خوایان کرد، ئه&zwnj;وانه&zwnj;خوا کاروکرده&zwnj;وه&zwnj;کانیانی ون و پووچ کردۆته&zwnj;وه&zwnj;، (ته&zwnj;نانه&zwnj;ت چاکه&zwnj;کانیشیان بێ پاداشته&zwnj;، چونکه&zwnj;مه&zwnj;به&zwnj;ستیان خوا نه&zwnj;بووه&zwnj;).';
$t[2] = 'ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان هێناوه و کارو کرده&zwnj;وه&zwnj;چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;و باوه&zwnj;ڕیان هێناوه به&zwnj;و قورئانه&zwnj;بوو که&zwnj;بۆ محمد (صلى الله عليه وسلم) دابه&zwnj;زێنراوه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش ڕاسته&zwnj;و له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردیگاره&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;خوا چاوپۆشی کردووه&zwnj;له&zwnj;هه&zwnj;موو گوناهه&zwnj;کانیان، چاکه&zwnj;شی ڕواندووه&zwnj;له&zwnj;دڵ و ده&zwnj;روونیانداو بۆچوونیانی ڕێک و پێک کردووه&zwnj;.';
$t[3] = 'ئه&zwnj;وه&zwnj;ش به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;یه چونکه&zwnj;به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;ڕ بوون شوێنی ناڕاستی که&zwnj;وتبوون... بێگومان ئه&zwnj;وانه&zwnj;ش باوه&zwnj;ڕیان هێناوه و موسڵمان بوون، شوێنی حه&zwnj;ق و ڕاستیه&zwnj;ک که&zwnj;وتوون که&zwnj;له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاره&zwnj;وه&zwnj;هاتووه&zwnj;، ئا به&zwnj;و شێوه&zwnj;یه خوا نموونه&zwnj;ی ئه&zwnj;و دوو کۆمه&zwnj;ڵه ڕوون ده&zwnj;کاته&zwnj;وه&zwnj;بۆ خه&zwnj;ڵکی (تا فریای خۆیان بکه&zwnj;ون).';
$t[4] = '(ئه&zwnj;م فه&zwnj;رمانه&zwnj;بۆ کاتی ڕووبه&zwnj;ڕوو بوونه&zwnj;وه&zwnj;یه&zwnj;ک له&zwnj;گۆره&zwnj;پانی جه&zwnj;نگدا) جا کاتێک گه&zwnj;یشتن به&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون، بێنه ده&zwnj;ست لێیان و له&zwnj;گه&zwnj;ردنیان بده&zwnj;ن (ئه&zwnj;گینا ئه&zwnj;وان ئێوه ده&zwnj;کوژن)، تا کاتێک کوشتاری زۆریان لێ ده&zwnj;که&zwnj;ن، ئینجا که&zwnj;هێزیان نه&zwnj;ما، به&zwnj;دیل بیانگرن به&zwnj;توندی باڵبه&zwnj;ستیان بکه&zwnj;ن، ئه&zwnj;وسا یه&zwnj;کێک له&zwnj;م دوو کاره&zwnj;یان له&zwnj;گه&zwnj;ڵدا ئه&zwnj;نجام بده&zwnj;ن: یان به&zwnj;پیاوه&zwnj;تی و منه&zwnj;ت، یاخود به&zwnj;گۆڕینه&zwnj;وه&zwnj;و فیدیه ئازادیان بکه&zwnj;ن، تا شه&zwnj;ر کۆتایی دێت و ئاسه&zwnj;واری نامێنێت و دوژمنان چه&zwnj;ک داده&zwnj;نێن، بڕیاره&zwnj;که ئاوهایه&zwnj;، ئه&zwnj;گه&zwnj;ر خوا بیویستایه خۆی تۆڵه&zwnj;ی لێ ده&zwnj;سه&zwnj;ندن، به&zwnj;ڵام ده&zwnj;یه&zwnj;وێت به&zwnj;یه&zwnj;کتر تاقیان بکاته&zwnj;وه&zwnj;، ئه&zwnj;وانه&zwnj;ش له&zwnj;پێناوی خوادا شه&zwnj;هید بوون، هه&zwnj;رگیز پاداشتی هه&zwnj;وڵ و کۆششیان به&zwnj;زایه نادات.';
$t[5] = 'ئه&zwnj;وانه&zwnj;رێنموویی ده&zwnj;کات بۆ جێگه و ڕێگه&zwnj;ی خۆش و دڵ و ده&zwnj;رون و بیرو هۆشیان ئاسووده&zwnj;ده&zwnj;کات.';
$t[6] = 'هه&zwnj;روه&zwnj;ها ده&zwnj;یانخاته ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;وه که&zwnj;به&zwnj;جۆره&zwnj;ها شێواز بۆی باس کردوون.';
$t[7] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه&zwnj;، ئه&zwnj;گه&zwnj;ر ئێوه پشتیوانی له ئاینی خوا بکه&zwnj;ن و هه&zwnj;وڵی سه&zwnj;رکه&zwnj;وتنی بده&zwnj;ن، ئه&zwnj;وه&zwnj;خوایش پشتیوانی له&zwnj;ئێوه ده&zwnj;کات و سه&zwnj;رتان ده&zwnj;خات و پایه&zwnj;دارو جێگیرتان ده&zwnj;کات.';
$t[8] = 'ئه&zwnj;وانه&zwnj;ش بێ بڕوا بوون خه&zwnj;جاڵه&zwnj;تی و سه&zwnj;رشۆریان بۆ هه&zwnj;یه کاروکرده&zwnj;وه&zwnj;شیان له&zwnj;دنیادا بێ ئاکامه&zwnj;و له&zwnj;قیامه&zwnj;تیشدا مایه پووچن.';
$t[9] = 'ئائه&zwnj;وه&zwnj;به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;یه بێگومان ئه&zwnj;وان ڕقیان له&zwnj;قورئان بۆته&zwnj;وه و حه&zwnj;زیان له&zwnj;به&zwnj;رنامه&zwnj;ی خوا نه&zwnj;بووه&zwnj;، جا خوایش کارو کرده&zwnj;وه&zwnj;کانی پووچ کردنه&zwnj;وه&zwnj;.';
$t[10] = 'ئایا ئه&zwnj;وه&zwnj;نه&zwnj;گه&zwnj;ڕاون به&zwnj;سه&zwnj;ر زه&zwnj;ویداو سه&zwnj;رنج بده&zwnj;ن، تا بزانن سه&zwnj;ره&zwnj;نجامی ئه&zwnj;و خه&zwnj;ڵکه ناله&zwnj;باره&zwnj;ی پێش ئه&zwnj;مان چۆن بووه&zwnj;؟! خوای به&zwnj;ده&zwnj;سه&zwnj;ڵات ماڵ و حاڵیانی به&zwnj;سه&zwnj;ردا ڕووخاندن، جا بۆ هه&zwnj;موو بێ باوه&zwnj;ڕان سزای ئاوا شتێکی چاوه&zwnj;ڕوان کراوه (دره&zwnj;نگ یان زوو) دووچاریان ده&zwnj;بێت.';
$t[11] = 'ئه&zwnj;وه&zwnj;به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;یه چونکه&zwnj;به&zwnj;ڕاستی خوا پشتگیرو پشتیوانی ئه&zwnj;وانه&zwnj;یه که&zwnj;ئیمان و باوه&zwnj;ڕیان هێناوه&zwnj;، بێگومان بێ باوه&zwnj;ڕانیش هیچ پشتیوانێکیان نیه.';
$t[12] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه وکارو کرده&zwnj;وه&zwnj;چاکه&zwnj;کانیان ئه&zwnj;نجام داوه&zwnj;، خوا ده&zwnj;یانخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه&zwnj;، که&zwnj;چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ناویدا ده&zwnj;ڕوات، ئه&zwnj;وانه&zwnj;ش بێ بڕوا بوون له&zwnj;م دنیایه&zwnj;دا ڕاده&zwnj;بوێرن و ده&zwnj;خۆن وه&zwnj;ک ئاژه&zwnj;ڵ، له&zwnj;ئاینه&zwnj;که&zwnj;شدا دۆزه&zwnj;خ جێ و شوێنیانه!';
$t[13] = 'زۆر شارو شارۆچکه&zwnj;مان له&zwnj;ناو برد، له&zwnj;و شاره&zwnj;ی که&zwnj;تۆیان ده&zwnj;رکرد به&zwnj;هێزتر بوون، هیچ که&zwnj;سیش نه&zwnj;یتوانی پشتیوانیان بێت.';
$t[14] = 'ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که&zwnj;له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاریه&zwnj;وه به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ڕوونی پێگه&zwnj;یشتووه و، باوه&zwnj;ڕی دامه&zwnj;زراوی پێیه&zwnj;تی و له&zwnj;سه&zwnj;ری ده&zwnj;ڕوات، وه&zwnj;کو ئه&zwnj;و که&zwnj;سه&zwnj;وایه که&zwnj;کاروکرده&zwnj;وه&zwnj;ی ناشرین و نادروستی له&zwnj;به&zwnj;ر چاو ڕازێنرابێته&zwnj;وه&zwnj;، ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;شوێنی ویست و ئاره&zwnj;زووی ناپوختی خۆیان که&zwnj;وتوون؟!';
$t[15] = 'وێنه&zwnj;ی ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;ی که&zwnj;به&zwnj;ڵێن دراوه&zwnj;بدرێت به&zwnj;خواناس و پارێزکاران، ئه&zwnj;مه&zwnj;یه&zwnj;: چه&zwnj;نده&zwnj;ها ڕووباری له&zwnj;ئاوی سازگارو تام و نه&zwnj;گۆڕاوی تێدایه&zwnj;، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها ڕووباری شیر که&zwnj;تامی تێک نه&zwnj;چووه&zwnj;، هه&zwnj;روه&zwnj;ها چه&zwnj;نده&zwnj;ها ڕوبار له&zwnj;شه&zwnj;رابی تام خۆش و بۆنخۆش، (که&zwnj;له&zwnj;خواردنه&zwnj;وه&zwnj;ی بێزار نابن و له&zwnj;به&zwnj;ر چاویان ناکه&zwnj;وێت) بۆ ئه&zwnj;وانه&zwnj;ی ده&zwnj;یخۆنه&zwnj;وه&zwnj;، چه&zwnj;نده&zwnj;ها ڕوبار له&zwnj;هه&zwnj;نگوینی پاڵفته&zwnj;و بێگه&zwnj;رد، هه&zwnj;روه&zwnj;ها بۆیان هه&zwnj;یه له&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;دا له&zwnj;هه&zwnj;موو جۆره به&zwnj;رو و بووم و میوه&zwnj;یه&zwnj;کی جوان و به&zwnj;تام و بۆنخۆش، هاوکات له&zwnj;گه&zwnj;ڵ لێخۆشبوون و خۆشه&zwnj;ویستی په&zwnj;روه&zwnj;ردگاریاندا، ئایا ئه&zwnj;و به&zwnj;خته&zwnj;وه&zwnj;رانه&zwnj;وه&zwnj;کو ئه&zwnj;و که&zwnj;سانه&zwnj;وان که&zwnj;له&zwnj;ناو ئاگری دۆزه&zwnj;خدا نه&zwnj;مرن و ئاوی له&zwnj;کوڵ ده&zwnj;کرێت به&zwnj;گه&zwnj;روویاندا، که&zwnj;ریخۆڵه&zwnj;کانیان پارچه&zwnj;پارچه&zwnj;و تۆی تۆی ده&zwnj;کات؟!';
$t[16] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم) هه&zwnj;ندێک له&zwnj;و بێ باوه&zwnj;ڕو دووڕوانه دێن وگوێت بۆ ده&zwnj;گرن، هه&zwnj;تا کاتێک له&zwnj;قسه&zwnj;و گوفتارت بوویته&zwnj;وه&zwnj;، جێت ده&zwnj;هێڵن و به&zwnj;دوودڵیه&zwnj;وه ده&zwnj;پرسن له&zwnj;ئیمانداران: ئه&zwnj;وه&zwnj;باسی چی ده&zwnj;کرد؟ ئه&zwnj;وه&zwnj;له&zwnj;چی ده&zwnj;دوا؟ تا ئه&zwnj;وانه&zwnj;خوا مۆری ناوه&zwnj;به&zwnj;سه&zwnj;ر دڵیاندا و شوێنی ئاره&zwnj;زوو ویستی نه&zwnj;گونجاوی خۆیان که&zwnj;وتوون.';
$t[17] = 'ئه&zwnj;وانه&zwnj;ش ڕێبازی هیدایه&zwnj;ت و ئیماندارییان گرتۆته به&zwnj;ر به&zwnj;گوفتاری پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) هیدایه&zwnj;ت و دیندارییان گه&zwnj;شه&zwnj;ده&zwnj;کات و په&zwnj;ره&zwnj;ده&zwnj;سێنێت، خوایش شاره&zwnj;زایی و پارێزگاریی و دڵنیایی زیاتری پێبه&zwnj;خشین.';
$t[18] = 'ئایا ئه&zwnj;وانه&zwnj;چاوه&zwnj;ڕێی چی ده&zwnj;که&zwnj;ن جگه&zwnj;له&zwnj;قیامه&zwnj;ت که&zwnj;له&zwnj;ناکاو یه&zwnj;خه&zwnj;یان پێ بگرێت؟ چونکه&zwnj;به&zwnj;ڕاستی هه&zwnj;ندێک له&zwnj;نیشانه&zwnj;کانی ده&zwnj;رکه&zwnj;وتووه (که&zwnj;یه&zwnj;کێک له&zwnj;وانه&zwnj;ره&zwnj;وانه&zwnj;کردنی دواهه&zwnj;مین پێغه&zwnj;مبه&zwnj;ره&zwnj;)، جا ئه&zwnj;وکاته&zwnj;که&zwnj;به&zwnj;رپا بێت بیرکردنه&zwnj;وه و ئامۆژگارییان چ سوودێکی ده&zwnj;بێت؟!';
$t[19] = 'ئه&zwnj;ی ئینسان!! ده&zwnj;ی که&zwnj;واتا چاک بزانه&zwnj;و دڵنیابه جگه&zwnj;له&zwnj;خوا، هیچ خوایه&zwnj;کی ترنیه&zwnj;، داوای لێخۆشبوونی گوناه بۆ خۆت و ئیمانداران له&zwnj;پیاوان و ئافره&zwnj;تان هه&zwnj;ر له&zwnj;و بکه&zwnj;و دڵنیابه که&zwnj;خوا ده&zwnj;زانێت له&zwnj;رۆژدا به&zwnj;چی یه&zwnj;وه خه&zwnj;ریکن و، له&zwnj;شه&zwnj;ویشدا له&zwnj;کۆی ده&zwnj;حه&zwnj;وێنه&zwnj;وه و ئارام ده&zwnj;گرن، یاخود ده&zwnj;زانێت چی ده&zwnj;که&zwnj;ن له&zwnj;دونیاداو، ده&zwnj;ره&zwnj;نجامه&zwnj;که&zwnj;شی له&zwnj;قیامه&zwnj;تدا چۆنه.';
$t[20] = 'هه&zwnj;ندێک له&zwnj;ئیمانداران ده&zwnj;ڵێن: خۆزگه سووره&zwnj;تێک (جه&zwnj;نگ دژی کافران ده&zwnj;هاته&zwnj;خواره&zwnj;وه&zwnj;)، که&zwnj;چی کاتێک که&zwnj;سووره&zwnj;تێک به&zwnj;روونی داده&zwnj;به&zwnj;زێت و باسی جه&zwnj;نگی تێدایه&zwnj;، ئه&zwnj;وه&zwnj;ده&zwnj;بینیت ئه&zwnj;وانه&zwnj;ی له&zwnj;دڵیاندا نه&zwnj;خۆشی باوه&zwnj;ڕ لاوازی ودووڕوویی هه&zwnj;یه&zwnj;، چاویان ئه&zwnj;بڵه&zwnj;ق ده&zwnj;بێت، سه&zwnj;یرت ده&zwnj;که&zwnj;ن وه&zwnj;کو که&zwnj;سێک له&zwnj;سه&zwnj;ره&zwnj;مه&zwnj;رگدا بێ هۆشی به&zwnj;سه&zwnj;ردا هاتبێت، جا باشتر وابوو....';
$t[21] = 'که گوێڕایه&zwnj;ڵ بوونایه و قسه&zwnj;ی جوان و به&zwnj;جێیان بکردایه&zwnj;، جا کاتێک کار گه&zwnj;یشته قایمی و جه&zwnj;نگ به&zwnj;رپابوو ئه&zwnj;گه&zwnj;ر له&zwnj;گه&zwnj;ڵ خوادا ڕاست بوونایه&zwnj;، ئه&zwnj;وه&zwnj;چاکتر بوو بۆیان.';
$t[22] = 'ئایا ئێوه به&zwnj;ته&zwnj;مان ئه&zwnj;گه&zwnj;ر له&zwnj;غه&zwnj;زا پشت هه&zwnj;ڵبکه&zwnj;ن یاخود ده&zwnj;سه&zwnj;ڵاتتان که&zwnj;وته&zwnj;ده&zwnj;ست، تۆوی ئاژاوه&zwnj;و فه&zwnj;ساد و تاوان له&zwnj;زه&zwnj;ودا بچێنن و هه&zwnj;رچی په&zwnj;یوه&zwnj;ندی خزمایه&zwnj;تیش هه&zwnj;یه بیپچڕێنن و له&zwnj;ناوی به&zwnj;رن؟!';
$t[23] = 'ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;ئه&zwnj;وانه&zwnj;ن که&zwnj;خوا نه&zwnj;فرینی لێ کردوون و که&zwnj;ڕی کردوون کوێرایی هێناوه به&zwnj;سه&zwnj;ر چاویاندا (ڕاستیه&zwnj;کان نابینن)';
$t[24] = 'ئایا ئه&zwnj;وه&zwnj;بۆچی ئه&zwnj;وانه&zwnj;وردبینی له&zwnj;قورئاندا ناکه&zwnj;ن و گوێی بۆ ناگرن؟! یان ئه&zwnj;وه&zwnj;یه دڵه&zwnj;کان له&zwnj;ئاستی ڕاستیدا قفڵ دراوه&zwnj;؟!';
$t[25] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی پاشگه&zwnj;ز بوونه&zwnj;وه&zwnj;و به&zwnj;ره&zwnj;ودوا گه&zwnj;ڕانه&zwnj;وه&zwnj;، دوای ئه&zwnj;وه&zwnj;ی ڕێگه&zwnj;ی هیدایه&zwnj;ت و ئیمانیان بۆ ده&zwnj;رکه&zwnj;وتبوو، ئه&zwnj;وانه شه&zwnj;یتان به&zwnj;فێڵ و ته&zwnj;ڵه&zwnj;که ته&zwnj;ماداری کرد بوون، به&zwnj;هیوای ته&zwnj;مه&zwnj;ن درێژی و خۆشگوزه&zwnj;رانیش فریوی ده&zwnj;بوون.';
$t[26] = 'ئه&zwnj;و زاڵ بوونی شه&zwnj;یتانه به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه&zwnj;بوو که&zwnj;به&zwnj;ڕاستی دووڕووه&zwnj;کان ده&zwnj;یانوت به&zwnj;وانه&zwnj;ی که&zwnj;داخ له&zwnj;دڵ بوون به&zwnj;رامبه&zwnj;ر ئه&zwnj;وه&zwnj;ی خوا دایبه&zwnj;زاندووه که&zwnj;قورئانه&zwnj;: ئێمه له&zwnj;مه&zwnj;ودوا له&zwnj;هه&zwnj;ندێک هه&zwnj;ڵوێستدا به&zwnj;قسه&zwnj;تان ده&zwnj;که&zwnj;ین، خوایش خۆی چاک ئاگاداره&zwnj;له&zwnj;و نهێنی و پیلانانه&zwnj;ی که&zwnj;ده&zwnj;یشارنه&zwnj;وه.';
$t[27] = 'ئاخۆ حاڵیان چۆن بێت که&zwnj;فریشته&zwnj;کان له&zwnj;کاتی گیان کێشانیاندا به&zwnj;ده&zwnj;م و چاوو به&zwnj;ره&zwnj;و پشتیاندا ده&zwnj;ماڵن.';
$t[28] = 'ئه&zwnj;و سووکایه&zwnj;تی سه&zwnj;ره&zwnj;مه&zwnj;رگه&zwnj;بۆیه به&zwnj;سه&zwnj;ریاندا دێت، چونکه&zwnj;ئه&zwnj;وانه&zwnj;شوێنی ئه&zwnj;و شتانه&zwnj;که&zwnj;وتبوون که&zwnj;قین و خه&zwnj;شمی خوا ده&zwnj;بزوێنێت، ئه&zwnj;وانه&zwnj;بێزار بوون له&zwnj;ره&zwnj;زامه&zwnj;ندی خوا، خوایش کاروکرده&zwnj;وه&zwnj;یانی پووچ و بێ ئاکام کرد.';
$t[29] = 'ئایا ئه&zwnj;وانه&zwnj;ی که&zwnj;دڵیان نه&zwnj;خۆشی دووڕویی تیادایه&zwnj;، وایانزانیووه خوا هه&zwnj;رگیز کینه و بوغزی ده&zwnj;روونیان ئاشرا ناکات؟!';
$t[30] = 'خۆ ئه&zwnj;گه&zwnj;ر بمانویستایه ئه&zwnj;وانه&zwnj;مان پیشان ده&zwnj;دات، ئه&zwnj;وکاته&zwnj;به&zwnj;رواڵه&zwnj;ت ده&zwnj;تناسینه&zwnj;وه&zwnj;، سوێند به&zwnj;خوا له&zwnj;شێواز و ناوه&zwnj;رۆکی قسه&zwnj;و گوفتاریاندا ده&zwnj;یانناسیته&zwnj;وه&zwnj;، خوا خۆیشی ئاگا و زانایه به&zwnj;کارده&zwnj;وه&zwnj;کانتان.';
$t[31] = 'سوێند به&zwnj;خوا ئێمه ده&zwnj;تانخه&zwnj;ینه به&zwnj;ر تاقیکردنه&zwnj;وه&zwnj;، تا له&zwnj;جیهانی واقعدا تێکۆشه&zwnj;ر و خۆگرانتان ده&zwnj;ربکه&zwnj;وێت و ئاشکرا ببێت بۆمان، بۆ ئه&zwnj;وه&zwnj;ش هه&zwnj;واڵی چالاکی ئێوه&zwnj;مان بێته ده&zwnj;ست.';
$t[32] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;ڕن و ڕێگه له&zwnj;بڵاوکردنه&zwnj;وه&zwnj;ی په&zwnj;یامی خوا ده&zwnj;گرن دژایه&zwnj;تی ڕێبازی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) ده&zwnj;که&zwnj;ن، دوای ئه&zwnj;وه&zwnj;ی ڕێبازی هیدایه&zwnj;ت و ڕاستیان بۆ ڕوون بۆته&zwnj;وه&zwnj;، ئا ئه&zwnj;وانه&zwnj;هه&zwnj;رگیز ناتوانن زیان به&zwnj;خواو ئاینی خوا بگه&zwnj;یه&zwnj;نن.... خوای گه&zwnj;وره&zwnj;ش له&zwnj;ئاینده&zwnj;یه&zwnj;کی نزیکدا هه&zwnj;موو کاروکرده&zwnj;وه&zwnj;و پیلانیان پووچه&zwnj;ڵ ده&zwnj;کاته&zwnj;وه&zwnj;.';
$t[33] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی ئیمان و باوه&zwnj;ڕتان هێناوه! گوێڕایه&zwnj;ڵی فه&zwnj;رمانی خواو فه&zwnj;رمانی پێغه&zwnj;مبه&zwnj;ر بن، کاروکرده&zwnj;وه&zwnj;چاکه&zwnj;کانتان پووچ و بێ نرخ مه&zwnj;که&zwnj;ن، (به ڕووپامایی و خۆ به&zwnj;زل زانین و په&zwnj;شیمان بونه&zwnj;وه&zwnj;له&zwnj;خێر ومنه&zwnj;ت کردن و.... هتد)';
$t[34] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;بێ باوه&zwnj;ڕ بوون و به&zwnj;ربه&zwnj;ست و کۆسپیان ده&zwnj;خسته&zwnj;به&zwnj;رده&zwnj;م ڕێبازی خوا، پاشان مردن له&zwnj;کاتێکدا که&zwnj;کافر بوون، ئه&zwnj;وه&zwnj;هه&zwnj;رگیز خوا لێیان خۆش نابێت و چاوپۆشیان لێ ناکات.';
$t[35] = 'ئێوه سستی مه&zwnj;که&zwnj;ن و داوای ئاشتی پێش وه&zwnj;خت له&zwnj;دوژمنان مه&zwnj;که&zwnj;ن، چونکه&zwnj;هه&zwnj;ر ئێوه سه&zwnj;رکه&zwnj;وتوو ده&zwnj;بن، دڵنیا بن که&zwnj;خوایش له&zwnj;گه&zwnj;ڵتانه&zwnj;، کاروکرده&zwnj;وه&zwnj;تان به&zwnj;زایه نادات و لێی که&zwnj;م ناکاته&zwnj;وه&zwnj;.';
$t[36] = 'به&zwnj;ڕاستی ژیانی دنیا ته&zwnj;نها بریتیه له&zwnj;یاری و گه&zwnj;مه&zwnj;یه&zwnj;کی ته&zwnj;مه&zwnj;ن کۆتا، جا ئه&zwnj;گه&zwnj;ر باوه&zwnj;ڕ بهێنن و پارێزکاربن، ئه&zwnj;وه&zwnj;خوا به&zwnj;چاکی پاداشتان ده&zwnj;داته&zwnj;وه&zwnj;، خوایش وه&zwnj;نه&zwnj;بێت داوای به&zwnj;خشینی هه&zwnj;موو سامانتان لێ بکات.';
$t[37] = 'خۆ ئه&zwnj;گه&zwnj;ر داوای به&zwnj;خشینی هه&zwnj;موو سامانتان لێ بکات و ئه&zwnj;رکێکی گران بخاته&zwnj;سه&zwnj;رتان، ئه&zwnj;وه&zwnj;ره&zwnj;زیل و به&zwnj;رچاو ته&zwnj;نگ ده&zwnj;بن، هه&zwnj;موو كینه و بوغزیشتان ده&zwnj;رده&zwnj;هێنێت.';
$t[38] = 'ئا ئه&zwnj;وه&zwnj;ته&zwnj;ئێوه بانگ ده&zwnj;کرێن تا به&zwnj;خشینتان هه&zwnj;بێت له&zwnj;پێناوی خوادا، که&zwnj;چی هه&zwnj;تانه&zwnj;ره&zwnj;زیل و به&zwnj;رچاو ته&zwnj;نگه&zwnj;، خۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;زیل و به&zwnj;رچاو ته&zwnj;نگی هه&zwnj;بێت، ئه&zwnj;وه&zwnj;بێگومان هه&zwnj;ر ڕه&zwnj;زیلی له&zwnj;خۆی ده&zwnj;کات و زه&zwnj;ره&zwnj;ر له&zwnj;خۆی ده&zwnj;دات، چونکه&zwnj;هه&zwnj;ر خوا خۆی ده&zwnj;وڵه&zwnj;مه&zwnj;ندو بێ نیازه&zwnj;، هه&zwnj;ر ئێوه&zwnj;ن هه&zwnj;ژارو نه&zwnj;دارو که&zwnj;مده&zwnj;ستن.. جا ئه&zwnj;گه&zwnj;ر ئێوه پشت هه&zwnj;ڵكه&zwnj;ن له&zwnj;م دین و به&zwnj;رنامه&zwnj;یه&zwnj;، خوا كه&zwnj;سانی چاكتر له ئێوه ده&zwnj;هێنێته مه&zwnj;یدان، كه به هیچ شێوه&zwnj;یه&zwnj;ك له ئێوه ناچن و وه&zwnj;ك ئێوه نابن.';
?>