<?php
$t = array();
$t[1] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه پێشی خواو پێغه&zwnj;مبه&zwnj;ری خوا مه&zwnj;که&zwnj;ون له&zwnj;بڕیار و پێشنیاردا له&zwnj;خوا بترسن و پارێزکاربن، چونکه&zwnj;به&zwnj;ڕاستی خوا بیسه&zwnj;ره به&zwnj;گوفتار&zwnj;تان، زانایه به&zwnj;نیازو نهێنیه&zwnj;کانتان.';
$t[2] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، ده&zwnj;نگتان به&zwnj;سه&zwnj;ر ده&zwnj;نگی پێغه&zwnj;مبه&zwnj;ر (صلى الله عليه وسلم) به&zwnj;رز مه&zwnj;که&zwnj;نه&zwnj;وه&zwnj;له&zwnj;کاتی گفتو گۆدا، قسه&zwnj;بێ په&zwnj;رده&zwnj;مه&zwnj;که&zwnj;ن له&zwnj;خزمه&zwnj;تیدا، نه&zwnj;وه&zwnj;کو کرده&zwnj;وه&zwnj;کانتان پووچ بێته&zwnj;وه و هه&zwnj;ستیشی پێ نه&zwnj;که&zwnj;ن.';
$t[3] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;ده&zwnj;نگیان هێواش ده&zwnj;که&zwnj;نه&zwnj;وه&zwnj;له&zwnj;خزمه&zwnj;ت پێغه&zwnj;مبه&zwnj;ری خوادا، ئه&zwnj;وانه&zwnj;ئه&zwnj;و که&zwnj;سانه&zwnj;ن که&zwnj;خوای گه&zwnj;وره&zwnj;دڵه&zwnj;کانیانی زاخاو داوه&zwnj;بۆ ته&zwnj;قواو خواناسین، ئه&zwnj;وانه&zwnj;لێ خۆشبوون و پاداشتی گه&zwnj;وره&zwnj;و گرانبه&zwnj;ها بۆیان ئاماده&zwnj;یه.';
$t[4] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی له&zwnj;ده&zwnj;ره&zwnj;وه&zwnj;ی ژووره&zwnj;کان بانگت ده&zwnj;که&zwnj;ن، ئه&zwnj;وانه&zwnj;زۆربه&zwnj;یان نه&zwnj;فامن و ژیری وه&zwnj;رناگرن (چونکه&zwnj;ره&zwnj;وشتی کۆمه&zwnj;ڵایه&zwnj;تی دروستیان له&zwnj;و بواره&zwnj;دا ڕه&zwnj;چاو نه&zwnj;کردووه&zwnj;).5. وَڵوْ ڕَنَّهُمْ ێَبَرُوا حَتَّى تَخْرُجَ إِڵیْهِمْ ڵكَانَ خَیْرًا لَّهُمْ وَاللَّهُ غَفُورٌ ڕَّحِیمٌ: خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه&zwnj;خۆگربوونایه&zwnj;، تا ده&zwnj;چویته ده&zwnj;ره&zwnj;وه&zwnj;بۆ لایان، ئه&zwnj;وه&zwnj;چاکتر و ڕه&zwnj;واتر بوو بۆیان خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;، (ده&zwnj;کرێت ئه&zwnj;م چوار ئایه&zwnj;ته ڕێنموویی بێت بۆ سه&zwnj;رجه&zwnj;م ئیمانداران، تا ڕێزی پێشه&zwnj;واکانیان بگرن).';
$t[5] = 'خۆ ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه&zwnj;خۆگربوونایه&zwnj;، تا ده&zwnj;چویته ده&zwnj;ره&zwnj;وه&zwnj;بۆ لایان، ئه&zwnj;وه&zwnj;چاکتر و ڕه&zwnj;واتر بوو بۆیان خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;، (ده&zwnj;کرێت ئه&zwnj;م چوار ئایه&zwnj;ته ڕێنموویی بێت بۆ سه&zwnj;رجه&zwnj;م ئیمانداران، تا ڕێزی پێشه&zwnj;واکانیان بگرن).';
$t[6] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناون ئه&zwnj;گه&zwnj;ر که&zwnj;سێکی متمانه&zwnj;پێ نه&zwnj;کراو هه&zwnj;واڵێکی گرنگی بۆ هێنان، خێرا بڕوای پێ مه&zwnj;که&zwnj;ن، به&zwnj;ڵکو سه&zwnj;رنج بده&zwnj;ن، لێی بکۆڵنه&zwnj;وه تا بۆتان ڕوون ده&zwnj;بێته&zwnj;وه&zwnj;، نه&zwnj;وه&zwnj;کو ئازارو ناخۆشی و گیروگرفت بۆ خه&zwnj;ڵکی دروست بکه&zwnj;ن به&zwnj;نه&zwnj;زانی، جا دوای په&zwnj;شیمان ببنه&zwnj;وه&zwnj;له&zwnj;و کاره&zwnj;ی که&zwnj;پێی هه&zwnj;ستاون.';
$t[7] = 'ئێوه دڵنیابن و بزانن که&zwnj;پێغه&zwnj;مبه&zwnj;ری خوا له&zwnj;ناوتاندایه (پرس و ڕای پێ بکه&zwnj;ن) زۆر شت هه&zwnj;یه ئه&zwnj;گه&zwnj;ر ئه&zwnj;و، به&zwnj;ڕای ئێوه&zwnj;ی بکردایه تووشی گیروگرفت و ناخۆشی ده&zwnj;بوون... به&zwnj;ڵام خوا ئیمان و باوه&zwnj;ڕی له&zwnj;لا خۆشه&zwnj;ویست کردوون و له&zwnj;دڵ و ده&zwnj;رونتاندا ڕازاندویه&zwnj;تیه&zwnj;وه... له&zwnj;و له&zwnj;یشه&zwnj;وه کوفرو بێ باوه&zwnj;ڕی وگوناهوو یاخی بوون له&zwnj;فه&zwnj;رمانی خوای لا تاڵ و ناشیرین کردوون، ئا ئه&zwnj;وانه&zwnj;هه&zwnj;ر خۆیان ڕێگه&zwnj;ی ژیری و سه&zwnj;رفرازیان گرتۆته&zwnj;به&zwnj;ر.';
$t[8] = 'ئه&zwnj;مه&zwnj;ش خۆی ڕێزو به&zwnj;خششێکی تایبه&zwnj;تی و نازو نیعمه&zwnj;تێکه له&zwnj;لایه&zwnj;ن خواوه&zwnj;پێیان دراوه&zwnj;، خوایش زاناو داناو کاربه&zwnj;جێیه.';
$t[9] = 'ئه&zwnj;گه&zwnj;ر دوو ده&zwnj;سته&zwnj;، یان دوو تاقم له&zwnj;موسوڵمانان بوو به&zwnj;شه&zwnj;ڕیان، ئه&zwnj;وه&zwnj;ده&zwnj;ستبه&zwnj;جێ هه&zwnj;وڵ بده&zwnj;ن ڕێکیان بخه&zwnj;ن و ئاشتیان بکه&zwnj;نه&zwnj;وه&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر لایه&zwnj;کیان هه&zwnj;ر ده&zwnj;ستدرێژی ده&zwnj;کرده&zwnj;سه&zwnj;ر لاکه&zwnj;ی تر، ئه&zwnj;وا ئێوه به&zwnj;گژ ئه&zwnj;وه&zwnj;یاندا بچن که&zwnj;سته&zwnj;م و ده&zwnj;ستدرێژی ده&zwnj;کات،، هه&zwnj;تا ده&zwnj;گه&zwnj;ڕێته&zwnj;وه بۆ فه&zwnj;رمانی خوا... جا گه&zwnj;ر لایه&zwnj;نه سه&zwnj;رکه&zwnj;شه&zwnj;که&zwnj;بۆ فه&zwnj;رمانی خوا سه&zwnj;ری دانه&zwnj;واند، ئه&zwnj;وه&zwnj;ئێوه ناوبژیوانێکی دادگه&zwnj;رانه&zwnj;له&zwnj;نێوانیاندا بکه&zwnj;ن و دادپه&zwnj;روه&zwnj;ری ئه&zwnj;نجام بده&zwnj;ن، چونکه&zwnj;به&zwnj;ڕاستی خوا ئه&zwnj;وانه&zwnj;ی خۆش ده&zwnj;وێت که&zwnj;دادپه&zwnj;روه&zwnj;ری ئه&zwnj;نجام ده&zwnj;ده&zwnj;ن.';
$t[10] = 'له&zwnj;ڕاستیدا هه&zwnj;ر ئیمانداران برای یه&zwnj;کن، که&zwnj;واته&zwnj;هه&zwnj;رکاتێک له&zwnj;نێوان دوو براتاندا ناکۆکی په&zwnj;یدا بوو، ئێوه نێوانیان چاک بکه&zwnj;ن و ئاشتیان بكه&zwnj;نه&zwnj;وه&zwnj;، هه&zwnj;میشه له&zwnj;خوا بترسن و پارێزکار بن، بۆ ئه&zwnj;وه&zwnj;ی ڕه&zwnj;حمتان پێ بکرێت.';
$t[11] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه! هیچ که&zwnj;س و هیچ هۆزێک گاڵته به&zwnj;که&zwnj;س و هۆزێکی تر نه&zwnj;کات و زه&zwnj;لیلی نه&zwnj;کات، چونکه&zwnj;له&zwnj;وانه&zwnj;یه ئه&zwnj;وان چاکتر بن له&zwnj;مان، ژنانیش به&zwnj;گاڵته به&zwnj;یه&zwnj;ک نه&zwnj;که&zwnj;ن و یه&zwnj;کتر زه&zwnj;لیل نه&zwnj;که&zwnj;ن، چونکه&zwnj;له&zwnj;وانه&zwnj;یه ئه&zwnj;مان له&zwnj;وان چاکتر بن... سووکایه&zwnj;تی به&zwnj;خۆتان مه&zwnj;که&zwnj;ن و یه&zwnj;کتری عه&zwnj;یبدار مه&zwnj;که&zwnj;ن (چونکه&zwnj;سووکایه&zwnj;تی به&zwnj;یه&zwnj;کتر سووکایه&zwnj;تی یه به&zwnj;خۆتان) هه&zwnj;روه&zwnj;ها ناوو ناتۆره&zwnj;ی ناشیرین بۆ یه&zwnj;کتر دامه&zwnj;تاشن، ئای که&zwnj;چه&zwnj;ند ناشیرینه ناوو ناتۆره&zwnj;ی خراپ له&zwnj;دوای باوه&zwnj;ڕهێنان و موسڵمان بوون، ئه&zwnj;وه&zwnj;ش ته&zwnj;وبه&zwnj;ی نه&zwnj;کردبێت و په&zwnj;شیمان نه&zwnj;بووبێته&zwnj;ووه له&zwnj;گوناهه&zwnj;کانی، جا ئه&zwnj;و جۆره که&zwnj;سانه&zwnj;هه&zwnj;ر سته&zwnj;مکارن.';
$t[12] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، خۆتان زۆر بپارێزن له&zwnj;گومانی خراپ، له&zwnj;ڕاستیدا هه&zwnj;ندێ له&zwnj;و گومانانه گوناهو تاوانن (بۆیه حه&zwnj;ق وایه له&zwnj;هه&zwnj;موو گومانێک خۆپارێزبین) به&zwnj;شوێن که&zwnj;مو کووڕیی و عه&zwnj;یب و عاری یه&zwnj;کتردا مه&zwnj;گه&zwnj;ڕێن و سیخوڕی مه&zwnj;که&zwnj;ن و له&zwnj;پاش مله&zwnj;باسی یه&zwnj;کتری به&zwnj;خراپ مه&zwnj;به&zwnj;ن، ئایا که&zwnj;ستان خۆشی دێت له&zwnj;وه&zwnj;ی گۆشتی برای خۆی به&zwnj;مردوویی بخوات؟! دیاره کاری واتان زۆر پێ سه&zwnj;خت و ناخۆشه&zwnj;، ده&zwnj;ی که&zwnj;واته&zwnj;له&zwnj;خوا بترسن و پارێزگار بن، چونکه&zwnj;به&zwnj;ڕاستی خوا گه&zwnj;ڕانه&zwnj;وه&zwnj;ی خه&zwnj;ڵکی وه&zwnj;رده&zwnj;گرێت و میهره&zwnj;بانه بۆتان.';
$t[13] = 'ئه&zwnj;ی خه&zwnj;ڵکینه بێگومان ئێمه هه&zwnj;مووتانمان له&zwnj;نێرو مێیه&zwnj;ک دروستکردووه (که&zwnj;باوکه&zwnj;ئاده&zwnj;م و دایکه حه&zwnj;وایه&zwnj;) و کردوومانن به&zwnj;چه&zwnj;نده&zwnj;ها گه&zwnj;ل و تیره و هۆزی جۆربه&zwnj;جۆر، تا یه&zwnj;کتر بناسن و په&zwnj;یوه&zwnj;ندیتان خۆش بێت به&zwnj;یه&zwnj;که&zwnj;وه... به&zwnj;ڕاستی به&zwnj;ڕێزترینتان لای خوا ئه&zwnj;و که&zwnj;سانه&zwnj;تان که&zwnj;زۆرتر له&zwnj;خواترسه&zwnj;و فه&zwnj;رمانبه&zwnj;رداری خوایه&zwnj;، بێگومان خوا زاناو ئاگایه به&zwnj;هه&zwnj;مووان.';
$t[14] = 'هه&zwnj;ندێک له&zwnj;بیابان نشینه عه&zwnj;ره&zwnj;به&zwnj;کان وتیان: باوه&zwnj;ڕمان هێناو و موسڵمانین، پێیان بڵێ: نه&zwnj;خێر، باوه&zwnj;ڕتان نه&zwnj;هێناوه&zwnj;، به&zwnj;ڵام بڵێن: به&zwnj;ناچاری موسوڵمان بووین، هێشتا باوه&zwnj;ڕ نه&zwnj;چۆته ناو دڵه&zwnj;کانتانه&zwnj;وه و تیایدا جێگیر نه&zwnj;بووه (چاوه&zwnj;ڕێ ده&zwnj;کرێت که&zwnj;باوه&zwnj;ڕ بهێنن)، خۆ ئه&zwnj;گه&zwnj;ر به&zwnj;ڕاستی ئێوه گوێڕایه&zwnj;ڵی فه&zwnj;رمانی خواو پێغه&zwnj;مبه&zwnj;ری خوا بن، ئه&zwnj;وه&zwnj;خوای گه&zwnj;وره&zwnj;له&zwnj;پاداشتی چاکه&zwnj;کانتان هیچ که&zwnj;م ناکاته&zwnj;وه&zwnj;، چونکه&zwnj;به&zwnj;ڕاستی خوا لێخۆشبوو میهره&zwnj;بانه.';
$t[15] = 'بێگومان ئیماندارانی ڕاست ئه&zwnj;وانه&zwnj;ن که&zwnj;باوه&zwnj;ڕیان به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ری خوا هێناوه و پاشان نه&zwnj;که&zwnj;وتوونه&zwnj;ته&zwnj;هیچ گومان و دوودڵیه&zwnj;که&zwnj;وه&zwnj;، به&zwnj;خۆیان و سه&zwnj;رو ماڵیشیانه&zwnj;وه تێکۆشاون له&zwnj;پێناوی ئاینی خوادا، ته&zwnj;نها ئا ئه&zwnj;وانه&zwnj;ڕاستن.';
$t[16] = 'ئه&zwnj;ی محمد (صلى الله عليه وسلم) بڵێ: ئایا ئێوه خوای گه&zwnj;وره&zwnj;له&zwnj;ئاینی خۆتان ئاگادار ده&zwnj;که&zwnj;ن و پێی ڕاده&zwnj;گه&zwnj;یه&zwnj;نن، له حاڵێکدا خوا زانایه به&zwnj;هه&zwnj;رچی له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه&zwnj;، خوا به&zwnj;هه&zwnj;موو شتێک زاناو ئاگاداره&zwnj;؟!';
$t[17] = 'ئه&zwnj;وانه&zwnj;منه&zwnj;ت ده&zwnj;که&zwnj;ن به&zwnj;سه&zwnj;ر تۆدا که&zwnj;گوایه موسڵمان بوون، پێیان بڵێ: منه&zwnj;ت مه&zwnj;که&zwnj;ن به&zwnj;سه&zwnj;رمدا له&zwnj;سه&zwnj;ر موسڵمان بوونه&zwnj;که&zwnj;تان، به&zwnj;ڵکو خوا منه&zwnj;ت ده&zwnj;کات به&zwnj;سه&zwnj;ر ئێوه&zwnj;دا که&zwnj;هیدایه&zwnj;تی داون بۆ ئیمان و باوه&zwnj;ڕ ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن.';
$t[18] = 'بێگومان خوا نهێنی و شاراوه&zwnj;کانی ئاسمان و زه&zwnj;وی ده&zwnj;زانێت، هه&zwnj;روه&zwnj;ها خوای گه&zwnj;وره&zwnj;بینایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کرده&zwnj;وانه&zwnj;ی که&zwnj;ئه&zwnj;نجانی ده&zwnj;ده&zwnj;ن.';
?>