$t[75] = 'سوێندم به&zwnj;شوێنی ئه&zwnj;ستێره&zwnj;کان (له&zwnj;ئاسمانی فراواندا که&zwnj;له&zwnj;ژماره&zwnj;نایه&zwnj;ن)';
$t[76] = 'بێگومان ئه&zwnj;و سوێنده گه&zwnj;ر بزانن سوێندێکی زۆر گه&zwnj;وره&zwnj;یه (له&zwnj;م سه&zwnj;رده&zwnj;مه&zwnj;دا زیاتر ڕوون بۆته&zwnj;وه که&zwnj;خوای به&zwnj;دیهێنه&zwnj;ر ملیۆنه&zwnj;ها کاکێشان و ملیاره&zwnj;ها ئه&zwnj;ستێره&zwnj;ی دروستکردووه&zwnj;، که&zwnj;کۆمه&zwnj;ڵه&zwnj;ی خۆر له&zwnj;ئاستیدا زۆر بچووکن)';
$t[77] = 'به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی که&zwnj;محمد (صلی الله علیه وسلم) ده&zwnj;یخوێنێته&zwnj;وه قورئانێکه زۆر پیرۆزو به&zwnj;ڕێزه&zwnj;';
$t[78] = 'له&zwnj;کتێبێکی شاراوه&zwnj;دا که&zwnj;_ لوح المحفوظ _ ه&zwnj;، پارێزراوه&zwnj;، (هه&zwnj;روه&zwnj;ها نسخه&zwnj;کانی سه&zwnj;ر زه&zwnj;ویشی پارێزراوه و پارێزراو ده&zwnj;بێت هه&zwnj;تا دنیا دنیایه&zwnj;)';
$t[79] = 'ته&zwnj;نها پاکان نه&zwnj;بێت که&zwnj;س ده&zwnj;ستی لێ نادات';
$t[80] = 'ئه&zwnj;م قورئانه&zwnj;له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگاری جیهانیانه&zwnj;وه نێردراوه&zwnj;ته خواره&zwnj;وه&zwnj;';
$t[81] = 'له&zwnj;گه&zwnj;ڵ ئه&zwnj;و هه&zwnj;موو ڕێزه&zwnj;دا که&zwnj;ده&zwnj;رباره&zwnj;ی قورئان وترا، هێشتا ئێوه ئه&zwnj;ی باوه&zwnj;ڕ لاوازان دوودڵن به&zwnj;رامبه&zwnj;ر ئه&zwnj;م گرفتار و فه&zwnj;رمووده&zwnj;یه و که&zwnj;مته&zwnj;رخه&zwnj;من له&zwnj;به&zwnj;جێ هێنانی فه&zwnj;رمانه&zwnj;کانیدا؟';
$t[82] = 'هه&zwnj;موو به&zwnj;هره&zwnj;یه&zwnj;ک که&zwnj;ده&zwnj;بوایه له&zwnj;قورئان وه&zwnj;ری بگرن بۆته ئه&zwnj;وه&zwnj;ی که&zwnj;بڕوای پێ ناکه&zwnj;ن و به&zwnj;درۆی ده&zwnj;زانن؟';
$t[83] = 'باشه&zwnj;، ئێوه بۆ بیرناکه&zwnj;نه&zwnj;وه&zwnj;، ڕۆژێک دێت که&zwnj;: ڕۆحتان ده&zwnj;گاته&zwnj;گه&zwnj;رووتان';
$t[84] = 'ئێوه&zwnj;ش به&zwnj;ده&zwnj;وری ئه&zwnj;و که&zwnj;سه&zwnj;دا بێ ده&zwnj;سه&zwnj;ڵاتانه ته&zwnj;ماشا ده&zwnj;که&zwnj;ن و سه&zwnj;رنج ده&zwnj;ده&zwnj;ن';
$t[85] = 'له&zwnj;کاتێکدا که&zwnj;ئێمه له&zwnj;ئێوه نزیکترین لێی، به&zwnj;ڵام ئێوه نامانبینن';
$t[86] = 'خۆ ئه&zwnj;گه&zwnj;ر ئێوه زیندوو ناکرێنه&zwnj;وه و پاداشت نادرێته&zwnj;وه....';
$t[87] = 'ئه&zwnj;و ڕوحه&zwnj;تان ده&zwnj;گێڕایه&zwnj;وه ئه&zwnj;گه&zwnj;ر ڕاست ده&zwnj;که&zwnj;ن و ده&zwnj;سه&zwnj;ڵاتتان هه&zwnj;یه&zwnj;';
$t[88] = 'جا ئه&zwnj;گه&zwnj;ر ئه&zwnj;و که&zwnj;سه&zwnj;ی که&zwnj;له&zwnj;سه&zwnj;ره&zwnj;مه&zwnj;رگدایه له&zwnj;نزیکان و خۆشه&zwnj;ویستانی ئێمه بێت';
$t[89] = 'ئه&zwnj;وه&zwnj;ئیتر خۆشی و کامه&zwnj;رانی و ئاسوده&zwnj;یی به&zwnj;هه&zwnj;شتی پڕ له&zwnj;نازو نیعمه&zwnj;ت چاوه&zwnj;ڕێیه&zwnj;تی';
$t[90] = 'خۆ ئه&zwnj;گه&zwnj;ر له&zwnj;ده&zwnj;سته&zwnj;ی ڕاستانیش بێت';
$t[91] = 'ئه&zwnj;وه&zwnj;له&zwnj;لایه&zwnj;ن ده&zwnj;سته&zwnj;ی ڕاستانه&zwnj;وه&zwnj;سڵاوت پێشکه&zwnj;ش ده&zwnj;کرێت';
$t[92] = 'به&zwnj;ڵام ئه&zwnj;گه&zwnj;ر له&zwnj;ده&zwnj;سته&zwnj;ی ئه&zwnj;وانه&zwnj;بێت که&zwnj;گومڕان و بڕوایان به&zwnj;به&zwnj;رنامه&zwnj;ی خوا نیه...';
$t[93] = 'به&zwnj;ئاوی له&zwnj;کوڵ پێشوازیی لێ ده&zwnj;کات...';
$t[94] = 'ده&zwnj;گه&zwnj;یه&zwnj;نرێته دۆزه&zwnj;خ';
$t[95] = 'به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی که&zwnj;باسمان کرد حه&zwnj;قیقه&zwnj;ت و ڕاسته&zwnj;قینه&zwnj;یه و دوور له&zwnj;گومانه&zwnj;';
$t[96] = 'که&zwnj;واته&zwnj;ستایش و سوپاسگوزاری په&zwnj;روه&zwnj;ردگاری گه&zwnj;وره&zwnj;بکه (با ناوی پیرۆزی هه&zwnj;میشه له&zwnj;سه&zwnj;ر زارت بێت)';
