$t[4] = 'هه&zwnj;ر ئه&zwnj;و زاته&zwnj;خۆی، ئاسمانه&zwnj;کان و زه&zwnj;وی له&zwnj;شه&zwnj;ش ڕۆژدا دروست کردووه&zwnj;، له&zwnj;وه&zwnj;ودوا له&zwnj;سه&zwnj;ر ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایی خۆی پایه&zwnj;دارکرد (ئه&zwnj;ڵبه&zwnj;ته چۆنیه&zwnj;تی وه&zwnj;ستان و ته&zwnj;ختی فه&zwnj;رمانڕه&zwnj;وایه&zwnj;تی و گه&zwnj;لێ شتی له&zwnj;و بابه&zwnj;تانه&zwnj;له&zwnj;راده&zwnj;ی بۆچوونی ئێمه به&zwnj;ده&zwnj;ره&zwnj;، ته&zwnj;فسیری ئه&zwnj;م ئایه&zwnj;ته&zwnj;یان له&zwnj;پێشه&zwnj;وا مالێک پرسیوه له&zwnj;وه&zwnj;ڵامدا وتوویه&zwnj;تی: وه&zwnj;ستانه&zwnj;که&zwnj;حه&zwnj;قیقه&zwnj;تی هه&zwnj;یه&zwnj;، چۆنیه&zwnj;تیه&zwnj;که&zwnj;ی شاراوه&zwnj;یه&zwnj;، پرسیارکردن ده&zwnj;رباره&zwnj;ی بیدعه&zwnj;یه&zwnj;، باوه&zwnj;ڕ بوون پێی واجبه&zwnj;)، هه&zwnj;ر خۆی ده&zwnj;زانێت چی ده&zwnj;چێت به&zwnj;ناخی زه&zwnj;ویدا و چیشی لێ دێته ده&zwnj;ره&zwnj;وه&zwnj;، چی له&zwnj;ئاسمان دێته خواره&zwnj;وه&zwnj;و چی پیادا سه&zwnj;رده&zwnj;که&zwnj;وێت، جا له&zwnj;هه&zwnj;ر شوێنێک دابن ئه&zwnj;وزاته&zwnj;له&zwnj;گه&zwnj;ڵتاندایه و پێتان ده&zwnj;زانێت، دڵنیابن که&zwnj;خوا به&zwnj;هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن بینایه';
$t[5] = 'هه&zwnj;رچی له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه ئه&zwnj;و زاته&zwnj;خاوه&zwnj;نیانه&zwnj;، هه&zwnj;موو شتێکیش بۆ لای خوا ده&zwnj;برێته&zwnj;وه&zwnj;';
$t[6] = 'شه&zwnj;و تێهه&zwnj;ڵکێشی ڕۆژ ده&zwnj;کات و ڕۆژ تێهه&zwnj;ڵکێشی شه&zwnj;و ده&zwnj;کات (به&zwnj;درێژ بوونی لایه&zwnj;کیان له&zwnj;سه&zwnj;ر حسابی ئه&zwnj;وی تریان، یان له&zwnj;به&zwnj;ره&zwnj;به&zwnj;یان و ئێواراندا تێهه&zwnj;ڵکێش ده&zwnj;بن)، ئه&zwnj;و زاته&zwnj;زانایه به&zwnj;هه&zwnj;رچی له&zwnj;دڵ و ده&zwnj;روون و سینه&zwnj;کاندایه';
$t[7] = 'خه&zwnj;ڵکینه ئیمان و باوه&zwnj;ڕی دامه&zwnj;زراو بهێنن به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی، له&zwnj;و ماڵ و سامانه&zwnj;ی که&zwnj;خستویه&zwnj;تیه ژێر ده&zwnj;ستان و جێنشینن تیایدا، ببه&zwnj;خشن، بێگومان ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه له&zwnj;ئێوه و ماڵ و سامان ده&zwnj;به&zwnj;خشن پاداشتی گه&zwnj;وره&zwnj;چاوه&zwnj;ڕێیانه&zwnj;';
$t[8] = 'جا ئه&zwnj;وه&zwnj;چیتانه و بۆچی باوه&zwnj;ڕ ناهێنن به&zwnj;خوا له&zwnj;کاتێکدا پێغه&zwnj;مبه&zwnj;ر بانگتان ده&zwnj;کات: که&zwnj;باوه&zwnj;ڕ بهێنن به&zwnj;په&zwnj;روه&zwnj;ردگارتان، خۆ ئه&zwnj;و بێگومان په&zwnj;یمانیشی لێ وه&zwnj;رگرتن، ئه&zwnj;گه&zwnj;ر ئێوه ئیماندارن';
$t[9] = 'خوا ئه&zwnj;و زاته&zwnj;یه ئایه&zwnj;ت ڕوون و ئاشکراکانی قورئان داده&zwnj;به&zwnj;زێنێت بۆ سه&zwnj;ر به&zwnj;نده&zwnj;ی خۆی، تا له&zwnj;تاریکایی یه&zwnj;کان ده&zwnj;رتان بهێنێت و بتانخاته&zwnj;ناو نوور و ڕوناکیه&zwnj;وه&zwnj;، به&zwnj;ڕاستی ئنو خوایه به&zwnj;سۆزو میهره&zwnj;بانه بۆتان';
$t[10] = 'جا ئیتر ئه&zwnj;وه&zwnj;چیتانه&zwnj;و بۆچی ماڵ و سامانتان نابه&zwnj;خشین له&zwnj;رێگه&zwnj;ی خوادا، خۆ هه&zwnj;رچی له&zwnj;ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه بۆ خوا ده&zwnj;مێنێته&zwnj;وه&zwnj;، بێگومان چوونیه&zwnj;ک نین ئه&zwnj;وانه&zwnj;تان که&zwnj;پێش ڕزگاری مه&zwnj;ککه&zwnj;، یاخود پێش سه&zwnj;رکه&zwnj;وتن، ماڵ و سامانی به&zwnj;خشیووه&zwnj;و جه&zwnj;نگاوه&zwnj;، چونکه&zwnj;ئه&zwnj;وانه&zwnj;پله&zwnj;و پایه&zwnj;یان زۆر به&zwnj;رزتره&zwnj;له&zwnj;وانه&zwnj;ی که&zwnj;دوای ڕزگاری مه&zwnj;ککه&zwnj;و به&zwnj;ده&zwnj;ست هێنانی سه&zwnj;رکه&zwnj;وتن ماڵ و سامانیان به&zwnj;خشیووه و جه&zwnj;نگاون، به&zwnj;هه&zwnj;ردوولاشیان خوا به&zwnj;ڵێنی به&zwnj;هه&zwnj;شتی به&zwnj;خشیووه&zwnj;، بێگومان ئه&zwnj;و خوایه ئاگایه به&zwnj;کاروکرده&zwnj;وه&zwnj;تان';
$t[11] = 'ئایا کێ یه ئه&zwnj;وه&zwnj;ی قه&zwnj;رزی چاک ده&zwnj;دات به&zwnj;خواو ماڵ و سامانی ده&zwnj;به&zwnj;خشێت له&zwnj;پێناوی به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی خوادا، ئینجا خوایش پاداشتی بێ سنووری ده&zwnj;داتێ و بۆی چه&zwnj;ند به&zwnj;رابه&zwnj;ر ده&zwnj;کات، هه&zwnj;روه&zwnj;ها پاداشتی به&zwnj;رێزو به&zwnj;نرخی بۆ ئاماده&zwnj;کردووه&zwnj;';
$t[12] = 'ڕۆژێک دێت که&zwnj;ده&zwnj;بینیت پیاوانی ئیماندارو ژنانی ئیماندار به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت به&zwnj;ڕێ ده&zwnj;کرێن، نوورو ڕوناکایه&zwnj;کی ئاشکرا له&zwnj;به&zwnj;رده&zwnj;م و له&zwnj;لای ڕاستیانه&zwnj;وه ده&zwnj;ڕوا، فریشته&zwnj;کان پێیان ده&zwnj;ڵێن: مژده&zwnj;ی ئێوه ئه&zwnj;مرۆ باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;که&zwnj;چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ناویاندا ده&zwnj;ڕوات، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی هه&zwnj;میشه&zwnj;یی و نه&zwnj;بڕاوه تیایدا، ئا ئه&zwnj;وه&zwnj;یه سه&zwnj;رکه&zwnj;وتن و سه&zwnj;رفرازی گه&zwnj;وره&zwnj;و بێ سنوور';
$t[13] = '(ئا له&zwnj;و ڕۆژه&zwnj;دا که&zwnj;ئیمانداران به&zwnj;ره&zwnj;و به&zwnj;هه&zwnj;شت به&zwnj;ڕێ ده&zwnj;کرێن) پیاوان و ژنانی دووڕوو ده&zwnj;هێنرێنه سه&zwnj;ر ڕێیان، به&zwnj;غه&zwnj;مبارییه&zwnj;وه بۆ ئیمانداران ده&zwnj;ڕوانن، ئیماندارانیش هیچ لایان لێ ناکه&zwnj;نه&zwnj;وه&zwnj;، بۆیه به&zwnj;غه&zwnj;مو په&zwnj;ژاره&zwnj;یه&zwnj;کی زۆره&zwnj;وه پێیان ده&zwnj;ڵێن: ده ته&zwnj;ماشایه&zwnj;کمان بکه&zwnj;ن با له&zwnj;نووری ڕووخسارتان به&zwnj;هره&zwnj;وه&zwnj;ر ببین، به&zwnj;ڵام پێیان ده&zwnj;وترێت بگه&zwnj;رێنه&zwnj;وه دواوه&zwnj;، خۆتان به&zwnj;شوێن نوورو ڕوناکیدا بگه&zwnj;ڕێن، ده&zwnj;ستبه&zwnj;جێ دیوارێک دروست ده&zwnj;کرێت له&zwnj;نێوان ئیمانداران و دووڕووه&zwnj;کاندا ده&zwnj;روازه&zwnj;یه&zwnj;کیشی تێدایه&zwnj;، دیوی ناوه&zwnj;وه که&zwnj;رووی له&zwnj;ئیماندارانه ڕووی له&zwnj;ره&zwnj;حمه&zwnj;ت و به&zwnj;هه&zwnj;شته&zwnj;و دیوی ده&zwnj;ره&zwnj;وه&zwnj;، که&zwnj;رووی له&zwnj;دووڕووه&zwnj;کانه ڕووی له&zwnj;سزاو دۆزه&zwnj;خه';
$t[14] = 'ئینجا دووڕووه&zwnj;کان هاوار له&zwnj;ئیمانداران ده&zwnj;که&zwnj;ن، ئایا ئێمه هاوڕێی ئێوه نه&zwnj;بووین؟!، ئێمه هاوکاری ئێوه نه&zwnj;بووین؟! ئیمانداران ده&zwnj;ڵێن: به&zwnj;ڵێ وایه&zwnj;، به&zwnj;ڵام ئێوه خۆتان تووشی دووڕوویی و پیلانگێڕیی کرد، خۆتان مه&zwnj;ڵاس دابوو، بۆ فرسه&zwnj;ت ده&zwnj;گه&zwnj;ڕان که&zwnj;زه&zwnj;ره&zwnj;ر بده&zwnj;ن له&zwnj;ئیمانداران، هه&zwnj;میشه له&zwnj;گومان و دوودڵیدا ده&zwnj;ژیان و بڵاویشتان دکرده&zwnj;وه&zwnj;، هیواو ئاواتی بێ جێ سه&zwnj;ری لێ تێک دابوون، هه&zwnj;تا ویستی خوا یه&zwnj;خه&zwnj;ی پێگرتن و کۆتایی به&zwnj;ژیانتان هات، شه&zwnj;یتانیش غه&zwnj;ڕاو گومڕای کردبوون به&zwnj;میهره&zwnj;بانی خوا و له&zwnj;رێبازه&zwnj;که&zwnj;ی وێڵی کردبوون';
$t[15] = 'ئه&zwnj;مڕۆ ئیتر هیچ جۆره پاکانه&zwnj;و بارامته&zwnj;یه&zwnj;ک له&zwnj;ئێوه و له&zwnj;وانه&zwnj;ش که&zwnj;بێ باوه&zwnj;ڕ بوون وه&zwnj;رناگیرێت، شوێنه&zwnj;وار و مه&zwnj;نزڵگای هه&zwnj;مووتان ئاگری دۆزه&zwnj;خه&zwnj;، هه&zwnj;ر ئه&zwnj;وه&zwnj;ش شایسته&zwnj;تانه&zwnj;، ئای چ سه&zwnj;ره&zwnj;نجامێکی خراپ و ناله&zwnj;باره&zwnj;';
$t[16] = 'ئایا ئیتر ئه&zwnj;وه&zwnj;نه&zwnj;هاتووه بۆ ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه&zwnj;، دڵه&zwnj;کانیان به&zwnj;نوری ئیمان ملکه&zwnj;چ ورو&zwnj;ناک بۆته&zwnj;ووه&zwnj;، ترسی خوای تێدا بڕوێنن به&zwnj;هۆی یادی به&zwnj;رده&zwnj;وامی خواو ئه&zwnj;و قورئانه&zwnj;ی که&zwnj;ره&zwnj;وانه&zwnj;کراوه&zwnj;، وه&zwnj;ک ئه&zwnj;و که&zwnj;سانه&zwnj;نه&zwnj;بن که&zwnj;کتێبی ئاسمانییان بۆ ڕه&zwnj;وانه&zwnj;کرا، به&zwnj;ڵام دوای تێپه&zwnj;ر بوونی ماوه&zwnj;یه&zwnj;کی دوورو درێژ، دڵیان ڕه&zwnj;ق و ڕه&zwnj;ش بوو، زۆربه&zwnj;شیان یاخی و سه&zwnj;رکه&zwnj;ش و ناله&zwnj;بار ده&zwnj;رچوون';
$t[17] = 'به&zwnj;ڕاستی چاک بزانن که&zwnj;هه&zwnj;ر خوا زه&zwnj;وی زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;دوای مردنی (ئێوه&zwnj;ش ئاوا زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;)، بێگومان به&zwnj;ڵگه&zwnj;و نیشانه&zwnj;ی ده&zwnj;سه&zwnj;ڵاتداری خۆمانمان بۆ ڕوون کردنه&zwnj;وه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ژیربن و بفامن';
$t[18] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که&zwnj;خێر خوازن له&zwnj;پیاوان و ئافره&zwnj;تان، ئه&zwnj;وانه&zwnj;ی که&zwnj;قه&zwnj;رز ده&zwnj;ده&zwnj;ن به&zwnj;خواو ماڵ و سامانیان له&zwnj;پێناوی ڕه&zwnj;زامه&zwnj;ندی ئه&zwnj;ودا ده&zwnj;به&zwnj;خشن، ئه&zwnj;وانه&zwnj;چه&zwnj;ند به&zwnj;رابه&zwnj;ر پاداشتیان ده&zwnj;درێته&zwnj;وه و پاداشتی به&zwnj;ڕێزو به&zwnj;نرخ چاوه&zwnj;ڕێیانه&zwnj;';
$t[19] = 'ئه&zwnj;وانه&zwnj;ی که&zwnj;باوه&zwnj;ڕیان هێناوه به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی، ئا ئه&zwnj;وانه&zwnj;ڕاست و ڕاستگۆکانن، شه&zwnj;هیده&zwnj;کانیش لای په&zwnj;روه&zwnj;ردگاریان پاداشت و نوور و ڕوناکی تایبه&zwnj;تی خۆیان هه&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ش که&zwnj;باوه&zwnj;ڕیان نه&zwnj;هێناوه&zwnj;و ئایه&zwnj;ت و فه&zwnj;رمانه&zwnj;کانی ئێمه&zwnj;یان به&zwnj;درۆزانی، ئا ئه&zwnj;وانه&zwnj;نیشته&zwnj;جێی دۆزه&zwnj;خن';
$t[20] = 'چاک بزانن ژیانی دنیا ته&zwnj;نها بریتیه له&zwnj;یاری و گه&zwnj;مه&zwnj;و ڕازاندنه&zwnj;وه&zwnj;و خۆ هه&zwnj;ڵکێشان له&zwnj;نێوان یه&zwnj;کتریدا، هه&zwnj;روه&zwnj;ها زۆرکردنی سامان و ماڵ و منداڵ، جا ئه&zwnj;وانه&zwnj;هه&zwnj;ر هه&zwnj;مووی وه&zwnj;ک بارانێکی به&zwnj;پیت وایه که&zwnj;ببێته به&zwnj;رهه&zwnj;مێکی زۆر، کشتیاره&zwnj;کان سه&zwnj;رسام بکات، له&zwnj;وه&zwnj;ودوا وشک ببێت و زه&zwnj;رد هه&zwnj;ڵگه&zwnj;ڕێت، پاشان وردوخاش ببێت و بکه&zwnj;وێت، بێگومان له&zwnj;قیامه&zwnj;تیشدا سزای زۆر سه&zwnj;خت بۆ دنیا په&zwnj;رستان ئاماده&zwnj;یه&zwnj;، له&zwnj;ولاشه&zwnj;وه لێ خۆشبوون و ڕه&zwnj;زامه&zwnj;ندی له&zwnj;لایه&zwnj;ن خواوه&zwnj;ئیماندارانی چاکه&zwnj;خوازی لێ به&zwnj;هره&zwnj;وه&zwnj;ر ده&zwnj;کرێت، ژیانی دنیاش دوور له&zwnj;خواناسی و دینداری جگه&zwnj;له&zwnj;وه&zwnj;ی که&zwnj;بریتیه له&zwnj;رابواردنێکی که&zwnj;م، شتێکی تر نیه&zwnj;، زۆر که&zwnj;سیشی پێ غه&zwnj;ڕاو ده&zwnj;ستخه&zwnj;ڕۆ ده&zwnj;بێت';
$t[21] = '(که&zwnj;واته&zwnj;) پێشبڕکێ بکه&zwnj;ن بۆ به&zwnj;ده&zwnj;ست هێنانی لێ خۆشبوون له&zwnj;لایه&zwnj;ن په&zwnj;روه&zwnj;ردگارتانه&zwnj;وه&zwnj;و به&zwnj;ده&zwnj;ست هێنانی ئه&zwnj;و به&zwnj;هه&zwnj;شته&zwnj;ی که&zwnj;پانیه&zwnj;که&zwnj;ی وه&zwnj;ک پانتایی ئاسمان و زه&zwnj;ویه&zwnj;، ئاماده&zwnj;کراوه&zwnj;بۆ ئه&zwnj;وانه&zwnj;ی که&zwnj;ئیمان و باوه&zwnj;ڕیان هێناوه به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;کانی، ئه&zwnj;ویش خۆی زیاده ڕێزێکی تایبه&zwnj;تیه له&zwnj;لایه&zwnj;ن خواوه&zwnj;ده&zwnj;یدات به&zwnj;و که&zwnj;سانه&zwnj;ی که&zwnj;خۆی ده&zwnj;یه&zwnj;وێت، هه&zwnj;ر خوا خۆی خاوه&zwnj;نی به&zwnj;خشش و به&zwnj;خشنده&zwnj;یی زۆر گه&zwnj;وره&zwnj;و بێ سنووره&zwnj;';
$t[22] = 'هیچ به&zwnj;ڵاو ڕوداوێک له&zwnj;زه&zwnj;ویدا، بۆتان پێش نه&zwnj;هاتووه&zwnj;که&zwnj;له&zwnj;_ لوح المحفوظ _ دا تۆمارمان نه&zwnj;کردبێت، پێش ئه&zwnj;وه&zwnj;ی ڕووداوه&zwnj;که&zwnj;پێش بێت، ئه&zwnj;و کاره&zwnj;ش بۆ خوا زۆر ئاسانه&zwnj;';
$t[23] = 'ئه&zwnj;مه&zwnj;ش بۆ ئه&zwnj;وه&zwnj;ی که&zwnj;خه&zwnj;فه&zwnj;ت نه&zwnj;خۆن بۆ ئه&zwnj;وه&zwnj;ی له&zwnj;کیستان چووه&zwnj;و به&zwnj;وه&zwnj;ش دڵخۆش و غه&zwnj;ڕڕا نه&zwnj;بن که&zwnj;پێمان به&zwnj;خشیوون (واته&zwnj;که&zwnj;شخه&zwnj;نه&zwnj;که&zwnj;ن)، چونکه&zwnj;خوا که&zwnj;سانی خۆ به&zwnj;گه&zwnj;وره&zwnj;زان و فه&zwnj;خرفرۆشی خۆش ناوێت (ئه&zwnj;وانه&zwnj;ی به&zwnj;به&zwnj;خشینی به&zwnj;هره&zwnj;کان غه&zwnj;ڕڕا ده&zwnj;بن و خۆ باده&zwnj;ده&zwnj;ن و فیز ده&zwnj;که&zwnj;ن)....';
$t[24] = 'یاخود ڕه&zwnj;زیلی ده&zwnj;که&zwnj;ن و فه&zwnj;رمان ده&zwnj;که&zwnj;ن به&zwnj;خه&zwnj;ڵکیش که&zwnj;ره&zwnj;زیل بن و ماڵ و سامان نه&zwnj;به&zwnj;خشن (بۆ به&zwnj;رژه&zwnj;وه&zwnj;ندی گشتی، یان به&zwnj;هه&zwnj;ژاران و لێقه&zwnj;وماوان)، جا ئه&zwnj;وه&zwnj;ی پشت هه&zwnj;ڵکات له&zwnj;فه&zwnj;رمانه&zwnj;کانی خواو ده&zwnj;ست نوقاو بێت، ئه&zwnj;وه&zwnj;بێگومان با چاک بزانێت که&zwnj;خوا هه&zwnj;ر خۆی بێ نیازه و سوپاسکراوه&zwnj;له&zwnj;سه&zwnj;ر به&zwnj;خششه&zwnj;بێ سنووره&zwnj;کانی.';
$t[25] = 'سوێند به&zwnj;خوا بێگومان ئێمه پێغه&zwnj;مبه&zwnj;ره&zwnj;کانمان ڕه&zwnj;وانه&zwnj;کردووه&zwnj;هاوڕێ له&zwnj;گه&zwnj;ڵ به&zwnj;ڵگه و نیشانه&zwnj;و موعجیزه&zwnj;ی ڕوون و ئاشکراداو کتێبه ئاسمانه&zwnj;کانیشمان هێنایه خواره&zwnj;وه&zwnj;که&zwnj;له&zwnj;گه&zwnj;ڵ ئه&zwnj;و پێغه&zwnj;مبه&zwnj;رانه&zwnj;دا بێت، ته&zwnj;رازوو پێوه&zwnj;ری شه&zwnj;رعی و دادپه&zwnj;روه&zwnj;رانه&zwnj;مان بۆ ڕوون کردونه&zwnj;ته&zwnj;وه&zwnj;تا خه&zwnj;ڵکی به&zwnj;عه&zwnj;داله&zwnj;ت و دادپه&zwnj;روه&zwnj;ریی ڕه&zwnj;فتار بکه&zwnj;ن، هه&zwnj;روه&zwnj;ها ئاسنیشمان دروست کردووه&zwnj;که&zwnj;مادده&zwnj;یه&zwnj;کی سه&zwnj;خت و ڕه&zwnj;قه&zwnj;و سوود به&zwnj;خشیشه بۆ خه&zwnj;ڵکی، تا ئه&zwnj;وه&zwnj;ی خوا ده&zwnj;یزانێ ل جیهانی واقعیشدا ده&zwnj;ربکه&zwnj;وێت که&zwnj;کێ پشتگیرو لایه&zwnj;نگری خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;کانیه&zwnj;تی له&zwnj;کاتێکدا که&zwnj;ئه&zwnj;و زاته&zwnj;په&zwnj;نهانه&zwnj;، چونکه&zwnj;به&zwnj;ڕاستی خوا به&zwnj;هێزو باڵاده&zwnj;سته&zwnj;';
$t[26] = 'بێگومان ئێمه نوح و ئیبراهیممان ڕه&zwnj;وانه&zwnj;کردووه&zwnj;، له&zwnj;نه&zwnj;وه&zwnj;کانیشیاندا پێغه&zwnj;مبه&zwnj;رایه&zwnj;تی و کتێبی ئاسمانیمان دابه&zwnj;زاندووه&zwnj;، جا له&zwnj;نه&zwnj;وه&zwnj;کانیاندا که&zwnj;سانێک هه&zwnj;بوون که&zwnj;رێگه و ڕێبازی هیدایه&zwnj;ت و خواناسی گرتۆته&zwnj;به&zwnj;ر، زۆربه&zwnj;شیان خوانه&zwnj;ناس و لارو وێر بوون';
$t[27] = 'له&zwnj;وه&zwnj;ودوا به&zwnj;شوێن پێغه&zwnj;مبه&zwnj;راندا پێغه&zwnj;مبه&zwnj;رانی ترمان ڕه&zwnj;وانه&zwnj;کرد، کۆتایی زنجیره&zwnj;ی ئیسحاقی کوڕی ئیبراهیممان هێنا به&zwnj;عیسای کوڕی مه&zwnj;ریه&zwnj;م، ئینجیلیشمان پێبه&zwnj;خشی سۆزو میهره&zwnj;بانیمان له&zwnj;دڵی ئه&zwnj;وانه&zwnj;دا که&zwnj;شوێنی که&zwnj;وتبووه&zwnj;دابین کرد، به&zwnj;ڵام ئه&zwnj;وان ڕه&zwnj;هبانیه&zwnj;ت و دووره&zwnj;په&zwnj;رێزیان له&zwnj;دنیادا کرده&zwnj;پیشه&zwnj;یان، وه&zwnj;نه&zwnj;بێت ئێمه ئه&zwnj;و حاڵه&zwnj;ته&zwnj;مان له&zwnj;سه&zwnj;ر فه&zwnj;رز کردبن، به&zwnj;ڵکو ته&zwnj;نها به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی خوامان لێ داوا کردبوون، که&zwnj;چی ئه&zwnj;وان ڕه&zwnj;هبانییه&zwnj;تیان به&zwnj;چاکی په&zwnj;یڕه&zwnj;و نه&zwnj;کرد، ئێمه&zwnj;ش پاداشتی ئه&zwnj;وانمان دایه&zwnj;وه که&zwnj;باوه&zwnj;ڕی ڕاست و دروستیان هه&zwnj;بوو، هه&zwnj;رچه&zwnj;نده&zwnj;زۆربه&zwnj;یان لارو وێرو خوانه&zwnj;ناس و یاخی ده&zwnj;رچوون';
$t[28] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی که&zwnj;باوه&zwnj;ڕتان هێناوه له&zwnj;خوا بترسن و پارێزگاربن و باوه&zwnj;ڕی پته&zwnj;و بهێنن به&zwnj;پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی، تا دوو به&zwnj;رابه&zwnj;ر پاداشت و ڕه&zwnj;حمه&zwnj;تتان به&zwnj;سه&zwnj;ردا ببارێنێت، هه&zwnj;روه&zwnj;ها نوور و ڕوناکیه&zwnj;تان پێ ببه&zwnj;خشێت له&zwnj;به&zwnj;ر ڕۆشناییه&zwnj;که&zwnj;ی دا چالاکی بنوێنن و ڕێبازی ژیان بگرنه&zwnj;به&zwnj;ر، تا لێتان خۆش ببێت، چونکه&zwnj;ئه&zwnj;و خوایه لێخۆشبوو میهره&zwnj;بانه&zwnj;';
$t[29] = 'جا ئیتر خاوه&zwnj;نی کتێبه ئاسمانیه&zwnj;کان، با بزانن که&zwnj;ناتوانن چوارچێوه دانێن بۆ به&zwnj;خشنده&zwnj;یی و ڕه&zwnj;حمه&zwnj;ت و میهره&zwnj;بانی خوا، خۆیان به&zwnj;هه&zwnj;ڵبژارده&zwnj;ی خوا بزانن، چونکه&zwnj;به&zwnj;خشش و ڕه&zwnj;حمه&zwnj;ت، ته&zwnj;نها به&zwnj;ده&zwnj;ستی خوایه و هه&zwnj;ر ئه&zwnj;و ده&zwnj;یبه&zwnj;خشێت به&zwnj;وانه&zwnj;ی که&zwnj;خۆی ده&zwnj;یه&zwnj;وێت، ئه&zwnj;و خوایه خاوه&zwnj;نی به&zwnj;خشش و ڕێزو میهره&zwnj;بانیه&zwnj;کی گه&zwnj;وره&zwnj;و بێ سنووره&zwnj;';
?>