$t = array();
$t[1] = 'ئه&zwnj;ی محمد (صلی الله علیه وسلم) بێگومان خوا بیستی و وه&zwnj;ریگرت وتووێژی ئه&zwnj;و ئافره&zwnj;ته&zwnj;ی که گفتوگۆت له&zwnj;گه&zwnj;لدا ده&zwnj;کات ده&zwnj;رباره&zwnj;ی هاوسه&zwnj;ره&zwnj;که&zwnj;ی، سکاڵای خۆیشی بۆلای خوا ده&zwnj;بات، خوایش گوێی له گفتوگۆکه&zwnj;تان بوو، به&zwnj;ڕاستی خوا بیسه&zwnj;رو بینایه&zwnj;. (چوار ئایه&zwnj;تی سه&zwnj;ره&zwnj;تای ئه&zwnj;م سووره&zwnj;ته ده&zwnj;رباره&zwnj;ی خه&zwnj;وله&zwnj;ی کچی ثه&zwnj;عله&zwnj;به&zwnj;و مێرده&zwnj;که&zwnj;ی هاتۆته خواره&zwnj;وه... ڕۆژێک مێرده&zwnj;که&zwnj;ی لێی تووڕه ده&zwnj;بێت و پێی ده&zwnj;ڵێت: تۆ له&zwnj;جێگه&zwnj;ی دایکمی، خه&zwnj;وله دێت بۆ خزمه&zwnj;تی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم)، باسه&zwnj;که&zwnj;ی بۆ ده&zwnj;گێڕێته&zwnj;وه&zwnj;، دوای تاوێک حاله&zwnj;تی وه&zwnj;حی به&zwnj;سه&zwnj;ر پێغه&zwnj;مبه&zwnj;ردا دێت و چوار ئایه&zwnj;تی سه&zwnj;ره&zwnj;تای ئه&zwnj;م سووره&zwnj;ته داده&zwnj;به&zwnj;زێت).';
$t[2] = 'ئه&zwnj;وانه&zwnj;تان به&zwnj;هاوسه&zwnj;رانیان ده&zwnj;ڵێن: تۆ له جێگه&zwnj;ی ده&zwnj;یکمدایت، هاوسه&zwnj;ره&zwnj;که&zwnj;یان به&zwnj;و `قسه&zwnj;یه نابنه دایکیان، چونکه دایکیان ته&zwnj;نها ئه&zwnj;وانه&zwnj;ن که ئه&zwnj;وانیان لێ بووه&zwnj;، ئه&zwnj;وانه قسه&zwnj;یه&zwnj;کی نابه&zwnj;جێ ده&zwnj;که&zwnj;ن وبوختانێکی ناپه&zwnj;سه&zwnj;ند ده&zwnj;ڵێن، بێگومان خوا چاوپۆشی ده&zwnj;کات و لێخۆش ده&zwnj;بێت (ئه&zwnj;گه&zwnj;ر ئه&zwnj;وانه په&zwnj;شیمان ببنه&zwnj;وه و داوای لێخۆشبوون بکه&zwnj;ن)...';
$t[3] = 'ئه&zwnj;وانه&zwnj;ی وا به هاوسه&zwnj;ره&zwnj;کانیان ده&zwnj;ڵێن و دوایی په&zwnj;شیمان ده&zwnj;بنه&zwnj;وه له&zwnj;وه&zwnj;ی که وتویانه&zwnj;، ئه&zwnj;وه ده&zwnj;بێت کۆیله&zwnj;یه&zwnj;ک ئازاد بکه&zwnj;ن پێش ئه&zwnj;وه&zwnj;ی ئه&zwnj;و ژن و مێرده به&zwnj;یه&zwnj;ک بگه&zwnj;ن، ئه&zwnj;و بڕیاره ئێوه&zwnj;ی پێ ئامۆژگاری ده&zwnj;کرێت، خوایش ئاگاداره به&zwnj;و کارو&zwnj;کرده&zwnj;وانه&zwnj;ی ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن...';
$t[4] = 'ئینجا ئه&zwnj;وه&zwnj;ی کۆیله&zwnj;ی ده&zwnj;ست نه&zwnj;که&zwnj;وت، ئه&zwnj;وه ده&zwnj;بێت دوو مانگ له&zwnj;سه&zwnj;ر یه&zwnj;ک به&zwnj;ڕۆژوو بێت پێش به&zwnj;یه&zwnj;کگه&zwnj;یشتنیان، جا ئه&zwnj;وه&zwnj;ی ناتوانێت به ڕۆژوو بێت، با شه&zwnj;ست هه&zwnj;ژار تێر بکات له ژه&zwnj;مێک خواردن، ئه&zwnj;وه&zwnj;ی که باسکرا بۆ ئه&zwnj;وه&zwnj;یه باوه&zwnj;ڕتان پته&zwnj;و بێت به&zwnj;خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی (چونکه خوا چاره&zwnj;سه&zwnj;ر داده&zwnj;نێت بۆ گیروگرفته&zwnj;کان)، ئه&zwnj;و بڕیارانه&zwnj;ش سنووری خوان، بۆ ئه&zwnj;وانه&zwnj;ش که کافرن (ملکه&zwnj;چی فه&zwnj;رمانی خوا نابن) سزای به ئێش و ئازار بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[5] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی دژایه&zwnj;تی و دوژمنایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن، سه&zwnj;رشۆڕ و خه&zwnj;جاڵه&zwnj;ت و شکست خورده&zwnj;ن، هه&zwnj;روه&zwnj;ک ئه&zwnj;وانه&zwnj;ی پێش ئه&zwnj;مان که سه&zwnj;رشۆر و خه&zwnj;جاڵه&zwnj;ت کران و تێکشکان، بێگومان ئێمه به&zwnj;ڵگه و نیشانه&zwnj;ی ڕوون و ئاشکرامان ناردۆته خواره&zwnj;وه له&zwnj;سه&zwnj;ر ئه&zwnj;م ڕاستیانه&zwnj;، جا بۆ ئه&zwnj;وانه&zwnj;ی که کافرو بێ باوه&zwnj;ڕن سزای ڕیسواکه&zwnj;ر ئاماده&zwnj;یه&zwnj;...';
$t[6] = 'له ڕۆژێکدا که خوا هه&zwnj;موویان زیندوو ده&zwnj;کاته&zwnj;وه پێکه&zwnj;وه&zwnj;، ئه&zwnj;وسا هه&zwnj;واڵی هه&zwnj;موو کاروکرده&zwnj;وه&zwnj;کانیان پێ ڕاده&zwnj;گه&zwnj;یه&zwnj;نێت، خوا ئاماری کردووه ئه&zwnj;وان فه&zwnj;رامۆشیان کردووه&zwnj;، خوایش شایه&zwnj;ت و ئاگایه به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا.';
$t[7] = 'ئایه سه&zwnj;رنجت نه&zwnj;داوه که به&zwnj;ڕاستی خوا به هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ریدا هه&zwnj;یه زانا و ئاگایه&zwnj;، سێ که&zwnj;س نیه چپه بکه&zwnj;ن به&zwnj;یه&zwnj;که&zwnj;وه خوا جوار&zwnj;هه&zwnj;میان نه&zwnj;بێت، پێنج که&zwnj;س نیه ئه&zwnj;و زاته شه&zwnj;شه&zwnj;میان نه&zwnj;بێت، له&zwnj;و ژمارانه که&zwnj;متر یان زیاتریش نیه که ئه&zwnj;و لایان نه&zwnj;بێت و ئاگادار نه&zwnj;بێت به سرته و چپه&zwnj;یان، له&zwnj;هه&zwnj;ر شوێنێک و له&zwnj;هه&zwnj;ر کوێدا بن، پاشان له ڕۆژی قیامه&zwnj;تدا ئاگاداریان ده&zwnj;کاته&zwnj;وه به هه&zwnj;موو ئه&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامیانداوه&zwnj;، چونکه به&zwnj;ڕاستی خوا به هه&zwnj;موو شتێک زانایه&zwnj;.';
$t[8] = 'ئایا سه&zwnj;رنجت نه&zwnj;داوه له&zwnj;وانه&zwnj;ی قه&zwnj;ده&zwnj;غه&zwnj;ی چپه&zwnj;یان لێ کراوه&zwnj;، که&zwnj;چی له&zwnj;وه&zwnj;ودوا هه&zwnj;ر ده&zwnj;گه&zwnj;ڕێنه&zwnj;وه سه&zwnj;ر ئه&zwnj;و ڕه&zwnj;فتاره&zwnj;ی که لێیان قه&zwnj;ده&zwnj;غه&zwnj;کراوه&zwnj;، به&zwnj;رده&zwnj;وام خه&zwnj;ریکی چپه&zwnj;یه&zwnj;کن که هه&zwnj;مووی گوناهو دوژمنایه&zwnj;تی و نافه&zwnj;رمانی پێغه&zwnj;مبه&zwnj;ره&zwnj;، کاتێکیش دێن بۆ لات جۆرێک سڵاوت لێ ده&zwnj;که&zwnj;ن، که خوا به&zwnj;و شێوه&zwnj;یه سڵاوی لێ نه&zwnj;کردویت!! به&zwnj;گاڵته&zwnj;وه ده&zwnj;ڵێن: ئه&zwnj;وه بۆچی خوا سزامان نادات به&zwnj;هۆی ئه&zwnj;و قسانه&zwnj;وه که ده&zwnj;یڵێین!! ئه&zwnj;وانه دۆزه&zwnj;خیان به&zwnj;سه که بچنه ناوی و ببرژێنرێن تیایدا، ئای که سه&zwnj;رنجامێکی خراپه&zwnj;.';
$t[9] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه کاتێک چپه ده&zwnj;که&zwnj;ن به&zwnj;یه&zwnj;که&zwnj;وه&zwnj;، چپه&zwnj;یه&zwnj;ک مه&zwnj;که&zwnj;ن که گوناهو دوژمنایه&zwnj;تی و نافه&zwnj;رمانی پێغه&zwnj;مبه&zwnj;ر بێت، با چپه&zwnj;کانتان هه&zwnj;مووی ده&zwnj;رباره&zwnj;ی چاکه و خواناسی و پارێزکاری بێت، له&zwnj;و خوایه بترسن که هه&zwnj;ر بۆ لای ئه&zwnj;و کۆده&zwnj;کرێنه&zwnj;وه (بۆ لێپرسینه&zwnj;وه&zwnj;).';
$t[10] = 'به&zwnj;ڕاستی چپه&zwnj;ی خراپ و نابه&zwnj;جێ هه&zwnj;ر له شه&zwnj;یتانه&zwnj;وه&zwnj;یه&zwnj;، تا ئه&zwnj;وانه غه&zwnj;مبار بکات که باوه&zwnj;ڕیان هێناوه&zwnj;، وه نه&zwnj;بێت ئه&zwnj;و چپه&zwnj;یه هیچ زیانێکیان پێ بگه&zwnj;یه&zwnj;نێت مه&zwnj;گه&zwnj;ر ویستی خوای له&zwnj;سه&zwnj;ر بێت، ده&zwnj;با ئیمانداران هه&zwnj;ر پشت به خوا ببه&zwnj;ستن تا له زیان به دوور بن.';
$t[11] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه کاتێک له دانیشتن و کۆبونه&zwnj;وه&zwnj;کاندا پێتان ده&zwnj;وترێت: ڕێ بده&zwnj;ن به&zwnj;که&zwnj;سانی ترو ئه&zwnj;ڵقه&zwnj;ی دانیشتنه&zwnj;که فراوان بکه&zwnj;ن، ئه&zwnj;وه ئێوه&zwnj;ش فراوانی بکه&zwnj;ن، ئه&zwnj;گه&zwnj;ر ئێوه واتان کرد خوا پاداشتی فراوانتان پێ ده&zwnj;به&zwnj;خشێت، کاتێکیش پێتان ده&zwnj;وترێت هه&zwnj;ستن و جێگه چۆڵ بکه&zwnj;ن، (بۆ پیاوماقووڵ و ڕیش سپی و گه&zwnj;وره&zwnj;کانتان) جێگه چۆڵ بکه&zwnj;ن، جا ئه&zwnj;گه&zwnj;ر واتان کرد خوای گه&zwnj;وره ئه&zwnj;وانه&zwnj;ی ئیمانیان هێناوه له ئێوه و ئه&zwnj;وانه&zwnj;شتان زانست و زانیاریان پێدراوه چه&zwnj;نده&zwnj;ها پله&zwnj;و پایه به&zwnj;رزیان ده&zwnj;کاته&zwnj;وه&zwnj;، خوایش ئاگایه به&zwnj;و کارو کرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[12] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه کاتێک ویستتان چپه&zwnj;یه&zwnj;ک یان قسه&zwnj;یه&zwnj;کی نهێنی له&zwnj;گه&zwnj;ڵ پێغه&zwnj;مبه&zwnj;ردا (صلی الله علیه وسلم) بکه&zwnj;ن، پێش ئه&zwnj;و کاره خێرێک بکه&zwnj;ن (به&zwnj;گوێره&zwnj;ی توانا شتێک ببه&zwnj;خشن به&zwnj;هه&zwnj;ژاران)، ئه&zwnj;وه چاکتره بۆتان و خاوێنتره&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر نه&zwnj;تان بوو، ئه&zwnj;وه چاک بزانن که خوا لێ خۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[13] = 'ئایا ده&zwnj;ترسان له هه&zwnj;ژاری پێش ئه&zwnj;وه&zwnj;ی که به نهێنی قسه بکه&zwnj;ن، خێرو چاکه&zwnj;یه&zwnj;ک ئه&zwnj;نجام بده&zwnj;ن؟! جا کاتێکیش که نه&zwnj;تانکرد خوا هه&zwnj;ر لێتان خۆش بوو، ده ئیتر ئێوه نوێژه&zwnj;کانتان به&zwnj;چاکی ئه&zwnj;نجام بده&zwnj;ن و زه&zwnj;کات بده&zwnj;ن و فه&zwnj;رمانبه&zwnj;رداریی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی بکه&zwnj;ن، خوایش زۆر ئاگایه به&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[14] = 'ئایا سه&zwnj;رنجی ئه&zwnj;وانه&zwnj;ت نه&zwnj;داوه پشتگیریان له هۆزێك کرد، که خوا خه&zwnj;شمی لێ گرتوون و ڕقی لێیانه، ئه&zwnj;و دووڕووانه نه سه&zwnj;ر به ئێوه&zwnj;ن، نه سه&zwnj;ر به&zwnj;وانیشن، سوێند له&zwnj;سه&zwnj;ر درۆ ده&zwnj;خۆن له کاتێکدا که خۆشیان ده&zwnj;زانن...';
$t[15] = 'خوا سزایه&zwnj;کی سه&zwnj;خت و به&zwnj;تینی بۆ ئاماده کردوون، به&zwnj;ڕاستی ئه&zwnj;وه&zwnj;ی ئه&zwnj;وان ده&zwnj;یانکرد ڕه&zwnj;فتارێکی ناشیرین و کارێکی نابه&zwnj;جێ بوو.';
$t[16] = 'ئه&zwnj;وانه سوێنده&zwnj;کانیان کردووه به&zwnj;قه&zwnj;ڵغان بۆ خۆیان تا سه&zwnj;روماڵیان سه&zwnj;لامه&zwnj;ت بێت، به&zwnj;و هۆیه&zwnj;شه&zwnj;وه به&zwnj;ربه&zwnj;ستیان داناوه له&zwnj;به&zwnj;رده&zwnj;م به&zwnj;ره&zwnj;و پێش چوونی ڕێبازی خوادا، هه&zwnj;ر بۆیه&zwnj;ش سزاو ئازارێکی سه&zwnj;رشۆرکه&zwnj;ر بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[17] = 'نه ماڵ و سامانیان نه ڕۆڵه&zwnj;کانیان هه&zwnj;رگیز نابنه هۆی ئه&zwnj;وه&zwnj;ی که له ده&zwnj;ست تۆڵه&zwnj;ی خوا ڕزگاریان بکات، ئه&zwnj;وانه نیشته&zwnj;جێی ناو دۆزه&zwnj;خن و تیایدا بۆ هه&zwnj;میشه به&zwnj;زیندوویی ده&zwnj;مێننه&zwnj;وه.';
$t[18] = 'له&zwnj;و ڕۆژه&zwnj;دا که خوا هه&zwnj;موویان زیندوو ده&zwnj;کاته&zwnj;وه&zwnj;، ده&zwnj;ست ده&zwnj;که&zwnj;نه&zwnj;وه به سوێند خواردن هه&zwnj;روه&zwnj;ك بۆ ئێوه سوێندیان ده&zwnj;خوارد، له کاتێکدا واده&zwnj;زانن که ڕزگاریان ده&zwnj;بێت و شتێکیان ده&zwnj;ست ده&zwnj;که&zwnj;وێت!! (خوای گه&zwnj;وره له ناو خه&zwnj;ڵکیدا ڕیسوایان ده&zwnj;کات و ده&zwnj;فه&zwnj;رموێت): ئاگاداربن که به&zwnj;ڕاستی هه&zwnj;ر ئه&zwnj;وانن درۆزنن.';
$t[19] = 'ئه&zwnj;وانه شه&zwnj;یتان زاڵ بووه به&zwnj;سه&zwnj;ریانداو داگیری کردوون، به&zwnj;و هۆیه&zwnj;وه یادی خوای له&zwnj;بیر بردوون، ئا ئه&zwnj;وانه ده&zwnj;سته و تاقم و حزبی شه&zwnj;یتانن، ئاگاداربن که بێگومان ده&zwnj;سته و تاقم و حزبی شه&zwnj;یتان خه&zwnj;ساره&zwnj;تمه&zwnj;ند و زه&zwnj;ره&zwnj;رمه&zwnj;ندن.';
$t[20] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی دژایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن ئا ئه&zwnj;وانه له نزمترینترینی خه&zwnj;ڵکین له دنیاو قیامه&zwnj;تدا.';
$t[21] = 'خوا بڕیاریداوه که&zwnj;: بێگومان هه&zwnj;ر خۆم و پێغه&zwnj;مبه&zwnj;ره&zwnj;کانم سه&zwnj;رکه&zwnj;وتوو ده&zwnj;بین، چونکه به&zwnj;ڕاستی هه&zwnj;ر خوا خۆی به&zwnj;هێزو باڵاده&zwnj;سته&zwnj;.';
$t[22] = 'که&zwnj;سانێکت ده&zwnj;ست ناکه&zwnj;وێت باوه&zwnj;ڕ بهێنن به خواو به ڕۆژی دوایی، له&zwnj;کاتێکدا که&zwnj;سانێکیان خۆش بوێت که دژایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان کردبێت، ئه&zwnj;گه&zwnj;رچی ئه&zwnj;و دوژمنانه باوو باپیریان بن یان نه&zwnj;وه&zwnj;یان بن یان برایان بن یان عه&zwnj;شیره&zwnj;تیان بن، ئه&zwnj;وانه&zwnj;ی که به&zwnj;و شێوه&zwnj;یه&zwnj;&zwnj;ن (ئیمان و باوه&zwnj;ڕیان له هه&zwnj;موو که&zwnj;سێک لا به نرخترو پیرۆزتره&zwnj;)، خوا ئیمان و باوه&zwnj;ڕی له&zwnj;دڵیاندا چه&zwnj;سپاندووه&zwnj;و جێگیری کردووه&zwnj;و له به&zwnj;هره&zwnj;و میهره&zwnj;بانی تایبه&zwnj;تی خۆی، وه به هێزو گیانێکی تایبه&zwnj;تی له&zwnj;لایه&zwnj;ن خۆیه&zwnj;وه (مه&zwnj;به&zwnj;ست قورئان و ئیمانه&zwnj;) پشتگیری کردوون و یارمه&zwnj;تی داون، سه&zwnj;رنجامیش ده&zwnj;یانخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕوبار به&zwnj;ژێر دره&zwnj;خت و کۆشکه&zwnj;کانیدا تێ ده&zwnj;په&zwnj;ڕێت، هاوڕێ له&zwnj;گه&zwnj;ڵ ژیانی به&zwnj;بڕاوه&zwnj;دا، ئه&zwnj;وانه خوا لێیان ڕازیه و ئه&zwnj;وانیش له خوا ڕازین، ئا ئه&zwnj;وانه ده&zwnj;سته&zwnj;و تاقم و حزب و لایه&zwnj;نی خوای گه&zwnj;وره&zwnj;ن، ئاگاداربن که به&zwnj;ڕاستی هه&zwnj;ر لایه&zwnj;ن و حزب و ده&zwnj;سته و تاقمی خوا سه&zwnj;رکه&zwnj;وتوو و سه&zwnj;رفرازن.';
?>