<?php
$t = array();
$t[1] = 'هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه به&zwnj;پاکی و بێگه&zwnj;ردی یادی خوایان کردووه&zwnj;، هه&zwnj;ر ئه&zwnj;ویش باڵاده&zwnj;ست و دانایه&zwnj;.';
$t[2] = 'ئه&zwnj;و خوایه ئه&zwnj;و زاته&zwnj;یه&zwnj;، ئه&zwnj;وانه&zwnj;ی بێ باوه&zwnj;ڕ بوون له خاوه&zwnj;نانی کتێب، هه&zwnj;ڵیکه&zwnj;ندن له شوێنه&zwnj;واریان و ناچاری کۆچی کردن له یه&zwnj;که&zwnj;م ده&zwnj;ست پێکردنی کۆکردنه&zwnj;وه&zwnj;و ده&zwnj;رکردنیاندا، ئێوه به&zwnj;ته&zwnj;ما نه&zwnj;بوون و گومانتان نه&zwnj;ده&zwnj;برد ئاوا به&zwnj;ئاسانی بڕۆن، ئه&zwnj;وانیش گومانیان وابوو که به&zwnj;ڕاستی قه&zwnj;ڵاکانیان به&zwnj;رگریان ده&zwnj;کات له ویست و فه&zwnj;رمانی خوا، ئه&zwnj;وسا ئیتر له لا&zwnj;یه&zwnj;که&zwnj;وه خوا به&zwnj;ڵای به&zwnj;سه&zwnj;ر هێنان که به&zwnj;خه&zwnj;یاڵیاندا نه&zwnj;هاتبوون و حسابیان بۆ نه&zwnj;کردبوو، ئه&zwnj;وسا خوا ترسێکی فڕێدایه دڵیانه&zwnj;وه&zwnj;، به&zwnj;ناچاری ده&zwnj;ستیان کرد به&zwnj;کاولکردنی خانووبه&zwnj;ره&zwnj;و ماڵه&zwnj;کانیان به&zwnj;ده&zwnj;ستی خۆیان و ده&zwnj;ستی ئیمانداران، که&zwnj;واته&zwnj;: ئه&zwnj;ی خاوه&zwnj;ن بیروهۆش و دوور بینه&zwnj;کان ده&zwnj;رس و په&zwnj;ندو ئامۆژگاری وه&zwnj;ربگرن (جووله&zwnj;که هه&zwnj;روا بۆیان ناچێته سه&zwnj;ر).';
$t[3] = 'خۆ ئه&zwnj;گه&zwnj;ر خوا چۆڵکردنی له&zwnj;سه&zwnj;ر بڕیار نه&zwnj;دانایه&zwnj;، ئه&zwnj;وه هه&zwnj;ر له دنیادا سزای ده&zwnj;دان، له قیامه&zwnj;تیشدا سزای ئاگری دۆزه&zwnj;خ بۆیان ئاماده&zwnj;یه (چو&zwnj;نکه زۆر دڵڕه&zwnj;ق و پیلانگێرن).';
$t[4] = 'ئه&zwnj;و سزایه به&zwnj;هۆی ئه&zwnj;وه&zwnj;وه بوو به&zwnj;ڕاستی ئه&zwnj;وانه دوژمنایه&zwnj;تی خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;یان کرد، ئه&zwnj;وه&zwnj;ش دژایه&zwnj;تی ئاینی خوا بکات، با چاك بزانێت خوا سزاو تۆڵه&zwnj;ی زۆر به&zwnj;ئێش و ئازاره&zwnj;.';
$t[5] = 'هه&zwnj;ر دره&zwnj;ختێکی ته&zwnj;ڕتان بڕی بێت یان وازتان لێ هێنابێت و له&zwnj;سه&zwnj;ر بنجی خۆی ما بێت (له خه&zwnj;یبه&zwnj;ردا)، به&zwnj;فه&zwnj;رمانی خوا بووه و ویستی ئه&zwnj;وی له&zwnj;سه&zwnj;ره&zwnj;، تا تاوانباران و له سنوور ده&zwnj;رچووه&zwnj;کان سه&zwnj;رشۆڕ و خه&zwnj;جاڵه&zwnj;ت بکات.';
$t[6] = 'ئه&zwnj;و ده&zwnj;ستکه&zwnj;وتانه&zwnj;، خوا له جووله&zwnj;که&zwnj;ی سه&zwnj;ندووه و به&zwnj;خشیوویه&zwnj;تی به&zwnj;پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی، ئێوه هیچ خۆتان ماندوو نه&zwnj;کردبوو بۆ به&zwnj;ده&zwnj;ست هێنانی، نه به&zwnj;سواری ئه&zwnj;سپ بۆی چووبوون نه به سواری وشتر، به&zwnj;ڵکو ئه&zwnj;وه خوایه که پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی زاڵ ده&zwnj;کات به&zwnj;سه&zwnj;ر هه&zwnj;ر ده&zwnj;سته و تاقمێکدا که بیه&zwnj;وێت، خوایش ده&zwnj;سته&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[7] = 'ئه&zwnj;و ده&zwnj;ستکه&zwnj;وتانه&zwnj;ی، خوا به&zwnj;خشی به پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی له خه&zwnj;ڵکی دێهات و شاره&zwnj;کانی جوو، ئه&zwnj;وه خوا بڕیاری له&zwnj;سه&zwnj;رداوه که بۆ پێغه&zwnj;مبه&zwnj;ر و خزمانیه&zwnj;تی، بۆ هه&zwnj;تیوان و هه&zwnj;ژاران و ڕێبوارانیشه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی ئه&zwnj;و سامانه ته&zwnj;نها له نێوان ده&zwnj;وڵه&zwnj;مه&zwnj;نده&zwnj;کانی ئێوه&zwnj;دا نه&zwnj;بێت، جا ئه&zwnj;وه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) پێی به&zwnj;خشیوون وه&zwnj;ری بگرن، هه&zwnj;ر فه&zwnj;رمانێکی پێدان به&zwnj;گوێی بکه&zwnj;ن، قه&zwnj;ده&zwnj;غه&zwnj;ی هه&zwnj;ر شتێکی لێ کردن مه&zwnj;یکه&zwnj;ن، له خوا بترسن؛ چونکه به&zwnj;ڕاستی خوا به&zwnj;توندی تۆڵه ده&zwnj;سێنێت.';
$t[8] = 'هه&zwnj;روه&zwnj;ها له&zwnj;و ده&zwnj;ستکه&zwnj;وتانه&zwnj;، به&zwnj;شی کۆچبه&zwnj;رانی هه&zwnj;ژار ده&zwnj;بێت بدرێت، ئه&zwnj;وانه&zwnj;ی له شوێنه&zwnj;وارو سامانیان دوور خراونه&zwnj;ته&zwnj;وه&zwnj;و ده&zwnj;رکراون، ئه&zwnj;وانه به&zwnj;ته&zwnj;مای به&zwnj;خششی خواو ڕه&zwnj;زامه&zwnj;ندیی ئه&zwnj;ون و به&zwnj;بێ هیچ ته&zwnj;ماعێک پشتیوانی له خواو پێغه&zwnj;مبه&zwnj;ره&zwnj;که&zwnj;ی ده&zwnj;که&zwnj;ن و هه&zwnj;وڵی سه&zwnj;رخستنی به&zwnj;رنامه&zwnj;که&zwnj;یان ده&zwnj;ده&zwnj;ن، هه&zwnj;ر ئا ئه&zwnj;وانه ڕاستگۆو ڕاستانن.';
$t[9] = 'ئه&zwnj;وانه&zwnj;ی که پێشتر ماڵ و شوێنی خۆیان ئاماده کرد بۆ پێشوازیی له کۆچبه&zwnj;ران و دڵ و ده&zwnj;روونی خۆیان ڕازانده&zwnj;وه به&zwnj;ئیمان، ئه&zwnj;وانه&zwnj;شیان خۆش ده&zwnj;وێت که کۆچیان کردووه بۆ لایان و له دڵ و ده&zwnj;رونیاندا جێی هیچ جۆره حه&zwnj;سوودی و به&zwnj;خیلیه&zwnj;ك نابێته&zwnj;وه (له&zwnj;سه&zwnj;ر ئه&zwnj;وه&zwnj;ی که به&zwnj;شی کۆچبه&zwnj;ران دراوه و به&zwnj;شی ئه&zwnj;وان نه&zwnj;دراوه&zwnj;)، حه&zwnj;ز به به&zwnj;خشینی ده&zwnj;ستکه&zwnj;وته&zwnj;کان به&zwnj;وان ده&zwnj;که&zwnj;ن، فه&zwnj;زڵی ئه&zwnj;وان ده&zwnj;ده&zwnj;ن به&zwnj;سه&zwnj;ر خۆیاندا هه&zwnj;رچه&zwnj;نده هه&zwnj;ژارو نه&zwnj;داریشن، جا ئه&zwnj;وه&zwnj;ی خۆی له نه&zwnj;فسی ڕه&zwnj;زیلی بپارێزێت و زاڵ بێت به&zwnj;سه&zwnj;ریدا، ئا ئه&zwnj;وانه هه&zwnj;ر سه&zwnj;رفرازن.';
$t[10] = 'ئه&zwnj;وانه&zwnj;ش که به شوین کۆچبه&zwnj;ران و پشتیواناندا هاتن (تا کۆتایی دنیا ئه&zwnj;مه دۆعا و نزایانه و به&zwnj;رده&zwnj;وام) ده&zwnj;ڵێن: په&zwnj;روه&zwnj;ردگارا له ئێمه و له برایانه&zwnj;شمان خۆش ببه که پێش ئێمه ڕێبازی ئیمانیان گرته&zwnj;به&zwnj;ر و له دڵه&zwnj;کانماندا هیچ جۆره بوغزو کینه&zwnj;یه&zwnj;ک مه&zwnj;خه&zwnj;ره دڵمانه&zwnj;وه له ئاستی ئه&zwnj;وانه&zwnj;ی ئیمانیان هێناوه&zwnj;، په&zwnj;روه&zwnj;ردگارا تۆ به&zwnj;ڕاستی به&zwnj;سۆزو به&zwnj;خشنده و میهره&zwnj;بانیت.';
$t[11] = 'ئایا سه&zwnj;رنجت نه&zwnj;داوه له&zwnj;وانه&zwnj;ی که دووڕوو بوون و به&zwnj;برا بێباوه&zwnj;ڕه&zwnj;کانیان له خاوه&zwnj;ن کتێب ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر ئێوه ده&zwnj;ربکرێن ئێمه&zwnj;ش له&zwnj;گه&zwnj;ڵتاندا ده&zwnj;رده&zwnj;چین، هه&zwnj;رگیز دژی ئێوه به&zwnj;قسه&zwnj;ی که&zwnj;س ناکه&zwnj;ین، خۆ ئه&zwnj;گه&zwnj;ر جه&zwnj;نگتان دژ بکرێت ئێمه پشتیوانیتان لێ ده&zwnj;که&zwnj;ین، به&zwnj;مه&zwnj;رجێک؛ خوا خۆی شایه&zwnj;ته که ئه&zwnj;وانه بێگومان درۆزنن.';
$t[12] = 'سوێند به خوا ئه&zwnj;گه&zwnj;ر ده&zwnj;ربکرێن له&zwnj;گه&zwnj;ڵیاندا ده&zwnj;رناچن و ئه&zwnj;گه&zwnj;ر جه&zwnj;نگیان دژبکرێت، پشتیوانییان لێناکه&zwnj;ن و ئه&zwnj;گه&zwnj;ر پشتیوانیشیان لێکردن، دوای تاوێک پشت هه&zwnj;ڵده&zwnj;که&zwnj;ن و پاشان سه&zwnj;رکه&zwnj;وتوو نابن.';
$t[13] = 'به&zwnj;ڕاستی ئێوه&zwnj;ی ئیماندار ترستان زیاتره له دڵ و ده&zwnj;روونیاندا تا خوای گه&zwnj;وره&zwnj;، چونکه بێگومان ئه&zwnj;وانه که&zwnj;سانێکی ناحاڵی و تێ نه&zwnj;گه&zwnj;یشتوون (هه&zwnj;میشه له دڵه&zwnj;ڕاوکێدان).';
$t[14] = 'هۆزی جوو&zwnj;له&zwnj;که هه&zwnj;رگیز به&zwnj;یه&zwnj;ک پارچه&zwnj;یی ناوێرن دژی ئێوه بجه&zwnj;نگن، مه&zwnj;گه&zwnj;رله شوێنی قایم و قه&zwnj;ڵای سه&zwnj;ختدا، یان له پشتی دیواری به&zwnj;رزو قایمه&zwnj;وه&zwnj;، چونکه ئه&zwnj;و جووانه نێوانیان زۆر ناخۆشه و چاره&zwnj;ی یه&zwnj;کتریان ناوێت، هه&zwnj;رچه&zwnj;نده وا ده&zwnj;زانیت یه&zwnj;کپارچه&zwnj;ن، به&zwnj;ڵام له&zwnj;ڕاستیدا دڵه&zwnj;کانیان دوورن له یه&zwnj;که&zwnj;وه&zwnj;، چونکه ئه&zwnj;وانه ژیر و هۆشمه&zwnj;ند نین.';
$t[15] = 'نموونه&zwnj;یان وه&zwnj;ك نموونه&zwnj;ی بێباواڕانی ئه&zwnj;م نزیکانه وایه (که له جه&zwnj;نگی به&zwnj;دردا) به&zwnj;ئه&zwnj;نجامی کارو کرده&zwnj;وه&zwnj;ی خۆیان گه&zwnj;یشتن، له قیامه&zwnj;تیشدا سزای به ئێش و ئازار بۆیان ئاماده&zwnj;یه&zwnj;.';
$t[16] = '(نێوانی دووڕووه&zwnj;کان له گه&zwnj;ڵ جووه&zwnj;کان) وه&zwnj;ك شه&zwnj;یتان وایه که هانی ئاده&zwnj;میزاد ده&zwnj;دات له ڕێی خه&zwnj;یاڵ و وه&zwnj;سوه&zwnj;سه&zwnj;وه&zwnj;؛ پێی ده&zwnj;ڵێت: وه&zwnj;ره کافربه (به جوانکردنی هه&zwnj;موو جۆره تاوانێک که خوا پێی ناخۆشه&zwnj;)، جا کاتێك که کافر بوو (واته ئه&zwnj;و کارانه&zwnj;ی ئه&zwnj;نجام دا که شه&zwnj;یتان پێی ڕازیه&zwnj;) پێی ده&zwnj;ڵێت: به&zwnj;ڕاستی من به&zwnj;ریم له تۆ من له په&zwnj;روه&zwnj;ردگاری جیهانیان ده&zwnj;ترسم!!.';
$t[17] = 'سه&zwnj;رئه&zwnj;نجامی هه&zwnj;ردووکیان (شه&zwnj;یتان و ئاده&zwnj;میزادی کافر) ئه&zwnj;وه&zwnj;یه که هه&zwnj;ردوو له دۆزه&zwnj;خدا بۆ هه&zwnj;میشه&zwnj;، به&zwnj;نه&zwnj;مریی ده&zwnj;مێننه&zwnj;و، ئه&zwnj;وه&zwnj;ش پاداشتی سته&zwnj;مکارانه&zwnj;.';
$t[18] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;، خواناس و پارێزکاربن و له سنووره&zwnj;کانی مه&zwnj;ترازێن، با هه&zwnj;موو که&zwnj;سێك سه&zwnj;رنج بدات و بزانێت چی بۆ سبه&zwnj;ی ده&zwnj;ست پێشکه&zwnj;ری کردووه&zwnj;، هه&zwnj;وڵ بده&zwnj;ن هه&zwnj;میشه&zwnj;و به&zwnj;رده&zwnj;وام پارێزکارو دیندارو خواناس بن، چونکه به&zwnj;ڕاستی خوا زۆر ئاگایه به&zwnj;هه&zwnj;موو ئه&zwnj;و کاروکرده&zwnj;وانه&zwnj;ی که ئه&zwnj;نجامی ده&zwnj;ده&zwnj;ن.';
$t[19] = 'نه&zwnj;که&zwnj;ن وه&zwnj;کو ئه&zwnj;و که&zwnj;سانه بن که خوایان فه&zwnj;رامۆش کرد، شوێنی به&zwnj;رنامه&zwnj;که&zwnj;ی نه&zwnj;که&zwnj;وتن، خوایش ئه&zwnj;وانی له بیر خۆیان برده&zwnj;وه&zwnj;، ئه&zwnj;وانه له سنوور ده&zwnj;رچوو تاوانباره&zwnj;کانن.';
$t[20] = '(بێگومان) نیشته&zwnj;جێکانی دۆزه&zwnj;خ وه&zwnj;ك به&zwnj;هه&zwnj;شتیه&zwnj;کان نین، چونکه هه&zwnj;ر به&zwnj;هه&zwnj;شتیه&zwnj;کان سه&zwnj;رفرازو کامه&zwnj;ران و به&zwnj;خته&zwnj;وه&zwnj;رن.';
$t[21] = 'ئه&zwnj;گه&zwnj;ر ئه&zwnj;م قورئانه&zwnj;مان داببه&zwnj;زاندایه سه&zwnj;ر که&zwnj;ژو کێوێك، ده&zwnj;تبینی ملکه&zwnj;چ ده&zwnj;بوو، وردو و خاش ده&zwnj;بوو، شه&zwnj;ق و په&zwnj;ق ده&zwnj;بوو، له ترسی فه&zwnj;رمانی خوا، له هه&zwnj;یبه&zwnj;تی گه&zwnj;وره&zwnj;یی و ده&zwnj;سه&zwnj;ڵاتی خوا، ئێمه ئه&zwnj;م نموونانه ده&zwnj;هێنینه&zwnj;وه بۆ خه&zwnj;ڵکی بۆ ئه&zwnj;وه&zwnj;ی بیربکه&zwnj;نه&zwnj;وه و ژیرییان بخه&zwnj;نه کار و شوین قورئانی پیرۆز بکه&zwnj;ون.';
$t[22] = 'خوا ئه&zwnj;و زاته&zwnj;یه که جگه له&zwnj;و خوایه&zwnj;کی تر نیه زانایه به هه&zwnj;موو نهێنی و ئاشکراکان، ئه&zwnj;و خوایه به&zwnj;خشنده و میهره&zwnj;بان و دلۆڤانه کانگای ڕه&zwnj;حم و به&zwnj;زه&zwnj;ییه&zwnj;.';
$t[23] = 'خوا ئه&zwnj;و زاته&zwnj;یه که جگه له&zwnj;و خوایه&zwnj;کی تر نیه پاشای هه&zwnj;موو بوونه&zwnj;وه&zwnj;ره&zwnj;، پاك و بێگه&zwnj;ردو پوخته&zwnj;، ئاشتی خواز و ئاشتی په&zwnj;روه&zwnj;ره&zwnj;، ئاسایش به&zwnj;خش و په&zwnj;ناگای ئیماندارانه&zwnj;، ده&zwnj;سه&zwnj;ڵاتداره و چاودێری دروستکراوه&zwnj;کانیه&zwnj;تی، باڵاده&zwnj;سته&zwnj;، خاوه&zwnj;نی خێری بێسنووره&zwnj;، گه&zwnj;وره&zwnj;یه و خۆی به&zwnj;گه&zwnj;وره ده&zwnj;زانێت، پاکی و بێگه&zwnj;ردی بۆ ئه&zwnj;و خوایه&zwnj;یه له&zwnj;وه&zwnj;ی تینه&zwnj;گه&zwnj;یشتوان شه&zwnj;ریك و هاوه&zwnj;ڵی بۆ بڕیار ده&zwnj;ده&zwnj;ن.';
$t[24] = 'هه&zwnj;ر ئه&zwnj;و خوایه به&zwnj;دیهێنه&zwnj;رو دروستکراوه&zwnj;، (له نه&zwnj;بوون هه&zwnj;رچی بۆی به&zwnj;دی ده&zwnj;هێنێت)، نه&zwnj;خشه و وێنه کێشه&zwnj;ره&zwnj;، ناوه پیرۆزو جوانه&zwnj;کان، هه&zwnj;مووی شایسته&zwnj;ی ئه&zwnj;ون، هه&zwnj;رچی له ئاسمانه&zwnj;کان و زه&zwnj;ویدا هه&zwnj;یه ستایشی ئه&zwnj;و ده&zwnj;که&zwnj;ن، به&zwnj;ڕاستی ئه&zwnj;و زاته خوایه&zwnj;کی باڵاده&zwnj;ست و دانایه&zwnj;.';
?>